INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(12, 'Những nụ hôn ngọt ngào trong đêm tình nhân ', 'Tối 13/2, hàng nghìn bạn trẻ có mặt tại cầu Ánh Sao (quận 7, TP HCM) chứng kiến những lời tỏ tình cùng những nụ hôn ngọt ngào của 100 cặp tình nhân trong "Đêm Valentine thế kỷ".\r\n', '250h_jpg_thumb210x0_ns.jpg', NULL, '<h1 class="Title">Những nụ h&ocirc;n ngọt ng&agrave;o trong đ&ecirc;m t&igrave;nh nh&acirc;n</h1>\n<h2 class="Lead">Tối 13/2, h&agrave;ng ngh&igrave;n bạn trẻ c&oacute; mặt tại cầu &Aacute;nh Sao (quận 7, TP  HCM) chứng kiến những lời tỏ t&igrave;nh c&ugrave;ng những nụ h&ocirc;n ngọt ng&agrave;o của 100 cặp t&igrave;nh  nh&acirc;n trong "Đ&ecirc;m Valentine thế kỷ".</h2>\n<p><img src="h7.jpg" alt="" width="495" height="330" /></p>\n<p>Cầu &Aacute;nh Sao (quận 7, TP HCM) tối 13/2 l&agrave; nơi ghi lại khoảnh khắc 100 đ&ocirc;i t&igrave;nh  nh&acirc;n trao nhau những nụ h&ocirc;n ngọt ng&agrave;o.</p>\n<p><img src="h.jpg" alt="" width="495" height="374" /></p>\n<p>Đ&ocirc;i t&igrave;nh nh&acirc;n may mắn duy nhất Thế Mẫn - Thu Hương trong "Đ&ecirc;m Valentine thế kỷ"  được thể hiện t&igrave;nh y&ecirc;u của m&igrave;nh tr&ecirc;n khinh kh&iacute; cầu. Cả hai đ&atilde; trao nhau nụ h&ocirc;n  nồng n&agrave;n.</p>\n<p><img src="h2.jpg" alt="" width="495" height="356" /></p>\n<p>Kinh kh&iacute; cầu bay bổng đưa đ&ocirc;i bạn trẻ Thế Mẫn - Thu Hương l&ecirc;n bầu trời mịt tối.  "C&oacute; một nụ h&ocirc;n tr&ecirc;n cao v&agrave; trong kh&ocirc;ng kh&iacute; như thế n&agrave;y th&igrave; kh&ocirc;ng g&igrave; sung sướng  bằng", Thu Hương t&acirc;m sự.</p>\n<p><img src="h4.jpg" alt="" width="495" height="344" /></p>\n<p>H&agrave;ng trăm bạn trẻ kh&ocirc;ng c&oacute; dịp h&ocirc;n tr&ecirc;n khinh kh&iacute; cầu chia sẻ niềm vui v&agrave; ch&uacute;c  mừng hai bạn trẻ.</p>\n<p><img src="h17.jpg" alt="" width="495" height="352" /></p>\n<p>Đ&ocirc;i t&igrave;nh nh&acirc;n Qu&acirc;n v&agrave; Thương c&oacute; được những gi&acirc;y ph&uacute;t l&atilde;ng mạn.</p>\n<p><img src="h16.jpg" alt="" width="495" height="355" /></p>\n<p>V&agrave; ng&agrave;y t&igrave;nh nh&acirc;n năm nay, Thương nhận được m&oacute;n qu&agrave; 999 nụ hồng xanh do người  y&ecirc;u d&agrave;nh tặng.</p>\n<p><img src="h11.jpg" alt="" width="495" height="373" /></p>\n<p>C&ograve;n với Ngọc Yến, ng&agrave;y Valentine năm nay, người y&ecirc;u Minh Tuấn tỏ lời cầu h&ocirc;n c&ocirc;  bằng đ&ocirc;i nhẫn cưới.</p>\n<p><img src="h9.jpg" alt="" width="495" height="367" /></p>\n<p>Ngọc Yến đ&atilde; nhận lời cầu h&ocirc;n v&agrave; cả hai trao nhau nụ h&ocirc;n trước sự chứng kiến của  rất đ&ocirc;ng bạn trẻ.</p>\n<p><img src="h12.jpg" alt="" width="495" height="359" /></p>\n<p>Kim Ph&uacute;c hạnh ph&uacute;c khi người y&ecirc;u l&agrave; ch&agrave;ng trai đến từ Ph&aacute;p c&oacute; mặt tại Việt Nam  trong đ&ecirc;m đặc biệt n&agrave;y.</p>\n<p><img src="h18.jpg" alt="" width="495" height="342" /></p>\n<p>C&ocirc; g&aacute;i hạnh ph&uacute;c khi nhận m&oacute;n qu&agrave; l&aacute; đ&oacute;a hoa từ người y&ecirc;u trong đ&ecirc;m t&igrave;nh nh&acirc;n.</p>\n<p><img src="h22.jpg" alt="" width="495" height="371" /></p>\n<p>Ghi lại gi&acirc;y ph&uacute;t l&atilde;ng mạn trong đ&ecirc;m t&igrave;nh nh&acirc;n.</p>\n<p><img src="h21.jpg" alt="" width="495" height="351" /></p>\n<p>H&agrave;ng trăm đ&ocirc;i c&oacute; mặt trong "Đ&ecirc;m Valentine thế kỷ".</p>\n<p style="text-align: right;"><strong>An Nhơn</strong></p>', 2, 1, 63, 0, 1),
(21, 'Học sinh vùng cao nghỉ Tết kéo dài vì giá rét', 'Học sinh Hà Giang có thể nghỉ Tết Tân Mão gần 20 ngày còn học sinh Lào Cai được nghỉ Tết hơn các vùng khác 3 ngày để tránh giá rét.\r\n', 'sapa9.jpg', NULL, '<h1 class="Title">Học sinh v&ugrave;ng cao nghỉ Tết k&eacute;o d&agrave;i v&igrave; gi&aacute; r&eacute;t</h1>\n<h2 class="Lead">Học sinh H&agrave; Giang c&oacute; thể nghỉ Tết T&acirc;n M&atilde;o gần 20 ng&agrave;y c&ograve;n học  sinh L&agrave;o Cai được nghỉ Tết hơn c&aacute;c v&ugrave;ng kh&aacute;c 3 ng&agrave;y để tr&aacute;nh gi&aacute; r&eacute;t.</h2>\n<p class="Normal">S&aacute;ng 8/1, trao đổi với <em>VnExpress.net</em>, &ocirc;ng Lương Văn  So&ograve;ng, Gi&aacute;m đốc Sở GD&amp;ĐT H&agrave; Giang cho biết, tỉnh n&agrave;y chưa c&oacute; lịch nghỉ Tết  cụ thể v&igrave; thời tiết chưa ổn định. Tuy nhi&ecirc;n so với mọi năm, năm nay r&eacute;t hơn,  nhất l&agrave; v&ugrave;ng n&uacute;i đ&aacute; Đồng Văn, M&egrave;o Vạc. V&igrave; thế, thời gian nghỉ Tết của học sinh  c&oacute; thể được k&eacute;o d&agrave;i gần 20 ng&agrave;y.</p>\n<p class="Normal">Theo &ocirc;ng So&ograve;ng, ngay từ đầu năm học, Sở đ&atilde; c&oacute; kế hoạch cụ thể  cho ng&agrave;y bắt đầu v&agrave; kết th&uacute;c năm học. Học sinh H&agrave; Giang khai giảng sớm hơn cả  nước hai tuần. Nếu r&eacute;t hại k&eacute;o d&agrave;i th&igrave; c&aacute;c Ph&ograve;ng Gi&aacute;o dục tự xem x&eacute;t, đề nghị  với UBND huyện cho học sinh được nghỉ.</p>\n<p class="Normal">"Do học trước n&ecirc;n ch&uacute;ng t&ocirc;i kh&ocirc;ng lo chậm chương tr&igrave;nh. Mặt  kh&aacute;c, trong điều kiện gi&aacute; r&eacute;t th&igrave; việc giữ sức khỏe cho học sinh phải được xem  l&agrave; trọng t&acirc;m", &ocirc;ng So&ograve;ng n&oacute;i.</p>\n<p class="Normal"><img src="sapa9.jpg" alt="" width="494" height="349" /></p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td class="Image">Học sinh ở Sapa đi học trong gi&aacute; r&eacute;t. Ảnh: <em>M&atilde; Anh  L&acirc;m.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Gi&aacute;m đốc Sở GD&amp;ĐT L&agrave;o Cai Trương Kim Minh cho hay d&ugrave; đ&atilde; c&oacute;  kế hoạch cho học sinh tiểu học nghỉ Tết 10 ng&agrave;y, trung học một tuần, bắt đầu từ  28 Tết, nhưng để đối ph&oacute; với thời tiết biến đổi thất thường Sở kh&ocirc;ng quy định  ng&agrave;y nghỉ cứng. C&aacute;c trường đều được trang bị biểu đo nhiệt độ ri&ecirc;ng, căn cứ v&agrave;o  nhiệt độ chiều h&ocirc;m trước, nếu thấy dưới 10 độ C th&igrave; c&oacute; thể b&aacute;o c&aacute;o cho Ph&ograve;ng  Gi&aacute;o dục để xin chỉ đạo của UBND huyện, từ đ&oacute; cho học sinh nghỉ.</p>\n<p class="Normal">"Ch&uacute;ng t&ocirc;i giao quyền chủ động cho c&aacute;c trường bởi chỉ dựa v&agrave;o dự  b&aacute;o thời tiết của tỉnh th&igrave; chưa đủ. V&igrave; khu vực Sapa hay &Ocirc; Qu&yacute; Hồ c&oacute; thể dưới 5  độ C, nhưng v&ugrave;ng Thanh Kim, Thanh Qu&yacute; lại tr&ecirc;n 10 độ C", &ocirc;ng Minh n&oacute;i v&agrave; cho  biết sau Tết học sinh L&agrave;o Cai được nghỉ k&eacute;o d&agrave;i hơn nơi kh&aacute;c khoảng 3 ng&agrave;y.</p>\n<p class="Normal">Gi&aacute;m đốc Sở khẳng định việc chống r&eacute;t t&iacute;ch cực kh&ocirc;ng phải l&agrave; cho  học sinh nghỉ học bởi L&agrave;o Cai c&oacute; những v&ugrave;ng quanh năm r&eacute;t, nếu nghỉ d&agrave;i th&igrave;  kh&ocirc;ng thể ho&agrave;n th&agrave;nh chương tr&igrave;nh. Ch&iacute;nh v&igrave; vậy, ngay từ th&aacute;ng 10/2010, Sở đ&atilde; c&oacute;  kế hoạch đ&oacute;n r&eacute;t v&agrave; r&egrave;n cho học sinh chống chọi với thời tiết.</p>\n<p class="Normal">Việc đầu ti&ecirc;n l&agrave; c&aacute;c trường sửa chữa ph&ecirc;n, v&aacute;ch, mua bao tải về  bịt khe hở. Sở GD&amp;ĐT H&agrave; Giang c&ograve;n li&ecirc;n hệ với H&agrave; Nội k&ecirc;u gọi ủng hộ quần &aacute;o  để học sinh giữ ấm. "Mới đ&acirc;y, Sở đ&atilde; nhận 200 khối quần &aacute;o, đồ d&ugrave;ng cho m&ugrave;a r&eacute;t  từ H&agrave; Nội gửi l&ecirc;n. Số quần &aacute;o n&agrave;y đ&atilde; được ph&acirc;n ph&aacute;t đến tận trường cho học  sinh", &ocirc;ng Minh cho hay.</p>\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 1827, 0, 1),
(24, 'Hỗ trợ gần 3.000 vé xe tết cho sinh viên', 'Mỗi khi máy bay sắp hạ cánh xuống sân bay Suvarnabhumi, tôi đều có cảm giác mình sắp được trở về nhà, về quê hương thân thương thứ hai của mình. Bạn Đặng Quốc Chí chia sẻ.Khoảng 2.700 sinh viên ngoại tỉnh có hoàn cảnh khó khăn, đang học tập tại TP HCM sẽ được tặng vé xe miễn phí về quê trong dịp tết Tân Mão.', 'sinhvien[1]_1.jpg', NULL, '<h1 class="Title">Hỗ trợ gần 3.000 v&eacute; xe tết cho sinh vi&ecirc;n</h1>\n<h2 class="Lead">Khoảng 2.700 sinh vi&ecirc;n ngoại tỉnh c&oacute; ho&agrave;n cảnh kh&oacute; khăn, đang học tập tại TP HCM sẽ được tặng v&eacute; xe miễn ph&iacute; về qu&ecirc; trong dịp tết T&acirc;n M&atilde;o.</h2>\n<p class="Normal">Đ&acirc;y l&agrave; một trong chuỗi c&aacute;c hoạt động hỗ trợ sinh vi&ecirc;n dịp tết của Trung t&acirc;m hỗ trợ sinh vi&ecirc;n TP HCM. Trong đ&oacute;, dự kiến sẽ d&agrave;nh 1.800 v&eacute; cho sinh vi&ecirc;n ngh&egrave;o c&oacute; hộ khẩu thường tr&uacute; tại c&aacute;c tỉnh miền Trung bị thiệt hại nặng nề trong c&aacute;c đợt b&atilde;o lũ vừa qua. 900 v&eacute; c&ograve;n lại sẽ tặng cho c&aacute;c sinh vi&ecirc;n ngh&egrave;o, sinh vi&ecirc;n khuyết tật, sinh vi&ecirc;n d&acirc;n tộc c&aacute;c tỉnh th&agrave;nh kh&aacute;c trong cả nước.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sinhvien[1].jpg" border="1" alt="Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết" width="480" height="328" /></td>\n</tr>\n<tr>\n<td class="Image">Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Ph&oacute; gi&aacute;m đốc trung t&acirc;m hỗ trợ sinh vi&ecirc;n Dương Trọng Ph&uacute;c cho biết, so với c&aacute;c năm trước số lượng v&eacute; hỗ trợ năm nay &iacute;t hơn nhiều v&igrave; kh&ocirc;ng c&oacute; c&aacute;c đơn vị v&agrave; doanh nghiệp đồng h&agrave;nh. Đa phần kinh ph&iacute; trong hoạt động n&agrave;y đều do Ủy ban Mặt trận tổ quốc Việt Nam TP HCM hỗ trợ.</p>\n<p class="Normal">Số lượng v&eacute; tặng sẽ đưa xuống c&aacute;c trường theo chỉ ti&ecirc;u. Sinh vi&ecirc;n kh&oacute; khăn trong c&aacute;c diện được x&eacute;t tặng v&eacute; xe c&oacute; thể li&ecirc;n hệ trực tiếp tại văn ph&ograve;ng Đo&agrave;n thanh ni&ecirc;n - Hội sinh vi&ecirc;n, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n của trường trước ng&agrave;y 31/12 để tiến h&agrave;nh l&agrave;m hồ sơ.</p>\n<p class="Normal">Ngo&agrave;i tặng v&eacute; xe, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n sẽ cung cấp khoảng 3.500 đầu việc với nhiều loại h&igrave;nh theo tuần, ng&agrave;y, giờ... cho c&aacute;c bạn ở lại c&oacute; nhu cầu kiếm th&ecirc;m thu nhập. Chương tr&igrave;nh vui tết xa nh&agrave; với nhiều hoạt động th&uacute; vị cũng sẽ được tổ chức cho những sinh vi&ecirc;n kh&ocirc;ng c&oacute; điều kiện về qu&ecirc;. Đại diện Th&agrave;nh Đo&agrave;n v&agrave; Trung t&acirc;m hỗ trợ sinh vi&ecirc;n cũng sẽ đi thăm hỏi, giao lưu v&agrave; tặng qu&agrave; cho sinh vi&ecirc;n trong KTX của c&aacute;c trường.</p>\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 2687, 0, 1),
(1546, 'Trung Quốc xây đô thị siêu sạch', 'Người dân trong đô thị sinh thái của Trung Quốc có thể uống nước từ vòi, di chuyển trên các phương tiện giao thông không xả khí thải.', 'eco6.jpg', NULL, '<p class="Title">Trung Quốc x&acirc;y đ&ocirc; thị si&ecirc;u sạch</p>\r\n<p class="Lead">Người d&acirc;n trong đ&ocirc; thị sinh th&aacute;i của Trung Quốc c&oacute; thể uống nước từ v&ograve;i, di chuyển tr&ecirc;n c&aacute;c phương tiện giao th&ocirc;ng kh&ocirc;ng xả kh&iacute; thải.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="eco6.jpg" alt="M&ocirc; h&igrave;nh th&agrave;nh phố sinh th&aacute;i gần Thi&ecirc;n T&acirc;n. Ảnh: AFP." width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&ocirc; h&igrave;nh th&agrave;nh phố sinh th&aacute;i gần Thi&ecirc;n T&acirc;n. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>AFP</em>&nbsp;cho biết, th&agrave;nh phố sinh th&aacute;i của Trung Quốc được x&acirc;y tr&ecirc;n khu đất kh&ocirc;ng thể trồng trọt do nhiễm mặn gần th&agrave;nh phố cảng Thi&ecirc;n T&acirc;n v&agrave; c&oacute; diện t&iacute;ch 30 km2. Qu&aacute; tr&igrave;nh x&acirc;y dựng sẽ diễn ra trong &iacute;t nhất 10 năm.</p>\r\n<p class="Normal">C&aacute;c nh&agrave; thầu hy vọng th&agrave;nh phố sinh th&aacute;i của họ sẽ l&agrave; h&igrave;nh mẫu của một loại đ&ocirc; thị si&ecirc;u sạch, được quy hoạch tốt v&agrave; ph&aacute;t triển bền vững.</p>\r\n<p class="Normal">&ldquo;Ch&uacute;ng t&ocirc;i hy vọng th&agrave;nh phố sinh th&aacute;i mới sẽ t&aacute;c động tới những nước l&aacute;ng giềng&rdquo;, Goh Chye Boon, tổng gi&aacute;m đốc điều h&agrave;nh tập đo&agrave;n đầu tư v&agrave; ph&aacute;t triển đ&ocirc; thị sinh th&aacute;i Thi&ecirc;n T&acirc;n, ph&aacute;t biểu.</p>\r\n<p class="Normal">Ch&iacute;nh phủ Trung Quốc v&agrave; Singapore hợp t&aacute;c với nhau về t&agrave;i ch&iacute;nh v&agrave; kỹ thuật để x&acirc;y dựng th&agrave;nh phố sinh th&aacute;i. Theo kế hoạch th&agrave;nh phố sẽ c&oacute; 350.000 d&acirc;n với cơ sở hạ tầng to&agrave;n diện như trường học, bệnh viện v&agrave; c&aacute;c khu thương mại.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="eco2.jpg" alt="Một xe tải tiến v&agrave;o c&ocirc;ng trường x&acirc;y dựng đ&ocirc; thị sinh th&aacute;i gần th&agrave;nh phố Thi&ecirc;n T&acirc;n v&agrave;o ng&agrave;y 15/9. Ảnh: AFP." width="450" height="281" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một xe tải tiến v&agrave;o c&ocirc;ng trường x&acirc;y dựng đ&ocirc; thị sinh th&aacute;i gần th&agrave;nh phố Thi&ecirc;n T&acirc;n v&agrave;o ng&agrave;y 15/9. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhiều c&ocirc;ng ty nước ngo&agrave;i, như Hitachi (Nhật Bản) v&agrave; Philips (H&agrave; Lan), sẽ cung cấp c&ocirc;ng nghệ sạch cho th&agrave;nh phố. C&aacute;c t&ograve;a nh&agrave; sẽ c&oacute; tường c&aacute;ch &acirc;m v&agrave; được lắp cửa sổ c&oacute; khả năng nhận nhiều &aacute;nh s&aacute;ng để tiết kiệm điện.</p>\r\n<p class="Normal">Gần hai phần ba r&aacute;c thải của d&acirc;n sẽ được t&aacute;i chế. 20% điện trong th&agrave;nh phố sẽ được sản xuất từ c&aacute;c nguồn t&agrave;i nguy&ecirc;n v&ocirc; tận v&agrave; kh&ocirc;ng g&acirc;y &ocirc; nhiễm m&ocirc;i trường như gi&oacute; v&agrave; &aacute;nh s&aacute;ng mặt trời.</p>\r\n<p class="Normal">Sau khi được xử l&yacute;, nước thải sẽ chảy theo c&aacute;c k&ecirc;nh để v&agrave;o một hồ. Người d&acirc;n c&oacute; thể lấy nước trong hồ để sử dụng trong sinh hoạt h&agrave;ng ng&agrave;y.</p>\r\n<p class="Normal">&ldquo;Đ&ocirc; thị sinh th&aacute;i trở th&agrave;nh nhu cầu cần thiết bởi Trung Quốc đang đối mặt với t&igrave;nh trạng &ocirc; nhiễm nghi&ecirc;m trọng trong c&aacute;c th&agrave;nh phố&rdquo;, &ocirc;ng Hiroaki Suzuki - một chuy&ecirc;n gia h&agrave;ng đầu về t&agrave;i ch&iacute;nh, kinh tế v&agrave; ph&aacute;t triển đ&ocirc; thị của Ng&acirc;n h&agrave;ng Thế giới &ndash; nhận x&eacute;t. Ng&acirc;n h&agrave;ng Thế giới l&agrave; tổ chức hỗ trợ dự &aacute;n đ&ocirc; thị sinh th&aacute;i của Trung Quốc.</p>\r\n<p class="Normal">Giới chức cao cấp của Trung Quốc hy vọng đ&ocirc; thị sinh th&aacute;i sẽ gi&uacute;p họ giải quyết t&igrave;nh trạng số d&acirc;n đ&ocirc; thị tăng qu&aacute; nhanh. Kể từ khi Trung Quốc thực hiện cải c&aacute;ch kinh tế c&aacute;ch đ&acirc;y hơn 30 năm, d&acirc;n số đ&ocirc; thị nước n&agrave;y đ&atilde; tăng với tốc độ cao nhất trong lịch sử do v&agrave;i trăm triệu người di chuyển ra th&agrave;nh phố để t&igrave;m kiếm cơ hội.</p>\r\n<p class="Normal">Đ&agrave;i truyền h&igrave;nh Trung Quốc cho biết, để đ&aacute;p ứng nhu cầu của cư d&acirc;n th&agrave;nh phố, Trung Quốc phải đầu tư tới 3,6 ngh&igrave;n tỷ USD v&agrave;o cơ sở hạ tầng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 34, 8, 2836, 0, 1),
(32, '4 độc giả viết về du lịch Thái Lan được tặng quà', 'Trong số nhiều độc giả gửi bài tham dự cuộc thi viết "Trải nghiệm kỳ thú khi du lịch Thái Lan", Ban biên tập đã lựa chọn 4 bài viết để trao quà tặng cho tháng 1. ', 'a3.jpg', NULL, '<div style="overflow: hidden;">\n<h1 class="Title">4 độc giả viết về du lịch Th&aacute;i Lan được tặng qu&agrave;</h1>\n<h2 class="Lead">Trong số nhiều độc giả gửi b&agrave;i tham dự cuộc thi viết  "Trải nghiệm kỳ th&uacute; khi du lịch Th&aacute;i Lan", Ban bi&ecirc;n tập đ&atilde; lựa chọn 4  b&agrave;i viết để trao qu&agrave; tặng cho th&aacute;ng 1.</h2>\n<p class="Normal">*Giải nhất l&agrave; một v&eacute; m&aacute;y bay khứ hồi đến Th&aacute;i Lan:</p>\n<p class="Normal">- Độc giả bạn Th&ugrave;y Linh với b&agrave;i viết <a class="Normal" href="http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba252f8/">Bangkok, điểm đến của những người th&iacute;ch kh&aacute;m ph&aacute;</a></p>\n<p class="Normal">*3 giải khuyến kh&iacute;ch l&agrave; c&aacute;c phần qu&agrave; lưu niệm:</p>\n<p class="Normal">- Độc giả Di Linh với b&agrave;i viết <a class="Normal" href="http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba25546/">Tản mạn về Th&aacute;i Lan</a></p>\n<p class="Normal">- Độc giả L&ecirc; Trực với b&agrave;i viết <a class="Normal" href="http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba2587a/">Cầu s&ocirc;ng Kwai, nơi g&igrave;n giữ thời gian</a></p>\n<p class="Normal">- Độc giả Thuc Nga với b&agrave;i viết <a class="Normal" href="http://vnexpress.net/gl/xa-hoi/du-lich/2011/01/3ba25dce/">Bangkok v&agrave; Pattaya, gi&oacute; v&agrave; những đ&ecirc;m kh&ocirc;ng ngủ </a></p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="a3.jpg" border="1" alt="Ho&agrave;ng Gia" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Cung điện Ho&agrave;ng Gia Th&aacute;i Lan lu&ocirc;n đ&ocirc;ng kh&aacute;ch du lịch. Ảnh: <em>Th&ugrave;y Linh.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">C&aacute;c t&aacute;c giả trong danh s&aacute;ch nhận qu&agrave; mời li&ecirc;n hệ với  Anh Khoa, Tổng cục du lịch Th&aacute;i Lan, Lầu 5 - T&ograve;a nh&agrave; Empire - 26 - 28  H&agrave;m Nghi - quận 1, TP HCM, điện thoại: 08.62913885, 0913883685 để nhận  qu&agrave; tặng.</p>\n<p class="Normal" style="text-align: right;"><strong>Đo&agrave;n Loan</strong></p>\n</div>', 3, 1, 2564, 0, 1),
(33, 'Lễ hội thổ dân nơi tận cùng thế giới ', 'Lễ hội thổ dân ở thung lũng Baliem, trên hòn đảo West Papua New Guinea thuộc Indonesia là một điểm nhấn văn hóa mang đậm hơi thở của những bộ tộc hoang dã nhất hành tinh. Bạn Trần Thu Thủy chia sẻ.', '7.jpg', NULL, '<h1 class="Title">Lễ hội thổ d&acirc;n nơi tận c&ugrave;ng thế giới</h1>\n<h2 class="Lead">Lễ hội thổ d&acirc;n ở thung lũng Baliem, tr&ecirc;n h&ograve;n đảo West  Papua New Guinea thuộc Indonesia l&agrave; một điểm nhấn văn h&oacute;a mang đậm hơi  thở của những bộ tộc hoang d&atilde; nhất h&agrave;nh tinh. Bạn Trần Thu Thủy chia sẻ.</h2>\n<p class="Normal">Lễ hội văn h&oacute;a thổ d&acirc;n ở thung lũng Baliem l&agrave; một  trong những t&agrave;i sản văn h&oacute;a phong ph&uacute; v&agrave; độc đ&aacute;o nhất ở Indonesia. Bản  th&acirc;n lễ hội đ&atilde; l&agrave; một sự h&aacute;o hức với ch&iacute;nh thổ d&acirc;n địa phương chứ kh&ocirc;ng  chỉ hấp dẫn với kh&aacute;ch phương xa. Th&aacute;ng 8 l&agrave; thời điểm khắp nơi tr&ecirc;n h&ograve;n  đảo West Papua New Guinea thuộc Indonesia diễn ra c&aacute;c lễ hội.</p>\n<p class="Normal">Tham dự v&agrave;o lễ hội thung lũng Baliem, du kh&aacute;ch sẽ c&oacute;  cơ hội t&igrave;m hiểu v&agrave; trải nghiệm trực tiếp những hoạt động mang đậm văn  h&oacute;a thổ d&acirc;n c&ograve;n như thuở sơ khai với sự hiện diện của h&agrave;ng ngh&igrave;n thổ  d&acirc;n. Lễ hội t&aacute;i hiện từ những trận đ&aacute;nh giữa c&aacute;c bộ lạc, những vũ điệu  của chiến binh h&acirc;n hoan chiến thắng c&aacute;c cuộc đi săn đầu người, những  điệu m&uacute;a của thiếu nữ ngực trần, những người đ&agrave;n &ocirc;ng t&oacute;c xoăn da đen lực  lưỡng trong c&aacute;c cuộc đi săn th&uacute;&hellip;</p>\n<p class="Normal">Những điệu m&uacute;a xuất ph&aacute;t từ trong cuộc sống rất đỗi  b&igrave;nh dị của họ, từ việc nh&oacute;m lửa, ca h&aacute;t, &acirc;n &aacute;i hay những tranh chấp,  ghen tu&ocirc;ng, c&atilde;i lộn, đ&aacute;nh nhau, giảng h&ograve;a cho đến tục lệ linh thi&ecirc;ng như  ướp x&aacute;c t&ugrave; trưởng. Một điều dễ nhận thấy l&agrave; họ tham gia v&agrave;o lễ hội, họ  nhảy m&uacute;a, họ ca h&aacute;t một c&aacute;ch say sưa, rất đỗi tự nhi&ecirc;n v&agrave; đầy l&ograve;ng nhiệt  t&igrave;nh. Cứ như thể trong m&aacute;u họ đ&atilde; sẵn c&oacute; những nhịp điệu v&agrave; những lời ca  tiếng h&aacute;t.</p>\n<p class="Normal">Thổ d&acirc;n đến với lễ hội ai ai cũng muốn ph&ocirc; trương sự  đẹp đẽ của h&igrave;nh thể v&agrave; sự uy nghi của khu&ocirc;n mặt. M&agrave;u trắng ng&agrave; của những  c&aacute;i nanh lợn như t&uacute;a ra từ hốc mũi khiến cho c&aacute;c chiến binh Dani c&agrave;ng  th&ecirc;m vẻ oai phong dữ tợn. Họ lấy đất s&eacute;t c&aacute;c m&agrave;u để vẽ l&ecirc;n mặt, lấy bồ  h&oacute;ng để b&ocirc;i l&ecirc;n l&agrave;n da đ&atilde; sẵn đen b&oacute;ng căng tr&agrave;n sức sống. Họ vẽ c&aacute;c  mảng m&agrave;u lớn, c&aacute;c chấm hoặc c&aacute;c h&igrave;nh tr&ograve;n, lục gi&aacute;c quanh cơ thể, đầu  đội mũ l&ocirc;ng chim sặc sỡ &aacute;nh l&ecirc;n rực rỡ trong nắng v&agrave;ng v&agrave; tr&ecirc;n nền trời  xanh ngăn ngắt&hellip; &Aacute;nh mắt của những người thổ d&acirc;n c&ograve;n nguy&ecirc;n c&aacute;i vẻ hoang  dại nhưng khi họ cười, bạn sẽ nhận thấy ngay th&ocirc;ng điệp th&acirc;n thiện từ  &aacute;nh mắt đ&oacute;.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="7.jpg" alt="thổ d&acirc;n" width="500" height="330" /></td>\n</tr>\n<tr>\n<td class="Image">Nanh lợn l&agrave;m tăng vẻ dữ tợn cho đ&agrave;n &ocirc;ng Dani. Ảnh: <em>Thu Thủy.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">C&oacute; ba bộ lạc ch&iacute;nh sinh sống trong l&ograve;ng thung lũng  Baliem. Tộc người Dani chiếm đa số, người Lai sống ở ph&iacute;a t&acirc;y thung lũng  v&agrave; người Yali ở ph&iacute;a đ&ocirc;ng nam. Mỗi bộ lạc c&oacute; một nền văn h&oacute;a kh&aacute;c biệt.  Một c&aacute;ch chắc chắn v&agrave; th&uacute; vị để ph&acirc;n biệt giữa c&aacute;c bộ lạc ch&iacute;nh l&agrave; quả  bầu (penis gourd) che phần k&iacute;n của người đ&agrave;n &ocirc;ng hay c&ograve;n gọi l&agrave; Koteka.</p>\n<p class="Normal">Người Dani sử dụng loại bầu d&agrave;i, nhọn, vỏ mỏng. Ph&iacute;a  b&ecirc;n tr&ecirc;n quả bầu được trang tr&iacute; bằng một nh&uacute;m l&ocirc;ng chim hoặc một mảnh da  th&uacute;. Ngo&agrave;i mục đ&iacute;ch sử dụng koteka để che dương vật, người Lani lại  d&ugrave;ng quả bầu c&oacute; k&iacute;ch thước kh&aacute; to như một nơi để đựng những thứ ri&ecirc;ng tư  của m&igrave;nh v&agrave; thậm ch&iacute; họ đựng cả thực phẩm ở trong đ&oacute;. C&ograve;n người Yali  lại sử dụng Koteka vừa nhỏ, vừa d&agrave;i, thậm ch&iacute; đủ d&agrave;i để n&acirc;ng đỡ những  v&ograve;ng m&acirc;y đeo quanh bụng.</p>\n<p class="Normal">Ngo&agrave;i những thứ phụ trang tr&ecirc;n đầu, tr&ecirc;n cổ, phụ nữ  c&aacute;c bộ lạc đều mặc v&aacute;y cỏ. Đặc biệt phụ nữ Yali mặc v&aacute;y ph&acirc;n theo tầng  theo lớp, mỗi lớp tương đương với 4 tuổi. C&ocirc; n&agrave;o v&aacute;y c&oacute; 4 lớp trở l&ecirc;n l&agrave;  đến tuổi lấy chồng. Tr&ecirc;n dọc đường đi s&acirc;u v&agrave;o thung lũng, đ&acirc;u đ&acirc;u t&ocirc;i  cũng thấy những phụ nữ Dani đeo một c&aacute;i t&uacute;i tự đan từ sợi cỏ gọi l&agrave;  &ldquo;noken&rdquo;, d&acirc;y t&uacute;i vắt ngang qua đầu v&agrave; bu&ocirc;ng xuống qu&aacute; lưng, n&oacute; như l&agrave;  một phần thể thiếu trong trang phục của họ vậy. Nhiều năm trước đ&acirc;y, khi  x&atilde; hội văn minh c&ograve;n chưa chạm tới nơi n&agrave;y, vỏ ốc, nanh ch&oacute; v&agrave; lợn c&ograve;n  l&agrave; phương tiện c&oacute; gi&aacute; trị cao để trao đổi h&agrave;ng h&oacute;a, thậm ch&iacute; l&agrave; để đổi  lấy vợ.</p>\n<p class="Normal">Đến thung lũng Baliem, tham gia lễ hội thổ d&acirc;n, d&otilde;i  theo những trận đ&aacute;nh giả, h&ograve;a nhịp ch&acirc;n v&agrave;o những điệu m&uacute;a m&ecirc; mải... tất  cả hoạt động lễ hội như dần được hiển thị qua một &ldquo;m&agrave;n h&igrave;nh lớn&rdquo; về văn  h&oacute;a bản địa độc đ&aacute;o v&agrave; đầy m&agrave;u sắc. Nhiều l&uacute;c t&ocirc;i thấy m&igrave;nh bị ngợp như  đang được sống trong kh&ocirc;ng gian của thời kỳ sơ khai nguy&ecirc;n thủy. Ở đ&oacute;  t&ocirc;i t&igrave;m thấy những gi&aacute; trị ch&acirc;n thực về cuộc sống, về kh&aacute;i niệm hạnh  ph&uacute;c rất đỗi giản dị v&agrave; mộc mạc.</p>\n<p class="Normal">Dẫu biết cuộc sống kh&ocirc;ng ngừng vận động để hướng về  những điều kiện tốt đẹp hơn cho con người, nhưng vẫn mong sao những n&eacute;t  đẹp văn h&oacute;a của lễ hội thổ d&acirc;n v&ugrave;ng thung lũng Baliem sẽ được giữ lại v&agrave;  c&ograve;n m&atilde;i với thời gian. Mong sao những n&eacute;t đẹp trong đời sống văn h&oacute;a  của họ sẽ lu&ocirc;n l&agrave; những trải nghiệm mới mẻ cho ai từng một lần đến, rồi  tr&oacute;t y&ecirc;u mến những con người hồn hậu, y&ecirc;u cuộc sống tựa thủa hồng hoang  v&agrave; y&ecirc;u mảnh đất c&ograve;n nhiều điều b&iacute; ẩn n&agrave;y.</p>\n<table border="1" cellspacing="0" cellpadding="3" width="100%" bgcolor="#ececff">\n<tbody>\n<tr>\n<td>\n<p class="Normal">H&agrave;nh tr&igrave;nh bay tới thung lũng Balierm được biết đến l&agrave;  h&agrave;nh tr&igrave;nh bay xa nhất v&agrave; d&agrave;i nhất trong c&aacute;c nước khu vực Đ&ocirc;ng Nam &Aacute;  với 24 giờ bay li&ecirc;n tục v&agrave; với 5 chặng bay: H&agrave; Nội &ndash; Kulalumpur&ndash; Jakatar  &ndash;Ujung Pandang &ndash; Jayapura v&agrave; Wamena.</p>\n<p class="Normal">Thị trấn Wamena l&agrave; nơi trung chuyển duy nhất bằng  đường h&agrave;ng kh&ocirc;ng để tiếp cận được với thung lũng Baliem. Chi ph&iacute; ở đ&acirc;y  cao gấp 5 lần so với Jakarta bởi tất cả h&agrave;ng h&oacute;a v&agrave;o được thung lũng đều  bằng đường h&agrave;ng kh&ocirc;ng. Chi ph&iacute; tối thiểu để tổ chức một chuyến đi từ 4  đến 5 ng&agrave;y khoảng 1.700 USD.</p>\n<p class="Normal">Từ năm 2010, điều kiện để v&agrave;o thung lũng Baliem v&agrave; tới  thăm c&aacute;c bộ lạc ở đ&acirc;y bắt buộc phải th&ocirc;ng qua một đơn vị tổ chức du  lịch v&agrave; xin giấy ph&eacute;p của cảnh s&aacute;t địa phương.</p>\n<p class="Normal">Lễ hội Baliem được tổ chức v&agrave;o những ng&agrave;y kh&ocirc;ng cố  định trong nửa đầu th&aacute;ng 8. Năm 2011, lễ hội được dự kiến tổ chức v&agrave;o  dịp m&ugrave;ng 8 v&agrave; 9 th&aacute;ng 8.</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Trần Thu Thủy v&agrave; Nguyễn Lưu Triều Dương</strong></p>', 3, 1, 1719, 0, 1),
(35, 'Tôi đi trốn rét ở Thái Lan', 'Với tour tự do đi trốn rét này, được bạn người Thái hướng dẫn, chúng tôi thăm thú mấy đô thị nhỏ lân cận Pattaya, đều trong tỉnh Chonburi, Đông Nam nước Thái. Bạn An Bình chia sẻ.', 'thai1.jpg', NULL, '<h1 class="Title">T&ocirc;i đi trốn r&eacute;t ở Th&aacute;i Lan</h1>\n<h2 class="Lead">Với tour tự do đi trốn r&eacute;t n&agrave;y, được bạn người Th&aacute;i  hướng dẫn, ch&uacute;ng t&ocirc;i thăm th&uacute; mấy đ&ocirc; thị nhỏ l&acirc;n cận Pattaya, đều trong  tỉnh Chonburi, Đ&ocirc;ng Nam nước Th&aacute;i. Bạn An B&igrave;nh chia sẻ.</h2>\n<p class="Normal">Lần đầu đến Th&aacute;i của t&ocirc;i cũng đ&atilde; kh&aacute; l&acirc;u. Hồi đ&oacute;, mới  xuống m&aacute;y bay đ&atilde; thấy ngộp. S&acirc;n bay Đ&ocirc;n Mường qu&aacute; rộng v&agrave; b&acirc;y giờ l&agrave; s&acirc;n  bay Suvarnabhumi c&ograve;n lớn v&agrave; hiện đại hơn nhiều. Từ c&aacute;c khu vực l&agrave;m thủ  tục đến khu vực chuẩn bị l&ecirc;n m&aacute;y bay đi bộ &ldquo;tướt bơ&rdquo;, nhưng kh&ocirc;ng &ldquo;to&aacute;t  mồ h&ocirc;i hột&rdquo;, v&igrave; trong nh&agrave; rất m&aacute;t mặc d&ugrave; ngo&agrave;i trời n&oacute;ng như m&ugrave;a h&egrave; ở  ta.</p>\n<p class="Normal">Đầu ti&ecirc;n l&agrave; quận Chonburi, nghỉ trưa tr&ecirc;n b&atilde;i biển.  H&agrave;ng loạt lều qu&aacute;n san s&aacute;t chạy dọc bờ biển xấp xỉ c&acirc;y số, với c&ugrave;ng một  kiểu bạt che, ghế gỗ sofa. V&eacute; ngồi/nằm nghỉ chỉ 30 baht/người (chưa bằng  b&aacute;t phở b&igrave;nh d&acirc;n ở H&agrave; Nội), c&oacute; thể ngồi cả ng&agrave;y lu&ocirc;n, nếu bạn th&iacute;ch.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="thai1.jpg" alt="Th&aacute;i Lan" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">S&ocirc;ng Chao Phraya chảy qua trung t&acirc;m BKK. Ảnh: <em>An B&igrave;nh.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Pattaya đ&uacute;ng l&agrave; th&agrave;nh phố du lịch. Chao &ocirc;i c&aacute;c qu&aacute;n  bar chật k&iacute;n bởi du kh&aacute;ch, m&agrave; chủ yếu l&agrave; người T&acirc;y. Khu vực  Centrralfestival v&agrave; kh&aacute;ch sạn Hilton s&aacute;t ngay bờ biển c&ograve;n đ&ocirc;ng hơn nữa.  To&agrave; nh&agrave; gửi &ocirc;t&ocirc; k&iacute;n đặc xe n&ecirc;n ch&uacute;ng t&ocirc;i phải l&ecirc;n tầng ch&oacute;t v&oacute;t 11, may  qu&aacute; c&oacute; xe ra. Si&ecirc;u thị thật l&agrave; lộng lẫy, nườm nượp người mua h&agrave;ng hoặc  kết hợp đi chơi hoặc chỉ đi chơi. Ở trung t&acirc;m thương mại n&agrave;y c&oacute; một h&agrave;ng  phở, trương biển &ldquo;Saigon Bistro&rdquo;.</p>\n<p class="Normal">Buổi tối, tr&ecirc;n bờ biển ngay s&aacute;t đ&oacute;, cơ man n&agrave;o l&agrave; c&aacute;c bộ b&agrave;n ghế v&agrave; du kh&aacute;ch ngồi ăn nhậu hoặc chỉ uống.</p>\n<p class="Normal">Ở chợ nổi Pattaya<strong> </strong>t&ocirc;i nh&igrave;n thấy một  c&ocirc; g&aacute;i nhỏ mặt mũi h&iacute; hửng với chiếc gối tr&ecirc;n tay. Đi du lịch m&agrave; mang  gối l&agrave;m g&igrave;? T&eacute; ra đ&oacute; l&agrave; qu&agrave; tr&uacute;ng thưởng tr&ograve; chơi giải tr&iacute; ở chợ. N&oacute; sẽ  được k&ecirc; đầu tr&ecirc;n xe b&aacute;n tải - loại xe được sử dụng rất nhiều ở Th&aacute;i,  thật l&agrave; tiện lợi.</p>\n<p class="Normal">Cả khu chợ nổi nằm tr&ecirc;n mặt hồ, nh&agrave; nổi, cầu nổi, cứ  san s&aacute;t. Người ng&oacute; nghi&ecirc;ng xem h&agrave;ng, người tay x&aacute;ch n&aacute;ch mang, tạo ra  c&aacute;c d&ograve;ng người chen vai s&aacute;t c&aacute;nh c&ugrave;ng đi. C&oacute; h&igrave;nh tượng ch&uacute; g&agrave; trống đặc  trưng rất Th&aacute;i, đang d&otilde;ng dạc ng&acirc;n vang tr&ecirc;n mặt nước. Bạn thử cảm gi&aacute;c  đu người tr&ecirc;n d&acirc;y c&aacute;p chưa. Độ cao, khoảng c&aacute;ch như trong ảnh, b&ecirc;n dưới  l&agrave; m&ecirc;nh m&ocirc;ng nước. Ho&agrave;n to&agrave;n kh&aacute;c ngồi cabin c&aacute;p treo đấy bạn ạ.</p>\n<p class="Normal">Cũng gần Pattaya c&oacute; một trang trại trồng nho &ldquo;nho nhỏ&rdquo;  m&agrave; thu h&uacute;t du kh&aacute;ch nhiều kh&ocirc;ng ngờ. Sản phẩm của trang trại đ&ocirc;ng nghịt  người mua, n&agrave;o l&agrave; nước &eacute;p nho c&oacute; ga, kem vị nho, kem c&oacute; nho kh&ocirc;, b&aacute;nh  ngọt c&oacute; nho&hellip;</p>\n<p class="Normal">Sattahip - một th&agrave;nh phố nhỏ s&aacute;t biển, phong c&aacute;ch  Th&aacute;i, thanh b&igrave;nh, c&aacute;ch Pattaya chỉ khoảng 30 km th&ocirc;i. Rất đ&ocirc;ng du kh&aacute;ch  nước ngo&agrave;i gh&eacute; tới đ&acirc;y.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="thai2.jpg" alt="Khu phố T&agrave;u ở BKK Ảnh: An B&igrave;nh" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Khu phố T&agrave;u ở Bang kok. Ảnh: <em>An B&igrave;nh.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Sớm tinh mơ t&ocirc;i được rủ đi chợ Chong, s&aacute;t ngay cảng  c&aacute;, thật sảng kho&aacute;i. Chợ c&aacute;, chợ rau ngo&agrave;i trời m&agrave; xen kẽ l&agrave; c&aacute;c sạp  h&agrave;ng đơn sơ b&agrave;y b&aacute;n cả quần &aacute;o, mỹ phẩm&hellip; Nhiều thứ c&ograve;n rẻ hơn ở VN.</p>\n<p class="Normal">B&atilde;i tắm Sai Keaw Beach nhỏ, hẹp, đ&ocirc;ng du kh&aacute;ch. Bạn  biết kh&ocirc;ng, địa điểm du lịch n&agrave;y thuộc khu vực qu&acirc;n sự đấy, thế m&agrave; vẫn  được l&agrave;m du lịch.</p>\n<p class="Normal">Chỗ ch&uacute;ng t&ocirc;i nghỉ lại ở Bangkok l&agrave; một v&ugrave;ng ven đ&ocirc;  c&aacute;ch s&acirc;n bay Suvarnabhumi khoảng 12 km. Độ d&agrave;i đường đi n&agrave;y chả l&agrave; g&igrave; so  với loại đ&ocirc; thị cực lớn như BKK. Muốn v&agrave;o trung t&acirc;m th&agrave;nh phố, người  d&acirc;n v&ugrave;ng ven thường chọn c&aacute;ch đi mấy chặng: xe hơi, t&agrave;u điện ngầm, t&agrave;u  điện tr&ecirc;n cao rồi xe bu&yacute;t l&agrave; nhanh v&agrave; rẻ nhất.</p>\n<p class="Normal">Nếu kh&ocirc;ng muốn đi đường bộ, mời bạn đi taxi đường s&ocirc;ng  tr&ecirc;n chiếc t&agrave;u nhỏ, cũng v&agrave;o loại rẻ. M&agrave;u nước s&ocirc;ng Chao Phraya gần  bằng c&aacute;c s&ocirc;ng T&ocirc; Lịch, Kim Ngưu, Nhuệ của H&agrave; Nội vậy m&agrave; người Th&aacute;i vẫn  l&agrave;m được du lịch tr&ecirc;n s&ocirc;ng.</p>\n<p class="Normal">Ch&uacute;ng t&ocirc;i gh&eacute; ch&ugrave;a Mangkon Kamalawat ở khu vực n&agrave;y,  cũng thắp hương, khấn mong Đức Phật ph&ugrave; hộ như rất đ&ocirc;ng người Th&aacute;i đang  l&agrave;m quanh ch&uacute;ng t&ocirc;i.</p>\n<p class="Normal" style="text-align: right;"><strong>An B&igrave;nh</strong></p>\n<div class="fl"><img src="http://vnexpress.net/Images/Point/TopLeft.gif" alt="" /></div>', 3, 1, 1128, 0, 1),
(36, '3 tiêu đề và biểu tượng du lịch Việt Nam đoạt giải cao', 'Tối 26/1, Tổng cục Du lịch đã công bố và trao giải cuộc thi "Tiêu đề - Biểu tượng" cho chương trình xúc tiến du lịch Việt Nam giai đoạn 2011 - 2015.', 'logo6.jpg', NULL, '<div style="overflow: hidden;">\n<h1 class="Title">3 ti&ecirc;u đề v&agrave; biểu tượng du lịch Việt Nam đoạt giải cao</h1>\n<h2 class="Lead">Tối 26/1, Tổng cục Du lịch đ&atilde; c&ocirc;ng bố v&agrave; trao giải cuộc  thi "Ti&ecirc;u đề - Biểu tượng" cho chương tr&igrave;nh x&uacute;c tiến du lịch Việt Nam  giai đoạn 2011 - 2015.</h2>\n<p class="Normal">Ban tổ chức đ&atilde; trao giải nhất cho ti&ecirc;u đề v&agrave; biểu  tượng "Việt Nam - Sự kh&aacute;c biệt &Aacute; Đ&ocirc;ng", trị gi&aacute; 50 triệu đồng; giải nh&igrave;  l&agrave; "Đất nước của những nụ cười" trị gi&aacute; 30 triệu đồng v&agrave; giải ba "Việt  Nam - Niềm tự h&agrave;o ch&acirc;u &Aacute;" với 20 triệu đồng. Ngo&agrave;i ra, Ban tổ chức c&ograve;n  trao 7 giải khuyến kh&iacute;ch cho c&aacute;c t&aacute;c phẩm, trị gi&aacute; 10 triệu đồng.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="logo6.jpg" alt="Logo v&agrave; slogan được giải nhất" width="450" height="200" /></td>\n</tr>\n<tr>\n<td class="Image">Biều tượng v&agrave; ti&ecirc;u đề giải nhất. Ảnh: <em>Đo&agrave;n Loan.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Sau gần 4 th&aacute;ng ph&aacute;t động, Ban tổ chức đ&atilde; nhận được  413 t&aacute;c phẩm của 223 t&aacute;c giả, trong đ&oacute; c&oacute; nhiều t&aacute;c phẩm của c&aacute; c&ocirc;ng ty  chuy&ecirc;n nghiệp trong lĩnh vực tư vấn, x&acirc;y dựng thương hiệu trong nước v&agrave;  quốc tế. Ban gi&aacute;m khảo đ&atilde; chọn được 10 t&aacute;c phẩm v&agrave;o chung kết.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="logo5.jpg" alt="" width="450" height="200" /></td>\n</tr>\n<tr>\n<td class="Image">Biểu tượng v&agrave; ti&ecirc;u đề đạt giải nh&igrave;. Ảnh:<em> Đo&agrave;n Loan.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">&Ocirc;ng Nguyễn Mạnh Cường, Ph&oacute; tổng cục trưởng Tổng cục Du  lịch Việt Nam, cho biết c&aacute;c t&aacute;c phẩm lọt v&agrave;o chung kết đều đạt chất  lượng, c&oacute; nhiều &yacute; tưởng v&agrave; c&oacute; thể ph&aacute;t triển thương hiệu x&uacute;c tiến cho du  lịch Việt Nam.</p>\n<p class="Normal">Giai đoạn 2010-2020, ng&agrave;nh du lịch phấn đấu đạt tốc độ  tăng trưởng kh&aacute;ch quốc tế 10-15% v&agrave; kh&aacute;ch nội địa đạt 15-18% mỗi năm.  Để thu h&uacute;t kh&aacute;ch như mục ti&ecirc;u đề ra, ng&agrave;nh sẽ đẩy mạnh ph&aacute;t triển thương  hiệu v&agrave; chương tr&igrave;nh x&uacute;c tiến du lịch.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="logo4.jpg" alt="" width="450" height="200" /></td>\n</tr>\n<tr>\n<td class="Image">Biều tượng v&agrave; ti&ecirc;u đề giải ba. Ảnh: <em>Đo&agrave;n Loan</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Dự kiến trong qu&yacute; 1 năm nay, ng&agrave;nh du lịch sẽ lựa chọn  ti&ecirc;u đề v&agrave; biểu tượng ch&iacute;nh thức cho chương tr&igrave;nh x&uacute;c tiến giai đoạn  2011-2015.</p>\n<table border="1" cellspacing="0" cellpadding="3" width="190">\n<tbody>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="0" width="100%">\n<form method="POST"><input type="hidden" name="phpMyAdmin" value="a4c14c12d106f8f87a752ed51b37018f" /></form> \n<tbody>\n<tr style="vertical-align: top;">\n<td style="height: 3px; background: url(../../images/vote/bg-top.gif) repeat-x scroll center top #ffffff;" colspan="2"><img src="http://vnexpress.net/images/vote/left-top.gif" alt="" /></td>\n<td class="txtr" style="height: 3px; background: url(../../images/vote/bg-top.gif) repeat-x scroll center top #ffffff;"><img src="http://vnexpress.net/images/vote/right-top.gif" alt="" /></td>\n</tr>\n<tr style="vertical-align: top;">\n<td style="width: 4px; height: 21px; background: url(../../images/vote/bg-left.gif) repeat-y scroll center top #ffffff;"><br /></td>\n<td style="height: 21px; vertical-align: top; padding-left: 8px; padding-top: 1px; padding-bottom: 2px; background-color: #ffffff;"><label class="link-folder">Thăm d&ograve; &yacute; kiến</label></td>\n<td style="width: 4px; height: 21px; background: url(../../images/vote/bg-right.gif) repeat-y scroll center top #ffffff;"><br /></td>\n</tr>\n<tr>\n<td style="background: url(../../images/vote/bg-left-c.gif) repeat-y scroll center top #ffffff;"><br /></td>\n<td style="background-color: #eeeedd;" valign="top">\n<table border="0" cellspacing="0" cellpadding="0" width="100%" height="100%">\n<tbody>\n<tr>\n<td width="100%" valign="top">\n<table border="0" cellspacing="0" cellpadding="0" width="100%" height="100%">\n<tbody>\n<tr valign="top">\n<td style="padding: 10px 0px 10px 8px; background-color: #eeeedd;"><label style="font: bold 12px arial; color: #4d4d4d;">Bạn th&iacute;ch ti&ecirc;u đề v&agrave; biểu tượng n&agrave;o nhất?</label></td>\n</tr>\n<tr height="100%" valign="top">\n<td style="height: 100%; padding-left: 7px; background-color: #eeeedd;" valign="top"><input name="fsubjectid" type="hidden" value="1000496697" /> <input name="fpageid" type="hidden" value="0" /> <input name="fvoteid" type="hidden" value="349442519" /> <input name="fvotetitle" type="hidden" value="Bạn th&iacute;ch ti&ecirc;u đề v&agrave; biểu tượng n&agrave;o nhất?" /> <input name="fvotefor" type="hidden" /> <input name="faction" type="hidden" value="0" /> <input name="fDescription" type="hidden" /> <input name="fnumitem" type="hidden" value="3" /> \n<table border="0" cellspacing="0" cellpadding="0" width="100%" height="100%">\n<tbody>\n<tr>\n<input name="fT_0" type="hidden" value="Giải nhất" /> <input name="fI_0" type="hidden" value="0" /> <input name="fN_0" type="hidden" value="0" />\n<td width="20px" align="right" valign="top"><input style="font: 12px arial; color: #000000;" name="fC_0" type="checkbox" value="0" /></td>\n<td valign="top"><label style="font: 12px arial; color: #000000; margin-left: 2px; margin-right: 2px;">Giải nhất</label></td>\n</tr>\n<tr>\n<input name="fT_1" type="hidden" value="Giải nh&igrave;" /> <input name="fI_1" type="hidden" value="0" /> <input name="fN_1" type="hidden" value="1" />\n<td width="20px" align="right" valign="top"><input style="font: 12px arial; color: #000000;" name="fC_1" type="checkbox" value="1" /></td>\n<td valign="top"><label style="font: 12px arial; color: #000000; margin-left: 2px; margin-right: 2px;">Giải nh&igrave;</label></td>\n</tr>\n<tr>\n<input name="fT_2" type="hidden" value="Giải ba" /> <input name="fI_2" type="hidden" value="0" /> <input name="fN_2" type="hidden" value="2" />\n<td width="20px" align="right" valign="top"><input style="font: 12px arial; color: #000000;" name="fC_2" type="checkbox" value="2" /></td>\n<td valign="top"><label style="font: 12px arial; color: #000000; margin-left: 2px; margin-right: 2px;">Giải ba</label></td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr height="10px" valign="top">\n<td style="background-color: #eeeedd; text-align: right;" align="center" valign="top"><br /></td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n</tbody>\n</table>\n</td>\n<td style="width: 4px; background: url(../../images/vote/bg-right-c.gif) repeat-y scroll center top #ffffff;"><br /></td>\n</tr>\n<tr>\n<td style="width: 4px; height: 25px; background: url(../../images/vote/bg-left.gif) repeat-y scroll center top #ffffff;"><br /></td>\n<td style="height: 25px; padding: 5px 5px 0px; background-color: #ffffff;"><input style="cursor: pointer;" src="http://vnexpress.net/Images/Vote/submit.gif" type="image" />&nbsp;&nbsp;<input style="cursor: pointer;" src="http://vnexpress.net/Images/Vote/view.gif" type="image" /></td>\n<td style="height: 25px; background: url(../../images/vote/bg-right.gif) repeat-y scroll center top transparent;"><br /></td>\n</tr>\n<tr>\n<td style="width: 4px; height: 5px; background: url(../../images/vote/left-bottom.gif) no-repeat scroll center bottom #ffffff;"><br /></td>\n<td style="height: 5px; background: url(../../images/vote/bg-bottom.gif) repeat-x scroll center bottom #ffffff; font-size: 1px;">&nbsp;</td>\n<td style="width: 4px; height: 5px; background: url(../../images/vote/right-bottom.gif) no-repeat scroll center bottom #ffffff;"><br /></td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Đo&agrave;n Loan</strong></p>\n</div>', 3, 1, 758, 0, 1),
(37, 'Bangkok và Pattaya, gió và những đêm không ngủ', '"Ngày ở Pattaya, tôi được thử cảm giác đi xe nghịch hướng. Ngồi sau xe máy, làn gió làm bay tóc, những ánh đèn pha ngược chiều, ngược cả hướng làm tôi hơi lo sợ, tôi thấy bờ vai che chở lúc này thật là... hữu dụng", bạn Thuc Nga chia sẻ.', 'Cho-Noi-Pattaya1.jpg', NULL, '<h1 class="Title">Bangkok v&agrave; Pattaya, gi&oacute; v&agrave; những đ&ecirc;m kh&ocirc;ng ngủ</h1>\n<h2 class="Lead">"Ng&agrave;y ở Pattaya, t&ocirc;i được thử cảm gi&aacute;c đi xe nghịch  hướng. Ngồi sau xe m&aacute;y, l&agrave;n gi&oacute; l&agrave;m bay t&oacute;c, những &aacute;nh đ&egrave;n pha ngược  chiều, ngược cả hướng l&agrave;m t&ocirc;i hơi lo sợ, t&ocirc;i thấy bờ vai che chở l&uacute;c n&agrave;y  thật l&agrave;... hữu dụng", bạn Thuc Nga chia sẻ.</h2>\n<p class="Normal">T&ocirc;i gặp anh giữa s&acirc;n bay Survanabuhmi một trưa nắng  nhẹ v&agrave; hanh hao gi&oacute;, trong h&agrave;nh tr&igrave;nh Bangkok - Pattaya. Những cử chỉ  th&acirc;n thiện đầu ti&ecirc;n đ&atilde; thể hiện sự mến kh&aacute;ch của con người nơi đ&acirc;y. Một  c&aacute;ch v&ocirc; tư v&agrave; x&atilde; giao, t&ocirc;i đ&aacute;p lại bằng một c&aacute;i &ocirc;m người bạn hướng dẫn  vi&ecirc;n của m&igrave;nh tr&ograve;n v&ograve;ng tay!</p>\n<p class="Normal">Dường như một h&agrave;nh động ấy th&ocirc;i đ&atilde; th&agrave;nh nguồn cảm  hứng cho cả t&ocirc;i v&agrave; anh trong suốt h&agrave;nh tr&igrave;nh. D&ugrave; mệt sau cả ng&agrave;y đưa  đo&agrave;n đi tham quan, anh vẫn nhiệt t&igrave;nh đưa t&ocirc;i đến những nơi m&agrave; anh muốn  giới thiệu với ri&ecirc;ng t&ocirc;i. Ng&agrave;y ở Pattaya, t&ocirc;i được thử cảm gi&aacute;c đi xe  nghịch hướng. Ngồi sau xe m&aacute;y, l&agrave;n gi&oacute; l&agrave;m bay t&oacute;c, những &aacute;nh đ&egrave;n pha  ngược chiều, ngược cả hướng l&agrave;m t&ocirc;i hơi lo sợ, t&ocirc;i thấy bờ vai che chở  l&uacute;c n&agrave;y thật l&agrave;... hữu dụng. Cảm gi&aacute;c được ngồi sau một bờ vai rộng, với  m&ugrave;i hương lạ lẫm nhưng lại rất th&acirc;n quen l&agrave;m t&ocirc;i mơ hồ thấy như m&igrave;nh  đang ở một nơi rất gần gũi v&agrave; quen thuộc, t&ocirc;i thấy l&ograve;ng m&igrave;nh b&igrave;nh y&ecirc;n...  T&ocirc;i thấy Pattaya thật đẹp...</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="Chua-Binh-Minh.jpg" border="1" alt="ch&ugrave;a" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Một ngồi ch&ugrave;a b&ecirc;n bờ s&ocirc;ng. Ảnh: <em>Thuc Nga.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">T&ocirc;i thật may mắn, v&igrave; được đ&iacute;ch th&acirc;n hướng dẫn vi&ecirc;n bản  địa đưa đi ngắm th&agrave;nh phố - những nơi kh&ocirc;ng c&oacute; trong lịch tr&igrave;nh. Anh  bạn dường như muốn giới thiệu cho t&ocirc;i tất cả những g&igrave; anh cho l&agrave; n&ecirc;n  biết đến nhất. Từ những ban c&ocirc;ng đầy hoa t&iacute;m đẹp m&ecirc; hồn của kh&aacute;ch sạn  Mariot, đến những con phố ngang dọc ngập đầy hoa đủ sắc m&agrave;u bao quanh  những khu kh&aacute;ch sạn xinh đẹp, con đường bờ biển r&igrave; r&agrave;o gi&oacute; l&agrave;m t&ocirc;i nhớ  đến biển Việt Nam v&agrave; bớt nhớ nh&agrave;, anh ph&oacute;ng xe đưa t&ocirc;i l&ecirc;n đỉnh ngọn đồi  cao nhất Pattaya để ngắm to&agrave;n cảnh th&agrave;nh phố về đ&ecirc;m, đo&aacute;n xem đo&agrave;n  kh&aacute;ch của m&igrave;nh đang dạo chơi ở đ&acirc;u giữa những &aacute;nh đ&egrave;n lung linh đ&oacute;, hay  dẫn t&ocirc;i lang thang trong chợ nổi Pattaya để ngắm những ng&ocirc;i nh&agrave; gỗ,  những đồ thủ c&ocirc;ng v&agrave; thử những m&oacute;n ăn vặt đặc trưng. T&ocirc;i h&aacute;o hức ăn no  căng bụng những m&oacute;n ăn vặt ấy, rồi cay ch&aacute;y lưỡi bởi một quả ớt xanh b&eacute;  t&iacute; v&agrave; thơm lừng!</p>\n<p class="Normal">Về Bangkok, anh lại c&ugrave;ng t&ocirc;i shopping, v&agrave; đưa t&ocirc;i đi  ngắm cảnh ngoại &ocirc; Bangkok về đ&ecirc;m. Bỏ sau lưng những ồn &agrave;o ban ng&agrave;y, kẹt  xe v&agrave; hối hả, ch&uacute;ng t&ocirc;i cảm nhận sự tĩnh lặng v&agrave; &ecirc;m ả của đ&ecirc;m miền nhiệt  đới. C&ugrave;ng ăn ch&egrave; Th&aacute;i, ăn những m&oacute;n ăn đ&ecirc;m lạ với t&ocirc;i v&agrave; qu&aacute; quen với  anh. V&agrave; c&ugrave;ng cảm nhận sự b&igrave;nh y&ecirc;n trong t&acirc;m hồn... Anh đ&atilde; kể cho t&ocirc;i  nghe về đạo Phật, về triết l&yacute; cuộc sống m&agrave; anh vẫn lu&ocirc;n t&acirc;m niệm, anh kể  về c&aacute;c đời vua Rama, t&ocirc;i nhận thấy ở anh một t&acirc;m hồn nhạy cảm v&agrave; s&acirc;u  sắc, dường như c&aacute;ch gi&aacute;o dục đặc trưng Th&aacute;i Lan từ thời ni&ecirc;n thiếu đ&atilde;  cho anh những suy ngẫm rất ch&acirc;n thực nhưng đầy t&iacute;nh triết l&yacute; về cuộc  sống, về con người. T&ocirc;i học hỏi anh...</p>\n<p class="Normal">Ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; những đ&ecirc;m kh&ocirc;ng ngủ, để chia sẻ những  c&acirc;u chuyện về cuộc sống, những buồn vui, quan niệm, v&agrave; để c&ugrave;ng lang  thang dạo chơi...</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="Cho-Noi-Pattaya1.jpg" border="1" alt="chợ nổi" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Chợ nổi ở Pattaya. Ảnh: <em>Thuc Nga.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Hai th&agrave;nh phố, hai nơi kh&aacute;c nhau. Một nơi gi&oacute; biển r&igrave;  r&agrave;o, quang đ&atilde;ng v&agrave; y&ecirc;n b&igrave;nh, đặc trưng bởi dải bờ biển v&agrave; những dịch vụ  chủ yếu d&agrave;nh cho du kh&aacute;ch nước ngo&agrave;i, với kh&ocirc;ng kh&iacute; tấp nập v&agrave; n&oacute;ng  bỏng, những qu&aacute;n bar v&agrave; cửa h&agrave;ng mở cửa th&acirc;u đ&ecirc;m. Một nơi đặc trưng bởi  những m&aacute;i ch&ugrave;a, những cung điện nguy nga của chốn kinh đ&ocirc;, với nhịp sống  c&ocirc;ng nghiệp hối hả nhộn nhịp, với nạn kẹt xe nổi tiếng thế giới, đ&ocirc;ng  đ&uacute;c v&agrave; s&ocirc;i động. Nhưng t&ocirc;i y&ecirc;u cả hai nơi! V&agrave; tạm biệt Th&aacute;i Lan trong  lưu luyến!</p>\n<p class="Normal">Mới đ&acirc;y, t&ocirc;i lại c&oacute; dịp đến Th&aacute;i Lan. Vẫn h&agrave;nh tr&igrave;nh  quen thuộc đ&oacute;. Đối với t&ocirc;i, mỗi một điểm tr&ecirc;n h&agrave;nh tr&igrave;nh thật sự th&acirc;n  thương v&agrave; quen thuộc. Cũng l&agrave; những đ&ecirc;m trở gi&oacute;! Anh giữ lời hứa như đ&atilde;  hẹn trong lần gặp trước, anh đưa t&ocirc;i đi ăn ở nh&agrave; h&agrave;ng đẹp nhất Pattaya  lung linh &aacute;nh đ&egrave;n soi b&oacute;ng tr&ecirc;n mặt hồ nước, thấp tho&aacute;ng trong v&ograve;m c&acirc;y  l&aacute;, đi ăn lẩu b&ograve; phong vị Trung Hoa ở khu Ratchada - Bangkok. Anh đưa  t&ocirc;i l&ecirc;n Bayoke Sky ăn tối v&agrave; ngắm Bangkok về đ&ecirc;m, nghe gi&oacute; h&ograve;a c&ugrave;ng  tiếng đập của con tim...</p>\n<p class="Normal">Nhớ lắm, Th&aacute;i Lan, t&ocirc;i muốn quay trở lại!</p>\n<p class="Normal" style="text-align: right;"><strong>Thuc Nga</strong></p>', 3, 1, 1655, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(38, 'Ngôi đền cổ Phimai', 'Phimai xưa kia từng được xem là một trong những thành phố quan trọng của người Khmer. Kiến trúc và điêu khắc của đền được xem là cùng thời kỳ với kiến trúc của Bayon, Baphuon và Angkor Wat.', 'den4.jpg', NULL, '<h1 class="Title">Ng&ocirc;i đền cổ Phimai</h1>\n<h2 class="Lead">Phimai xưa kia từng được xem l&agrave; một trong những th&agrave;nh  phố quan trọng của người Khmer. Kiến tr&uacute;c v&agrave; đi&ecirc;u khắc của đền được xem  l&agrave; c&ugrave;ng thời kỳ với kiến tr&uacute;c của Bayon, Baphuon v&agrave; Angkor Wat.</h2>\n<p class="Normal">Sau 5 giờ đi xe bu&yacute;t từ Bangkok, th&agrave;nh phố đầu ti&ecirc;n m&agrave;  đo&agrave;n ch&uacute;ng t&ocirc;i đến l&agrave; Korat, tỉnh cửa ng&otilde; v&agrave;o v&ugrave;ng Isan, Isarn hay  Esarn, được xem như v&ugrave;ng Đ&ocirc;ng Bắc Th&aacute;i Lan, v&ugrave;ng của sự giao thoa nhiều  nền văn h&oacute;a - nơi m&agrave; nền văn h&oacute;a của L&agrave;o, Campuchia v&agrave; Th&aacute;i Lan gặp  nhau.</p>\n<p class="Normal">Do đ&oacute; m&agrave; ng&ocirc;n ngữ ở đ&acirc;y ho&agrave;n to&agrave;n kh&aacute;c biệt. Tiếng L&agrave;o  l&agrave; ng&ocirc;n ngữ được phổ biến nhất b&ecirc;n cạnh tiếng Khmer cũng được sử dụng  rộng r&atilde;i. Nhiều người d&acirc;n trong v&ugrave;ng c&oacute; thể biết hoặc kh&ocirc;ng biết tiếng  Anh nhưng họ c&oacute; thể biết nhiều thứ tiếng địa phương như L&agrave;o, Th&aacute;i hay  Khmer. Thức ăn ở v&ugrave;ng n&agrave;y nặng khẩu vị L&agrave;o. M&oacute;n ăn đặc trưng v&ugrave;ng n&agrave;y l&agrave;  gỏi đủ đủ (tiếng Th&aacute;i gọi l&agrave; Som Tam hay Tam Mak Hong theo tiếng L&agrave;o),  g&agrave; nướng, gạo dẻo v&agrave; lạp Korat giữ vị tr&iacute; đặc biệt tr&ecirc;n tuyến đường từ  Bangkok đến v&ugrave;ng Đ&ocirc;ng Bắc.</p>\n<p class="Normal">Hướng dẫn vi&ecirc;n địa phương c&ograve;n cho ch&uacute;ng t&ocirc;i biết mỗi  khi c&oacute; lễ hội, nhất l&agrave; Tết của Th&aacute;i Lan, Korat v&agrave; con đường ch&iacute;nh v&agrave;o  th&agrave;nh phố, c&aacute;c qu&aacute;n ăn, nh&agrave; h&agrave;ng sẽ tổ chức c&aacute;c hoạt động thu h&uacute;t kh&aacute;ch  bằng việc miễn ph&iacute; hay giảm gi&aacute; đặc biệt đối với m&oacute;n ăn đặc sản ch&iacute;nh  của th&agrave;nh phố n&agrave;y. C&aacute;ch l&agrave;m n&agrave;y d&ugrave;ng để quảng b&aacute; thương hiệu ri&ecirc;ng của  c&aacute;c đơn vị kinh doanh v&agrave; l&agrave;m nổi bật đặc sản ri&ecirc;ng c&oacute; của địa phương.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="den.jpg" alt="" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Đền Phimai c&oacute; kiến tr&uacute;c giống đền Angko của Campuchia. Ảnh: <em>L&acirc;m Văn Sơn.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Sau bữa cơm trưa Th&aacute;i, ch&uacute;ng t&ocirc;i được đưa đến tham  quan c&ocirc;ng vi&ecirc;n lịch sử Phimai, l&agrave; một khu phức hợp di t&iacute;ch gồm nhiều đền  v&agrave; những bức tượng Phật nằm trong một diện t&iacute;ch lớn đang được bảo vệ.  Trong đ&oacute; c&oacute; hai c&ocirc;ng tr&igrave;nh c&oacute; gi&aacute; trị lớn về mặt kiến tr&uacute;c đ&oacute; l&agrave; c&ocirc;ng  vi&ecirc;n lịch sử Phanom Rung v&agrave; Pra Sat Hin Phimai.</p>\n<p class="Normal">C&ocirc;ng vi&ecirc;n lịch sử Phimai tọa lạc tại quận Phimai,  th&agrave;nh phố Korat, thủ phủ tỉnh Nakhon Ratchasima - một tỉnh cửa ng&otilde; của  v&ugrave;ng Đ&ocirc;ng Bắc Th&aacute;i Lan. Korat cũng l&agrave; t&ecirc;n gọi của cao nguy&ecirc;n Korat. Từ  quốc lộ số 2 rẽ phải theo đường 206 qua một c&acirc;y cầu đo&agrave;n ch&uacute;ng t&ocirc;i đến  c&ocirc;ng vi&ecirc;n. Ph&iacute;a ngo&agrave;i c&ocirc;ng vi&ecirc;n l&agrave; bảo t&agrave;ng lịch sử Phimai v&agrave; tiếp b&ecirc;n  trong l&agrave; khu phức hợp nhiều ng&ocirc;i đền linh thi&ecirc;ng.</p>\n<p class="Normal">Thời kỳ Khmer, Phimai từng l&agrave; một th&agrave;nh phố quan  trọng. Trong đ&oacute;, Pra Sat Hin Phimai l&agrave; ng&ocirc;i đền Khmer lớn nhất tại Th&aacute;i  Lan v&agrave; l&agrave; ng&ocirc;i đền c&oacute; kiến tr&uacute;c độc đ&aacute;o nhất trong quần thể Phimai nằm ở  trung t&acirc;m th&agrave;nh phố.</p>\n<p class="Normal">Trước khi đến được ng&ocirc;i đền thờ trung t&acirc;m ch&uacute;ng t&ocirc;i  phải đi qua một c&acirc;y cầu người ta gọi l&agrave; cầu Naga, cầu c&oacute; đầu rắn thần.  Naga được x&acirc;y dựng quay theo hướng Nam. Trước cầu Naga l&agrave; bục trung t&acirc;m  h&igrave;nh chữ thập trước c&oacute; 2 con sư tử.</p>\n<p class="Normal">Đền được x&acirc;y dựng trong suốt thời kỳ vua Jayavaraman  VI trị v&igrave; từ năm 1080 đến năm 1107. Sau đ&oacute; ng&ocirc;i đền lại được tiếp tục  ho&agrave;n thiện bởi vua Jayavarman VII từ năm 1181 đến năm 1219, l&agrave; vị vua c&oacute;  vị tr&iacute; rất quan trọng trong việc củng cố v&agrave; ph&aacute;t triển ng&ocirc;i đền. Nếu  như trước đ&oacute;, ng&ocirc;i đền ảnh hưởng của kiến tr&uacute;c đạo Hindu th&igrave; sau khi vua  Jayavaraman 6 l&ecirc;n ng&ocirc;i, ng&ocirc;i đền lại chịu ảnh hưởng của kiến tr&uacute;c đạo  B&agrave; La M&ocirc;n.</p>\n<p class="Normal">Ng&ocirc;i đền được người d&acirc;n ở đ&acirc;y cho l&agrave; c&oacute; một con đường  b&iacute; mật dẫn đến đền Angkor Wat của Campuchia. Theo giả thuyết của c&aacute;c nh&agrave;  khoa học th&igrave; con đường đ&aacute; trực tiếp đến đền Angkor từ cầu Naga theo  hướng Đ&ocirc;ng Bắc l&agrave; khoảng 225 km. Hầu như con đường n&agrave;y hiện bị bao phủ  bởi rừng c&acirc;y v&agrave; chỉ c&oacute; thể nh&igrave;n thấy được từ tr&ecirc;n kh&ocirc;ng. Phần c&ograve;n lại  của con đường duy nhất hiện nay l&agrave; đường v&agrave;o cổng Phimai. Phần n&agrave;y đ&atilde;  chứng thực sự tồn tại của con đường đ&atilde; c&oacute; từ trước thế kỷ 12-13 từ thời  kỳ vua Jayavarman VII.</p>\n<p class="Normal">Mặc d&ugrave; đền Angkor được x&acirc;y dựng trước Phimai, tuy  nhi&ecirc;n về cơ bản cả hai ng&ocirc;i đền đều kh&ocirc;ng mấy thay đổi. Đền Angkor huy  ho&agrave;ng, đặc sắc v&agrave; bề thế hơn Phimai bởi năm ngọn th&aacute;p lớn như năm ngọn  n&uacute;i lớn vươn l&ecirc;n cao, trong khi c&aacute;c th&aacute;p thờ của đền Phimai sắp xếp một  c&aacute;ch đơn lẻ.</p>\n<p class="Normal">To&agrave;n bộ ng&ocirc;i đền x&acirc;y dựng theo h&igrave;nh vu&ocirc;ng v&agrave; bức tường  th&agrave;nh bao quanh đền c&oacute; chiều rộng l&agrave; 580 m v&agrave; chiều d&agrave;i l&agrave; 1020 m. Về  mặt địa l&yacute;, người d&acirc;n địa phương cho rằng n&oacute; đ&atilde; được x&acirc;y dựng tr&ecirc;n một  ngọn đồi cao v&agrave; được bao bọc bởi 3 con k&ecirc;nh đ&agrave;o.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="den4.jpg" alt="" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Những h&igrave;nh tượng tại đền Phimai. Ảnh: <em>L&acirc;m Văn Sơn.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Phimai xưa kia từng được xem l&agrave; một trong những th&agrave;nh  phố quan trọng của người Khmer. Kiến tr&uacute;c v&agrave; đi&ecirc;u khắc của đền được xem  l&agrave; c&ugrave;ng thời kỳ với kiến tr&uacute;c của Bayon, Baphuon v&agrave; Angkor Wat. Chất  liệu x&acirc;y dựng chủ yếu l&agrave; sa thạch, đ&aacute; ong v&agrave; l&agrave; sự kết hợp của đ&aacute; sa  thạch đỏ c&ugrave;ng với c&aacute;t trộn tạo th&agrave;nh chất kết d&iacute;nh vững chắc.</p>\n<p class="Normal">Hiện, ng&ocirc;i đền bị b&agrave;o m&ograve;n đến tận phần đ&aacute; b&ecirc;n trong  n&ecirc;n v&aacute;ch đ&aacute; ở đ&acirc;y nhiều chỗ đ&atilde; để lộ những m&agrave;u sắc đỏ ch&oacute;i của sa thạch  đỏ. Bộ phận bảo vệ di sản ở tỉnh n&agrave;y đ&atilde; cố tr&ugrave;ng tu kh&ocirc;i phục lại đền  bằng những khung nẹp sắt v&agrave; c&aacute;c vữa hồ xi măng.</p>\n<p class="Normal">Gần ng&ocirc;i đền c&ograve;n c&oacute; bảo t&agrave;ng quốc gia Phimai nơi lưu  giữ những cổ vật thời kỳ Angkor v&agrave; đặc biệt hơn nữa l&agrave; c&aacute;ch c&ocirc;ng vi&ecirc;n  quốc gia Phimai khoảng 1,5 km c&oacute; c&acirc;y Sai Ngam l&agrave; giống c&acirc;y bany khổng  lồ. C&acirc;y Sai Ngam thật ra chỉ l&agrave; một c&acirc;y duy nhất nhưng nếu nh&igrave;n từ xa  c&acirc;y Sai Ngam tr&ocirc;ng giống như một rừng c&acirc;y lớn. Người địa phương c&ograve;n cho  biết c&oacute; thể n&oacute;i Sai Ngam l&agrave; c&acirc;y lớn nhất Đ&ocirc;ng Nam &Aacute;, c&oacute; tuổi thọ tr&ecirc;n  350 năm v&agrave; người Th&aacute;i xem c&acirc;y n&agrave;y như l&agrave; &ldquo;c&acirc;y thần&rdquo;.</p>\n<p class="Normal">C&oacute; cơ hội đến tham quan được Phimai ở Nakhon  Ratchashima Th&aacute;i Lan, Watphou ở Nam L&agrave;o v&agrave; Angkor ở Campuchia, t&ocirc;i mới  nhận ra về sự to lớn v&agrave; bao tr&ugrave;m của thời kỳ đế chế Angkor h&ugrave;ng vĩ.</p>\n<p class="Normal" style="text-align: right;"><strong>L&acirc;m Văn Sơn</strong></p>', 3, 1, 562, 0, 1),
(45, 'Du lịch kết hợp mua sắm Tết tại Trung Quốc', 'Hai tour du lịch kết hợp mua sắm Tết tại Trung Quốc là Nam Ninh - Quảng Châu và Thượng Hải - Hàng Châu - Tô Châu phục vụ du khách có nhu cầu mua sắm trong mùa siêu giảm giá trước Tết Nguyên đán.', 'Hang-Chau--Trung-Quoc.jpg', NULL, '<h1 class="Title">Du lịch kết hợp mua sắm Tết tại Trung Quốc</h1>\n<h2 class="Lead">Hai tour du lịch kết hợp mua sắm Tết tại Trung Quốc l&agrave; Nam Ninh - Quảng Ch&acirc;u v&agrave; Thượng Hải - H&agrave;ng Ch&acirc;u - T&ocirc; Ch&acirc;u phục vụ du kh&aacute;ch c&oacute; nhu cầu mua sắm trong m&ugrave;a si&ecirc;u giảm gi&aacute; trước Tết Nguy&ecirc;n đ&aacute;n.</h2>\n<p class="Normal">Trong đ&oacute;, tour Nam Ninh - Quảng Ch&acirc;u (5 ng&agrave;y 4 đ&ecirc;m, gi&aacute; trọn g&oacute;i 229 USD một kh&aacute;ch) sẽ mang đến cơ hội lựa chọn nhiều mặt h&agrave;ng ưa th&iacute;ch với gi&aacute; hấp dẫn. Đặc biệt, khi mua sắm tại Quảng Ch&acirc;u, du kh&aacute;ch c&oacute; thể t&igrave;m thấy nhiều loại h&agrave;ng thời trang Ph&aacute;p, Anh, Italy, &ocirc;t&ocirc; Mỹ, đồ điện tử Nhật s&acirc;m Cao Ly, gấm Thượng Hải... cao cấp, nổi tiếng thế giới hoặc loại đồ b&igrave;nh d&acirc;n, gi&aacute; rẻ.</p>\n<p class="Normal">Đến Quảng Ch&acirc;u, du kh&aacute;ch c&ograve;n được thưởng ngoạn những thắng cảnh đẹp nổi tiếng như: ch&ugrave;a Lục Dung. Bạn cũng c&oacute; thể shopping tại trung t&acirc;m thương mại Thi&ecirc;n H&agrave; lớn nhất Quảng Ch&acirc;u, phố mua sắm Bắc Kinh lộ, trung t&acirc;m thương mại h&agrave;ng may mặc quần &aacute;o gi&aacute; rẻ sầm uất nhất Quảng Ch&acirc;u - Thi&ecirc;n M&atilde; v&agrave; Bạch M&atilde;, trung t&acirc;m h&agrave;ng điện tử, điện m&aacute;y&hellip; Quảng Ch&acirc;u c&ograve;n l&agrave; nơi c&oacute; thuốc Trung y tốt của Trung Quốc. Nơi đ&acirc;y c&oacute; trường đại học Trung y. Nếu lo lắng hay quan t&acirc;m tới t&igrave;nh trạng sức khỏe, bạn c&oacute; thể tới bắt mạch v&agrave; tư vấn sức khỏe miễn ph&iacute; tại hiệu thuốc Bảo Thụ Đường. Tại Nam Ninh - địa danh được v&iacute; l&agrave; &ldquo;Nơi c&oacute; m&ugrave;a đ&ocirc;ng kh&ocirc;ng kh&ocirc; h&eacute;o, hoa kh&ocirc;ng phải m&ugrave;a xu&acirc;n cũng nở rộ&rdquo;. Kh&aacute;ch du lịch cũng sẽ được thăm c&ocirc;ng vi&ecirc;n Thanh T&uacute; Sơn, quảng trường D&acirc;n tộc, khu triển l&atilde;m quốc tế Trung Quốc ASEAN, c&ocirc;ng vi&ecirc;n Nam Hồ, cửa h&agrave;ng thuốc bỏng nổi tiếng Trung Quốc...</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="Hang-Chau--Trung-Quoc.jpg" border="1" alt="" width="450" height="338" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">B&ecirc;n cạnh tour Nam Ninh - Quảng Ch&acirc;u, tour Thượng Hải - H&agrave;ng Ch&acirc;u - T&ocirc; Ch&acirc;u (4 ng&agrave;y 4 đ&ecirc;m, gi&aacute; trọn g&oacute;i 499 USD một kh&aacute;ch) sẽ gi&uacute;p kh&aacute;ch h&agrave;ng s&agrave;nh điệu h&agrave;nh l&ograve;ng. Thượng Hải được xem l&agrave; c&aacute;i n&ocirc;i của thời trang với những thương hiệu nổi tiếng thế giới như Chanel, Cartier, Prada, Gucci, Hugo Boss, Tod&rsquo;s, Omega, Rado, Longines, Pierre Cardin, Giorgio Armani, Louis Vuitton&hellip; Mua sắm tại đ&acirc;y rẻ hơn so với ch&acirc;u &Acirc;u.</p>\n<p class="Normal">Đến thăm th&agrave;nh phố của b&ecirc;n kia s&ocirc;ng Ho&agrave;ng Phố, du kh&aacute;ch c&ograve;n c&oacute; thể ngắm c&aacute;c danh lam thắng cảnh nổi tiếng: H&agrave;ng Ch&acirc;u, T&ocirc; Ch&acirc;u, ch&ugrave;a Ngọc Phật, miếu Th&agrave;nh Ho&agrave;ng, cửa h&agrave;ng ngọc trai Th&aacute;i Hồ, Khu Phố Đ&ocirc;ng, ng&ocirc;i ch&ugrave;a cổ nổi tiếng Trung Quốc- H&agrave;n Sơn Tự... Nếu H&agrave;ng Ch&acirc;u nổi tiếng l&agrave; một trong bảy kinh đ&ocirc; cổ của Trung Hoa cổ th&igrave; T&ocirc; Ch&acirc;u lại nổi tiếng v&igrave; ng&agrave;nh dệt lụa tơ tằm. Nơi đ&acirc;y sở hữu hơn 100 vườn hoa c&acirc;y cảnh xanh tươi, những con k&ecirc;nh lượn lờ, b&ecirc;n tr&ecirc;n l&agrave; cầu đ&aacute; h&igrave;nh v&ograve;ng cung v&agrave; những căn nh&agrave; cổ vẫn vững v&agrave;ng trước mưa gi&oacute;, thời gian. H&agrave;ng Ch&acirc;u, T&ocirc; Ch&acirc;u l&agrave; hai trong số những danh lam đẹp nhất Trung Hoa.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="shanghai---harbor.jpg" border="1" alt="" width="450" height="324" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Nh&acirc;n dịp năm mới, C&ocirc;ng ty Du lịch v&agrave; Thể thao Việt Nam, Vietran Tour sẽ triển khai ph&aacute;t h&agrave;nh h&agrave;ng ngh&igrave;n thẻ mua h&agrave;ng v&agrave; thẻ tặng qu&agrave; tới kh&aacute;ch h&agrave;ng, &aacute;p dụng cho c&aacute;c đo&agrave;n kh&aacute;ch đăng k&yacute; đi tour c&oacute; ng&agrave;y khởi h&agrave;nh từ 1/1 đến hết 31/3/2011.</p>\n<p class="Normal">Theo đ&oacute;, kh&aacute;ch đăng k&yacute; theo đo&agrave;n ri&ecirc;ng đi du lịch trong nước, c&aacute;c nước trong khu vực Đ&ocirc;ng Nam &Aacute; v&agrave; tour tới Trung Quốc (bằng đường bộ) sẽ được tặng ngay một thẻ mua h&agrave;ng hoặc tiền mặt trị gi&aacute; 500.000 đồng cho mỗi đo&agrave;n kh&aacute;ch. Đối với c&aacute;c tour ch&acirc;u &Acirc;u v&agrave; c&aacute;c điểm du lịch kh&aacute;c, đo&agrave;n sẽ được tặng thẻ mua h&agrave;ng hoặc tiền mặt với trị gi&aacute; 1.000.000 đồng. Ngo&agrave;i ra, mỗi du kh&aacute;ch trong đo&agrave;n c&ograve;n được tặng một m&oacute;n qu&agrave; &yacute; nghĩa trước chuyến đi.</p>\n<p class="Normal">Để biết th&ecirc;m th&ocirc;ng tin chi tiết về c&aacute;c tour du lịch mua sắm Tết v&agrave; chương tr&igrave;nh khuyến m&atilde;i của Vietran Tour, kh&aacute;ch h&agrave;ng li&ecirc;n hệ Vietran Tour</p>\n<p class="Normal">Địa chỉ: 49 Quang Trung - H&agrave; Nội<br />Điện thoại: 04 3943 8777<br />Hotline: 0943121318&nbsp;<br />Web:&nbsp;<a class="Normal" href="http://www.vietrantour.com.vn/">www.vietrantour.com.vn</a></p>\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Vietrantour</em>)</p>', 3, 1, 925, 0, 1),
(46, 'Ngăn chặn hiện tượng tiêu cực mùa lễ hội', 'Bộ Văn hóa Thể thao và Du lịch vừa chỉ đạo các khách sạn, cơ sở lưu trú du lịch đảm bảo an toàn vệ sinh thực phẩm, không tăng giá, ép giá đối với du khách dịp tết Tân Mão.', 'a4.jpg', NULL, '<h1 class="Title">Ngăn chặn hiện tượng ti&ecirc;u cực m&ugrave;a lễ hội</h1>\n<h2 class="Lead">Bộ Văn h&oacute;a Thể thao v&agrave; Du lịch vừa chỉ đạo c&aacute;c kh&aacute;ch sạn, cơ sở lưu tr&uacute; du lịch đảm bảo an to&agrave;n vệ sinh thực phẩm, kh&ocirc;ng tăng gi&aacute;, &eacute;p gi&aacute; đối với du kh&aacute;ch dịp tết T&acirc;n M&atilde;o.</h2>\n<p class="Normal">Với c&ocirc;ng t&aacute;c lễ hội, Bộ Văn h&oacute;a Thể thao v&agrave; Du lịch chỉ đạo c&aacute;c cơ quan trực thuộc tăng cường thanh kiểm tra ngăn chặn c&aacute;c biểu hiện ti&ecirc;u cực, kh&ocirc;ng l&agrave;nh mạnh trong dịp Tết v&agrave; m&ugrave;a lễ hội. Tại c&aacute;c di t&iacute;ch, nơi thờ tự phải c&oacute; quy định việc đặt h&ograve;m c&ocirc;ng đức, kh&ocirc;ng để xảy ra t&igrave;nh trạng để tiền c&ocirc;ng đức, tiền cầu lộc g&acirc;y phản cảm. C&aacute;c h&agrave;ng qu&aacute;n, b&atilde;i để xe tại khu du lịch v&agrave; khu vực lễ hội phải được quản l&yacute;.</p>\n<p class="Normal">Ngo&agrave;i ra, c&aacute;c đơn vị trực thuộc cần chủ động trao đổi th&ocirc;ng tin để c&aacute;c lễ hội tr&ecirc;n địa b&agrave;n ph&ugrave; hợp với truyền thống, tổ chức khoa học về thời gian, giảm tải người tham gia tại một khu vực trong một thời gian nhất định; thực hiện nghi&ecirc;m chỉ đạo của Thủ tướng về cấm sản xuất, bu&ocirc;n b&aacute;n, đốt ph&aacute;o nổ v&agrave; thả đ&egrave;n trời.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="a4.jpg" alt="" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Người d&acirc;n thường đổ đi lễ ch&ugrave;a dịp năm mới. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Bộ Văn h&oacute;a cũng y&ecirc;u cầu c&aacute;c địa phương tăng cường hoạt động văn ho&aacute;, thể thao v&agrave; du lịch, biểu diễn nghệ thuật phục vụ nh&acirc;n d&acirc;n trước, trong v&agrave; sau Tết, ưu ti&ecirc;n biểu diễn phục vụ đồng b&agrave;o, chiến sĩ v&ugrave;ng bi&ecirc;n giới, hải đảo, n&ocirc;ng th&ocirc;n v&ugrave;ng xa.</p>\n<p class="Normal">Đối với việc tổ chức ch&uacute;c Tết trong ng&agrave;nh, Bộ trưởng Văn h&oacute;a thể thao v&agrave; Du lịch chỉ đạo c&aacute;c đơn vị ch&uacute;c Tết c&aacute;n bộ, c&ocirc;ng chức, vi&ecirc;n chức v&agrave; người lao động tại nơi l&agrave;m việc v&agrave; thăm hỏi c&aacute;n bộ l&atilde;o th&agrave;nh, c&aacute;n bộ đ&atilde; nghỉ hưu; kh&ocirc;ng tổ chức đi ch&uacute;c Tết l&atilde;nh đạo c&aacute;c cấp; nghi&ecirc;m cấm sử dụng tiền ng&acirc;n s&aacute;ch nh&agrave; nước để li&ecirc;n hoan, chi&ecirc;u đ&atilde;i, thưởng, biếu; kh&ocirc;ng d&ugrave;ng rượu ngoại để chi&ecirc;u đ&atilde;i, tiếp kh&aacute;ch.</p>\n<p class="Normal" style="text-align: right;"><strong>Đo&agrave;n Loan</strong></p>', 3, 1, 843, 0, 1),
(48, 'Noel và năm mới tại Vinpearl Resort & Spa', 'Ngày 24, 25/12, khách du lịch ở tại Vinpearl Resort & Spa sẽ được tham gia các hoạt động vui chơi ngoài trời vui nhộn. Vào ngày 31/12, tiệc đón năm mới sẽ được tổ chức trong không gian ấm cúng với nến, rượu vang, chocolate.', '8.jpg', NULL, '<h1 class="Title">Noel v&agrave; năm mới tại Vinpearl Resort &amp; Spa</h1>\n<h2 class="Lead">Ng&agrave;y 24, 25/12, kh&aacute;ch du lịch ở tại Vinpearl Resort &amp; Spa sẽ được tham gia c&aacute;c hoạt động vui chơi ngo&agrave;i trời vui nhộn. V&agrave;o ng&agrave;y 31/12, tiệc đ&oacute;n năm mới sẽ được tổ chức trong kh&ocirc;ng gian ấm c&uacute;ng với nến, rượu vang, chocolate.</h2>\n<p class="Normal">Du kh&aacute;ch chọn Vinpearl Nha Trang l&agrave;m điểm đến trong m&ugrave;a Gi&aacute;ng sinh v&agrave; năm mới chỉ phải trả th&ecirc;m từ 100 USD để tham dự chương tr&igrave;nh tiệc được thiết kế d&agrave;nh ri&ecirc;ng cho m&ugrave;a lễ hội.</p>\n<p class="Normal">Người lớn sẽ c&oacute; nhiều game vận động được tổ chức tại bể bơi trong kh&ocirc;ng kh&iacute; s&ocirc;i động bởi sự cộng hưởng của &acirc;m thanh, hoạt n&aacute;o vi&ecirc;n v&agrave; c&ugrave;ng tham gia v&agrave;o cuộc thi cải trang &ocirc;ng gi&agrave; Noel. C&ograve;n c&aacute;c b&eacute; sẽ được tham gia nhiều game mini với &ocirc;ng gi&agrave; Noel như t&ocirc; tượng, vẽ tranh, thi dẫn b&oacute;ng, thi chuyển qu&agrave; c&ugrave;ng &ocirc;ng gi&agrave; Noel v&agrave; sẽ nhận được c&aacute;c phần thưởng ngộ nghĩnh. Điểm nhấn trong c&aacute;c hoạt động đ&oacute;n Noel ch&iacute;nh l&agrave; bữa tiệc đ&ecirc;m Gi&aacute;ng sinh tr&agrave;n ngập kh&ocirc;ng kh&iacute; từ c&aacute;ch trang tr&iacute;, b&agrave;i tr&iacute; m&oacute;n ăn, &acirc;m nhạc&hellip; Tiệc cocktail được phục vụ trước bữa tối ch&iacute;nh, tiệc buffet với c&aacute;c m&oacute;n ăn mang hương vị đặc trưng Noel với đồ uống trong tiệc ch&iacute;nh miễn ph&iacute;.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="8.jpg" border="1" alt="Gi&aacute;ng sinh tr&ecirc;n biển." width="400" height="267" /></td>\n</tr>\n<tr>\n<td class="Image">Gi&aacute;ng sinh tại Vinpearl Nha Trang .</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Tiệc đ&oacute;n năm mới tại Vinpearl Nha Trang Resort &amp; Spa được tổ chức từ 17h ng&agrave;y 31/12 trong kh&ocirc;ng gian ấm c&uacute;ng với ph&aacute;o b&ocirc;ng, nến, rượu vang v&agrave; chocolate. Kh&aacute;ch h&agrave;ng sẽ được tham dự "Gala dinner new year" với tiệc buffet c&oacute; hơn 60 m&oacute;n ăn hương vị bốn phương c&ugrave;ng đồ uống miễn ph&iacute;, thưởng thức m&agrave;n tr&igrave;nh diễn của ban nhạc, c&ugrave;ng tham gia c&aacute;c tr&ograve; chơi vui nhộn, c&ugrave;ng đếm ngược đ&oacute;n ch&agrave;o năm mới v&agrave; khi&ecirc;u vũ trước khi kết th&uacute;c bữa tiệc. Về ph&ograve;ng, mỗi kh&aacute;ch sẽ nhận được một m&oacute;n qu&agrave; ch&uacute;c mừng năm mới &yacute; nghĩa. Sau đ&oacute;, trong ng&agrave;y đầu ti&ecirc;n của năm mới 2010 c&aacute;c em nhỏ sẽ được tiếp tục tham gia một số hoạt động vui chơi tại khu Kid Club trong buổi s&aacute;ng.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="3.jpg" border="1" alt="" width="400" height="269" /></td>\n</tr>\n<tr>\n<td class="Image">"Gala dinner new year" với tiệc buffet c&oacute; hơn 60 m&oacute;n ăn hương vị bốn phương.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Ngo&agrave;i ra nh&acirc;n dịp n&agrave;y, c&aacute;c cửa h&agrave;ng thời trang, đồ lưu niệm tại Vinpearl &hellip; đều đang &aacute;p dụng chương tr&igrave;nh &ldquo;crazy sales&rdquo; để &ldquo;mừng tuổi&rdquo; du kh&aacute;ch với c&aacute;c mức từ 10 đến 70% t&ugrave;y mặt h&agrave;ng.</p>\n<p class="Normal">Vinpearl Nha Trang tọa lạc tr&ecirc;n đảo H&ograve;n Tre, l&agrave; khu li&ecirc;n hợp du lịch giải tr&iacute; biển đảo ti&ecirc;u chuẩn 5 sao, đẳng cấp quốc tế nhưng vẫn g&igrave;n giữ vẻ đẹp hoang sơ. Tại đ&acirc;y, c&ograve;n c&oacute; b&atilde;i tắm biển tự nhi&ecirc;n d&agrave;i 700 m, được xem l&agrave; đẹp nhất Nha Trang, khu kh&aacute;ch sạn c&oacute; kiến tr&uacute;c đẹp với 485 ph&ograve;ng nghỉ, trong đ&oacute; c&oacute; 17 ph&ograve;ng hạng sang, 2 ph&ograve;ng tổng thống v&agrave; nhiều dịch vụ giải tr&iacute;, chăm s&oacute;c sức khỏe.</p>\n<p class="Normal">Xem th&ecirc;m h&igrave;nh ảnh lễ hội của Vinpearl Nha Trang&nbsp;<a class="Normal" href="http://vnexpress.net/GL/Xa-hoi/Du-lich/2010/12/3BA247E4/page_1.asp">tại đ&acirc;y</a>.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="472" align="center" bgcolor="#dfdfdf">\n<tbody>\n<tr>\n<td>C&ocirc;ng ty cổ phần Vinpearl &aacute;p dụng chương tr&igrave;nh &ldquo;Gift Voucher&rdquo;<strong>&nbsp;</strong>(thẻ qu&agrave; tặng) với 2 mệnh gi&aacute;: Deluxe Gift Voucher gi&aacute; 399 USD v&agrave; VIP Gift Voucher gi&aacute; 490 USD. Ngo&agrave;i việc đăng k&yacute; thụ hưởng trực tiếp những ưu đ&atilde;i tại Vinpearl Nha Trang Resort v&agrave; Spa, kh&aacute;ch c&ograve;n c&oacute; thể sử dụng &ldquo;Gift Voucher&rdquo; như m&oacute;n qu&agrave; d&agrave;nh tặng bạn b&egrave;, người th&acirc;n. Sử dụng một trong hai loại thẻ đặc biệt tr&ecirc;n, kh&aacute;ch h&agrave;ng sẽ được nhận những m&oacute;n qu&agrave; lưu niệm từ Vinpearl. Đặc biệt, kh&aacute;ch sử dụng Voucher được ưu ti&ecirc;n khi đặt ph&ograve;ng v&agrave; đợi đầu ti&ecirc;n khi kh&ocirc;ng c&ograve;n ph&ograve;ng trống.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>C&ocirc;ng ty cổ phần Vinpearl</em>)</p>', 3, 1, 745, 0, 1),
(50, 'Ana Mandara Villas Dalat lưu giữ kiến trúc Pháp cổ', 'Khu nghỉ Ana Mandara Villas Dalat được trùng tu nguyên vẹn kiến trúc cổ biệt thự Pháp, đan xen trang trí vật dụng xưa làm toát lên vẻ quyền uy của giới thượng lưu.', 'du-lich1 (1).jpg', NULL, '<h1 class="Title">Ana Mandara Villas Dalat lưu giữ kiến tr&uacute;c Ph&aacute;p cổ</h1>\n<h2 class="Lead">Khu nghỉ Ana Mandara Villas Dalat được tr&ugrave;ng tu nguy&ecirc;n vẹn kiến tr&uacute;c cổ biệt thự Ph&aacute;p, đan xen trang tr&iacute; vật dụng xưa l&agrave;m to&aacute;t l&ecirc;n vẻ quyền uy của giới thượng lưu.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich1.jpg" border="1" alt="Kiến tr&uacute;c cổ của biệt thự Ph&aacute;p lu&ocirc;n hấp dẫn c&aacute;c du kh&aacute;ch trong v&agrave; ngo&agrave;i nước." width="480" height="322" /></td>\n</tr>\n<tr>\n<td class="Image">Kiến tr&uacute;c cổ của biệt thự Ph&aacute;p lu&ocirc;n hấp dẫn c&aacute;c du kh&aacute;ch trong v&agrave; ngo&agrave;i nước.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich2.jpg" border="1" alt="Nh&agrave; h&agrave;ng Le Petit Dalat mang phong c&aacute;ch ch&acirc;u &Acirc;u." width="480" height="360" /></td>\n</tr>\n<tr>\n<td class="Image">Nh&agrave; h&agrave;ng Le Petit Dalat mang phong c&aacute;ch ch&acirc;u &Acirc;u.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich3.jpg" border="1" alt="Khung cảnh l&atilde;ng mạn." width="480" height="322" /></td>\n</tr>\n<tr>\n<td class="Image">Khung cảnh l&atilde;ng mạn.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich4.jpg" border="1" alt="Kiến tr&uacute;c từ cổng đến to&agrave;n bộ villa được phục hồi nguy&ecirc;n vẹn." width="480" height="322" /></td>\n</tr>\n<tr>\n<td class="Image">Kiến tr&uacute;c từ cổng đến to&agrave;n bộ villa được phục hồi nguy&ecirc;n vẹn.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich5.jpg" border="1" alt="N&eacute;t đẹp quyến rũ l&agrave; sự kết hợp h&agrave;i ho&agrave; giữa cảnh quan v&agrave; sự cổ k&iacute;nh." width="480" height="322" /></td>\n</tr>\n<tr>\n<td class="Image">N&eacute;t đẹp quyến rũ l&agrave; sự kết hợp h&agrave;i ho&agrave; giữa cảnh quan v&agrave; sự cổ k&iacute;nh.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich6.jpg" border="1" alt="Ph&ograve;ng kh&aacute;ch của Villa 3 cho du kh&aacute;ch cảm gi&aacute;c sống với phong th&aacute;i tầng lớp thượng lưu xa xưa." width="480" height="324" /></td>\n</tr>\n<tr>\n<td class="Image">Ph&ograve;ng kh&aacute;ch của Villa 3 cho du kh&aacute;ch cảm gi&aacute;c sống với phong th&aacute;i tầng lớp thượng lưu xa xưa.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich7.jpg" border="1" alt="Tr&agrave; chiều mang phong c&aacute;ch ch&acirc;u &Acirc;u với c&aacute;c loại b&aacute;nh." width="400" height="533" /></td>\n</tr>\n<tr>\n<td class="Image">Tr&agrave; chiều mang phong c&aacute;ch ch&acirc;u &Acirc;u với c&aacute;c loại b&aacute;nh.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich9.jpg" border="1" alt="Bữa tối l&atilde;ng mạn tại hầm rượu." width="480" height="322" /></td>\n</tr>\n<tr>\n<td class="Image">Bữa tối l&atilde;ng mạn.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="du-lich11.jpg" border="1" alt="N&eacute;t hiện đại của khu biệt thự cổ được t&ocirc; điểm bởi hồ bơi nước n&oacute;ng ngo&agrave;i trời." width="480" height="360" /></td>\n</tr>\n<tr>\n<td class="Image">N&eacute;t hiện đại của khu biệt thự cổ được t&ocirc; điểm bởi hồ bơi nước n&oacute;ng ngo&agrave;i trời.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;">(Nguồn:<em>&nbsp;Ninh Van Bay Holiday Club</em>)</p>', 3, 1, 904, 0, 1),
(53, 'Lên mái nhà của châu Âu', 'Cao 3.454 m, ngọn núi Jungfrau cao nhất thuộc dãy Alps (Thụy Sĩ) quanh năm tuyết phủ. Du khách như lạc trong thế giới truyện cổ tích với những ngôi nhà thơ mộng phủ tuyết trắng xóa bên sườn núi, trong cảnh thiên nhiên hùng vĩ. VnExpress.net ghi nhận một số hình ảnh trên mái nhà châu Âu.', '14.jpg', NULL, '<h1 class="Title">L&ecirc;n m&aacute;i nh&agrave; của ch&acirc;u &Acirc;u</h1>\n<h2 class="Lead">Cao 3.454 m, ngọn n&uacute;i Jungfrau cao nhất thuộc d&atilde;y Alps (Thụy Sĩ) quanh năm tuyết phủ. Du kh&aacute;ch như lạc trong thế giới truyện cổ t&iacute;ch với những ng&ocirc;i nh&agrave; thơ mộng phủ tuyết trắng x&oacute;a b&ecirc;n sườn n&uacute;i, trong cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ.&nbsp;<em>VnExpress.net</em>&nbsp;ghi nhận một số h&igrave;nh ảnh tr&ecirc;n m&aacute;i nh&agrave; ch&acirc;u &Acirc;u.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\n<tbody>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="18.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Từ thị trấn dưới thung lũng mang t&ecirc;n Interlaken, du kh&aacute;ch đi l&ecirc;n đỉnh n&uacute;i Jungfrau qua hệ thống t&agrave;u b&aacute;nh x&iacute;ch. Chuyến t&agrave;u đặc biệt n&agrave;y đi l&ecirc;n nh&agrave; ga Jungfrau ở độ cao hơn 3.000 m, l&agrave; nh&agrave; ga cao nhất ch&acirc;u &Acirc;u.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="23.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Một nh&agrave; ga tr&ecirc;n đường l&ecirc;n n&uacute;i phủ tuyết trắng. Mỗi ng&agrave;y, c&aacute;c chuyến t&agrave;u đ&oacute;n khoảng 5.000 du kh&aacute;ch l&ecirc;n đỉnh n&uacute;i.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="21.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Những ng&ocirc;i nh&agrave; &ecirc;m đềm b&ecirc;n sườn n&uacute;i.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="19.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">B&ecirc;n những h&agrave;ng th&ocirc;ng trắng x&oacute;a.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="22.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Cảnh thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ v&agrave; thơ mộng.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="14.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">L&ecirc;n cao, m&acirc;y trắng bao phủ quanh những ngọn n&uacute;i.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="13.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Tuyết phủ d&agrave;y.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="11.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Ngọn n&uacute;i Jungfrau cao nhất ch&acirc;u &Acirc;u thuộc d&atilde;y Alps.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="10.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Du kh&aacute;ch b&ecirc;n cộc mốc tr&ecirc;n n&uacute;i tuyết.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="9.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">L&aacute; cờ Thụy Sĩ tung bay trong nắng.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="8.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Thời tiết -7 độ C kh&ocirc;ng l&agrave;m giảm nhiệt t&igrave;nh của du kh&aacute;ch miền nhiệt đới tr&ecirc;n n&uacute;i tuyết.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="12.jpg" alt="" width="495" height="327" /></td>\n</tr>\n<tr>\n<td class="Image">Vui th&iacute;ch v&ugrave;i m&igrave;nh trong tuyết.</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Đo&agrave;n Loan</strong></p>', 3, 1, 1765, 0, 1),
(54, 'Sắc màu văn hóa Ấn Độ trên đất Singapore', 'Các thiếu nữ say sưa trong điệu múa truyền thống, người đàn ông bước đi trên than nóng..., lễ hội Ánh sáng của người Ấn trên đảo quốc sư tử đã thu hút sự chú ý của đông đảo người dân bản địa và du khách nước ngoài.', 'anh-sang8.jpg', NULL, '<h1 class="Title">Sắc m&agrave;u văn h&oacute;a Ấn Độ tr&ecirc;n đất Singapore</h1>\n<h2 class="Lead">C&aacute;c thiếu nữ say sưa trong điệu m&uacute;a truyền thống, người đ&agrave;n &ocirc;ng bước đi tr&ecirc;n than n&oacute;ng..., lễ hội &Aacute;nh s&aacute;ng của người Ấn tr&ecirc;n đảo quốc sư tử đ&atilde; thu h&uacute;t sự ch&uacute; &yacute; của đ&ocirc;ng đảo người d&acirc;n bản địa v&agrave; du kh&aacute;ch nước ngo&agrave;i.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang8.jpg" border="1" alt="Cổng ch&agrave;o của lễ hội được được trang tr&iacute; lộng lẫy với biểu tượng những ch&uacute; voi v&agrave;ng." width="480" height="347" /></td>\n</tr>\n<tr>\n<td class="Image">Lễ hội &Aacute;nh s&aacute;ng (Deepavali), một lễ hội lớn trong năm của người Ấn Độ, l&agrave; dịp để mọi người vui chơi v&agrave; phục hồi năng lượng. Hằng năm, lễ hội n&agrave;y cũng được tổ chức rất quy m&ocirc; lớn từ đầu th&aacute;ng 10 đến đầu th&aacute;ng 11 tại khu Tiểu Ấn (Little India) thuộc Singapore, nơi tập trung đ&ocirc;ng đảo cộng đồng người Ấn. Cổng ch&agrave;o của lễ hội được được trang tr&iacute; lộng lẫy với biểu tượng hai ch&uacute; voi v&agrave;ng.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang5.jpg" border="1" alt="Người Ấn Độ th&iacute;ch được vẽ h&igrave;nh săm l&ecirc;n cơ thể." width="480" height="319" /></td>\n</tr>\n<tr>\n<td class="Image">Hội tụ những n&eacute;t văn h&oacute;a truyền thống của người theo đạo Hin du, đ&acirc;y c&ograve;n l&agrave; nơi thể hiện những đặc trưng về đời sống t&acirc;m linh, x&atilde; hội của người Ấn. Đường phố, nơi diễn ra lễ hội được trang ho&agrave;ng lộng lẫy với h&igrave;nh ảnh chim c&ocirc;ng sặc sỡ.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang1.jpg" border="1" alt="Em b&eacute; Ấn Độ h&oacute;a trang giống một nữ thần." width="400" height="602" /></td>\n</tr>\n<tr>\n<td class="Image">Em b&eacute; Ấn Độ h&oacute;a trang giống một nữ thần.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang2.jpg" border="1" alt="Thiếu nữ m&uacute;a điệu truyền thống." width="480" height="360" /></td>\n</tr>\n<tr>\n<td class="Image">Thiếu nữ uyển chuyển với điệu m&uacute;a truyền thống.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang9.jpg" border="1" alt="Nghệ sĩ xay m&ecirc; điệu nhảy truyền thống" width="480" height="329" /></td>\n</tr>\n<tr>\n<td class="Image">Lễ hội thu h&uacute;t nhiều nghệ sỹ từ Ấn Độ sang biểu diễn.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang12.jpg" border="1" alt="Trẻ em cũng vui tuoi nhay mua trong le hoi." width="480" height="325" /></td>\n</tr>\n<tr>\n<td class="Image">C&aacute;c em nhỏ cũng vui tươi nhảy m&uacute;a trong lễ hội.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang3.jpg" border="1" alt="Người đ&agrave;n &ocirc;ng bế con tr&ecirc;n tay đứng trước nh&agrave; thờ v&agrave; cầu nguyện cho gia đ&igrave;nh." width="400" height="582" /></td>\n</tr>\n<tr>\n<td class="Image">Người đ&agrave;n &ocirc;ng bế con tr&ecirc;n tay đứng trước vị thần v&agrave; cầu nguyện.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang11.jpg" border="1" alt="Người Ấn Độ th&iacute;ch vẽ những h&igrave;nh săm l&ecirc;n cơ thể." width="400" height="560" /></td>\n</tr>\n<tr>\n<td class="Image">Người Ấn Độ th&iacute;ch vẽ những h&igrave;nh trang tr&iacute; l&ecirc;n cơ thể.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang4.jpg" border="1" alt="Những người đ&agrave;n &ocirc;ng thực hiện nghi lễ bước đi tr&ecirc;n lớp than n&oacute;ng." width="480" height="379" /></td>\n</tr>\n<tr>\n<td class="Image">Những người đ&agrave;n &ocirc;ng thực hiện nghi lễ bước đi tr&ecirc;n lớp than n&oacute;ng.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang6.jpg" border="1" alt="Những v&ograve;ng hoa nhiều m&agrave;u sắc thường d&ugrave;ng trong lễ hội của ngưới Ấn." width="480" height="349" /></td>\n</tr>\n<tr>\n<td class="Image">Những v&ograve;ng hoa treo b&aacute;n cho người đi lễ.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="anh-sang7.jpg" border="1" alt="Những bức tượng truyền thống kh&aacute; bắt mắt được trưng b&agrave;y trong lễ hội." width="480" height="334" /></td>\n</tr>\n<tr>\n<td class="Image">Những bức tượng thủ c&ocirc;ng mỹ nghệ truyền thống kh&aacute; bắt mắt được trưng b&agrave;y trong lễ hội.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n</strong></p>', 3, 1, 1288, 0, 1),
(55, 'Disneyland - nơi giấc mơ cổ tích trở thành sự thực', 'Đây là địa điểm tôi thích nhất trong chuyến du ngoạn Hong Kong mùa thu năm nay vì những cảm xúc có được ở đây là những cảm xúc tuyệt vời nhất mà tôi không thể nào quên được. Bạn Nguyễn Thu Hà chia sẻ', '3.jpg', NULL, '<p class="Title">Disneyland - nơi gi&acirc;́c mơ c&ocirc;̉ tích trở thành sự thực</p>\n<p class="Lead">Đ&acirc;y là địa đi&ecirc;̉m t&ocirc;i thích nh&acirc;́t trong chuy&ecirc;́n du ngoạn Hong Kong mùa thu năm nay vì những cảm xúc có được ở đ&acirc;y là những cảm xúc tuy&ecirc;̣t vời nh&acirc;́t mà t&ocirc;i kh&ocirc;ng th&ecirc;̉ nào qu&ecirc;n được. Bạn Nguyễn Thu H&agrave; chia sẻ.</p>\n<p class="Normal">T&ocirc;i bị cu&ocirc;́n hút ngay từ đi&ecirc;̣u nhạc du dương th&acirc;̀n ti&ecirc;n khi bước vào c&ocirc;̉ng c&ocirc;ng vi&ecirc;n. C&ocirc;ng vi&ecirc;n này có 4 khu chính, Fantasy Land là nơi dành cho các em nhỏ gặp gỡ với các nh&acirc;n v&acirc;̣t d&ecirc;̃ thương như chu&ocirc;̣t Mickey, vịt Donald cùng những trò chơi dành cho trẻ con. Hình ảnh đặc trưng cho khu này là l&acirc;u đài của c&ocirc;ng chúa ngủ trong rừng &ldquo;Sleeping beauty castle&rdquo;, nó cũng đ&ocirc;̀ng thời là hình ảnh đặc trưng khi ta nói đ&ecirc;́n Disneyland park.</p>\n<p class="Normal">Thứ hai là khu Adventure Land g&ocirc;̀m những trò chơi dành cho thanh thi&ecirc;́u ni&ecirc;n, đặc sắc nh&acirc;́t là hành trình khám phá s&ocirc;ng Amazon kỳ̀ thú và ng&ocirc;i nhà tr&ecirc;n c&acirc;y của Tarzan. Khu Tomorrow Land là những trò chơi khám phá vũ trụ, th&ecirc;́ giới tương lai mang tính khoa học vi&ecirc;̃n tưởng, cũng là nơi chúng ta có th&ecirc;̉ gặp hình ảnh của nh&acirc;n v&acirc;̣t Stich đáng y&ecirc;u trong Lilo v&agrave; Stich. Cu&ocirc;́i cùng là khu Main Street Land được x&acirc;y m&ocirc; phỏng theo khung cảnh thành ph&ocirc;́ ở Mỹ đ&acirc;̀u th&ecirc;́ kỷ 20.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="3.jpg" alt="" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Đại nhạc hội đường phố. Ảnh:&nbsp;<em>Nguyễn Thu H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Các trò chơi thì dĩ nhi&ecirc;n là đặc sắc r&ocirc;̀i, nhưng cái mà t&ocirc;i say m&ecirc; Disneyland nh&acirc;́t chính là được đắm mình trong khung cảnh của th&ecirc;́ giới th&acirc;̀n ti&ecirc;n qua các đại nhạc h&ocirc;̣i s&ocirc;i đ&ocirc;̣ng, những tòa l&acirc;u đài tráng l&ecirc;̣, từ những shop đ&ocirc;̀ lưu ni&ecirc;̣m d&ecirc;̃ thương cho đ&ecirc;́n nhà hàng và cả khách sạn được thi&ecirc;́t k&ecirc;́ theo ki&ecirc;̉u c&ocirc;̉ tích.</p>\n<p class="Normal">Có th&ecirc;̉ nói các show di&ecirc;̃n chính là &ldquo;đặc sản&rdquo; của Disneyland, n&ocirc;̉i ti&ecirc;́ng nh&acirc;́t là đại nhạc h&ocirc;̣i đường ph&ocirc;́ &ldquo;Disney on Parade&rdquo; và màn pháo hoa bu&ocirc;̉i t&ocirc;́i mang t&ecirc;n &ldquo;Disney in the Stars&rdquo; tại Main Street Land, hay &ldquo;Lion King Show&rdquo; tại Advanture Land. Chúng t&ocirc;i đ&ecirc;́n g&acirc;̀n 3h chi&ecirc;̀u, là lúc sắp đ&ecirc;́n giờ bi&ecirc;̉u di&ecirc;̃n đại nhạc h&ocirc;̣i đường ph&ocirc;́ &ldquo;Disney on Parade&rdquo;. Vì đã bi&ecirc;́t trước n&ecirc;n t&ocirc;i r&acirc;́t háo hức đứng x&ecirc;́p hàng trước đ&ecirc;̉ chọn ch&ocirc;̃ đứng xem cho đẹp.</p>\n<p class="Normal">Th&acirc;̣t khó di&ecirc;̃n tả được cảm xúc khi được gặp Alice cùng chú thỏ trắng đãng trí, cả bà hoàng h&acirc;̣u trong &ldquo;Alice in Wonderland&rdquo;. Khi đọc truy&ecirc;̣n này t&ocirc;i th&acirc;́y cái bà hoàng h&acirc;̣u này vừa đáng ghét lại vừa nh&ocirc;̣n. R&ocirc;̀i thì Cinderalla, nàng ti&ecirc;n cá và nàng Bạch Tuy&ecirc;́t xinh đẹp tuy&ecirc;̣t tr&acirc;̀n đúng ki&ecirc;̉u c&ocirc;̉ tích thoát tục, cũng nhảy múa tung tăng tr&ecirc;n các tòa l&acirc;u đài tráng l&ecirc;̣, kh&ocirc;ng th&ecirc;̉ tin là tr&ecirc;n đời lại có những c&ocirc; c&ocirc;ng chúa đẹp như th&ecirc;́.</p>\n<p class="Normal">Ti&ecirc;́p sau đ&acirc;́y là Mickey, Minnie, Donal, Daisy và Pluto nữa. Cả các nh&acirc;n v&acirc;̣t trong Toy Story cũng ra v&acirc;̃y chào khán giả như th&acirc;̣t. Đám đ&ocirc;ng xung quanh thì ph&acirc;́n khích v&ocirc;̃ tay &acirc;̀m ĩ. Hi&ecirc;̣u ứng &acirc;m thanh được sử dụng r&acirc;́t thành c&ocirc;ng khi&ecirc;́n khán giả chỉ mu&ocirc;́n nhảy múa lắc lư cùng các nh&acirc;n v&acirc;̣t. Trong bài hát có đoạn &ldquo;Yes, you believe that dream comes true&hellip;&rdquo;.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="4.jpg" border="1" alt="" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Alice in Wonderland. Ảnh:&nbsp;<em>Nguyễn Thu H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Cả khi ng&ocirc;̀i trong phòng phim 3D cũng v&acirc;̣y, cảm xúc d&acirc;ng trào mãnh li&ecirc;̣t khi được ng&ocirc;̀i tr&ecirc;n thảm bay cùng Aladin và c&ocirc;ng chúa trong giai đi&ecirc;̣u ngọt ngào hứng khởi của bài &ldquo;The whole new world&rdquo;. N&ecirc;́u là xem m&ocirc;̣t b&ocirc;̣ phim 3D bom t&acirc;́n nào đó thì sẽ kh&ocirc;ng có cảm giác đặc bi&ecirc;̣t đ&ecirc;́n v&acirc;̣y.</p>\n<p class="Normal">Vì những nh&acirc;n v&acirc;̣t hoạt hình của Walt Disney đã là m&ocirc;̣t ph&acirc;̀n của tu&ocirc;̉i thơ t&ocirc;i, từ Bạch Tuy&ecirc;́t đ&ecirc;́n Cinderalla, Ariel, Belle &amp; The beast, r&ocirc;̀i Aladin, Lion King&hellip; Phút gi&acirc;y đó đưa t&acirc;m thức t&ocirc;i ngược dòng thời gian trở lại những ngày xưa, cái h&ocirc;̀i bé thơ v&acirc;̃n còn m&ecirc; m&acirc;̉n với truy&ecirc;̣n c&ocirc;̉ tích v&agrave; phim hoạt hình, được bi&ecirc;́t đ&ecirc;́n thảm bay và ước ao mình cũng được bay như th&ecirc;́.</p>\n<p class="Normal">H&ocirc;̀i bé, trong những khả năng si&ecirc;u nhi&ecirc;n mà t&ocirc;i được bi&ecirc;́t đ&ecirc;́n, t&ocirc;i v&acirc;̃n thích bay nh&acirc;́t, và khi ngủ t&ocirc;i r&acirc;́t hay mơ là mình bay đ&ecirc;́n những nơi v&ocirc; cùng đẹp đẽ, có thung lũng và đ&ocirc;̀ng cỏ xanh mướt. Lớn l&ecirc;n t&ocirc;i kh&ocirc;ng còn có những gi&acirc;́c mơ kỳ̀ thú trẻ con đó nữa, và thỉnh thoảng t&ocirc;i v&acirc;̃n ti&ecirc;́c nhớ những gi&acirc;́c mơ &acirc;́y. Với m&ocirc;̃i chúng ta, ngoài h&ocirc;̀i ức v&ecirc;̀ m&ocirc;̣t cu&ocirc;̣c tình ra, thì h&ocirc;̀i ức v&ecirc;̀ tu&ocirc;̉i thơ là m&ocirc;̣t mi&ecirc;̀n k&yacute; ức nhạy cảm và thi&ecirc;ng li&ecirc;ng nh&acirc;́t, chỉ c&acirc;̀n chạm nhẹ vào là đã đủ khi&ecirc;́n t&acirc;m h&ocirc;̀n chúng ta rung đ&ocirc;̣ng x&ocirc;́n xang.</p>\n<p class="Normal">T&ocirc;i đ&ecirc;́n với Disneyland ở tu&ocirc;̉i 21, và đ&acirc;y chính là nơi mà gi&acirc;́c mơ tu&ocirc;̉i thơ t&ocirc;i trở thành sự thực. Kh&ocirc;ng hi&ecirc;̉u sao sau khi đ&ecirc;́n đ&acirc;y, t&ocirc;i cảm th&acirc;́y có nhi&ecirc;̀u ni&ecirc;̀m tin hơn, y&ecirc;u cu&ocirc;̣c s&ocirc;́ng hơn, gi&ocirc;́ng như cảm giác m&ocirc;̣t t&acirc;m h&ocirc;̀n đang trưởng thành còn vương chút trẻ thơ vừa tìm được nơi nương tựa. Đ&ocirc;i lúc t&ocirc;i v&acirc;̃n cảm th&acirc;́y mình gi&ocirc;́ng Holden Caufield, anh chàng mới lớn lu&ocirc;n mu&ocirc;́n níu giữ tu&ocirc;̉i thơ, lu&ocirc;n l&acirc;̉m nh&acirc;̉m bài hát &ldquo;bắt trẻ đ&ocirc;̀ng xanh&rdquo; và chỉ có m&ocirc;̣t ước mơ là sẽ đứng ở m&ocirc;̣t đ&ocirc;̀ng cỏ tr&ecirc;n núi đ&ecirc;̉ làm nhi&ecirc;̣m vụ đỡ những đứa trẻ bị rơi ra khỏi cánh đ&ocirc;̀ng xanh mướt tu&ocirc;̉i thơ của chúng. Và t&acirc;m h&ocirc;̀n Holden dường như đã tìm được nơi nương tựa khi anh dắt tay đứa em gái nhỏ đi tr&ecirc;n con đường trải dài trước mắt. Thì đ&acirc;y, cảm giác của t&ocirc;i khi đắm mình trong kh&ocirc;ng gian của Disneyland cũng g&acirc;̀n gi&ocirc;́ng như v&acirc;̣y.</p>\n<p class="Normal">M&ocirc;̣t ngày nào đó n&ecirc;́u như tr&ecirc;n đường đời, t&ocirc;i có gặp đi&ecirc;̀u gì đau kh&ocirc;̉ hay đ&ocirc;̉ vỡ, nh&acirc;́t định t&ocirc;i sẽ nghĩ đ&ecirc;́n Disneyland như là m&ocirc;̣t đi&ecirc;̉m đ&ecirc;́n đ&ecirc;̉ tìm lại ni&ecirc;̀m hứng khởi. Hoặc n&ecirc;́u t&ocirc;i có con, t&ocirc;i cũng sẽ c&ocirc;́ gắng cho chúng tới đ&acirc;y, tu&ocirc;̉i đẹp nh&acirc;́t đ&ecirc;̉ vào đ&acirc;y theo t&ocirc;i là khoảng 16-17. Hoặc n&ecirc;́u kh&ocirc;ng phải cả hai trường hợp tr&ecirc;n, chỉ là tình cờ t&ocirc;i được trở lại đ&acirc;y, thì t&ocirc;i cũng sẽ v&ocirc; cùng hạnh phúc, khi l&acirc;̀n thứ hai được trở v&ecirc;̀ với gi&acirc;́c mơ xưa cũ đẹp đẽ và v&ocirc; tư.</p>\n<p class="Normal" style="text-align: right;"><strong>Nguyễn Thu H&agrave;</strong></p>', 3, 1, 574, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(56, 'Thắng cảnh suối nước Vàng', 'Cách thành phố Bắc Giang chừng 60 km về phía đông bắc, thắng cảnh nước Vàng ở xã Lục Sơn (Lục Nam, Bắc Giang) thuộc khu bảo tồn thiên nhiên Tây Yên Tử là điểm đến đầy kỳ thú cho các chuyến dã ngoại của những du khách “bụi” đi tìm cảm giác mạnh. Độc giả Nguyễn Văn Hưởng viết.', '1.jpg', NULL, '<p class="Title">Thắng cảnh suối nước V&agrave;ng</p>\n<p class="Lead">C&aacute;ch th&agrave;nh phố Bắc Giang chừng 60 km về ph&iacute;a đ&ocirc;ng bắc, thắng cảnh nước V&agrave;ng ở x&atilde; Lục Sơn (Lục Nam, Bắc Giang) thuộc khu bảo tồn thi&ecirc;n nhi&ecirc;n T&acirc;y Y&ecirc;n Tử l&agrave; điểm đến đầy kỳ th&uacute; cho c&aacute;c chuyến d&atilde; ngoại của những du kh&aacute;ch &ldquo;bụi&rdquo; đi t&igrave;m cảm gi&aacute;c mạnh. Độc giả Nguyễn Văn Hưởng viết.</p>\n<p class="Normal">Đường v&agrave;o suối nước V&agrave;ng quanh co, uốn lượn, những cung đường đất đ&aacute; ch&ecirc;nh v&ecirc;nh, mỗi khi c&oacute; trận lũ đi qua mọi thứ đ&aacute; sỏi, c&agrave;nh c&acirc;y, l&aacute; rừng lại chặn ngang khiến đường đi c&agrave;ng th&ecirc;m kh&oacute;. Những con suối vắt ngang đường l&agrave;m xe kh&ocirc;ng thể lội qua, người d&acirc;n Lục Sơn dựng những chiếc cầu tạm bằng gỗ th&igrave; mới c&oacute; thể đi được.</p>\n<p class="Normal">Từ UBND x&atilde; Lục Sơn đến suối nước V&agrave;ng chừng 5 km, đường đ&aacute; sỏi l&ocirc; nh&ocirc;, những con &ldquo;tuấn m&atilde;&rdquo; mang nh&atilde;n hiệu Mink của Nga được nhiều du kh&aacute;ch chọn l&agrave;m phương tiện tr&ecirc;n h&agrave;nh tr&igrave;nh đầy gian nan n&agrave;y.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="1.jpg" alt="" width="480" height="330" /></td>\n</tr>\n<tr>\n<td class="Image">N&uacute;i rừng T&acirc;y Y&ecirc;n Tử. Ảnh:&nbsp;<em>Nguyễn Văn Hưởng.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Nước V&agrave;ng l&agrave; t&ecirc;n gọi của d&ograve;ng suối chảy từ tr&ecirc;n đỉnh d&atilde;y n&uacute;i Phật Sơn thuộc khu bảo tồn thi&ecirc;n nhi&ecirc;n T&acirc;y Y&ecirc;n Tử, (n&uacute;i Phật Sơn), t&ecirc;n gọi của con suối cũng ch&iacute;nh l&agrave; m&agrave;u nước rất độc đ&aacute;o v&agrave; kỳ lạ ở đ&acirc;y. Quanh năm nước chỉ c&oacute; một m&agrave;u duy nhất đ&oacute; l&agrave; m&agrave;u v&agrave;ng &oacute;ng như m&agrave;u mật ong rừng, những du kh&aacute;ch mới đến đ&acirc;y lần đầu đều kh&ocirc;ng khỏi ngạc nhi&ecirc;n về m&agrave;u nước độc đ&aacute;o v&agrave; đặc trưng c&oacute; một kh&ocirc;ng hai tr&ecirc;n đất nước ta n&agrave;y.</p>\n<p class="Normal">Đến suối nước V&agrave;ng l&agrave; đến một v&ugrave;ng đại ng&agrave;n h&ugrave;ng vĩ, bao la rộng lớn, khu bảo tồn rừng nguy&ecirc;n sinh T&acirc;y Y&ecirc;n Tử với hệ thảm thực vật, động vật phong ph&uacute;, đa dạng, c&aacute;c lo&agrave;i lan rừng, thảo dược qu&yacute; hiếm như: trầm hương, ba k&iacute;ch, sa nh&acirc;n, Pơ mu&hellip; c&ugrave;ng c&aacute;c lo&agrave;i động vật như: vọc đen m&aacute; trắng, gấu ngựa, hươu v&agrave;ng, s&oacute;c, cầy hương c&ugrave;ng c&aacute;c lo&agrave;i chim rừng&hellip; Kh&iacute; hậu nơi đ&acirc;y &ocirc;n h&ograve;a, m&aacute;t mẻ c&aacute;c th&aacute;ng cuối năm thường c&oacute; m&acirc;y m&ugrave; bao phủ rất th&iacute;ch hợp cho việc du lịch nghỉ dưỡng.</p>\n<p class="Normal">Du kh&aacute;ch trải qua những khoảnh khắc vượt th&aacute;c, băng rừng, lội suối đầy th&uacute; vị v&agrave; đ&aacute;ng nhớ. Để kh&aacute;m ph&aacute; v&agrave; chinh phục con suối từ ph&iacute;a hạ nguồn l&ecirc;n miền thượng mất qu&aacute; nửa ng&agrave;y, sau khi băng qua gần 20 th&aacute;c ghềnh lớn nhỏ, một số th&aacute;c đẹp như th&aacute;c Anh Vũ, th&aacute;c M&acirc;y, Th&aacute;c Gi&oacute;t, Th&aacute;c Nước V&agrave;ng&hellip; Th&aacute;c n&agrave;o cũng đẹp, cũng hấp dẫn v&agrave; lạ mắt, ph&iacute;a dưới mỗi th&aacute;c đều c&oacute; những bồn tắm thi&ecirc;n tạo c&oacute; thể chứa đến v&agrave;i chục người, l&agrave; nơi để bạn c&oacute; thể thỏa sức v&ugrave;ng vẫy, giải nhiệt c&ugrave;ng d&ograve;ng nước m&aacute;t lạnh từ tr&ecirc;n cao dội l&ecirc;n cơ thể ta.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="2.jpg" alt="" width="480" height="330" /></td>\n</tr>\n<tr>\n<td class="Image">Đường đến nước V&agrave;ng. Ảnh:&nbsp;<em>Nguyễn Văn Hưởng.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Đỉnh suối nước V&agrave;ng c&aacute;ch ch&ugrave;a Đồng - Y&ecirc;n Tử v&agrave; am Ngọa V&acirc;n (nơi Điều Ngự Gi&aacute;c Ho&agrave;ng - vua Trần Nh&acirc;n T&ocirc;ng tu ẩn v&agrave; vi&ecirc;n tịch tại đ&acirc;y) kh&ocirc;ng xa, đứng b&ecirc;n n&agrave;y c&oacute; thể tr&ocirc;ng thấy rất r&otilde; Y&ecirc;n Tử b&ecirc;n sườn đ&ocirc;ng. Từ đ&acirc;y du kh&aacute;ch c&oacute; thể vượt n&uacute;i Phật Sơn để l&ecirc;n thiền viện Hồ Thi&ecirc;n (nơi tổ đệ nhất Trần Nh&acirc;n T&ocirc;ng tu h&agrave;nh, giảng đạo).</p>\n<p class="Normal">Danh thắng nước V&agrave;ng được UBND tỉnh Bắc Giang khoanh v&ugrave;ng bảo vệ v&agrave; ra quyết định c&ocirc;ng nhận l&agrave; danh lam thắng cảnh của tỉnh.</p>\n<p class="Normal">Danh thắng nước V&agrave;ng, n&uacute;i Phật Sơn - Y&ecirc;n Tử với những c&aacute;nh rừng nguy&ecirc;n sinh xanh thẳm v&agrave; nhiều thảo mộc, mu&ocirc;ng th&uacute;, lại vắt l&ecirc;n m&igrave;nh một d&ograve;ng suối c&oacute; sắc v&agrave;ng lung linh v&agrave; bồn tắm thi&ecirc;n tạo, chắc chắn sẽ l&agrave;m h&agrave;i l&ograve;ng du kh&aacute;ch khi đến đ&acirc;y.</p>\n<p class="Normal" style="text-align: right;"><strong>Nguyễn Văn Hưởng</strong></p>', 3, 1, 551, 0, 1),
(62, 'Phố phường Hà Nội rực rỡ trước đại lễ', '20 ngày trước đại lễ nghìn năm, đường phố Hà Nội rực rỡ ánh đèn cùng những hình tượng hoa văn màu sắc. Hồ Gươm như lung linh hơn.', 'dienbienphu.jpg', NULL, '<p class="Title">Phố phường H&agrave; Nội rực rỡ trước đại lễ</p>\r\n<p class="Lead">20 ng&agrave;y trước đại lễ ngh&igrave;n năm, đường phố H&agrave; Nội rực rỡ &aacute;nh đ&egrave;n c&ugrave;ng những h&igrave;nh tượng hoa văn m&agrave;u sắc. Hồ Gươm như lung linh hơn.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td class="Image" align="middle"><img src="dienbienphu.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Đường Điện Bi&ecirc;n Phủ rực rỡ, lung linh nhất thủ đ&ocirc; những ng&agrave;y n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="thaprua.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Hồ Ho&agrave;n Kiếm lung linh.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="dinhtienhoang.jpg" alt="" width="360" height="541" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Chạy dọc đường Đinh Ti&ecirc;n Ho&agrave;ng c&oacute; thể ngắm biểu tượng Khu&ecirc; Văn C&aacute;c.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="hoguom.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Cầu Th&ecirc; H&uacute;c v&agrave; Đền Ngọc Sơn.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="le-duan.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Biểu tượng trống đồng Đ&ocirc;ng Sơn tr&ecirc;n đường L&ecirc; Duẩn.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="langthuong.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Những b&ocirc;ng hoa đổi mầu tại ng&atilde; tư Nguyễn Ch&iacute; Thanh - đường L&aacute;ng.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="phamhung.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">C&aacute;nh chim h&ograve;a b&igrave;nh v&agrave; Khu&ecirc; Văn C&aacute;c tr&ecirc;n đường Phạm H&ugrave;ng.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="trannhantong.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Phố Trần Nh&acirc;n T&ocirc;ng, trước cửa c&ocirc;ng vi&ecirc;n Thống Nhất rự rỡ mầu sắc.</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle"><img src="tranduyhung.jpg" alt="" width="492" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Những ch&ugrave;m &aacute;nh s&aacute;ng như những tia nước chảy tr&ecirc;n đường Trần Duy Hưng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng H&agrave;</strong></p>', 3, 1, 1146, 0, 1),
(63, 'Một chuyến đi Hà Giang', 'Ngày 30 cả đoàn được đi trên con đường đèo uốn lượn, những vòng cua nhỏ sát ngay mép vực khiến các xế thả sức nghiêng mình nằm trọn trong vòng ôm của đèo. Bạn Đỗ Quang Trung viết về chuyến đi Hà Giang.', 'a21.jpg', NULL, '<p class="Title">Một chuyến đi H&agrave; Giang</p>\r\n<p class="Lead">Ng&agrave;y 30 cả đo&agrave;n được đi tr&ecirc;n con đường đ&egrave;o uốn lượn, những v&ograve;ng cua nhỏ s&aacute;t ngay m&eacute;p vực khiến c&aacute;c xế thả sức nghi&ecirc;ng m&igrave;nh nằm trọn trong v&ograve;ng &ocirc;m của đ&egrave;o. Bạn Đỗ Quang Trung viết về chuyến đi H&agrave; Giang.</p>\r\n<p class="Normal">Nắng cứ ch&ecirc;nh chếch xi&ecirc;n ngang, đ&egrave;o với những con dốc cao v&uacute;t giờ chỉ c&ograve;n lại tiếng động cơ vang l&ecirc;n vỗ vồn v&atilde; v&agrave;o đ&aacute;.</p>\r\n<table border="1" cellspacing="0" cellpadding="3" width="243" align="center" bgcolor="#ecf7ff">\r\n<tbody>\r\n<tr>\r\n<td><a href="page_1.asp">* Ảnh những gương mặt v&ugrave;ng cao</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&aacute;i buổi trưa h&ocirc;m ấy, t&ocirc;i đi ra trước cửa nh&agrave; trọ v&agrave; thấy một cậu b&eacute;. T&ocirc;i lại gần n&oacute;i chuyện với em. T&ocirc;i hỏi em &ldquo;Nh&agrave; em ở đ&acirc;u? Trưa thế n&agrave;y sao kh&ocirc;ng về ngủ?&ldquo;. Em trả lời &ldquo;Nh&agrave; em ở kia&rdquo; rồi chỉ vu vơ về ph&iacute;a trước mặt. T&ocirc;i dẫn em v&agrave;o trong nh&agrave;, thấy những người chủ nh&agrave; nh&igrave;n em một c&aacute;ch &aacute;i ngại. C&ocirc; chủ nh&agrave; lại gần t&ocirc;i kể: &ldquo;Bố mẹ n&oacute; bỏ nhau, mẹ bỏ v&agrave;o Nam, bố đi lang thang. N&oacute; đi quanh x&oacute;m ai c&oacute; g&igrave; cho th&igrave; ăn, n&oacute; thường hay lấy đồ của kh&aacute;ch du lịch".</p>\r\n<p class="Normal">T&ocirc;i thấy em &ocirc;m đầu ngồi bất động. Ở tuổi ấy, ai bắt em phải suy nghĩ nhiều đến thế? V&agrave; t&ocirc;i hỏi em c&oacute; muốn ăn kẹo kh&ocirc;ng? Em trả lời c&oacute;. T&ocirc;i liền chạy l&ecirc;n g&aacute;c v&agrave; đưa cho em cả 3 que kẹo. Nh&igrave;n em ăn, t&ocirc;i hỏi &ldquo;C&oacute; ngon kh&ocirc;ng?&rdquo;. T&ocirc;i thấy em cười. Em bảo em muốn uống nước. T&ocirc;i quay v&agrave;o nh&agrave;. Trở ra với ly nước. Em uống nhanh, dường như sợ c&oacute; ai đ&oacute; cướp thứ đ&oacute; từ tr&ecirc;n tay em vậy. T&ocirc;i hỏi em c&oacute; muốn g&igrave; nữa kh&ocirc;ng? Em lắc đầu.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="a21.jpg" border="1" alt="" width="480" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trồng trọt tr&ecirc;n n&uacute;i đ&aacute;. Ảnh:&nbsp;<em>Đỗ Quang Trung.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Khi t&ocirc;i v&agrave;o ăn cơm, trở ra đ&atilde; thấy em đi mất. T&ocirc;i kh&ocirc;ng c&oacute; g&igrave; để cho em ngoại trừ 3 thanh kẹo. Liệu t&ocirc;i c&ograve;n c&oacute; thể l&agrave;m g&igrave; cho em hơn thế khi m&agrave; người tạo ra em đ&atilde; kh&ocirc;ng thể cho em một cuộc sống đ&uacute;ng nghĩa?</p>\r\n<p class="Normal">Rời xa nơi ấy c&ugrave;ng với những h&igrave;nh ảnh về em, t&ocirc;i l&ecirc;n đường đến M&egrave;o Vạc, nơi t&ocirc;i từng nghe trong b&agrave;i h&aacute;t&nbsp;<em>Cướp vợ</em>&nbsp;của Ngũ Cung. Ng&agrave;y h&ocirc;m ấy l&agrave; một chuyến đi vất vả. Một chiếc xe trong đo&agrave;n hỏng xăm v&agrave; t&ocirc;i xuất ph&aacute;t chậm 3h so với đo&agrave;n. Ch&uacute;ng t&ocirc;i đ&aacute;nh v&agrave;o những con đường đ&egrave;o quanh co &aacute;nh đ&egrave;n pha yếu ớt. C&oacute; những đoạn đường đang l&agrave;m, t&ocirc;i ngồi tr&ecirc;n xe m&agrave; cảm gi&aacute;c lơ lửng tận nơi n&agrave;o. Chạy suốt từ 7h s&aacute;ng tới tận 10h đ&ecirc;m mới tới M&egrave;o Vạc, người r&atilde; ra v&igrave; mệt. Cũng may, vừa hay đến M&egrave;o Vạc th&igrave; xe t&ocirc;i kh&ocirc;ng c&ograve;n một giọt xăng.</p>\r\n<p class="Normal">Cả đo&agrave;n đang đợi ch&uacute;ng t&ocirc;i tr&ecirc;n Kh&acirc;u Vai v&agrave; ch&uacute;ng t&ocirc;i lại trở l&ecirc;n tr&ecirc;n đ&oacute;. Tắc đường, bởi h&ocirc;m đ&oacute; tr&ecirc;n Kh&acirc;u Vai tổ chức chợ t&igrave;nh. T&ocirc;i đến nơi ngồi c&ugrave;ng mọi người để biết rằng đo&agrave;n đ&atilde; c&oacute; mặt đ&ocirc;ng đủ. Rồi t&ocirc;i lại đi để &ldquo;xem thế n&agrave;o l&agrave; chợ t&igrave;nh?&ldquo;. Hơi thất vọng bởi người Kinh l&ecirc;n đ&acirc;y nhiều qu&aacute;, t&ocirc;i cứ ngồi m&atilde;i ở một chỗ để c&oacute; thể lưu lại được một bức ảnh về phi&ecirc;n chợ v&agrave; cũng để nghe về c&aacute;i phong tục của lễ hội n&agrave;y.</p>\r\n<p class="Normal">Cuối c&ugrave;ng t&ocirc;i đem nỗi thất vọng trở về, t&ocirc;i về khi cả đo&agrave;n đ&atilde; gần ngủ hết v&agrave; nhớ về m&oacute;n thắng cố. T&ocirc;i lại dậy đi t&igrave;m, một qu&aacute;n c&oacute; cả thắng cố lẫn m&egrave;n m&eacute;n. V&agrave;o qu&aacute;n, t&ocirc;i xin ph&eacute;p nhập b&agrave;n c&ugrave;ng với hai người ngồi trước ở đ&oacute; v&igrave; t&ocirc;i đi c&oacute; một m&igrave;nh, hỏi ra họ l&agrave; người Tuy&ecirc;n Quang. T&ocirc;i n&oacute;i chuyện với họ xung quanh m&oacute;n ăn v&agrave; về chợ t&igrave;nh. Thắng cố nơi đ&acirc;y nấu kh&ocirc;ng giống như những g&igrave; t&ocirc;i đọc, t&ocirc;i ăn thấy n&oacute; giống như giả cầy, c&ograve;n m&egrave;n m&eacute;n l&agrave;m từ ng&ocirc; chan nước thắng cố v&agrave;o ăn, n&oacute; nh&agrave;n nhạt vữa vụn.</p>\r\n<p class="Normal">Sau bữa rượu, t&ocirc;i định trở về ph&ograve;ng, tr&ecirc;n đường về t&ocirc;i thấy một chiếc chiếu vẫn đang trải v&agrave; thắp những chiếc nến tr&ecirc;n ấy. T&ocirc;i xin ph&eacute;p được ngồi c&ugrave;ng. T&ocirc;i hỏi anh về chợ t&igrave;nh. Anh bảo ng&agrave;y n&agrave;y con trai con g&aacute;i đi với một chiếc đ&egrave;n pin, người con trai chủ động l&agrave;m quen. Khi người con g&aacute;i đồng &yacute;, người con g&aacute;i sẽ dẫn người con trai đi đến một nơi n&agrave;o đ&oacute;, họ n&oacute;i chuyện với nhau. Nếu một trong hai đ&atilde; c&oacute; vợ hoặc c&oacute; chồng th&igrave; họ sẽ l&agrave; bạn, c&ograve;n nếu kh&ocirc;ng họ sẽ tiếp tục l&agrave;m quen với nhau.</p>\r\n<p class="Normal">Khi anh bảo sắp dọn chiếu, t&ocirc;i trở l&ecirc;n khu nghỉ thiếp đi với giấc mơ mộng mị đ&ecirc;m chợ t&igrave;nh.</p>\r\n<p class="Normal">"Đ&acirc;u rồi thời rung reng v&ograve;ng bạc lắc đồng?<br />Em k&igrave;a, những c&acirc;y sa mộc lặng lẽ trong thung<br />Ch&uacute;ng đang nghĩ g&igrave;?<br />Em đang nghĩ g&igrave;?".<br /><br />(Chợ t&igrave;nh Kh&acirc;u Vai - Trần H&ograve;a B&igrave;nh)</p>\r\n<p class="Normal">Đến Đồng Văn, t&ocirc;i v&agrave;o một khu chợ, dựng xe v&agrave; t&ocirc;i thấy một v&agrave;i người đ&agrave;n &ocirc;ng đang uống rượu. T&ocirc;i hỏi người đang chế biến m&oacute;n ăn v&agrave; được biết đ&acirc;y l&agrave; m&oacute;n thắng cố. T&ocirc;i v&agrave;o v&agrave; gọi 2 lạng thắng cố. Như t&ocirc;i được biết thắng cố l&agrave;m trong dịp lễ bảo vệ thần rừng của d&acirc;n tộc H&rsquo;M&ocirc;ng, người ta cắt lấy đầu để thờ v&agrave; cho nội tạng của những con vật như tr&acirc;u, b&ograve;, lợn, ngựa v&agrave;o chung trong một nồi. Nhưng thắng cố ở đ&acirc;y kh&aacute;c, thắng được chế biến từ một con duy nhất. H&ocirc;m đ&oacute;, m&oacute;n t&ocirc;i ăn được chế biến từ thịt b&ograve;, họ nướng sau đ&oacute; để tr&ecirc;n một chiếc b&agrave;n. Ai ăn, họ cắt ra c&acirc;n, cho v&agrave;o nồi trần qua, m&uacute;c &iacute;t nước đổ v&agrave;o b&aacute;t. Đ&oacute; l&agrave; thắng cố ở Đồng Văn.</p>\r\n<p class="Normal">&ldquo;Vị n&uacute;i, hương đ&egrave;o, xu&acirc;n gia vị<br />Kh&iacute; trời, nghĩa đất, thắng cố đ&acirc;y!&rdquo;.</p>\r\n<p class="Normal">T&ocirc;i biết sẽ l&agrave; kh&ocirc;ng bao giờ đủ khi n&oacute;i về một chuyến đi như thế. Nhưng quan trọng l&agrave; tất cả ch&uacute;ng ta đ&atilde; quay trở về. Ch&uacute;ng ta đ&atilde; gặp nhau, đ&atilde; cười, đ&atilde; vui vẻ qu&acirc;y quần b&ecirc;n những bữa ăn v&agrave; ngủ chung trong những gian nh&agrave; như thế.</p>\r\n<p class="Normal">Bởi vậy, t&ocirc;i v&agrave; bạn h&atilde;y c&ugrave;ng nhau mơ về những chuyến đi&hellip;</p>\r\n<p class="Normal" style="text-align: right;"><strong>Đỗ Quang Trung</strong></p>', 3, 1, 541, 0, 1),
(66, 'Shangri-La thung lũng huyền thoại', 'Thành phố Shangri-La thuộc tỉnh Vân Nam, Trung Quốc là điểm du lịch hấp dẫn với cảnh quan thiên nhiên tươi đẹp, những ngôi chùa theo kiến trúc Phật giáo Tây Tạng…', 'dulich2.jpg', NULL, '<p class="Title">Shangri-La thung lũng huyền thoại</p>\r\n<p class="Lead">Th&agrave;nh phố Shangri-La thuộc tỉnh V&acirc;n Nam, Trung Quốc l&agrave; điểm du lịch hấp dẫn với cảnh quan thi&ecirc;n nhi&ecirc;n tươi đẹp, những ng&ocirc;i ch&ugrave;a theo kiến tr&uacute;c Phật gi&aacute;o T&acirc;y Tạng&hellip;</p>\r\n<p class="Normal">M&ugrave;a hạ nơi đ&acirc;y đẹp hơn khi cỏ c&acirc;y căng tr&agrave;n sức sống, những triền đồi trải đầy hoa nở, m&acirc;y n&uacute;i trập tr&ugrave;ng như tranh thủy mặc. Shangri-La nằm trong một g&oacute;c phố nhỏ, ẩn m&igrave;nh s&acirc;u trong phố ch&iacute;nh với những con đường l&aacute;t đ&aacute; đậm phong c&aacute;ch kiến tr&uacute;c T&acirc;y Tạng. Th&agrave;nh phố ch&agrave;o đ&oacute;n kh&aacute;ch du lịch với sự thanh b&igrave;nh v&agrave; y&ecirc;n ả.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dulich2.jpg" border="1" alt="Những hồ nước ngọt phẳng lặng, nằm phơi m&igrave;nh b&ecirc;n những d&atilde;y n&uacute;i cao tạo cảm gi&aacute;c y&ecirc;n b&igrave;nh cho Shangrila." width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những hồ nước ngọt phẳng lặng, nằm phơi m&igrave;nh b&ecirc;n d&atilde;y n&uacute;i cao tạo cảm gi&aacute;c y&ecirc;n b&igrave;nh cho thị trấn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&aacute;c gian h&agrave;ng b&agrave;y nhiều sản phẩm d&acirc;n gian của người Tạng. Những chiếc v&ograve;ng đeo tay v&agrave; khuy&ecirc;n tai đủ m&agrave;u sắc; chiếc chu&ocirc;ng c&oacute; tiếng k&ecirc;u leng keng vui tai; b&uacute;p b&ecirc; mang h&igrave;nh ảnh những c&ocirc; b&eacute; d&acirc;n tộc Nạp T&acirc;y Tạng hoặc Moshua; c&aacute;c con b&ograve; Yak nhồi b&ocirc;ng nhỏ xinh. Ngo&agrave;i ra, cặp, t&uacute;i l&agrave;m từ da thuộc l&agrave; những m&oacute;n qu&agrave; th&uacute; vị cho kh&aacute;ch du lịch.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dulich04.jpg" border="1" alt="Những m&oacute;n qu&agrave; lưu niệm đặc trưng khi đến Shangri-La" width="400" height="366" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những m&oacute;n qu&agrave; lưu niệm đặc trưng Shangri-La.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đến với Shangri-La, một địa danh kh&ocirc;ng thể bỏ qua l&agrave; ng&ocirc;i ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n ngọn đồi trong khu cổ trấn. Bạn c&oacute; thể nh&igrave;n to&agrave;n cảnh khu th&agrave;nh cổ khi đến đ&acirc;y. &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; khổng lồ cao 24 m (c&ograve;n gọi l&agrave; v&ograve;ng quay tụng ch&uacute;) trong s&acirc;n ch&ugrave;a được cho l&agrave; lớn nhất thế giới.</p>\r\n<p class="Normal">Người d&acirc;n nơi đ&acirc;y tin rằng vừa niệm Phật vừa d&ugrave;ng tay k&eacute;o &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; nhiều v&ograve;ng sẽ đem lại những điều an l&agrave;nh, như &yacute;. Bởi vậy, h&igrave;nh ảnh &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; được mạ v&agrave;ng, chạm trổ h&igrave;nh Phật với những v&ograve;ng quay kh&ocirc;ng dứt gi&uacute;p tăng th&ecirc;m vẻ uy nghi&ecirc;m, thanh khiết của ng&ocirc;i ch&ugrave;a.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dulich4.jpg" border="1" alt="Ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n một ngọn đồi." width="450" height="309" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n một ngọn đồi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chi tiết chương tr&igrave;nh v&agrave; gi&aacute; xem tại Website:&nbsp;<a class="Normal" href="http://www.travel365.com.vn/">http://www.travel365.com.vn/</a></p>\r\n<p class="Normal">Hoặc li&ecirc;n hệ trực tiếp Asiana Travel</p>\r\n<p class="Normal">Địa chỉ: 159 Phương Mai, Đống Đa, H&agrave; Nội</p>\r\n<p class="Normal">Tel: (84 - 4) 35 736 748 &ndash; 49/ Fax: (84 - 4) 35 736 750 - Hotline: 0983.388.387 (Ms. Mỹ); 0983.000.448 (Ms. Nh&acirc;n)</p>\r\n<p class="Normal">Email:&nbsp;<a class="Normal" href="mailto:info@travel365.com.vn">info@travel365.com.vn</a></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Asiana Travel</em>)</p>', 3, 1, 434, 0, 1),
(67, 'Công viên đá ở Côn Minh', 'Tạo hóa đã ưu ái cho Thạch Lâm (Côn Minh, Vân Nam, Trung Quốc) một phong cảnh thần tiên của đá và nước. Những cột đá nằm trong một khu vực rộng tới 350 km2, tập trung nhất là khu công viên đá Thạch Lâm.', '1 (3).jpg', NULL, '<p class="Title">C&ocirc;ng vi&ecirc;n đ&aacute; ở C&ocirc;n Minh</p>\r\n<p class="Lead">Tạo h&oacute;a đ&atilde; ưu &aacute;i cho Thạch L&acirc;m (C&ocirc;n Minh, V&acirc;n Nam, Trung Quốc) một phong cảnh thần ti&ecirc;n của đ&aacute; v&agrave; nước. Những cột đ&aacute; nằm trong một khu vực rộng tới 350 km2, tập trung nhất l&agrave; khu c&ocirc;ng vi&ecirc;n đ&aacute; Thạch L&acirc;m.</p>\r\n<p class="Normal">Kh&aacute;ch du lịch Việt Nam đi theo tuyến H&agrave; Nội &ndash; Alư Cổ Động &ndash; Thạch L&acirc;m &ndash; C&ocirc;n Minh &ndash; H&agrave; Nội đều c&oacute; cảm nhận: Thạch L&acirc;m l&agrave; điểm tham quan l&yacute; tưởng với vẻ đẹp kỳ diệu, một sự kết hợp h&agrave;i h&ograve;a giữa thi&ecirc;n nhi&ecirc;n v&agrave; con người.</p>\r\n<p class="Normal">Ngắm nh&igrave;n bạn sẽ thấy c&aacute;c khối đ&aacute; như một ng&ocirc;i ch&ugrave;a, h&igrave;nh th&ugrave; giống một rừng kiếm, một d&atilde;y cột, một con voi, một con phượng ho&agrave;ng x&ograve;e c&aacute;nh, rồi h&igrave;nh tượng của H&ograve;a thượng Đường Tăng, của một thiếu nữ h&oacute;a đ&aacute;, lại c&oacute; cả giường Ti&ecirc;n, những hồ nước trong xanh với c&aacute;c đảo đ&aacute; nhấp nh&ocirc;... Đi trong rừng đ&aacute; với mu&ocirc;n v&agrave;n những h&igrave;nh dạng kỳ diệu ấy du kh&aacute;ch như lạc v&agrave;o một thế giới ri&ecirc;ng, chỉ c&oacute; trong chuyện thần ti&ecirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1.jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Rừng đ&aacute; h&ugrave;ng vĩ. Ảnh:&nbsp;<em>Hồng Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nơi đ&acirc;y đ&atilde; được chọn l&agrave; một trong những trường quay của bộ phim &ldquo;T&acirc;y du k&yacute;&rdquo;, với những điểm ghi dấu ấn của T&ocirc;n Ngộ Kh&ocirc;ng v&agrave; c&aacute;c sư đệ đ&atilde; thể hiện t&agrave;i tr&iacute; trong việc th&aacute;p t&ugrave;ng Đường Tăng đi T&acirc;y Tr&uacute;c lấy kinh. Đ&oacute; l&agrave; lỗ đ&aacute; h&igrave;nh tr&ograve;n, l&agrave; khe đ&aacute; hẹp chỉ cho lọt cổ.</p>\r\n<p class="Normal">Cảnh đẹp thần ti&ecirc;n của Thạch L&acirc;m c&agrave;ng được du kh&aacute;ch h&agrave;i l&ograve;ng khi đến tham quan ch&iacute;nh bởi những con người nơi đ&acirc;y: những c&aacute;n bộ, nh&acirc;n vi&ecirc;n quản l&yacute; c&ocirc;ng vi&ecirc;n. Nhiều người trong số họ thuộc d&acirc;n tộc Yi, một trong những d&acirc;n tộc thiểu số đ&ocirc;ng nhất ở V&acirc;n Nam. Những c&ocirc; g&aacute;i d&acirc;n tộc Yi chăm chỉ v&agrave; hiếu kh&aacute;ch, trong trang phục đặc sắc, l&aacute;i &ocirc;t&ocirc; điện đưa du kh&aacute;ch đến c&aacute;c điểm tham quan trong &ldquo;rừng đ&aacute;&rdquo;. Đ&oacute; l&agrave; loại xe rất tiện lợi, đặc biệt l&agrave; giữ cho m&ocirc;i trường sạch sẽ, kh&ocirc;ng bị &ocirc; nhiễm, kh&ocirc;ng ồn &agrave;o.</p>\r\n<p class="Normal">Những c&ocirc;ng nh&acirc;n chuy&ecirc;n sửa sang, bảo dưỡng c&ocirc;ng vi&ecirc;n một c&aacute;ch rất tỉ mỉ, rất khoa học đối với từng c&acirc;y hoa, ngọn cỏ... để b&ecirc;n đường đi l&agrave; những thảm cỏ xanh mượt m&agrave; v&agrave; l&agrave; những vườn hoa đẹp rực rỡ như đ&oacute;n ch&agrave;o du kh&aacute;ch. Tất cả đều lu&ocirc;n nở nụ cười th&acirc;n thiện với kh&aacute;ch. Nhiều người giao tiếp được bằng tiếng Việt c&agrave;ng l&agrave;m cho quan hệ chủ, kh&aacute;ch th&ecirc;m th&acirc;n mật.</p>\r\n<p class="Normal">Ch&iacute;nh v&igrave; thế m&agrave; nơi đ&acirc;y &ndash; một c&ocirc;ng vi&ecirc;n, một di sản thi&ecirc;n nhi&ecirc;n thế giới &ndash; đ&atilde; được giữ g&igrave;n một c&aacute;ch ho&agrave;n hảo, hết sức sạch sẽ. Bản th&acirc;n điều đ&oacute; như một lời nhắc nhở, tạo cho du kh&aacute;ch một th&oacute;i quen rất văn minh: kh&ocirc;ng dẫm l&ecirc;n cỏ, kh&ocirc;ng h&aacute;i một b&ocirc;ng hoa, vứt r&aacute;c đ&uacute;ng nơi quy định...</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="5.jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cảnh quan trữ t&igrave;nh hiếm c&oacute;. Ảnh:&nbsp;<em>Hồng Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ch&uacute;ng t&ocirc;i đến Thạch L&acirc;m đ&uacute;ng dịp mở đầu tuần Lễ hội của người Sani, một nh&aacute;nh của d&acirc;n tộc Yi, được chứng kiến điệu nhảy tập thể, giản dị nhưng đầy mầu sắc. Những ch&agrave;ng trai, c&ocirc; g&aacute;i người d&acirc;n tộc trong trang phục truyền thống nhảy trong tiếng nhạc d&acirc;n gian, tay trong tay, nhảy theo một v&ograve;ng tr&ograve;n, sẵn s&agrave;ng mời v&agrave; nhảy rất vui vẻ c&ugrave;ng du kh&aacute;ch.</p>\r\n<p class="Normal">L&uacute;c n&agrave;y kh&ocirc;ng c&ograve;n ph&acirc;n biệt chủ - kh&aacute;ch, tất cả h&ograve;a trong kh&ocirc;ng kh&iacute; lễ hội, trong t&igrave;nh hữu nghị giữa c&aacute;c d&acirc;n tộc, c&aacute;c quốc gia. Lễ hội của người Sani diễn ra giữa khung cảnh h&ugrave;ng tr&aacute;ng, thơ mộng của đ&aacute;, của nước c&agrave;ng l&agrave;m cho Thạch L&acirc;m th&ecirc;m quyến rũ, để lại cho du kh&aacute;ch một ấn tượng kh&ocirc;ng thể n&agrave;o qu&ecirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hồng Phương</strong></p>', 3, 1, 1090, 0, 1),
(68, 'Học sinh Hà Nội dự kiến nghỉ Tết 11 ngày', 'Ngày 6/1, Sở Giáo dục Đào tạo đã có tờ trình gửi UBND Hà Nội đề xuất thời gian nghỉ Tết Tân Mão của ngành giáo dục là 11 ngày. ', 'hs-gialanh13.jpg', NULL, '<h1 class="Title">Học sinh H&agrave; Nội dự kiến nghỉ Tết 11 ng&agrave;y</h1>\r\n<h2 class="Lead">Ng&agrave;y 6/1, Sở Gi&aacute;o dục Đ&agrave;o tạo đ&atilde; c&oacute; tờ tr&igrave;nh gửi UBND H&agrave; Nội đề xuất thời gian nghỉ Tết T&acirc;n M&atilde;o của ng&agrave;nh gi&aacute;o dục l&agrave; 11 ng&agrave;y.&nbsp;</h2>\r\n<p class="Normal">Theo tờ tr&igrave;nh, c&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh c&aacute;c trường mầm non, tiểu học, THCS, THPT, bổ t&uacute;c văn h&oacute;a, trung t&acirc;m gi&aacute;o dục thường xuy&ecirc;n, trung t&acirc;m gi&aacute;o dục kỹ thuật tổng hợp tr&ecirc;n địa b&agrave;n H&agrave; Nội sẽ được nghỉ Tết 11 ng&agrave;y, từ 28/1 (25 th&aacute;ng Chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng Gi&ecirc;ng).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hs-gialanh13.jpg" border="1" alt="" width="495" height="340" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Học sinh thủ đ&ocirc; đang chịu cảnh gi&aacute; lạnh. Ảnh:&nbsp;<em>L&ecirc; Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ri&ecirc;ng c&aacute;c trường trung cấp chuy&ecirc;n nghiệp được nghỉ 17 ng&agrave;y, từ ng&agrave;y 22/1 (19 th&aacute;ng Chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng Gi&ecirc;ng). C&aacute;n bộ c&ocirc;ng chức, vi&ecirc;n chức của Sở GD&amp;ĐT, c&aacute;c ph&ograve;ng GD&amp;ĐT quận, huyện, thị x&atilde; sẽ đi l&agrave;m v&agrave;o ng&agrave;y thứ bảy (29/1), chủ nhật (30/1) v&agrave; nghỉ Tết từ ng&agrave;y 31/1 đến hết ng&agrave;y 7/2 (8 ng&agrave;y).&nbsp;<br /><br />Sau khi c&oacute; quyết định ph&ecirc; duyệt của UBND H&agrave; Nội, Sở GD&amp;ĐT H&agrave; Nội sẽ c&oacute; th&ocirc;ng b&aacute;o cụ thể về thời gian nghỉ Tết để c&aacute;c nh&agrave; trường, phụ huynh, học sinh thực hiện.</p>\r\n<p class="Normal">Trước đ&oacute;, Thủ tướng cũng đ&atilde; quyết định cho c&ocirc;ng chức, vi&ecirc;n chức cả nước được nghỉ Tết nguy&ecirc;n đ&aacute;n trong 8 ng&agrave;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Đo&agrave;n Loan</strong></p>', 1, 1, 1107, 0, 1),
(69, 'Tổ hợp hỗ trợ giáo dục ở THPT hiện đại nhất thủ đô', 'Sáng 5/1, tổ hợp hỗ trợ giáo dục tiên tiến Amsers’ Town đã được khai trương tại trường THPT chuyên Hà Nội – Amsterdam trong sự hào hứng của học sinh.', 'thu-vien.jpg', NULL, '<h1 class="Title">Tổ hợp hỗ trợ gi&aacute;o dục ở THPT hiện đại nhất thủ đ&ocirc;</h1>\r\n<h2 class="Lead">S&aacute;ng 5/1, tổ hợp hỗ trợ gi&aacute;o dục ti&ecirc;n tiến Amsers&rsquo; Town đ&atilde; được khai trương tại trường THPT chuy&ecirc;n H&agrave; Nội &ndash; Amsterdam trong sự h&agrave;o hứng của học sinh.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="thu-vien.jpg" border="1" alt="To&agrave;n cảnh" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Amsers&rsquo; Town được đặt trong khu&ocirc;n vi&ecirc;n trường H&agrave; Nội &ndash; Amsterdam. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đ&acirc;y l&agrave; nơi cung cấp c&aacute;c dịch vụ phục vụ việc dạy v&agrave; học đối với gi&aacute;o vi&ecirc;n v&agrave; học sinh của nh&agrave; trường như: s&aacute;ch tham khảo, đồ d&ugrave;ng học tập, photocoppy, văn ph&ograve;ng phẩm...</p>\r\n<p class="Normal">Amsers&rsquo; Town được xem như điểm kết nối giữa nh&agrave; trường v&agrave; c&aacute;c nh&agrave; xuất bản lớn, c&oacute; uy t&iacute;n. Được đặt ngay trong khu&ocirc;n vi&ecirc;n trường n&ecirc;n tổ hợp đ&aacute;p ứng nhanh y&ecirc;u cầu của học sinh v&agrave; gi&aacute;o vi&ecirc;n, tăng sự y&ecirc;n t&acirc;m cho phụ huynh.</p>\r\n<p class="Normal">Tổ hợp Amsers&rsquo; Town kh&ocirc;ng chỉ cung cấp c&aacute;c dịch vụ gi&aacute;o dục m&agrave; trong kế hoạch ph&aacute;t triển của m&igrave;nh, sẽ trở th&agrave;nh nơi giao lưu, học tập. Trong tương lai gần, Amsers&rsquo; Town sẽ l&agrave; nơi gặp gỡ giữa c&aacute;c cựu học sinh th&agrave;nh đạt với học sinh trong trường, đồng thời cũng l&agrave; nơi diễn ra c&aacute;c buổi giao lưu với c&aacute;c học giả, chuy&ecirc;n vi&ecirc;n v&agrave; doanh nh&acirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="thu-vien-1.jpg" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Học sinh h&agrave;o hứng với m&ocirc; h&igrave;nh hỗ trợ gi&aacute;o dục mới Amsers&rsquo; Town. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ph&aacute;t biểu tại buổi lễ, thầy L&ecirc; Trọng Tuấn, Ph&oacute; hiệu trưởng nh&agrave; trường cho biết: &ldquo;M&ocirc; h&igrave;nh tổ hợp như Amsers&rsquo; Town l&agrave; một phần thiết yếu của c&aacute;c trường ở những nước ti&ecirc;n tiến nhưng lại l&agrave; lần đầu tại trường phổ th&ocirc;ng ở H&agrave; Nội. THPT chuy&ecirc;n H&agrave; Nội &ndash; Amsterdam tự h&agrave;o l&agrave; đơn vị đầu ti&ecirc;n tạo dựng những gi&aacute; trị gi&aacute;o dục gia tăng cho học sinh v&agrave; mong muốn m&ocirc; h&igrave;nh n&agrave;y c&oacute; thể được nh&acirc;n rộng trong khối c&aacute;c trường phổ th&ocirc;ng ở H&agrave; Nội v&agrave; c&aacute;c địa phương kh&aacute;c...&rdquo;.</p>\r\n<p class="Normal">Trong buổi ra mắt, c&aacute;c đơn vị hợp t&aacute;c đ&atilde; trao 64 m&oacute;n qu&agrave;, bao gồm s&aacute;ch v&agrave; đồ d&ugrave;ng học tập cho c&aacute;n bộ c&aacute;c lớp của trường. NXB Kim Đồng v&agrave; Nh&agrave; s&aacute;ch Long Minh trao tặng thư viện 150 đầu s&aacute;ch.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="amser.jpg" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Rất đ&ocirc;ng học sinh v&agrave; gi&aacute;o vi&ecirc;n đ&atilde; đến dự buổi khai trương tổ hợp Amsers&rsquo; Town. Ảnh:<em>&nbsp;Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tổ hợp Amsers&rsquo; Town l&agrave; kết quả hợp t&aacute;c của THPT chuy&ecirc;n H&agrave; Nội &ndash; Amsterdam với NXB Kim Đồng, NXB Trẻ, Nh&agrave; s&aacute;ch Long Minh v&agrave; c&ocirc;ng ty An Sinh. Trong tương lai, tổ hợp n&agrave;y sẽ tiếp tục đ&oacute;n nhận sự hợp t&aacute;c của c&aacute;c nh&agrave; xuất bản lớn, c&oacute; uy t&iacute;n ở cả trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 2156, 0, 1),
(70, 'Hỗ trợ gần 3.000 vé xe tết cho sinh viên', 'Khoảng 2.700 sinh viên ngoại tỉnh có hoàn cảnh khó khăn, đang học tập tại TP HCM sẽ được tặng vé xe miễn phí về quê trong dịp tết Tân Mão.', 'sinhvien[1].jpg', NULL, '<h1 class="Title">Hỗ trợ gần 3.000 v&eacute; xe tết cho sinh vi&ecirc;n</h1>\r\n<h2 class="Lead">Khoảng 2.700 sinh vi&ecirc;n ngoại tỉnh c&oacute; ho&agrave;n cảnh kh&oacute; khăn, đang học tập tại TP HCM sẽ được tặng v&eacute; xe miễn ph&iacute; về qu&ecirc; trong dịp tết T&acirc;n M&atilde;o.</h2>\r\n<p class="Normal">Đ&acirc;y l&agrave; một trong chuỗi c&aacute;c hoạt động hỗ trợ sinh vi&ecirc;n dịp tết của Trung t&acirc;m hỗ trợ sinh vi&ecirc;n TP HCM. Trong đ&oacute;, dự kiến sẽ d&agrave;nh 1.800 v&eacute; cho sinh vi&ecirc;n ngh&egrave;o c&oacute; hộ khẩu thường tr&uacute; tại c&aacute;c tỉnh miền Trung bị thiệt hại nặng nề trong c&aacute;c đợt b&atilde;o lũ vừa qua. 900 v&eacute; c&ograve;n lại sẽ tặng cho c&aacute;c sinh vi&ecirc;n ngh&egrave;o, sinh vi&ecirc;n khuyết tật, sinh vi&ecirc;n d&acirc;n tộc c&aacute;c tỉnh th&agrave;nh kh&aacute;c trong cả nước.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="sinhvien[1].jpg" border="1" alt="Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết" width="480" height="328" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sinh vi&ecirc;n được hỗ trợ v&eacute; xe trong ng&agrave;y về qu&ecirc; đ&oacute;n tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ph&oacute; gi&aacute;m đốc trung t&acirc;m hỗ trợ sinh vi&ecirc;n Dương Trọng Ph&uacute;c cho biết, so với c&aacute;c năm trước số lượng v&eacute; hỗ trợ năm nay &iacute;t hơn nhiều v&igrave; kh&ocirc;ng c&oacute; c&aacute;c đơn vị v&agrave; doanh nghiệp đồng h&agrave;nh. Đa phần kinh ph&iacute; trong hoạt động n&agrave;y đều do Ủy ban Mặt trận tổ quốc Việt Nam TP HCM hỗ trợ.</p>\r\n<p class="Normal">Số lượng v&eacute; tặng sẽ đưa xuống c&aacute;c trường theo chỉ ti&ecirc;u. Sinh vi&ecirc;n kh&oacute; khăn trong c&aacute;c diện được x&eacute;t tặng v&eacute; xe c&oacute; thể li&ecirc;n hệ trực tiếp tại văn ph&ograve;ng Đo&agrave;n thanh ni&ecirc;n - Hội sinh vi&ecirc;n, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n của trường trước ng&agrave;y 31/12 để tiến h&agrave;nh l&agrave;m hồ sơ.</p>\r\n<p class="Normal">Ngo&agrave;i tặng v&eacute; xe, Trung t&acirc;m hỗ trợ sinh vi&ecirc;n sẽ cung cấp khoảng 3.500 đầu việc với nhiều loại h&igrave;nh theo tuần, ng&agrave;y, giờ... cho c&aacute;c bạn ở lại c&oacute; nhu cầu kiếm th&ecirc;m thu nhập. Chương tr&igrave;nh vui tết xa nh&agrave; với nhiều hoạt động th&uacute; vị cũng sẽ được tổ chức cho những sinh vi&ecirc;n kh&ocirc;ng c&oacute; điều kiện về qu&ecirc;. Đại diện Th&agrave;nh Đo&agrave;n v&agrave; Trung t&acirc;m hỗ trợ sinh vi&ecirc;n cũng sẽ đi thăm hỏi, giao lưu v&agrave; tặng qu&agrave; cho sinh vi&ecirc;n trong KTX của c&aacute;c trường.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 2669, 0, 1),
(72, 'Bộ Giáo dục tri ân nhà giáo ưu tú', 'Sáng nay tại Hà Nội, Bộ Giáo dục và Đào tạo đã tổ chức kỷ niệm 28 năm ngày nhà giáo Việt Nam và gặp gỡ các nhà giáo nhân dân, nhà giáo ưu tú năm 2010.', 'gap_mat_nha_giao_nhan_dan.JPG', NULL, '<h1 class="Title">Bộ Gi&aacute;o dục tri &acirc;n nh&agrave; gi&aacute;o ưu t&uacute;</h1>\r\n<h2 class="Lead">S&aacute;ng nay tại H&agrave; Nội, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo đ&atilde; tổ chức kỷ niệm 28 năm ng&agrave;y nh&agrave; gi&aacute;o Việt Nam v&agrave; gặp gỡ c&aacute;c nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n, nh&agrave; gi&aacute;o ưu t&uacute; năm 2010.</h2>\r\n<p class="Normal">&Ocirc;ng Trần C&ocirc;ng Phong, Chủ tịch c&ocirc;ng đo&agrave;n gi&aacute;o dục Việt Nam khẳng định, nh&agrave; gi&aacute;o lu&ocirc;n lu&ocirc;n được Đảng, nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n t&ocirc;n vinh. Những nh&agrave; gi&aacute;o, c&aacute;n bộ quản l&yacute; gi&aacute;o dục c&oacute; th&agrave;nh t&iacute;ch xuất sắc trong phong tr&agrave;o thi đua y&ecirc;u nước v&agrave; c&aacute;c cuộc vận động của ng&agrave;nh c&agrave;ng được trọng vọng.</p>\r\n<p class="Normal">"Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n, nh&agrave; gi&aacute;o ưu t&uacute; lu&ocirc;n đi đầu trong c&aacute;c phong tr&agrave;o thi đua dạy tốt, học tốt, chăm lo sự nghiệp gi&aacute;o dục, l&agrave; người c&oacute; c&ocirc;ng lao to lớn trong việc n&acirc;ng cao d&acirc;n tr&iacute;, ph&aacute;t triển nguồn nh&acirc;n lực, ph&aacute;t hiện v&agrave; bồi dưỡng nh&acirc;n t&agrave;i cho đất nước", &ocirc;ng Phong n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="gap_mat_nha_giao_nhan_dan.JPG" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&agrave; gi&aacute;o lu&ocirc;n được Đảng, nh&agrave; nước v&agrave; nh&acirc;n d&acirc;n t&ocirc;n vinh.&nbsp;<em>Ảnh: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tham gia buổi gặp mặt, nhiều nh&agrave; gi&aacute;o đ&atilde; nhớ lại những ng&agrave;y đầu gian kh&oacute; trong sự nghiệp trồng người. Nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n Đặng Quang Việt, Hiệu trưởng ĐH T&acirc;y Bắc cho biết, những ng&agrave;y đầu về Cao đẳng T&acirc;y Bắc nhận c&ocirc;ng t&aacute;c, &ocirc;ng gần như ng&atilde; quỵ bởi phải chịu c&aacute;i gi&aacute; lạnh của v&ugrave;ng rừng n&uacute;i trong căn ph&ograve;ng trống hơ trống ho&aacute;c v&agrave; c&aacute;i đ&oacute;i triền mi&ecirc;n.</p>\r\n<p class="Normal">"D&ugrave; vất vả nhưng đổi lại, sinh vi&ecirc;n của t&ocirc;i rất ham học. C&aacute;c em nghe giảng h&aacute;o hức như muốn nuốt lấy lời thầy. Ch&iacute;nh điều đ&oacute; đ&atilde; l&agrave;m t&ocirc;i qu&ecirc;n hết kh&oacute; khăn, cố gắng truyền tải cho c&aacute;c em thật nhiều kiến thức trong mỗi tiết học", thầy Việt n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td class="Normal">Từ năm 1988 đến nay đ&atilde; c&oacute; 11 đợt phong tặng, ghi nhận v&agrave; tuy&ecirc;n dương th&agrave;nh t&iacute;ch xuất sắc cho 488 nh&agrave; gi&aacute;o nh&acirc;n d&acirc;n v&agrave; 6.165 nh&agrave; gi&aacute;o ưu t&uacute; ở tất cả c&aacute;c bậc học tr&ecirc;n cả nước. Số gi&aacute;o vi&ecirc;n đứng lớp tr&ecirc;n cả nước l&agrave; hơn một triệu, trong đ&oacute; gi&aacute;o vi&ecirc;n mầm non 195.852, gi&aacute;o vi&ecirc;n phổ th&ocirc;ng 804.183, gi&aacute;o vi&ecirc;n trung cấp chuy&ecirc;n nghiệp 17.488 người. Giảng vi&ecirc;n trong c&aacute;c trường đại học, cao đẳng c&oacute; 70.558 người, trong đ&oacute; 277 GS, 1.925 PGS, 7.104 TS v&agrave; 26.715 thạc sĩ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 843, 0, 1),
(74, 'Vinh danh 121 nhà giáo trẻ tiêu biểu TP HCM', 'Những nhà giáo điển hình về đạo đức, xuất sắc trong chuyên môn, nghiên cứu và nhiệt tình cống hiến cho xã hội được Thành đoàn TP HCM tuyên dương đêm 14/11, tại Nhà văn hóa thanh niên.', 'giang-vien-tre-1.jpg', NULL, '<h1 class="Title">Vinh danh 121 nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu TP HCM</h1>\r\n<h2 class="Lead">Những nh&agrave; gi&aacute;o điển h&igrave;nh về đạo đức, xuất sắc trong chuy&ecirc;n m&ocirc;n, nghi&ecirc;n cứu v&agrave; nhiệt t&igrave;nh cống hiến cho x&atilde; hội được Th&agrave;nh đo&agrave;n TP HCM tuy&ecirc;n dương đ&ecirc;m 14/11, tại Nh&agrave; văn h&oacute;a thanh ni&ecirc;n.</h2>\r\n<p class="Normal">Đ&acirc;y l&agrave; một hoạt động nhằm ch&agrave;o mừng ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20/11 được Th&agrave;nh đo&agrave;n TP HCM thực hiện thường ni&ecirc;n từ năm 2008.</p>\r\n<p class="Normal">Để khuyến kh&iacute;ch sự nỗ lực phấn đấu của c&aacute;c nh&agrave; gi&aacute;o trẻ, năm nay Ban thường vụ th&agrave;nh đo&agrave;n ch&iacute;nh thức ban h&agrave;nh quy chế, n&acirc;ng danh hiệu "Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu" l&ecirc;n th&agrave;nh "Giải thưởng Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="giang-vien-tre-1.jpg" border="1" alt="C&aacute;c Nh&agrave; gi&aacute;o trẻ được vinh danh" width="480" height="335" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gương c&aacute;c "Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu" được vinh danh. Ảnh:&nbsp;<em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Từ 369 hồ sơ đề cử của c&aacute;c trường, ph&ograve;ng gi&aacute;o dục v&agrave; c&aacute;c cơ sở đo&agrave;n tr&ecirc;n địa b&agrave;n TP HCM, sau khi x&eacute;t chọn theo quy chế, Hội đồng giải thưởng chọn ra 121 gương nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu để tuy&ecirc;n dương. Trong đ&oacute; c&oacute; nhiều nh&agrave; gi&aacute;o đ&atilde; ba năm liền đạt danh hiệu n&agrave;y như: thầy Đặng Tất Dũng, thầy V&otilde; Trung T&iacute;n (giảng vi&ecirc;n ĐH Luật TP HCM), c&ocirc; Trần Thị Nguyệt Sương (giảng vi&ecirc;n ĐH T&ocirc;n Đức Thắng)...</p>\r\n<p class="Normal">Đặc biệt trong c&aacute;c gương nh&agrave; gi&aacute;o trẻ năm nay c&ograve;n c&oacute; thầy Phan Thanh Sơn Nam, 33 tuổi l&agrave; một trong những Ph&oacute; gi&aacute;o sư trẻ tuổi nhất của Việt Nam.</p>\r\n<p class="Normal">Kh&ocirc;ng chỉ l&agrave; những gi&aacute;o vi&ecirc;n ti&ecirc;u biểu về đạo đức lối sống trong trường học, l&agrave; người c&oacute; nhiều s&aacute;ng kiến trong giảng dạy, c&aacute;c nh&agrave; gi&aacute;o trẻ c&ograve;n l&agrave; những chiến sĩ t&igrave;nh nguyện, c&aacute;n bộ đo&agrave;n giỏi, tham gia nhiều hoạt động x&atilde; hội như: m&ugrave;a h&egrave; xanh, lập học bổng hỗ trợ cho c&aacute;c em học sinh ngh&egrave;o hiếu học...</p>\r\n<p class="Normal">"Nh&agrave; gi&aacute;o trẻ ti&ecirc;u biểu" l&agrave; danh hiệu chỉ d&agrave;nh cho c&aacute;c thầy c&ocirc; gi&aacute;o c&oacute; tuổi đời kh&ocirc;ng qu&aacute; 35.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n</strong></p>', 1, 1, 2508, 0, 1),
(75, '2.000 tỷ đồng phát triển trường chuyên', 'Số tiền này dành để tăng cường đầu tư cơ sở vật chất, trang thiết bị dạy học, đồng thời phát triển đội ngũ cán bộ quản lý, giáo viên trong các trường chuyên.', 'chuyen1.jpg', NULL, '<h1 class="Title">2.000 tỷ đồng ph&aacute;t triển trường chuy&ecirc;n</h1>\r\n<h2 class="Lead">Số tiền n&agrave;y d&agrave;nh để tăng cường đầu tư cơ sở vật chất, trang thiết bị dạy học, đồng thời ph&aacute;t triển đội ngũ c&aacute;n bộ quản l&yacute;, gi&aacute;o vi&ecirc;n trong c&aacute;c trường chuy&ecirc;n.</h2>\r\n<p class="Normal">&Ocirc;ng Vũ Đ&igrave;nh Chuẩn, Vụ trưởng Gi&aacute;o dục trung học (Bộ GD&amp;ĐT) cho biết, đề &aacute;n ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n giai đoạn 2010-2020 đặt ra mục ti&ecirc;u mỗi tỉnh, th&agrave;nh phố sẽ c&oacute; &iacute;t nhất một trường THPT chuy&ecirc;n, tổng số học sinh chuy&ecirc;n chiếm khoảng 2% số học sinh phổ th&ocirc;ng của từng tỉnh th&agrave;nh.</p>\r\n<p class="Normal">Đến năm 2015, 100% trường THPT chuy&ecirc;n đạt chuẩn quốc gia, 100% c&aacute;n bộ quản l&yacute;, gi&aacute;o vi&ecirc;n giỏi về chuy&ecirc;n m&ocirc;n, nghiệp vụ, sử dụng th&ocirc;ng thạo tin học v&agrave; thiết bị dạy học hiện đại...</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chuyen1.jpg" alt="" width="495" height="362" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mỗi tỉnh th&agrave;nh sẽ c&oacute; &iacute;t nhất một trường chuy&ecirc;n.&nbsp;<em>Ảnh: Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Để đạt được mục ti&ecirc;u, đề &aacute;n đ&atilde; đặt ra 6 nhiệm vụ v&agrave; giải ph&aacute;p trong đ&oacute; tăng cường c&ocirc;ng t&aacute;c quản l&yacute; v&agrave; hợp t&aacute;c quốc tế trong việc ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n. Đề &aacute;n sẽ thực hiện theo 2 giai đoạn. Giai đoạn 1 (2010-2015) tập trung tạo cơ sở, nền tảng ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n. Giai đoạn 2 (2015-2020) ch&uacute; trọng ph&aacute;t triển hệ thống trường THPT chuy&ecirc;n một c&aacute;ch vững chắc.</p>\r\n<p class="Normal">Thứ trưởng Bộ GD&amp;ĐT Nguyễn Vinh Hiển l&agrave; trưởng ban điều h&agrave;nh đề &aacute;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 1, 1, 2475, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(76, 'Học viện đầu tiên công khai tuyển phó giám đốc', 'Học viện Công nghệ Bưu chính Viễn thông (Tập đoàn Bưu chính Viễn thông Việt Nam), vừa thông báo mời giới thiệu hoặc ứng cử 2 chức danh Phó giám đốc làm việc tại Hà Nội, nhằm đáp ứng sự phát triển mạnh của học viện.', 'Hoc-vien.jpg', NULL, '<p class="Title">Học viện đầu ti&ecirc;n c&ocirc;ng khai tuyển ph&oacute; gi&aacute;m đốc</p>\r\n<p class="Lead">Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng (Tập đo&agrave;n Bưu ch&iacute;nh Viễn th&ocirc;ng Việt Nam), vừa th&ocirc;ng b&aacute;o mời giới thiệu hoặc ứng cử 2 chức danh Ph&oacute; gi&aacute;m đốc l&agrave;m việc tại H&agrave; Nội, nhằm đ&aacute;p ứng sự ph&aacute;t triển mạnh của học viện.</p>\r\n<p class="Normal">Theo đ&oacute;, một Ph&oacute; gi&aacute;m đốc sẽ đảm nhiệm việc quản l&yacute; đ&agrave;o tạo, một Ph&oacute; gi&aacute;m đốc quản l&yacute; nghi&ecirc;n cứu khoa học v&agrave; chuyển giao c&ocirc;ng nghệ của học viện. Y&ecirc;u cầu của trường l&agrave; ứng vi&ecirc;n nữ kh&ocirc;ng qu&aacute; 40 tuổi v&agrave; ứng vi&ecirc;n nam kh&ocirc;ng qu&aacute; 50 tuổi, trường hợp đặc biệt cũng kh&ocirc;ng được qu&aacute; 45 (nữ) v&agrave; 55 tuổi (nam).</p>\r\n<p class="Normal">Những ứng vi&ecirc;n đang l&agrave;m Hiệu trưởng, Hiệu ph&oacute;, Trưởng khoa đ&agrave;o tạo đại học hoặc sau đại học c&aacute;c trường; Gi&aacute;m đốc, Ph&oacute; gi&aacute;m đốc Học viện (c&oacute; đ&agrave;o tạo đại học); Viện trưởng, Viện ph&oacute; Viện nghi&ecirc;n cứu trực thuộc cấp Bộ trở l&ecirc;n; Vụ trưởng, Vụ ph&oacute; c&aacute;c vụ thuộc Bộ; Trưởng ban, Ph&oacute; trưởng ban chức năng thuộc VNPT; Gi&aacute;m đốc, Ph&oacute; gi&aacute;m đốc c&aacute;c doanh nghiệp hoạt động trong lĩnh vực khoa học c&ocirc;ng nghệ v&agrave; đ&agrave;o tạo... đủ ti&ecirc;u chuẩn ứng cử chức danh Ph&oacute; gi&aacute;m đốc học viện.<br /><br />B&ecirc;n cạnh y&ecirc;u cầu phải c&oacute; học vị Tiến sĩ trở l&ecirc;n về khoa học kỹ thuật (đ&atilde; qua bồi dưỡng về quản l&yacute; kinh tế) hoặc quản l&yacute; kinh tế hoặc quản l&yacute; đ&agrave;o tạo, sử dụng th&agrave;nh thạo &iacute;t nhất một ngoại ngữ th&ocirc;ng dụng (ưu ti&ecirc;n tiếng Anh), c&aacute;c ứng vi&ecirc;n phải c&oacute; phẩm chất ch&iacute;nh trị, đạo đức tốt; c&oacute; uy t&iacute;n trong giới khoa học, gi&aacute;o dục; T&acirc;m huyết với sự nghiệp gi&aacute;o dục, đ&agrave;o tạo; c&oacute; tầm nh&igrave;n v&agrave; năng lực triển vọng đ&oacute;ng g&oacute;p được tốt ở cương vị Ph&oacute; gi&aacute;m đốc học viện...</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Hoc-vien.jpg" alt="Ảnh:" width="480" height="348" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cơ sở của Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng ở H&agrave; Nội. Ảnh:&nbsp;<em>Mai Hạnh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo PGS TS Ho&agrave;ng Minh, Gi&aacute;m đốc học viện, việc th&ocirc;ng b&aacute;o rộng r&atilde;i n&agrave;y vừa để mở rộng cơ hội lựa chọn, t&igrave;m kiếm c&aacute;n bộ, vừa g&oacute;p phần n&acirc;ng cao h&igrave;nh ảnh v&agrave; n&acirc;ng cao năng lực cạnh tranh nguồn nh&acirc;n lực của học viện. Đồng thời, học viện cũng cần được bổ sung th&ecirc;m c&aacute;c nh&acirc;n tố mới trong đội ngũ c&aacute;n bộ quản l&yacute; khi chuyển sang hoạt động theo cơ chế tự chủ, tự chịu tr&aacute;ch nhiệm.</p>\r\n<p class="Normal">"Ch&uacute;ng t&ocirc;i quan t&acirc;m v&agrave; ưu ti&ecirc;n c&aacute;c ứng vi&ecirc;n c&oacute; chức danh gi&aacute;o sư, ph&oacute; gi&aacute;o sư v&agrave; khuyến kh&iacute;ch c&aacute;c nh&agrave; gi&aacute;o, nh&agrave; khoa học gốc Việt ứng cử. C&aacute;c ứng vi&ecirc;n sẽ được l&agrave;m việc trong m&ocirc;i trường năng động, s&aacute;ng tạo, tr&acirc;n trọng c&aacute;n bộ tr&iacute; thức, với nhiều cơ hội để cống hiến v&agrave; ph&aacute;t triển sự nghiệp", &ocirc;ng Minh nhấn mạnh.</p>\r\n<p class="Normal">Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng nhận hồ sơ giới thiệu ứng vi&ecirc;n hoặc tự ứng cử đến hết ng&agrave;y 30/11. Đ&acirc;y l&agrave; cơ sở gi&aacute;o dục đại học c&ocirc;ng lập đầu ti&ecirc;n c&ocirc;ng bố "tuyển" chức danh l&atilde;nh đạo. Trước đ&oacute;, Bộ GD&amp;ĐT cũng c&ocirc;ng bố "tuyển" ứng vi&ecirc;n cho chức danh Thứ trưởng, Vụ trưởng. V&agrave; Sở GD&amp;ĐT Đ&agrave; Nẵng, Bắc Giang, Ph&uacute; Thọ cũng đ&atilde; tổ chức thi tuyển c&aacute;n bộ chức danh hiệu trưởng, hiệu ph&oacute; của h&agrave;ng chục trường.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="99%">\r\n<tbody>\r\n<tr>\r\n<td bgcolor="#dcdcba">Học viện C&ocirc;ng nghệ Bưu ch&iacute;nh Viễn th&ocirc;ng hiện c&oacute; 2 cơ sở đ&agrave;o tạo tại H&agrave; Nội v&agrave; TP HCM với 12 khoa đ&agrave;o tạo đại học v&agrave; sau đại học c&aacute;c ng&agrave;nh: Điện tử - Viễn th&ocirc;ng, Điện - Điện tử, C&ocirc;ng nghệ th&ocirc;ng tin, Quản trị Kinh doanh, T&agrave;i ch&iacute;nh Kế to&aacute;n c&ugrave;ng gần 26 ng&agrave;n sinh vi&ecirc;n c&aacute;c hệ đang theo học. Ban gi&aacute;m đốc Học viện hiện gồm một Gi&aacute;m đốc v&agrave; 2 Ph&oacute; gi&aacute;m đốc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 948, 0, 1),
(77, 'Những tân sinh viên mồ côi, hiếu học', 'Mẹ mất năm 2007, 2 năm sau bố cũng ra đi, khiến 6 anh chị em của Nguyễn Huy Chiến (Hà Tĩnh) sau giờ học lại phải đi làm để kiếm sống. Nhờ nỗ lực vươn lên, Chiến vừa trở thành tân sinh viên của ĐH Điện lực Hà Nội.', 'Sinh-vien-ngheo.jpg', NULL, '<p class="Title">Những t&acirc;n sinh vi&ecirc;n mồ c&ocirc;i, hiếu học</p>\r\n<p class="Lead">Mẹ mất năm 2007, 2 năm sau bố cũng ra đi, khiến 6 anh chị em của Nguyễn Huy Chiến (H&agrave; Tĩnh) sau giờ học lại phải đi l&agrave;m để kiếm sống. Nhờ nỗ lực vươn l&ecirc;n, Chiến vừa trở th&agrave;nh t&acirc;n sinh vi&ecirc;n của ĐH Điện lực H&agrave; Nội.</p>\r\n<p class="Normal">Mồ c&ocirc; cả cha lẫn mẹ, Nguyễn Th&agrave;nh Sơn v&agrave; em g&aacute;i (qu&ecirc; H&agrave; Tĩnh) lớn l&ecirc;n nhờ sự chăm s&oacute;c của &ocirc;ng b&agrave; ngoại đ&atilde; ngo&agrave;i 70 tuổi. Cả gia đ&igrave;nh sống nhờ v&agrave;o mấy s&agrave;o ruộng n&ecirc;n cuộc sống gặp nhiều kh&oacute; khăn, vất vả. Hiểu được điều đ&oacute;, cộng với ước mơ trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n giỏi, Sơn đ&atilde; cố gắng học tập tốt v&agrave; đỗ v&agrave;o khoa C&ocirc;ng nghệ Th&ocirc;ng tin (ĐH Thủy lợi) trong kỳ tuyển sinh vừa qua.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, niềm hạnh ph&uacute;c kh&ocirc;ng trọn vẹn v&igrave; hai &ocirc;ng b&agrave; ở tuổi gần đất xa trời v&agrave; cậu t&acirc;n sinh vi&ecirc;n đang phải đối mặt với g&aacute;nh nặng cơm &aacute;o gạo tiền khi Sơn l&ecirc;n đường ra H&agrave; Nội học tập, nu&ocirc;i dưỡng ước mơ trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n.</p>\r\n<p class="Normal">Cũng mồ c&ocirc;i từ nhỏ, cuộc sống của Đỗ Mạnh Tiến (Thanh H&oacute;a) dựa chủ yếu v&agrave;o sự gi&uacute;p đỡ, cưu mang của họ h&agrave;ng v&agrave; người th&acirc;n. Kh&ocirc;ng đầu h&agrave;ng trước số phận, Tiến đ&atilde; nỗ lực học tập v&agrave; thi đỗ v&agrave;o khoa C&ocirc;ng nghệ Th&ocirc;ng tin (ĐH Thủy lợi). Cậu t&acirc;n sinh vi&ecirc;n cho hay, sẽ cố gắng học thật tốt để kh&ocirc;ng phụ c&ocirc;ng chăm s&oacute;c của mọi người, để bố mẹ em y&ecirc;n l&ograve;ng, đồng thời để cuộc sống sau n&agrave;y đỡ vất vả.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Sinh-vien-ngheo.jpg" alt="Ảnh: Tiến Dũng." width="480" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c t&acirc;n sinh vi&ecirc;n chia sẻ ho&agrave;n cảnh kh&oacute; khăn của m&igrave;nh. Ảnh:&nbsp;<em>Tiến Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Gia đ&igrave;nh c&oacute; 6 anh chị em, mẹ mất năm 2007, 2 năm sau bố cũng ra đi, tất cả anh chị em của Nguyễn Huy Chiến (H&agrave; Tĩnh) đều đang trong độ tuổi đi học, chưa c&oacute; việc l&agrave;m. Những buổi kh&ocirc;ng đi học, c&aacute;c anh chị em đều phải đi l&agrave;m để kiếm sống v&agrave; chăm s&oacute;c đứa em bị dị tật bẩm sinh. Cuộc sống của gia đ&igrave;nh cậu t&acirc;n sinh vi&ecirc;n ng&agrave;nh Hệ thống điện - ĐH Điện lực hiện chủ yếu nhờ v&agrave;o sự gi&uacute;p đỡ của b&agrave; con l&agrave;ng x&oacute;m n&ecirc;n ho&agrave;n cảnh v&ocirc; c&ugrave;ng kh&oacute; khăn.</p>\r\n<p class="Normal">Mồ c&ocirc;i cha mẹ từ khi c&ograve;n nhỏ, Đinh Văn Ngọc (Cao Bằng) được nu&ocirc;i dưỡng v&agrave; học tập tại Trung t&acirc;m Bảo trợ x&atilde; hội tỉnh. D&ugrave; ho&agrave;n cảnh kh&oacute; khăn nhưng Ngọc vẫn nỗ lực v&agrave; thi đỗ ng&agrave;nh Chăn nu&ocirc;i của ĐH Th&aacute;i Nguy&ecirc;n.</p>\r\n<p class="Normal">Kh&ocirc;ng c&oacute; cha, mẹ t&acirc;m thần bất ổn, l&agrave;m việc tự do thu nhập thấp, n&ecirc;n năm 10 tuổi, B&ugrave;i Văn Hải (Từ Li&ecirc;m, H&agrave; Nội) được đưa v&agrave;o nu&ocirc;i dưỡng trong l&agrave;ng trẻ mồ c&ocirc;i Birla. Nhờ cố gắng học tập, 8 năm sau Hải trở th&agrave;nh cậu học sinh duy nhất của trường đỗ đại học năm 2009- 2010. Hiện, cậu sinh vi&ecirc;n ng&agrave;nh Cơ kh&iacute; (ĐH Giao th&ocirc;ng Vận tải) trở về nh&agrave; để tiện chăm s&oacute;c &ocirc;ng b&agrave; ngoại tuổi đ&atilde; cao.</p>\r\n<p class="Normal">Vắng sự chăm s&oacute;c của cha mẹ từ khi c&ograve;n nhỏ nhưng 4 anh chị em của Nguyễn Đức Hưng (Thanh H&oacute;a) vẫn đ&ugrave;m bọc, nu&ocirc;i nhau kh&ocirc;n lớn. Nỗ lực n&agrave;y đ&atilde; gi&uacute;p Hưng được 21 điểm v&agrave; đỗ v&agrave;o ng&agrave;nh B&aacute;c sĩ Đa khoa của ĐH Y H&agrave; Nội.</p>\r\n<p class="Normal">May mắn hơn nhiều bạn kh&aacute;c, d&ugrave; thiếu bố nhưng Nguyễn Mộng Dung vẫn c&ograve;n c&oacute; mẹ v&agrave; &ocirc;ng b&agrave; ngoại. Ng&agrave;y ng&agrave;y người mẹ bị bệnh v&agrave; dị tật bẩm sinh miệt m&agrave;i ngồi b&ecirc;n chiếc m&aacute;y kh&acirc;u kiếm ch&uacute;t tiền trang trải cuộc sống cho gia đ&igrave;nh 4 người. Tuy nhi&ecirc;n, gần đ&acirc;y mẹ Dung gần như kh&ocirc;ng đi lại được, vận động cũng kh&oacute; khăn khiến kinh tế gia đ&igrave;nh c&agrave;ng th&ecirc;m eo hẹp.</p>\r\n<p class="Normal">Để phụ gi&uacute;p mẹ v&agrave; &ocirc;ng b&agrave; ngoại gi&agrave; yếu, c&ocirc; t&acirc;n sinh vi&ecirc;n ng&agrave;nh Sư phạm Địa l&yacute; (ĐH Th&aacute;i Nguy&ecirc;n) vừa đi học vừa đi l&agrave;m th&ecirc;m để kiếm tiền ăn học.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="494">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" bgcolor="#dfdfdf">Tr&ecirc;n đ&acirc;y l&agrave; 7 trong số 101 t&acirc;n sinh vi&ecirc;n ph&iacute;a Bắc c&oacute; ho&agrave;n cảnh kh&oacute; khăn vừa được b&aacute;o Tuổi trẻ TP HCM c&ugrave;ng ng&acirc;n h&agrave;ng HSBC trao học bổng "Tiếp sức đến trường" (trị gi&aacute; 5 triệu đồng một suất). Năm nay, 1.000 học sinh, sinh vi&ecirc;n sẽ được nhận học bổng "Tiếp sức đến trường".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 207, 0, 1),
(78, '130.000 học sinh vùng lũ trở lại trường  ', 'Hôm nay gần 100.000 học sinh ở Quảng Bình và hơn 30.000 em ở huyện Hương Khê (Hà Tĩnh) đã đi học trở lại sau một tuần nghỉ tránh lũ. Hiện nơi này vẫn còn 24.000 em chưa thể đến lớp do trường hư hỏng quá nặng.', 'truong.jpg', NULL, '<p class="Title">130.000 học sinh v&ugrave;ng lũ trở lại trường</p>\r\n<p class="Lead">H&ocirc;m nay gần 100.000 học sinh ở Quảng B&igrave;nh v&agrave; hơn 30.000 em ở huyện Hương Kh&ecirc; (H&agrave; Tĩnh) đ&atilde; đi học trở lại sau một tuần nghỉ tr&aacute;nh lũ. Hiện nơi n&agrave;y vẫn c&ograve;n 24.000 em chưa thể đến lớp do trường hư hỏng qu&aacute; nặng.</p>\r\n<p class="Normal">Sở Gi&aacute;o dục v&agrave; Đ&agrave;o tạo Quảng B&igrave;nh cho biết, trong đợt lũ vừa qua, to&agrave;n bộ trường học của tỉnh bị ngập, 5 em bị lũ cuốn chết, một số em bị thương. Khi nước r&uacute;t, c&aacute;c trường đ&atilde; huy động học sinh, thanh ni&ecirc;n t&igrave;nh nguyện dọn dẹp b&ugrave;n đất, lau dọn b&agrave;n ghế, lớp học&hellip; Đến s&aacute;ng 12/10, gần 100.000 học sinh c&aacute;c cấp bắt đầu đi học trở lại.</p>\r\n<p class="Normal">Mặc d&ugrave; vậy, hiện nay Quảng B&igrave;nh c&ograve;n hơn 50 trường với 14.000 em, chủ yếu l&agrave; học sinh mầm non v&agrave; tiểu học vẫn chưa thể đến lớp do trường bị hư hỏng qu&aacute; nặng, cần thời gian sửa chữa.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="truong.jpg" alt="" width="495" height="337" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Học sinh v&ugrave;ng rốn lũ x&atilde; H&agrave; Linh, huyện Hương Kh&ecirc;, đến lớp trong ngập ngụa b&ugrave;n đất. Ảnh:&nbsp;<em>Nguy&ecirc;n Khoa.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại rốn lũ Hương Kh&ecirc; (H&agrave; Tĩnh), sau một tuần nghỉ học, từ h&ocirc;m qua, hơn 30.000 học sinh THPT của huyện n&agrave;y đ&atilde; trở lại trường. Hiện c&ograve;n gần 10.000 em mầm non v&agrave; tiểu học kh&aacute;c vẫn chưa thể đến lớp.</p>\r\n<p class="Normal">Lũ r&uacute;t, song ng&agrave;nh gi&aacute;o dục hai tỉnh H&agrave; Tĩnh, Quảng B&igrave;nh đang gặp rất nhiều kh&oacute; khăn. Do bị ng&acirc;m nước qu&aacute; l&acirc;u, trường lớp, b&agrave;n ghế, sổ s&aacute;ch, s&aacute;ch gi&aacute;o khoa, thiết bị dạy học, tủ hồ sơ, nh&agrave; c&ocirc;ng vụ của gi&aacute;o vi&ecirc;n&hellip; đều bị hư hỏng nghi&ecirc;m trọng. Ước t&iacute;nh, đợt lũ đ&atilde; khiến ng&agrave;nh gi&aacute;o dục Quảng B&igrave;nh thiệt hại 84 tỷ đồng, H&agrave; Tĩnh hơn 50 tỷ đồng...</p>\r\n<p class="Normal">Điều khiến ng&agrave;nh gi&aacute;o dục hai tỉnh lo ngại nhất hiện nay l&agrave; t&igrave;nh trạng học sinh bỏ học. Sau lũ h&agrave;ng chục ngh&igrave;n gia đ&igrave;nh đ&atilde; bị mất nh&agrave; cửa, t&agrave;i sản v&agrave; kế sinh nhai, việc cho con đến trường trở n&ecirc;n v&ocirc; c&ugrave;ng kh&oacute; khăn.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#fff2e6">\r\n<tbody>\r\n<tr>\r\n<td>Theo Ban chỉ đạo ph&ograve;ng chống lụt b&atilde;o trung ương, đợt mưa lũ từ ng&agrave;y 1 đến 5/10 tại miền Trung đ&atilde; cướp đi 66 sinh mạng, l&agrave;m 18 người mất t&iacute;ch, thiệt hại vật chất ước t&iacute;nh 2.750 tỷ đồng. Trong đ&oacute;, ri&ecirc;ng Quảng B&igrave;nh c&oacute; 45 người chết, 16 người mất t&iacute;ch, thiệt hại 1.580 tỷ đồng; H&agrave; Tĩnh c&oacute; 12 người chết, thiệt hại 845 tỷ đồng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Nguy&ecirc;n Khoa</strong></p>', 1, 1, 2106, 0, 1),
(80, 'Tuyển 80 chỉ tiêu liên thông đại học ngành tiếng Anh', 'Đại học Hoa Sen công bố 80 chỉ tiêu ngành tiếng Anh (chuyên ngành Anh văn thương mại) dành cho chương trình liên thông từ cao đẳng lên đại học khóa 10.2 của niên học 2010 - 2011.', '1509_400280.jpg', NULL, '<p class="Title">Tuyển 80 chỉ ti&ecirc;u li&ecirc;n th&ocirc;ng đại học ng&agrave;nh tiếng Anh</p>\r\n<p class="Lead">Đại học Hoa Sen c&ocirc;ng bố 80 chỉ ti&ecirc;u ng&agrave;nh tiếng Anh (chuy&ecirc;n ng&agrave;nh Anh văn thương mại) d&agrave;nh cho chương tr&igrave;nh li&ecirc;n th&ocirc;ng từ cao đẳng l&ecirc;n đại học kh&oacute;a 10.2 của ni&ecirc;n học 2010 - 2011.</p>\r\n<p class="Normal">Chương tr&igrave;nh d&agrave;nh cho c&aacute;c bạn đ&atilde; c&oacute; bằng cao đẳng ch&iacute;nh quy c&ugrave;ng ng&agrave;nh đ&agrave;o tạo, th&acirc;m ni&ecirc;n c&ocirc;ng t&aacute;c từ một năm trở l&ecirc;n t&iacute;nh đến ng&agrave;y dự thi. Sinh vi&ecirc;n tốt nghiệp loại kh&aacute;, giỏi được miễn quy định th&acirc;m ni&ecirc;n c&ocirc;ng t&aacute;c.</p>\r\n<p class="Normal">Chương tr&igrave;nh học theo lộ tr&igrave;nh học chế t&iacute;n chỉ k&eacute;o d&agrave;i 3-4 học kỳ, thời gian linh động (c&oacute; thể vừa l&agrave;m vừa học). Giảng vi&ecirc;n được đ&agrave;o tạo từ nước ngo&agrave;i, học h&agrave;m từ thạc sĩ trở l&ecirc;n, c&oacute; kỹ năng sư phạm lẫn kinh nghiệm thực tế. Ngo&agrave;i ra, chương tr&igrave;nh cũng hỗ trợ vay vốn học tập v&agrave; tạo cơ hội việc l&agrave;m sau khi ra trường.</p>\r\n<p class="Normal">Sinh vi&ecirc;n tốt nghiệp được nhận bằng cử nh&acirc;n tiếng Anh, nằm trong hệ thống văn bằng quốc gia, c&oacute; thể học l&ecirc;n c&aacute;c bậc cao hơn hoặc chuyển tiếp sang chương tr&igrave;nh cử nh&acirc;n kinh doanh quốc tế (hợp t&aacute;c giữa ĐH Hoa Sen v&agrave; ĐH Paris Est &ndash; Ph&aacute;p) để nhận bằng gi&aacute; trị thuộc chuẩn LMD (Licence &ndash; Master &ndash; Doctorat), c&ocirc;ng nhận khắp ch&acirc;u &Acirc;u.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1509_400280.jpg" border="1" alt="" width="400" height="280" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Thời gian nhận hồ sơ dự tuyển của kỳ thi li&ecirc;n th&ocirc;ng kh&oacute;a 10.2 k&eacute;o d&agrave;i đến hết ng&agrave;y 20/11. Th&iacute; sinh phải tham dự thi tuyển sinh đầu v&agrave;o với hai m&ocirc;n, bao gồm Anh văn cơ sở (90 ph&uacute;t) v&agrave; Anh văn chuy&ecirc;n ng&agrave;nh kinh doanh (120 ph&uacute;t) v&agrave;o ng&agrave;y thứ bảy (27/11). Để tạo nhiều cơ hội cho th&iacute; sinh, đại học Hoa Sen sẽ tổ chức lớp &ocirc;n tập v&agrave;o c&aacute;c buổi tối trong tuần v&agrave; ng&agrave;y thứ bảy, chủ nhật. Hạn ch&oacute;t nhận đăng k&yacute; lớp &ocirc;n tập l&agrave; ng&agrave;y 25/9.</p>\r\n<p class="Normal">Th&ocirc;ng tin chi tiết, li&ecirc;n hệ ph&ograve;ng tuyển sinh, ĐH Hoa Sen &ndash; 93 Cao Thắng, quận 3, TP HCM; điện thoại (08) 3830.1877 (số nội bộ 131,154) hoặc tr&ecirc;n website<a class="Normal" href="http://www.hoasen.edu.vn/">http://www.hoasen.edu.vn/</a>.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="489" align="center" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td class="Normal">Hội thảo giới thiệu chương tr&igrave;nh li&ecirc;n th&ocirc;ng cao đẳng - đại học 2010 (các ngành: quản trị kinh doanh, k&ecirc;́ toán, Anh văn thương mại, c&ocirc;ng ngh&ecirc;̣ th&ocirc;ng tin và Quản trị Du lịch Nh&agrave; h&agrave;ng Kh&aacute;ch sạn) được tổ chức v&agrave;o thứ tư (ng&agrave;y 22/9) tại 93 Cao Thắng, quận 3, TP HCM. Đăng k&yacute; tại&nbsp;<a class="Normal" href="mailto:nntanh@hoasen.edu.vn">nntanh@hoasen.edu.vn</a>&nbsp;hoặc điện thoại: 08 3830 1877 Ext. 114, 141, 154.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Đại học Hoa Sen</em>)</p>', 1, 1, 2829, 0, 1),
(82, 'Phát hành sách về các nhà Toán học và Ngô Bảo Châu', 'Cuốn sách đầu tiên tổng hợp thông tin đầy đủ nhất về các nhà Toán học nổi tiếng, trong đó, có 12 trang viết về giáo sư Ngô Bảo Châu sẽ được phát hành tại buổi đón tiếp GS Ngô Bảo Châu tối 29/8.', '2GS-Ngo.jpg', NULL, '<p class="Title">Ph&aacute;t h&agrave;nh s&aacute;ch về c&aacute;c nh&agrave; To&aacute;n học v&agrave; Ng&ocirc; Bảo Ch&acirc;u</p>\r\n<p class="Lead">Cuốn s&aacute;ch đầu ti&ecirc;n tổng hợp th&ocirc;ng tin đầy đủ nhất về c&aacute;c nh&agrave; To&aacute;n học nổi tiếng, trong đ&oacute;, c&oacute; 12 trang viết về gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u sẽ được ph&aacute;t h&agrave;nh tại buổi đ&oacute;n tiếp GS Ng&ocirc; Bảo Ch&acirc;u tối 29/8.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="right">\r\n<tbody>\r\n<tr>\r\n<td><img src="2GS-Ngo.jpg" alt="" width="250" height="183" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">GS Ng&ocirc; Việt Trung - Viện trưởng Viện To&aacute;n học v&agrave; GS Ng&ocirc; Bảo Ch&acirc;u.Ảnh:&nbsp;<em>Nguyễn Việt Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chiều 27/8, Ph&oacute; tổng gi&aacute;m đốc NXB Gi&aacute;o dục Trần Qu&yacute; Thao cho<em>VnExpress.net</em>&nbsp;biết, ng&agrave;y 29/8 sẽ ph&aacute;t h&agrave;nh cuốn s&aacute;ch "Kể chuyện về To&aacute;n v&agrave; c&aacute;c nh&agrave; To&aacute;n học" của Việt Nam cũng như thế giới, d&agrave;y hơn 300 trang với những mẩu chuyện d&iacute; dỏm về c&aacute;c nh&agrave; To&aacute;n học.</p>\r\n<p class="Normal">"Đ&acirc;y l&agrave; cuốn s&aacute;ch đầu ti&ecirc;n tổng hợp th&ocirc;ng tin đầy đủ nhất về c&aacute;c nh&agrave; To&aacute;n học nổi tiếng, trong đ&oacute;, c&oacute; 12 trang viết về gi&aacute;o sư Ng&ocirc; Bảo Ch&acirc;u để tặng những người y&ecirc;u th&iacute;ch To&aacute;n v&agrave; Ng&ocirc; Bảo Ch&acirc;u", &ocirc;ng Thao n&oacute;i.</p>\r\n<p class="Normal">Cũng theo &ocirc;ng Ph&oacute; tổng gi&aacute;m đốc, cuốn s&aacute;ch n&agrave;y được GS Nguyễn Duy Tiến - giảng vi&ecirc;n ĐH Khoa học Tự nhi&ecirc;n (ĐH Quốc gia H&agrave; Nội) chuẩn bị từ 6 th&aacute;ng trước v&agrave; được GS TSKH Trần Văn Nhung (Nguy&ecirc;n Thứ trưởng Bộ GD&amp;ĐT), GS TSKH L&ecirc; Tuấn Hoa (Viện ph&oacute; Viện To&aacute;n học) viết lời giới thiệu.</p>\r\n<p class="Normal">Tối 29/8, Ch&iacute;nh phủ sẽ tổ chức ch&agrave;o mừng GS Ng&ocirc; Bảo Ch&acirc;u với sự tham dự của l&atilde;nh đạo Đảng, Nh&agrave; nước, Bộ KH&amp;CN, Bộ Gi&aacute;o dục, Viện Khoa học v&agrave; C&ocirc;ng nghệ Việt Nam, Viện To&aacute;n học, ĐH Quốc gia H&agrave; Nội... Tổng số kh&aacute;ch mời tới dự chương tr&igrave;nh l&agrave; khoảng 3.000 người. V&agrave; lễ ch&agrave;o mừng sẽ được truyền h&igrave;nh trực tiếp tr&ecirc;n VTV1 từ 20h.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 2120, 0, 1),
(83, '300 chỉ tiêu NV2 vào CĐ Nghề quốc tế', 'Học viện Công nghệ Thông tin Bách khoa (BKACAD) - ĐH Bách Khoa Hà Nội tuyển sinh 300 chỉ tiêu NV2 hệ Cử nhân Cao đẳng nghề quốc tế dành cho thí sinh dự thi khối A, B, D có điểm thi từ 10 trở lên.', 'Bkad.jpg', NULL, '<div class="content">\r\n<div>\r\n<p class="Title">300 chỉ ti&ecirc;u NV2 v&agrave;o CĐ Nghề quốc tế</p>\r\n<p class="Lead"><strong>Học viện C&ocirc;ng nghệ Th&ocirc;ng tin B&aacute;ch khoa (BKACAD) - ĐH B&aacute;ch Khoa H&agrave; Nội tuyển sinh 300 chỉ ti&ecirc;u NV2 hệ Cử nh&acirc;n Cao đẳng nghề quốc tế d&agrave;nh cho th&iacute; sinh dự thi khối A, B, D c&oacute; điểm thi từ 10 trở l&ecirc;n.<br /></strong></p>\r\n<p class="Normal">Năm nay, được sự cho ph&eacute;p của ĐH B&aacute;ch khoa H&agrave; Nội, Học viện C&ocirc;ng nghệ Th&ocirc;ng tin B&aacute;ch khoa tuyển sinh theo h&igrave;nh thức x&eacute;t tuyển thẳng v&agrave; thi tuyển trực tiếp hệ cao đẳng nghề quốc tế, chuy&ecirc;n ng&agrave;nh Chuy&ecirc;n gia cao cấp Quản trị mạng v&agrave; Bảo mật hệ thống l&agrave;<strong>&nbsp;</strong>chương tr&igrave;nh đ&agrave;o tạo tập trung d&agrave;i hạn 3 năm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Bkad.jpg" alt="Ảnh: Trần Phương." width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Giờ học tại CĐ Nghề B&aacute;ch khoa. Ảnh:&nbsp;<em>Trần Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kết th&uacute;c kh&oacute;a học, sinh vi&ecirc;n được nhận bằng cử nh&acirc;n CĐ Nghề B&aacute;ch khoa H&agrave; Nội, chứng chỉ của ĐH B&aacute;ch khoa H&agrave; Nội v&agrave; c&aacute;c chứng chỉ quốc tế theo ti&ecirc;u chuẩn Mỹ.</p>\r\n<p class="Normal">Th&iacute; sinh dự thi khối A, B, D đạt điểm thi từ 10 đều c&oacute; thể nộp hồ sơ tham gia x&eacute;t tuyển. Thời gian nhận hồ sơ đến ng&agrave;y 30/10.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>\r\n</div>\r\n</div>', 1, 1, 2618, 0, 1),
(86, 'Hai đại học đầu tiên bị tạm ngừng tuyển sinh', 'Ngày 5/8, Thứ trưởng Bộ GD&ĐT Nguyễn Thị Nghĩa đã ký quyết định tạm ngừng tuyển sinh năm 2010 đối với ĐH Công nghệ Đông Á (Bắc Ninh) và ĐH Phan Châu Trinh (Quảng Nam) do nhiều vi phạm.', 'Phu-huynh.jpg', NULL, '<p class="Title">Hai đại học đầu ti&ecirc;n bị tạm ngừng tuyển sinh</p>\r\n<p class="Lead">Ng&agrave;y 5/8, Thứ trưởng Bộ GD&amp;ĐT Nguyễn Thị Nghĩa đ&atilde; k&yacute; quyết định tạm ngừng tuyển sinh năm 2010 đối với ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; (Bắc Ninh) v&agrave; ĐH Phan Ch&acirc;u Trinh (Quảng Nam) do nhiều vi phạm.</p>\r\n<p class="Normal">Ng&agrave;y 5/8, Thứ trưởng Bộ GD&amp;ĐT Nguyễn Thị Nghĩa vừa k&yacute; quyết định tạm ngừng tuyển sinh năm 2010 đối với ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute;. L&yacute; do bị tạm ngừng tuyển sinh l&agrave; v&igrave; trường vi phạm Quy chế tổ chức v&agrave; hoạt động của đại học tư thục.</p>\r\n<p class="Normal">Theo đ&oacute;, trường được th&agrave;nh lập từ cuối năm 2008 theo quyết định của Thủ tướng, hơn một th&aacute;ng sau Hội đồng quản trị của trường được Bộ GD&amp;ĐT c&ocirc;ng nhận. Năm 2009, trường bắt đầu tuyển sinh v&agrave; đ&agrave;o tạo tr&igrave;nh độ đại học nhưng đến nay vẫn chưa ban h&agrave;nh được Quy chế tổ chức v&agrave; hoạt động. Hội đồng quản trị mất đo&agrave;n kết, ảnh hưởng đến uy t&iacute;n của ng&agrave;nh, địa phương v&agrave; nh&agrave; trường.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Phu-huynh.jpg" alt="Ảnh: Ho&agrave;ng H&agrave;." width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Phụ huynh, học sinh vẫn l&agrave; những người thiệt th&ograve;i nhất khi lỡ theo học những trường đại học k&eacute;m chất lượng. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Th&aacute;ng 5 vừa qua, ĐH C&ocirc;ng nghệ Đ&ocirc;ng &Aacute; đ&atilde; khởi c&ocirc;ng x&acirc;y dựng 2 khối nh&agrave; đầu ti&ecirc;n tại l&agrave;ng đại học ở th&agrave;nh phố Bắc Ninh phục vụ 4.000 sinh vi&ecirc;n. C&aacute;c ng&agrave;nh học của trường gồm: Kinh tế, Ngoại ngữ, C&ocirc;ng nghệ th&ocirc;ng tin, Kiến tr&uacute;c, Cơ kh&iacute; v&agrave; c&aacute;c kh&oacute;a li&ecirc;n kết với c&aacute;c trường đại học tại Ph&aacute;p, Mỹ, Australia...</p>\r\n<p class="Normal">Năm 2009 - 2010, trường tuyển sinh kh&oacute;a đầu ti&ecirc;n với 500 sinh vi&ecirc;n cho 3 ng&agrave;nh Kế to&aacute;n, T&agrave;i ch&iacute;nh Ng&acirc;n h&agrave;ng v&agrave; Quản trị kinh doanh. Năm nay, trường được giao 1.400 chỉ ti&ecirc;u ĐH, CĐ; hơn 1.000 chỉ ti&ecirc;u vừa học vừa l&agrave;m, li&ecirc;n th&ocirc;ng; v&agrave; 700 chỉ ti&ecirc;u TCCN.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">C&ugrave;ng ng&agrave;y, Bộ GD&amp;ĐT đ&atilde; c&oacute; buổi l&agrave;m việc với l&atilde;nh đạo ĐH Phan Ch&acirc;u Trinh</span></strong>, c&oacute; sự tham gia của l&atilde;nh đạo tỉnh Quảng Nam v&agrave; TP Hội An. Ph&oacute; thanh tra Phạm Ngọc Tr&uacute;c đ&atilde; đọc quyết định tạm ngừng tuyển sinh đối với ĐH Phan Ch&acirc;u Trinh trong năm học n&agrave;y.</p>\r\n<p class="Normal">Đại diện Bộ GD&amp;ĐT cho biết, năm 2007, ĐH Phan Ch&acirc;u Trinh đ&atilde; tuyển sinh khi chưa được sự đồng &yacute; của Bộ, c&oacute; nhiều sai s&oacute;t trong kh&acirc;u ra đề, chấm thi, x&eacute;t tuyển; tự &yacute; tăng điểm gi&atilde;n c&aacute;ch giữa c&aacute;c khu vực ưu ti&ecirc;n kh&ocirc;ng đ&uacute;ng quy định; một số ng&agrave;nh x&eacute;t tuyển th&iacute; sinh kh&ocirc;ng đ&uacute;ng khối thi v&agrave;o học. Sau hơn 2 năm th&agrave;nh lập, hiện trường chưa c&oacute; Hội đồng cổ đ&ocirc;ng, kh&ocirc;ng họp Hội đồng quản trị, chưa c&oacute; Ban kiểm so&aacute;t...</p>\r\n<p class="Normal">Để đảm bảo chất lượng đ&agrave;o tạo đối với kh&oacute;a tuyển sinh năm 2007 của ĐH Phan Ch&acirc;u Trinh, Bộ GD&amp;ĐT đ&atilde; gửi c&ocirc;ng văn tới ĐH Huế v&agrave; ĐH Đ&agrave; Nẵng đề nghị hỗ trợ giảng vi&ecirc;n, t&agrave;i liệu học tập, điều kiện vật chất phục vụ th&iacute; nghiệm thực h&agrave;nh, hướng dẫn luận văn, thi tốt nghiệp v&agrave; đ&aacute;nh gi&aacute; tốt nghiệp cho sinh vi&ecirc;n trường n&agrave;y.</p>\r\n<p class="Normal">Ph&oacute; chủ tịch tỉnh Quảng Nam Trần Minh Cả cho rằng, những vấn đề ri&ecirc;ng tư nội bộ của trường cần sớm giải quyết dứt điểm, c&aacute;c th&agrave;nh vi&ecirc;n trong Hội đồng quản trị của trường cần nh&oacute;m họp v&agrave; t&igrave;m c&aacute;ch th&aacute;o gỡ những vướng mắc.</p>\r\n<p class="Normal">Trong khi đ&oacute;, đại diện ĐH Phan Ch&acirc;u Trinh đ&atilde; l&ecirc;n tiếng phản đối v&agrave; trả lại quyết định cho đo&agrave;n c&ocirc;ng t&aacute;c của Bộ, đồng thời đề nghị được l&agrave;m việc gặp trực tiếp Bộ trưởng GD&amp;ĐT Phạm Vũ Luận.</p>\r\n<p class="Normal">Đ&acirc;y l&agrave; lần đầu ti&ecirc;n Bộ GD&amp;ĐT quyết định tạm ngừng tuyển sinh đối với trường đại học, nhằm n&acirc;ng cao chất lượng đ&agrave;o tạo, siết chặt kỷ cương của ng&agrave;nh.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng - Tr&agrave; Bang</strong></p>', 1, 1, 1707, 0, 1),
(87, 'Thí sinh cao đẳng đầu tiên được 30 điểm', 'Trương Thanh Hà thủ khoa CĐ Kinh tế Đối ngoại là thí sinh thi cao đẳng đầu tiên được 30 điểm. Á khoa khối A của trường này là Hà Quốc Thi được 29,75 điểm. ', 'Thi_1.jpg', NULL, '<p class="Title">Th&iacute; sinh cao đẳng đầu ti&ecirc;n được 30 điểm</p>\r\n<p class="Lead">Trương Thanh H&agrave; thủ khoa CĐ Kinh tế Đối ngoại l&agrave; th&iacute; sinh thi cao đẳng đầu ti&ecirc;n được 30 điểm. &Aacute; khoa khối A của trường n&agrave;y l&agrave; H&agrave; Quốc Thi được 29,75 điểm.&nbsp;</p>\r\n<p class="Normal">Ở khối D1, Phan Thị Thảo (TP HCM) được tổng điểm cao nhất, 26 điểm (đ&atilde; l&agrave;m tr&ograve;n).</p>\r\n<p class="Normal">Ở hệ cao đẳng, thủ khoa Huỳnh Văn Trung T&iacute;n của CĐ C&ocirc;ng nghiệp Thực phẩm TP HCM cũng c&oacute; điểm số kh&aacute; cao: 29,5. Hai &aacute; khoa Trần Trung Hiếu v&agrave; Ninh Khiết Ho&agrave;ng c&ugrave;ng được 29,5 điểm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Thi_1.jpg" alt="Ảnh: Ho&agrave;ng H&agrave;." width="480" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Th&iacute; sinh dự thi đại học năm 2010. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo thống k&ecirc; của Cục C&ocirc;ng nghệ Th&ocirc;ng tin (Bộ GD&amp;ĐT), kh&aacute; nhiều trong số 100 th&iacute; sinh c&oacute; điểm thi cao nhất hiện nay thuộc về c&aacute;c trường cao đẳng. Điển h&igrave;nh, Đ&agrave;o Xu&acirc;n Ph&aacute;t (CĐ Sư phạm Nha Trang) được 29,5 điểm; Nguyễn Thị Ka Ly (CĐ X&acirc;y dựng 3) được 29,5 điểm; Huynh Tr&uacute;c Thanh (CĐ C&ocirc;ng thương TP HCM) được 29 điểm&hellip;</p>\r\n<p class="Normal">Thống k&ecirc; n&agrave;y cũng cho thấy, ngo&agrave;i Tăng Văn B&igrave;nh (ĐH Ngoại thương) v&agrave; Trương Thanh H&agrave; (CĐ Kinh tế Đối ngoại) được tr&ograve;n 30 điểm, Lưu Thanh Xu&acirc;n (ĐH Sư phạm H&agrave; Nội 2) v&agrave; H&agrave; Quốc Thi (CĐ Kinh tế Đối ngoại) cũng được 30 điểm sau khi đ&atilde; l&agrave;m tr&ograve;n. 100 th&iacute; sinh c&oacute; điểm cao nhất đều được 28,25 điểm trở l&ecirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 2739, 0, 1),
(89, 'ĐH Kinh tế Quốc dân dự kiến điểm chuẩn 18-28,5', 'Năm nay điểm chuẩn khối A vào trường là 21, khối D là 20 (tiếng Anh hệ số 1), riêng 7 ngành lấy điểm khối A, D là 18. Bốn ngành dạy bằng tiếng Anh lấy điểm chuẩn 21-28,5 (tiếng Anh hệ số 2).\r\n', 'DSC_6904.jpg', NULL, '<p class="Title">ĐH Kinh tế Quốc d&acirc;n dự kiến điểm chuẩn 18-28,5</p>\r\n<p class="Lead">Năm nay điểm chuẩn khối A v&agrave;o trường l&agrave; 21, khối D l&agrave; 20 (tiếng Anh hệ số 1), ri&ecirc;ng 7 ng&agrave;nh lấy điểm khối A, D l&agrave; 18. Bốn ng&agrave;nh dạy bằng tiếng Anh lấy điểm chuẩn 21-28,5 (tiếng Anh hệ số 2).<br /><br /></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="DSC_6904.jpg" alt="Ảnh: Ho&agrave;ng H&agrave;." width="495" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&ugrave;a tuyển sinh n&agrave;y, điểm chuẩn của nhiều trường giảm hơn so với năm 2009. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chiều nay, ĐH Kinh tế Quốc d&acirc;n đưa ra mức điểm chuẩn dự kiến của kh&oacute;a K52. Theo Trưởng ph&ograve;ng Đ&agrave;o tạo Nguyễn Quang Dong, trường đ&atilde; b&aacute;o c&aacute;o Bộ Gi&aacute;o dục mức điểm dự kiến n&agrave;y. Đ&acirc;y l&agrave; điểm chuẩn dự kiến cho học sinh KV3, mức điểm chuẩn ch&ecirc;nh lệch giữa 2 nh&oacute;m đối tượng kế tiếp l&agrave; 1 điểm v&agrave; giữa 2 khu vực kế tiếp l&agrave; 0,5 điểm.</p>\r\n<table border="1" cellspacing="0" cellpadding="0" width="490" bgcolor="#f1f1e2">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Kinh tế n&ocirc;ng nghiệp v&agrave; ph&aacute;t triển n&ocirc;ng th&ocirc;n</td>\r\n<td class="Normal" width="74" align="center">417</td>\r\n<td class="Normal" width="77" align="center">A/D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Thống k&ecirc; kinh tế x&atilde; hội</td>\r\n<td class="Normal" width="74" align="center">424</td>\r\n<td class="Normal" width="77" align="center">A/D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Tin học kinh tế</td>\r\n<td class="Normal" width="74" align="center">444</td>\r\n<td class="Normal" width="77" align="center">A/D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Hệ thống th&ocirc;ng tin quản l&iacute;</td>\r\n<td class="Normal" width="74" align="center">453</td>\r\n<td class="Normal" width="77" align="center">A/D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Luật kinh doanh</td>\r\n<td class="Normal" width="74" align="center">545</td>\r\n<td class="Normal" width="77" align="center">A/D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Luật kinh doanh quốc tế</td>\r\n<td class="Normal" width="74" align="center">546</td>\r\n<td class="Normal" width="77" align="center">A/D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">Chuy&ecirc;n ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin</td>\r\n<td class="Normal" width="74" align="center">146</td>\r\n<td class="Normal" width="77" align="center">A</td>\r\n<td class="Normal" width="78" align="center">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">Chuy&ecirc;n ng&agrave;nh Tiếng Anh Thương mại (tiếng Anh hệ số 2)</td>\r\n<td class="Normal" width="74" align="center">701</td>\r\n<td class="Normal" width="77" align="center">D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">28,5</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top"><strong>C&aacute;c lớp tuyển sinh theo đặc th&ugrave;:</strong></td>\r\n<td class="BreakLine" width="74">&nbsp;</td>\r\n<td class="BreakLine" width="77">&nbsp;</td>\r\n<td class="BreakLine" width="78">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Quản trị lữ h&agrave;nh (tiếng Anh hệ số 2)</td>\r\n<td class="Normal" width="74" align="center">454</td>\r\n<td class="Normal" width="77" align="center">D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">21</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- Quản trị kh&aacute;ch sạn (tiếng Anh hệ số 2)</td>\r\n<td class="Normal" width="74" align="center">455</td>\r\n<td class="Normal" width="77" align="center">D<sub>1</sub></td>\r\n<td class="Normal" width="78" align="center">21</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- QTKD bằng tiếng Anh (E-BBA)</td>\r\n<td class="Normal" rowspan="2" width="74" align="center">457</td>\r\n<td class="Normal" width="77" align="center">A</td>\r\n<td class="Normal" width="78" align="center">21</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="261" valign="top">- QTKD bằng tiếng Anh (tiếng Anh hệ số 2)</td>\r\n<td class="Normal" width="77" align="center">D1</td>\r\n<td class="Normal" width="78" align="center">28,5</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 368, 0, 1),
(90, 'Hai nữ sinh được điểm 10 môn Địa lý', 'Tại ĐH Khoa học Xã hội và Nhân văn Hà Nội, điểm thi 9,75 (làm tròn thành 10) môn Địa lý thuộc về thí sinh Nguyễn Thị Hữu và Hoàng Thị Thu Trang. Điểm 9,5 môn Lịch sử là của thí sinh Trần Thị Cẩm.', 'Gio-thi.jpg', NULL, '<p class="Title">Hai nữ sinh được điểm 10 m&ocirc;n Địa l&yacute;</p>\r\n<p class="Lead">Tại ĐH Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn H&agrave; Nội, điểm thi 9,75 (l&agrave;m tr&ograve;n th&agrave;nh 10) m&ocirc;n Địa l&yacute; thuộc về th&iacute; sinh Nguyễn Thị Hữu v&agrave; Ho&agrave;ng Thị Thu Trang. Điểm 9,5 m&ocirc;n Lịch sử l&agrave; của th&iacute; sinh Trần Thị Cẩm.<br />Trưa nay, ĐH Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn (ĐH Quốc gia H&agrave; Nội) c&ocirc;ng bố điểm thi khối C, với hai thủ khoa c&ugrave;ng được 26,5 điểm l&agrave; Phạm Thị Minh Duy&ecirc;n - ng&agrave;nh C&ocirc;ng t&aacute;c x&atilde; hội (Văn 8,5; Sử 8,5; Địa 9,25) v&agrave; Nguyễn Thị B&iacute;ch Ngọc - ng&agrave;nh Đ&ocirc;ng phương học (Văn 8; Sử 9; Địa 9,25).</p>\r\n<p class="Normal">Điểm thi 9,75 (l&agrave;m tr&ograve;n th&agrave;nh 10) m&ocirc;n Địa l&yacute; thuộc về th&iacute; sinh Nguyễn Thị Hữu v&agrave; Ho&agrave;ng Thị Thu Trang. Điểm 9,5 m&ocirc;n Lịch sử l&agrave; của th&iacute; sinh Trần Thị Cẩm. C&ograve;n điểm 9 m&ocirc;n Văn thuộc về th&iacute; sinh Trịnh Ngọc Gi&aacute;p.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Gio-thi.jpg" alt="Ảnh: Ho&agrave;ng H&agrave;." width="480" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Th&iacute; sinh dự thi đại học đợt 2 tại H&agrave; Nội. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo thống k&ecirc; của trường, năm nay c&aacute;c ng&agrave;nh T&acirc;m l&yacute; học, Khoa học Quản l&yacute; v&agrave; Văn học c&oacute; kh&aacute; đ&ocirc;ng th&iacute; sinh đạt mức điểm x&eacute;t tuyển của năm 2009. Cao nhất l&agrave; ng&agrave;nh Văn học, với 205 th&iacute; sinh đạt từ 18 điểm trở l&ecirc;n, trong khi chỉ ti&ecirc;u tuyển sinh chỉ l&agrave; 100 (gồm cả khối C v&agrave; D).</p>\r\n<p class="Normal">Đ&acirc;y l&agrave; năm đầu ti&ecirc;n trường tuyển sinh ng&agrave;nh Việt Nam học, c&oacute; 32 th&iacute; sinh đạt từ 18 điểm (mức điểm chuẩn thấp nhất năm 2009) trở l&ecirc;n, trong khi chỉ ti&ecirc;u l&agrave; 60.</p>\r\n<p class="Normal">Dưới đ&acirc;y l&agrave; bảng thống k&ecirc; số th&iacute; sinh dự thi khối C năm 2010 c&oacute; tổng điểm lớn hơn hoặc bằng mức điểm x&eacute;t tuyển năm 2009. Điểm số n&agrave;y đ&atilde; bao gồm cả điểm ưu ti&ecirc;n.</p>\r\n<table border="1" cellspacing="0" cellpadding="3" width="492" bgcolor="#f3f3e9">\r\n<tbody>\r\n<tr>\r\n<td class="BoxTitle" width="45%" align="center" bgcolor="#c6c68c">Ng&agrave;nh</td>\r\n<td class="BoxTitle" width="18%" align="center" bgcolor="#c6c68c">Điểm XT&nbsp;<br />năm 2009</td>\r\n<td class="BoxTitle" width="18%" align="center" bgcolor="#c6c68c">Số th&iacute; sinh năm 2010&nbsp;<br />đạt điểm XT 2009</td>\r\n<td class="BoxTitle" width="18%" align="center" bgcolor="#c6c68c">Chỉ ti&ecirc;u 2010</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">T&acirc;m l&yacute; học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">92</td>\r\n<td class="Normal" width="18%" align="center">80</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Khoa học quản l&yacute;</td>\r\n<td class="Normal" width="18%" align="center">18,5</td>\r\n<td class="Normal" width="18%" align="center">189</td>\r\n<td class="Normal" width="18%" align="center">110</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">X&atilde; hội học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">59</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Triết học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Ch&iacute;nh trị học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">35</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">C&ocirc;ng t&aacute;c x&atilde; hội</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">80</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Văn học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">205</td>\r\n<td class="Normal" width="18%" align="center">100</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Ng&ocirc;n ngữ học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">35</td>\r\n<td class="Normal" width="18%" align="center">50</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Lịch sử</td>\r\n<td class="Normal" width="18%" align="center">19,5</td>\r\n<td class="Normal" width="18%" align="center">92</td>\r\n<td class="Normal" width="18%" align="center">100</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">B&aacute;o ch&iacute;</td>\r\n<td class="Normal" width="18%" align="center">21</td>\r\n<td class="Normal" width="18%" align="center">40</td>\r\n<td class="Normal" width="18%" align="center">100</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Th&ocirc;ng tin &ndash; Thư viện</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">13</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Lưu trữ học v&agrave; QTVP</td>\r\n<td class="Normal" width="18%" align="center">20</td>\r\n<td class="Normal" width="18%" align="center">37</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Đ&ocirc;ng phương học</td>\r\n<td class="Normal" width="18%" align="center">20</td>\r\n<td class="Normal" width="18%" align="center">144</td>\r\n<td class="Normal" width="18%" align="center">110</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Quốc tế học</td>\r\n<td class="Normal" width="18%" align="center">18,5</td>\r\n<td class="Normal" width="18%" align="center">93</td>\r\n<td class="Normal" width="18%" align="center">80</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Du lịch học</td>\r\n<td class="Normal" width="18%" align="center">19,5</td>\r\n<td class="Normal" width="18%" align="center">77</td>\r\n<td class="Normal" width="18%" align="center">90</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">H&aacute;n N&ocirc;m</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">25</td>\r\n<td class="Normal" width="18%" align="center">30</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Nh&acirc;n học</td>\r\n<td class="Normal" width="18%" align="center">18</td>\r\n<td class="Normal" width="18%" align="center">17</td>\r\n<td class="Normal" width="18%" align="center">70</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="45%">Việt Nam học</td>\r\n<td class="Normal" width="18%" align="center">-</td>\r\n<td class="Normal" width="18%" align="center">-</td>\r\n<td class="Normal" width="18%" align="center">60</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>Lưu &yacute;: Số th&iacute; sinh tr&ecirc;n chỉ t&iacute;nh ri&ecirc;ng khối C, kh&ocirc;ng bao gồm khối A v&agrave; D.</em></p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 1522, 0, 1),
(91, 'Được 1,5 điểm Văn vẫn đỗ đại học', 'Chiều 28/7, ĐH Sân khấu Điện ảnh công bố danh sách thí sinh trúng tuyển. Nhiều thí sinh dù chỉ được 1,5 điểm Văn nhưng môn năng khiếu đạt điểm cao, vẫn trúng tuyển vào trường này.', '4.jpg', NULL, '<p class="Title">Được 1,5 điểm Văn vẫn đỗ đại học</p>\r\n<p class="Lead">Chiều 28/7, ĐH S&acirc;n khấu Điện ảnh c&ocirc;ng bố danh s&aacute;ch th&iacute; sinh tr&uacute;ng tuyển. Nhiều th&iacute; sinh d&ugrave; chỉ được 1,5 điểm Văn nhưng m&ocirc;n năng khiếu đạt điểm cao, vẫn tr&uacute;ng tuyển v&agrave;o trường n&agrave;y.<br /><br /></p>\r\n<p class="Normal">Theo t&igrave;m hiểu của&nbsp;<em>VnExpress.net</em>, ở khoa Quay phim Điện ảnh, điểm tr&uacute;ng tuyển lấy thấp nhất l&agrave; 14,5 (đối với th&iacute; sinh KV3), với điều kiện m&ocirc;n chuy&ecirc;n ng&agrave;nh phải được từ 12 điểm trở l&ecirc;n. Ở mức điểm n&agrave;y, c&oacute; 2 th&iacute; sinh được 1,5 điểm Văn vẫn nằm trong danh s&aacute;ch tr&uacute;ng tuyển.</p>\r\n<p class="Normal">Khoa Quay phim Truyền h&igrave;nh, điểm chuẩn từ 16 trở l&ecirc;n. Một số th&iacute; sinh được 2-3 điểm Văn cũng tr&uacute;ng tuyển.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="4.jpg" alt="Ảnh: Ho&agrave;ng Th&ugrave;y." width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Th&iacute; sinh dự thi ng&agrave;nh nghệ thuật ở H&agrave; Nội. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Năm nay, khoa Đạo diễn truyền h&igrave;nh lấy từ 18 trở l&ecirc;n (điểm chuy&ecirc;n ng&agrave;nhđược từ 13). Khoa Diễn vi&ecirc;n S&acirc;n khấu lấy 17,5 điểm; Khoa Đạo diễn Truyền h&igrave;nh lấy từ 16 điểm; Khoa Bi&ecirc;n kịch Điện ảnh lấy từ 18 điểm. Với điểm chuẩn 20, khoa L&yacute; luận Ph&ecirc; b&igrave;nh điện ảnh ch&iacute; c&oacute; 4 th&iacute; sinh tr&uacute;ng tuyển...</p>\r\n<p class="Normal">Theo quy định, sau khi vượt qua v&ograve;ng sơ tuyển, th&iacute; sinh dự thi v&agrave;o ĐH S&acirc;n khấu Điện ảnh phải dự thi m&ocirc;n Văn v&agrave; m&ocirc;n năng khiếu (hệ số 2). Tổng chỉ ti&ecirc;u năm 2010 của trường l&agrave; 358 sinh vi&ecirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 768, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(92, 'Hơn 100 trường công bố điểm tuyển sinh', 'Bên cạnh các trường lớn như ĐH Hà Nội, Y Hà Nội, Kinh tế TP HCM... nhiều trường cao đẳng cũng công bố điểm thi. Nhiều trường cho hay dự kiến điểm chuẩn sẽ thấp hơn năm 2009.', 'Thi-TP-HCM.jpg', NULL, '<p class="Title">Hơn 100 trường c&ocirc;ng bố điểm tuyển sinh</p>\r\n<p class="Lead">B&ecirc;n cạnh c&aacute;c trường lớn như ĐH H&agrave; Nội, Y H&agrave; Nội, Kinh tế TP HCM... nhiều trường cao đẳng cũng c&ocirc;ng bố điểm thi. Nhiều trường cho hay dự kiến điểm chuẩn sẽ thấp hơn năm 2009.</p>\r\n<p class="Lead">Chiều 28/7, ĐH H&agrave; Nội c&ocirc;ng bố điểm thi. Với 38 điểm (ngoại ngữ nh&acirc;n đ&ocirc;i), th&iacute; sinh thi khoa tiếng Nhật Phạm Thị Quỳnh Trang trở th&agrave;nh thủ khoa của trường. C&ograve;n thủ khoa khối A đạt 25,5 điểm</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>VnExpress.net</em>, Trưởng ph&ograve;ng Đ&agrave;o tạo ĐH H&agrave; Nội L&ecirc; Quốc Hạnh cho biết, trường đang l&ecirc;n phương &aacute;n điểm x&eacute;t tuyển nhưng "chắc chắn điểm chuẩn sẽ thấp hơn năm 2009 v&agrave; hạ đều ở c&aacute;c ng&agrave;nh, c&aacute;c khối", &ocirc;ng Hạnh n&oacute;i.</p>\r\n<p class="Normal">Cũng ng&agrave;y, ĐH Y H&agrave; Nội c&ocirc;ng bố điểm thi, với thủ khoa L&ecirc; Thị Minh Vượng được 30 điểm (sau khi đ&atilde; l&agrave;m tr&ograve;n v&agrave; cộng th&ecirc;m 1 điểm ưu ti&ecirc;n). &Aacute; khoa Trần Văn Minh được 29 điểm. Cuối tuần n&agrave;y, trường sẽ c&ocirc;ng bố điểm tr&uacute;ng tuyển.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Thi-TP-HCM.jpg" alt="Th&iacute; sinh tập chuẩn bị l&agrave;m b&agrave;i thi đại học. Ảnh: Hải Duy&ecirc;n." width="480" height="341" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Th&iacute; sinh chuẩn bị l&agrave;m b&agrave;i thi đại học. Ảnh:&nbsp;<em>Hải Duy&ecirc;n</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Với 28,5 điểm, Phan Thiện T&ocirc;n (trường Phổ th&ocirc;ng Năng khiếu TP HCM) trở th&agrave;nh thủ khoa ĐH Kinh tế TP HCM. Đồng &aacute; khoa l&agrave; 3 th&iacute; sinh c&oacute; chung mức điểm 28,25 l&agrave; Trần Mỹ Đạt, Dư Th&acirc;n Duy, v&agrave; Triệu Ngọc Diệp.</p>\r\n<p class="Normal">Trong số gần 20.000 hồ sơ dự thi th&igrave; c&oacute; khoảng 11.000 th&iacute; sinh đạt mức điểm s&agrave;n của năm ngo&aacute;i trở l&ecirc;n (13 điểm). C&oacute; tới 3.800 th&iacute; sinh đạt 19 điểm trở l&ecirc;n (chưa t&iacute;nh điểm ưu ti&ecirc;n), trong khi năm nay ĐH Kinh tế lấy 4.000 chỉ ti&ecirc;u. Ch&iacute;nh v&igrave; vậy, theo đại diện nh&agrave; trường, mức điểm tr&uacute;ng tuyển sẽ khoảng 19.</p>\r\n<p class="Normal">Theo đ&aacute;nh gi&aacute; của l&atilde;nh đạo ĐH Khoa học X&atilde; hội v&agrave; Nh&acirc;n văn TP HCM, điểm thi v&agrave; điểm tr&uacute;ng tuyển năm nay cũng tương đương năm ngo&aacute;i. Th&iacute; sinh đạt thủ khoa khối C l&agrave; Nguyễn Huỳnh Lu&acirc;n, với 25,5 điểm v&agrave; Ng&ocirc; Phan Bảo Tr&acirc;n đứng đầu khối D với 25 điểm.</p>\r\n<p class="Normal">ĐH Nha Trang cũng c&ocirc;ng bố kết quả thi đai học ở cả ba ph&acirc;n hiệu. Theo thống k&ecirc;, điểm thi kh&aacute; thấp chỉ c&oacute; khoảng 1.200 th&iacute; sinh c&oacute; điểm từ 13 trở l&ecirc;n trong số gần 13.000 bạn dự thi. Chỉ ti&ecirc;u năm nay của trường l&agrave; 2.200 ở hệ đại học v&agrave; 800 hệ cao đẳng. Theo đại diện trường, v&igrave; điểm thi tương đối thấp n&ecirc;n nếu Bộ Gi&aacute;o dục hạ điểm s&agrave;n th&igrave; trường cũng sẽ phải hạ điểm tr&uacute;ng tuyển mới lấy đủ chỉ ti&ecirc;u. Dự kiến, trường sẽ phải d&agrave;nh hơn 1.000 chỉ ti&ecirc;u cho nguyện vọng 2.</p>\r\n<p class="Normal">Cả nước c&oacute; hơn 100 ĐH, CĐ c&ocirc;ng bố điểm thi. Đ&atilde; c&oacute; 2 thủ khoa được 30 điểm, trong đ&oacute; 1 thủ khoa đạt điểm tuyệt đối.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n - Tiến Dũng</strong></p>', 1, 1, 1685, 0, 1),
(93, 'Việt Nam đoạt 4 huy chương Olympic Hóa học', 'Sáng nay, Cục phó Khảo thí (Bộ GD&ĐT) Trần Văn Nghĩa cho hay, 4 học sinh Việt Nam dự Olympic Hóa học tại Nhật Bản đã giành được 2 huy chương Bạc và 2 huy chương Đồng', 'IChO.jpg', NULL, '<p class="Title">Việt Nam đoạt 4 huy chương Olympic H&oacute;a học</p>\r\n<p class="Lead">S&aacute;ng nay, Cục ph&oacute; Khảo th&iacute; (Bộ GD&amp;ĐT) Trần Văn Nghĩa cho hay, 4 học sinh Việt Nam dự Olympic H&oacute;a học tại Nhật Bản đ&atilde; gi&agrave;nh được 2 huy chương Bạc v&agrave; 2 huy chương Đồng</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="IChO.jpg" alt="" width="450" height="325" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trong ph&ograve;ng thi l&yacute; thuyết H&oacute;a học. Ảnh:&nbsp;<em>Icho.org.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trong đ&oacute;, Nguyễn Đức B&igrave;nh (THPT chuy&ecirc;n Lam Sơn, Thanh H&oacute;a) v&agrave; Lưu Nguyễn Hồng Anh (THPT Năng khiếu, ĐH Quốc gia TP HCM) được huy chương Bạc. C&ograve;n L&ecirc; Anh Tuấn (THPT chuy&ecirc;n Trần Ph&uacute;, Hải Ph&ograve;ng) v&agrave; Mai Thu C&uacute;c (THPT chuy&ecirc;n Nguyễn Tr&atilde;i, Hải Dương) được huy chương Đồng.</p>\r\n<p class="Normal">C&oacute; khoảng 280 học sinh đến từ hơn 70 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ dự thi Olympic H&oacute;a học lần thứ 42 tại Nhật Bản.</p>\r\n<p class="Normal">Năm 2009, tại kỳ thi Olympic H&oacute;a học quốc tế lần thứ 41 ở Anh, đo&agrave;n Việt Nam gi&agrave;nh được 1 huy chương V&agrave;ng, 2 huy chương Bạc v&agrave; 1 huy chương Đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tiến Dũng</strong></p>', 1, 1, 270, 0, 1),
(94, 'Chàng trai mồ côi trở thành thủ khoa 30 điểm', 'Ngay khi nhận kết quả thủ khoa ĐH Ngoại thương với số điểm tuyệt đối, ngôi nhà nhỏ ở xã Yên Sơn (Đô Lương, Nghệ An) của Bình luôn chật kín khách. \r\nNgười ở gần thì đến chúc mừng cậu học trò cưng của xóm làng, người làng xa đến “xem mặt” thủ khoa của một trường danh tiếng. Còn Bình thì vừa bận tiếp khách, vừa tranh thủ nghe điện thoại chúc mừng, khuôn mặt đỏ ửng ngượng nghịu.', 'binh.jpg', NULL, '<p class="Title">Ch&agrave;ng trai mồ c&ocirc;i trở th&agrave;nh thủ khoa 30 điểm</p>\r\n<p class="Lead">Ngay khi nhận kết quả thủ khoa ĐH Ngoại thương với số điểm tuyệt đối, ng&ocirc;i nh&agrave; nhỏ ở x&atilde; Y&ecirc;n Sơn (Đ&ocirc; Lương, Nghệ An) của B&igrave;nh lu&ocirc;n chật k&iacute;n kh&aacute;ch.&nbsp;<br />Người ở gần th&igrave; đến ch&uacute;c mừng cậu học tr&ograve; cưng của x&oacute;m l&agrave;ng, người l&agrave;ng xa đến &ldquo;xem mặt&rdquo; thủ khoa của một trường danh tiếng. C&ograve;n B&igrave;nh th&igrave; vừa bận tiếp kh&aacute;ch, vừa tranh thủ nghe điện thoại ch&uacute;c mừng, khu&ocirc;n mặt đỏ ửng ngượng nghịu.</p>\r\n<p class="Normal">8 th&aacute;ng sau khi lọt l&ograve;ng mẹ, Tăng Văn B&igrave;nh đ&atilde; phải sống cảnh mồ c&ocirc;i bố. L&uacute;c ấy, chị g&aacute;i cậu mới bi b&ocirc; l&ecirc;n 3. Một n&aacute;ch hai con nhỏ, với phụ cấp &iacute;t ỏi của c&ocirc; gi&aacute;o mầm non trường l&agrave;ng, chị Trần Thị Dung phải tất bật sớm h&ocirc;m để vừa lo việc đồng &aacute;ng, vừa lo việc nh&agrave; trường mới tạm đủ c&aacute;i ăn, c&aacute;i mặc cho c&aacute;c con.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="binh.jpg" border="1" alt="" width="450" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&igrave;nh v&agrave; mẹ. Ảnh:&nbsp;<em>Trường Long.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&ldquo;Từ nhỏ, hai chị em đ&atilde; rất thương mẹ, lại si&ecirc;ng học. T&ocirc;i thường động vi&ecirc;n muốn mẹ hết khổ th&igrave; phải cố gắng học n&ecirc;n hai đứa c&agrave;ng thi đua nhau. Những khi ấy, mệt nhọc trong t&ocirc;i tan biến hết, v&agrave; t&ocirc;i lu&ocirc;n tin rằng hai con của m&igrave;nh sẽ th&agrave;nh c&ocirc;ng&rdquo;, chị Dung cười n&oacute;i vui vẻ.</p>\r\n<p class="Normal">Ngay từ nhỏ, B&igrave;nh đ&atilde; sớm thể hiện khả năng To&aacute;n học. Năm học lớp 5, B&igrave;nh l&agrave; học sinh duy nhất của huyện Đ&ocirc; Lương đạt giải nhất m&ocirc;n To&aacute;n của tỉnh Nghệ An. Cuối năm học lớp 9, cậu đậu thủ khoa Trường chuy&ecirc;n THPT Phan Bội Ch&acirc;u của tỉnh Nghệ An, &aacute; khoa thi v&agrave;o lớp 10 chuy&ecirc;n To&aacute;n ĐH Khoa học tự nhi&ecirc;n H&agrave; Nội. Năm lớp 12 B&igrave;nh đạt giải nhất m&ocirc;n To&aacute;n của tỉnh v&agrave; giải nh&igrave; m&ocirc;n To&aacute;n kỳ thi học sinh giỏi quốc gia&hellip;</p>\r\n<p class="Normal">Kh&ocirc;ng chỉ c&oacute; th&agrave;nh t&iacute;ch đ&aacute;ng nể trong c&aacute;c kỳ thi m&agrave; điểm số những m&ocirc;n học kh&aacute;c như Văn, Sử, Địa, Ngoại ngữ&hellip; của B&igrave;nh từ nhỏ đến nay cũng lu&ocirc;n ở tốp đầu. Đặc biệt, điểm tổng kết m&ocirc;n To&aacute;n của B&igrave;nh lu&ocirc;n l&agrave; 10, tổng kết c&aacute;c năm học lu&ocirc;n tr&ecirc;n ch&iacute;n phẩy.</p>\r\n<p class="Normal">N&oacute;i về kinh nghiệm cũng như phương ph&aacute;p học, ch&agrave;ng trai xứ Nghệ chỉ cười bẽn lẽn: &ldquo;Em rất &iacute;t đi học th&ecirc;m v&igrave; nh&agrave; ngh&egrave;o, kh&ocirc;ng c&oacute; tiền. Chủ yếu em học v&agrave; hiểu b&agrave;i khi thầy c&ocirc; gi&aacute;o giảng tr&ecirc;n lớp rồi về nh&agrave; t&igrave;m kiếm s&aacute;ch vở, t&agrave;i liệu tham khảo, chỗ n&agrave;o kh&ocirc;ng biết th&igrave; hỏi bạn b&egrave;, thầy c&ocirc;&rdquo;.</p>\r\n<p class="Normal">Say m&ecirc; To&aacute;n học nhưng lại th&iacute;ch c&aacute;c ng&agrave;nh kinh tế, sau khi kết th&uacute;c đợt thi khối A v&agrave;o ĐH Ngoại thương, biết chắc m&igrave;nh sẽ đậu v&igrave; l&agrave;m b&agrave;i tốt, nhưng B&igrave;nh kh&ocirc;ng d&aacute;m nghĩ rằng sẽ đạt điểm tuyệt đối. &ldquo;Chắc do em may mắn hơn c&aacute;c bạn, chứ thi đậu v&agrave;o ĐH Ngoại thương th&igrave; ai cũng xuất sắc&rdquo;, t&acirc;n thủ khoa khi&ecirc;m tốn n&oacute;i.</p>\r\n<p class="Normal">Đạt được nguyện vọng trở th&agrave;nh sinh vi&ecirc;n kinh tế, ước mơ lớn nhất của B&igrave;nh hiện nay l&agrave; được đi du học, v&agrave; trở th&agrave;nh một CEO nổi tiếng. &ldquo;D&ugrave; rất y&ecirc;u m&ocirc;n To&aacute;n nhưng trở th&agrave;nh một gi&aacute;m đốc điều h&agrave;nh, một nh&agrave; l&atilde;nh đạo kinh tế mới l&agrave; ước mơ của em&rdquo;, t&acirc;n thủ khoa b&agrave;y tỏ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Trường Long</strong></p>', 1, 1, 721, 0, 1),
(95, 'Thủ khoa đại học Khoa học tự nhiên TP HCM đạt 28,5 điểm', 'hiều 26/7, Đại học Khoa học tự nhiên TP HCM công bố kết quả điểm thi và điểm chuẩn dự kiến đối với các ngành.\r\n\r\nThủ khoa khối A là Nguyễn Mạnh Tiến đạt 28,5 điểm (Toán 9,75; Lý 9; Hóa 9,5). Thí sinh Phạm Thị Lộc Xuân dẫn đầu khối B với 27,5 điểm (Sinh 9; Toán 9; Hóa 9,5).', 'diem1.jpg', NULL, '<p class="Title">Thủ khoa đại học Khoa học tự nhi&ecirc;n TP HCM đạt 28,5 điểm</p>\r\n<p class="Lead">Chiều 26/7, Đại học Khoa học tự nhi&ecirc;n TP HCM c&ocirc;ng bố kết quả điểm thi v&agrave; điểm chuẩn dự kiến đối với c&aacute;c ng&agrave;nh.</p>\r\n<p class="Normal">Thủ khoa khối A l&agrave;&nbsp;<span style="color: #000000;">Nguyễn Mạnh Tiến đạt 28,5 điểm (To&aacute;n 9,75; L&yacute; 9; H&oacute;a 9,5). Th&iacute; sinh Phạm Thị Lộc Xu&acirc;n dẫn đầu khối B với 27,5 điểm (Sinh 9; To&aacute;n 9; H&oacute;a 9,5).</span></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="diem1.jpg" border="1" alt="" width="480" height="357" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Th&iacute; sinh dự thi đại học 2010. Ảnh:&nbsp;<em>Hải Duy&ecirc;n.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><span style="color: #000000;">C&oacute; ba b&agrave;i thi đạt điểm 10 đều thuộc m&ocirc;n To&aacute;n, trong đ&oacute; khối A một b&agrave;i v&agrave; khối B hai b&agrave;i. Nhằm khuyến kh&iacute;ch tinh thần học tập, những th&iacute; sinh c&oacute; điểm thi từ 27 trở l&ecirc;n đối với khối A v&agrave; 26 trở l&ecirc;n đối với khối B đều được nh&agrave; trường tặng một suất học bổng tương đương một năm học miễn ph&iacute;. Trong suốt thời gian học, nếu th&iacute; sinh đạt kết quả tốt sẽ tiếp tục được nhận c&aacute;c học bổng kh&aacute;c của trường.</span></p>\r\n<p><span style="color: #000000;"> </span></p>\r\n<p class="Normal">Một số ng&agrave;nh dự kiến tuyển sinh nguyện vọng 2 của ĐH Khoa học tự nhi&ecirc;n gồm: To&aacute;n - Tin, Vật l&yacute;, C&ocirc;ng nghệ th&ocirc;ng tin, Hải dương học - Kh&iacute; tượng v&agrave; thủy văn, Khoa học vật liệu, Sinh học.</p>\r\n<p class="Normal">Điểm chuẩn dự kiến của c&aacute;c ng&agrave;nh:&nbsp;<br />(Điểm c&aacute;ch biệt với c&aacute;c nh&oacute;m đối tượng l&agrave; 1 v&agrave; khu vực ưu ti&ecirc;n l&agrave; 0,5)</p>\r\n<table border="1" cellspacing="0" cellpadding="0" align="center">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">M&atilde; ng&agrave;nh</td>\r\n<td class="Normal" width="150" valign="top">T&ecirc;n ng&agrave;nh</td>\r\n<td class="Normal" width="66" valign="top">Khối thi</td>\r\n<td class="Normal" width="72" valign="top">Chi ti&ecirc;u</td>\r\n<td class="Normal" width="72" valign="top">Điểm</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">101</td>\r\n<td class="Normal" width="150" valign="top">To&aacute;n - Tin</td>\r\n<td class="Normal" width="66" valign="top">A</td>\r\n<td class="Normal" width="72" valign="top">300</td>\r\n<td class="Normal" width="72" valign="top">15</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">104</td>\r\n<td class="Normal" width="150" valign="top">Vật L&yacute;</td>\r\n<td class="Normal" width="66" valign="top">A</td>\r\n<td class="Normal" width="72" valign="top">250</td>\r\n<td class="Normal" width="72" valign="top">14.5</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">105</td>\r\n<td class="Normal" width="150" valign="top">Điện tử viễn th&ocirc;ng</td>\r\n<td class="Normal" width="66" valign="top">A</td>\r\n<td class="Normal" width="72" valign="top">200</td>\r\n<td class="Normal" width="72" valign="top">17</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">107</td>\r\n<td class="Normal" width="150" valign="top">C&ocirc;ng nghệ th&ocirc;ng tin</td>\r\n<td class="Normal" width="66" valign="top">A</td>\r\n<td class="Normal" width="72" valign="top">550</td>\r\n<td class="Normal" width="72" valign="top">18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">109</td>\r\n<td class="Normal" width="150" valign="top">Hải Dương học, Kh&iacute; tượng thủy văn</td>\r\n<td class="Normal" width="66" valign="top">A, B</td>\r\n<td class="Normal" width="72" valign="top">50/50</td>\r\n<td class="Normal" width="72" valign="top">14.5/15</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">201</td>\r\n<td class="Normal" width="150" valign="top">H&oacute;a học</td>\r\n<td class="Normal" width="66" valign="top">A</td>\r\n<td class="Normal" width="72" valign="top">250</td>\r\n<td class="Normal" width="72" valign="top">16</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">203</td>\r\n<td class="Normal" width="150" valign="top">Đại chất</td>\r\n<td class="Normal" width="66" valign="top">A, B</td>\r\n<td class="Normal" width="72" valign="top">75/75</td>\r\n<td class="Normal" width="72" valign="top">14/17</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">205</td>\r\n<td class="Normal" width="150" valign="top">Khoa học m&ocirc;i trường</td>\r\n<td class="Normal" width="66" valign="top">A, B</td>\r\n<td class="Normal" width="72" valign="top">75/75</td>\r\n<td class="Normal" width="72" valign="top">15.5/18</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">206</td>\r\n<td class="Normal" width="150" valign="top">C&ocirc;ng nghệ m&ocirc;i trường</td>\r\n<td class="Normal" width="66" valign="top">A, B</td>\r\n<td class="Normal" width="72" valign="top">60/60</td>\r\n<td class="Normal" width="72" valign="top">16/19</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">207</td>\r\n<td class="Normal" width="150" valign="top">Khoa học vật liệu</td>\r\n<td class="Normal" width="66" valign="top">A</td>\r\n<td class="Normal" width="72" valign="top">180</td>\r\n<td class="Normal" width="72" valign="top">14</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">301</td>\r\n<td class="Normal" width="150" valign="top">Sinh học</td>\r\n<td class="Normal" width="66" valign="top">B</td>\r\n<td class="Normal" width="72" valign="top">300</td>\r\n<td class="Normal" width="72" valign="top">16</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="55" valign="top">312</td>\r\n<td class="Normal" width="150" valign="top">C&ocirc;ng nghệ sinh học</td>\r\n<td class="Normal" width="66" valign="top">A, B</td>\r\n<td class="Normal" width="72" valign="top">70/130</td>\r\n<td class="Normal" width="72" valign="top">17/21</td>\r\n</tr>\r\n<tr>\r\n<td colspan="5" width="415" valign="top">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" colspan="5" width="415" valign="top">Hệ cao đẳng ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin: 800 chỉ ti&ecirc;u, lấy bằng điểm s&agrave;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n</strong></p>\r\n<p>&nbsp;</p>', 1, 1, 737, 0, 1),
(96, 'Hai học sinh lớp 11 đoạt huy chương Olympic Vật lý', 'Trong số 5 học sinh Việt Nam đoạt huy chương Olympic Vật lý Quốc tế (IPHO) 41 tại Croatia, Phan Văn Trung (THPT Hà Nội - Amsterdam) và Nguyễn Hoành Đạo (Chuyên Lam Sơn, Thanh Hóa) là 2 học sinh trẻ nhất.\r\n', 'HCV.jpg', NULL, '<p class="Title">Hai học sinh lớp 11 đoạt huy chương Olympic Vật l&yacute;</p>\r\n<p class="Lead">Trong số 5 học sinh Việt Nam đoạt huy chương Olympic Vật l&yacute; Quốc tế (IPHO) 41 tại Croatia, Phan Văn Trung (THPT H&agrave; Nội - Amsterdam) v&agrave; Nguyễn Ho&agrave;nh Đạo (Chuy&ecirc;n Lam Sơn, Thanh H&oacute;a) l&agrave; 2 học sinh trẻ nhất.</p>\r\n<p class="Lead"><br />Theo th&ocirc;ng tin từ Bộ GD&amp;ĐT, em Đinh Anh Minh (lớp 12 THPT chuy&ecirc;n Quốc học Huế) đoạt huy chương V&agrave;ng, Phan Minh Trung (lớp 11, THPT Chuy&ecirc;n H&agrave; Nội - Amsterdam), Phạm B&igrave;nh Minh (lớp 12, THPT chuy&ecirc;n Trần Ph&uacute; - Hải Ph&ograve;ng) v&agrave; Phạm Văn Quyền (lớp 12, THPT chuy&ecirc;n L&ecirc; Hồng Phong - Nam Định) c&ugrave;ng đoạt huy chương Bạc. C&ograve;n Nguyễn Ho&agrave;nh Đạo (lớp 11, THPT chuy&ecirc;n Lam Sơn - Thanh Ho&aacute;) đoạt huy chương Đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="HCV.jpg" alt="Ảnh: TTXVN." width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đinh Anh Minh - huy chương V&agrave;ng Olympic Vật l&yacute; 41. Ảnh:&nbsp;<em>TTXVN.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Olympic Vật l&yacute; Quốc tế (IPHO) 41 tại Croatia (ng&agrave;y 17-25/7) c&oacute; 80 nước v&agrave; v&ugrave;ng l&atilde;nh thổ tham dự. Đo&agrave;n Việt Nam c&oacute; 5 học sinh tham dự. Chiều tối 27/7 đo&agrave;n sẽ về tới s&acirc;n bay Nội B&agrave;i.</p>\r\n<p class="Normal">Năm nay, đo&agrave;n Việt Nam cũng gi&agrave;nh được 2 HCB v&agrave; 1 HCĐ&nbsp;<a class="Normal" href="http://vnexpress.net/GL/Xa-hoi/Giao-duc/2010/07/3BA1E33F/">Olympic Sinh học Quốc tế</a>&nbsp;(IBO 21, H&agrave;n Quốc), v&agrave; 1 HCV, 4 HCB, 1 HCĐ tại&nbsp;<a class="Normal" href="http://vnexpress.net/GL/Xa-hoi/Giao-duc/2010/07/3BA1DF50/">Olympic To&aacute;n học Quốc tế</a>&nbsp;(IMO 51, Kazakhstan).</p>\r\n<p class="Normal"><strong>Tiến Dũng</strong></p>', 1, 1, 729, 0, 1),
(98, 'Nữ sinh báo chí đoạt giải đặc biệt cuộc thi hát tiếng Anh', 'Luôn dành thời gian chuẩn bị chu đáo trước khi lên sân khấu và sở hữu giọng ca mượt mà, Phan Khánh Hà, sinh viên Học viện Báo chí, đã được hội đồng giám khảo trao giải đặc biệt.', 'chung_ket_hat_ta_(5).jpg', NULL, '<h1 class="Title">Nữ sinh b&aacute;o ch&iacute; đoạt giải đặc biệt cuộc thi h&aacute;t tiếng Anh</h1>\r\n<h2 class="Lead">Lu&ocirc;n d&agrave;nh thời gian chuẩn bị chu đ&aacute;o trước khi l&ecirc;n s&acirc;n khấu v&agrave; sở hữu giọng ca mượt m&agrave;, Phan Kh&aacute;nh H&agrave;, sinh vi&ecirc;n Học viện B&aacute;o ch&iacute;, đ&atilde; được hội đồng gi&aacute;m khảo trao giải đặc biệt.</h2>\r\n<p class="Normal">hung kết cuộc thi h&aacute;t tiếng Anh Let''s get loud diễn ra tại H&agrave; Nội v&agrave;o tối 16/1, hai th&iacute; sinh được trao giải đặc biệt (một do hội đồng gi&aacute;m khảo v&agrave; một do kh&aacute;n giả b&igrave;nh chọn), một th&iacute; sinh được trao giải nhất. Tổng gi&aacute; trị giải thưởng l&agrave; 300 triệu đồng.</p>\r\n<p class="Normal">Chọn ca kh&uacute;c&nbsp;<em>The prayer</em>, Kh&aacute;nh H&agrave;, sinh vi&ecirc;n Khoa Quan hệ quốc tế, Học viện B&aacute;o ch&iacute; v&agrave; Tuy&ecirc;n truyền, đ&atilde; khoe được giọng ca ngọt ng&agrave;o, s&acirc;u lắng v&agrave; lối diễn xuất c&oacute; hồn. Phần minh họa v&agrave; h&aacute;t b&egrave; của c&aacute;c em nhỏ c&acirc;u lạc bộ Chim Sơn Ca khiến cho tiết mục của c&ocirc; sinh vi&ecirc;n b&aacute;o ch&iacute; đi v&agrave;o l&ograve;ng người.</p>\r\n<p class="Normal">H&agrave; cho biết h&aacute;t tiếng Anh với c&ocirc; l&agrave; đam m&ecirc;. Ngay từ nhỏ, c&ocirc; đ&atilde; m&agrave;y m&ograve; học h&aacute;t theo c&aacute;c ca sĩ tr&ecirc;n tivi v&agrave; được học th&ecirc;m tại nh&agrave; văn h&oacute;a. Những l&uacute;c rảnh rỗi, H&agrave; đều học tiếng Anh v&agrave; tập h&aacute;t bởi "muốn l&agrave;m một nh&agrave; ngoại giao th&igrave; trước hết phải giỏi tiếng Anh".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung_ket_hat_ta_(5).jpg" alt="Phan Kh&aacute;nh H&agrave;" width="495" height="353" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Phan Kh&aacute;nh H&agrave; đ&atilde; d&agrave;nh được giải đặc biệt do ban gi&aacute;m khảo b&igrave;nh chọn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhận giải đặc biệt do kh&aacute;n giả b&igrave;nh chọn, Nguyễn Duy T&ugrave;ng vỡ &ograve;a trong niềm hạnh ph&uacute;c. Cậu t&acirc;m sự, khi tất cả c&aacute;c bạn đều đ&atilde; c&oacute; giải, chỉ một m&igrave;nh đứng tr&ecirc;n s&acirc;n khấu vẫn "tay kh&ocirc;ng", T&ugrave;ng thấy rất c&ocirc; đơn. "Nếu kh&ocirc;ng phải con trai th&igrave; em đ&atilde; kh&oacute;c v&igrave; tủi th&acirc;n", T&ugrave;ng t&acirc;m sự.</p>\r\n<p class="Normal">Nhưng anh ch&agrave;ng k&iacute;nh cận Duy T&ugrave;ng đ&atilde; chinh phục được kh&aacute;n giả với sự đa t&agrave;i của m&igrave;nh. Tự đệm đ&agrave;n ghi ta thể hiện ca kh&uacute;c&nbsp;<em>One more Try&nbsp;</em>nhẹ nh&agrave;ng, trữ t&igrave;nh, T&ugrave;ng đ&atilde; tiếp tục khẳng định h&igrave;nh ảnh trẻ trung v&agrave; "đa di năng" của m&igrave;nh ở chặng thi cuối.</p>\r\n<p class="Normal">Cuộc thi &ldquo;Let&rsquo;s Get Loud&rdquo; do đ&agrave;i Truyền h&igrave;nh Việt Nam, Tập đo&agrave;n gi&aacute;o dục quốc tế Language Link, Trung t&acirc;m hỗ trợ, ph&aacute;t triển sinh vi&ecirc;n Việt Nam... phối hợp tổ chức. Đ&acirc;y l&agrave; cuộc thi h&aacute;t tiếng Anh được mong chờ d&agrave;nh cho học sinh, sinh vi&ecirc;n H&agrave; Nội.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y - B&aacute; Đ&ocirc;</strong></p>\r\n<p>&nbsp;</p>', 2, 1, 2963, 0, 1),
(101, 'Muôn kiểu chống rét của sinh viên', 'Lấy máy sấy sấy cho chăn ấm lên trước khi đi ngủ, thắp bóng điện dây tóc công suất lớn hoặc đổ nước ấm vào chai để ôm là những cách để chống rét của sinh viên Hà Nội.', 'retmoi.jpg', NULL, '<h1 class="Title">Mu&ocirc;n kiểu chống r&eacute;t của sinh vi&ecirc;n</h1>\r\n<h2 class="Lead">Lấy m&aacute;y sấy sấy cho chăn ấm l&ecirc;n trước khi đi ngủ, thắp b&oacute;ng điện d&acirc;y t&oacute;c c&ocirc;ng suất lớn hoặc đổ nước ấm v&agrave;o chai để &ocirc;m l&agrave; những c&aacute;ch để chống r&eacute;t của sinh vi&ecirc;n H&agrave; Nội.</h2>\r\n<p class="Normal">10 ng&agrave;y nay r&eacute;t đậm k&eacute;o d&agrave;i, sinh vi&ecirc;n ở k&yacute; t&uacute;c x&aacute; hay thu&ecirc; trọ nh&aacute;o nh&agrave;o t&igrave;m c&aacute;ch giữ ấm. Ra đường th&igrave; quần &aacute;o, mũ m&atilde;o, găng tay che k&iacute;n, ai cũng thực hiện nguy&ecirc;n tắc "chỉ để hở hai con mắt" cho khỏi r&eacute;t.</p>\r\n<p class="Normal">"Chỉ cần hở chỗ n&agrave;o l&agrave; lạnh buốt chỗ đ&oacute; n&ecirc;n bọn em cố gắng tiết kiệm tiền để sắm cho đầy đủ phụ kiện như mũ len, găng tay, bịt tai, khăn, &aacute;o. C&aacute;c bạn ở xa đi học bằng xe m&aacute;y c&oacute; khi c&ograve;n mặc th&ecirc;m &aacute;o mưa, đội mũ bảo hiểm c&oacute; k&iacute;nh chắn gi&oacute; nữa", Hạnh Hoa (Học viện B&aacute;o ch&iacute;) cho biết.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="retmoi.jpg" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">D&ugrave; phải chắt b&oacute;p chi ti&ecirc;u, sinh vi&ecirc;n vẫn cố gắng sắm đủ đồ giữ ấm như bịt tai, găng tay, mũ len. Ảnh:&nbsp;<em>Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Những d&atilde;y trọ thường n&oacute;ng về m&ugrave;a h&egrave;, lạnh về m&ugrave;a đ&ocirc;ng n&ecirc;n c&aacute;c cử nh&acirc;n tương lai đ&atilde; truyền tai nhau c&aacute;ch giữ ấm mới được "ph&aacute;t minh". "Em c&oacute; m&aacute;y sấy t&oacute;c, m&ugrave;a đ&ocirc;ng được sử dụng tối đa v&igrave; hết sấy tay ch&acirc;n cho khỏi c&oacute;ng, trước l&uacute;c đi ngủ n&oacute; c&ograve;n được trưng dụng để sấy chăn, tạo hơi ấm ở b&ecirc;n trong", Thu C&uacute;c (sinh vi&ecirc;n ĐH Thương mại) kể.</p>\r\n<p class="Normal">Theo C&uacute;c, sau khi sấy chăn phồng l&ecirc;n v&agrave; n&oacute;ng dần, tỏa hơi ấm ra xung quanh. Nếu như trước đ&acirc;y nằm v&agrave;o chăn, c&ocirc; phải xu&yacute;t xoa mất một l&uacute;c để ủ ấm th&igrave; b&acirc;y giờ c&oacute; thể thoải m&aacute;i ngủ đến s&aacute;ng. C&uacute;c đ&atilde; th&ocirc;ng b&aacute;o "s&aacute;ng kiến" n&agrave;y cho cả x&oacute;m trọ. Vậy l&agrave; đ&ecirc;m n&agrave;o, chiếc m&aacute;y sấy cũng được cả x&oacute;m chuyền tay nhau.</p>\r\n<p class="Normal">Trời r&eacute;t, mặc năm chiếc &aacute;o ấm m&agrave; Ho&agrave;ng Thảo (ĐH Y H&agrave; Nội) vẫn chưa hết run. Ngồi học b&agrave;i nhưng tay lạnh c&oacute;ng n&ecirc;n Thảo chẳng viết được g&igrave;. Đứng dậy đ&oacute;ng hết cửa sổ, Thảo đem hết c&aacute;c b&oacute;ng đ&egrave;n học của mấy đứa bạn trong ph&ograve;ng ra thắp.</p>\r\n<p class="Normal">"B&oacute;ng điện d&acirc;y t&oacute;c khi thắp l&ecirc;n sẽ tỏa nhiệt rất ấm. Đứa bạn lớp m&igrave;nh c&oacute; s&aacute;ng kiến bật đ&egrave;n rồi đưa v&agrave;o trong chăn cho ấm, nhưng m&igrave;nh sợ r&ograve; rỉ điện n&ecirc;n kh&ocirc;ng d&aacute;m l&agrave;m", Thảo chia sẻ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="retmoi1.jpg" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sấy chăn cho phồng l&ecirc;n v&agrave; ấm n&oacute;ng l&agrave; một trong nhiều chi&ecirc;u giữ ấm của sinh vi&ecirc;n. Ảnh<em>: Ho&agrave;ng Th&ugrave;y.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ở trọ trong ng&otilde; 315 đường Nguyễn Khang (Cầu Giấy), Nguyễn Thị Toan v&agrave; hai người bạn tối n&agrave;o cũng đun một si&ecirc;u nước n&oacute;ng. Trong ph&ograve;ng, Toan d&agrave;nh ri&ecirc;ng một g&oacute;c xếp h&agrave;ng chục vỏ chai lavi. "Nhớ lại ng&agrave;y ở nh&agrave; mẹ thường đổ nước ấm v&agrave;o chai để chườm chỗ sưng, thế l&agrave; bọn em đun nước, để nguội một ch&uacute;t v&agrave; đổ v&agrave;o chai lavi rồi &ocirc;m ch&uacute;ng khi ngủ. Đ&uacute;ng l&agrave; s&aacute;ng kiến của &ocirc;ng b&agrave; thật hữu hiệu, bọn em ngủ rất ngon l&agrave;nh", Toan vui vẻ cho hay.</p>\r\n<p class="Normal">C&ocirc; cho biết, trong x&oacute;m c&oacute; một số người đ&atilde; l&agrave;m theo c&aacute;ch n&agrave;y, nhưng được v&agrave;i h&ocirc;m th&igrave; ngại đun nước n&ecirc;n t&igrave;m mua t&uacute;i chườm n&oacute;ng đa năng với gi&aacute; khoảng 80.000-90.000 đồng. Trước khi đi ngủ cắm điện khoảng 5 đến 10 ph&uacute;t, sau đ&oacute; &ocirc;m t&uacute;i đi ngủ sẽ ấm cả đ&ecirc;m.</p>\r\n<p class="Normal">D&ugrave;ng một chăn b&ocirc;ng l&agrave;m đệm, tr&ecirc;n l&agrave; hai chiếc chăn đắp, nhưng "đ&ocirc;i bạn c&ugrave;ng tiến" H&ugrave;ng v&agrave; Khoa (Học viện T&agrave;i ch&iacute;nh) vẫn thấy lạnh n&ecirc;n phải nghĩ c&aacute;ch sưởi ấm căn ph&ograve;ng. "Ch&uacute;ng em vừa bước sang học kỳ mới. Do đăng k&yacute; t&iacute;n chỉ n&ecirc;n học rất vất vả, nếu kh&ocirc;ng cẩn thận th&igrave; thi lại, học lại mệt lắm, lại tốn tiền nữa. Tụi em đ&agrave;nh hy sinh chiếc chậu nh&ocirc;m v&agrave; đi mua &iacute;t củi về đốt", H&ugrave;ng cho hay.</p>\r\n<p class="Normal">"D&ugrave; tốn th&ecirc;m ch&uacute;t tiền nhưng được c&aacute;i ấm c&uacute;ng. Cả x&oacute;m chỉ c&oacute; ph&ograve;ng em l&agrave; l&uacute;c n&agrave;o cũng đỏ lửa", Khoa cười.</p>\r\n<p class="Normal">Bắt đầu từ ng&agrave;y 2/1 v&agrave; dự b&aacute;o k&eacute;o d&agrave;i &iacute;t nhất đến 21/1, đợt r&eacute;t đậm, r&eacute;t hại đang ho&agrave;nh h&agrave;nh ở miền Bắc được đ&aacute;nh gi&aacute; chỉ đứng sau đợt r&eacute;t lịch sử đầu năm 2008 k&eacute;o d&agrave;i 38 ng&agrave;y. Nhiệt độ thấp nhất ở miền Bắc đ&atilde; xuống xấp xỉ -4 độ, tại H&agrave; Nội l&agrave; 8 độ C.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p>&nbsp;</p>', 2, 1, 782, 0, 1),
(106, 'Màn tỏ tình ấn tượng trong giá rét 10 độ C', 'Trong cái lạnh tê cóng 10 độ C, giữa bãi sông Hồng (Hà Nội), một chàng trai miệt mài xe cát thành hình trái tim lớn, viết tên mình và người yêu lên đó với hy vọng bạn gái nhìn thấy.', 'to_tinh1990-6.jpg', NULL, '<h1 class="Title">M&agrave;n tỏ t&igrave;nh ấn tượng trong gi&aacute; r&eacute;t 10 độ C</h1>\r\n<h2 class="Lead">Trong c&aacute;i lạnh t&ecirc; c&oacute;ng 10 độ C, giữa b&atilde;i s&ocirc;ng Hồng (H&agrave; Nội), một ch&agrave;ng trai miệt m&agrave;i xe c&aacute;t th&agrave;nh h&igrave;nh tr&aacute;i tim lớn, viết t&ecirc;n m&igrave;nh v&agrave; người y&ecirc;u l&ecirc;n đ&oacute; với hy vọng bạn g&aacute;i nh&igrave;n thấy.</h2>\r\n<p class="Normal">Ch&agrave;ng trai t&ecirc;n Tuấn Anh, 25 tuổi, ở phường Ph&uacute;c T&acirc;n (Ho&agrave;n Kiếm, H&agrave; Nội). Chiều 6/1, chọn b&atilde;i giữa s&ocirc;ng Hồng dưới ch&acirc;n cầu Long Bi&ecirc;n l&agrave;m địa điểm, Tuấn Anh miệt m&agrave;i vẽ tr&aacute;i tim lớn tr&ecirc;n nền c&aacute;t. Sau nhiều giờ, một tr&aacute;i tim lớn viết t&ecirc;n 2 người hiện l&ecirc;n tr&ecirc;n nền c&aacute;t.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="to_tinh1990-6.jpg" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tuấn Anh vui mừng v&igrave; sản phẩm sắp ho&agrave;n th&agrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ocirc;ng việc sắp ho&agrave;n th&agrave;nh th&igrave; những đợt gi&oacute; lớn li&ecirc;n tục thổi cuốn theo bụi c&aacute;t s&ocirc;ng Hồng, san lấp đi những g&igrave; m&agrave; ch&agrave;ng trai đa t&igrave;nh đang thể hiện. Tuấn Anh lại h&igrave; hụi đ&agrave;o s&acirc;u hơn v&agrave; đắp c&aacute;t nổi cao hơn.</p>\r\n<p class="Normal">Cậu cho biết, chiều bạn g&aacute;i của cậu sẽ qua đ&acirc;y n&ecirc;n hy vọng sẽ nh&igrave;n thấy v&agrave; cảm động. "Mong l&agrave; trời đừng mưa", Tuấn Anh hồ hởi.</p>\r\n<p class="Normal">M&agrave;n chuẩn bị tỏ t&igrave;nh l&atilde;ng mạn đ&atilde; thu h&uacute;t sự quan t&acirc;m của nhiều người qua cầu Long Bi&ecirc;n, đặc biệt l&agrave; những bạn trẻ.</p>\r\n<p class="Normal" style="text-align: right;">(Theo&nbsp;<em>C&ocirc;ng an Nh&acirc;n d&acirc;n</em>)</p>\r\n<p>&nbsp;</p>', 2, 1, 2692, 0, 1),
(111, 'Thử tài thiết kế thời trang của sinh viên', 'Mới lạ, độc đáo và mang tính ứng dụng cao là tiêu chí dành cho các tác phẩm dự thi "Hutech Designer 2011". Năm nay cuộc thi được mở rộng trong phạm vi toàn quốc.', 'thiet-ke-5.jpg', NULL, '<h1 class="Title">Thử t&agrave;i thiết kế thời trang của sinh vi&ecirc;n</h1>\r\n<h2 class="Lead">Mới lạ, độc đ&aacute;o v&agrave; mang t&iacute;nh ứng dụng cao l&agrave; ti&ecirc;u ch&iacute; d&agrave;nh cho c&aacute;c t&aacute;c phẩm dự thi "Hutech Designer 2011". Năm nay cuộc thi được mở rộng trong phạm vi to&agrave;n quốc.</h2>\r\n<p class="Normal">Cuộc thi d&agrave;nh cho tất cả sinh vi&ecirc;n, học sinh c&aacute;c trường ĐH, CĐ, TCCN v&agrave; c&aacute;c bạn trẻ y&ecirc;u th&iacute;ch thiết kế thời trang trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class="Normal">Với chủ đề "H&agrave;nh tinh xanh", th&ocirc;ng điệp ban tổ chức gửi đến c&aacute;c bạn trẻ qua cuộc thi l&agrave; h&atilde;y chung tay bảo vệ m&ocirc;i trường. Đ&acirc;y cũng được coi l&agrave; một trong những ti&ecirc;u ch&iacute; lựa chọn cho c&aacute;c t&aacute;c phẩm dự thi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="thiet-ke-5.jpg" border="1" alt="Một trong những mẫu thiết kế đạt giải cao ở cuộc thi " width="480" height="352" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một trong những mẫu thiết kế đạt giải cao ở cuộc thi "Hutech Designer 2010".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">"Đ&acirc;y l&agrave; cơ hội để c&aacute;c bạn trẻ m&ecirc; thiết kế thời trang thể hiện sự s&aacute;ng tạo, giao lưu học hỏi ở những người từng đạt giải tại cuộc thi lần trước. Đồng thời c&aacute;c bạn sẽ chứng tỏ khả năng lĩnh hội kiến thức được học từ nh&agrave; trường qua c&aacute;c thiết kế dự thi", hiệu trưởng ĐH Kỹ thuật C&ocirc;ng nghệ TP HCM Hồ Đắc Lộc, đơn vị tổ chức cuộc thi n&oacute;i.</p>\r\n<p class="Normal">Mẫu vẽ tham gia v&ograve;ng loại của cuộc thi sẽ được nhận từ 2/1 đến 18/2/2011 tại ĐH Kỹ thuật c&ocirc;ng nghệ - 144/24 Điện Bi&ecirc;n Phủ, phường 25, quận B&igrave;nh Thạnh. Mỗi th&iacute; sinh tham dự được quyền gửi từ 5 đến 10 mẫu vẽ. 15 bộ sưu tập tốt nhất của th&iacute; sinh được chọn sẽ c&ocirc;ng bố v&agrave;o ng&agrave;y 10/3 v&agrave; tiến h&agrave;nh may mẫu thật để tranh t&agrave;i ở v&ograve;ng chung kết.</p>\r\n<p class="Normal">Đ&ecirc;m chung kết trao giải sẽ diễn ra v&agrave;o ng&agrave;y 23/4/2011 tại Nh&agrave; thi đấu Phan Đ&igrave;nh Ph&ugrave;ng, TP HCM. Cuộc thi c&oacute; một giải đặc biệt trị gi&aacute; 30 triệu đồng v&agrave; 4 giải phụ gồm: &yacute; tưởng, m&agrave;u sắc, phối hợp chất liệu v&agrave; kỹ thuật.</p>\r\n<p class="Normal">Ban gi&aacute;m khảo cuộc thi l&agrave; một số giảng vi&ecirc;n c&aacute;c trường đại học v&agrave; nh&agrave; thiết kế Sỹ Ho&agrave;ng, si&ecirc;u mẫu H&agrave; Anh.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Duy&ecirc;n</strong></p>', 2, 1, 132, 0, 1),
(112, 'Hải Yến đoạt giải hoa khôi sinh viên Hà Nội', 'Không chỉ được Ban giám khảo trao danh hiệu Hoa khôi sinh viên Hà Nội 2010, Hải Yến - sinh viên CĐ Cộng đồng Hà Nội còn là thí sinh được khán giả bình chọn nhiều nhất qua mạng.', 'hoakhoi19.jpg', NULL, '<h1 class="Title">Hải Yến đoạt giải hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội</h1>\r\n<h2 class="Lead">Kh&ocirc;ng chỉ được Ban gi&aacute;m khảo trao danh hiệu Hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội 2010, Hải Yến - sinh vi&ecirc;n CĐ Cộng đồng H&agrave; Nội c&ograve;n l&agrave; th&iacute; sinh được kh&aacute;n giả b&igrave;nh chọn nhiều nhất qua mạng.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td class="Image">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi19.jpg" alt="" width="495" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cuộc thi hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội l&agrave; cuộc thi t&ocirc;n vinh vẻ đẹp sinh vi&ecirc;n c&aacute;c trường ĐH, CĐ ngo&agrave;i c&ocirc;ng lập của thủ đ&ocirc;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi50.jpg" alt="" width="495" height="308" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Năm nay cuộc thi chọn ra 25 th&iacute; sinh xuất sắc nhất tranh t&agrave;i ở đ&ecirc;m chung kết tối 26/12.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi14.jpg" alt="" width="495" height="359" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hoa hậu Việt Nam 2004 Nguyễn Thị Huyền đến dự với tư c&aacute;ch th&agrave;nh vi&ecirc;n Ban gi&aacute;m khảo cuộc thi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi49.jpg" alt="" width="495" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Top 5 th&iacute; sinh lọt v&agrave;o phần thi ứng xử.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle" valign="center">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi44.jpg" alt="" width="380" height="632" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mai Th&ugrave;y Dương (ĐH Nguyễn Tr&atilde;i) l&agrave; th&iacute; sinh mặc &aacute;o d&agrave;i đẹp nhất.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi8.jpg" alt="" width="380" height="588" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Danh hiệu &Aacute; kh&ocirc;i 1 v&agrave; ứng xử hay nhất thuộc về Cầm Thu Huyền (CĐ Kinh tế Kỹ thuật).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi69.jpg" alt="" width="380" height="570" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Danh hiệu &Aacute; kh&ocirc;i 2 được trao cho Lương Ngọc Linh (ĐH Nguyễn Tr&atilde;i).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="middle">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi5.jpg" alt="" width="380" height="570" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hoa kh&ocirc;i Hải Yến (CĐ Cộng đồng H&agrave; Nội) trả lời phần thi ứng xử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td class="Image">\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoakhoi79.jpg" alt="" width="495" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Kh&ocirc;ng chỉ được Ban gi&aacute;m khảo trao danh hiệu Hoa kh&ocirc;i sinh vi&ecirc;n H&agrave; Nội 2010, c&ocirc; sinh vi&ecirc;n CĐ Cộng đồng H&agrave; Nội c&ograve;n được kh&aacute;n giả b&igrave;nh chọn nhiều nhất qua mạng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>L&ecirc; Hiếu</strong></p>', 2, 1, 1536, 0, 1),
(115, 'Tuyển người yêu đi chơi Noel', '"Noel sắp đến mà tình yêu tan vỡ nên cần thuê người đi chơi cùng. Bờ vai sẽ được nhận quà do mình chuẩn bị". Chỉ sau một ngày đăng trên mạng, Hương (23 tuổi) đóng topic vì đã tìm được đối tác trong đêm Giáng sinh.', 'Noel-2.jpg', NULL, '<h1 class="Title">Tuyển người y&ecirc;u đi chơi Noel</h1>\r\n<h2 class="Lead">"Noel sắp đến m&agrave; t&igrave;nh y&ecirc;u tan vỡ n&ecirc;n cần thu&ecirc; người đi chơi c&ugrave;ng. Bờ vai sẽ được nhận qu&agrave; do m&igrave;nh chuẩn bị". Chỉ sau một ng&agrave;y đăng tr&ecirc;n mạng, Hương (23 tuổi) đ&oacute;ng topic v&igrave; đ&atilde; t&igrave;m được đối t&aacute;c trong đ&ecirc;m Gi&aacute;ng sinh.</h2>\r\n<p class="Normal">Một tuần nay, c&aacute;c diễn đ&agrave;n tr&ecirc;n mạng tr&agrave;n ngập những lời rao tuyển bạn g&aacute;i (bạn trai) đi chơi Noel. Chủ nh&acirc;n đều c&oacute; phần giới thiệu về bản th&acirc;n, nội dung tuyển, y&ecirc;u cầu v&agrave; quyền lợi của người được tuyển. L&yacute; do m&agrave; c&aacute;c bạn đưa ra l&agrave; ng&agrave;y lễ, kh&ocirc;ng muốn ở nh&agrave; nằm ngủ hay đi chơi một m&igrave;nh.</p>\r\n<p class="Normal">Tr&ecirc;n một diễn đ&agrave;n, c&ocirc; g&aacute;i c&oacute; ngoại h&igrave;nh kh&aacute; dễ thương viết: "T&igrave;nh h&igrave;nh l&agrave; sắp đến Noel rồi m&agrave; em th&igrave; vẫn chưa c&oacute; người y&ecirc;u. Ai chưa c&oacute; người y&ecirc;u th&igrave; đi chơi với em". H&agrave;ng loạt tin nhắn trả lời với những c&acirc;u hỏi "Ti&ecirc;u chuẩn thế n&agrave;o? Em đang ở đ&acirc;u? Quyền lợi v&agrave; nghĩa vụ l&agrave; g&igrave;?...".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Noel-2.jpg" alt="" width="480" height="230" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lời đăng tuyển của nickname t&ecirc;n Bi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&oacute; bạn c&ograve;n đưa cả ảnh c&aacute; nh&acirc;n v&agrave; số điện thoại k&egrave;m theo những d&ograve;ng giới thiệu "Em t&ecirc;n Bi, 21 tuổi. Noel lạnh qu&aacute; n&ecirc;n cần t&igrave;m bạn đi c&ugrave;ng. Ti&ecirc;u chuẩn l&agrave; tr&ecirc;n 22 tuổi, vui vẻ, h&ograve;a đồng, chững chạc". Khi gọi đến số điện thoại tr&ecirc;n, Bi nhấc m&aacute;y v&agrave; th&ocirc;ng b&aacute;o m&igrave;nh đang ở trong lớp học.</p>\r\n<p class="Normal">Tr&ecirc;n một trang web, c&ocirc; g&aacute;i giới thiệu t&ecirc;n Hương 23 tuổi, đang l&agrave;m gi&aacute;o vi&ecirc;n ở Hải Ph&ograve;ng nhắn: "Noel sắp đến m&agrave; t&igrave;nh y&ecirc;u th&igrave; tan vỡ n&ecirc;n cần thu&ecirc; người đi chơi c&ugrave;ng". C&ocirc; đảm bảo bờ vai được thu&ecirc; sẽ kh&ocirc;ng sứt mẻ, được nhận qu&agrave; do c&ocirc; chuẩn bị v&agrave; kh&ocirc;ng phải chịu một khoản ph&iacute; n&agrave;o ph&aacute;t sinh.</p>\r\n<p class="Normal">Ngay sau khi đăng tuyển, c&ocirc; g&aacute;i nhận được rất nhiều sự quan t&acirc;m của cộng đồng mạng. Hơn một ng&agrave;y sau đ&oacute;, c&ocirc; tuy&ecirc;n bố "đ&oacute;ng topic v&igrave; chuẩn bị hợp đồng với một anh".</p>\r\n<p class="Normal">Nhiều &yacute; kiến cho rằng đ&acirc;y l&agrave; một c&aacute;ch hay để những người c&ocirc; đơn t&igrave;m đến với nhau. Tuy nhi&ecirc;n, kh&ocirc;ng &iacute;t người &aacute;i ngại bởi "Noel hay tắc đường, tắc đường th&igrave; kh&ocirc;ng về được nh&agrave;, m&agrave; kh&ocirc;ng về được nh&agrave; th&igrave; phải về nh&agrave;... nghỉ". Hay c&oacute; người lo xa hơn "lỡ gặp phải Sở Khanh l&agrave; hết đời lu&ocirc;n".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bo_vai.jpg" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiều bạn chia sẻ, thấy người ta c&oacute; đ&ocirc;i, m&igrave;nh lẻ b&oacute;ng, buồn qu&aacute; n&ecirc;n t&igrave;m đến dịch vụ thu&ecirc; người y&ecirc;u. Ảnh minh họa của&nbsp;<em>L&ecirc; Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kh&ocirc;ng chỉ ph&aacute;i đẹp, c&aacute;c boy cũng thi nhau rao tuyển. Hưng, nam sinh vi&ecirc;n ở H&agrave; Nội nhắn: "Cần tuyển người đi chơi Noel v&agrave; Tết dương lịch. M&igrave;nh đẹp trai nhưng hơi k&eacute;n chọn n&ecirc;n vẫn chưa c&oacute; người y&ecirc;u. Ai quan t&acirc;m th&igrave; nhắn v&agrave;o nick... Quyền lợi m&agrave; c&aacute;c bạn được hưởng l&agrave; đi xem phim, ăn KFC hay t&ugrave;y chọn c&aacute;c nh&agrave; h&agrave;ng ăn kh&aacute;c miễn ph&iacute;. Ngo&agrave;i ra, c&ograve;n được nhận lương cứng 500.000 đồng một ng&agrave;y".</p>\r\n<p class="Normal"><span style="color: #000000;">Y&ecirc;u cầu m&agrave; ch&agrave;ng trai n&agrave;y đưa ra l&agrave; bạn g&aacute;i c</span><span style="color: #000000;">&oacute; ngoại h&igrave;nh ưa nh&igrave;n, hiền l&agrave;nh, ăn n&oacute;i dễ thương c&agrave;ng tốt, đặc biệt l&agrave; phải chưa c&oacute; chồng hay bạn trai. Sau v&agrave;i ng&agrave;y đăng tin, bạn trai n&agrave;y x&aacute;c nhận, c&oacute; rất nhiều c&ocirc; g&aacute;i đ&atilde; add nick v&agrave; hỏi chuyện khiến v&ograve;ng "sơ tuyển" v&ocirc; c&ugrave;ng nhộn nhịp.</span></p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Tuy nhi&ecirc;n, để tr&aacute;nh rủi ro, kh&ocirc;ng &iacute;t người bỏ tiền thu&ecirc; người y&ecirc;u để đi chơi Noel</span></strong>. Đ&igrave;nh Thắng, một kỹ sư đang l&agrave;m việc tại Cầu Giấy cho biết, c&ocirc;ng việc bận rộn khiến anh chẳng c&ograve;n thời gian cho chuyện ri&ecirc;ng. Noel thấy người ta đi chơi hết, ở nh&agrave; một m&igrave;nh buồn n&ecirc;n đ&atilde; nhờ tới dịch vụ cho thu&ecirc; người y&ecirc;u.</p>\r\n<p class="Normal">"Bỏ ra tiền triệu để c&oacute; được niềm vui, đ&oacute; cũng l&agrave; sự b&ugrave; trừ th&ocirc;i. B&igrave;nh thường t&ocirc;i l&agrave;m việc nhiều để kiếm tiền n&ecirc;n qu&ecirc;n mất sự c&ocirc; đơn. Những ng&agrave;y lễ Tết mới sực nhớ đến, c&aacute;ch tốt nhất l&agrave; d&ugrave;ng tiền để b&ugrave; đắp sự trống trải đ&oacute;", Thắng t&acirc;m sự.</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>VnExpress</em>, gi&aacute;m đốc một c&ocirc;ng ty cho thu&ecirc; người y&ecirc;u cho biết, dịp Noel năm nay, số lượng kh&aacute;ch h&agrave;ng tăng l&ecirc;n đột biến. Do đ&atilde; dự tr&ugrave; từ trước n&ecirc;n c&ocirc;ng ty vẫn đ&aacute;p ứng được nhu cầu của kh&aacute;ch h&agrave;ng.</p>\r\n<p class="Normal">"Ch&uacute;ng t&ocirc;i đ&atilde; giảm gi&aacute; một nửa cho kh&aacute;ch. Để thu&ecirc; một người đi chơi cả tối Noel, kh&aacute;ch h&agrave;ng phải trả một khoản ph&iacute; khoảng một triệu đồng, đồng thời phải cam kết đảm bảo an to&agrave;n cho người được thu&ecirc;", vị gi&aacute;m đốc n&agrave;y cho hay.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p class="Normal"><em>* T&ecirc;n nh&acirc;n vật đ&atilde; được thay đổi</em></p>\r\n<p>&nbsp;</p>', 2, 1, 2768, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(116, 'Những món quà dễ thương của teen mùa Giáng sinh', 'Những món quà handmade dễ thương, ý nghĩa đang là lựa chọn của các bạn trẻ dành tặng bạn bè và người yêu dịp Noel.', 'goi_in_hinh_1.jpg', NULL, '<h1 class="Title">Những m&oacute;n qu&agrave; dễ thương của teen m&ugrave;a Gi&aacute;ng sinh</h1>\r\n<h2 class="Lead">Những m&oacute;n qu&agrave; handmade dễ thương, &yacute; nghĩa đang l&agrave; lựa chọn của c&aacute;c bạn trẻ d&agrave;nh tặng bạn b&egrave; v&agrave; người y&ecirc;u dịp Noel.</h2>\r\n<p class="Normal">Cả tuần nay, Huy Tuấn (ĐH Quốc gia H&agrave; Nội) h&aacute;o hức l&ecirc;n phương &aacute;n chọn qu&agrave; cho bạn g&aacute;i dịp Noel. Sau hai th&aacute;ng y&ecirc;u nhau, cậu mới tặng cho bạn g&aacute;i những m&oacute;n qu&agrave; nho nhỏ như khăn ấm m&ugrave;a đ&ocirc;ng, tất tay, dịp n&agrave;y cậu muốn d&agrave;nh bất ngờ cho người thương.</p>\r\n<p class="Normal">Hỏi han bậc "đ&agrave;n anh" c&oacute; kinh nghiệm, thăm d&ograve; &yacute; kiến c&aacute;c bạn nữ, Tuấn quyết t&igrave;m m&oacute;n qu&agrave; "đẹp, độc, &yacute; nghĩa" l&agrave;m cho bạn g&aacute;i x&uacute;c động đến... ph&aacute;t kh&oacute;c. Tuy nhi&ecirc;n, lang thang c&aacute;c phố b&aacute;n đồ lưu niệm suốt một ng&agrave;y m&agrave; Tuấn chưa t&igrave;m ra m&oacute;n n&agrave;o "độc".</p>\r\n<p class="Normal"><span style="color: #000000;">Đến khi người bạn th&ocirc;ng b&aacute;o c&oacute; một cửa h&agrave;ng l&agrave;m gối handmade in h&igrave;nh theo y&ecirc;u cầu, Tuấn mừng rơn v&agrave; ph&oacute;ng một mạch đến đặt ngay chiếc gối h&igrave;nh tr&aacute;i tim c&oacute; in h&igrave;nh hai đứa với lời nhắn "Ngủ ngon nha c&uacute;n y&ecirc;u".</span></p>\r\n<p class="Normal">"Mỗi khi ngủ c&ocirc; ấy lại nh&igrave;n chiếc gối v&agrave; nhớ đến em. Hy vọng lời ch&uacute;c tr&ecirc;n đ&oacute; sẽ l&agrave;m c&ocirc; ấy thấy ấm &aacute;p trong m&ugrave;a đ&ocirc;ng lạnh gi&aacute; n&agrave;y", Tuấn n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="goi_in_hinh_1.jpg" alt="" width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">In h&igrave;nh người y&ecirc;u l&ecirc;n gối l&agrave;m qu&agrave; tặng đang l&agrave; "mốt" của teen.&nbsp;<em>Ảnh: HT.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">L&agrave; người l&atilde;ng mạn, Phạm H&ugrave;ng (ĐH B&aacute;ch khoa H&agrave; Nội) lu&ocirc;n tạo sự bất ngờ cho bạn g&aacute;i. Dịp Noel n&agrave;y, ngo&agrave;i việc dẫn n&agrave;ng đi chơi, H&ugrave;ng đ&atilde; nghe ng&oacute;ng c&oacute; dịch vụ mới, đ&oacute; cơm hộp santu với những suất ăn được tạo h&igrave;nh theo y&ecirc;u cầu.</p>\r\n<p class="Normal">"Hai đứa ngồi b&ecirc;n nhau, c&ugrave;ng ăn cơm được tạo h&igrave;nh &ocirc;ng gi&agrave; Noel, ng&ocirc;i nh&agrave; tuyết, c&oacute; những b&iacute;t tất v&agrave; hộp qu&agrave; với lời ch&uacute;c mừng Gi&aacute;ng sinh v&agrave; "I love you" th&igrave; c&ograve;n g&igrave; tuyệt vời bằng", H&ugrave;ng n&oacute;i.</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>VnExpress</em>, Trưởng nh&oacute;m l&agrave;m cơm hộp santu Hồ Thị Thanh Huyền cho biết, nh&oacute;m c&oacute; 3 người đều l&agrave; sinh vi&ecirc;n. Từ đầu m&ugrave;a đến giờ, cả nh&oacute;m tất bật v&igrave; lượng đặt h&agrave;ng rất lớn. Gi&aacute; mỗi suất từ 120.000 đồng trở l&ecirc;n, bao gồm cả hộp v&agrave; th&igrave;a.</p>\r\n<p class="Normal">"Bọn em l&agrave;m theo mẫu tự nghĩ ra, đồng thời cũng l&agrave;m theo y&ecirc;u cầu của kh&aacute;ch h&agrave;ng", Huyền cho hay.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nha_noel.jpg" alt="" width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&ocirc;i nh&agrave; với &ocirc;ng gi&agrave; Noel, đầy tuyết v&agrave; lời ch&uacute;c an l&agrave;nh được giới trẻ săn t&igrave;m.&nbsp;<em>Ảnh: HT.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ng&ocirc;i nh&agrave; Gi&aacute;ng sinh được phủ đầy tuyết với &ocirc;ng gi&agrave; Noel cũng l&agrave; mẫu qu&agrave; được giới trẻ săn đ&oacute;n. L&ecirc; Minh (ĐH Kinh tế) cho biết, do lang thang ở tr&ecirc;n mạng cậu v&ocirc; t&igrave;nh bắt gặp lời giới thiệu về ng&ocirc;i nh&agrave; Gi&aacute;ng sinh. Xem qua c&aacute;c mẫu, cậu vui sướng đặt l&agrave;m một ng&ocirc;i nh&agrave; đ&ocirc;i với lời ch&uacute;c an l&agrave;nh cho bạn g&aacute;i. Một tr&aacute;i tim xinh xắn l&agrave;m biển chỉ dẫn v&agrave;o ng&ocirc;i nh&agrave; với hai từ "hạnh ph&uacute;c".</p>\r\n<p class="Normal">"Ng&ocirc;i nh&agrave; l&agrave; lời nhắn gửi về một tương lại hạnh ph&uacute;c của hai đứa. C&oacute; những m&oacute;n qu&agrave; gi&aacute; trị rất lớn nhưng &yacute; nghĩa th&igrave; kh&ocirc;ng thể n&agrave;o bằng m&oacute;n qu&agrave; thủ c&ocirc;ng n&agrave;y", Minh chia sẻ.</p>\r\n<p class="Normal">Thiệp handmade với những h&igrave;nh ảnh &ocirc;ng gi&agrave; Noel, c&acirc;y th&ocirc;ng, tuyết trắng... cũng được giới trẻ t&igrave;m kiếm. Quỳnh Anh (Học viện B&aacute;o ch&iacute;) cho biết, năm n&agrave;o đến Gi&aacute;ng sinh c&ocirc; cũng d&agrave;nh thời gian l&agrave;m thiệp để tặng bạn b&egrave;, người th&acirc;n. Sau buổi học, c&ocirc; ngồi cặm cụi b&ecirc;n đống giấy m&agrave;u cứng, những c&acirc;y b&uacute;t m&agrave;u &oacute;ng &aacute;nh v&agrave; miệt m&agrave;i cắt d&aacute;n.</p>\r\n<p class="Normal">"D&ugrave; phải thức rất khuya để l&agrave;m em vẫn thấy hạnh ph&uacute;c bởi th&ocirc;ng qua tấm thiệp đ&oacute;, em muốn gửi t&igrave;nh cảm của m&igrave;nh với những người m&igrave;nh thương y&ecirc;u", Quỳnh Anh chia sẻ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>\r\n<p>&nbsp;</p>', 2, 1, 176, 0, 1),
(117, 'Gửi thiệp Giáng sinh cho người yêu thương', '“Tặng thiệp Giáng sinh – Rinh quà hấp dẫn” là chương trình do báo điện tử Ngoisao.net tổ chức. Những chiếc thiệp do độc giả sáng tạo hoặc lấy từ mẫu của chương trình sẽ được đăng và được BTC gửi tới e-mail người nhận.', 'Thiep-1.jpg', NULL, '<h1 class="Title">Gửi thiệp Gi&aacute;ng sinh cho người y&ecirc;u thương</h1>\r\n<h2 class="Lead">&ldquo;Tặng thiệp Gi&aacute;ng sinh &ndash; Rinh qu&agrave; hấp dẫn&rdquo; l&agrave; chương tr&igrave;nh do b&aacute;o điện tử Ngoisao.net tổ chức. Những chiếc thiệp do độc giả s&aacute;ng tạo hoặc lấy từ mẫu của chương tr&igrave;nh sẽ được đăng v&agrave; được BTC gửi tới e-mail người nhận.</h2>\r\n<p class="Normal">Chương tr&igrave;nh k&eacute;o d&agrave;i tới ng&agrave;y 25/12 nhằm tạo điều kiện cho mọi người cơ hội b&agrave;y tỏ t&igrave;nh cảm của m&igrave;nh với người th&acirc;n, bạn b&egrave;&hellip; nh&acirc;n dịp Gi&aacute;ng sinh. Độc giả truy cập v&agrave;o&nbsp;<a class="Normal" href="http://event.ngoisao.net/xmas">http://event.ngoisao.net/xmas</a>, tự tạo thiệp hoặc chọn theo mẫu sẵn của chương tr&igrave;nh, sau đ&oacute; điền c&aacute;c th&ocirc;ng tin theo y&ecirc;u cầu để l&agrave;m căn cứ trao giải. Thiệp của người gửi sẽ được ban tổ chức đăng tr&ecirc;n website của chương tr&igrave;nh v&agrave; gửi email th&ocirc;ng b&aacute;o đến cho người được nhận thiệp.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Thiep-1.jpg" border="1" alt="" width="400" height="260" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Những tấm thiệp do bạn đọc tự thiết kế c&oacute; k&iacute;ch thước kh&ocirc;ng qu&aacute; 1.000 x 1.000 pixel v&agrave; dung lượng kh&ocirc;ng qu&aacute; 1 MB. Phần ti&ecirc;u đề của thiệp tối đa 200 k&yacute; tự v&agrave; nội dung của thiệp chỉ 1.000 k&yacute; tự. Thiệp của độc giả sẽ được đăng tr&ecirc;n website của chương tr&igrave;nh v&agrave; được th&ocirc;ng b&aacute;o đến địa chỉ email của người nhận trong thời gian chậm nhất 24h kể từ khi gửi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="404" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Thiep-2.jpg" border="1" alt="" width="400" height="270" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">10 thiệp đẹp nhất do ban tổ chức chọn ra sẽ nhận được một phần qu&agrave; của mỹ phẩm Quốc Hưng trị gi&aacute; 2.500.000 đồng. Ngo&agrave;i ra, chương tr&igrave;nh cũng trao giải cho người gửi v&agrave; người nhận thuộc top 10 thiệp được b&igrave;nh chọn nhiều nhất. Nhiều phần qu&agrave; kh&aacute;c l&agrave; phiếu mua h&agrave;ng trị gi&aacute; của c&aacute;c h&atilde;ng mỹ phẩm, thời trang, đồng hồ...</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Điệp</strong></p>', 2, 1, 2017, 0, 1),
(119, 'Thành đoàn Hà Nội nhận giải tổ chức đoàn xuất sắc ASEAN', 'Trong ngày thanh niên Đông Nam Á được tổ chức từ 18 đến 21/12, thành đoàn Hà Nội đã nhận giải tổ chức thanh niên xuất sắc khu vực.', 'doan.jpg', NULL, '<h1 class="Title">Th&agrave;nh đo&agrave;n H&agrave; Nội nhận giải tổ chức đo&agrave;n xuất sắc ASEAN</h1>\r\n<h2 class="Lead">Trong ng&agrave;y thanh ni&ecirc;n Đ&ocirc;ng Nam &Aacute; được tổ chức từ 18 đến 21/12, th&agrave;nh đo&agrave;n H&agrave; Nội đ&atilde; nhận giải tổ chức thanh ni&ecirc;n xuất sắc khu vực.</h2>\r\n<p class="Normal">Ho&agrave;n th&agrave;nh xuất sắc nhiệm vụ ch&iacute;nh trị của thủ đ&ocirc;, đ&oacute;ng g&oacute;p quan trọng trong đại lễ kỷ niệm 1000 năm Thăng Long, th&agrave;nh đo&agrave;n H&agrave; Nội c&ograve;n được đ&aacute;nh gi&aacute; cao v&igrave; c&oacute; nhiều th&agrave;nh t&iacute;ch trong lĩnh vực bảo vệ m&ocirc;i trường, văn h&oacute;a gi&aacute;o dục, nghề nghiệp, việc l&agrave;m, sức khỏe vị th&agrave;nh ni&ecirc;n, thanh ni&ecirc;n t&igrave;nh nguyện&hellip;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="doan.jpg" alt="" width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Anh Ngọ Duy Hiểu (phải) nhận huy hiệu tổ chức thanh ni&ecirc;n xu&acirc;t sắc.&nbsp;<em>Ảnh: TĐ.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">B&iacute; thư Th&agrave;nh đo&agrave;n H&agrave; Nội Ngọ Duy Hiểu cho biết<em>,&nbsp;</em>rất x&uacute;c động khi được thay mặt c&aacute;c bạn trẻ thủ đ&ocirc; đi nhận giải thưởng. "Vinh dự n&agrave;y thuộc về gần 600.000 đo&agrave;n vi&ecirc;n thanh ni&ecirc;n H&agrave; Nội chứ kh&ocirc;ng phải một m&igrave;nh th&agrave;nh đo&agrave;n", anh Hiểu n&oacute;i.</p>\r\n<p class="Normal">Ng&agrave;y Thanh ni&ecirc;n Đ&ocirc;ng Nam &Aacute; do Ủy ban hợp t&aacute;c thanh ni&ecirc;n ASEAN v&agrave; Hội đồng quốc gia thanh ni&ecirc;n Philippines tổ chức tại Manila từ ng&agrave;y 18 đến 21/12. 10 tổ chức thanh ni&ecirc;n được nhận danh hiệu tổ chức Đo&agrave;n xuất sắc, trong đ&oacute; th&agrave;nh đo&agrave;n H&agrave; Nội được chấm điểm cao nhất.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>', 2, 1, 29, 0, 1),
(123, 'Du học hè Singapore 2011', 'Đăng ký trước ngày 29/1, học sinh sẽ được tặng 6 triệu đồng khi tham gia lịch trình 4 tuần ở tại resort và tặng 4 triệu đồng cho học sinh tham gia lịch trình 2 hoặc 3 tuần ở tại resort.', '400.jpg', NULL, '<h1 class="Title">Du học h&egrave; Singapore 2011</h1>\r\n<h2 class="Lead">Đăng k&yacute; trước ng&agrave;y 29/1, học sinh sẽ được tặng 6 triệu đồng khi tham gia lịch tr&igrave;nh 4 tuần ở tại resort v&agrave; tặng 4 triệu đồng cho học sinh tham gia lịch tr&igrave;nh 2 hoặc 3 tuần ở tại resort.</h2>\r\n<p class="Normal">Ngo&agrave;i ra, học sinh l&agrave; anh chị em, bạn c&ugrave;ng lớp, bạn c&ugrave;ng cơ quan bố mẹ được khuyến mại 1.000.000 đồng. Tất cả học sinh được tặng một sim điện thoại di động hoặc một thẻ gọi điện thoại quốc tế tại Singapore, bal&ocirc; thời trang, &aacute;o ph&ocirc;ng đồng phục, mũ, một qu&agrave; lưu niệm biểu tượng của Singapore v&agrave; tặng đĩa CD, DVD quay kỷ niệm to&agrave;n cảnh chuyến đi cho mỗi học sinh.</p>\r\n<p class="Normal">Lịch khởi h&agrave;nh: 2 tuần: 29/5, 12/6, 5/6, 3/7, 17/7; 3 tuần: 29/5, 5/6, 3/7, 10/7; 4 tuần: 29/5, 3/7.</p>\r\n<p class="Normal">Chuyến đi gi&uacute;p học sinh n&acirc;ng cao khả năng tiếng Anh qua c&aacute;c kh&oacute;a học v&agrave; giao tiếp với bạn b&egrave; quốc tế. C&aacute;c em sẽ c&oacute; một kỳ nghỉ h&egrave; l&yacute; th&uacute;, được vui chơi tại những thắng cảnh nổi tiếng, thư gi&atilde;n sau một năm học v&agrave; thật nhiều cơ hội t&igrave;m hiểu, l&agrave;m quen với m&ocirc;i trường v&agrave; cuộc sống khi du học.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="400.jpg" border="1" alt="" width="400" height="282" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chương tr&igrave;nh chi tiết: Học sinh c&oacute; thế lựa chọn ở tại Resort Costa Sands Resort - Down Town East hoặc Homestay (căn hộ chung cư).</p>\r\n<p class="Normal">Học sinh sẽ học tiếng Anh 3 đến 3,5 giờ mỗi ng&agrave;y từ thứ 2 đến thứ 6 hằng tuần ngay tại trường ngoại ngữ Coleman ngay tại trung t&acirc;m Singapore. Tất cả học sinh được kiểm tra xếp lớp ph&ugrave; hợp tr&igrave;nh độ khi tới Singapore. Nửa ng&agrave;y c&ograve;n lại v&agrave; buổi tối, c&aacute;c em sẽ t&igrave;m hiểu, thăm quan, vui chơi, d&atilde; ngoại v&agrave; xem phim.</p>\r\n<p class="Normal">So với chương tr&igrave;nh học h&egrave; của c&aacute;c năm trước, c&aacute;c em được trải nghiệm hai điều đặc biệt:</p>\r\n<p class="Normal">- Sky Park: chi&ecirc;m ngưỡng to&agrave;n cảnh Singapore v&agrave; một phần đất nước Malaysia tr&ecirc;n độ cao gần 200 m, kh&aacute;m ph&aacute; thiết kế độc đ&aacute;o của kh&aacute;ch sạn 7 sao tại vịnh Marina.</p>\r\n<p class="Normal">- Kh&aacute;m ph&aacute; phim trường Hollywood tại Singapore Universal Studios. Thăm quan Đại lộ danh vọng (Walk of Fame), nơi gắn liền với t&ecirc;n tuổi của nhiều ng&ocirc;i sao m&agrave;n ảnh nổi tiếng, gh&eacute; thăm th&agrave;nh phố m&ocirc; phỏng lớn nhất nước Mỹ - New York với hệ thống đ&egrave;n m&agrave;u rực rỡ. Kh&aacute;m ph&aacute; Thế giới kỳ lạ (Far Far Away) v&agrave; xem phim 4D với ch&agrave;ng khổng lồ Shrek nổi tiếng...</p>\r\n<p class="Normal">C&ugrave;ng với c&aacute;c chương tr&igrave;nh: Singapore Flyer; Thăm đảo Sentosa: thế giới đại dương với nhiều lo&agrave;i c&aacute; qu&yacute; hiếm, d&agrave;n nhạc nước Song of the sea, xiếc c&aacute; heo hồng, bảo t&agrave;ng s&aacute;p&hellip; Thư gi&atilde;n cắm trại v&agrave; tắm biển, trượt patin v&agrave; xe đạp địa h&igrave;nh tại b&atilde;i biển East Coast Park Way; thăm vườn chim Jurong v&agrave; vườn th&uacute; Quốc gia Singapore Zoo, vườn th&uacute; đ&ecirc;m Night Safari. Kh&aacute;m ph&aacute; khu khoa học quốc gia, vui chơi tại c&ocirc;ng vi&ecirc;n nước v&agrave; c&ocirc;ng vi&ecirc;n giải tr&iacute; với 14 tr&ograve; chơi cảm gi&aacute;c mạnh...</p>\r\n<p class="Normal">Visco sẽ lu&ocirc;n theo s&aacute;t từng học sinh, hướng dẫn, chăm s&oacute;c sức khoẻ, hỗ trợ v&agrave; tư vấn cho c&aacute;c em để c&oacute; một chuyến đi an to&agrave;n v&agrave; th&uacute; vị trong suốt h&agrave;nh tr&igrave;nh.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><span style="color: #800000;"><strong>Li&ecirc;n hệ: Văn ph&ograve;ng Visco</strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 230 Kim M&atilde;, Ba Đ&igrave;nh; ĐT: 04.3726 1938; Fax: 04.3726 1936 - E-mail:<a class="Normal" href="mailto:viscohanoi@visco.edu.vn">viscohanoi@visco.edu.vn</a></p>\r\n<p class="Normal">TP HCM: 239 CMT 8, nh&agrave; A02 Chung cư Văn h&oacute;a, phường 4, quận 3 - ĐT: 08 3832 8416; Fax: 08 3832 8417; E-mail:&nbsp;<a class="Normal" href="mailto:viscohcm@visco.edu.vn">viscohcm@visco.edu.vn</a></p>\r\n<p class="Normal">Đ&agrave; Nẵng: 433 Phan Chu Trinh, Hải Ch&acirc;u - ĐT: 0511 3552597/96; Fax : 0511 355 2596; E-mail:&nbsp;<a class="Normal" href="mailto:viscodn@visco.edu.vn">viscodn@visco.edu.vn</a></p>\r\n<p class="Normal">Hải Ph&ograve;ng: 309 Hai B&agrave; Trưng, C&aacute;t D&agrave;i, L&ecirc; Ch&acirc;n - ĐT: 031 3950215/3950748; Fax : 0313 950215; Email:&nbsp;<a class="Normal" href="mailto:viscohp@visco.edu.vn">viscohp@visco.edu.vn</a>.</p>\r\n<p class="Normal">Tư vấn online Ym!: duhocvisco; Website&nbsp;<a class="Normal" href="http://www.visco.edu.vn/">http://www.visco.edu.vn/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Visco)</em></p>', 4, 1, 449, 0, 1),
(124, 'Chương trình trung học tại Canada', 'Bà Birgit Hartel, Giám đốc tuyển sinh nhóm các trường công lập River East Transcona, tỉnh Manitoba, Canada sẽ giới thiệu về môi trường sống và học tập từ 18h đến 20h ngày 21/1 tại số 22 phố Phạm Huy Thông, Ba Đình, Hà Nội.', 'New-Image250.jpg', NULL, '<h1 class="Title">Chương tr&igrave;nh trung học tại Canada</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="New-Image250.jpg" border="1" alt="" width="250" height="195" align="left" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">B&agrave; Birgit Hartel, Gi&aacute;m đốc tuyển sinh nh&oacute;m c&aacute;c trường c&ocirc;ng lập River East Transcona, tỉnh Manitoba, Canada sẽ giới thiệu về m&ocirc;i trường sống v&agrave; học tập từ 18h đến 20h ng&agrave;y 21/1 tại số 22 phố Phạm Huy Th&ocirc;ng, Ba Đ&igrave;nh, H&agrave; Nội.</h2>\r\n<p class="Normal">Tổng chi ph&iacute; một năm của học sinh tại c&aacute;c trường trung học tỉnh Manitoba, Canada từ 18.000 đến 20.000 CAD bao gồm tiền học, s&aacute;ch vở, bảo hiểm, c&aacute;c hoạt động ngoại kh&oacute;a, ăn ở, đi lại, ti&ecirc;u vặt... Học sinh c&oacute; khả năng định cư chắc chắn nếu tiếp tục theo học một kh&oacute;a học sau trung học tại tỉnh Manitoba. Học sinh sẽ được quyền nộp đơn để c&oacute; thẻ xanh (thẻ mang lại lợi &iacute;ch giống như một c&ocirc;ng d&acirc;n Canada, trừ quyền bầu cử).</p>\r\n<p class="Normal">River East Transcona, tọa lạc ở v&ugrave;ng Đ&ocirc;ng Bắc Winnipeg, Manitoba. Bang Manitoba c&oacute; chất lượng gi&aacute;o dục rất cao, học ph&iacute; v&agrave; sinh hoạt ph&iacute; rẻ tại Canada v&igrave; ch&iacute;nh s&aacute;ch hỗ trợ ph&aacute;t triển gi&aacute;o dục m&atilde;nh mẽ của bang.</p>\r\n<p class="Normal">River East Transcona l&agrave; nh&oacute;m trường c&ocirc;ng lập lớn thứ hai của tỉnh Manitoba. Đ&acirc;y l&agrave; nh&oacute;m trường c&oacute; chất lượng gi&aacute;o dục cao, được ch&iacute;nh phủ c&ocirc;ng nhận với nhiều giảng vi&ecirc;n gi&agrave;u kinh nghiệm. Bằng trung học của trường được c&ocirc;ng nhận tr&ecirc;n to&agrave;n thế giới. Nh&oacute;m trường c&oacute; li&ecirc;n kết chặt chẽ với c&aacute;c đại học c&oacute; uy t&iacute;n trong v&agrave; ngo&agrave;i nước v&agrave; đi đầu trong việc n&acirc;ng cao đ&agrave;o tạo lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin. Ngo&agrave;i ra, chương tr&igrave;nh ESL chuy&ecirc;n s&acirc;u gi&uacute;p học sinh n&acirc;ng cao khả năng tiếng Anh tổng qu&aacute;t, đ&atilde; bao gồm trong c&aacute;c chương tr&igrave;nh học n&ecirc;n kh&ocirc;ng phải mất th&ecirc;m chi ph&iacute;.</p>\r\n<p class="Normal">Nh&oacute;m trường tuyển sinh kh&ocirc;ng y&ecirc;u cầu tr&igrave;nh độ tiếng Anh tối thiểu để tham gia v&agrave;o chương tr&igrave;nh gi&aacute;o dục quốc tế. Chấp nhận học sinh với cấp độ tiếng Anh kh&aacute;c nhau từ sơ cấp đến n&acirc;ng cao, sắp xếp lớp học ph&ugrave; hợp với tr&igrave;nh độ của học vi&ecirc;n.&nbsp;<br /><br />C&aacute;c chương tr&igrave;nh đ&agrave;o tạo: T&uacute; t&agrave;i quốc tế (IB) v&agrave; chương tr&igrave;nh học trung học (từ lớp 10 đến lớp 12) c&oacute; chất lượng gi&aacute;o dục cao, với nhiều gi&aacute;o vi&ecirc;n gi&agrave;u kinh nghiệm.</p>\r\n<p class="Normal">Nh&oacute;m trường trang thiết bị hiện đại v&agrave; đầy đủ để đ&aacute;p ứng nhu cầu học tập cho học sinh. C&aacute;c trường đều c&oacute; thư viện lớn, ph&ograve;ng th&iacute; nghiệm khoa học kết nối Internet v&agrave; ph&ograve;ng tập thể dục.</p>\r\n<p class="Normal">Ngo&agrave;i ra, bể bơi lớn v&agrave; c&aacute;c dụng tập thể dục trong nh&agrave; nằm gần c&aacute;c khu trường học. Học vi&ecirc;n c&oacute; thể tham gia c&aacute;c hoạt động giải tr&iacute; v&agrave;o cuối giờ học hoặc c&aacute;c buổi tối v&agrave; những ng&agrave;y cuối tuần ho&agrave;n to&agrave;n miễn ph&iacute;. C&aacute;c hoạt động văn ho&aacute; được tổ chức thường xuy&ecirc;n.</p>\r\n<p class="Normal">Học sinh c&oacute; thể ở với c&aacute;c gia đ&igrave;nh bản xứ (Homestay). C&aacute;c gia đ&igrave;nh n&agrave;y đều được lựa chọn kỹ lưỡng, đảm bảo cho c&aacute;c em đạt được nhiều th&agrave;nh tựu nhất từ những kinh nghiệm sống tại Canada. C&aacute;c gia đ&igrave;nh bản xứ cung cấp 3 bữa ăn mỗi ng&agrave;y v&agrave; mỗi em c&oacute; một ph&ograve;ng ngủ ri&ecirc;ng với c&aacute;c phương tiện học tập đầy đủ. Học sinh quốc tế trở th&agrave;nh th&agrave;nh vi&ecirc;n của c&aacute;c gia đ&igrave;nh Homestay. Đồng thời, nh&acirc;n vi&ecirc;n của trường duy tr&igrave; li&ecirc;n lạc với học sinh v&agrave; gia đ&igrave;nh Homestay để hỗ trợ c&aacute;c em tốt nhất.</p>\r\n<p class="Normal">AIT miễn ph&iacute; tư vấn v&agrave; nhập học trị gi&aacute; 200 CAD. Học sinh đăng k&yacute; tham dự hội thảo trước ng&agrave;y 20/1.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Văn ph&ograve;ng tư vấn du học AIT</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Số 22 phố Phạm Huy Th&ocirc;ng, Ba Đ&igrave;nh, H&agrave; Nội.</p>\r\n<p class="Normal">Điện thoại: 04 377 10 750/1- 180 hoặc Mobile: 0983 029 987</p>\r\n<p class="Normal">Email:&nbsp;<a class="Normal" href="mailto:vu.sinh@aitduhoc.com">vu.sinh@aitduhoc.com</a>&nbsp;Website:&nbsp;<span style="text-decoration: underline;"><a href="http://www.aitduhoc.com/">http://www.aitduhoc.com</a></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>AIT</em>)</p>', 4, 1, 1377, 0, 1),
(125, 'Ngành quản trị khách sạn và du lịch tại Thụy Sĩ', 'G’Connect Education tổ chức buổi giới thiệu thông tin du học Thụy Sĩ với đại diện trường Quản lý khách sạn IHTTI vào từ 17h30 đến 20h30 ngày 24/2 tại khách sạn Sheraton, 88 Đồng Khởi, quận 1, TP HCM (Hanoi Room, tầng 2)', 'Festival-17_Ve-tranh.jpg', NULL, '<h1 class="Title">Ng&agrave;nh quản trị kh&aacute;ch sạn v&agrave; du lịch tại Thụy Sĩ</h1>\r\n<h2 class="Lead">G&rsquo;Connect Education tổ chức buổi giới thiệu th&ocirc;ng tin du học Thụy Sĩ với đại diện trường Quản l&yacute; kh&aacute;ch sạn IHTTI v&agrave;o từ 17h30 đến 20h30 ng&agrave;y 24/2 tại kh&aacute;ch sạn Sheraton, 88 Đồng Khởi, quận 1, TP HCM (Hanoi Room, tầng 2)</h2>\r\n<p class="Normal">IHTTI (International Hotel &amp; Tourism Training Institute) l&agrave; trường dạy về du lịch, kh&aacute;ch sạn bằng tiếng Anh tại Thụy Sĩ. IHTTI được đ&aacute;nh gi&aacute; c&oacute; chất lượng đ&agrave;o tạo tốt của Tập đo&agrave;n Swiss Education Group (SEG) - Tập đo&agrave;n gồm những trường dạy quản l&yacute; kh&aacute;ch sạn h&agrave;ng đầu Thụy Sĩ. Ngo&agrave;i ra, IHTTI c&ograve;n nằm trong Association Suisse des Ecoles H&ocirc;teli&egrave;res (ASEH - Hiệp hội c&aacute;c trường dạy về du lịch, kh&aacute;ch sạn được Bộ Gi&aacute;o dục Thụy Sĩ c&ocirc;ng nhận).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="400x300.jpg" border="1" alt="" width="400" height="300" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trường tọa lạc tại Neuch&acirc;tel, chỉ mất từ 40 ph&uacute;t đến một tiếng đến thủ đ&ocirc; Bern v&agrave; khoảng hơn một tiếng để đến trung t&acirc;m t&agrave;i ch&iacute;nh thế giới Geneva. Ngo&agrave;i ra, th&agrave;nh phố Neuch&acirc;tel c&ograve;n c&oacute; c&aacute;c tuyến xe lửa nối trực tiếp tới c&aacute;c nước Ph&aacute;p, Đức, Italy.</p>\r\n<p class="Normal">Tại IHTTI, chương tr&igrave;nh được giảng dạy bằng tiếng Anh. Tuy nhi&ecirc;n, Neuch&acirc;tel thuộc v&ugrave;ng n&oacute;i tiếng Ph&aacute;p của Thụy Sĩ n&ecirc;n sinh vi&ecirc;n được sống trong m&ocirc;i trường đa văn h&oacute;a tạo điều kiện thuận lợi cho c&ocirc;ng việc trong tương lai.</p>\r\n<p class="Normal">Với chương tr&igrave;nh học một kỳ l&yacute; thuyết + một kỳ thực tập (từ 4 đến 6 th&aacute;ng) c&oacute; hưởng lương, sinh vi&ecirc;n kh&ocirc;ng chỉ được trang bị kiến thức về chuy&ecirc;n ng&agrave;nh m&agrave; c&ograve;n c&oacute; cơ hội va chạm với thực tiễn. Mức lương mỗi sinh vi&ecirc;n nhận được trong thời gian thực tập trung b&igrave;nh từ 2.168 đến 2.450 CHF một th&aacute;ng.</p>\r\n<p class="Normal">Trường c&oacute; 2 kỳ khai giảng v&agrave;o th&aacute;ng 2 v&agrave; 9 hằng năm. Chương tr&igrave;nh Anh ngữ hỗ trợ sinh vi&ecirc;n chưa đạt điều kiện y&ecirc;u cầu về Anh ngữ sẽ khai giảng trước chương tr&igrave;nh ch&iacute;nh từ 4 đến 16 tuần.</p>\r\n<p class="Normal">Theo học tại IHTTI, học sinh c&oacute; thể chuyển tiếp giữa c&aacute;c trường trong hệ thống SEG. Bằng cấp được c&ocirc;ng nhận rộng r&atilde;i tr&ecirc;n to&agrave;n thế giới. Đặc biệt, v&agrave;o th&aacute;ng 4 v&agrave; 10 hằng năm, Tập đo&agrave;n SEG tạo cơ hội cho sinh vi&ecirc;n của c&aacute;c trường thuộc tập đo&agrave;n gặp gỡ c&aacute;c nh&agrave; tuyển dụng lớn, điều n&agrave;y gi&uacute;p cho c&aacute;c sinh vi&ecirc;n của IHTTI c&oacute; thể t&igrave;m được việc l&agrave;m thậm ch&iacute; khi c&ograve;n chưa ra trường.</p>\r\n<p class="Normal">G&rsquo;Connect Education tặng iPad 3G (32 GB) cho học sinh đăng k&yacute; nhập học tại IHTTI v&agrave;o kỳ th&aacute;ng 9.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="486" align="center" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: G&rsquo;Connect Education</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">567A Trần Hưng Đạo, P. Cầu Kho, quận 1, TP HCM.</p>\r\n<p class="Normal">Điện thoại: 08 3838 9797, Ms. Ng&acirc;n, Ms. Th&ugrave;y: 0913 839 963</p>\r\n<p class="Normal">Email:&nbsp;<a class="Normal" href="mailto:info@gconnect.edu.vn">info@gconnect.edu.vn</a>&nbsp;hoặc Website:&nbsp;<a class="Normal" href="http://www.gconnect.edu.vn/">www.gconnect.edu.vn</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>G&rsquo;Connect Education</em>)</p>', 4, 1, 1240, 0, 1),
(126, 'Du học tại Queen Mary và London Metropolitan (Anh)', 'Đại diện trường Queen Mary và London Metropolitan sẽ tham gia hội thảo tuyển sinh được tổ chức tại Hà Nội và TP HCM.', 'sunrise-1.jpg', NULL, '<h1 class="Title">Du học tại Queen Mary v&agrave; London Metropolitan (Anh)</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="sunrise-1.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Đại diện trường Queen Mary v&agrave; London Metropolitan sẽ tham gia hội thảo tuyển sinh được tổ chức tại H&agrave; Nội v&agrave; TP HCM.</h2>\r\n<p class="Normal">Đại diện tuyển sinh quốc tế của trường Queen Mary, &ocirc;ng Lee Wildman v&agrave; &ocirc;ng Shazia Hussain sẽ trực tiếp phỏng vấn học sinh từ 11h đến 12h ng&agrave;y 14/1 tại Sunrise H&agrave; Nội v&agrave; từ 10h đến 12h ng&agrave;y 17/1 tại Sunrise TP HCM.</p>\r\n<p class="Normal">Queen Mary giảng dạy c&aacute;c tr&igrave;nh độ dự bị đại học, dự bị thạc sĩ, đại học, thạc sĩ v&agrave; c&aacute;c chương tr&igrave;nh tiếng Anh. Đặc biệt, kh&oacute;a dự bị thạc sĩ gi&uacute;p học sinh đăng k&yacute; v&agrave;o thạc sĩ tại c&aacute;c chuy&ecirc;n ng&agrave;nh: quản trị t&agrave;i ch&iacute;nh quốc tế, t&agrave;i ch&iacute;nh kế to&aacute;n, t&agrave;i ch&iacute;nh v&agrave; đầu tư, t&agrave;i ch&iacute;nh ng&acirc;n h&agrave;ng. Khai giảng th&aacute;ng 9 hằng năm.</p>\r\n<p class="Normal">Y&ecirc;u cầu đầu v&agrave;o:</p>\r\n<p class="Normal">- Dự bị đại học (một năm): kết th&uacute;c THPT tại Việt Nam, ILETS 5.</p>\r\n<p class="Normal">- Đại học (3 năm): hết Foundation hoặc năm thứ nhất hoặc thứ 2 đại học, IELTS: 6,5.</p>\r\n<p class="Normal">- Dự bị thạc sĩ (9 th&aacute;ng): tốt nghiệp đại học, IELTS: 5,5.</p>\r\n<p class="Normal">- Thạc sĩ (một năm): tốt nghiệp đại học, IELTS: 6,5.</p>\r\n<p class="Normal">- Tiếng Anh (Pre-sessional English): kh&oacute;a 5, 9 v&agrave; 13 tuần (v&agrave;o th&aacute;ng 6, 7 v&agrave; 8).</p>\r\n<p class="Normal">Đại diện trường London Metropolitan, &ocirc;ng Mel MacCarthy, phỏng vấn học sinh đăng k&yacute; nhập học v&agrave; nhận hồ sơ học bổng cho kỳ nhập học th&aacute;ng 9/2011 từ 9h30 đến 11h ng&agrave;y 14/1 tại Sunrise H&agrave; Nội v&agrave; l&uacute;c 14h ng&agrave;y 17/1 tại Sunrise TP HCM.</p>\r\n<p class="Normal">Trong thời gian n&agrave;y học sinh c&oacute; thể l&agrave;m b&agrave;i tiếng Anh (Password English Test) của trường thay IELTS khi xin học v&agrave; Visa. Trường c&oacute; tới 14 khoa ch&iacute;nh, trong đ&oacute; chia ra tr&ecirc;n 300 chuy&ecirc;n ng&agrave;nh c&oacute; tại c&aacute;c kh&oacute;a: dự bị, đại học, dự bị thạc sĩ, thạc sĩ v&agrave; tiến sĩ, gi&uacute;p sinh vi&ecirc;n c&oacute; thể dễ d&agrave;ng lựa chọn kho&aacute; học v&agrave; theo học ng&agrave;nh nghề m&igrave;nh mong muốn. Kỳ khai giảng hằng năm: th&aacute;ng 2 v&agrave; th&aacute;ng 9. Trường c&oacute; k&yacute; t&uacute;c x&aacute; cho sinh vi&ecirc;n quốc tế, hoặc gia đ&igrave;nh người bản xứ.<br /><br />Học bổng: 100% học ph&iacute; hoặc to&agrave;n phần (học ph&iacute; v&agrave; k&yacute; t&uacute;c x&aacute;) kh&oacute;a học thạc sĩ (hạn nộp ng&agrave;y 31/5).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Sunrise Việt Nam</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 3A Cao B&aacute; Qu&aacute;t, quận Ba Đ&igrave;nh ĐT: 37224878&nbsp;<br />TP HCM: 34 Mai Thị Lựu, P. Đakao, Quận 1 ĐT: 38236160<br />Hải Ph&ograve;ng: 103 Văn Cao, Q. Ng&ocirc; Quyền ĐT: 031.2640 689<br />E-mail:&nbsp;<a class="Normal" href="mailto:info@sunrisevietnam.com">info@sunrisevietnam.com</a>&nbsp;hoặc&nbsp;<a class="Normal" href="mailto:sunrisevietnam@fpt.vn">sunrisevietnam@fpt.vn</a>&nbsp;<br /><a class="Normal" href="http://www.sunrisevietnam.com/">http://www.sunrisevietnam.com/</a></p>\r\n<p class="Normal">Chương tr&igrave;nh IELTS:&nbsp;<a class="Normal" href="http://www.sunrise.edu.vn/">www.sunrise.edu.vn</a>&nbsp;hoặc E-mail:&nbsp;<a class="Normal" href="mailto:edu@sunrisevietnam.com">edu@sunrisevietnam.com</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Sunrise</em>)</p>', 4, 1, 2922, 0, 1),
(127, 'Các trường Nhật ngữ tuyển sinh', 'Đại diện tuyển sinh của các trường Nhật ngữ tổ chức phỏng vấn và nhận hồ sơ trực tiếp từ học sinh có nguyện vọng du học Nhật Bản cho kỳ khai giảng tháng 7 tại Trung tâm thương mại và hợp tác quốc tế UDIC, Hà Nội.', 'toi1.jpg', NULL, '<div class="folder-header">\r\n<div class="parentfolder-title fl"><span style="font-size: 20px;"><strong>\r\n<h1 class="Title">C&aacute;c trường Nhật ngữ tuyển sinh</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="Nhat-250.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Đại diện tuyển sinh của c&aacute;c trường Nhật ngữ tổ chức phỏng vấn v&agrave; nhận hồ sơ trực tiếp từ học sinh c&oacute; nguyện vọng du học Nhật Bản cho kỳ khai giảng th&aacute;ng 7 tại Trung t&acirc;m thương mại v&agrave; hợp t&aacute;c quốc tế UDIC, H&agrave; Nội.</h2>\r\n<p class="Normal">Đại diện tuyển sinh c&aacute;c trường sẽ trực tiếp giải th&iacute;ch c&aacute;c thắc mắc về du học Nhật Bản. Học sinh được tư vấn miễn ph&iacute;, cung cấp c&aacute;c th&ocirc;ng tin chi tiết về trường v&agrave; điều kiện học tập tại Nhật v&agrave; c&aacute;c th&ocirc;ng tin về việc l&agrave;m th&ecirc;m, cư tr&uacute; sau tốt nghiệp tại Nhật. Ngo&agrave;i ra, chương tr&igrave;nh cũng sẽ cung cấp th&ocirc;ng tin về cơ hội nhận c&aacute;c suất học bổng gi&aacute; trị.</p>\r\n<h3 class="SubTitle">Thời gian cụ thể như sau:</h3>\r\n<p class="Normal">8h30-10h30 ng&agrave;y 19/2: Phỏng vấn với đại diện trường Nhật ngữ An school, th&agrave;nh phố Tokyo.</p>\r\n<p class="Normal">9h-11h ng&agrave;y 26/2: Học viện gi&aacute;o dục Quốc tế Nishinihon, th&agrave;nh phố Fukuoka.</p>\r\n<p class="Normal">17h-19h ng&agrave;y 3/3 v&agrave; từ 9h &ndash;12h ng&agrave;y 6/3: Trường Nhật ngữ Tokyo Sanrisu, th&agrave;nh phố Tokyo.</p>\r\n<p class="Normal">UDIC- ICTC phối hợp với c&aacute;c trường của Nhật c&oacute; ch&iacute;nh s&aacute;ch khuyến học như sau: Học bổng 100.000 JPY cho c&aacute;c bạn đăng k&yacute; học tại Học viện gi&aacute;o dục quốc tế Nishinihon kỳ nhập học th&aacute;ng 7/2011. Tặng 5 suất v&eacute; m&aacute;y bay v&agrave; 20 kim từ điển Nhật - Việt cho học sinh.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Trung t&acirc;m thương mại v&agrave; hợp t&aacute;c quốc tế UDIC</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Số 6,7 - L&ocirc; 1B - Vũ Phạm H&agrave;m- Trung Y&ecirc;n, Cầu Giấy, H&agrave; Nội.</p>\r\n<p class="Normal">Điện thoại: 04.37834893 - Hotline: 0904137646.</p>\r\n<p class="Normal">Email: info@duhocudic.vn -Website:<a class="Normal" href="http://www.duhocudic.vn/duhoc">&nbsp;www.duhocudic.vn</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">(Nguồn<em>: UDIC &ndash; ICTC)</em></p>\r\n<br /></strong></span></div>\r\n</div>\r\n<div class="content">\r\n<div></div>\r\n</div>', 4, 1, 2059, 0, 1),
(128, 'Học bổng 70% học phí đại học và cao học tại Mỹ', 'Hội thảo giới thiệu chương trình học bổng được tổ chức lúc 14h ngày 24/2 tại số 25 Vạn Bảo, Ba Đình, Hà Nội. 5 sinh viên khá, giỏi nộp hồ sơ đầu tiên sẽ được miễn 100% dịch vụ phí.', 'ASCI-250.jpg', NULL, '<h1 class="Title">Học bổng 70% học ph&iacute; đại học v&agrave; cao học tại Mỹ</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="ASCI-250.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Hội thảo giới thiệu chương tr&igrave;nh học bổng được tổ chức l&uacute;c 14h ng&agrave;y 24/2 tại số 25 Vạn Bảo, Ba Đ&igrave;nh, H&agrave; Nội. 5 sinh vi&ecirc;n kh&aacute;, giỏi nộp hồ sơ đầu ti&ecirc;n sẽ được miễn 100% dịch vụ ph&iacute;.</h2>\r\n<p class="Normal">Tại buổi hội thảo, đại diện tuyển sinh của Tập đo&agrave;n ESLI sẽ giới thiệu chương tr&igrave;nh tiếng Anh, đại học v&agrave; thạc sĩ tại c&aacute;c trường đại học c&ocirc;ng lập lớn của Mỹ như Mc McNeese State University, West Texas A&amp;M University, Texas A&amp;M University - Corpus Christi, Western Kentucky University, Morehead State University v&agrave; Eastern University. Đ&acirc;y l&agrave; c&aacute;c trường đại học tổng hợp đ&agrave;o tạo đa ng&agrave;nh với c&aacute;c ng&agrave;nh nổi bật như quản trị kinh doanh, t&agrave;i ch&iacute;nh, marketing, kế to&aacute;n, c&ocirc;ng nghệ th&ocirc;ng tin, truyền th&ocirc;ng...</p>\r\n<p class="Normal">Sinh vi&ecirc;n học kh&oacute;a dự bị tiếng Anh tại trường sẽ được miễn ho&agrave;n to&agrave;n điểm TOEFL, được nợ điểm GMAT, GRE hoặc SAT tại thời điểm nộp hồ sơ. C&aacute;c kỳ khai giảng th&aacute;ng 1, 6 v&agrave; 8 cho tất cả c&aacute;c kh&oacute;a học tiếng Anh, đại học, cao học.</p>\r\n<p class="Normal">Hạn nộp hồ sơ: Kh&oacute;a học khai giảng v&agrave;o th&aacute;ng 6 ng&agrave;y 10/3 v&agrave; kh&oacute;a học khai giảng v&agrave;o th&aacute;ng 8 ng&agrave;y 10/5. Ngo&agrave;i ra, c&aacute;c bạn sinh vi&ecirc;n sẽ được cung cấp c&aacute;c th&ocirc;ng tin về điều kiện v&agrave; thủ tục xin học bổng, c&aacute;c thủ tục hồ sơ xin visa, hỗ trợ chứng minh t&agrave;i ch&iacute;nh.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: ASCI</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">25 Vạn Bảo, Ba Đ&igrave;nh, H&agrave; Nội<br />Điện thoại: 04.62730966; Fax: 04.62730963; E-mail:&nbsp;<a class="Normal" href="mailto:asci@asci.edu.vn">asci@asci.edu.vn</a></p>\r\n<p class="Normal">TP HCM: 18/24 (Đường số 4) Vườn Chuối, phường 4, quận 3<br />Điện thoại: 08.62908521; 62908524; E-mail:&nbsp;<a class="Normal" href="mailto:ascihcm@asci.edu.vn">ascihcm@asci.edu.vn</a></p>\r\n<p class="Normal">Website:&nbsp;<a class="Normal" href="http://www.asci.edu.vn/">http://www.asci.edu.vn/</a>&nbsp;hoặc&nbsp;<a class="Normal" href="http://www.duhoctoancau.com/">http://www.duhoctoancau.com/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>ASCI</em>)</p>', 4, 1, 2250, 0, 1),
(129, 'Du học Singapore cùng Tập đoàn giáo dục Kaplan, Mỹ', 'Hội thảo giới thiệu chương trình được tổ chức lúc 16h ngày 19/2 tại khách sạn Kim Đô, 133 Nguyễn Huệ, quận 1, TP HCM. Tham gia chương trình học sinh có cơ hội nhận học bổng trị giá 135 triệu đồng.', 'INCE.jpg', NULL, '<h1 class="Title">Du học Singapore c&ugrave;ng Tập đo&agrave;n gi&aacute;o dục Kaplan, Mỹ</h1>\r\n<h2 class="Lead">Hội thảo giới thiệu chương tr&igrave;nh được tổ chức l&uacute;c 16h ng&agrave;y 19/2 tại kh&aacute;ch sạn Kim Đ&ocirc;, 133 Nguyễn Huệ, quận 1, TP HCM. Tham gia chương tr&igrave;nh học sinh c&oacute; cơ hội nhận học bổng trị gi&aacute; 135 triệu đồng.</h2>\r\n<p class="Normal">C&oacute; mặt tại Singapore v&agrave;o năm 1989, hiện Kaplan Singapore c&oacute; gần 10.000 sinh vi&ecirc;n theo tại 2 khu học x&aacute; hiện đại. Ngo&agrave;i ra, Kaplan Singapore được chọn l&agrave; trung t&acirc;m khảo th&iacute; TOEFL.</p>\r\n<p class="Normal">Sau một năm học tại đ&acirc;y, học sinh dễ d&agrave;ng chuyển sang Mỹ, Anh, Australia để học tiếp. Kaplan Singapore l&agrave; th&agrave;nh vi&ecirc;n của một trong những tổ chức gi&aacute;o dục với hơn 80 cơ sở đ&agrave;o tạo tại 30 quốc gia. Tập đo&agrave;n gi&aacute;o dục Kaplan sở hữu bởi The Washington Post của Mỹ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="INCE.jpg" border="1" alt="" width="400" height="278" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kaplan Singapore được trao giải thưởng chất lượng gi&aacute;o dục Singapore v&agrave; xếp thứ hai trong số &ldquo;C&aacute;c trường đại học được y&ecirc;u th&iacute;ch nhất tại Singapore&rdquo; (theo b&aacute;o c&aacute;o của Jobscentral Learning).</p>\r\n<h3 class="SubTitle">Chương tr&igrave;nh đ&agrave;o tạo:</h3>\r\n<p class="Normal">Tiếng Anh</p>\r\n<p class="Normal">Dự bị đại học (D&agrave;nh cho học sinh học hết lớp 10, 11)</p>\r\n<p class="Normal">Cao đẳng: Ng&acirc;n h&agrave;ng, đầu tư, chứng kho&aacute;n; C&ocirc;ng nghệ th&ocirc;ng tin; Quản l&yacute; marketing; Quản l&yacute; logistic; Quản l&yacute; du lịch, kh&aacute;ch sạn; Quản l&yacute; t&agrave;i ch&iacute;nh, Quản trị kinh doanh.</p>\r\n<p class="Normal">Đại học:</p>\r\n<table border="1" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" width="451" align="left"><strong>Chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo</strong></td>\r\n<td class="Normal" width="271" align="left"><strong>C&aacute;c trường đối t&aacute;c</strong></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n (danh dự) kế to&aacute;n</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Bedfordshire, Anh</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n (danh dự) quản trị kinh doanh</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Bedfordshire, Anh</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n CNTT chuy&ecirc;n ng&agrave;nh hệ thống th&ocirc;ng tin thương mại v&agrave; khoa học m&aacute;y t&iacute;nh (bằng k&eacute;p)</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Murdoch, Australia</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n quản trị du lịch, kh&aacute;ch sạn v&agrave; quản trị marketing (bằng k&eacute;p)</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Murdoch, Australia</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n (danh dự) t&agrave;i ch&iacute;nh</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH quốc gia Ireland, Dublin</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n (danh dự) logistics</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH quốc gia Ireland, Dublin</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n (danh dự)quản Trị</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Quốc Gia Ireland, Dublin</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n (danh dự) marketing</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Quốc Gia Ireland, Dublin</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n về quản l&yacute;</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Northeastern</td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" width="451" align="left" valign="top">Cử nh&acirc;n về quản l&yacute; t&agrave;i ch&iacute;nh v&agrave; kế to&aacute;n</td>\r\n<td class="Normal" width="271" align="left" valign="top">ĐH Northeastern</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Thạc sĩ: Quản l&yacute; hoặc t&agrave;i ch&iacute;nh của của ĐH quốc gia Ireland, Dublin v&agrave; Quản trị kinh doanh của ĐH Murdoch, Australia.</p>\r\n<p class="Normal">Học sinh nộp hồ sơ tại INEC được tặng v&eacute; m&aacute;y bay khi c&oacute; Visa du học, miễn 100% ph&iacute; dịch thuật, dịch vụ, miễn 100% ph&iacute; kiểm tra tiếng Anh v&agrave; miễn 100% ph&iacute; chuyển hồ sơ.</p>\r\n<p class="Normal">Đăng k&yacute; tham gia hội thảo: Điện thoại: (08) 3938 1080 &ndash; (08) 3938 1081. Hotline: 0902 490 488. Email:&nbsp;<a class="Normal" href="mailto:hanh@inec.vn">hanh@inec.vn</a>.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>INEC</em>)</p>', 4, 1, 2050, 0, 1),
(130, 'Chương trình học bổng 2011 của MDIS, Singapore', 'MDIS giới thiệu 4 chương trình học bổng cho các bậc thạc sĩ, cử nhân, cao đẳng hoặc cao đẳng nâng cao và học bổng khuyến học chương trình cử nhân hoặc thạc sĩ năm 2011.', 'MDIS.jpg', NULL, '<h1 class="Title">Chương tr&igrave;nh học bổng 2011 của MDIS, Singapore</h1>\r\n<h2 class="Lead">MDIS giới thiệu 4 chương tr&igrave;nh học bổng cho c&aacute;c bậc thạc sĩ, cử nh&acirc;n, cao đẳng hoặc cao đẳng n&acirc;ng cao v&agrave; học bổng khuyến học chương tr&igrave;nh cử nh&acirc;n hoặc thạc sĩ năm 2011.</h2>\r\n<p class="Normal">Chương tr&igrave;nh 1: 6 suất học bổng trị gi&aacute; 10.000 SGD, một suất d&agrave;nh cho sinh vi&ecirc;n xuất sắc đăng k&yacute; một trong c&aacute;c chương tr&igrave;nh thạc sĩ. Sinh vi&ecirc;n cần c&oacute; bằng đại học, điểm trung b&igrave;nh m&ocirc;n (4 năm) từ 7,5 trở l&ecirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="MDIS.jpg" border="1" alt="" width="400" height="305" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chương tr&igrave;nh 2: 6 suất học bổng trị gi&aacute; 10.000 SGD một suất d&agrave;nh cho sinh vi&ecirc;n giỏi đăng k&yacute; một trong c&aacute;c chương tr&igrave;nh cử nh&acirc;n. Sinh vi&ecirc;n cần học hết lớp 10 tại Việt Nam, 16 tuổi trở l&ecirc;n, c&oacute; điểm tổng kết hai năm học gần nhất từ 8.</p>\r\n<p class="Normal">Chương tr&igrave;nh 3: 10 suất học bổng trị gi&aacute; 3.000 SGD một suất d&agrave;nh cho sinh vi&ecirc;n giỏi đăng k&yacute; một trong c&aacute;c chương tr&igrave;nh cao đẳng hoặc cao đẳng n&acirc;ng cao tại MDIS. Sinh vi&ecirc;n cần học hết lớp 10 tại Việt Nam, 16 tuổi trở l&ecirc;n c&oacute; điểm tổng kết hai năm học gần nhất từ 8.</p>\r\n<p class="Normal">Thời gian nộp hồ sơ cho 3 học bổng tr&ecirc;n từ 25/2 đến 31/8. Hồ sơ học bổng sẽ được x&eacute;t sau khi hết hạn nộp hồ sơ. C&aacute;c sinh vi&ecirc;n được lựa chọn sẽ tham gia phỏng vấn ở v&ograve;ng 2.</p>\r\n<p class="Normal">Ngo&agrave;i ra, MDIS d&agrave;nh học bổng khuyến học cho tất cả sinh vi&ecirc;n đăng k&yacute; học cử nh&acirc;n hoặc thạc sĩ trước ng&agrave;y 30/4 gồm: Học bổng cử nh&acirc;n 1.000 SGD một suất v&agrave; học bổng thạc sĩ 1.500 SGD một suất. Sinh vi&ecirc;n đạt học bổng của c&aacute;c chương tr&igrave;nh 1, 2 v&agrave; 3 sẽ kh&ocirc;ng được nhận học bổng n&agrave;y.</p>\r\n<p><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td><em> \r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: VPDD MDIS tại Việt Nam</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</em>\r\n<p class="Normal">Tầng 9, to&agrave; nh&agrave; VIT, 519 Kim M&atilde;, H&agrave; Nội<br />Tel: 04 22 20 9292. Mobile: 0934597716<br />Email:&nbsp;<a class="Normal" href="mailto:mdis@mdis.vn">mdis@mdis.vn</a>&nbsp;hoặc website:&nbsp;<a class="Normal" href="http://www.mdis.vn/">http://www.mdis.vn</a>;<a class="Normal" href="http://www.mdis.edu.sg/">http://www.mdis.edu.sg</a><br />Tư vấn online (YM): mdisvn_tuvanonline</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>MDIS</em>)</p>', 4, 1, 2116, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(132, 'Chương trình quản trị kinh doanh quốc tế tại Việt Nam', 'Đây là chương trình diploma về quản trị kinh doanh quốc tế, học tại Việt Nam nhận bằng quốc tế của hội đồng khảo thí đại học Cambridge, Anh, tương đương chương trình Foundation tại Anh.', 'Sunrise-250.jpg', NULL, '<h1 class="Title">Chương tr&igrave;nh quản trị kinh doanh quốc tế tại Việt Nam</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="Sunrise-250.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Đ&acirc;y l&agrave; chương tr&igrave;nh diploma về quản trị kinh doanh quốc tế, học tại Việt Nam nhận bằng quốc tế của hội đồng khảo th&iacute; đại học Cambridge, Anh, tương đương chương tr&igrave;nh Foundation tại Anh.</h2>\r\n<p class="Normal">Ưu điểm lớn nhất của chương tr&igrave;nh l&agrave; tiết kiệm chi ph&iacute; một năm học dự bị đại học tại Anh. B&ecirc;n cạnh đ&oacute;, một năm học tại Việt Nam cũng gi&uacute;p học vi&ecirc;n bổ sung kiến thức học thuật cũng như tiếng Anh. Sau khi nhận bằng, học vi&ecirc;n c&oacute; thể nộp hồ sơ v&agrave;o những trường đại học tốt tại Anh, Australia, Mỹ.</p>\r\n<p class="Normal">Từ năm học 2010 &ndash; 2011, Sunrise Việt Nam th&agrave;nh lập Sunrise Edu chuy&ecirc;n mở lớp học IELTS v&agrave; tổ chức đ&agrave;o tạo chương tr&igrave;nh diploma về quản trị kinh doanh quốc tế do Hội đồng khảo th&iacute; đại học Cambridge cấp bằng trong đ&oacute; Sunrise Edu chịu tr&aacute;ch nhiệm đ&agrave;o tạo, Hội đồng khảo th&iacute; đại học Cambridge tổ chức thi v&agrave; chấm thi. Chương tr&igrave;nh giảng dạy v&agrave;o cuối tuần n&ecirc;n kh&ocirc;ng ảnh hưởng đến việc học tập tr&ecirc;n lớp của học vi&ecirc;n.</p>\r\n<p class="Normal">Chương tr&igrave;nh d&agrave;nh cho học vi&ecirc;n tr&ecirc;n 16 tuổi đ&atilde; học hết lớp 11 loại kh&aacute; hoặc tốt nghiệp lớp 12. Học vi&ecirc;n cần c&oacute; chứng chỉ ngoại ngữ IELTS 5 hoặc TOEFL 500. Học vi&ecirc;n chưa đủ tr&igrave;nh độ ngoại ngữ tr&ecirc;n sẽ được tham gia chương tr&igrave;nh tiếng Anh hỗ trợ do nh&agrave; trường cung cấp.</p>\r\n<p class="Normal">Chương tr&igrave;nh đ&agrave;o tạo:</p>\r\n<p class="Normal"><strong>Tiếng Anh tổng qu&aacute;t</strong>&nbsp;(4 kỹ năng) nhập m&ocirc;n căn bản gồm: Nhập m&ocirc;n h&agrave;nh vi tổ chức kinh doanh; Nguy&ecirc;n l&yacute; giao tiếp trong kinh doanh; Nguy&ecirc;n l&yacute; tiếp thị v&agrave; Nhập m&ocirc;n quản trị nh&acirc;n sự.</p>\r\n<p class="Normal"><strong>Tiếng Anh học thuật n&acirc;ng cao</strong>&nbsp;(giảng dạy bằng tiếng Anh) gồm: Giao tiếp hiệu quả trong kinh doanh; M&ocirc;i trường v&agrave; tổ chức kinh doanh; Tiếp thị; Quản trị nh&acirc;n sự.</p>\r\n<p class="Normal">Nh&acirc;n dịp khai giảng kh&oacute;a học th&aacute;ng 3 (22/3), Sunrise tặng từ điển tiếng Anh Cambridge cho học vi&ecirc;n đăng k&yacute; tham gia kh&oacute;a học.</p>\r\n<p class="Normal">Sunrise Edu tổ chức buổi học thử với nội dung &ldquo;H&agrave;nh vi người ti&ecirc;u d&ugrave;ng&rdquo; bằng cả tiếng Việt v&agrave; tiếng Anh 9h &ndash; 11h (Tiếng Anh) v&agrave; 14h - 16h (Tiếng Việt) ng&agrave;y 26/2 tại Văn ph&ograve;ng Sunrise, số 3 Cao B&aacute; Qu&aacute;t, Ba Đ&igrave;nh, HN.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Sunrise Việt Nam</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội (HO): 3A Cao B&aacute; Qu&aacute;t, Quận Ba Đ&igrave;nh<br />ĐT: +84 4 37224878 / 37224898 F: +84 4 37224855</p>\r\n<p class="Normal">Hải Ph&ograve;ng: 103 Văn Cao, Quận Ng&ocirc; Quyền<br />ĐT: +84 31 2640689 / 3732895</p>\r\n<p class="Normal">Sunrise Edu: Email&nbsp;<a class="Normal" href="mailto:edu@sunrisevietnam.com">edu@sunrisevietnam.com</a>; ĐT: 04 3722 4878; Hotline: 0936 772 474 (Ms. Mai); Đ&agrave;o tạo tiếng Anh/ Luyện thi IELTS:<a class="Normal" href="http://www.sunrise.edu.vn/">http://www.sunrise.edu.vn/</a>; Đ&agrave;o tạo c&aacute;c kỹ năng chuy&ecirc;n nghiệp Email:<a class="Normal" href="mailto:info@sunrisevietnam.com">info@sunrisevietnam.com</a>; Tư vấn du học:&nbsp;<a class="Normal" href="http://www.sunrisevietnam.com/">http://www.sunrisevietnam.com/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Sunrise Việt Nam</em>)</p>', 4, 1, 679, 0, 1),
(133, 'Chương trình trung học tại Oxford, Anh', 'EF Việt Nam giới thiệu chương trình tú tài quốc tế (IBDP) và A-level dành cho học sinh trên 16 tuổi của Học viện EF quốc tế đặt tại các phân viện Oxford và Torbay, Anh.', 'EF.jpg', NULL, '<h1 class="Title">Chương tr&igrave;nh trung học tại Oxford, Anh</h1>\r\n<h2 class="Lead">EF Việt Nam giới thiệu chương tr&igrave;nh t&uacute; t&agrave;i quốc tế (IBDP) v&agrave; A-level d&agrave;nh cho học sinh tr&ecirc;n 16 tuổi của Học viện EF quốc tế đặt tại c&aacute;c ph&acirc;n viện Oxford v&agrave; Torbay, Anh.</h2>\r\n<p class="Normal">Bằng t&uacute; t&agrave;i quốc tế (IBDP) v&agrave; A-level gi&uacute;p học sinh vượt qua c&aacute;c y&ecirc;u cầu đầu v&agrave;o của những trường đại học danh tiếng v&agrave; nhập học đ&uacute;ng chuy&ecirc;n ng&agrave;nh như: luật, y khoa, quản trị kinh doanh, t&agrave;i ch&iacute;nh, c&ocirc;ng nghệ th&ocirc;ng tin, phim ảnh, kh&aacute;ch sạn du lịch, quan hệ c&ocirc;ng ch&uacute;ng&hellip; Sau khi ho&agrave;n tất chương tr&igrave;nh, học sinh sẽ nộp đơn thẳng v&agrave;o những trường đại học theo nguyện vọng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="EF.jpg" border="1" alt="" width="400" height="180" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đặc biệt, tại ph&acirc;n viện Torbay, học sinh tr&ecirc;n 14 tuổi v&agrave; ho&agrave;n tất lớp 8 hoặc 9 tại Việt Nam c&oacute; thể v&agrave;o thẳng lớp 9 hoặc 10 chương tr&igrave;nh IGCSE.</p>\r\n<p class="Normal">Tiến sĩ Ted McGrath, hiệu trưởng Học viện quốc tế EF tại Oxford v&agrave; EF Việt Nam, sẽ phối hợp với Hội đồng Anh tại Việt Nam tổ chức buổi hội thảo th&ocirc;ng tin v&agrave; phỏng vấn học bổng theo lịch sau:</p>\r\n<p class="Normal">9h- 12h ng&agrave;y 19/2 tại 510 Nguyễn Đ&igrave;nh Chiểu, phường 4, quận 3, TP HCM.</p>\r\n<p class="Normal">9h- 12h ng&agrave;y 20/2 Ph&ograve;ng học 11, tầng 12, 20 Thụy Khu&ecirc;, quận T&acirc;y Hồ, HN.</p>\r\n<p class="Normal">Tham gia chương tr&igrave;nh học sinh được phỏng vấn v&agrave; trao bổng l&ecirc;n đến 15.000 USD cho 2 năm học, hỗ trợ t&agrave;i ch&iacute;nh 3.000 USD cho học sinh đ&aacute;p ứng điều kiện tuyển sinh v&agrave; qu&agrave; tặng 4-8 tuần tiếng Anh tại Oxford d&agrave;nh cho học sinh chưa đủ điều kiện tiếng Anh. Chương tr&igrave;nh &aacute;p dụng cho học sinh đăng k&yacute; đến hết ng&agrave;y 28/2.</p>\r\n<p class="Normal">Li&ecirc;n hệ: 04-397432910 (gặp c&ocirc; Lan Anh); 08-39291340 (gặp c&ocirc; Mỹ Hạnh/ c&ocirc; Trang Anh) hoặc tham khảo:&nbsp;<a class="Normal" href="http://www.ef.com/academy">www.ef.com/academy</a>.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>EF</em>)</p>', 4, 1, 533, 0, 1),
(135, 'Học bổng quản trị kinh doanh của Australia tại Việt Nam', 'Học bổng trị giá 15 triệu đồng mỗi suất (số lượng có hạn) chương trình quản trị kinh doanh của trường Martin College, Australia (MCP) dành cho học sinh của ILA.', '400x259.jpg', NULL, '<h1 class="Title">Học bổng quản trị kinh doanh của Australia tại Việt Nam</h1>\r\n<h2 class="Lead">Học bổng trị gi&aacute; 15 triệu đồng mỗi suất (số lượng c&oacute; hạn) chương tr&igrave;nh quản trị kinh doanh của trường Martin College, Australia (MCP) d&agrave;nh cho học sinh của ILA.</h2>\r\n<p class="Normal">Chương tr&igrave;nh học được g&oacute;i gọn trong 52 tuần. Sinh vi&ecirc;n học to&agrave;n ho&agrave;n bằng tiếng Anh trong m&ocirc;i trường quốc tế ngay tại Việt Nam, được giảng dạy bởi đội ngũ giảng vi&ecirc;n của MCP c&oacute; bằng cấp chuy&ecirc;n m&ocirc;n. Tr&igrave;nh độ của giảng vi&ecirc;n được Tổ chức quản l&yacute; chất lượng đ&agrave;o tạo của ch&iacute;nh phủ Australia c&ocirc;ng nhận. Tất cả c&oacute; &iacute;t nhất 5 năm kinh nghiệm trong lĩnh vực quản l&yacute;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="400x259.jpg" border="1" alt="Đại" width="400" height="259" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đại diện l&atilde;nh sự qu&aacute;n Australia trao bằng tốt nghiệp cho sinh vi&ecirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Khi tốt nghiệp, học vi&ecirc;n MCP được nhận bằng quản trị kinh doanh (Advanced Diploma of Management) được c&ocirc;ng nhận quốc tế do trường Martin (Australia) cấp, tương đương bằng cao đẳng quốc tế, c&oacute; gi&aacute; trị chuyển tiếp khi du học v&agrave; gi&uacute;p việc xin visa dễ d&agrave;ng hơn. C&aacute;c m&ocirc;n học trong chương tr&igrave;nh c&oacute; t&iacute;nh ứng dụng cao n&ecirc;n ngo&agrave;i việc tiếp tục du học, một số sinh vi&ecirc;n MCP đ&atilde; lựa chọn đi l&agrave;m ngay sau khi tốt nghiệp.</p>\r\n<p class="Normal">Với 10 năm kinh nghiệm tư vấn du học quốc tế, Trung t&acirc;m tư vấn du học ILA sẽ tư vấn miễn ph&iacute; về những cơ hội học tập v&agrave; học bổng để c&oacute; sự lựa chọn tốt nhất khi tiếp tục du học chuyển tiếp.</p>\r\n<p class="Normal">C&aacute;c kỳ khai giảng trong năm 2011: Kh&oacute;a 27: ng&agrave;y 14/3; Kh&oacute;a 28: ng&agrave;y 6/6; Kh&oacute;a 29: ng&agrave;y 29/8; Kh&oacute;a 30 ng&agrave;y 21/11.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Trung t&acirc;m đ&agrave;o tạo ILA</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">51 Nguyễn Cư Trinh, Phường Nguyễn Cư Trinh, quận 1, TP HCM.</p>\r\n<p class="Normal">Đăng k&yacute; học bổng: Ms. Uy&ecirc;n &ndash; ĐT: 38386788 (1332)</p>\r\n<p class="Normal">Email:&nbsp;<a class="Normal" href="mailto:uyentran@ilavietnam.com"></a><a href="mailto:uyentran@ilavietnam.com">uyentran@ilavietnam.com</a><em>&nbsp;</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><em>(</em>Nguồn:<em>&nbsp;ILA)</em></p>', 4, 1, 2471, 0, 1),
(136, 'Học bổng tú tài quốc tế tại AIC, New Zealand', 'Học bổng kéo dài từ 2 đến 3 năm, tùy thuộc vào kết quả thi tuyển, học tập và hoạt động ngoại khóa hiện tại của học sinh tại Việt Nam.', '400x300.jpg', NULL, '<h1 class="Title">Học bổng t&uacute; t&agrave;i quốc tế tại AIC, New Zealand</h1>\r\n<h2 class="Lead">Học bổng k&eacute;o d&agrave;i từ 2 đến 3 năm, t&ugrave;y thuộc v&agrave;o kết quả thi tuyển, học tập v&agrave; hoạt động ngoại kh&oacute;a hiện tại của học sinh tại Việt Nam.</h2>\r\n<p class="Normal">Chương tr&igrave;nh học bổng 2011 bắt đầu ph&aacute;t đơn từ ng&agrave;y 8/2. Thi học bổng sẽ diễn ra v&agrave;o ng&agrave;y 19 v&agrave; 26/2 với hai m&ocirc;n to&aacute;n v&agrave; Anh văn, k&eacute;o d&agrave;i tr&ecirc;n 2 giờ. Học bổng d&agrave;nh cho học sinh lớp 10 v&agrave; 11 c&oacute; học lực giỏi v&agrave; mong muốn v&agrave;o c&aacute;c trường đại học danh tiếng thế giới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="400x300.jpg" border="1" alt="" width="400" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sinh vi&ecirc;n Nam được trao học bổng chương tr&igrave;nh t&uacute; t&agrave;i quốc tế tại NZ 2009.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&uacute; t&agrave;i quốc tế (IB Diploma) được c&aacute;c trường đại học h&agrave;ng đầu thế giới đ&aacute;nh gi&aacute; l&agrave; chương tr&igrave;nh trung học danh tiếng v&agrave; l&agrave; sự chuẩn bị tốt cho sinh vi&ecirc;n bước v&agrave;o đại học. C&aacute;n bộ phụ tr&aacute;ch tuyển sinh của c&aacute;c trường đại học v&agrave; c&aacute;c chuy&ecirc;n gia của ch&iacute;nh phủ c&aacute;c nước đều c&oacute; thể kiểm tra trực tiếp chương tr&igrave;nh học, kết quả c&aacute;c kỳ thi v&agrave; kiểm tra học sinh khi nộp đơn. Hiện c&oacute; hơn 700.000 học sinh khắp thế giới chọn học chương tr&igrave;nh n&agrave;y tại hơn 126 quốc gia.</p>\r\n<p class="Normal">Chương tr&igrave;nh đ&agrave;o tạo học sinh giỏi qua 6 nh&oacute;m m&ocirc;n học ch&iacute;nh, c&aacute;c m&ocirc;n học nền tảng như l&yacute; thuyết kiến thức, tiểu luận mở rộng, thể hiện t&agrave;i năng nghệ thuật v&agrave; y&ecirc;u cầu ho&agrave;n th&agrave;nh hơn 1.500 giờ hoạt động cộng đồng. Học sinh được đ&aacute;nh gi&aacute; theo hai hệ thống gồm:</p>\r\n<p class="Normal">- Hệ thống nội bộ trong suốt qu&aacute; tr&igrave;nh học bao gồm c&aacute;c b&agrave;i tập tại lớp, b&agrave;i tập về nh&agrave;, b&agrave;i thi tại trường v&agrave; hệ thống b&ecirc;n ngo&agrave;i qua c&aacute;c b&agrave;i thi do c&aacute;c gi&aacute;o vi&ecirc;n của tổ chức IB chấm điểm nhằm đảm bảo chất lượng đ&agrave;o tạo cao cấp, t&iacute;nh kh&aacute;ch quan v&agrave; độ tin cậy của hệ thống đ&aacute;nh gi&aacute; theo chuẩn mực cao.</p>\r\n<p class="Normal">- Hệ thống cho điểm dựa tr&ecirc;n ti&ecirc;u chuẩn c&oacute; căn cứ, tin cậy v&agrave; c&ocirc;ng bằng. Ngay sau khi kết quả được c&ocirc;ng bố, tổ chức IB sẽ chuyển điểm của học sinh v&agrave;o c&aacute;c văn ph&ograve;ng tuyển sinh của c&aacute;c trường đại học tr&ecirc;n khắp thế giới.</p>\r\n<p class="Normal">Học sinh c&oacute; bảng điểm từ 1 đến 7 (thấp đến cao nhất của chương tr&igrave;nh t&uacute; t&agrave;i quốc tế) v&agrave; học sinh được cộng th&ecirc;m 3 điểm từ nh&oacute;m m&ocirc;n học nền tảng, do đ&oacute; điểm thủ khoa của IB sẽ l&agrave; 45 điểm. Để nhận được bằng t&uacute; t&agrave;i quốc tế, điểm thấp nhất học sinh phải đạt l&agrave; 24 điểm v&agrave; phải đạt đủ số giờ v&agrave; đạt kết quả theo y&ecirc;u cầu chương tr&igrave;nh học về s&aacute;ng tạo, hoạt động v&agrave; phục vụ cộng đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Văn ph&ograve;ng đại diện của Auckland International College</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chương tr&igrave;nh T&uacute; t&agrave;i quốc tế (IB) tại Việt Nam - Văn ph&ograve;ng tư vấn gi&aacute;o dục v&agrave; di tr&uacute; NZ - ENC, 40/7 Lữ Gia, Phường 15, quận 11, TP HCM.<br />ĐT: (08) 38 663635 - (08) 386 41457 - 0908036949<br />Fax: (08) 38 665 261 Email:&nbsp;<a class="Normal" href="mailto:enzc@hcm.vnn.vn">enzc@hcm.vnn.vn</a>;&nbsp;<br />Website:&nbsp;<a class="Normal" href="http://www.dinuocngoai.com/">http://www.dinuocngoai.com/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>ENC</em>)</p>', 4, 1, 1071, 0, 1),
(138, 'Đức Anh tuyển sinh các khoá tiếng Anh tại Hà Nội', 'Đức Anh tuyển sinh các lớp tiếng Anh khai giảng tại Dace vào ngày 14/2. Tất cả các học sinh học tại Dace sẽ được hỗ trợ 200 USD khi đăng ký du học.', 'tuyensinh.jpg', NULL, '<h1 class="Title">Đức Anh tuyển sinh c&aacute;c kho&aacute; tiếng Anh tại H&agrave; Nội</h1>\r\n<h2 class="Lead">Đức Anh tuyển sinh c&aacute;c lớp tiếng Anh khai giảng tại Dace v&agrave;o ng&agrave;y 14/2. Tất cả c&aacute;c học sinh học tại Dace sẽ được hỗ trợ 200 USD khi đăng k&yacute; du học.</h2>\r\n<p class="Normal">Đức Anh l&agrave; c&ocirc;ng ty tư vấn du học chuy&ecirc;n nghiệp, tư vấn về thủ tục, quy định xuất cảnh, gi&uacute;p học sinh hiểu r&otilde; tr&igrave;nh tự v&agrave; đặc điểm của việc học tập tại nhiều quốc gia. Tr&ecirc;n 4.000 học sinh đ&atilde; được gửi đi du học th&agrave;nh c&ocirc;ng tr&ecirc;n 19 nước như Anh, Australia, Canada, Mỹ, New Zealand, Ph&aacute;p, H&agrave; Lan, Thụy Sĩ, Singapore, Malaysia... th&ocirc;ng qua Đức Anh. C&ocirc;ng ty cũng đ&atilde; được Đại sứ qu&aacute;n Australia trao giải "Đơn vị tuyển sinh đạt th&agrave;nh t&iacute;ch cao khu vực ph&iacute;a Bắc" năm 2006 v&agrave; giải "Đơn vị tuyển sinh du học xuất sắc khu vực ph&iacute;a Bắc" năm 2003-2004.</p>\r\n<p class="Normal">Đức Anh c&ograve;n được Đại sứ qu&aacute;n New Zealand trao giải "Top Achieving Agent" năm 2005; được đại học Monash - Australia b&igrave;nh chọn l&agrave; "Đơn vị tuyển sinh ti&ecirc;u biểu trong th&aacute;ng" th&aacute;ng 2/2008 v&agrave; th&aacute;ng 8/2009; đạt danh hiệu "Đại l&yacute; Bạc" năm 2009 v&agrave; 2010 bởi EASB của Singapore v&agrave; danh hiệu "Đại l&yacute; xuất sắc" năm 2009 v&agrave; 2010 bởi HTMi, Thụy Sĩ. B&ecirc;n cạnh đ&oacute;, Đức Anh c&ograve;n đoạt giải "Top Performance"- TAFE NSW, Australia, l&agrave; đại diện độc quyền của nhiều đại học danh tiếng tr&ecirc;n thế giới. C&aacute;c dịch vụ tại Đức Anh c&oacute; t&iacute;nh kh&eacute;p k&iacute;n, hiệu quả v&agrave; minh bạch.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tuyensinh.jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại Đức Anh c&oacute; 11 chương tr&igrave;nh du học th&ocirc;ng dụng. Dịch vụ du học cụ thể tại Đức Anh A&amp;T gồm: Tư vấn v&agrave; cung cấp th&ocirc;ng tin về nước, trường v&agrave; ng&agrave;nh học; Tư vấn thủ tục xin thư mời học - lấy thư mời; Tư vấn thủ tục xin visa du học - đăng k&yacute; xin visa du học; Đ&agrave;o tạo tiếng Anh cho học sinh; R&agrave; so&aacute;t hồ sơ, luyện phỏng vấn xin học bổng, xin visa du học; Đặt kh&aacute;m sức khoẻ, v&eacute; m&aacute;y bay, đ&oacute;n tại s&acirc;n bay, nh&agrave; ở, chuyển tiền học v&agrave; l&agrave;m thủ tục thăm con cho phụ huynh.</p>\r\n<p class="Normal">C&ocirc;ng ty c&oacute; c&aacute;c trung t&acirc;m Anh ngữ Dace, trực thuộc Đức Anh A&amp;T, được c&aacute;c học vi&ecirc;n v&agrave; phụ huynh học sinh đ&aacute;nh gi&aacute; l&agrave; một trong những nơi tốt để chọn học c&aacute;c chương tr&igrave;nh tiếng Anh học thuật v&agrave; luyện IELTS, TOEFL.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">C&ocirc;ng ty Tư vấn - Gi&aacute;o dục v&agrave; Dịch thuật Quốc tế Đức Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 96 L&ograve; Đ&uacute;c, ĐT: (04) 3971 6229, Email:&nbsp;<a class="Normal" href="mailto:duhoc@ducanh.edu.vn">duhoc@ducanh.edu.vn</a><br />Hải Ph&ograve;ng: 32F Điện Bi&ecirc;n Phủ, ĐT: 0313 686 689, Email:<a class="Normal" href="mailto:adminhp@ducanh.edu.vn">adminhp@ducanh.edu.vn</a><br />TP HCM: 351 V&otilde; Văn Tần, quận 3, ĐT: (08) 3929 3995, Fax: (08) 39293 996, Email:&nbsp;<a class="Normal" href="mailto:adminhcm@ducanh.edu.vn">adminhcm@ducanh.edu.vn</a><br />Trung t&acirc;m Anh ngữ Dace - Luyện IELTS v&agrave; tiếng Anh học thuật: 8D H&agrave;m Long, H&agrave; Nội. ĐT: (04) 3944 8589, Email:&nbsp;<a class="Normal" href="mailto:dacehn@ducanh.edu.vn">dacehn@ducanh.edu.vn</a></p>\r\n<p class="Normal">Website:&nbsp;<a class="Normal" href="http://www.ducanhduhoc.com/">www.ducanhduhoc.com</a></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Đức Anh</em>)</p>', 4, 1, 2502, 0, 1),
(139, 'Học bổng du học hè tại Mỹ 2011', 'Tổ chức giáo dục tại Mỹ AMVN Exchange (America – Vietnam Exchange) và trung tâm du học Mỹ AECT giới thiệu 3 chương trình trại hè 2011 tại Mỹ. Tham gia chương trình học sinh có cơ hội nhận học bổng trị giá tới 2.250 USD.', 'hinh_2_400X194.jpg', NULL, '<h1 class="Title">Học bổng du học h&egrave; tại Mỹ 2011</h1>\r\n<h2 class="Lead">Tổ chức gi&aacute;o dục tại Mỹ AMVN Exchange (America &ndash; Vietnam Exchange) v&agrave; trung t&acirc;m du học Mỹ AECT giới thiệu 3 chương tr&igrave;nh trại h&egrave; 2011 tại Mỹ. Tham gia chương tr&igrave;nh học sinh c&oacute; cơ hội nhận học bổng trị gi&aacute; tới 2.250 USD.</h2>\r\n<p class="Normal">Tham gia chương tr&igrave;nh học sinh được n&acirc;ng cao khả năng tiếng Anh v&agrave; cơ hội học c&ugrave;ng c&aacute;c gi&aacute;o sư đại học c&oacute; kinh nghiệm nhiều năm trong việc giảng dạy tiếng Anh. C&aacute;c em được kết bạn với học sinh quốc tế để t&igrave;m hiểu nhiều nền văn h&oacute;a. Học sinh được tham gia c&aacute;c hoạt động ngoại kh&oacute;a như: b&oacute;ng rổ, đua thuyền, tiệc nướng ngo&agrave;i trời, hướng đạo sinh&hellip; Ngo&agrave;i ra, học sinh c&ograve;n được tham quan c&aacute;c địa điểm nổi tiếng v&agrave; c&aacute;c trường đại học của bang.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hinh_2_400X194.jpg" border="1" alt="" width="400" height="194" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đặc biệt, học sinh c&ograve;n c&oacute; cơ hội gi&agrave;nh c&aacute;c suất học bổng 1.000 USD, 500 USD, 250 USD khi tham gia chương tr&igrave;nh du học h&egrave; của AECT.</p>\r\n<h3 class="SubTitle">3 chương tr&igrave;nh trại h&egrave; gồm:</h3>\r\n<p class="Normal"><span style="font-family: Arial;">-</span>&nbsp;Chương tr&igrave;nh "World of Fun" tại đảo Hawaii, trường Hawaii Community College.</p>\r\n<p class="Normal"><span style="font-family: Arial;">-</span>&nbsp;Chương tr&igrave;nh "Sun and fun camp" tại bang California &ndash; Diablo Valley Community College.</p>\r\n<p class="Normal"><span style="font-family: Arial;">-</span>&nbsp;Chương tr&igrave;nh "American Cuture" tại bang Nebraska &ndash; trường University Nebraska</p>\r\n<p class="Normal">Trại h&egrave; năm nay c&ograve;n tổ chức cho học sinh từ 13 tuổi tham dự cuộc thi &ldquo;Săn t&igrave;m học bổng Mỹ&rdquo; d&agrave;nh cho c&aacute;c chương tr&igrave;nh THPT, đại học v&agrave; sau đại học với c&aacute;c giải thưởng sau: Giải xu&acirc;n đặc biệt: Học bổng du học h&egrave; 2 tuần tại Mỹ trị gi&aacute; 2.250 USD; giải nhất: 1.000 USD (một giải); giải nh&igrave;: 500 USD (2 giải); giải ba: 250 USD (3 giải). Học sinh c&oacute; thể đăng k&yacute; trực tuyến&nbsp;<a class="Normal" href="http://aectvn.com/summer-camp/summer-camp-form">tại đ&acirc;y</a>. Thi tiếng Anh.</p>\r\n<table border="0" cellspacing="0" cellpadding="0" width="477" align="center" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="0" width="100%" align="right" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Trung t&acirc;m du học Mỹ AECT</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Phong</p>\r\n<p class="Normal">Ph&ograve;ng 1.01, t&ograve;a nh&agrave; eSmart, 481 Điện Bi&ecirc;n Phủ, phường 3, quận 3, TP HCM</p>\r\n<p class="Normal">Điện thoại: 08.3938.1436 - Di động: 0933.986.966</p>\r\n<p class="Normal">Email:&nbsp;<a class="Normal" href="mailto:info_hcm@aectvn.com">info_hcm@aectvn.com</a></p>\r\n<p class="Normal">Số 12/172 Lạc Long Qu&acirc;n, T&acirc;y Hồ, H&agrave; Nội</p>\r\n<p class="Normal">ĐT: 04.3759.1688 &ndash; Hotline: (84) 986.672.984</p>\r\n<p class="Normal">Email:&nbsp;<a class="Normal" href="mailto:info@aectvn.com">info@aectvn.com</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>AECT</em>)</p>', 4, 1, 1836, 0, 1),
(140, 'Hội thảo ĐH Quản trị Khách sạn Du lịch Thụy Sĩ - SHMS', 'Chương trình hội thảo được tổ chức ngày 17h30 ngày 25/2 tại khách sạn Movenpick, 83A Lý Thường Kiệt, Hà Nội và 17h30 ngày 26/2 tại khách sạn Hoàng Anh Gia Lai, số 1 Nguyễn Văn Linh, Đà Nẵng.', 'duhoc.jpg', NULL, '<h1 class="Title">Hội thảo ĐH Quản trị Kh&aacute;ch sạn Du lịch Thụy Sĩ - SHMS</h1>\r\n<h2 class="Lead">Chương tr&igrave;nh hội thảo được tổ chức ng&agrave;y 17h30 ng&agrave;y 25/2 tại kh&aacute;ch sạn Movenpick, 83A L&yacute; Thường Kiệt, H&agrave; Nội v&agrave; 17h30 ng&agrave;y 26/2 tại kh&aacute;ch sạn Ho&agrave;ng Anh Gia Lai, số 1 Nguyễn Văn Linh, Đ&agrave; Nẵng.</h2>\r\n<p class="Normal">SHMS l&agrave; trường đại học quản trị kh&aacute;ch sạn du lịch đ&agrave;o tạo bằng tiếng Anh tại Thụy Sĩ, l&agrave; th&agrave;nh vi&ecirc;n của ASEH - Hiệp hội c&aacute;c trường quản trị kh&aacute;ch sạn du lịch tốt nhất Thụy Sĩ (<a class="Normal" href="http://www.aseh.ch/">http://www.aseh.ch/</a>). SHMS tuyển sinh cho kỳ khai giảng tiếng Anh v&agrave;o th&aacute;ng 5, 6 v&agrave; 7 cũng như kỳ khai giảng chương tr&igrave;nh cử nh&acirc;n, thạc sĩ v&agrave;o th&aacute;ng 9.</p>\r\n<p class="Normal">SHMS đ&agrave;o tạo cử nh&acirc;n (ba năm); sau đại học - PGD (một năm), thạc sĩ (một năm) v&agrave; kh&oacute;a tiếng Anh dự bị (4-16 tuần). SHMS hỗ trợ 60% tổng chi ph&iacute; kh&oacute;a tiếng Anh.<br /><br />C&aacute;c chuy&ecirc;n ng&agrave;nh được đ&agrave;o tạo: Cử nh&acirc;n quản trị kh&aacute;ch sạn; cử nh&acirc;n quản trị kh&aacute;ch sạn v&agrave; sự kiện, cử nh&acirc;n quản trị kh&aacute;ch sạn v&agrave; du lịch, PGD quản trị kh&aacute;ch sạn, PGD quản trị hoạt động kh&aacute;ch sạn; PGD quản trị sự kiện; PGD quản trị du lịch; thạc sĩ quản trị kh&aacute;ch sạn quốc tế.</p>\r\n<p class="Normal">Sinh vi&ecirc;n tốt nghiệp cử nh&acirc;n tại SHMS được nhận ba bằng cấp quốc tế của Thụy Sĩ, Mỹ v&agrave; Anh. Một năm học gồm năm th&aacute;ng học l&yacute; thuyết v&agrave; s&aacute;u th&aacute;ng đi thực tập hưởng lương tại Thụy Sĩ hoặc nước ngo&agrave;i. Mức lương tối thiểu ở Thụy Sĩ l&agrave; 44 - 48 triệu đồng một th&aacute;ng. Bằng cử nh&acirc;n v&agrave; thạc sĩ của Thụy Sĩ trong lĩnh vực n&agrave;y được đ&agrave;o tạo đặc biệt kết hợp giữa l&yacute; thuyết, thực h&agrave;nh v&agrave; thực tập hưởng lương. Ngo&agrave;i chương tr&igrave;nh học ho&agrave;n to&agrave;n bằng tiếng Anh, sinh vi&ecirc;n SHMS được học th&ecirc;m ngoại ngữ hai l&agrave; tiếng Ph&aacute;p, Đức hoặc T&acirc;y Ban Nha.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duhoc.jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ocirc;ng ty Visco, đại diện ch&iacute;nh thức của SHMS tại Việt Nam gi&uacute;p sinh vi&ecirc;n nhập học tại trườngmiễn dịch vụ ph&iacute;. Visco kết hợp với trường tặng v&eacute; m&aacute;y bay v&agrave; m&aacute;y t&iacute;nh x&aacute;ch tay (tổng gi&aacute; trị l&agrave; 29 triệu đồng) cho sinh vi&ecirc;n đăng k&yacute; chương tr&igrave;nh học đại học v&agrave; tặng v&eacute; m&aacute;y bay v&agrave; lệ ph&iacute; xin visa cho sinh vi&ecirc;n đăng k&yacute; chương tr&igrave;nh sau đại học hoặc thạc sỹ năm 2011.</p>\r\n<p class="Normal">Thủ tục hồ sơ đơn giản, kh&ocirc;ng y&ecirc;u cầu chứng minh t&agrave;i ch&iacute;nh, sinh vi&ecirc;n được cấp visa ch&acirc;u &Acirc;u để tự do đi lại trong 25 nước ch&acirc;u &Acirc;u.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><span style="color: #800000;"><strong>Li&ecirc;n hệ: C&ocirc;ng ty du học Visco</strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">230 Kim M&atilde;, quận Ba Đ&igrave;nh, H&agrave; Nội, ĐT: (04) 3726 1938, Email:<a class="Normal" href="mailto:viscohanoi@visco.edu.vn">viscohanoi@visco.edu.vn</a><br /><br />239 C&aacute;ch Mạng Th&aacute;ng T&aacute;m, Nh&agrave; A02, Chung cư văn h&oacute;a, phường 4, quận 3, TP HCM, ĐT: (08) 3832 8416 - 3839 0718, Email:&nbsp;<a class="Normal" href="mailto:viscohcm@visco.edu.vn">viscohcm@visco.edu.vn</a></p>\r\n<p class="Normal">433 Phan Chu Trinh, quận Hải Ch&acirc;u, Đ&agrave; Nẵng, ĐT: (0511)3552597 - 3552596, Email:&nbsp;<a class="Normal" href="mailto:viscodn@visco.edu.vn">viscodn@visco.edu.vn</a></p>\r\n<p class="Normal">309 Hai B&agrave; Trưng (C&aacute;t D&agrave;i), quận L&ecirc; Ch&acirc;n, Hải Ph&ograve;ng, ĐT: (031) 3950215, Email:&nbsp;<a class="Normal" href="mailto:viscohp@visco.edu.vn">viscohp@visco.edu.vn</a></p>\r\n<p class="Normal">Tư vấn online: YM duhocvisco<br />Th&ocirc;ng tin chi tiết, sinh vi&ecirc;n c&oacute; thể tham khảo tại website&nbsp;<a class="Normal" href="http://www.shms.com/">http://www.shms.com/</a>;<a class="Normal" href="http://www.visco.edu.vn/">www.visco.edu.vn</a></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Visco</em>)</p>', 4, 1, 332, 0, 1),
(141, 'Học bổng tại đại học Monash, Australia', 'Đại diện trường, tiến sĩ Hoa Levitas sẽ giới thiệu nội dung học bổng tại hội thảo tổ chức từ 16h đến 18h ngày 18/1 tại 32F Điện Biên Phủ, Hải Phòng và từ 16h đến 18h ngày 20/1 tại 96 Lò Đúc, Hà Nội.', 'monash.jpg', NULL, '<h1 class="Title">Học bổng tại đại học Monash, Australia</h1>\r\n<h2 class="Lead">Đại diện trường, tiến sĩ Hoa Levitas sẽ giới thiệu nội dung học bổng tại hội thảo tổ chức từ 16h đến 18h ng&agrave;y 18/1 tại 32F Điện Bi&ecirc;n Phủ, Hải Ph&ograve;ng v&agrave; từ 16h đến 18h ng&agrave;y 20/1 tại 96 L&ograve; Đ&uacute;c, H&agrave; Nội.</h2>\r\n<p class="Normal">Trường cung cấp 25 suất học bổng 5 tuần kh&oacute;a tiếng Anh bắc cầu trị gi&aacute; 2.250 AUD mỗi suất v&agrave; 30 suất học bổng đại học hoặc thạc sĩ trị gi&aacute; 3.000 AUD mỗi suất cho sinh vi&ecirc;n đăng k&yacute; nhập học kỳ một th&aacute;ng 3.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="monash.jpg" border="1" alt="" width="400" height="290" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đại học Monash xếp thứ 43/100 trường tốt nhất thế giới theo The Times Good University Guide đồng thời trường thuộc nh&oacute;m Go8 (một trong 8 trường tốt nhất tại Australia). Học sinh c&oacute; thể học 2 chuy&ecirc;n ng&agrave;nh ch&iacute;nh trong khi học một bằng đơn 3 năm.</p>\r\n<p class="Normal">Monash c&oacute; được sự t&agrave;i trợ nhiều từ ch&iacute;nh phủ, AusAID v&agrave; học bổng to&agrave;n phần cho sinh vi&ecirc;n so với c&aacute;c trường đại học kh&aacute;c của Australia.</p>\r\n<h3 class="SubTitle">Chương tr&igrave;nh đ&agrave;o tạo:</h3>\r\n<p class="Normal">Monash cung cấp hơn 160 kho&aacute; học bậc cử nh&acirc;n v&agrave; 240 kho&aacute; sau đại học trong 10 ph&acirc;n khoa v&agrave; 8 cơ sở đ&agrave;o tạo tại 3 ch&acirc;u lục. C&aacute;c ph&acirc;n khoa bao gồm: Mỹ thuật v&agrave; thiết kế, gi&aacute;o dục, luật; khoa học, nghệ thuật, kỹ thuật, y khoa - y t&aacute; v&agrave; khoa học sức khoẻ, kinh doanh v&agrave; kinh tế, c&ocirc;ng nghệ th&ocirc;ng tin, dược khoa v&agrave; khoa học dược.<strong>&nbsp;</strong></p>\r\n<p class="Normal">C&aacute;c bậc học: Tiếng Anh; Dự bị đại học, cao đẳng: 1 năm; Đại học: 3 - 4 năm; Thạc sĩ: 1 - 2 năm.</p>\r\n<p class="Normal">Học sinh được miễn ph&iacute; x&eacute;t hồ sơ xin học, trị gi&aacute; 100 AUD. C&ocirc;ng ty tư vấn du học Đức Anh hỗ trợ học sinh thực hiện c&aacute;c thủ tục h&agrave;nh ch&iacute;nh trong c&aacute;c việc xin học, xin visa du học. Ngo&agrave;i ra, c&ocirc;ng ty c&ograve;n hỗ trợ đến 10% học ph&iacute; tiếng Anh tại Trung t&acirc;m Anh ngữ Đức Anh &ndash; Dace.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: C&ocirc;ng ty tư vấn du học v&agrave; dịch thuật Đức Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 96 L&ograve; Đ&uacute;c, ĐT: 39716229 &ndash; 39720889, Email:&nbsp;<a class="Normal" href="mailto:duhoc@ducanh.edu.vn">duhoc@ducanh.edu.vn</a></p>\r\n<p class="Normal">Hải Ph&ograve;ng: Điện Bi&ecirc;n Phủ, ĐT: 0313.686.689, Email:&nbsp;<a class="Normal" href="http://vnexpress.net/gl/xa-hoi/co-hoi-du-hoc/2011/01/3ba25630/dichthuatducanh@vnn.vn">dichthuatducanh@vnn.vn</a></p>\r\n<p class="Normal">TP HCM: 531 V&otilde; Văn Tần, Q.3, ĐT: (08) 39293 995,&nbsp;<br />Fax: (08) 39293 996, Email:&nbsp;<a class="Normal" href="http://vnexpress.net/gl/xa-hoi/co-hoi-du-hoc/2011/01/3ba25630/adminhcm@ducanh.edu.vn">adminhcm@ducanh.edu.vn</a></p>\r\n<p class="Normal">Trung t&acirc;m Anh ngữ Dace- Luyện IELTS v&agrave; tiếng Anh học thuật: 8D H&agrave;m Long, H&agrave; Nội. ĐT: 043944 8589, email:&nbsp;<a class="Normal" href="http://vnexpress.net/gl/xa-hoi/co-hoi-du-hoc/2011/01/3ba25630/Dacehn@ducanh.edu.vn">Dacehn@ducanh.edu.vn</a></p>\r\n<a class="Normal" href="http://www.ducanhduhoc.com/">http://www.ducanhduhoc.com</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Đức Anh)</em></p>', 4, 1, 2147, 0, 1),
(142, 'UK Alumni Talk đầu năm của Hội đồng Anh', 'Alumni Talk tháng 1 chủ đề “Bí quyết học tập và trau dồi các kỹ năng phát triển nghề nghiệp qua các bậc học tại Anh”.', 'HDA-250.jpg', NULL, '<h1 class="Title">UK Alumni Talk đầu năm của Hội đồng Anh</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="HDA-250.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Alumni Talk th&aacute;ng 1 chủ đề &ldquo;B&iacute; quyết học tập v&agrave; trau dồi c&aacute;c kỹ năng ph&aacute;t triển nghề nghiệp qua c&aacute;c bậc học tại Anh&rdquo;.</h2>\r\n<p class="Normal">Chương tr&igrave;nh nhằm đem lại cho sinh vi&ecirc;n những kiến thức về phương ph&aacute;p học tập tại Anh, c&aacute;ch lĩnh hội v&agrave; ph&aacute;t triển c&aacute;c kỹ năng mềm, c&aacute;c kỹ năng m&agrave; c&aacute;c nh&agrave; tuyển dụng tr&ecirc;n thế giới y&ecirc;u cầu khi sống v&agrave; học tập tại đ&acirc;y.</p>\r\n<p class="Normal">Nền gi&aacute;o dục Anh kh&ocirc;ng dạy bạn &ldquo;nghĩ c&aacute;i g&igrave;&rdquo; m&agrave; l&agrave; &ldquo;nghĩ như thế n&agrave;o&rdquo;. N&oacute;i một c&aacute;ch kh&aacute;c, đ&oacute; l&agrave; nền gi&aacute;o dục lu&ocirc;n ch&uacute; trọng đến sự ph&aacute;t triển kỹ năng cho mỗi c&aacute; nh&acirc;n, gi&uacute;p cho c&aacute;c sinh vi&ecirc;n tốt nghiệp c&oacute; thể tự tin bước v&agrave;o thị trường tuyển dụng v&igrave; đ&atilde; được trang bị những kỹ năng m&agrave; c&aacute;c nh&agrave; tuyển dụng h&agrave;ng đầu thế giới lu&ocirc;n y&ecirc;u cầu.</p>\r\n<p class="Normal">Để tận dụng lợi thế đ&oacute; của nền gi&aacute;o dục h&agrave;ng đầu thế giới, bạn cần phải biết phương ph&aacute;p học tập như thế n&agrave;o l&agrave; hiệu quả nhất. L&agrave;m thế n&agrave;o để qu&atilde;ng thời gian du học tại Anh sẽ đem lại cho bạn kh&ocirc;ng chỉ những kiến thức thiết yếu m&agrave; cả những kỹ năng quan trọng như kỹ năng thu nhận kiến thức nhanh v&agrave; hiệu quả, kỹ năng ph&acirc;n t&iacute;ch vấn đề, giải quy&ecirc;t vấn đề từ những th&ocirc;ng tin sẵn c&oacute;, kỹ năng giao tiếp, kỹ năng thuyết phục, kỹ năng chịu &aacute;p lực v&agrave; l&agrave;m việc với cường độ cao.</p>\r\n<h3 class="SubTitle">Tham gia chương tr&igrave;nh c&oacute;:</h3>\r\n<p class="Normal">Tại H&agrave; Nội: Tạ Th&aacute;i Li&ecirc;n - Cựu sinh vi&ecirc;n trường đại học London Metropolitan, thạc sĩ chuy&ecirc;n ng&agrave;nh truyền th&ocirc;ng v&agrave; marketing quốc tế. Li&ecirc;n đang phụ tr&aacute;ch hoạt động truyền th&ocirc;ng tại C&ocirc;ng ty Savills Việt Nam.</p>\r\n<p class="Normal">Đặng Hương Giang: Cựu sinh vi&ecirc;n trường kinh tế Ch&iacute;nh trị London (LSE), cử nh&acirc;n chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh kế to&aacute;n. Cựu sinh vi&ecirc;n trường đại học Aston, thạc sĩ chuy&ecirc;n ng&agrave;nh quản trị kinh doanh.</p>\r\n<p class="Normal">Nguyễn H&agrave; Việt: Tốt nghiệp A-level của trường Ruthin với kết quả học tập xuất sắc v&agrave; đạt nhiều giải thưởng như huy chương v&agrave;ng giải to&aacute;n to&agrave;n Anh năm 2009. Việt c&oacute; kinh nghiệm l&agrave;m việc part-time v&agrave; internship tại nhiều c&ocirc;ng ty v&agrave; tổ chức quốc tế như Citibank; Cathay Pacific Airways.</p>\r\n<p class="Normal">Tại TP HCM: Bạn Đặng Ho&agrave;ng Uy&ecirc;n Thy - Cựu sinh vi&ecirc;n đại học Birmingham, cử nh&acirc;n chuy&ecirc;n ng&agrave;nh thương mại. Cựu sinh vi&ecirc;n đại học Aston, thạc sĩ chuy&ecirc;n ng&agrave;nh quản trị Marketing. Thy c&oacute; 5 năm học tập tại Anh từ bậc dự bị đại học đến thạc sĩ, c&oacute; nhiều kinh nghiệm l&agrave;m việc part-time v&agrave; internship tại c&aacute;c c&ocirc;ng ty tại Anh v&agrave; Việt Nam như PepsiCo International Việt Nam, Unilever Foodsolutions Việt Nam. Hiện Thy đang l&agrave;m việc cho PricewaterhouseCoopers.</p>\r\n<p class="Normal">H&agrave; Nội: 18h30 đến 20h ng&agrave;y 11/1 tại ph&ograve;ng học 9 v&agrave; 10 số 20 Thụy Khu&ecirc;. Li&ecirc;n hệ: điện thoại 04 3843 6780 hoặc E-mail:&nbsp;<a class="Normal" href="mailto:educationuk@britishcouncil.org.vn">educationuk@britishcouncil.org.vn</a>.</p>\r\n<p class="Normal">TP HCM: 18h30 đến 20h ng&agrave;y 11/1 tại kh&aacute;ch sạn Norfolk, 117 L&ecirc; Th&aacute;nh T&ocirc;n, quận 1. Li&ecirc;n hệ: điện thoại 08 3823 2862 hoặc E-mail:<a class="Normal" href="mailto:educationuk.hcmc@britishcouncil.org.vn">educationuk.hcmc@britishcouncil.org.vn</a></p>\r\n<p><strong>&nbsp;</strong></p>\r\n<p class="Normal">Chương tr&igrave;nh được t&agrave;i trợ bởi đại học Central Lancashire, East Anglia v&agrave; West of England.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Hội đồng Anh</em>)</p>', 4, 1, 506, 0, 1),
(143, 'Chương trình đào tạo mới của MDIS, Singapore', 'Tiến sĩ Eric Kuan, hiệu trưởng MDIS sẽ trực tiếp giới thiệu về các chương trình đào tạo mới, ký túc xá mới và các chính sách học bổng khuyến học dành cho sinh viên quốc tế trong hai ngày 15 và 16 tại Hà Nội và TP HCM.', 'MDIS (1).jpg', NULL, '<h1 class="Title">Chương tr&igrave;nh đ&agrave;o tạo mới của MDIS, Singapore</h1>\r\n<h2 class="Lead">Tiến sĩ Eric Kuan, hiệu trưởng MDIS sẽ trực tiếp giới thiệu về c&aacute;c chương tr&igrave;nh đ&agrave;o tạo mới, k&yacute; t&uacute;c x&aacute; mới v&agrave; c&aacute;c ch&iacute;nh s&aacute;ch học bổng khuyến học d&agrave;nh cho sinh vi&ecirc;n quốc tế trong hai ng&agrave;y 15 v&agrave; 16 tại H&agrave; Nội v&agrave; TP HCM.</h2>\r\n<p class="Normal">Năm 2011, MDIS khai giảng c&aacute;c kh&oacute;a học mới bao gồm thạc sĩ quản l&yacute; y tế (một trong số c&aacute;c ng&agrave;nh mũi nhọn của Singapore) v&agrave; c&aacute;c chương tr&igrave;nh cử nh&acirc;n: quản l&yacute; hội nghị v&agrave; sự kiện, quản l&yacute; kh&aacute;ch sạn v&agrave; resort, quản trị du lịch v&agrave; kh&aacute;ch sạn ứng dụng, quản l&yacute; c&ocirc;ng nghiệp. Đ&acirc;y l&agrave; những ng&agrave;nh đang được tập trung ph&aacute;t triển mạnh tại Singapore v&agrave; c&aacute;c nước đang ph&aacute;t triển.</p>\r\n<h3 class="SubTitle">Hội thảo giới thiệu về MDIS được tổ chức theo lịch sau:</h3>\r\n<p class="Normal">TP HCM: 14h đến 17h ng&agrave;y 15/1 tại kh&aacute;ch sạn Sheraton 88 Đồng Khởi, quận 1.&nbsp;<br />Li&ecirc;n hệ: C&ocirc;ng ty tư vấn du học ASCI. 18/24, đường Vườn Chuối, phường 4, quận 3. Tel: 08 6 290 8521/ 0983 010 580.</p>\r\n<p class="Normal">H&agrave; Nội: 9h đến 12h ng&agrave;y 16/1 tại kh&aacute;ch sạn Daewoo, 360 Kim M&atilde;.<br />Li&ecirc;n hệ: SMT - Trung t&acirc;m đ&agrave;o tạo ủy quyền của MDIS tại H&agrave; Nội. 26 Nguyễn Thị Định, Trung H&ograve;a, Cầu Giấy. Tel: 04 3 5561 888.</p>\r\n<p class="Normal">Tham khảo c&aacute;c kh&oacute;a học tại MDIS:&nbsp;<a class="Normal" href="http://www.mdis.vn/">http://www.mdis.vn</a>.</p>\r\n<p class="Normal">MDIS được th&agrave;nh lập năm 1956 l&agrave; học viện phi lợi nhuận l&acirc;u đời tại Singapore. Trường nằm trong 10 trường tư thục Singapore 2010 (theo Edupoll). Ngo&agrave;i ra, MDIS c&ograve;n c&oacute; c&aacute;c chứng chỉ chất lượng SQC, ISO9001, OHSAS 18001. Mới đ&acirc;y trường đạt th&ecirc;m ti&ecirc;u chuẩn Edutrust 4 năm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="MDIS.jpg" border="1" alt="" width="400" height="305" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&aacute;c chương tr&igrave;nh đ&agrave;o tạo của MDIS được sự hỗ trợ của 5 trung t&acirc;m thực h&agrave;nh chuy&ecirc;n m&ocirc;n được trang bị hiện đại, sự cố vấn của c&aacute;c chuy&ecirc;n gia trong từng ng&agrave;nh tương ứng v&agrave; li&ecirc;n kết với c&aacute;c tập đo&agrave;n kinh tế, y tế, du lịch&hellip; Chương tr&igrave;nh đ&agrave;o tạo với mục đ&iacute;ch gi&uacute;p sinh vi&ecirc;n được tham quan, thực h&agrave;nh v&agrave; học hỏi kiến thức thực tế tạo nền tảng kiến thức, kỹ năng v&agrave; phương ph&aacute;p tư duy hiện đại cho sinh vi&ecirc;n.</p>\r\n<p class="Normal">MDIS đ&atilde; đầu tư v&agrave; ho&agrave;n th&agrave;nh k&yacute; t&uacute;c x&aacute; t&iacute;ch hợp hiện đại nằm trong khu&ocirc;n vi&ecirc;n của trường với sức chứa 1.700 sinh vi&ecirc;n. K&yacute; t&uacute;c x&aacute; sẽ đi v&agrave;o hoạt động từ th&aacute;ng 2 tới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Văn ph&ograve;ng đại diện MDIS tại Việt Nam</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tầng 9, t&ograve;a nh&agrave; VIT, 519 Kim M&atilde;, H&agrave; Nội.</p>\r\n<p class="Normal">Tel: 04 2220 9292; Fax: 04 2220 9293; Email:&nbsp;<a class="Normal" href="mailto:mdis@mdis.vn">mdis@mdis.vn</a></p>\r\n<p class="Normal">YM: mdisvn_tuvanonline; Skype: mdisvn_tuvanonline</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>MDIS</em>)</p>', 4, 1, 516, 0, 1),
(144, 'Học bổng 2011 tại học viện ERC Singapore', 'Sinh viên có điểm trung bình toàn khoá học tại ERC đạt 7,5 trở lên được nhận học bổng lên đến 70% học phí cho khoá học cử nhân (trường hoàn trả 10.000 SGD) và thạc sĩ (trường hoàn trả 6.500 SGD).', 'UK.jpg', NULL, '<h1 class="Title">Học bổng 2011 tại học viện ERC Singapore</h1>\r\n<h2 class="Lead">Sinh vi&ecirc;n c&oacute; điểm trung b&igrave;nh to&agrave;n kho&aacute; học tại ERC đạt 7,5 trở l&ecirc;n được nhận học bổng l&ecirc;n đến 70% học ph&iacute; cho kho&aacute; học cử nh&acirc;n (trường ho&agrave;n trả 10.000 SGD) v&agrave; thạc sĩ (trường ho&agrave;n trả 6.500 SGD).</h2>\r\n<p class="Normal">Ngo&agrave;i ra, ERC c&ograve;n c&oacute; học bổng kh&oacute;a ch&iacute;nh trị gi&aacute; 1.000 SGD cho chương tr&igrave;nh cao đẳng, cao đẳng n&acirc;ng cao, năm cuối cử nh&acirc;n v&agrave; thạc sĩ.</p>\r\n<p class="Normal">ERC l&agrave; một trong những trường đầu ti&ecirc;n nhận được chứng nhận Edutrust 4 năm v&agrave; l&agrave; một trong những trường tư h&agrave;ng đầu của Singapore. Trường được s&aacute;ng lập bởi những nh&agrave; quản trị doanh nghiệp h&agrave;ng đầu ch&acirc;u &Aacute; với mục ti&ecirc;u đ&agrave;o tạo n&ecirc;n những thế hệ doanh nh&acirc;n th&agrave;nh đạt.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="UK.jpg" border="1" alt="" width="400" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">ERC đ&atilde; đầu tư hơn 50 triệu USD để ho&agrave;n thiện cơ sở đ&agrave;o tạo mới thứ hai đặt tại trung t&acirc;m Singapore, đối diện Thư viện quốc gia Singapore.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">ERC sẽ hỗ trợ vốn đầu tư cho những dự &aacute;n kinh doanh khả thi của học vi&ecirc;n với gi&aacute; trị l&ecirc;n đến 100.000 SGD. ERC bố tr&iacute; l&agrave;m việc tại Singapore với mức lương tr&ecirc;n 1.700 SGD một th&aacute;ng cho học sinh tốt nghiệp với điểm trung b&igrave;nh mỗi m&ocirc;n học 7,5 trở l&ecirc;n.</p>\r\n<p class="Normal">Học sinh c&oacute; cơ hội nhận 2 bằng thạc sĩ chỉ trong một năm 4 th&aacute;ng. Trường kh&ocirc;ng y&ecirc;u cầu chứng chỉ IELTS hoặc TOEFL (chỉ cần vượt qua b&agrave;i test của trường). Sinh vi&ecirc;n tốt nghiệp tại ERC được nhận bằng cử nh&acirc;n v&agrave; thạc sĩ của ĐH Greenwich, Anh.</p>\r\n<h3 class="SubTitle">Chương tr&igrave;nh đ&agrave;o tạo tại học viện ERC:</h3>\r\n<p class="Normal">Anh ngữ: 4 cấp độ, thời gian học 8 th&aacute;ng.</p>\r\n<p class="Normal">O Level: Dành cho học sinh hoàn t&acirc;́t lớp 9, thời gian học 8 th&aacute;ng.</p>\r\n<p class="Normal">Dự bị đại học: Dành cho học sinh hoàn t&acirc;́t lớp 10 hoặc 11, thời gian học 4 th&aacute;ng.</p>\r\n<p class="Normal">Cao đẳng: Đào tạo các chuy&ecirc;n ngành: Quản l&yacute; DL v&agrave; kh&aacute;ch sạn, ph&aacute;t triển doanh nghiệp, quản trị kinh doanh. Thời gian học 9 th&aacute;ng.</p>\r\n<p class="Normal">Cao đẳng n&acirc;ng cao: chuy&ecirc;n ng&agrave;nh quản trị kinh doanh, thời gian học 9 th&aacute;ng.</p>\r\n<p class="Normal">Đại học: đ&agrave;o tạo c&aacute;c chuy&ecirc;n ng&agrave;nh quản trị kinh doanh, quản l&yacute; t&agrave;i sản, t&agrave;i ch&iacute;nh, quản l&yacute; du lịch v&agrave; kh&aacute;ch sạn. Học sinh ho&agrave;n tất chương tr&igrave;nh sẽ được đại học Greenwich, Anh cấp bằng.</p>\r\n<p class="Normal">Thạc sĩ: đ&agrave;o tạo c&aacute;c chuy&ecirc;n ng&agrave;nh kinh doanh quốc tế, điều h&agrave;nh Logistics, quản trị du lịch kh&aacute;ch sạn, dịch vụ t&agrave;i ch&iacute;nh. Học sinh ho&agrave;n tất chương tr&igrave;nh sẽ được đại học Greenwich, Anh cấp bằng.</p>\r\n<p class="Normal">C&ocirc;ng ty Đức Anh A&amp;T hỗ trợ 200 USD cho học sinh k&yacute; hợp đồng với c&ocirc;ng ty trong th&aacute;ng 1/2010. Ngo&agrave;i ra, c&ocirc;ng ty cũng tư vấn chọn ng&agrave;nh học, trường học v&agrave; xin thư mời học v&agrave; trong suốt qu&aacute; tr&igrave;nh học. Hỗ trợ 10% học ph&iacute; tại Trung t&acirc;m Anh ngữ Đức Anh &ndash; Dace (Trung t&acirc;m cao cấp chuy&ecirc;n đ&agrave;o tạo tiếng Anh học thuật v&agrave; luyện IELTS bởi c&aacute;c gi&aacute;o vi&ecirc;n nước ngo&agrave;i v&agrave; chuy&ecirc;n gia Việt Nam).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: C&ocirc;ng ty tư vấn du học, gi&aacute;o dục v&agrave; dịch thuật quốc tế Đức Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 96 L&ograve; Đ&uacute;c, ĐT: 04 3971 6229 - 04 3972 0889 -&nbsp;<br />Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:duhoc@ducanh.edu.vn">duhoc@ducanh.edu.vn</a></span></p>\r\n<p class="Normal">TP HCM: 351 V&otilde; Văn Tần, phường 5, quận 3.<br />ĐT: (08) 3929 3995 - Fax: (08) 3929 3996, Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:adminhcm@ducanh.edu.vn">adminhcm@ducanh.edu.vn</a></span></p>\r\n<p class="Normal">Hải Ph&ograve;ng: 32F Điện Bi&ecirc;n Phủ,&nbsp;<br />ĐT: 0313 686 689 - Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:adminhp@ducanh.edu.vn">adminhp@ducanh.edu.vn</a>.</span></p>\r\n<p class="Normal">Trung t&acirc;m Anh ngữ Dace: 8D H&agrave;m Long, H&agrave; Nội. ĐT: 04 3944 8589<br />Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:Dacehn@ducanh.edu.vn">Dacehn@ducanh.edu.vn</a>.</span></p>\r\n<a class="Normal" href="http://www.ducanhduhoc.com/">http://www.ducanhduhoc.com/</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Đức Anh)</em></p>', 4, 1, 2870, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(145, 'Chương trình du học hè của ILA 2011', 'ILA giới thiệu 10 chương trình du học hè dành cho học sinh từ 8 đến 18 tuổi tại các nước Anh, Mỹ, Canada, Australia và Singapore.', 'H1_400.jpg', NULL, '<h1 class="Title">Chương tr&igrave;nh du học h&egrave; của ILA 2011</h1>\r\n<h2 class="Lead">ILA giới thiệu 10 chương tr&igrave;nh du học h&egrave; d&agrave;nh cho học sinh từ 8 đến 18 tuổi tại c&aacute;c nước Anh, Mỹ, Canada, Australia v&agrave; Singapore.</h2>\r\n<p class="normal">Tại Anh (London - Manchester 2 tuần): d&agrave;nh cho học sinh đam m&ecirc; b&oacute;ng đ&aacute; v&agrave; muốn được học b&oacute;ng đ&aacute; dưới dự dẫn dắt của Manchescher United Soccer School. C&oacute; cơ hội được mời tham dự V&ograve;ng chung kết kỹ năng thế giới tổ chức tại S&acirc;n Old Trafford v&agrave;o th&aacute;ng 10/2011.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="H1_400.jpg" border="1" alt="" width="400" height="300" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại Mỹ (New York or San Fransisco 2 tuần): d&agrave;nh cho học sinh muốn trau dồi tiếng Anh 24/24 v&agrave; c&oacute; kế hoạch du học d&agrave;i hạn. Học sinh sẽ giao tiếp với gi&aacute;o vi&ecirc;n, sinh vi&ecirc;n quốc tế v&agrave; bản địa bằng tiếng Anh 24/24 trong suốt kh&oacute;a học</p>\r\n<p class="Normal">Tại Singapore: nhiều lựa chọn: một tuần, 2 tuần, 4 tuần. Một tuần d&agrave;nh cho học sinh nhỏ tuổi lần đầu l&agrave;m quen với m&ocirc;i trường du học</p>\r\n<p class="normal">Tham gia chương tr&igrave;nh du học h&egrave; của ILA, học sinh đảm bảo được học ở những trường uy t&iacute;n, ăn, ở tiện nghi v&agrave; an to&agrave;n. Trưởng đo&agrave;n ILA lu&ocirc;n c&oacute; mặt 24/24 để chăm s&oacute;c v&agrave; hỗ trợ c&aacute;c em.</p>\r\n<p class="normal">C&ugrave;ng với những hoạt động vui chơi, tham quan những địa danh nổi tiếng của từng quốc gia, học sinh sẽ tham gia những lớp học Anh ngữ tại c&aacute;c trường chất lượng, với đội ngũ gi&aacute;o vi&ecirc;n bản ngữ đầy kinh nghiệm.</p>\r\n<p class="normal">Những b&agrave;i học vui nhộn, sinh động, thực tiễn, sẽ gi&uacute;p n&acirc;ng cao kỹ năng nghe, n&oacute;i, đọc, viết của học sinh.C&aacute;c hoạt động hấp dẫn như diễn kịch, thuyết tr&igrave;nh, hội thảo nh&oacute;m c&ugrave;ng bạn b&egrave; đến từ khắp nơi tr&ecirc;n thế giới sẽ gi&uacute;p học sinh tự tin diễn đạt suy nghĩ v&agrave; quan điểm bằng tiếng Anh.</p>\r\n<div class="normal">\r\n<p class="normal">&nbsp;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="225" align="right" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td class="Normal">Trung t&acirc;m tư vấn Du học ILA tặng ngay 3,9 triệu đồng cho c&aacute;c bạn đăng k&yacute; trước ng&agrave;y 30/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\nHoạt động ngoại kh&oacute;a được thiết kế một c&aacute;ch ho&agrave;n hảo gi&uacute;p c&aacute;c em ứng dụng ngay những kiến thức vừa học v&agrave;o m&ocirc;i trường thực tế. Học sinh sẽ nhận được chứng nhận ho&agrave;n tất kh&oacute;a học h&egrave; c&ugrave;ng những m&oacute;n qu&agrave; lưu niệm &yacute; nghĩa từ trường ở nước ngo&agrave;i.\r\n<p>&nbsp;</p>\r\n</div>\r\n<p class="normal">C&aacute;c kh&oacute;a du học h&egrave; do ILA Vietnam phối hợp với c&aacute;c trường thuộc c&aacute;c tổ chức gi&aacute;o dục quốc tế c&oacute; uy t&iacute;n như Study Group, SMG, CCI&hellip; sẽ khởi h&agrave;nh v&agrave;o th&aacute;ng 6 v&agrave; 7. Học sinh c&oacute; nhiều lựa chọn phong ph&uacute; từ c&aacute;c chương tr&igrave;nh từ 1 đến 4 tuần.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" align="center" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: ILA</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">TPHCM: 08-3929 0100; H&agrave; Nội: 04-3936 3334; Đ&agrave; Nẵng: 0511- 3647 444.</p>\r\n<p class="Normal">Vũng T&agrave;u: Tư vấn trực tiếp l&uacute;c 9h30 ng&agrave;y 9/1. Đăng k&yacute;: c&ocirc; Tuyền 0938 445 442, c&ocirc; T&uacute; 0982 324 064.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:<em>&nbsp;ILA</em>)</p>', 4, 1, 1339, 0, 1),
(146, 'Triển lãm du học Anh 2011', 'Đại diện 20 trường cao đẳng, đại học và các tổ chức giáo dục đến từ Anh sẽ tham gia triển lãm du học bậc sau phổ thông và dự bị đại học do Hội đồng Anh tổ chức ngày 15/1 tại Hà Nội và ngày 16/1 tại TP HCM.', 'anh-2.jpg', NULL, '<h1 class="Title">Triển l&atilde;m du học Anh 2011</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="anh-2.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Đại diện 20 trường cao đẳng, đại học v&agrave; c&aacute;c tổ chức gi&aacute;o dục đến từ Anh sẽ tham gia triển l&atilde;m du học bậc sau phổ th&ocirc;ng v&agrave; dự bị đại học do Hội đồng Anh tổ chức ng&agrave;y 15/1 tại H&agrave; Nội v&agrave; ng&agrave;y 16/1 tại TP HCM.</h2>\r\n<p class="Normal">Chương tr&igrave;nh tập trung giới thiệu c&aacute;c kho&aacute; học bậc sau phổ th&ocirc;ng v&agrave; dự bị đại học của Anh. Đ&acirc;y l&agrave; cầu nối chuyển tiếp l&ecirc;n bậc đại học. Đặc biệt, chương tr&igrave;nh giới thiệu c&aacute;c kho&aacute; học hướng nghiệp, học nghề li&ecirc;n th&ocirc;ng l&ecirc;n đại học trong nhiều lĩnh vực kh&aacute;c nhau như thời trang, thiết kế đồ hoạ, c&ocirc;ng nghệ th&ocirc;ng tin, kế to&aacute;n, ng&acirc;n h&agrave;ng, kinh doanh, kh&aacute;ch sạn... Chương tr&igrave;nh được cung cấp bởi nhiều loại h&igrave;nh trường học như: trường phổ th&ocirc;ng nội tr&uacute;, trường cao đẳng c&ocirc;ng lập, trường cao đẳng tư thục, đại học&hellip; C&aacute;c b&agrave;i kiểm tra v&agrave; phỏng vấn trực tiếp để trao học bổng cũng sẽ được c&aacute;c trường thực hiện tại triển l&atilde;m.</p>\r\n<div class="Normal">\r\n<p class="Normal">&nbsp;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="259" align="right" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class="Normal">Thời gian cụ thể diễn ra triển l&atilde;m:</p>\r\n<p class="Normal">H&agrave; Nội: 14h &ndash; 18h ng&agrave;y 15/1 tại kh&aacute;ch sạn Horison, 40 C&aacute;t Linh</p>\r\n<p class="Normal">TP HCM: 14h &ndash; 18h ng&agrave;y 16/1 tại kh&aacute;ch sạn Duxton, 63 Nguyễn Huệ, quận 1</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\nHọc sinh v&agrave; phụ huynh sẽ c&oacute; cơ hội trực tiếp tr&ograve; chuyện, t&igrave;m hiểu th&ocirc;ng tin v&agrave; kinh nghiệm thực tế từ c&aacute;c bạn cựu du học sinh Anh xuất sắc về học tập v&agrave; sinh hoạt tại Anh.\r\n<p>&nbsp;</p>\r\n</div>\r\n<p class="Normal">Đến với triển l&atilde;m, c&aacute;c học sinh c&ograve;n c&oacute; cơ hội tham gia lớp học mẫu về kỹ năng học tập do gi&aacute;o vi&ecirc;n một trường đại học của Anh trực tiếp đứng lớp.</p>\r\n<p class="Normal">Tham khảo web&nbsp;<a class="Normal" href="http://www.educationuk.org/vietnam">www.educationuk.org/vietnam</a>.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:<em>&nbsp;Hội đồng Anh</em>)</p>', 4, 1, 426, 0, 1),
(148, 'Luật visa mới cho cao đẳng chuyển tiếp tại Australia', 'Từ ngày 1/1, du học sinh Việt Nam học các chương trình cao đẳng liên thông vào đại học của Navitas sẽ được hưởng mức xét cấp visa 2 (như đối với du học sinh vào thẳng đại học tại Australia).', '400x180.jpg', NULL, '<h1 class="Title">Luật visa mới cho cao đẳng chuyển tiếp tại Australia</h1>\r\n<h2 class="Lead">Từ ng&agrave;y 1/1, du học sinh Việt Nam học c&aacute;c chương tr&igrave;nh cao đẳng li&ecirc;n th&ocirc;ng v&agrave;o đại học của Navitas sẽ được hưởng mức x&eacute;t cấp visa 2 (như đối với du học sinh v&agrave;o thẳng đại học tại Australia).</h2>\r\n<p class="Normal">IDP Education c&ugrave;ng Navitas tổ chức hội thảo &ldquo;V&agrave;o đại học từ Navitas&rdquo; để t&igrave;m hiểu c&aacute;c cơ hội học tập v&agrave; th&ocirc;ng tin mới nhất về luật visa &aacute;p dụng cho c&aacute;c chương tr&igrave;nh đ&agrave;o tạo n&agrave;y. Đặc biệt, c&aacute;c chương tr&igrave;nh đ&agrave;o tạo li&ecirc;n th&ocirc;ng n&agrave;y kh&ocirc;ng y&ecirc;u cầu điểm IELTS. Hội thảo được tổ chức từ 17h30 đến 19h30 ng&agrave;y 7/1/2011 tại IDP Education 33 Mạc Đĩnh Chi, quận 1, TP HCM. Ngo&agrave;i ra, hội thảo c&ograve;n l&agrave; cơ hội để t&igrave;m hiểu th&ocirc;ng tin về c&aacute;c chương tr&igrave;nh học bổng c&oacute; gi&aacute; trị từ 35 triệu đồng đến 230 triệu đồng của c&aacute;c trường thuộc Navitas.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="400x180.jpg" border="1" alt="" width="400" height="180" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Navitas l&agrave; tổ chức gi&aacute;o dục c&oacute; mối li&ecirc;n kết chặt chẽ với nhiều trường cao đẳng, đại học tr&ecirc;n to&agrave;n cầu chuy&ecirc;n đ&agrave;o tạo c&aacute;c chương tr&igrave;nh trung học, dự bị đại học, cao đẳng, đại học v&agrave; sau đại học. Tại Australia, Navitas li&ecirc;n kết với c&aacute;c trường đại học như Adelaide, Curtin, Deakin, Edith Cowan, Flinders, Griffith, La Trobe Sydney, Macquarie, South Australia bảo đảm chất lượng đ&agrave;o tạo chuẩn quốc gia Australia v&agrave; được c&ocirc;ng nhận tr&ecirc;n thế giới.<br /><br />Th&ocirc;ng tin chi tiết v&agrave; đăng k&yacute; tham dự xin li&ecirc;n hệ điện thoại: 08 2249 0000 - 08 3835 0133 hoặc truy cập&nbsp;<a class="Normal" href="http://www.idp.com.vn/">http://www.idp.com.vn</a>&nbsp;v&agrave;&nbsp;<a class="Normal" href="http://www.vietnam.idp.com/">http://www.vietnam.idp.com</a>.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>IDP Education)</em></p>', 4, 1, 2588, 0, 1),
(149, 'Thi và phỏng vấn học bổng 100% học phí tại Phần Lan', 'Các ĐH công lập Phần Lan tuyển sinh các khóa ĐH khai giảng tháng 9/2011. Sinh viên nộp hồ sơ trước ngày 15/2/2011 để đăng ký dự thi đầu vào tổ chức vào tháng 4. Sinh viên thi đỗ sẽ nhận được học bổng 100% học phí.', 'phan-lan-1.jpg', NULL, '<h1 class="Title">Thi v&agrave; phỏng vấn học bổng 100% học ph&iacute; tại Phần Lan</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="phan-lan-1.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">C&aacute;c ĐH c&ocirc;ng lập Phần Lan tuyển sinh c&aacute;c kh&oacute;a ĐH khai giảng th&aacute;ng 9/2011. Sinh vi&ecirc;n nộp hồ sơ trước ng&agrave;y 15/2/2011 để đăng k&yacute; dự thi đầu v&agrave;o tổ chức v&agrave;o th&aacute;ng 4. Sinh vi&ecirc;n thi đỗ sẽ nhận được học bổng 100% học ph&iacute;.</h2>\r\n<p class="Normal">Phần Lan l&agrave; một trong c&aacute;c quốc gia c&oacute; nền gi&aacute;o dục tốt tr&ecirc;n thế giới. C&aacute;c trường đại học của Phần Lan đều giảng dạy theo hệ thống t&iacute;n chỉ ch&acirc;u &Acirc;u (ECTS), bằng cấp được quốc tế c&ocirc;ng nhận v&agrave; chương tr&igrave;nh học giảng dạy ho&agrave;n to&agrave;n bằng tiếng Anh. M&ocirc;i trường sống thanh b&igrave;nh, điều kiện ăn ở tốt, chi ph&iacute; sinh hoạt 400-500 EUR một th&aacute;ng.</p>\r\n<p class="Normal">Sinh vi&ecirc;n được ph&eacute;p l&agrave;m th&ecirc;m 20 tiếng mỗi tuần v&agrave; tất cả c&aacute;c kỳ nghỉ lễ, tết trong năm. Lương l&agrave;m th&ecirc;m từ 8 đến 15 EUR một tiếng. Sau khi tốt nghiệp sinh vi&ecirc;n được ph&eacute;p ở lại một năm để l&agrave;m việc.</p>\r\n<p class="Normal">Hằng năm, Visco gửi khoảng 20 sinh vi&ecirc;n sinh đến học tập tại c&aacute;c trường đại học như Haaga &ndash; Helia, Hamk, Laurea, Turku, Lahti, Metropolia, Savonia, Vaasa, Central Ostrobothnia, Kemi Tornio, Jyvaskyla, Kajaania, Rovaniemi&hellip; với c&aacute;c chuy&ecirc;n ng&agrave;nh cử nh&acirc;n: kinh doanh quốc tế, quản trị du lịch kh&aacute;ch sạn, quản trị hệ thống th&ocirc;ng tin, quản l&yacute; kinh doanh, tự động h&oacute;a, c&ocirc;ng nghệ th&ocirc;ng tin, marketing, cơ kh&iacute; v&agrave; c&ocirc;ng nghệ sản xuất, quản trị cơ sở vật chất, m&ocirc;i trường. Hoặc chương tr&igrave;nh thạc sĩ: quản l&yacute; kinh doanh quốc tế (1,5 năm). Học ph&iacute; 12.000 EUR cho cả kh&oacute;a học.</p>\r\n<p class="Normal">Chương tr&igrave;nh tuyển sinh đại học d&agrave;nh cho học sinh học lớp 12 trở l&ecirc;n. IELTS 5,5 - 6/ TOEFL IBT 79 - 80. Hạn nộp hồ sơ: 15/2/2011. Nếu học sinh nộp điểm TOEFL IBT phải trước ng&agrave;y 20/1/2011. Đối với chương tr&igrave;nh thạc sĩ, sinh vi&ecirc;n cần tốt nghiệp đại học chuy&ecirc;n ng&agrave;nh kinh tế, IELTS 60 /TOEFL 575. Hạn nộp hồ sơ 19/2/2011.</p>\r\n<p class="Normal">Tất cả sinh vi&ecirc;n sẽ tham dự kỳ thi đầu v&agrave;o được tổ chức th&aacute;ng 4/2011 tại văn ph&ograve;ng của Visco. C&ocirc;ng ty sẽ tư vấn chọn trường, chọn ng&agrave;nh theo 4 nguyện vọng. Sinh vi&ecirc;n được hướng dẫn nội dung thi, &ocirc;n luyện v&agrave; thi thử theo c&aacute;c đề thi của c&aacute;c năm trước, hướng dẫn, luyện phỏng vấn của trường v&agrave; của Đại sứ qu&aacute;n, hướng dẫn thủ tục xin Visa, sắp xếp nh&agrave; ở. Ngo&agrave;i ra, Visco tổ chức thi thử cho học sinh v&agrave;o s&aacute;ng thứ 7 hằng tuần.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><span style="color: #800000;"><strong>Li&ecirc;n hệ: Văn ph&ograve;ng Visco</strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 230 Kim M&atilde;, Ba Đ&igrave;nh; ĐT: 04.3726 1938; Fax: 04.3726 1936 - E-mail:<a class="Normal" href="mailto:viscohanoi@visco.edu.vn">viscohanoi@visco.edu.vn</a></p>\r\n<p class="Normal">TP HCM: 239 CMT 8, nh&agrave; A02 Chung cư Văn h&oacute;a, phường 4, quận 3 - ĐT: 08 3832 8416; Fax: 08 3832 8417; E-mail:&nbsp;<a class="Normal" href="mailto:viscohcm@visco.edu.vn">viscohcm@visco.edu.vn</a></p>\r\n<p class="Normal">Đ&agrave; Nẵng: 433 Phan Chu Trinh, Hải Ch&acirc;u - ĐT: 0511 3552597/96; Fax : 0511 355 2596; E-mail:&nbsp;<a class="Normal" href="mailto:viscodn@visco.edu.vn">viscodn@visco.edu.vn</a></p>\r\n<p class="Normal">Hải Ph&ograve;ng: 309 Hai B&agrave; Trưng, C&aacute;t D&agrave;i, L&ecirc; Ch&acirc;n - ĐT: 031 3950215/3950748; Fax : 0313 950215; Email:&nbsp;<a class="Normal" href="mailto:viscohp@visco.edu.vn">viscohp@visco.edu.vn</a>.</p>\r\n<p class="Normal">Website:&nbsp;<a class="Normal" href="http://www.visco.edu.vn/">http://www.visco.edu.vn/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Visco)</em></p>', 4, 1, 2331, 0, 1),
(150, 'Cơ hội học tập và định cư tại bang Manitoba, Canada', 'Chi phí cho du học sinh học tại Manitoba không cao. Bên cạnh đó, sinh viên được đăng ký xin định cư ngay sau khi tốt nghiệp cao đẳng, đại học, kể cả khi chưa tìm được việc làm.', 'duhoc-2.jpg', NULL, '<h1 class="Title">Cơ hội học tập v&agrave; định cư tại bang Manitoba, Canada</h1>\r\n<h2 class="Lead">Chi ph&iacute; cho du học sinh học tại Manitoba kh&ocirc;ng cao. B&ecirc;n cạnh đ&oacute;, sinh vi&ecirc;n được đăng k&yacute; xin định cư ngay sau khi tốt nghiệp cao đẳng, đại học, kể cả khi chưa t&igrave;m được việc l&agrave;m.</h2>\r\n<p class="Normal">Canada l&agrave; một trong số c&aacute;c quốc gia G8 c&oacute; mức học ph&iacute; thấp v&agrave; l&agrave; một trong số quốc gia an to&agrave;n, th&acirc;n thiện tr&ecirc;n thế giới. Đ&acirc;y l&agrave; quốc gia song ngữ, n&ecirc;n bạn c&oacute; thể học th&agrave;nh thạo tiếng Anh v&agrave; Ph&aacute;p. Tại đ&acirc;y c&oacute; hơn 390 cơ sở gi&aacute;o dục, bằng cấp được quốc tế c&ocirc;ng nhận. Sinh vi&ecirc;n được ph&eacute;p l&agrave;m th&ecirc;m 20 tiếng một tuần trong khi đang học v&agrave; 48 tiếng một tuần khi nghỉ h&egrave; (theo luật visa sinh vi&ecirc;n). Ngo&agrave;i ra, sinh vi&ecirc;n được ở lại l&agrave;m việc 1-3 năm sau khi tốt nghiệp chuy&ecirc;n ng&agrave;nh, cơ hội l&agrave;m việc l&acirc;u d&agrave;i v&agrave; định cư v&agrave; cơ hội được kh&aacute;m ph&aacute;, ph&aacute;t triển bản th&acirc;n.</p>\r\n<p class="Normal">C&aacute;c chương tr&igrave;nh học tại đ&acirc;y gồm c&oacute;: Tiếng Anh; phổ th&ocirc;ng trung học; cao đẳng; đại học v&agrave; sau đại học. Khi học tập ở Canada, sinh vi&ecirc;n sẽ c&oacute; cơ hội để t&iacute;ch lũy kinh nghi&ecirc;m l&agrave;m việc. C&aacute;c trường đại học ở Canada thường hợp t&aacute;c với c&aacute;c c&ocirc;ng ty v&agrave; c&aacute;c tổ chức, tạo điều kiện cho sinh vi&ecirc;n l&agrave;m việc thực tế trước khi tốt nghiệp. Năm học ở trường đại học thường bắt đầu từ th&aacute;ng 9 đến th&aacute;ng 5. Một số trường theo hệ thống hai hoặc ba học kỳ c&ugrave;ng với c&aacute;c kho&aacute; học sẵn c&oacute; kể cả trong m&ugrave;a h&egrave;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duhoc-2.jpg" border="1" alt="" width="450" height="320" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chi ph&iacute; học tại Manitoba kh&ocirc;ng cao. Sinh vi&ecirc;n c&ograve;n được đăng k&yacute; xin định cư ngay sau khi tốt nghiệp cao đẳng, đại học, kể cả khi chưa t&igrave;m được việc l&agrave;m. Trước đ&acirc;y, c&aacute;c sinh vi&ecirc;n quốc tế chỉ được đăng k&yacute; xin định cư sau khi đ&atilde; c&oacute; việc l&agrave;m được &iacute;t nhất l&agrave; s&aacute;u th&aacute;ng tại bang Manitoba. Tuy nhi&ecirc;n, theo ch&iacute;nh s&aacute;ch mới, từ năm 2011, c&aacute;c sinh vi&ecirc;n quốc tế học v&agrave; tốt nghiệp c&aacute;c chương tr&igrave;nh cao đẳng, đại học dạng th&ocirc;ng thường (hai năm cao đẳng, bốn năm đại học, 1-2 năm thạc sĩ, 3-5 năm nghi&ecirc;n cứu sinh) hoặc "fast track" (li&ecirc;n th&ocirc;ng v&agrave; cắt giảm thời gian học) l&ecirc;n đại học đều c&oacute; c&ugrave;ng quyền lợi tr&ecirc;n. Sinh vi&ecirc;n sẽ được tham gia v&agrave;o chương tr&igrave;nh đ&agrave;o tạo theo định hướng nghề nghiệp c&oacute; sẵn, bao gồm: c&oacute; sẵn việc l&agrave;m cho sinh vi&ecirc;n, tư vấn v&agrave; chỉ dẫn về ng&agrave;nh nghề - c&ocirc;ng việc trong dự định v&agrave; hưởng c&aacute;c dịch vụ bố tr&iacute; c&ocirc;ng việc ph&ugrave; hợp.</p>\r\n<p class="Normal">Học ph&iacute; cao đẳng hay đại học m&agrave; bạn đ&atilde; trả cho trường sẽ được ho&agrave;n lại dần qua c&aacute;c năm bạn sống v&agrave; l&agrave;m việc c&oacute; trả thuế tại Manitoba, theo c&aacute;c quy định cụ thể. Mức ho&agrave;n học ph&iacute; tối đa l&ecirc;n đến 60% tổng ph&iacute; m&agrave; sinh vi&ecirc;n đ&atilde; nộp cho trường theo học. Ngo&agrave;i ra, việc ho&agrave;n ph&iacute; n&agrave;y kh&ocirc;ng ảnh hưởng đến c&aacute;c quyền lợi kh&aacute;c của bạn khi sống v&agrave; l&agrave;m việc tại Manitoba.</p>\r\n<p class="Normal">Đức Anh A&amp;T l&agrave; một c&ocirc;ng ty tư vấn c&oacute; uy t&iacute;n v&agrave; l&acirc;u năm, cam kết tư vấn v&agrave; hỗ trợ cho c&aacute;c du học sinh đạt được nguyện vọng du học Canada. Tỷ lệ visa được cấp của c&aacute;c học sinh tại đ&acirc;y đạt đến 94%.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: C&ocirc;ng ty Tư vấn - Gi&aacute;o dục v&agrave; Dịch thuật Quốc tế Đức Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">96 L&ograve; Đ&uacute;c, H&agrave; Nội. ĐT: (04) 3971 6229 - 3972 0889, Email:<a class="Normal" href="mailto:duhoc@ducanh.edu.vn">duhoc@ducanh.edu.vn</a></p>\r\n<p class="Normal">351 V&otilde; Văn Tần, phường 5, quận 3, TP HCM. ĐT: (08) 3929 3995,&nbsp;<br />Fax: (08) 3929 3996, Email:&nbsp;<a class="Normal" href="mailto:adminhcm@ducanh.edu.vn">adminhcm@ducanh.edu.vn</a></p>\r\n<p class="Normal">32F Điện Bi&ecirc;n Phủ, Hải Ph&ograve;ng. ĐT: (0313) 686 689, Email:<a class="Normal" href="mailto:adminhp@ducanh.edu.vn">adminhp@ducanh.edu.vn</a></p>\r\n<p class="Normal">Trung t&acirc;m Anh ngữ Dace - Luyện IELTS v&agrave; tiếng Anh học thuật: 8D H&agrave;m Long, H&agrave; Nội. ĐT: (04) 3944 8589, Email:&nbsp;<a class="Normal" href="mailto:Dacehn@ducanh.edu.vn">Dacehn@ducanh.edu.vn</a></p>\r\n<p class="Normal">Website:&nbsp;<a class="Normal" href="http://www.ducanhduhoc.com/">http://www.ducanhduhoc.com/</a></p>\r\n<p class="Normal" style="text-align: right;"><br />(Nguồn:&nbsp;<em>Đức Anh</em>)</p>', 4, 1, 1419, 0, 1),
(151, 'Bí quyết học ngành tài chính tại Anh', 'UK Alumni Talk tháng 12 với chủ đề “Học chuyên ngành tài chính tại Anh - những điều cần biết” giúp sinh viên chọn khoá học, chọn trường, kỹ năng học tập trong lĩnh vực này qua sự chia sẻ của các cựu du học sinh Anh.', 'HDA.jpg', NULL, '<h1 class="Title">B&iacute; quyết học ng&agrave;nh t&agrave;i ch&iacute;nh tại Anh</h1>\r\n<h2 class="Lead">UK Alumni Talk th&aacute;ng 12 với chủ đề &ldquo;Học chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh tại Anh - những điều cần biết&rdquo; gi&uacute;p sinh vi&ecirc;n chọn kho&aacute; học, chọn trường, kỹ năng học tập trong lĩnh vực n&agrave;y qua sự chia sẻ của c&aacute;c cựu du học sinh Anh.</h2>\r\n<p class="Normal">C&aacute;c cựu du học sinh Anh xuất sắc đang l&agrave;m việc tại c&aacute;c c&ocirc;ng ty t&agrave;i ch&iacute;nh hoặc ng&acirc;n h&agrave;ng tại Việt Nam sẽ chia sẻ kinh nghiệm học tập chuy&ecirc;n ng&agrave;nh n&agrave;y.</p>\r\n<p class="Normal">Tại H&agrave; Nội:</p>\r\n<p class="Normal">Đ&agrave;o Phương Lan - Cựu sinh vi&ecirc;n trường kinh tế ch&iacute;nh trị London (LSE), cử nh&acirc;n chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh, kế to&aacute;n v&agrave; kinh tế học. Lan đạt học bổng to&agrave;n phần của trường v&agrave; đạt nhiều giải thưởng trong qu&aacute; tr&igrave;nh học tập như giải thưởng CS McTaggart d&agrave;nh cho 3 sinh vi&ecirc;n xuất sắc nhất của LSE, giải Gonner Prize d&agrave;nh cho th&agrave;nh t&iacute;ch học tập xuất sắc năm cuối v&agrave; giải thưởng của Citibank. Lan c&oacute; nhiều kinh nghiệm l&agrave;m việc tại c&aacute;c c&ocirc;ng ty lớn của Anh như Merrill Lynch, Lehman Brothers v&agrave; đang l&agrave;m việc tại Tập đo&agrave;n Temasek Holdings của Singapore.</p>\r\n<p class="Normal">Đỗ Phương Huyền - cựu sinh vi&ecirc;n trường đại học East Anglia, thạc sĩ chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh v&agrave; kinh tế học (tốt nghiệp hạng xuất sắc), đang l&agrave;m vị tr&iacute; điều phối vi&ecirc;n v&agrave; giảng vi&ecirc;n của đại học quốc gia H&agrave; Nội.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="HDA.jpg" border="1" alt="" width="400" height="266" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại TP HCM:</p>\r\n<p class="Normal">Nguyễn Ho&agrave;ng Anh Thư - cựu sinh vi&ecirc;n đại học Loughborough, cử nh&acirc;n chuy&ecirc;n ng&agrave;nh ng&acirc;n h&agrave;ng, t&agrave;i ch&iacute;nh v&agrave; quản l&yacute;. Cựu sinh vi&ecirc;n đại học Nottingham, thạc sĩ chuy&ecirc;n ng&agrave;nh quản trị v&agrave; chiến lược. Thư đạt học bổng của đại học Nottingham v&agrave; gi&agrave;nh giải thưởng top 5 kế hoạch kinh doanh xuất sắc nhất trong cuộc thi tại Anh. Thư c&oacute; kinh nghiệm l&agrave;m việc ở c&aacute;c c&ocirc;ng ty của Anh như Absolute Occasions, Goldman Sachs International v&agrave; hiện giữ vị tr&iacute; gi&aacute;m đốc đầu tư cho quỹ đầu tư LD Investment của Đan Mạch tại Việt Nam.</p>\r\n<p class="Normal">Ng&ocirc; Thu Ba - cựu sinh vi&ecirc;n đại học Middlesex, đạt học bổng thạc sĩ chuy&ecirc;n ng&agrave;nh t&agrave;i ch&iacute;nh v&agrave; đầu tư (tốt nghiệp hạng xuất sắc). Thu Ba từng c&oacute; kinh nghiệm l&agrave;m việc ở nhiều vị tr&iacute; kh&aacute;c nhau tại Ng&acirc;n h&agrave;ng Australia and New Zealand Banking Group Limited, chi nh&aacute;nh tại TP HCM v&agrave; hiện l&agrave;m việc tại c&ocirc;ng ty chứng kho&aacute;n Bảo Việt.</p>\r\n<p class="Normal">Đăng k&yacute; với Hội đồng Anh để tham dự buổi n&oacute;i chuyện theo lịch sau:</p>\r\n<p class="Normal">H&agrave; Nội: 18h &ndash; 19h30 ng&agrave;y 21/12 tại ph&ograve;ng học 9 v&agrave; 10 số 20 Thuỵ Khu&ecirc;, quận T&acirc;y Hồ. ĐT: 04 3843 6780 - Email:&nbsp;<a class="Normal" href="mailto:educationuk@britishcouncil.org.vn">educationuk@britishcouncil.org.vn</a></p>\r\n<p class="Normal">TP HCM: 18h30 &ndash; 20h ng&agrave;y 21/12 tại ph&ograve;ng học 3 v&agrave; 4 số 25 L&ecirc; Duẩn, quận 1. ĐT 08 3823 2862 - Email:&nbsp;<a class="Normal" href="mailto:educationuk.hcmc@britishcouncil.org.vn">educationuk.hcmc@britishcouncil.org.vn</a></p>\r\n<p class="Normal">Chương tr&igrave;nh được t&agrave;i trợ bởi ĐH Central Lancashire, ĐH East Anglia v&agrave; ĐH West of England.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Hội Đồng Anh</em>)</p>', 4, 1, 1444, 0, 1),
(152, 'Tháng du học New Zealand', 'Đức Anh A&T dành một tháng chuyên du học New Zealand từ ngày 25/12. Tất cả các học sinh, sinh viên đăng ký du học trong thời gian này sẽ được hỗ trợ 100% phí visa và phí khám sức khỏe sau khi nhận visa du học.', 'DA.jpg', NULL, '<h1 class="Title">Th&aacute;ng du học New Zealand</h1>\r\n<h2 class="Lead">Đức Anh A&amp;T d&agrave;nh một th&aacute;ng chuy&ecirc;n du học New Zealand từ ng&agrave;y 25/12. Tất cả c&aacute;c học sinh, sinh vi&ecirc;n đăng k&yacute; du học trong thời gian n&agrave;y sẽ được hỗ trợ 100% ph&iacute; visa v&agrave; ph&iacute; kh&aacute;m sức khỏe sau khi nhận visa du học.</h2>\r\n<p class="Normal">Học sinh, sinh vi&ecirc;n c&oacute; thể chọn c&aacute;c trường đại học c&ocirc;ng lập hoặc cao đẳng c&ocirc;ng lập &ndash; tư thục với c&aacute;c hỗ trợ bao gồm hoặc một phần c&aacute;c ph&iacute; gồm: Miễn ph&iacute; ghi danh (300 NZD), đưa đ&oacute;n s&acirc;n bay (70 NZD), nh&agrave; 2 tuần đầu (400 NZD), tặng sim card (30 NZD), tặng laptop mới v&agrave; phần mềm cho tất cả sinh vi&ecirc;n kh&oacute;a Diploma (1.500 NZD), ph&iacute; hội vi&ecirc;n của Hiệp hội C&ocirc;ng nghệ th&ocirc;ng tin (50 NZD) v&agrave; bảo đảm 80 giờ l&agrave;m việc tại New Zealand (d&agrave;nh cho sinh vi&ecirc;n trả đầy đủ to&agrave;n bộ học ph&iacute;).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="DA.jpg" border="1" alt="Đức anh" width="450" height="130" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ngo&agrave;i ra, c&aacute;c trường chuy&ecirc;n đ&agrave;o tạo về du lịch - kh&aacute;ch sạn đảm bảo bố tr&iacute; việc l&agrave;m th&ecirc;m cho sinh vi&ecirc;n trong thời gian học v&agrave; thực h&agrave;nh c&oacute; trả lương trong thời gian thực h&agrave;nh 6 th&aacute;ng trong một năm. Đ&acirc;y l&agrave; thu nhập đ&aacute;ng kể, gi&uacute;p c&aacute;c sinh vi&ecirc;n chi trả một phần chi ph&iacute; du học.</p>\r\n<p class="Normal">Học sinh c&oacute; thể chọn học c&aacute;c trường tr&igrave;nh phổ th&ocirc;ng cấp bằng phổ th&ocirc;ng trung học New Zealand hoặc cấp bằng t&uacute; t&agrave;i quốc tế (IB).</p>\r\n<p class="Normal">Học sinh c&oacute; thể chọn học b&aacute;n tr&uacute; hoặc nội tr&uacute;. C&aacute;c học sinh dưới 18 tuổi c&oacute; người bảo trợ. Khi học xong cấp 3, học sinh chuyển l&ecirc;n đại học v&agrave; chỉ học 3 năm l&agrave; ho&agrave;n th&agrave;nh bậc cử nh&acirc;n để nhận bằng đại học. Học ph&iacute; từ 10.000 đến 14.000 NZD.</p>\r\n<p class="Normal">Hầu hết c&aacute;c trường đều c&oacute; học bổng cho sinh vỉ&ecirc;n xuất sắc. Trong đ&oacute; NTEC (National Technology Institute of New Zealand), TP Auckland cấp học bổng 100% học ph&iacute; trị gi&aacute; 13.500 NZD (3 suất), 50% học ph&iacute; trị gi&aacute; 6.750 NZD (3 suất), 25% học ph&iacute; trị gi&aacute; 3.500 NZD (5 suất).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: C&ocirc;ng ty tư vấn du học, gi&aacute;o dục v&agrave; dịch thuật quốc tế Đức Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&agrave; Nội: 96 L&ograve; Đ&uacute;c, ĐT: 04 3971 6229 - 04 3972 0889 -&nbsp;<br />Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:duhoc@ducanh.edu.vn">duhoc@ducanh.edu.vn</a></span></p>\r\n<p class="Normal">TP HCM: 351 V&otilde; Văn Tần, phường 5, quận 3.<br />ĐT: (08) 3929 3995 - Fax: (08) 3929 3996, Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:adminhcm@ducanh.edu.vn">adminhcm@ducanh.edu.vn</a></span></p>\r\n<p class="Normal">Hải Ph&ograve;ng: 32F Điện Bi&ecirc;n Phủ,&nbsp;<br />ĐT: 0313 686 689 - Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:adminhp@ducanh.edu.vn">adminhp@ducanh.edu.vn</a>.</span></p>\r\n<p class="Normal">Trung t&acirc;m Anh ngữ Dace: 8D H&agrave;m Long, H&agrave; Nội. ĐT: 04 3944 8589<br />Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:Dacehn@ducanh.edu.vn">Dacehn@ducanh.edu.vn</a>.</span></p>\r\n<a class="Normal" href="http://www.ducanhduhoc.com/">http://www.ducanhduhoc.com/</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Đức Anh)</em></p>', 4, 1, 894, 0, 1),
(153, 'Miễn 100% học phí du học Pháp', 'Các ĐH công lập Pháp như Université de Paris–Sud, Université de Paris 11, Université de Tours, Université d’Angers, Université de Lyon được Chính phủ hỗ trợ 100% học phí (khoảng 7.000 EUR một năm cho mỗi sinh viên).', '1412_thap.jpg', NULL, '<h1 class="Title">Miễn 100% học ph&iacute; du học Ph&aacute;p</h1>\r\n<h2 class="Lead">C&aacute;c ĐH c&ocirc;ng lập Ph&aacute;p như Universit&eacute; de Paris&ndash;Sud, Universit&eacute; de Paris 11, Universit&eacute; de Tours, Universit&eacute; d&rsquo;Angers, Universit&eacute; de Lyon được Ch&iacute;nh phủ hỗ trợ 100% học ph&iacute; (khoảng 7.000 EUR một năm cho mỗi sinh vi&ecirc;n).</h2>\r\n<p class="Normal">Ngo&agrave;i ra, học sinh c&ograve;n được Ch&iacute;nh phủ v&agrave; c&aacute;c tổ chức nh&agrave; ở Ph&aacute;p hỗ trợ từ 30 đến 50% tiền thu&ecirc; nh&agrave;.<strong>&nbsp;</strong>Du học sinh được ph&eacute;p l&agrave;m th&ecirc;m 20 giờ mỗi tuần trong thời gian học.</p>\r\n<p class="Normal">Hệ thống gi&aacute;o dục đại học Ph&aacute;p dựa theo m&ocirc; h&igrave;nh (3-5-8): 3 năm lấy bằng cử nh&acirc;n (Licence), 5 năm lấy bằng thạc sĩ (DEA, DESS) tương đương Master của Mỹ v&agrave; 8 năm lấy bằng tiến sĩ (Doctorat). Kh&ocirc;ng &aacute;p dụng cho ng&agrave;nh y, nha, dược v&agrave; kiến tr&uacute;c.</p>\r\n<p class="Normal">Chi ph&iacute; sinh hoạt ước t&iacute;nh từ 4.500 đến 6.000 EUR một năm gồm: Ph&iacute; đăng k&yacute; đại học: 174 &ndash; 355 EUR một năm; Bảo hiểm y tế: 200 EUR một năm; Ph&iacute; l&agrave;m thẻ cư tr&uacute;, kh&aacute;m sức khỏe: Miễn ph&iacute; hoặc 95 EUR một năm; Tiền ăn: 180 EUR 1 th&aacute;ng; Tiền ở: 250 &ndash; 586 EUR một th&aacute;ng; Đi lại v&agrave; chi ti&ecirc;u c&aacute; nh&acirc;n (tối thiểu): 80 EUR một th&aacute;ng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1412_thap.jpg" border="1" alt="" width="400" height="299" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class="SubTitle">C&aacute;c loại h&igrave;nh đ&agrave;o tạo tại Ph&aacute;p:</h3>\r\n<p class="Normal">Đại học &ldquo;Universit&eacute;&rdquo;: Đại học c&ocirc;ng lập Ph&aacute;p giảng dạy c&aacute;c chuy&ecirc;n ng&agrave;nh đ&agrave;o tạo cơ bản: cơ kh&iacute;, điện &ndash; điện tử, bảo tr&igrave; c&ocirc;ng nghiệp, c&ocirc;ng nghệ sinh học, y &ndash; dược, x&acirc;y dựng, h&agrave;ng kh&ocirc;ng, du lịch &ndash; kh&aacute;ch sạn, h&agrave;ng hải, kinh tế, ngoại thương, c&ocirc;ng nghệ th&ocirc;ng tin, dầu kh&iacute;, b&aacute;o ch&iacute;, quản l&yacute; thời trang, thiết kế tạo mẫu, thương mại điện tử, t&agrave;i ch&iacute;nh &ndash; kế to&aacute;n...</p>\r\n<p class="Normal">Trường lớn &ldquo;Grande &Eacute;cole&rdquo;: Đ&acirc;y l&agrave; n&eacute;t đặc th&ugrave; ri&ecirc;ng của nền gi&aacute;o dục đại học Ph&aacute;p. Được th&agrave;nh lập song song với c&aacute;c trường đại học &ldquo; Universit&eacute;&rdquo;, cung cấp chương tr&igrave;nh đ&agrave;o tạo uy t&iacute;n, hiệu quả v&agrave; chuy&ecirc;n nghiệp. Đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh: kỹ sư, quản l&yacute; kinh tế - thương mại, ch&iacute;nh trị... Thời gian học 5 năm.</p>\r\n<p class="Normal">Trường quản l&yacute; v&agrave; thương mại: Đ&agrave;o tạo đa ng&agrave;nh về kinh tế.</p>\r\n<p class="Normal">Trường kỹ sư &ldquo;Grandes &Eacute;coles d&rsquo;Ingenieurs&rdquo;: l&agrave; một trong những tấm bằng quốc gia c&oacute; gi&aacute; trị nhất trong hệ đ&agrave;o tạo của Ph&aacute;p. Thời gian học 5 năm.</p>\r\n<p class="Normal">Trường kiến tr&uacute;c &ldquo;Ecole d&rsquo;Architecture&rdquo;: Đ&agrave;o tạo v&agrave; cấp bằng kiến tr&uacute;c sư DPLG (văn bằng duy nhất được c&ocirc;ng nhận đối với những kiến tr&uacute;c sư muốn l&agrave;m việc tại Ph&aacute;p). Thời gian đ&agrave;o tạo: 6 năm.</p>\r\n<p class="Normal">Trường nghệ thuật bao gồm: Trường mỹ thuật, trường nghệ thuật quốc gia, v&agrave; trường nghệ thuật ứng dụng. Thời gian đ&agrave;o tạo: từ 3 đến 5 năm.</p>\r\n<p class="Normal">Trường chuy&ecirc;n nghiệp: Đ&agrave;o tạo c&aacute;c ng&agrave;nh nghệ thuật, kiến tr&uacute;c, y tế, luật...</p>\r\n<p class="Normal">Ngo&agrave;i ra, sinh vi&ecirc;n c&oacute; thể học c&aacute;c ng&agrave;nh học ngắn hạn (2 hoặc 3 năm) để lấy bằng BTS (bằng kỹ thuật vi&ecirc;n cao cấp) hoặc DUT (Bằng đại học c&ocirc;ng nghệ). Sinh vi&ecirc;n được thực tập tại c&aacute;c ph&acirc;n xưởng, x&iacute; nghiệp. Bằng cấp n&agrave;y đang rất được trọng dụng v&agrave; khuyến kh&iacute;ch trong giới chuy&ecirc;n m&ocirc;n.</p>\r\n<h3 class="SubTitle">Y&ecirc;u cầu đầu v&agrave;o:</h3>\r\n<p class="Normal">- Tốt nghiệp PTTH hoặc đang học lớp 12 (chương tr&igrave;nh cử nh&acirc;n) v&agrave; tốt nghiệp đại học (chương tr&igrave;nh thạc sĩ).</p>\r\n<p class="Normal">- Tr&igrave;nh độ tiếng Ph&aacute;p: từ TCF 200 đến TCF 699 (cho mọi đối tượng) v&agrave; IELTS 5 (cho chương tr&igrave;nh giảng dạy bằng tiếng Anh kh&oacute;a thạc sĩ hoặc tiến sĩ).</p>\r\n<p class="Normal">- Đặc biệt, sinh vi&ecirc;n kh&ocirc;ng phải tham dự kỳ thi đầu v&agrave;o, hồ sơ sẽ được trường x&eacute;t duyệt.</p>\r\n<p class="Normal">- Sinh vi&ecirc;n được chọn 3 nguyện vọng.</p>\r\n<p class="Normal">- Khai giảng: th&aacute;ng 1 v&agrave; th&aacute;ng 9 hằng năm.</p>\r\n<h3 class="SubTitle">Trung t&acirc;m du học Edulinks tổ chức buổi hội thảo giới thiệu du học Ph&aacute;p l&uacute;c 18 giờ ng&agrave;y 17/12 tại kh&aacute;ch sạn New Epoch, 120 C&aacute;ch mạng Th&aacute;ng 8, quận 3, TP HCM.</h3>\r\n<p class="Normal">Trung t&acirc;m du học Edulinks thường xuy&ecirc;n mở c&aacute;c lớp luyện thi TCF - DAP cho học sinh, sinh vi&ecirc;n chuẩn bị du học Ph&aacute;p.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Trung t&acirc;m du học Edulinks</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">439 Ho&agrave;ng Văn Thụ, phường 4, quận T&acirc;n B&igrave;nh, TP HCM.<br />Tel: (08) 3847 9525 &ndash; Fax: (08) 3847 9975 &ndash; Hotline: 0909.538.656<br />E-mail:&nbsp;<a class="Normal" href="mailto:info@edulinks.com.vn">info@edulinks.com.vn</a>; Website:&nbsp;<a class="Normal" href="http://www.edulinks.com.vn/"><span style="color: #0000ff;">www.edulinks.com.vn</span></a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><em>(</em>Nguồn:&nbsp;<em>Edulinks)</em></p>', 4, 1, 2402, 0, 1),
(154, 'Học bổng 50% học phí tại ĐH Kent, Anh', 'Đại học Kent có một số học bổng 50% học phí (tương đương khoảng 5.000 GBP) cho các khóa đại học và thạc sĩ. Hạn chót nộp hồ sơ xin học bổng là ngày 31/5/2011.', 'Kent-2.jpg', NULL, '<h1 class="Title">Học bổng 50% học ph&iacute; tại ĐH Kent, Anh</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="Kent-2.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Đại học Kent c&oacute; một số học bổng 50% học ph&iacute; (tương đương khoảng 5.000 GBP) cho c&aacute;c kh&oacute;a đại học v&agrave; thạc sĩ. Hạn ch&oacute;t nộp hồ sơ xin học bổng l&agrave; ng&agrave;y 31/5/2011.</h2>\r\n<p class="Normal">Đại học Kent nằm tại th&agrave;nh phố cổ k&iacute;nh v&agrave; y&ecirc;n b&igrave;nh Canterbury, đ&ocirc;ng nam nước Anh, c&aacute;ch London một tiếng đi t&agrave;u. Trường c&oacute; khoảng 20.000 sinh vi&ecirc;n trong đ&oacute; 16% l&agrave; sinh vi&ecirc;n quốc tế đến từ hơn 124 quốc gia tr&ecirc;n thế giới. Kent được mệnh danh l&agrave; &ldquo;trường đại học ch&acirc;u &Acirc;u tại Anh&rdquo; v&igrave; sinh vi&ecirc;n c&oacute; cơ hội trải nghiệm cơ hội học tập tại cơ sở của trường tại th&agrave;nh phố Brussel, Bỉ v&agrave; Paris, Ph&aacute;p.</p>\r\n<p class="Normal">Đại học Kent c&oacute; m&ocirc;i trường học tập hiện đại, th&acirc;n thiện v&agrave; y&ecirc;n b&igrave;nh. Trường được xếp hạng 16 trong hơn 100 trường đại học của Anh theo bảng xếp hạng của tạp ch&iacute; the Times năm 2011. Trường đ&agrave;o tạo c&aacute;c chương tr&igrave;nh: tiếng Anh, dự bị đại học, dự bị thạc sĩ, đại học, thạc sĩ, tiến sĩ.</p>\r\n<p class="Normal">Trường c&oacute; thế mạnh về c&aacute;c kh&oacute;a học quản l&yacute; trong số c&aacute;c trường đại học khu vực đ&ocirc;ng nam nước Anh. Kent xếp thứ 3 tại Anh về c&aacute;c kh&oacute;a học ng&agrave;nh kinh doanh theo tạp ch&iacute; the Times năm 2010-2011 v&agrave; xếp hạng thứ 19 về ng&agrave;nh t&agrave;i ch&iacute;nh v&agrave; kế to&aacute;n theo tạp ch&iacute; The Independent 2010.</p>\r\n<p class="Normal">Trường c&oacute; trung t&acirc;m việc l&agrave;m ri&ecirc;ng d&agrave;nh cho những sinh vi&ecirc;n muốn t&igrave;m kiếm việc l&agrave;m ngo&agrave;i giờ. Học sinh tốt nghiệp tại trường c&oacute; cơ hội l&agrave;m việc cao. Ngo&agrave;i ra, trường c&oacute; mạng lưới hỗ trợ sinh vi&ecirc;n quốc tế tốt.</p>\r\n<p class="Normal">Kent c&oacute; c&aacute;c suất học bổng v&agrave; qu&agrave; tặng gi&aacute; trị của như sau:</p>\r\n<p class="Normal">Kh&oacute;a học thạc sĩ quản trị kinh doanh (MBA): Sinh vi&ecirc;n nộp hồ sơ trước 30/1/2011 v&agrave; trả đặt cọc trước ng&agrave;y 31/3/2011: sẽ nhận được học bổng 1.000 GBP hoặc sinh vi&ecirc;n nộp hồ sơ trước 30/3/2011 v&agrave; trả đặt cọc trước ng&agrave;y 30/5/2011 sẽ tự động được xem x&eacute;t cho học bổng của trường kinh doanh Kent (l&ecirc;n tới 50% học ph&iacute;).</p>\r\n<p class="Normal">Đối với c&aacute;c chương tr&igrave;nh cử nh&acirc;n v&agrave; thạc sĩ kh&aacute;c như: Msc Finance, Msc Marketing, Msc Human Resource Management... sinh vi&ecirc;n nộp hồ sơ trước ng&agrave;y 30/1 v&agrave; trả đặt cọc trước ng&agrave;y 31/3 sẽ được xem x&eacute;t nhận học bổng của trường kinh doanh Kent, miễn ph&iacute; gi&aacute;o tr&igrave;nh cho những m&ocirc;n học chuy&ecirc;n ng&agrave;nh, miễn ph&iacute; m&aacute;y t&iacute;nh x&aacute;ch tay. Sinh vi&ecirc;n nộp hồ sơ trước 30/3 v&agrave; trả đặt cọc trước ng&agrave;y 30/5 sẽ nhận được: miễn ph&iacute; gi&aacute;o tr&igrave;nh cho những m&ocirc;n học chuy&ecirc;n ng&agrave;nh, miễn ph&iacute; m&aacute;y t&iacute;nh x&aacute;ch tay.</p>\r\n<p class="Normal">Đại học Kent c&oacute; một số học bổng 50% học ph&iacute; (tương đương khoảng 5.000 GBP) cho kh&oacute;a đại học v&agrave; thạc sĩ với c&aacute;c chuy&ecirc;n ng&agrave;nh kh&aacute;c.</p>\r\n<p class="Normal">Điều kiện xin học bổng: Một bộ hồ sơ đầy đủ xin nhập học cho kh&oacute;a học tại Đại học Kent; Ứng vi&ecirc;n phải nhận được thư nhập học kh&ocirc;ng điều kiện (Unconditional Offer) của một kh&oacute;a học tại trường đại học Kent; đại học Kent phải nhận được quyết định đồng &yacute; học của ứng vi&ecirc;n; ứng vi&ecirc;n phải chứng minh đủ khả năng thanh to&aacute;n mọi chi ph&iacute; cho chương tr&igrave;nh học của m&igrave;nh.</p>\r\n<p class="Normal">Để nhận được học bổng, học sinh sẽ phải nộp một b&agrave;i viết từ 750 đến 1.000 chữ về chủ đề: "Tại sao bạn chọn chương tr&igrave;nh giảng dạy đại học hoặc thạc sĩ?" hoặc "Tại sao học sinh lại nộp hồ sơ v&agrave;o trường đại học Kent?" hoặc "Học sinh nhận thấy bằng cấp của trường đại học Kent sẽ t&aacute;c động thế n&agrave;o đến cuộc sống của bạn sau khi tốt nghiệp?".</p>\r\n<p class="Normal">Hạn ch&oacute;t nộp hồ sơ học bổng 31/5/2011.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: C&ocirc;ng ty gi&aacute;o dục quốc tế GetSet</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Địa chỉ: No 58 phố Ngọc Kh&aacute;nh, Ba Đ&igrave;nh, H&agrave; Nội</p>\r\n<p class="Normal">Điện thoại: + 3 771356; Email:&nbsp;<a class="Normal" href="mailto:Hanoi@getsetglobal.com">Hanoi@getsetglobal.com</a>;</p>\r\n<p class="Normal">Website:&nbsp;<a class="Normal" href="http://www.getset.edu.vn/">http://www.getset.edu.vn/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>GetSet International Education Company)</em></p>', 4, 1, 1141, 0, 1),
(155, 'Học bổng khuyến học INTO, Mỹ', 'INTO Oregon State University và ĐH South Florida dành 12 suất học bổng cho sinh viên Việt Nam đáp ứng được các điều kiện đầu vào để tham gia các khóa học dự bị học kỳ tháng 9 hoặc tháng 12/2011.', '1_250x195.jpg', NULL, '<h1 class="Title">Học bổng khuyến học INTO, Mỹ</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="1_250x195.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">INTO Oregon State University v&agrave; ĐH South Florida d&agrave;nh 12 suất học bổng cho sinh vi&ecirc;n Việt Nam đ&aacute;p ứng được c&aacute;c điều kiện đầu v&agrave;o để tham gia c&aacute;c kh&oacute;a học dự bị học kỳ th&aacute;ng 9 hoặc th&aacute;ng 12/2011.</h2>\r\n<p class="Normal">12 học bổng khuyến học c&oacute; trị gi&aacute; mỗi suất l&agrave; 2.500 USD cho kh&oacute;a học dự bị k&eacute;o d&agrave;i từ 9 đến 12 th&aacute;ng. Sau khi ho&agrave;n tất kh&oacute;a dự bị, sinh vi&ecirc;n sẽ được chuyển tiếp v&agrave;o năm 2 của chương tr&igrave;nh cử nh&acirc;n tại Oregon State University (OSU) hoặc University of South Florida (USF).</p>\r\n<p class="Normal">Kh&oacute;a dự bị 9 th&aacute;ng tại trung t&acirc;m INTO OSU/USF chuy&ecirc;n ng&agrave;nh kinh doanh hoặc tổng qu&aacute;t sẽ được chuyển tiếp v&agrave;o c&aacute;c ng&agrave;nh học gồm:</p>\r\n<p class="Normal">Kế to&aacute;n, t&agrave;i ch&iacute;nh, marketing, quản l&yacute;, kinh doanh v&agrave; quản trị kinh doanh.</p>\r\n<p class="Normal">Nh&acirc;n chủng học, nghệ thuật thị gi&aacute;c ứng dụng, kinh tế học, tiếng Anh v&agrave; &acirc;m nhạc.</p>\r\n<p class="Normal">Triết học, x&atilde; hội học, giao tiếp ng&ocirc;n ngữ, du lịch, thiết kế nội thất, thiết kế trang phục, quản l&yacute; kinh doanh, quản l&yacute; y tế v&agrave; giải tr&iacute;.</p>\r\n<p class="Normal">Kh&oacute;a dự bị 12 th&aacute;ng tại INTO OSU hoặc 9 th&aacute;ng tại INTO USF chuy&ecirc;n ng&agrave;nh khoa học kỹ thuật sẽ chuyển tiếp v&agrave;o c&aacute;c ng&agrave;nh học sau:</p>\r\n<p class="Normal">Kỹ thuật sinh học, kỹ thuật h&oacute;a học, kỹ thuật d&acirc;n dụng, khoa học m&aacute;y t&iacute;nh, quản l&yacute; kỹ thuật x&acirc;y dựng, kỹ thuật điện, kỹ thuật c&ocirc;ng nghiệp, kỹ thuật cơ kh&iacute; v&agrave; kỹ thuật chế tạo.</p>\r\n<p class="Normal">H&oacute;a sinh v&agrave; l&yacute; sinh, sinh học, h&oacute;a học, khoa học về tr&aacute;i đất, khoa học đại cương, địa l&yacute;, to&aacute;n học, vật l&yacute;, động vật học, khoa học về c&aacute;c lo&agrave;i vật, khoa học thực phẩm, thể dục thể thao, dinh dưỡng v&agrave; quản l&yacute; thực phẩm, khoa học m&ocirc;i trường v&agrave; nhiều ng&agrave;nh kh&aacute;c.</p>\r\n<p class="Normal">Ngo&agrave;i ra, INTO c&ograve;n c&oacute; chương tr&igrave;nh dự bị thạc sĩ cho sinh vi&ecirc;n quốc tế chưa đ&aacute;p ứng y&ecirc;u cầu về học vấn hoặc thiếu tr&igrave;nh độ tiếng Anh TOEFL/IELTS, GMAT/GRE để c&oacute; thể v&agrave;o thẳng chương tr&igrave;nh thạc sĩ. Chương tr&igrave;nh do c&aacute;c gi&aacute;o sư của ĐH Oregon State v&agrave; ĐH Nam Florida giảng dạy trong lớp học với sĩ số nhỏ, nằm trong khu&ocirc;n vi&ecirc;n ĐH bang Oregon v&agrave; ĐH Nam Florida. Sau khi kết th&uacute;c kh&oacute;a dự bị, sinh vi&ecirc;n sẽ được chấp thuận v&agrave;o chương tr&igrave;nh thạc sĩ của trường ĐH bang Oregon v&agrave; ĐH Nam Florida m&agrave; kh&ocirc;ng cần phải thi lấy bằng TOEFL v&agrave; GRE/GMAT. Thời gian kh&oacute;a học k&eacute;o d&agrave;i từ 3, 6 đến 9 th&aacute;ng t&ugrave;y khả năng của sinh vi&ecirc;n.</p>\r\n<p class="Normal">ĐH Bang Oregon nằm trong số 150 trường đại học h&agrave;ng đầu tr&ecirc;n thế giới v&agrave; ĐH Nam Florida nằm trong top 50 trường đại học h&agrave;ng đầu về lĩnh vực nghi&ecirc;n cứu v&agrave; trong top 100 trường đại học đ&agrave;o tạo kế to&aacute;n xuất sắc nhất.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: C&ocirc;ng ty tư vấn du học Quốc Anh I.E.C</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">86 Sương Nguyệt &Aacute;nh, quận 1, TP HCM.<br />ĐT: 39250945 - 39253980 - số m&aacute;y lẻ 602 hoặc qua tổng đ&agrave;i 1088<br />Email:&nbsp;<a class="Normal" href="mailto:iec-hcm-usa@hcm.fpt.vn">iec-hcm-usa@hcm.fpt.vn</a>; Website:&nbsp;<a class="Normal" href="http://www.quocanhiec-hcm.com.vn/">www.quocanhiec-hcm.com.vn</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>I.E.C</em>)</p>', 4, 1, 1255, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(156, 'Đào tạo ngành thời trang theo tiêu chuẩn châu Âu', 'Học viện Thời trang London (Hà Nội) là nơi đào tạo chuyên về thời trang của trường đại học Northumbria (Anh). Tại đây, toàn bộ quy trình đào tạo taijd dây đều theo tiêu chuẩn châu Âu.', 'thoitrang7.jpg', NULL, '<h1 class="Title">Đ&agrave;o tạo ng&agrave;nh thời trang theo ti&ecirc;u chuẩn ch&acirc;u &Acirc;u</h1>\r\n<h2 class="Lead">Học viện Thời trang London (H&agrave; Nội) l&agrave; nơi đ&agrave;o tạo chuy&ecirc;n về thời trang của trường đại học Northumbria (Anh). Tại đ&acirc;y, to&agrave;n bộ quy tr&igrave;nh đ&agrave;o tạo taijd d&acirc;y đều theo ti&ecirc;u chuẩn ch&acirc;u &Acirc;u.</h2>\r\n<p class="Normal">Quy tr&igrave;nh đ&agrave;o tạo thời trang tại đ&acirc;y gồm ba nội dung ch&iacute;nh: Đầu ti&ecirc;n l&agrave; &yacute; tưởng, bao gồm: ph&aacute;c thảo, thiết kế v&agrave; đồ họa. Bước thứ hai l&agrave; sản xuất, bao gồm: cắt mẫu rập, x&acirc;y dựng cấu tr&uacute;c sản phẩm, cho đến l&uacute;c ho&agrave;n tất v&agrave; tr&igrave;nh b&agrave;y. Bước cuối c&ugrave;ng l&agrave; c&aacute;c chiến lược kinh doanh, marketing, ph&acirc;n phối b&aacute;n lẻ. Tiến tr&igrave;nh đ&agrave;o tạo chuy&ecirc;n s&acirc;u từ cơ bản đến n&acirc;ng cao theo từng kỳ học.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="thoitrang7.jpg" border="1" alt="Cắt mẫu giấy s&aacute;ng tạo." width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cắt mẫu giấy s&aacute;ng tạo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ngo&agrave;i ra, tại Học viện Thời trang London (H&agrave; Nội), l&yacute; thuyết lu&ocirc;n đi đ&ocirc;i với thực h&agrave;nh n&ecirc;n ngay sau kỳ đầu ti&ecirc;n, học vi&ecirc;n đ&atilde; c&oacute; sản phẩm thực tế.</p>\r\n<p class="Normal">Với hơn s&aacute;u năm đ&agrave;o tạo tại Việt Nam, học viện n&agrave;y đ&atilde; tạo n&ecirc;n danh tiếng trong ng&agrave;nh. Giảng vi&ecirc;n của trường l&agrave; những chuy&ecirc;n gia nước ngo&agrave;i gi&agrave;u kinh nghiệm. To&agrave;n bộ kết quả học tập của sinh vi&ecirc;n được chuyển sang Anh đ&aacute;nh gi&aacute; v&agrave; cấp bằng theo ti&ecirc;u chuẩn quốc tế bởi trường đại học Northumbria.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="thoitrang11.jpg" border="1" alt="Ng&agrave;y lễ tốt nghiệp." width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&agrave;y lễ tốt nghiệp.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Hiện nh&agrave; trường đang chuẩn bị cho kh&oacute;a học mới, khai giảng v&agrave;o th&aacute;ng 1/2011.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Học viện Thời trang Lu&acirc;n Đ&ocirc;n (LCFS Hanoi)</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">48 T&ocirc; Ngọc V&acirc;n, T&acirc;y Hồ, H&agrave; Nội<br />Website:&nbsp;<a class="Normal" href="http://www.lcfs.vn/">http://www.lcfs.vn/</a><br />Facebook: Lcfs Hanoi (link&nbsp;<a class="Normal" href="http://www.facebook.com/lcfs.hanoi">www.facebook.com/lcfs.hanoi</a>)<br />ĐT: (04) 37199706<br />Emai:&nbsp;<a class="Normal" href="mailto:info@fashionstudies.com.vn">info@fashionstudies.com.vn</a></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Học viện Thời trang London - LCFS Hanoi</em>)</p>', 4, 1, 694, 0, 1),
(157, 'Học bổng cử nhân tại NUS, Singapore', 'NUS có ba chương trình học bổng gồm ASEAN, Singapore và Dr. Goh Keng Swee. Với ba loại học bổng này, ngoài học phí, sinh viên được nhận sinh hoạt phí 3.866 USD một năm.', '1_3011_400300.jpg', NULL, '<h1 class="Title">Học bổng cử nh&acirc;n tại NUS, Singapore</h1>\r\n<h2 class="Lead">NUS c&oacute; ba chương tr&igrave;nh học bổng gồm ASEAN, Singapore v&agrave; Dr. Goh Keng Swee. Với ba loại học bổng n&agrave;y, ngo&agrave;i học ph&iacute;, sinh vi&ecirc;n được nhận sinh hoạt ph&iacute; 3.866 USD một năm.</h2>\r\n<p class="Normal">Đại học Quốc gia Singapore (NUS) nhiều năm liền được xếp trong top 50 viện đại học h&agrave;ng đầu tr&ecirc;n thế giới do b&aacute;o The Times (Anh) b&igrave;nh chọn. Th&aacute;ng 7 vừa qua, đ&atilde; c&oacute; hơn 130 bạn trẻ Việt Nam tr&uacute;ng tuyển v&agrave;o NUS trong đ&oacute; c&oacute; 20 học sinh được cấp học bổng to&agrave;n phần. Những học sinh c&ograve;n lại đều được Ch&iacute;nh phủ Singapore hỗ trợ cho vay v&agrave; trả d&agrave;i hạn trong v&ograve;ng 20 năm sau khi ra trường với l&atilde;i suất thấp (mỗi th&aacute;ng sinh vi&ecirc;n chỉ phải trả tối thiểu 100 SGD). Nh&agrave; trường c&ograve;n c&oacute; chế độ trợ cấp (bursary) cho những sinh vi&ecirc;n gặp kh&oacute; khăn. Trong thời gian học, sinh vi&ecirc;n được ph&eacute;p đi l&agrave;m th&ecirc;m sau học kỳ một (một giờ đi l&agrave;m th&ecirc;m từ 10 SGD). Từ năm thứ hai trở đi, sinh vi&ecirc;n giỏi được gửi đi học ở 200 trường ở ch&acirc;u &Acirc;u hoặc Mỹ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1_3011_400300.jpg" border="1" alt="" width="400" height="300" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">NUS tuyển sinh tại Việt Nam với hơn 300 ng&agrave;nh học trong đ&oacute; c&oacute; ng&agrave;nh: quản trị kinh doanh, kiến tr&uacute;c, khoa học x&atilde; hội v&agrave; nh&acirc;n văn, Khoa học tự nhi&ecirc;n, Kỹ nghệ sinh học, khoa học m&aacute;y t&iacute;nh, luật v&agrave; kinh tế học, c&ocirc;ng nghệ th&ocirc;ng tin, y - nha - dược...</p>\r\n<p class="Normal">Chương tr&igrave;nh d&agrave;nh cho học sinh đang học lớp 12 tại Việt Nam (sẽ phải qua thi tuyển của NUS v&agrave;o th&aacute;ng 3/2011) v&agrave; c&aacute;c bạn đ&atilde; c&oacute; bằng t&uacute; t&agrave;i (chỉ x&eacute;t tuyển).</p>\r\n<p class="Normal">Đối với học sinh đang học lớp 12, hồ sơ dự tuyển bao gồm: Học bạ hoặc phiếu điểm lớp 11, qua đ&oacute; nh&agrave; trường sẽ x&eacute;t điểm c&aacute;c m&ocirc;n tiếng Anh, to&aacute;n, l&yacute;, h&oacute;a (hoặc sinh), sử (hoặc địa) t&ugrave;y ng&agrave;nh chọn thi (c&oacute; thể gửi k&egrave;m học bạ lớp 10). C&aacute;c giấy tờ về giải thưởng, th&agrave;nh t&iacute;ch học tập, th&agrave;nh t&iacute;ch văn thể mỹ, c&aacute;c hoạt động x&atilde; hội từ cấp hai đến cấp ba (nếu c&oacute;). Giấy khai sinh. Lệ ph&iacute; nộp đơn l&agrave; 20 SGD mua bằng bankdraft (hối phiếu mua tại ng&acirc;n h&agrave;ng) hoặc trả bằng thẻ t&iacute;n dụng th&igrave; ghi số thẻ t&iacute;n dụng v&agrave;o đơn gửi đi.</p>\r\n<p class="Normal">B&ecirc;n cạnh đ&oacute;, người đ&atilde; c&oacute; bằng t&uacute; t&agrave;i (hoặc đang theo học đại học, hoặc đ&atilde; tốt nghiệp đại học) phải nộp học bạ lớp 12, bằng t&uacute; t&agrave;i, giấy chứng nhận thi t&uacute; t&agrave;i c&oacute; ghi điểm, điểm thi đại học (nếu c&oacute;). C&aacute;c giấy tờ về th&agrave;nh t&iacute;ch học tập, th&agrave;nh t&iacute;ch văn thể mỹ, hoạt động x&atilde; hội từ trung học đến đại học (nếu c&oacute;). Giấy khai sinh. Giấy x&aacute;c nhận điểm IELTS (6,5 trở l&ecirc;n) hoặc TOEFL iBT (92-93 trở l&ecirc;n). Lệ ph&iacute; nộp đơn l&agrave; 20 SGD mua bằng bankdraft (hối phiếu mua tại ng&acirc;n h&agrave;ng) hoặc trả bằng thẻ t&iacute;n dụng th&igrave; ghi số thẻ t&iacute;n dụng v&agrave;o đơn gửi đi.</p>\r\n<p class="Normal">Tham khảo:&nbsp;<span style="text-decoration: underline;"><a href="http://vietnamcentrepoint.edu.vn/nus">http://vietnamcentrepoint.edu.vn/nus</a></span>. Hạn ch&oacute;t nộp đơn v&agrave; hồ sơ cho nh&agrave; trường l&agrave; ng&agrave;y 31/12 (căn cứ v&agrave;o dấu bưu điện). C&aacute;c ứng vi&ecirc;n điền đơn online<a href="http://www.nus.edu.sg/oam/apply/international/applications/WYA-applicationform.html">tại đ&acirc;y</a>.</p>\r\n<p class="Normal">Đại diện tuyển sinh của NUS tại Việt Nam tham gia buổi b&aacute;o c&aacute;o v&agrave; hướng dẫn điền đơn cho chương tr&igrave;nh &ldquo;Học bổng v&agrave; trợ gi&uacute;p t&agrave;i ch&iacute;nh&rdquo; bậc cử nh&acirc;n từ 17h đến 20h ng&agrave;y 4/12 tại nh&agrave; Hữu Nghị, số 31 L&ecirc; Duẩn, quận 1, TP HCM.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Trung t&acirc;m đ&agrave;o tạo Việt Nam Hợp Điểm</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">8E Lương Hữu Kh&aacute;nh, quận 1, TP HCM<br />ĐT: (08) 3925 3183 hoặc 3925 2602<br />Email:&nbsp;<span style="text-decoration: underline;"><a href="mailto:thacmacnus@vietnamhopdiem.edu.vn">thacmacnus@vietnamhopdiem.edu.vn</a></span>.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Việt Nam Hợp Điểm</em>)</p>', 4, 1, 335, 0, 1),
(159, 'Đào tạo bác sĩ, dược sĩ bằng tiếng Anh tại châu Âu', 'Viện đại học Debrecen - UOD (Hungary) đang tuyển sinh chương trình đào tạo bác sĩ y khoa, nha khoa, dược sĩ học hoàn toàn bằng tiếng Anh.', 'duhoc (1).jpg', NULL, '<h1 class="Title">Đ&agrave;o tạo b&aacute;c sĩ, dược sĩ bằng tiếng Anh tại ch&acirc;u &Acirc;u</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="duhoc.jpg" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Viện đại học Debrecen - UOD (Hungary) đang tuyển sinh chương tr&igrave;nh đ&agrave;o tạo b&aacute;c sĩ y khoa, nha khoa, dược sĩ học ho&agrave;n to&agrave;n bằng tiếng Anh.</h2>\r\n<p class="Normal">Được th&agrave;nh lập v&agrave;o năm 1918, Viện ĐH Debrecen c&oacute; đầy đủ cơ sở giảng dạy hiện đại cho c&aacute;c ng&agrave;nh học. Trường c&oacute; bệnh viện với 2.000 giường phục vụ cho nh&acirc;n d&acirc;n của TP Debrecen v&agrave; c&aacute;c tỉnh l&acirc;n cận. Ngo&agrave;i ra, trường c&oacute; nhiều viện nghi&ecirc;n cứu kỹ thuật cao như tim mạch, sinh học ph&acirc;n tử, y học ph&oacute;ng xạ.</p>\r\n<p class="Normal">Hầu hết giảng vi&ecirc;n của nh&agrave; trường đều c&oacute; bằng tiến sĩ, đ&atilde; tốt nghiệp hoặc tu nghiệp ở Mỹ v&agrave; c&aacute;c nước ti&ecirc;n tiến. Nhiều b&aacute;c sĩ tốt nghiệp tại Debrecen được mời tham gia giảng dạy, nghi&ecirc;n cứu ở nhiều nước &Acirc;u - Mỹ.</p>\r\n<p class="Normal">Debrecen được Tổ chức Y tế Thế giới WHO xếp v&agrave;o danh s&aacute;ch những trường y được c&ocirc;ng nhận tr&ecirc;n thế giới, được Bộ Y tế Mỹ v&agrave; c&aacute;c nước EU c&ocirc;ng nhận bằng cấp.</p>\r\n<p class="Normal">Từ th&aacute;ng 5/2004, Hungary l&agrave; th&agrave;nh vi&ecirc;n ch&iacute;nh thức của Li&ecirc;n hiệp ch&acirc;u &Acirc;u (EU), n&ecirc;n sinh vi&ecirc;n học tại Hungary c&oacute; thể thực tập, l&agrave;m việc v&agrave; đi lại dễ d&agrave;ng trong hơn 20 nước EU. Mặt kh&aacute;c, sinh vi&ecirc;n năm thứ tư v&agrave; thứ năm ng&agrave;nh y v&agrave; nha c&oacute; thể tham gia kh&oacute;a luyện thi chứng chỉ h&agrave;nh nghề y tại Mỹ (USMLE) ngay tại UOD.</p>\r\n<p class="Normal">Tại UOD, sinh vi&ecirc;n quốc tế học giỏi c&oacute; thể nhận học bổng 5-20% gi&aacute; trị học ph&iacute; một năm. Học ph&iacute; của UOD thấp hơn rất nhiều so với c&aacute;c trường dạy y - nha - dược của c&aacute;c nước &Acirc;u, Mỹ kh&aacute;c.</p>\r\n<p class="Normal">Chương tr&igrave;nh đ&agrave;o tạo y khoa (6 năm) với học ph&iacute; khoảng 12.500 USD một năm, nha khoa (5 năm) học ph&iacute; khoảng 12.500 USD một năm, v&agrave; dược khoa (5 năm) học ph&iacute; khoảng 7.500 USD một năm.</p>\r\n<p class="Normal">Để nhập học UOD, c&aacute;c ứng vi&ecirc;n sẽ tham dự kỳ phỏng vấn v&agrave; kiểm tra (sinh, l&yacute;, h&oacute;a bằng tiếng Anh) tại Việt Nam. Mỗi năm nh&agrave; trường sẽ qua Việt Nam tuyển sinh v&agrave;o c&aacute;c th&aacute;ng 3, 6 v&agrave; 11. Sinh vi&ecirc;n tr&uacute;ng tuyển sẽ đi học v&agrave;o th&aacute;ng 9 hoặc th&aacute;ng 1 h&agrave;ng năm.<br /><br />Trường c&oacute; hai k&yacute; t&uacute;c x&aacute; hiện đại v&agrave; đầy đủ tiện nghi, nằm ngay trong khu học x&aacute;. Nh&agrave; trường đ&atilde; giảng dạy bằng tiếng Anh cho sinh vi&ecirc;n quốc tế từ những năm 1980. Hằng năm trường tiếp nhận tr&ecirc;n 1.000 sinh vi&ecirc;n &Acirc;u Mỹ, ch&acirc;u &Aacute; v&agrave; c&aacute;c nước kh&aacute;c.</p>\r\n<p class="Normal">Việt Nam Hợp Điểm l&agrave; đại diện tuyển sinh duy nhất của UOD tại Việt Nam từ năm 2004 đến nay. Hiện c&oacute; hơn 90 sinh vi&ecirc;n Việt Nam đang theo học tại Debrecen, trong đ&oacute; c&oacute; khoảng 70% l&agrave; con em c&aacute;c gia đ&igrave;nh y - b&aacute;c sĩ. Th&ocirc;ng tin về sinh vi&ecirc;n Việt Nam tại UOD, c&aacute;c bạn c&oacute; thể v&agrave;o forum&nbsp;<a class="Normal" href="http://vnexpress.net/gl/xa-hoi/co-hoi-du-hoc/2010/11/3ba236f8/vns.has.it">vns.has.it</a>.&nbsp;<br /><br />Nhằm hỗ trợ kiến thức cho c&aacute;c bạn học sinh trước kỳ thi tuyển v&agrave; chuẩn bị đi học tại UOD, Việt Nam Hợp Điểm mở c&aacute;c lớp &ocirc;n tập bằng tiếng Anh với c&aacute;c m&ocirc;n l&yacute;, h&oacute;a, sinh v&agrave; Anh văn. Cụ thể, sinh vi&ecirc;n sẽ được học với c&aacute;c m&ocirc;n l&yacute; - h&oacute;a - sinh bằng tiếng Anh, thời gian học l&agrave; 36 buổi, từ thứ hai - tư - s&aacute;u (17h30 - 19h30). Hạn ch&oacute;t đăng k&yacute; l&agrave; 30/11. Khai giảng v&agrave;o 1 v&agrave; 13/12. Đặc biệt, c&oacute; chương tr&igrave;nh ưu đ&atilde;i giảm ph&iacute; đặc biệt cho c&aacute;c bạn đăng k&yacute; trước ng&agrave;y 26/11.</p>\r\n<p class="Normal">Truy cập website:&nbsp;<a class="Normal" href="http://www.vietnamcentrepoint.edu.vn/debrecen">www.vietnamcentrepoint.edu.vn/debrecen</a>&nbsp;hoặc<a class="Normal" href="http://www.edu.dote.hu/">http://www.edu.dote.hu/</a>&nbsp;để c&oacute; th&ecirc;m th&ocirc;ng tin.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Văn ph&ograve;ng đại diện tuyển sinh Việt Nam Hợp Điểm</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">- 26 L&ecirc; Qu&yacute; Đ&ocirc;n, quận 3, TP HCM.<br />ĐT: (08) 39304812 - 39304992 gặp c&ocirc; Thảo (0984770587).<br />- 8E Lương Hữu Kh&aacute;nh, quận 1, TP HCM.<br />ĐT: (08) 39252602 - 39253183 gặp ch&uacute; Đức<br />Website:&nbsp;<a class="Normal" href="mailto:debrecenvn@vietnamhopdiem.edu.vn">debrecenvn@vietnamhopdiem.edu.vn</a></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Việt Nam Hợp Điểm</em>)</p>', 4, 1, 1505, 0, 1),
(160, 'Tư vấn chuyên ngành khoa học máy tính tại Anh', 'Hội thảo chủ đề “Học chuyên ngành khoa học máy tính tại Anh - những điều cần biết” được tổ chức ngày 22/11 từ 18h đến 19h30 tại Hội đồng Anh Hà Nội và từ 18h30 đến 20h tại khách sạn Norfolk, 117 Lê Thánh Tôn, quận 1, TP HCM.', 'hoi-dong-anh-1.jpg', NULL, '<h1 class="Title">Tư vấn chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh tại Anh</h1>\r\n<h2 class="Lead">Hội thảo chủ đề &ldquo;Học chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh tại Anh - những điều cần biết&rdquo; được tổ chức ng&agrave;y 22/11 từ 18h đến 19h30 tại Hội đồng Anh H&agrave; Nội v&agrave; từ 18h30 đến 20h tại kh&aacute;ch sạn Norfolk, 117 L&ecirc; Th&aacute;nh T&ocirc;n, quận 1, TP HCM.</h2>\r\n<p class="Normal">Chương tr&igrave;nh nhằm đem lại cho sinh vi&ecirc;n những kiến thức bổ &iacute;ch về c&aacute;ch chọn kho&aacute; học, chọn trường c&ugrave;ng những kỹ năng học tập trong lĩnh vực n&agrave;y th&ocirc;ng qua sự chia sẻ kinh nghiệm của c&aacute;c cựu du học sinh Anh.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hoi-dong-anh-1.jpg" border="1" alt="" width="400" height="267" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kể từ khi chiếc m&aacute;y vi t&iacute;nh đầu ti&ecirc;n tr&ecirc;n thế giới được ph&aacute;t minh bởi nh&agrave; to&aacute;n học người Anh Charles Babbage v&agrave;o những năm 1830, đến nay Anh vẫn lu&ocirc;n l&agrave; một trong những nước dẫn đầu thế giới trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; khoa học m&aacute;y t&iacute;nh. C&aacute;c trường đại học đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh n&agrave;y tại Anh lu&ocirc;n c&oacute; mối li&ecirc;n hệ, hợp t&aacute;c chặt chẽ với c&aacute;c tập đo&agrave;n h&agrave;ng đầu tr&ecirc;n thế giới, do đ&oacute; c&aacute;c kho&aacute; học tại Anh trong lĩnh vực n&agrave;y kh&ocirc;ng chỉ trau dồi cho bạn những kiến thức cập nhật nhất m&agrave; cả những kỹ năng về kinh doanh, quản l&yacute; dự &aacute;n v&agrave; l&agrave;m việc nh&oacute;m. Sinh vi&ecirc;n tốt nghiệp chuy&ecirc;n ng&agrave;nh n&agrave;y tại Anh lu&ocirc;n được c&aacute;c c&ocirc;ng ty h&agrave;ng đầu thế giới mở rộng cửa đ&oacute;n nhận.</p>\r\n<p class="Normal">Tham gia chương tr&igrave;nh UK Alumni Talk th&aacute;ng 11 của Hội đồng Anh chủ đề &ldquo;Học chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh tại Anh - Những điều cần biết&rdquo; c&oacute; c&aacute;c cựu du học sinh Anh xuất sắc trong ng&agrave;nh n&agrave;y gồm:</p>\r\n<p class="Normal">H&agrave; Nội: Nguyễn Thanh B&igrave;nh - Cựu sinh vi&ecirc;n đại học East of Anglia, bậc thạc sĩ v&agrave; tiến sĩ chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh. Đạt học bổng 322 của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo v&agrave; học bổng của tổ chức EPSR, Anh</p>\r\n<p class="Normal">L&ecirc; Việt Thắng - Cựu sinh vi&ecirc;n đại học Warwick, cử nh&acirc;n chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh. Từng c&ocirc;ng t&aacute;c tại ng&acirc;n h&agrave;ng UBS tại Anh.</p>\r\n<p class="Normal">TP HCM: Trần Huy Đức - Cựu sinh vi&ecirc;n đại học Northumbria, cử nh&acirc;n chuy&ecirc;n ng&agrave;nh c&ocirc;ng nghệ th&ocirc;ng tin tại Anh. Vừa ho&agrave;n th&agrave;nh kho&aacute; thạc sĩ c&ugrave;ng chuy&ecirc;n ng&agrave;nh tại Thụy Điển. Đức c&oacute; 4 năm kinh nghiệm l&agrave;m việc trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin tại nhiều tổ chức kh&aacute;c nhau v&agrave; hiện l&agrave; chuy&ecirc;n vi&ecirc;n ban dự &aacute;n của ng&acirc;n h&agrave;ng ANZ tại TP HCM.</p>\r\n<p class="Normal">Chương tr&igrave;nh được t&agrave;i trợ bởi đại học Central Lancashire, đại học East Anglia v&agrave; đại học West of England.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#efefef">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Hội đồng Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Điện thoại: HN 04 3843 6780 - TP HCM: 08 3823 2862</p>\r\n<p class="Normal">Emai: HN&nbsp;<a class="Normal" href="mailto:educationuk@britishcouncil.org.vn">educationuk@britishcouncil.org.vn</a>&nbsp;hoặc&nbsp;<br />Email TP HCM:&nbsp;<a class="Normal" href="mailto:educationuk.hcmc@britishcouncil.org.vn">educationuk.hcmc@britishcouncil.org.vn</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Hội đồng Anh</em>)</p>', 4, 1, 395, 0, 1),
(162, '100% học phí cho 7 trường ĐH tại Anh', 'Ông Tom Keenan, đại diện tuyển sinh của INTO sẽ có buổi nói chuyện về chương trình và cơ hội học bổng cùng Tập đoàn INTO vào 17h ngày 29/11 tại TP HCM và 15h ngày 30/11 tại Hà Nội.', 'duhoc-250.jpg', NULL, '<h1 class="Title">100% học ph&iacute; cho 7 trường ĐH tại Anh</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="duhoc-250.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">&Ocirc;ng Tom Keenan, đại diện tuyển sinh của INTO sẽ c&oacute; buổi n&oacute;i chuyện về chương tr&igrave;nh v&agrave; cơ hội học bổng c&ugrave;ng Tập đo&agrave;n INTO v&agrave;o 17h ng&agrave;y 29/11 tại TP HCM v&agrave; 15h ng&agrave;y 30/11 tại H&agrave; Nội.</h2>\r\n<p class="Normal">Chương tr&igrave;nh cụ thể:</p>\r\n<p class="Normal">17h, thứ hai ng&agrave;y 29/11, tại Visco 239 C&aacute;ch Mạng Th&aacute;ng T&aacute;m, nh&agrave; A2, chung cư Văn H&oacute;a, phường 4, quận 3, TP HCM v&agrave; 15h, thứ 3 ng&agrave;y 30/11, tại Visco 230 Kim M&atilde;, Ba Đ&igrave;nh, H&agrave; Nội.</p>\r\n<p class="Normal">Trung t&acirc;m INTO l&agrave; một phần của 7 trường đại học danh tiếng tại Anh: East Anglia, Exeter, Newcastle, Manchester, Glasgow Caledonian, City London v&agrave; Queen&rsquo;s Belfast, chịu tr&aacute;ch nhiệm đ&agrave;o tạo c&aacute;c kh&oacute;a tiếng Anh, A-level, dự bị đại học, năm nhất đại học, dự bị thạc sĩ cho sinh vi&ecirc;n quốc tế mong muốn v&agrave;o học tại c&aacute;c trường đại học tr&ecirc;n v&agrave; c&aacute;c trường đại học danh tiếng kh&aacute;c.</p>\r\n<p class="Normal">Trung t&acirc;m INTO đại học East Anglia đ&atilde; mở cơ sở mới tại London với đầy đủ c&aacute;c chương tr&igrave;nh đ&agrave;o tạo.</p>\r\n<p class="Normal">Hiện INTO d&agrave;nh những suất học bổng l&ecirc;n tới 100% học ph&iacute; cho kh&oacute;a dự bị đại học v&agrave; 30% học ph&iacute; cho c&aacute;c chương tr&igrave;nh A-level, năm thứ nhất đại học v&agrave; dự bị thạc sĩ, khai giảng th&aacute;ng 1 v&agrave; 9/2011.</p>\r\n<p class="Normal">Khi đến nộp hồ sơ, c&aacute;c em mang theo bản copy kết quả học tập mới nhất v&agrave; chứng chỉ tiếng Anh (nếu c&oacute;). Sinh vi&ecirc;n chưa c&oacute; điểm IELTS, c&oacute; thể l&agrave;m b&agrave;i thi tiếng Anh INTO thay thế.</p>\r\n<p class="Normal">Chương tr&igrave;nh học bổng như sau:</p>\r\n<p class="Normal">Chương tr&igrave;nh A-level tại đại học East Anglia v&agrave; đại học Manchester: Học bổng 10-30% học ph&iacute;, d&agrave;nh cho học sinh ho&agrave;n tất lớp 11, điểm trung b&igrave;nh 7 trở l&ecirc;n, IELTS 5-6.</p>\r\n<p class="Normal">Ho&agrave;n tất chương tr&igrave;nh A-level sinh vi&ecirc;n c&oacute; cơ hội học tại c&aacute;c trường đại học h&agrave;ng đầu của Anh: Oxford, Cambridge, LSE&hellip; v&agrave; c&aacute;c trường đại học danh tiếng kh&aacute;c tr&ecirc;n thế giới. Đặc biệt ở đ&acirc;y sinh vi&ecirc;n sẽ do c&aacute;c gi&aacute;o sư đại học giảng dạy v&agrave; được sử dụng cơ sở vật chất hiện đại của trường đại học.</p>\r\n<p class="Normal">Chương tr&igrave;nh dự bị đại học: Học bổng 10-100% học ph&iacute;, d&agrave;nh cho học sinh đ&atilde; ho&agrave;n tất lớp 11, điểm trung b&igrave;nh 7 trở l&ecirc;n, IELTS tối thiểu l&agrave; 5.</p>\r\n<p class="Normal">Chương tr&igrave;nh gồm c&aacute;c ng&agrave;nh học: Quản trị kinh doanh, kỹ sư m&aacute;y t&iacute;nh, y sinh học, h&oacute;a sinh, c&ocirc;ng nghệ m&ocirc;i trường, dược, to&aacute;n, kiến tr&uacute;c, luật, ch&iacute;nh trị&hellip; Sau khi ho&agrave;n tất kh&oacute;a học dự bị, sinh vi&ecirc;n được nhận v&agrave;o học thẳng năm nhất đại học tại c&aacute;c trường đại học của Into.</p>\r\n<p class="Normal">Chương tr&igrave;nh năm nhất đại học: Học bổng 10-30% học ph&iacute;, d&agrave;nh cho học sinh tốt nghiệp lớp 12, điểm trung b&igrave;nh 7 trở l&ecirc;n, IELTS tối thiểu 5,5 - 6. Đối với những trường hợp điểm trung b&igrave;nh lớp 12 dưới 7, th&igrave; phải l&agrave; sinh vi&ecirc;n của đại học hoặc cao đẳng tại Việt Nam.</p>\r\n<p class="Normal">Chương tr&igrave;nh gồm c&oacute; c&aacute;c ng&agrave;nh học: Kinh doanh, kinh tế, hệ thống th&ocirc;ng tin v&agrave; m&aacute;y t&iacute;nh&hellip; Sau khi ho&agrave;n tất kh&oacute;a học tại Into, học sinh được nhận học thẳng năm thứ hai tại một trong c&aacute;c trường đại học danh tiếng của INTO.</p>\r\n<p class="Normal">Chương tr&igrave;nh dự bị thạc sĩ: Học bổng 10-30% học ph&iacute;, d&agrave;nh cho sinh vi&ecirc;n tốt nghiệp đại học, cao đẳng, điểm trung b&igrave;nh từ kh&aacute; trở l&ecirc;n, IELTS tối thiểu 5,5 (cho kh&oacute;a học th&aacute;ng 9) v&agrave; 6 (cho kh&oacute;a học th&aacute;ng 1).</p>\r\n<p class="Normal">Chương tr&igrave;nh gồm c&aacute;c ng&agrave;nh học: Kinh doanh, t&agrave;i ch&iacute;nh, luật, khoa học tự nhi&ecirc;n, m&aacute;y t&iacute;nh, khoa học x&atilde; hội, kỹ thuật, ch&iacute;nh trị...</p>\r\n<p class="Normal">Ngo&agrave;i ra, INTO c&oacute; c&aacute;c kh&oacute;a tiếng Anh học thuật chuy&ecirc;n s&acirc;u khai giảng quanh năm d&agrave;nh cho sinh vi&ecirc;n chưa c&oacute; đủ tr&igrave;nh độ tiếng Anh học ch&iacute;nh kh&oacute;a.</p>\r\n<p class="Normal"><a class="Normal" href="http://vnexpress.net/GL/Xa-hoi/Co-hoi-Du-hoc/2010/11/3BA235EE/page_2.asp">Th&ocirc;ng tin chi tiết hơn về 7 trường đại học kể tr&ecirc;n</a></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style="color: #800000;">Li&ecirc;n hệ: Visco</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">- 230 Kim M&atilde;, Ba Đ&igrave;nh, H&agrave; Nội.<br />ĐT: (04) 3726 1938. E-mail:&nbsp;<a class="Normal" href="mailto:viscohanoi@visco.edu.vn">viscohanoi@visco.edu.vn</a><br />- 239 C&aacute;ch Mạng Th&aacute;ng T&aacute;m, Nh&agrave; A02, Chung cư văn ho&aacute;, phường 4, quận 3&nbsp;<br />ĐT: (08) 38328416. E-mail:&nbsp;<a class="Normal" href="mailto:viscohcm@visco.edu.vn">viscohcm@visco.edu.vn</a><br />- 309 Hai B&agrave; Trưng, C&aacute;t d&agrave;i, L&ecirc; Ch&acirc;n, Hải Ph&ograve;ng.<br />ĐT: (031) 3950748. Email:&nbsp;<a class="Normal" href="mailto:viscohp@visco.edu.vn">viscohp@visco.edu.vn</a><br />- 433 Phan Chu Trinh, Hải Ch&acirc;u, Đ&agrave; Nẵng.<br />ĐT: (0511) 3552596 - 3552597. E-mail: viscodn@visco.edu.vn<span style="color: #000000;"><br /></span>YM:&nbsp;<a class="Normal" href="mailto:duhocvisco@yahoo.com">duhocvisco@yahoo.com</a>&nbsp;<br />Th&ocirc;ng tin c&oacute; thể tham khảo th&ecirc;m tại:&nbsp;<a class="Normal" href="http://www.into.uk.com/">http://www.into.uk.com/</a>&nbsp;hoặc<a class="Normal" href="http://www.visco.edu.vn/">http://www.visco.edu.vn/</a></p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Visco</em>)</p>', 4, 1, 894, 0, 1),
(163, 'Vợ đánh hổ cứu chồng', 'Một phụ nữ ở phía bắc Malaysia đánh hổ bằng muỗng gỗ khi con thú tấn công chồng bà, nhờ đó người đàn ông thoát chết.', 'tiger.jpg', NULL, '<h1 class="Title">Vợ đ&aacute;nh hổ cứu chồng</h1>\r\n<h2 class="Lead">Một phụ nữ ở ph&iacute;a bắc Malaysia đ&aacute;nh hổ bằng muỗng gỗ khi con th&uacute; tấn c&ocirc;ng chồng b&agrave;, nhờ đ&oacute; người đ&agrave;n &ocirc;ng tho&aacute;t chết.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tiger.jpg" alt="Một con hổ tại Malaysia. Ảnh:" width="450" height="267" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con hổ tại Malaysia. Ảnh:&nbsp;<em>ecoficial.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tambun Gediu l&agrave; một người d&acirc;n sống s&aacute;t rừng ở bang Perak, Malaysia.&nbsp;<em>AP</em>&nbsp;h&ocirc;m qua dẫn lời một cảnh s&aacute;t giấu t&ecirc;n cho hay, &ocirc;ng bị một con hổ vồ khi săn thỏ trong rừng h&ocirc;m 12/2.</p>\r\n<p class="Normal">B&agrave; Han Besau, người vợ 55 tuổi của Gediu, chạy ra ngo&agrave;i với chiếc muỗng gỗ khi nghe tiếng h&eacute;t của chồng. Người phụ nữ đập chiếc muỗng v&agrave;o đầu con vật nhiều lần khiến n&oacute; hoảng sợ v&agrave; bỏ chạy.</p>\r\n<p class="Normal">Khi v&agrave;o viện để điều trị c&aacute;c vết r&aacute;ch tr&ecirc;n mặt v&agrave; đ&ocirc;i ch&acirc;n do m&oacute;ng vuốt của hổ g&acirc;y n&ecirc;n, Gediu kể rằng ống đ&atilde; cố leo l&ecirc;n c&acirc;y để trốn, song con hổ k&eacute;o &ocirc;ng xuống.</p>\r\n<p class="Normal">&ldquo;T&ocirc;i rất sợ v&agrave; d&ugrave;ng hết sức để đấm v&agrave;o mặt con hổ, song n&oacute; kh&ocirc;ng bu&ocirc;ng. T&ocirc;i cố gắng vật lộn để đẩy h&agrave;m răng của n&oacute; ra xa cơ thể t&ocirc;i. C&oacute; lẽ con hổ đ&atilde; t&aacute;t t&ocirc;i đến chết nếu vợ t&ocirc;i kh&ocirc;ng xuất hiện&rdquo;,&nbsp;<em>New Straits Times</em>&nbsp;dẫn lời kể của &ocirc;ng Gediu.</p>\r\n<p class="Normal">Shabrina Shariff, gi&aacute;m đốc Cơ quan bảo vệ động vật hoang d&atilde; của bang, n&oacute;i với nhật b&aacute;o&nbsp;<em>The Star</em>&nbsp;rằng lực lượng bảo vệ rừng sẽ truy t&igrave;m con hổ v&agrave; đuổi n&oacute; v&agrave;o s&acirc;u hơn trong rừng, nơi kh&ocirc;ng c&oacute; con người sinh sống.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Việt Linh</strong></p>', 5, 2, 1727, 0, 1),
(164, 'Nga đưa tên lửa tối tân lên đảo tranh chấp với Nhật', 'Matxcơva sẽ triển khai hệ thống tên lửa phòng không hiện đại tầm ngắn và trung lên các đảo tranh chấp với Nhật Bản, quan chức cấp cao của Bộ Tổng tham mưu Nga cho biết hôm nay.', 's400.jpg', NULL, '<h1 class="Title">Nga đưa t&ecirc;n lửa tối t&acirc;n l&ecirc;n đảo tranh chấp với Nhật</h1>\r\n<h2 class="Lead">Matxcơva sẽ triển khai hệ thống t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng hiện đại tầm ngắn v&agrave; trung l&ecirc;n c&aacute;c đảo tranh chấp với Nhật Bản, quan chức cấp cao của Bộ Tổng tham mưu Nga cho biết h&ocirc;m nay.</h2>\r\n<p class="Normal"><em>RIA Novosti</em>&nbsp;dẫn lời một quan chức cấp cao của qu&acirc;n đội Nga cho biết trong số c&aacute;c t&ecirc;n lửa sẽ được triển khai c&oacute; cả hệ thống S-400 Triumf mới nhất.</p>\r\n<p class="Normal">Nga v&agrave; Nhật đang tranh chấp chủ quyền của bốn h&ograve;n đảo ph&iacute;a nam thuộc quần đảo Kuril theo c&aacute;ch gọi của người Nga v&agrave; l&agrave; L&atilde;nh thổ ph&iacute;a bắc theo c&aacute;ch gọi của người Nhật.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="s400.jpg" border="1" alt="Hệ thống t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng S-400 của Nga. Ảnh: RIA Novosti." width="480" height="288" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hệ thống t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng S-400 của Nga. Ảnh: RIA Novosti.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tuần trước, Tổng thống Nga Dmitry Medvedev ph&aacute;t biểu trong một cuộc họp với Bộ trưởng Quốc ph&ograve;ng Anatoly Serdyukov v&agrave; Bộ trưởng ph&aacute;t triển c&aacute;c v&ugrave;ng Viktor Basagrin, rằng triển khai vũ kh&iacute; hiện đại l&agrave; điều bắt buộc để đảm bảo an ninh v&agrave; chủ quyền c&aacute;c đảo cho Nga.</p>\r\n<p class="Normal">D&agrave;n t&ecirc;n lửa S-400 được triển khai tr&ecirc;n c&aacute;c đảo tranh chấp l&agrave; nhằm "bảo vệ ch&uacute;ng khỏi những cuộc tấn c&ocirc;ng", quan chức cấp cao của Bộ Tổng tham mưu Nga cho biết.</p>\r\n<p class="Normal">Bốn h&ograve;n đảo n&oacute;i tr&ecirc;n do qu&acirc;n đội Li&ecirc;n X&ocirc; chiếm từ tay Nhật Bản v&agrave;o những ng&agrave;y cuối cuộc chiến tranh thế giới II. Tokyo đ&ograve;i chủ quyền với những đảo n&agrave;y. Tranh chấp k&eacute;o d&agrave;i hơn 60 năm nay khiến hai quốc gia chưa thể k&yacute; hiệp ước h&ograve;a b&igrave;nh.</p>\r\n<p class="Normal">Vấn đề n&oacute;ng l&ecirc;n trong thời gian gần đ&acirc;y, đặc biệt l&agrave; sau chuyến thăm đảo của đ&iacute;ch th&acirc;n Medvedev năm ngo&aacute;i. &Ocirc;ng Medvedev l&agrave; tổng thống Nga đầu ti&ecirc;n đặt ch&acirc;n l&ecirc;n một trong số c&aacute;c đảo tranh chấp n&agrave;y. Nhiều quan chức cấp cao kh&aacute;c của Nga cũng đ&atilde; c&oacute; h&agrave;nh động tương tự.</p>\r\n<p class="Normal">Ph&aacute;t biểu tại Tokyo đầu th&aacute;ng n&agrave;y, Thủ tướng Nhật Bản Naoto Kan chỉ tr&iacute;ch chuyến đi của Medvedev v&agrave; gọi đ&oacute; l&agrave; "một h&agrave;nh động kh&ocirc;ng thể tha thứ". Ngoại trưởng Nhật Bản trong chuyến thăm Matxcơva vừa rồi cũng n&ecirc;u vấn đề tranh chấp chủ quyền, nhưng đ&aacute;p lại l&agrave; th&aacute;i độ cứng rắn từ c&aacute;c quan chức Nga.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p>', 5, 2, 2904, 0, 1),
(165, 'Mỹ - Hàn sắp tập trận khổng lồ', 'Mỹ và Hàn Quốc sẽ tổ chức một cuộc diễn tập chung vào tháng này, cho dù căng thẳng với Triều Tiên vẫn kéo dài sau vụ đụng độ gần biên giới vào năm ngoái.', 'carrier.jpg', NULL, '<h1 class="Title">Mỹ - H&agrave;n sắp tập trận khổng lồ</h1>\r\n<h2 class="Lead">Mỹ v&agrave; H&agrave;n Quốc sẽ tổ chức một cuộc diễn tập chung v&agrave;o th&aacute;ng n&agrave;y, cho d&ugrave; căng thẳng với Triều Ti&ecirc;n vẫn k&eacute;o d&agrave;i sau vụ đụng độ gần bi&ecirc;n giới v&agrave;o năm ngo&aacute;i.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="carrier.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một t&agrave;u s&acirc;n bay lớn của Mỹ. Ảnh:&nbsp;<em>Yonhap</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cuộc tập trận mang t&ecirc;n Key Resolve/Foal Eagle bắt đầu v&agrave;o ng&agrave;y 28/2 v&agrave; c&oacute; sự tham gia của 12.800 l&iacute;nh Mỹ, Bộ chỉ huy lực lượng chung (CFC) cho biết. Khoảng 200.000 l&iacute;nh H&agrave;n Quốc cũng sẽ tham dự, bộ Quốc ph&ograve;ng nước n&agrave;y th&ocirc;ng b&aacute;o.</p>\r\n<p class="Normal">Key Resolve, cuộc diễn tập bao gồm c&aacute;c t&igrave;nh huống giả định tr&ecirc;n m&aacute;y t&iacute;nh, sẽ k&eacute;o d&agrave;i đến ng&agrave;y 10/3. C&ograve;n Foal Eagle, cuộc tập trận phối hợp tr&ecirc;n bộ, tr&ecirc;n kh&ocirc;ng v&agrave; dưới nước, sẽ kết th&uacute;c v&agrave;o ng&agrave;y 30/4.</p>\r\n<p class="Normal">Theo&nbsp;<em>Yonhap</em>, một t&agrave;u s&acirc;n bay Mỹ sẽ tham dự đợt tập trận n&agrave;y. Cũng như những năm trước, cuộc tập trận nhằm kiểm tra khả năng xử l&yacute; mọi bất ngờ c&oacute; thể xảy ra từ ph&iacute;a bắc.</p>\r\n<p class="Normal">Chương tr&igrave;nh tập huấn bao gồm xử l&yacute; c&aacute;c t&igrave;nh huống như bạo loạn, d&ograve; t&igrave;m vũ kh&iacute; hủy diệt, sự thay đổi chế độ bất ngờ tại nước ở ph&iacute;a bắc v&agrave; l&agrave;n s&oacute;ng di tản ồ ạt của d&acirc;n cư.</p>\r\n<p class="Normal">Tướng Mỹ Walter Sharp, đứng đầu lực lượng Mỹ tại H&agrave;n Quốc, cho biết cuộc tập trận nhằm tập luyện c&aacute;c phản ứng "trước một loạt viễn cảnh thực tế, kh&ocirc;ng chỉ l&agrave; đ&aacute;nh bại một cuộc tấn c&ocirc;ng th&ocirc;ng thường".</p>\r\n<p class="Normal">Theo&nbsp;<em>AFP</em>, vụ việc Triều Ti&ecirc;n n&atilde; ph&aacute;o v&agrave;o h&ograve;n đảo nằm ở bi&ecirc;n giới của H&agrave;n Quốc th&aacute;ng 11 năm ngo&aacute;i, khiến 4 người chết, đ&atilde; dấy l&ecirc;n nỗi lo về một cuộc chiến.</p>\r\n<p class="Normal">Seoul cũng buộc tội B&igrave;nh Nhưỡng d&ugrave;ng ngư l&ocirc;i đ&aacute;nh ch&igrave;m một t&agrave;u chiến ở gần v&ugrave;ng biển tranh chấp thuộc Ho&agrave;ng Hải v&agrave;o th&aacute;ng 3 năm ngo&aacute;i. Triều Ti&ecirc;n b&aacute;c bỏ c&aacute;o buộc n&agrave;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong></p>', 5, 2, 1409, 0, 1),
(167, 'Trung Quốc ủng hộ đại tướng trẻ của Triều Tiên', 'Một quan chức cấp cao của Trung Quốc bày tỏ sự ủng hộ đối với kế hoạch chuyển giao quyền lực của Chủ tịch Triều Tiên Kim Jong-il cho con trai út, hãng thông tấn chính thức của Triều Tiên cho hay.', 'jong-un.jpg', NULL, '<h1 class="Title">Trung Quốc ủng hộ đại tướng trẻ của Triều Ti&ecirc;n</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="jong-un.jpg" border="1" alt="Con trai &uacute;t của Chủ tịch Triều Ti&ecirc;n Kim Jong-un. Ảnh: AP" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con trai &uacute;t của Chủ tịch Triều Ti&ecirc;n Kim Jong-un. Ảnh: AP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Một quan chức cấp cao của Trung Quốc b&agrave;y tỏ sự ủng hộ đối với kế hoạch chuyển giao quyền lực của Chủ tịch Triều Ti&ecirc;n Kim Jong-il cho con trai &uacute;t, h&atilde;ng th&ocirc;ng tấn ch&iacute;nh thức của Triều Ti&ecirc;n cho hay.<br /><span style="font-size: 10px; font-weight: normal;">H&atilde;ng tin&nbsp;<em>KCNA</em>&nbsp;h&ocirc;m qua loan b&aacute;o &ocirc;ng Mạnh Kiến Trụ, Bộ trưởng C&ocirc;ng an v&agrave; l&agrave; ủy vi&ecirc;n Hội đồng Nh&agrave; nước Trung Quốc, "nồng nhiệt ch&uacute;c mừng" Kim Jong-il v&igrave; đ&atilde; t&aacute;i đắc cử trong đại hội đảng Lao động hồi th&aacute;ng 9, v&agrave; ch&uacute;c mừng Kim Jong-un được bầu l&agrave;m ph&oacute; chủ tịch qu&acirc;n ủy trung ương.</span></h2>\r\n<p class="Normal">&Ocirc;ng Mạnh ca ngợi "giải ph&aacute;p th&agrave;nh c&ocirc;ng cho vấn đề kế thừa c&aacute;ch mạng Triều Ti&ecirc;n", KCNA th&ocirc;ng b&aacute;o trong một lần hiếm hoi họ n&oacute;i trực tiếp đến vấn đề kế nhiệm.</p>\r\n<p class="Normal">&Ocirc;ng Mạnh đ&atilde; gặp chủ tịch Kim h&ocirc;m qua để chuyển "lời ch&uacute;c mừng th&acirc;n thiết" từ Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o v&agrave; c&aacute;c quan chức kh&aacute;c của Trung Quốc.</p>\r\n<p class="Normal">Bộ trưởng C&ocirc;ng an Trung Quốc đang ở chặng dừng ch&acirc;n đầu ti&ecirc;n trong chuyến c&ocirc;ng du c&aacute;c nước Triều Ti&ecirc;n, L&agrave;o, Singapore v&agrave; Malaysia,<em>&nbsp;AFP</em>&nbsp;cho hay. &Ocirc;ng cũng ch&uacute;c mừng Kim nh&acirc;n sinh nhật của chủ tịch, diễn ra ng&agrave;y mai; tặng qu&agrave; cho Kim v&agrave; đại tướng Jong-un.</p>\r\n<p class="Normal">Trung Quốc l&agrave; đồng minh th&acirc;n thiết nhất v&agrave; cũng l&agrave; đối t&aacute;c thương mại quan trọng nhất của Triều Ti&ecirc;n. Hồi năm ngo&aacute;i, trước khi đại hội đảng cầm quyền Triều Ti&ecirc;n diễn ra, &ocirc;ng Kim đ&atilde; tới thăm Trung Quốc. Trong đại hội, Kim Jong-un, t&acirc;n đại tướng được cho l&agrave; 27 tuổi v&agrave;o l&uacute;c đ&oacute;, được bầu v&agrave;o vị tr&iacute; l&atilde;nh đạo số 2 của qu&acirc;n đội Triều Ti&ecirc;n. Jong-un được giới quan s&aacute;t cho rằng sẽ kế nhiệm chức chủ tịch khi bố anh kh&ocirc;ng l&atilde;nh đạo nữa.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Thanh Mai</strong></p>', 5, 2, 2949, 0, 1),
(168, 'Biểu tình chống chính phủ lan khắp Trung Đông', 'Làn sóng biểu tình chống chính phủ đang lan từ Tunisia, Ai Cập sang Iran và Yemen.', 'i1.jpg', NULL, '<h1 class="Title">Biểu t&igrave;nh chống ch&iacute;nh phủ lan khắp Trung Đ&ocirc;ng</h1>\r\n<h2 class="Lead">L&agrave;n s&oacute;ng biểu t&igrave;nh chống ch&iacute;nh phủ đang lan từ Tunisia, Ai Cập sang Iran v&agrave; Yemen.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="i1.jpg" alt="Biểu t&igrave;nh tr&ecirc;n đường phố Iran." width="450" height="279" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Biểu t&igrave;nh tr&ecirc;n đường phố Iran. Ảnh:<em>&nbsp;AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại Iran, cuộc tuần h&agrave;nh ủng hộ phong tr&agrave;o nổi dậy của Ai Cập v&agrave; Tunisia biến th&agrave;nh biểu t&igrave;nh chống ch&iacute;nh phủ sau khi cảnh s&aacute;t d&ugrave;ng đến hơi cay v&agrave; đạn cao su để giải t&aacute;n đ&aacute;m đ&ocirc;ng.</p>\r\n<p class="Normal">Nhiều người tập trung th&agrave;nh c&aacute;c nh&oacute;m nhỏ v&agrave; tiến về ph&iacute;a quảng trường Azadi (Tự do). Khi họ bắt đầu h&ocirc; khẩu hiệu "Đả đảo độc t&agrave;i", cảnh s&aacute;t x&ocirc;ng v&agrave;o giải t&aacute;n đ&aacute;m đ&ocirc;ng. Một cuộc tuần h&agrave;nh kh&aacute;c diễn ra gần quảng trường Imam Khomeini,&nbsp;<em>AFP</em>cho hay.</p>\r\n<p class="Normal">Một số người đốt c&aacute;c th&ugrave;ng r&aacute;c tr&ecirc;n phố. Mạng điện thoại di động bị cắt v&agrave; một số khu vực nơi c&aacute;c cuộc biểu t&igrave;nh diễn ra bị mất điện. Một số nh&acirc;n chứng cho biết lực lượng an ninh Iran bắn hơi cay để giải t&aacute;n biểu t&igrave;nh. Đụng độ cũng nổ ra giữa cảnh s&aacute;t v&agrave; những người ủng hộ phe đối lập.</p>\r\n<p class="Normal">"Mubarak, Ben Ali v&agrave; giờ l&agrave; Sayed Ali!", một số người tuần h&agrave;nh h&ocirc; t&ecirc;n của cựu tổng thống Ai Cập, Tunisia v&agrave; nh&agrave; l&atilde;nh đạo tối cao Iran Ali Khamenei.</p>\r\n<p class="Normal">Trang web của nh&agrave; l&atilde;nh đạo phe đối lập Mir Hossein Mousavi dẫn những th&ocirc;ng tin chưa được x&aacute;c định cho hay h&agrave;ng trăm người biểu t&igrave;nh bị bắt. H&atilde;ng th&ocirc;ng tấn&nbsp;<em>Fars</em>dẫn nguồn tin giấu t&ecirc;n cho hay một người thiệt mạng v&agrave; nhiều người bị thương.</p>\r\n<p class="Normal">Ngoại trưởng Mỹ Hillary Clinton gọi h&agrave;nh động của những người tuần h&agrave;nh tại Iran l&agrave; "can đảm" v&agrave; "kh&aacute;t vọng", đồng thời k&ecirc;u gọi Iran "khai th&ocirc;ng" theo gương Ai Cập. "Ch&uacute;ng t&ocirc;i mong phe đối lập v&agrave; những con người dũng cảm tr&ecirc;n đường phố khắp đất nước Iran c&oacute; cơ hội như những người Ai Cập c&oacute; được tuần trước", b&agrave; Clinton n&oacute;i.</p>\r\n<p class="Normal">Tại trung t&acirc;m Sanaa, Yemen, h&ocirc;m qua, đụng độ giữa phe ủng hộ v&agrave; chống Tổng thống Ali Abdullah Saleh nổ ra. Khoảng 3.000 người tuần h&agrave;nh từ Đại học Sanaa đến quảng trường Al-Tahrir để y&ecirc;u cầu Saleh từ chức sau 32 năm cầm quyền.</p>\r\n<p class="Normal">C&aacute;c cuộc đụng độ giữa người biểu t&igrave;nh v&agrave; cảnh s&aacute;t cũng diễn ra ở th&agrave;nh phố Taez, ph&iacute;a nam thủ đ&ocirc; Sanaa, nơi h&agrave;ng ngh&igrave;n người tập trung phản đối &ocirc;ng Saleh.</p>\r\n<p class="Normal">C&aacute;c cuộc tuần h&agrave;nh rầm rộ tại Ai Cập v&agrave; Tunisia đ&atilde; khiến c&aacute;c nh&agrave; l&atilde;nh đạo hai nước n&agrave;y phải từ chức. Giới ph&acirc;n t&iacute;ch cho rằng c&aacute;c cuộc biểu t&igrave;nh lật đổ ch&iacute;nh phủ đ&oacute; c&oacute; thể lan truyền trong thế giới Ảrập.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 5, 2, 623, 0, 1),
(169, 'Chiến đấu cơ Thái Lan lao vào nhau', 'Hai phi cơ chiến đấu F-16 của Thái Lan lao vào nhau trong cuộc tập trận Hổ mang Vàng ở miền bắc nước này.', 'b1.jpg', NULL, '<h1 class="Title">Chiến đấu cơ Th&aacute;i Lan lao v&agrave;o nhau</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="b1.jpg" alt="M&aacute;y bay chiến đấu F-16. Ảnh: aviationspectator.com." width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&aacute;y bay chiến đấu F-16. Ảnh:<em>aviationspectator.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Hai phi cơ chiến đấu F-16 của Th&aacute;i Lan lao v&agrave;o nhau trong cuộc tập trận Hổ mang V&agrave;ng ở miền bắc nước n&agrave;y.<br />&gt;&nbsp;<a class="Lead" href="http://vnexpress.net/gl/the-gioi/anh/2011/02/cuoc-tap-tran-tren-bo-lon-nhat-the-gioi/">Ảnh tập trận</a></h2>\r\n<p class="Normal">Ph&aacute;t ng&ocirc;n vi&ecirc;n kh&ocirc;ng qu&acirc;n Th&aacute;i Lan Monthon Sanchukorn cho biết cả hai phi c&ocirc;ng kịp nhảy ra khỏi m&aacute;y bay v&agrave; tho&aacute;t chết.</p>\r\n<p class="Normal">"X&aacute;c hai phi cơ được t&igrave;m thấy cạnh nhau. Ch&uacute;ng t&ocirc;i cho rằng đ&acirc;y l&agrave; kết cục của một vụ va chạm d&ugrave; chưa c&oacute; th&ocirc;ng tin chi tiết",&nbsp;<em>Xinhua</em>&nbsp;dẫn lời &ocirc;ng cho hay.</p>\r\n<p class="Normal">Một chiếc trực thăng cứu hộ li&ecirc;n lạc với hai phi c&ocirc;ng v&agrave; đưa họ về căn cứ kh&ocirc;ng qu&acirc;n Nakhon Ratchasima, c&aacute;ch Bangkok khoảng 275 km về ph&iacute;a đ&ocirc;ng bắc.</p>\r\n<p class="Normal">Hai chiến đấu cơ kể tr&ecirc;n do Mỹ sản xuất v&agrave; c&ugrave;ng tham gia chiến dịch tập trận mang t&ecirc;n Hổ mang V&agrave;ng tại miền bắc Th&aacute;i Lan. F16 vẫn l&agrave; m&aacute;y bay chiến đấu chủ lực của kh&ocirc;ng qu&acirc;n ho&agrave;ng gia Th&aacute;i Lan v&agrave; phi đội n&agrave;y gồm hơn 30 chiếc.</p>\r\n<p class="Normal">Hồi th&aacute;ng 10/2010, một chiếc F-16 gặp nạn khiến phi c&ocirc;ng thiệt mạng. Phi cơ đ&oacute; bay c&ugrave;ng ba chiếc kh&aacute;c từ tỉnh Nakhon Sawan tới Chiang Mai v&agrave; rơi v&igrave; thời tiết xấu.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 5, 2, 613, 0, 1),
(170, 'Boeing công bố siêu phẩm bay liên lục địa', 'Nhà sản xuất máy bay Mỹ Boeing hôm qua công bố phiên bản mới của dòng 747 cỡ lớn mang tên Boeing 747-8 Intercontinental, với thiết kế tiện dụng và tiết kiệm nhiên liệu hơn.', 'B8.jpg', NULL, '<h1 class="Title">Boeing c&ocirc;ng bố si&ecirc;u phẩm bay li&ecirc;n lục địa</h1>\r\n<h2 class="Lead">Nh&agrave; sản xuất m&aacute;y bay Mỹ Boeing h&ocirc;m qua c&ocirc;ng bố phi&ecirc;n bản mới của d&ograve;ng 747 cỡ lớn mang t&ecirc;n Boeing 747-8 Intercontinental, với thiết kế tiện dụng v&agrave; tiết kiệm nhi&ecirc;n liệu hơn.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="B8.jpg" border="1" alt="Chiếc Boeing 747-8 Intercontinental trong lễ ra mắt. Ảnh: AFP" width="456" height="284" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chiếc Boeing 747-8 Intercontinental trong lễ ra mắt. Ảnh:&nbsp;<em>AFP</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Boeing 747-8 Intercontinental c&oacute; 467 chỗ ngồi, nhiều hơn 51 ghế so với những chiếc 747 đang khai th&aacute;c, trong khi lượng nhi&ecirc;n liệu lại ti&ecirc;u tốn &iacute;t hơn. Tuy vậy đơn đặt h&agrave;ng phi&ecirc;n bản m&aacute;y bay chở kh&aacute;ch n&agrave;y tương đối &iacute;t với 33 chiếc từ h&atilde;ng h&agrave;ng kh&ocirc;ng Đức Lufthansa v&agrave; H&agrave;n Quốc Korean Airlines.</p>\r\n<p class="Normal">Buổi lễ c&ocirc;ng bố phi&ecirc;n bản m&aacute;y bay mới diễn ra trước 10.000 nh&acirc;n vi&ecirc;n h&atilde;ng Boeing c&ugrave;ng người th&acirc;n v&agrave; kh&aacute;ch mời. Sự kiện được tổ chức tại nh&agrave; chứa m&aacute;y bay, nơi từng đ&aacute;nh dấu sự ra đời của chiếc Boeing 747 đời đầu ti&ecirc;n năm 1968.</p>\r\n<p class="Normal">"Trong tất cả những chiếc m&aacute;y bay ch&uacute;ng t&ocirc;i từng chế tạo, chỉ c&oacute; một chiếc gắn liền với t&ecirc;n tuổi của Boeing nhiều nhất v&agrave; đ&oacute; ch&iacute;nh l&agrave; d&ograve;ng 747", gi&aacute;m đốc bộ phận m&aacute;y bay thương mại của Boeing James Albaugh n&oacute;i với&nbsp;<em>BBC</em>.</p>\r\n<p class="Normal">Ngo&agrave;i khả năng chở nhiều kh&aacute;ch v&agrave; tiết kiện nhi&ecirc;n liệu hơn, Boeing 747-8 Intercontinental c&ograve;n c&oacute; một số cải tiến như thiết kế c&aacute;nh v&agrave; đu&ocirc;i mới, phần mũi gọn g&agrave;ng hơn, động cơ ti&ecirc;n tiến v&agrave; buồng l&aacute;i mới. D&ugrave; phi&ecirc;n bản n&agrave;y chở &iacute;t kh&aacute;ch hơn so với Airbus A380 nhưng l&agrave; m&aacute;y bay c&oacute; chiều d&agrave;i lớn nhất thế giới.</p>\r\n<p class="Normal">Phi&ecirc;n bản Boeing 747-8 Intercontinental chở kh&aacute;ch chưa bay thử, trong khi phi&ecirc;n bản chở h&agrave;ng của d&ograve;ng m&aacute;y bay n&agrave;y đ&atilde; được đưa v&agrave;o khai th&aacute;c v&agrave; b&aacute;n được 74 chiếc. Boeing tin tưởng rằng họ sẽ nhận được nhiều đơn đặt h&agrave;ng hơn khi phi&ecirc;n bản chở kh&aacute;ch đi v&agrave;o phục vụ cuối năm nay.</p>\r\n<p class="Normal">Chiếc Boeing 747-8 Intercontinental chở kh&aacute;ch đầu ti&ecirc;n sẽ được giao cho một kh&aacute;ch h&agrave;ng VIP giấu t&ecirc;n. H&atilde;ng cũng đang hy vọng đội bay hai chiếc chuy&ecirc;n cơ Air Force One của tổng thống Mỹ sẽ được thay thế bằng những chiếc Boeing 747-8.</p>\r\n<p class="Normal">Chiếc Boeing 747 đầu ti&ecirc;n ra mắt 42 năm trước v&agrave; b&aacute;n được hơn 1.400 chiếc cho đến khi h&atilde;ng ngừng b&aacute;n phi&ecirc;n bản n&agrave;y v&agrave;o năm ngo&aacute;i. Phi&ecirc;n bản mới của 747 được coi l&agrave; sản phẩm cạnh tranh của Boeing với chiếc A380 khổng lồ của đối thủ Airbus.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, giới ph&acirc;n t&iacute;ch cho rằng c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng đang quan t&acirc;m đến những chiếc m&aacute;y bay th&acirc;n rộng nhỏ hơn 747-8 v&agrave; A380 như Boeing 787 Dreamliner v&agrave; Airbus A350, vốn được thiết kế cho những s&acirc;n bay địa phương. Điều n&agrave;y gi&uacute;p c&aacute;c h&atilde;ng tr&aacute;nh được những trạm trung chuyển h&agrave;ng kh&ocirc;ng đ&ocirc;ng đ&uacute;c v&agrave; đưa h&agrave;nh kh&aacute;ch tới gần điểm đến cuối c&ugrave;ng của họ hơn.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Đ&igrave;nh Nguyễn</strong></p>', 5, 2, 1936, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(171, 'Người biểu tình Ai Cập giải tán', 'Hàng nghìn người biểu tình đã rời khỏi quảng trường Tahrir ở Cairo, Ai Cập, sau khi các nhà lãnh đạo quân sự mới cho biết sẽ giải tán quốc hội và tạm đình chỉ hiến pháp.', 'cairo.jpg', NULL, '<h1 class="Title">Người biểu t&igrave;nh Ai Cập giải t&aacute;n</h1>\r\n<h2 id="story_continues_1" class="Lead">H&agrave;ng ngh&igrave;n người biểu t&igrave;nh đ&atilde; rời khỏi quảng trường Tahrir ở Cairo, Ai Cập, sau khi c&aacute;c nh&agrave; l&atilde;nh đạo qu&acirc;n sự mới cho biết sẽ giải t&aacute;n quốc hội v&agrave; tạm đ&igrave;nh chỉ hiến ph&aacute;p.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cairo.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người biểu t&igrave;nh giơ c&aacute;c tấm ảnh chế giễu cựu tổng thống Mubarak tại quảng trường Tahrir ở Cairo, Ai Cập. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ủy ban qu&acirc;n sự tối cao c&ograve;n cho biết sẽ tạm điều h&agrave;nh đất nước trong 6 th&aacute;ng hoặc cho đến khi cuộc bầu cử diễn ra.</p>\r\n<p class="Normal">Tuy&ecirc;n bố n&agrave;y được rất nhiều người d&acirc;n hoan ngh&ecirc;nh như một dấu hiệu ph&aacute; vỡ chế độ cũ. Tổng thống Ai Cập Hosni Mubarak đ&atilde; từ chức h&ocirc;m 11/2 sau 18 ng&agrave;y biểu t&igrave;nh rầm rộ tr&ecirc;n to&agrave;n quốc.</p>\r\n<p class="Normal">H&ocirc;m nay, quốc gia n&agrave;y cũng tuy&ecirc;n bố ng&agrave;y lễ nh&agrave; băng nhằm đưa đất nước trở lại b&igrave;nh thường, sau khi c&aacute;c c&ocirc;ng nh&acirc;n l&agrave;m gi&aacute;n đoạn hoạt động tại c&aacute;c nh&agrave; băng của ch&iacute;nh phủ.</p>\r\n<p class="Normal">Qu&acirc;n đội cũng dự định cấm c&aacute;c cuộc họp của c&aacute;c li&ecirc;n đo&agrave;n lao động hay c&aacute;c tổ chức nghề nghiệp, để ngăn chặn đ&igrave;nh c&ocirc;ng, v&agrave; y&ecirc;u cầu mọi người d&acirc;n Ai Cập trở lại l&agrave;m việc.</p>\r\n<p class="Normal">Theo&nbsp;<em>BBC</em>, quốc gia n&agrave;y dường như đ&atilde; ổn định sau một thời gian d&agrave;i. Kh&ocirc;ng c&ograve;n những cuộc biểu t&igrave;nh rầm rộ, nhưng những cuộc tuần h&agrave;nh nhỏ vẫn tiếp diễn v&agrave; một cuộc tập trung lớn dự kiến diễn ra v&agrave;o 18/2.</p>\r\n<p class="Normal">Rất nhiều người Ai Cập đang bỏ việc, thậm ch&iacute; một số c&ocirc;ng an cũng đổ ra đường h&ocirc;m qua v&agrave; than phiền rằng họ bị ra lệnh phải trấn &aacute;p những người biểu t&igrave;nh v&agrave; họ c&ograve;n bị va chạm với qu&acirc;n đội.</p>\r\n<p id="story_continues_2" class="Normal">C&aacute;c ph&oacute;ng vi&ecirc;n cho biết một số nh&acirc;n vi&ecirc;n c&ograve;n tranh thủ sự thắng lợi của những người biểu t&igrave;nh m&agrave; đ&ograve;i sa thải c&aacute;c &ocirc;ng chủ đang g&acirc;y ra sự ch&ecirc;nh lệch thu nhập trong c&ocirc;ng ty.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong></p>', 5, 2, 1704, 0, 1),
(172, 'Người tình của tỷ phú hà tiện thua kiện', 'Tòa án Hong Kong hôm nay bác bỏ đơn kiện từ người tình của tỷ phú hà tiện Nina Wang. Ông này đòi thừa kế khối tài sản khổng lồ của bà. ', 'nn1.jpg', NULL, '<h1 class="Title">Người t&igrave;nh của tỷ ph&uacute; h&agrave; tiện thua kiện</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="nn1.jpg" alt="Tỷ ph&uacute; Nina Wang khi c&ograve;n sống v&agrave; người t&igrave;nh Tony Chan. Ảnh: Standdard." width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tỷ ph&uacute; Nina Wang khi c&ograve;n sống v&agrave; người t&igrave;nh Tony Chan. Ảnh:&nbsp;<em>Standdard.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">T&ograve;a &aacute;n Hong Kong h&ocirc;m nay b&aacute;c bỏ đơn kiện từ người t&igrave;nh của tỷ ph&uacute; h&agrave; tiện Nina Wang. &Ocirc;ng n&agrave;y đ&ograve;i thừa kế khối t&agrave;i sản khổng lồ của b&agrave;.&nbsp;<br /><span style="font-size: 10px; font-weight: normal;">Vụ kiện thu h&uacute;t sự ch&uacute; &yacute; của b&aacute;o giới suốt mấy th&aacute;ng qua khi Tony Chan, thầy phong thủy đồng thời l&agrave; người t&igrave;nh cũ của Wang, kiện tổ chức nh&acirc;n đạo m&agrave; anh chị em Wang điều h&agrave;nh. Chan đ&ograve;i quyền sở hữu đế chế bất động sản c&oacute; tổng trị gi&aacute; khoảng 13 tỷ USD, khối t&agrave;i sản từng khiến b&agrave; trở th&agrave;nh người phụ nữ gi&agrave;u nhất ch&acirc;u &Aacute;.</span></h2>\r\n<p class="Normal">Thẩm ph&aacute;n Anthony Rogers tuy&ecirc;n bố Chan theo đuổi vụ kiện ho&agrave;n to&agrave;n kh&ocirc;ng trung thực. "T&ograve;a kh&ocirc;ng chần chừ tuy&ecirc;n bố b&aacute;c bỏ vụ kiện n&agrave;y",&nbsp;<em>AFP&nbsp;</em>dẫn lời Rogers cho hay. Hiện chưa r&otilde; Chan c&oacute; khiếu kiện l&ecirc;n t&ograve;a &aacute;n cấp cao hơn hay kh&ocirc;ng.</p>\r\n<p class="Normal">Th&aacute;ng 2 năm ngo&aacute;i, thẩm ph&aacute;n t&ograve;a &aacute;n tối cao Hong Kong tuy&ecirc;n bố bản di ch&uacute;c m&agrave; Wang để lại t&agrave;i sản cho Chan bị l&agrave;m giả v&agrave; tổ chức nh&acirc;n đạo Chinachem thừa hưởng khối t&agrave;i sản đ&oacute;.</p>\r\n<p class="Normal">Trong phi&ecirc;n lấy lời khai th&aacute;ng trước, luật sư của Chan n&oacute;i rằng thẩm ph&aacute;n hiểu sai bằng chứng v&agrave; thi&ecirc;n vị v&igrave; kh&ocirc;ng th&iacute;ch chuyện ngoại t&igrave;nh giữa Wang v&agrave; Chan. Ngay sau ph&aacute;n quyết của t&ograve;a, cảnh s&aacute;t Hong Kong c&ograve;ng tay Chan v&igrave; nghi &ocirc;ng giả di ch&uacute;c nhưng rồi cho ph&eacute;p thầy b&oacute;i n&agrave;y tại ngoại với số tiền bảo l&atilde;nh 640.000 USD.</p>\r\n<p class="Normal">Nữ doanh nh&acirc;n c&oacute; kiểu ăn mặc kỳ dị v&agrave; bản t&iacute;nh h&agrave; tiện n&agrave;y qua đời năm 2007 v&igrave; ung thư, để lại khối t&agrave;i sản khổng lồ v&agrave; một cuộc chiến ph&aacute;p l&yacute; nhiều tiếng tăm. Nhiều người cho rằng Chan lợi dụng l&ograve;ng tin của Wang, hứa hẹn sẽ t&igrave;m được người chồng mất t&iacute;ch v&agrave; chữa bệnh ung thư cho tỷ ph&uacute; Wang để moi tiền.</p>\r\n<p class="Normal">Chồng của Nina Wang l&agrave; Teddy, chủ tịch tập đo&agrave;n Chinachem, bị bắt c&oacute;c năm 1990 v&agrave; bị tuy&ecirc;n l&agrave; đ&atilde; chết năm 1999. Tuy nhi&ecirc;n thi thể của &ocirc;ng chưa từng được t&igrave;m thấy. Vụ mất t&iacute;ch n&agrave;y ch&acirc;m ng&ograve;i một cuộc chiến ph&aacute;p l&yacute; giữa Nina Wang v&agrave; bố của Teddy nhằm tranh quyền kiểm so&aacute;t Chinachem. B&agrave; cũng chỉ mới thắng cuộc hai năm trước khi chết.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 5, 2, 2463, 0, 1),
(175, 'Phụ nữ Italy đòi Thủ tướng từ chức', 'Hàng chục nghìn phụ nữ Italy đổ ra đường tại hơn 60 đô thị trên khắp đất nước để yêu cầu Thủ tướng Silvio Berlusconi từ chức do tai tiếng tình ái.', 'be.jpg', NULL, '<h1 class="Title">Phụ nữ Italy đ&ograve;i Thủ tướng từ chức</h1>\r\n<h2 class="Lead">H&agrave;ng chục ngh&igrave;n phụ nữ Italy đổ ra đường tại hơn 60 đ&ocirc; thị tr&ecirc;n khắp đất nước để y&ecirc;u cầu Thủ tướng Silvio Berlusconi từ chức do tai tiếng t&igrave;nh &aacute;i.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="be.jpg" alt="Những người biểu t&igrave;nh" width="450" height="284" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những người biểu t&igrave;nh tuần h&agrave;nh tại th&agrave;nh phố Naples h&ocirc;m 13/2 để phản đối những vụ tai tiếng t&igrave;nh &aacute;i của Thủ tướng Berlusconi. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>BBC</em>&nbsp;đưa tin h&agrave;ng ngh&igrave;n người tuần h&agrave;nh tr&ecirc;n c&aacute;c đường phố tại hơn 60 thị trấn v&agrave; th&agrave;nh phố tr&ecirc;n to&agrave;n quốc, bao gồm Naples, Palermo, Bari, Trieste v&agrave; Venice. V&agrave;i chục ngh&igrave;n phụ nữ tập trung tại quảng trường ch&iacute;nh ở thủ đ&ocirc; Rome. Những người tổ chức cuộc biểu t&igrave;nh n&oacute;i &ocirc;ng Berlusconi đ&atilde; l&agrave;m hoen ố thanh danh, giảm gi&aacute; trị nh&acirc;n phẩm của phụ nữ Italy với những vụ tai tiếng t&igrave;nh &aacute;i của &ocirc;ng trong thời gian qua.</p>\r\n<p class="Normal">Nhiều phụ nữ nổi tiếng tham gia tuần h&agrave;nh, trong đ&oacute; c&oacute; Rosa Russo, nữ thị trưởng th&agrave;nh phố Naples. Họ mang theo biểu ngữ với nội dung k&ecirc;u gọi &ocirc;ng Berlusconi từ chức v&agrave; h&ocirc; vang c&aacute;c khẩu hiệu, như "Italy kh&ocirc;ng phải l&agrave; nh&agrave; thổ". Nam giới cũng tuần h&agrave;nh để b&agrave;y tỏ sự đo&agrave;n kết đối với phụ nữ.</p>\r\n<p class="Normal">C&aacute;c cuộc biểu t&igrave;nh phản đối &ocirc;ng Berlusconi cũng nổ ra tại c&aacute;c th&agrave;nh phố ở Nhật Bản, Mỹ v&agrave; nhiều nước ch&acirc;u &Acirc;u kh&aacute;c.</p>\r\n<p class="Normal">Hồi đầu th&aacute;ng 2 c&aacute;c c&ocirc;ng tố vi&ecirc;n tại th&agrave;nh phố Milan buộc tội Thủ tướng Italy trả tiền để quan hệ t&igrave;nh dục với Karima El Mahroug - một vũ nữ m&uacute;a bụng ở hộp đ&ecirc;m - v&agrave;o năm ngo&aacute;i, khi c&ocirc; g&aacute;i mới 17 tuổi. Quan hệ t&igrave;nh dục với người dưới 18 tuổi l&agrave; h&agrave;nh vi phạm ph&aacute;p tại Italy. Berlusconi khẳng định &ocirc;ng kh&ocirc;ng l&agrave;m điều g&igrave; sai tr&aacute;i, cho rằng c&aacute;c c&ocirc;ng tố vi&ecirc;n cố t&igrave;nh đưa ra lời buộc tội "đ&aacute;ng gh&ecirc; tởm" để buộc &ocirc;ng phải từ chức.</p>\r\n<p class="Normal">Một thẩm ph&aacute;n sẽ nghi&ecirc;n cứu c&aacute;c chứng cứ để xem t&ograve;a &aacute;n c&oacute; thể x&eacute;t xử vị thủ tướng 74 tuổi hay kh&ocirc;ng. Nếu bị kết tội, &ocirc;ng Berlusconi c&oacute; thể ngồi t&ugrave; tới 15 năm.</p>\r\n<p class="Normal">Bất chấp những vụ tai tiếng gần đ&acirc;y, tỷ lệ ủng hộ &ocirc;ng Berlusconi vẫn v&agrave;o khoảng 35%.</p>\r\n<p class="Normal">Luật ph&aacute;p Italy quy định c&aacute;c th&agrave;nh vi&ecirc;n ch&iacute;nh phủ kh&ocirc;ng bị x&eacute;t xử trong thời gian tại nhiệm. Song th&aacute;ng trước To&agrave; &aacute;n Hiến ph&aacute;p Italy đ&atilde; sửa đổi luật n&agrave;y. Theo nội dung của luật sửa đổi, c&aacute;c thẩm ph&aacute;n c&oacute; quyền x&eacute;t xử Thủ tướng v&agrave; c&aacute;c bộ trưởng trong thời gian họ đảm đương chức vụ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 5, 2, 2025, 0, 1),
(176, 'Biểu tình chống chính phủ nổ ra tại Thái Lan', 'Hàng nghìn người Thái Lan thuộc phe "áo đỏ" tuần hành tại thủ đô Bangkok hôm qua để gây sức ép đối với chính phủ.', 'red.jpg', NULL, '<h1 class="Title">Biểu t&igrave;nh chống ch&iacute;nh phủ nổ ra tại Th&aacute;i Lan</h1>\r\n<h2 class="Lead">H&agrave;ng ngh&igrave;n người Th&aacute;i Lan thuộc phe "&aacute;o đỏ" tuần h&agrave;nh tại thủ đ&ocirc; Bangkok h&ocirc;m qua để g&acirc;y sức &eacute;p đối với ch&iacute;nh phủ.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="red.jpg" alt="Một người biểu t&igrave;nh tuần h&agrave;nh bằng xe m&aacute;y tr&ecirc;n đường phố tại Bangkok h&ocirc;m 13/1. Ảnh: AFP." width="450" height="295" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người biểu t&igrave;nh tuần h&agrave;nh bằng xe m&aacute;y tr&ecirc;n đường phố tại Bangkok h&ocirc;m 13/1. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cảnh s&aacute;t Th&aacute;i Lan ước t&iacute;nh khoảng 25.000 người thuộc phong tr&agrave;o "&aacute;o đỏ" tham gia cuộc biểu t&igrave;nh. Họ tập trung gần Tượng đ&agrave;i D&acirc;n chủ để nghe cựu thủ tướng lưu vong Thaksin Shinawatra ph&aacute;t biểu qua điện thoại,&nbsp;<em>AFP</em>&nbsp;đưa tin.</p>\r\n<p class="Normal">"Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn g&igrave; ngo&agrave;i ho&agrave; b&igrave;nh, bầu cử tự do v&agrave; c&ocirc;ng bằng. Ch&uacute;ng t&ocirc;i muốn c&oacute; một ch&iacute;nh phủ do d&acirc;n bầu", &ocirc;ng Thaksin n&oacute;i.</p>\r\n<p class="Normal">Những người biểu t&igrave;nh cũng đặt hoa hồng trước cổng To&agrave; &aacute;n H&igrave;nh sự tại Bangkok, nơi người ta đọc một l&aacute; thư do c&aacute;c thủ lĩnh phe "&aacute;o đỏ" đang bị giam gửi.</p>\r\n<p class="Normal">Mới đ&acirc;y phe "&aacute;o v&agrave;ng" cũng biểu t&igrave;nh gần to&agrave; nh&agrave; ch&iacute;nh phủ. Họ k&ecirc;u gọi Thủ tướng Abhisit Vejjajiva từ chức do kh&ocirc;ng giải quyết được tranh chấp bi&ecirc;n giới với Campuchia.</p>\r\n<p class="Normal">Phe "&aacute;o v&agrave;ng" từng l&agrave; đồng minh của ch&iacute;nh phủ đương nhiệm, song mối quan hệ giữa hai b&ecirc;n trở n&ecirc;n xấu dần trong v&agrave;i th&aacute;ng qua v&agrave; "&aacute;o v&agrave;ng" đang trở th&agrave;nh lực lượng chống lại đảng D&acirc;n chủ cầm quyền. Tuần trước Thủ tướng Abhisit tuy&ecirc;n bố &ocirc;ng sẽ tổ chức tổng tuyển cử trong nửa đầu năm nay nếu bạo lực ch&iacute;nh trị kh&ocirc;ng xảy ra. Ch&iacute;nh phủ của &ocirc;ng cũng quyết định thực thi Luật An ninh nội địa tại Bangkok để đối ph&oacute; với những cuộc biểu t&igrave;nh ch&iacute;nh trị mới.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 5, 2, 827, 0, 1),
(177, 'Quân đội Ai Cập giải tán quốc hội', 'Hội đồng Tối cao quân đội Ai Cập hôm qua tuyên bố họ sẽ giải tán quốc hội và đình chỉ Hiến pháp, hai trong số các yêu cầu chủ yếu của những người biểu tình.', 'cyo.jpg', NULL, '<h1 class="Title">Qu&acirc;n đội Ai Cập giải t&aacute;n quốc hội</h1>\r\n<h2 class="Lead">Hội đồng Tối cao qu&acirc;n đội Ai Cập h&ocirc;m qua tuy&ecirc;n bố họ sẽ giải t&aacute;n quốc hội v&agrave; đ&igrave;nh chỉ Hiến ph&aacute;p, hai trong số c&aacute;c y&ecirc;u cầu chủ yếu của những người biểu t&igrave;nh.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cyo.jpg" alt="Binh sĩ k&ecirc;u gọi người d&acirc;n trong quảng trường Tự do ở thủ đ&ocirc; Cairo trở về nh&agrave; h&ocirc;m 13/2. Ảnh: AP." width="450" height="316" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Binh sĩ Ai Cập k&ecirc;u gọi người d&acirc;n trong quảng trường Tự do ở thủ đ&ocirc; Cairo trở về nh&agrave; h&ocirc;m 13/2. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>BBC</em>&nbsp;cho biết, người ph&aacute;t ng&ocirc;n của Hội đồng Tối cao Ai Cập th&ocirc;ng b&aacute;o tr&ecirc;n truyền h&igrave;nh rằng c&aacute;c tướng lĩnh sẽ điều h&agrave;nh đất nước trong 6 th&aacute;ng, tức l&agrave; tới khi cuộc bầu cử tổng thống được tổ chức.</p>\r\n<p class="Normal">Quyết định giải t&aacute;n của qu&acirc;n đội được c&ocirc;ng bố chỉ v&agrave;i ph&uacute;t sau khi Thủ tướng Ahmed Shafiq xuất hiện tr&ecirc;n truyền h&igrave;nh quốc gia v&agrave; n&oacute;i nền kinh tế Ai Cập đang ở trong t&igrave;nh trạng ổn định v&agrave; ưu ti&ecirc;n chủ yếu của ch&iacute;nh phủ tạm quyền l&agrave; kh&ocirc;i phục an ninh v&agrave; đưa cuộc sống của người d&acirc;n trở lại trạng th&aacute;i b&igrave;nh thường.</p>\r\n<p class="Normal">Trước đ&oacute; những người biểu t&igrave;nh th&uacute;c giục Hội đồng Tối cao qu&acirc;n đội Ai Cập khởi động qu&aacute; tr&igrave;nh chuyển giao quyền lực ngay lập tức bằng c&aacute;ch th&agrave;nh lập một hội đồng đảm đương tr&aacute;ch nhiệm của tổng thống, giải t&aacute;n quốc hội, thả mọi t&ugrave; nh&acirc;n ch&iacute;nh trị, b&atilde;i bỏ lệnh giới nghi&ecirc;m.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="span.jpg" alt="Một số người biểu t&igrave;nh kh&aacute;ng cự khi binh l&iacute;nh th&aacute;o dỡ lều tạm v&agrave; y&ecirc;u cầu họ rời khỏi quảng trường Tự do tại thủ đ&ocirc; Cairo h&ocirc;m 13/2. Ảnh: AP." width="450" height="248" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một số người biểu t&igrave;nh kh&aacute;ng cự khi binh l&iacute;nh v&agrave; cảnh s&aacute;t th&aacute;o dỡ lều tạm v&agrave; y&ecirc;u cầu họ rời khỏi quảng trường Tự do h&ocirc;m 13/2. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&ocirc;m qua binh l&iacute;nh th&aacute;o dỡ c&aacute;c lều tạm m&agrave; người biểu t&igrave;nh dựng l&ecirc;n trong quảng trường Tự do để tạo điệu kiện cho xe cộ lưu th&ocirc;ng.&nbsp;<span style="color: #000000;">Cảnh s&aacute;t, d&acirc;n thường v&agrave; binh l&iacute;nh kho&aacute;c vai nhau, tạo th&agrave;nh h&agrave;ng r&agrave;o ngăn chặn đ&aacute;m đ&ocirc;ng cản trở sự di chuyển của xe cộ,&nbsp;<em>The New York Times</em>&nbsp;đưa tin.</span></p>\r\n<p class="Normal">V&agrave;i trăm người biểu t&igrave;nh vẫn tiếp tục ở lại quảng trường. Họ n&oacute;i họ sẽ kh&ocirc;ng về nh&agrave; cho tới khi qu&acirc;n đội c&ocirc;ng bố lộ tr&igrave;nh cụ thể cho việc chuyển giao quyền lực cho ch&iacute;nh quyền d&acirc;n sự. Những người biểu t&igrave;nh cứng rắn đ&atilde; chống trả binh l&iacute;nh v&agrave; cảnh s&aacute;t khi lều của họ bị dỡ.</p>\r\n<p class="Normal">Qu&acirc;n đội Ai Cập đang nỗ lực trấn an người d&acirc;n v&agrave; thế giới rằng họ sẽ thực hiện qu&aacute; tr&igrave;nh chuyển giao quyền lực cho ch&iacute;nh quyền d&acirc;n sự th&ocirc;ng qua bầu cử v&agrave; t&ocirc;n trọng mọi cam kết quốc tế, bao gồm hiệp ước ho&agrave; b&igrave;nh với Israel. Tuy nhi&ecirc;n, giới quan s&aacute;t vẫn chưa đo&aacute;n được c&aacute;ch thức v&agrave; thời gian m&agrave; giới l&atilde;nh đạo qu&acirc;n sự sẽ gặp những thủ lĩnh của cuộc biểu t&igrave;nh để bắt đầu qu&aacute; tr&igrave;nh đưa c&aacute;c ch&iacute;nh trị gia đối lập v&agrave;o ch&iacute;nh phủ mới.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Việt Lin</strong></p>', 5, 2, 2262, 0, 1),
(180, 'Thoát nanh vuốt báo nhờ xe đạp', 'Gặp báo gấm khi đang đạp xe đi làm, người đàn ông ở Nam Phi dùng xe đạp để chống cự.', 'Leopard-Attack.jpg', NULL, '<h1 class="Title">Tho&aacute;t nanh vuốt b&aacute;o nhờ xe đạp</h1>\r\n<h2 class="Lead">Gặp b&aacute;o gấm khi đang đạp xe đi l&agrave;m, người đ&agrave;n &ocirc;ng ở Nam Phi d&ugrave;ng xe đạp để chống cự.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Leopard-Attack.jpg" alt="" width="450" height="281" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Pieta Ncube v&agrave; chiếc xe đạp m&agrave; anh d&ugrave;ng để chống trả b&aacute;o. Ảnh:&nbsp;<em>BARCROFT</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><span>Pieta Ncube - 39 tuổi v&agrave; sống tại th&agrave;nh phố Brits, Nam Phi - thường xuy&ecirc;n đạp xe qua một khu vực c&oacute; rừng để tới chỗ l&agrave;m việc. Ng&agrave;y 8/2, trong l&uacute;c đạp xe tr&ecirc;n đường anh nghe thấy một tiếng động sau lưng,&nbsp;<em>Telegraph</em>&nbsp;đưa tin.</span></p>\r\n<p class="Normal"><span>"T&ocirc;i giảm tốc độ để xem c&aacute;i g&igrave; ở ph&iacute;a sau v&agrave; thấy con b&aacute;o. N&oacute; cố gắng vồ lấy lưng t&ocirc;i", Ncube kể.</span></p>\r\n<p class="Normal"><span>Ncube nhảy xuống v&agrave; nhấc xe đạp l&ecirc;n để chống trả. Anh cố gắng để ch&acirc;n con b&aacute;o lọt v&agrave;o giữa c&aacute;c khe tr&ecirc;n khung xe để n&oacute; kh&ocirc;ng thể cử động tự do hoặc cắn anh. Sau đ&oacute; anh r&uacute;t thanh th&eacute;p m&agrave; anh c&agrave;i v&agrave;o khung từ trước rồi đập li&ecirc;n tiếp v&agrave;o đầu con b&aacute;o khi n&oacute; c&agrave;o v&agrave;o đầu v&agrave; mặt anh. Con vật bỏ chạy v&igrave; hoảng sợ. Ncube tự sơ cứu c&aacute;c vết thương rồi gọi điện thoại tới c&ocirc;ng ty để y&ecirc;u cầu gi&uacute;p đỡ.</span></p>\r\n<p class="Normal"><span>Cảnh s&aacute;t đang truy t&igrave;m con b&aacute;o. Ncube đ&atilde; xuất viện song phải ti&ecirc;m ba ng&agrave;y mỗi lần để ngăn chặn nguy cơ l&acirc;y một bệnh n&agrave;o đ&oacute; từ con vật.</span></p>\r\n<p class="Normal" style="text-align: right;"><span><strong>Việt Linh</strong></span></p>', 5, 2, 1624, 0, 1),
(182, 'Tuyết rơi muộn tại Bắc Kinh', 'Tuyết đổ xuống Bắc Kinh đêm qua và tiếp tục rơi trong sáng nay. Đây là đợt tuyết đổ xuống muộn nhất tại thành phố này trong 60 năm.', 'tuyet1.jpg', NULL, '<h1 class="Title">Tuyết rơi muộn tại Bắc Kinh</h1>\r\n<h2 class="Lead">Tuyết đổ xuống Bắc Kinh đ&ecirc;m qua v&agrave; tiếp tục rơi trong s&aacute;ng nay. Đ&acirc;y l&agrave; đợt tuyết đổ xuống muộn nhất tại th&agrave;nh phố n&agrave;y trong 60 năm.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tuyet1.jpg" border="1" alt="" width="480" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tuyết phủ trắng xe hơi tr&ecirc;n một con phố ở Bắc Kinh. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">M&ugrave;a đ&ocirc;ng n&agrave;y, Bắc Kinh chưa c&oacute; mưa v&agrave; tuyết trong 108 ng&agrave;y liền.</p>\r\n<p class="Normal">C&aacute;c nh&agrave; dự b&aacute;o thời tiết cho biết trong khoảng từ 8 giờ tối qua tới 6 giờ s&aacute;ng nay, lượng tuyết rơi trung b&igrave;nh ở Bắc Kinh l&agrave; 1 mm.</p>\r\n<p class="Normal">Những người d&acirc;n Bắc Kinh hồ hơi đ&oacute;n những hạt tuyết rơi đầu ti&ecirc;n. "Thế giới ch&igrave;m trong m&agrave;u trắng to&aacute;t", một người n&oacute;i. "Đ&oacute; l&agrave; cảnh thi&ecirc;n nhi&ecirc;n đẹp nhất trong m&ugrave;a đ&ocirc;ng".</p>\r\n<p class="Normal">Zhang Qiang, người đứng đầu cơ quan kh&iacute; hậu nh&acirc;n tạo, cho biết văn ph&ograve;ng đ&atilde; bắt đầu tạo m&acirc;y v&agrave; tiếp tục nỗ lực để gia tăng lượng tuyết rơi tại 9 khu vực ở Bắc Kinh.</p>\r\n<p class="Normal">Cũng theo c&aacute;c nh&agrave; dự b&aacute;o thời tiết, lượng tuyết đầu m&ugrave;a sẽ kết th&uacute;c v&agrave;o trưa nay. Đến đ&ecirc;m, trời nhiều m&acirc;y v&agrave; ng&agrave;y mai trời hửng nắng.</p>\r\n<p class="Normal">"Bởi lượng tuyết rơi &iacute;t n&ecirc;n n&oacute; sẽ &iacute;t c&oacute; t&aacute;c động tới t&igrave;nh trạng hạn h&aacute;n",&nbsp;<em>Xinhua</em>&nbsp;dẫn lời Song Jisong, người đứng đầu cơ quan kh&iacute; tượng thủy văn trung ương cho biết.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="60%" align="center" bgcolor="#ffff93">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" align="center"><a class="Normal" href="http://vnexpress.net/gl/the-gioi/cuoc-song-do-day/2011/02/tuyet-roi-muon-tai-bac-kinh/page_1.asp">Ảnh tuyết rơi muộn tại Bắc Kinh.</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đợt hạn h&aacute;n l&acirc;u nhất trong m&ugrave;a đ&ocirc;ng tại thủ đ&ocirc; Bắc Kinh l&agrave; v&agrave;o năm 1970/71, khi đ&oacute; trời kh&ocirc;ng mưa tuyết trong 114 ng&agrave;y liền. Đợt hạn h&aacute;n trong m&ugrave;a đ&ocirc;ng năm nay l&agrave; k&eacute;o d&agrave;i thứ hai trong 60 năm.</p>\r\n<p class="Normal">Tuyết l&agrave;m giảm nguy cơ hỏa hoạn, nhưng lại khiến giao th&ocirc;ng đ&igrave;nh trệ. C&aacute;c quan chức giao th&ocirc;ng cho biết xe cộ bị đi chậm lại chưa tới 20 km/giờ v&agrave;o giờ cao điểm h&ocirc;m nay, tr&ecirc;n hơn 40 đường cao tốc.</p>\r\n<p class="Normal">Khoảng 520 nh&acirc;n vi&ecirc;n vệ sinh phải l&agrave;m việc trong suốt gần 11 tiếng đồng hồ để dọn sạch đường phố, c&ugrave;ng với 350 xe ủi tuyết.</p>\r\n<p class="Normal">Ph&aacute;t ng&ocirc;n vi&ecirc;n của s&acirc;n bay quốc tế Bắc Kinh cho biết một số chuyến bay cũng bị trễ do tuyết rơi.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong></p>', 5, 2, 713, 0, 1),
(183, 'Buôn ma túy bằng máy lăng đá', 'Những phần tử buôn ma túy dùng một phát minh từ thời cổ đại để đưa cần sa từ Mexico sang Mỹ.\r\n', 'catapult2.jpg', NULL, '<h1 class="Title">Bu&ocirc;n ma t&uacute;y bằng m&aacute;y lăng đ&aacute;</h1>\r\n<h2 class="Lead">Những phần tử bu&ocirc;n ma t&uacute;y d&ugrave;ng một ph&aacute;t minh từ thời cổ đại để đưa cần sa từ Mexico sang Mỹ.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="catapult2.jpg" alt="" width="450" height="447" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một m&aacute;y lăng đ&aacute;. Ảnh:&nbsp;<em>wordpress.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Lực lượng vệ binh quốc gia Mỹ th&ocirc;ng b&aacute;o một số binh sĩ của họ nh&igrave;n thấy nhiều người b&ecirc;n ph&iacute;a Mexico d&ugrave;ng một s&uacute;ng lăng đ&aacute; để bắn c&aacute;c g&oacute;i qua h&agrave;ng r&agrave;o ngăn c&aacute;ch Mỹ với Mexico tại bang Arizona,&nbsp;<em>AP</em>&nbsp;đưa tin.</p>\r\n<p class="Normal">Một quan chức qu&acirc;n đội Mexico n&oacute;i binh sĩ nước n&agrave;y t&igrave;m thấy một m&aacute;y lăng đ&aacute; c&oacute; chiều cao khoảng 3 m tại một nơi c&aacute;ch bi&ecirc;n giới Mỹ khoảng 20 m. Chiếc m&aacute;y lăng đ&aacute; được đặt tr&ecirc;n một bệ phẳng do xe thể thao k&eacute;o.</p>\r\n<p class="Normal">Vị quan chức n&oacute;i n&oacute; c&oacute; thể quẳng một lượng cần sa c&oacute; khối lượng chừng hai kg trong mỗi lần bắn. Binh l&iacute;nh Mexico thu được 16 kg cần sa, xe hơi thể thao v&agrave; m&aacute;y lăng đ&aacute;. Những kẻ bu&ocirc;n ma t&uacute;y rời hiện trường trước binh l&iacute;nh tới. Giới chức Mexico c&ocirc;ng bố đoạn video ghi lại cảnh ch&uacute;ng d&ugrave;ng m&aacute;y lăng đ&aacute; để đưa cần sa sang Mỹ h&ocirc;m 26/1.</p>\r\n<p class="Normal">Đ&uacute;ng một ng&agrave;y sau người ta ph&aacute;t hiện một m&aacute;y lăng đ&aacute; kh&aacute;c gần Agua Prieta, một th&agrave;nh phố của Mexico v&agrave; nằm s&aacute;t bi&ecirc;n giới Mỹ. Một quan chức qu&acirc;n đội trong th&agrave;nh phố n&oacute;i một người giấu t&ecirc;n đ&atilde; th&ocirc;ng b&aacute;o vị tr&iacute; của m&aacute;y lăng đ&aacute; cho binh l&iacute;nh.</p>\r\n<p class="Normal">Giới chức Mexico khẳng định đ&acirc;y l&agrave; lần đầu ti&ecirc;n họ ph&aacute;t hiện phương ph&aacute;p n&eacute;m ma t&uacute;y qua bi&ecirc;n giới của bọn tội phạm. Trước đ&oacute; những băng đảng bu&ocirc;n lậu ma t&uacute;y tại Mexico từng d&ugrave;ng m&aacute;y bay, đường hầm, xe cơ giới, xuống cao tốc, bưu t&aacute; để vận chuyển ma t&uacute;y. Bọn x&atilde; hội đen Colombia c&ograve;n d&ugrave;ng cả t&agrave;u ngầm tự chế để vận chuyển &ldquo;h&agrave;ng trắng&rdquo;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 5, 2, 984, 0, 1),
(184, 'Thuê người chuyển giới làm tiếp viên hàng không', 'Một hãng hàng không mới thành lập của Thái Lan tuyển những người chuyển giới làm tiếp viên.\r\n', 'b11.jpg', NULL, '<h1 class="Title">Thu&ecirc; người chuyển giới l&agrave;m tiếp vi&ecirc;n h&agrave;ng kh&ocirc;ng</h1>\r\n<h2 class="Lead">Một h&atilde;ng h&agrave;ng kh&ocirc;ng mới th&agrave;nh lập của Th&aacute;i Lan tuyển những người chuyển giới l&agrave;m tiếp vi&ecirc;n.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="b11.jpg" alt="C&aacute;c ladyboy Th&aacute;i Lan. Ảnh: Alamy." width="450" height="391" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c ladyboy Th&aacute;i Lan. Ảnh:&nbsp;<em>Alamy.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">H&atilde;ng PC Air vừa chọn ba "ladyboy" (nam giới ăn mặc như phụ nữ) trong v&ograve;ng tuyển dụng đầu ti&ecirc;n. Những tiếp vi&ecirc;n chuyển giới n&agrave;y sẽ mang tấm biển t&ecirc;n đặc biệt m&agrave;u v&agrave;ng tr&ecirc;n đ&oacute; ghi họ thuộc giới t&iacute;nh thứ ba.</p>\r\n<p class="Normal">Peter Chan, gi&aacute;m đốc h&atilde;ng, rất h&agrave;o hứng với &yacute; tưởng n&agrave;y v&igrave; n&oacute; tạo cơ hội việc l&agrave;m cho những người chuyển giới. "Người ta c&oacute; thể l&agrave;m rất nhiều nghề, chứ kh&ocirc;ng chỉ trong lĩnh vực giải tr&iacute;, v&agrave; nhiều người trong số họ mơ ước trở th&agrave;nh tiếp vi&ecirc;n h&agrave;ng kh&ocirc;ng",&nbsp;<em>Telegraph</em>&nbsp;dẫn lời Chan cho hay.</p>\r\n<p class="Normal">Một trong số những ứng vi&ecirc;n th&agrave;nh c&ocirc;ng l&agrave; Thanyarat "Film" Jiraphatpakorn, người chiến thắng trong cuộc thi sắc đẹp của người chuyển giới năm 2007.</p>\r\n<p class="Normal">"L&uacute;c đầu t&ocirc;i tưởng họ chỉ nhận đơn vậy th&ocirc;i chứ kh&ocirc;ng tuyển ch&uacute;ng t&ocirc;i, như nhiều nơi kh&aacute;c từng l&agrave;m vậy", Thanyarat, 23 tuổi, cho biết v&agrave; n&oacute;i th&ecirc;m rằng c&ocirc; rất vui sướng v&igrave; được chọn.</p>\r\n<p class="Normal">Th&aacute;i Lan l&agrave; quốc gia c&oacute; số người chuyển giới cao nhất thế giới. Nhiều ladyboy kh&ocirc;ng l&agrave;m phẫu thuật v&igrave; tốn k&eacute;m hoặc phẫu thuật phức tạp nhưng ăn mặc như phụ nữ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 5, 2, 258, 0, 1),
(185, 'Ly dị vì mẹ chồng đi cùng trăng mật', 'Một phụ nữ Italy đệ đơn ly hôn chỉ một tháng sau khi đám cưới vì chồng mang theo mẹ chồng đi nghỉ trăng mật.', 'i1 (1).jpg', NULL, '<h1 class="Title">Ly dị v&igrave; mẹ chồng đi c&ugrave;ng trăng mật</h1>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\n<tbody>\n<tr>\n<td><img src="i1 (1).jpg" alt="Ảnh minh họa" width="250" height="195" /></td>\n</tr>\n<tr>\n<td class="Image">Ảnh minh họa của&nbsp;<em>Global and Mail</em>.</td>\n</tr>\n</tbody>\n</table>\n<h2 class="Lead">Một phụ nữ Italy đệ đơn ly h&ocirc;n chỉ một th&aacute;ng sau khi đ&aacute;m cưới v&igrave; chồng mang theo mẹ chồng đi nghỉ trăng mật.</h2>\n<p class="Normal">Khi đ&ocirc;i t&acirc;n lang t&acirc;n nương ra s&acirc;n bay để chuẩn bị đi Paris hưởng tuần trăng mật, c&ocirc; d&acirc;u Marianna C. sốc khi thấy mẹ chồng c&oacute; mặt sẵn ở s&acirc;n bay Fiumicino,<em>Telegraph</em>&nbsp;cho biết h&ocirc;m nay.</p>\n<p class="Normal">Marianna đ&atilde; t&igrave;m c&aacute;ch lịch sự phản đối việc mẹ chồng đi nghỉ c&ugrave;ng nhưng chồng c&ocirc; n&oacute;i rằng anh kh&ocirc;ng thể để người mẹ đang đau ốm ở nh&agrave; một m&igrave;nh được.</p>\n<p class="Normal">Sau kỳ trăng mật ba người, b&agrave; mẹ chồng tiếp tục nghỉ Gi&aacute;ng sinh c&ugrave;ng đ&ocirc;i vợ chồng mới cưới v&agrave; khiến Marianna ph&aacute;t đi&ecirc;n. C&ocirc; bỏ về nh&agrave; bố mẹ đẻ.</p>\n<p class="Normal">Trong đơn ly h&ocirc;n, c&ocirc; n&oacute;i rằng sự gắn b&oacute; qu&aacute; mức giữa chồng c&ocirc; với mẹ anh khiến việc x&acirc;y dựng mối quan hệ vợ chồng mật thiết l&agrave; kh&ocirc;ng tưởng.</p>\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 5, 2, 606, 0, 1),
(187, 'Cô gái rơi 23 tầng không chết', 'Một cô gái Argentina thoát chết thần kỳ khi rơi xuống từ tầng 23 của một khách sạn ở Buenos Aires và đáp trúng một chiếc taxi đậu ở dưới.', 'fall1.jpg', NULL, '<h1 class="Title">C&ocirc; g&aacute;i rơi 23 tầng kh&ocirc;ng chết</h1>\r\n<h2 class="Lead">Một c&ocirc; g&aacute;i Argentina tho&aacute;t chết thần kỳ khi rơi xuống từ tầng 23 của một kh&aacute;ch sạn ở Buenos Aires v&agrave; đ&aacute;p tr&uacute;ng một chiếc taxi đậu ở dưới.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fall1.jpg" border="1" alt="" width="460" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ocirc; g&aacute;i rơi xuống trong tư thế ngồi. Ảnh:&nbsp;<em>AAP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo&nbsp;<em>BBC</em>, c&ocirc; rơi đ&uacute;ng v&agrave;o ghế trước của taxi, l&agrave;m nắp xe bẹp r&uacute;m v&agrave; gương chắn gi&oacute; th&igrave; dập n&aacute;t. T&agrave;i xế may mắn vừa bước ra khỏi xe ngay trước khi sự cố xảy ra.</p>\r\n<p class="Normal">C&aacute;c nh&acirc;n chứng cho biết c&ocirc; g&aacute;i tr&egrave;o qua chấn song sắt v&agrave; nhảy từ cửa sổ một nh&agrave; h&agrave;ng nằm ở tầng tr&ecirc;n c&ugrave;ng của kh&aacute;ch sạn Crown Plaza Panamericano. C&ocirc; đ&atilde; được đưa đi cấp cứu để điều trị một số vết thương như g&atilde;y xương sườn, v&agrave; chảy m&aacute;u trong.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fall2.jpg" border="1" alt="" width="460" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chiếc taxi bị bẹp r&uacute;m. Ảnh:&nbsp;<em>AAP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&agrave;i xế xe kể lại về sự may mắn kh&ocirc;ng ngờ của m&igrave;nh. "T&ocirc;i vừa bước ra khỏi xe chỉ một gi&acirc;y trước đ&oacute;. Nếu kh&ocirc;ng kịp, c&oacute; thể t&ocirc;i đ&atilde; chết", Miguel n&oacute;i với&nbsp;<em>Radio 10</em>h&ocirc;m qua. "T&ocirc;i chỉ ở đ&oacute; c&aacute;ch đ&oacute; 10 m&eacute;t. Tiếng động l&uacute;c đ&oacute; thật kinh ho&agrave;ng".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fall3.jpg" border="1" alt="" width="460" height="308" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Taxi đậu trước cửa kh&aacute;ch sạn Panamericano. Ảnh:&nbsp;<em>AAP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kh&aacute;ch sạn Crown Plaza nằm đối diện Obelisk - một trong những điểm tham quan nổi tiếng nhất ở thủ đ&ocirc; Buenos Aires.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 185, 0, 1),
(188, 'Khai trương nhà hàng ''cao nhất thế giới''', 'Hôm qua, nhà hàng ở độ cao 422 m giữa bầu trời, nằm trên tầng 122 của tòa nhà cao nhất thế giới Burj Khalifa tại Dubai (UAE), đã mở cửa đón khách.', 'top.jpg', NULL, '<h1 class="Title">Khai trương nh&agrave; h&agrave;ng ''cao nhất thế giới''</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="top.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&agrave; h&agrave;ng Atmosphere ở Dubai. Ảnh:&nbsp;<em>ahlanlive.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">H&ocirc;m qua, nh&agrave; h&agrave;ng ở độ cao 422 m giữa bầu trời, nằm tr&ecirc;n tầng 122 của t&ograve;a nh&agrave; cao nhất thế giới Burj Khalifa tại Dubai (UAE), đ&atilde; mở cửa đ&oacute;n kh&aacute;ch.</h2>\r\n<p class="Normal">Qu&aacute;n ăn mang t&ecirc;n&nbsp;<em>Atmosphere</em>&nbsp;nằm trong t&ograve;a th&aacute;p cao 828 m l&agrave; "một trong những tiệm ăn xa hoa, sang trọng nhất tr&ecirc;n thế giới", tập đo&agrave;n x&acirc;y dựng c&ocirc;ng tr&igrave;nh Emaar Hospitality Group cho biết.</p>\r\n<p class="Normal">Theo&nbsp;<em>AFP</em>, nằm lơ lửng giữa trời, "qu&aacute;n ăn cao nhất thế giới" c&oacute; thể chứa tới 210 vị kh&aacute;ch v&agrave; c&oacute; một sảnh chờ rộng th&ecirc;nh thang, một khu vực ăn nằm ch&iacute;nh giữa, c&ugrave;ng nhiều ph&ograve;ng ri&ecirc;ng v&agrave; một kh&ocirc;ng gian nấu nướng rộng mở.</p>\r\n<p class="Normal">B&ecirc;n cạnh c&aacute;c văn ph&ograve;ng, căn hộ, th&aacute;p Burj Khalifa do kiến tr&uacute;c sư Italy Giorgio Armani thiết kế c&oacute; một tầng quan s&aacute;t nằm ở tầng thứ 124, cho ph&eacute;p kh&aacute;ch tham quan nh&igrave;n to&agrave;n cảnh th&agrave;nh phố Dubai với gi&aacute; v&eacute; l&agrave; 27 USD/người.</p>\r\n<p class="Normal">Tập đo&agrave;n Emaar đ&atilde; chi 1,5 tỷ USD để x&acirc;y dựng c&ocirc;ng tr&igrave;nh sang nhất thế giới n&agrave;y v&agrave; lấy t&ecirc;n của Tổng thống C&aacute;c tiểu vương quốc Ảrập thống nhất để đặt cho t&ograve;a th&aacute;p.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 3, 0, 1),
(190, 'Đeo mặt nạ Obama cướp nhà băng', 'Cảnh sát Áo đang săn tìm một tay súng đột nhập một ngân hàng địa phương cướp tiền, nhưng lại đeo mặt nạ Tổng thống Mỹ Barack Obama.', 'top_1.jpg', NULL, '<h1 class="Title">Đeo mặt nạ Obama cướp nh&agrave; băng</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="top_1.jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">T&ecirc;n cướp đeo mặt nạ. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Cảnh s&aacute;t &Aacute;o đang săn t&igrave;m một tay s&uacute;ng đột nhập một ng&acirc;n h&agrave;ng địa phương cướp tiền, nhưng lại đeo mặt nạ Tổng thống Mỹ Barack Obama.</h2>\r\n<p class="Normal">T&ecirc;n cướp "Obama" xuất hiện tại thị trấn Handenberg h&ocirc;m 20/1, cướp ng&acirc;n h&agrave;ng thứ s&aacute;u tại v&ugrave;ng Inn River kể từ năm 2008, cảnh s&aacute;t cho biết. Phương thức của t&ecirc;n n&agrave;y kh&ocirc;ng hề thay đổi, đều trốn chạy tr&ecirc;n những chiếc xe hơi.</p>\r\n<p class="Normal">Hắn xuất hiện với mặt nạ ngay trước giờ đ&oacute;ng cửa, mang theo một khẩu s&uacute;ng m&agrave;u bạc v&agrave; một t&uacute;i kho&aacute;c vai m&agrave;u đen.</p>\r\n<p class="Normal">Theo&nbsp;<em>BBC</em>, h&igrave;nh ảnh qua camera gi&aacute;m s&aacute;t cho thấy t&ecirc;n cướp bước v&agrave;o ng&acirc;n h&agrave;ng trong mặt nạ Obama v&agrave; đội mũ lưỡi trai, sau đ&oacute; chĩa s&uacute;ng v&agrave;o nh&acirc;n vi&ecirc;n phục vụ.</p>\r\n<p class="Normal">T&ecirc;n cướp n&oacute;i giọng địa phương v&agrave; c&oacute; thể đến từ v&ugrave;ng Inn River hoặc Bavaria, tại v&ugrave;ng bi&ecirc;n giới nước Đức, ph&aacute;t ng&ocirc;n vi&ecirc;n cảnh s&aacute;t &Aacute;o Mark Mitloehner cho biết.</p>\r\n<p class="Normal">Cảnh s&aacute;t tin rằng t&ecirc;n n&agrave;y đ&atilde; cướp 10.000 euro (13.000 USD) tại Handenberg. Trong khi đ&oacute;, mẻ lưới lớn nhất của hắn t&iacute;nh đến nay l&agrave; 40.000 euro v&agrave;o th&aacute;ng 7/2009. Trong những vụ cướp trước, tay s&uacute;ng chỉ đeo mặt nạ một người đ&agrave;n &ocirc;ng gi&agrave; nua l&agrave;m bằng cao su.</p>\r\n<p class="Normal">Chỉ đến th&aacute;ng 12/2009 hắn mới đeo mặt nh&agrave; của nh&agrave; l&atilde;nh đạo Mỹ để cướp ng&acirc;n h&agrave;ng ở Kirchheim. Th&aacute;ng 2/2010, hắn cũng định cướp nh&agrave; băng ở Kirchheim nhưng thất bại v&igrave; đến khi người ta đ&atilde; đ&oacute;ng cửa. Kh&ocirc;ng nản ch&iacute;, hắn lại cướp nh&agrave; băng thứ 6 tại St Johann im Walde v&agrave;o th&aacute;ng sau đ&oacute;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 197, 0, 1),
(191, 'Berlusconi và cuộc chiến với cáo buộc mua dâm', 'Với phán quyết mới của Toà án Hiến pháp Italy về tước quyền miễn tố, ông Berlusconi đứng trước nguy cơ phải ra tòa với tội danh liên quan đến sex khi đang tại nhiệm.', 'ruby.jpg', NULL, '<h1 class="Title">Berlusconi v&agrave; cuộc chiến với c&aacute;o buộc mua d&acirc;m</h1>\r\n<h2 class="Lead">Với ph&aacute;n quyết mới của To&agrave; &aacute;n Hiến ph&aacute;p Italy về tước quyền miễn tố, &ocirc;ng Berlusconi đứng trước nguy cơ phải ra t&ograve;a với tội danh li&ecirc;n quan đến sex khi đang tại nhiệm.</h2>\r\n<p class="Normal">Thủ tướng Italy, &ocirc;ng Silvio Berlusconi đ&atilde; từng bị điều tra li&ecirc;n quan đến v&ocirc; số "tộ danh cao cấp" như tham nhũng, gian lận thuế v&agrave; hối lộ kể từ lần đầu nhậm chức v&agrave;o năm 1994. V&agrave; giờ l&agrave; một tội danh kh&aacute;c, cổ xưa hơn nhiều: li&ecirc;n quan đến tội phạm t&igrave;nh dục. &Ocirc;ng tr&ugrave;m truyền th&ocirc;ng đang chịu sự điều tra với c&aacute;o buộc c&oacute; quan hệ th&acirc;n x&aacute;c với một g&aacute;i b&aacute;n d&acirc;m vị th&agrave;nh ni&ecirc;n v&agrave; lạm quyền trong việc cố gắng che đậy vụ việc.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ruby.jpg" border="1" alt="" width="450" height="306" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ruby (tr&aacute;i) v&agrave; Thủ tướng Italy Silvio Berlusconi. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tuần trước, To&agrave; &aacute;n Hiến ph&aacute;p Italy đ&atilde; tước bỏ quyền miễn tố của &ocirc;ng Berlusconi với việc sửa đổi đạo luật m&agrave; ch&iacute;nh phủ của &ocirc;ng đưa ra v&agrave; được Quốc hội nước n&agrave;y th&ocirc;ng qua v&agrave;o năm ngo&aacute;i.</p>\r\n<p class="Normal">C&aacute;c quan to&agrave; của th&agrave;nh phố Milan đ&atilde; kh&ocirc;ng ph&iacute; thời gian để đưa ph&aacute;n quyết mới n&agrave;y v&agrave;o thực tế: chỉ một ng&agrave;y sau, họ đ&atilde; ch&iacute;nh thức th&ocirc;ng b&aacute;o với &ocirc;ng Berlusconi rằng &ocirc;ng đang bị điều tra v&agrave; triệu tập để cung cấp bằng chứng cho c&aacute;c nh&agrave; điều tra, những người n&oacute;i họ đ&atilde; c&oacute; chứng cứ quan trọng chứng minh tội lỗi của Thủ tướng.</p>\r\n<p class="Normal">Nếu bị kết tội, &ocirc;ng sẽ đối mặt với mức &aacute;n 15 năm t&ugrave;. Berlusconi cũng đang li&ecirc;n quan đến 3 phi&ecirc;n to&agrave; chưa xử về gian lận thuế v&agrave; tham nhũng. Hai trong số c&aacute;c vụ đ&oacute; đ&atilde; tạm ngừng khi luật miễn tố c&oacute; hiệu lực v&agrave; một vụ kh&aacute;c đang chờ c&aacute;o trạng.</p>\r\n<p class="Normal">C&aacute;c c&ocirc;ng tố vi&ecirc;n th&agrave;nh phố Milan cho rằng vị thủ tướng 74 tuổi đ&atilde; trả tiền để quan hệ t&igrave;nh dục nhiều lần v&agrave;o m&ugrave;a xu&acirc;n năm ngo&aacute;i với vũ nữ người Moroco c&oacute; t&ecirc;n l&agrave; Ruby, l&uacute;c đ&oacute; 17 tuổi. Theo một t&agrave;i liệu của to&agrave; &aacute;n m&agrave; tạp ch&iacute;&nbsp;<em>Newsweek</em>&nbsp;c&oacute; được th&igrave; th&aacute;ng 5 năm ngo&aacute;i, thủ tướng đ&atilde; g&acirc;y &aacute;p lực với cảnh s&aacute;t th&agrave;nh phố Milan nhằm che giấu mối quan hệ với Ruby.</p>\r\n<p class="Normal">C&aacute;c hoạt động hỗ trợ cho g&aacute;i mại d&acirc;m kh&ocirc;ng vi phạm ph&aacute;p luật Italy, ngoại trừ người đ&oacute; l&agrave; vị th&agrave;nh ni&ecirc;n. Tuổi kết h&ocirc;n ở nước n&agrave;y l&agrave; 14 tuổi nhưng g&aacute;i mại d&acirc;m &iacute;t nhất phải từ 18 tuổi trở l&ecirc;n. C&aacute;c quan chức hay lui tới g&aacute;i mại d&acirc;m vị th&agrave;nh ni&ecirc;n c&ograve;n chịu th&ecirc;m thời hạn t&ugrave; nếu bị kết &aacute;n.</p>\r\n<p class="Normal">Xuất ph&aacute;t của vụ b&ecirc; bối n&agrave;y l&agrave; một cuộc gọi đ&ecirc;m khuya của &ocirc;ng Berlusconi tới một đồn cảnh s&aacute;t th&agrave;nh phố Milan sau khi Ruby bị bắt do bị t&igrave;nh nghi c&oacute; h&agrave;nh vi trộm cắp. Sau cuộc gọi n&agrave;y, c&ocirc; g&aacute;i đ&atilde; được thả v&agrave; được một cộng sự của &ocirc;ng Berlusconi, Nicole Minetti, cựu phụ t&aacute; nha sĩ của Thủ tướng v&agrave; đại diện cho đảng ch&iacute;nh trị của &ocirc;ng ở ch&iacute;nh quyền địa phương Milan. Bản th&acirc;n Minetti cũng đang bị điều tra trong một vụ &aacute;n ri&ecirc;ng v&igrave; c&aacute;o buộc t&igrave;m g&aacute;i mại d&acirc;m cho Thủ tướng.</p>\r\n<p class="Normal">&Ocirc;ng Berlusconi thừa nhận c&oacute; gọi điện cho cảnh s&aacute;t để bảo l&atilde;nh cho Ruby, người m&agrave; &ocirc;ng m&ocirc; tả l&agrave; &ldquo;người c&oacute; &iacute;t may mắn&rdquo; nhưng &ocirc;ng từ b&aacute;c bỏ &yacute; kiến cho rằng l&yacute; do việc can thiệp l&agrave; do sự gắn b&oacute; giữa hai người trong mối quan hệ t&igrave;nh-tiền.</p>\r\n<p class="Normal">&ldquo;T&ocirc;i l&agrave; một người h&agrave;o hiệp&rdquo;, Berlusconi đ&atilde; trả lời c&aacute;c ph&oacute;ng vi&ecirc;n sau vụ việc. &ldquo;T&ocirc;i h&agrave;nh động bất cứ l&uacute;c n&agrave;o c&oacute; người cần gi&uacute;p đỡ&rdquo;.</p>\r\n<p class="Normal">Ruby, t&ecirc;n thật l&agrave; Karima el-Mahroug v&agrave; hiện 18 tuổi, được cho l&agrave; đ&atilde; nhận nhiều tiền từ mối quan hệ của c&ocirc; với Berlusconi. C&ocirc; mi&ecirc;u tả kỹ c&agrave;ng việc m&igrave;nh tham dự c&aacute;c bữa tiệc tại biệt thự của &ocirc;ng Berlusconi ở Milan, nơi m&agrave; như c&ocirc; n&oacute;i, c&ocirc; tham dự v&agrave;o một nghi thức t&igrave;nh &aacute;i c&oacute; t&ecirc;n l&agrave; &ldquo;bunga-bunga&rdquo; trong một bể bơi. C&ocirc; thừa nhận với cảnh s&aacute;t l&agrave; m&igrave;nh đ&atilde; n&oacute;i dối &ocirc;ng Berlusconi về tuổi v&agrave; được trả 9.400 USD cho những lần tham dự tiệc.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n Ruby khẳng định kh&ocirc;ng quan hệ t&igrave;nh dục với Thủ tướng, cũng như &ocirc;ng Berlusconi lu&ocirc;n cho rằng m&igrave;nh chưa bao giờ trả tiền v&igrave; quan hệ t&igrave;nh dục v&agrave; &ldquo;kh&ocirc;ng c&oacute; kho&aacute;i cảm nếu kh&ocirc;ng c&oacute; sự chinh phục&rdquo;. Luật sư của &ocirc;ng, Niccolo Ghedini n&oacute;i cuộc điều tra tr&ecirc;n l&agrave; &ldquo;sự can thiệp nghi&ecirc;m trọng đến đời tư Thủ tướng, chưa c&oacute; tiền lệ trong lịch sử luật ph&aacute;p ở quốc gia n&agrave;y&rdquo;.</p>\r\n<p class="Normal">C&acirc;u chuyện phi&ecirc;u lưu t&igrave;nh &aacute;i mới nhất của Thủ tướng Italy kh&ocirc;ng phải l&agrave; một th&ocirc;ng tin tốt l&agrave;nh cho những người ủng hộ đ&atilde; s&aacute;t c&aacute;nh c&ugrave;ng &ocirc;ng.. Berlusconi chiến thắng trong một cuộc bỏ phiếu bất t&iacute;n nhiệm với chiến thắng s&iacute;t sao chỉ 3 phiếu v&agrave;o ng&agrave;y 14/12 vừa qua, l&agrave;m dấy l&ecirc;n c&aacute;o buộc &ocirc;ng đ&atilde; cho c&aacute;c thuộc hạ m&igrave;nh d&agrave;n xếp mua phiếu.</p>\r\n<p class="Normal">Mất quyền miễn tố, tấm &aacute;o gi&aacute;p của &ocirc;ng Berlusconi dường như c&oacute; những lỗ thủng. To&agrave; &aacute;n Hiến ph&aacute;p nước n&agrave;y ph&aacute;n quyết rằng đạo luật được Quốc hội do đảng của &ocirc;ng Berlusconi chiếm ưu thế th&ocirc;ng qua năm ngo&aacute;i đ&atilde; vi hiến. Quy định trong đạo luật n&agrave;y đ&atilde; bảo vệ c&aacute;c quan chức cao cấp được hưởng quyền miễn tố khi tại nhiệm. Nhưng 15 th&agrave;nh vi&ecirc;n To&agrave; &aacute;n Hiến ph&aacute;p ph&aacute;n quyết rằng c&aacute;c thẩm ph&aacute;n trong vụ việc c&oacute; quyền quyết định liệu một quan chức &ndash; chẳng hạn như &ocirc;ng Berlusconi - c&oacute; được tấm l&aacute; chắn hợp ph&aacute;p để tr&aacute;nh t&ograve;a &aacute;n hay kh&ocirc;ng</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, vụ b&ecirc; bối mới n&agrave;y dường như cũng kh&ocirc;ng đồng nghĩa với dấu chấm hết cho ch&agrave;ng &ldquo;il Cavaliere&rdquo; (tiếng Italy nghĩa l&agrave; Hiệp sĩ - &aacute;m chỉ &ocirc;ng Berlusconi). Tất cả cuộc thăm d&ograve; dư luận cho thấy nếu c&aacute;c cuộc bầu cử được tổ chức sớm v&agrave;o m&ugrave;a xu&acirc;n, &ocirc;ng Berlusconi gi&agrave;nh chiến thắng &aacute;p đảo.</p>\r\n<p class="Normal">Daniele Capezzone, người ph&aacute;t ng&ocirc;n của &ocirc;ng. n&oacute;i c&aacute;c lời c&aacute;o buộc mới hiện nay l&agrave; một phần của chiến dịch b&ocirc;i nhọ do phe đối lập yếu k&eacute;m tiến h&agrave;nh sau khi đ&atilde; kh&ocirc;ng thể lật đổ &ocirc;ng bằng h&ograve;m phiếu. Capezzone n&oacute;i &ldquo;Đối diện với kịch b&agrave;n cũ kỹ được tạo ra bởi c&aacute;c th&ocirc;ng tin r&ograve; rỉ v&agrave; lời buộc tội vu vơ, người d&acirc;n Italy c&oacute; thể th&ecirc;m một lần nữa lựa chọn c&aacute;ch phẫn nộ hay ng&aacute;n ngẩm&rdquo;. V&igrave; thế nếu ai tin rằng Berlusconi c&oacute; thể bị đ&aacute;nh bại th&ocirc;ng qua x&eacute;t xử c&oacute; thể sẽ th&ecirc;m một lần nữa thất vọng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Trần T&uacute;</strong>&nbsp;(theo&nbsp;<em>Newsweek</em>)</p>', 5, 2, 2076, 0, 1),
(192, 'Bạch tuộc Paul được dựng miếu thờ', 'Con bạch tuộc Paul nổi tiếng sau kỳ World Cup mùa hè năm ngoái sẽ được dựng miếu thờ tại bể sinh thái ở Đức, nhằm đánh dấu 3 tháng kể từ ngày nó qua đời.', 'paul.jpg', NULL, '<h1 class="Title">Bạch tuộc Paul được dựng miếu thờ</h1>\r\n<h2 class="Lead">Con bạch tuộc Paul nổi tiếng sau kỳ World Cup m&ugrave;a h&egrave; năm ngo&aacute;i sẽ được dựng miếu thờ tại bể sinh th&aacute;i ở Đức, nhằm đ&aacute;nh dấu 3 th&aacute;ng kể từ ng&agrave;y n&oacute; qua đời.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paul.jpg" border="1" alt="" width="468" height="268" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bạch tuộc Paul. Ảnh:&nbsp;<em>DPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&aacute;c quan chức tại Viện Hải dương học Oberhausen h&ocirc;m qua cho biết nh&agrave; ti&ecirc;n tri 8 ch&acirc;n sẽ được vinh danh bằng một miếu thờ mang t&ecirc;n "Paul Corner" tại ch&iacute;nh nh&agrave; cũ của con vật, trong đ&oacute; c&oacute; chứa t&agrave;n tro v&agrave; một tượng đ&agrave;i lớn.</p>\r\n<p class="Normal">"Sẽ c&oacute; một bức tượng Paul cao 1,8 m, đặt tr&ecirc;n một quả b&oacute;ng, ở giữa l&agrave; tấm k&iacute;nh trong suốt để mọi người c&oacute; thể nh&igrave;n r&otilde; b&igrave;nh dựng di cốt của Paul", ph&aacute;t ng&ocirc;n vi&ecirc;n Tanja Munzig cho&nbsp;<em>AFP</em>&nbsp;biết.</p>\r\n<p class="Normal">Bạch tuộc Paul đ&atilde; l&agrave;m cả thế giới kinh ngạc khi đo&aacute;n tr&uacute;ng kết quả tỷ số mọi trận đấu m&agrave; n&oacute; được đo&aacute;n, trong kỳ World Cup 2010 tại Nam Phi hồi th&aacute;ng 6-7/2010.</p>\r\n<p class="Normal">Khả năng dự đo&aacute;n t&agrave;i t&igrave;nh của con vật đưa n&oacute; trở th&agrave;nh ng&ocirc;i sao to&agrave;n cầu. Con vật qua đời y&ecirc;n b&igrave;nh trong giấc ngủ khi gần 3 tuổi v&agrave;o th&aacute;ng 10 năm ngo&aacute;i, v&agrave; nhận được h&agrave;ng trăm lời chia buồn từ c&aacute;c fan trong Hội người h&acirc;m mộ tr&ecirc;n Facebook, với số th&agrave;nh vi&ecirc;n l&ecirc;n tới 200.000.</p>\r\n<p class="Normal">Ng&agrave;y 3/11, Bể sinh học tại Đức đ&atilde; thay thế con vật bằng một ch&uacute; bạch tuộc đến từ Ph&aacute;p v&agrave; mang t&ecirc;n Paul II. Con vật 8 ch&acirc;n mới n&agrave;y chưa được thử nghiệm t&agrave;i dự đo&aacute;n nhưng người ta mong đợi n&oacute; sẽ trổ t&agrave;i trong giải v&ocirc; địch b&oacute;ng đ&aacute; ch&acirc;u &Acirc;u tại Ukraine v&agrave; Ba Lan v&agrave;o năm 2012.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong></p>', 5, 2, 1166, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(193, 'Nhọc nhằn đường về quê của người Trung Quốc', 'Ngày Tết sắp đến gần, Yang Yongfen cùng với hàng triệu người ở khắp Trung Quốc chuẩn bị trở về nhà, trong cuộc di chuyển hằng năm đông nhất của loài người, để chào đón năm mới.', 'train.jpg', NULL, '<h1 class="Title">Nhọc nhằn đường về qu&ecirc; của người Trung Quốc</h1>\n<h2 class="Lead">Ng&agrave;y Tết sắp đến gần, Yang Yongfen c&ugrave;ng với h&agrave;ng triệu người ở khắp Trung Quốc chuẩn bị trở về nh&agrave;, trong cuộc di chuyển hằng năm đ&ocirc;ng nhất của lo&agrave;i người, để ch&agrave;o đ&oacute;n năm mới.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="train.jpg" border="1" alt="" width="480" height="320" /></td>\n</tr>\n<tr>\n<td class="Image">H&agrave;nh kh&aacute;ch nh&iacute; tr&ecirc;n một chuyến t&agrave;u hỏa của Trung Quốc. Ảnh:&nbsp;<em>China Daily</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Yang phải mất &iacute;t nhất 30 tiếng để trải qua qu&atilde;ng đường d&agrave;i 1.946 km từ H&agrave;ng Ch&acirc;u, thủ phủ tỉnh Chiết Giang, tới An Thuận, thuộc tỉnh Qu&yacute; Ch&acirc;u, đi bằng t&agrave;u hỏa.</p>\n<p class="Normal">C&ocirc; c&ugrave;ng cậu con trai 7 tuổi, em g&aacute;i v&agrave; ch&aacute;u g&aacute;i (con của anh ruột), sẽ chui v&agrave;o một kh&ocirc;ng gian rộng 1 m2 tr&ecirc;n mỗi toa t&agrave;u v&agrave; ngồi chung tr&ecirc;n hai chiếc ghế gấp.</p>\n<p class="Normal">Yang l&ecirc;n chuyến t&agrave;u ng&agrave;y 31/12/2010, 20 ng&agrave;y trước khi tết Nguy&ecirc;n Đ&aacute;n, nhưng vẫn rất kh&oacute; khăn trong việc t&igrave;m chỗ ngồi. Kể cả khi con t&agrave;u đ&atilde; bắt đầu chuyển b&aacute;nh, v&agrave;i h&agrave;nh kh&aacute;ch v&aacute;c theo những t&uacute;i x&aacute;ch to sụ tr&ecirc;n đầu, vẫn cố gắng l&aacute;ch qua đ&aacute;m đ&ocirc;ng để t&igrave;m nơi đặt h&agrave;nh l&yacute;.</p>\n<p class="Normal">Hầu hết h&agrave;nh kh&aacute;ch n&oacute;i tiếng địa phương th&ocirc;ng dụng ở v&ugrave;ng t&acirc;y nam Trung Quốc, trong đ&oacute; c&oacute; tỉnh Tứ Xuy&ecirc;n v&agrave; Qu&yacute; Ch&acirc;u - l&agrave; nơi c&oacute; h&agrave;ng triệu người d&acirc;n đổ ra th&agrave;nh thị l&agrave;m việc.</p>\n<p class="Normal">Với những người thu nhập thấp th&igrave; chi ph&iacute; đi lại l&agrave; cả một chuyện đau đầu v&igrave; suy t&iacute;nh. Yang, b&agrave; mẹ 37 tuổi ăn mặc giản đơn, l&agrave;m c&ocirc;ng nh&acirc;n tại H&agrave;ng Ch&acirc;u, đ&atilde; chọn chuyến t&agrave;u số 1271 bởi đ&oacute; l&agrave; c&aacute;ch rẻ nhất để với nh&agrave;, với gi&aacute; v&eacute; đứng l&agrave; 107 nh&acirc;n d&acirc;n tệ (16,24 USD).</p>\n<p class="Normal">Nếu chọn giường nằm, Yang phải mất th&ecirc;m 210 nh&acirc;n d&acirc;n tệ, nhưng "ch&uacute;ng t&ocirc;i muốn tiết kiệm tiền", c&ocirc; n&oacute;i.</p>\n<p class="Normal">V&eacute; t&agrave;u hỏa của Trung Quốc - c&oacute; m&agrave;u điển h&igrave;nh l&agrave; xanh l&aacute; c&acirc;y - l&agrave; rẻ nhất so với c&aacute;c loại t&agrave;u kh&aacute;c, nhưng t&agrave;u kh&ocirc;ng c&oacute; hệ thống sưởi v&agrave; đi l&acirc;u hơn rất nhiều so với c&aacute;c phương tiện kh&aacute;c.</p>\n<p class="Normal">"T&ocirc;i kh&ocirc;ng bận t&acirc;m tới thời gian", Jiang Bo, một thanh ni&ecirc;n 23 tuổi n&oacute;i. Anh mất 44 tiếng ngồi tr&ecirc;n t&agrave;u để về nh&agrave; ở th&agrave;nh phố Tự Cống, tỉnh Tứ Xuy&ecirc;n.</p>\n<p class="Normal">Anh cũng c&oacute; thể mất th&ecirc;m 200 nh&acirc;n t&acirc;n tệ nếu muốn đi t&agrave;u nhanh rồi chuyển sang xe kh&aacute;ch để về nh&agrave;. Nhưng anh n&oacute;i đ&oacute; kh&ocirc;ng phải l&agrave; một giải ph&aacute;p kinh tế, bởi thu nhập của anh tại nh&agrave; m&aacute;y dệt may chưa tới 100 tệ/ng&agrave;y.</p>\n<p class="Normal">Jiang Long, một ch&agrave;ng trai 21 tuổi, chỉ c&oacute; một khoảng trống đủ để đứng, vui vẻ n&oacute;i: "Năm ngo&aacute;i, t&ocirc;i đi t&agrave;u n&agrave;y n&oacute; đ&ocirc;ng hơn nhiều. T&ocirc;i c&ograve;n kh&ocirc;ng thể c&uacute;i người xuống. Giờ đ&atilde; kh&aacute; hơn nhiều".</p>\n<p class="Normal">So với t&agrave;u tốc h&agrave;nh, t&agrave;u hỏa Trung Quốc kh&ocirc;ng chỉ chạy chậm m&agrave; c&ograve;n tốn nhiều thời gian. T&agrave;u dừng tại nhiều ga hơn - tại c&aacute;c thị trấn v&agrave; th&agrave;nh phố nhỏ - trong khi t&agrave;u tốc h&agrave;nh th&igrave; kh&ocirc;ng dừng. Nhưng nếu kh&ocirc;ng c&oacute; loại t&agrave;u n&agrave;y, h&agrave;nh kh&aacute;ch sẽ phải tới c&aacute;c th&agrave;nh phố lớn để bắt t&agrave;u tốc h&agrave;nh - một giải ph&aacute;p vừa tốn k&eacute;m vừa mất thời gian.</p>\n<p class="Normal">Trung Quốc đang n&acirc;ng cấp hệ thống đường sắt tr&ecirc;n to&agrave;n quốc v&agrave; những con t&agrave;u &igrave; ạch n&agrave;y đang dần bị loại trừ. C&oacute; 7 t&agrave;u hỏa đi v&agrave;o Bắc Kinh đ&atilde; bị dừng hoạt động v&agrave;o th&aacute;ng 7/2010.</p>\n<p class="Normal">Một b&agrave;i b&aacute;o tr&ecirc;n tờ&nbsp;<em>Beijing Times</em>&nbsp;ng&agrave;y 29/6/2010, tr&iacute;ch lời của Cục ph&oacute; cục đường sắt Bắc Kinh Liu Ruiyang cho biết, bắt đầu từ 1/7/2010, mọi t&agrave;u hỏa v&agrave;o Bắc Kinh, Thượng Hải v&agrave; Quảng Ch&acirc;u sẽ được thay thế.</p>\n<p class="Normal">Nhưng sự n&acirc;ng cấp n&agrave;y cũng đồng nghĩa với gi&aacute; v&eacute; tăng. Gi&aacute; v&eacute; cao nhất tr&ecirc;n một t&agrave;u tốc h&agrave;nh, bắt đầu hoạt động v&agrave;o ng&agrave;y 11/1 nối Thượng Hải với Th&agrave;nh Đ&ocirc;, l&agrave; 2.330 nh&acirc;n d&acirc;n tệ (353, 64 USD), trong khi gi&aacute; v&eacute; rẻ nhất l&agrave; 501 nh&acirc;n d&acirc;n tệ.</p>\n<p class="Normal">Phản ứng trước sự gia tăng gi&aacute; v&eacute;, Luo Yinguo - một c&ocirc;ng nh&acirc;n 53 tuổi tại nh&agrave; m&aacute;y da ở &Ocirc;n Ch&acirc;u, tỉnh Chiết Giang, n&oacute;i: "Đ&oacute; l&agrave; t&agrave;u 5 sao chắc?".</p>\n<p class="Normal">Luo n&oacute;i &ocirc;ng kh&ocirc;ng d&aacute;m nghĩ tới n&oacute; bởi gi&aacute; v&eacute; tương đương với thu nhập hằng th&aacute;ng của &ocirc;ng.</p>\n<p class="Normal">Nhưng" t&agrave;u tốc h&agrave;nh l&agrave; một chiến lược quốc gia, nhằm đ&aacute;p ứng nhu cầu của sự ph&aacute;t triển kinh tế v&agrave; x&atilde; hội", Xia Xueluan - gi&aacute;o sư x&atilde; hội học tại Đại học Bắc Kinh n&oacute;i.</p>\n<p class="Normal">Theo Ji Jialun tại Đại học Giao th&ocirc;ng Bắc Kinh: "Sự ph&aacute;t triển đường sắt cao tốc của Trung Quốc sẽ mở ra nhiều kh&ocirc;ng gian hơn cho t&agrave;u chở h&agrave;ng".</p>\n<p class="Normal">"Hiện, khả năng vận tải h&agrave;ng h&oacute;a bằng đường sắt chỉ đ&aacute;p ứng 35% nhu cầu của quốc gia. Để một t&agrave;u chở kh&aacute;ch c&oacute; thể hoạt động, ch&uacute;ng t&ocirc;i phải hủy bỏ hai t&agrave;u chở h&agrave;ng".</p>\n<p class="Normal">Hiện tại, mọi tuyến đường sắt, trừ những tuyến d&agrave;nh ri&ecirc;ng cho t&agrave;u tốc h&agrave;nh, được vận h&agrave;nh cho cả t&agrave;u chở h&agrave;ng v&agrave; t&agrave;u chở kh&aacute;ch, v&igrave; vậy m&agrave; hạn chế sự ph&aacute;t triển của ng&agrave;nh vận tải h&agrave;ng h&oacute;a.</p>\n<p class="Normal">"Việc loại bỏ những con t&agrave;u chậm chạp sẽ mở ra kh&ocirc;ng gian cho t&agrave;u chở h&agrave;ng", Ji n&oacute;i.</p>\n<p class="Normal">Gi&aacute;o sư Hu Xingdou, tại Viện C&ocirc;ng nghệ Bắc Kinh, th&igrave; nhận thấy cả hệ thống t&agrave;u chở h&agrave;ng lẫn chở kh&aacute;ch đều cần được mở rộng. Hu cho rằng việc x&acirc;y dựng hệ thống đường sắt ở v&ugrave;ng trung t&acirc;y Trung Quốc chưa đủ để đ&aacute;p ứng nhu cầu. Vấn đề sẽ c&agrave;ng tồi tệ hơn nếu nhiều t&agrave;u chở kh&aacute;ch bị loại ra khỏi khu vực.</p>\n<p class="Normal">Thực tế, đ&acirc;y l&agrave; v&ugrave;ng c&oacute; d&acirc;n cư thu nhập thấp đ&ocirc;ng nhất Trung Quốc v&agrave; tạo ra nhu cầu lớn nhất cho t&agrave;u hỏa. Một số chuy&ecirc;n gia th&igrave; cho rằng c&oacute; thể d&ugrave;ng xe kh&aacute;ch thay cho t&agrave;u hỏa. Nhưng Luo Yinguo, c&ocirc;ng nh&acirc;n nh&agrave; m&aacute;y da ở &Ocirc;n Ch&acirc;u, th&igrave; b&aacute;c bỏ: "Qu&aacute; đắt".</p>\n<p class="Normal">Một chuyến xe kh&aacute;ch về qu&ecirc; anh ở tỉnh V&acirc;n Nam mất hơn 350 nh&acirc;n d&acirc;n tệ. V&agrave; v&agrave;o m&ugrave;a du lịch, như ng&agrave;y tết, gi&aacute; v&eacute; c&oacute; thể tăng tới 800 nh&acirc;n d&acirc;n tệ. Trong khi đ&oacute;, v&eacute; t&agrave;u hỏa như t&agrave;u 1271 chỉ c&oacute; 200 nh&acirc;n d&acirc;n tệ. Đi xe kh&aacute;ch cũng kh&ocirc;ng an to&agrave;n bằng đi t&agrave;u, Luo n&oacute;i th&ecirc;m.</p>\n<p class="Normal">Tuy nhi&ecirc;n, gi&aacute;o sư Xia tại Đại học Bắc Kinh tin rằng khi chất lượng cuộc sống tăng l&ecirc;n th&igrave; nhiều người sẽ dần chấp nhận gi&aacute; v&eacute; t&agrave;u tốc h&agrave;nh. "Đ&oacute; chỉ l&agrave; vấn đề thời gian", &ocirc;ng n&oacute;i.</p>\n<p class="Normal">Nhưng b&ecirc;n cạnh gi&aacute; tiền, một điều kh&aacute;c Luo quan t&acirc;m l&agrave; liệu con t&agrave;u mới c&oacute; dừng lại tại qu&ecirc; nh&agrave; anh hay kh&ocirc;ng.</p>\n<p class="Normal">Tr&ecirc;n tuyến đường nối Vũ H&aacute;n, tỉnh Hồ Bắc, với Quảng Ch&acirc;u, tỉnh Quảng Đ&ocirc;ng, t&agrave;u th&ocirc;ng thường dừng lại tại 26 điểm, nhưng t&agrave;u cao tốc thế hệ mới chỉ dừng lại tại 4 điểm. Hệ thống trạm ga d&agrave;y đặc ở c&aacute;c tỉnh th&agrave;nh cũng l&agrave; một l&yacute; do gi&aacute;o sư Hu tại Viện C&ocirc;ng nghệ Bắc Kinh cho rằng n&ecirc;n duy tr&igrave; t&agrave;u hỏa.</p>\n<p class="Normal">Đồng &yacute; kiến với &ocirc;ng, Ji tại Đại học Giao th&ocirc;ng Bắc Kinh, cho rằng một số tuyến đường sắt m&agrave; kh&ocirc;ng c&oacute; nhiều nhu cầu vận tải h&agrave;ng h&oacute;a, n&ecirc;n được duy tr&igrave; cho t&agrave;u hỏa. Khi đ&oacute;, b&ecirc;n cạnh mạng lưới đường sắt cao tốc, những người lao động vẫn c&oacute; thể lựa chọn tuyến t&agrave;u hỏa quen thuộc của họ.</p>\n<p class="Normal">Trong m&ugrave;a tết năm nay, Bộ Đường sắt Trung Quốc cũng đ&atilde; bổ sung th&ecirc;m nhiều chuyến t&agrave;u phục vụ việc đi lại tại c&aacute;c tỉnh Tứ Xuy&ecirc;n, H&agrave; Nam v&agrave; An Huy. Cụ thể, 123 t&agrave;u mới sẽ được đưa v&agrave;o hoạt động.</p>\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(theo&nbsp;<em>China Daily</em>)</p>', 5, 2, 895, 0, 1),
(194, 'Đàn ông Nhật mất hứng với sex', 'Các chàng trai Nhật Bản ngày càng giảm hứng thú với sex và điều này càng gây lo ngại về tỷ lệ sinh thấp ở đất nước mặt trời mọc.', 'n1.jpg', NULL, '<h1 class="Title">Đ&agrave;n &ocirc;ng Nhật mất hứng với sex</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="right">\r\n<tbody>\r\n<tr>\r\n<td><img src="n1.jpg" alt="Ảnh minh họa: một đ&ocirc;i thanh ni&ecirc;n Nhật. Ảnh:" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ảnh minh họa: một đ&ocirc;i thanh ni&ecirc;n Nhật. Ảnh:<em>flickriver.com.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">C&aacute;c ch&agrave;ng trai Nhật Bản ng&agrave;y c&agrave;ng giảm hứng th&uacute; với sex v&agrave; điều n&agrave;y c&agrave;ng g&acirc;y lo ngại về tỷ lệ sinh thấp ở đất nước mặt trời mọc.</h2>\r\n<p class="Normal">Theo một khảo s&aacute;t do ch&iacute;nh phủ Nhật tiến h&agrave;nh, hơn 40% số người đ&atilde; c&oacute; gia đ&igrave;nh n&oacute;i rằng họ kh&ocirc;ng quan hệ t&igrave;nh dục cả th&aacute;ng trước đ&oacute;,&nbsp;<em>AFP</em>&nbsp;cho biết h&ocirc;m nay.</p>\r\n<p class="Normal">Kunio Kitamura, gi&aacute;m đốc bệnh viện thuộc Hiệp hội kế hoạch h&oacute;a gia đ&igrave;nh Nhật Bản, cho rằng t&igrave;nh trạng tr&ecirc;n gắn liền với việc tỷ lệ sinh tại nước n&agrave;y đang giảm đi. &Ocirc;ng k&ecirc;u gọi ch&iacute;nh phủ ra c&aacute;c ch&iacute;nh s&aacute;ch v&agrave; h&agrave;nh động.</p>\r\n<p class="Normal">Cuộc thăm d&ograve; n&agrave;y cũng x&aacute;c nhận niềm tin của người Nhật rằng nam giới nước n&agrave;y ng&agrave;y c&agrave;ng trở n&ecirc;n "chay tịnh" v&agrave; kh&ocirc;ng h&agrave;o hứng t&igrave;m kiếm bạn g&aacute;i hay quan hệ t&igrave;nh dục.</p>\r\n<p class="Normal">36% số người được hỏi, tuổi từ 16 đến 19, n&oacute;i rằng họ kh&ocirc;ng một ch&uacute;t th&iacute;ch th&uacute;, thậm ch&iacute; c&ograve;n căm gh&eacute;t sex. Theo khảo s&aacute;t tương tự năm 2008, tỷ lệ n&agrave;y l&agrave; 17,5%. Vấn đề c&oacute; vẻ trầm trọng hơn khi 59% số c&aacute;c c&ocirc; g&aacute;i trong c&ugrave;ng độ tuổi n&oacute;i rằng họ c&oacute; cảm nhận tương tự.</p>\r\n<p class="Normal">Kết quả điều tra khiến ch&iacute;nh phủ Nhật lo lắng bởi họ đang t&igrave;m c&aacute;ch khuyến kh&iacute;ch người d&acirc;n sinh đẻ do d&acirc;n số đang gi&agrave; đi nhanh ch&oacute;ng. Nhiều thanh ni&ecirc;n Nhật tr&igrave; ho&atilde;n việc kết h&ocirc;n v&igrave; sợ g&aacute;nh nặng về t&agrave;i ch&iacute;nh cũng như thay đổi trong lối sống v&agrave; sự nghiệp.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 5, 2, 2297, 0, 1),
(195, 'Cáo bắn thợ săn', 'Người thợ săn cố gắng hạ gục con cáo bị thương bằng báng súng trường, song con vật vô tình đưa một chân trước lên cò súng và giật mạnh.', 'Foxx.jpg', NULL, '<h1 class="Title">C&aacute;o bắn thợ săn</h1>\r\n<h2 class="Lead">Người thợ săn cố gắng hạ gục con c&aacute;o bị thương bằng b&aacute;ng s&uacute;ng trường, song con vật v&ocirc; t&igrave;nh đưa một ch&acirc;n trước l&ecirc;n c&ograve; s&uacute;ng v&agrave; giật mạnh.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Foxx.jpg" alt="Một con c&aacute;o. Ảnh: ALAMY." width="450" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con c&aacute;o. Ảnh:&nbsp;<em>ALAMY</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>Telegraph</em>&nbsp;cho biết, một thợ săn tại v&ugrave;ng Grodno của Belarus ph&aacute;t hiện con c&aacute;o trong l&uacute;c đi săn trong rừng. Anh nổ s&uacute;ng từ khoảng c&aacute;ch xa khiến con vật bị thương. Sau đ&oacute; người thợ săn đuổi theo con c&aacute;o v&agrave; t&oacute;m được n&oacute;. Tuy nhi&ecirc;n, con vật chống cự n&ecirc;n người thợ săn đ&aacute;nh n&oacute; bằng b&aacute;ng s&uacute;ng.</p>\r\n<p class="Normal">Đột nhi&ecirc;n con c&aacute;o đưa một ch&acirc;n trước l&ecirc;n c&ograve; s&uacute;ng v&agrave; giật mạnh khiến đạn găm v&agrave;o ch&acirc;n thợ săn. Trong l&uacute;c người thợ săn vẫn c&ograve;n sửng sốt v&agrave; đau đớn, con c&aacute;o thừa cơ chạy mất.</p>\r\n<p class="Normal">"Con c&aacute;o chống cự quyết liệt v&agrave; v&ocirc; t&igrave;nh b&oacute;p c&ograve; s&uacute;ng bằng ch&acirc;n trong l&uacute;c vật lộn với người thợ săn", một c&ocirc;ng tố vi&ecirc;n x&aacute;c nhận.</p>\r\n<p class="Normal">Săn c&aacute;o l&agrave; hoạt động kh&aacute; phổ biến ở v&ugrave;ng t&acirc;y bắc Belarus, nơi tiếp gi&aacute;p với Ba Lan.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 5, 2, 187, 0, 1),
(196, 'Xế hộp hạng sang làm bằng gạch', 'Một tay chơi Trung Quốc đã tạo ra một chiếc BMW làm bằng gạch hệt như thật và rao bán nó với giá 600.000 nhân dân tệ (90.641 USD).', 'car4.jpg', NULL, '<h1 class="Title">Xế hộp hạng sang l&agrave;m bằng gạch</h1>\r\n<h2 class="Lead">Một tay chơi Trung Quốc đ&atilde; tạo ra một chiếc BMW l&agrave;m bằng gạch hệt như thật v&agrave; rao b&aacute;n n&oacute; với gi&aacute; 600.000 nh&acirc;n d&acirc;n tệ (90.641 USD).</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="car4.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Xe c&oacute; kiểu d&aacute;ng v&agrave; k&iacute;ch cỡ hệt một chiếc BMW Z4. Ảnh:&nbsp;<em>asianewsphoto</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chiếc xe c&oacute; h&igrave;nh d&aacute;ng v&agrave; k&iacute;ch cỡ kh&ocirc;ng kh&aacute;c g&igrave; một chiếc BMW Z4 đang được trưng b&agrave;y tr&ecirc;n một con phố tại V&ocirc; T&iacute;ch, tỉnh Giang T&ocirc; v&agrave; thu h&uacute;t rất nhiều sự ch&uacute; &yacute; của kh&aacute;ch qua đường.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="car1.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chiếc xe đang được rao b&aacute;n với gi&aacute; 600.000 nh&acirc;n d&acirc;n tệ. Ảnh:&nbsp;<em>asianewsphoto</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chủ nh&acirc;n của n&oacute; l&agrave; Dai Yun, một thợ đi&ecirc;u khắc đến từ Th&acirc;m Quyến, Quảng Đ&ocirc;ng. Anh cho biết mất hơn một năm để gắn kết c&aacute;c vi&ecirc;n gạch bằng xi măng v&agrave; đục khắc cả khối để tạo n&ecirc;n t&aacute;c phẩm độc đ&aacute;o n&agrave;y.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="car3.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chủ nh&acirc;n của xe hy vọng một đại gia Trung Quốc sẽ bổ sung t&aacute;c phẩm v&agrave;o bộ sưu tập nghệ thuật của m&igrave;nh. Ảnh:&nbsp;<em>asianewsphoto</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cỗ xe d&agrave;i 5 m, rộng 2,15 m, cao 1,6 m v&agrave; nặng 6,5 tấn. Xe được l&agrave;m ho&agrave;n to&agrave;n bằng gạch đỏ, xi măng, th&eacute;p v&agrave; c&oacute; gi&aacute; 600.000 nh&acirc;n d&acirc;n tệ, bằng gi&aacute; của một chiếc BMW thật.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="car2.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngo&agrave;i cửa sổ l&agrave;m bằng k&iacute;nh, mọi bộ phận kh&aacute;c như lốp, b&aacute;nh l&aacute;i, gương, ống xả... đều l&agrave;m bằng gạch. Ảnh:&nbsp;<em>asianewsphoto</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nh&igrave;n qua k&iacute;nh cửa sổ c&oacute; thể thấy r&otilde; nội thất b&ecirc;n trong, bao gồm b&aacute;nh l&aacute;i v&agrave; ghế ngồi v&agrave; cần số.</p>\r\n<p class="Normal">Trước đ&oacute;, trong bộ sưu tập của m&igrave;nh, Dai Yun c&ograve;n tạo ra một chiếc BMW bằng đ&aacute; v&agrave; một chiếc Mercedes-Benz SLK cũng l&agrave;m bằng gạch.</p>\r\n<p><span style="font-size: small;"><br /></span></p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 1751, 0, 1),
(197, 'Anh chàng hắt xì ra viên đạn', 'Một người đàn ông Italy, từng bị trúng một viên đạn vào đầu trong lễ kỷ niệm năm mới, may mắn thoát khỏi nó nhờ hắt xì hơi.', 'top (2).jpg', NULL, '<h1 class="Title">Anh ch&agrave;ng hắt x&igrave; ra vi&ecirc;n đạn</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="top (2).jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Darco Sangermano. Ảnh:&nbsp;<em>Facebook</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Một người đ&agrave;n &ocirc;ng Italy, từng bị tr&uacute;ng một vi&ecirc;n đạn v&agrave;o đầu trong lễ kỷ niệm năm mới, may mắn tho&aacute;t khỏi n&oacute; nhờ hắt x&igrave; hơi.</h2>\r\n<p class="Normal">Darco Sangermano, 28 tuổi, bị tr&uacute;ng một vi&ecirc;n đạn lạc v&agrave;o th&aacute;i dương khi anh đang đi dạo c&ugrave;ng bạn g&aacute;i tại Naples - một th&agrave;nh phố Italy nổi tiếng với những m&agrave;n ăn mừng năm mới bạo lực c&oacute; d&ugrave;ng đến ph&aacute;o mạnh v&agrave; s&uacute;ng.</p>\r\n<p class="Normal">Vi&ecirc;n đạn bay xuy&ecirc;n qua phần b&ecirc;n phải đầu, ph&iacute;a sau hốc mắt, v&agrave; lọt v&agrave;o khoang mũi, nhưng điều kỳ diệu l&agrave; n&oacute; kh&ocirc;ng g&acirc;y tổn thương nghi&ecirc;m trọng g&igrave; cho mắt,&nbsp;<em>Telegraph</em>&nbsp;h&ocirc;m qua đưa tin.</p>\r\n<p class="Normal">Anh được đưa cấp cứu v&agrave;o bệnh viện ngay trong đ&ecirc;m với m&aacute;u chảy r&ograve;ng r&ograve;ng. Nhưng trong khi đang chờ b&aacute;c sĩ th&igrave; anh bỗng hắt hơi v&agrave; vi&ecirc;n đạn bắn thẳng ra khỏi lỗ mũi.</p>\r\n<p class="Normal">Sau cuộc phẫu thuật, Sangermano được b&aacute;c sĩ cho ra viện v&agrave; trở về qu&ecirc; nh&agrave;.</p>\r\n<p class="Normal">"Vi&ecirc;n đạn bay tr&uacute;ng xương th&aacute;i dương, l&agrave;m vỡ xương n&agrave;y v&agrave; v&igrave; thế bay chậm lại khiến n&oacute; sượt qua con mắt m&agrave; kh&ocirc;ng g&acirc;y tổn hại g&igrave;", b&aacute;c sĩ Guglielmo Ramieri cho biết.</p>\r\n<p class="Normal">C&aacute;c b&aacute;c sĩ đ&atilde; phẫu thuật mắt cho Sangermano, gắp c&aacute;c mảnh xương vụn ra. "Cuộc phẫu thuật k&eacute;o d&agrave;i v&agrave;i tiếng nhưng cậu ta đ&atilde; ổn", Vito Sangermano - ch&uacute; của Sangermano cho biết. "C&aacute;c b&aacute;c sĩ khẳng định mắt của n&oacute; sẽ kh&ocirc;ng sao, cho d&ugrave; b&acirc;y giờ n&oacute; nh&igrave;n hơi k&eacute;m".</p>\r\n<p class="Normal">Trong v&agrave;i tuần tới, Sangermano sẽ phẫu thuật laser để chỉnh lại v&otilde;ng mạc v&agrave; sau đ&oacute; sẽ hồi phục ho&agrave;n to&agrave;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 410, 0, 1),
(198, 'Xe ủi nhảy breakdance', 'Những tài xế xe hạng nặng ở Trung Quốc đã điều khiển cỗ máy của mình trình diễn những động tác nhảy đặc biệt, tại một khu triển lãm quốc tế ở Trường Xuân, thủ phủ tỉnh Cát Lâm.', 'truck.jpg', NULL, '<h1 class="Title">Xe ủi nhảy breakdance</h1>\r\n<h2 class="Lead">Những t&agrave;i xế xe hạng nặng ở Trung Quốc đ&atilde; điều khiển cỗ m&aacute;y của m&igrave;nh tr&igrave;nh diễn những động t&aacute;c nhảy đặc biệt, tại một khu triển l&atilde;m quốc tế ở Trường Xu&acirc;n, thủ phủ tỉnh C&aacute;t L&acirc;m.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="truck.jpg" border="1" alt="" width="460" height="291" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cỗ m&aacute;y khổng lồ nh&uacute;n nhảy tr&ecirc;n hai b&aacute;nh trước. Ảnh:&nbsp;<em>CFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">M&agrave;n tr&igrave;nh diễn ấn tượng diễn ra h&ocirc;m 8/1 thu h&uacute;t rất nhiều kh&aacute;ch tham quan với &aacute;nh nh&igrave;n ngưỡng mộ. Những cỗ xe hầm hố đung đưa, lắc lư khi chỉ giữ thăng bằng tr&ecirc;n hai b&aacute;nh trước.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, một sự cố đ&atilde; xảy ra khi t&agrave;i xế Cao Bing bị một vết thương ở đầu l&uacute;c đang biểu diễn. "T&ocirc;i mất hơn nửa năm để luyện tập. Việc n&agrave;y rất kh&oacute;. Cuối c&ugrave;ng t&ocirc;i cũng th&agrave;nh c&ocirc;ng sau qu&aacute; tr&igrave;nh luyện tập vất vả",&nbsp;<em>Telegraph</em>&nbsp;dẫn lời Cao Bing cho biết.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 1345, 0, 1),
(199, 'Sống sót thần kỳ dưới gầm tàu điện', 'Một bé gái vừa được mẹ đặt xuống đường ray để trốn người soát vé, thì bất ngờ một tàu hỏa lao tới.', 'top (3).jpg', NULL, '<h1 class="Title">Sống s&oacute;t thần kỳ dưới gầm t&agrave;u điện</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="top (3).jpg" border="1" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&agrave; mẹ đưa con xuống đường ray để trốn v&eacute;. Ảnh:<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Một b&eacute; g&aacute;i vừa được mẹ đặt xuống đường ray để trốn người so&aacute;t v&eacute;, th&igrave; bất ngờ một t&agrave;u hỏa lao tới.</h2>\r\n<p class="Normal">C&acirc;u chuyện xảy ra ở Argentina. Đoạn video được tung ra h&ocirc;m 7/1 cho thấy người phụ nữ hướng dẫn con tr&egrave;o xuống một đường ray ở ph&iacute;a dưới, nhằm tr&aacute;nh nh&acirc;n vi&ecirc;n so&aacute;t v&eacute;. Khi b&agrave; n&agrave;y định tr&egrave;o xuống c&ugrave;ng con th&igrave; bất ngờ nh&igrave;n thấy một t&agrave;u hỏa lao tới. Người phụ nữ vội v&agrave;ng l&ugrave;i trở lại nhưng đứa b&eacute; th&igrave; vẫn ở ph&iacute;a dưới.</p>\r\n<p class="Normal">Những người xung quanh r&ugrave;ng m&igrave;nh khi thấy t&agrave;u hỏa lao v&uacute;t qua. Đến khi t&agrave;u dừng lại, tất cả lao tới nh&igrave;n xuống khe hẹp giữa t&agrave;u hỏa v&agrave; bờ tường để xem b&eacute; g&aacute;i thế n&agrave;o.</p>\r\n<p class="Normal">"Ch&uacute;ng t&ocirc;i vội v&agrave;ng cắt d&ograve;ng điện để tr&aacute;nh sự cố", Guillermo D''Abenigno - Gi&aacute;m đốc đường sắt Buenos Aires cho biết. "Ch&uacute;ng t&ocirc;i chỉ lo điều tồi tệ nhất đ&atilde; xảy ra".</p>\r\n<p class="Normal">Sau đ&oacute; c&aacute;c nh&acirc;n vi&ecirc;n cứu hộ chui xuống đường ray v&agrave; t&igrave;m thấy b&eacute; g&aacute;i đang v&ocirc; c&ugrave;ng hoảng sợ nhưng kh&ocirc;ng bị ảnh hưởng g&igrave;. Em đ&atilde; nằm lọt an to&agrave;n giữa khoảng trống v&ocirc; c&ugrave;ng hẹp giữa t&agrave;u hỏa v&agrave; bờ tường.</p>\r\n<p class="Normal">"Đ&oacute; thật l&agrave; một vụ tho&aacute;t chết thần kỳ",&nbsp;<em>AP</em>&nbsp;dẫn lời D''Abenigno n&oacute;i.</p>\r\n<p class="Normal">Vụ việc xảy ra v&agrave;o ng&agrave;y 4/1 nhưng chỉ được đưa ra &aacute;nh s&aacute;ng ba ng&agrave;y sau đ&oacute;, khi c&aacute;c nh&agrave; điều h&agrave;nh đường sắt c&ocirc;ng bố đoạn video thu được từ camera gi&aacute;m s&aacute;t.</p>\r\n<p class="Normal">C&aacute;c quan chức kh&ocirc;ng c&ocirc;ng bố chi tiết về người mẹ v&agrave; đứa b&eacute;, cũng như chưa x&aacute;c định c&oacute; truy tố b&agrave; mẹ trốn v&eacute; n&agrave;y hay kh&ocirc;ng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 5, 2, 1551, 0, 1),
(200, 'Tình nhân của tỷ phú muốn đòi tiền thừa kế', 'Thầy phong thủy Hong Kong từng là tình nhân của nữ tỷ phú giàu nhất châu Á hôm nay sẽ đâm đơn phản đối một phán quyết của tòa án. Năm ngoái, tòa đã tuyên anh ta không được một đồng nào trong 13 tỷ USD mà tỷ phú để lại.', 'nina.jpg', NULL, '<h1 class="Title">T&igrave;nh nh&acirc;n của tỷ ph&uacute; muốn đ&ograve;i tiền thừa kế</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="nina.jpg" border="1" alt="Tỷ ph&uacute; Nina Wang l&uacute;c sinh thời với m&aacute;i t&oacute;c tết b&iacute;m đặc trưng. Ảnh: shm." width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tỷ ph&uacute; Nina Wang l&uacute;c sinh thời với m&aacute;i t&oacute;c tết b&iacute;m đặc trưng. Ảnh:&nbsp;<em>shm.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Thầy phong thủy Hong Kong từng l&agrave; t&igrave;nh nh&acirc;n của nữ tỷ ph&uacute; gi&agrave;u nhất ch&acirc;u &Aacute; h&ocirc;m nay sẽ đ&acirc;m đơn phản đối một ph&aacute;n quyết của t&ograve;a &aacute;n. Năm ngo&aacute;i, t&ograve;a đ&atilde; tuy&ecirc;n anh ta kh&ocirc;ng được một đồng n&agrave;o trong 13 tỷ USD m&agrave; tỷ ph&uacute; để lại.</h2>\r\n<p class="Normal">Việc n&agrave;y giở lại vụ việc từng l&agrave;m x&ocirc;n xao Trung Quốc v&agrave; Hong Kong, trong cuộc chiến ph&aacute;p l&yacute; giữa t&igrave;nh nh&acirc;n Tony Chan với quỹ từ thiện mang t&ecirc;n nữ tỷ ph&uacute; qu&aacute; cố Nina Wang.</p>\r\n<p class="Normal">Nữ doanh nh&acirc;n c&oacute; kiểu ăn mặc kỳ dị v&agrave; bản t&iacute;nh h&agrave; tiện n&agrave;y qua đời năm 2007 v&igrave; ung thư, để lại khối t&agrave;i sản khổng lồ v&agrave; một cuộc chiến ph&aacute;p l&yacute; nhiều tiếng tăm.</p>\r\n<p class="Normal">Chan, người t&igrave;nh l&acirc;u năm của Wang, cho rằng b&agrave; đ&atilde; viết ch&uacute;c thư để lại t&agrave;i sản cho &ocirc;ng ta. Tuy nhi&ecirc;n th&aacute;ng hai vừa rồi t&ograve;a &aacute;n Hong Kong ra ph&aacute;n quyết ủng hộ quỹ từ thiện, tuy&ecirc;n bố t&agrave;i sản thuộc về quỹ n&agrave;y theo một ch&uacute;c thư c&oacute; từ trước văn bản m&agrave; Chan đưa ra.</p>\r\n<p class="Normal">Ngay sau khi ph&aacute;n quyết được đưa ra, cảnh s&aacute;t Hong Kong bắt Chan với c&aacute;o buộc l&agrave;m giả di ch&uacute;c, sau đ&oacute; cho người n&agrave;y tại ngoại với mức bảo l&atilde;nh 5 triệu HKD (tương đường 640.000 USD).</p>\r\n<p class="Normal">Tin tức về vụ kiện tranh t&agrave;i sản từng đốt n&oacute;ng b&aacute;o ch&iacute; Hong Kong, bởi nhiều người cho rằng Chan l&agrave; một kẻ lợi dụng l&ograve;ng tin của Wang, hứa hẹn sẽ t&igrave;m được người chồng mất t&iacute;ch v&agrave; chữa bệnh ung thư cho tỷ ph&uacute; Wang để moi tiền.</p>\r\n<p class="Normal">Chồng của Nina Wang l&agrave; Teddy, chủ tịch tập đo&agrave;n Chinachem, bị bắt c&oacute;c năm 1990 v&agrave; bị tuy&ecirc;n l&agrave; đ&atilde; chết năm 1999. Tuy nhi&ecirc;n thi thể của &ocirc;ng chưa từng được t&igrave;m thấy. Vụ mất t&iacute;ch n&agrave;y ch&acirc;m ng&ograve;i một cuộc chiến ph&aacute;p l&yacute; giữa Nina Wang v&agrave; bố của Teddy nhằm tranh quyền kiểm so&aacute;t Chinachem. B&agrave; cũng chỉ mới thắng cuộc hai năm trước khi chết.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p>', 5, 2, 2800, 0, 1),
(201, 'Sinh viên làm bài tốt nghiệp bằng phim sex', 'Ba giáo sư tại một trong những trường đại học danh giá nhất Thổ Nhĩ Kỳ vừa bị đuổi việc vì để sinh viên làm phim khiêu dâm cho bài tốt nghiệp.', 'b1 (1).jpg', NULL, '<h1 class="Title">Sinh vi&ecirc;n l&agrave;m b&agrave;i tốt nghiệp bằng phim sex</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="b1 (1).jpg" border="1" alt="Một cảnh trong phim sex tốt nghiệp của một sinh vi&ecirc;n Thổ Nhĩ Kỳ. Ảnh: takvim." width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một cảnh trong phim sex tốt nghiệp của một sinh vi&ecirc;n Thổ Nhĩ Kỳ. Ảnh:&nbsp;<em>takvim</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Ba gi&aacute;o sư tại một trong những trường đại học danh gi&aacute; nhất Thổ Nhĩ Kỳ vừa bị đuổi việc v&igrave; để sinh vi&ecirc;n l&agrave;m phim khi&ecirc;u d&acirc;m cho b&agrave;i tốt nghiệp.</h2>\r\n<p class="Normal"><span style="color: #000000;">Khi cậu sinh vi&ecirc;n điện ảnh Deniz Ozgun đưa ra &yacute; tưởng cho b&agrave;i tốt nghiệp, c&aacute;c gi&aacute;o sư của anh tỏ ra lưỡng lự. Ozgun n&oacute;i anh muốn l&agrave;m phim sex đồng thời ph&acirc;n t&iacute;ch sự "kh&ocirc;ng thật" trong những cảnh sex của phim.</span></p>\r\n<p class="Normal">C&aacute;c gi&aacute;o sư y&ecirc;u cầu anh l&agrave;m r&otilde; hơn t&iacute;nh học thuật trong b&agrave;i tập. Cuối c&ugrave;ng, anh ch&agrave;ng kh&ocirc;ng th&agrave;nh c&ocirc;ng v&agrave; bị đ&aacute;nh trượt với b&agrave;i tốt nghiệp n&agrave;y,&nbsp;<em>BBC</em>&nbsp;đưa tin.</p>\r\n<p class="Normal">B&agrave;i tập n&agrave;y kh&ocirc;ng hề g&acirc;y tranh c&atilde;i g&igrave; ở Thổ Nhĩ Kỳ cho tới khi Ozgun trả lời phỏng vấn tr&ecirc;n một tạp ch&iacute; rằng anh l&agrave;m phim ngay trong trường học. Phụ huynh ngay lập tức đ&ograve;i được biết r&otilde; c&aacute;c hoạt động ở Bilgi, một trong những đại học h&agrave;ng đầu ở Thổ Nhĩ Kỳ. Bộ Gi&aacute;o dục cũng ra sức &eacute;p buộc đại học n&agrave;y phải c&oacute; h&agrave;nh động.</p>\r\n<p class="Normal">Kết quả l&agrave;, ban gi&aacute;m hiệu Bilgi đ&atilde; sa thải ba gi&aacute;o sư v&igrave; để Ozgun l&agrave;m phim sex. Khoa truyền th&ocirc;ng cũng bị đ&oacute;ng cửa. Ba gi&aacute;o sư kể tr&ecirc;n c&ograve;n bị cảnh s&aacute;t điều tra c&ograve;n Uzgun c&ugrave;ng nh&acirc;n vật ch&iacute;nh trong phim tốt nghiệp đang lẩn trốn.</p>\r\n<p class="Normal"><span style="color: #000000;">Một loạt c&aacute;c gi&aacute;o sư của Bilgi phản đối động th&aacute;i của nh&agrave; trường. Bilgi vốn nổi danh l&agrave; một trong những trường c&oacute; quan điểm "tho&aacute;ng" ở Thổ Nhĩ Kỳ. Sự việc n&agrave;y cũng khắc r&otilde; xung đột giữa c&aacute;c gi&aacute; trị truyền thống v&agrave; nghệ thuật cũng như lối sống t&aacute;o bạo ở Istanbul.</span></p>\r\n<p class="Normal"><span style="color: #000000;">Trong một quốc gia vẫn tu&acirc;n theo nhiều quy tắc đạo đức khắt khe, sex được gọi l&agrave; một điều cấm kị v&agrave; kh&ocirc;ng được b&agrave;n luận nơi c&ocirc;ng cộng. Phần lớn ch&iacute;nh trị gia trong đảng cầm quyền Thổ Nhĩ Kỳ ủng hộ quan điểm n&agrave;y.</span></p>\r\n<p class="Normal">Tuy nhi&ecirc;n, ngay giữa thủ đ&ocirc; Istanbul, nhiều người tỏ ra "tho&aacute;ng" hơn về t&igrave;nh dục, rượu v&agrave; nghệ thuật. Thực tế, du kh&aacute;ch c&oacute; thể xem phim, kịch, c&aacute;c triển l&atilde;m nghệ thuật t&aacute;o bạo kh&ocirc;ng thua g&igrave; c&aacute;c th&agrave;nh phố kh&aacute;c ở ch&acirc;u &Acirc;u.</p>\r\n<p class="Normal"><span style="color: #000000;">Ch&iacute;nh điều n&agrave;y đ&atilde; g&acirc;y ra xung đột. Th&aacute;ng 9 năm ngo&aacute;i, kh&aacute;ch tới xem ba triển l&atilde;m tranh ở Istanbul bị tấn c&ocirc;ng v&igrave; uống rượu ngo&agrave;i đường phố. Nếu những người tấn c&ocirc;ng v&agrave;o xem triển l&atilde;m, chắc hẳn họ c&ograve;n tức giận hơn nữa,&nbsp;<em>BBC</em>&nbsp;nhận định.&nbsp;</span>Năm ngo&aacute;i, một chủ nh&agrave; xuất bản phải ra t&ograve;a v&igrave; dịch văn họa ch&acirc;u &Acirc;u. Cuối c&ugrave;ng, &ocirc;ng n&agrave;y được xử trắng &aacute;n.</p>\r\n<p class="Normal" style="text-align: right;"><span style="color: #000000;"><strong>Mai Trang</strong></span></p>', 5, 2, 2380, 0, 1),
(202, 'Người nợ tiền thuế không được ngồi xe taxi', 'Chính quyền Đài Loan cấm những người nợ tiền thuế di chuyển bằng taxi và mua những hàng hóa đắt tiền', 'taxi4.jpg', NULL, '<h1 class="Title">Người nợ tiền thuế kh&ocirc;ng được ngồi xe taxi</h1>\r\n<h2 class="Lead">Ch&iacute;nh quyền Đ&agrave;i Loan cấm những người nợ tiền thuế di chuyển bằng taxi v&agrave; mua những h&agrave;ng h&oacute;a đắt tiền.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="taxi4.jpg" alt="Một người d&acirc;n bắt xe taxi tr&ecirc;n đảo Đ&agrave;i Loan. Ảnh: travelpod.com." width="450" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một người d&acirc;n bắt xe taxi tr&ecirc;n đảo Đ&agrave;i Loan. Ảnh:&nbsp;<em>travelpod.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhiều người d&acirc;n tr&ecirc;n đảo Đ&agrave;i Loan nợ tới 10 triệu Đ&agrave;i tệ (331.000 USD) tiền thuế nhưng lại hưởng thụ cuộc sống xa hoa. V&igrave; thế m&agrave; cơ quan tư ph&aacute;p của đảo muốn ngăn chặn h&agrave;nh vi ti&ecirc;u hoang của họ,&nbsp;<em>AFP</em>&nbsp;đưa tin.</p>\r\n<p class="Normal">&ldquo;Do những người nợ ch&iacute;nh quyền rất nhiều tiền n&ecirc;n họ kh&ocirc;ng được ph&eacute;p ti&ecirc;u qu&aacute; 2.000 Đ&agrave;i tệ (66 USD) trong một lần mua h&agrave;ng h&oacute;a hoặc sử dụng dịch vụ. Họ cũng bị cấm sử dụng những phương tiện giao th&ocirc;ng đắt tiền như taxi hay t&agrave;u cao tốc&rdquo;, một quan chức tư ph&aacute;p ph&aacute;t biểu.</p>\r\n<p class="Normal">Vị quan chức n&oacute;i ch&iacute;nh quyền sẽ gi&aacute;m s&aacute;t hoạt động ti&ecirc;u tiền của những c&ocirc;ng d&acirc;n nợ tiền thuế nhiều lần, đồng thời cảnh b&aacute;o họ c&oacute; thể bị bắt nếu vi phạm lệnh cấm.</p>\r\n<p class="Normal">Năm ngo&aacute;i ch&iacute;nh quyền đảo Đ&agrave;i Loan ban h&agrave;nh luật &ldquo;chống lối sống xa hoa&rdquo; đối với những người nợ tiền thuế sau khi dư luận l&ecirc;n &aacute;n việc một nh&agrave; t&agrave;i phiệt nợ khoản tiền thuế l&ecirc;n tới 300 triệu Đ&agrave;i tệ (khoảng 9,9 triệu USD) song lại thường xuy&ecirc;n xuất hiện trong c&aacute;c cửa h&agrave;ng sang trọng.</p>\r\n<p class="Normal">Tuần trước ch&iacute;nh quyền xứ Đ&agrave;i c&ocirc;ng bố danh s&aacute;ch những người nợ nhiều tiền thuế nhất v&agrave; khuyến kh&iacute;ch người d&acirc;n theo d&otilde;i họ. Đ&acirc;y l&agrave; biện ph&aacute;p đầu ti&ecirc;n m&agrave; giới chức h&ograve;n đảo thực hiện nhằm thu tiền thuế c&ograve;n đọng trong d&acirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 5, 2, 996, 0, 1),
(207, 'Lễ tình nhân trên khắp thế giới', 'Không khí ngày lễ tình yêu Valentine tưng bừng, nhộn nhịp, trên khắp các con phố từ Trung Quốc, Nhật Bản, cho tới New York, Mexico.\r\n', 'va10.jpg', NULL, '<h1 class="Title">Lễ t&igrave;nh nh&acirc;n tr&ecirc;n khắp thế giới</h1>\r\n<h2 class="Lead">Kh&ocirc;ng kh&iacute; ng&agrave;y lễ t&igrave;nh y&ecirc;u Valentine tưng bừng, nhộn nhịp, tr&ecirc;n khắp c&aacute;c con phố từ Trung Quốc, Nhật Bản, cho tới New York, Mexico.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va10.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&oacute; hoa hồng - chocolate được trang tr&iacute; cầu kỳ tại một cửa h&agrave;ng hoa ở Macao, miền nam Trung Quốc. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="kiss.jpg" border="1" alt="" width="480" height="298" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c cặp t&igrave;nh nh&acirc;n h&ocirc;n nhau trong cuộc thi "Nụ h&ocirc;n d&agrave;i nhất thế giới" để kỷ niệm ng&agrave;y Valetine tại Pattaya, Th&aacute;i Lan, h&ocirc;m 13/2. 14 cặp tham gia v&agrave;o cuộc thi n&agrave;y nhằm ph&aacute; vỡ kỷ lục Guiness v&agrave; đoạt giải thưởng c&oacute; tổng gi&aacute; trị l&ecirc;n tới 6.666 USD v&agrave; một chiếc nhẫn kim cương. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><span style="color: #000080;"><img src="va11.jpg" border="1" alt="" width="360" height="540" /></span></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Kh&ocirc;ng kh&iacute; Valentine cũng xuất hiện trong một cửa h&agrave;ng thời trang cao cấp ở New York, Mỹ. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va3.jpg" border="1" alt="" width="480" height="296" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i trẻ b&aacute;n b&oacute;ng bay h&igrave;nh tr&aacute;i tim tr&ecirc;n một con phố ở Tegucigalpa, Honduras. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va16.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những phụ nữ lớn tuổi cũng h&ograve;a m&igrave;nh v&agrave;o kh&ocirc;ng kh&iacute; ng&agrave;y lễ Valentine khi tham dự cuộc diễu h&agrave;nh mang t&ecirc;n Old Love tại Guatemela, h&ocirc;m 11/2. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><span style="color: #000080;"><img src="va13.jpg" border="1" alt="" width="480" height="320" /></span></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một cửa h&agrave;ng hoa ở Macao chuy&ecirc;n b&aacute;n những b&oacute; hoa h&igrave;nh c&aacute;c con vật ngộ nghĩnh, đ&aacute;ng y&ecirc;u. Ảnh:<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va4.jpg" border="1" alt="" width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c đồ vật lưu niệm sặc sỡ được b&agrave;y b&aacute;n tr&ecirc;n vỉa h&egrave; nh&acirc;n ng&agrave;y Valentine ở Maturin, Venezuela. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va20.jpg" border="1" alt="" width="347" height="512" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một thợ l&agrave;m b&aacute;nh trang tr&iacute; c&aacute;c vi&ecirc;n chocolate ngộ nghĩnh tại một cửa h&agrave;ng nh&acirc;n sự kiện "Amour du Chocolat" tại Tokyo, Nhật Bản. Ở nước n&agrave;y trong ng&agrave;y th&aacute;nh Valentine, c&aacute;c c&ocirc; g&aacute;i thường tặng chocolate cho bạn trai hoặc đồng nghiệp nam. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va15.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một g&oacute;c rực rỡ trong cửa h&agrave;ng chocolate ở New York. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va8.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chủ cửa h&agrave;ng g&oacute;i c&aacute;c b&oacute; hoa hồng chuẩn bị cho dịp l&agrave;m ăn nh&acirc;n ng&agrave;y lễ t&igrave;nh y&ecirc;u ở Buenos Aires, Argentina. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va21.jpg" border="1" alt="" width="480" height="295" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chữ "Love" (T&igrave;nh y&ecirc;u) khổng lồ được dựng l&ecirc;n nh&acirc;n ng&agrave;y Valentine trước cửa một trung t&acirc;m mua sắm ở Jakart, Indonesia. Một số nh&oacute;m Hồi gi&aacute;o k&ecirc;u gọi tẩy chay hoặc cấm c&aacute;c hoạt động thể hiện t&igrave;nh y&ecirc;u nam nữ, nhưng điều đ&oacute; kh&ocirc;ng ngăn được kh&ocirc;ng kh&iacute; hưởng ứng ng&agrave;y Valentine. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va9.jpg" border="1" alt="" width="360" height="531" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những chiếc kẹo h&igrave;nh tr&aacute;i tim xinh xắn cũng l&agrave; một m&oacute;n h&agrave;ng được ưa th&iacute;ch nh&acirc;n ng&agrave;y Valentine ở Panama City, thủ đ&ocirc; Panama. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va14.jpg" border="1" alt="" width="480" height="344" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ocirc; g&aacute;i Trung Quốc b&ecirc;n b&oacute; hoa hồng t&iacute;m khổng lồ. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va12.jpg" border="1" alt="" width="480" height="344" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những chiếc kẹo chocolate đặt h&agrave;ng theo y&ecirc;u cầu tại một cửa h&agrave;ng ở Ph&uacute;c Ch&acirc;u, tỉnh Giang T&ocirc;, Trung Quốc. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va17.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người d&acirc;n h&oacute;a trang trong c&aacute;c bộ trang phục sặc sỡ trước cửa một quầy h&agrave;ng đồ lưu niệm nh&acirc;n ng&agrave;y Valetine ở Beirut, Lebanon. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="va18.jpg" border="1" alt="" width="341" height="512" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con th&uacute; b&ocirc;ng - biểu tượng của t&igrave;nh y&ecirc;u trong một cửa h&agrave;ng tại Ahmedabad, Ấn Độ. C&aacute;c nh&oacute;m HIndu gi&aacute;o cũng k&ecirc;u gọi cấm đo&aacute;n c&aacute;c hoạt động m&agrave; họ cho l&agrave; "t&acirc;y h&oacute;a" trong ng&agrave;y lễ t&igrave;nh nh&acirc;n. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>Xinhua</em>)</p>', 6, 2, 1659, 0, 1),
(215, 'Bạo loạn leo thang tại Ai Cập', 'Hôm qua, hàng nghìn người biểu tình tiếp tục đổ ra đường, đốt lửa, ném đá vào cảnh sát, nhằm phản đối chính phủ Ai Cập, dẫn tới cuộc xô xát giữa cảnh sát và dân chúng.', 'loan1.jpg', NULL, '<h1 class="Title">Bạo loạn leo thang tại Ai Cập</h1>\r\n<h2 class="Lead">H&ocirc;m qua, h&agrave;ng ngh&igrave;n người biểu t&igrave;nh tiếp tục đổ ra đường, đốt lửa, n&eacute;m đ&aacute; v&agrave;o cảnh s&aacute;t, nhằm phản đối ch&iacute;nh phủ Ai Cập, dẫn tới cuộc x&ocirc; x&aacute;t giữa cảnh s&aacute;t v&agrave; d&acirc;n ch&uacute;ng.<br />&gt;&nbsp;<a class="Lead" href="http://vnexpress.net/gl/the-gioi/2011/01/3ba25f92/">Ch&iacute;nh phủ Ai Cập giải t&aacute;n</a></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan1.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&aacute;m đ&ocirc;ng biểu t&igrave;nh đốt ph&aacute; giữa đường phố thủ đ&ocirc; Cairo h&ocirc;m 28/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan2.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người d&acirc;n nấp sau những th&ugrave;ng r&aacute;c lớn khi đối đầu với qu&acirc;n đội Ai Cập.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan3.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lửa bốc l&ecirc;n ng&ugrave;n ngụt tr&ecirc;n khắp c&aacute;c con phố.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan4.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người biểu t&igrave;nh bao v&acirc;y một chiếc xe cứu thương khi n&oacute; đang tr&ecirc;n đường l&agrave;m nhiệm vụ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan5.jpg" border="1" alt="" width="480" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một người đ&agrave;n &ocirc;ng giơ b&igrave;nh xịt hơi cay do lực lượng an ninh Ai Cập sử dụng để chống bạo loạn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan6.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trước t&igrave;nh trạng bạo lực leo thang, Tổng thống Ai Cập Hosni Mubarak tối qua tuy&ecirc;n bố lệnh giới nghi&ecirc;m tr&ecirc;n to&agrave;n quốc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan7.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người d&acirc;n bỏ chạy khi c&aacute;c xe cảnh s&aacute;t x&ocirc;ng v&agrave;o quảng trường Tự do ở Cairo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan8.jpg" border="1" alt="" width="480" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một t&ograve;a nh&agrave; bị đốt ch&aacute;y trong cuộc biểu t&igrave;nh tối qua.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="loan9.jpg" border="1" alt="" width="480" height="306" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&iacute;nh phủ Ai Cập buộc phải giải t&aacute;n sau khi kh&ocirc;ng thể ngăn chặn được đ&aacute;m đ&ocirc;ng biểu t&igrave;nh ng&agrave;y c&agrave;ng cuồng loạn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>Xinhua</em>)</p>', 6, 2, 1649, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(219, 'Châu Á rộn ràng đón Tết', 'Màu đỏ, sắc hồng rực rỡ của hoa, đèn, bánh kẹo, thư pháp, váy áo đã ngập tràn khắp châu Á, xen lẫn giữa những gương mặt lo âu và dòng người hối hả lo cho Tết Nguyên đán đang đến rất gần.', '2 (1).jpg', NULL, '<h1 class="Title">Ch&acirc;u &Aacute; rộn r&agrave;ng đ&oacute;n Tết</h1>\r\n<h2 class="Lead">M&agrave;u đỏ, sắc hồng rực rỡ của hoa, đ&egrave;n, b&aacute;nh kẹo, thư ph&aacute;p, v&aacute;y &aacute;o đ&atilde; ngập tr&agrave;n khắp ch&acirc;u &Aacute;, xen lẫn giữa những gương mặt lo &acirc;u v&agrave; d&ograve;ng người hối hả lo cho Tết Nguy&ecirc;n đ&aacute;n đang đến rất gần.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="2.jpg" border="1" alt="" width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trẻ em ở tỉnh An Huy, Trung Quốc, mặc đồ c&oacute; thư ph&aacute;p chữ "Thỏ". Năm &acirc;m lịch tới đ&acirc;y ở Trung Quốc được cho l&agrave; năm con Thỏ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="3.jpg" border="1" alt="" width="480" height="370" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">V&igrave; thế Thỏ hiện diện khắp nơi khi Tết Nguy&ecirc;n đ&aacute;n gần kề. Trong ảnh, hai người đ&agrave;n &ocirc;ng n&oacute;i chuyện ph&iacute;a trước một h&igrave;nh nộm thỏ to tướng tại một trung t&acirc;m thương mại ở Bắc Kinh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="4.jpg" border="1" alt="" width="480" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tr&ecirc;n s&acirc;n ga Bắc Kinh, cũng như v&ocirc; số nh&agrave; ga kh&aacute;c ở Trung Quốc, người người đang hối hả đổ về qu&ecirc; hương để ăn tết sum họp với gia đ&igrave;nh. Nh&agrave; chức tr&aacute;ch Trung Quốc cho hay sẽ c&oacute; tới 2 tỷ lượt di chuyển tại nước n&agrave;y trước trong v&agrave; sau Tết, tạo th&agrave;nh cuộc di cư lớn nhất của lo&agrave;i người mỗi năm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="5.jpg" border="1" alt="" width="380" height="530" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&acirc;y hoa m&ugrave;a xu&acirc;n được b&agrave;y b&aacute;n ở Jakarta, Indonesia. Một số người nước n&agrave;y cũng ăn tết &acirc;m lịch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="7.jpg" border="1" alt="" width="480" height="313" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&ecirc;n cạnh h&igrave;nh trang tr&iacute; l&agrave; Thỏ cũng c&oacute; cả h&igrave;nh M&egrave;o. Người Việt Nam ta gọi năm nay l&agrave; năm con m&egrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="8.jpg" border="1" alt="" width="380" height="532" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một nh&acirc;n c&ocirc;ng đang được hai đồng nghiệp giữ để treo đ&egrave;n lồng đ&oacute;n Tết tại Bắc Kinh, ng&agrave;y 21/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="9.jpg" border="1" alt="" width="480" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những ng&agrave;y n&agrave;y, thỏ l&agrave; con vật nu&ocirc;i được t&igrave;m mua nhiều. Tuy nhi&ecirc;n c&aacute;c nh&agrave; hoạt động v&igrave; quyền động vật e ngại rằng sau Tết sẽ c&oacute; nhiều con thỏ bị bỏ mặc kh&ocirc;ng được chăm s&oacute;c. Trong ảnh l&agrave; những con thỏ dễ thương được b&agrave;y b&aacute;n tr&ecirc;n vỉa h&egrave; ở T&acirc;yJava, Indonesia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="11.jpg" border="1" alt="" width="380" height="487" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c b&agrave; c&aacute;c chị Đ&agrave;i Loan chen ch&acirc;n chọn b&aacute;nh kẹo trong chợ Tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1.jpg" border="1" alt="" width="480" height="371" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một cửa h&agrave;ng b&aacute;n độ trang tr&iacute; Tết ở Đ&agrave;i Loan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="10.jpg" border="1" alt="" width="480" height="343" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c em thiếu nhi Đ&agrave;i Loan trang điểm v&agrave; chơi đồ c&oacute; h&igrave;nh ảnh đẹp đẽ của con vật tượng trưng cho năm mới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong>&nbsp;(Ảnh:&nbsp;<em>AFP</em>)</p>', 6, 2, 241, 0, 1),
(220, 'Người Trung Quốc nhọc nhằn về quê ăn tết', 'Hành lý nặng trĩu vai, dòng người chen chân tại các nhà ga, bến xe ở Trung Quốc để về đoàn tụ cùng gia đình dịp Tết Nguyên đán.', 'cn11.jpg', NULL, '<h1 class="Title">Người Trung Quốc nhọc nhằn về qu&ecirc; ăn tết</h1>\r\n<h2 class="Lead">H&agrave;nh l&yacute; nặng trĩu vai, d&ograve;ng người chen ch&acirc;n tại c&aacute;c nh&agrave; ga, bến xe ở Trung Quốc để về đo&agrave;n tụ c&ugrave;ng gia đ&igrave;nh dịp Tết Nguy&ecirc;n đ&aacute;n.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn11.jpg" alt="Đ&aacute;m đ&ocirc;ng chen lấn l&ecirc;n xe về qu&ecirc; ăn tết ở tỉnh Chiết Giang, Trung Quốc. Ảnh: China Daily." width="450" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&aacute;m đ&ocirc;ng chen lấn l&ecirc;n xe về qu&ecirc; ăn tết ở tỉnh Chiết Giang, Trung Quốc. Ảnh:&nbsp;<em>China Daily</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn34.jpg" alt="Người đ&agrave;n &ocirc;ng n&agrave;y nặng cả trước cả sau khi tr&ecirc;n lưng l&agrave; h&agrave;nh l&yacute; v&agrave; trước bụng l&agrave; chiếc Tivi từ Chiết Giang về Th&agrave;nh Đ&ocirc;, Tứ Xuy&ecirc;n. Ảnh: Xinhua." width="380" height="462" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người đ&agrave;n &ocirc;ng n&agrave;y nặng cả trước cả sau khi tr&ecirc;n lưng l&agrave; h&agrave;nh l&yacute; v&agrave; trước bụng l&agrave; chiếc tivi từ Chiết Giang về Th&agrave;nh Đ&ocirc;, Tứ Xuy&ecirc;n. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn32.jpg" alt="Hai phụ nữ mang h&agrave;nh l&yacute; nặng trĩu ra t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về qu&ecirc; nh&agrave; ở tỉnh V&acirc;n Nam. Ảnh: Xinhua." width="450" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai phụ nữ mang h&agrave;nh l&yacute; nặng trĩu ra t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về qu&ecirc; nh&agrave; ở tỉnh V&acirc;n Nam. Bộ đường sắt Trung Quốc triển khai th&ecirc;m 350 chuyến t&agrave;u mỗi ng&agrave;y suốt 40 ng&agrave;y trước v&agrave; sau tết. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn31.jpg" alt="H&agrave;nh kh&aacute;ch xếp h&agrave;ng chờ l&ecirc;n t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang." width="450" height="293" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&agrave;nh kh&aacute;ch xếp h&agrave;ng chờ l&ecirc;n t&agrave;u ở H&agrave;ng Ch&acirc;u, Chiết Giang. Khoảng 230 triệu người Trung Quốc đi t&agrave;u về qu&ecirc; dịp tết nguy&ecirc;n đ&aacute;n n&agrave;y. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn33.jpg" alt="B&agrave; c&otilde;ng ch&aacute;u ra ga ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về Qu&yacute; Ch&acirc;u." width="380" height="471" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&agrave; c&otilde;ng ch&aacute;u ra ga ở H&agrave;ng Ch&acirc;u, Chiết Giang, để về Qu&yacute; Ch&acirc;u. Wang Zhigang, Thứ trưởng Bộ Đường sắt Trung Quốc, cho rằng để giảm t&igrave;nh trạng khan v&eacute;, nước n&agrave;y cần cải hiện hệ thống đường sắt v&agrave; tăng sức vận chuyển. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn35.jpg" alt="Cậu b&eacute; 13 tuổi n&agrave;y c&otilde;ng đống h&agrave;nh l&yacute; to hơn người. Ảnh: Xinhua." width="450" height="281" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cậu b&eacute; 13 tuổi n&agrave;y c&otilde;ng đống h&agrave;nh l&yacute; to hơn người ở nh&agrave; ga H&agrave;ng Ch&acirc;u. T&igrave;nh trạng khan v&eacute; trở n&ecirc;n nghi&ecirc;m trọng hơn bởi nhiều lao động di cư chủ yếu tập trung tới c&aacute;c th&agrave;nh phố lớn v&agrave; ph&aacute;t triển như Bắc Kinh, Thượng Hải v&agrave; Quảng Ch&acirc;u. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cn36.jpg" alt="C&aacute;c em b&eacute; cũng theo cha mẹ xếp h&agrave;ng chờ về qu&ecirc;. Ảnh: Xinhua." width="450" height="274" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c em b&eacute; cũng theo cha mẹ xếp h&agrave;ng chờ về qu&ecirc;. Ảnh:&nbsp;<em>Xinhua.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 6, 2, 1204, 0, 1),
(221, 'Yến tiệc đãi Chủ tịch Trung Quốc tại Mỹ', 'Đệ nhất phu nhân Mỹ trong bộ dạ hội lộng lẫy đứng bên cạnh Tổng thống Barack Obama đón chào Chủ tịch Trung Quốc Hồ Cẩm Đào tới dự bữa yến tiệc được trưng bày xa hoa tại Nhà Trắng, tối 19/1.', 'ho1.jpg', NULL, '<h1 class="Title">Yến tiệc đ&atilde;i Chủ tịch Trung Quốc tại Mỹ</h1>\r\n<h2 class="Lead">Đệ nhất phu nh&acirc;n Mỹ trong bộ dạ hội lộng lẫy đứng b&ecirc;n cạnh Tổng thống Barack Obama đ&oacute;n ch&agrave;o Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o tới dự bữa yến tiệc được trưng b&agrave;y xa hoa tại Nh&agrave; Trắng, tối 19/1.<br /><br /></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho1.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tổng thống Mỹ Barack Obama, Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o c&ugrave;ng Đệ nhất phu nh&acirc;n Mỹ Michelle Obama tới dự bữa quốc tiệc tại Nh&agrave; Trắng ở Washington DC, h&ocirc;m 19/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho3.jpg" border="1" alt="" width="480" height="296" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đệ nhất phu nh&acirc;n Mỹ Michelle Obama c&ugrave;ng Tổng thống Obama đ&oacute;n ch&agrave;o Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o khi &ocirc;ng tới North Portico ở Nh&agrave; Trắng, trước khi tham dự bữa đại tiệc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho6.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c b&agrave;n tiệc trong ph&ograve;ng Đỏ của Nh&agrave; Trắng được b&agrave;i tr&iacute; sẵn s&agrave;ng cho bữa quốc tiệc đ&oacute;n mừng Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho7.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&agrave;n tiệc được sắp đặt c&ocirc;ng phu cho bữa quốc tiệc tại ph&ograve;ng Xanh dương của Nh&agrave; Trắng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho8.jpg" border="1" alt="" width="341" height="512" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c nh&acirc;n vi&ecirc;n Nh&agrave; Trắng cẩn thận chuẩn bị b&agrave;n ăn cho bữa đại tiệc đ&oacute;n ch&agrave;o Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho9.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ph&oacute; tổng thống Mỹ Joe Biden n&acirc;ng cốc ch&uacute;c mừng Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o trong bữa trưa tại Bộ Ngoại giao Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho4.jpg" border="1" alt="" width="480" height="323" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o c&ugrave;ng Ngoại trưởng Mỹ Hillary Rodham Clinton n&acirc;ng cốc ch&uacute;c mừng trong bữa trưa tại Bộ Ngoại giao Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho12.jpg" border="1" alt="" width="480" height="323" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trước đ&oacute; v&agrave;o buổi s&aacute;ng Tổng thống Mỹ Obama ch&agrave;o mừng Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o trong lễ đ&oacute;n tiếp long trọng tại dinh tổng thống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho11.jpg" border="1" alt="" width="480" height="301" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o c&ugrave;ng Tổng thống Mỹ Obama vẫy ch&agrave;o người d&acirc;n tại Lễ đ&oacute;n quốc gia ở Nh&agrave; Trắng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho5.jpg" border="1" alt="" width="341" height="512" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tổng thống Mỹ Obama lắng nghe lời ph&aacute;t biểu của Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o trong cuộc gặp mặt với c&aacute;c nh&agrave; l&atilde;nh đạo kinh doanh ở T&ograve;a nh&agrave; văn ph&ograve;ng Eisenhower. &Ocirc;ng Hồ đang c&oacute; chuyến thăm ch&iacute;nh thức cấp nh&agrave; nước d&agrave;i 4 ng&agrave;y tới Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>AFP</em>)</p>', 6, 2, 1899, 0, 1),
(222, 'Đại hội Đảng XI Việt Nam trên báo nước ngoài', 'Đại hội lần thứ 11 của Đảng Cộng sản Việt Nam thu hút sự chú ý của báo chí quốc tế. Dưới đây là một số hình ảnh mà các hãng thông tấn nước ngoài ghi lại về sự kiện chính trị quan trọng này. ', '1 (2)_1.jpg', NULL, '<h1 class="Title">Đại hội Đảng XI Việt Nam tr&ecirc;n b&aacute;o nước ngo&agrave;i</h1>\r\n<h2 class="Lead">Đại hội lần thứ 11 của Đảng Cộng sản Việt Nam thu h&uacute;t sự ch&uacute; &yacute; của b&aacute;o ch&iacute; quốc tế. Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh m&agrave; c&aacute;c h&atilde;ng th&ocirc;ng tấn nước ngo&agrave;i ghi lại về sự kiện ch&iacute;nh trị quan trọng n&agrave;y.&nbsp;<br />&gt;<a class="Lead" href="http://vnexpress.net/GL/Xa-hoi/Dai-hoi-Dang-XI/2011/01/3BA258D1/">&Ocirc;ng Nguyễn Ph&uacute; Trọng đắc cử Tổng b&iacute; thư</a>/&nbsp;<a class="Lead" href="http://vnexpress.net/GL/Xa-hoi/2011/01/3BA25947/">&lsquo;T&ocirc;i kh&ocirc;ng chủ trương l&agrave;m để tạo dấu ấn&rsquo;</a></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1.jpg" border="1" alt="" width="480" height="436" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tổng b&iacute; thư vừa đắc cử Nguyễn Ph&uacute; Trọng trả lời phỏng vấn của b&aacute;o ch&iacute; trong nước v&agrave; quốc tế sau Đại hội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="2.jpg" border="1" alt="" width="480" height="226" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">(H&agrave;ng 1, tr&aacute;i sang) C&aacute;c ủy vi&ecirc;n Bộ Ch&iacute;nh trị mới đắc cử Nguyễn Tấn Dũng, Nguyễn Ph&uacute; Trọng, Trương Tấn Sang, Nguyễn Sinh H&ugrave;ng c&ugrave;ng c&aacute;c ủy vi&ecirc;n trung ương mới ra mắt đại hội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="3.jpg" border="1" alt="" width="480" height="428" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tổng b&iacute; thư kh&oacute;a X N&ocirc;ng Đức Mạnh (phải) tặng hoa v&agrave; &ocirc;m h&ocirc;n ch&uacute;c mừng t&acirc;n Tổng b&iacute; thư Nguyễn Ph&uacute; Trọng. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="4.jpg" border="1" alt="" width="480" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">To&agrave;n cảnh hội trường Đại hội Đảng Cộng sản Việt Nam lần thứ XI, diễn ra từ 12 đến 19/1 tại H&agrave; Nội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="5.jpg" border="1" alt="" width="480" height="316" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c vị l&atilde;nh đạo của Việt Nam tr&ecirc;n đo&agrave;n chủ tịch đại hội. Từ tr&aacute;i sang: &ocirc;ng Nguyễn Sinh H&ugrave;ng, ph&oacute; thủ tướng; &ocirc;ng Nguyễn Tấn Dũng, thủ tướng; &ocirc;ng L&ecirc; Hồng Anh, bộ trưởng C&ocirc;ng an. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="6.jpg" border="1" alt="" width="480" height="254" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c đại biểu giơ cao thẻ đảng vi&ecirc;n trước khi bắt đầu cuộc bỏ phiếu bầu Ban chấp h&agrave;nh trung ương mới. Gần 1.400 đảng vi&ecirc;n Cộng sản tham dự Đại hội XI. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="7.jpg" border="1" alt="" width="480" height="344" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một đảng vi&ecirc;n cầm tấm phiếu bầu chờ đến lượt bỏ phiếu ra Ban chấp h&agrave;nh trung ương đảng, gồm gần 200 vị. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="8.jpg" border="1" alt="" width="380" height="531" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&acirc;y l&agrave; quang cảnh ngo&agrave;i sảnh của Trung t&acirc;m Hội nghị Quốc gia, nơi diễn ra Đại hội Đảng XI. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="9.jpg" border="1" alt="" width="480" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cảnh vệ c&oacute; vũ trang đứng g&aacute;c b&ecirc;n ngo&agrave;i trung t&acirc;m hội nghị. B&ecirc;n cạnh anh l&agrave; h&agrave;ng loạt băng r&ocirc;n khẩu hiệu ch&agrave;o mừng đại hội. Ảnh: AFP</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="11.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tr&ecirc;n c&aacute;c con phố của H&agrave; Nội chăng nhiều cờ v&agrave; biểu ngữ ch&agrave;o mừng. Một chiếc xe hơi đắt tiền đang lướt đi b&ecirc;n dưới c&aacute;c biểu ngữ. L&atilde;nh đạo để ph&aacute;t triển kinh tế l&agrave; một trong những nhiệm vụ quan trọng của Đảng cầm quyền. Ảnh: AP.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="10.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Với lực lượng d&acirc;n số đ&ocirc;ng v&agrave; trẻ, Việt Nam c&oacute; nguồn lao động dồi d&agrave;o ở "độ tuổi v&agrave;ng". Trong ảnh, nam nữ thanh ni&ecirc;n chụp ảnh kỷ niệm b&ecirc;n c&aacute;c thảm hoa kết th&agrave;nh chữ ch&agrave;o mừng Đại hội XI. Ảnh: AP.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Thanh Mai</strong></p>', 6, 2, 1272, 0, 1),
(223, 'Quân đội Ấn Độ phô diễn sức mạnh', 'Binh lính thuộc các binh chủng Ấn Độ biểu dương sức mạnh trong cuộc diễu hành tại New Delhi, hôm 15/1, nhằm kỷ niệm 63 năm ngày thành lập quân đội.', 'an1.jpg', NULL, '<h1 class="Title">Qu&acirc;n đội Ấn Độ ph&ocirc; diễn sức mạnh</h1>\r\n<h2 class="Lead">Binh l&iacute;nh thuộc c&aacute;c binh chủng Ấn Độ biểu dương sức mạnh trong cuộc diễu h&agrave;nh tại New Delhi, h&ocirc;m 15/1, nhằm kỷ niệm 63 năm ng&agrave;y th&agrave;nh lập qu&acirc;n đội.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an1.jpg" border="1" alt="" width="480" height="265" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khẩu đội ph&aacute;o khai hỏa mở m&agrave;n cho cuộc diễu h&agrave;nh ho&agrave;nh tr&aacute;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an2.jpg" border="1" alt="" width="480" height="286" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lực lượng xe tăng diễu h&agrave;nh qua lễ đ&agrave;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an3.jpg" border="1" alt="" width="480" height="282" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&agrave;n duyệt binh của lực lượng bộ binh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an4.jpg" border="1" alt="" width="480" height="278" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Binh l&iacute;nh Ấn Độ trong bộ qu&acirc;n phục truyền thống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an5.jpg" border="1" alt="" width="480" height="381" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c chiến sĩ tạo h&igrave;nh ấn tượng tr&ecirc;n chiếc m&ocirc; t&ocirc; ph&acirc;n khối lớn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an6.jpg" border="1" alt="" width="480" height="386" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một pha biểu diễn mạo hiểm tr&ecirc;n xe m&aacute;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an7.jpg" border="1" alt="" width="480" height="269" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trực thăng qu&acirc;n sự cũng tham gia m&agrave;n diễu h&agrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an8.jpg" border="1" alt="" width="480" height="241" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Xe tăng Ấn Độ diễu h&agrave;nh c&ugrave;ng c&aacute;c binh l&iacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an9.jpg" border="1" alt="" width="480" height="265" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&agrave;n đồng diễn ấn tượng tr&ecirc;n c&aacute;c xe m&ocirc; t&ocirc; ph&acirc;n khối lớn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>Xinhua</em>)</p>', 6, 2, 772, 0, 1),
(224, 'Khoảnh khắc ấn tượng của Obama năm 2010', 'Pete Souza - nhiếp ảnh gia chính thức của Nhà Trắng - đưa người xem trải qua những giây phút riêng tư của người đàn ông quyền lực nhất nước Mỹ - Barack Obama, trong năm 2010.', 'obama1.jpg', NULL, '<h1 class="Title">Khoảnh khắc ấn tượng của Obama năm 2010</h1>\r\n<h2 class="Lead"><strong>Pete Souza - nhiếp ảnh gia ch&iacute;nh thức của Nh&agrave; Trắng - đưa người xem trải qua những gi&acirc;y ph&uacute;t ri&ecirc;ng tư của người đ&agrave;n &ocirc;ng quyền lực nhất nước Mỹ - Barack Obama, trong năm 2010.</strong></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama1.jpg" border="1" alt="" width="480" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">20/1/2010.</p>\r\n<p class="Image">"Đ&oacute; l&agrave; đ&ecirc;m sau cuộc bầu cử đặc biệt tại Massachusetts, khi th&agrave;nh vi&ecirc;n đảng Cộng h&ograve;a Scott Brown bất ngờ d&agrave;nh được ghế của nghị sĩ Edward Kennedy tại Nghị viện. Đảng D&acirc;n chủ mất đi 60 số phiếu trong Nghị viện, khiến cho việc th&ocirc;ng qua kế hoạch cải c&aacute;ch y tế gặp kh&oacute; khăn. Sự căng thẳng hiện r&otilde; tr&ecirc;n mặt của Tổng thống v&agrave; c&aacute;c trợ l&yacute;" - Pete Souza, ph&oacute;ng vi&ecirc;n ảnh ch&iacute;nh thức của Nh&agrave; Trắng.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em>&nbsp;</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama4.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">29/1/2010.</p>\r\n<p class="Image">Obama nh&igrave;n từ đằng sau khi &ocirc;ng ngồi trong ghế Tổng thống tại ph&ograve;ng Nội c&aacute;c. Miếng thẻ gắn tr&ecirc;n ghế ghi ng&agrave;y &ocirc;ng nhậm chức tổng thống.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama5.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">29/1/2010.</p>\r\n<p class="Image">Tổng thống c&oacute; mặt tại Baltimore để ph&aacute;t biểu tại một cuộc họp của Hạ viện. Khi &ocirc;ng chuẩn bị bước v&agrave;o ph&ograve;ng họp, Obama đề nghị Bộ trưởng giao th&ocirc;ng Ray LaHood đi trước để &ocirc;ng n&uacute;p đằng sau.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama6.jpg" border="1" alt="" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">19/3/2010.</p>\r\n<p class="Image">Sau bữa tối, Tổng thống trở lại ph&ograve;ng Bầu dục để tiếp tục th&uacute;c &eacute;p Hạ viện bỏ phiếu th&ocirc;ng qua dự luật cải c&aacute;ch y tế. Trong những ng&agrave;y trước cuộc bỏ phiếu, Obama thực hiện h&agrave;ng trăm cuộc gọi.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama7.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">23/3/2010.</p>\r\n<p class="Image">Cận cảnh chữ k&yacute; của Tổng thống Obama th&ocirc;ng qua dự luật cải c&aacute;ch y tế. Tổng thống đ&atilde; k&yacute; văn bản n&agrave;y bằng 22 chiếc b&uacute;t kh&aacute;c nhau.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama8.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">14/4/2010.</p>\r\n<p class="Image">Tổng thống lắng nghe chăm ch&uacute; c&aacute;c cố vấn cấp cao của m&igrave;nh tại một cuộc họp ở ph&ograve;ng Bầu dục.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> <br />\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(Ảnh: Nh&agrave; Trắng)</p>\r\n</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama9.jpg" border="1" alt="" width="480" height="295" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">28/4/2010.</p>\r\n<p class="Image">Tại một qu&aacute;n cafe ở Missouri, Obama chụp ảnh th&acirc;n mật với c&aacute;c nh&acirc;n vi&ecirc;n v&agrave; một người đ&agrave;n &ocirc;ng đ&atilde; l&agrave;m một cử chỉ h&agrave;i hước ph&iacute;a sau Tổng thống.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="obama10.jpg" border="1" alt="" width="480" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">31/5/2010.</p>\r\n<p class="Image">Trời đổ mưa trong ng&agrave;y Memorial Day ở Chicago. Khi sấm chớp nổi l&ecirc;n, c&aacute;c nh&acirc;n vi&ecirc;n an ninh khuyến c&aacute;o tổng thống kh&ocirc;ng n&ecirc;n ph&aacute;t biểu. Obama tự m&igrave;nh l&ecirc;n s&acirc;n khấu v&agrave; th&ocirc;ng b&aacute;o với kh&aacute;n giả rằng buổi tr&ograve; chuyện của &ocirc;ng bị ho&atilde;n v&agrave; y&ecirc;u cầu mọi người trở lại xe v&igrave; sự an to&agrave;n. Sau đ&oacute;, &ocirc;ng c&ograve;n l&ecirc;n một số xe bu&yacute;t để cảm ơn mọi người đ&atilde; tới dự v&agrave; xin lỗi v&igrave; kh&ocirc;ng thể ph&aacute;t biểu.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', 6, 2, 1561, 0, 1),
(225, 'Bà Clinton ngã trên máy bay', 'Ngoại trưởng Mỹ Hillary Clinton vấp ngã khi lên máy bay ở Yemen sau khi kết thúc chuyến công cán tại nước này.', 'cl1.jpg', NULL, '<h1 class="Title">B&agrave; Clinton ng&atilde; tr&ecirc;n m&aacute;y bay</h1>\r\n<h2 class="Lead">Ngoại trưởng Mỹ Hillary Clinton vấp ng&atilde; khi l&ecirc;n m&aacute;y bay ở Yemen sau khi kết th&uacute;c chuyến c&ocirc;ng c&aacute;n tại nước n&agrave;y.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cl1.jpg" border="1" alt="Ngoại trưởng Mỹ Hillary Clinton. Ảnh: AFP." width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngoại trưởng Mỹ Hillary Clinton. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nữ ngoại trưởng Mỹ vẫy tay ch&agrave;o đ&aacute;m đ&ocirc;ng v&agrave; nhiếp ảnh gia khi b&agrave; l&ecirc;n m&aacute;y bay. Khi vừa tới cửa, b&agrave; bị vấp ng&atilde;. Một người đ&agrave;n &ocirc;ng nhanh ch&oacute;ng gi&uacute;p b&agrave; đứng dậy, sau đ&oacute;, đ&egrave;n trong m&aacute;y bay từ từ tối dần.</p>\r\n<p class="Normal">Trợ l&yacute; của nữ ngoại trưởng cho biết kh&ocirc;ng giống như lần ng&atilde; ở Nh&agrave; Trắng, lần n&agrave;y Clinton kh&ocirc;ng bị hề hấn g&igrave;,&nbsp;<em>CNN</em>&nbsp;đưa tin.</p>\r\n<p class="Normal">C&aacute;c ch&iacute;nh trị gia Mỹ hiểu đường l&ecirc;n m&aacute;y bay "nguy hiểm" thế n&agrave;o nhất l&agrave; khi c&oacute; v&ocirc; số camera theo từng bước ch&acirc;n của họ. V&igrave; thế, mỗi tổng thống Mỹ tự tạo cho m&igrave;nh một phong c&aacute;ch ri&ecirc;ng. Barack Obama thường nh&uacute;n nhảy. Bill Clinton th&igrave; bước đi thư th&aacute;i. George Bush th&igrave; kh&ocirc;ng rời tay khỏi thanh b&aacute;m - điều m&agrave; Gerald Ford đ&aacute;ng lẽ n&ecirc;n l&agrave;m. Video c&uacute; ng&atilde; của Ford khi &ocirc;ng rời chiếc chuy&ecirc;n cơ Air Force One xuống đất &Aacute;o từng được phối nhạc v&agrave; được lan truyền rộng r&atilde;i tr&ecirc;n mạng.</p>\r\n<p class="Normal">Kh&ocirc;ng chỉ quan chức Mỹ c&oacute; xu hướng vấp ng&atilde; tr&ecirc;n m&aacute;y bay trong l&uacute;c v&ocirc; số con mắt đổ v&agrave;o họ. Tổng thống Ph&aacute;p Nicholas Sarkozy c&oacute; lần lảo đảo su&yacute;t ng&atilde;. Obama chưa từng gặp sự cố tương tự song &ocirc;ng đ&aacute;nh rơi mất chiếc Blackberry khi nh&uacute;n nhảy l&ecirc;n m&aacute;y bay. &Ocirc;ng cũng từng đập đầu v&agrave;o cửa chiếc trực thăng của tổng thống.</p>\r\n<p class="Normal">Tổng thống George W. Bush từng hơn một lần đập đầu v&agrave;o m&aacute;y bay. Đương kim đệ nhất phu nh&acirc;n Michelle Obama cũng gặp sự cố tương tự.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong></p>', 6, 2, 505, 0, 1),
(227, 'Lễ hội tuổi 20 của thiếu nữ Nhật Bản', 'Ngày hội trưởng thành được tổ chức hằng năm tại Nhật Bản vào ngày thứ Hai thứ 2 trong tháng một để chúc mừng những cô cậu tròn 20 tuổi.', 'girl1.jpg', NULL, '<h1 class="Title">Lễ hội tuổi 20 của thiếu nữ Nhật Bản</h1>\r\n<h2 class="Lead">Ng&agrave;y hội trưởng th&agrave;nh được tổ chức hằng năm tại Nhật Bản v&agrave;o ng&agrave;y thứ Hai thứ 2 trong th&aacute;ng một để ch&uacute;c mừng những c&ocirc; cậu tr&ograve;n 20 tuổi.</h2>\r\n<p class="Normal">Theo một b&aacute;o c&aacute;o của Nhật Bản, c&oacute; khoảng 1.240.000 c&ocirc; g&aacute;i v&agrave; ch&agrave;ng trai sinh năm 1990 v&agrave; đạt độ tuổi trưởng th&agrave;nh đầu năm nay. Đ&acirc;y l&agrave; con số nhỏ nhất kể từ năm 1968 tại đất nước c&oacute; d&acirc;n số gi&agrave; nhất thế giới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl1.jpg" border="1" alt="" width="460" height="351" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i diện trang phục kimono truyền thống đến tham dự lễ hội Ng&agrave;y trưởng th&agrave;nh tại một c&ocirc;ng vi&ecirc;n giải tr&iacute; ở Tokyo, h&ocirc;m qua. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl2.jpg" border="1" alt="" width="345" height="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i trong trang phục rực rỡ đứng chụp h&igrave;nh với nhau. Ảnh:<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl3.jpg" border="1" alt="" width="460" height="306" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trong ng&agrave;y hội n&agrave;y, những c&ocirc; g&aacute;i mới lớn viết lời ước nguyện l&ecirc;n c&aacute;c miếng thẻ tại một ng&ocirc;i đền để cầu mong lời ước của m&igrave;nh sẽ th&agrave;nh hiện thực. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl4.jpg" border="1" alt="" width="460" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai c&ocirc; g&aacute;i hồ hởi viết lời ước cho m&igrave;nh. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl5.jpg" border="1" alt="" width="460" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một đ&ocirc;i nam nữ chụp ảnh kỷ niệm nh&acirc;n ng&agrave;y trọng đại trong đời. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl7.jpg" border="1" alt="" width="460" height="276" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i l&ecirc;n t&agrave;u điện ngầm để tới c&ocirc;ng vi&ecirc;n giải tr&iacute;. Con g&aacute;i Nhật Bản c&oacute; thể kết h&ocirc;n ở tuổi 16, v&agrave; con trai ở tuổi 18, nhưng họ chỉ thực sự được coi l&agrave; trưởng th&agrave;nh khi tr&ograve;n 20 tuổi. Ảnh:&nbsp;<em>EPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="girl6.jpg" border="1" alt="" width="460" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i xinh đẹp h&ograve; reo khi chơi tr&ograve; t&agrave;u lượn tại c&ocirc;ng vi&ecirc;n giải tr&iacute;, nh&acirc;n ng&agrave;y hội Trưởng th&agrave;nh. Ảnh:&nbsp;<em>Xinhua</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong></p>', 6, 2, 2775, 0, 1),
(237, 'Ngoại ô Paris trắng xóa tuyết', 'Những mái nhà trắng xóa như trong truyện cổ tích, bông tuyết bám lên cành cây tạo ra khung cảnh lãng mạn cho các nhiếp ảnh gia. Paris trắng tuyết qua ảnh của độc giả Hà Hải.', 'paris1.jpg', NULL, '<h1 class="Title">Ngoại &ocirc; Paris trắng x&oacute;a tuyết</h1>\r\n<h2 class="Lead">Những m&aacute;i nh&agrave; trắng x&oacute;a như trong truyện cổ t&iacute;ch, b&ocirc;ng tuyết b&aacute;m l&ecirc;n c&agrave;nh c&acirc;y tạo ra khung cảnh l&atilde;ng mạn cho c&aacute;c nhiếp ảnh gia. Paris trắng tuyết qua ảnh của độc giả H&agrave; Hải.<br />&gt;&nbsp;<a class="Lead" href="http://vnexpress.net/ContactUs/?id=thegioi@vnexpress.net">Chia sẻ ảnh ở đ&acirc;y</a></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris1.jpg" border="1" alt="Chỉ trong rơi trong một thời gian ngắn l&uacute;c rạng s&aacute;ng, tuyết đ&atilde; phủ hết mọi vật." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&ugrave;a đ&ocirc;ng năm nay ở ch&acirc;u &Acirc;u tuyết đến sớm v&agrave; nhiều, li&ecirc;n tục trong v&ograve;ng một th&aacute;ng. Ở Paris, c&aacute;c kỷ lục về độ d&agrave;y của tuyết rơi được lập rồi lại được ph&aacute;. Tuyết l&agrave;m ảnh hưởng kh&ocirc;ng &iacute;t đến sinh hoạt v&agrave; c&ocirc;ng việc của nhiều người. C&oacute; những đoạn đường kẹt xe h&agrave;ng chục km v&igrave; tuyết. C&oacute; những người phải ngủ đ&ecirc;m tr&ecirc;n xe v&igrave; kh&ocirc;ng thể về nh&agrave;. Ảnh được chụp ở Evry, ngoại &ocirc; Paris.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris2.jpg" border="1" alt="M&aacute;i nh&agrave; biến th&agrave;nh m&agrave;u trắng, c&aacute;c c&agrave;nh c&acirc;y như được phết b&ocirc;ng." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&aacute;i nh&agrave; biến th&agrave;nh m&agrave;u trắng, c&aacute;c c&agrave;nh c&acirc;y như được phết b&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris3.jpg" border="1" alt="Lối đi ngập tr&agrave;n tuyết xốp, tinh kh&ocirc;i." width="480" height="324" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lối đi ngập tr&agrave;n tuyết xốp, tinh kh&ocirc;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris4.jpg" border="1" alt="Từng bước ch&acirc;n ngập s&acirc;u trong tuyết." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Từng bước ch&acirc;n ngập s&acirc;u trong tuyết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris5.jpg" border="1" alt="C&aacute;c lối đi bộ được x&uacute;c tuyết v&agrave; rải muối chống trơn trượt. Người đ&agrave;n &ocirc;ng n&agrave;y đang đẩy một c&aacute;i xe rải muối." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c lối đi bộ được x&uacute;c tuyết v&agrave; rải muối chống trơn trượt. Người đ&agrave;n &ocirc;ng n&agrave;y đang đẩy một c&aacute;i xe rải muối.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris6.jpg" border="1" alt="Nhưng đường &ocirc; t&ocirc; vẫn vắng vẻ hơn thường ng&agrave;y." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đường &ocirc; t&ocirc; vẫn vắng vẻ hơn thường ng&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris7.jpg" border="1" alt="Xe bu&yacute;t ngưng hoạt động. C&oacute; người chọn giải ph&aacute;p thay thế. Người đ&agrave;n &ocirc;ng n&agrave;y đẹp xe được một l&uacute;c th&igrave; đ&agrave;nh dắt bộ v&igrave; b&aacute;nh xe l&uacute;n s&acirc;u trong tuyết." width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Xe bu&yacute;t ngưng hoạt động. Nhiều người chọn giải ph&aacute;p thay thế. Người đ&agrave;n &ocirc;ng n&agrave;y đạp xe được một l&uacute;c th&igrave; đ&agrave;nh dắt bộ v&igrave; b&aacute;nh xe l&uacute;n s&acirc;u trong tuyết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris8.jpg" border="1" alt="C&aacute;c đường t&agrave;u c&ugrave;ng phủ trắng tuyết. Đa số c&aacute;c tuyến t&agrave;u điện đều giảm chuyến hoặc hủy chuyến." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c đường t&agrave;u cũng phủ trắng tuyết. Đa số c&aacute;c tuyến t&agrave;u điện đều giảm chuyến hoặc hủy chuyến.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris9.jpg" border="1" alt="Nhiệt độ kh&ocirc;ng qu&aacute; thấp nhưng c&aacute;c trường học đều đ&oacute;ng cửa." width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiệt độ kh&ocirc;ng qu&aacute; thấp nhưng c&aacute;c trường học đều đ&oacute;ng cửa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris10.jpg" border="1" alt="Tuy g&acirc;y nhiều thiệt hại, khung cảnh tuyết cũng rất l&atilde;ng mạn, nhất l&agrave; trong mắt của c&aacute;c tay nhiếp ảnh." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tuy g&acirc;y nhiều thiệt hại, khung cảnh tuyết cũng rất l&atilde;ng mạn, nhất l&agrave; trong mắt của c&aacute;c tay nhiếp ảnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris11.jpg" border="1" alt="Cũng l&agrave; cơ hội để vui chơi với th&uacute; đắp người tuyết." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&acirc;y cũng l&agrave; cơ hội để vui chơi với th&uacute; đắp người tuyết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="paris12.jpg" border="1" alt="Hấp dẫn kh&ocirc;ng chỉ trẻ con." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tr&ograve; n&agrave;y hấp dẫn kh&ocirc;ng chỉ trẻ con.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Hải</strong></p>', 6, 2, 2272, 0, 1),
(241, 'Phái đẹp Israel xung trận trên sa mạc', 'Các cô gái thuộc Lực lượng tự vệ Israel đã tham gia vào một cuộc tập trận trên sa mạc gần thành phố Sede Boker, nằm sát biên giới Ai Cập, hôm 13/12.', 'nu1.jpg', NULL, '<h1 class="Title">Ph&aacute;i đẹp Israel xung trận tr&ecirc;n sa mạc</h1>\r\n<h2 class="Lead">C&aacute;c c&ocirc; g&aacute;i thuộc Lực lượng tự vệ Israel đ&atilde; tham gia v&agrave;o một cuộc tập trận tr&ecirc;n sa mạc gần th&agrave;nh phố Sede Boker, nằm s&aacute;t bi&ecirc;n giới Ai Cập, h&ocirc;m 13/12.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu1.jpg" border="1" alt="" width="480" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i hăng h&aacute;i xung trận tr&ecirc;n sa trường.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu2.jpg" border="1" alt="" width="480" height="345" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Họ sử dụng th&agrave;nh thạo c&aacute;c vũ kh&iacute; hạng nặng kh&ocirc;ng kh&aacute;c g&igrave; ph&aacute;i mạnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu3.jpg" border="1" alt="" width="480" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cuộc tập trận diễn ra li&ecirc;n tục bất kể ng&agrave;y v&agrave; đ&ecirc;m.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu4.jpg" border="1" alt="" width="480" height="277" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ph&aacute;i nữ ng&agrave;y c&agrave;ng chứng tỏ mạnh mẽ kh&ocirc;ng k&eacute;m g&igrave; ph&aacute;i mạnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu5.jpg" border="1" alt="" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một c&ocirc; g&aacute;i chỉnh sửa đầu t&oacute;c trước khi v&agrave;o cuộc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu7.jpg" border="1" alt="" width="399" height="600" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&acirc;n dung một b&oacute;ng hồng tr&ecirc;n sa mạc Israel.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu8.jpg" border="1" alt="" width="480" height="362" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Điều kiện thời tiết khắc nghiệt kh&ocirc;ng l&agrave;m c&aacute;c c&ocirc; g&aacute;i nản l&ograve;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu9.jpg" border="1" alt="" width="480" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gi&acirc;y ph&uacute;t cười sảng kho&aacute;i của một nữ chiến sĩ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nu10.jpg" border="1" alt="" width="480" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c c&ocirc; g&aacute;i tập dượt khi&ecirc;ng đồng đội bị thương tr&ecirc;n vai.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Song Minh</strong>&nbsp;(Ảnh:&nbsp;<em>Xinhua</em>)</p>', 6, 2, 1493, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(245, '10 ảnh chiến sự khốc liệt năm 2010', 'Đôi trai gái dắt díu nhau chạy qua đường phố Bangkok ngút ngàn khói lửa. Em bé trong vòng tay người lính, gương mặt trẻ thơ đầy sợ hãi. Dưới đây là ảnh đáng nhớ về các cuộc xung đột năm qua.', 'bangkok.jpg', NULL, '<h1 class="Title">10 ảnh chiến sự khốc liệt năm 2010</h1>\r\n<h2 class="Lead">Đ&ocirc;i trai g&aacute;i dắt d&iacute;u nhau chạy qua đường phố Bangkok ng&uacute;t ng&agrave;n kh&oacute;i lửa. Em b&eacute; trong v&ograve;ng tay người l&iacute;nh, gương mặt trẻ thơ đầy sợ h&atilde;i. Dưới đ&acirc;y l&agrave; ảnh đ&aacute;ng nhớ về c&aacute;c cuộc xung đột năm qua.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bangkok.jpg" alt="Những cư d&acirc;n ở gần khu trại của &aacute;o đỏ đi qua đống kh&oacute;i lửa cuồn cuộn, bốc l&ecirc;n từ những chiếc lốp xe bị đốt tại Bangkok, Th&aacute;i Lan." width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những cư d&acirc;n ở gần khu trại của người biểu t&igrave;nh đi qua đống kh&oacute;i lửa cuồn cuộn, bốc l&ecirc;n từ những chiếc lốp xe bị đốt tại Bangkok, Th&aacute;i Lan, h&ocirc;m 16/5. C&aacute;c cuộc biểu t&igrave;nh của phe &aacute;o đỏ Th&aacute;i Lan k&eacute;o d&agrave;i từ đầu th&aacute;ng ba đến cuối th&aacute;ng 5 khiến một phần thủ đ&ocirc; t&ecirc; liệt. Ảnh:&nbsp;<em>AFP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="kyrgyzstan.jpg" alt="Binh sĩ đặc nhiệm Uzbekistan bế một em b&eacute; tị nạn từ Kyrgyzstan. Ảnh: Ria Novosti." width="480" height="363" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Binh sĩ đặc nhiệm Uzbekistan bế một em b&eacute; tị nạn từ Kyrgyzstan sang hồi th&aacute;ng 6. C&aacute;c cuộc đụng độ sắc tộc giữa người thiểu số Uzbekistan v&agrave; Kyrgyzstan tại miềm nam Kyrgyzstan khiến &iacute;t nhất 138 người thiệt mạng v&agrave; hơn 1.700 người bị thương. Khoảng 100.000 người chạy loạn đ&atilde; đổ v&agrave;o Uzbekistan. Ảnh:&nbsp;<em>Ria Novosti.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="n21.jpg" alt="" width="480" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Binh sĩ Mỹ kh&aacute;m người một t&ograve;ng phạm đ&aacute;nh bom của Taliban tại tỉnh Kandahar ở Afghanistan h&ocirc;m 10/10. Nghi phạm, nằm trong xe k&eacute;o, bị hạ s&aacute;t. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="israel.jpg" alt="" width="480" height="305" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Binh sĩ Israel bắn hơi cay về ph&iacute;a c&aacute;c thiếu ni&ecirc;n Palestine trong một cuộc đụng độ ở th&agrave;nh phố Beit Fajjar, khu Bờ T&acirc;y, gần Bethlehem, Israel, hồi th&aacute;ng 10. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="kyrgyzstan2.jpg" alt="" width="480" height="293" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cảnh sát Kyrgyzstan bị thương co cụm lại khi bị người biểu t&igrave;nh tấn c&ocirc;ng h&ocirc;m 7/4. Cuộc nổi dậy đẫm m&aacute;u của phe đối lập đ&atilde; lật đổ ch&iacute;nh phủ của tổng thống Kurmanbek Bakiyev. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="han-quoc.jpg" alt="Những cột kh&oacute;i bốc l&ecirc;n từ đảo H&agrave;n Quốc sau trận ph&aacute;o k&iacute;ch của Triều Ti&ecirc;n h&ocirc;m 23/11. Ảnh: AFP." width="480" height="306" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những cột kh&oacute;i bốc l&ecirc;n từ đảo Yeonpyeong H&agrave;n Quốc sau trận ph&aacute;o k&iacute;ch của Triều Ti&ecirc;n h&ocirc;m 23/11. Ảnh:&nbsp;<em>AFP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="han-quoc-2.jpg" alt="Đạn ph&oacute;ng đi từ t&agrave;u chiến H&agrave;n Quốc trong một cuộc tập trận." width="480" height="292" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đạn ph&oacute;ng đi từ t&agrave;u chiến H&agrave;n Quốc trong một cuộc tập trận chống t&agrave;u ngầm chung với Mỹ h&ocirc;m 27/5 sau khi chiếm hạm Cheonan nước n&agrave;y gặp nạn khiến 46 thủy thủ thiệt mạng. Ủy ban điều tra quốc tế cho rằng ngư l&ocirc;i từ t&agrave;u ngầm Triều Ti&ecirc;n khiến con t&agrave;u nổ tung h&ocirc;m 26/3. Ảnh:&nbsp;<em>EPA.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="mexico.jpg" alt="Binh sĩ Mexico bắt một thanh ni&ecirc;n bị nghi l&agrave; kẻ giết thu&ecirc; cho băng đảng bu&ocirc;n b&aacute;n ma t&uacute;y" width="480" height="325" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Binh sĩ Mexico bắt một thanh ni&ecirc;n bị nghi l&agrave; kẻ giết thu&ecirc; cho băng đảng bu&ocirc;n b&aacute;n ma t&uacute;y ở th&agrave;nh phố Cuernavaca. Khoảng 28.000 người thiệt mạng trong cuộc chiến chống ma t&uacute;y k&eacute;o d&agrave;i khoảng 4 năm kể từ năm 2006 dưới thời Tổng thống Felipe Calderon. Năm nay, cuộc chiến trở n&ecirc;n khốc liệt hơn với h&agrave;ng loạt vụ thanh to&aacute;n băng đảng đẫm m&aacute;u v&agrave; bắn giết cảnh s&aacute;t. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="kashmir.jpg" alt="Một thiếu ni&ecirc;n Kashmir n&eacute;m gạch đ&aacute; về ph&iacute;a cảnh s&aacute;t" width="353" height="500" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một thiếu ni&ecirc;n n&eacute;m gạch đ&aacute; về ph&iacute;a cảnh s&aacute;t trong cuộc biểu t&igrave;nh ở Srinagar, phần đất do Ấn Độ kiểm so&aacute;t tại khu vực Kashmir, h&ocirc;m 29/6. Bạo lực vẫn &acirc;m ỉ tại khu vực đ&ograve;i ly khai n&agrave;y. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="iraq.jpg" alt="Những binh sĩ chiến đấu cuối c&ugrave;ng của Mỹ r&uacute;t khỏi Iraq, đ&aacute;nh dấu sự kết th&uacute;c của cuộc chiến k&eacute;o d&agrave;i 7 năm tại quốc gia v&ugrave;ng Vịnh n&agrave;y." width="480" height="275" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những binh sĩ chiến đấu cuối c&ugrave;ng của Mỹ r&uacute;t khỏi Iraq hồi th&aacute;ng 8, đ&aacute;nh dấu sự kết th&uacute;c của cuộc chiến k&eacute;o d&agrave;i 7 năm tại quốc gia v&ugrave;ng Vịnh. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 6, 2, 2795, 0, 1),
(1632, 'Tiêu chuẩn làm nên một bức ảnh mẫu mực', 'Người chụp ảnh có thể áp dụng nhiều quy luật nhiếp ảnh hay thử nghiệm các kỹ thuật khác nhau nhưng một bức ảnh đẹp phải lột tả được những yêu cầu về bố cục, ánh sáng...', 'photo-1 (1).jpg', NULL, '<h1 class="Title">Ti&ecirc;u chuẩn l&agrave;m n&ecirc;n một bức ảnh mẫu mực</h1>\r\n<h2 class="Lead">Người chụp ảnh c&oacute; thể &aacute;p dụng nhiều quy luật nhiếp ảnh hay thử nghiệm c&aacute;c kỹ thuật kh&aacute;c nhau nhưng một bức ảnh đẹp phải lột tả được những y&ecirc;u cầu về bố cục, &aacute;nh s&aacute;ng...</h2>\r\n<p class="Normal">Blog&nbsp;<em>Hongkiat</em>&nbsp;đ&atilde; liệt k&ecirc; những yếu tố để đ&aacute;nh gi&aacute; ảnh xuất sắc:</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="307" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="photo-1 (1).jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">Tạo ấn tượng từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-2.jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">Bố cục ho&agrave;n hảo.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-3.jpg" border="1" alt="" width="450" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">&Aacute;nh s&aacute;ng hợp l&yacute;.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-4.jpg" border="1" alt="" width="450" height="313" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">&Aacute;p dụng đ&uacute;ng kỹ thuật cho từng khoảnh khắc.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-5.jpg" border="1" alt="" width="450" height="449" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">T&iacute;nh s&aacute;ng tạo.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-6.jpg" border="1" alt="" width="450" height="282" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">C&acirc;n bằng m&agrave;u sắc.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-7.jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">Khả năng phối cảnh.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-8.jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">T&igrave;m sự kh&aacute;c biệt giữa những thứ giống nhau.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-14.jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">H&uacute;t &aacute;nh mắt người xem v&agrave;o t&acirc;m điểm.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-9.jpg" border="1" alt="" width="450" height="448" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">Thể hiện kỹ thuật chụp ảnh ấn tượng.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="photo-10.jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center">Tạo cảm x&uacute;c v&agrave; truyền tải th&ocirc;ng điệp nhất định từ bức ảnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ch&acirc;u An</strong></p>', 38, 9, 2382, 0, 1),
(1633, 'Tự làm kính 3D chỉ trong 1 phút', '2 video được chia sẻ trên YouTube hướng dẫn cách làm kính 2 màu xanh đỏ xem được hình ảnh nổi ba chiều một cách nhanh và đơn giản nhất.', '3D-1.jpg', NULL, '<h1 class="Title">Tự l&agrave;m k&iacute;nh 3D chỉ trong 1 ph&uacute;t</h1>\r\n<h2 class="Lead">2 video được chia sẻ tr&ecirc;n YouTube hướng dẫn c&aacute;ch l&agrave;m k&iacute;nh 2 m&agrave;u xanh đỏ xem được h&igrave;nh ảnh nổi ba chiều một c&aacute;ch nhanh v&agrave; đơn giản nhất.</h2>\r\n<p class="Normal">Vật dụng cần thiết để l&agrave;m k&iacute;nh 3D l&agrave; chỉ cần một vỏ đĩa CD cũ, c&oacute; thể nh&igrave;n xuy&ecirc;n qua r&otilde; r&agrave;ng v&agrave; 2 chiếc b&uacute;t m&agrave;u.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="418" align="center">\r\n<tbody>\r\n<tr>\r\n<td><a class="Normal" href="http://vnexpress.net/GL/Vi-tinh/Kinh-nghiem/2010/12/3BA24ABA/page_2.asp"><img src="3D-1.jpg" border="1" alt="" width="450" height="180" /></a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nếu muốn thời trang hơn, người d&ugrave;ng c&oacute; thể th&aacute;o mắt k&iacute;nh ra khỏi một sản phẩm cũ v&agrave; cho miếng nhựa đ&atilde; t&ocirc; m&agrave;u v&agrave;o.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="68" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="3D-2.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="center"><span style="color: #0000ff;"><strong><br /></strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tr&ecirc;n thị trường Việt Nam đang b&aacute;n k&iacute;nh 3D 2 m&agrave;u xanh đỏ với gọng bằng giấy gi&aacute; 10.000 đồng v&agrave; bằng nhựa l&agrave; 50.000 đồng.</p>\r\n<p class="Normal"><strong>Nguyễn H&ugrave;ng</strong></p>', 38, 9, 1556, 0, 1),
(1634, 'Các tiêu chí khi chọn mực in chính hãng', 'Mực in chính hãng phải đảm bảo đạt 4 tiêu chí gồm: độ ổn định tốt, phân bổ chất lượng trang in đồng đều, độ phủ quang học và mức độ kết dính mực.', 'HP-1.jpg', NULL, '<h1 class="Title">C&aacute;c ti&ecirc;u ch&iacute; khi chọn mực in ch&iacute;nh h&atilde;ng</h1>\r\n<h2 class="Lead">Mực in ch&iacute;nh h&atilde;ng phải đảm bảo đạt 4 ti&ecirc;u ch&iacute; gồm: độ ổn định tốt, ph&acirc;n bổ chất lượng trang in đồng đều, độ phủ quang học v&agrave; mức độ kết d&iacute;nh mực.</h2>\r\n<p class="Normal">Để đảm bảo những ti&ecirc;u ch&iacute; tr&ecirc;n, chất lượng mực in được quyết định bởi hai yếu tố quan trọng l&agrave; th&agrave;nh phần nguy&ecirc;n liệu v&agrave; c&ocirc;ng nghệ chế tạo. C&aacute;c sản phẩm mực in ch&iacute;nh h&atilde;ng c&oacute; t&ecirc;n tuổi đều phải trải qua qu&aacute; tr&igrave;nh xử l&yacute; bằng c&ocirc;ng nghệ nghi&ecirc;m ngặt nhằm đảm bảo chất lượng tốt nhất cho sản phẩm cũng như giảm thiểu tối đa ảnh hướng tới m&ocirc;i trường xung quanh, đặc biệt l&agrave; sức khỏe của người sử dụng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="HP-1.jpg" border="1" alt="" width="420" height="213" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Mực in HP đ&atilde; vượt qua c&aacute;c kiểm tra khắt khe. Theo kết quả nghi&ecirc;n cứu v&agrave; kiểm tra của QualityLogic th&aacute;ng 4/2010, thực hiện test khoảng 40 hộp mực ch&iacute;nh h&atilde;ng HP v&agrave; 80 hộp mực kh&ocirc;ng ch&iacute;nh h&atilde;ng tại Thượng Hải (Trung Quốc) v&agrave; New Delhi (Ấn Độ), kết quả cho thấy về độ ổn định, mực in HP kh&ocirc;ng gặp trục trặc, trong khi đ&oacute; 63,8% c&aacute;c loại mực kh&ocirc;ng ch&iacute;nh h&atilde;ng cho chất lượng k&eacute;m (11,3 % kh&ocirc;ng in được ngay khi nhận lệnh in v&agrave; hơn 52,5% l&agrave; tạo ra c&aacute;c bản in k&eacute;m chất lượng). Ph&acirc;n bổ chất lượng trang in đồng đều cũng l&agrave; một trong những ưu điểm nổi bật của mực in HP. Kết quả của cuộc kiểm tra lần n&agrave;y cũng chỉ ra rằng, gần 96% trang in sử dụng mực in HP được ph&acirc;n bổ đồng đều so với hơn 30% trang in m&agrave; họ nhận được từ mực in kh&ocirc;ng ch&iacute;nh h&atilde;ng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="HP-2.jpg" border="1" alt="" width="420" height="207" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">HP t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến để tạo ra hạt mực in si&ecirc;u nhỏ gi&uacute;p n&eacute;t chữ của bản in sắc n&eacute;t, đồng đều. Yếu tố th&acirc;n thiện với m&ocirc;i trường, kh&ocirc;ng ảnh hưởng v&agrave; g&acirc;y hại tới sức khỏe người sử dụng cũng lu&ocirc;n được đặt l&ecirc;n h&agrave;ng đầu.</p>\r\n<p class="Normal">Người ti&ecirc;u d&ugrave;ng n&ecirc;n c&acirc;n nhắc để việc đầu tư kh&ocirc;ng l&atilde;ng ph&iacute;. Kh&aacute;ch h&agrave;ng thường c&oacute; t&acirc;m l&yacute; &ldquo;tham h&agrave;ng rẻ&rdquo; m&agrave; kh&ocirc;ng t&iacute;nh đến hậu quả g&acirc;y về l&acirc;u d&agrave;i như bản in k&eacute;m chất lượng, mực in nh&ograve;e nhoẹt do bị r&ograve; rỉ khiến phải mất th&ecirc;m giấy in, thậm ch&iacute; l&acirc;u ng&agrave;y sẽ l&agrave;m bẩn, hỏng m&aacute;y in...</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="393" align="center" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td>HP Việt Nam c&ocirc;ng bố chương tr&igrave;nh khuyến mại mới d&agrave;nh cho mực in laser HP từ ng&agrave;y 1/1 đến 1/3. Khi mua một hộp mực in laser Q2612A, kh&aacute;ch h&agrave;ng sẽ được nhận ngay một phiếu mua h&agrave;ng si&ecirc;u thị trị gi&aacute; 100.000 đồng; phiếu mua h&agrave;ng si&ecirc;u thị trị gi&aacute; 50.000 đồng cho mỗi sản phẩm mực in laser HP C7115A hoặc CB435A tại hơn 30 đại l&yacute; v&agrave; nh&agrave; ph&acirc;n phối được ủy quyền của HP tr&ecirc;n cả nước. Li&ecirc;n hệ: 08-3920 5564.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;">(Nguồn<em>: HP</em>)</p>', 38, 9, 231, 0, 1),
(331, 'Đề xuất sở hữu chung cư có thời hạn gây nhiều tranh cãi', 'Bộ Xây dựng cho rằng, sở hữu nhà chung cư có thời hạn sẽ thuận tiện cho việc nâng cấp, cải tạo đồng thời làm giảm giá chung cư. Tuy nhiên, nhiều ý kiến lo ngại, người dân có thể bị sốc và quay lưng lại với mô hình này.', 'chung1.jpg', NULL, '<h1 class="Title">Đề xuất sở hữu chung cư c&oacute; thời hạn g&acirc;y nhiều tranh c&atilde;i</h1>\r\n<h2 class="Lead">Bộ X&acirc;y dựng cho rằng, sở hữu nh&agrave; chung cư c&oacute; thời hạn sẽ thuận tiện cho việc n&acirc;ng cấp, cải tạo đồng thời l&agrave;m giảm gi&aacute; chung cư. Tuy nhi&ecirc;n, nhiều &yacute; kiến lo ngại, người d&acirc;n c&oacute; thể bị sốc v&agrave; quay lưng lại với m&ocirc; h&igrave;nh n&agrave;y.</h2>\r\n<p class="Normal">Dự kiến sở hữu nh&agrave; chung cư c&oacute; thời hạn sẽ được đưa v&agrave;o Luật Nh&agrave; ở sửa đổi trong năm 2012. Theo đề xuất của Bộ X&acirc;y dựng, người d&acirc;n chỉ được sở hữu nh&agrave; chung cư c&oacute; thời hạn, khi hết hạn định sẽ phải trả lại nh&agrave;. Việc quy định sở hữu nh&agrave; ở c&oacute; thời hạn sẽ tạo điều kiện thuận tiện cho việc n&acirc;ng cấp, cải tạo lại nh&agrave; cũ. Hiện thời hạn sở hữu nh&agrave; chung cư vẫn chưa c&oacute; dự kiến.&nbsp;<em>VnExpress.net</em>&nbsp;ghi nhận &yacute; kiến của một số doanh nghiệp v&agrave; chuy&ecirc;n gia về đề xuất n&agrave;y của Bộ X&acirc;y dựng.</p>\r\n<p class="Normal"><strong>&Ocirc;ng Đặng H&ugrave;ng V&otilde;, Cố vấn cấp cao của Bộ T&agrave;i nguy&ecirc;n M&ocirc;i trường</strong>:&nbsp;<em>Thuận tiện trong việc cải tạo nh&agrave; chung cư.</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="vo-2.jpg" border="1" alt="" width="150" height="200" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">&Ocirc;ng Đặng H&ugrave;ng V&otilde;. Ảnh:<em>&nbsp;HL</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&ocirc;i cho rằng đề xuất sở hữu chung cư c&oacute; thời hạn tại thời điểm n&agrave;y mới được đưa ra l&agrave; hơi muộn. Nhiều nước như Trung Quốc, Australia, Singapore, Mỹ... đ&atilde; &aacute;p tuổi cho chung cư từ rất l&acirc;u. Hiện nhiều nh&agrave; chung cư cũ đang xuống cấp như khu vực Th&agrave;nh C&ocirc;ng, Nguyễn C&ocirc;ng Trứ song việc cải tạo rất kh&oacute; khăn do người d&acirc;n kh&ocirc;ng chấp nhận mức đền b&ugrave; chủ đầu tư đưa ra.</p>\r\n<p class="Normal">Nhiều vụ l&ugrave;m x&ugrave;m xung quanh c&aacute;c khu chung cư cũ như chủ đầu tư l&aacute;ch luật xin n&acirc;ng tầng để kinh doanh, c&ograve;n người d&acirc;n th&igrave; đua nhau l&agrave;m chuồng cọp. Người d&acirc;n đ&atilde; quen với c&aacute;ch tư duy nh&agrave; chung cư sở hữu v&ocirc; thời hạn, để đời đời cho con ch&aacute;u, khi đập đi x&acirc;y lại, họ được đền b&ugrave; rất cao.</p>\r\n<p class="Normal">C&ocirc;ng tr&igrave;nh đ&atilde; hết ni&ecirc;n hạn sử dụng chỉ n&ecirc;n ph&aacute; đi x&acirc;y lại chứ kh&ocirc;ng thể sửa chữa được. Đ&uacute;ng l&agrave; t&acirc;m l&yacute; người d&acirc;n c&oacute; thể hoang mang nhưng t&ocirc;i cho rằng, nếu để họ sử dụng vĩnh viễn căn hộ, ch&uacute;ng ta sẽ lại rơi v&agrave;o t&igrave;nh trạng những khu nh&agrave; tập thể cũ xưa xập xệ m&agrave; th&ocirc;i. Để tr&aacute;nh sốc cho người d&acirc;n, ch&uacute;ng ta cần phải tuy&ecirc;n truyền v&agrave; x&acirc;y dựng văn h&oacute;a chung cư. Hạn sử dụng mỗi chung cư chỉ n&ecirc;n 50-70 năm t&ugrave;y theo chất lượng x&acirc;y dựng của từng c&ocirc;ng tr&igrave;nh. Ngay cả chung cư mini cũng cần phải đưa v&agrave;o khu&ocirc;n khổ, sử dụng c&oacute; thời hạn.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="315" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Dự kiến đề xuất sở hữu nh&agrave; chung cư c&oacute; thời hạn sẽ được đưa v&agrave;o Luật Nh&agrave; ở sửa đổi trong năm 2012. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>&Ocirc;ng Nguyễn Văn Đực</strong>,&nbsp;<strong>Ph&oacute; gi&aacute;m đốc C&ocirc;ng ty TNHH địa ốc Đất L&agrave;nh</strong>:<em>Người d&acirc;n sẽ dễ quay lưng với nh&agrave; chung cư để mua đất nền.</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="nvd1.jpg" border="1" alt="Nguyễn Văn Đực" width="150" height="200" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">&Ocirc;ng Nguyễn Văn Đực.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&ocirc;i e rằng, nếu kh&ocirc;ng cẩn thận, đề xuất sở hữu c&oacute; thời hạn sẽ đi ngược lại chủ trương khuyến kh&iacute;ch ưu ti&ecirc;n ph&aacute;t triển nh&agrave; chung cư của Nh&agrave; nước. Bởi nhiều người d&acirc;n sẽ rơi v&agrave;o t&acirc;m l&yacute; sốc khi bỏ một khoản tiền ra nhưng chỉ được sở hữu nh&agrave; trong một khoảng thời gian nhất định. Thay v&agrave;o đ&oacute; họ sẽ mua đất nền. Theo t&ocirc;i c&oacute; một điều bất cập l&agrave; đất nền nh&agrave; phố cũng đ&oacute;ng tiền sử dụng đất nhưng được sở hữu vĩnh viễn. Trong khi đ&oacute; đất x&acirc;y dựng chung cư cũng phải đ&oacute;ng tiền sử dụng đất v&agrave; đầu tư cơ sở hạ tầng, đường x&aacute;, c&ocirc;ng vi&ecirc;n&hellip; lại chỉ được sở hữu c&oacute; thời hạn.</p>\r\n<p class="Normal">Gi&aacute; th&agrave;nh chung cư hiện đang kh&aacute; cao, mỗi căn hộ thấp nhất cũng khoảng 600-800 triệu đồng do doanh nghiệp mất nhiều chi ph&iacute; cho gi&aacute; th&agrave;nh x&acirc;y dựng, cộng th&ecirc;m tiền bồi thường đất v&agrave; đầu tư hạ tầng. T&ocirc;i cho rằng, sở hữu nh&agrave; ở c&oacute; thời hạn l&agrave; một ch&iacute;nh s&aacute;ch ph&aacute;t triển m&agrave; nhiều nước đ&atilde; l&agrave;m từ rất l&acirc;u, nhưng ở Việt Nam muốn thực hiện được, Nh&agrave; nước cần phải đưa k&egrave;m một số giải ph&aacute;p như miễn tiền sử dụng đất, miễn thuế hoặc cho ph&eacute;p x&acirc;y dựng căn hộ nhỏ hơn 45 m2&hellip; Khi đ&oacute; gi&aacute; th&agrave;nh chung cư sẽ giảm v&agrave; người d&acirc;n sẽ chấp nhận d&ugrave; sở hữu c&oacute; thời hạn, c&ograve;n ph&iacute;a doanh nghiệp cũng c&oacute; &ldquo;đất sống&rdquo;.</p>\r\n<p class="Normal"><strong>&Ocirc;ng Phạm Thanh Hưng, Tổng gi&aacute;m đốc C&ocirc;ng ty Thẩm định gi&aacute; Thế kỷ</strong>:<em>Hạn chế t&igrave;nh trạng lướt s&oacute;ng chung cư</em>.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="right">\r\n<tbody>\r\n<tr>\r\n<td><img src=\r\n"hung1.jpg" border="1" alt="Phạm Thanh Hưng" width="150" height="200" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">&Ocirc;ng Phạm Thanh Hưng. Ảnh:<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&ocirc;i cho rằng, việc người d&acirc;n sở hữu chung cư vĩnh viễn l&agrave; phi l&yacute;. Khi chung cư đ&atilde; xuống cấp, ai sẽ bỏ tiền ra để x&acirc;y lại? Đất c&oacute; thể sở hữu vĩnh viễn nhưng chung cư th&igrave; n&ecirc;n giới hạn thời gian. Nếu sử dụng v&ocirc; thời hạn, gi&aacute; chung cư sẽ cao ngất ngưởng v&agrave; c&agrave;ng v&ocirc; l&yacute; nếu đến 70 năm sau, khi c&ocirc;ng tr&igrave;nh đ&atilde; xuống cấp m&agrave; gi&aacute; vẫn cao ch&oacute;t v&oacute;t.</p>\r\n<p class="Normal">Trong năm qua, nhiều t&igrave;nh trạng lướt s&oacute;ng chung cư v&agrave; hậu quả l&agrave; người d&acirc;n c&oacute; nhu cầu thực kh&ocirc;ng tiếp cận được gi&aacute; gốc. Gi&aacute; nh&agrave; đất n&oacute;i chung v&agrave; chung cư n&oacute;i ri&ecirc;ng cứ theo đ&agrave; đ&oacute; tăng ch&oacute;ng mặt. Nếu &aacute;p dụng h&igrave;nh thức sở hữu c&oacute; thời hạn, gi&aacute; chung cư sẽ hạ nhiệt v&igrave; t&igrave;nh trạng đầu cơ sẽ giảm đi.</p>\r\n<p class="Normal"><strong>&Ocirc;ng Đặng Văn Quang, Gi&aacute;m đốc C&ocirc;ng ty tư vấn Navigat</strong>:&nbsp;<br /><em>Việc l&agrave;m trước mắt l&agrave; l&agrave;m hạ nhiệt gi&aacute; đất.</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="anh-Quang-1.jpg" border="1" alt="Ảnh:" width="150" height="200" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">&Ocirc;ng Đặng Văn Quang. Ảnh:<em>T.Nguy&ecirc;n.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&ocirc;i cho rằng, việc đa dạng sở hữu nh&agrave; chung cư chưa chắc l&agrave; một phương thức tốt để hạ gi&aacute;. Thực tế, một số chung cư như Vincom v&agrave; Pacific đang sở hữu c&oacute; thời hạn, nhưng gi&aacute; vẫn cao ngất ngưởng. Gi&aacute; chung cư được h&igrave;nh th&agrave;nh gồm gi&aacute; đất v&agrave; t&agrave;i sản gắn liền với đất. Để l&agrave;m gi&aacute; chung cư hạ nhiệt, Nh&agrave; nước cần phải can thiệp ch&iacute;nh s&aacute;ch vĩ m&ocirc; để l&agrave;m hạ gi&aacute; đất. Nhiều nước đ&atilde; &aacute;p dụng h&igrave;nh thức sở hữu chung cư c&oacute; thời hạn song Việt Nam cần c&acirc;n nhắc kỹ trước khi học hỏi c&aacute;c nước kh&aacute;c bởi thực tế, mỗi nước c&oacute; những phong tục tập qu&aacute;n kh&aacute;c nhau.</p>\r\n<p class="Normal">Ngo&agrave;i ra, t&iacute;nh tuổi của chung cư kh&ocirc;ng đơn giản. Ai d&aacute;m chắc rằng cứ 50 năm hoặc 70 năm sau chung cư sẽ xuống cấp. Nếu chưa đến thời điểm gia hạn, chung cư đ&atilde; xuống cấp th&igrave; sao. Liệu ch&uacute;ng ta c&oacute; thể dễ d&agrave;ng cải tạo lại chung cư n&agrave;y kh&ocirc;ng? T&ocirc;i e rằng, c&acirc;u trả lời l&agrave; kh&ocirc;ng. Do đ&oacute;, Nh&agrave; nước cần c&acirc;n nhắc kỹ trước khi đưa ra quyết định. T&ocirc;i cho rằng, để l&agrave;m hạ nhiệt gi&aacute; chung cư, trước ti&ecirc;n Bộ X&acirc;y dựng phải đưa ra c&aacute;c giải ph&aacute;p hạ gi&aacute; đầu v&agrave;o của gi&aacute; th&agrave;nh tạo n&ecirc;n c&ocirc;ng tr&igrave;nh. Chỉ khi nguồn cung dồi d&agrave;o, chung cư mới hạ nhiệt.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 2408, 0, 1),
(332, 'Thép và xi măng cùng tăng giá', 'Từ đầu tháng đến nay, nhiều doanh nghiệp thép đã tăng giá từ 200.000 đồng đến 500.000 đồng mỗi tấn. Xi măng cũng tăng 60.000 đồng. Theo dự báo, hai mặt hàng này sẽ còn biến động nếu điện và than đồng loạt tăng giá.', 'thep_1.jpg', NULL, '<h1 class="Title">Th&eacute;p v&agrave; xi măng c&ugrave;ng tăng gi&aacute;</h1>\r\n<h2 class="Lead">Từ đầu th&aacute;ng đến nay, nhiều doanh nghiệp th&eacute;p đ&atilde; tăng gi&aacute; từ 200.000 đồng đến 500.000 đồng mỗi tấn. Xi măng cũng tăng 60.000 đồng. Theo dự b&aacute;o, hai mặt h&agrave;ng n&agrave;y sẽ c&ograve;n biến động nếu điện v&agrave; than đồng loạt tăng gi&aacute;.</h2>\r\n<p class="Normal">Theo Hiệp hội xi măng, hiện một số doanh nghiệp đ&atilde; tăng gi&aacute; b&aacute;n xi măng th&ecirc;m 60.000 đồng mỗi tấn, với mức phổ biến từ 900.000 đồng- 1,36 triệu đồng mỗi tấn t&ugrave;y chủng loại. Hiệp hội xi măng cho biết, cước vận tải v&agrave; một số nguy&ecirc;n liệu đầu v&agrave;o phục vụ sản xuất đ&atilde; bắt đầu tăng n&ecirc;n gi&aacute; b&aacute;n buộc phải tăng theo.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="thep_1.jpg" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiều doanh nghiệp th&eacute;p đ&atilde; tăng gi&aacute; từ 200.000 đồng đến 500.000 đồng mỗi tấn. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&Ocirc;ng Nguyễn Tiến Nghi, Ph&oacute; chủ tịch Hiệp hội th&eacute;p (VSA) cho hay, từ đầu th&aacute;ng hai, nhiều doanh nghiệp th&eacute;p đ&atilde; tăng gi&aacute; từ 200.000 đồng đến 500.000 đồng mỗi tấn. Gi&aacute; th&eacute;p tại nh&agrave; m&aacute;y dao động quanh mức 16,5 triệu đồng mỗi tấn t&ugrave;y chủng loại (chưa t&iacute;nh VAT v&agrave; ph&iacute; vận chuyển).</p>\r\n<p class="Normal">Theo &ocirc;ng Nghi, th&eacute;p tăng do gi&aacute; nguy&ecirc;n liệu đầu v&agrave;o tăng cao. Hiện gi&aacute; ph&ocirc;i tr&ecirc;n thế giới đ&atilde; ở mức 670-690 USD mỗi tấn, tăng khoảng 90 USD so với hồi cuối th&aacute;ng 12. Th&eacute;p phế cũng từ 400 USD mỗi tấn vọt l&ecirc;n 550 USD.</p>\r\n<p class="Normal">Theo ước t&iacute;nh của Hiệp hội Th&eacute;p Việt Nam, sức ti&ecirc;u thụ th&eacute;p trong th&aacute;ng đầu năm khả quan, đạt 340.000 tấn, tăng hơn 40.000 tấn so với c&ugrave;ng kỳ năm 2010. Hiệp hội n&agrave;y dự b&aacute;o, gi&aacute; th&eacute;p sẽ c&ograve;n biến động. Bởi khi gi&aacute; than, điện tiếp tục được điều chỉnh th&igrave; gi&aacute; b&aacute;n của th&eacute;p v&agrave; xi măng sẽ c&ograve;n tăng.</p>\r\n<p class="Normal">"C&aacute;c doanh nghiệp tăng từng ch&uacute;t một để d&ograve; x&eacute;t động th&aacute;i của thị trường. Khi điện v&agrave; than tăng gi&aacute;, th&eacute;p cũng sẽ biến động theo", &ocirc;ng Nghi nhận x&eacute;t.</p>\r\n<p class="Normal">Hồi trung tuần th&aacute;ng 1, Bộ C&ocirc;ng Thương đ&atilde; chỉ đạo, trong trường hợp phải tiết giảm điện, Bộ sẽ ưu ti&ecirc;n cung cấp điện cho c&aacute;c nh&agrave; m&aacute;y sản xuất th&eacute;p v&agrave; xi măng c&oacute; hiệu suất sử dụng điện cao. Bộ C&ocirc;ng Thương đ&atilde; đề nghị Hiệp hội Th&eacute;p Việt Nam, Hiệp hội Xi măng Việt Nam, Tổng c&ocirc;ng ty Th&eacute;p Việt Nam, Tổng c&ocirc;ng ty Xi măng Việt Nam th&ocirc;ng b&aacute;o cho c&aacute;c đơn vị th&agrave;nh vi&ecirc;n về những kh&oacute; khăn trong việc cung cấp điện năm 2011 để đưa ra c&aacute;c giải ph&aacute;p cải tiến c&ocirc;ng nghệ, thiết bị nhằm n&acirc;ng cao hiệu quả sử dụng điện năng. Mặt kh&aacute;c, c&aacute;c đơn vị n&agrave;y phải x&acirc;y dựng phương &aacute;n tận thu nguồn năng lượng ph&aacute;t thải để sản xuất điện phục vụ lại cho sản xuất.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 1664, 0, 1),
(333, 'Kinh Bắc sẽ khởi công dự án tỷ đô trong quý I', 'Trao đổi với VnExpress.net, ông Đặng Thành Tâm, Chủ tịch Tập đoàn Kinh Bắc cho hay, chủ đầu tư đã được chấp thuận khởi công dự án tòa nhà phức hợp 100 tầng có tên Lotus trong quý I.', 'kb1.jpg', NULL, '<h1 class="Title">Kinh Bắc sẽ khởi c&ocirc;ng dự &aacute;n tỷ đ&ocirc; trong qu&yacute; I</h1>\r\n<h2 class="Lead">Trao đổi với&nbsp;<em>VnExpress.net</em>, &ocirc;ng Đặng Th&agrave;nh T&acirc;m, Chủ tịch Tập đo&agrave;n Kinh Bắc cho hay, chủ đầu tư đ&atilde; được chấp thuận khởi c&ocirc;ng dự &aacute;n t&ograve;a nh&agrave; phức hợp 100 tầng c&oacute; t&ecirc;n Lotus trong qu&yacute; I.<br />&gt;<a class="Lead" href="http://vnexpress.net/gl/kinh-doanh/bat-dong-san/2010/08/3ba1fdc2/">Kh&aacute;ch sạn 100 tầng ''ứng cử'' t&ograve;a nh&agrave; cao nhất VN</a></h2>\r\n<p class="Normal">Dự &aacute;n Lotus c&oacute; số vốn khoảng 1 tỷ đ&ocirc;la do Tập đo&agrave;n Kinh Bắc l&agrave;m chủ đầu tư. T&ograve;a nh&agrave; l&agrave; c&ocirc;ng tr&igrave;nh phức hợp đa chức năng gồm trung t&acirc;m thương mại, vườn treo v&agrave; kh&aacute;ch sạn 6 sao được thiết kế bởi h&atilde;ng Foster + Partners theo dạng ph&acirc;n khu chức năng ri&ecirc;ng biệt.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="kb1.jpg" border="1" alt="Ảnh: Ho&agrave;ng Lan" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&ocirc; đất rộng khoảng 4ha nằm tr&ecirc;n đường Phạm H&ugrave;ng, gần trung t&acirc;m hội nghị Quốc gia dự kiến x&acirc;y dự &aacute;n Lotus. Ảnh:&nbsp;<em>Ho&agrave;ng Lan.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&Ocirc;ng&nbsp;<a class="Normal" href="http://vnexpress.net/gl/kinh-doanh/2011/01/3ba25026/">Đặng Th&agrave;nh T&acirc;m</a>&nbsp;cho hay, tập đo&agrave;n đ&atilde; l&agrave;m việc với th&agrave;nh phố H&agrave; Nội xin &yacute; kiến về việc thay đổi số tầng cao, c&ocirc;ng năng sử dụng của dự &aacute;n kh&aacute;ch sạn Lotus ở x&atilde; Mễ Tr&igrave;, huyện Từ Li&ecirc;m. Chủ đầu tư đ&atilde; đưa ra 24 phương &aacute;n n&acirc;ng tầng với chiều cao từ 120 m đến 600 m.</p>\r\n<p class="Normal">Trong số 24 thiết kế, chủ đầu tư t&acirc;m đắc nhất với hai phương &aacute;n: T&ograve;a nh&agrave; xanh với rất nhiều vườn tr&ecirc;n cao (sky garden) v&agrave; t&ograve;a nh&agrave; sạch tiết kiệm 40% năng lượng với hệ thống lưu chuyển kh&ocirc;ng kh&iacute; tự nhi&ecirc;n.</p>\r\n<p class="Normal">"Hiện H&agrave; Nội vẫn chưa c&oacute; quyết định ch&iacute;nh thức về đề xuất xin n&acirc;ng tầng. Ch&uacute;ng t&ocirc;i mong muốn được khởi c&ocirc;ng trong qu&yacute; I để mừng th&agrave;nh c&ocirc;ng của Đại hội Đảng", Chủ tịch Tập đo&agrave;n Kinh Bắc n&oacute;i.</p>\r\n<p class="Normal">Một số mẫu như h&igrave;nh hoa sen, b&ocirc;ng l&uacute;a, ch&ugrave;a Một Cột v&agrave; s&ocirc;ng Hồng đang được nh&agrave; đầu tư v&agrave; c&ocirc;ng ty tư vấn thiết kế nghi&ecirc;n cứu chọn l&agrave;m biểu tượng cho t&ograve;a nh&agrave;. Trong đ&oacute;, biểu tượng b&ocirc;ng l&uacute;a với nhiều sky garden h&igrave;nh bậc thang ở n&uacute;i rừng T&acirc;y Bắc được chủ đầu tư ưa th&iacute;ch nhất.</p>\r\n<p class="Normal">Trước đ&oacute;, dự &aacute;n Lotus do Tập đo&agrave;n Riviera (Nhật Bản) l&agrave;m chủ đầu tư, c&oacute; số vốn 500 triệu USD cao 15 tầng. Khi gặp kh&oacute; khăn về t&agrave;i ch&iacute;nh, chủ đầu tư đ&atilde; xin r&uacute;t khỏi dự &aacute;n v&agrave; chuyển giao cho Tập đo&agrave;n Kinh Bắc. Sau đ&oacute;, Kinh Bắc đ&atilde; điều chỉnh thiết kế, n&acirc;ng l&ecirc;n 35 tầng, mật độ x&acirc;y dựng 35-40%. Đến nay, số tầng được đề nghị tăng l&ecirc;n 100 v&agrave; Lotus được dự kiến xếp v&agrave;o một trong những t&ograve;a nh&agrave; cao nhất Việt Nam.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 20, 0, 1),
(334, 'Thành phố ven sông Hồng sẽ hạn chế nhà cao tầng', 'Phó giám đốc Sở Quy hoạch Kiến trúc Hà Nội Dương Đức Tuấn cho hay, sau khi lồng ghép vào quy hoạch chung thủ đô, dự án thành phố ven sông Hồng sẽ hạn chế, giảm bớt các nhà cao tầng để phát triển không gian xanh.', 'dan.jpg', NULL, '<h1 class="Title">Th&agrave;nh phố ven s&ocirc;ng Hồng sẽ hạn chế nh&agrave; cao tầng</h1>\r\n<h2 class="Lead">Ph&oacute; gi&aacute;m đốc Sở Quy hoạch Kiến tr&uacute;c H&agrave; Nội Dương Đức Tuấn cho hay, sau khi lồng gh&eacute;p v&agrave;o quy hoạch chung thủ đ&ocirc;, dự &aacute;n th&agrave;nh phố ven s&ocirc;ng Hồng sẽ hạn chế, giảm bớt c&aacute;c nh&agrave; cao tầng để ph&aacute;t triển kh&ocirc;ng gian xanh.</h2>\r\n<p class="Normal">Đại diện của Sở Quy hoạch Kiến tr&uacute;c H&agrave; Nội cho hay, Thủ tướng y&ecirc;u cầu lồng gh&eacute;p dự &aacute;n s&ocirc;ng Hồng đoạn qua H&agrave; Nội v&agrave;o quy hoạch chung của Thủ đ&ocirc;. Dự kiến, cuối th&aacute;ng 2 đầu th&aacute;ng 3 năm nay, Thủ tướng sẽ xem x&eacute;t ph&ecirc; duyệt quy hoạch chung của Thủ đ&ocirc; H&agrave; Nội đồng nghĩa với việc duyệt nội dung khung của quy hoạch th&agrave;nh phố ven s&ocirc;ng Hồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dan.jpg" border="1" alt="Ảnh: Nguyễn Hưng" width="400" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Dự &aacute;n th&agrave;nh phố ven s&ocirc;ng Hồng sẽ được lồng gh&eacute;p với quy hoạch chung thủ đ&ocirc;. Ảnh:&nbsp;<em>Nguyễn Hưng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trong qu&aacute; tr&igrave;nh đ&oacute;, dự &aacute;n th&agrave;nh phố ven s&ocirc;ng Hồng sẽ điều chỉnh một số nội dung để ph&ugrave; hợp với H&agrave; Nội mở rộng. &Ocirc;ng Dương Đức Tuấn cho hay, theo thiết kế ban đầu, dự &aacute;n th&agrave;nh phố ven s&ocirc;ng Hồng c&oacute; 4 khu chức năng. Sau khi lồng với quy hoạch chung, khu 2 của hữu ngạn ph&iacute;a nam s&ocirc;ng Hồng thuộc b&atilde;i Tứ Li&ecirc;n sẽ th&agrave;nh kh&ocirc;ng gian xanh với nhiều c&ocirc;ng tr&igrave;nh văn h&oacute;a quốc gia mang t&iacute;nh chất cộng đồng thay v&igrave; những d&atilde;y nh&agrave; cao tầng như ph&iacute;a H&agrave;n Quốc đề xuất.</p>\r\n<p class="Normal">Th&ecirc;m v&agrave;o đ&oacute;, tả ngạn khu 4, địa phận gần với l&agrave;ng cổ B&aacute;t Tr&agrave;ng cũng kh&ocirc;ng ph&aacute;t triển nh&agrave; cao tầng nữa. Ngo&agrave;i ra ở ph&iacute;a hữu ngạn khu 1 Thượng C&aacute;t sẽ được chuyển th&agrave;nh c&ocirc;ng vi&ecirc;n xanh.</p>\r\n<p class="Normal">"Sau khi điều chỉnh, dự &aacute;n vẫn đảm bảo kh&ocirc;ng gian giữa tả v&agrave; hữu ngạn s&ocirc;ng Hồng với khu đ&ocirc; thị trung t&acirc;m. Đoạn qua H&agrave; Nội mở rộng dự kiến sẽ th&agrave;nh một ph&acirc;n khu", &ocirc;ng Tuấn n&oacute;i.</p>\r\n<p class="Normal">Sau khi quy hoạch chung được duyệt, dự &aacute;n th&agrave;nh phố ven s&ocirc;ng Hồng sẽ phải lập quy hoạch ph&acirc;n khu v&agrave; quy hoạch chi tiết. Ứng với mỗi quy hoạch chi tiết c&oacute; một dự &aacute;n đầu tư, l&uacute;c đ&oacute; mới t&iacute;nh đến vấn đề về vốn, k&ecirc;u gọi đầu tư v&agrave; giải ph&oacute;ng mặt bằng. 39.000 hộ d&acirc;n sẽ được ưu ti&ecirc;n t&aacute;i định cư tại chỗ.</p>\r\n<p class="Normal">"H&agrave;n Quốc chỉ l&agrave; đối tượng nghi&ecirc;n cứu quy hoạch th&agrave;nh phố ven s&ocirc;ng Hồng. Dự &aacute;n sẽ k&ecirc;u gọi nh&agrave; đầu tư tr&ecirc;n từng hạng mục như quy hoạch về ph&aacute;t triển hai b&ecirc;n bờ, ph&aacute;t triển đ&ocirc; thị, nh&agrave; ở c&ocirc;ng tr&igrave;nh văn h&oacute;a v&agrave; c&ocirc;ng vi&ecirc;n xanh", &ocirc;ng Tuấn n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#dfdfdf">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class="Normal">Dự &aacute;n Quy hoạch ph&aacute;t triển s&ocirc;ng Hồng đoạn qua H&agrave; Nội dự kiến c&oacute; vốn đầu tư tr&ecirc;n 7 tỷ USD v&agrave; phải di dời 39.100 hộ (170.000 d&acirc;n). Sau khi ho&agrave;n th&agrave;nh, s&ocirc;ng Hồng sẽ trở th&agrave;nh trục kh&ocirc;ng gian ch&iacute;nh của th&agrave;nh phố H&agrave; Nội với những cao ốc t&agrave;i ch&iacute;nh quốc tế, chung cư cao cấp, c&ocirc;ng vi&ecirc;n đ&ocirc; thị ở ven bờ s&ocirc;ng.</p>\r\n<p class="Normal">Cuối năm 2008, H&agrave; Nội đ&atilde; tổ chức triển l&atilde;m Quy hoạch cơ bản ph&aacute;t triển s&ocirc;ng Hồng, đoạn qua H&agrave; Nội. Thống k&ecirc; cho thấy, 37,8% người trả lời phiếu đồng &yacute; to&agrave;n bộ dự &aacute;n; 30,5% đồng &yacute; hầu hết th&agrave;nh phần dự &aacute;n v&agrave; 27% chỉ đồng &yacute; một phần dự &aacute;n. C&oacute; 4,6% kh&ocirc;ng đồng &yacute; triển khai dự &aacute;n.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 1212, 0, 1),
(335, 'iá nhà ở Hong Kong cao nhất thế giới', 'Hong Kong là nơi có giá nhà đất cao nhất thế giới, trên cả London và New York.', 'New_Image.JPG', NULL, '<h1 class="Title">Gi&aacute; nh&agrave; ở Hong Kong cao nhất thế giới</h1>\r\n<h2 class="Lead">Hong Kong l&agrave; nơi c&oacute; gi&aacute; nh&agrave; đất cao nhất thế giới, tr&ecirc;n cả London v&agrave; New York.</h2>\r\n<p class="Normal">Theo số liệu mới đ&acirc;y của c&ocirc;ng ty m&ocirc;i giới bất động sản Savills Plc, gi&aacute; nh&agrave; ở Hong Kong đắt đỏ hơn London (Anh) tới 55%. So với London, Moscow (Nga) đắt hơn 7,4%, c&ograve;n New York (Mỹ) th&igrave; lại rẻ hơn 15%.</p>\r\n<p class="Normal">Khảo s&aacute;t của h&atilde;ng n&agrave;y cho thấy, một nh&acirc;n vi&ecirc;n văn ph&ograve;ng sẽ phải chi khoảng 659 bảng (1.050 USD) một bộ vu&ocirc;ng (1 bộ = 0,3048 m) để mua một căn nh&agrave; tại Hong Kong, đắt hơn 76% nếu mua ở London. Một căn nh&agrave; tại đặc khu The Peak c&oacute; thể được b&aacute;n với gi&aacute; 6.353 bảng một bộ vu&ocirc;ng. Số tiền đ&oacute; nhiều gấp hơn hai lần so với gi&aacute; tại Mayfair hay Knightbridge ở London &ndash; th&agrave;nh phố c&oacute; gi&aacute; nh&agrave; ở cao cấp đắt đỏ thứ hai thế giới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="New_Image.JPG" border="1" alt="Gi&aacute; nh&agrave; ở Hong Kong đắt hơn London &ndash; th&agrave;nh phố c&oacute; gi&aacute; nh&agrave; cao thứ hai thế giới &ndash; tới 55%. Ảnh: Goway" width="450" height="313" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gi&aacute; nh&agrave; ở Hong Kong đắt hơn London tới 55%. Ảnh:&nbsp;<em>Goway.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo Savills, gi&aacute; nh&agrave; ở Hong Kong đ&atilde; bị đẩy l&ecirc;n cao do chi ph&iacute; đi vay thấp kỷ lục, thiếu nguồn cung v&agrave; lượng người mua nh&agrave; Trung Quốc gia tăng. H&atilde;ng m&ocirc;i giới bất động sản lớn nhất Hong Kong &ndash; Centaline Property Agency Ltd, cho biết, gi&aacute; nh&agrave; tại đ&acirc;y đ&atilde; tăng hơn 55% kể từ đầu năm 2009.</p>\r\n<p class="Normal">Th&aacute;ng 11 năm ngo&aacute;i, ch&iacute;nh quyền Hong Kong đ&atilde; ban h&agrave;nh th&ecirc;m c&aacute;c ch&iacute;nh s&aacute;ch v&agrave; thuế nhằm kiềm chế lạm ph&aacute;t. Trưởng đặc khu Hong Kong &ndash; Donald Tsang, cam kết sẽ đưa th&ecirc;m đất đai v&agrave;o thị trường để x&acirc;y dựng 20.000 đơn vị nh&agrave; ở mỗi năm, đồng thời tăng thuế nh&agrave; đất nhằm ngăn chặn đầu cơ.</p>\r\n<p class="Normal">&ldquo;Ch&iacute;nh quyền Hong Kong đang nỗ lực để hạ nhiệt thị trường, nhưng kh&ocirc;ng được hiệu quả cho lắm. Gi&aacute; nh&agrave; sẽ c&ograve;n tiếp tục l&ecirc;n cao trong v&ograve;ng 12 đến 18 th&aacute;ng tới&rdquo;, Simon Smith, người đứng đầu cơ quan nghi&ecirc;n cứu bất động sản ch&acirc;u &Aacute; của Hong Kong, cho hay.</p>\r\n<p class="Normal">Chỉ số bất động sản Hang Seng đ&atilde; tăng 76% kể từ đầu năm 2009 sau khi rơi xuống mức thấp nhất trong hơn bốn năm hồi khủng hoảng kinh tế to&agrave;n cầu.</p>\r\n<p class="Normal">Savills x&acirc;y dựng số liệu dựa tr&ecirc;n số tiền m&agrave; một người phải chi ra để tới định cư tại Hong Kong, London, Moscow v&agrave; New York. Chỉ số n&agrave;y phản &aacute;nh gi&aacute; trị ng&ocirc;i nh&agrave; của một gi&aacute;m đốc điều h&agrave;nh, hai gi&aacute;m đốc v&agrave; bốn nh&acirc;n vi&ecirc;n văn ph&ograve;ng. T&iacute;nh to&aacute;n được dựa tr&ecirc;n gi&aacute; mỗi bộ vu&ocirc;ng của từng loại bất động sản m&agrave; c&aacute;c đối tượng n&agrave;y mua.</p>\r\n<p class="Normal" style="text-align: right;"><strong>An L&acirc;m</strong>&nbsp;(theo&nbsp;<em>Bloomberg)</em></p>', 10, 3, 1757, 0, 1),
(336, 'Hà Nội có thêm khu đô thị hiện đại', 'Sáng 25/1, khu đô thị mới Kim Văn - Kim Lũ (Golden Silk) được khởi công xây dựng tại phường Đại Kim (Hoàng Mai, Hà Nội) với tổng vốn đầu tư trên 3.000 tỷ đồng.', 'do-thi3.jpg', NULL, '<h1 class="Title">H&agrave; Nội c&oacute; th&ecirc;m khu đ&ocirc; thị hiện đại</h1>\r\n<h2 class="Lead">S&aacute;ng 25/1, khu đ&ocirc; thị mới Kim Văn - Kim Lũ (Golden Silk) được khởi c&ocirc;ng x&acirc;y dựng tại phường Đại Kim (Ho&agrave;ng Mai, H&agrave; Nội) với tổng vốn đầu tư tr&ecirc;n 3.000 tỷ đồng.</h2>\r\n<p class="Normal">Theo &ocirc;ng Đỗ Trọng Quỳnh, Tổng gi&aacute;m đốc Vinaconex2 - chủ đầu tư, dự &aacute;n c&oacute; diện t&iacute;ch gần 27 ha, được x&acirc;y dựng đồng bộ hạ tầng kỹ thuật theo quy hoạch chi tiết tỷ lệ 1/500 đ&atilde; được UBND th&agrave;nh phố H&agrave; Nội ph&ecirc; duyệt.</p>\r\n<p class="Normal">Khu đ&ocirc; thị sẽ cung cấp hơn 350.000 m2 nh&agrave; với 6 chung cư cao cấp, ngo&agrave;i ra c&ograve;n c&oacute; trung t&acirc;m mua sắm, trường học, bể bơi, c&ocirc;ng vi&ecirc;n, c&acirc;y xanh, s&acirc;n chơi cho trẻ em, b&atilde;i đỗ xe c&ocirc;ng cộng...</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="do-thi3.jpg" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một phương &aacute;n thiết kế khu đ&ocirc; thị Kim Văn - Kim Lũ. Ảnh:&nbsp;<em>Đo&agrave;n Loan.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chủ đầu tư cho biết, dự &aacute;n d&agrave;nh gần 28.000 m2 đất để x&acirc;y dựng hệ thống trường học gồm mẫu gi&aacute;o, tiểu học, trung học cơ sở phục vụ người d&acirc;n sinh sống trong khu đ&ocirc; thị v&agrave; khu vực l&acirc;n cận.</p>\r\n<p class="Normal">Tại lễ khởi c&ocirc;ng, &ocirc;ng Trịnh Đ&igrave;nh Dũng, Thứ trưởng Bộ X&acirc;y dựng y&ecirc;u cầu chủ đầu tư cần x&acirc;y dựng khu đ&ocirc; thị mới Kim Văn - Kim Lũ đồng bộ, đảm bảo tiến độ v&agrave; chất lượng để đ&aacute;p ứng chỗ ở tiện nghi cho hơn mười ngh&igrave;n d&acirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Đo&agrave;n Loan</strong></p>', 10, 3, 2697, 0, 1),
(338, 'Giới thiệu 19 villa đầu tiên tại Ngọc Viên Islands', 'Beverly Investment giới thiệu ra thị trường 19 villa đầu tiên (diện tích từ 900 đến 1.250 m2) trên khu đảo Ngọc rộng 4,5 ha thuộc dự án Ngọc Viên, Sơn Tây, Ba Vì, Hà Nội bắt đầu từ ngày 11/1.', '1PC-Tong-mat-bang.jpg', NULL, '<h1 class="Title">Giới thiệu 19 villa đầu ti&ecirc;n tại Ngọc Vi&ecirc;n Islands</h1>\r\n<h2 class="Lead">Beverly Investment giới thi&ecirc;̣u ra thị trường 19 villa đ&acirc;̀u ti&ecirc;n (di&ecirc;̣n tích từ 900 đến 1.250 m2) tr&ecirc;n khu đảo Ngọc r&ocirc;̣ng 4,5 ha thuộc dự &aacute;n Ngọc Vi&ecirc;n, Sơn T&acirc;y, Ba V&igrave;, H&agrave; Nội bắt đầu từ ng&agrave;y 11/1.</h2>\r\n<p class="Normal">&nbsp;</p>\r\n<p class="Normal">Gi&aacute; ban đầu của mỗi căn villa từ 15 triệu đồng một m2, đ&atilde; bao gồm biệt thự s&acirc;n vườn, cảnh quan (theo phương thức ch&igrave;a kh&oacute;a trao tay). Kh&aacute;ch h&agrave;ng muốn sở hữu biệt thự tại Ngọc Vi&ecirc;n Islands sẽ thanh to&aacute;n theo 4 đợt từ ng&agrave;y k&yacute; hợp đồng đặt cọc đến khi chủ đầu tư v&agrave; người sở hữu kết th&uacute;c thủ tục b&agrave;n giao bất động sản. C&aacute;c căn biệt thự được đảm bảo ho&agrave;n th&agrave;nh v&agrave; b&agrave;n giao trong v&ograve;ng 12 th&aacute;ng từ l&uacute;c kh&aacute;ch k&yacute; hợp đồng cho thu&ecirc; bất động sản trực tiếp với chủ đầu tư. Thời hạn thu&ecirc; dự &aacute;n l&agrave; 43 năm kể từ ng&agrave;y k&yacute; hợp đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1PC-Tong-mat-bang.jpg" border="1" alt="" width="450" height="253" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">T&acirc;́t cả các villa đ&ecirc;̀u có g&oacute;c nh&igrave;n ri&ecirc;ng bi&ecirc;̣t hướng ra h&ocirc;̀ Đ&ocirc;̀ng M&ocirc;, được thi&ecirc;́t k&ecirc;́, thi c&ocirc;ng và trang bị theo ti&ecirc;u chu&acirc;̉n cao c&acirc;́p v&ecirc;̀ bi&ecirc;̣t thự nghỉ dưỡng sinh thái (2-3 ph&ograve;ng ngủ, bể bơi ri&ecirc;ng d&agrave;i tr&ecirc;n 50 m2).</p>\r\n<p class="Normal">Kh&aacute;ch h&agrave;ng c&oacute; thể sở hữu biệt thự theo hình thức thu&ecirc; dài hạn, đ&ocirc;̀ng thời có th&ecirc;̉ tham gia chương trình cùng khai thác với chủ đ&acirc;̀u tư, đơn vị quản lý khách sạn chuy&ecirc;n nghi&ecirc;̣p v&agrave; có cơ h&ocirc;̣i tham gia chương trình trao đ&ocirc;̉i kỳ nghỉ với những khách hàng của h&ecirc;̣ th&ocirc;́ng The Leading Hotels of The World tại hơn 80 qu&ocirc;́c gia và vùng lãnh th&ocirc;̉ tr&ecirc;n th&ecirc;́ giới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1aToan-canh-DA.jpg" border="1" alt="" width="450" height="244" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Dự ki&ecirc;́n, những bi&ecirc;̣t thự n&agrave;y sẽ được bàn giao vào cu&ocirc;́i năm 2011. Toàn dự án sẽ khai trương trong khoảng thời gian 30 tháng k&ecirc;̉ từ tháng 1. Trong dịp giới thiệu 19 căn villa đầu ti&ecirc;n n&agrave;y, chủ đầu tư cũng c&ocirc;ng bố chương tr&igrave;nh bốc thăm tr&uacute;ng thưởng đặc biệt &ldquo;Ngọc Vi&ecirc;n Islands &ndash; Gia tăng gi&aacute; trị cho bạn&rdquo; d&agrave;nh cho 3 kh&aacute;ch h&agrave;ng may mắn nhất trong th&aacute;ng 1. Mỗi giải thưởng l&agrave; một chuyến du lịch ch&acirc;u &Acirc;u d&agrave;nh cho 2 người hoặc giải thưởng tiền mặt trị gi&aacute; 400 triệu đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1PC-biet-thu-1.jpg" border="1" alt="" width="450" height="311" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&ldquo;Ngọc Vi&ecirc;n Islands&rdquo; l&agrave; dự &aacute;n khu nghi dưỡng theo ti&ecirc;u chu&acirc;̉n quốc tế 5 sao do C&ocirc;ng ty cổ phần Sỹ Ngàn, thành vi&ecirc;n của Beverly Investment, đầu tư. Đ&acirc;y l&agrave; tổ hợp bao g&ocirc;̀m resort, biệt thự nghỉ dưỡng và căn h&ocirc;̣ dịch vụ theo ti&ecirc;u chu&acirc;̉n qu&ocirc;́c t&ecirc;́ 5 sao tọa lạc tr&ecirc;n 3 đảo của hồ Đồng M&ocirc;, dưới ch&acirc;n n&uacute;i Tản thuộc khu vực Sơn T&acirc;y, Ba V&igrave;, cách trung t&acirc;m Hà N&ocirc;̣i 30 phút đi xe dọc theo trục phía T&acirc;y Đại l&ocirc;̣ Thăng Long.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="a11.jpg" border="1" alt="" width="450" height="273" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Với tổng diện t&iacute;ch 30 ha, khu nghỉ dưỡng bao g&ocirc;̀m 65 căn bi&ecirc;̣t thự cao c&acirc;́p từ 2 đ&ecirc;́n 5 phòng ngủ với diện t&iacute;ch từ 850 đến 3.000 m2, 80 căn h&ocirc;̣ dịch vụ, 112 phòng khách sạn, khu nhà hàng, h&ocirc;̣i nghị, vui chơi giải trí&hellip; CB Richard Ellis (CBRE) tham gia l&agrave;m đối t&aacute;c tiếp thị đ&ocirc;̣c quy&ecirc;̀n trong nước cho Ngọc Vi&ecirc;n Islands.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Kỳ Duy&ecirc;n</strong></p>', 10, 3, 2921, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(339, 'Bộ Xây dựng đề xuất lập Quỹ tín thác và Quỹ phát triển nhà', 'Hai quỹ này sẽ có mục đích chuyên đầu tư vào lĩnh vực bất động sản nhằm tháo gỡ khó khăn về vốn, tạo điều kiện cho mọi người dân có thể tiếp cận với nhà ở.', 'anh-1.jpg', NULL, '<h1 class="Title">Bộ X&acirc;y dựng đề xuất lập Quỹ t&iacute;n th&aacute;c v&agrave; Quỹ ph&aacute;t triển nh&agrave;</h1>\r\n<h2 class="Lead">Hai quỹ n&agrave;y sẽ c&oacute; mục đ&iacute;ch chuy&ecirc;n đầu tư v&agrave;o lĩnh vực bất động sản nhằm th&aacute;o gỡ kh&oacute; khăn về vốn, tạo điều kiện cho mọi người d&acirc;n c&oacute; thể tiếp cận với nh&agrave; ở.</h2>\r\n<p class="Normal">Bộ X&acirc;y dựng vừa dự thảo Chiến lược ph&aacute;t triển nh&agrave; ở đến năm 2020 v&agrave; tầm nh&igrave;n đến năm 2030, xin &yacute; kiến c&aacute;c đơn vị c&oacute; li&ecirc;n quan. Theo đ&aacute;nh gi&aacute; của cơ quan n&agrave;y, cả nước c&oacute; hơn 2.500 dự &aacute;n nh&agrave; ở đ&atilde; v&agrave; đang được triển khai như Linh Đ&agrave;m, Định C&ocirc;ng, Ciputra, Ph&uacute; Mỹ Hưng, An Ph&uacute; - An Kh&aacute;nh... nhưng nh&igrave;n chung thị trường nhà ở chưa phát tri&ecirc;̉n c&acirc;n bằng, đặc bi&ecirc;̣t là chưa đi&ecirc;̀u ti&ecirc;́t v&ecirc;̀ giá cả.</p>\r\n<p class="Normal">Giá nhà ở qu&aacute; cao, nhiều người có nhu c&acirc;̀u thực sự v&ecirc;̀ ch&ocirc;̃ ở thì kh&ocirc;ng có khả năng mua, trong khi những người tham gia đ&acirc;̀u tư lại c&oacute; cơ hội đẩy gi&aacute; nh&agrave; l&ecirc;n cao v&igrave; qua nhi&ecirc;̀u kh&acirc;u trung gian. Các quy định v&ecirc;̀ vi&ecirc;̣c tạo quỹ đ&acirc;́t cho phát tri&ecirc;̉n nhà ở, đặc bi&ecirc;̣t là x&acirc;y dựng nhà ở giá th&acirc;́p, các quy định v&ecirc;̀ giao đ&acirc;́t, cho thu&ecirc; đ&acirc;́t vẫn còn mang nặng tính xin - cho.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="anh-1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Quỹ t&iacute;n th&aacute;c bất động sản l&agrave; m&ocirc; h&igrave;nh chuy&ecirc;n đầu tư trong lĩnh vực bất động sản n&oacute;i chung v&agrave; nh&agrave; ở n&oacute;i ri&ecirc;ng. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Bộ X&acirc;y dựng cho rằng cần thiết phải lập Quỹ ph&aacute;t triển nh&agrave; ở v&agrave; Quỹ t&iacute;n th&aacute;c bất động sản để giải quyết t&igrave;nh trạng tr&ecirc;n. Theo đ&oacute;, Quỹ t&iacute;n th&aacute;c bất động sản l&agrave; m&ocirc; h&igrave;nh chuy&ecirc;n đầu tư trong lĩnh vực bất động sản n&oacute;i chung v&agrave; nh&agrave; ở n&oacute;i ri&ecirc;ng. Mục đ&iacute;ch của quỹ l&agrave; huy động vốn nh&agrave;n rỗi của c&aacute;c nh&agrave; đầu tư nhỏ lẻ th&ocirc;ng qua ph&aacute;t h&agrave;nh chứng chỉ quỹ v&agrave; đầu tư v&agrave;o lĩnh vực nh&agrave; ở. Quỹ nảy được đầu tư 100% vốn v&agrave;o bất động sản. Quỹ t&iacute;n th&aacute;c bất động sản được ưu đ&atilde;i về thuế v&agrave; l&agrave; k&ecirc;nh huy động vốn nh&agrave;n rỗi của người d&acirc;n cho thị trường nh&agrave; ở, khắc phục được t&igrave;nh trạng vốn cho thị trường nh&agrave; ở chỉ tr&ocirc;ng chờ v&agrave; ng&acirc;n h&agrave;ng như hiện nay.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, m&ocirc; h&igrave;nh Quỹ t&iacute;n th&aacute;c bất động sản l&agrave; một trong những c&aacute;ch chứng kho&aacute;n h&oacute;a bất động sản th&ocirc;ng qua chứng chỉ quỹ, v&igrave; vậy cần c&oacute; biện ph&aacute;p kiểm so&aacute;t, hạn chế những nguy cơ do chứng kho&aacute;n h&oacute;a bất động sản đem lại. Do đ&oacute;, Quỹ n&agrave;y cần được quy định cụ thể c&aacute;c ti&ecirc;u chuẩn về nh&acirc;n lực, nguồn vốn, quản l&yacute; rủi ro, chế độ b&aacute;o c&aacute;o... Quỹ t&iacute;n th&aacute;c bất động sản nhằm hỗ trợ vốn để tăng nguồn cung cho thị trường nh&agrave; ở.</p>\r\n<p class="Normal">Quỹ ph&aacute;t triển nh&agrave; ở được h&igrave;nh th&agrave;nh từ nguồn thu tiền b&aacute;n, cho thu&ecirc; nh&agrave; ở thuộc sở hữu nh&agrave; nước v&agrave; tiền sử dụng đất thu được từ c&aacute;c dự &aacute;n ph&aacute;t triển nh&agrave; ở. Ngo&agrave;i ra quỹ n&agrave;y c&ograve;n được th&agrave;nh lập từ tiền ng&acirc;n s&aacute;ch của địa phương v&agrave; c&aacute;c nguồn huy động kh&aacute;c.</p>\r\n<p class="Normal">Quỹ ph&aacute;t triển nh&agrave; ở được sử dụng v&agrave;o mục đ&iacute;ch tạo lập quỹ nh&agrave; ở thuộc sở hữu c&ocirc;ng nhằm duy tr&igrave; vai tr&ograve; của Nh&agrave; nước tr&ecirc;n thị trường, hỗ trợ cho c&ocirc;ng t&aacute;c giải ph&oacute;ng mặt bằng để tạo quỹ đất d&agrave;nh cho ph&aacute;t triển nh&agrave; ở x&atilde; hội. C&aacute;c chủ đầu tư dự &aacute;n nh&agrave; ở x&atilde; hội cũng như c&aacute;c đối tượng mua, thu&ecirc; sẽ được vay với l&atilde;i suất ưu đ&atilde;i.</p>\r\n<p class="Normal">Theo dự b&aacute;o, từ nay đến năm 2020, tại khu vực đ&ocirc; thị cả nước cần ph&aacute;t triển tăng th&ecirc;m khoảng 6,74 triệu căn nh&agrave; ở, trong đ&oacute; c&oacute; khoảng 500.000 căn nh&agrave; thu nhập thấp. Bộ phấn đấu, diện t&iacute;ch nh&agrave; ở b&igrave;nh qu&acirc;n tr&ecirc;n đầu người tại c&aacute;c đ&ocirc; thị đến năm 2015 đạt 26m2 s&agrave;n mỗi người, đến năm 2020 đạt 29m2 s&agrave;n mỗi người.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 1487, 0, 1),
(341, 'Imperia An Phú hỗ trợ lãi suất 0%', 'Chủ đầu tư của dự án Imperia An Phú (nằm trong khu quy hoạch tổng thể City Horse quận 2, TP HCM) - Inveskia đưa ra mức hỗ trợ lãi suất 0% cho khách hàng, không phân biệt đối tượng mua nhà.', 'New_Image2.JPG', NULL, '<h1 class="Title">Imperia An Ph&uacute; hỗ trợ l&atilde;i suất 0%</h1>\r\n<h2 class="Lead">Chủ đầu tư của dự &aacute;n Imperia An Ph&uacute; (nằm trong khu quy hoạch tổng thể City Horse quận 2, TP HCM) - Inveskia đưa ra mức hỗ trợ l&atilde;i suất 0% cho kh&aacute;ch h&agrave;ng, kh&ocirc;ng ph&acirc;n biệt đối tượng mua nh&agrave;.</h2>\r\n<p class="Normal">Từ th&aacute;ng 12/2010, dự &aacute;n khu căn hộ cao cấp Imperia An Ph&uacute;, quận 2, TP HCM mở đợt b&aacute;n h&agrave;ng lần thứ 6 với nhiều ưu đ&atilde;i cho người mua nh&agrave;. Cụ thể, trong đợt b&aacute;n h&agrave;ng n&agrave;y, Imperia An Ph&uacute; d&agrave;nh 100 căn hộ (số lượng n&agrave;y c&oacute; thể tăng l&ecirc;n nếu nhu cầu thị trường lớn) được b&aacute;n với mức gi&aacute; 30 triệu đồng một m2.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="New_Image2.JPG" border="1" alt="Phối cảnh dự &aacute;n." width="448" height="269" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Phối cảnh dự &aacute;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ngo&agrave;i ra, khi mua bất kỳ căn hộ cao cấp n&agrave;o của Imperia An Ph&uacute;, kh&aacute;ch h&agrave;ng chỉ cần thanh to&aacute;n 25% gi&aacute; trị căn hộ kể từ khi k&yacute; hợp đồng mua đến thời điểm nhận nh&agrave; (th&aacute;ng 10). Ng&acirc;n h&agrave;ng tham gia cho vay ngay khi kh&aacute;ch h&agrave;ng nộp được 15% v&agrave; thanh khoản đủ cho chủ đầu tư trước thời điểm nhận nhận nh&agrave;. Từ l&uacute;c bắt đầu vay đến khi nhận nh&agrave;, Inveskia - chủ đầu tư sẽ hỗ trợ to&agrave;n bộ l&atilde;i suất cho khoản vay của kh&aacute;ch h&agrave;ng (tương đương 75% gi&aacute; trị căn hộ). Đợt b&aacute;n h&agrave;ng n&agrave;y sẽ k&eacute;o d&agrave;i đến hết th&aacute;ng 3.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="New_Image3.JPG" border="1" alt="H&igrave;nh chụp thực tế dự &aacute;n." width="400" height="314" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&igrave;nh chụp thực tế dự &aacute;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Dự &aacute;n Imperia An Ph&uacute; nằm trong khu quy hoạch tổng thể City Horse quận 2 với diện t&iacute;ch 2,3 ha, liền kề c&ocirc;ng vi&ecirc;n c&acirc;y xanh rộng 7 ha, khu đ&ocirc; thị mới Thủ Thi&ecirc;m, si&ecirc;u thị Metro... Dự &aacute;n d&agrave;nh 72% diện t&iacute;ch để tạo c&aacute;c mảng xanh k&egrave;m với những tiện &iacute;ch ngo&agrave;i trời như: c&ocirc;ng vi&ecirc;n trung t&acirc;m, hồ bơi, s&acirc;n tennis&hellip; Khi ho&agrave;n th&agrave;nh trong qu&yacute; IV, Imperia An Ph&uacute; sẽ cung cấp cho thị trường nh&agrave; ở 700 căn hộ cao cấp. Cư d&acirc;n sống tại đ&acirc;y c&oacute; thể tận hưởng tất cả c&aacute;c dịch vụ k&egrave;m theo như c&ocirc;ng vi&ecirc;n, hồ bơi, nh&agrave; h&agrave;ng, trung t&acirc;m thương mại, trường học, trung t&acirc;m thể dục thể thao, ph&ograve;ng kh&aacute;m y tế, v&agrave; khu tầng hầm để xe miễn ph&iacute;...</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Imperia An Ph&uacute;</em>)</p>', 10, 3, 1707, 0, 1),
(342, 'Bán 19 villa đầu tiên tại Ngọc Viên Islands', 'Beverly Investment vừa giới thiệu ra thị trường 19 villa đầu tiên (diện tích từ 900 đến 1.250 m2) trên khu đảo Ngọc rộng 4,5 ha thuộc dự án Ngọc Viên, Sơn Tây, Ba Vì, Hà Nội.', 'duan2.jpg', NULL, '<h1 class="Title">B&aacute;n 19 villa đầu ti&ecirc;n tại Ngọc Vi&ecirc;n Islands</h1>\r\n<h2 class="Lead">Beverly Investment vừa giới thi&ecirc;̣u ra thị trường 19 villa đ&acirc;̀u ti&ecirc;n (di&ecirc;̣n tích từ 900 đến 1.250 m2) tr&ecirc;n khu đảo Ngọc r&ocirc;̣ng 4,5 ha thuộc dự &aacute;n Ngọc Vi&ecirc;n, Sơn T&acirc;y, Ba V&igrave;, H&agrave; Nội.</h2>\r\n<p class="Normal">Thời gian mở b&aacute;n l&ocirc; biệt thự đầu ti&ecirc;n tại đảo Ngọc bắt đầu từ ng&agrave;y 11/1 với gi&aacute; b&aacute;n từ 15 triệu đồng một m2. Kh&aacute;ch h&agrave;ng muốn sở hữu biệt thự tại Ngọc Vi&ecirc;n Islands sẽ thanh to&aacute;n theo 4 đợt từ ng&agrave;y k&yacute; hợp đồng đặt cọc đến khi chủ đầu tư v&agrave; người sở hữu kết th&uacute;c thủ tục b&agrave;n giao bất động sản. C&aacute;c căn biệt thự được đảm bảo ho&agrave;n th&agrave;nh v&agrave; b&agrave;n giao trong v&ograve;ng 12 th&aacute;ng từ l&uacute;c kh&aacute;ch k&yacute; hợp đồng trực tiếp với chủ đầu tư.</p>\r\n<p class="Normal">T&acirc;́t cả các villa đ&ecirc;̀u có g&oacute;c nh&igrave;n ri&ecirc;ng bi&ecirc;̣t hướng ra h&ocirc;̀ Đ&ocirc;̀ng M&ocirc;, được thi&ecirc;́t k&ecirc;́, thi c&ocirc;ng và trang bị theo ti&ecirc;u chu&acirc;̉n cao c&acirc;́p v&ecirc;̀ bi&ecirc;̣t thự nghỉ dưỡng sinh thái (2-3 ph&ograve;ng ngủ, bể bơi ri&ecirc;ng d&agrave;i tr&ecirc;n 50 m2).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan2.jpg" border="1" alt="" width="450" height="243" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kh&aacute;ch h&agrave;ng c&oacute; thể sở hữu biệt thự theo hình thức thu&ecirc; dài hạn, đ&ocirc;̀ng thời có th&ecirc;̉ tham gia chương trình cùng khai thác với chủ đ&acirc;̀u tư, đơn vị quản lý khách sạn chuy&ecirc;n nghi&ecirc;̣p v&agrave; có cơ h&ocirc;̣i tham gia chương trình trao đ&ocirc;̉i kỳ nghỉ với những khách hàng của h&ecirc;̣ th&ocirc;́ng The Leading Hotels of The World tại hơn 80 qu&ocirc;́c gia và vùng lãnh th&ocirc;̉ tr&ecirc;n th&ecirc;́ giới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="du-an1.jpg" border="1" alt="" width="450" height="273" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Dự ki&ecirc;́n, những bi&ecirc;̣t thự n&agrave;y sẽ được bàn giao vào cu&ocirc;́i năm 2011. Toàn dự án sẽ khai trương trong khoảng thời gian 30 tháng k&ecirc;̉ từ tháng 1. Trong dịp giới thiệu 19 căn villa đầu ti&ecirc;n n&agrave;y, chủ đầu tư cũng c&ocirc;ng bố chương tr&igrave;nh bốc thăm tr&uacute;ng thưởng đặc biệt &ldquo;Ngọc Vi&ecirc;n Islands &ndash; Gia tăng gi&aacute; trị cho bạn&rdquo; d&agrave;nh cho 3 kh&aacute;ch h&agrave;ng may mắn nhất trong th&aacute;ng 1. Mỗi giải thưởng l&agrave; một chuyến du lịch ch&acirc;u &Acirc;u d&agrave;nh cho 2 người hoặc giải thưởng tiền mặt trị gi&aacute; 400 triệu đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan3.jpg" border="1" alt="" width="450" height="311" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&ldquo;Ngọc Vi&ecirc;n Islands&rdquo; l&agrave; dự &aacute;n khu nghi dưỡng theo ti&ecirc;u chu&acirc;̉n quốc tế 5 sao do C&ocirc;ng ty cổ phần Sỹ Ngàn, thành vi&ecirc;n của Beverly Investment, đầu tư. Đ&acirc;y l&agrave; tổ hợp bao g&ocirc;̀m resort, biệt thự nghỉ dưỡng và căn h&ocirc;̣ dịch vụ theo ti&ecirc;u chu&acirc;̉n qu&ocirc;́c t&ecirc;́ 5 sao tọa lạc tr&ecirc;n 3 đảo của hồ Đồng M&ocirc;, dưới ch&acirc;n n&uacute;i Tản thuộc khu vực Sơn T&acirc;y, Ba V&igrave;, cách trung t&acirc;m Hà N&ocirc;̣i 30 phút đi xe dọc theo trục phía T&acirc;y Đại l&ocirc;̣ Thăng Long.</p>\r\n<p class="Normal">Với tổng diện t&iacute;ch 30 ha, khu nghỉ dưỡng bao g&ocirc;̀m 65 căn bi&ecirc;̣t thự cao c&acirc;́p từ 2 đ&ecirc;́n 5 phòng ngủ với diện t&iacute;ch từ 850 đến 3.000 m2, 80 căn h&ocirc;̣ dịch vụ, 112 phòng khách sạn, khu nhà hàng, h&ocirc;̣i nghị, vui chơi giải trí&hellip; CB Richard Ellis (CBRE) tham gia l&agrave;m đối t&aacute;c tiếp thị đ&ocirc;̣c quy&ecirc;̀n trong nước cho Ngọc Vi&ecirc;n Islands.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Kỳ Duy&ecirc;n</strong></p>', 10, 3, 2444, 0, 1),
(343, 'Giá mặt bằng bán lẻ Hà Nội - TP HCM cùng hạ nhiệt', 'Giá thuê trung bình toàn thị trường mặt bằng bán lẻ ở TP HCM năm 2010 giảm 11% so với năm 2009, tỷ lệ trống tăng 8,7%. Tại Hà Nội, giá thuê khu vực trung tâm vẫn đắt đỏ, song khu vực ngoại ô bắt đầu giảm.', 'lan1.jpg', NULL, '<h1 class="Title">Gi&aacute; mặt bằng b&aacute;n lẻ H&agrave; Nội - TP HCM c&ugrave;ng hạ nhiệt</h1>\r\n<h2 class="Lead">Gi&aacute; thu&ecirc; trung b&igrave;nh to&agrave;n thị trường mặt bằng b&aacute;n lẻ ở TP HCM năm 2010 giảm 11% so với năm 2009, tỷ lệ trống tăng 8,7%. Tại H&agrave; Nội, gi&aacute; thu&ecirc; khu vực trung t&acirc;m vẫn đắt đỏ, song khu vực ngoại &ocirc; bắt đầu giảm.</h2>\r\n<p class="Normal">B&aacute;o c&aacute;o qu&yacute; của 4 năm 2010 của CBRE tại TP HCM cho thấy, mặt bằng bỏ trống ng&agrave;y một nhiều l&ecirc;n. Tỷ lệ trống của tổng mặt bằng b&aacute;n lẻ trung t&acirc;m thương mại tăng 8,7% trong so với qu&yacute; trước đ&oacute;. Cũng theo đơn vị n&agrave;y, gi&aacute; thu&ecirc; mặt bằng tại khu vực trung t&acirc;m TP HCM chỉ tăng 1% so với c&ugrave;ng kỳ qu&yacute; trước, đạt 125,38 USD mỗi m2 một th&aacute;ng.</p>\r\n<p class="Normal">C&ograve;n thống k&ecirc; của Savills, gi&aacute; thu&ecirc; trung b&igrave;nh của to&agrave;n thị trường giảm 11% so với năm 2009, do gi&aacute; thu&ecirc; tại khu vực ngoại th&agrave;nh giảm. Ri&ecirc;ng Knight Frank cho biết, gi&aacute; thu&ecirc; mặt bằng b&aacute;n lẻ đang chững lại. Theo đ&oacute;, khu trung t&acirc;m c&oacute; gi&aacute; thu&ecirc; 95-150 USD mỗi m2 một th&aacute;ng trong khi khu vực ngo&agrave;i trung t&acirc;m gi&aacute; thu&ecirc; 35-85 USD mỗi m2 một th&aacute;ng.</p>\r\n<p class="Normal">Khảo s&aacute;t của C&ocirc;ng ty Savills, hiện to&agrave;n TP HCM c&oacute; 6 trung t&acirc;m b&aacute;ch h&oacute;a, 20 trung t&acirc;m mua sắm, 6 khu b&aacute;n lẻ khối đế, 58 si&ecirc;u thị v&agrave; 3 si&ecirc;u thị b&aacute;n sỉ với tổng diện t&iacute;ch khoảng 625.000 m2, tăng 27% so với năm 2009. Mức tăng n&agrave;y ph&acirc;n bố kh&ocirc;ng đồng đều, khu trung t&acirc;m nguồn cung mặt bằng b&aacute;n lẻ tăng 58% c&ograve;n c&aacute;c quận ngoại th&agrave;nh tăng 24%. Quận 2, 7 đang nổi l&ecirc;n l&agrave; hai địa b&agrave;n được quan t&acirc;m nhiều nhất do gần trung t&acirc;m v&agrave; mật độ d&acirc;n số cao.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="lan1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="450" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thị trường b&aacute;n lẻ ở Việt Nam c&ograve;n nhiều tiềm năng. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong><span style="color: #2f2f2f;">Tại H&agrave; Nội</span></strong>, &ocirc;ng Richard Leech, Gi&aacute;m đốc Điều h&agrave;nh CBRE Việt Nam nhận định ,&nbsp;<strong><span style="color: #2f2f2f;">do gi&aacute; trong khu vực trung t&acirc;m qu&aacute; đắt đỏ n&ecirc;n sẽ c&oacute; sự chuyển dịch của nhiều doanh nghiệp sang khu vực ph&iacute;a T&acirc;y</span></strong>, nơi c&oacute; nhiều dự &aacute;n mới ph&aacute;t triển. CBRE cho hay, gi&aacute; ch&agrave;o thu&ecirc; trung b&igrave;nh to&agrave;n thị trường qu&yacute; 4 năm 2010 đạt 45,58 USD mỗi m2 một th&aacute;ng, khu vực ngo&agrave;i trung t&acirc;m khoảng hơn 36 USD mỗi m2 một th&aacute;ng giảm 3,8% so với qu&yacute; trước.</p>\r\n<p class="Normal">Nghi&ecirc;n cứu của Savills Việt Nam cũng cho thấy, gi&aacute; thu&ecirc; mặt bằng b&aacute;n lẻ dao động từ 6 USD đến 185 USD mỗi m2. Khối đế b&aacute;n lẻ trong khu vực trung t&acirc;m th&agrave;nh phố lu&ocirc;n đạt mức gi&aacute; cao nhất. Hoạt động của thị trường b&aacute;n lẻ ổn định so với qu&yacute; trước. C&ocirc;ng suất thu&ecirc; trung b&igrave;nh của cả thị trường đạt 93%, giảm 1% so với qu&yacute; 3 năm 2010.</p>\r\n<p class="Normal">Nguồn cung b&aacute;n lẻ to&agrave;n thị trường H&agrave; Nội đạt khoảng 430.000 m2 từ 130 dự &aacute;n, bao gồm 13 trung t&acirc;m thương mại, trung t&acirc;m b&aacute;ch h&oacute;a, đại si&ecirc;u thị, 67 si&ecirc;u thị, 34 si&ecirc;u thị điện m&aacute;y, 2 si&ecirc;u thị b&aacute;n sỉ v&agrave; 14 khối đế b&aacute;n lẻ.</p>\r\n<p class="Normal">Theo Savills,&nbsp;<strong><span style="color: #2f2f2f;">thị trường b&aacute;n lẻ Việt Nam c&ograve;n nhiều tiềm năng</span></strong>. Xu hướng ti&ecirc;u d&ugrave;ng hiện nay tại Việt Nam vẫn c&ograve;n rộng mở với c&aacute;c thương hiệu cao cấp, c&aacute;c cửa h&agrave;ng nhượng quyền quốc tế v&agrave; h&agrave;ng h&oacute;a nội địa chất lượng cao do kinh tế tăng trưởng đều, d&acirc;n số trẻ, h&agrave;nh vi ti&ecirc;u d&ugrave;ng thay đổi v&agrave; lượng người gi&agrave;u l&ecirc;n ng&agrave;y c&agrave;ng tăng.</p>\r\n<p class="Normal">Dự b&aacute;o của Knight Frank, c&aacute;c nh&agrave; b&aacute;n lẻ ng&agrave;y c&agrave;ng đ&ograve;i hỏi nhiều hơn, đưa ra y&ecirc;u cầu cao hơn về vị tr&iacute;, c&aacute;ch thiết kế gian h&agrave;ng, mức thu&ecirc; phản &aacute;nh mức độ kinh doanh m&agrave; vị tr&iacute; mang lại. Sắp tới sẽ c&oacute; những cuộc dịch chuyển, thay đổi kh&aacute;ch thu&ecirc; khi c&oacute; nhiều trung t&acirc;m b&aacute;n lẻ ra đời.</p>\r\n<p class="Normal">Tại hai th&agrave;nh phố H&agrave; Nội v&agrave; TP HCM, trong qu&yacute; 4 năm 2010,&nbsp;<strong><span style="color: #2f2f2f;">nhu cầu mặt bằng b&aacute;n lẻ vẫn tăng đều</span>.</strong>&nbsp;Khu vực TP HCM, The Body Shop mở cửa h&agrave;ng thứ hai, The Faceshop mở cửa hiệu thứ s&aacute;u tr&ecirc;n đường Nguyễn Tr&atilde;i, quận 1. C&aacute;c nh&agrave; b&aacute;n lẻ F&amp;B như Kichi-Kichi v&agrave; Breadtalk cũng mở th&ecirc;m cửa h&agrave;ng tại đường Cao Thắng, quận 3. Tại H&agrave; Nội, nhiều h&atilde;ng thời trang, điện m&aacute;y, đồ ăn nhanh như Aldo, Guciano, KFC, Pico Plaza, E Best đồng loạt gia nhập thị trường.</p>\r\n<p class="Normal">Trong qu&yacute; 1 năm 2011 dự kiến sẽ c&oacute; th&ecirc;m &iacute;t nhất 3 trung t&acirc;m b&aacute;n lẻ đi v&agrave;o hoạt động ở khu vực TP HCM: The Crescent, Bitexco Financial Tower v&agrave; The Manor giai đoạn 2, cung cấp khoảng 36.000 m2 s&agrave;n b&aacute;n lẻ cho thị trường. Trong v&ograve;ng 5 năm tới, dự kiến thị trường b&aacute;n lẻ tại TP HCM sẽ c&oacute; th&ecirc;m khoảng 800.000 m2 nguồn cung mới. Nguồn cung tương lai chủ yếu tập trung tại c&aacute;c quận 1, 2, 7.</p>\r\n<p class="Normal">Thị trường H&agrave; Nội, trong năm nay, c&aacute;c c&ocirc;ng ty tư vấn dự đo&aacute;n sẽ c&oacute; 12 dự &aacute;n gia nhập thị trường H&agrave; Nội với 116.000 m2. Nhiều dự &aacute;n mới sắp đi v&agrave;o hoạt động như H&agrave;ng Da Galleria dự kiến mở cửa v&agrave;o th&aacute;ng 4 tới. Pico Mall dự kiến mở cửa v&agrave;o th&aacute;ng 3. Vincom City Towers đang trong qu&aacute; tr&igrave;nh cải thiện n&acirc;ng cấp. Savills cho hay, h&igrave;nh thức cho thu&ecirc; d&agrave;i hạn mặt bằng b&aacute;n lẻ đang trở th&agrave;nh một k&ecirc;nh đầu tư mới cho c&aacute;c nh&agrave; đầu tư.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan- Vũ L&ecirc;</strong></p>', 10, 3, 2994, 0, 1),
(344, 'Thủ tướng yêu cầu kiểm tra biệt thự bỏ hoang ở Hà Nội', 'Thủ tướng Nguyễn Tấn Dũng vừa yêu cầu UBND thành phố Hà Nội kiểm tra việc hàng chục căn biệt thự tiền tỷ bỏ hoang nhiều năm nay báo cáo Thủ tướng trước 15/2.', '8_1.jpg', NULL, '<h1 class="Title">Thủ tướng y&ecirc;u cầu kiểm tra biệt thự bỏ hoang ở H&agrave; Nội</h1>\n<h2 class="Lead">Thủ tướng Nguyễn Tấn Dũng vừa y&ecirc;u cầu UBND th&agrave;nh phố H&agrave; Nội kiểm tra việc h&agrave;ng chục căn biệt thự tiền tỷ bỏ hoang nhiều năm nay b&aacute;o c&aacute;o Thủ tướng trước 15/2.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="8_1.jpg" border="1" alt="Ảnh: Đo&agrave;n Loan" width="480" height="330" /></td>\n</tr>\n<tr>\n<td class="Image">Những ng&ocirc;i biệt thự ở khu vực Ph&aacute;p V&acirc;n- Tứ Hiệp bị bỏ hoang cỏ mọc tốt um. Ảnh:&nbsp;<em>Đo&agrave;n Loan.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Tại cụm chung cư An Sinh Mỹ Đ&igrave;nh 2, Từ Li&ecirc;m, H&agrave; Nội c&oacute; v&agrave;i chục căn biệt thự lớn đ&atilde; được x&acirc;y xong phần th&ocirc; từ 5 năm nay đang bị bỏ hoang cỏ c&acirc;y mọc um t&ugrave;m. Trong khi đ&oacute;, quỹ đất của th&agrave;nh phố đang rất eo hẹp v&agrave; nhiều người d&acirc;n thiếu chỗ ở. Sự tồn tại của c&aacute;c biệt thự tr&ecirc;n kh&ocirc;ng chỉ l&atilde;ng ph&iacute; v&agrave; quỹ đất m&agrave; c&ograve;n thể hiện sự quy hoạch v&ocirc; lối l&agrave;m bẩn bộ mặt đ&ocirc; thị v&agrave; trở th&agrave;nh nơi tụ tập của những kẻ trộm cắp, nghiện h&uacute;t tạo điều kiện cho tệ nạn x&atilde; hội ph&aacute;t triển.</p>\n<p class="Normal">Thủ tướng y&ecirc;u cầu th&agrave;nh phố kiểm tra h&agrave;ng chục căn biệt thự tại cụm chung cư An Sinh, Mỹ Đ&igrave;nh 2, Từ Li&ecirc;m đồng thời đề xuất biện ph&aacute;p ph&aacute;p xử l&yacute;, b&aacute;o c&aacute;o Thủ tướng trước ng&agrave;y 15/2.</p>\n<p class="Normal">Thực tế c&oacute; h&agrave;ng loạt biệt thự x&acirc;y th&ocirc; rộng h&agrave;ng trăm m2 ở c&aacute;c khu đ&ocirc; thị mới gi&aacute; l&ecirc;n tới 20-30 tỷ đồng bị bỏ hoang 5-6 năm nay. Theo t&igrave;m hiểu của&nbsp;<em>VnExpress.net,</em>trong khu đ&ocirc; thị mới Ph&aacute;p V&acirc;n - Tứ Hiệp (quận Ho&agrave;ng Mai) c&oacute; h&agrave;ng chục biệt thự chưa c&oacute; người ở, để cỏ dại mọc um t&ugrave;m, r&aacute;c rưởi tr&agrave;n lan. C&oacute; biệt thự đ&atilde; qua nhiều chủ nh&acirc;n, song vẫn chưa c&oacute; người đến ở. Phần lớn chủ nh&acirc;n l&agrave; người kh&ocirc;ng c&oacute; nhu cầu ở, mua b&aacute;n biệt thự để đầu tư kiếm lời. Khu đ&ocirc; thị Văn Qu&aacute;n (quận H&agrave; Đ&ocirc;ng) cũng c&oacute; kh&aacute; nhiều biệt thự "bỏ hoang" tại vị tr&iacute; mặt đường đẹp.</p>\n<p class="Normal">C&aacute;c chuy&ecirc;n gia cho rằng, t&igrave;nh trạng &ocirc;m đất bỏ hoang đang rất b&aacute;o động. Th&agrave;nh phố H&agrave; Nội đ&atilde; ra th&ocirc;ng điệp sẽ chỉ đạo r&agrave; so&aacute;t, kiểm tra ph&acirc;n loại xử l&yacute; c&aacute;c trường hợp dự &aacute;n chậm tiến độ bỏ hoang h&oacute;a song nhiều chuy&ecirc;n gia lo ngại đ&acirc;y chỉ l&agrave; h&ocirc; h&agrave;o su&ocirc;ng.</p>\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 2752, 0, 1),
(345, 'Địa ốc Hà Nội năm 2011 có nhiều điểm sáng', 'Cơn xả hàng ồ ạt vào cuối năm 2010 khiến nhiều chuyên gia địa ốc kỳ vọng thị trường sẽ khởi sắc vào năm nay. Đa số ý kiến cho rằng, thị trường nhà trung bình và mặt bằng bán lẻ sẽ thu hút được sự quan tâm của nhiều người.', '2_2.jpg', NULL, '<h1 class="Title">Địa ốc H&agrave; Nội năm 2011 c&oacute; nhiều điểm s&aacute;ng</h1>\n<h2 class="Lead">Cơn xả h&agrave;ng ồ ạt v&agrave;o cuối năm 2010 khiến nhiều chuy&ecirc;n gia địa ốc kỳ vọng thị trường sẽ khởi sắc v&agrave;o năm nay. Đa số &yacute; kiến cho rằng, thị trường nh&agrave; trung b&igrave;nh v&agrave; mặt bằng b&aacute;n lẻ sẽ thu h&uacute;t được sự quan t&acirc;m của nhiều người.</h2>\n<p class="Normal">&Ocirc;ng Troy Griffiths, Gi&aacute;m đốc Bộ phận Nghi&ecirc;n cứu v&agrave; Định gi&aacute; C&ocirc;ng ty Savills Việt Nam cho rằng, trong năm 2011, thị trường bất động sản Việt Nam c&oacute; nhiều t&iacute;n hiệu lạc quan với điều kiện kinh tế vĩ m&ocirc; ổn định. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; tỷ lệ đ&ocirc; thị h&oacute;a cao, d&acirc;n số trẻ, tỷ lệ người gi&agrave;u gia tăng nhanh... "Điều kiện vĩ m&ocirc; thuận lợi sẽ hỗ trợ cho c&aacute;c sản phẩm bất động sản nhắm đến nh&oacute;m d&acirc;n số chiếm tỷ lệ lớn tr&ecirc;n thị trường. Nhiều nh&agrave; đầu tư vẫn sẽ v&agrave;o Việt Nam", &ocirc;ng Troy Griffiths nhận định.</p>\n<p class="Normal">Trong năm 2011, ph&acirc;n kh&uacute;c nh&agrave; gi&aacute; trung b&igrave;nh sẽ được nhiều kh&aacute;ch h&agrave;ng chọn lựa. Theo &ocirc;ng Ng&ocirc; Thế Vinh, Gi&aacute;m đốc s&agrave;n giao dịch bất động sản Vinh Gia, cuối th&aacute;ng 12/2010, trong khi ph&acirc;n kh&uacute;c nh&agrave; cao c&oacute; lượng giao dịch chậm lại th&igrave; d&ograve;ng sản phẩm nh&agrave; diện t&iacute;ch nhỏ dưới 60 m2 gi&aacute;&nbsp;<a class="Normal" href="http://vnexpress.net/GL/Kinh-doanh/Bat-Dong-san/2010/12/3BA23B09/">1-2 tỷ đồng</a>&nbsp;vẫn thu h&uacute;t sự quan t&acirc;m của nhiều kh&aacute;ch h&agrave;ng v&agrave; xu hướng n&agrave;y sẽ tiếp diễn trong năm 2011.</p>\n<p class="Normal">&Ocirc;ng Peter Ryder, Tổng gi&aacute;m đốc điều h&agrave;nh Tập đo&agrave;n Indochina Capital tiết lộ, trong năm 2011, tập đo&agrave;n sẽ bắt đầu "tấn c&ocirc;ng" ph&acirc;n kh&uacute;c nh&agrave; b&igrave;nh d&acirc;n thay v&igrave; chỉ đầu tư v&agrave;o mảng cao cấp. "Mặc d&ugrave; trong thời gian qua thị trường bất động sản Việt Nam trầm lắng, nhưng đ&acirc;y chỉ l&agrave; ngắn hạn. Thị trường sẽ s&ocirc;i động hơn trong thời gian tới khi nền kinh tế phục hồi v&agrave; tăng trường ổn định", &ocirc;ng Rider n&oacute;i.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="2_2.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="350" /></td>\n</tr>\n<tr>\n<td class="Image">Ph&acirc;n kh&uacute;c nh&agrave; ở b&igrave;nh d&acirc;n vẫn h&uacute;t kh&aacute;ch. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">B&ecirc;n cạnh đ&oacute; ph&acirc;n kh&uacute;c b&aacute;n lẻ cũng được đ&aacute;nh gi&aacute; l&agrave; sẽ ph&aacute;t triển trong năm nay. C&ocirc;ng ty CBRE cũng cho hay, trong qu&yacute; 4/2010, nhiều thương hiệu b&aacute;n lẻ c&oacute; uy t&iacute;n tiếp tục mở rộng mạng lưới kinh doanh tại H&agrave; Nội. Một loạt chuỗi si&ecirc;u thị điện m&aacute;y thiết lập th&ecirc;m điểm kinh doanh như Pico Plaza tr&ecirc;n phố Xu&acirc;n Thủy, E-Best tr&ecirc;n phố B&agrave; Triệu, Hương Anh tr&ecirc;n phố Giảng V&otilde;...</p>\n<p class="Normal">Theo CBRE, năm 2010, tổng cung diện t&iacute;ch b&aacute;n lẻ ở mức 118.500 m2. Năm 2011 dự đo&aacute;n thị trường sẽ đ&oacute;n nhận nguồn cung bổ sung lớn từ c&aacute;c dự &aacute;n: Pico Mall (30.000 m2), H&agrave;ng Da Galleria (9.310 m2), Savico Plaza (52.000 m2), Keangnam Hanoi Landmark Tower (97.000 m2). H&atilde;ng tư vấn n&agrave;y dự b&aacute;o, thị trường mặt bằng b&aacute;n lẻ sẽ tiếp tục thịnh vượng trong năm nay v&agrave; gi&aacute; thu&ecirc; sẽ tăng.</p>\n<p class="Normal">Trong khi ph&acirc;n kh&uacute;c b&aacute;n lẻ v&agrave; nh&agrave; gi&aacute; trung b&igrave;nh được dự b&aacute;o l&agrave; điểm s&aacute;ng tr&ecirc;n thị trường th&igrave; mảng dự &aacute;n cao cấp kh&ocirc;ng mấy khả quan. &Ocirc;ng Richard Leech, Gi&aacute;m đốc Điều h&agrave;nh CBRE Việt Nam cho hay, trong năm 2010, c&aacute;c chủ đầu tư đ&atilde; sử dụng rất nhiều biện ph&aacute;p như giảm gi&aacute; b&aacute;n, khuyến m&atilde;i nhưng tốc độ b&aacute;n kh&aacute; chậm bởi giới đầu cơ kh&ocirc;ng c&ograve;n mặn m&agrave; với ph&acirc;n kh&uacute;c n&agrave;y.</p>\n<p class="Normal">"Sang năm 2011, xu hướng n&agrave;y sẽ c&ograve;n tiếp diễn. Chủ đầu tư sẽ phải cam kết n&acirc;ng cao dịch vụ, chất lượng v&agrave; c&aacute;c sản phẩm đi k&egrave;m để k&iacute;ch cầu b&aacute;n h&agrave;ng. Thị trường chỉ c&oacute; chỗ cho những sản phẩm chất lượng thực sự", &ocirc;ng &Ocirc;ng Richard Leech n&oacute;i.</p>\n<p class="Normal">Theo &ocirc;ng Phạm Trung H&agrave;, Tổng gi&aacute;m đốc H&ograve;a Ph&aacute;t Land, trong năm nay t&igrave;nh h&igrave;nh bất động sản sẽ c&oacute; những chuyển biến đ&aacute;ng kể. Thị trường sẽ kh&ocirc;ng chỉ n&oacute;ng ở khu vực ph&iacute;a T&acirc;y m&agrave; cả ph&iacute;a Đ&ocirc;ng cũng sẽ h&uacute;t kh&aacute;ch. C&aacute;c c&acirc;y cầu Vĩnh Tuy, Thanh Tr&igrave;, Nhật T&acirc;n, Tứ Li&ecirc;n đi v&agrave;o hoạt động sẽ khiến bất động sản khu vực n&agrave;y khởi sắc hơn.</p>\n<p class="Normal">L&atilde;nh đạo của c&ocirc;ng ty bất động sản n&agrave;y dự b&aacute;o, trong giai đoạn ngắn hạn, thị trường sẽ tiếp tục tăng gi&aacute;, đặc biệt l&agrave; những dự &aacute;n mới bắt đầu ch&agrave;o b&aacute;n hoặc những dự &aacute;n c&oacute; cơ sở hạ tầng n&acirc;ng cấp ổn định. Tuy nhi&ecirc;n, x&eacute;t về d&agrave;i hạn, khi nguồn cung dồi d&agrave;o cũng như hạ tầng giao th&ocirc;ng kết nối giữa c&aacute;c khu đ&ocirc; thị v&agrave; trung t&acirc;m tốt hơn, gi&aacute; bất động sản sẽ chững lại hoặc giảm nhẹ, đặc biệt l&agrave; ph&acirc;n kh&uacute;c chung cư.</p>\n<p class="Normal">&Ocirc;ng Đặng H&ugrave;ng V&otilde;, cố vấn cấp cao của Bộ T&agrave;i nguy&ecirc;n M&ocirc;i trường dự đo&aacute;n, kịch bản của thị trường bất động sản năm 2011 sẽ kh&ocirc;ng kh&aacute;c so với năm 2010, vẫn n&oacute;ng lạnh cục bộ. Cuối năm 2010, tỷ gi&aacute; đ&ocirc;la biến động c&ugrave;ng l&atilde;i suất ng&acirc;n h&agrave;ng tăng cao l&agrave;m thị trường n&oacute;i chung ph&aacute;t triển chậm lại. "Điều n&agrave;y dẫn đến những giao dịch nh&agrave; ở thuộc khu vực gi&aacute; rẻ c&oacute; thể sẽ tăng cao nhưng nh&agrave; ở gi&aacute; cao sẽ chậm lại, &iacute;t dần đi", &ocirc;ng V&otilde; dự đo&aacute;n.</p>\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 1346, 0, 1),
(346, 'Hà Nội nới điều kiện mua nhà thu nhập thấp', 'Hà Nội kiến nghị, các cán bộ, công nhân, viên chức được hưởng lương từ ngân sách Nhà nước có diện tích nhà ở bình quân trên 5 m2 mỗi người cũng sẽ được mua nhà thu nhập thấp.', 'tn.jpg', NULL, '<h1 class="Title">H&agrave; Nội nới điều kiện mua nh&agrave; thu nhập thấp</h1>\r\n<h2 class="Lead">H&agrave; Nội kiến nghị, c&aacute;c c&aacute;n bộ, c&ocirc;ng nh&acirc;n, vi&ecirc;n chức được hưởng lương từ ng&acirc;n s&aacute;ch Nh&agrave; nước c&oacute; diện t&iacute;ch nh&agrave; ở b&igrave;nh qu&acirc;n tr&ecirc;n 5 m2 mỗi người cũng sẽ được mua nh&agrave; thu nhập thấp.<br /><span style="font-size: 10px; font-weight: normal;">H&agrave; Nội vừa c&oacute; văn bản gửi Văn ph&ograve;ng Ch&iacute;nh phủ, Văn ph&ograve;ng Quốc hội, Bộ X&acirc;y dựng, Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường xin &yacute; kiến về Đề &aacute;n nh&agrave; ở cho đối tượng được hưởng lương ng&acirc;n s&aacute;ch tr&ecirc;n địa b&agrave;n th&agrave;nh phố giai đoạn 2011-2015 v&agrave; 2020. Theo Luật Nh&agrave; ở v&agrave; Nghị Quyết 18 của Ch&iacute;nh phủ, nh&agrave; ở cho người thu nhập thấp tại khu vực đ&ocirc; thị chỉ d&agrave;nh cho c&aacute;c đối tượng chưa c&oacute; nh&agrave; hoặc đ&atilde; c&oacute; nh&agrave; nhưng diện t&iacute;ch b&igrave;nh qu&acirc;n dưới 5 m2 s&agrave;n mỗi người.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tn.jpg" border="1" alt="" width="480" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Năm 2011 sẽ c&oacute; khoảng 70.000 hộ thu nhập thấp được c&oacute; nh&agrave; ở. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">UBND th&agrave;nh phố cho rằng, c&aacute;c đối tượng hưởng lương từ ng&acirc;n s&aacute;ch c&oacute; nh&agrave; ở với diện t&iacute;ch tr&ecirc;n 5 m2 mỗi người chưa c&oacute; ch&iacute;nh s&aacute;ch để giải quyết. Mặc d&ugrave; gần đ&acirc;y, Nh&agrave; nước đ&atilde; điều chỉnh mức lương tối thiểu nhưng do gi&aacute; cả nh&agrave; ở đất ở vẫn rất cao n&ecirc;n những người n&agrave;y kh&ocirc;ng c&oacute; điều kiện để mua nh&agrave; ở thương mại theo gi&aacute; thị trường.</p>\r\n<p class="Normal">Do đ&oacute;, đề &aacute;n cho rằng, c&aacute;c c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n chức hưởng lương từ ng&acirc;n s&aacute;ch Nh&agrave; nước chưa được ph&acirc;n nh&agrave; v&agrave; c&oacute; diện t&iacute;ch nh&agrave; ở tr&ecirc;n 5 m2 mỗi đầu người n&ecirc;n được sở hữu nh&agrave; thu nhập thấp. Chủ đầu tư x&acirc;y dựng nh&agrave; ở thu nhập thấp được hưởng ch&iacute;nh s&aacute;ch ưu đ&atilde;i như được miễn thuế thu nhập doanh nghiệp trong 4 năm đầu ti&ecirc;n v&agrave; được giảm 50% thuế trong 5 năm tiếp theo... Nh&agrave; đầu tư được th&agrave;nh phố xem x&eacute;t cho ph&eacute;p được nộp tiền sử dụng v&agrave;o thời điểm thanh to&aacute;n lần cuối với người mua nh&agrave;, được th&agrave;nh phố hỗ trợ về cơ sở hạ tầng giao th&ocirc;ng, cấp điện, cấp tho&aacute;t nước.</p>\r\n<p class="Normal">Quỹ đất để x&acirc;y nh&agrave; thu nhập thấp được lấy từ quỹ đất 20% chủ đầu tư c&aacute;c dự &aacute;n khu đ&ocirc; thị mới, khu nh&agrave; ở b&agrave;n giao lại th&agrave;nh phố, đất xen kẹt trong c&aacute;c khu d&acirc;n cư... Mỗi căn hộ sẽ c&oacute; diện tịch từ 50-120 m2 để người mua c&oacute; điều kiện chọn lựa.</p>\r\n<p class="Normal">Trước mắt, dự thảo đề &aacute;n cho rằng, từ 2011-2015, dự &aacute;n sẽ giải quyết cơ bản chỗ ở cho người thu nhập thấp (chưa c&oacute; nh&agrave; hoặc diện t&iacute;ch nh&agrave; ở nhỏ hơn 20 m2 mỗi người) đạt 25.500 căn hộ tương ứng 3 triệu m2 nh&agrave; ở.</p>\r\n<p class="Normal">Trước đ&oacute;, t&ograve;a nh&agrave; CT1-Ng&ocirc; Th&igrave; Nhậm, quận H&agrave; Đ&ocirc;ng, dự &aacute;n nh&agrave; ở thu nhập thấp đầu ti&ecirc;n của H&agrave; Nội đ&atilde; thu h&uacute;t đ&ocirc;ng đảo người d&acirc;n quan t&acirc;m. Chủ đầu tư đ&atilde; phải tiến h&agrave;nh bốc thăm để chọn người mua. Ngay sau đ&oacute;, h&agrave;ng loạt dự &aacute;n nh&agrave; ở cho người thu nhập thấp đ&atilde; được khởi c&ocirc;ng tại c&aacute;c quận H&agrave; Đ&ocirc;ng, Long Bi&ecirc;n v&agrave; huyện Gia L&acirc;m. Bộ X&acirc;y dựng hiện đang tiếp tục phối hợp với c&aacute;c bộ, ng&agrave;nh để huy động nguồn vốn nhằm đẩy nhanh tiến độ triển khai c&aacute;c dự &aacute;n nh&agrave; thu nhập thấp v&agrave; phấn đấu năm 2011 sẽ c&oacute; khoảng 70.000 hộ thu nhập thấp được c&oacute; nh&agrave; ở.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 2313, 0, 1),
(347, 'Biệt thự quận Cầu Giấy đắt nhất Hà Nội', 'Với mức giá trung bình khoảng 8.500 USD mỗi m2, quận Cầu Giấy có giá biệt thự và nhà liền kề cao nhất Hà Nội. Đứng kế tiếp là khu vực Tây Hồ, Từ Liêm với giá lần lượt là 7.500 USD và 7.000 USD.', 'biet-thu-5.jpg', NULL, '<h1 class="Title">Biệt thự quận Cầu Giấy đắt nhất H&agrave; Nội</h1>\r\n<h2 class="Lead">Với mức gi&aacute; trung b&igrave;nh khoảng 8.500 USD mỗi m2, quận Cầu Giấy c&oacute; gi&aacute; biệt thự v&agrave; nh&agrave; liền kề cao nhất H&agrave; Nội. Đứng kế tiếp l&agrave; khu vực T&acirc;y Hồ, Từ Li&ecirc;m với gi&aacute; lần lượt l&agrave; 7.500 USD v&agrave; 7.000 USD.</h2>\r\n<p class="Normal">Theo b&aacute;o c&aacute;o qu&yacute; 4/2010 của C&ocirc;ng ty Nghi&ecirc;n cứu v&agrave; Tư vấn Savills, tổng nguồn cung của H&agrave; Nội gồm 12.300 biệt thự v&agrave; 15.500 nh&agrave; liền kề từ 86 dự &aacute;n. Khảo s&aacute;t tại 14 quận huyện cũ v&agrave; mở rộng của H&agrave; Nội như Hai B&agrave; Trưng, Thanh Xu&acirc;n, Ho&agrave;ng Mai, Cầu Giấy, Từ Li&ecirc;m..., Savills Việt Nam cho hay, gi&aacute; của biệt thự v&agrave; nh&agrave; liền kề đều tiếp tục tăng so với qu&yacute; trước v&agrave; biến động tr&ecirc;n hầu hết c&aacute;c khu vực. Quận H&agrave; Đ&ocirc;ng vẫn giữ vị tr&iacute; đứng đầu về nguồn cung với 8.300 biệt thự, liền kề, tương ứng với 35% tổng lượng cung của thị trường H&agrave; Nội.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="biet-thu-5.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khu vực Cầu Giấy c&oacute; gi&aacute; nh&agrave; liền kề v&agrave; biệt thự cao nhất H&agrave; Nội với mức trung b&igrave;nh khoảng 8.500 USD mỗi m2. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Khu vực Cầu Giấy c&oacute; gi&aacute; nh&agrave; liền kề v&agrave; biệt thự cao nhất H&agrave; Nội với mức trung b&igrave;nh khoảng 8.500 USD mỗi m2. Đứng thứ hai l&agrave; quận T&acirc;y Hồ khoảng 7.500 USD mỗi m2. Tiếp đến l&agrave; khu vực Từ Li&ecirc;m khoảng 7.000 USD mỗi m2. Biệt thự M&ecirc; Linh gi&aacute; rẻ nhất khoảng 800 USD mỗi m2.</p>\r\n<p class="Normal">Trao đổi với<em>&nbsp;VnExpress.net</em>, b&agrave; Đỗ Thu Hằng, Trưởng bộ phận nghi&ecirc;n cứu v&agrave; tư vấn Savills Việt Nam cho hay, gi&aacute; biệt thự, nh&agrave; liền kề được khảo s&aacute;t l&agrave; gi&aacute; mua đi b&aacute;n lại tr&ecirc;n thị trường, v&igrave; phần lớn c&aacute;c dự &aacute;n hiện tại đ&atilde; được chủ đầu tư b&aacute;n hết trước qu&yacute; 4 năm 2010. Biệt thự v&agrave; nh&agrave; liền kề quận Cầu Giấy c&oacute; mức gi&aacute; cao nhất do đ&acirc;y l&agrave; khu vực c&oacute; cơ sở hạ tầng ph&aacute;t triển tốt, li&ecirc;n th&ocirc;ng giữa c&aacute;c khu đ&ocirc; thị như Trung Y&ecirc;n, Y&ecirc;n H&ograve;a, Trung H&ograve;a...</p>\r\n<p class="Normal">"Tại đ&acirc;y, hầu hết c&aacute;c dự &aacute;n biệt thự, nh&agrave; liền kề đ&atilde; được ho&agrave;n thiện v&agrave; h&igrave;nh th&agrave;nh những cộng đồng d&acirc;n cư ổn định. Th&ecirc;m v&agrave;o đ&oacute;, quận Cầu Giấy cũng được coi l&agrave; trung t&acirc;m h&agrave;nh ch&iacute;nh quốc gia mới của H&agrave; Nội", b&agrave; Hằng nhận định</p>\r\n<p class="Normal">Theo Savills Việt Nam, kh&aacute;ch h&agrave;ng tiềm năng của thị trường biệt thự v&agrave; nh&agrave; liền kề l&agrave; những người c&oacute; tiềm lực t&agrave;i ch&iacute;nh. Trong trung hạn, ước t&iacute;nh c&oacute; khoảng 69 dự &aacute;n biệt thự, nh&agrave; liền kề sẽ tham gia thị trường.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 179, 0, 1),
(349, 'Nhà bán bằng vàng giảm giá', 'Nhiều căn nhà phố ở TP HCM trước đây được rao bán bằng vàng với giá không đổi nay tính đến chuyện sẵn sàng thương lượng. Điều này khá lạ vì nhà phố hiếm khi chịu giảm giá hay đi ngược chiều tăng của vàng.', 'a-tb-nha-ban-bang-vang-2.jpg', NULL, '<h1 class="Title">Nh&agrave; b&aacute;n bằng v&agrave;ng giảm gi&aacute;</h1>\r\n<h2 class="Lead">Nhiều căn nh&agrave; phố ở TP HCM trước đ&acirc;y được rao b&aacute;n bằng v&agrave;ng với gi&aacute; kh&ocirc;ng đổi nay t&iacute;nh đến chuyện sẵn s&agrave;ng thương lượng. Điều n&agrave;y kh&aacute; lạ v&igrave; nh&agrave; phố hiếm khi chịu giảm gi&aacute; hay đi ngược chiều tăng của v&agrave;ng.</h2>\r\n<p class="Normal">Nh&agrave; c&aacute; thể ở mặt tiền đường trong khu trung t&acirc;m S&agrave;i G&ograve;n c&ograve;n gọi l&agrave; nh&agrave; phố c&oacute; lịch sử giao dịch bằng v&agrave;ng từ l&acirc;u đời. Những căn nh&agrave; n&agrave;y được h&eacute;t ở gi&aacute; cao h&agrave;ng ngh&igrave;n lượng l&agrave; chuyện thường. Ưu điểm của sản phẩm n&agrave;y l&agrave; vị tr&iacute; đất độc nhất v&ocirc; nhị kh&ocirc;ng thể thay thế, kết nối hạ tầng x&atilde; hội v&agrave; hạ tầng kỹ thuật tốt.</p>\r\n<p class="Normal">Chủ nh&agrave; phố thường rao b&aacute;n một mức gi&aacute; đ&atilde; được ấn định sẵn, đơn vị quy th&agrave;nh lượng v&agrave;ng, &iacute;t khi xảy ra hiện tượng giảm gi&aacute;. Tuy nhi&ecirc;n, trong năm 2010, sau những biến động mạnh của gi&aacute; v&agrave;ng (tăng trung b&igrave;nh 30%), nh&agrave; phố đ&atilde; kh&ocirc;ng c&ograve;n "k&ecirc;nh kiệu" như trước nữa. C&aacute;c căn nh&agrave; n&agrave;y một mặt được m&ocirc;i giới ch&agrave;o gi&aacute; "cứng", mặt kh&aacute;c chủ nh&agrave; c&ograve;n gửi đi những th&ocirc;ng điệp mềm mỏng hơn chẳng hạn như: "c&oacute; thể giảm gi&aacute; v&agrave;i lượng mỗi m2 hoặc thỏa thuận, thương lượng lại".</p>\r\n<p class="Normal">Đơn cử trường hợp của mảnh đất rộng hơn 1.000 m2 tr&ecirc;n đường Mạc Đỉnh Chi, quận 1, được rao gi&aacute; 25 lượng v&agrave;ng mỗi m2. Thế nhưng gần đ&acirc;y m&ocirc;i giới để lại lời nhắn của chủ nh&agrave;: "Nếu kh&aacute;ch trả gi&aacute; 20 lượng mỗi m2 l&agrave; c&oacute; thể giao dịch".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="a-tb-nha-ban-bang-vang-2.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&agrave; phố b&aacute;n bằng v&agrave;ng c&oacute; xu hướng giảm gi&aacute;. Ảnh:&nbsp;<em>Vũ L&ecirc;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Một số căn nh&agrave; phố tr&ecirc;n đường Hồ T&ugrave;ng Mậu, quận 1, được h&eacute;t gi&aacute; 21 lượng v&agrave;ng mỗi m2. Song m&ocirc;i giới tiết lộ, nếu kh&aacute;ch thực sự muốn mua v&agrave; chồng tiền ngay, chủ nh&agrave; c&oacute; thể giảm gi&aacute; v&agrave;i lượng v&agrave;ng mỗi m2.</p>\r\n<p class="Normal">Hiện tượng n&agrave;y cũng xuất hiện tại quận 3. Chẳng hạn như căn nh&agrave; rộng 400 m2 tr&ecirc;n đường Trần Quang Diệu, quận 3 được rao gi&aacute; khoảng 6,5 lượng v&agrave;ng mỗi m2. Tuy nhi&ecirc;n, m&ocirc;i giới nh&agrave; đất vẫn k&egrave;m theo lời nhắn, c&oacute; thể trả gi&aacute; giảm 1-2 lượng v&agrave;ng mỗi m2.</p>\r\n<p class="Normal">Theo c&aacute;c chuy&ecirc;n gia bất động sản, hiện tượng n&agrave;y cho thấy, tốc độ tăng gi&aacute; của hiện kim đ&atilde; vượt xa tốc độ tăng gi&aacute; của nh&agrave; đất. Điều n&agrave;y khiến cho gi&aacute; v&agrave;ng đ&atilde; vượt l&ecirc;n tr&ecirc;n gi&aacute; trị thực sự của bất động sản. Đ&acirc;y l&agrave; l&yacute; do tại sao thị trường xuất hiện việc nh&agrave; rao b&aacute;n bằng v&agrave;ng được điều chỉnh khung gi&aacute; hợp l&yacute; hơn.</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>VnExpress.net</em>, Tổng gi&aacute;m đốc C&ocirc;ng ty địa ốc &Aacute; Ch&acirc;u (ACBR) Phạm Văn Hải ph&acirc;n t&iacute;ch: "Nh&agrave; phố rao b&aacute;n bằng v&agrave;ng chịu hạ gi&aacute; c&oacute; thể xuất ph&aacute;t từ nhiều l&yacute; do".</p>\r\n<p class="Normal">L&yacute; do thứ nhất, chủ nh&agrave; hoặc m&ocirc;i giới n&oacute;i th&aacute;ch (n&oacute;i qu&aacute; gi&aacute; trị thật). Khi gặp kh&aacute;ch trả gi&aacute; thấp hơn th&igrave; thuận mua vừa b&aacute;n. Hai l&agrave; chủ nh&agrave; nhận thấy quy gi&aacute; v&agrave;ng ra th&agrave;nh tiền đồng đ&atilde; vượt xa gi&aacute; trị thật của căn nh&agrave; n&ecirc;n họ tự điều chỉnh gi&aacute; thấp xuống cho hợp l&yacute;. Trường hợp thứ ba, c&oacute; thể do cần tiền chủ nh&agrave; phải b&aacute;n th&aacute;o.</p>\r\n<p class="Normal">Theo &ocirc;ng Hải, c&aacute;c trường hợp b&aacute;n th&aacute;o kh&ocirc;ng phổ biến. Trường hợp n&oacute;i th&aacute;ch gi&aacute; nh&agrave; bằng v&agrave;ng cũng kh&ocirc;ng c&ograve;n nhiều do gi&aacute; v&agrave;ng li&ecirc;n tục tăng n&oacute;ng trong năm 2010. Bởi lẽ, giới bu&ocirc;n nh&agrave; phố đều thạo tin, nắm chắc gi&aacute; đất mặt tiền ở từng quận c&oacute; gi&aacute; thị trường ra sao. "Do đ&oacute;,&nbsp;<strong><span style="color: #4f4f4f;">trường hợp chủ nh&agrave; phải tự điều chỉnh gi&aacute; b&aacute;n nh&agrave; v&igrave; gi&aacute; v&agrave;ng li&ecirc;n tục tăng mạnh trong năm 2010, vượt xa gi&aacute; trị thật của căn nh&agrave; v&agrave; đất sẽ hợp l&yacute; hơn</span></strong>", &ocirc;ng Hải n&oacute;i.</p>\r\n<p class="Normal">L&atilde;nh đạo ACBR cho biết th&ecirc;m, từ năm 2000 đến năm 2008, do gi&aacute; v&agrave;ng trong nước trải qua nhiều biến động n&ecirc;n tỷ lệ mua b&aacute;n nh&agrave; t&iacute;nh theo đơn vị lượng v&agrave;ng ng&agrave;y c&agrave;ng giảm dần, chiếm 50-50 so với giao dịch bằng tiền đồng. Năm 2009, tỷ lệ giao dịch nh&agrave; đất bằng v&agrave;ng chiếm 40% so với tổng số c&aacute;c thương được ghi nhận tại s&agrave;n. Trong năm 2010, tỷ lệ nh&agrave; phố giao dịch bằng v&agrave;ng chiếm tỷ lệ rất thấp, dưới 10% so với tổng số bất động sản được giao dịch qua s&agrave;n ACBR.</p>\r\n<p class="Normal">Ri&ecirc;ng Chủ tịch HĐQT C&ocirc;ng ty cổ phần Nh&agrave; Việt Nam Trần Văn Th&agrave;nh dự b&aacute;o, gi&aacute; v&agrave;ng biến động th&igrave; địa ốc sẽ được hưởng lợi. Nhiều người sẽ t&igrave;m kiếm nh&agrave; đất để đầu tư trong thời điểm gi&aacute; v&agrave;ng ngất ngưỡng để bảo to&agrave;n vốn. Bởi lẽ, nơi cất tiền tốt nhất hiện nay l&agrave; v&agrave;ng v&agrave; bất động sản. Nhưng mua v&agrave;ng th&igrave; gi&aacute; qu&aacute; cao, lại phải mang đi gửi, giữ v&agrave;ng đối với nhiều người cũng l&agrave; g&aacute;nh nặng. Trong khi đ&oacute;, nh&agrave; đất c&oacute; thể cho thu&ecirc;, kinh doanh v&agrave; bản th&acirc;n n&oacute; cũng tăng gi&aacute; đều đều h&agrave;ng năm.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ L&ecirc;</strong></p>', 10, 3, 558, 0, 1),
(351, 'Xây dựng đô thị mới đầu tiên tại Bảo Lộc', 'Dự án B’lao Charming do Công ty cổ phần Tập đoàn Đầu tư Xây dựng Phát triển Đông Đô (Công ty Đông Đô) làm chủ đầu tư, với số vốn khoảng 400 tỷ đồng, dự kiến sẽ được hoàn thành trong vòng hai năm.', 'Picture01-m.jpg', NULL, '<h1 class="Title">X&acirc;y dựng đ&ocirc; thị mới đầu ti&ecirc;n tại Bảo Lộc</h1>\r\n<h2 class="Lead">Dự &aacute;n B&rsquo;lao Charming do C&ocirc;ng ty cổ phần Tập đo&agrave;n Đầu tư X&acirc;y dựng Ph&aacute;t triển Đ&ocirc;ng Đ&ocirc; (C&ocirc;ng ty Đ&ocirc;ng Đ&ocirc;) l&agrave;m chủ đầu tư, với số vốn khoảng 400 tỷ đồng, dự kiến sẽ được ho&agrave;n th&agrave;nh trong v&ograve;ng hai năm.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Picture01-m.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nằm tại phường Lộc Sơn, TP Bảo Lộc, L&acirc;m Đồng, ngay quốc lộ 20 đi TP HCM, Đ&agrave; Lạt v&agrave; quốc lộ 55 hay c&ograve;n gọi l&agrave; trục đ&ocirc;ng - t&acirc;y li&ecirc;n kết c&aacute;c tỉnh miền Đ&ocirc;ng Nam Bộ v&agrave; T&acirc;y Nguy&ecirc;n, B&rsquo;lao Charming được quy hoạch tổng thể v&agrave; đồng bộ. Tr&ecirc;n diện t&iacute;ch 80 ha, dự &aacute;n bao gồm nh&agrave; li&ecirc;n kế, biệt thự, trường học, cơ sở y tế, trung t&acirc;m thương mại, kh&aacute;ch sạn, khu vui chơi giải tr&iacute;, s&acirc;n tập golf, vườn hoa c&ocirc;ng vi&ecirc;n&hellip;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Picture04-song-lap.jpg" border="1" alt="" width="450" height="264" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Giai đoạn I của dự &aacute;n được triển khai tr&ecirc;n diện t&iacute;ch 16,7 ha, trong đ&oacute;, nh&agrave; li&ecirc;n kế phố l&agrave; 241 căn với diện t&iacute;ch điển h&igrave;nh l&agrave; 132 m2; biệt thự song lập 71 căn c&oacute; diện t&iacute;ch điển h&igrave;nh 264 m2; biệt thự đơn lập l&agrave; 71 căn với diện t&iacute;ch trung b&igrave;nh 330 m2. Hệ thống nh&agrave; trẻ, mẫu gi&aacute;o chiếm hơn 0,5 ha; c&ocirc;ng vi&ecirc;n c&acirc;y xanh gần 2 ha; hệ thống giao th&ocirc;ng l&agrave; 5,13 ha; si&ecirc;u thị v&agrave; c&aacute;c dịch vụ kh&aacute;c chiếm gần 1 ha.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Ban Quản l&yacute; Dự &aacute;n B''Lao Charming</em>)</p>', 10, 3, 454, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(353, 'Khung giá đất chi tiết năm 2011của Hà Nội', 'Giá đất Hà Nội năm 2011 nằm trong mức tối thiểu là 2,34 triệu đồng mỗi m2 thuộc đường 72 qua phường Dương Nội, Hà Đông. Các phố Hàng Ngang, Hàng Đào có giá cao nhất là 81 triệu đồng như năm 2010.', 'dat-1.jpg', NULL, '<h1 class="Title">Khung gi&aacute; đất chi tiết năm 2011của H&agrave; Nội</h1>\r\n<h2 class="Lead">Gi&aacute; đất H&agrave; Nội năm 2011 nằm trong mức tối thiểu l&agrave; 2,34 triệu đồng mỗi m2 thuộc đường 72 qua phường Dương Nội, H&agrave; Đ&ocirc;ng. C&aacute;c phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o c&oacute; gi&aacute; cao nhất l&agrave; 81 triệu đồng như năm 2010.<br /><span style="font-size: 10px; font-weight: normal;">UBND th&agrave;nh phố H&agrave; Nội vừa k&yacute; quyết định ban h&agrave;nh gi&aacute; c&aacute;c loại đất tr&ecirc;n địa b&agrave;n th&agrave;nh phố H&agrave; Nội năm 2011. Theo đ&oacute;, từ ng&agrave;y 1/1/2011, khung gi&aacute; mới sẽ l&agrave; cơ sở t&iacute;nh thuế đối với việc sử dụng đất, thuế thu nhập trong việc giao dịch chuyển nhượng quyền sử dụng đất.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dat-1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="430" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mức gi&aacute; cao nhất trong năm 2011 vẫn giữ nguy&ecirc;n như năm 2010. Ảnh:<em>&nbsp;Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo đ&oacute;, gi&aacute; đất tại c&aacute;c trục đường ch&iacute;nh hướng t&acirc;m v&agrave;o Th&agrave;nh phố được điều chỉnh theo hướng giảm dần từ trung t&acirc;m H&agrave; Nội trở ra. Như vậy, năm 2011 gi&aacute; đất tại một số đường, phố sẽ cao hơn năm 2010, một số đường phố sẽ được giữ nguy&ecirc;n. Mức cao nhất thuộc về c&aacute;c phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o với 81 triệu đồng mỗi m2. Gi&aacute; đất nằm trong mức tối thiểu l&agrave; 2.340.000 đồng mỗi m2 (đường 72 qua phường Dương Nội, quận H&agrave; Đ&ocirc;ng).</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="303" align="center">\r\n<tbody>\r\n<tr>\r\n<td><a href="2011.rar">Click v&agrave;o đ&acirc;y để xem khung gi&aacute; đất năm 2011</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Những khu vực chưa được x&aacute;c định trong bảng gi&aacute;, hoặc c&aacute;c trường hợp mới ph&aacute;t sinh do x&acirc;y dựng đường, phố, khu đ&ocirc; thị mới, UBND th&agrave;nh phố giao Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường c&ugrave;ng c&aacute;c sở, ng&agrave;nh li&ecirc;n quan lập phương &aacute;n gi&aacute;, b&aacute;o c&aacute;o để th&agrave;nh phố quyết định.</p>\r\n<p class="Normal">Đối với đất chưa sử dụng (đất chưa x&aacute;c định mục đ&iacute;ch sử dụng), khi cần c&oacute; gi&aacute; để t&iacute;nh tiền bồi thường th&igrave; căn cứ v&agrave;o gi&aacute; của loại đất liền kề c&oacute; mức gi&aacute; cao nhất. Khi đất chưa sử dụng được đưa v&agrave;o sử dụng th&igrave; căn cứ v&agrave;o gi&aacute; đất c&ugrave;ng loại, c&ugrave;ng mục đ&iacute;ch sử dụng để định mức gi&aacute; cụ thể</p>\r\n<p class="Normal">Th&agrave;nh phố đề ra mục ti&ecirc;u khung gi&aacute; đất năm 2011 phải s&aacute;t thị trường. Trong trường hợp chưa s&aacute;t gi&aacute; thị trường th&igrave; Sở T&agrave;i ch&iacute;nh lập phương &aacute;n tr&igrave;nh th&agrave;nh phố để điều chỉnh trong giới hạn cho ph&eacute;p cao hơn hoặc thấp hơn kh&ocirc;ng qu&aacute; 20% mức gi&aacute; của khu đất c&ugrave;ng vị tr&iacute;. C&aacute;c trường hợp điều chỉnh cao hơn 20% mức gi&aacute; khu đất c&ugrave;ng vị tr&iacute; th&igrave; li&ecirc;n ng&agrave;nh phải lập phương &aacute;n b&aacute;o c&aacute;o UBND Th&agrave;nh phố để xin &yacute; kiến Thường trực Hội đồng nh&acirc;n d&acirc;n Th&agrave;nh phố trước khi quyết định.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 3002, 0, 1),
(356, 'Bắc Ninh đề xuất xây sân golf 36 lỗ', 'UBND tỉnh Bắc Ninh vừa có tờ trình đề nghị Thủ tướng xem xét, phê duyệt bổ sung sân golf Hạp Lĩnh, tỉnh Bắc Ninh vào danh mục Quy hoạch sân golf Việt Nam đến năm 2020 với quy mô 36 lỗ.', 'g1.jpg', NULL, '<h1 class="Title">Bắc Ninh đề xuất x&acirc;y s&acirc;n golf 36 lỗ</h1>\r\n<h2 class="Lead">UBND tỉnh Bắc Ninh vừa c&oacute; tờ tr&igrave;nh đề nghị Thủ tướng xem x&eacute;t, ph&ecirc; duyệt bổ sung s&acirc;n golf Hạp Lĩnh, tỉnh Bắc Ninh v&agrave;o danh mục Quy hoạch s&acirc;n golf Việt Nam đến năm 2020 với quy m&ocirc; 36 lỗ.<br />&gt;<a class="Lead" href="http://vnexpress.net/GL/Kinh-doanh/Bat-Dong-san/2009/05/3BA0EB4B/">N&uacute;p b&oacute;ng s&acirc;n golf để x&acirc;y biệt thự</a>&nbsp;/&nbsp;<a class="Lead" href="http://vnexpress.net/GL/Kinh-doanh/Bat-Dong-san/2010/10/3BA2147C/">R&agrave; so&aacute;t quy hoạch s&acirc;n golf</a></h2>\r\n<p class="Normal">Theo đ&oacute;, khu vực dự kiến quy hoạch s&acirc;n golf Hạp Lĩnh c&oacute; diện t&iacute;ch khoảng 150 ha thuộc địa phận huyện Ti&ecirc;n Du v&agrave; th&agrave;nh phố Bắc Ninh. UBND tỉnh cho rằng, khu vực n&agrave;y c&oacute; đất đai chủ yếu l&agrave; đồi n&uacute;i, k&ecirc;nh, ao hồ v&agrave; một phần đất l&uacute;a một vụ năng suất thấp, hiệu quả kinh tế kh&ocirc;ng cao.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="g1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="400" height="250" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bắc Ninh khẳng định, x&acirc;y s&acirc;n golf ở khu vực Ti&ecirc;n Du sẽ kh&ocirc;ng g&acirc;y tổn hại về m&ocirc;i trường. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Khu vực dự kiến quy hoạch s&acirc;n golf Hạp Lĩnh nằm liền kề với Khu c&ocirc;ng nghiệp, đ&ocirc; thị v&agrave; dịch vụ Nam Sơn - Hạp Lĩnh c&oacute; diện t&iacute;ch 1.000 ha. X&acirc;y s&acirc;n golf sẽ kết nối chuỗi c&ocirc;ng nghiệp - đ&ocirc; thị - dịch vụ, đ&aacute;p ứng v&agrave; cung cấp nhu cầu dịch vụ cho c&aacute;c nh&agrave; đầu tư nước ngo&agrave;i tại Bắc Ninh như: Tập đo&agrave;n Samsung, Tập đo&agrave;n Canon, Tập đo&agrave;n Foxconn, Tập đo&agrave;n Orion.</p>\r\n<p class="Normal">UBND tỉnh Bắc Ninh khẳng định, x&acirc;y s&acirc;n golf ở khu vực Ti&ecirc;n Du sẽ kh&ocirc;ng g&acirc;y tổn hại về m&ocirc;i trường cũng như ảnh hưởng đến c&aacute;c di sản văn h&oacute;a vật thể v&agrave; phi vật thể, danh lam thắng cảnh của tỉnh.</p>\r\n<p class="Normal">Theo quy hoạch đ&atilde; được duyệt, đến năm 2020, cả nước sẽ c&oacute; 89 s&acirc;n golf, tập trung chủ yếu ở v&ugrave;ng Bắc Trung Bộ v&agrave; duy&ecirc;n hải Nam Trung Bộ. Thủ tướng đ&atilde; chỉ đạo c&aacute;c Bộ X&acirc;y dựng, T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường, Khoa học v&agrave; C&ocirc;ng nghệ... x&acirc;y dựng v&agrave; sớm ban h&agrave;nh c&aacute;c quy định, hướng dẫn li&ecirc;n quan đến việc h&igrave;nh th&agrave;nh c&aacute;c dự &aacute;n s&acirc;n golf.</p>\r\n<p class="Normal">Ch&iacute;nh phủ y&ecirc;u cầu c&aacute;c dự &aacute;n s&acirc;n golf kh&ocirc;ng được lấy v&agrave;o đất l&uacute;a, đất m&agrave;u v&agrave; đất quy hoạch x&acirc;y dựng khu c&ocirc;ng nghiệp; khu đ&ocirc; thị; đất rừng (đặc biệt l&agrave; rừng ph&ograve;ng hộ, rừng đặc dụng).</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 528, 0, 1),
(357, 'Làm nhà ở bằng container', 'Công ty cổ phần Hưng Đạo vừa tung ra thị trường mô hình nhà bằng container với giá từ 70 triệu đến 250 triệu đồng. Thời gian sử dụng căn nhà loại này là 10-20 năm.', 'House_hanoi.jpg', NULL, '<h1 class="Title">L&agrave;m nh&agrave; ở bằng container</h1>\r\n<h2 class="Lead">C&ocirc;ng ty cổ phần Hưng Đạo vừa tung ra thị trường m&ocirc; h&igrave;nh nh&agrave; bằng container với gi&aacute; từ 70 triệu đến 250 triệu đồng. Thời gian sử dụng căn nh&agrave; loại n&agrave;y l&agrave; 10-20 năm.</h2>\r\n<p class="Normal">&Ocirc;ng Nguyễn Tiến Cường, gi&aacute;m đốc C&ocirc;ng ty cổ phần Hưng Đạo chi nh&aacute;nh H&agrave; Nội cho hay, một căn hộ x&acirc;y dựng bằng container sẽ bao gồm ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, bếp t&ugrave;y theo y&ecirc;u cầu của người sử dụng. Diện t&iacute;ch của mỗi căn c&oacute; thể dao động từ 30 m2 đến 60 m2 v&agrave; c&oacute; gi&aacute; từ 70 triệu đồng đến 250 triệu đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="House_hanoi.jpg" border="1" alt="M&ocirc; h&igrave;nh nh&agrave;." width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&ocirc; h&igrave;nh nh&agrave; ở bằng container. Ảnh:&nbsp;<em>HD</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo &ocirc;ng Cường, nh&agrave; bằng container c&oacute; d&aacute;ng vẻ chắc chắn, chịu được ngoại lực lớn, đồng thời thuận tiện trong việc lắp r&aacute;p v&agrave; th&aacute;o rời. Th&ecirc;m v&agrave;o đ&oacute;, m&ocirc; h&igrave;nh nh&agrave; n&agrave;y được lắp r&aacute;p trong một thời gian rất ngắn, chỉ trong v&ograve;ng một tuần l&agrave; c&oacute; thể ho&agrave;n thiện. Thời gian sử dụng l&agrave; 10-20 năm.</p>\r\n<p class="Normal">"Loại nh&agrave; n&agrave;y ph&ugrave; hợp với những người c&oacute; thu nhập thấp, c&oacute; đất sẵn nhưng kh&ocirc;ng c&oacute; tiền để x&acirc;y nh&agrave;. Trong trường hợp muốn di rời, muốn chuyển sang nh&agrave; mới, kh&aacute;ch h&agrave;ng c&oacute; thể b&aacute;n lại container cho ch&iacute;nh c&ocirc;ng ty", &ocirc;ng Cường cho hay.</p>\r\n<p class="Normal">&Ocirc;ng tiết lộ, c&ocirc;ng ty đ&atilde; c&oacute; nhiều đơn đặt h&agrave;ng nhưng nhiều trường hợp đường v&agrave;o khu đất cần x&acirc;y container qu&aacute; nhỏ n&ecirc;n xe tải kh&ocirc;ng thể v&agrave;o được. "Th&ocirc;ng thường, ng&otilde; phải rộng 3m xe tải mới c&oacute; thể v&agrave;o được", &ocirc;ng Cường n&oacute;i.</p>\r\n<p class="Normal">Trước đ&oacute;, C&ocirc;ng ty Hưng Đạo Container cũng đ&atilde; tung ra thị trường m&ocirc; h&igrave;nh văn ph&ograve;ng l&agrave;m bằng container. Theo &ocirc;ng Cường, loại nh&agrave; ở bằng container c&ograve;n mới mẻ đối với người Việt Nam nhưng rất quen thuộc đối với nhiều nước tr&ecirc;n thế giới. &Ocirc;ng Cường cho hay, nh&agrave; ở bằng container đ&atilde; được b&aacute;n cho kh&aacute;ch h&agrave;ng ở Bắc Ninh v&agrave; Australia.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 1823, 0, 1),
(358, 'Một năm diễn biến trái chiều của bất động sản Nam - Bắc', 'Địa ốc Hà Nội năm 2010 nóng lạnh cục bộ ở một số khu vực và sôi động nhất vào tháng 5 nhờ cơn sốt ăn theo quy hoạch. Ngược lại, bất động sản TP HCM trượt dài trong trầm lắng.', 'a-tb-mot-nam-lan-dan-bds-2[1].jpg', NULL, '<h1 class="Title">Một năm diễn biến tr&aacute;i chiều của bất động sản Nam - Bắc</h1>\r\n<h2 class="Lead">Địa ốc H&agrave; Nội năm 2010 n&oacute;ng lạnh cục bộ ở một số khu vực v&agrave; s&ocirc;i động nhất v&agrave;o th&aacute;ng 5 nhờ cơn sốt ăn theo quy hoạch. Ngược lại, bất động sản TP HCM trượt d&agrave;i trong trầm lắng.</h2>\r\n<p class="Normal">Th&ocirc;ng tin c&ocirc;ng bố về 5 đ&ocirc; thị vệ tinh của H&agrave; Nội v&agrave; trục Thăng Long, bắt đầu từ đường Ho&agrave;ng Quốc Việt tới ch&acirc;n n&uacute;i Ba V&igrave; thuộc địa phận H&agrave; T&acirc;y cũ, khiến đất khu vực n&agrave;y tăng gi&aacute; tới 2-3 lần.</p>\r\n<p class="Normal">Sức n&oacute;ng từ những v&ugrave;ng ăn theo quy hoạch c&ograve;n lan tỏa sang nhiều khu vực kh&aacute;c khiến đất ở T&acirc;y Hồ, Gia L&acirc;m, Đ&ocirc;ng Anh... cũng tăng gi&aacute; theo. Đặc biệt, đất nền dọc đường L&ecirc; Trọng Tấn, Kim Chung Di Trạch rồi Ba V&igrave;, Tiến Xu&acirc;n, Đ&ocirc;ng Xu&acirc;n, Y&ecirc;n B&igrave;nh... thuộc ph&iacute;a T&acirc;y th&agrave;nh phố c&oacute; mức tăng gi&aacute; ch&oacute;ng mặt.</p>\r\n<p class="Normal"><a class="Normal" href="http://vnexpress.net/GL/Kinh-doanh/Bat-Dong-san/2010/05/3BA1BCBE/">Cơn sốt&nbsp;</a>ăn theo quy hoạch đ&atilde; l&ocirc;i cuốn h&agrave;ng loạt nh&agrave; đầu tư, cũng như d&acirc;n c&ocirc;ng sở nh&aacute;o nh&agrave;o đi bu&ocirc;n đất. Ch&iacute;nh phủ, Bộ X&acirc;y dựng, UBND TP đ&atilde; phải v&agrave;o cuộc, li&ecirc;n tiếp đưa ra h&agrave;ng loạt kế s&aacute;ch nhằm hạ nhiệt thị trường. Chỉ khoảng hơn một th&aacute;ng sau, đất đai ở nhiều khu vực, nhất l&agrave; Ba V&igrave; đ&atilde; hạ nhiệt. Nhiều nh&agrave; đầu tư nhanh tay th&igrave; hốt bạc tỷ, số kh&aacute;c vỡ mộng ngay tại ch&acirc;n n&uacute;i Ba V&igrave;.</p>\r\n<p class="Normal">Sau cơn sốt, thị trường địa ốc H&agrave; Nội lại rơi v&agrave;o trạng th&aacute;i trầm lắng. Th&ecirc;m v&agrave;o đ&oacute; Nghị định 71 c&oacute; hiệu lực từ 8/8 đ&atilde; t&aacute;c động mạnh l&ecirc;n thị trường. Theo quy định mới, c&aacute;c b&ecirc;n chỉ được k&yacute; hợp đồng ủy quyền c&ocirc;ng chứng quyền sử dụng, b&aacute;n, cho thu&ecirc; khi nh&agrave; đ&atilde; x&acirc;y xong v&agrave; mỗi người chỉ được sở hữu một nh&agrave; trong mỗi dự &aacute;n.</p>\r\n<p class="Normal">Giới chuy&ecirc;n gia nhận định, nghị định n&agrave;y l&agrave;m thị trường minh bạch hơn nhưng đồng thời cũng siết chặt cửa ''lướt s&oacute;ng'' đối với nh&agrave; đầu tư. Th&ecirc;m v&agrave;o đ&oacute;, ng&acirc;n h&agrave;ng chưa mở rộng hầu bao n&ecirc;n thị trường bất động sản vẫn trong t&igrave;nh cảnh kh&aacute;t vốn.</p>\r\n<p class="Normal">Những th&aacute;ng cuối năm, địa ốc H&agrave; Nội lại tiếp tục tăng gi&aacute; cục bộ ở một số khu vực quanh c&aacute;c trục đường mới th&ocirc;ng. Ti&ecirc;u biểu l&agrave; khu vực quanh đường L&ecirc; Văn Lương, đại lộ Thăng Long, gi&aacute; tăng 10-25%, nhưng lượng giao dịch th&agrave;nh c&ocirc;ng kh&ocirc;ng nhiều. Cuối năm, thị trường địa ốc được chứng kiến nhiều chủ đầu tư ồ ạt&nbsp;<a class="Normal" href="http://vnexpress.net/GL/Kinh-doanh/Bat-Dong-san/2010/12/3BA23FE1/">ra h&agrave;ng</a>&nbsp;h&acirc;m n&oacute;ng thị trường bất động sản. Tuy nhi&ecirc;n, động th&aacute;i n&agrave;y kh&ocirc;ng tạo ra s&oacute;ng lớn cho thị trường.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="xem-hang-1.jpg" border="1" alt="" width="480" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Việc chủ đầu tư ồ ạt tung h&agrave;ng cuối năm k&egrave;m c&aacute;c h&igrave;nh thức ưu đ&atilde;i c&oacute; thể coi l&agrave; động th&aacute;i h&acirc;m n&oacute;ng thị trường. Ảnh:&nbsp;<em>Ho&agrave;ng Lan</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong>Thị trường bất động sản TP HCM năm 2010 trượt d&agrave;i trong sự trầm lắng, kh&ocirc;ng c&oacute; nhiều giao dịch th&agrave;nh c&ocirc;ng</strong>. Trong qu&yacute; I, ngoại trừ việc gi&aacute; thu&ecirc; mặt bằng b&aacute;n lẻ sốt, c&aacute;c ph&acirc;n kh&uacute;c c&ograve;n lại của bất động sản TP HCM giẫm ch&acirc;n tại chỗ. Căn hộ v&agrave; đất nền đều đứng gi&aacute;, giao dịch th&agrave;nh c&ocirc;ng kh&ocirc;ng nhiều, c&ograve;n văn ph&ograve;ng cho thu&ecirc; đứng tiếp tục giảm gi&aacute; so với cuối năm 2009.</p>\r\n<p class="Normal">Thậm ch&iacute;, qu&yacute; I c&ograve;n l&agrave; thời điểm bất động sản TP HCM l&eacute;p vế so với người l&aacute;ng giềng Đồng Nai - nơi đang chạy đua hạ tầng để triển khai h&agrave;ng loạt c&aacute;c dự &aacute;n khu đ&ocirc; thị lớn.</p>\r\n<p class="Normal">Bước sang qu&yacute; II t&igrave;nh h&igrave;nh cũng chẳng kh&aacute; hơn l&agrave; bao. Nhiều doanh nghiệp ở S&agrave;i G&ograve;n thậm ch&iacute; c&ograve;n ch&agrave;o b&aacute;n bất động sản v&ugrave;ng ven ở B&igrave;nh Dương, Đồng Nai trong khi thị trường nh&agrave; đất TP HCM rơi v&agrave;o t&igrave;nh trạng chờ. C&aacute;c động th&aacute;i giảm gi&aacute; v&agrave; ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng mua nh&agrave; chưa đủ sức l&agrave;m tăng nhiệt cho thị trường.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, kh&ocirc;ng kh&iacute; c&aacute;c "chợ" bất động sản c&oacute; ch&uacute;t thay đổi trong qu&yacute; II khi C&ocirc;ng ty Đất L&agrave;nh x&acirc;y căn hộ mẫu si&ecirc;u nhỏ 20-40 m2, gi&aacute; khoảng 350 triệu đồng một căn v&agrave; mời kh&aacute;ch h&agrave;ng đến xem để lấy &yacute; kiến. Giới kinh doanh bất động sản kỳ vọng căn hộ si&ecirc;u nhỏ sẽ đ&aacute;nh thức thị trường nh&agrave; đất tại TP n&agrave;y.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="a-tb-mot-nam-lan-dan-bds-2[1].jpg" border="1" alt="Ảnh:" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trục Đại lộ Đ&ocirc;ng T&acirc;y trở th&agrave;nh t&acirc;m điểm của nhiều dự &aacute;n ăn theo hạ tầng. Ảnh:&nbsp;<em>K.C.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhưng mọi kh&oacute; khăn đổ ập v&agrave;o qu&yacute; III, thị trường căn hộ v&agrave; đất nền ch&igrave;m trong y&ecirc;n ắng ngoại trừ một số dự &aacute;n ăn theo trục Đại lộ Đ&ocirc;ng T&acirc;y được kh&aacute;ch h&agrave;ng quan t&acirc;m nhiều hơn. Thời điểm n&agrave;y h&agrave;ng loạt c&aacute;c dự &aacute;n bất động sản gi&aacute; trung b&igrave;nh, diện t&iacute;ch vừa phải thi nhau tr&igrave;nh l&agrave;ng tại c&aacute;c quận 9, 12, Thủ Đức, T&acirc;n Ph&uacute;, B&igrave;nh T&acirc;n, huyện Nh&agrave; B&egrave;, B&igrave;nh Ch&aacute;nh. Mặc d&ugrave; nhu cầu căn hộ gi&aacute; trung b&igrave;nh được đ&aacute;nh gi&aacute; rất lớn nhưng khả năng chi trả của nh&oacute;m kh&aacute;ch h&agrave;ng n&agrave;y (c&ocirc;ng chức, vợ chồng trẻ, người độc th&acirc;n) vẫn c&ograve;n hạn chế.</p>\r\n<p class="Normal">Thị trường nh&agrave; đất qu&yacute; IV c&agrave;ng trở n&ecirc;n ngột ngạt hơn khi tại TP HCM nổ ra h&agrave;ng loạt vụ tranh chấp trong nh&agrave; chung cư. Trong hai th&aacute;ng 10-11, tại S&agrave;i G&ograve;n đ&atilde; diễn ra &iacute;t nhất 7 vụ lộn xộn dẫn đến xung đột v&agrave; tranh chấp trong nh&agrave; chung cư. Thị trường kh&ocirc;ng những phải nối tiếp kh&ocirc;ng kh&iacute; trầm lặng m&agrave; c&ograve;n chịu sức &eacute;p khi l&atilde;i suất d&acirc;ng cao v&agrave;o th&aacute;ng 12.</p>\r\n<p class="Normal">Điểm s&aacute;ng duy nhất xuy&ecirc;n suốt thị trường địa ốc TP HCM năm 2010 l&agrave; nguồn cung tăng đều. Theo thống k&ecirc; của c&aacute;c đơn vị tư vấn, quản l&yacute;, trong qu&yacute; I, nguồn cung l&agrave; 3.200 căn hộ, qu&yacute; II tăng th&ecirc;m 2.900 căn hộ. Đặc biệt trong qu&yacute; III, thị trường đ&oacute;n nhận một nguồn cung kh&aacute; dồi d&agrave;o với khoảng 7.200 căn hộ từ 20 dự &aacute;n mới.</p>\r\n<p class="Normal">Hơn 75% tổng nguồn cung l&agrave; căn hộ c&oacute; gi&aacute; trị 780-810 USD mỗi m2. C&aacute;c khu vực quận 2, 7, T&acirc;n Ph&uacute;, B&igrave;nh T&acirc;n c&oacute; số lượng căn hộ ch&agrave;o b&aacute;n lớn nhất, chiếm hơn 45% tổng nguồn cung. Ph&acirc;n kh&uacute;c căn hộ trung b&igrave;nh đang chiếm thế thượng phong tr&ecirc;n thị trường v&agrave; xu hướng n&agrave;y được đ&aacute;nh gi&aacute; sẽ c&agrave;ng đậm n&eacute;t hơn trong v&agrave;i năm tới, đặc biệt ở c&aacute;c khu vực ven trung t&acirc;m TP HCM.</p>\r\n<p class="Normal">Điều đ&aacute;ng lưu &yacute; l&agrave; năm 2010 chứng kiến sự sự tăng nhanh của gi&aacute; v&agrave;ng đ&atilde; ảnh hưởng kh&ocirc;ng nhỏ đến giao dịch bất động sản. Theo c&aacute;c chuy&ecirc;n gia kinh tế, sự thiếu ổn định của tỷ gi&aacute; hối đo&aacute;i khiến c&aacute;c nh&agrave; đầu cơ v&agrave; đầu tư c&aacute; nh&acirc;n e ngại. Th&ecirc;m v&agrave;o đ&oacute;, t&iacute;n dụng hạn chế v&agrave; chi ph&iacute; vay cao hơn đang trở th&agrave;nh mối lo lớn v&igrave; cả chủ đầu tư v&agrave; kh&aacute;ch h&agrave;ng. Đ&acirc;y cũng l&agrave; c&aacute;c nguy cơ tiềm ẩn đe dọa thị trường bất động sản hiện nay.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan - Vũ L&ecirc;</strong></p>', 10, 3, 807, 0, 1),
(359, 'Văn phòng đặt ở chung cư không được cấp phép kinh doanh', 'Trong khi chờ rà soát lại các chung cư làm văn phòng, Bộ Xây dựng vừa đề nghị thành phố Hà Nội không cấp mới giấy phép đăng ký kinh doanh cho các công ty đặt trụ sở tại khu nhà chung cư.', 'nc2.jpg', NULL, '<h1 class="Title">Văn ph&ograve;ng đặt ở chung cư kh&ocirc;ng được cấp ph&eacute;p kinh doanh</h1>\r\n<h2 class="Lead">Trong khi chờ r&agrave; so&aacute;t lại c&aacute;c chung cư l&agrave;m văn ph&ograve;ng, Bộ X&acirc;y dựng vừa đề nghị th&agrave;nh phố H&agrave; Nội kh&ocirc;ng cấp mới giấy ph&eacute;p đăng k&yacute; kinh doanh cho c&aacute;c c&ocirc;ng ty đặt trụ sở tại khu nh&agrave; chung cư.<br /><span style="font-size: 10px; font-weight: normal;">Bộ X&acirc;y dựng vừa c&oacute; &yacute; kiến về vấn đề quản l&yacute;, sử dụng tại khu t&aacute;i định cư Nam Trung Y&ecirc;n, v&agrave; khu đ&ocirc; thị mới Trung H&ograve;a Nh&acirc;n Ch&iacute;nh (H&agrave; Nội). Theo đ&oacute;, khu t&aacute;i định cư Nam Trung Y&ecirc;n được đưa v&agrave;o sử dụng từ năm 2006 nhưng kh&ocirc;ng c&oacute; tầng hầm để xe. V&igrave; vậy, nhiều hộ d&acirc;n sống bằng nghề kinh doanh nhỏ lẻ, b&aacute;n h&agrave;ng chợ đ&atilde; phải bố tr&iacute; tầng một l&agrave;m khu vực để xe.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nc2.jpg" border="1" alt="Qu&aacute;n nước vỉa h&egrave; b&agrave;y b&aacute;n khắp nơi. Ảnh: Ho&agrave;ng Lan" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Qu&aacute;n nước vỉa h&egrave; b&agrave;y b&aacute;n ngay ở khu đ&ocirc; thị mới. Ảnh:&nbsp;<em>Ho&agrave;ng Lan</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Bộ X&acirc;y dựng nhấn mạnh, hiện ở Nam Trung Y&ecirc;n c&oacute; một số căn hộ được sử dụng l&agrave;m nơi b&aacute;n h&agrave;ng, kinh doanh, một số trường hợp kh&aacute;c sử dụng l&agrave;m văn ph&ograve;ng. Phần diện t&iacute;ch sử dụng chung b&agrave;y b&aacute;n h&agrave;ng qu&aacute;n lộn xộn, b&atilde;i đỗ xe &ocirc;t&ocirc; tự ph&aacute;t l&agrave;m ảnh hưởng đến sinh hoạt cộng đồng d&acirc;n cư v&agrave; c&ocirc;ng t&aacute;c ph&ograve;ng ch&aacute;y chữa ch&aacute;y, g&acirc;y &ocirc; nhiễm m&ocirc;i trường.</p>\r\n<p class="Normal">Về quản l&yacute; sử dụng v&agrave; c&ocirc;ng t&aacute;c ph&ograve;ng ch&aacute;y chữa ch&aacute;y tại nh&agrave; chung cư khu đ&ocirc; thị mới Trung H&ograve;a Nh&acirc;n Ch&iacute;nh cũng c&ograve;n nhiều điểm bất cập. Hiện tại nh&agrave; chung cư 34T Trung H&ograve;a Nh&acirc;n Ch&iacute;nh c&oacute; 50 căn hộ tr&ecirc;n tồng số 365 căn được sử dụng l&agrave;m cơ sở kinh doanh. Trước đ&oacute; cuối th&aacute;ng 10/2010, khu 24 tầng n&agrave;y c&ograve;n xảy ra vụ ch&aacute;y do chập điện th&ocirc;ng gi&oacute;.</p>\r\n<p class="Normal">Bộ X&acirc;y dựng y&ecirc;u cầu ban quản l&yacute; c&aacute;c t&ograve;a nh&agrave; cần phối hợp chặt chẽ với UBND phường Trung H&ograve;a, quận Cầu Giấy nhắc nhở v&agrave; v&agrave; c&oacute; h&igrave;nh thức xử l&yacute; c&aacute;c h&agrave;nh vi vi phạm tr&ecirc;n.</p>\r\n<p class="Normal">Bộ X&acirc;y dựng đề nghị, trong khi chờ&nbsp;r&agrave; so&aacute;t, thống k&ecirc; c&aacute;c căn hộ nh&agrave; chung cư l&agrave;m cơ sở sản xuất, kinh doanh, văn ph&ograve;ng tr&ecirc;n địa b&agrave;n, UBND th&agrave;nh phố H&agrave; Nội chỉ đạo c&aacute;c cơ quan chức năng kh&ocirc;ng thực hiện cấp mới giấy ph&eacute;p đăng k&yacute; kinh doanh cho c&aacute;c c&ocirc;ng ty đặt trụ sở tại căn hộ nh&agrave; chung cư. Đồng thời, H&agrave; Nội cần khẩn trương nghi&ecirc;n cứu, ban h&agrave;nh c&aacute;c quy định quản l&yacute;, sử dụng v&agrave; ph&ograve;ng ch&aacute;y chữa ch&aacute;y nh&agrave; chung cư t&aacute;i định cư trong đ&oacute; đặc biệt quan t&acirc;m đến nhu cầu c&ocirc;ng việc của c&aacute;c hộ d&acirc;n t&aacute;i định cư.</p>\r\n<p class="Normal" style="text-align: right;"><strong>B&aacute;ch Hợp</strong></p>', 10, 3, 2394, 0, 1),
(360, 'Quỹ Tiết kiệm nhà ở khó khả thi', 'Mặc dù đánh giá cao mục tiêu tốt đẹp trong việc giải quyết nhu cầu bức xúc về nhà ở, song nhiều chuyên gia lo ngại Quỹ tiết kiệm nhà ở sẽ khó đi vào thực tế vì dễ nảy sinh tiêu cực.', 'nha-o-1.jpg', NULL, '<h1 class="Title">Quỹ Tiết kiệm nh&agrave; ở kh&oacute; khả thi</h1>\r\n<h2 class="Lead">Mặc d&ugrave; đ&aacute;nh gi&aacute; cao mục ti&ecirc;u tốt đẹp trong việc giải quyết nhu cầu bức x&uacute;c về nh&agrave; ở, song nhiều chuy&ecirc;n gia lo ngại Quỹ tiết kiệm nh&agrave; ở sẽ kh&oacute; đi v&agrave;o thực tế v&igrave; dễ nảy sinh ti&ecirc;u cực.<br /><span style="font-size: 10px; font-weight: normal;">Mới đ&acirc;y, Bộ X&acirc;y dựng đ&atilde; đề xuất Ch&iacute;nh phủ th&agrave;nh lập Quỹ tiết kiệm nh&agrave; ở để giải quyết kh&oacute; khăn về nh&agrave; ở cho người d&acirc;n trong t&igrave;nh cảnh gi&aacute; bất động sản đang leo thang ch&oacute;ng mặt. Cơ quan n&agrave;y dự kiến, quỹ được x&acirc;y dựng giống như h&igrave;nh thức bảo hiểm x&atilde; hội với mức đ&oacute;ng g&oacute;p dự kiến khoảng 1 - 2% tiền lương h&agrave;ng th&aacute;ng của người lao động.</span></h2>\r\n<p class="Normal">Quỹ sẽ hỗ trợ những người thu nhập thấp vay mua nh&agrave; với l&atilde;i suất ưu đ&atilde;i. Ri&ecirc;ng những người kh&ocirc;ng c&oacute; nhu cầu mua nh&agrave;, khi về hưu sẽ được r&uacute;t to&agrave;n bộ số tiền đ&oacute;ng g&oacute;p cộng với l&atilde;i suất mang t&iacute;nh hỗ trợ trượt gi&aacute;.</p>\r\n<p class="Normal">&Ocirc;ng Đặng H&ugrave;ng V&otilde;, Cố vấn cấp cao của Bộ T&agrave;i nguy&ecirc;n M&ocirc;i trường cho rằng, quỹ ph&aacute;t triển nh&agrave; ở kh&ocirc;ng phải l&agrave; một h&igrave;nh thức mới tr&ecirc;n thế giới. Trung Quốc, Singapore hay một số nước Bắc &Acirc;u đ&atilde; th&agrave;nh lập quỹ nh&agrave; ở dưới nhiều t&ecirc;n kh&aacute;c nhau.</p>\r\n<p class="Normal">Một số nước Bắc &Acirc;u giải quyết th&ocirc;ng qua h&igrave;nh thức hợp t&aacute;c x&atilde; nh&agrave; ở. Họ lập một hội những người c&oacute; nhu cầu mua nh&agrave; ở, c&oacute; ban kiểm so&aacute;t v&agrave; tự quản l&yacute; ph&acirc;n phối. Mỗi khi x&eacute;t đối tượng được mua nh&agrave;, họ bỏ phiếu c&ocirc;ng khai.</p>\r\n<p class="Normal">"Gi&aacute; đất đang leo thang, nhiều người d&acirc;n c&oacute; mức thu nhập trung b&igrave;nh phải từ bỏ giấc mơ sở hữu một ng&ocirc;i nh&agrave; th&igrave; Quỹ n&agrave;y lại mở ra một cơ hội gi&uacute;p cho người d&acirc;n tiếp cận được với nh&agrave; ở th&agrave;nh thị", &ocirc;ng V&otilde; n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nha-o-1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiều chuy&ecirc;n gia lo ngại quỹ tiết kiệm nh&agrave; ở dễ nảy sinh ti&ecirc;u cực. Ảnh:<em>&nbsp;Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tuy nhi&ecirc;n, nhiều người lại b&agrave;y tỏ mối lo ngại xung quanh đề xuất của Bộ X&acirc;y dựng. &Ocirc;ng Nguyễn Văn Đực, Ph&oacute; tổng gi&aacute;m đốc C&ocirc;ng ty địa ốc Đất L&agrave;nh cho rằng, quyết định th&agrave;nh lập quỹ ph&aacute;t triển nh&agrave; ở với tỷ lệ đ&oacute;ng g&oacute;p 1-2% mức lương để giải quyết kh&oacute; khăn về nh&agrave; ở l&agrave; kh&ocirc;ng tưởng.</p>\r\n<p class="Normal">&Ocirc;ng Đực ph&acirc;n t&iacute;ch, lương c&ocirc;ng nh&acirc;n vi&ecirc;n t&iacute;nh trung b&igrave;nh khoảng 5 triệu đồng mỗi th&aacute;ng. Tr&iacute;ch 1-2% chỉ tương đương với khoảng 50.000-100.000 đồng mỗi th&aacute;ng, vậy cả năm chỉ g&oacute;p được khoảng 600.000-1,2 triệu đồng. 10 năm được khoảng 6-12 triệu. "Số tiền qu&aacute; nhỏ như ''muối bỏ biển'' để c&oacute; thể sở hữu một căn nh&agrave;", &ocirc;ng Đực lo ngại.</p>\r\n<p class="Normal">Để giải được b&agrave;i to&aacute;n n&agrave;y, vị gi&aacute;m đốc cho rằng cần phải tr&iacute;ch 20-30% tổng thu nhập hằng th&aacute;ng g&oacute;p quỹ trong &iacute;t nhất 10 năm mới c&oacute; thể mua được nh&agrave;. Cũng theo &ocirc;ng Đực, xử l&yacute; thỏa đ&aacute;ng quyền lợi của tất cả mọi người d&ugrave; kh&ocirc;ng c&oacute; nhu cầu mua nh&agrave; nhưng vẫn phải đ&oacute;ng g&oacute;p l&agrave; điều rất kh&oacute;. Do đ&oacute;, vị gi&aacute;m đốc kiến nghị chỉ n&ecirc;n thu từ những người c&oacute; nhu cầu mua nh&agrave; thực sự v&agrave; n&ecirc;n để cơ chế tự nguyện thay v&igrave; h&igrave;nh thức &eacute;p buộc.</p>\r\n<p class="Normal">"C&oacute; người ăn c&ograve;n kh&ocirc;ng đủ v&agrave; họ cần ưu ti&ecirc;n giải quyết nhu cầu sinh sống hằng ng&agrave;y. Họ sẵn s&agrave;ng chấp nhận phận đi thu&ecirc; v&igrave; c&ograve;n phải vật lộn với cuộc sống mưu sinh", &ocirc;ng Đực n&oacute;i.</p>\r\n<p class="Normal">Đồng t&igrave;nh với quan điểm tr&ecirc;n, chuy&ecirc;n gia kinh tế Phạm Chi Lan cho rằng,&nbsp;<strong><span style="color: #3f3f3f;">nếu y&ecirc;u cầu tất cả người lao động Việt Nam phải đ&oacute;ng g&oacute;p v&agrave;o Quỹ nh&agrave; ở sẽ chẳng kh&aacute;c n&agrave;o đ&aacute;nh một loại thuế mới l&ecirc;n người d&acirc;n.</span></strong></p>\r\n<p class="Normal">Nhiều độc giả gửi thư cho&nbsp;<em>VnExpress.net</em>&nbsp;cũng b&agrave;y tỏ sự lo ngại về đề xuất của Bộ X&acirc;y dựng. Số đ&ocirc;ng cho rằng, lương của người lao động Việt Nam chưa cao lại phải đ&oacute;ng nhiều khoản l&agrave; điều bất cập hiện nay. Mộc độc giả than, hiện bảo hiểm x&atilde; hội tăng từ 5% l&ecirc;n 6,5%, bảo hiểm y tế từ 1% l&ecirc;n 1,5%, rồi bảo hiểm thất nghiệp 1%. "Nay &eacute;p buộc trừ 1-2% tiền lương v&agrave;o quỹ tiết kiệm nh&agrave; ở l&agrave; kh&ocirc;ng hợp l&yacute;. Hiện tại t&ocirc;i c&oacute; 2 căn nh&agrave;, t&ocirc;i đ&acirc;u cần mua th&ecirc;m m&agrave; phải đ&oacute;ng quỹ mua nh&agrave;", một độc giả phản &aacute;nh.</p>\r\n<p class="Normal">"Chỉ n&ecirc;n y&ecirc;u cầu những người c&oacute; nhu cầu mua nh&agrave; mới phải đ&oacute;ng tiền v&agrave;o quỹ. Th&ecirc;m v&agrave;o đ&oacute;, ng&acirc;n s&aacute;ch phải tr&iacute;ch một phần đ&oacute;ng g&oacute;p để tạo hạt nh&acirc;n cho quỹ bởi tr&aacute;ch nhiệm kh&ocirc;ng thể đổ cho 9 triệu người lao động", &ocirc;ng Đặng H&ugrave;ng V&otilde; đề xuất.</p>\r\n<p class="Normal">B&ecirc;n cạnh đ&oacute;, nhiều chuy&ecirc;n gia b&agrave;y tỏ lo ngại về nguy cơ nảy sinh ti&ecirc;u cực trong qu&aacute; tr&igrave;nh vận h&agrave;nh. Chuy&ecirc;n gia kinh tế Phạm Chi Lan đ&aacute;nh gi&aacute; cao &yacute; tưởng của Bộ X&acirc;y dựng song b&agrave; lo ngại về t&iacute;nh khả thi. T&iacute;nh minh bạch trong quản trị của Việt Nam chưa cao, vấn đề tham nhũng rất kh&oacute; tr&aacute;nh khỏi. Theo b&agrave; Lan, nếu quản l&yacute; lỏng lẻo sẽ rất dễ nảy sinh ti&ecirc;u cực.</p>\r\n<p class="Normal">Tại buổi đối thoại về&nbsp;ph&ograve;ng chống tham nhũng&nbsp;trong lĩnh vực quản l&yacute; v&agrave; sử dụng đất đai được tổ chức v&agrave;o cuối th&aacute;ng 11 vừa qua, Đại sứ qu&aacute;n Thụy Điển đ&atilde; đưa ra con số 86% c&aacute;c hộ gia đ&igrave;nh ở Việt Nam nhận biết c&oacute; tham nhũng trong quản l&yacute; sử dụng đất đai. "Kh&ocirc;ng phải ai cũng tin tưởng v&agrave;o t&iacute;nh hiệu quả của quỹ. Để đảm bảo quỹ quản l&yacute; v&agrave; vận h&agrave;nh đ&uacute;ng mục đ&iacute;ch, tr&aacute;nh ti&ecirc;u cực tham nhũng th&igrave; cần phải c&oacute; cơ chế gi&aacute;m s&aacute;t chặt chẽ", b&agrave; Lan n&oacute;i.</p>\r\n<p class="Normal">Tại một cuộc họp b&aacute;o gần đ&acirc;y, thứ trưởng Bộ X&acirc;y dựng Nguyễn Trần Nam cho rằng, nếu những người c&oacute; nh&agrave; kh&ocirc;ng đ&oacute;ng g&oacute;p th&igrave; Quỹ tiết kiệm nh&agrave; ở sẽ rất kh&oacute; h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t huy đ&uacute;ng t&aacute;c dụng. Bởi những người chưa c&oacute; nh&agrave; hầu hết l&agrave; người ngh&egrave;o, kh&ocirc;ng đủ tiền để mua nh&agrave;. "Nay những người n&agrave;y lại phải g&oacute;p v&agrave;o quỹ tiết kiệm nh&agrave; ở th&igrave; số tiền sẽ rất nhỏ v&agrave; cơ hội mua được nh&agrave; l&agrave; rất kh&oacute;. Thử hỏi, những người thu nhập thấp, chưa c&oacute; nh&agrave; m&agrave; tự g&oacute;p với nhau th&igrave; đến bao giờ mới c&oacute; đủ tiền để mua nh&agrave;?", &ocirc;ng Nam lo ngại.</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>Vnexpress,</em>&nbsp;một c&aacute;n bộ thuộc Bộ X&acirc;y dựng cho biết, Bộ vừa cử người đi khảo s&aacute;t tại một số nước c&oacute; quỹ nh&agrave; ở. "Bộ sẽ học hỏi kinh nghiệm của c&aacute;c nước để ứng dụng v&agrave;o Việt Nam v&agrave; chỉ đ&uacute;c kết những kinh nghiệm ph&ugrave; hợp nhất", vị c&aacute;n bộ chia sẻ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 1197, 0, 1),
(361, '228.000 tỷ đồng cho vay bất động sản năm 2010', 'Theo Ngân hàng Nhà nước Việt Nam, ước đến hết 31/12/2010, dư nợ cho vay để đầu tư kinh doanh bất động sản đạt khoảng 228.000 tỷ đồng, tăng 23,5% so với cùng ký năm ngoái.', 'p1_2.jpg', NULL, '<h1 class="Title">228.000 tỷ đồng cho vay bất động sản năm 2010</h1>\n<h2 class="Lead">Theo Ng&acirc;n h&agrave;ng Nh&agrave; nước Việt Nam, ước đến hết 31/12/2010, dư nợ cho vay để đầu tư kinh doanh bất động sản đạt khoảng 228.000 tỷ đồng, tăng 23,5% so với c&ugrave;ng k&yacute; năm ngo&aacute;i.</h2>\n<p class="Normal">Th&ocirc;ng tin từ Ng&acirc;n h&agrave;ng Nh&agrave; nước Việt Nam cho biết, trong 10 th&aacute;ng đầu năm nay, tăng trưởng t&iacute;n dụng đối với lĩnh vực bất động sản ở mức xấp xỉ tỷ lệ tăng trưởng t&iacute;n dụng chung của to&agrave;n ng&agrave;nh ng&acirc;n h&agrave;ng. B&ecirc;n cạnh đ&oacute;, chất lượng t&iacute;n dụng đối với lĩnh vực n&agrave;y cũng được đảm bảo.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="p1_2.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="350" /></td>\n</tr>\n<tr>\n<td class="Image">Tổng dư nợ cho vay để đầu tư kinh doanh bất động sản của c&aacute;c tổ chức t&iacute;n dụng đạt 10 th&aacute;ng đầu năm đạt hơn 224.000 tỷ đồng. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Theo b&aacute;o c&aacute;o, đến ng&agrave;y 31/10/2010, đạt 224.843 tỷ đồng, tăng 22,01% so với 31/12/2009, trong đ&oacute; tăng trưởng t&iacute;n dụng nền kinh tế của to&agrave;n hệ thống c&ugrave;ng thời kỳ l&agrave; 23,87%. Ước đến hết 31/12, dư nợ cho vay để đầu tư kinh doanh bất động sản đạt khoảng 228.000 tỷ đồng, tăng 23,5% so với c&ugrave;ng kỳ năm ngo&aacute;i.</p>\n<p class="Normal">Ng&acirc;n h&agrave;ng Nh&agrave; nước nhận định, đ&acirc;y l&agrave; tỷ lệ c&oacute; thể chấp nhận được trong điều kiện mặt bằng l&atilde;i suất hiện nay. Nh&igrave;n chung, c&aacute;c dự &aacute;n bất động sản c&oacute; hiệu quả lu&ocirc;n được c&aacute;c tổ chức t&iacute;n dụng xem x&eacute;t v&agrave; đ&aacute;p ứng nhu cầu vốn kịp thời.</p>\n<p class="Normal">Việc cho vay ưu đ&atilde;i đối với lĩnh vực bất động sản thời gian qua được thực hiện bởi Ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch x&atilde; hội v&agrave; Ng&acirc;n h&agrave;ng Ph&aacute;t triển Việt Nam. Theo đ&oacute;, Ng&acirc;n h&agrave;ng Ch&iacute;nh s&aacute;ch x&atilde; hội thực hiện cho vay đối với hộ ngh&egrave;o về nh&agrave; ở. Ng&acirc;n h&agrave;ng Ph&aacute;t triển Việt Nam thực hiện cho vay ưu đ&atilde;i đối với một số dự &aacute;n đầu tư x&acirc;y dựng khu đ&ocirc; thị, nh&agrave; ở cho c&ocirc;ng nh&acirc;n c&aacute;c khu c&ocirc;ng nghiệp, cho sinh vi&ecirc;n, người c&oacute; thu nhập thấp ở khu vực đ&ocirc; thị.</p>\n<p class="Normal">Theo Ng&acirc;n h&agrave;ng Nh&agrave; nước Việt Nam, c&aacute;c h&igrave;nh thức cho vay bất động sản trong thời gian qua kh&aacute; đa dạng: Cho vay chủ đầu tư c&aacute;c dự &aacute;n, cho vay c&aacute;c đơn vị x&acirc;y lắp, cho vay đối với c&aacute;c tổ chức, c&aacute; nh&acirc;n c&oacute; nhu cầu mua, sử dụng c&aacute;c sản phẩm của dự &aacute;n như căn hộ, văn ph&ograve;ng.</p>\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 689, 0, 1),
(362, 'Chạy đua làm nhà giá trung bình', 'Khu Nam (huyện Nhà Bè, Bình Chánh), khu Đông (quận 2, 9, Thủ Đức) và hiện nay là phía Tây TP HCM gồm các quận 12, Tân Phú, Bình Tân đang nổ ra cuộc giành thị phần nhà giá mềm, 13-15 triệu đồng mỗi m2. ', 'bung_no_dua_nha_gia_trung_binh.jpg', NULL, '<h1 class="Title">Chạy đua l&agrave;m nh&agrave; gi&aacute; trung b&igrave;nh</h1>\r\n<h2 class="Lead">Khu Nam (huyện Nh&agrave; B&egrave;, B&igrave;nh Ch&aacute;nh), khu Đ&ocirc;ng (quận 2, 9, Thủ Đức) v&agrave; hiện nay l&agrave; ph&iacute;a T&acirc;y TP HCM gồm c&aacute;c quận 12, T&acirc;n Ph&uacute;, B&igrave;nh T&acirc;n đang nổ ra cuộc gi&agrave;nh thị phần nh&agrave; gi&aacute; mềm, 13-15 triệu đồng mỗi m2.&nbsp;<br /><span style="font-size: 10px; font-weight: normal;">Manh nha từ nhiều năm nay, rục rịch khởi động v&agrave;o cuối năm 2009 nhưng phải đợi đến năm 2010 c&aacute;c dự &aacute;n "t&acirc;n binh"của d&ograve;ng sản phẩm căn hộ b&igrave;nh d&acirc;n mới rầm rộ c&ocirc;ng bố, loan tin tr&ecirc;n thị trường nh&agrave; đất. Trong đ&oacute;, nội cộm nhất l&agrave; sự kiện C&ocirc;ng ty Đất L&agrave;nh giới thiệu căn hộ mẫu si&ecirc;u nhỏ tại quận 12, TP HCM. Đ&oacute; l&agrave; d&ograve;ng sản phẩm căn hộ 40 m2 d&agrave;nh cho hai người v&agrave; 20 m2 d&agrave;nh cho một người. Mặc d&ugrave; chỉ l&agrave; động th&aacute;i lấy &yacute; kiến kh&aacute;ch h&agrave;ng đơn thuần, thế nhưng sản phẩm đ&atilde; thu h&uacute;t sự quan t&acirc;m của nhiều người c&oacute; thu nhập khi&ecirc;m tốn đang bức b&aacute;ch về nh&agrave; ở.</span></h2>\r\n<p class="Normal">B&agrave;i to&aacute;n được C&ocirc;ng ty Đất L&agrave;nh đưa ra l&agrave;, với gi&aacute; 300 triệu đồng một căn hộ, người mua chỉ g&oacute;p khoảng 2-4 triệu đồng mỗi th&aacute;ng nhưng vẫn đ&aacute;p ứng đủ nhu cầu thiết yếu đi k&egrave;m với tiện &iacute;ch c&ocirc;ng cộng. Ph&oacute; gi&aacute;m đốc C&ocirc;ng ty địa ốc Đất L&agrave;nh Nguyễn Văn Đực cũng từng nhiều lần thổ lộ, &ocirc;ng mong l&atilde;nh đạo th&agrave;nh phố lưu t&acirc;m định h&igrave;nh những khu đất d&agrave;nh ri&ecirc;ng ph&aacute;t triển v&agrave;nh đai dự &aacute;n căn hộ gi&aacute; rẻ thương mại, chẳng hạn như tại quận 12.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bung_no_dua_nha_gia_trung_binh.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một số kh&aacute;ch h&agrave;ng d&ugrave;ng bản đồ để xem vị tr&iacute; dự &aacute;n v&agrave; vị tr&iacute; căn hộ ngay tại c&ocirc;ng trường dự &aacute;n Happy Land, gi&aacute; 13 triệu đồng mỗi m2. Ảnh:&nbsp;<em>Vũ L&ecirc;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cũng thuộc khu vực ph&iacute;a T&acirc;y TP HCM, c&aacute;c quận ven đ&ocirc; S&agrave;i G&ograve;n đang c&oacute; nhiều dự &aacute;n gi&aacute; trung b&igrave;nh đổ bộ v&agrave;o thị trường như: Lotus Garden (T&acirc;n Ph&uacute;) 14 triệu đồng mỗi m2, Green Hills (B&igrave;nh T&acirc;n) 15,3 triệu đồng mỗi m2, Đại Th&agrave;nh (T&acirc;n Ph&uacute;) 14 triệu đồng mỗi m2...</p>\r\n<p class="Normal">C&ograve;n khu Đ&ocirc;ng TP HCM cũng kh&ocirc;ng chịu l&eacute;p vế, trong th&aacute;ng 11 vừa qua, một doanh nghiệp th&ocirc;ng b&aacute;o với kh&aacute;ch h&agrave;ng về việc nhận mẫu đăng k&yacute; đợt một cho dự &aacute;n nh&agrave; chung cư tại quận Thủ Đức, gi&aacute; 320 triệu đồng một căn hộ rộng 35 m2. Dự &aacute;n gồm 2 block, cao 19 tầng, với hơn 500 căn hộ, đ&atilde; g&acirc;y sốc gi&aacute; b&aacute;n trung b&igrave;nh dưới 10 triệu đồng mỗi m2.</p>\r\n<p class="Normal">C&aacute;c dự &aacute;n kh&aacute;c tại khu Đ&ocirc;ng TP HCM đang xếp h&agrave;ng tr&ecirc;n thị trường gồm: TDH Trường Thọ (Thủ Đức) gi&aacute; khởi điểm 15 triệu đồng mỗi m2, Phố Đ&ocirc;ng Hoa Sen v&agrave; Trung Đ&ocirc;ng Plaza (quận 9) 13,7-13,8 triệu đồng mỗi m2...</p>\r\n<p class="Normal">G&oacute;p kh&ocirc;ng kh&iacute; cho cuộc đua n&agrave;y, hồi th&aacute;ng 9, tại khu T&acirc;y Nam S&agrave;i G&ograve;n, thuộc địa b&agrave;n huyện B&igrave;nh Ch&aacute;nh, Tập đo&agrave;n vận tải Happy đ&atilde; tranh thủ c&ocirc;ng bố dự &aacute;n bất động sản Happy Plaza, gi&aacute; trung b&igrave;nh 13 triệu đồng mỗi m2. Dự &aacute;n gồm 2 block nh&agrave; cao 22 tầng, sẽ cung cấp cho thị trường 644 căn hộ.</p>\r\n<p class="Normal">Gi&aacute;m đốc C&ocirc;ng ty địa ốc Ho&agrave;ng Anh S&agrave;i G&ograve;n Đo&agrave;n Ch&iacute; Thanh nhận x&eacute;t: "Việc doanh nghiệp chạy đua x&acirc;y dựng c&aacute;c dự &aacute;n chung cư gi&aacute; trung b&igrave;nh l&agrave; diễn biến tất yếu của thị trường. Tuy nhi&ecirc;n thật đ&aacute;ng tiếc l&agrave; cuộc đua n&agrave;y kh&ocirc;ng c&oacute; người cầm trịch v&agrave; đang diễn ra tự ph&aacute;t n&ecirc;n tiềm năng của thị trường bị hạn chế".</p>\r\n<p class="Normal">Theo &ocirc;ng Thanh, x&eacute;t về ngắn hạn, căn hộ gi&aacute; trung b&igrave;nh thấp v&agrave; trung b&igrave;nh kh&aacute; l&agrave; điểm s&aacute;ng của cả thị trường bất động sản TP HCM trong cả năm 2010 ảm đạm. Bởi lẽ d&ograve;ng sản phẩm n&agrave;y c&oacute; nhiều cơ hội t&igrave;m được đầu ra hơn l&agrave; nh&agrave;, căn hộ cao cấp. Nếu được sự hỗ trợ t&iacute;ch cực của ng&acirc;n h&agrave;ng, người c&oacute; thu nhập trung b&igrave;nh 10-15 triệu đồng một th&aacute;ng dễ d&agrave;ng nắm bắt được cơ hội sở hữu căn hộ, rất ph&ugrave; hợp với t&igrave;nh h&igrave;nh đ&ocirc; thị h&oacute;a nhanh tại TP HCM. Sự b&ugrave;ng nổ nguồn cung d&ograve;ng sản phẩm n&agrave;y với t&iacute;nh cạnh tranh rất cao về gi&aacute; b&aacute;n đang mở ra kịch bản s&aacute;ng lạn cho người mua nh&agrave; v&igrave; họ c&oacute; nhiều cơ hội chọn lựa. Song, &ocirc;ng Thanh cho rằng, sẽ ho&agrave;n hảo hơn nếu cuộc đua n&agrave;y được Nh&agrave; nước hỗ trợ, định hướng về quy hoạch, k&egrave;m theo ho&agrave;n thiện hạ tầng c&aacute;c quận ngoại th&agrave;nh.</p>\r\n<p class="Normal">Nhận định về sự đổ bộ của c&aacute;c dự &aacute;n nh&agrave; trung b&igrave;nh tr&ecirc;n địa b&agrave;n TP HCM, Chủ tịch HĐQT C&ocirc;ng ty cổ phần Nh&agrave; Việt Nam Trần Văn Th&agrave;nh ph&acirc;n t&iacute;ch: "<strong><span style="color: #4f4f4f;">Nh&agrave; gi&aacute; rẻ kh&ocirc;ng thể b&ugrave;ng nổ nguồn cung v&igrave; doanh nghiệp kh&ocirc;ng l&agrave;m nổi. Kế đến, c&aacute;c dự &aacute;n gi&aacute; trung b&igrave;nh đang chịu sức &eacute;p l&atilde;i suất rất lớn, chi ph&iacute; đầu v&agrave;o tăng cao, đ&acirc;y l&agrave; cuộc đua đường trường</span></strong>".</p>\r\n<p class="Normal">Theo &ocirc;ng Th&agrave;nh, cần nh&igrave;n cuộc đua gi&agrave;nh thị phần nh&agrave; gi&aacute; trung b&igrave;nh thấp v&agrave; trung b&igrave;nh kh&aacute; dưới nhiều kh&iacute;a cạnh v&igrave; sự việc lu&ocirc;n c&oacute; bề nổi v&agrave; phần ch&igrave;m. Nh&oacute;m doanh nghiệp thứ nhất đ&atilde; c&oacute; quỹ đất từ l&acirc;u, đ&oacute;ng xong tiền sử dụng đất, chuẩn bị vốn chu đ&aacute;o. Việc họ tung h&agrave;ng ra thị trường trong thời điểm n&agrave;y l&agrave; bước chuyển tiếp của những kh&acirc;u chuẩn bị từ trước. Hơn nữa gi&aacute; đất trước đ&acirc;y rẻ hơn hiện tại n&ecirc;n khả năng cạnh tranh của c&aacute;c doanh nghiệp n&agrave;y rất cao. Đ&acirc;y l&agrave; nh&oacute;m c&ocirc;ng ty bất động sản nắm chắc th&agrave;nh c&ocirc;ng.</p>\r\n<p class="Normal">Nh&oacute;m thứ hai l&agrave; nh&oacute;m doanh nghiệp mới tiếp cận thị trường. Họ kh&ocirc;ng c&oacute; quỹ đất, phải mua với gi&aacute; cao. Gặp phải thị trường đ&oacute;ng băng, gi&aacute; nh&agrave; đất chững lại họ sẽ gặp nhiều kh&oacute; khăn khi cạnh tranh với nh&oacute;m cũ. Hầu như những lợi thế cạnh tranh sẽ đứng về ph&iacute;a nh&oacute;m doanh nghiệp cũ v&igrave; cơ hội thu lợi nhuận của nh&oacute;m doanh nghiệp mới rất thấp, thậm ch&iacute; c&oacute; thể lỗ.</p>\r\n<p class="Normal">Chuy&ecirc;n gia n&agrave;y đề cập đến phần ch&igrave;m của tảng băng l&agrave; hầu như tất cả c&aacute;c doanh nghiệp bất động sản đều phải vay để thực hiện dự &aacute;n v&agrave; lu&ocirc;n đối mặt với việc thanh to&aacute;n nợ ng&acirc;n h&agrave;ng trong thời điểm l&atilde;i suất tăng cao. C&oacute; nhiều dự &aacute;n chủ đầu tư chuẩn bị vốn kh&ocirc;ng chu đ&aacute;o đến giai đoạn n&agrave;y họ c&oacute; thể t&iacute;nh đến chuyện b&aacute;n rẻ để xoay vốn trả nợ nh&agrave; băng. Thực tế l&agrave; số dự &aacute;n tr&igrave;nh l&agrave;ng nhưng t&igrave;nh h&igrave;nh triển khai (&eacute;p cọc, l&agrave;m m&oacute;ng, x&acirc;y dựng) kh&aacute; &iacute;t so với th&ocirc;ng tin c&ocirc;ng bố. Trong đ&oacute; cũng c&oacute; nhiều dự &aacute;n rơi v&agrave;o t&igrave;nh trạng chờ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ L&ecirc;</strong></p>', 10, 3, 372, 0, 1),
(363, 'Ký hợp đồng mua bán chung cư Long Phụng', 'Dự kiến cuối tháng 1/2011, chung cư Long Phụng, quận Bình Tân (TP HCM) sẽ hoàn thành phần móng. Khi đó chủ đầu tư sẽ tiến hành ký hợp đồng mua bán với các khách hàng đã đăng ký giữ chỗ.', 'duan1.jpg', NULL, '<h1 class="Title">K&yacute; hợp đồng mua b&aacute;n chung cư Long Phụng</h1>\r\n<h2 class="Lead">Dự kiến cuối th&aacute;ng 1/2011, chung cư Long Phụng, quận B&igrave;nh T&acirc;n (TP HCM) sẽ ho&agrave;n th&agrave;nh phần m&oacute;ng. Khi đ&oacute; chủ đầu tư sẽ tiến h&agrave;nh k&yacute; hợp đồng mua b&aacute;n với c&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; đăng k&yacute; giữ chỗ.</h2>\r\n<p class="Normal">Số tiền đặt cọc giữ chỗ chỉ 10% gi&aacute; trị căn hộ, tương đương khoảng từ 90 đến 100 triệu đồng. Sau khi x&acirc;y dựng xong m&oacute;ng, chủ đầu tư v&agrave; kh&aacute;ch h&agrave;ng k&yacute; hợp đồng mua b&aacute;n (dự kiến th&aacute;ng 2/2011), l&uacute;c n&agrave;y kh&aacute;ch h&agrave;ng sẽ đ&oacute;ng th&ecirc;m 15%. Sau đ&oacute; mỗi ba th&aacute;ng, kh&aacute;ch chỉ đ&oacute;ng tiếp 10% mỗi đợt. Đặc biệt, kh&aacute;ch h&agrave;ng chỉ cần thanh to&aacute;n đến 70 % gi&aacute; trị căn hộ cho đến khi nhận b&agrave;n giao nh&agrave;. Như vậy tiến độ thanh to&aacute;n sẽ th&iacute;ch hợp cho người c&oacute; thu nhập trung b&igrave;nh muốn c&oacute; điều kiện tận hưởng cuộc sống tại Long Phụng Apartment.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan1.jpg" border="1" alt="Dự &aacute;n" width="450" height="280" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&igrave;nh ảnh dự &aacute;n Long Phụng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ocirc;ng ty Cổ phần Gia Gia Ph&uacute; - đơn vị tiếp thị v&agrave; ph&acirc;n phối dự &aacute;n chung cư Long Phụng quận B&igrave;nh T&acirc;n đang triển khai chương tr&igrave;nh tặng v&agrave;ng cho kh&aacute;ch h&agrave;ng, nh&acirc;n dịp mừng năm mới 2011. Mỗi kh&aacute;ch h&agrave;ng sẽ được tặng ngay ba chỉ v&agrave;ng SJC khi đăng k&yacute; đặt cọc giữ quyền mua căn hộ Long Phụng Apartment.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan2.jpg" border="1" alt="Ph&ograve;ng ngủ." width="450" height="280" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ph&ograve;ng ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chung cư Long Phụng gồm một t&ograve;a Long v&agrave; một t&ograve;a Phụng, tổng số 240 căn hộ, mỗi căn c&oacute; hai mặt tiền th&ocirc;ng tho&aacute;ng gi&oacute;. Ngo&agrave;i ra, c&ograve;n c&oacute; c&aacute;c tiện &iacute;ch như: si&ecirc;u thị Big C, bệnh viện Triều An, trường tiểu học Quốc tế...</p>\r\n<p class="Normal">Hiện nay, dự &aacute;n đ&atilde; c&oacute; hơn 120 căn hộ được đặt giữ chỗ chỉ sau hai th&aacute;ng mở nhận đặt cọc. Chương tr&igrave;nh tặng v&agrave;ng cho kh&aacute;ch h&agrave;ng được thực hiện trong 20 căn hộ, từ căn thứ 121 đến căn thứ 140 v&agrave; k&eacute;o d&agrave;i qua Tết dương lịch. Chủ đầu tư cho biết, sau căn thứ 140 sẽ điều chỉnh tăng gi&aacute; để ph&ugrave; hợp với thị trường. Những căn hộ đợt n&agrave;y c&oacute; diện t&iacute;ch 79 m2 (hai ph&ograve;ng ngủ), 80 m2 (ba ph&ograve;ng ngủ) v&agrave; căn hộ VIP 161 m2 (ba ph&ograve;ng ngủ). Gi&aacute; mỗi căn hộ đợt n&agrave;y chỉ từ 900 triệu đồng đến hơn một tỷ đồng một căn. Hiện nh&agrave; mẫu đ&atilde; được khai trương đ&oacute;n kh&aacute;ch ngay tại dự &aacute;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan3.jpg" border="1" alt="Ph&ograve;ng ăn v&agrave; bếp thiết kế kh&ocirc;ng gian mở rộng r&atilde;i." width="450" height="280" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ph&ograve;ng ăn v&agrave; bếp thiết kế kh&ocirc;ng gian mở rộng r&atilde;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chung cư Long Phụng nằm mặt tiền quốc lộ 1A, khu d&acirc;n cư sầm uất trung t&acirc;m quận B&igrave;nh T&acirc;n. Căn n&agrave;o cũng c&oacute; hai mặt tiền th&ocirc;ng tho&aacute;ng gi&oacute;. Điểm nổi bật của dự &aacute;n l&agrave; dải c&ocirc;ng vi&ecirc;n c&acirc;y xanh d&agrave;i đến 200 m&eacute;t, trung t&acirc;m thương mại hiện đại, si&ecirc;u thị, nh&agrave; trẻ, nh&agrave; h&agrave;ng tại tầng trệt v&agrave; bể bơi rất lớn nằm ngo&agrave;i trời tại tầng hai của t&ograve;a nh&agrave;. Ngo&agrave;i ra, dự &aacute;n c&ograve;n c&oacute; c&aacute;c tiện &iacute;ch kh&aacute;c như: si&ecirc;u thị Big C, bệnh viện đa khoa Quốc &Aacute;nh, bệnh viện Triều An, chợ B&agrave; Hom, trường tiểu học Quốc tế nằm ngay gần khu vực dự &aacute;n.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Gia Gia Phu Realestate</em>)</p>', 10, 3, 2370, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(365, 'Bàn giao 251 căn nhà phố tại dự án EcoLakes Mỹ Phước', 'Ngày 18/12, Công ty cổ phần SetiaBecamex đã bàn giao 251 căn nhà phố thuộc khu Vườn Thiên Đàng, giai đoạn một của dự án khu đô thị sinh thái EcoLakes Mỹ Phước rộng 226 ha.', 'duan3.jpg', NULL, '<h1 class="Title">B&agrave;n giao 251 căn nh&agrave; phố tại dự &aacute;n EcoLakes Mỹ Phước</h1>\r\n<h2 class="Lead">Ng&agrave;y 18/12, C&ocirc;ng ty cổ phần SetiaBecamex đ&atilde; b&agrave;n giao 251 căn nh&agrave; phố thuộc khu Vườn Thi&ecirc;n Đ&agrave;ng, giai đoạn một của dự &aacute;n khu đ&ocirc; thị sinh th&aacute;i EcoLakes Mỹ Phước rộng 226 ha.</h2>\r\n<p class="Normal">C&aacute;c nh&agrave; phố được b&agrave;n giao khi đ&atilde; ho&agrave;n thiện mặt ngo&agrave;i với mặt tiền trang nh&atilde; v&agrave; cảnh quan, đường phố ho&agrave;n chỉnh. Tất cả c&aacute;c c&ocirc;ng tr&igrave;nh tiện &iacute;ch thiết yếu phục vụ cuộc sống đ&atilde; được ho&agrave;n th&agrave;nh. To&agrave;n bộ c&aacute;p điện, viễn th&ocirc;ng v&agrave; hệ thống tho&aacute;t nước đều được ch&ocirc;n ngầm, vừa tăng t&iacute;nh thẩm mỹ, vừa đảm bảo an to&agrave;n. Mỗi khu nh&agrave; đều c&oacute; cổng bảo vệ. SetiaBecamex cũng cung cấp miễn ph&iacute; dịch vụ tuần tra bảo vệ cho cư d&acirc;n trong hai năm đầu.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan3.jpg" border="1" alt="Lễ b&agrave;n giao nh&agrave;." width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lễ b&agrave;n giao nh&agrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Giai đoạn hai của dự &aacute;n EcoLakes đ&atilde; được mở b&aacute;n tại H&agrave; Nội v&agrave;o cuối th&aacute;ng 10. C&oacute; ba loại: nh&agrave; phố, biệt thự song lập v&agrave; biệt thự đơn lập với tổng diện t&iacute;ch s&agrave;n từ 100 m2 trở l&ecirc;n đang được ch&agrave;o b&aacute;n trong giai đoạn hai, với gi&aacute; từ 1,26 tỷ đồng một căn.</p>\r\n<p class="Normal">Hiện nay vẫn c&ograve;n cơ hội cuối c&ugrave;ng cho kh&aacute;ch sở hữu c&aacute;c căn nh&agrave; phố v&agrave; biệt thự tại dự &aacute;n. Đặc biệt, kh&aacute;ch mua nh&agrave; giai đoạn hai c&oacute; thể tham gia chương tr&igrave;nh Kế hoạch sở hữu nh&agrave; EcoLakes 30/70. Theo đ&oacute; kh&aacute;ch mua chỉ phải thanh to&aacute;n 30% gi&aacute; trị sản phẩm, c&ograve;n lại 70% gi&aacute; trị căn nh&agrave; được ng&acirc;n h&agrave;ng cho vay với l&atilde;i suất được chủ đầu tư SetiaBecamex t&agrave;i trợ trong suốt hai năm x&acirc;y dựng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="duan1.jpg" border="1" alt="To&agrave;n cảnh dự &aacute;n EcoLakes Mỹ Phước." width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">To&agrave;n cảnh dự &aacute;n EcoLakes Mỹ Phước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Mọi th&ocirc;ng tin về dự &aacute;n EcoLakes Mỹ Phước, li&ecirc;n hệ Savills Việt Nam tại địa chỉ<a class="Normal" href="mailto:prestige@savills.com.vn">prestige@savills.com.vn</a>&nbsp;hoặc truy cập website:&nbsp;<a class="Normal" href="http://www.ecolakes.vn/">http://www.ecolakes.vn/</a></p>\r\n<p class="Normal">(Nguồn:&nbsp;<em>EcoLakes Mỹ Phước</em>)</p>', 10, 3, 1752, 0, 1),
(366, 'Đề xuất lập Quỹ tiết kiệm nhà ở', 'Quỹ tiết kiệm nhà ở sẽ được xây dựng giống như hình thức bảo hiểm xã hội với mức đóng góp dự kiến từ 1 - 2% tổng tiền lương hàng tháng của người lao động.', 'ld1.jpg', NULL, '<h1 class="Title">Đề xuất lập Quỹ tiết kiệm nh&agrave; ở</h1>\r\n<h2 class="Lead">Quỹ tiết kiệm nh&agrave; ở sẽ được x&acirc;y dựng giống như h&igrave;nh thức bảo hiểm x&atilde; hội với mức đ&oacute;ng g&oacute;p dự kiến từ 1 - 2% tổng tiền lương h&agrave;ng th&aacute;ng của người lao động.<br /><span style="font-size: 10px; font-weight: normal;">Bộ X&acirc;y dựng vừa đề xuất Ch&iacute;nh phủ cho ph&eacute;p lập Quỹ tiết kiệm nh&agrave; ở với mục đ&iacute;ch giải b&agrave;i to&aacute;n về chỗ ở cho người lao động. Theo đ&oacute;, quỹ sẽ được th&agrave;nh lập với mục ti&ecirc;u cho người d&acirc;n vay tiền mua nh&agrave; ở với l&atilde;i suất thấp k&egrave;m những quy định về đối tượng v&agrave; điều kiện vay. Quỹ cũng sẽ d&agrave;nh một phần nhất định ưu ti&ecirc;n cho c&aacute;c doanh nghiệp x&acirc;y dựng tham gia chương tr&igrave;nh ph&aacute;t triển nh&agrave; ở x&atilde; hội.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ld1.jpg" border="1" alt="Ảnh: Ho&agrave;ng H&agrave;" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Quỹ sẽ l&agrave; cầu nối gi&uacute;p cho d&acirc;n ngh&egrave;o, những người c&oacute; thu nhập thấp c&oacute; cơ hội được mua nh&agrave;. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Quỹ tiết kiệm nh&agrave; ở sẽ được x&acirc;y dựng giống như h&igrave;nh thức bảo hiểm x&atilde; hội với mức đ&oacute;ng g&oacute;p dự kiến từ 1- 2% tổng tiền lương hằng th&aacute;ng của người lao động. Những người nghỉ hưu sẽ được r&uacute;t về cộng với l&atilde;i suất mang t&iacute;nh hỗ trợ trượt gi&aacute;. Số tiền n&agrave;y sẽ cho những người thu nhập thấp vay mua nh&agrave; với l&atilde;i suất chỉ bằng 20-25% so với vay của ng&acirc;n h&agrave;ng thương mại</p>\r\n<p class="Normal">Tại một cuộc họp b&aacute;o gần đ&acirc;y, Thứ trưởng Bộ X&acirc;y dựng Nguyễn Trần Nam cho rằng, với hơn 9 triệu người lao động đang hưởng lương, chỉ cần g&oacute;p 1% số lương mỗi th&aacute;ng, h&agrave;ng năm quỹ sẽ c&oacute; kh&ocirc;ng dưới 10.000 tỷ đồng, g&oacute;p phần đ&aacute;ng kể cải thiện chỗ ở cho người d&acirc;n.</p>\r\n<p class="Normal">"Quỹ sẽ l&agrave; cầu nối gi&uacute;p cho d&acirc;n ngh&egrave;o, những người c&oacute; thu nhập thấp c&oacute; cơ hội được mua nh&agrave;. Những người kh&ocirc;ng c&oacute; nhu cầu về nh&agrave;, khi về hưu, họ được r&uacute;t to&agrave;n bộ số tiền cả gốc lẫn l&atilde;i", Thứ trưởng Nam n&oacute;i.</p>\r\n<p class="Normal">Theo thống k&ecirc; của Bộ X&acirc;y dựng, tại khu vực đ&ocirc; thị tr&ecirc;n cả nước hiện vẫn c&ograve;n khoảng 7 triệu người c&oacute; nhu cầu thu&ecirc;, thu&ecirc; mua nh&agrave; ở x&atilde; hội với tổng diện t&iacute;ch l&ecirc;n tới 150 triệu m2, tương đương nguồn vốn đầu tư tới 300.000 - 400.000 tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>B&aacute;ch Hợp</strong></p>', 10, 3, 2439, 0, 1),
(367, 'Bộ Xây dựng đề nghị hạn chế phát triển nhà ở riêng lẻ', 'Giá nhà đất có thể tăng cao dẫn đến việc sở hữu nhà ở riêng lẻ sẽ rất khó khăn. Bộ xây dựng cho rằng, cần đẩy mạnh phát triển loại hình nhà cho thuê và hạn chế xây nhà ở riêng lẻ.', 'nha-o-1 (1).jpg', NULL, '<h1 class="Title">Bộ X&acirc;y dựng đề nghị hạn chế ph&aacute;t triển nh&agrave; ở ri&ecirc;ng lẻ</h1>\r\n<h2 class="Lead">Gi&aacute; nh&agrave; đất c&oacute; thể tăng cao dẫn đến việc sở hữu nh&agrave; ở ri&ecirc;ng lẻ sẽ rất kh&oacute; khăn. Bộ x&acirc;y dựng cho rằng, cần đẩy mạnh ph&aacute;t triển loại h&igrave;nh nh&agrave; cho thu&ecirc; v&agrave; hạn chế x&acirc;y nh&agrave; ở ri&ecirc;ng lẻ.</h2>\r\n<p class="Normal">Theo kết quả điều tra d&acirc;n số v&agrave; nh&agrave; ở vừa được Bộ X&acirc;y dựng tr&igrave;nh Ch&iacute;nh phủ, t&iacute;nh đến 1/4/2009, tổng số hộ có nhà ở tr&ecirc;n cả nước l&agrave; hơn 22 triệu. Số hộ kh&ocirc;ng c&oacute; nh&agrave; khoảng hơn 11.000. Số hộ d&acirc;n sống tại nh&agrave; ở ri&ecirc;ng lẻ chiếm tỷ lệ tr&ecirc;n 95%. Loại h&igrave;nh nh&agrave; ở chung cư trong một v&agrave;i năm gần đ&acirc;y c&oacute; ph&aacute;t triển nhưng vẫn chiếm tỷ trọng thấp, chỉ khoảng 1,23%.</p>\r\n<p class="Normal">So với c&aacute;c nước trong khu vực th&igrave; diện t&iacute;ch nh&agrave; ở b&igrave;nh qu&acirc;n tr&ecirc;n đầu người của Việt Nam c&ograve;n thấp. Diện t&iacute;ch s&agrave;n nh&agrave; ở b&igrave;nh qu&acirc;n tr&ecirc;n đầu người đạt 16,7 m2 mỗi người, ri&ecirc;ng khu vực đ&ocirc; thị đạt 19,2 m2 mỗi người. Ở c&aacute;c th&agrave;nh phố lớn của Trung Quốc l&agrave; 25-29 m2 mỗi người, Th&aacute;i Lan l&agrave; 22 m2, Malaysia l&agrave; 31 m2.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nha-o-1 (1).jpg" border="1" alt="Bộ X&acirc;y dựng cho rằng cần hạn chế ph&aacute;t triển nh&agrave; ở ri&ecirc;ng lẻ. Ảnh: Ho&agrave;ng H&agrave;." width="480" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bộ X&acirc;y dựng cho rằng cần hạn chế ph&aacute;t triển nh&agrave; ở ri&ecirc;ng lẻ. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Thống k&ecirc; của Bộ X&acirc;y dựng cho thấy, tỷ lệ hộ c&oacute; diện t&iacute;ch sử dụng lớn (từ 60m2 trở l&ecirc;n) đạt 52,83%. Số hộ c&oacute; nh&agrave; ở với diện t&iacute;ch dưới 25m2 l&agrave; 8%.</p>\r\n<p class="Normal">Về h&igrave;nh thức sở hữu, nh&agrave; ri&ecirc;ng chiếm 92,8% c&ograve;n nh&agrave; thu&ecirc; hoặc mượn chiếm 6,5%. C&aacute;c h&igrave;nh thức sở hữu kh&aacute;c chiếm tỷ lệ kh&ocirc;ng đ&aacute;ng kể. Số hộ d&acirc;n sống trong nh&agrave; thu&ecirc;/mượn chiếm tới 13,6% trong khu vực th&agrave;nh thị v&agrave; 3,3% tại khu vực n&ocirc;ng th&ocirc;n.</p>\r\n<p class="Normal">Bộ X&acirc;y dựng đ&aacute;nh gi&aacute;, do gi&aacute; đất đai tăng cao, người d&acirc;n kh&oacute; c&oacute; khả năng sở hữu nh&agrave; ở ri&ecirc;ng lẻ. Mặt kh&aacute;c, th&oacute;i quen sở hữu ri&ecirc;ng về nh&agrave; ở c&oacute; thể g&acirc;y n&ecirc;n t&acirc;m l&yacute; người d&acirc;n chỉ lo t&iacute;ch lũy một lượng lớn tiền cho việc mua nh&agrave; m&agrave; kh&ocirc;ng xem x&eacute;t đầu tư v&agrave;o c&aacute;c lĩnh vực kh&aacute;c, phần n&agrave;o đ&oacute; c&oacute; thể g&acirc;y l&atilde;ng ph&iacute; nguồn vốn của x&atilde; hội.</p>\r\n<p class="Normal">Thứ trưởng Bộ X&acirc;y dựng Nguyễn Trần Nam cho rằng, để ph&ugrave; hợp với thu nhập người d&acirc;n, cần đẩy mạnh ph&aacute;t triển loại h&igrave;nh nh&agrave; ở cho thu&ecirc;. Quỹ nh&agrave; ở cho thu&ecirc; được ph&aacute;t triển bởi ch&iacute;nh quyền địa phương v&agrave; c&aacute;c doanh nghiệp thuộc c&aacute;c th&agrave;nh phần kinh tế.</p>\r\n<p class="Normal">Đối với khu vực đ&ocirc; thị c&oacute; mật độ d&acirc;n số cao, Bộ X&acirc;y dựng kiến nghị cần tiếp tục duy tr&igrave; quy định ưu ti&ecirc;n quỹ đất để ph&aacute;t triển nh&agrave; chung cư cao tầng, hạn chế ph&aacute;t triển nh&agrave; ở ri&ecirc;ng lẻ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 2059, 0, 1),
(368, 'Bán căn hộ IPH giá gốc đợt cuối năm', 'Trong 2 ngày (17 và 18/12), sàn bất động sản DTJ, nhà tiếp thị căn hộ trực tiếp cho chủ đầu tư dự án Indochina Plaza Hanoi (IPH), sẽ tổ chức đợt bán căn hộ IPH giá gốc cuối cùng trong năm 2010.', 'bat-dong-san2.jpg', NULL, '<h1 class="Title">B&aacute;n căn hộ IPH gi&aacute; gốc đợt cuối năm</h1>\r\n<h2 class="Lead">Trong 2 ng&agrave;y (17 v&agrave; 18/12), sàn b&acirc;́t đ&ocirc;̣ng sản DTJ, nh&agrave; tiếp thị căn hộ trực tiếp cho chủ đầu tư dự &aacute;n Indochina Plaza Hanoi (IPH), sẽ tổ chức đợt b&aacute;n căn hộ IPH gi&aacute; gốc cuối c&ugrave;ng trong năm 2010.</h2>\r\n<p class="Normal">Đến nay, dự &aacute;n đang x&acirc;y tầng 25. Dự kiến, to&agrave;n bộ phần th&ocirc; sẽ được ho&agrave;n thiện v&agrave;o đầu năm 2011 v&agrave; c&oacute; thể b&agrave;n giao cho kh&aacute;ch h&agrave;ng đ&acirc;̀u qu&yacute; I/2012.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bat-dong-san2.jpg" border="1" alt="" width="450" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">IPH đang trong qu&aacute; tr&igrave;nh x&acirc;y dựng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Dự &aacute;n do Indochina Land thu&ocirc;̣c T&acirc;̣p đoàn Indochina Capital (Mỹ) làm chủ đ&acirc;̀u tư với mức vốn 150 triệu USD. IPH bao gồm hai t&ograve;a th&aacute;p căn hộ với 390 căn hộ cao cấp, một t&ograve;a th&aacute;p văn ph&ograve;ng v&agrave; trung t&acirc;m thương mại nằm tại 4 tầng đế, 2 t&acirc;̀ng h&acirc;̀m làm bãi đ&ocirc;̃ xe tọa lạc tr&ecirc;n 1,6 ha tại đường Xu&acirc;n Thủy cắt đường Phạm H&ugrave;ng v&agrave; Phạm Văn Đồng. Ngo&agrave;i ra, tại đ&acirc;y c&ograve;n c&oacute; khu văn ph&ograve;ng hạng A v&agrave; trung t&acirc;m thương mại.</p>\r\n<p class="Normal">IPH được t&iacute;ch hợp như một khu quần thể đa dạng với hệ th&ocirc;́ng tiện nghi bao gồm c&acirc;u lạc bộ, trung t&acirc;m thể thao v&agrave; spa, vườn tạo cảnh tr&ecirc;n tầng thượng, bể bơi ngo&agrave;i trời, vườn trẻ v&agrave; hồ bơi d&agrave;nh cho trẻ em...</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>DTJ)</em></p>', 10, 3, 1088, 0, 1),
(369, 'TP HCM cũng giữ mức trần giá đất 81 triệu đồng mỗi m2', 'Chỉ điều chỉnh giá đất một số tuyến đường được nâng cấp, có cơ sở hạ tầng hoàn chỉnh, bảng giá đất TP HCM năm 2011 giữ nguyên mức trần 81 triệu đồng mỗi m2 (đối với đất đô thị) của năm 2010. ', 'a-tb-bang-gia-dat-2011.jpg', NULL, '<h1 class="Title">TP HCM cũng giữ mức trần gi&aacute; đất 81 triệu đồng mỗi m2</h1>\r\n<h2 class="Lead">Chỉ điều chỉnh gi&aacute; đất một số tuyến đường được n&acirc;ng cấp, c&oacute; cơ sở hạ tầng ho&agrave;n chỉnh, bảng gi&aacute; đất TP HCM năm 2011 giữ nguy&ecirc;n mức trần 81 triệu đồng mỗi m2 (đối với đất đ&ocirc; thị) của năm 2010.&nbsp;<br /><span style="font-size: 10px; font-weight: normal;">Ng&agrave;y 10/12, HĐND TP HCM đ&atilde; th&ocirc;ng qua bảng gi&aacute; đất mới cho th&agrave;nh phố, sẽ c&oacute; hiệu lực kể từ ng&agrave;y 1/1/2011.</span></h2>\r\n<p class="Normal">Theo đ&oacute;, bảng gi&aacute; đất mới tiếp tục giữ nguy&ecirc;n khung tối đa v&agrave; tối thiểu của năm 2010. Mức trần kh&ocirc;ng vượt 81 triệu đồng mỗi m2, vẫn l&agrave; 3 tuyến đường L&ecirc; Lợi, Đồng Khởi, Nguyễn Huệ. Mức đ&aacute;y kh&ocirc;ng thấp hơn 1,2 triệu đồng mỗi m2 đối với đất đ&ocirc; thị.</p>\r\n<p class="Normal">To&agrave;n th&agrave;nh phố c&oacute; 3.003 tuyến đường được quy định gi&aacute; tại bảng gi&aacute; đất năm 2011. So với năm 2010, bảng gi&aacute; đất năm 2011 sẽ giữ nguy&ecirc;n 2.885 tuyến đường, loại bỏ 5 tuyến đường được điều chỉnh t&ecirc;n v&agrave; được gh&eacute;p nối với c&aacute;c tuyến liền kề; bổ sung 118 tuyến, đổi t&ecirc;n v&agrave; chỉnh đoạn 111 tuyến đường.</p>\r\n<p class="Normal">Bảng gi&aacute; đất n&agrave;y sẽ d&ugrave;ng v&agrave;o việc t&iacute;nh tiền sử dụng đất, thuế thu nhập từ chuyển quyền sử dụng đất; t&iacute;nh gi&aacute; trị quyền sử dụng đất khi giao đất kh&ocirc;ng thu tiền sử dụng đất; t&iacute;nh lệ ph&iacute; trước bạ. Khung gi&aacute; n&agrave;y cũng được &aacute;p dụng v&agrave;o việc bồi thường đối với người c&oacute; h&agrave;nh vi vi phạm ph&aacute;p luật về đất đai. Bảng gi&aacute; đất mới cũng l&agrave; cơ sở t&iacute;nh tiền sử dụng đất cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="a-tb-bang-gia-dat-2011.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tuyến đường Đồng Khởi, quận 1, TP HCM nhiều năm liền được quy định c&oacute; gi&aacute; 81 triệu đồng mỗi m2 trong bảng gi&aacute; đất th&agrave;nh phố. Ảnh:&nbsp;<em>Vũ L&ecirc;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Mục ti&ecirc;u x&aacute;c định bảng gi&aacute; đất năm 2011 của th&agrave;nh phố l&agrave; b&igrave;nh ổn gi&aacute;, thu h&uacute;t đầu tư, kiềm chế lạm ph&aacute;t, tạo điều kiện thuận lợi để cấp giấy chứng nhận quyền sử dụng đất đối với bộ phận người d&acirc;n c&oacute; thu nhập thấp chưa ho&agrave;n th&agrave;nh hoặc c&ograve;n nợ tiền sử dụng đất. Năm tới, th&agrave;nh phố tập trung k&iacute;ch cầu đầu tư, k&iacute;ch cầu ti&ecirc;u thụ c&aacute;c sản phẩm bất động sản. Ngo&agrave;i ra, do t&igrave;nh h&igrave;nh thị trường nh&agrave; đất năm 2010 kh&ocirc;ng c&oacute; biến động lớn n&ecirc;n hầu như bảng gi&aacute; đất năm 2011 kh&ocirc;ng điều chỉnh nhiều.</p>\r\n<p class="Normal">Nhận định về khung gi&aacute; đất mới, Tổng gi&aacute;m đốc C&ocirc;ng ty địa ốc &Aacute; Ch&acirc;u (ACBR) Phạm Văn Hải cho rằng, với khung gi&aacute; đất kh&ocirc;ng c&oacute; g&igrave; thay đổi so với năm 2010 th&igrave; bảng gi&aacute; đất TP HCM c&ograve;n rất thấp so với giao dịch thực tế tr&ecirc;n thị trường.</p>\r\n<p class="Normal">&Ocirc;ng Hải lấy v&iacute; dụ, trong khi 3 tuyến đường khu trung t&acirc;m quận 1 được quy định gi&aacute; 81 triệu đồng mỗi m2 th&igrave; gi&aacute; đất mặt tiền ở quận 1 tr&ecirc;n thị trường c&oacute; khung 7-18 lượng v&agrave;ng mỗi m2, tức 250-644 triệu đồng mỗi m2. Gi&aacute; đất mặt tiền quận 3 cũng kh&ocirc;ng k&eacute;m cạnh, khoảng 5-12 lượng v&agrave;ng mỗi m2, tương đương 179-430 triệu đồng mỗi m2.</p>\r\n<p class="Normal">"Bảng gi&aacute; đất chỉ được &aacute;p dụng chủ yếu v&agrave;o việc t&iacute;nh tiền sử dụng đất, lệ ph&iacute; trước bạ v&agrave; một số t&igrave;nh huống đặc th&ugrave; th&igrave; t&iacute;nh tương t&aacute;c với thị trường vẫn c&ograve;n hạn chế", &ocirc;ng Hải n&oacute;i.</p>\r\n<p class="Normal">Trong khi đ&oacute;, Tổng gi&aacute;m đốc C&ocirc;ng ty cổ phần ph&aacute;t triển nh&agrave; Thủ Đức, L&ecirc; Ch&iacute; Hiếu cho rằng, bảng gi&aacute; đất do Nh&agrave; nước ban h&agrave;nh được điều chỉnh h&agrave;ng năm theo xu hướng tiệm cận gi&aacute; thị trường l&agrave; chủ trương tốt, song sẽ kh&oacute; xảy ra kịch bản khung gi&aacute; đất th&agrave;nh phố &aacute;p s&aacute;t với gi&aacute; thị trường. "Điều quan trọng l&agrave; sử dụng bảng gi&aacute; đất n&agrave;y như thế n&agrave;o. Hiện nay tiền sử dụng đất đang g&acirc;y tranh c&atilde;i v&igrave; doanh nghiệp bất động sản gặp nhiều &aacute;ch tắc n&ecirc;n cần mở ra một số phương &aacute;n giải b&agrave;i to&aacute;n t&iacute;nh tiền sử dụng đất", &ocirc;ng n&oacute;i.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ L&ecirc;</strong></p>', 10, 3, 2669, 0, 1),
(370, 'Đảo chiều giữa phiên, Vn-Index giảm gần 5 điểm', 'Giao dịch trên thị trường chứng khoán tiếp tục lình xình khi các cổ phiếu lớn không chứng tỏ vai trò dẫn dắt. Vn-Index và HNX-Index đồng loạt giảm khoảng 1%.', 'chung-khoan-0.jpg', NULL, '<h1 class="Title">Đảo chiều giữa phi&ecirc;n, Vn-Index giảm gần 5 điểm</h1>\r\n<h2 class="Lead">Giao dịch tr&ecirc;n thị trường chứng kho&aacute;n tiếp tục l&igrave;nh x&igrave;nh khi c&aacute;c cổ phiếu lớn kh&ocirc;ng chứng tỏ vai tr&ograve; dẫn dắt. Vn-Index v&agrave; HNX-Index đồng loạt giảm khoảng 1%.</h2>\r\n<p class="Normal">Đ&agrave; tăng điểm của Vn-Index từ phi&ecirc;n trước được nối d&agrave;i trong &iacute;t ph&uacute;t đầu giờ s&aacute;ng nay khi chỉ số của s&agrave;n TP HCM tăng 1,68 điểm, tiệm cận ngưỡng 520 điểm trong đợt khớp lệnh mở cửa. Tuy v&acirc;y, khối lượng giao dịch chưa đạt một triệu chứng kho&aacute;n, tương đương hơn 25 tỷ đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Nh&agrave; đầu tư vẫn chưa ghi nhận được t&iacute;n hiệu t&iacute;ch cực từ thị trường. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&agrave; đầu tư vẫn chưa ghi nhận được t&iacute;n hiệu t&iacute;ch cực từ thị trường. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại thời điểm n&agrave;y, chưa c&oacute; m&atilde; n&agrave;o giao dịch được qu&aacute; 100.000 đơn vị. Khối ngoại rục rịch giải ng&acirc;n v&agrave;o c&aacute;c blue-chip như FPT, REE, STB&hellip; Tuy nhi&ecirc;n, LAF mới l&agrave; m&atilde; được mua mạnh nhất trong đợt khớp lệnh mở cửa với khối lượng tr&ecirc;n 70.000 cổ phiếu.</p>\r\n<p class="Normal">Bước sang đợt 2, thị trường dường như kh&ocirc;ng xuất hiện th&ecirc;m t&iacute;n hiệu lạc quan n&agrave;o khi ch&ecirc;nh lệch cung &ndash; cầu gia tăng k&eacute;o đẩy chỉ số đảo chiều li&ecirc;n hồi thoắt xanh rồi lại đỏ. Phần lớn c&aacute;c m&atilde; chủ chốt c&oacute; thời điểm bật tăng nhưng lực cầu chưa c&oacute; sự chuyển biến t&iacute;ch cực, t&igrave;nh trạng mua thấp &ndash; b&aacute;n cao tiếp tục h&atilde;m lại đ&agrave; tăng của chỉ số. H&agrave;ng loạt m&atilde; giảm gi&aacute; đi k&egrave;m với tốc độ khớp lệnh chậm chạp đẩy thị trường v&agrave;o xu thế mất điểm.</p>\r\n<p class="Normal">Ngay trước 10 giờ, Vn-Index ch&iacute;nh thức chuyển sang xu thế giảm điểm. Mức giảm t&iacute;nh đến cuối phi&ecirc;n l&agrave; 4,76 điểm đưa chỉ số của s&agrave;n TP HCM về 513,11 điểm. Khối lượng giao dịch tr&ecirc;n s&agrave;n cũng giảm tr&ecirc;n 12% so với phi&ecirc;n trước với khoảng 30 triệu chứng kho&aacute;n được chuyển nhượng, tương đương 748,54 tỷ đồng.</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội,&nbsp;<strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;cũng giảm 1,16 điểm, về 105,49 điểm. Khối lượng giao dịch tr&ecirc;n s&agrave;n đạt chưa đầy 20 triệu chứng kho&aacute;n, tương đương 371,21 tỷ đồng. Khối ngoại tiếp tục b&aacute;n r&ograve;ng phi&ecirc;n thứ 2 li&ecirc;n tiếp tr&ecirc;n s&agrave;n n&agrave;y với gi&aacute; trị hơn 4,4 tỷ đồng. Trong khi đ&oacute;,&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;đứng y&ecirc;n ở 41,39 điểm trong phi&ecirc;n giao dịch s&aacute;ng nay với gần 210.000 cổ phiếu được chuyển nhượng, tương đương 4,28 tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 538, 0, 1),
(371, 'Vn-Index mất điểm phiên thứ 3 liên tiếp', 'Xu hướng điều chỉnh của thị trường chứng khoán tiếp tục nối dài trong phiên giao dịch sáng nay khi các chỉ số đồng loạt giảm điểm. Tuy nhiên, lực cầu bắt đáy có dấu hiệu tăng cao, đặc biệt là trên sàn Hà Nội.', 'chung-khoan-0 (1).jpg', NULL, '<div class="folder-header">\r\n<div class="parentfolder-title fl"><span style="font-size: 20px; font-weight: bold;">Vn-Index mất điểm phi&ecirc;n thứ 3 li&ecirc;n tiếp</span></div>\r\n</div>\r\n<div class="content">\r\n<div>\r\n<h2 class="Lead">Xu hướng điều chỉnh của thị trường chứng kho&aacute;n tiếp tục nối d&agrave;i trong phi&ecirc;n giao dịch s&aacute;ng nay khi c&aacute;c chỉ số đồng loạt giảm điểm. Tuy nhi&ecirc;n, lực cầu bắt đ&aacute;y c&oacute; dấu hiệu tăng cao, đặc biệt l&agrave; tr&ecirc;n s&agrave;n H&agrave; Nội.<br /><span style="font-size: 10px; font-weight: normal;">Những diễn biến tr&ecirc;n thị trường ngoại tệ sau quyết định điều chỉnh tỷ gi&aacute; của Ng&acirc;n h&agrave;ng Nh&agrave; nước được coi l&agrave; th&ocirc;ng tin quan trọng nhất t&aacute;c động l&ecirc;n giao dịch chứng kho&aacute;n trong tuần n&agrave;y. Theo đ&oacute;, tuy được đ&aacute;nh gi&aacute; l&agrave; thuận chiều, g&oacute;p phần khơi th&ocirc;ng luồng vốn ngoại, nhưng điều chỉnh về tỷ gi&aacute;, trước mắt, sẽ khiến c&aacute;c nh&agrave; đầu tư chứng kho&aacute;n trở n&ecirc;n thận trọng hơn khi quan s&aacute;t c&aacute;c diễn biến tr&ecirc;n thị trường.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Chứng kho&aacute;n tiếp tục xu hướng điều chỉnh. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chứng kho&aacute;n tiếp tục xu hướng điều chỉnh. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhận định n&agrave;y của c&aacute;c c&ocirc;ng ty chứng kho&aacute;n tỏ ra kh&aacute; ch&iacute;nh x&aacute;c trong những ph&uacute;t giao dịch đầu phi&ecirc;n s&aacute;ng nay, khi Vn-Index tăng nhẹ 0,75 điểm, l&ecirc;n 520,73 điểm nhưng khối lượng giao dịch đợt một lại giảm đ&aacute;ng kể. To&agrave;n s&agrave;n TP HCM, trong 15 ph&uacute;t đầu phi&ecirc;n chỉ ghi nhận được chưa đầy một triệu chứng kho&aacute;n được chuyển nhượng, tương đương 25,31 tỷ đồng.</p>\r\n<p class="Normal">Hiện một số blue-chip như BVH, VNM, DPM&hellip; được giải ng&acirc;n thận trọng. Dẫn đầu về giao dịch tr&ecirc;n s&agrave;n l&uacute;c n&agrave;y l&agrave; m&atilde; IJC với hơn 90.000 cổ phiếu được sang tay. Tiếp đ&oacute; l&agrave; c&aacute;c m&atilde; ITC, VMD, KSH, TDC&hellip;</p>\r\n<p class="Normal">Bước sang đợt 2, Vn-Index c&oacute; xu hướng đi ngang. Một loạt blue-chip chuyển sang chỉ b&aacute;o đỏ trong đ&oacute; BVH giảm 2.000 đồng một cổ phiếu, PVD giảm 1.000 đồng trong khi VCB cũng mất 700 đồng trị gi&aacute;. Trong khi đ&oacute; DPM, FPT, GMD, QCG, REE, SSI&hellip; chững lại ở tham chiếu, chỉ c&oacute; MSN v&agrave; VIC bật xanh.</p>\r\n<p class="Normal">Sau 10h, Vn-Index ch&iacute;nh thức khẳng định xu hướng giảm điểm. Chỉ số n&agrave;y kết phi&ecirc;n ở 514,05 điểm, giảm gần 6 điểm so với phi&ecirc;n trước. Tuy vậy, gi&aacute; trị giao dịch tr&ecirc;n s&agrave;n tăng nhẹ l&ecirc;n gần 35,5 triệu chứng kho&aacute;n, tương đương 884,07 tỷ đồng. Đ&acirc;y l&agrave; phi&ecirc;n thứ ba li&ecirc;n tiếp Vn-Index giảm điểm.</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội, thanh khoản thậm ch&iacute; c&ograve;n tăng mạnh hơn với gần 25,7 triệu chứng kho&aacute;n được chuyển nhượng, tăng hơn 30% so với phi&ecirc;n trước. Tuy vậy,<strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;cũng đảo chiều giảm 0,43 điểm, xuống 107,38 điểm.</p>\r\n<p class="Normal">Kết th&uacute;c giao dịch buổi s&aacute;ng,&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;giảm nhẹ 0,11 điểm, xuống 41,53 điểm. Khối lượng giao dịch trong phi&ecirc;n đạt 156.476 cổ phiếu, tương đương 1,65 tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>\r\n</div>\r\n</div>', 9, 3, 1367, 0, 1),
(372, 'Đầu năm, chứng khoán khởi động chậm', 'Trong tuần giao dịch đầu tiên của năm Tân Mão, khối lượng cổ phiếu được chuyển nhượng trên 2 sàn Hà Nội và TP HCM đồng loạt giảm khoảng 7%. Index tuy vẫn tăng nhưng chủ yếu dựa vào lực đẩy từ các cổ phiếu lớn.', 'chung-khoan-0 (2).jpg', NULL, '<h1 class="Title">Đầu năm, chứng kho&aacute;n khởi động chậm</h1>\r\n<h2 class="Lead">Trong tuần giao dịch đầu ti&ecirc;n của năm T&acirc;n M&atilde;o, khối lượng cổ phiếu được chuyển nhượng tr&ecirc;n 2 s&agrave;n H&agrave; Nội v&agrave; TP HCM đồng loạt giảm khoảng 7%. Index tuy vẫn tăng nhưng chủ yếu dựa v&agrave;o lực đẩy từ c&aacute;c cổ phiếu lớn.<br /><span style="font-size: 10px; font-weight: normal;">Kết th&uacute;c tuần giao dịch 8-11/2, Vn-Index tăng 9,38 điểm, l&ecirc;n 519,98 điểm. Trong khi đ&oacute;, mức tăng của HNX-Index tr&ecirc;n s&agrave;n H&agrave; Nội tỏ ra khi&ecirc;m tốn hơn, chỉ khoảng 1,2 điểm. Chỉ số n&agrave;y kết tuần ở 107,81 điểm.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Giao dịch chứng kho&aacute;n giảm nhẹ tr&ecirc;n 2 s&agrave;n trong tuần đầu năm. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Giao dịch chứng kho&aacute;n giảm nhẹ tr&ecirc;n 2 s&agrave;n trong tuần đầu năm. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Diễn biến của c&aacute;c chỉ số tr&ecirc;n 2 s&agrave;n trong tuần qua tiếp tục bị chi phối mạnh bởi c&aacute;c m&atilde; c&oacute; vốn h&oacute;a lớn, tương tự giai đoạn trước Tết &Acirc;m lịch. Biểu hiện r&otilde; nhất được ghi nhận tr&ecirc;n s&agrave;n TP HCM khi phần lớn điểm số m&agrave; Vn-Index t&iacute;ch lũy được đều đến từ phi&ecirc;n giao dịch đầu năm, khi m&agrave; c&aacute;c blue-chip như BVH, CTG, PVF, VCB&hellip;&nbsp;<a class="Normal" href="http://vnexpress.net/gl/kinh-doanh/chung-khoan/2011/02/3ba262c1/">đồng loạt dựng trần</a>. Đến khi c&aacute;c m&atilde; n&agrave;y tỏ ra &ldquo;đuối sức&rdquo;, HOSE lại ghi nhận 2 phi&ecirc;n điều chỉnh v&agrave;o cuối tuần.</p>\r\n<p class="Normal">Trong khi c&aacute;c m&atilde; blue-chip tiếp tục khiến Index biến động tương đối mạnh th&igrave; cổ phiếu c&oacute; vốn h&oacute;a trung b&igrave;nh v&agrave; penny-stock giao dịch kh&ocirc;ng thực sự tốt. S&agrave;n TP HCM ghi nhận trung b&igrave;nh khoảng 31 triệu chứng kho&aacute;n (810 tỷ đồng) được chuyển nhượng mỗi phi&ecirc;n. Trong khi đ&oacute;, con số n&agrave;y tr&ecirc;n s&agrave;n H&agrave; Nội l&agrave; gần 20 triệu chứng kho&aacute;n (383 tỷ đồng). Cả 2 con số n&agrave;y đều giảm khoảng 7% so với tuần trước.</p>\r\n<p class="Normal">Cũng trong tuần đầu giao dịch đầu năm, khối ngoại tiếp tục thực hiện mua r&ograve;ng tr&ecirc;n cả 2 s&agrave;n với khối lượng l&ecirc;n tới hơn 220 tỷ đồng. C&aacute;c blue-chip tr&ecirc;n s&agrave;n TP HCM vẫn được nh&agrave; đầu tư nước ngo&agrave;i giải ng&acirc;n mạnh như VCB, DPM, ITC, PVD, PVF&hellip; Trong khi đ&oacute;, tại s&agrave;n H&agrave; Nội, khối n&agrave;y mua mạnh VND, PVC, PVI v&agrave; VCG.</p>\r\n<p class="Normal">Trước t&igrave;nh trạng c&aacute;c chỉ số bị m&eacute;o m&oacute; do t&aacute;c động của c&aacute;c cổ phiếu vốn h&oacute;a lớn, trong tuần qua, Ủy ban Chứng kho&aacute;n đ&atilde; c&oacute; văn bản&nbsp;y&ecirc;u cầu&nbsp;c&aacute;c Sở giao dịch t&igrave;m biện ph&aacute;p khắc phục. Phương &aacute;n được t&iacute;nh tới nhiều nhất l&agrave; việc x&acirc;y dựng một bộ chỉ số phụ, phản &aacute;nh ch&iacute;nh x&aacute;c hơn diễn biến giao dịch tr&ecirc;n thị trường, b&ecirc;n cạnh 2 chỉ số Vn-Index v&agrave; HNX-Index hiện tại.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 2372, 0, 1),
(373, 'Xem xét xây dựng chỉ số phụ cho 2 sàn chứng khoán', 'Ủy ban Chứng khoán Nhà nước (SSC) vừa có văn bản yêu cầu 2 Sở giao dịch chứng khoán Hà Nội và TP HCM tìm biện pháp khắc phục tình trạng "méo mó" của Index do tác động từ các cổ phiếu có vốn hóa lớn trên thị trường.', 'chung-khoan-0_1.jpg', NULL, '<h1 class="Title">Xem x&eacute;t x&acirc;y dựng chỉ số phụ cho 2 s&agrave;n chứng kho&aacute;n</h1>\r\n<h2 class="Lead">Ủy ban Chứng kho&aacute;n Nh&agrave; nước (SSC) vừa c&oacute; văn bản y&ecirc;u cầu 2 Sở giao dịch chứng kho&aacute;n H&agrave; Nội v&agrave; TP HCM t&igrave;m biện ph&aacute;p khắc phục t&igrave;nh trạng "m&eacute;o m&oacute;" của Index do t&aacute;c động từ c&aacute;c cổ phiếu c&oacute; vốn h&oacute;a lớn tr&ecirc;n thị trường.<br /><span style="font-size: 10px; font-weight: normal;">Theo Ủy ban Chứng kho&aacute;n, việc gi&aacute;m s&aacute;t giao dịch thời gian qua cho thấy Vn-Index v&agrave; HNX-Index thường bị t&aacute;c động bởi một số cổ phiếu c&oacute; khối lượng ni&ecirc;m yết lớn. Những m&atilde; chứng kho&aacute;n n&agrave;y c&oacute; khả năng t&aacute;c động đến xu hướng của c&aacute;c chỉ số nhưng lại c&oacute; lượng giao dịch kh&ocirc;ng cao. Thực trạng n&agrave;y khiến c&aacute;c chỉ số kh&ocirc;ng phản &aacute;nh đ&uacute;ng thực tế tr&ecirc;n thị trường, ảnh hưởng đến t&acirc;m l&yacute; nh&agrave; đầu tư.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Khả năng đươc t&iacute;nh đến nhiều nhất l&agrave; x&acirc;y dựng chỉ số phụ cho 2 s&agrave;n. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khả năng đươc t&iacute;nh đến nhiều nhất l&agrave; x&acirc;y dựng chỉ số phụ cho 2 s&agrave;n. Ảnh minh họa: Ho&agrave;ng H&agrave;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trước thực tế n&agrave;y, Ủy ban Chứng kho&aacute;n Nh&agrave; nước đ&atilde; c&oacute; văn bản y&ecirc;u cầu 2 Sở giao dịch nghi&ecirc;n cứu c&aacute;c giải ph&aacute;p khắc phục t&igrave;nh trạng nh&agrave; "đ&aacute;nh m&eacute;o" chỉ số th&ocirc;ng qua c&aacute;c m&atilde; c&oacute; vốn h&oacute;a lớn.</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>VnExpress.net</em>&nbsp;s&aacute;ng nay, &ocirc;ng Nguyễn Sơn, Vụ trưởng Vụ Ph&aacute;t triển thị trường (SSC) cho rằng một trong những giải ph&aacute;p được c&acirc;n nhắc nhiều nhất tại thời điểm hiện tại l&agrave; x&acirc;y dựng chỉ số phụ (c&oacute; khả năng phản &aacute;nh ch&iacute;nh x&aacute;c hơn diễn biến giao dịch) cho 2 s&agrave;n.</p>\r\n<p class="Normal">Ngo&agrave;i ra, một giải ph&aacute;p kh&aacute;c cũng được t&iacute;nh tới l&agrave; thay đổi c&ocirc;ng thức t&iacute;nh c&aacute;c chỉ số hiện tại, trong đ&oacute; chỉ đưa c&aacute;c cổ phiếu được chuyển nhượng tự do (thay v&igrave; to&agrave;n bộ khối lượng ni&ecirc;m yết) v&agrave;o rổ cổ phiếu t&iacute;nh Index.</p>\r\n<p class="Normal">Về ph&iacute;a c&aacute;c Sở Giao dịch, &ocirc;ng L&ecirc; Hải Tr&agrave;, Ủy vi&ecirc;n thường trực Hội đồng quản trị Sở giao dịch chứng kho&aacute;n TP HCM cho biết, trong thời gian tới, Sở sẽ họp b&agrave;n t&igrave;m giải ph&aacute;p khắc phục t&igrave;nh trạng n&oacute;i tr&ecirc;n.</p>\r\n<p class="Normal">Dựa tr&ecirc;n kinh nghiệm thực tế ở c&aacute;c thị trường kh&aacute;c, HSX sẽ c&oacute; những điều chỉnh th&iacute;ch hợp nhất trong c&aacute;ch t&iacute;nh chỉ số, phản &aacute;nh trung thực hơn hoạt động giao dịch chứng kho&aacute;n v&agrave; sẽ c&ocirc;ng bố ra thị trường trong thời gian tới.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh - Bạch Hường</strong></p>', 9, 3, 2595, 0, 1),
(374, 'Sóng’ blue-chip', 'Trong nhiều phiên giao dịch gần đây, một số cổ phiếu lớn như BVH, MSN, HAG, CTG… tăng giá rất mạnh đẩy Vn-Index từ 480 điểm vượt 520 điểm. Những blue-chip đang tạo ra "sóng" trên thị trường.', 'song-bluechip.jpg', NULL, '<h1 class="Title">S&oacute;ng&rsquo; blue-chip</h1>\r\n<h2 class="Lead">Trong nhiều phi&ecirc;n giao dịch gần đ&acirc;y, một số cổ phiếu lớn như BVH, MSN, HAG, CTG&hellip; tăng gi&aacute; rất mạnh đẩy Vn-Index từ 480 điểm vượt 520 điểm. Những blue-chip đang tạo ra "s&oacute;ng" tr&ecirc;n thị trường.<br /><span style="font-size: 10px; font-weight: normal;">Li&ecirc;n tục trong nhiều phi&ecirc;n, BVH v&agrave; MSN l&agrave; 2 cổ phiếu lớn tạo ra những động lực tăng điểm rất mạnh cho Vn-Index. Ngay cả trong những phi&ecirc;n nhiều chứng kho&aacute;n kh&aacute;c giảm gi&aacute;, 2 blue-chip n&agrave;y vẫn tăng trần gi&uacute;p chỉ số s&agrave;n TP HCM l&ecirc;n điểm. Một số cổ phiếu lớn kh&aacute;c cũng theo đ&agrave; tăng gi&aacute; như DPM, CTG, HAG, VCB, PVD&hellip;</span></h2>\r\n<p class="Normal">Trong phi&ecirc;n ng&agrave;y 9/2, bất chấp việc giảm gi&aacute; của nhiều m&atilde; chứng kho&aacute;n, 3 cổ phiếu lớn gồm BVH, MSN v&agrave; CTG tiếp tục tăng gần kịch trần gi&uacute;p Vn-Index tăng gần 2 điểm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="song-bluechip.jpg" border="1" alt="BVH v&agrave; MSN l&agrave; 2 cổ phiếu đẩy Vn-Index mạnh nhất. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="480" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">BVH v&agrave; MSN l&agrave; 2 cổ phiếu đẩy Vn-Index mạnh nhất. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kể từ giữa th&aacute;ng 1 đến 9/2, Vn-Index đ&atilde; c&oacute; th&ecirc;m 40 điểm &ndash; mức tăng rất ấn tượng trong nhiều th&aacute;ng trở lại đ&acirc;y. Đi k&egrave;m với đ&oacute;, rất nhiều cổ phiếu lớn cả những m&atilde; c&oacute; kết quả kinh doanh tốt, kh&aacute;, lẫn trung b&igrave;nh đều lần lượt l&ecirc;n gi&aacute; khiến cho nh&agrave; đầu tư nghĩ tới những đợt s&oacute;ng của blue-chip.</p>\r\n<p class="Normal">Một m&ocirc;i giới nổi tiếng trong giới chứng kho&aacute;n, người chuy&ecirc;n đ&aacute;nh penny trong năm 2010, cũng đưa ra tư vấn cho kh&aacute;ch h&agrave;ng n&ecirc;n mua blue-chip trong nửa đầu năm 2011. Anh n&agrave;y thậm ch&iacute; c&ograve;n dự b&aacute;o về mức tăng trưởng n&oacute;ng tới 50-60% của Vn-Index nhờ d&ograve;ng tiền đổ v&agrave;o cổ phiếu lớn đến từ nh&agrave; đầu tư tổ chức trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class="Normal">Trong khi đ&oacute;, &ocirc;ng Nguyễn Quang Bảo &ndash; Ph&oacute; tổng gi&aacute;m đốc C&ocirc;ng ty chứng kho&aacute;n Bản Việt nhận x&eacute;t, đ&agrave; tăng của blue-chip giữ cho chỉ số s&agrave;n TP HCM tăng ổn định nhưng kh&oacute; c&oacute; đột biến. &ldquo;C&aacute;c cổ phiếu lớn tăng ở mức từ từ v&agrave; sẽ c&oacute; qu&atilde;ng nghỉ chứ kh&ocirc;ng l&ecirc;n li&ecirc;n tục như kiểu penny hồi đầu năm 2010. C&ograve;n trường hợp đặc biệt như BVH, MSN l&agrave; chuyện kh&aacute;c&rdquo;, &ocirc;ng n&agrave;y n&oacute;i.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, chuy&ecirc;n gia của Bản Việt khẳng định, gi&aacute; tăng do blue-chip l&agrave; xuất ph&aacute;t từ nhu cầu thật chứ kh&ocirc;ng thể c&oacute; chuyện l&agrave;m gi&aacute; bởi số lượng cổ phiếu lưu h&agrave;nh với c&aacute;c m&atilde; n&agrave;y qu&aacute; lớn. &Ocirc;ng Bảo đưa ra dự b&aacute;o, khi &ldquo;s&oacute;ng&rdquo; blue-chip giảm th&igrave; c&aacute;c penny sẽ bắt đầu tăng nhưng c&ograve;n t&ugrave;y thuộc v&agrave;o t&igrave;nh h&igrave;nh cụ thể của c&aacute;c biến số vĩ m&ocirc; thời điểm đ&oacute;.</p>\r\n<p class="Normal">Trong khi đ&oacute;, l&atilde;nh đạo cấp cao của một c&ocirc;ng ty chứng kho&aacute;n n&oacute;i thẳng: &ldquo;S&oacute;ng blue-chip c&oacute; yếu tố &lsquo;ảo&rsquo; bắt nguồn từ 2 cổ phiếu t&aacute;c động mạnh đến Vn-Index l&agrave; BVH v&agrave; MSN&rdquo;. &Ocirc;ng n&agrave;y cho biết, lượng cổ phiếu lưu h&agrave;nh thực sự của 2 m&atilde; chứng kho&aacute;n tr&ecirc;n kh&ocirc;ng nhiều n&ecirc;n những nh&agrave; đầu tư lớn về mặt bản chất c&oacute; thể kiểm so&aacute;t được việc tăng gi&aacute;.</p>\r\n<p class="Normal">Trao đổi với&nbsp;<em>VnExpress.net</em>, &ocirc;ng Ho&agrave;ng Xu&acirc;n Quyến &ndash; Tổng gi&aacute;m đốc C&ocirc;ng ty chứng kho&aacute;n Li&ecirc;n Việt cho rằng, thị trường đang m&eacute;o m&oacute; bởi một v&agrave;i cổ phiếu lớn, với &ldquo;b&agrave;n tay&rdquo; của c&aacute;c tổ chức nước ngo&agrave;i. &Ocirc;ng n&agrave;y cho rằng, "s&oacute;ng" với một số blue-chip l&agrave; kh&ocirc;ng thực chất v&agrave; Vn-Index l&ecirc;n nhanh th&igrave; cũng c&oacute; thể xuống nhanh. &ldquo;Tr&ecirc;n thực tế, d&ograve;ng tiền v&agrave;o thị trường vẫn chưa nhiều, biểu hiện ở mức thanh khoản thấp tr&ecirc;n thị trường&rdquo;, &ocirc;ng Quyến n&oacute;i.</p>\r\n<p class="Normal">C&ograve;n &ocirc;ng Nhữ Đ&igrave;nh H&ograve;a, Tổng gi&aacute;m đốc C&ocirc;ng ty chứng kho&aacute;n Bảo Việt cho rằng, một số blue-chip được nh&agrave; đầu tư nước ngo&agrave;i quan t&acirc;m đều l&agrave; những m&atilde; c&oacute; kết quả kinh doanh tốt hoặc c&oacute; những yếu tố đặc biệt. Th&ecirc;m v&agrave;o đ&oacute;, x&eacute;t ở g&oacute;c độ c&aacute;c chỉ số th&igrave; mức tăng của nhiều blue-chip hiện nay chưa phải đột biến v&agrave; cũng c&oacute; cơ sở nhất định. Chuy&ecirc;n gia n&agrave;y chỉ lưu &yacute;: &ldquo;Trong khi c&aacute;c blue-chip đang gi&uacute;p Vn-Index đang ở mức tr&ecirc;n 520 điểm th&igrave; nhiều cổ phiếu cỡ vừa v&agrave; nhỏ c&oacute; gi&aacute; ở ngưỡng chỉ số 450 điểm, thậm ch&iacute; thấp hơn&rdquo;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Ly</strong></p>', 9, 3, 1703, 0, 1),
(375, 'Đảo chiều giữa phiên, Vn-Index về sát 520 điểm', 'Sự đuối sức của các blue-chip khiến Vn-Index và HNX-Index không giữ được đà tăng điểm kéo dài từ trước Tết. Giá trị giao dịch trên 2 sàn cũng giảm mạnh so với phiên trước.', 'chung-khoan-0 (1)_1.jpg', NULL, '<h1 class="Title">Đảo chiều giữa phi&ecirc;n, Vn-Index về s&aacute;t 520 điểm</h1>\r\n<h2 class="Lead">Sự đuối sức của c&aacute;c blue-chip khiến Vn-Index v&agrave; HNX-Index kh&ocirc;ng giữ được đ&agrave; tăng điểm k&eacute;o d&agrave;i từ trước Tết. Gi&aacute; trị giao dịch tr&ecirc;n 2 s&agrave;n cũng giảm mạnh so với phi&ecirc;n trước.</h2>\r\n<p class="Normal">Sau 4-5 phi&ecirc;n tăng điểm li&ecirc;n tiếp, nhiều cổ phiếu, đặc biệt l&agrave; c&aacute;c blue-chip đ&atilde; về t&agrave;i khoản v&agrave; đi v&agrave;o v&ugrave;ng gi&aacute; c&oacute; lợi cho nh&agrave; đầu tư. Ch&iacute;nh v&igrave; vậy, hoạt động chốt lời đ&atilde; được đẩy mạnh trong phi&ecirc;n giao dịch s&aacute;ng nay.</p>\r\n<p class="Normal">Mở phi&ecirc;n tr&ecirc;n s&agrave;n TP HCM, Vn-Index vẫn tăng nhẹ 0,66 điểm l&ecirc;n 523,25 điểm với gần 1,6 triệu chứng kho&aacute;n (tương đương 37,46 tỷ đồng) được chuyển nhượng. Tại thời điểm n&agrave;y, BVH v&agrave; một số cổ phiếu bất động sản như TDC, IJC&hellip; vẫn tăng trần. Tuy vậy, trong số gần 130 m&atilde; c&oacute; giao dịch v&agrave;o thời điểm n&agrave;y, lượng giảm gi&aacute; l&ecirc;n tới hơn 60.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Vn-Index điều chỉnh sau 4 phi&ecirc;n tăng li&ecirc;n tiếp." width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vn-Index điều chỉnh sau 4 phi&ecirc;n tăng li&ecirc;n tiếp.&nbsp;<em>Ảnh: N.M.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Bước sang khớp lệnh li&ecirc;n tục, s&agrave;n TP HCM vẫn giữ được đ&agrave; tăng điểm. Vn-Index thậm ch&iacute; đ&atilde; tăng hơn 3 điểm, vượt ngưỡng 526 điểm. Tuy nhi&ecirc;n, c&agrave;ng về cuối phi&ecirc;n, c&aacute;c blue-chip c&agrave;ng để mất đ&agrave; tăng gi&aacute;. BVH nhanh ch&oacute;ng rời mốc gi&aacute; trần v&agrave; quay về v&ugrave;ng giảm kh&ocirc;ng l&acirc;u sau đ&oacute;.</p>\r\n<p class="Normal">C&ugrave;ng với cổ phiếu Bảo Việt, HAG cũng giảm 500 đồng, FPT, MSN, SJS, VNM đứng gi&aacute; trong khi VIC giảm tới 2.000 đồng&hellip; Tại nh&oacute;m ng&acirc;n h&agrave;ng CTG, EIB, VCB v&agrave; STB đồng loạt bật chỉ b&aacute;o đỏ.</p>\r\n<p class="Normal">Blue-chip đuối sức khiến Vn-Index ch&iacute;nh thức quay đầu giảm điểm v&agrave;o trước 10 giờ. Chỉ số n&agrave;y dao động quanh v&ugrave;ng 520 điểm trước khi kết phi&ecirc;n ở 520,23 điểm, giảm 2,36 điểm. Khối lượng giao dịch tr&ecirc;n s&agrave;n TP HCM đạt hơn 31,5 triệu chứng kho&aacute;n, tương đương 796,36 tỷ đồng.</p>\r\n<p class="Normal">Tuy chỉ số điều chỉnh nhưng lực cầu đối với c&aacute;c cổ phiếu lớn vẫn kh&aacute; mạnh. ITA giao dịch được gần 4,6 triệu chứng kho&aacute;n trong khi lượng khớp với REE v&agrave; SSI cũng đạt tr&ecirc;n một triệu.</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội,&nbsp;<strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;giảm 0,72 điểm, xuống 107,67 điểm. Tuy nhi&ecirc;n, khối lượng giao dịch tr&ecirc;n s&agrave;n giảm hơn 10 triệu chứng kho&aacute;n so với phi&ecirc;n trước khi chỉ c&oacute; 17,47 triệu cổ phiếu, tr&aacute;i phiếu được chuyển nhượng, tương đương 340,49 tỷ đồng.&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;cũng giảm nhẹ 0,24 điểm, xuống 42,22 điểm trong buổi s&aacute;ng với gần 98.000 cổ phiếu (khoảng 940 triệu đồng) được giao dịch.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 1701, 0, 1),
(376, 'Vn-Index tăng nhẹ, giao dịch khởi sắc', 'Không còn giữ mức tăng 8 điểm như đợt đầu, chỉ số sàn TP HCM đuối sức dần từ giữa đợt khớp lệnh liên tục và đến cuối phiên chỉ còn tăng 1,9 điểm, khi hàng loạt cổ phiếu vốn hóa lớn đột ngột quay đầu đi xuống.', 'ck-490x322.jpg', NULL, '<h1 class="Title">Vn-Index tăng nhẹ, giao dịch khởi sắc</h1>\r\n<h2 class="Lead">Kh&ocirc;ng c&ograve;n giữ mức tăng 8 điểm như đợt đầu, chỉ số s&agrave;n TP HCM đuối sức dần từ giữa đợt khớp lệnh li&ecirc;n tục v&agrave; đến cuối phi&ecirc;n chỉ c&ograve;n tăng 1,9 điểm, khi h&agrave;ng loạt cổ phiếu vốn h&oacute;a lớn đột ngột quay đầu đi xuống.</h2>\r\n<p class="Normal">H&agrave;ng loạt cổ phiếu vốn h&oacute;a lớn t&iacute;ch lũy th&ecirc;m điểm gi&uacute;p Vn-Index tiến một mạch 8 điểm sau đợt đầu để tiến tới chinh phục mốc tiếp theo ở 530 điểm.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, diễn biến thị trường từ giữa đợt 2 kh&ocirc;ng khả quan, khi chứng kiến nhiều blue-chip "ng&atilde; ngựa". Nếu ở đầu phi&ecirc;n, DPM, FPT, ITA, SJS... c&ograve;n t&iacute;ch lũy 0,2-1 điểm th&igrave; gần cuối đợt khớp lệnh li&ecirc;n tục đ&atilde; đồng loạt đi xuống, với mức giảm dao động 0,3-0,7 điểm.</p>\r\n<p class="Normal">CTG kh&ocirc;ng c&ograve;n dư mua gi&aacute; trần tr&ecirc;n 600 ngh&igrave;n như đầu đợt 2, m&agrave; c&aacute;c mức gi&aacute; chỉ c&ograve;n tr&ecirc;n tham chiếu một ch&uacute;t v&agrave;o l&uacute;c đ&oacute;ng cửa. Đến cuối phi&ecirc;n, BVH chỉ c&oacute; gần 3.000 cổ phiếu chờ khớp gi&aacute; trần, chứ kh&ocirc;ng phải tr&ecirc;n 100 ngh&igrave;n như ban đầu. SSI c&ugrave;ng STB đứng ở gi&aacute; tham chiếu, thay v&igrave; được cộng th&ecirc;m 0,2-0,6 điểm như diễn biến đầu đợt 2.</p>\r\n<p class="Normal">Cầu gi&aacute; thấp tăng dần trong phi&ecirc;n, khiến số cổ phiếu giảm điểm nh&iacute;ch dần l&ecirc;n. Diễn biến n&agrave;y tr&aacute;i ngược với h&ocirc;m trước, khi số nh&agrave; đầu tư chịu mua gi&aacute; cao xuất hiện nhiều hơn đ&atilde; gi&uacute;p Vn-Index tăng 10,09 điểm.</p>\r\n<p class="Normal">Chung cuộc s&aacute;ng nay, s&agrave;n TP HCM c&oacute; hơn 160 m&atilde; giảm, gấp 2,4 lần lượng cổ phiếu tăng điểm. Tuy nhi&ecirc;n, nhờ lực đỡ từ c&aacute;c trụ cột n&ecirc;n Vn-Index v&agrave;o cuối ng&agrave;y vẫn giữ mức tăng nhẹ 1,9 điểm, chốt tại 522,59, đ&aacute;nh dấu phi&ecirc;n tăng thứ tư li&ecirc;n tiếp, t&iacute;nh từ ng&agrave;y 27/1 tới nay.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ck-490x322.jpg" border="1" alt="" width="490" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c chỉ số tăng nhẹ, thanh khoản khởi sắc so với phi&ecirc;n trước. Ảnh:<em>&nbsp;B.H.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chưa đạt tới mốc 1.000 tỷ đồng như giai đoạn trước Tết &acirc;m lịch, nhưng thanh khoản s&agrave;n TP HCM s&aacute;ng nay vẫn tăng hơn 20% so với h&ocirc;m qua, tương ứng với 31,7 triệu chứng kho&aacute;n, trị gi&aacute; 857,6 tỷ đồng. Giao dịch thỏa thuận đạt một triệu chứng kho&aacute;n, tương đương 31,8 tỷ đồng.</p>\r\n<p class="Normal">C&oacute; 6 m&atilde; đạt giao dịch tr&ecirc;n một triệu cổ phiếu l&agrave;: CTG, REE, SSI, ITA, DPM v&agrave; VCB. Nh&agrave; đầu tư nước ngo&agrave;i mua chủ yếu c&aacute;c blue-chip: DPM, HAG, HPG, ITA, PVD, REE, SJS, VCB, VIC, với số lượng đều tr&ecirc;n 100 ngh&igrave;n ở mỗi m&atilde;. H&ocirc;m qua, khối n&agrave;y mua r&ograve;ng 82,7 tỷ đồng.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;cũng l&ecirc;n nhẹ 0,73 điểm trong phi&ecirc;n s&aacute;ng nay, chốt tại 108,39 điểm. S&agrave;n H&agrave; Nội ghi nhận 27,6 triệu chứng kho&aacute;n chuyển nhượng, ứng với 543,4 tỷ đồng, tăng gần gấp đ&ocirc;i phi&ecirc;n trước. To&agrave;n s&agrave;n c&oacute; tr&ecirc;n 120 cổ phiếu tăng, trong khi số m&atilde; giảm gần 150.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;kết th&uacute;c buổi s&aacute;ng ở mức 42,09 điểm, giảm 0,59 điểm, đạt 0,37 triệu chứng kho&aacute;n chuyển nhượng, tương đương 2,38 tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Bạch Hường</strong></p>', 9, 3, 1424, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(377, 'Người ‘phải lòng’ nghề tư vấn chứng khoán độc lập', 'Chuyên gia chứng khoán Huy Nam là một người hiếm hoi trong giới tài chính hành nghề độc lập mà không làm việc toàn thời gian tại bất cứ tổ chức nào. Đầu xuân Tân Mão, ông Nam có cuộc trao đổi với VnExpress.net.', 'huy-nam-trao-bang-tot-nghie.jpg', NULL, '<h1 class="Title">Người &lsquo;phải l&ograve;ng&rsquo; nghề tư vấn chứng kho&aacute;n độc lập</h1>\n<h2 class="Lead">Chuy&ecirc;n gia chứng kho&aacute;n Huy Nam l&agrave; một người hiếm hoi trong giới t&agrave;i ch&iacute;nh h&agrave;nh nghề độc lập m&agrave; kh&ocirc;ng l&agrave;m việc to&agrave;n thời gian tại bất cứ tổ chức n&agrave;o. Đầu xu&acirc;n T&acirc;n M&atilde;o, &ocirc;ng Nam c&oacute; cuộc trao đổi với VnExpress.net.<br /><span style="font-size: 10px; font-weight: normal;"><em>- &Ocirc;ng bắt đầu với chứng kho&aacute;n thế n&agrave;o? V&igrave; sao &ocirc;ng kh&ocirc;ng l&agrave;m việc ch&iacute;nh thức trong một tổ chức t&agrave;i ch&iacute;nh n&agrave;o hoặc g&oacute;p vốn th&agrave;nh lập c&ocirc;ng ty chứng kho&aacute;n khi bản th&acirc;n rất am hiểu về thị trường n&agrave;y?</em></span></h2>\n<p class="Normal">- Sau năm 1990, khi c&oacute; Luật C&ocirc;ng ty, chủ trương cổ phần h&oacute;a v&agrave; những bước khởi động thị trường chứng kho&aacute;n, t&ocirc;i tham gia một số c&ocirc;ng ty, l&agrave; người khởi lập v&agrave; s&aacute;ng lập ra ba đơn vị lớn (Saigon Burotel, Nuti Food, Thi&ecirc;n Nam<span style="text-decoration: underline;">)</span>. Từ năm 1995, t&ocirc;i quyết định nghỉ bi&ecirc;n chế, ra l&agrave;m tư vấn độc lập cho c&aacute;c doanh nghiệp c&oacute; toan t&iacute;nh ph&aacute;t triển đ&oacute;n đầu. C&oacute; thể n&oacute;i t&ocirc;i l&agrave; một &ldquo;freelance&rdquo; rất sớm tại VN.</p>\n<p class="Normal">Với việc lập c&ocirc;ng ty chứng kho&aacute;n, c&oacute; lẽ cần c&oacute; điều kiện mới th&agrave;nh c&ocirc;ng chứ kh&ocirc;ng đơn giản. Thực ra t&ocirc;i c&oacute; nghĩ đến v&agrave;i lần, nhưng th&ocirc;i v&igrave; một số l&yacute; do, mặc d&ugrave; t&ocirc;i đ&atilde; tập hợp v&agrave; điều phối &ldquo;Nh&oacute;m Đầu tư Chứng kho&aacute;n S&agrave;i G&ograve;n&rdquo; đầu ti&ecirc;n từ cuối những năm 90. Gọi l&agrave; nh&oacute;m nhưng c&oacute; cả trăm anh em v&agrave; sau n&agrave;y họ l&agrave; những hạt nh&acirc;n t&iacute;ch cực của thị trường.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="huy-nam-1.jpg" border="1" alt="&Ocirc;ng Huy Nam coi việc l&agrave;m chuy&ecirc;n gia chứng kho&aacute;n độc lập như một c&aacute;i nghiệp. ANVCC" width="320" height="450" /></td>\n</tr>\n<tr>\n<td class="Image">&Ocirc;ng Huy Nam coi việc l&agrave;m chuy&ecirc;n gia chứng kho&aacute;n độc lập như một c&aacute;i nghiệp. ANVCC</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Do vậy, như c&aacute;i nghiệp, t&ocirc;i lại tiếp tục l&agrave;m một freelance chuy&ecirc;n nghiệp b&ecirc;n cạnh những c&ocirc;ng ty t&iacute;n nhiệm m&igrave;nh. T&ocirc;i đ&atilde; cộng t&aacute;c hiệu quả với họ, v&agrave; nhiều doanh nghiệp đ&atilde; gặt h&aacute;i được kết quả tốt, như SAM, REE, SAV, BT6&hellip; trước đ&acirc;y v&agrave; Nam Long Group, Trường Hải Group&hellip; gần đ&acirc;y. Ri&ecirc;ng việc kinh doanh chứng kho&aacute;n, t&ocirc;i c&oacute; chủ trương v&agrave; c&aacute;ch ri&ecirc;ng.<em>&nbsp;</em></p>\n<p class="Normal"><em>- Sau nhiều năm gắn b&oacute; với chứng kho&aacute;n, điều g&igrave; khiến &ocirc;ng t&acirc;m đắc nhất khi quyết định theo đuổi nghề chuy&ecirc;n gia tư vấn độc lập?</em></p>\n<p class="Normal">- Đ&oacute; l&agrave; sự thử th&aacute;ch kh&ocirc;ng ngừng v&agrave; việc l&agrave;m sao c&oacute; thể giữ được m&igrave;nh. Mặt kh&aacute;c, nhờ tư c&aacute;ch độc lập, t&ocirc;i c&oacute; thể c&oacute; c&aacute;c kiến giải thẳng thắn, kh&aacute;ch quan, v&igrave; t&ocirc;i l&agrave;m việc với ch&iacute;nh c&aacute;i đầu của m&igrave;nh chứ kh&ocirc;ng qua tay người kh&aacute;c. Một số trường hợp c&oacute; thể &yacute; kiến của t&ocirc;i kh&ocirc;ng được tiếp nhận ngay, nhưng &iacute;t l&acirc;u sau lại được đ&aacute;nh gi&aacute; cao, nhất l&agrave; với c&aacute;ch l&agrave;m qui ước hoặc nội dung c&oacute; t&iacute;nh nền tảng. Nhiều nội dung đ&oacute;ng g&oacute;p của t&ocirc;i đ&atilde; được đưa v&agrave;o c&aacute;c văn bản luật, như Luật Doanh Nghiệp.</p>\n<p class="Normal">C&ocirc;ng việc cũng buộc t&ocirc;i lu&ocirc;n s&aacute;ng tạo để c&oacute; c&aacute;c giải ph&aacute;p hữu &iacute;ch chuyển giao cho doanh nghiệp m&agrave; t&ocirc;i tư vấn/cố vấn. C&oacute; c&aacute;i đ&atilde; được doanh nghiệp cả nước sử dụng nhiều năm nay như mẫu &ldquo;Chứng nhận cổ đ&ocirc;ng&rdquo; sử dụng cho c&aacute;c doanh nghiệp t&ocirc;i tư vấn trước năm 2000 v&agrave; được c&ocirc;ng bố rộng r&atilde;i năm 2001</p>\n<p class="Normal"><em>- Anh c&oacute; thể kể một kỷ niệm vui với nghề chuy&ecirc;n gia tư vấn độc lập của m&igrave;nh?</em></p>\n<p class="Normal">- C&aacute;ch đ&acirc;y gần s&aacute;u năm, c&oacute; anh chủ tịch hội đồng quản trị ki&ecirc;m tổng gi&aacute;m đốc một đơn vị, tham gia với tư c&aacute;ch s&aacute;ng lập v&agrave;o qu&aacute; tr&igrave;nh th&agrave;nh lập một c&ocirc;ng ty kh&aacute;c do t&ocirc;i tư vấn. H&ocirc;m th&ocirc;ng qua điều lệ, do đến trễ v&agrave; chưa đọc kỹ, anh ấy đ&atilde; c&oacute; những lời lẽ kh&aacute; căng với t&ocirc;i về một nội dung li&ecirc;n quan đến cổ đ&ocirc;ng s&aacute;ng lập. L&uacute;c đ&oacute; t&ocirc;i v&agrave; anh chưa biết nhau. Biết anh n&oacute;ng vội n&ecirc;n t&ocirc;i cố kiềm chế. May l&agrave; ngay sau đ&oacute;, c&aacute;c anh s&aacute;ng lập kh&aacute;c đ&atilde; giải th&iacute;ch lại v&agrave; anh hiểu ra&hellip;</p>\n<p class="Normal">Bẵng đi v&agrave;i th&aacute;ng sau, thật bất ngờ, ch&iacute;nh vị chủ tịch ki&ecirc;m tổng gi&aacute;m đốc đ&oacute; đ&atilde; gọi điện một c&aacute;ch trọng thị mời t&ocirc;i về l&agrave;m cố vấn cho c&ocirc;ng ty anh. T&iacute;nh c&aacute;ch của anh như vậy l&agrave;m t&ocirc;i nể phục n&ecirc;n kh&ocirc;ng thể kh&ocirc;ng nhận lời. Sau đ&oacute;, t&ocirc;i gi&uacute;p anh hoạch định hướng đi chiến lược, dựng lộ tr&igrave;nh v&agrave; c&ugrave;ng anh v&agrave; bộ m&aacute;y c&ocirc;ng ty tiến h&agrave;nh c&aacute;c bước cấu tr&uacute;c lại c&ocirc;ng ty để sẵn s&agrave;ng đ&oacute;n nh&agrave; đầu tư v&agrave; ph&aacute;t triển lớn.</p>\n<p class="Normal">Kết quả l&agrave;, đến giữa năm 2008, khi cả nền kinh tế bế tắc thanh khoản, h&agrave;ng loạt doanh nghiệp l&acirc;m v&agrave;o t&igrave;nh trạng kh&aacute;t vốn, th&igrave; c&ocirc;ng ty của anh được hai đối t&aacute;c uy t&iacute;n nước ngo&agrave;i tham gia g&oacute;p vốn 30 triệu USD. T&ocirc;i chắc anh rất vui n&ecirc;n v&agrave;o một buổi chiều nọ, sau giờ l&agrave;m việc, anh mời t&ocirc;i v&agrave;o ph&ograve;ng v&agrave; n&oacute;i &ldquo;hiệu quả qu&aacute;, tốt qu&aacute;&rdquo; rồi bắt tay cảm ơn t&ocirc;i. Ch&uacute;t cảm x&uacute;c, t&ocirc;i đ&aacute;p lại &ldquo;chỉ l&agrave; phước chủ may thầy th&ocirc;i anh&rdquo;.</p>\n<p><em> </em></p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="huy-nam-trao-bang-tot-nghie.jpg" border="1" alt="&Ocirc;ng Huy Nam: " width="480" height="320" /></td>\n</tr>\n<tr>\n<td class="Image">&Ocirc;ng Huy Nam: "Chứng kho&aacute;n chỉ l&agrave; một phần trong c&ocirc;ng việc của t&ocirc;i". ANVCC</td>\n</tr>\n</tbody>\n</table>\n<p><em> </em></p>\n<p class="Normal"><em>- Nhiều năm l&agrave;m nghề tư vấn chứng kho&aacute;n độc lập m&agrave; kh&ocirc;ng gắn với bất kỳ tổ chức n&agrave;o, &ocirc;ng c&oacute; bao giờ bị người kh&aacute;c n&oacute;i rằng m&igrave;nh hơi kh&aacute;c người kh&ocirc;ng?</em></p>\n<p class="Normal">- Chưa bao giờ. Thực ra, chứng kho&aacute;n chỉ l&agrave; một mảng việc của t&ocirc;i. N&oacute; l&agrave; một phần hoạt động nghi&ecirc;n cứu v&agrave; l&agrave;m việc của t&ocirc;i trong lĩnh vực kinh tế t&agrave;i ch&iacute;nh. Gọi chuy&ecirc;n gia độc lập chỉ l&agrave; đặc điểm để ph&acirc;n biệt về tr&aacute;ch nhiệm, tư c&aacute;ch xử l&yacute; t&igrave;nh huống kh&aacute;ch quan, hay t&igrave;nh trạng kh&ocirc;ng li&ecirc;n lập khi h&agrave;nh sự đối với một số hoạt động n&agrave;o đ&oacute; th&ocirc;i, kể cả hoạt động của c&aacute;c doanh nghiệp.</p>\n<p class="Normal">C&ograve;n lại khi l&agrave;m, c&ocirc;ng việc của t&ocirc;i thường l&agrave; li&ecirc;n lập với bộ m&aacute;y của doanh nghiệp hay tổ chức. Vả lại, người ta gọi t&ocirc;i l&agrave; &ldquo;chuy&ecirc;n gia chứng kho&aacute;n&rdquo; c&oacute; lẽ chỉ l&agrave; c&aacute;ch nh&igrave;n nhận c&aacute;c hoạt động nổi bật của t&ocirc;i trong ng&agrave;nh chứng kho&aacute;n.</p>\n<p class="Normal"><em>- C&oacute; rất nhiều người đ&atilde; rời bỏ ng&agrave;nh chứng kho&aacute;n khi thị trường khủng hoảng nhưng &ocirc;ng th&igrave; vẫn trụ lại. V&agrave;o những thời điểm đ&oacute;, &ocirc;ng l&agrave;m g&igrave;?</em></p>\n<p class="Normal">- T&ocirc;i tập trung c&ocirc;ng việc kh&aacute;c, ph&acirc;n kh&uacute;c kh&aacute;c, d&agrave;nh nhiều thời gian cho mảng doanh nghiệp, giảng dạy, nghi&ecirc;n cứu viết s&aacute;ch, thực hiện c&aacute;c dự &aacute;n ri&ecirc;ng. Cũng nhờ vậy m&agrave; cuốn s&aacute;ch thứ mười của t&ocirc;i &ldquo;<em>T&agrave;i ch&iacute;nh chứng kho&aacute;n qua Nhịp cầu Anh Việt</em>&rdquo; mới ra đời, do Lạc Việt v&agrave; NXB TPHCM ấn h&agrave;nh cuối năm 2010. Những l&uacute;c rỗi việc đ&oacute; t&ocirc;i cũng tranh thủ đi chơi với gia đ&igrave;nh, thăm bạn b&egrave;, đi nước ngo&agrave;i&hellip;</p>\n<p class="Normal"><em>- L&agrave; một chuy&ecirc;n gia tư vấn độc lập, điều g&igrave; khiến &ocirc;ng t&acirc;m tư nhất sau nhiều năm gắn b&oacute; với thị trường chứng kho&aacute;n?</em></p>\n<p class="Normal">- L&agrave; người đ&atilde; đi c&ugrave;ng với thị trường ngay từ thời gian chuẩn bị, t&ocirc;i nếm trải buồn vui theo từng thăng trầm của thị trường. Thế nhưng biết nhiều, khổ nhiếu.</p>\n<p class="Normal">T&ocirc;i thấy x&oacute;t trước những c&aacute;ch nghĩ, c&aacute;ch h&agrave;nh xử c&ograve;n kh&aacute; &ldquo;lệ l&agrave;ng&rdquo; dễ l&agrave;m ngh&egrave;o thị trường, thấy sốt ruột trước thực trạng chậm chạp so với y&ecirc;u cầu cải thiện v&agrave; x&acirc;y dựng một thị trường như đ&aacute;ng lẽ đ&atilde; c&oacute;, để bảo vệ nh&agrave; đầu tư v&agrave; m&ocirc;i trường đầu tư&hellip;</p>\n<p class="Normal">Trong nhiều trường hợp, người l&agrave;m việc độc lập lại như người ngo&agrave;i, v&agrave; đ&acirc;y l&agrave; một t&igrave;nh tự kh&aacute;c. Đ&oacute; l&agrave; ưu tư của người biết r&otilde; nhưng bị đứng ngo&agrave;i, bức x&uacute;c nhưng kh&ocirc;ng thể tham gia s&acirc;u&hellip; D&ugrave; sao, với t&acirc;m huyết của người đ&atilde; v&agrave; đang đồng h&agrave;nh, t&ocirc;i tin v&agrave; mong thị trường sẽ nhanh được cải thiện, để chứng kho&aacute;n kh&ocirc;ng chỉ l&agrave; k&ecirc;nh dẫn vốn m&agrave; cần thiết sẽ l&agrave; m&ocirc;i trường đầu tư sinh lợi của đại ch&uacute;ng được bảo hiểm bởi niềm tin.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="90%" align="center" bgcolor="#cfcfcf">\n<tbody>\n<tr>\n<td>\n<p class="Normal"><strong>Huy Nam</strong></p>\n<p class="Normal">Chuy&ecirc;n gia tư vấn độc lập về Kinh tế T&agrave;i ch&iacute;nh v&agrave; Thị trường Chứng kho&aacute;n</p>\n<p class="Normal">Giảng vi&ecirc;n CFO &ndash; PFA &ndash; PACE</p>\n<p class="Normal">Trọng t&agrave;i vi&ecirc;n Trung t&acirc;m Trọng t&agrave;i Quốc tế Việt Nam</p>\n<p class="Normal"><strong>Kinh nghiệm l&agrave;m việc:</strong></p>\n<p class="Normal">S&aacute;ng lập v&agrave; l&agrave; Gi&aacute;m đốc điều h&agrave;nh Saigon Burotel. Gi&aacute;m đốc Trung t&acirc;m Mỹ nghệ S&agrave;i G&ograve;n. Chuy&ecirc;n vi&ecirc;n Đầu tư/ Gi&aacute;m đốc điều phối LD Larkhal TimeSquare. Th&agrave;nh vi&ecirc;n HĐQT/S&aacute;ng lập C&ocirc;ng ty Nuti-Food. Th&agrave;nh vi&ecirc;n HĐQT/S&aacute;ng lập/ Cố vấn C&ocirc;ng ty Thi&ecirc;n Nam. Th&agrave;nh vi&ecirc;n HĐQT C&ocirc;ng ty Nam Long DC. Chuy&ecirc;n gia tư vấn về t&aacute;i cấu tr&uacute;c, cổ phần h&oacute;a, chiến lược cho nhiều doanh nghiệp&hellip;</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;ng Ly</strong></p>', 9, 3, 2188, 0, 1),
(378, 'Blue-chip dựng trần phiên đầu xuân, Vn-Index vượt 520 điểm', 'Các cổ phiếu lớn như BVH, CTG, PVF, VCB… tăng trần đẩy Vn-Index lên hơn 10 điểm. Tuy vậy, giá trị giao dịch trên cả 2 sàn Hà Nội và TP HCM đều ở mức rất thấp.', 'chung-khoan-0 (2)_1.jpg', NULL, '<div class="folder-header">\r\n<div class="parentfolder-title fl"><span style="font-size: 20px; font-weight: bold;">Blue-chip dựng trần phi&ecirc;n đầu xu&acirc;n, Vn-Index vượt 520 điểm</span></div>\r\n</div>\r\n<div class="content">\r\n<div>\r\n<h2 class="Lead">C&aacute;c cổ phiếu lớn như BVH, CTG, PVF, VCB&hellip; tăng trần đẩy Vn-Index l&ecirc;n hơn 10 điểm. Tuy vậy, gi&aacute; trị giao dịch tr&ecirc;n cả 2 s&agrave;n H&agrave; Nội v&agrave; TP HCM đều ở mức rất thấp.</h2>\r\n<p class="Normal">Mở cửa giao dịch trở lại sau kỳ nghỉ Tết &Acirc;m lịch, 2 s&agrave;n chứng kho&aacute;n nhận được kh&aacute; nhiều t&iacute;n hiệu thuận lợi từ thị trường quốc tế. Tại Mỹ, S&amp;P500 tăng điểm phi&ecirc;n thứ 6 li&ecirc;n tiếp, trong khi Dow Jones cũng giữ vững mốc 12.000 điểm. Trong khi đ&oacute;, c&aacute;c sở giao dịch tại ch&acirc;u &Acirc;u cũng xanh s&agrave;n trong ng&agrave;y thứ 5 li&ecirc;n tiếp.</p>\r\n<p class="Normal">C&ugrave;ng với tin quốc tế, nh&agrave; đầu tư cũng nhận được kh&aacute; nhiều b&aacute;o c&aacute;o kết quả hoạt động qu&yacute; IV của doanh nghiệp ni&ecirc;m yết, trong đ&oacute;, số c&ocirc;ng ty c&oacute; lợi nhuận vượt dự kiến vẫn chiếm đa số.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Index tiếp tục tăng mạnh nhưng giao dịch vẫn ở mức thấp. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Index tiếp tục tăng mạnh nhưng giao dịch vẫn ở mức thấp. Ảnh minh họa: Ho&agrave;ng H&agrave;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Mở phi&ecirc;n s&aacute;ng nay tr&ecirc;n s&agrave;n TP HCM, Vn-Index tăng ngay 5,1 điểm, l&ecirc;n 515,7 điểm trong 15 ph&uacute;t. Tuy vậy, khối lượng giao dịch đợt một chỉ ở mức trung b&igrave;nh, với gần 1,2 triệu chứng kho&aacute;n, tương đương 25,64 tỷ đồng được sang nhượng.</p>\r\n<p class="Normal">Tại thời điểm n&agrave;y, BVH nhanh ch&oacute;ng tăng trần l&ecirc;n 93.000 đồng một cổ phiếu. Một số blue-chip kh&aacute;c như FPT, REE, HAG v&agrave; PVF cũng tăng khoảng 500-600 đồng. Trong khi đ&oacute;, NVT hiện l&agrave; m&atilde; được giao dịch nhiều nhất tr&ecirc;n s&agrave;n với lượng khớp đạt khoảng 300.000 cổ phiếu.</p>\r\n<p class="Normal">Bước sang đợt 2, Vn-Index tiếp tục duy tr&igrave; được đ&agrave; tăng điểm nhưng giao dịch diễn ra hết sức trầm lắng. T&iacute;nh đến 10 giờ, mới c&oacute; khoảng 13 triệu cổ phiếu được chuyển nhượng tr&ecirc;n s&agrave;n.</p>\r\n<p class="Normal">Tại điểm n&agrave;y, ngoại trừ EIB đứng gi&aacute;, tất cả c&aacute;c cổ phiếu của ng&agrave;nh ng&acirc;n h&agrave;ng đều tăng gi&aacute;. Ngo&agrave;i ra, BVH, PVD tiếp tục tăng trần. PVF cũng tăng 1.300 đồng một cổ phiếu.</p>\r\n<p class="Normal">Đ&agrave; tăng điểm của chỉ số được duy tr&igrave; đến cuối phi&ecirc;n gi&uacute;p Vn-Index t&iacute;ch lũy được hơn 10 điểm, l&ecirc;n 520,69 điểm. Tuy vậy, khối lượng giao dịch tr&ecirc;n s&agrave;n chỉ đạt khoảng 24,8 triệu cổ phiếu, tương đương 707 tỷ đồng, thấp hơn nhiều so với mức hơn 900 tỷ của phi&ecirc;n cuối năm. Giao dịch thỏa thuận kh&ocirc;ng đ&aacute;ng kể.</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội,&nbsp;<strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;tăng nhẹ 1,03 điểm, l&ecirc;n 107,66 điểm. Khối lượng giao dịch chỉ đạt gần 15,2 triệu chứng kho&aacute;n, tương đương khoảng 280 tỷ đồng. Trong khi đ&oacute;,&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;điều chỉnh nhẹ 0,22 điểm trong buổi s&aacute;ng, xuống 42,73 điểm. Lượng giao dịch đạt gần 30.000 cổ phiếu, tương đương hơn 442 triệu đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>\r\n</div>\r\n</div>', 9, 3, 2463, 0, 1),
(379, 'Giai nhân chứng khoán', 'Người đẹp luôn là tâm điểm trong bất kỳ lĩnh vực nào và chứng khoán không phải ngoại lệ. Chính vì điều này một số công ty chứng khoán đã có chủ trương tuyển nữ nhân viên môi giới có ngoại hình đẹp.', 'giai-nhan.jpg', NULL, '<h1 class="Title">Giai nh&acirc;n chứng kho&aacute;n</h1>\r\n<h2 class="Lead">Người đẹp lu&ocirc;n l&agrave; t&acirc;m điểm trong bất kỳ lĩnh vực n&agrave;o v&agrave; chứng kho&aacute;n kh&ocirc;ng phải ngoại lệ. Ch&iacute;nh v&igrave; điều n&agrave;y một số c&ocirc;ng ty chứng kho&aacute;n đ&atilde; c&oacute; chủ trương tuyển nữ nh&acirc;n vi&ecirc;n m&ocirc;i giới c&oacute; ngoại h&igrave;nh đẹp.</h2>\r\n<p class="Normal">Cho đến trước năm 2008, hầu như c&aacute;c c&ocirc;ng ty chứng kho&aacute;n kh&ocirc;ng ch&uacute; trọng đến vấn đề ngoại h&igrave;nh của nh&acirc;n vi&ecirc;n. Đ&acirc;y l&agrave; giai đoạn sơ khởi của thị trường, nh&agrave; đầu tư cứ chơi l&agrave; thắng. Nhưng kể từ năm 2008, khi chứng kho&aacute;n lao dốc, nh&agrave; đầu tư thua lỗ r&uacute;t khỏi thị trường, c&ocirc;ng ty chứng kho&aacute;n l&uacute;c bấy giờ mới t&iacute;nh đến chuyện t&igrave;m v&agrave; giữ kh&aacute;ch h&agrave;ng.</p>\r\n<p class="Normal">Một c&ocirc;ng ty chứng kho&aacute;n c&oacute; vốn đầu tư nước ngo&agrave;i được xem l&agrave; ti&ecirc;n phong trong việc cải thiện h&igrave;nh ảnh của bộ phận m&ocirc;i giới. Hầu hết vị tr&iacute; giao tiếp với kh&aacute;ch h&agrave;ng tại c&ocirc;ng ty đều do ph&aacute;i yếu đảm nhận với t&aacute;c phong, trang phục rất chuy&ecirc;n nghiệp. Sang năm 2009, khi thị trường chứng kho&aacute;n phục hồi, c&aacute;c c&ocirc;ng ty chứng kho&aacute;n bắt đầu ch&uacute; trọng hơn hoạt động m&ocirc;i giới, l&uacute;c n&agrave;y c&aacute;c b&oacute;ng hồng m&ocirc;i giới mới thật sự xuất hiện.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="giai-nhan.jpg" border="1" alt="M&ocirc;i giới xinh đẹp cũng c&oacute; nỗi khổ ri&ecirc;ng." width="490" height="340" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&ocirc;i giới xinh đẹp cũng c&oacute; nỗi khổ ri&ecirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trong những c&ocirc;ng ty chứng kho&aacute;n c&oacute; nhiều m&ocirc;i giới c&oacute; sắc v&oacute;c, c&aacute;c c&ocirc;ng ty c&oacute; vốn đầu tư nước ngo&agrave;i chiếm &aacute;p đảo. Nguy&ecirc;n do l&agrave; tại thị trường nước ngo&agrave;i, sức h&uacute;t của c&aacute;c m&ocirc;i giới c&oacute; sắc v&oacute;c rất lớn n&ecirc;n nhiều tổ chức muốn đưa &yacute; tưởng n&agrave;y v&agrave;o Việt Nam. Điểm nữa do c&aacute;c c&ocirc;ng ty chứng kho&aacute;n c&oacute; yếu tố nước ngo&agrave;i xuất hiện kh&aacute; muộn n&ecirc;n phải tiếp thị h&igrave;nh ảnh thật tốt mới mong thu h&uacute;t được kh&aacute;ch h&agrave;ng.</p>\r\n<p class="Normal">Mặc d&ugrave; vậy, ngoại h&igrave;nh kh&ocirc;ng phải l&agrave; ti&ecirc;u ch&iacute; h&agrave;ng đầu của một nh&acirc;n vi&ecirc;n m&ocirc;i giới. &Ocirc;ng Ho&agrave;ng Thạch L&acirc;n, Gi&aacute;m đốc khối m&ocirc;i giới c&ocirc;ng ty chứng kho&aacute;n MHB, cho rằng với đặc th&ugrave; c&ocirc;ng việc thường xuy&ecirc;n giao tiếp với kh&aacute;ch h&agrave;ng, sự năng động, kh&eacute;o ăn n&oacute;i l&agrave; những ti&ecirc;u ch&iacute; đầu ti&ecirc;n. Sau đ&oacute; l&agrave; nền tảng chuy&ecirc;n m&ocirc;n tốt, khả năng thuyết phục kh&aacute;ch h&agrave;ng rồi mới đến ngoại h&igrave;nh. Điều n&agrave;y kh&ocirc;ng c&oacute; nghĩa ngoại h&igrave;nh kh&ocirc;ng quan trọng, nhưng nếu chỉ c&oacute; ngoại h&igrave;nh, thiếu c&aacute;c ti&ecirc;u ch&iacute; c&ograve;n lại th&igrave; cũng &ldquo;vứt&rdquo;.</p>\r\n<p class="Normal">Chị T&acirc;m đang l&agrave;m ở bộ phận lưu k&yacute; một c&ocirc;ng ty chứng kho&aacute;n lớn kể chị vốn l&agrave; một trong những m&ocirc;i giới con cưng của c&ocirc;ng ty nhờ c&oacute; chuy&ecirc;n m&ocirc;n tốt v&agrave; ngoại h&igrave;nh kh&aacute;. Nhưng c&agrave;ng kiếm được nhiều kh&aacute;ch h&agrave;ng, chị c&agrave;ng được giao nhiều việc hơn, th&agrave;nh thử sinh ra &aacute;p lực. Nhiều kh&aacute;ch h&agrave;ng thấy m&ocirc;i giới c&oacute; ch&uacute;t sắc v&oacute;c đ&atilde; nảy sinh những &yacute; định kh&ocirc;ng nghi&ecirc;m t&uacute;c, hết nhắn tin rồi lại gọi điện mời đi chơi, hứa mở t&agrave;i khoản nhưng chỉ hứa cuội. Biết, nhưng từ chối kh&ocirc;ng dễ v&igrave; kh&ocirc;ng kh&eacute;o sẽ mang tiếng &ldquo;chảnh&rdquo;. Sau khi lập gia đ&igrave;nh, phải đặt việc chăm s&oacute;c chồng con, kh&ocirc;ng thể đi nhiều như trước, chị quyết định lui về ph&iacute;a sau.</p>\r\n<p class="Normal">L&agrave; một nh&acirc;n vi&ecirc;n m&ocirc;i giới kh&aacute;ch VIP, ngo&agrave;i thu nhập kh&aacute; ổn định, chị Lan lại c&oacute; những nỗi khổ ri&ecirc;ng. Khi mới v&agrave;o c&ocirc;ng ty, chị bị n&oacute;i sau lưng l&agrave; &ldquo;g&agrave;&rdquo; của sếp n&ecirc;n mới được &ldquo;điều&rdquo; về đ&acirc;y để l&agrave;m việc. Đến khi mang về h&agrave;ng loạt t&agrave;i khoản lớn cho c&ocirc;ng ty, chị lại bị nghi ngờ d&ugrave;ng nhan sắc đ&aacute;nh đổi chứ chuy&ecirc;n m&ocirc;n n&agrave;o c&oacute; g&igrave; hay. Nhiều kh&aacute;ch h&agrave;ng n&oacute;i nửa đ&ugrave;a nửa thật với chị &ldquo;em xinh qu&aacute;, giao t&agrave;i khoản của anh cho em quản l&yacute;, vợ anh thế n&agrave;o cũng ghen...&rdquo;, &ldquo;cứ ngồi nh&igrave;n em anh kh&ocirc;ng thể n&agrave;o tập trung theo d&otilde;i thị trường...&rdquo;.</p>\r\n<p class="Normal">Kim, Gi&aacute;m đốc chi nh&aacute;nh một c&ocirc;ng ty chứng kho&aacute;n lớn tại Nha Trang, cho biết anh cũng chủ trương tuyển nh&acirc;n vi&ecirc;n nữ c&oacute; sắc v&oacute;c. Tuy nhi&ecirc;n ngo&agrave;i những c&aacute;i lợi, anh gặp kh&ocirc;ng &iacute;t chuyện dở kh&oacute;c dở cười. Nhiều kh&aacute;ch h&agrave;ng nhỏ to với nhau nh&acirc;n vi&ecirc;n nữ ở đ&acirc;y xinh thế, nhưng nhan sắc tỷ lệ nghịch với tr&iacute; tuệ, n&ecirc;n chắc chuy&ecirc;n m&ocirc;n kh&ocirc;ng tốt mới phải lấy ngoại h&igrave;nh b&ugrave; v&agrave;o. Đau đầu hơn nữa l&agrave; chuyện ghen tu&ocirc;ng. Đầu ti&ecirc;n ch&iacute;nh l&agrave; b&agrave; x&atilde; anh với lập luận đ&agrave;n &ocirc;ng ở cạnh g&aacute;i đẹp kh&ocirc;ng xi&ecirc;u l&ograve;ng th&igrave;... kh&ocirc;ng phải đ&agrave;n &ocirc;ng, n&ecirc;n kiểm so&aacute;t rất chặt. Mỗi lần c&oacute; việc về nh&agrave; trễ thể n&agrave;o cũng bị lườm, hay n&oacute;i b&oacute;ng gi&oacute;, điện thoại thường xuy&ecirc;n bị kiểm tra. Đ&oacute; l&agrave; chưa kể nhiều l&uacute;c bạn trai của nh&acirc;n vi&ecirc;n nữ điện thoại tới mắng vốn về chuyện &ldquo;sao cứ gọi điện l&agrave;m phiền người y&ecirc;u của t&ocirc;i&rdquo;.</p>\r\n<p class="Normal">&Ocirc;ng Phạm Linh, Tổng gi&aacute;m đốc c&ocirc;ng ty chứng kho&aacute;n Quốc Tế, kết luận: &ldquo;Một m&ocirc;i giới tốt l&agrave; người đem lại nhiều lợi &iacute;ch cho kh&aacute;ch h&agrave;ng nhất. V&agrave; sự gắn b&oacute; giữa nh&agrave; đầu tư v&agrave; m&ocirc;i giới l&agrave; mối quan hệ l&acirc;u d&agrave;i chứ kh&ocirc;ng phải theo kiểu mua đứt b&aacute;n đoạn. Ch&iacute;nh điều n&agrave;y n&ecirc;n m&ocirc;i giới c&oacute; ngoại h&igrave;nh chỉ l&agrave; yếu tố ban đầu. C&oacute; một đội ngũ m&ocirc;i giới xinh đẹp v&agrave; chuy&ecirc;n m&ocirc;n tốt l&agrave; điều c&ocirc;ng ty chứng kho&aacute;n n&agrave;o cũng mong muốn. Nhưng chỉ n&ecirc;n xem ti&ecirc;u ch&iacute; ngoại h&igrave;nh l&agrave; &ldquo;hương hoa&rdquo;, thay v&igrave; ch&uacute; trọng rồi bỏ qu&ecirc;n những điều cốt yếu kh&aacute;c&rdquo;.</p>\r\n<p class="Normal" style="text-align: right;">(Theo&nbsp;<em>S&agrave;i G&ograve;n Đầu tư T&agrave;i ch&iacute;nh</em>)</p>', 9, 3, 2966, 0, 1),
(380, 'Hoàng Anh Gia Lai nhận xếp hạng tín nhiệm điểm B', 'Hãng đánh giá tín nhiệm Standard & Poor''s vừa công bố mức đánh giá đối với các khoản tín dụng dài hạn của Tập đoàn Hoàng Anh Gia Lai. Đây là lần đầu tiên một doanh nghiệp tư nhân của Việt Nam được S&P xếp hạng.', 'HAGL-0.jpg', NULL, '<h1 class="Title">Ho&agrave;ng Anh Gia Lai nhận xếp hạng t&iacute;n nhiệm điểm B</h1>\r\n<h2 class="Lead">H&atilde;ng đ&aacute;nh gi&aacute; t&iacute;n nhiệm Standard &amp; Poor''s vừa c&ocirc;ng bố mức đ&aacute;nh gi&aacute; đối với c&aacute;c khoản t&iacute;n dụng d&agrave;i hạn của Tập đo&agrave;n Ho&agrave;ng Anh Gia Lai. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n một doanh nghiệp tư nh&acirc;n của Việt Nam được S&amp;P xếp hạng.</h2>\r\n<p class="Normal">Xếp hạng n&agrave;y thấp hơn một bậc so với mức&nbsp;<a class="Normal" href="http://vnexpress.net/gl/kinh-doanh/2010/12/3ba24970/">đ&aacute;nh gi&aacute;</a>&nbsp;BB- được Standard &amp; Poor''s đưa ra đối với c&aacute;c khoản vay của Ch&iacute;nh phủ Việt Nam cũng như 3 ng&acirc;n h&agrave;ng lớn l&agrave; Vietcombank, Techcombank v&agrave; BIDV hồi cuối th&aacute;ng 12/2010.</p>\r\n<p class="Normal">Tuy vậy, trong b&aacute;o c&aacute;o xếp hạng đối với Ho&agrave;ng Anh Gia Lai được S&amp;P c&ocirc;ng bố trong ng&agrave;y 28/1, S&amp;P nhận định triển vọng đối với c&aacute;c khoản vay của doanh nghiệp n&agrave;y l&agrave; "t&iacute;ch cực".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="HAGL-0.jpg" alt="Xếp hạng t&iacute;n nhiệm của S&amp;P đối với Ho&agrave;ng Anh Gia Lai." width="490" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Xếp hạng t&iacute;n nhiệm của S&amp;P đối với Ho&agrave;ng Anh Gia Lai.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">"Điểm B phản &aacute;nh kỳ vọng của ch&uacute;ng t&ocirc;i v&agrave;o sự tăng trưởng của Ho&agrave;ng Anh Gia Lai với những khoản đầu tư của họ". B l&agrave; điểm đ&aacute;nh gi&aacute; cao thứ 5 trong thang điểm đ&aacute;nh gi&aacute; đối với t&iacute;n dụng d&agrave;i hạn của S&amp;P. B&ecirc;n cạnh đ&oacute;, tổ chức xếp hạng cũng lưu &yacute; Ho&agrave;ng Anh Gia Lai cần cẩn trọng trước những bất trắc c&oacute; thể xảy ra khi thực hiện đầu tư v&agrave;o c&aacute;c dự &aacute;n mới.</p>\r\n<p class="Normal">S&aacute;ng nay, trao đổi với&nbsp;<em>VnExpress.net</em>, Ph&oacute; tổng gi&aacute;m đốc Tập đo&agrave;n Ho&agrave;ng Anh Gia Lai - V&otilde; Trường Sơn cho rằng đ&aacute;nh gi&aacute; của S&amp;P đối với doanh nghiệp m&igrave;nh l&agrave; tương đối x&aacute;c đ&aacute;ng. Đ&aacute;nh gi&aacute; n&agrave;y cũng tạo điều kiện thuận lợi cho doanh nghiệp khi thực hiện&nbsp;<a class="Normal" href="http://vnexpress.net/gl/kinh-doanh/chung-khoan/2010/12/3ba24379/">kế hoạch</a>&nbsp;ph&aacute;t h&agrave;nh 200 triệu USD tr&aacute;i phiếu quốc tế trong thời gian tới.</p>\r\n<p class="Normal">"Trong thời gian qua, do ảnh hưởng của t&igrave;nh h&igrave;nh kinh tế thế giới cũng như sự kiện Vinashin, đ&aacute;nh gi&aacute; của c&aacute;c tổ chức t&iacute;n nhiệm đối với doanh nghiệp Việt Nam đ&atilde; bị ảnh hưởng mạnh. Trong điều kiện đ&oacute;, việc Ho&agrave;ng Anh Gia Lai c&oacute; được điểm B cũng đ&atilde; l&agrave; rất tốt", &ocirc;ng Sơn nhận định.</p>\r\n<p class="Normal">Cũng theo Ph&oacute; tổng gi&aacute;m đốc Ho&agrave;ng Anh Gia Lai, việc nhận điểm B về t&iacute;n nhiệm v&agrave;o thời điểm hiện tại cũng gi&uacute;p tr&aacute;i phiếu của doanh nghiệp thu được nhiều sự ch&uacute; &yacute; của nh&agrave; đầu tư quốc tế do kỳ vọng v&agrave;o mức xếp hạng cao hơn trong tương lai.</p>\r\n<p class="Normal">Theo kế hoạch được đại hội cổ đ&ocirc;ng Ho&agrave;ng Anh Gia Lai th&ocirc;ng qua, việc ph&aacute;t h&agrave;nh tr&aacute;i phiếu kỳ hạn 5 năm sẽ được doanh nghiệp thực hiện v&agrave;o đầu năm ngay với l&atilde;i suất căn cứ v&agrave;o t&igrave;nh h&igrave;nh thực tế. Vốn tr&aacute;i phiếu sau đ&oacute; sẽ được tập đo&agrave;n n&agrave;y sử dụng cho c&aacute;c dự &aacute;n như trồng c&acirc;y cao su v&agrave; x&acirc;y nh&agrave; m&aacute;y thủy điện.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Thanh B&igrave;nh - Nhật Minh</strong></p>', 9, 3, 24, 0, 1),
(381, 'SCIC chuẩn bị bán vốn tại 281 doanh nghiệp', 'Tổng công ty Đầu tư và Kinh doanh vốn Nhà nước (SCIC) cho biết năm 2011, đơn vị này sẽ triển khai bán vốn tại 281 doanh nghiệp (gấp 2,65 lần so với năm 2010). Trong đó, SCIC sẽ bán hết vốn tại 170 doanh nghiệp.', 'SCIC.jpg', NULL, '<h1 class="Title">SCIC chuẩn bị b&aacute;n vốn tại 281 doanh nghiệp</h1>\r\n<h2 class="Lead">Tổng c&ocirc;ng ty Đầu tư v&agrave; Kinh doanh vốn Nh&agrave; nước (SCIC) cho biết năm 2011, đơn vị n&agrave;y sẽ triển khai b&aacute;n vốn tại 281 doanh nghiệp (gấp 2,65 lần so với năm 2010). Trong đ&oacute;, SCIC sẽ b&aacute;n hết vốn tại 170 doanh nghiệp.</h2>\r\n<p class="Normal">Năm 2010, SCIC đ&atilde; b&aacute;n vốn th&agrave;nh c&ocirc;ng tại 422 doanh nghiệp. Trong đ&oacute;, tổ chức n&agrave;y b&aacute;n to&aacute;n bộ vốn Nh&agrave; nước tại 399 doanh nghiệp, gi&aacute; trị sổ s&aacute;ch đạt 948 tỷ đồng. Số thực thu về l&agrave; 2.209 tỷ đồng, đạt tỷ lệ 2,25 lần so với mệnh gi&aacute;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="SCIC.jpg" alt="" width="380" height="255" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">SCIC sẽ đ&atilde; b&aacute;n vốn th&agrave;nh c&ocirc;ng tại 422 doanh nghiệp với gi&aacute; trị thực thu về 2.209 tỷ đồng trong năm 2010. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Hiện SCIC chỉ c&ograve;n nắm giữ vốn tại 538 doanh nghiệp với số vốn nh&agrave; nước theo gi&aacute; trị sổ s&aacute;ch kế to&aacute;n l&agrave; 11.865 tỷ đồng.</p>\r\n<p class="Normal">Năm 2010, tổng doanh thu của SCIC đạt 2.753 tỷ đồng, vượt 46% kế hoạch; lợi nhuận trước thuế đạt 2.445 tỷ đồng, vượt 40% kế hoạch v&agrave; tăng hơn 30% so với năm 2009.</p>\r\n<p class="Normal">T&ocirc;̉ng giám đ&ocirc;́c SCIC Lại Văn Đạo cho biết, năm 2011, SCIC sẽ tiếp tục đẩy nhanh c&ocirc;ng t&aacute;c b&aacute;n vốn. Theo đ&oacute;, đơn vị n&agrave;y dự kiến triển khai b&aacute;n vốn tại 281 doanh nghiệp với mục ti&ecirc;u b&aacute;n hết vốn tại 170 doanh nghiệp.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hồng Anh</strong></p>', 9, 3, 602, 0, 1),
(382, 'Chứng khoán tăng mạnh trong phiên tất niên', '“Phiên chợ” cuối cùng của chứng khoán trong năm Canh Dần diễn ra khá suôn sẻ khi sắc xanh bao trùm cả 2 sàn. Tuy nhiên, thanh khoản thị trường vẫn chưa thực sự lạc quan', 'chung-khoan-0 (3).jpg', NULL, '<h1 class="Title">Chứng kho&aacute;n tăng mạnh trong phi&ecirc;n tất ni&ecirc;n</h1>\r\n<h2 class="Lead">&ldquo;Phi&ecirc;n chợ&rdquo; cuối c&ugrave;ng của chứng kho&aacute;n trong năm Canh Dần diễn ra kh&aacute; su&ocirc;n sẻ khi sắc xanh bao tr&ugrave;m cả 2 s&agrave;n. Tuy nhi&ecirc;n, thanh khoản thị trường vẫn chưa thực sự lạc quan.</h2>\r\n<p class="Normal">Đ&oacute;n nhận tin xấu từ thị trường t&agrave;i ch&iacute;nh quốc tế (Fitch hạ dự b&aacute;o tăng trưởng ch&acirc;u &Aacute;, Standard&amp;Poor&rsquo;s hạ xếp hạng t&iacute;n nhiệm của Nhật&hellip;) nhưng nhờ kết quả kinh doanh của doanh nghiệp ni&ecirc;m yết tốt, chứng kho&aacute;n vẫn giữ được đ&agrave; tăng điểm trong s&aacute;ng nay.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Chứng kho&aacute;n c&oacute; dấu hiệu khởi sắc trong phi&ecirc;n giao dịch cuối năm. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chứng kho&aacute;n c&oacute; dấu hiệu khởi sắc trong phi&ecirc;n giao dịch cuối năm. Ảnh minh họa: Ho&agrave;ng H&agrave;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">S&agrave;n TP HCM mở phi&ecirc;n thuận lợi hơn hẳn phi&ecirc;n trước với gần một triệu chứng kho&aacute;n được chuyển nhượng (tương đương 25,94 tỷ đồng) trong 15 ph&uacute;t. Tuy v&acirc;y, Vn-Index chỉ tăng nhẹ th&ecirc;m 0,09 điểm, l&ecirc;n 502,77 điểm.</p>\r\n<p class="Normal">Tại thời điểm n&agrave;y, to&agrave;n s&agrave;n TP HCM c&oacute; khoảng 120 m&atilde; c&oacute; giao dịch với ch&ecirc;nh lệch giữa số m&atilde; tăng v&agrave; giảm gi&aacute; kh&ocirc;ng đ&aacute;ng kể. Vốn ngoại tiếp tục đổ mạnh v&agrave;o c&aacute;c blue-chip như FPT, VCB, REE. OGC&hellip; Tuy nhi&ecirc;n, IJC mới l&agrave; m&atilde; được chuyển nhượng mạnh nhất tr&ecirc;n s&agrave;n khi c&oacute; hơn 160.000 cổ phiếu được giao dịch ngay trước đợt 2.</p>\r\n<p class="Normal">Vn-Index chỉ thực sự đột ph&aacute; khi bước sang khớp lệnh li&ecirc;n tục nhờ lực đẩy từ c&aacute;c cổ phiếu lớn. BVH sau phi&ecirc;n giảm s&agrave;n h&ocirc;m qua đ&atilde; tăng trần trở lại. Một số m&atilde; đ&aacute;ng ch&uacute; &yacute; kh&aacute;c như IFS, NVN, PVF&hellip; cũng tăng trần mạnh mẽ, MSN tăng 1.000 đồng mỗi cổ phiếu.</p>\r\n<p class="Normal">Chỉ sau nửa giờ khớp lệnh li&ecirc;n tục, Vn-Index đ&atilde; tăng hơn 8 điểm, l&ecirc;n s&aacute;t 512 điểm. Tuy nhi&ecirc;n, khối lượng giao dịch kh&ocirc;ng lớn khiến chỉ số bị thử th&aacute;ch mạnh ở ngưỡng n&agrave;y. Index l&ugrave;i về dao động quanh ngưỡng 510 điểm kể từ thời điểm n&agrave;y đến cuối phi&ecirc;n.</p>\r\n<p class="Normal">Chỉ số của s&agrave;n TP HCM kh&eacute;p lại phi&ecirc;n giao dịch, đồng thời cũng l&agrave; kh&eacute;p lại năm Canh Dần ở 510,6 điểm, tăng 7,92 điểm so với đầu phi&ecirc;n. Tuy nhi&ecirc;n, gi&aacute; trị giao dịch trong phi&ecirc;n cuối năm vẫn dưới 1.000 tỷ đồng khi chỉ c&oacute; hơn 37,6 triệu chứng kho&aacute;n được chuyển nhượng (tương đương 960 tỷ đồng).</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội,&nbsp;<strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;tăng nhẹ 0,01 điểm trong phi&ecirc;n tất ni&ecirc;n, l&ecirc;n 106,63 điểm. Khối lượng giao dịch, tuy được cải thiện nhẹ so với phi&ecirc;n trước nhưng chỉ đạt chưa đầy 22 triệu chứng kho&aacute;n, tương đương 402,7 tỷ đồng. Khối ngoại tiếp tục mua r&ograve;ng hơn 8 tỷ đồng cổ phiếu tr&ecirc;n s&agrave;n n&agrave;y trong s&aacute;ng nay.</p>\r\n<p class="Normal">Kết th&uacute;c giao dịch buổi s&aacute;ng,&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;tăng 0,44 điểm, l&ecirc;n 42,9 điểm. Khối lượng giao dịch tr&ecirc;n s&agrave;n chưa ni&ecirc;m yết đạt 85.500 cổ phiếu, tương đương hơn một tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 849, 0, 1),
(383, 'Chứng khoán tăng điểm trở lại', 'Nhà đầu tư đẩy mạnh mua vào sau khi Vn-Index “thủng” ngưỡng 500 điểm, giúp chỉ số này hồi phục mạnh. Sắc xanh đồng thời cũng được duy trì trong suốt phiên trên sàn Hà Nội.', 'chung-khoan-0 (4).jpg', NULL, '<h1 class="Title">Chứng kho&aacute;n tăng điểm trở lại</h1>\r\n<h2 class="Lead">Nh&agrave; đầu tư đẩy mạnh mua v&agrave;o sau khi Vn-Index &ldquo;thủng&rdquo; ngưỡng 500 điểm, gi&uacute;p chỉ số n&agrave;y hồi phục mạnh. Sắc xanh đồng thời cũng được duy tr&igrave; trong suốt phi&ecirc;n tr&ecirc;n s&agrave;n H&agrave; Nội.</h2>\r\n<p class="Normal">T&acirc;m l&yacute; nghỉ Tết tiếp tục bao tr&ugrave;m s&agrave;n chứng kho&aacute;n trong những ph&uacute;t đầu phi&ecirc;n s&aacute;ng nay khi nh&agrave; đầu tư tỏ ra hết sức b&agrave;ng quan với c&aacute;c lệnh giao dịch. Mở phi&ecirc;n tr&ecirc;n s&agrave;n TP HCM, lượng chứng kho&aacute;n chuyển nhượng chỉ đạt 20.000 cổ phiếu, tương đương 1,54 tỷ đồng. Vn-Index, do vậy, chỉ c&oacute; thể đứng y&ecirc;n ở tham chiếu 501,25 điểm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Chứng kho&aacute;n tăng điểm trở lại nhưng giao dịch vẫn thấp k&eacute;m. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chứng kho&aacute;n tăng điểm trở lại nhưng giao dịch vẫn thấp k&eacute;m. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại thời điểm n&agrave;y, s&agrave;n TP HCM ghi nhận được một số blue-chip nh&uacute;c nh&iacute;ch tăng nhẹ. Tuy vậy, lực b&aacute;n kh&aacute; lớn khiến c&aacute;c m&atilde; n&agrave;y nhanh ch&oacute;ng quay đầu giảm gi&aacute;. BVH nhanh ch&oacute;ng chạm s&agrave;n trong khi MSN cũng giảm gi&aacute; mạnh. Bảng điện tử l&aacute;c đ&aacute;c ghi nhận một v&agrave;i penny-stock tăng gi&aacute;. Tuy nhi&ecirc;n lực k&eacute;o từ c&aacute;c cổ phiếu n&agrave;y chưa đủ mạnh để đẩy Vn-Index đi l&ecirc;n.</p>\r\n<p class="Normal">Chỉ số của s&agrave;n TP HCM bắt đầu giảm mạnh khi thị trường bước sang khớp lệnh li&ecirc;n tục v&agrave; mất mốc 500 điểm ngay sau 9 giờ. Tuy vậy, một số blue-chip như SSI, PVF, REE, DPM, HAG, GMD&hellip; vẫn duy tr&igrave; được đ&agrave; tăng nhẹ do lực cầu bắt đ&aacute;y c&oacute; dấu hiệu cải thiện v&agrave;o giữa phi&ecirc;n. C&aacute;c cổ phiếu ng&acirc;n h&agrave;ng cũng ghi nhận đ&agrave; tăng nhẹ do c&oacute; c&aacute;c tin tức lợi nhuận khả quan.</p>\r\n<p class="Normal">Đến cuối đợt khớp lệnh li&ecirc;n tục, DPM, HAG, PVF tiếp tục tăng gi&aacute;. FPT, KBC, IJC thậm ch&iacute; c&ograve;n tăng trần với dư mua lớn. Đ&agrave; tăng điểm được duy tr&igrave; đến cuối phi&ecirc;n gi&uacute;p Vn-Index t&iacute;ch lũy được 1,43 điểm, l&ecirc;n 502,58 điểm. Khối lượng giao dịch tr&ecirc;n s&agrave;n cũng tăng so với phi&ecirc;n trước với hơn 30 triệu chứng kho&aacute;n được chuyển nhượng, tương đương 815,29 tỷ đồng.</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội,&nbsp;<strong><span style="color: #4f4f4f;">HNX-Index&nbsp;</span></strong>cũng duy tr&igrave; được đ&agrave; tăng điểm trong gần suốt phi&ecirc;n với gần 160 m&atilde; tăng gi&aacute;. Chỉ số n&agrave;y tăng nhẹ 0,86 điểm l&ecirc;n 106,62 điểm v&agrave;o l&uacute;c chốt phi&ecirc;n. Tuy nhi&ecirc;n, khối lượng giao dịch vẫn trong trạng th&aacute;i buồn tẻ với hơn 19 triệu chứng kho&aacute;n được chuyển nhượng, tương đương 343,61 tỷ đồng.</p>\r\n<p class="Normal">Kết th&uacute;c giao dịch buổi s&aacute;ng,&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;giảm 0,39 điểm, xuống 42,67 điểm. Khối lượng giao dịch đạt 312.700 cổ phiếu, tương đương 5,75 tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 569, 0, 1),
(384, 'Cổ phiếu lớn tiếp tục kìm hãm Vn-Index', 'Áp lực chốt hàng ở những trụ cột một lần nữa giữ chặt chỉ số sàn TP HCM ở vùng giảm. Vn-Index bị trừ thêm 7,91 điểm và khoảng cách với mốc 520 mỗi lúc một xa dần.', 'ck-490x322 (1).jpg', NULL, '<h1 class="Title">Cổ phiếu lớn tiếp tục k&igrave;m h&atilde;m Vn-Index</h1>\r\n<h2 class="Lead">&Aacute;p lực chốt h&agrave;ng ở những trụ cột một lần nữa giữ chặt chỉ số s&agrave;n TP HCM ở v&ugrave;ng giảm. Vn-Index bị trừ th&ecirc;m 7,91 điểm v&agrave; khoảng c&aacute;ch với mốc 520 mỗi l&uacute;c một xa dần.</h2>\r\n<p class="Normal">Cổ phiếu c&oacute; vốn h&oacute;a lớn thứ 2 tại HOSE l&agrave; BVH tiếp tục giảm s&agrave;n, hết dư mua, trong khi &aacute;p lực b&aacute;n gi&aacute; s&agrave;n tăng l&ecirc;n r&otilde; rệt, với số lượng tr&ecirc;n 70 ngh&igrave;n. 2 mức gi&aacute; thấp hơn tham chiếu (94.000 v&agrave; 94.500 đồng) c&oacute; hơn 8.000 cổ phiếu chờ được b&aacute;n. Giao dịch s&aacute;ng nay của BVH chủ yếu đến từ khối ngoại. M&atilde; n&agrave;y khớp 61.450 cổ phiếu, trong đ&oacute; nh&agrave; đầu tư nước ngo&agrave;i đ&atilde; sở hữu 54.690.</p>\r\n<p class="Normal">Tương tự, MSN sau những ng&agrave;y tăng n&oacute;ng ở tuần trước (với mức tăng 20%), đ&atilde; rớt s&agrave;n trong phi&ecirc;n h&ocirc;m qua v&agrave; s&aacute;ng nay tiếp tục mất th&ecirc;m 3,5 điểm. 85% lượng khớp lệnh của MSN xuất ph&aacute;t từ lực cầu ngoại. Song song đ&oacute;, h&agrave;ng loạt trụ cột kh&aacute;c cũng bị trừ 0,1-2 điểm, thậm ch&iacute; giảm s&agrave;n (DPM), k&eacute;o theo nhiều m&atilde; kh&aacute;c giảm theo. Thị trường mất đi sự hậu thuẫn để c&oacute; thể lội ngược d&ograve;ng đi l&ecirc;n. Sắc xanh trong nh&oacute;m cổ phiếu vốn h&oacute;a lớn chỉ c&oacute; VIC, VPL, KDC.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ck-490x322.jpg" border="1" alt="" width="490" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Giao dịch chứng kho&aacute;n ảm đạm trong những ng&agrave;y gi&aacute;p Tết. Ảnh minh họa:&nbsp;<em>B.H.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Kết th&uacute;c phi&ecirc;n 25/1, Vn-Index đ&aacute;nh mất th&ecirc;m 7,91 điểm, xuống 501,97. Mức giảm n&agrave;y c&ograve;n cao hơn so với giao dịch đợt đầu kết th&uacute;c (5,82 điểm). Nếu cuối tuần trước, chỉ số s&agrave;n TP HCM đ&atilde; tiến rất s&aacute;t mốc 520 th&igrave; chỉ mới 2 phi&ecirc;n đầu tuần, Vn-Index đ&atilde; c&aacute;ch biệt kh&aacute; lớn với mức n&agrave;y. T&iacute;nh chung 2 h&ocirc;m nay, Vn-Index mất tổng cộng 17,53 điểm v&agrave; đang l&ugrave;i dần về ngưỡng 500.</p>\r\n<p class="Normal">Hoạt động t&aacute;i cấu tr&uacute;c danh mục khi Tết đang đến gần, trong khi lực cầu suy yếu chưa thấy g&igrave; khởi sắc, được cho l&agrave; nguy&ecirc;n nh&acirc;n khiến thanh khoản duy tr&igrave; ở mức thấp nhiều ng&agrave;y qua. Tại HOSE, c&oacute; 39,6 triệu chứng kho&aacute;n chuyển nhượng, tương đương 1.008 tỷ đồng. Trong đ&oacute;, STB, SSI, QCG khớp nhiều nhất, 1,1-2,9 triệu. Ở s&agrave;n H&agrave; Nội l&agrave; c&aacute;c m&atilde;: BVS, HBB, KLS, PVX, SHN, VND, với lượng giao dịch 1,1-2,1 triệu.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;chốt ng&agrave;y 25/1 tại 105,3 điểm, giảm th&ecirc;m 0,92 điểm. Cổ phiếu giảm &aacute;p đảo thị trường, với số lượng tr&ecirc;n 200. Thanh khoản giảm nhẹ so với phi&ecirc;n trước, đạt 24,1 triệu chứng kho&aacute;n, trị gi&aacute; 423 tỷ đồng.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;ở mức 42,84 điểm, vơi 0,38 điểm, ghi nhận 0,2 triệu cổ phiếu sang tay, ứng với 3,6 tỷ đồng, l&uacute;c 11h10.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Bạch Hường</strong></p>', 9, 3, 1456, 0, 1),
(386, 'Cổ phiếu lớn tiếp tục đẩy Vn-Index tăng mạnh', 'Dù không trụ lại được trên ngưỡng 520 điểm nhưng Vn-Index vẫn tăng hơn 7 điểm trong phiên giao dịch sáng nay. HNX-Index cũng đảo chiều tăng nhẹ sau 3 phiên điều chỉnh liên tiếp.', 'chung-khoan-0 (5).jpg', NULL, '<h1 class="Title">Cổ phiếu lớn tiếp tục đẩy Vn-Index tăng mạnh</h1>\r\n<h2 class="Lead">D&ugrave; kh&ocirc;ng trụ lại được tr&ecirc;n ngưỡng 520 điểm nhưng Vn-Index vẫn tăng hơn 7 điểm trong phi&ecirc;n giao dịch s&aacute;ng nay. HNX-Index cũng đảo chiều tăng nhẹ sau 3 phi&ecirc;n điều chỉnh li&ecirc;n tiếp.</h2>\r\n<p class="Normal">Tương tự những phi&ecirc;n giao dịch gần đ&acirc;y, đ&agrave; tăng điểm của s&agrave;n TP HCM được x&aacute;c lập ngay trong đợt khớp lệnh mở cửa khi Vn-Index tăng mạnh 6,34 điểm, l&ecirc;n 518,32 điểm chỉ sau 15 ph&uacute;t. Lượng giao dịch tại thời điểm n&agrave;y cũng tương đối khả quan với gần 1,7 triệu chứng kho&aacute;n được sang tay, tương đương hơn 54,5 tỷ đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0.jpg" alt="Chứng kho&aacute;n tiếp tục tăng điểm nhờ blue-chip. Ảnh minh họa: Ho&agrave;ng H&agrave;" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chứng kho&aacute;n tiếp tục tăng điểm nhờ blue-chip. Ảnh minh họa:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại thời điểm n&agrave;y, sàn TP HCM ghi nhận hơn 171 mã c&oacute; giao dịch với số m&atilde; tăng khoảng 70 trong khi số giảm gi&aacute; chỉ khoảng 40. V&ocirc;́n ngoại ti&ecirc;́p tục t&igrave;m tới các blue-chip với lượng giải ng&acirc;n mạnh mẽ v&agrave;o BVH, HAG, FPT&hellip; B&ecirc;n cạnh đ&oacute;, TLH, CMG, VIS, LAF, VNM cũng được nh&agrave; đầu tư giao dịch mạnh.</p>\r\n<p class="Normal">Bước sang khớp lệnh li&ecirc;n tục, BVH, CTG, MSN v&agrave; VCB tiếp tục chứng tỏ được vai tr&ograve; của c&aacute;c cổ phiếu dẫn dắt khi đồng loạt tăng trần v&agrave; gi&uacute;p Vn-Index c&oacute; l&uacute;c tăng gần 10 điểm. Khoảng 9h30, to&agrave;n thị trường ghi nhận gần 100 m&atilde; cổ phiếu tăng gi&aacute;, trong đ&oacute; SSI, REE, SAM, EIB, DPM tăng nhẹ. NTL tăng 1.000 đồng trong khi VPL tăng tới 1.600 đồng.</p>\r\n<p class="Normal">Đến cuối phi&ecirc;n, lực mua cổ phiếu c&oacute; phần chững lại, khiến Vn-Index chỉ c&ograve;n tăng được 7,52 điểm, l&ecirc;n 519,5 điểm. Như vậy, chỉ số của s&agrave;n TP HCM chỉ c&oacute; thể thử th&aacute;ch ngưỡng 520 điểm trong tuần sau. Trong phi&ecirc;n s&aacute;ng nay, khối lượng giao dịch to&agrave;n s&agrave;n đạt gần 47 triệu chứng kho&aacute;n, tương đương 1.272,07 tỷ đồng.</p>\r\n<p class="Normal">Tr&ecirc;n s&agrave;n H&agrave; Nội,&nbsp;<strong><span style="color: #4f4f4f;">HNX-Index</span></strong>&nbsp;tăng 0,81 điểm trong phi&ecirc;n giao dịch cuối tuần, l&ecirc;n 107,83 điểm. Khối lượng giao dịch, tuy vậy, chưa được cải thiện với gần 26,8 triệu chứng kho&aacute;n, tương đương 481,97 tỷ đồng được chuyển nhượng .</p>\r\n<p class="Normal">Kết th&uacute;c giao dịch buổi s&aacute;ng,&nbsp;<strong><span style="color: #4f4f4f;">UPCoM-Index</span></strong>&nbsp;giảm 0,87 điểm, xuống 43,92 điểm. Khối lượng giao dịch đạt 108.860 cổ phiếu, tương đương 1,46 tỷ đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 2395, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(387, 'Chứng khoán toàn cầu lùi bước vì Trung Quốc', 'Sắc đỏ tràn ngập các thị trường chứng khoán thế giới hôm qua do giới đầu tư dự đoán Trung Quốc sẽ thắt chặt tín dụng, tăng lãi suất để kiềm chế lạm phát và đà tăng trưởng chóng mặt của nền kinh tế.', 'buy.jpg', NULL, '<h1 class="Title">Chứng kho&aacute;n to&agrave;n cầu l&ugrave;i bước v&igrave; Trung Quốc</h1>\r\n<h2 class="Lead">Sắc đỏ tr&agrave;n ngập c&aacute;c thị trường chứng kho&aacute;n thế giới h&ocirc;m qua do giới đầu tư dự đo&aacute;n Trung Quốc sẽ thắt chặt t&iacute;n dụng, tăng l&atilde;i suất để kiềm chế lạm ph&aacute;t v&agrave; đ&agrave; tăng trưởng ch&oacute;ng mặt của nền kinh tế.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buy.jpg" alt="" width="450" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lạm ph&aacute;t tại Trung Quốc đang trở th&agrave;nh nỗi lo của giới đầu tư to&agrave;n cầu. Ảnh:&nbsp;<em>theepochtime.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Chuyến thăm của Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o đang thu h&uacute;t sự ch&uacute; &yacute; của giới truyền th&ocirc;ng quốc tế trong những ng&agrave;y qua</span>.</strong>&nbsp;Sự b&ugrave;ng nổ mạnh mẽ của nền kinh tế Trung Quốc gi&uacute;p &ocirc;ng Hồ c&oacute; lợi thế quan trọng hơn tr&ecirc;n b&agrave;n đ&agrave;m ph&aacute;n so với khi &ocirc;ng thăm Mỹ 5 năm trước.</p>\r\n<p class="Normal">Trung Quốc đ&atilde; trở th&agrave;nh nền kinh tế lớn thứ hai thế giới, c&oacute; thị trường xe hơi lớn nhất h&agrave;nh tinh v&agrave; số người sử dụng Internet lớn hơn cả d&acirc;n số Mỹ. Trong c&ugrave;ng khoảng thời gian đ&oacute;, Mỹ mất 5 triệu việc l&agrave;m, hứng chịu cuộc khủng hoảng t&agrave;i ch&iacute;nh trầm trọng v&agrave; chứng kiến tỷ lệ thất nghiệp tăng gấp đ&ocirc;i. V&igrave; thế, chẳng c&oacute; g&igrave; lạ khi &ocirc;ng Hồ Cẩm Đ&agrave;o chỉ nhượng bộ đ&ocirc;i ch&uacute;t đối với những đ&ograve;i hỏi từ Nh&agrave; Trắng, như nới lỏng kiểm so&aacute;t tiền tệ v&agrave; g&acirc;y sức &eacute;p đối với Triều Ti&ecirc;n.</p>\r\n<p class="Normal">Ch&iacute;nh phủ Trung Quốc th&ocirc;ng b&aacute;o nền kinh tế nước n&agrave;y tăng trưởng 10,3% trong năm ngo&aacute;i. Giới chuy&ecirc;n gia kinh tế dự đo&aacute;n Ng&acirc;n h&agrave;ng trung ương Trung Quốc sẽ tăng l&atilde;i suất để h&atilde;m tốc độ tăng trưởng. Động th&aacute;i đ&oacute; c&oacute; thể l&agrave;m giảm nhu cầu của Trung Quốc đối với nhiều loại nguy&ecirc;n liệu sản xuất, như dầu v&agrave; đồng.</p>\r\n<p class="Normal">Mối lo ngại rằng Trung Quốc sẽ thực hiện những biện ph&aacute;p cần thiết để h&atilde;m đ&agrave; tăng trưởng kinh tế khiến gi&aacute; c&aacute;c chỉ số chứng kho&aacute;n tại Mỹ lao dốc. Chỉ số c&ocirc;ng nghiệp Dow Jones v&agrave; S&amp;P 500 c&ugrave;ng mất 0,1% số điểm, c&ograve;n chỉ số Nasdaq tụt 0,8%.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Ở b&ecirc;n kia bờ Đại T&acirc;y Dương, giới đầu tư lo ch&acirc;u &Acirc;u cũng lo ngại Trung Quốc sẽ thắt chặt t&iacute;n dụng để kiềm chế lạm ph&aacute;t.</span></strong>&nbsp;Chỉ số FTSE 100 của Anh giảm 1,8%, c&ograve;n DAX của Đức v&agrave; CAC-40 của Ph&aacute;p lần lượt mất 0,8% v&agrave; 0,3%.</p>\r\n<p class="Normal">Người đứng đầu quỹ cứu trợ của Li&ecirc;n minh ch&acirc;u &Acirc;u khẳng định quỹ c&oacute; đủ tiền để cho Bồ Đ&agrave;o Nha v&agrave; T&acirc;y Ban Nha vay trong trường hợp hai nước n&agrave;y y&ecirc;u cầu.</p>\r\n<p class="Normal">Chỉ số niềm tin của người ti&ecirc;u d&ugrave;ng tại 17 nước sử dụng đồng euro giảm trong th&aacute;ng thứ hai li&ecirc;n tiếp kể từ th&aacute;ng 12 năm ngo&aacute;i. Người d&acirc;n vẫn tỏ ra do dự khi chi ti&ecirc;u trong bối cảnh gi&aacute; cả tăng li&ecirc;n tục, tỷ lệ thất nghiệp cao v&agrave; ch&iacute;nh phủ ban h&agrave;nh những biện ph&aacute;p thắt lưng buộc bụng.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Mặc d&ugrave; nền kinh tế Trung Quốc tăng trưởng mạnh mẽ trong năm ngo&aacute;i, giới l&atilde;nh đạo nước n&agrave;y lại đang vật lộn trong cuộc chiến chống đ&agrave; tăng vọt của gi&aacute; cả.</span></strong>&nbsp;Theo b&aacute;o c&aacute;o của Tổng cục Thống k&ecirc; Trung Quốc, tỷ lệ lạm ph&aacute;t đạt 4,6% v&agrave;o th&aacute;ng 12, thấp hơn một ch&uacute;t so với tỷ lệ 5,1% của th&aacute;ng 11.</p>\r\n<p class="Normal">B&aacute;o c&aacute;o về tỷ lệ lạm ph&aacute;t của ch&iacute;nh phủ Trung Quốc khiến giới đầu tư ch&acirc;u &Aacute; ch&ugrave;n bước do lo ngại Bắc Kinh sẽ tiếp tục thực hiện những biện ph&aacute;p để thắt chặt t&iacute;n dụng. C&aacute;c chỉ số chứng kho&aacute;n tại ch&acirc;u &Aacute; đồng loạt lao dốc. Shanghai Composite của Trung Quốc v&agrave; Hang Seng của Hong Kong mất 2,9% v&agrave; 1,7%, Nikkei 225 của Nhật Bản giảm 1,1%, Kospi của H&agrave;n Quốc mất 0,4%.</p>\r\n<p class="Normal">Trong phi&ecirc;n giao dịch đầu ti&ecirc;n v&agrave;o s&aacute;ng nay, c&aacute;c chỉ số chứng kho&aacute;n tại ch&acirc;u &Aacute; tiếp tục lao dốc. Chỉ số Nikkei 225 của Nhật Bản v&agrave; Kospi của H&agrave;n Quốc lần lượt mất 0,9% v&agrave; 1%, c&ograve;n chỉ số S&amp;P/ASX 200 của Australia giảm 0,8%.</p>\r\n<p class="Normal">Đầu tư nước ngo&agrave;i v&agrave;o Th&aacute;i Lan giảm gần một phần ba trong năm ngo&aacute;i do c&aacute;c doanh nghiệp lo ngại về bất ổn ch&iacute;nh trị tại đ&acirc;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Việt</strong></p>', 9, 3, 416, 0, 1),
(389, 'Vn-Index vượt mốc 500 điểm', 'Sàn TP HCM có phiên tăng điểm mạnh nhất kể từ đầu năm 2011 với hơn 8 điểm được cộng thêm cho Vn-Index. Tuy nhiên, HNX-Index vẫn chưa thoát khỏi áp lực điều chỉnh của phiên trước.', 'chung-khoan-0 (6).jpg', NULL, '<h1 class="Title">Chứng kho&aacute;n to&agrave;n cầu l&ugrave;i bước v&igrave; Trung Quốc</h1>\r\n<h2 class="Lead">Sắc đỏ tr&agrave;n ngập c&aacute;c thị trường chứng kho&aacute;n thế giới h&ocirc;m qua do giới đầu tư dự đo&aacute;n Trung Quốc sẽ thắt chặt t&iacute;n dụng, tăng l&atilde;i suất để kiềm chế lạm ph&aacute;t v&agrave; đ&agrave; tăng trưởng ch&oacute;ng mặt của nền kinh tế.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buy.jpg" alt="" width="450" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lạm ph&aacute;t tại Trung Quốc đang trở th&agrave;nh nỗi lo của giới đầu tư to&agrave;n cầu. Ảnh:&nbsp;<em>theepochtime.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Chuyến thăm của Chủ tịch Trung Quốc Hồ Cẩm Đ&agrave;o đang thu h&uacute;t sự ch&uacute; &yacute; của giới truyền th&ocirc;ng quốc tế trong những ng&agrave;y qua</span>.</strong>&nbsp;Sự b&ugrave;ng nổ mạnh mẽ của nền kinh tế Trung Quốc gi&uacute;p &ocirc;ng Hồ c&oacute; lợi thế quan trọng hơn tr&ecirc;n b&agrave;n đ&agrave;m ph&aacute;n so với khi &ocirc;ng thăm Mỹ 5 năm trước.</p>\r\n<p class="Normal">Trung Quốc đ&atilde; trở th&agrave;nh nền kinh tế lớn thứ hai thế giới, c&oacute; thị trường xe hơi lớn nhất h&agrave;nh tinh v&agrave; số người sử dụng Internet lớn hơn cả d&acirc;n số Mỹ. Trong c&ugrave;ng khoảng thời gian đ&oacute;, Mỹ mất 5 triệu việc l&agrave;m, hứng chịu cuộc khủng hoảng t&agrave;i ch&iacute;nh trầm trọng v&agrave; chứng kiến tỷ lệ thất nghiệp tăng gấp đ&ocirc;i. V&igrave; thế, chẳng c&oacute; g&igrave; lạ khi &ocirc;ng Hồ Cẩm Đ&agrave;o chỉ nhượng bộ đ&ocirc;i ch&uacute;t đối với những đ&ograve;i hỏi từ Nh&agrave; Trắng, như nới lỏng kiểm so&aacute;t tiền tệ v&agrave; g&acirc;y sức &eacute;p đối với Triều Ti&ecirc;n.</p>\r\n<p class="Normal">Ch&iacute;nh phủ Trung Quốc th&ocirc;ng b&aacute;o nền kinh tế nước n&agrave;y tăng trưởng 10,3% trong năm ngo&aacute;i. Giới chuy&ecirc;n gia kinh tế dự đo&aacute;n Ng&acirc;n h&agrave;ng trung ương Trung Quốc sẽ tăng l&atilde;i suất để h&atilde;m tốc độ tăng trưởng. Động th&aacute;i đ&oacute; c&oacute; thể l&agrave;m giảm nhu cầu của Trung Quốc đối với nhiều loại nguy&ecirc;n liệu sản xuất, như dầu v&agrave; đồng.</p>\r\n<p class="Normal">Mối lo ngại rằng Trung Quốc sẽ thực hiện những biện ph&aacute;p cần thiết để h&atilde;m đ&agrave; tăng trưởng kinh tế khiến gi&aacute; c&aacute;c chỉ số chứng kho&aacute;n tại Mỹ lao dốc. Chỉ số c&ocirc;ng nghiệp Dow Jones v&agrave; S&amp;P 500 c&ugrave;ng mất 0,1% số điểm, c&ograve;n chỉ số Nasdaq tụt 0,8%.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Ở b&ecirc;n kia bờ Đại T&acirc;y Dương, giới đầu tư lo ch&acirc;u &Acirc;u cũng lo ngại Trung Quốc sẽ thắt chặt t&iacute;n dụng để kiềm chế lạm ph&aacute;t.</span></strong>&nbsp;Chỉ số FTSE 100 của Anh giảm 1,8%, c&ograve;n DAX của Đức v&agrave; CAC-40 của Ph&aacute;p lần lượt mất 0,8% v&agrave; 0,3%.</p>\r\n<p class="Normal">Người đứng đầu quỹ cứu trợ của Li&ecirc;n minh ch&acirc;u &Acirc;u khẳng định quỹ c&oacute; đủ tiền để cho Bồ Đ&agrave;o Nha v&agrave; T&acirc;y Ban Nha vay trong trường hợp hai nước n&agrave;y y&ecirc;u cầu.</p>\r\n<p class="Normal">Chỉ số niềm tin của người ti&ecirc;u d&ugrave;ng tại 17 nước sử dụng đồng euro giảm trong th&aacute;ng thứ hai li&ecirc;n tiếp kể từ th&aacute;ng 12 năm ngo&aacute;i. Người d&acirc;n vẫn tỏ ra do dự khi chi ti&ecirc;u trong bối cảnh gi&aacute; cả tăng li&ecirc;n tục, tỷ lệ thất nghiệp cao v&agrave; ch&iacute;nh phủ ban h&agrave;nh những biện ph&aacute;p thắt lưng buộc bụng.</p>\r\n<p class="Normal"><strong><span style="color: #4f4f4f;">Mặc d&ugrave; nền kinh tế Trung Quốc tăng trưởng mạnh mẽ trong năm ngo&aacute;i, giới l&atilde;nh đạo nước n&agrave;y lại đang vật lộn trong cuộc chiến chống đ&agrave; tăng vọt của gi&aacute; cả.</span></strong>&nbsp;Theo b&aacute;o c&aacute;o của Tổng cục Thống k&ecirc; Trung Quốc, tỷ lệ lạm ph&aacute;t đạt 4,6% v&agrave;o th&aacute;ng 12, thấp hơn một ch&uacute;t so với tỷ lệ 5,1% của th&aacute;ng 11.</p>\r\n<p class="Normal">B&aacute;o c&aacute;o về tỷ lệ lạm ph&aacute;t của ch&iacute;nh phủ Trung Quốc khiến giới đầu tư ch&acirc;u &Aacute; ch&ugrave;n bước do lo ngại Bắc Kinh sẽ tiếp tục thực hiện những biện ph&aacute;p để thắt chặt t&iacute;n dụng. C&aacute;c chỉ số chứng kho&aacute;n tại ch&acirc;u &Aacute; đồng loạt lao dốc. Shanghai Composite của Trung Quốc v&agrave; Hang Seng của Hong Kong mất 2,9% v&agrave; 1,7%, Nikkei 225 của Nhật Bản giảm 1,1%, Kospi của H&agrave;n Quốc mất 0,4%.</p>\r\n<p class="Normal">Trong phi&ecirc;n giao dịch đầu ti&ecirc;n v&agrave;o s&aacute;ng nay, c&aacute;c chỉ số chứng kho&aacute;n tại ch&acirc;u &Aacute; tiếp tục lao dốc. Chỉ số Nikkei 225 của Nhật Bản v&agrave; Kospi của H&agrave;n Quốc lần lượt mất 0,9% v&agrave; 1%, c&ograve;n chỉ số S&amp;P/ASX 200 của Australia giảm 0,8%.</p>\r\n<p class="Normal">Đầu tư nước ngo&agrave;i v&agrave;o Th&aacute;i Lan giảm gần một phần ba trong năm ngo&aacute;i do c&aacute;c doanh nghiệp lo ngại về bất ổn ch&iacute;nh trị tại đ&acirc;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Việt</strong></p>', 9, 3, 1023, 0, 1),
(390, 'Hủy đợt chào bán chứng khoán của Dược Viễn Đông', 'Quyết định này vừa được Ủy ban Chứng khoán đưa ra sau khi Dược Viễn Đông không thể khắc phục hàng loạt thiếu sót trong kế hoạch phát hành hơn 7 triệu cổ phiếu mà doanh nghiệp này thực hiện hồi cuối năm 2010.', 'chung-khoan-0 (7).jpg', NULL, '<h1 class="Title">Hủy đợt ch&agrave;o b&aacute;n chứng kho&aacute;n của Dược Viễn Đ&ocirc;ng</h1>\r\n<h2 class="Lead">Quyết định n&agrave;y vừa được Ủy ban Chứng kho&aacute;n đưa ra sau khi Dược Viễn Đ&ocirc;ng kh&ocirc;ng thể khắc phục h&agrave;ng loạt thiếu s&oacute;t trong kế hoạch ph&aacute;t h&agrave;nh hơn 7 triệu cổ phiếu m&agrave; doanh nghiệp n&agrave;y thực hiện hồi cuối năm 2010.<br /><span style="font-size: 10px; font-weight: normal;">Đợt ph&aacute;t h&agrave;nh n&oacute;i tr&ecirc;n từng bị Ủy ban Chứng kho&aacute;n (SSC)&nbsp;đ&igrave;nh chỉ&nbsp;hồi cuối th&aacute;ng 11/2010 do doanh nghiệp c&ocirc;ng bố th&ocirc;ng tin sai sự thật trong bản c&aacute;o bạch ch&agrave;o b&aacute;n. Sau sự kiện n&agrave;y, Dược Viễn Đ&ocirc;ng c&oacute; 60 ng&agrave;y để khắc phục c&aacute;c thiếu s&oacute;t, sai lệch nhưng ph&iacute;a doanh nghiệp đ&atilde; kh&ocirc;ng thể ho&agrave;n th&agrave;nh đ&uacute;ng hạn. Đ&acirc;y ch&iacute;nh l&agrave; l&yacute; do khiến SSC đưa ra quyết định hủy đợt ch&agrave;o b&aacute;n n&oacute;i tr&ecirc;n.</span></h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chung-khoan-0 (7).jpg" alt="Dược Viễn Đ&ocirc;ng c&oacute; 15 ng&agrave;y để ho&agrave;n trả tiền cho nh&agrave; đầu tư. Ảnh minh họa: N.M" width="490" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Dược Viễn Đ&ocirc;ng c&oacute; 15 ng&agrave;y để ho&agrave;n trả tiền cho nh&agrave; đầu tư. Ảnh minh họa:&nbsp;<em>N.M</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Sai s&oacute;t lớn nhất của Dược Viễn Đ&ocirc;ng, theo Ủy ban Chứng kho&aacute;n, l&agrave; việc doanh nghiệp n&agrave;y đ&atilde; kh&ocirc;ng đưa ra được c&aacute;c chứng từ chứng minh doanh thu c&oacute; thật từ giao dịch với một số doanh nghiệp (đa số l&agrave; doanh nghiệp li&ecirc;n doanh hoặc li&ecirc;n quan trực tiếp với Dược Viễn Đ&ocirc;ng). Trong khi đ&oacute;, đa phần doanh thu của c&ocirc;ng ty lại xuất ph&aacute;t từ c&aacute;c giao dịch n&agrave;y.</p>\r\n<p class="Normal">Cũng theo Ủy ban Chứng kho&aacute;n, tổng t&agrave;i sản r&ograve;ng của Dược Viễn Đ&ocirc;ng hiện tương đương 800 tỷ đồng trong khi tổng gi&aacute; trị tiền vay v&agrave; l&atilde;i vay phải trả ng&acirc;n h&agrave;ng l&agrave; 728 tỷ đồng. Ngo&agrave;i ra, c&ocirc;ng ty phải trả lại tiền cho c&aacute;c cổ đ&ocirc;ng đề nghị ho&agrave;n trả tiền mua cổ phiếu từ đợt ch&agrave;o b&aacute;n l&agrave; hơn 69 tỷ đồng, c&aacute;c khoản phải trả tr&aacute;i phiếu cho c&ocirc;ng đo&agrave;n v&agrave; cổ phiếu tri &acirc;n kh&aacute;ch h&agrave;ng hơn 89 tỷ đồng (khoản n&agrave;y kh&ocirc;ng đưa v&agrave;o b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh từ trước đến nay). C&aacute;c khoản chưa thể x&aacute;c nhận, đối chiếu được l&agrave; 641 tỷ đồng.</p>\r\n<p class="Normal">Ngo&agrave;i ra, doanh nghiệp cũng thừa nhận việc đưa th&ocirc;ng tin kh&ocirc;ng được kiểm chứng, sai lệch về đối t&aacute;c chiến lược YVERY (c&ocirc;ng ty dược của Ph&aacute;p, li&ecirc;n doanh với Dược Viễn Đ&ocirc;ng th&agrave;nh lập Lily of France) g&acirc;y ảnh hưởng tới quyết định của nh&agrave; đầu tư. B&ecirc;n cạnh đ&oacute; việc sử dụng vốn c&oacute; được từ đợt ph&aacute;t h&agrave;nh của Dược Viễn Đ&ocirc;ng cũng được SSC đ&aacute;nh gi&aacute; l&agrave; sai mục đ&iacute;ch so với kế hoạch ban đầu.</p>\r\n<p class="Normal">Theo kết luận của Ủy ban Chứng kho&aacute;n, c&aacute;c sai phạm của Dược Viễn Đ&ocirc;ng đ&atilde; vi phạm c&aacute;c Điều 14, 17 v&agrave; 18 Luật Chứng kho&aacute;n khi lập v&agrave; x&aacute;c nhận hồ sơ ch&agrave;o b&aacute;n chứng kho&aacute;n. Đ&acirc;y l&agrave; cơ sở để Ủy ban đưa ra quyết định hủy đợt ch&agrave;o b&aacute;n hơn 7 triệu cổ phiếu DVD ra c&ocirc;ng ch&uacute;ng.</p>\r\n<p class="Normal">Sau quyết định n&agrave;y, ph&iacute;a Dược Viễn Đ&ocirc;ng c&oacute; 15 ng&agrave;y để ho&agrave;n trả lại tiền cho nh&agrave; đầu tư đ&atilde; mua cổ phiếu trong đợt ch&agrave;o b&aacute;n. Sau thời điểm n&agrave;y, nếu kh&ocirc;ng được doanh nghiệp ho&agrave;n trả tiền v&agrave; bồi thường thiệt hại, nh&agrave; đầu tư c&oacute; quyền khởi kiện ra cơ quan ph&aacute;p luật.</p>\r\n<p class="Normal">Cổ phiếu DVD của Dược Viễn Đ&ocirc;ng hiện bị đặt v&agrave;o diện&nbsp;kiểm so&aacute;t&nbsp;tại Sở Giao dịch chứng kho&aacute;n TP HCM v&agrave; chỉ được ph&eacute;p giao dịch trong v&ograve;ng 15 ph&uacute;t cuối mỗi phi&ecirc;n sau khi bị SSC đ&igrave;nh chỉ đợt ph&aacute;t h&agrave;nh th&ecirc;m. Bản th&acirc;n đội ngũ l&atilde;nh đạo của doanh nghiệp cũng c&oacute; nhiều x&aacute;o trộn sau khi Chủ tịch Hội đồng quản trị L&ecirc; Văn Dũng v&agrave; một số đồng sự bị cơ quan điều tra&nbsp;khởi tố&nbsp;v&igrave; tội l&agrave;m gi&aacute; chứng kho&aacute;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nhật Minh</strong></p>', 9, 3, 2129, 0, 1),
(575, 'Ảnh đẹp động vật trong tuần', 'Nàng gấu bị sa thải khỏi gánh xiếc vì quá béo, gấu trúc mừng sinh nhật 6 tuổi. Dưới đây là những hình ảnh đẹp về động vật tuần qua.', 'dv1 (1).jpg', NULL, '<p class="Title">Ảnh đẹp động vật trong tuần</p>\n<p class="Lead">N&agrave;ng gấu bị sa thải khỏi g&aacute;nh xiếc v&igrave; qu&aacute; b&eacute;o, gấu tr&uacute;c mừng sinh nhật 6 tuổi. Dưới đ&acirc;y l&agrave; những h&igrave;nh ảnh đẹp về động vật tuần qua.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv1 (1).jpg" border="1" alt="Ảnh: EPA." width="480" height="340" /></td>\n</tr>\n<tr>\n<td class="Image">Ch&uacute; đười ươi tập tạ trong một buổi tr&igrave;nh diễn ở vườn th&uacute; Bangkok, Th&aacute;i Lan. Ảnh:&nbsp;<em>EPA</em>.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv2.jpg" border="1" alt="Ảnh: Quirky China News." width="405" height="477" /></td>\n</tr>\n<tr>\n<td class="Image">Một c&ocirc; n&agrave;ng gấu trở th&agrave;nh ng&ocirc;i sao ở vườn th&uacute; Trung Quốc nhờ đứng chụp ảnh với du kh&aacute;ch. Tian Tian bị đuổi việc khỏi một g&aacute;nh xiếc v&igrave; qu&aacute; b&eacute;o. Giờ đ&acirc;y, c&ocirc; đứng chụp ảnh với du kh&aacute;ch ở vườn th&uacute; tại tỉnh Sơn Đ&ocirc;ng. Sau khi chụp ảnh, c&ocirc; thường nh&igrave;n chằm chằm v&agrave;o đồ ăn của du kh&aacute;ch cho tới khi họ đưa cho c&ocirc;. C&ocirc; rất th&iacute;ch bia. Ảnh:&nbsp;<em>Quirky China News.</em></td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv3.1.jpg" border="1" alt="Ảnh: AP." width="426" height="480" /></td>\n</tr>\n<tr>\n<td class="Image">Gấu tr&uacute;c Yuanyuan mừng sinh nhật lần thứ 6 với chiếc b&aacute;nh đặc biệt ở Đ&agrave;i Bắc, Đ&agrave;i Loan. Ảnh:&nbsp;<em>AP</em>.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv4.jpg" border="1" alt="Ảnh: AP." width="480" height="319" /></td>\n</tr>\n<tr>\n<td class="Image">Du kh&aacute;ch chi&ecirc;m ngưỡng ch&uacute; ch&oacute; x&ugrave; được nhuộm l&ocirc;ng cho giống gấu tr&uacute;c ở hội chợ vật nu&ocirc;i tại Thượng Hải, Trung Quốc. Ảnh:&nbsp;<em>AP</em>.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv5.jpg" border="1" alt="Ảnh: YouTube." width="480" height="384" /></td>\n</tr>\n<tr>\n<td class="Image">Con c&aacute; biển xấu x&iacute; c&oacute; t&ecirc;n Asian sheepshead wrasse bơi qua v&ugrave;ng biển Nhật Bản. Vẻ ngo&agrave;i đặc biệt của n&oacute; khiến n&oacute; được đặt biệt danh con c&aacute; Shrek, giống ch&agrave;ng Shrek xấu x&iacute; trong phim hoạt h&igrave;nh của Mỹ. Ảnh:&nbsp;<em>YouTube</em>.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv6.jpg" border="1" alt="Ảnh: Solent." width="480" height="386" /></td>\n</tr>\n<tr>\n<td class="Image">Cặp sếu n&agrave;y được đặt t&ecirc;n l&agrave; "mặt sấp, mặt ngửa" v&igrave; một con lu&ocirc;n quay đi khi bị chụp ảnh tại vườn th&uacute; ở Valencia, T&acirc;y Ban Nha. Ảnh:&nbsp;<em>Solent</em>.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="dv8.jpg" border="1" alt="Hai con ngựa cắn nhau ở Duelmen, Đức. Ảnh: AP." width="480" height="400" /></td>\n</tr>\n<tr>\n<td class="Image">Hai con ngựa cắn nhau ở Duelmen, Đức. Ảnh:&nbsp;<em>AP</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p>', 35, 8, 2808, 0, 1),
(576, 'Ảnh đẹp động vật trong tuần', 'Voi được sơn mình giống hổ, gấu trúc diện quần áo đẹp đón Tết. Dưới đây là một số hình ảnh đẹp về động vật tuần qua.', 'dv1 (2).jpg', NULL, '<p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">Voi được sơn m&igrave;nh giống hổ, gấu tr&uacute;c diện quần &aacute;o đẹp đ&oacute;n Tết. Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh đẹp về động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (2).jpg" border="1" alt="" width="480" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con hổ Siberia tr&igrave;nh diễn trước đ&aacute;m đ&ocirc;ng du kh&aacute;ch ở vườn th&uacute; Ph&uacute;c Ch&acirc;u, Trung Quốc, trong ng&agrave;y thứ hai của năm Canh Dần. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2.jpg" border="1" alt="" width="480" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gấu tr&uacute;c Lin Ping được diện đồ đẹp đ&oacute;n năm mới ở vườn th&uacute; Chiang Mai, Th&aacute;i Lan. Ảnh: EPA.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3.jpg" border="1" alt="" width="480" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con voi ở vườn th&uacute; Ayutthaya, Th&aacute;i Lan, được sơn m&igrave;nh giống như hổ. Ảnh:&nbsp;<em>Barcroft India.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5.jpg" border="1" alt="" width="348" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con c&aacute;o đuổi theo &oacute; biển giữa m&ecirc;nh m&ocirc;ng tuyết trắng tại miền bắc Nhật Bản. Ảnh:&nbsp;<em>EPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6.jpg" border="1" alt="" width="423" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con gấu so t&agrave;i với nhau ở c&ocirc;ng vi&ecirc;n quốc gia Katmai ở Alaska, Mỹ. Ảnh:&nbsp;<em>Solent</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7.jpg" border="1" alt="" width="297" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một c&ocirc; c&uacute;n được mặc trang phục truyền thống của Colombia trong lễ hội h&oacute;a trang. Ảnh:&nbsp;<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4.jpg" border="1" alt="" width="480" height="315" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con ngựa vằn chạy ra ngo&agrave;i phố ở th&agrave;nh phố Atlanta, Mỹ. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p>', 35, 8, 780, 0, 1),
(578, 'Voi chơi bóng rổ', 'Những bức ảnh về hai chú voi chơi bóng rổ tại Thái Lan một lần nữa chứng minh rằng khả năng học hỏi của chúng là vô hạn.', 'v0.jpg', NULL, '<p class="Title">Voi chơi b&oacute;ng rổ</p>\r\n<p class="Lead">Những bức ảnh về hai ch&uacute; voi chơi b&oacute;ng rổ tại Th&aacute;i Lan một lần nữa chứng minh rằng khả năng học hỏi của ch&uacute;ng l&agrave; v&ocirc; hạn.<br /><br /></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v0.jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Ch&uacute; voi 6 tuổi Malie v&agrave; ch&uacute; voi 9 tuổi trong trung t&acirc;m Island Safari, đảo Koh Samui, Th&aacute;i Lan được huấn luyện để chơi b&oacute;ng rổ. Ảnh:&nbsp;<em>Bronek Kaminiski</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v1.jpg" alt="Island Safari" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">C&aacute;c nh&acirc;n vi&ecirc;n ở Trung t&acirc;m Island Safari cho biết, những con vật to lớn phải tập luyện rất vất vả để học những kỹ thuật cơ bản của bộ m&ocirc;n b&oacute;ng rổ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v3.jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Đầu ti&ecirc;n ch&uacute;ng phải học những động t&aacute;c giữ b&oacute;ng thăng bằng tr&ecirc;n đầu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v4.jpg" alt="Rồi kỹ thuật giữ b&oacute;ng." width="380" height="542" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Rồi ch&uacute;ng tập giữ b&oacute;ng bằng v&ograve;i v&agrave; di chuyển.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v5.jpg" alt="N&eacute;m b&oacute;ng v&agrave;o rổ." width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">N&eacute;m b&oacute;ng v&agrave;o rổ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v7.jpg" alt="Với" width="380" height="567" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Với vẻ phấn kh&iacute;ch, Malie giữ b&oacute;ng v&agrave; giữ thăng bằng tr&ecirc;n hai ch&acirc;n sau để chờ hiệu lệnh của người huấn luyện.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v8.jpg" alt="Sau khi nghe hiệu lệnh, con voi n&eacute;m b&oacute;ng v&agrave;o rổ." width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Sau khi nghe hiệu lệnh, con voi n&eacute;m b&oacute;ng v&agrave;o rổ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v9.jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">"Ch&uacute;ng t&ocirc;i phải huấn luyện những con voi trong 2-3 th&aacute;ng để ch&uacute;ng nắm được những kỹ thuật cơ bản. Nhưng may mắn l&agrave; tr&igrave;nh độ của ch&uacute;ng cải thiện từng ng&agrave;y", Ning, một nh&acirc;n vi&ecirc;n của trung t&acirc;m, n&oacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v10.jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Hai con voi thực hiện những động t&aacute;c như thể ch&uacute;ng sắp so t&agrave;i với nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long (</strong>Ảnh<strong>:&nbsp;</strong><em>Bronek Kaminiski</em>)</p>', 35, 8, 2215, 0, 1),
(579, 'Khi động vật say ngủ', 'Ngay cả những con vật dữ tợn cũng trở nên hiền lành dễ thương khi ngủ.', '02_1.jpg', NULL, '<p class="Title">Khi động vật say ngủ</p>\r\n<p class="Lead">Ngay cả những con vật dữ tợn cũng trở n&ecirc;n hiền l&agrave;nh dễ thương khi ngủ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="02_1.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&egrave;o ngủ tr&ecirc;n ghế &ecirc;m.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="01.jpg" border="1" alt="" width="350" height="517" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gấu tr&uacute;c d&agrave;n h&agrave;ng ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="04.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hổ con nhắm t&iacute;t mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="05.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sư tử c&aacute;i ngủ v&ecirc;nh r&acirc;u.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="9.jpg" border="1" alt="" width="350" height="528" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngựa vừa ngủ vừa cười.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="06.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mẹ con gấu Bắc cực &ocirc;m nhau ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="07.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đười ươi vắt tay l&ecirc;n tr&aacute;n ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="08.jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chuột lim dim.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Minh</strong>&nbsp;<em>(Ảnh: Hufingtonpost)</em></p>', 35, 8, 2380, 0, 1),
(580, 'Động vật hôn nhau', 'Chó, mèo, chim cánh cụt cũng biết cách tỏ tình bằng những nụ hôn.', 'dv1 (3).jpg', NULL, '<p class="Title">Động vật h&ocirc;n nhau</p>\r\n<p class="Lead">Ch&oacute;, m&egrave;o, chim c&aacute;nh cụt cũng biết c&aacute;ch tỏ t&igrave;nh bằng những nụ h&ocirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (3).jpg" border="1" alt="" width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con ch&oacute; t&igrave;nh tứ b&ecirc;n nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (3).jpg" border="1" alt="" width="450" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&egrave;o cũng biết &acirc;u yếm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (3).jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khỉ mẹ nựng khỉ con.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4 (3).jpg" border="1" alt="" width="450" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim c&aacute;nh cụt h&ocirc;n kiểu Ph&aacute;p.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (3).jpg" border="1" alt="" width="450" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con s&oacute;c ch&oacute; đắm m&igrave;nh trong nụ h&ocirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (3).jpg" border="1" alt="" width="450" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đười ươi thơm nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv8 (3).jpg" border="1" alt="" width="450" height="340" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">R&ugrave;a cũng biết t&igrave;nh tứ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv9 (3).jpg" border="1" alt="" width="450" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&oacute; biển tỏ t&igrave;nh với nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong>&nbsp;(Ảnh:&nbsp;<em>Flickr</em>)</p>', 35, 8, 805, 0, 1),
(581, 'Ảnh đẹp động vật năm 2009', 'Khỉ lái xe máy đồ chơi, chó đeo kính đen sành điệu. Dưới đây là những bức ảnh động vật đẹp nhất trong năm của Reuters.', 'dv1 (4).jpg', NULL, '<p class="Title">Ảnh đẹp động vật năm 2009</p>\r\n<p class="Lead">Khỉ l&aacute;i xe m&aacute;y đồ chơi, ch&oacute; đeo k&iacute;nh đen s&agrave;nh điệu. Dưới đ&acirc;y l&agrave; những bức ảnh động vật đẹp nhất trong năm của&nbsp;<em>Reuters.</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (4).jpg" border="1" alt="" width="450" height="282" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ocirc; c&aacute; voi trắng Aurora, 20 tuổi, bơi c&ugrave;ng đứa con vừa mới ra đời ở Vancouver, Canada.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (4).jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cậu b&eacute; bơi c&ugrave;ng tr&acirc;u dưới s&ocirc;ng Tawi trong ng&agrave;y h&egrave; ở Jammu, Ấn Độ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (4).jpg" border="1" alt="" width="450" height="271" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con gấu trắng giũ nước khỏi người tại vườn th&uacute; ở Quebec, Canada.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4 (4).jpg" border="1" alt="" width="450" height="287" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con khỉ con uống nước trong một c&aacute;i vũng ở tỉnh H&agrave; Nam, Trung Quốc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5 (4).jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con vượn bị mẹ giữ chặt trong vườn th&uacute; ở Vienna, &Aacute;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (4).jpg" border="1" alt="" width="450" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con ch&oacute; diện quần &aacute;o tham gia lễ hội d&agrave;nh cho vật nu&ocirc;i tại Cali, Colombia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (4).jpg" border="1" alt="" width="450" height="298" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Diễn vi&ecirc;n xiếc dắt con khỉ ngồi tr&ecirc;n chiếc xe m&aacute;y đồ chơi dưới trời mưa ở Jakarta, Indonesia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv9 (4).jpg" border="1" alt="" width="337" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con kỳ nh&ocirc;ng ngồi ghế chụp ảnh trong buổi luyện tập với c&ocirc; chủ ở ngoại &ocirc; Bangkok, Th&aacute;i Lan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv11 (4).jpg" border="1" alt="" width="450" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ba ch&uacute; ch&oacute; x&ugrave; l&agrave;m d&aacute;ng trong lễ hội ch&oacute; ở Tokyo, Nhật Bản.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>H. Ninh</strong></p>', 35, 8, 2603, 0, 1),
(582, 'Cảnh tượng núi lửa phun trào dưới đáy đại dương', 'Các nhà khoa học ở Mỹ xúc động khi lần đầu tiên ghi lại được cảnh núi lửa phun trào dưới đáy đại dương.', 'nl.jpg', NULL, '<p class="Title">Cảnh tượng n&uacute;i lửa phun tr&agrave;o dưới đ&aacute;y đại dương</p>\r\n<p class="Lead">C&aacute;c nh&agrave; khoa học ở Mỹ x&uacute;c động khi lần đầu ti&ecirc;n ghi lại được cảnh n&uacute;i lửa phun tr&agrave;o dưới đ&aacute;y đại dương.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nl.jpg" border="1" alt="N&uacute;i lửa T&acirc;y Mata phun tr&agrave;o. Ảnh: Seatle Times." width="380" height="361" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">N&uacute;i lửa T&acirc;y Mata phun tr&agrave;o. Ảnh:&nbsp;<em>Seatle Times</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Gần ba phần tư c&aacute;c vụ n&uacute;i lửa phun tr&agrave;o tr&ecirc;n thế giới diễn ra dưới đại dương tuy nhi&ecirc;n con người chưa bao giờ tận mắt chứng kiến dung nham tu&ocirc;n tr&agrave;o từ miệng n&uacute;i.</p>\r\n<p class="Normal">Một nh&oacute;m c&aacute;c nh&agrave; khoa học thực hiện cuộc th&aacute;m hiểm tới khu vực ngọn n&uacute;i lửa T&acirc;y Mata ở Th&aacute;i B&igrave;nh Dương hồi th&aacute;ng 5. Trong khoảng 1 tiếng, họ nh&igrave;n thấy kh&oacute;i bốc l&ecirc;n. "Sau đ&oacute;, dung nham đỏ tu&ocirc;n ra", nh&agrave; hải dương học Joe Resing, trưởng đo&agrave;n th&aacute;m hiểm, cho biết. "Tất cả ch&uacute;ng t&ocirc;i đều ph&aacute;t đi&ecirc;n l&ecirc;n".</p>\r\n<p class="Normal">Ngọn n&uacute;i lửa n&agrave;y nằm c&aacute;ch mặt nước biển khoảng 1.200 m.<em>&nbsp;Seatle Times</em>&nbsp;cho hay n&uacute;i lửa phun tr&agrave;o tạo th&ecirc;m một đ&aacute;y biển mới.</p>\r\n<p class="Normal">Đoạn video được ghi lại từ đầu năm nay mới được c&ocirc;ng bố h&ocirc;m qua trong cuộc gặp mặt của Hiệp hội Địa vật l&yacute; tại th&agrave;nh phố San Francisco. Video c&oacute; h&igrave;nh ảnh những d&ograve;ng magma đỏ tu&ocirc;n tr&agrave;o v&agrave; chuyển th&agrave;nh m&agrave;u đen khi n&oacute; nguội bớt. Đ&aacute; bắn ra xa khoảng 45-90 m từ ngọn n&uacute;i.</p>\r\n<p class="Normal">Đoạn video được ghi lại từ một thiết bị được điều khiển từ xa. N&oacute; c&oacute; thể tới gần n&uacute;i lửa trong khoảng 2,4 m, một điều kh&ocirc;ng tưởng nếu ở tr&ecirc;n đất liền.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p>', 35, 8, 103, 0, 1),
(583, 'Ảnh đẹp động vật trong tuần', 'Chuột đồng chơi kèn, chó đón khách đổ xăng, gấu bắc cực dụi đầu trong tuyết. Dưới đây là những hỉnh ảnh đẹp về động vật tuần qua.', 'dv1.1.jpg', NULL, '<p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">Chuột đồng chơi k&egrave;n, ch&oacute; đ&oacute;n kh&aacute;ch đổ xăng, gấu bắc cực dụi đầu trong tuyết. Dưới đ&acirc;y l&agrave; những hỉnh ảnh đẹp về động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1.1.jpg" border="1" alt="Ảnh: AP." width="450" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; ch&oacute; c&oacute; t&ecirc;n Cody mặc đồng phục đ&oacute;n kh&aacute;ch tại trạm xăng bang Florida, Mỹ. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2.jpg" border="1" alt="Ảnh: Reuters." width="290" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con chim c&aacute;nh cụt mặc trang phục &ocirc;ng gi&agrave; Tuyết tại c&ocirc;ng vi&ecirc;n H&agrave;n Quốc. Ảnh:&nbsp;<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3.jpg" border="1" alt="" width="351" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chuy&ecirc;n gia về động vật của Hải qu&acirc;n Mỹ huấn luyện một con sư tử biển. Ảnh:&nbsp;<em>US NAVY / BARCROFT MEDIA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5.jpg" border="1" alt="Ảnh: Telegraph." width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chuột đồng quảng c&aacute;o biểu diễn gần Jazz Caf&eacute; ở London. Ảnh:&nbsp;<em>Telegraph</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6.jpg" border="1" alt="Ảnh: AP." width="328" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con gấu tr&uacute;c Wang Wang ăn tr&uacute;c tại c&ocirc;ng vi&ecirc;n ở tỉnh Tứ Xuy&ecirc;n, Trung Quốc. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7.jpg" border="1" alt="Ảnh: BARCROFT MEDIA." width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">C&ocirc; c&uacute;n đỏm d&aacute;ng trong cuộc thi t&agrave;i năng d&agrave;nh cho ch&oacute; ở Hải Nam, Trung Quốc. Ảnh:<em>BARCROFT MEDIA.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv8.jpg" border="1" alt="Ảnh: David C. Schultz/Solent" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">Ch&uacute; gấu bắc cực tr&igrave;nh diễn tr&ecirc;n tuyết ở c&ocirc;ng vi&ecirc;n quốc gia Wapusk, Canada. Ảnh:&nbsp;<em>David C. Schultz/Solent</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong></p>', 35, 8, 2696, 0, 1),
(584, 'Sao băng nổ rực rỡ trên bầu trời', 'Một ngôi sao băng đã phát nổ rực rỡ trên bầu trời ở Nam Phi, nhưng các chuyên gia chưa thể xác định vị khách đến từ ngoài vũ trụ này đã hạ cánh tại đâu.\r\n', 'sao.jpg', NULL, '<p class="Title">Sao băng nổ rực rỡ tr&ecirc;n bầu trời</p>\r\n<p class="Lead">Một ng&ocirc;i sao băng đ&atilde; ph&aacute;t nổ rực rỡ tr&ecirc;n bầu trời ở Nam Phi, nhưng c&aacute;c chuy&ecirc;n gia chưa thể x&aacute;c định vị kh&aacute;ch đến từ ngo&agrave;i vũ trụ n&agrave;y đ&atilde; hạ c&aacute;nh tại đ&acirc;u.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="sao.jpg" border="1" alt="" width="316" height="237" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&ocirc;i sao ph&aacute;t nổ giữa bầu trời đ&ecirc;m. Ảnh:&nbsp;<em>News</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Hiện tượng thi&ecirc;n văn hiếm c&oacute; n&agrave;y đ&atilde; được một camera giao th&ocirc;ng ghi lại v&agrave; được nhiều người d&acirc;n địa phương chứng kiến h&ocirc;m 21/11.</p>\r\n<p class="Normal">H&igrave;nh ảnh video cho thấy xe cộ đang đi lại đ&ocirc;ng đ&uacute;c tr&ecirc;n một con đường gần th&agrave;nh phố Johannesburg th&igrave; một ng&ocirc;i sao băng bỗng dưng xuất hiện tr&ecirc;n bầu trời đ&ecirc;m. Ng&ocirc;i sao ban đầu c&oacute; m&agrave;u xanh s&aacute;ng rực rồi bỗng dưng nổ tung ở đường ch&acirc;n trời, biến th&agrave;nh quả cầu lửa m&agrave;u cam rực rỡ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="60%" align="center" bgcolor="#e2e2c5">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" align="center"><a class="Normal" href="http://vnexpress.net/GL/Khoa-hoc/Anh/2009/11/3BA16126/page_2.asp">Xem video sao băng nổ rực rỡ tại đ&acirc;y.</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Một nh&acirc;n chứng n&oacute;i với&nbsp;<em>The Sun</em>: "Ch&uacute;ng t&ocirc;i nh&igrave;n thấy quả cầu lửa m&agrave;u xanh khổng lồ. N&oacute; giống như hiện ra th&igrave;nh l&igrave;nh từ ph&iacute;a ch&acirc;n trời".</p>\r\n<p class="Normal">"Rồi &aacute;nh s&aacute;ng vụt l&oacute;e l&ecirc;n, k&eacute;o theo một vụ nổ rực rỡ khiến bầu trời s&aacute;ng rực như ban ng&agrave;y".</p>\r\n<p class="Normal">Claire Flanagan, một chuy&ecirc;n gia tại Cung thi&ecirc;n văn Johannesburg, cho biết sao băng thường đ&aacute;p xuống mặt đất nhưng &iacute;t người nh&igrave;n thấy.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Thi</strong></p>', 35, 8, 941, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(586, 'Ngắm động vật cười', 'Hà mã, lạc đà không túi, thậm chí sư tử trở nên đáng yêu hơn khi cười.', 'dv1 (5).jpg', NULL, '<p class="Title">Trung Quốc x&acirc;y đ&ocirc; thị si&ecirc;u sạch</p>\r\n<p class="Lead">Người d&acirc;n trong đ&ocirc; thị sinh th&aacute;i của Trung Quốc c&oacute; thể uống nước từ v&ograve;i, di chuyển tr&ecirc;n c&aacute;c phương tiện giao th&ocirc;ng kh&ocirc;ng xả kh&iacute; thải.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="eco6 (5).jpg" alt="M&ocirc; h&igrave;nh th&agrave;nh phố sinh th&aacute;i gần Thi&ecirc;n T&acirc;n. Ảnh: AFP." width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&ocirc; h&igrave;nh th&agrave;nh phố sinh th&aacute;i gần Thi&ecirc;n T&acirc;n. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>AFP</em>&nbsp;cho biết, th&agrave;nh phố sinh th&aacute;i của Trung Quốc được x&acirc;y tr&ecirc;n khu đất kh&ocirc;ng thể trồng trọt do nhiễm mặn gần th&agrave;nh phố cảng Thi&ecirc;n T&acirc;n v&agrave; c&oacute; diện t&iacute;ch 30 km2. Qu&aacute; tr&igrave;nh x&acirc;y dựng sẽ diễn ra trong &iacute;t nhất 10 năm.</p>\r\n<p class="Normal">C&aacute;c nh&agrave; thầu hy vọng th&agrave;nh phố sinh th&aacute;i của họ sẽ l&agrave; h&igrave;nh mẫu của một loại đ&ocirc; thị si&ecirc;u sạch, được quy hoạch tốt v&agrave; ph&aacute;t triển bền vững.</p>\r\n<p class="Normal">&ldquo;Ch&uacute;ng t&ocirc;i hy vọng th&agrave;nh phố sinh th&aacute;i mới sẽ t&aacute;c động tới những nước l&aacute;ng giềng&rdquo;, Goh Chye Boon, tổng gi&aacute;m đốc điều h&agrave;nh tập đo&agrave;n đầu tư v&agrave; ph&aacute;t triển đ&ocirc; thị sinh th&aacute;i Thi&ecirc;n T&acirc;n, ph&aacute;t biểu.</p>\r\n<p class="Normal">Ch&iacute;nh phủ Trung Quốc v&agrave; Singapore hợp t&aacute;c với nhau về t&agrave;i ch&iacute;nh v&agrave; kỹ thuật để x&acirc;y dựng th&agrave;nh phố sinh th&aacute;i. Theo kế hoạch th&agrave;nh phố sẽ c&oacute; 350.000 d&acirc;n với cơ sở hạ tầng to&agrave;n diện như trường học, bệnh viện v&agrave; c&aacute;c khu thương mại.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="eco2 (5).jpg" alt="Một xe tải tiến v&agrave;o c&ocirc;ng trường x&acirc;y dựng đ&ocirc; thị sinh th&aacute;i gần th&agrave;nh phố Thi&ecirc;n T&acirc;n v&agrave;o ng&agrave;y 15/9. Ảnh: AFP." width="450" height="281" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một xe tải tiến v&agrave;o c&ocirc;ng trường x&acirc;y dựng đ&ocirc; thị sinh th&aacute;i gần th&agrave;nh phố Thi&ecirc;n T&acirc;n v&agrave;o ng&agrave;y 15/9. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Nhiều c&ocirc;ng ty nước ngo&agrave;i, như Hitachi (Nhật Bản) v&agrave; Philips (H&agrave; Lan), sẽ cung cấp c&ocirc;ng nghệ sạch cho th&agrave;nh phố. C&aacute;c t&ograve;a nh&agrave; sẽ c&oacute; tường c&aacute;ch &acirc;m v&agrave; được lắp cửa sổ c&oacute; khả năng nhận nhiều &aacute;nh s&aacute;ng để tiết kiệm điện.</p>\r\n<p class="Normal">Gần hai phần ba r&aacute;c thải của d&acirc;n sẽ được t&aacute;i chế. 20% điện trong th&agrave;nh phố sẽ được sản xuất từ c&aacute;c nguồn t&agrave;i nguy&ecirc;n v&ocirc; tận v&agrave; kh&ocirc;ng g&acirc;y &ocirc; nhiễm m&ocirc;i trường như gi&oacute; v&agrave; &aacute;nh s&aacute;ng mặt trời.</p>\r\n<p class="Normal">Sau khi được xử l&yacute;, nước thải sẽ chảy theo c&aacute;c k&ecirc;nh để v&agrave;o một hồ. Người d&acirc;n c&oacute; thể lấy nước trong hồ để sử dụng trong sinh hoạt h&agrave;ng ng&agrave;y.</p>\r\n<p class="Normal">&ldquo;Đ&ocirc; thị sinh th&aacute;i trở th&agrave;nh nhu cầu cần thiết bởi Trung Quốc đang đối mặt với t&igrave;nh trạng &ocirc; nhiễm nghi&ecirc;m trọng trong c&aacute;c th&agrave;nh phố&rdquo;, &ocirc;ng Hiroaki Suzuki - một chuy&ecirc;n gia h&agrave;ng đầu về t&agrave;i ch&iacute;nh, kinh tế v&agrave; ph&aacute;t triển đ&ocirc; thị của Ng&acirc;n h&agrave;ng Thế giới &ndash; nhận x&eacute;t. Ng&acirc;n h&agrave;ng Thế giới l&agrave; tổ chức hỗ trợ dự &aacute;n đ&ocirc; thị sinh th&aacute;i của Trung Quốc.</p>\r\n<p class="Normal">Giới chức cao cấp của Trung Quốc hy vọng đ&ocirc; thị sinh th&aacute;i sẽ gi&uacute;p họ giải quyết t&igrave;nh trạng số d&acirc;n đ&ocirc; thị tăng qu&aacute; nhanh. Kể từ khi Trung Quốc thực hiện cải c&aacute;ch kinh tế c&aacute;ch đ&acirc;y hơn 30 năm, d&acirc;n số đ&ocirc; thị nước n&agrave;y đ&atilde; tăng với tốc độ cao nhất trong lịch sử do v&agrave;i trăm triệu người di chuyển ra th&agrave;nh phố để t&igrave;m kiếm cơ hội.</p>\r\n<p class="Normal">Đ&agrave;i truyền h&igrave;nh Trung Quốc cho biết, để đ&aacute;p ứng nhu cầu của cư d&acirc;n th&agrave;nh phố, Trung Quốc phải đầu tư tới 3,6 ngh&igrave;n tỷ USD v&agrave;o cơ sở hạ tầng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p><p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">N&agrave;ng gấu bị sa thải khỏi g&aacute;nh xiếc v&igrave; qu&aacute; b&eacute;o, gấu tr&uacute;c mừng sinh nhật 6 tuổi. Dưới đ&acirc;y l&agrave; những h&igrave;nh ảnh đẹp về động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (5).jpg" border="1" alt="Ảnh: EPA." width="480" height="340" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; đười ươi tập tạ trong một buổi tr&igrave;nh diễn ở vườn th&uacute; Bangkok, Th&aacute;i Lan. Ảnh:&nbsp;<em>EPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (5).jpg" border="1" alt="Ảnh: Quirky China News." width="405" height="477" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một c&ocirc; n&agrave;ng gấu trở th&agrave;nh ng&ocirc;i sao ở vườn th&uacute; Trung Quốc nhờ đứng chụp ảnh với du kh&aacute;ch. Tian Tian bị đuổi việc khỏi một g&aacute;nh xiếc v&igrave; qu&aacute; b&eacute;o. Giờ đ&acirc;y, c&ocirc; đứng chụp ảnh với du kh&aacute;ch ở vườn th&uacute; tại tỉnh Sơn Đ&ocirc;ng. Sau khi chụp ảnh, c&ocirc; thường nh&igrave;n chằm chằm v&agrave;o đồ ăn của du kh&aacute;ch cho tới khi họ đưa cho c&ocirc;. C&ocirc; rất th&iacute;ch bia. Ảnh:&nbsp;<em>Quirky China News.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3.1 (5).jpg" border="1" alt="Ảnh: AP." width="426" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gấu tr&uacute;c Yuanyuan mừng sinh nhật lần thứ 6 với chiếc b&aacute;nh đặc biệt ở Đ&agrave;i Bắc, Đ&agrave;i Loan. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4 (5).jpg" border="1" alt="Ảnh: AP." width="480" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Du kh&aacute;ch chi&ecirc;m ngưỡng ch&uacute; ch&oacute; x&ugrave; được nhuộm l&ocirc;ng cho giống gấu tr&uacute;c ở hội chợ vật nu&ocirc;i tại Thượng Hải, Trung Quốc. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5 (5).jpg" border="1" alt="Ảnh: YouTube." width="480" height="384" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con c&aacute; biển xấu x&iacute; c&oacute; t&ecirc;n Asian sheepshead wrasse bơi qua v&ugrave;ng biển Nhật Bản. Vẻ ngo&agrave;i đặc biệt của n&oacute; khiến n&oacute; được đặt biệt danh con c&aacute; Shrek, giống ch&agrave;ng Shrek xấu x&iacute; trong phim hoạt h&igrave;nh của Mỹ. Ảnh:&nbsp;<em>YouTube</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (5).jpg" border="1" alt="Ảnh: Solent." width="480" height="386" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cặp sếu n&agrave;y được đặt t&ecirc;n l&agrave; "mặt sấp, mặt ngửa" v&igrave; một con lu&ocirc;n quay đi khi bị chụp ảnh tại vườn th&uacute; ở Valencia, T&acirc;y Ban Nha. Ảnh:&nbsp;<em>Solent</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv8<p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">Voi được sơn m&igrave;nh giống hổ, gấu tr&uacute;c diện quần &aacute;o đẹp đ&oacute;n Tết. Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh đẹp về động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (2) (5).jpg" border="1" alt="" width="480" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con hổ Siberia tr&igrave;nh diễn trước đ&aacute;m đ&ocirc;ng du kh&aacute;ch ở vườn th&uacute; Ph&uacute;c Ch&acirc;u, Trung Quốc, trong ng&agrave;y thứ hai của năm Canh Dần. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (5).jpg" border="1" alt="" width="480" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gấu tr&uacute;c Lin Ping được diện đồ đẹp đ&oacute;n năm mới ở vườn th&uacute; Chiang Mai, Th&aacute;i Lan. Ảnh: EPA.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (5).jpg" border="1" alt="" width="480" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con voi ở vườn th&uacute; Ayutthaya, Th&aacute;i Lan, được sơn m&igrave;nh giống như hổ. Ảnh:&nbsp;<em>Barcroft India.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5 (5).jpg" border="1" alt="" width="348" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con c&aacute;o đuổi theo &oacute; biển giữa m&ecirc;nh m&ocirc;ng tuyết trắng tại miền bắc Nhật Bản. Ảnh:&nbsp;<em>EPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (5).jpg" border="1" alt="" width="423" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con gấu so t&agrave;i với nhau ở c&ocirc;ng vi&ecirc;n quốc gia Katmai ở Alaska, Mỹ. Ảnh:&nbsp;<em>Solent</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (5).jpg" border="1" alt="" width="297" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một c&ocirc; c&uacute;n được mặc trang phục truyền thống của Colombia trong lễ hội h&oacute;a trang. Ảnh:&nbsp;<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4 (5).jpg" border="1" alt="" width="480" height="315" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con ngựa vằn chạy ra ngo&agrave;i phố ở th&agrave;nh phố Atlanta, Mỹ. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p><p class="Title">Voi chơi b&oacute;ng rổ</p>\r\n<p class="Lead">Những bức ảnh về hai ch&uacute; voi chơi b&oacute;ng rổ tại Th&aacute;i Lan một lần nữa chứng minh rằng khả năng học hỏi của ch&uacute;ng l&agrave; v&ocirc; hạn.<br /><br /></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v0 (5).jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Ch&uacute; voi 6 tuổi Malie v&agrave; ch&uacute; voi 9 tuổi trong trung t&acirc;m Island Safari, đảo Koh Samui, Th&aacute;i Lan được huấn luyện để chơi b&oacute;ng rổ. Ảnh:&nbsp;<em>Bronek Kaminiski</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v1 (5).jpg" alt="Island Safari" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">C&aacute;c nh&acirc;n vi&ecirc;n ở Trung t&acirc;m Island Safari cho biết, những con vật to lớn phải tập luyện rất vất vả để học những kỹ thuật cơ bản của bộ m&ocirc;n b&oacute;ng rổ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v3 (5).jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Đầu ti&ecirc;n ch&uacute;ng phải học những động t&aacute;c giữ b&oacute;ng thăng bằng tr&ecirc;n đầu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v4 (5).jpg" alt="Rồi kỹ thuật giữ b&oacute;ng." width="380" height="542" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Rồi ch&uacute;ng tập giữ b&oacute;ng bằng v&ograve;i v&agrave; di chuyển.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v5 (5).jpg" alt="N&eacute;m b&oacute;ng v&agrave;o rổ." width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">N&eacute;m b&oacute;ng v&agrave;o rổ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v7 (5).jpg" alt="Với" width="380" height="567" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Với vẻ phấn kh&iacute;ch, Malie giữ b&oacute;ng v&agrave; giữ thăng bằng tr&ecirc;n hai ch&acirc;n sau để chờ hiệu lệnh của người huấn luyện.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v8 (5).jpg" alt="Sau khi nghe hiệu lệnh, con voi n&eacute;m b&oacute;ng v&agrave;o rổ." width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Sau khi nghe hiệu lệnh, con voi n&eacute;m b&oacute;ng v&agrave;o rổ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v9 (5).jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">"Ch&uacute;ng t&ocirc;i phải huấn luyện những con voi trong 2-3 th&aacute;ng để ch&uacute;ng nắm được những kỹ thuật cơ bản. Nhưng may mắn l&agrave; tr&igrave;nh độ của ch&uacute;ng cải thiện từng ng&agrave;y", Ning, một nh&acirc;n vi&ecirc;n của trung t&acirc;m, n&oacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="v10 (5).jpg" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal">Hai con voi thực hiện những động t&aacute;c như thể ch&uacute;ng sắp so t&agrave;i với nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long (</strong>Ảnh<strong>:&nbsp;</strong><em>Bronek Kaminiski</em>)</p><p class="Title">Khi động vật say ngủ</p>\r\n<p class="Lead">Ngay cả những con vật dữ tợn cũng trở n&ecirc;n hiền l&agrave;nh dễ thương khi ngủ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="02_1 (5).jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&egrave;o ngủ tr&ecirc;n ghế &ecirc;m.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="01 (5).jpg" border="1" alt="" width="350" height="517" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gấu tr&uacute;c d&agrave;n h&agrave;ng ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="04 (5).jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hổ con nhắm t&iacute;t mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="05 (5).jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sư tử c&aacute;i ngủ v&ecirc;nh r&acirc;u.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="9 (5).jpg" border="1" alt="" width="350" height="528" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngựa vừa ngủ vừa cười.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="06 (5).jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mẹ con gấu Bắc cực &ocirc;m nhau ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="07 (5).jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đười ươi vắt tay l&ecirc;n tr&aacute;n ngủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="08 (5).jpg" border="1" alt="" width="480" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chuột lim dim.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Minh</strong>&nbsp;<em>(Ảnh: Hufingtonpost)</em></p><p class="Title">Động vật h&ocirc;n nhau</p>\r\n<p class="Lead">Ch&oacute;, m&egrave;o, chim c&aacute;nh cụt cũng biết c&aacute;ch tỏ t&igrave;nh bằng những nụ h&ocirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (5).jpg" border="1" alt="" width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con ch&oacute; t&igrave;nh tứ b&ecirc;n nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (5).jpg" border="1" alt="" width="450" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&egrave;o cũng biết &acirc;u yếm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (5).jpg" border="1" alt="" width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khỉ mẹ nựng khỉ con.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4 (5).jpg" border="1" alt="" width="450" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim c&aacute;nh cụt h&ocirc;n kiểu Ph&aacute;p.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (5).jpg" border="1" alt="" width="450" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con s&oacute;c ch&oacute; đắm m&igrave;nh trong nụ h&ocirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (5).jpg" border="1" alt="" width="450" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đười ươi thơm nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv8 (5).jpg" border="1" alt="" width="450" height="340" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">R&ugrave;a cũng biết t&igrave;nh tứ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv9 (5).jpg" border="1" alt="" width="450" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&oacute; biển tỏ t&igrave;nh với nhau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong>&nbsp;(Ảnh:&nbsp;<em>Flickr</em>)</p><p class="Title">Ảnh đẹp động vật năm 2009</p>\r\n<p class="Lead">Khỉ l&aacute;i xe m&aacute;y đồ chơi, ch&oacute; đeo k&iacute;nh đen s&agrave;nh điệu. Dưới đ&acirc;y l&agrave; những bức ảnh động vật đẹp nhất trong năm của&nbsp;<em>Reuters.</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (5).jpg" border="1" alt="" width="450" height="282" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ocirc; c&aacute; voi trắng Aurora, 20 tuổi, bơi c&ugrave;ng đứa con vừa mới ra đời ở Vancouver, Canada.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (5).jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cậu b&eacute; bơi c&ugrave;ng tr&acirc;u dưới s&ocirc;ng Tawi trong ng&agrave;y h&egrave; ở Jammu, Ấn Độ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (5).jpg" border="1" alt="" width="450" height="271" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con gấu trắng giũ nước khỏi người tại vườn th&uacute; ở Quebec, Canada.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv4 (5).jpg" border="1" alt="" width="450" height="287" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con khỉ con uống nước trong một c&aacute;i vũng ở tỉnh H&agrave; Nam, Trung Quốc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5 (5).jpg" border="1" alt="" width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con vượn bị mẹ giữ chặt trong vườn th&uacute; ở Vienna, &Aacute;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (5).jpg" border="1" alt="" width="450" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con ch&oacute; diện quần &aacute;o tham gia lễ hội d&agrave;nh cho vật nu&ocirc;i tại Cali, Colombia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (5).jpg" border="1" alt="" width="450" height="298" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Diễn vi&ecirc;n xiếc dắt con khỉ ngồi tr&ecirc;n chiếc xe m&aacute;y đồ chơi dưới trời mưa ở Jakarta, Indonesia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv9 (5).jpg" border="1" alt="" width="337" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con kỳ nh&ocirc;ng ngồi ghế chụp ảnh trong buổi luyện tập với c&ocirc; chủ ở ngoại &ocirc; Bangkok, Th&aacute;i Lan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv11 (5).jpg" border="1" alt="" width="450" height="329" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ba ch&uacute; ch&oacute; x&ugrave; l&agrave;m d&aacute;ng trong lễ hội ch&oacute; ở Tokyo, Nhật Bản.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>H. Ninh</strong></p><p class="Title">Cảnh tượng n&uacute;i lửa phun tr&agrave;o dưới đ&aacute;y đại dương</p>\r\n<p class="Lead">C&aacute;c nh&agrave; khoa học ở Mỹ x&uacute;c động khi lần đầu ti&ecirc;n ghi lại được cảnh n&uacute;i lửa phun tr&agrave;o dưới đ&aacute;y đại dương.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="nl (5).jpg" border="1" alt="N&uacute;i lửa T&acirc;y Mata phun tr&agrave;o. Ảnh: Seatle Times." width="380" height="361" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">N&uacute;i lửa T&acirc;y Mata phun tr&agrave;o. Ảnh:&nbsp;<em>Seatle Times</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Gần ba phần tư c&aacute;c vụ n&uacute;i lửa phun tr&agrave;o tr&ecirc;n thế giới diễn ra dưới đại dương tuy nhi&ecirc;n con người chưa bao giờ tận mắt chứng kiến dung nham tu&ocirc;n tr&agrave;o từ miệng n&uacute;i.</p>\r\n<p class="Normal">Một nh&oacute;m c&aacute;c nh&agrave; khoa học thực hiện cuộc th&aacute;m hiểm tới khu vực ngọn n&uacute;i lửa T&acirc;y Mata ở Th&aacute;i B&igrave;nh Dương hồi th&aacute;ng 5. Trong khoảng 1 tiếng, họ nh&igrave;n thấy kh&oacute;i bốc l&ecirc;n. "Sau đ&oacute;, dung nham đỏ tu&ocirc;n ra", nh&agrave; hải dương học Joe Resing, trưởng đo&agrave;n th&aacute;m hiểm, cho biết. "Tất cả ch&uacute;ng t&ocirc;i đều ph&aacute;t đi&ecirc;n l&ecirc;n".</p>\r\n<p class="Normal">Ngọn n&uacute;i lửa n&agrave;y nằm c&aacute;ch mặt nước biển khoảng 1.200 m.<em>&nbsp;Seatle Times</em>&nbsp;cho hay n&uacute;i lửa phun tr&agrave;o tạo th&ecirc;m một đ&aacute;y biển mới.</p>\r\n<p class="Normal">Đoạn video được ghi lại từ đầu năm nay mới được c&ocirc;ng bố h&ocirc;m qua trong cuộc gặp mặt của Hiệp hội Địa vật l&yacute; tại th&agrave;nh phố San Francisco. Video c&oacute; h&igrave;nh ảnh những d&ograve;ng magma đỏ tu&ocirc;n tr&agrave;o v&agrave; chuyển th&agrave;nh m&agrave;u đen khi n&oacute; nguội bớt. Đ&aacute; bắn ra xa khoảng 45-90 m từ ngọn n&uacute;i.</p>\r\n<p class="Normal">Đoạn video được ghi lại từ một thiết bị được điều khiển từ xa. N&oacute; c&oacute; thể tới gần n&uacute;i lửa trong khoảng 2,4 m, một điều kh&ocirc;ng tưởng nếu ở tr&ecirc;n đất liền.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p><p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">Chuột đồng chơi k&egrave;n, ch&oacute; đ&oacute;n kh&aacute;ch đổ xăng, gấu bắc cực dụi đầu trong tuyết. Dưới đ&acirc;y l&agrave; những hỉnh ảnh đẹp về động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1.1 (5).jpg" border="1" alt="Ảnh: AP." width="450" height="319" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; ch&oacute; c&oacute; t&ecirc;n Cody mặc đồng phục đ&oacute;n kh&aacute;ch tại trạm xăng bang Florida, Mỹ. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (5).jpg" border="1" alt="Ảnh: Reuters." width="290" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con chim c&aacute;nh cụt mặc trang phục &ocirc;ng gi&agrave; Tuyết tại c&ocirc;ng vi&ecirc;n H&agrave;n Quốc. Ảnh:&nbsp;<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (5).jpg" border="1" alt="" width="351" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chuy&ecirc;n gia về động vật của Hải qu&acirc;n Mỹ huấn luyện một con sư tử biển. Ảnh:&nbsp;<em>US NAVY / BARCROFT MEDIA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5 (5).jpg" border="1" alt="Ảnh: Telegraph." width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chuột đồng quảng c&aacute;o biểu diễn gần Jazz Caf&eacute; ở London. Ảnh:&nbsp;<em>Telegraph</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (5).jpg" border="1" alt="Ảnh: AP." width="328" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con gấu tr&uacute;c Wang Wang ăn tr&uacute;c tại c&ocirc;ng vi&ecirc;n ở tỉnh Tứ Xuy&ecirc;n, Trung Quốc. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (5).jpg" border="1" alt="Ảnh: BARCROFT MEDIA." width="450" height="338" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">C&ocirc; c&uacute;n đỏm d&aacute;ng trong cuộc thi t&agrave;i năng d&agrave;nh cho ch&oacute; ở Hải Nam, Trung Quốc. Ảnh:<em>BARCROFT MEDIA.</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv8 (5).jpg" border="1" alt="Ảnh: David C. Schultz/Solent" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class="Image">Ch&uacute; gấu bắc cực tr&igrave;nh diễn tr&ecirc;n tuyết ở c&ocirc;ng vi&ecirc;n quốc gia Wapusk, Canada. Ảnh:&nbsp;<em>David C. Schultz/Solent</em></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong></p><p class="Title">Sao băng nổ rực rỡ tr&ecirc;n bầu trời</p>\r\n<p class="Lead">Một ng&ocirc;i sao băng đ&atilde; ph&aacute;t nổ rực rỡ tr&ecirc;n bầu trời ở Nam Phi, nhưng c&aacute;c chuy&ecirc;n gia chưa thể x&aacute;c định vị kh&aacute;ch đến từ ngo&agrave;i vũ trụ n&agrave;y đ&atilde; hạ c&aacute;nh tại đ&acirc;u.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="sao (5).jpg" border="1" alt="" width="316" height="237" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&ocirc;i sao ph&aacute;t nổ giữa bầu trời đ&ecirc;m. Ảnh:&nbsp;<em>News</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Hiện tượng thi&ecirc;n văn hiếm c&oacute; n&agrave;y đ&atilde; được một camera giao th&ocirc;ng ghi lại v&agrave; được nhiều người d&acirc;n địa phương chứng kiến h&ocirc;m 21/11.</p>\r\n<p class="Normal">H&igrave;nh ảnh video cho thấy xe cộ đang đi lại đ&ocirc;ng đ&uacute;c tr&ecirc;n một con đường gần th&agrave;nh phố Johannesburg th&igrave; một ng&ocirc;i sao băng bỗng dưng xuất hiện tr&ecirc;n bầu trời đ&ecirc;m. Ng&ocirc;i sao ban đầu c&oacute; m&agrave;u xanh s&aacute;ng rực rồi bỗng dưng nổ tung ở đường ch&acirc;n trời, biến th&agrave;nh quả cầu lửa m&agrave;u cam rực rỡ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="60%" align="center" bgcolor="#e2e2c5">\r\n<tbody>\r\n<tr>\r\n<td class="Normal" align="center"><a class="Normal" href="http://vnexpress.net/GL/Khoa-hoc/Anh/2009/11/3BA16126/page_2.asp">Xem video sao băng nổ rực rỡ tại đ&acirc;y.</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Một nh&acirc;n chứng n&oacute;i với&nbsp;<em>The Sun</em>: "Ch&uacute;ng t&ocirc;i nh&igrave;n thấy quả cầu lửa m&agrave;u xanh khổng lồ. N&oacute; giống như hiện ra th&igrave;nh l&igrave;nh từ ph&iacute;a ch&acirc;n trời".</p>\r\n<p class="Normal">"Rồi &aacute;nh s&aacute;ng vụt l&oacute;e l&ecirc;n, k&eacute;o theo một vụ nổ rực rỡ khiến bầu trời s&aacute;ng rực như ban ng&agrave;y".</p>\r\n<p class="Normal">Claire Flanagan, một chuy&ecirc;n gia tại Cung thi&ecirc;n văn Johannesburg, cho biết sao băng thường đ&aacute;p xuống mặt đất nhưng &iacute;t người nh&igrave;n thấy.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Thi</strong></p><p class="Title">Ngắm động vật cười</p>\r\n<p class="Lead">H&agrave; m&atilde;, lạc đ&agrave; kh&ocirc;ng t&uacute;i, thậm ch&iacute; sư tử trở n&ecirc;n đ&aacute;ng y&ecirc;u hơn khi cười.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv1 (5) (5).jpg" border="1" alt="" width="450" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; chim cười sảng kho&aacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv3 (5) (5).jpg" border="1" alt="" width="450" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con lạc đ&agrave; kh&ocirc;ng bướu khoe h&agrave;m rằng khấp khểnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv2 (5).jpg" border="1" alt="" width="450" height="317" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con h&agrave; m&atilde; bơi trong d&ograve;ng nước xanh ngắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv5 (5).jpg" border="1" alt="" width="450" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&egrave;o con khoe nanh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv6 (5).jpg" border="1" alt="" width="332" height="400" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gấu t&uacute;i cười t&iacute;t mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv8 (5).jpg" border="1" alt="" width="450" height="315" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&oacute; cũng biết nh&aacute;y mắt.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dv7 (5).jpg" border="1" alt="" width="450" height="316" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nụ cười dữ tợn của con sư tử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong>&nbsp;(Ảnh:&nbsp;<em>Flickr</em>)</p> (5).jpg" border="1" alt="Hai con ngựa cắn nhau ở Duelmen, Đức. Ảnh: AP." width="480" height="400" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con ngựa cắn nhau ở Duelmen, Đức. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong></p>', 35, 8, 681, 0, 1),
(587, 'Thế giới bướm lộng lẫy', 'Hôm qua, một khu vườn bướm đã được khai trương tại Colombia bao gồm 500 con bướm thuộc 20 loài khác nhau với màu sắc rực rỡ và hình thù đa dạng.', 'buom1.jpg', NULL, '<p class="Title"><span style="color: #111111;">Thế giới bướm lộng lẫy</span></p>\r\n<p class="Lead">H&ocirc;m qua, một khu vườn bướm đ&atilde; được khai trương tại Colombia bao gồm 500 con bướm thuộc 20 lo&agrave;i kh&aacute;c nhau với m&agrave;u sắc rực rỡ v&agrave; h&igrave;nh th&ugrave; đa dạng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom1.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con bướm m&ecirc; mẩn giữa vườn hoa rực rỡ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom2.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Pha đập c&aacute;nh ngoạn mục của một con bướm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom3.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&agrave;u v&agrave;ng nổi bật.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom4.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những đường v&acirc;n kỳ diệu tr&ecirc;n c&aacute;nh bướm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom5.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con bướm s&agrave; v&agrave;o b&ocirc;ng hoa ngũ sắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom6.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">M&agrave;u xanh &oacute;ng biếc nổi bật tr&ecirc;n nền đ&aacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom7.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con bướm say sưa h&uacute;t mật hoa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom8.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con bướm thưởng thức hương vị cam tươi m&aacute;t.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom9.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&ocirc;i bướm kết bạn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="buom10.jpg" border="1" alt="" width="450" height="327" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đốm cam nổi bật.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Thi</strong>&nbsp;(Ảnh:&nbsp;<em>Huffintonpost</em>)</p>', 35, 8, 2162, 0, 1),
(588, 'Ảnh đẹp động vật trong tuần', 'Mèo và sóc ngắm nhau đăm đăm qua cửa kính. Vịt ngỡ ngàng khi bị cò nhấc bổng khỏi mặt đất. Dưới đây là những bức ảnh đẹp về thế giới động vật tuần qua.', 'meo1_1.jpg', NULL, '<p class="Title">Ảnh đẹp động vật trong tuần</p>\n<p class="Lead">M&egrave;o v&agrave; s&oacute;c ngắm nhau đăm đăm qua cửa k&iacute;nh. Vịt ngỡ ng&agrave;ng khi bị c&ograve; nhấc bổng khỏi mặt đất. Dưới đ&acirc;y l&agrave; những bức ảnh đẹp về thế giới động vật tuần qua.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="meo1_1.jpg" border="0" alt="" width="480" height="318" /></td>\n</tr>\n<tr>\n<td class="Image">S&oacute;c v&agrave; m&egrave;o ngắm nhau qua cửa sổ bằng k&iacute;nh ở Montreal, Canada. Ảnh:&nbsp;<em>Rex Features</em>.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="v.jpg" border="0" alt="" width="480" height="326" /></td>\n</tr>\n<tr>\n<td class="Image">Con vịt n&agrave;y c&oacute; lẽ đ&atilde; nghĩ n&oacute; sắp th&agrave;nh bữa trưa của con c&ograve; mỏ gi&agrave;y. Tuy nhi&ecirc;n, con chim bự chỉ nhấc vịt ra khỏi đường kiếm ăn của n&oacute;. Ảnh:&nbsp;<em>Solent.</em></td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sq.jpg" border="0" alt="" width="350" height="531" /></td>\n</tr>\n<tr>\n<td class="Image">Con s&oacute;c cố gi&atilde;y ra khỏi chiếc lồng cho chim ăn ở Manchester, Anh. Ảnh:<strong>&nbsp;</strong><em>MEN SYNDICATION.</em></td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="voi.jpg" border="0" alt="" width="450" height="467" /></td>\n</tr>\n<tr>\n<td class="Image">Con voi ch&acirc;u &Aacute; 4 th&aacute;ng tuổi Luk Chai đ&ugrave;a nghịch c&ugrave;ng quả b&oacute;ng trong vườn th&uacute; ở Sydney, Australia. Ảnh:&nbsp;<em>Getty.</em></td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="meo2.jpg" border="0" alt="" width="480" height="372" /></td>\n</tr>\n<tr>\n<td class="Image">M&egrave;o Chicken đội bộ t&oacute;c giả m&agrave;u đỏ rực để l&agrave;m mẫu cho cuốn s&aacute;ch ảnh sẽ ra mắt tuần tới ở Anh. Ảnh:&nbsp;<em>Caters.</em></td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="ti.jpg" border="0" alt="" width="480" height="323" /></td>\n</tr>\n<tr>\n<td class="Image">Vận động vi&ecirc;n điền kinh Jamaicai Usain Bolt cho b&aacute;o con ti b&igrave;nh ở khu bảo tồn động vật hoang d&atilde; Nairobi, Kenya. Ảnh:&nbsp;<em>EPA.</em></td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="n1.jpg" border="0" alt="" width="480" height="304" /></td>\n</tr>\n<tr>\n<td class="Image">Nh&iacute;m kh&aacute;m ph&aacute; hộp kem ở Krakow, Ba Lan. Ảnh:&nbsp;<em>EUROPICS.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 35, 8, 1469, 0, 1),
(589, 'Ảnh động vật đẹp nhất năm', 'Bức ảnh con sói đang săn mồi đã giành được giải thưởng danh giá Ảnh môi trường sống hoang dã Veolia năm 2009', 'mmm.jpg', NULL, '<p class="Title">Ảnh động vật đẹp nhất năm</p>\r\n<p class="Lead">Bức ảnh con s&oacute;i đang săn mồi đ&atilde; gi&agrave;nh được giải thưởng danh gi&aacute; Ảnh m&ocirc;i trường sống hoang d&atilde; Veolia năm 2009.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="mmm.jpg" border="1" alt="" width="450" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bức ảnh "s&oacute;i nhảy" gi&agrave;nh giải cao nhất năm nay. Ảnh:&nbsp;<em>BBC.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Jose Luis Rodriguez đ&atilde; khơi gợi được tr&iacute; tưởng tượng của ban gi&aacute;m khảo v&agrave;o bức h&igrave;nh m&agrave; anh đ&atilde; nung nấu trong nhiều năm v&agrave; thậm ch&iacute; đ&atilde; vẽ cả ph&aacute;c thảo ra giấy.</p>\r\n<p class="Normal">Khi bắt đầu dự định chụp tấm h&igrave;nh, Rodriguez sợ rằng anh kh&ocirc;ng thể lại đủ gần những con s&oacute;i Iberia, một nh&aacute;nh của lo&agrave;i s&oacute;i x&aacute;m. Lo&agrave;i s&oacute;i Iberia sống gần con người ở ph&iacute;a bắc T&acirc;y Ban Nha. Ch&uacute;ng thường bị con người xua đuổi v&igrave; lo sợ cho vật nu&ocirc;i v&agrave; do đ&oacute; rất thận trọng với con người.</p>\r\n<p class="Normal">Để chụp được bức ảnh n&agrave;y, Rodriguez đ&atilde; rất ki&ecirc;n nhẫn v&agrave; quan s&aacute;t cẩn thận c&aacute;c chuyển động của những con s&oacute;i. Anh đ&atilde; d&ugrave;ng một c&aacute;i bẫy hồng ngoại được chế tạo ri&ecirc;ng để chụp lại h&igrave;nh ảnh con s&oacute;i khi n&oacute; nhảy tr&ecirc;n kh&ocirc;ng. "T&ocirc;i muốn chụp một bức h&igrave;nh m&agrave; ở đ&oacute; bạn sẽ thấy một con s&oacute;i thực hiện động t&aacute;c săn mồi, nhưng kh&ocirc;ng c&oacute; m&aacute;u",&nbsp;<em>BBC</em>&nbsp;dẫn lời Rodriguez h&ocirc;m qua. "T&ocirc;i kh&ocirc;ng muốn một bức ảnh dữ tợn".</p>\r\n<p class="Normal">Rodriguez hy vọng rằng bức h&igrave;nh của anh sẽ lột tả được "sự lanh lợi v&agrave; sức mạnh to lớn của lo&agrave;i s&oacute;i" v&agrave; sẽ trở th&agrave;nh bức h&igrave;nh được sử dụng để chứng tỏ vẻ đẹp của lo&agrave;i s&oacute;i Iberia v&agrave; người T&acirc;y Ban Nha c&oacute; thể tự h&agrave;o thế n&agrave;o khi c&oacute; lo&agrave;i vật biểu tượng n&agrave;y.</p>\r\n<p class="Normal">Đ&acirc;y l&agrave; năm thứ 45 của cuộc thi, được tổ chức bởi tạp ch&iacute; Đời sống hoang d&atilde; của<em>BBC</em>&nbsp;v&agrave; bảo t&agrave;ng Lịch sử tự nhi&ecirc;n của London. Ban gi&aacute;m khảo đ&atilde; phải chọn lọc từ 43.000 bức ảnh gửi về để chọn ra người thắng cuộc.</p>\r\n<p class="Normal">Bức ảnh "s&oacute;i nhảy" chiến thắng ở thể loại ch&acirc;n dung động vật v&agrave; tiếp đ&oacute; gi&agrave;nh giải thưởng cao nhất v&igrave; theo c&aacute;c gi&aacute;m khảo th&igrave; n&oacute; lột tả được bản chất của lo&agrave;i s&oacute;i.</p>\r\n<p class="Normal">Dưới đ&acirc;y l&agrave; một số c&aacute;c bức h&igrave;nh đoạt giải kh&aacute;c:</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bbb.jpg" border="1" alt="" width="450" height="159" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con b&aacute;o đốm đực đứng y&ecirc;n lặng b&ecirc;n bờ s&ocirc;ng nh&igrave;n chăm ch&uacute; người chụp ảnh trong một giờ. Ảnh gi&agrave;nh giải Cuộc sống hoang d&atilde; đang bị nguy hiểm. Ảnh:&nbsp;<em>BBC</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="mot.jpg" border="1" alt="Chim cổ v&agrave;ng đực đang chiến đấu bảo vệ th&agrave;nh quả yến mạch. Tấm ảnh gi&agrave;nh giải Nhiếp ảnh gia trẻ của năm. Ảnh: BBC." width="450" height="305" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim cổ v&agrave;ng đực đang chiến đấu bảo vệ th&agrave;nh quả yến mạch. Tấm ảnh gi&agrave;nh giải Nhiếp ảnh gia trẻ của năm. Ảnh:&nbsp;<em>BBC</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hai.jpg" border="1" alt="" width="450" height="352" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con m&egrave;o của nh&agrave; nhiếp ảnh đang bảo vệ l&atilde;nh địa. Ảnh đạt giải Cuộc sống hoang d&atilde; vườn v&agrave; th&agrave;nh thị. Ảnh:&nbsp;<em>BBC</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ba.jpg" border="1" alt="Những b&ocirc;ng tuyết đang g&acirc;y kh&oacute; khăn cho những con bọ. Ảnh gi&agrave;nh giải Động vật trong m&ocirc;i trường sống. Ảnh: BBC" width="450" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những b&ocirc;ng tuyết đang g&acirc;y kh&oacute; khăn cho những con bọ. Ảnh gi&agrave;nh giải Động vật trong m&ocirc;i trường sống. Ảnh:&nbsp;<em>BBC</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Minh</strong></p>', 35, 8, 1887, 0, 1),
(590, 'Sư tử tấn công trâu nước trên phố', 'Các tài xế lái xe qua công viên quốc gia Kruger ở Nam Phi kinh hoàng khi thấy một con sư tử đang điên cuồng đuổi theo và tấn công một con trâu từ rừng ra giữa đường phố.', 'fight6.jpg', NULL, '<p class="Title">Sư tử tấn c&ocirc;ng tr&acirc;u nước tr&ecirc;n phố</p>\r\n<p class="Lead">C&aacute;c t&agrave;i xế l&aacute;i xe qua c&ocirc;ng vi&ecirc;n quốc gia Kruger ở Nam Phi kinh ho&agrave;ng khi thấy một con sư tử đang đi&ecirc;n cuồng đuổi theo v&agrave; tấn c&ocirc;ng một con tr&acirc;u từ rừng ra giữa đường phố.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fight6.jpg" border="1" alt="" width="450" height="339" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cuộc săn mồi diễn ra ngay trước mắt c&aacute;c du kh&aacute;ch. Ảnh:&nbsp;<em>mgdonny</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo&nbsp;<em>Daily Mail</em>, cảnh tượng kinh ho&agrave;ng diễn ra v&agrave;o giữa th&aacute;ng 8 khi c&aacute;c du kh&aacute;ch nhận thấy một con tr&acirc;u đang đứng một m&igrave;nh b&ecirc;n một gốc c&acirc;y c&aacute;ch con đường khoảng 10 m. Khi khu vực xung quanh dường như ho&agrave;n to&agrave;n hoang vắng th&igrave; h&agrave;nh kh&aacute;ch bỗng thấy c&aacute;c đầu sư tử nh&ocirc; l&ecirc;n từ một bụi rậm ở c&aacute;ch đ&oacute; 20 m.</p>\r\n<p class="Normal">Một nh&acirc;n chứng kể lại: "Ch&uacute;ng t&ocirc;i đứng đ&oacute; khoảng một tiếng đồng hồ v&agrave; kh&ocirc;ng c&oacute; g&igrave; xảy ra. Rồi con tr&acirc;u bỗng đứng dậy một c&aacute;ch rất kh&oacute; khăn".</p>\r\n<p class="Normal">"Khi n&oacute; vừa đứng l&ecirc;n một con sư tử c&aacute;i lao tới v&agrave; nhảy vồ l&ecirc;n lưng để hạ gục con tr&acirc;u. Con tr&acirc;u bắt đầu g&agrave;o rống v&agrave; cố l&ecirc; đi với con sư tử c&aacute;i ở tr&ecirc;n lưng".</p>\r\n<p class="Normal">Khi ra đến đường con tr&acirc;u đ&acirc;m phải đằng sau một chiếc xe khiến con sư tử bị hất xuống. Đến l&uacute;c n&agrave;y h&agrave;nh kh&aacute;ch mới nh&aacute;o nh&aacute;c khi hai con sư tử đực kh&aacute;c cũng tiến tới. Tuy nhi&ecirc;n, những con n&agrave;y chỉ đứng ở một b&ecirc;n lề đường để chờ c&ocirc; n&agrave;ng mang về cho m&igrave;nh bữa tối.</p>\r\n<p class="Normal">"Con tr&acirc;u tiếp tục loay hoay giữa đường v&agrave; lại lao sừng v&agrave;o một xe kh&aacute;c. Sau v&agrave;i lần cố gắng con sư tử c&aacute;i t&igrave;m c&aacute;ch hạ gục con tr&acirc;u nhưng kh&ocirc;ng th&agrave;nh".</p>\r\n<p class="Normal">May mắn cho con tr&acirc;u v&agrave; cả h&agrave;nh kh&aacute;ch, con mồi c&oacute; vẻ qu&aacute; sức với con sư tử c&aacute;i n&ecirc;n n&oacute; bỏ đi v&agrave; để mặc con tr&acirc;u sống s&oacute;t th&ecirc;m một ng&agrave;y nữa.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Minh</strong></p>', 35, 8, 480, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(591, 'Cận cảnh nàng Tiên Nữ', 'Một kính viễn vọng của Mỹ chụp được bức ảnh có độ nét lớn nhất từ trước tới nay về Tiên Nữ, một trong những thiên hà xoắn ốc gần dải Ngân hà nhất.', 'andromeda9.jpg', NULL, '<p class="Title">Cận cảnh n&agrave;ng Ti&ecirc;n Nữ</p>\r\n<p class="Lead">Một k&iacute;nh viễn vọng của Mỹ chụp được bức ảnh c&oacute; độ n&eacute;t lớn nhất từ trước tới nay về Ti&ecirc;n Nữ, một trong những thi&ecirc;n h&agrave; xoắn ốc gần dải Ng&acirc;n h&agrave; nhất.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="andromeda9.jpg" alt="Bức ảnh sắc n&eacute;t nhất về thi&ecirc;n h&agrave; Ti&ecirc;n Nữ. Ảnh: NASA." width="450" height="282" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bức ảnh sắc n&eacute;t nhất về thi&ecirc;n h&agrave; Ti&ecirc;n Nữ. Ảnh:&nbsp;<em>NASA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">K&iacute;nh viễn vọng Swift của Cơ quan H&agrave;ng kh&ocirc;ng vũ trụ Mỹ (NASA) chụp 330 bức ảnh về thi&ecirc;n h&agrave; Ti&ecirc;n Nữ (Andromeda) trong 24 giờ.</p>\r\n<p class="Normal">V&ugrave;ng s&aacute;ng giữa bức ảnh tr&ecirc;n l&agrave; nơi hội tụ của những ng&ocirc;i sao gi&agrave; v&agrave; lạnh. Ch&uacute;ng nằm gần l&otilde;i thi&ecirc;n h&agrave;. To&agrave;n bộ bức ảnh cho ch&uacute;ng ta thấy một v&ugrave;ng kh&ocirc;ng gian c&oacute; chiều rộng 200.000 năm &aacute;nh s&aacute;ng v&agrave; chiều cao 100.000 năm &aacute;nh s&aacute;ng. Đường k&iacute;nh của n&oacute; gần gấp đ&ocirc;i Ng&acirc;n h&agrave;.</p>\r\n<p class="Normal">Những nh&aacute;nh h&igrave;nh xoắn ốc của thi&ecirc;n h&agrave; được tạo n&ecirc;n bởi h&agrave;ng triệu ng&ocirc;i sao mới v&agrave; n&oacute;ng. Ch&uacute;ng vẫn đang "nuốt" bụi v&agrave; kh&iacute; để cung cấp cho phản ứng hạt nh&acirc;n trong l&otilde;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="and.jpg" alt="Ảnh thi&ecirc;n h&agrave; Ti&ecirc;n Nữ được chụp bằng camera hồng ngoại của k&iacute;nh viễn vọng Spitzer. Ảnh: NASA." width="450" height="237" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ảnh thi&ecirc;n h&agrave; Ti&ecirc;n Nữ được chụp bằng camera hồng ngoại của k&iacute;nh viễn vọng Spitzer. Ảnh:<em>NASA.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ti&ecirc;n Nữ l&agrave; thi&ecirc;n h&agrave; đầu ti&ecirc;n b&ecirc;n ngo&agrave;i dải Ng&acirc;n h&agrave; được con người ph&aacute;t hiện v&agrave; cũng l&agrave; ng&acirc;n h&agrave; xoắn gần Ng&acirc;n h&agrave;. Việc t&igrave;m ra n&oacute; thay đổi ho&agrave;n to&agrave;n hiểu biết của ch&uacute;ng ta về vũ trụ. Trước đ&oacute; giới học giả nghĩ rằng dải Ng&acirc;n h&agrave; - gồm khoảng hơn 200 tỷ ng&ocirc;i sao - ch&iacute;nh l&agrave; to&agrave;n bộ vũ trụ.</p>\r\n<p class="Normal">Kể từ khi Ti&ecirc;n Nữ được x&aacute;c nhận l&agrave; một thực thể ri&ecirc;ng biệt trong vũ trụ, giới thi&ecirc;n văn đ&atilde; t&iacute;nh to&aacute;n được rằng n&oacute; chứa hơn 100 tỷ thi&ecirc;n h&agrave; nhỏ hơn. Mỗi thi&ecirc;n h&agrave; nhỏ n&agrave;y c&oacute; từ v&agrave;i chục tới v&agrave;i trăm triệu ng&ocirc;i sao.</p>\r\n<p class="Normal">K&iacute;nh viễn vọng Swift đang theo d&otilde;i nhiều thi&ecirc;n h&agrave; gần ch&uacute;ng ta để t&igrave;m ra những tri thức mới về qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh của sao.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 35, 8, 685, 0, 1),
(592, 'Cuộc săn mồi vĩ đại dưới đại dương', 'Nhiếp ảnh gia Jason Heller đã du lịch tới vùng bờ biển hoang dã ở Nam Phi vào tháng 7 để ghi lại hình ảnh bữa tiệc cá ngoạn mục của những loài động vật lớn dưới biển và cả trên mặt nước.', 'fish2.jpg', NULL, '<p class="Title">Cuộc săn mồi vĩ đại dưới đại dương</p>\r\n<p class="Lead">Nhiếp ảnh gia Jason Heller đ&atilde; du lịch tới v&ugrave;ng bờ biển hoang d&atilde; ở Nam Phi v&agrave;o th&aacute;ng 7 để ghi lại h&igrave;nh ảnh bữa tiệc c&aacute; ngoạn mục của những lo&agrave;i động vật lớn dưới biển v&agrave; cả tr&ecirc;n mặt nước.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish2.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cảnh tượng được v&iacute; như m&agrave;n săn mồi vĩ đại nhất tr&ecirc;n tr&aacute;i đất, khi h&agrave;ng đ&agrave;n c&aacute; mập, c&aacute; voi, c&aacute; heo v&agrave; chim trời bao v&acirc;y h&agrave;ng ngh&igrave;n con c&aacute; sardine đang di cư từ v&ugrave;ng nước lạnh ở Nam Phi tới v&ugrave;ng nước ấm ở ph&iacute;a bắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish1.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cả đ&agrave;n c&aacute; b&eacute; x&iacute;u d&agrave;i tới 6.400 m v&agrave; rộng tới 1.600 m, c&oacute; thể nh&igrave;n r&otilde; từ tr&ecirc;n mặt nước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish4.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khoảng một ngh&igrave;n con c&aacute; heo hợp t&aacute;c lao tới để săn đuổi đ&agrave;n c&aacute; sardine.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish6.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute;ng thả bong b&oacute;ng để đ&agrave;n c&aacute; rối loạn co cụm th&agrave;nh đ&aacute;m nhỏ hơn khiến ch&uacute;ng dễ d&agrave;ng nuốt gọn cả đống.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish7.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Để tự bảo vệ m&igrave;nh, c&aacute;c con c&aacute; sardine kết th&agrave;nh những quả b&oacute;ng khổng lồ c&oacute; đường k&iacute;nh l&ecirc;n tới 20 m.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish8.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con chim trời cũng lao xuống nước như những mũi t&ecirc;n để săn c&aacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish9.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tham gia cuộc săn đuổi l&agrave; cả những con c&aacute; mập hung dữ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish10.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trong khi c&aacute; heo hợp t&aacute;c với nhau để ăn mồi, th&igrave; c&aacute; mập c&oacute; trật tự nhất định, dựa tr&ecirc;n k&iacute;ch cỡ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish11.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con lớn nhất đi trước, c&aacute;c con nhỏ theo sau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish12.jpg" border="1" alt="" width="450" height="255" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngay khi bữa tiệc chuẩn bị kết th&uacute;c, th&igrave; c&aacute; voi Brydes xuất hiện...</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish13.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">... v&agrave; nuốt gọn cả ngh&igrave;n con c&aacute; nhỏ trong một c&uacute; ngoạm khổng lồ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fish14.jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&agrave;ng trăm con chim trời lao xuống biển săn mồi, phủ k&iacute;n cả bầu trời.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Thi</strong><br />(Ảnh:&nbsp;<em>Barcott Media</em>)</p>', 35, 8, 1172, 0, 1),
(593, 'Mèo múa võ', 'Nhảy chồm chồm trên không trung, giương cao móng vuốt, những con mèo như đang tham dự vào một cuộc chiến quyết tử.', 'meo1 (1).jpg', NULL, '<p class="Title">M&egrave;o m&uacute;a v&otilde;</p>\n<p class="Lead">Nhảy chồm chồm tr&ecirc;n kh&ocirc;ng trung, giương cao m&oacute;ng vuốt, những con m&egrave;o như đang tham dự v&agrave;o một cuộc chiến quyết tử.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="meo1 (1).jpg" border="1" alt="" width="450" height="403" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Nhưng thực ra kh&ocirc;ng phải ch&uacute;ng đang cạnh tranh quyết liệt để gi&agrave;nh mồi hay l&atilde;nh thổ, m&agrave; chỉ đang vui đ&ugrave;a với nhau.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="meo2 (1).jpg" border="1" alt="" width="450" height="341" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Cuộc chiến đầy t&iacute;nh v&otilde; thuật của cặp m&egrave;o mang t&ecirc;n Muffi v&agrave; Tiger đ&atilde; được người chủ Mats Hamnas ghi lại tại khu vườn của m&igrave;nh ở Helsingborg, Thụy Điển.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="meo3 (1).jpg" border="1" alt="" width="400" height="439" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Vi&ecirc;n kỹ sư m&aacute;y t&iacute;nh cho biết: "Trong ảnh tr&ocirc;ng ch&uacute;ng như muốn cắn x&eacute; nhau. T&ocirc;i rất ngạc nhi&ecirc;n khi thấy ch&uacute;ng dữ tợn đến vậy. Thực tế, Muffi v&agrave; Tiger rất th&acirc;n thiết. Nhưng khi bạn nh&igrave;n h&igrave;nh ảnh n&agrave;y cảm tưởng như đ&oacute; l&agrave; một cặp ninja đang quyết chiến".</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="meo4 (1).jpg" border="1" alt="" width="400" height="397" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Mats, 62 tuổi, cũng cho biết hai con vật cưng 4-5 tuổi của m&igrave;nh rất th&iacute;ch đi c&ugrave;ng &ocirc;ng v&agrave;o khu rừng gần nh&agrave;. "Ch&uacute;ng giống như c&aacute;c con ch&oacute; lu&ocirc;n quẩn ch&acirc;n m&igrave;nh v&agrave; cũng rất tinh nghịch. Sau khi vật lộn ch&aacute;n ch&ecirc;, ch&uacute;ng nằm &ocirc;m ấp lấy nhau tại g&oacute;c nh&agrave; v&agrave; thư gi&atilde;n".</p>\n<p class="Normal" style="text-align: right;"><strong>Minh Thi</strong>&nbsp;<br />(Ảnh:&nbsp;<em>Caters News Agency</em>)</p>', 35, 8, 372, 0, 1),
(595, 'Chó mèo hóa trang thành rocker', 'Hãng Takkoda tại Anh đã tung ra một bộ lịch năm 2010 gồm hình ảnh về các con vật nuôi trong hình hài của những ngôi sao nhạc rock. Những con vật này được chụp tự nhiên tại nhà rồi được hóa trang nhờ công nghệ kỹ thuật số.', 'pet1.jpg', NULL, '<p class="Title">Ch&oacute; m&egrave;o h&oacute;a trang th&agrave;nh rocker</p>\r\n<p class="Lead">H&atilde;ng Takkoda tại Anh đ&atilde; tung ra một bộ lịch năm 2010 gồm h&igrave;nh ảnh về c&aacute;c con vật nu&ocirc;i trong h&igrave;nh h&agrave;i của những ng&ocirc;i sao nhạc rock. Những con vật n&agrave;y được chụp tự nhi&ecirc;n tại nh&agrave; rồi được h&oacute;a trang nhờ c&ocirc;ng nghệ kỹ thuật số.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet1.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet2.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet3.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet4.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet5.jpg" border="1" alt="" width="400" height="408" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet6.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet7.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet8.jpg" border="1" alt="" width="400" height="400" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet9.jpg" border="1" alt="" width="350" height="350" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet10.jpg" border="1" alt="" width="350" height="381" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet11.jpg" border="1" alt="" width="350" height="350" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pet12.jpg" border="1" alt="" width="350" height="361" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Thi</strong>&nbsp;(Ảnh:&nbsp;<em>Jeff Moore</em>)</p>', 35, 8, 2671, 0, 1),
(597, 'Thiên nhiên cuồng nộ', 'Mây đen phủ kín bầu trời, sấm sét dữ dội trong cơn bão điện từ. Dưới đây là hình ảnh những cơn bão ở Mỹ mà nhiếp ảnh gia Jim Reed tập hợp trong cuốn sách của ông.', 'stormchaser1.jpg', NULL, '<p class="Title">Thi&ecirc;n nhi&ecirc;n cuồng nộ</p>\r\n<p class="Lead">M&acirc;y đen phủ k&iacute;n bầu trời, sấm s&eacute;t dữ dội trong cơn b&atilde;o điện từ. Dưới đ&acirc;y l&agrave; h&igrave;nh ảnh những cơn b&atilde;o ở Mỹ m&agrave; nhiếp ảnh gia Jim Reed tập hợp trong cuốn s&aacute;ch của &ocirc;ng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser1.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tia s&eacute;t quất ngang trời v&agrave;o l&uacute;c cơn d&ocirc;ng k&eacute;o tới th&agrave;nh phố Medicine, bang Kansas, Mỹ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser11.jpg" border="1" alt="" width="406" height="430" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">V&ograve;i rồng qu&eacute;t qua con đường ở miền nam bang Kansas th&aacute;ng 5/2008.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser3.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">S&oacute;ng dữ ập v&agrave;o bờ ở Galveston, bang Texas, khi cơn b&atilde;o Ike c&agrave;n qu&eacute;t qua khu vực n&agrave;y th&aacute;ng 12/2008.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser4.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bầu trời rực m&agrave;u xanh v&agrave; t&iacute;m l&uacute;c ho&agrave;ng h&ocirc;n khi cơn b&atilde;o k&eacute;o tới bang Oklahoma th&aacute;ng 5/2002.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser6.jpg" border="1" alt="" width="325" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiếp ảnh gia Mike Theiss giữ một tấm biển chỉ đường để lấy thăng bằng trong cơn b&atilde;o Katrina khi n&oacute; cuốn qua bang Mississippi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser10.jpg" border="1" alt="" width="346" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chớp rạch loằng ngoằng tr&ecirc;n bầu trời bang Wyoming th&aacute;ng 6/2003.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="stormchaser9.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bầu trời th&agrave;nh phố New Orleans, bang Louisiana, x&aacute;m xịt khi b&atilde;o Gustav k&eacute;o tới th&aacute;ng 8/2008.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong>&nbsp;(Ảnh:&nbsp;<em>BARCROFT MEDIA</em>)</p>', 35, 8, 2461, 0, 1),
(598, 'Thiên nhiên hoang dã trong vườn thú Singapore', 'Độc giả Hoàn Vũ chia sẻ những bức ảnh về khu vườn thú Singapore, nơi ở của hơn 3.200 loài động vật có vú, chim chóc, bò sát và các loài cá. Ảnh chụp bằng máy Canon EOS 40D.', 'sing1 (1).jpg', NULL, '<p class="Title">Thi&ecirc;n nhi&ecirc;n hoang d&atilde; trong vườn th&uacute; Singapore</p>\n<p class="Lead">Độc giả Ho&agrave;n Vũ chia sẻ những bức ảnh về khu vườn th&uacute; Singapore, nơi ở của hơn 3.200 lo&agrave;i động vật c&oacute; v&uacute;, chim ch&oacute;c, b&ograve; s&aacute;t v&agrave; c&aacute;c lo&agrave;i c&aacute;. Ảnh chụp bằng m&aacute;y Canon EOS 40D.<br />Vườn th&uacute; Singapore l&agrave; một m&ocirc; h&igrave;nh sở th&uacute; mở. 3.200 lo&agrave;i động vật được sống trong m&ocirc;i trường tự nhi&ecirc;n, rất gần với m&ocirc;i trường sống b&ecirc;n ngo&agrave;i của ch&uacute;ng. Ngăn c&aacute;ch giữa ch&uacute;ng v&agrave; du kh&aacute;ch l&agrave; những h&agrave;o bảo vệ được che giấu kh&eacute;o l&eacute;o bởi thảm thực vật tầm thấp vừa tr&aacute;nh hạn chế tầm nh&igrave;n, vừa tạo cho du kh&aacute;ch cảm gi&aacute;c h&ograve;a nhập thật sự v&agrave;o thế giới thi&ecirc;n nhi&ecirc;n hoang d&atilde;.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing1 (1).jpg" border="1" alt="" width="470" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">Hai con hổ trắng v&ugrave;ng Bengal thư gi&atilde;n giữa rừng c&acirc;y xanh biếc.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing3 (1).jpg" border="1" alt="" width="470" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">D&ugrave; số lượng đ&agrave;n Hồng Hạc (Flamingo) kh&ocirc;ng bằng ở Vườn Chim Jurong, Singapore, nhưng cũng đủ l&agrave;m m&ecirc; mẩn du kh&aacute;ch.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing5 (1).jpg" border="1" alt="" width="470" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">Ch&agrave;ng hươu cao cổ thơ thẩn giữa hai n&agrave;ng.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing4 (1).jpg" border="1" alt="" width="300" height="450" /></td>\n</tr>\n<tr>\n<td class="Image">Ch&uacute; khỉ m&ecirc; mẩn với đồ chơi mới.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing6 (1).jpg" border="1" alt="" width="470" height="314" /></td>\n</tr>\n<tr>\n<td class="Image">Đ&aacute;m ngựa vằn thưởng ngoạn bữa trưa ngon l&agrave;nh.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing7 (1).jpg" border="1" alt="" width="470" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">"C&otilde;i" ri&ecirc;ng của một ch&uacute; r&ugrave;a.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing9 (1).jpg" border="1" alt="" width="470" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">Rồng komodo (Varanus komodoensis) l&agrave; lo&agrave;i thằn lằn lớn nhất thế giới hiện nay, chiều d&agrave;i con lớn l&ecirc;n tới 2-3 m.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing2 (1).jpg" border="1" alt="" width="470" height="314" /></td>\n</tr>\n<tr>\n<td class="Image">Du kh&aacute;ch cho động vật trong vườn th&uacute; ăn v&agrave; chụp ảnh lưu niệm.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="sing8 (1).jpg" border="1" alt="" width="470" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">Khung cảnh hữu t&igrave;nh b&ecirc;n trong vườn th&uacute;.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Ho&agrave;n Vũ</strong></p>', 35, 8, 365, 0, 1),
(599, 'Voi được lắp chân giả', 'Motola, nàng voi 48 tuổi ở Thái Lan, được gắn một chiếc chân giả thay thế cho phần bị mất vì giẫm phải mìn 10 năm trước.', 'alp6.jpg', NULL, '<p class="Title">Voi được lắp ch&acirc;n giả</p>\r\n<p class="Lead">Motola, n&agrave;ng voi 48 tuổi ở Th&aacute;i Lan, được gắn một chiếc ch&acirc;n giả thay thế cho phần bị mất v&igrave; giẫm phải m&igrave;n 10 năm trước.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="alp6.jpg" border="1" alt="" width="450" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&acirc;n vi&ecirc;n của tổ chức Prostheses Foundation, cũng chế tạo ch&acirc;n tay giả cho người, ho&agrave;n thiện phần ch&acirc;n giả cho Motola tuần trước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="elephant2.jpg" border="1" alt="" width="330" height="450" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Motola đ&atilde; phải trải qua h&agrave;ng loạt cuộc phẫu thuật. C&ocirc; trở th&agrave;nh biểu tượng cho số phận những con voi hiện nay. Thương tật của c&ocirc; đ&atilde; khiến cộng đồng quốc tế thương cảm v&agrave; quy&ecirc;n g&oacute;p tiền hỗ trợ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="alep7.jpg" border="1" alt="" width="450" height="309" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Terdchai Cheevakate,chuy&ecirc;n gia về ch&acirc;n giả, giơ một m&ocirc; h&igrave;nh ch&acirc;n của c&ocirc; n&agrave;ng voi Motola.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="alp4.jpg" border="1" alt="" width="401" height="430" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Motola đi được 10 ph&uacute;t v&agrave; đ&ugrave;a nghịch với c&aacute;t sau khi được lắp ch&acirc;n giả ở bệnh viện voi tỉnh Lampang, Th&aacute;i Lan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="elp3.jpg" border="1" alt="" width="430" height="387" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Năm 2007, một con voi kh&aacute;c l&agrave; Mosha cũng mất ch&acirc;n v&igrave; giẫm phải m&igrave;n đ&atilde; được gắn ch&acirc;n giả ở bệnh viện tỉnh Lampang.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="elp2.jpg" border="1" alt="" width="450" height="347" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mosha, giờ đ&atilde; 3 tuổi, được nh&acirc;n vi&ecirc;n bệnh viện voi ở Lampang dắt đi dạo. B&aacute;c sĩ cho biết Mosha ph&aacute;t triển b&igrave;nh thường.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Mai Trang</strong>&nbsp;(theo&nbsp;<em>AP</em>)</p>', 35, 8, 2026, 0, 1),
(601, 'Lặn cùng cá mập', 'Bộ hàm gớm giếc, những chiếc răng sắc nhọn của cá mập chanh được một nhiếp ảnh gia ghi lại sau 8 năm sống cùng sát thủ đại dương', 'cm2.jpg', NULL, '<p class="Title">Lặn c&ugrave;ng c&aacute; mập</p>\r\n<p class="Lead">Bộ h&agrave;m gớm giếc, những chiếc răng sắc nhọn của c&aacute; mập chanh được một nhiếp ảnh gia ghi lại sau 8 năm sống c&ugrave;ng s&aacute;t thủ đại dương.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm2.jpg" border="0" alt="" width="480" height="324" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Để chụp được những bức ảnh cận cảnh c&aacute; mập, nhiếp ảnh gia Eric Cheng đ&atilde; d&agrave;nh 8 năm qua tiếp cận lo&agrave;i c&aacute; n&agrave;y. Bức ảnh được chụp tại Grand Bahama Bank ở Bahamas tr&ecirc;n Đại T&acirc;y Dương.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm1.jpg" border="0" alt="" width="480" height="312" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chiếc h&agrave;m rộng ngo&aacute;c của c&aacute; mập canh chĩa thẳng v&agrave;o ống k&iacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm8.jpg" border="0" alt="" width="480" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con c&aacute; mập chanh th&ograve; h&agrave;m răng gớm giếc l&ecirc;n khỏi mặt nước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm3.jpg" border="0" alt="" width="480" height="314" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Để c&oacute; những bức ảnh như vậy, Eric phải đặt ống k&iacute;nh m&aacute;y cảnh s&aacute;t s&agrave;m con c&aacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm4.jpg" border="0" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khi tiến s&aacute;t gần, c&aacute; mập kh&ocirc;ng kh&aacute;c g&igrave; những g&atilde; s&aacute;t thủ khổng lồ gớm giếc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm5.jpg" border="0" alt="" width="480" height="316" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những chiếc răng sắc nhọn của ch&uacute;ng c&oacute; thể l&agrave; nỗi &aacute;m ảnh lớn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cm6.jpg" border="0" alt="" width="330" height="479" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Eric tin rằng nghệ thuật nhiếp ảnh dưới nước l&agrave; phải tiếp cận thật gần với đối tượng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong>&nbsp;(theo<em>&nbsp;Telegraph</em>)</p>', 35, 8, 1309, 0, 1),
(602, 'Đón bình minh ngày hạ chí', '36.000 người đổ về khu di tích Stonehenge, Anh, đón ánh mặt trời đầu tiên trong ngày dài nhất năm 21/6.', 'hc1.jpg', NULL, '<p class="Title">Đ&oacute;n b&igrave;nh minh ng&agrave;y hạ ch&iacute;</p>\r\n<p class="Lead">36.000 người đổ về khu di t&iacute;ch Stonehenge, Anh, đ&oacute;n &aacute;nh mặt trời đầu ti&ecirc;n trong ng&agrave;y d&agrave;i nhất năm 21/6.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc1.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&agrave;ng ngh&igrave;n người tới những khu vực mang &yacute; nghĩa t&ocirc;n gi&aacute;o như Stonehenge ở Wiltshire, t&acirc;y nam Anh, để chi&ecirc;m ngưỡng &aacute;nh mặt trời đầu ti&ecirc;n của m&ugrave;a h&egrave; ở bắc b&aacute;n cầu. Ảnh:&nbsp;<em>Getty Images.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc2.jpg" border="1" alt="" width="470" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Năm nay c&oacute; tới 36.000 người tập trung tại khu Stonehenge n&agrave;y để đ&oacute;n b&igrave;nh minh. Với những người theo đạo Pagan, hạ ch&iacute; l&agrave; ng&agrave;y của sự sống v&agrave; trưởng th&agrave;nh. Ảnh:&nbsp;<em>BBC</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc3.jpg" border="1" alt="" width="470" height="305" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&aacute;m đ&ocirc;ng h&ograve; reo khi mặt trời bắt đầu l&oacute; dạng v&agrave;o l&uacute;c 4h58. Ảnh:&nbsp;<em>PA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc4.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&agrave;y 21/6 l&agrave; ng&agrave;y d&agrave;i nhất trong năm khi mặt trời l&ecirc;n tới điểm cao nhất tr&ecirc;n bầu trời. Ảnh:&nbsp;<em>PA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc5.jpg" border="1" alt="" width="470" height="304" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Với nhiều người theo đạo Pagan, ng&agrave;y hạ ch&iacute; đ&aacute;nh dấu lễ cưới của ch&uacute;a trời v&agrave; nữ thần. Hai du kh&aacute;ch người Đức cũng tận dụng cơ hội n&agrave;y để kết h&ocirc;n. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc6.jpg" border="1" alt="" width="316" height="470" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Frank Summers - một người theo đạo Druid tin rằng Stonehenge l&agrave; một ng&ocirc;i đền linh thi&ecirc;ng - vui mừng khi nhiều người tới nơi n&agrave;y trong ng&agrave;y quan trọng tr&ecirc;n lịch Druid. "Ai nấy đều ra về với t&acirc;m trạng vui vẻ", anh n&oacute;i. Ảnh:&nbsp;<em>PA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc7.jpg" border="1" alt="" width="470" height="309" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cảnh s&aacute;t v&agrave; nh&acirc;n vi&ecirc;n an ninh được điều tới đ&acirc;y để giữ trật tự. Họ cho biết sự kiện diễn ra kh&aacute; y&ecirc;n b&igrave;nh. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc8.jpg" border="1" alt="" width="310" height="470" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&agrave;y hạ ch&iacute; diễn ra ngay trước lễ hội Glastonbury tại Somerset gần đ&oacute;. Nhiều du kh&aacute;ch coi khu Stonehenge n&agrave;y l&agrave; điểm dừng ch&acirc;n nghỉ ngơi. Ảnh:&nbsp;<em>Getty Images.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc9.jpg" border="1" alt="" width="312" height="470" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Isabelle Dale, 16 tuổi, mặc trang phục của người Druid đ&oacute;n b&igrave;nh minh ng&agrave;y hạ ch&iacute;. Ảnh:&nbsp;<em>PA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="hc10.jpg" border="1" alt="" width="470" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Du kh&aacute;ch đ&aacute;nh trống, h&aacute;t h&ograve; v&agrave; nhảy m&uacute;a ch&agrave;o mừng ng&agrave;y hạ ch&iacute;. Ảnh:&nbsp;<em>PA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong></p>', 35, 8, 1014, 0, 1),
(603, 'Ảnh đẹp động vật trong tuần', 'Vịt một cánh tung mình trên hồ nước. Rùa mất chân lướt trên xe lăn. Dưới đây là những bức ảnh đẹp về thế giới động vật trong tuần.', 'an2.jpg', NULL, '<p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">Vịt một c&aacute;nh tung m&igrave;nh tr&ecirc;n hồ nước. R&ugrave;a mất ch&acirc;n lướt tr&ecirc;n xe lăn. Dưới đ&acirc;y l&agrave; những bức ảnh đẹp về thế giới động vật trong tuần.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an2.jpg" border="0" alt="" width="460" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; vịt Charlie tung chiếc c&aacute;nh duy nhất tr&ecirc;n mặt hồ ở West Sussex, Anh. Charlie bị tr&uacute;ng đạn v&agrave; phải phẫu thuật bỏ c&aacute;nh phải. Ảnh:&nbsp;<em>M and Y Portsmouth.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an1.jpg" border="0" alt="" width="460" height="351" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">D&ecirc; con say sưa b&uacute; b&ograve; ở Hyderabad, Ấn Độ. Ảnh:&nbsp;<em>DEMOTIX.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an3.jpg" border="0" alt="" width="460" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con r&ugrave;a Tonka ở California, Mỹ, được gắn chiếc xe lăn sau khi bị ch&oacute; cắn mất ch&acirc;n tr&aacute;i ph&iacute;a trước. Ảnh:&nbsp;<em>Caters Pictures.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em>&nbsp;</em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an4.jpg" border="0" alt="" width="460" height="322" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sư tử c&aacute;i vươn cổ ng&aacute;p trong vườn th&uacute; Duisburg, Đức. Ảnh:&nbsp;<em>EPA.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an5.jpg" border="0" alt="" width="460" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con thằn lằn vật nhau trong c&ocirc;ng vi&ecirc;n Ahmedabad ở ph&iacute;a t&acirc;y Ấn Độ. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an6.jpg" border="0" alt="" width="460" height="308" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&aacute;c sĩ th&uacute; y giũa m&oacute;ng ch&acirc;n cho Boy, con voi ch&acirc;u &Aacute; 39 tuổi ở vườn th&uacute; Kiev, Ukraina. Ảnh:<em>AFP/Getty.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an7.jpg" border="0" alt="" width="460" height="315" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con c&aacute; sấu đớp gọn con c&aacute; trong miệng ở Guwahati, bang Assam, Ấn Độ. Ảnh:<em>AFP/Getty</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 35, 8, 156, 0, 1),
(604, 'Nga tăng dân số cho trạm không gian', 'Tàu vũ trụ Soyuz TMA-15 của Nga hôm nay rời bệ phóng ở Baikonur, đưa 3 phi hành gia lên bổ sung nhân sự cho trạm không gian quốc tế (ISS) và đây là lần đầu tiên ISS có 6 người cùng làm việc thường trực.', 'soyz1.jpg', NULL, '<p class="Title">Nga tăng d&acirc;n số cho trạm kh&ocirc;ng gian</p>\r\n<p class="Lead">T&agrave;u vũ trụ Soyuz TMA-15 của Nga h&ocirc;m nay rời bệ ph&oacute;ng ở Baikonur, đưa 3 phi h&agrave;nh gia l&ecirc;n bổ sung nh&acirc;n sự cho trạm kh&ocirc;ng gian quốc tế (ISS) v&agrave; đ&acirc;y l&agrave; lần đầu ti&ecirc;n ISS c&oacute; 6 người c&ugrave;ng l&agrave;m việc thường trực.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz1.jpg" border="0" alt="" width="460" height="294" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con t&agrave;u Soyuz TMA-15 nằm tr&ecirc;n bệ ph&oacute;ng tại s&acirc;n bay vũ trụ Baikonur h&ocirc;m 25/5.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz2.jpg" border="0" alt="" width="360" height="440" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c nh&acirc;n vi&ecirc;n kỹ thuật đang vận h&agrave;nh b&ecirc;n t&agrave;u Soyuz TMA-15. Con t&agrave;u mang theo ba nh&agrave; du h&agrave;nh của Nga, Canada v&agrave; Cơ quan vũ trụ ch&acirc;u &Acirc;u l&ecirc;n trạm ISS.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz4.jpg" border="0" alt="" width="360" height="556" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><span style="color: #000080;"><span style="color: #000000;">Phần đầu của t&agrave;u Soyuz TMA-15. Con t&agrave;u sẽ đưa th&ecirc;m qu&acirc;n số cho trạm kh&ocirc;ng gian ISS v&agrave; lần đầu ti&ecirc;n tr&ecirc;n đ&oacute; c&oacute; 6 người.</span></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz5.jpg" border="0" alt="" width="360" height="519" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Soyuz TMA-15 rời bệ ph&oacute;ng ở s&acirc;n bay vũ trụ Baikonur.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz6.jpg" border="0" alt="" width="360" height="531" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con t&agrave;u lao v&agrave;o kh&ocirc;ng gian, để lại vệt kh&oacute;i v&agrave; lửa d&agrave;i ph&iacute;a sau.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz8.jpg" border="0" alt="" width="460" height="352" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chẳng mấy chốc n&oacute; chỉ c&ograve;n l&agrave; một đốm lửa nhỏ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soyz9.jpg" border="0" alt="" width="460" height="284" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&oacute;m 3 phi h&agrave;nh gia đi tr&ecirc;n t&agrave;u Soyuz TMA-15 gồm nh&agrave; du h&agrave;nh Canada Robert Thirsk (<em>tr&aacute;i</em>), Roman Romanenko người Nga (<em>giữa</em>) v&agrave; phi h&agrave;nh gia thuộc Cơ quan kh&ocirc;ng gian ch&acirc;u &Acirc;u Frank De Winne (người Bỉ) vẫy tay ch&agrave;o trước khi rời tr&aacute;i đất.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><span style="color: #000000;"><strong>Ngọc Sơn</strong>&nbsp;(theo&nbsp;<em>Xinhua/Reuters</em>)</span></p>', 35, 8, 2634, 0, 1),
(605, 'Châu Phi hoang dã', 'Đại bàng quắp mồi tung cánh trên không. Sư tử vờn nhau trong công viên. Bướm hoàng đế hạ cánh trên lưng voi. Dưới đây là những bức ảnh về thiên nhiên hoang dã ở châu Phi.', 'cp1.jpg', NULL, '<p class="Title">Ch&acirc;u Phi hoang d&atilde;</p>\r\n<p class="Lead">Đại b&agrave;ng quắp mồi tung c&aacute;nh tr&ecirc;n kh&ocirc;ng. Sư tử vờn nhau trong c&ocirc;ng vi&ecirc;n. Bướm ho&agrave;ng đế hạ c&aacute;nh tr&ecirc;n lưng voi. Dưới đ&acirc;y l&agrave; những bức ảnh về thi&ecirc;n nhi&ecirc;n hoang d&atilde; ở ch&acirc;u Phi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp1.jpg" border="0" alt="" width="460" height="309" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute; sấu s&ocirc;ng Nile tung một con c&aacute; n&oacute; vừa bắt được l&ecirc;n kh&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp2.jpg" border="0" alt="" width="460" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con c&ograve; gi&agrave; săn được con c&aacute; nhỏ v&agrave; nuốt gọn trong c&ocirc;ng vi&ecirc;n quốc gia Kruger, Nam Phi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp3.jpg" border="0" alt="" width="460" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con b&aacute;o con tranh nhau xử l&yacute; x&aacute;c một con linh dương săn được trong c&ocirc;ng vi&ecirc;n Kgalagadi, Nam Phi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp4.jpg" border="0" alt="" width="460" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đại bằng sải c&aacute;nh bay, ch&acirc;n quặp con mồi vừa săn được tại c&ocirc;ng vi&ecirc;n quốc gia Kruger.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp5.jpg" border="0" alt="" width="460" height="299" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Bướm ho&agrave;ng đế hạ c&aacute;nh tr&ecirc;n lưng voi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp6.jpg" border="0" alt="" width="460" height="296" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Diều h&acirc;u lưng đen ăn mồi tr&ecirc;n ngọn c&acirc;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp7.jpg" border="0" alt="" width="460" height="296" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&ocirc;i sư tử đang t&aacute;n tỉnh nhau trong c&ocirc;ng vi&ecirc;n quốc gia Etosha, Namibia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp8.jpg" border="0" alt="" width="460" height="254" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&ocirc;ng đ&aacute; trong c&ocirc;ng vi&ecirc;n quốc gia Kruger.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp9.jpg" border="0" alt="" width="460" height="313" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vũ điệu của c&ograve;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cp10.jpg" border="0" alt="" width="460" height="306" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Kền kền rỉa x&aacute;c linh dương.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong>&nbsp;(theo&nbsp;<em>Telegraph</em>)</p>\r\n<p style="text-align: right;">&nbsp;</p>', 35, 8, 573, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(606, 'Những sinh vật kỳ lạ dưới đại dương', 'Qua ống kính của các máy ảnh chuyên dụng, những loài động vật có hình thù kỳ dị sống dưới đại dương cho thấy một cuộc sống kỳ ảo, ẩn sau màn đêm thường trực ở nơi cách xa ánh nắng mặt trời này.', '19.jpg', NULL, '<p class="Title">Những sinh vật kỳ lạ dưới đại dương</p>\r\n<p class="Lead">Qua ống k&iacute;nh của c&aacute;c m&aacute;y ảnh chuy&ecirc;n dụng, những lo&agrave;i động vật c&oacute; h&igrave;nh th&ugrave; kỳ dị sống dưới đại dương cho thấy một cuộc sống kỳ ảo, ẩn sau m&agrave;n đ&ecirc;m thường trực ở nơi c&aacute;ch xa &aacute;nh nắng mặt trời n&agrave;y.</p>\r\n<p class="Normal">Mạng lưới nghi&ecirc;n cứu to&agrave;n cầu Census for Marine Life (COML) gồm c&aacute;c nh&agrave; khoa học ở hơn 80 quốc gia. Tổ chức n&agrave;y ước t&iacute;nh c&oacute; khoảng 230.000 lo&agrave;i động vật biển đ&atilde; được ghi nhận. nhưng họ dự đo&aacute;n tr&ecirc;n thực tế c&oacute; từ 500.000 đến 10 triệu lo&agrave;ii n&ecirc;n sẽ c&ograve;n nhiều sinh vật mới được ph&aacute;t hiện. Dưới đ&acirc;y l&agrave; c&aacute;c bức ảnh do mạng lưới COML thực hiện.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="19.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Động vật ch&acirc;n c&aacute;nh th&acirc;n mềm oxygyrus keraudreni "bay" dưới nước giống một con chim trong suốt . Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="1.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Megaleledone Setebos, lo&agrave;i đặc hữu ở Nam Th&aacute;i B&igrave;nh Dương. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="2.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cua người tuyết kiwa hirsuta được ph&aacute;t hiện gần đảo Phục sinh (Easter Island). Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="3.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sứa Aequorea macrodactyla. Ảnh:&nbsp;<em>COML.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="55.jpg" border="1" alt="" width="480" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lo&agrave;i sứa c&oacute; phần nội tạng m&agrave;u v&agrave;ng trải khắp th&acirc;n trong suốt. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="24.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">S&acirc;u quạt (sabellid). Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="26.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trai biển. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="8.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sứa thalassocalyce. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="9.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sứa lược Bắc Cực. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="10.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lo&agrave;i elizabethina đa sắc. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="11.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhện biển đực mang trứng dưới bụng. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="12.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Gi&aacute;p x&aacute;c ch&acirc;n hai loại (amphipod). Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="21.jpg" border="1" alt="" width="480" height="361" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mực ống trang sức Histioteuthis sống ở độ s&acirc;u từ 500 m&eacute;t đến 2.000 m&eacute;t. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="14.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lo&agrave;i pyrosomella verticilliata. Ảnh:&nbsp;<em>COML</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="16.jpg" border="1" alt="" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">S&ecirc;n bơi thuộc họ động vật ch&acirc;n c&aacute;nh (Clione limacina) t&igrave;m thấy ở cả Bắc Cực v&agrave; Nam Cực. Ảnh:<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style="text-align: -webkit-center;"></div>\r\n<p class="Normal" style="text-align: right;"><strong>Đ&igrave;nh Ch&iacute;nh</strong></p>', 35, 8, 2009, 0, 1),
(608, 'Sức sống rừng nhiệt đới', 'Đảo Borneo lớn thứ ba thế giới thuộc chủ quyền của 3 nước Indonesia, Malaysia và Brunei được bao phủ bởi những cánh rừng đại ngàn và là nơi có mức độ đa dạng sinh học cao bậc nhất thế giới.', 'borneo1.jpg', NULL, '<p class="Title">Sức sống rừng nhiệt đới</p>\r\n<p class="Lead">Đảo Borneo lớn thứ ba thế giới thuộc chủ quyền của 3 nước Indonesia, Malaysia v&agrave; Brunei được bao phủ bởi những c&aacute;nh rừng đại ng&agrave;n v&agrave; l&agrave; nơi c&oacute; mức độ đa dạng sinh học cao bậc nhất thế giới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo1.jpg" border="0" alt="" width="460" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những c&aacute;nh rừng rậm tập trung nhiều lo&agrave;i động vật hoang d&atilde; của Borneo đ&atilde; khiến n&oacute; trở th&agrave;nh thi&ecirc;n đường cho c&aacute;c nh&agrave; nghi&ecirc;n cứu, nh&agrave; khoa học v&agrave; cả những người ưa kh&aacute;m ph&aacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo2.jpg" border="0" alt="" width="460" height="301" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những tia nắng ban mai bu&ocirc;ng xuống thung lũng Danum thuộc bang Sabah của Malaysia, nơi cư tr&uacute; của những lo&agrave;i động vật c&oacute; nguy cơ tuyệt chủng, trong đ&oacute; c&oacute; giống voi l&ugrave;n của Borneo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo3.jpg" border="0" alt="" width="460" height="333" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đười ươi ăn sầu ri&ecirc;ng tr&ecirc;n c&agrave;nh c&acirc;y ở thung lũng Danum. Theo tiếng Malay, từ đười ươi c&oacute; nghĩa l&agrave; "người rừng".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo4.jpg" border="0" alt="" width="460" height="316" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lo&agrave;i rắn độc trong c&ocirc;ng vi&ecirc;n quốc gia Bako, tr&ecirc;n đảo Borneo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo5.jpg" border="0" alt="" width="460" height="308" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những c&aacute;nh rừng xanh ng&uacute;t mắt của Borneo nh&igrave;n từ tr&ecirc;n cao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo6.jpg" border="0" alt="" width="460" height="297" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mẹ con khỉ đu&ocirc;i d&agrave;i chuyền c&agrave;nh c&acirc;y trong rừng Sabah ở Borneo. Lo&agrave;i khỉ n&agrave;y sống gần như cả đời tr&ecirc;n ngọn c&acirc;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo7.jpg" border="0" alt="" width="460" height="304" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhu cầu về dầu cọ l&ecirc;n cao khiến người d&acirc;n địa phương khai th&aacute;c những khoảnh rừng lớn ở Borneo, thu hẹp m&ocirc;i trường sống của c&aacute;c lo&agrave;i động vật hoang d&atilde;. Trong ảnh l&agrave; phần đất trống ở bang Sarawak của Malaysia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="borneo8.jpg" border="0" alt="" width="460" height="316" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khỉ đu&ocirc;i d&agrave;i &ocirc;m con ở thung lũng Danum. Lo&agrave;i linh trưởng n&agrave;y sống trong c&aacute;ch rừng gần bờ s&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong>&nbsp;(theo&nbsp;<em>NYT</em>)</p>', 35, 8, 1844, 0, 1),
(609, 'Ảnh động vật trong tuần', 'Hải cẩu ngậm núm vú giả, cô sư tử trắng ngáp trong cũi, con báo vờn khúc xương giả. Dưới đây là những hình ảnh đẹp về động vật tuần qua.', 'dv1 (6).jpg', NULL, '<p class="Title">Ảnh động vật trong tuần</p>\r\n<p class="Lead">Hải cẩu ngậm n&uacute;m v&uacute; giả, c&ocirc; sư tử trắng ng&aacute;p trong cũi, con b&aacute;o vờn kh&uacute;c xương giả. Dưới đ&acirc;y l&agrave; những h&igrave;nh ảnh đẹp về động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv1 (6).jpg" border="1" alt="" width="450" height="314" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con hải cẩu ngậm chiếc n&uacute;m v&uacute; giả m&agrave; n&oacute; t&igrave;m thấy trong bể ở vườn b&aacute;ch th&uacute; Jihlava, Czech. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv2 (6).jpg" border="1" alt="" width="450" height="282" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con chồn đồng cỏ ch&acirc;u Phi lọt thỏm trong b&agrave;n tay của người tr&ocirc;ng nom vườn th&uacute; ở London, Anh. Ảnh:&nbsp;<em>Getty Images.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv3 (6).jpg" border="1" alt="" width="450" height="306" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con gấu mới sinh tắm nắng ở vườn th&uacute; Athens, Hy Lạp. Ảnh:&nbsp;<em>EPA</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv4 (6).jpg" border="1" alt="" width="450" height="290" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con gorilla n&agrave;y được cứu từ tay những kẻ bu&ocirc;n lậu ở Congo. Ảnh :<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv6 (6).jpg" border="1" alt="" width="450" height="320" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mengala, vượn người 13 tuổi, ăn t&aacute;o ở vườn th&uacute; Hungary. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv7 (6).jpg" border="1" alt="" width="450" height="314" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ocirc; sư tử trắng ng&aacute;p b&ecirc;n trong lồng ở vườn th&uacute; tại Buenos Aires, Argentina. Ảnh:&nbsp;<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img "src=dv8 (6).jpg" border="1" alt="" width="450" height="353" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con b&aacute;o Nam Mỹ vờn chiếc xương giả c&oacute; chứa thức ăn ở vườn th&uacute; Buenos Aires. Ảnh:<em>Reuters</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Hải Ninh</strong></p>', 35, 8, 2410, 0, 1),
(610, 'Thiên đường của các loài chim', 'Những cánh rừng ở Belize, Trung Mỹ, là nơi sinh sống của vô số các loài chim, từ diệc hổ cổ trần cho đến tu căng ngọc lục bảo.', 'chim-7.jpg', NULL, '<p class="Title">Thi&ecirc;n đường của c&aacute;c lo&agrave;i chim</p>\r\n<p class="Lead">Những c&aacute;nh rừng ở Belize, Trung Mỹ, l&agrave; nơi sinh sống của v&ocirc; số c&aacute;c lo&agrave;i chim, từ diệc hổ cổ trần cho đến tu căng ngọc lục bảo.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-7.jpg" border="0" alt="" width="460" height="363" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ograve; đầu gỗ tung c&aacute;nh tr&ecirc;n nền trời xanh. Lo&agrave;i chim n&agrave;y thường kiếm ăn ở v&ugrave;ng nước n&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim1.jpg" border="0" alt="" width="460" height="344" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con diệc hổ cổ trần vươn chiếc mỏ v&agrave; cổ d&agrave;i tr&ecirc;n c&agrave;nh c&acirc;y. Ch&uacute;ng thường l&agrave;m tổ tr&ecirc;n những bụi c&acirc;y gần mặt nước.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-8.jpg" border="0" alt="" width="460" height="343" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim ong ch&acirc;n đỏ l&agrave;m tổ tr&ecirc;n c&acirc;y v&agrave; h&uacute;t mật hoa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-6.jpg" border="0" alt="" width="460" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&ocirc;i vẹt đu&ocirc;i d&agrave;i đậu tr&ecirc;n c&agrave;nh c&acirc;y. T&igrave;nh trạng khai th&aacute;c rừng khiến m&ocirc;i trường sinh sống của ch&uacute;ng bị thu hẹp lại. Lo&agrave;i chim n&agrave;y c&oacute; thể sống tới 75 năm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-2.jpg" border="0" alt="" width="460" height="340" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim tu căng ngọc lục bảo c&oacute; rất nhiều ở Belize. Lo&agrave;i chim n&agrave;y ăn c&ocirc;n tr&ugrave;ng, hoa quả v&agrave; cả trứng của c&aacute;c lo&agrave;i chim kh&aacute;c.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-3.jpg" border="0" alt="" width="460" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Để dụ c&aacute;, lo&agrave;i diệc xanh n&agrave;y thường thả c&ocirc;n tr&ugrave;ng hoặc s&acirc;u xuống những chỗ nước n&ocirc;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-4.jpg" border="0" alt="" width="460" height="342" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim aracari cổ đỏ thường sống theo nh&oacute;m khoảng 15 con.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim-5.jpg" border="0" alt="" width="360" height="477" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">G&otilde; kiến m&aacute; đen kiếm mồi tr&ecirc;n những c&agrave;nh c&acirc;y chết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong>&nbsp;(theo&nbsp;<em>NYT</em>)</p>', 35, 8, 1380, 0, 1),
(611, 'Ảnh động vật trong tuần  ', 'Chim làm tổ trong chụp đèn giao thông trên phố. Lạc đà không bướu tung mình qua hàng rào. Dưới đây là những bức ảnh đẹp về thế giới động vật tuần qua.', 'chim.jpg', NULL, '<p class="Title">Ảnh động vật trong tuần</p>\r\n<p class="Lead">Chim l&agrave;m tổ trong chụp đ&egrave;n giao th&ocirc;ng tr&ecirc;n phố. Lạc đ&agrave; kh&ocirc;ng bướu tung m&igrave;nh qua h&agrave;ng r&agrave;o. Dưới đ&acirc;y l&agrave; những bức ảnh đẹp về thế giới động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim.jpg" border="0" alt="" width="460" height="349" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chim l&agrave;m tổ trong chụp đ&egrave;n giao th&ocirc;ng ở trung t&acirc;m th&agrave;nh phố Leeds, Anh. Ảnh:&nbsp;<em>PA.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="meo.jpg" border="0" alt="" width="460" height="347" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ban nhạc rock của c&aacute;c ch&uacute; m&egrave;o, ng&ocirc;i sao của rạp xiếc American Acro-Cats. Ảnh:<em>AFP/GETTY</em><strong>.</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="lac-da.jpg" border="0" alt="lạc đ&agrave;" width="460" height="292" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; lạc đ&agrave; kh&ocirc;ng biếu tung m&igrave;nh qua h&agrave;ng r&agrave;o ở Dorset, Anh. Ảnh:&nbsp;<em>DORSET MEDIA SERVICE</em><strong>.</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="lemut.jpg" border="0" alt="" width="460" height="312" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con vượn c&aacute;o gặm hoa quả trong vườn th&uacute; Bernburg, Đức. Ảnh:&nbsp;<em>REX FEATURES</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="da-dieu.jpg" border="0" alt="" width="460" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đ&agrave; điểu con 10 ng&agrave;y tuổi theo ch&acirc;n bố trong vườn th&uacute; Berlin, Đức. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cho.jpg" border="0" alt="ch&oacute;" width="460" height="312" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&oacute; tham gia tr&igrave;nh diễn thời trang ở Bangkok, Th&aacute;i Lan. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="lion.jpg" border="0" alt="" width="460" height="313" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sư tử n&uacute;i đu m&igrave;nh tr&ecirc;n c&acirc;y ở Wisconsin, Mỹ. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 35, 8, 2869, 0, 1),
(612, 'Hình ảnh ấn tượng về Ngày Trái đất', 'Kể từ năm 1970, Ngày Trái đất được tổ chức thường niên vào 22/4, trong đó sinh viên các trường đại học khắp nước Mỹ tụ họp và tăng cường hiểu biết về sự suy thoái của môi trường. Sự kiện này cũng diễn ra tại một số nước khác.', 'ntd1.jpg', NULL, '<p class="Title">H&igrave;nh ảnh ấn tượng về Ng&agrave;y Tr&aacute;i đất</p>\r\n<p class="Lead">Kể từ năm 1970, Ng&agrave;y Tr&aacute;i đất được tổ chức thường ni&ecirc;n v&agrave;o 22/4, trong đ&oacute; sinh vi&ecirc;n c&aacute;c trường đại học khắp nước Mỹ tụ họp v&agrave; tăng cường hiểu biết về sự suy tho&aacute;i của m&ocirc;i trường. Sự kiện n&agrave;y cũng diễn ra tại một số nước kh&aacute;c.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd1.jpg" border="0" alt="" width="460" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&agrave;ng trai n&agrave;y giả l&agrave;m con ong ngửi hoa mộc lan gần đại học Pace, New York, Mỹ. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd2.jpg" border="0" alt="" width="460" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&agrave;ng ngh&igrave;n học sinh tiểu học v&agrave; trung học cũng tham gia Ng&agrave;y Tr&aacute;i đất năm 1970. Trong ảnh, c&aacute;c nữ sinh New York qu&eacute;t dọn gần đ&agrave;i tưởng niệm ở c&ocirc;ng vi&ecirc;n Union của th&agrave;nh phố. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd3.jpg" border="0" alt="" width="460" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Quả cầu m&ocirc; phỏng tr&aacute;i đất c&oacute; chiều cao bằng một t&ograve;a nh&agrave; 5 tầng được dựng ở Washington để kỷ niệm 25 năm Ng&agrave;y Tr&aacute;i đất năm 1995. Ảnh: AFP/Getty.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd7.jpg" border="0" alt="" width="460" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mỹ ph&aacute;t động chiến dịch thu dọn r&aacute;c thải g&acirc;y độc trong gia đ&igrave;nh nh&acirc;n Ng&agrave;y Tr&aacute;i đất năm 1990. Ảnh:&nbsp;<em>Corbis.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd4.jpg" border="0" alt="" width="460" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tr&igrave;nh diễn trang phục l&agrave;m từ vỏ hộp ở Seoul năm 1996. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd5.jpg" border="0" alt="" width="460" height="312" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Paul McCartney v&agrave; Ringo Starr, hai th&agrave;nh vi&ecirc;n ban nhạc huyền thoại The Beatles, h&aacute;t trong chương tr&igrave;nh &acirc;m nhạc nh&acirc;n Ng&agrave;y Tr&aacute;i đất tại Los Angeles năm 1993. Ảnh:&nbsp;<em>Getty.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ntd6.jpg" border="0" alt="" width="460" height="328" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&eacute; g&aacute;i Nepal trong trang phục m&ocirc; phỏng tr&aacute;i đất tại Katmandu năm 2002. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 35, 8, 1634, 0, 1),
(613, 'Ảnh đẹp động vật trong tuần', 'Ếch ngã bổ nhào trên tàu lá. Cá voi quẫy đuôi tung mặt nước bên cạnh nữ huấn luyện viên. Dưới đây là ảnh đẹp về thế giới động vật tuần qua.', 'ech.jpg', NULL, '<p class="Title">Ảnh đẹp động vật trong tuần</p>\r\n<p class="Lead">Ếch ng&atilde; bổ nh&agrave;o tr&ecirc;n t&agrave;u l&aacute;. C&aacute; voi quẫy đu&ocirc;i tung mặt nước b&ecirc;n cạnh nữ huấn luyện vi&ecirc;n. Dưới đ&acirc;y l&agrave; ảnh đẹp về thế giới động vật tuần qua.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ech.jpg" border="0" alt="" width="460" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một con ếch trượt tr&ecirc;n t&agrave;u l&aacute; tại vườn th&uacute; El Nispero, Panama. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="soc.jpg" border="0" alt="" width="360" height="378" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con s&oacute;c lộn ngược đầu xuống đất sau khi tấn c&ocirc;ng chiếc hộp gỗ đựng đồ cho chim ăn ở Ohio, Mỹ. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="chim.jpg" border="0" alt="" width="460" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đại b&agrave;ng biển bụng trắng tung m&igrave;nh khỏi mặt nước ở đảo Langkawi, Malaysia. Ảnh:<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ca-heo.jpg" border="0" alt="" width="460" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute; heo biểu diễn b&oacute;ng nước ở Nuremberg, Đức. Ảnh:<em>&nbsp;AFP/GETTY.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ca.jpg" border="0" alt="" width="460" height="301" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nữ huấn luyện vi&ecirc;n đang bơi c&ugrave;ng con c&aacute; voi mang t&ecirc;n Lovey trong chương tr&igrave;nh biểu diễn tại c&ocirc;ng vi&ecirc;n Kamogawa ở Nhật. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tho.jpg" border="0" alt="" width="460" height="307" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Con thỏ rừng n&acirc;u th&egrave; lưỡi khi chạy nhảy tr&ecirc;n đồng cỏ ở Slimbridge, Anh. Ảnh:&nbsp;<em>J.S.Lees/WWT</em><strong>.</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="khi.jpg" border="0" alt="" width="360" height="466" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khỉ con n&eacute;p m&igrave;nh trong l&ograve;ng mẹ trong c&ocirc;ng vi&ecirc;n Kilim Karts Geoforest ở Malaysia. Ảnh:&nbsp;<em>AP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="kangaroo.jpg" border="0" alt="" width="460" height="352" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ch&uacute; kangaroo con t&ecirc;n l&agrave; Tijana đang được nh&acirc;n vi&ecirc;n vườn th&uacute; cho b&uacute; b&igrave;nh ở Belgrade, Serbia. Mẹ của Tijana đ&atilde; bỏ chạy v&agrave; văng cả con ra khỏi t&uacute;i v&igrave; sợ đ&agrave; điểu. Ảnh:&nbsp;<em>Reuters.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><em> </em></p>\r\n<p class="Normal" style="text-align: right;"><strong>Ngọc Sơn</strong></p>', 35, 8, 1475, 0, 1),
(644, 'Bác sỹ Trung Quốc nuôi cấy tai trên tay bệnh nhân', 'Chiếc tai nhân tạo được nuôi cấy trên cánh tay và có thể khâu nối vào đầu bệnh nhân trong những tháng tới. ', '2-1478742658_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Chiếc tai nhân tạo được nuôi cấy trên cánh tay và có thể khâu nối vào đầu bệnh nhân trong những tháng tới. </h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="bac-sy-trung-quoc-nuoi-cay-tai-tren-tay-benh-nhan" data-natural-width="500" src="1-1740-1478742806.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Bác sỹ Trung Quốc vừa nuôi cấy tai người trên tay bệnh nhân. Ảnh: China Daily.</p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Bác sỹ Trung Quốc Guo Shuzhong vừa hoàn thành việc nuôi cấy tai người trên cánh tay bệnh nhân, <em>RT</em> hôm qua đưa tin.</p><p class="Normal">\r\n	Bệnh nhân tên Ji bị tai nạn cách đây một năm. Sau khi điều trị, phần da mặt và má của ông đã hồi phục nhưng chiếc tai phải bị đứt rời luôn khiến ông Ji không vui.</p><p class="Normal">\r\n	"Tôi mất một chiếc tai. Vì thế, tôi luôn cảm thấy cơ thể mình không hoàn thiện", ông Ji trả lời <em>Huanqiu</em>.</p><p class="Normal">\r\n	Ông được giới thiệu tới bác sỹ Guo, người thực hiện ca ghép mặt đầu tiên ở Trung Quốc năm 2006. Vị bác sỹ phẫu thuật nổi tiếng đã nghĩ ra cách để nuôi cấy tai trên cánh tay bệnh nhân.</p><p class="Normal">\r\n	Đầu tiên một túi giãn da được cấy ghép dưới cánh tay phải của ông Ji. Sau đó, bác sỹ lấy sụn từ xương sườn bệnh nhân để làm tai nhân tạo rồi cấy ghép vào cẳng tay. </p><p class="Normal">\r\n	"Đây là phần khó nhất trong ca phẫu thuật bởi nhiều biến chứng có thể xảy ra", bác sỹ Guo cho biết.</p><p class="Normal">\r\n	Bước cuối cùng của ca cấy ghép sẽ được thực hiện trong những tháng tới. Chiếc tai sau khi phát triển hoàn toàn sẽ được tách khỏi cánh tay và gắn vào đầu bệnh nhân bằng kỹ thuật khâu nối mạch máu.</p><p class="Normal">\r\n	Các bác sỹ sẽ mất ít nhất 4 tháng để xác định chiếc tai mới của ông Ji có phát triển hay không. Nếu thành công, cẳng tay có thể trở thành vật chủ mới để nuôi cấy tất cả bộ phận của con người.</p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong>Hiền Anh</strong></p>                                                </div>', 36, 8, 3, 0, 1),
(646, 'NASA chế tạo cánh mới giúp máy bay chuyển hướng nhẹ như chim', 'Cánh máy bay mới bao gồm 8 bộ phận có độ cứng khác nhau, nhờ vậy nó có thể dễ dàng uốn vặn, thay đổi hình dạng. ', '1-1478491113_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Cánh máy bay mới bao gồm 8 bộ phận có độ cứng khác nhau, nhờ vậy nó có thể dễ dàng uốn vặn, thay đổi hình dạng. </h3> <div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="nasa-che-tao-canh-moi-giup-may-bay-chuyen-huong-nhe-nhu-chim" data-natural-width="500" src="1-7203-1478491326.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Loại cánh máy bay mới có thể uốn vặn và "biến hình". Ảnh: <em>NASA/ Kenneth Cheung.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Các kỹ sư ở Viện Công nghệ Massachusetts và Cơ quan Hàng không Vũ trụ Mỹ (NASA) đang chế tạo loại cánh máy bay mới gồm 8 bộ phận nhỏ làm từ sợi nano giúp nó có thể uốn cong và "biến hình", <em>Nature World News</em> hôm 5/11 đưa tin.</p><p class="Normal">\r\n	Các loại máy bay hiện nay đều phụ thuộc vào các hệ thống cánh dọc, cánh ngang to lớn, kềnh càng để có thể chuyển hướng bay hoặc tăng hạ độ cao, trong khi loài chim có thể làm như vậy bằng một cú đập cánh rất nhẹ nhàng.</p><p class="Normal">\r\n	Cánh máy bay mới có thể giúp máy bay chuyển hướng, tăng giảm độ cao như loài chim bằng cách thay đổi hình dạng của cánh, thay vì sử dụng các loại cánh phụ. Loại cánh này được bọc trong lớp vỏ dẻo màu cam với các mảnh ghép chồng lên nhau, giống như một bộ lông hoặc vảy. Điều này khiến cấu trúc của nó trở nên mềm dẻo và bề mặt trơn nhẵn hơn. </p><p class="Normal">\r\n	Lớp vỏ cánh máy bay được cấu tạo từ màng polymide có độ dày 0,127 mm. Trong đó, mỗi bộ phận có độ cứng khác nhau, nhờ vậy phần cánh trở nên dễ dàng uốn vặn. Hai mô tơ nhỏ có khả năng vặn toàn bộ cánh, điều chỉnh cách bay của nó khi đang di chuyển trên bầu trời. </p><div style="text-align:center;">\r\n	<div style="text-align:center;">\r\n		<div id="video-105093" data-component="true" data-component-type="video" data-component-value="105093" data-component-typevideo="2"></div></div>\r\n</div><p class="Normal">\r\n	Kỹ thuật mới này sẽ được lắp trên máy bay tự động và máy bay không người lái loại nhỏ. Nếu thành công, hệ thống này có thể khiến các chuyến bay trở nên yên tĩnh, êm ái hơn và nâng cao hiệu suất hoạt động. Ngoài ra, thiết kế mới cũng giúp giảm nhẹ quá trình sản xuất và giảm tiêu thụ năng lượng. Các nhà nghiên cứu hy vọng trong tương lai sẽ có những chuyến bay sử dụng đôi cánh có thể vỗ giống như cánh chim.</p><p class="Normal">"Công nghệ này sẽ thay đổi hoàn toàn cấu trúc của máy bay", Kenny Cheung, trưởng nhóm dự án của NASA cho biết.</p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong>Hiền Anh</strong></p>                                                </div>', 36, 8, 3, 0, 1),
(647, 'Dùng kim cương làm ''thẻ nhớ vĩnh cửu''', 'Các nhà khoa học Mỹ đã tìm ra cách lưu trữ vĩnh cửu lượng thông tin tương đương hàng trăm đĩa DVD trong một viên kim cương nhỏ cỡ hạt gạo và mỏng hơn tờ giấy.', '2016092214745069014241660Diamond-1478325736_180x108.jpeg', NULL, '<h3 class="short_intro txt_666">Các nhà khoa học Mỹ đã tìm ra cách lưu trữ vĩnh cửu lượng thông tin tương đương hàng trăm đĩa DVD trong một viên kim cương nhỏ cỡ hạt gạo và mỏng hơn tờ giấy.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="luu-tru-thong-tin-vinh-cuu-trong-kim-cuong" data-natural-width="500" src="unnamed-4651-1478325905.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Ảnh hai nhà vật lý Einstein và Schoerdinger được mã hóa và lưu trữ lên kim cương. Ảnh: <em>Carlos A. Meriles/Siddharth Dhomkar</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Đây là công nghệ lưu trữ dữ liệu dựa trên các khiếm khuyết trong tinh thể kim cương, theo <em>New York Times.</em> Một vài nguyên tử ni-tơ có thể chen vào giữa cấu trúc mạng tinh thể carbon của kim cương. Phá hủy một nguyên tử carbon ở cạnh ni-tơ sẽ tạo ra một khoảng trống có thể lưu trữ dữ liệu.</p><p class="Normal">\r\n	<span>Từ ý tưởng này, một nhóm các nhà vật lý thuộc trường Đại học New York, Mỹ đã sử dụng laser để mã hóa và đọc dữ liệu trong các khoảng trống nhỏ này. Chúng có đặc tính giống như nam châm – kéo hoặc đẩy electron khi được chiếu laser. </span></p><p class="Normal">\r\n	Khi cần ghi dữ liệu, họ sử dụng laser xanh để thêm electron vào các khoảng trống và laser đỏ để lấy electron ra, tương đương với hai giá trị nhị phân 0 và 1. Quá trình đọc dữ liệu cũng tương tự như máy tính đọc số nhị phân, nhưng ở đây là ánh sáng biểu thị sự có mặt hoặc vắng mặt của electron.</p><p class="Normal">\r\n	"Khác với đĩa DVD chỉ có thể ghi dữ liệu lên một mặt phẳng 2D, kỹ thuật sử dụng kim cương này có thể lưu trữ dữ liệu trên nhiều mặt phẳng, giống như một chồng đĩa DVD", Jacob Henshaw, một thành viên của nhóm nghiên cứu cho biết.</p><p class="Normal">\r\n	Hiện một viên kim cương có kích thước hạt gạo có thể lưu trữ lượng thông tin tương đương hàng trăm đĩa DVD. Trong tương lai các nhà vật lý có thể tăng dung lượng này lên tương đương hàng triệu đĩa DVD hoặc hơn nữa.</p><p class="Normal">\r\n	Ngoài ra, dữ liệu lưu trữ theo phương pháp này có thể tồn tại vĩnh cửu. Với ổ cứng từ tính hiện nay, mỗi lần truy cập và ghi dữ liệu mới, độ bền của nó sẽ giảm dần và hỏng hoàn toàn sau 5 – 10 năm.</p><p class="Normal">\r\n	Các khiếm khuyết trong mạng tinh thể kim cương không thay đổi theo thời gian, nghĩa là dữ liệu lưu trữ trong đó được bảo quản vĩnh viễn.</p><p class="Normal">\r\n	"Bạn không có cách nào để thay đổi nó. Dữ liệu sẽ ở đó mãi mãi", Siddharth Dhomkar, tác giả chính của nghiên cứu cho biết.</p><p class="Normal">\r\n	Tuy nhiên các chuyên gia kỳ cựu trong lĩnh vực lưu trữ dữ liệu như Jon Toigo, giám đốc diều hành của công ty Toigo Partners International vẫn tỏ ra nghi ngờ tính khả thi của công nghệ này. Ông cho rằng chỉ có những người làm việc trong phòng thí nghiệm mới quen thuộc với công nghệ lưu trữ mới, và kim cương thì luôn có giá rất cao, ngay cả với kim cương không hoàn hảo.</p><p class="Normal">\r\n	"Có lẽ phải mất 10 năm để công nghệ này được sử dụng rộng rãi", ông cho biết.</p><p class="Normal">\r\n	Theo các nhà nghiên cứu, kim cương nhân tạo có giá 150 USD là thứ rẻ nhất trong thí nghiệm của họ.</p><p class="Normal">\r\n	"Viên kim cương càng lớn sẽ có càng nhiều khiếm khuyết tinh thể để lưu trữ thông tin", Henshaw nói.</p><p class="Normal">\r\n	Ngoài ra nhóm nghiên cứu cũng cho biết, ý tưởng lưu trữ dữ liệu này còn có thể sử dụng trên bất kỳ vật liệu nào có khiếm khuyết tinh thể tương tự kim cương. <span>Nghiên cứu đã được đăng tải trên tạp chí Science Advances hôm 26/10.</span></p><p class="Normal" style="text-align:right;">\r\n	<strong><span>Nguyễn Thành Minh</span></strong></p>                                                </div>\r\n                                                    <div id="sp_lien_quan"></div>', 36, 8, 2456, 0, 1),
(648, 'Lớp mạ mới ngăn vi khuẩn lây nhiễm trên thiết bị y tế', 'Lớp mạ SLIPS với độ bám dính rất thấp có thể ngăn vi khuẩn lây nhiễm và phát triển trên các thiết bị y tế.', '1-1478251971_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Lớp mạ SLIPS với độ bám dính rất thấp có thể ngăn vi khuẩn lây nhiễm và phát triển trên các thiết bị y tế.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="lop-ma-moi-ngan-vi-khun-lay-nhiem-tren-thiet-bi-y-te" data-natural-width="500" src="1-4052-1478252156.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Lớp mạ SLIPS có thể ngăn vi khuẩn lây nhiễm và bám dính trên thiết bị y tế. Ảnh: <em>Flickr/NIAID.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Joanna Aizenberg, giảng viên tại Viện nghiên cứu Wyss thuộc Đại học Harvard, Mỹ, chế tạo thành công lớp mạ SLIPS với độ bám dính rất thấp, có thể ngăn vi khuẩn phát triển trên dụng cụ y tế và các thiết bị khác, <em>UPI </em>hôm 1/11 đưa tin.</p><p class="Normal">\r\n	"Việc lây nhiễm bệnh qua dụng cụ là vấn đề lớn trong y tế, tạo gánh nặng cho xã hội với chi phí hàng triệu USD để chăm sóc sức khỏe. Thuốc kháng sinh không thể giải quyết vấn đề này. Chúng ta cần sử dụng phương pháp mới để giảm nguy cơ lây nhiễm. Sản phẩm mới này chính là một bước tiến quan trọng trong quá trình nghiên cứu", tiến sỹ Elliot Chaikof, bác sỹ phẫu thuật chính tại Trung tâm y tế Beth Israel Deaconess, Boston, Mỹ, cho biết.</p><p class="Normal">\r\n	Lớp mạ SLIPS có thể loại bỏ nhiều tạp chất và sử dụng trong nhiều điều kiện môi trường. Kết quả thử nghiệm cho thấy nó có thể giảm sự bám dính của vi khuẩn với tỷ lệ hơn 98%.</p><p class="Normal">\r\n	"Chúng tôi đang phát triển SLIPS cho nhiều ứng dụng y học bằng cách thử nghiệm nó trên nhiều vật liệu y tế khác nhau, đảm bảo độ bền vững của lớp mạ và kết hợp đặc tính không nhiễm bẩn của vật liệu SLIPS vào các điều kiện môi trường cụ thể. Ở đây, chúng tôi đã áp dụng SLIPS rất hiệu quả vào chất bôi trơn y tế, cho thấy tiềm năng lớn của nó trong việc tích hợp vào các thiết bị dễ bị vi khuẩn tích tụ và lây nhiễm ", Aizenberg nói.</p><p class="Normal">\r\n	Nhóm nghiên cứu cũng đã thử nghiệm thực tế bằng cách mạ SLIPS vào lưới y tế. Một con chuột được cấy ghép lớp lưới này rồi tiêm tụ cầu vàng, nguyên nhân phổ biến gây ra nhiễm khuẩn trong các loại tụ cầu. Sau ba ngày, kết quả cho thấy có rất ít dấu hiệu nhiễm khuẩn ở lưới, tỷ lệ kiểm soát lây nhiễm là hơn 90%. </p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong>Hiền Anh</strong></p>                                                </div>', 36, 8, 1756, 0, 1),
(649, 'Công nghệ biến cải bó xôi thành máy dò bom ngăn khủng bố', 'Hãng BMW giới thiệu kiểu xe máy tương lai với hệ thống tự cân bằng giúp loại bỏ nhu cầu dùng đồ bảo hiểm cho người lái.', '1-1477984517_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Cải bó xôi được cấy ghép ống nano cacbon có thể phản ứng với chất nitro-aromatics, giúp phát hiện mìn và đạn dược chôn dưới đất.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="cong-nghe-bien-cai-bo-xoi-thanh-may-do-bom-ngan-khung-bo" data-natural-width="500" src="1-8806-1477984752.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Sau khi cấy ghép ống nano cacbon, lá cải bó xôi có thể phát hiện bom đạn chôn dưới đất. Ảnh: <em>Christine Daniloff.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Giáo sư Michael Strano, đến từ Viện Công nghệ Massachusetts, Mỹ và các đồng nghiệp cho biết cây cải bó xôi sau khi hấp thụ ống nano cacbon có thể phát hiện hóa chất nitro-aromatics trong mìn và đạn dược chôn dưới đất, theo<em> BBC</em>. Nghiên cứu này được công bố trên tạp chí Nature Material hôm 31/10.</p><p class="Normal">\r\n	Phòng nghiên cứu của giáo sư Strano trước đó chế tạo các ống nano cacbon có thể sử dụng như một bộ cảm biến nhằm phát hiện chất hydrogen heroxide (H2O2), TNT và khí độc sarin. Khi phân tử của các chất này kết hợp với vật liệu polymer bọc xung quanh ống nano cacbon, nó làm thay đổi cách ống phát sáng.</p><p class="Normal">\r\n	Các nhà khoa học sau đó cấy ghép các hạt nano và ống nano cacbon vào lá cây cải bó xôi. Cây cải mất 10 phút để hấp thụ nước vào lá.</p><p class="Normal">\r\n	Để đọc được tín hiệu, nhóm nghiên cứu chiếu tia laser vào lá, khiến các ống nano phát ra ánh sáng huỳnh quang cận hồng ngoại. Ánh sáng này có thể phát hiện bằng máy chụp hồng ngoại nhỏ kết nối với máy tính hoặc một chiếc điện thoại thông minh loại bỏ bộ lọc tia hồng ngoại.</p><p class="Normal">\r\n	"Với công nghệ này, cây cải có thể giám sát nước ngầm rò rỉ từ đạn dược chôn dưới lòng đất hoặc chất thải chứa nitro-aromatics. Chúng có thể sử dụng trong các chương trình phòng thủ và giám sát khu vực công cộng để phát hiện các hoạt động khủng bố", giáo sư Strano nói.</p><p class="Normal">\r\n	Công nghệ hiện nay cho phép các nhà khoa học thu nhận dấu hiệu trong khoảng cách 1 m từ vị trí của cây cỏ. Họ đang nghiên cứu để mở rộng khoảng cách này.</p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong>Hiền Anh</strong></p>                                                </div>', 36, 8, 2125, 0, 1),
(650, 'Công nghệ chữa trị hiện tượng ''đau chi ma''', 'Nhờ công nghệ máy tính tương tác với não, các bệnh nhân khuyết tật sẽ không còn phải chịu đựng cơn đau ở phần chi bị liệt hoặc đã mất.', '2-1477972662_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Nhờ công nghệ máy tính tương tác với não, các bệnh nhân khuyết tật sẽ không còn phải chịu đựng cơn đau ở phần chi bị liệt hoặc đã mất.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="cong-nghe-chua-tri-hien-tuong-dau-chi-ma" data-natural-width="500" src="1-9866-1477974091.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Một số người khuyết tật vẫn cảm thấy cơn đau ở chi đã mất. Ảnh minh họa: <em>Painresource</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	<span>Các nhà khoa học Nhật Bản tìm ra cách điều trị chứng "đau chi ma" ở người khuyết tật, bằng cách sử dụng công nghệ sử dụng máy tính dùng để điều khiển các hoạt động não bộ, theo </span><em>Live Science</em><span>. </span><span>Nghiên cứu được đăng trên tạp chí Nature Communications hôm 27/10.</span></p><p class="Normal">\r\n	Hiện tượng "đau chi ma" là khi một người cảm thấy đau tay hoặc chân, dù tay chân đó đã mất hoặc bị liệt. "Họ vẫn cảm nhận được những đau đớn như bị bỏng hay đau do mẫn cảm mà các loại thuốc giảm đau thông thường không có tác dụng", tiến sĩ Ben Seymour, một nhà khoa học thần kinh thuộc Đại học Cambridge, Anh, đồng tác giả báo cáo cho biết.</p><p class="Normal">\r\n	Thông thường, não bộ điều khiển cử động các phần trên cơ thể, tiến sĩ <span>giải phẫu thần kinh </span><span>Takufumi Yanagisawa, Đại học Osaka, Nhật Bản, tác giả chính của nghiên cứu, cho hay.</span></p><p class="Normal">\r\n	Khi cử động xảy ra, não bộ sẽ tiếp nhận các thông tin phản hồi từ các giác quan để kiểm tra cử động đó có theo ý muốn của con người hay không. Đó không chỉ là các thông tin từ mắt (nhìn thấy phần cơ thể chuyển động) mà còn là thông tin xúc giác và cảm giác trong không gian từ chính phần cơ thể đó, được gửi từ cơ bắp tới não bộ.</p><p class="Normal">\r\n	Tuy nhiên, nếu phần cơ thể muốn cử động bị liệt hoặc không còn, não bộ sẽ không nhận được các thông tin phản hồi đó. Sự trái ngược giữa ý định cử động và kết quả thực tế, được cho là nguồn gốc của các cơn đau này, theo Yanagisawa.</p><p class="Normal">\r\n	<span>Trước đây, khi sử dụng công nghệ tương tác bằng máy tính với não bộ, các nhà nghiên cứu nghĩ rằng chỉ cần chuyển dịch thông tin não bộ thành cử động của các bộ phận giả là cơn đau sẽ biến mất. </span></p><p class="Normal">\r\n	<span>Tuy nhiên, bệnh nhân được điều trị theo cách này cho biết họ còn cảm thấy đau hơn, theo Yanagisawa. Nguyên nhân là do không loại bỏ được sự trái ngược giữa ý định cử động và tín hiệu phản hồi mà não bộ nhận được. Dù bệnh nhân nhìn thấy bộ phận giả chuyển động, họ vẫn không cảm nhận được nó hay có các thông tin phản hồi từ cơ bắp về vị trí của bộ phận giả trong không gian. </span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="cong-nghe-chua-tri-hien-tuong-dau-chi-ma-1" data-natural-width="500" src="unnamed-3194-1477913718.png"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Kết nối hệ thống tương tác với não bộ. Ảnh:<em> Đại học Osaka</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Nhóm nghiên cứu Nhật Bản đã thử cách tiếp cận khác trên cùng nhóm bệnh nhân. Họ yêu cầu bệnh nhân cố gắng di chuyển một cánh tay tưởng tượng từ phía đối diện với nơi cảm thấy đau. Ví dụ bệnh nhân có tay trái bị liệt hoặc đã mất, sẽ tưởng tượng có một cánh tay bên phải và cố gắng di chuyển nó. Tín hiệu hoạt động này của não sẽ được chương trình tương tác ghi lại và chuyển dịch thành cử động của bộ phận giả.</p><p class="Normal">\r\n	Với cách làm này, cơn đau của bệnh nhân đã thuyên giảm, dù chỉ là tạm thời. Do ra lệnh cho cánh tay tưởng tượng di chuyển, não bộ cũng sẽ chờ phản hồi từ cánh tay này chứ không phải từ tay thực.</p><p class="Normal">\r\n	Tuy nhiên, nhóm nghiên cứu cũng cho biết họ chưa thể áp dụng rộng rãi cách điều trị này và chi phí điều trị cũng rất cao.</p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong><span>Nguyễn Thành Minh</span></strong></p>                                                </div>\r\n                                                    ', 36, 8, 2199, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(651, 'Robot đoán trước chính xác phán quyết của quan tòa', 'Nhóm nghiên cứu của Anh phát triển thành công trí tuệ nhân tạo (AI) có thể dự đoán phán quyết của Tòa án Nhân quyền châu Âu với độ chính xác 79%.', '1-1477901909_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Nhóm nghiên cứu của Anh phát triển thành công trí tuệ nhân tạo (AI) có thể dự đoán phán quyết của Tòa án Nhân quyền châu Âu với độ chính xác 79%.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="robot-doan-truoc-chinh-xac-phan-quyet-cua-quan-toa" data-natural-width="500" src="1-2098-1477902138.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					AI có thể dự đoán chính xác 79% phán quyết của Tòa án Nhân quyền châu Âu. Ảnh: <em>Posteriori.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Nhóm nghiên cứu đến từ ba trường đại học của Anh thiết kế một thuật toán máy tính có thể dự đoán phán quyết của Tòa án Nhân quyền châu Âu (ECtHR) với độ chính xác là 79%, <em>IFL</em><span> </span><em>Science </em><span>dẫn</span><span> báo cáo công bố trên tạp chí Peerj Computer Science hôm 24/10 cho biết</span><span>.</span></p><p class="Normal">\r\n	Để tạo ra trí tuệ nhân tạo này, nhóm nghiên cứu đã huấn luyện một máy tính xác định mô hình chung từ 584 hồ sơ vụ án được xét xử theo điều 3, 6, 8 của Công ước châu Âu về Nhân quyền, đề cập đến quyền không bị tra tấn, xét xử công bằng và các quyền riêng tư tương ứng.</p><p class="Normal">\r\n	Bằng cách đối chiếu giữa các mô hình trong số hồ sơ này và kết quả xử án, cỗ máy sau đó có thể dự đoán chính xác quyết định của tòa án trong gần 80% trường hợp.</p><p class="Normal">\r\n	Thuật toán đưa ra kết quả đáng tin nhất khi phân tích tiểu mục "Hoàn cảnh", chứa đựng các chi tiết liên quan đến hành động và sự kiện xung quanh một trường hợp cụ thể. Ngược lại, tiểu mục "Luật" đề cập đến các lập luận pháp lý đưa ra bởi hai bên, là phần mà nó đưa ra tỷ lệ dự đoán chính xác thấp nhất.</p><p class="Normal">\r\n	Từ đó, nhóm nghiên cứu kết luận các thẩm phán của ECtHR là những người theo thuyết duy thực hơn là chủ nghĩa hình thức, nghĩa là quyết định của họ dựa trên các tình tiết thực tế nhiều hơn là khuôn khổ pháp lý. </p><p class="Normal">\r\n	Với thành công của nghiên cứu này, Nikolaos Aletras, đồng tác giả nghiên cứu, cho biết robot mang trí tuệ nhân tạo có thể trở thành công cụ quan trọng để xác định trường hợp nào nhiều khả năng vi phạm Công ước châu Âu về Nhân quyền. Công nghệ này có thể giúp tòa án xét xử hiệu quả hơn bằng cách xác định khả năng thắng, thua của các trường hợp, thậm chí trước khi họ ra tòa.</p><p class="Normal">\r\n	"Trí tuệ nhân tạo sẽ không thay thế thẩm phán và luật sư nhưng họ có thể thấy chúng hữu ích để xác định khuôn mẫu trong các trường hợp có cùng kết quả nhất định", Aletras giải thích.</p><p class="Normal">\r\n	AI đặc biệt hữu ích đối với các trường hợp khiếu nại phán quyết của ECtHR bởi nó sẽ cho luật sư và thẩm phán một nhận định tương đối về khả năng thành công của những kháng cáo này. Từ đó, các cơ quan luật pháp có thể đưa ra quyết định tốt hơn về việc có nên xét xử một vụ án hay không nhằm tiết kiệm thời gian.</p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong>Hiền Anh</strong></p>                                                </div>', 36, 8, 3, 0, 1),
(652, 'Nhật Bản sản xuất mưa sao băng nhân tạo', 'Công ty ALE, Nhật Bản sẽ sản xuất sao băng nhân tạo nhiều màu sắc và có độ sáng gấp 70 lần sao băng tự nhiên.', '3-1477877528_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Công ty ALE, Nhật Bản sẽ sản xuất sao băng nhân tạo nhiều màu sắc và có độ sáng gấp 70 lần sao băng tự nhiên.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="nhat-ban-san-xuat-mua-sao-bang-nhan-tao" data-natural-width="500" src="3-3242-1477880580.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Công ty ALE đang phát triển công nghệ sản xuất sao băng nhân tạo. Ảnh minh họa: <em>Sergey Balay.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	ALE, một công ty khởi nghiệp của Nhật Bản, đang phát triển công nghệ để tạo ra mưa sao băng nhân tạo theo yêu cầu, <em>CNN</em> hôm 24/10 đưa tin. Công nghệ này có thể được trình diễn trong tiết mục mở màn cho Olympic 2020 tổ chức ở Tokyo, Nhật Bản.</p><p class="Normal">\r\n	"Hãy tưởng tượng trong tương lai, bạn có thế sử dụng sao băng của chúng tôi để trình diễn pháo hoa quốc tế, cầu hôn hoặc cho lễ kỷ niệm đặc biệt. Chúng tôi muốn mọi người phải ngẩng đầu lên, thay vì cúi xuống mặt đất. Người dân Nhật Bản bận rộn mỗi ngày. Chúng ta cần nhiều sản phẩm văn hóa và khoa học trong cuộc sống hơn để giúp họ thư giãn và gần gũi với thiên nhiên", Shinsuke Abe, giáo sư kỹ thuật hàng không vũ trụ, Đại học Nihon, Nhật Bản, giám đốc nghiên cứu công ty ALE, chia sẻ.</p><p class="Normal">\r\n	Để sản xuất mưa sao băng nhân tạo, nhóm nghiên cứu phóng một vệ tinh vào quỹ đạo. Nó sẽ mất nhiều tháng để vào đúng vị trí ở độ cao khoảng 500 km trong thượng tầng khí quyển, nằm dưới lớp cao nhất của bầu khí quyển. </p><p class="Normal">\r\n	Mỗi vệ tinh sẽ chứa khoảng 300-500 sao băng nhân tạo của ALE và có khả năng quay theo quỹ đạo quanh Trái Đất trong bốn năm. Chúng sẽ thường xuyên đi qua các thành phố lớn vào buổi đêm, sẵn sàng tạo ra một trận mưa sao băng khi có yêu cầu.</p><p class="Normal">\r\n	Các buổi trình diễn đầu tiên sẽ có khoảng 12 sao băng nhân tạo vụt qua bầu trời. Những ngôi sao này sẽ khác và đẹp hơn sao băng thật.</p><p class="Normal">\r\n	"Nếu bạn thấy sao băng của chúng tôi, nó sẽ không bị nhầm lẫn với sao băng tự nhiên", Rie Yamamoto, giám đốc chiến lược toàn cầu của công ty ALE, trả lời <em>CNN</em>.</p><p class="Normal">\r\n	Với <span>tốc độ khoảng 8 km/s, s</span><span>ao băng của ALE sẽ di chuyển chậm hơn sao băng tự nhiên có vận tốc 72 km/s, do đó khán giả sẽ có nhiều thời gian để theo dõi buổi trình diễn.</span></p><p class="Normal">\r\n	<span>Sao băng thật, thực chất là các hòn đá nhỏ và hạt bụi có đường kính chỉ vài millimeter, sẽ bốc cháy khi đi qua khí quyển của Trái Đất và biến mất hoàn toàn trước khi chạm mặt đất. ALE cho biết sao băng nhân tạo sẽ trải qua quá trình tương tự.</span></p><table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="nhat-ban-san-xuat-mua-sao-bang-nhan-tao-1" data-natural-width="500" src="4-8186-1477880580.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Sao băng nhân tạo sẽ có nhiều màu sắc khác nhau và sáng hơn sao băng tự nhiên 70 lần. Ảnh minh họa: <em>Hendrik Schmidt.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	Điểm đặc biệt ở sao băng của ALE đó là nó phát sáng nhiều màu sắc khác nhau. Nhóm nghiên cứu đã chế tạo được sao băng màu cam, xanh da trời và xanh lá cây. Họ dự kiến sẽ tạo thêm nhiều màu sắc khác. Ngoài ra, sao băng nhân tạo sẽ có độ sáng gấp 70 lần sao băng tự nhiên.</p><p class="Normal">\r\n	<span>ALE cho biết, khách hàng tiềm năng của họ là nhà tổ chức của các trận đấu thể thao, lễ hội ngoài trời và các sự kiện quy mô lớn khác.</span></p><p class="Normal">\r\n	"Tất cả mọi người đều thích sao băng và mưa sao băng. Việc chiêm ngưỡng sao băng nhân tạo sẽ là trải nghiệm lần đầu tiên của họ", Yamamoto nhận xét.</p><p class="Normal"><p class="Normal" style="text-align:right;">\r\n	<strong>Hiền Anh</strong></p>                                                </div>', 36, 8, 1815, 0, 1),
(653, 'Trung Quốc chế tạo tàu đệm từ 600 km/h nhanh nhất thế giới', 'Một công ty Trung Quốc đang phát triển tàu đệm từ có thể chạy với tốc độ 600 km/h, nhanh hơn tất cả các mẫu tàu tương tự trên thế giới.', 'VNETrain-1477452573_180x108.jpg', NULL, '<h3 class="short_intro txt_666">Một công ty Trung Quốc đang phát triển tàu đệm từ có thể chạy với tốc độ 600 km/h, nhanh hơn tất cả các mẫu tàu tương tự trên thế giới.</h3><div id="left_calculator">\r\n                    	<div class="fck_detail width_common block_ads_connect">\r\n                        <table align="center" border="0" cellpadding="3" cellspacing="0" class="tplCaption"><tbody><tr><td>\r\n				<img alt="trung-quoc-che-tao-tau-dem-tu-600-km-h-nhanh-nhat-the-gioi" data-natural-width="500" src="VNE-Train-7714-1477452671.jpg"></td>\r\n		</tr><tr><td>\r\n				<p class="Image">\r\n					Trung Quốc có mạng lưới tàu tốc độ cao dài nhất thế giới. Ảnh: <em>Xinhua.</em></p>\r\n			</td>\r\n		</tr></tbody></table><p class="Normal">\r\n	CRRC Corp Ltd, nhà sản xuất thiết bị đường sắt lớn nhất Trung Quốc, cho biết công ty đang xây dựng một đường ray dài gần 5 km để thử nghiệm tàu đệm từ tốc độ 600 km/h, <em>China Daily</em> hôm 22/10 đưa tin. Mẫu tàu này có thể di chuyển từ Thượng Hải đến Bắc Kinh trong hai tiếng đồng hồ.</p><p class="Normal">\r\n	CRRC cũng đang chế tạo những mẫu tàu đạt tốc độ 200km/h với mục tiêu phát triển hệ thống công nghệ và tiêu chuẩn nội địa cho tàu đệm từ tốc độ cao và trung bình thế hệ mới, theo Sun Bangcheng, một lãnh đạo của CRRC.</p><p class="Normal">\r\n	Công ty còn lên kế hoạch sản xuất tàu tốc độ cao chạy xuyên biên giới với tốc độ trung bình 400 km/h, sử dụng nhiều khổ đường ray khác nhau từ 60 cm đến 1,676 m. Hồi tháng 5, CRRC đã vận hành thành công đường tàu đệm từ tự sản xuất đầu tiên ở tốc độ tối đa 100 km/h.</p><p class="Normal">\r\n	Đệm từ là cách gọi tắt của "đệm nâng từ trường" (magnetic levitation). Thay vì chạy bằng nhiên liệu hóa thạch, loại tàu này hoạt động nhờ nam châm và dòng điện nên lướt êm trên đường ray. CRRC cho biết công ty sẽ cần nâng cấp công nghệ đệm từ và độ bám đường cho loạt tàu mới.</p><p class="Normal">\r\n	Trung Quốc có mạng lưới đường tàu tốc độ cao dài nhất thế giới với tổng chiều dài hơn 20.000 km. Con số này có thể tăng lên 30.000 km vào năm 2020 và 45.000 km vào năm 2030.</p><p class="Normal"><p align="right" class="Normal">\r\n	<strong>Phương Hoa</strong></p>                                                </div>', 36, 8, 568, 0, 1),
(656, 'Châu Á đối mặt nguy cơ di cư ồ ạt', 'Hàng chục triệu người châu Á sẽ phải bỏ nhà bởi những tác động do các hiện tượng thời tiết khắc nghiệt gây nên, Ngân hàng Phát triển châu Á (ADB) cảnh báo.', 'flood2.jpg', NULL, '<h1 class="Title">Ch&acirc;u &Aacute; đối mặt nguy cơ di cư ồ ạt</h1>\r\n<h2 class="Lead">H&agrave;ng chục triệu người ch&acirc;u &Aacute; sẽ phải bỏ nh&agrave; bởi những t&aacute;c động do c&aacute;c hiện tượng thời tiết khắc nghiệt g&acirc;y n&ecirc;n, Ng&acirc;n h&agrave;ng Ph&aacute;t triển ch&acirc;u &Aacute; (ADB) cảnh b&aacute;o.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="flood2.jpg" alt="" width="450" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&acirc;n vi&ecirc;n cứu hộ v&agrave; người t&igrave;nh nguyện gi&uacute;p một cụ gi&agrave; chạy lũ tại tỉnh Davao del Sur, Philippines h&ocirc;m 18/1. Ảnh:&nbsp;<em>AP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>AFP</em>&nbsp;dẫn một dự thảo b&aacute;o c&aacute;o của ADB, mang t&ecirc;n "Biến đổi kh&iacute; hậu v&agrave; sự di cư ở ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương, cho hay, nếu c&aacute;c ch&iacute;nh phủ kh&ocirc;ng chuẩn bị cho việc quản l&yacute; hoạt động di cư ồ ạt ngay từ b&acirc;y giờ, ch&acirc;u &Aacute; sẽ đối mặt với h&agrave;ng loạt khủng hoảng nh&acirc;n đạo trong v&agrave;i thập kỷ tới. Người d&acirc;n c&oacute; thể di cư trong nước hoặc xuy&ecirc;n bi&ecirc;n giới.</p>\r\n<p class="Normal">B&aacute;o c&aacute;o cho rằng, hiện tại c&aacute;c ch&iacute;nh phủ tại ch&acirc;u &Aacute; đ&atilde; nhận thức được rằng những biến đổi thời tiết do t&igrave;nh trạng tr&aacute;i đất ấm l&ecirc;n g&acirc;y ra. Tuy nhi&ecirc;n, phần lớn ch&iacute;nh phủ chưa vạch ra những ch&iacute;nh s&aacute;ch v&agrave; cơ chế để ngăn chặn t&aacute;c động của biến đổi thời tiết đối với d&acirc;n số.</p>\r\n<p class="Normal">"R&otilde; r&agrave;ng ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương l&agrave; một trong những khu vực chịu t&aacute;c động mạnh nhất của biến đổi kh&iacute; hậu. Những t&aacute;c động n&agrave;y bao gồm sự gia tăng nhiệt độ, thay đổi lượng mưa, dịch chuyển m&ugrave;a mưa, sự gia tăng sức mạnh của c&aacute;c cơn b&atilde;o nhiệt đới, tần suất xuất hiện của lũ", b&aacute;o c&aacute;o nhấn mạnh.</p>\r\n<p class="Normal">B&aacute;o c&aacute;o của ADB được soạn thảo trong bối cảnh c&aacute;c trận lụt đang ho&agrave;nh h&agrave;nh ở nhiều nơi thuộc ch&acirc;u &Aacute; - Th&aacute;i B&igrave;nh Dương. Australia l&agrave; một trong những quốc gia hứng chịu lũ v&agrave; b&atilde;o trong v&agrave;i tuần gần đ&acirc;y. Năm ngo&aacute;i v&agrave;i triệu người phải sơ t&aacute;n v&igrave; thi&ecirc;n tai tại Philippines v&agrave; Sri Lanka trong năm 2010.</p>\r\n<p class="Normal">"Ch&acirc;u &Aacute; v&agrave; Th&aacute;i B&igrave;nh Dương dễ bị tổn thương bởi nguy cơ m&ocirc;i trường lớn v&agrave; mật độ d&acirc;n số cao. Do đ&oacute;, hiện tượng thay đổi d&acirc;n số tại ch&acirc;u lục n&agrave;y sẽ diễn ra với quy m&ocirc; lớn chưa từng thấy trong v&agrave;i thập kỷ tới", b&aacute;o c&aacute;o khẳng định.</p>\r\n<p class="Normal">Trung t&acirc;m Nghi&ecirc;n cứu Bệnh dịch (CRED) tại Bỉ th&ocirc;ng b&aacute;o 89% trong số 207 triệu người hứng chịu t&aacute;c động của thi&ecirc;n tai năm ngo&aacute;i sống tại ch&acirc;u &Aacute;.</p>\r\n<p class="Normal">"Nhiều ch&iacute;nh phủ chưa sẵn s&agrave;ng đối ph&oacute; với những nguy cơ do hiện tượng di cư ồ ạt g&acirc;y n&ecirc;n. Đ&oacute; l&agrave; l&yacute; do ADB soạn thảo b&aacute;o c&aacute;o n&agrave;y. Ch&uacute;ng ta chưa c&oacute; cơ chế phối hợp quốc tế để kiểm so&aacute;t hoạt động di cư do biến đổi kh&iacute; hậu. C&aacute;c nh&agrave; hoạch định ch&iacute;nh s&aacute;ch n&ecirc;n h&agrave;nh động ngay b&acirc;y giờ", &ocirc;ng Bart Edes, một quan chức cấp cao của ADB, ph&aacute;t biểu.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 257, 0, 1),
(658, '2010 là năm nóng nhất', 'Tổ chức Khí tượng Thế giới (WMO) hôm qua thông báo 2010 là năm nóng nhất kể từ khi Liên Hợp Quốc theo dõi nhiệt độ trung bình thường niên.', 'fire.jpg', NULL, '<h1 class="Title">2010 l&agrave; năm n&oacute;ng nhất</h1>\n<h2 class="Lead">Tổ chức Kh&iacute; tượng Thế giới (WMO) h&ocirc;m qua th&ocirc;ng b&aacute;o 2010 l&agrave; năm n&oacute;ng nhất kể từ khi Li&ecirc;n Hợp Quốc theo d&otilde;i nhiệt độ trung b&igrave;nh thường ni&ecirc;n.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="fire.jpg" alt="Đợt nắng n&oacute;ng kỷ lục g&acirc;y n&ecirc;n v&agrave;i trăm vụ ch&aacute;y rừng tại Nga trong m&ugrave;a h&egrave; 2010. Ảnh:" width="450" height="293" /></td>\n</tr>\n<tr>\n<td class="Image">Đợt nắng n&oacute;ng kỷ lục g&acirc;y n&ecirc;n v&agrave;i trăm vụ ch&aacute;y rừng tại Nga trong m&ugrave;a h&egrave; 2010. Ảnh:<em>dangerousminds.net</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal"><em>AP</em>&nbsp;dẫn lời WMO cho hay, nhiệt độ trung b&igrave;nh năm ngo&aacute;i cao hơn 0,5 độ C so với giai đoạn 1961-1990. WMO đ&atilde; sử dụng dữ liệu của Cục Kh&iacute; tượng Anh v&agrave; Cơ quan H&agrave;ng kh&ocirc;ng vũ trụ Mỹ (NASA).</p>\n<p class="Normal">&ldquo;Dữ liệu nhiệt độ năm 2010 cho thấy tr&aacute;i đất đang trải qua thời kỳ ấm l&ecirc;n d&agrave;i hạn. 10 năm n&oacute;ng nhất kể từ khi WMO thống k&ecirc; nhiệt độ thường ni&ecirc;n đều xuất hiện từ năm 1998&rdquo;, Michel Jarraud, gi&aacute;m đốc WMO, ph&aacute;t biểu.</p>\n<p class="Normal">1998 v&agrave; 2005 l&agrave; hai năm c&oacute; nhiệt độ trung b&igrave;nh tương đương với năm ngo&aacute;i.</p>\n<p class="Normal">Theo WMO, những hiện tượng thời tiết khắc nghiệt như năm ngo&aacute;i &ndash; nắng n&oacute;ng kỷ lục tại Nga v&agrave; lụt tr&ecirc;n diện rộng tại Pakistan &ndash; sẽ tiếp tục xảy ra trong năm nay. Những trận lũ lớn tại Sri Lanka, Philippines, Brazil v&agrave; Australia l&agrave; những v&iacute; dụ ti&ecirc;u biểu.</p>\n<p class="Normal">Mạng lưới Lịch sử Kh&iacute; hậu To&agrave;n cầu th&ocirc;ng b&aacute;o 2010 cũng l&agrave; năm c&oacute; độ ẩm lớn nhất. Do lượng mưa v&agrave; tuyết tr&ecirc;n khắp thế giới kh&ocirc;ng giống nhau, giới khoa học cho rằng họ cần nghi&ecirc;n cứu để t&igrave;m ra nguy&ecirc;n nh&acirc;n khiến 2010 l&agrave; năm vừa n&oacute;ng nhất vừa ẩm ướt nhất.</p>\n<p class="Normal">WMO cũng ph&aacute;t hiện ra rằng xu hướng ấm l&ecirc;n đang thể hiện mạnh nhất ở ch&acirc;u Phi, một phần ch&acirc;u &Aacute;, v&agrave;i khu vực ở Canada v&agrave; đảo Greenland. Nhiệt độ c&oacute; xu hướng giảm r&otilde; rệt trong thời gian cuối năm, đặc biệt tại ph&iacute;a bắc ch&acirc;u &Acirc;u, một số v&ugrave;ng của Nga v&agrave; ph&iacute;a đ&ocirc;ng nước Mỹ.</p>\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 2549, 0, 1),
(660, 'Chàng sinh viên dân tộc Thái mong phủ xanh đồi trọc', 'Mong muốn trồng cây jatropha trên đầu nguồn sông suối, khu đất trống, Lò Văn Chỉnh (Điện Biên) hy vọng tăng thu nhập cho người dân, bảo vệ môi trường và ổn định nguồn nước cung cấp cho sản xuất nông nghiệp nơi hạ du.', 'Jatropha.gif', NULL, '<h1 class="Title">Ch&agrave;ng sinh vi&ecirc;n d&acirc;n tộc Th&aacute;i mong phủ xanh đồi trọc</h1>\r\n<h2 class="Lead">Mong muốn trồng c&acirc;y jatropha tr&ecirc;n đầu nguồn s&ocirc;ng suối, khu đất trống, L&ograve; Văn Chỉnh (Điện Bi&ecirc;n) hy vọng tăng thu nhập cho người d&acirc;n, bảo vệ m&ocirc;i trường v&agrave; ổn định nguồn nước cung cấp cho sản xuất n&ocirc;ng nghiệp nơi hạ du.<br /><span style="font-size: 10px; font-weight: normal;">Sinh ra ở bản Gi&oacute;i x&atilde; Lu&acirc;n Gi&oacute;i (Điện Bi&ecirc;n Đ&ocirc;ng, Điện Bi&ecirc;n), l&agrave; người d&acirc;n tộc Th&aacute;i, n&oacute;i tiếng Kinh c&ograve;n chưa s&otilde;i, nhưng Chỉnh đ&atilde; lọt v&agrave;o tốp 15 đề &aacute;n ở v&ograve;ng chung kết cuộc thi &yacute; tưởng xanh 2009.</span></h2>\r\n<p class="Normal">Năm nay, từ những ng&agrave;y đầu ph&aacute;t động cuộc thi "&yacute; tưởng xanh 2010", Chỉnh đ&atilde; gửi dự &aacute;n &ldquo;Jatropha trồng đầu nguồn - tiềm năng mới&rdquo; để dự thi. Jatropha l&agrave; loại c&acirc;y được coi l&agrave; nguồn nhi&ecirc;n liệu sinh học ưa chuộng nhất hiện nay.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="Jatropha.gif" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&acirc;y Jatropha.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">L&agrave; sinh vi&ecirc;n K47 (ĐH L&acirc;m sinh, ĐH T&acirc;y Bắc), Chỉnh lu&ocirc;n đau đ&aacute;u trước thực trạng rừng qu&ecirc; m&igrave;nh. Ở v&ugrave;ng đầu nguồn, nơi được coi l&agrave; v&ugrave;ng xung yếu ph&ograve;ng hộ v&ugrave;ng hạ nguồn th&igrave; độ che phủ rừng c&ograve;n thấp. Đ&acirc;y lại l&agrave; địa b&agrave;n sinh sống của hơn 25 triệu d&acirc;n tộc thiểu số, đời sống của họ phụ thuộc ho&agrave;n to&agrave;n v&agrave;o rừng, canh t&aacute;c nương rẫy v&agrave; sản xuất l&uacute;a nước ven khe suối.</p>\r\n<p class="Normal">C&ocirc;ng t&aacute;c trồng rừng ở miền n&uacute;i chủ yếu l&agrave; trồng xung quanh &ldquo;rừng thi&ecirc;ng&rdquo;, quản l&yacute; v&agrave; x&uacute;c tiến t&aacute;i sinh tại cộng đồng. Điều đ&oacute; đ&atilde; v&agrave; đang v&ocirc; t&igrave;nh đẩy người d&acirc;n đi canh t&aacute;c nơi xa hơn, đến đầu nguồn c&aacute;c con s&ocirc;ng, con suối, g&acirc;y ra hiện tượng lũ lụt về m&ugrave;a mưa, kh&ocirc; hạn về m&ugrave;a h&egrave;.</p>\r\n<p class="Normal">Từ thực trạng tr&ecirc;n, Chỉnh đ&atilde; l&ecirc;n phương &aacute;n trồng Jatropha tr&ecirc;n đầu nguồn c&aacute;c khe suối, khu đất trống, khu vực canh t&aacute;c nương rẫy của cộng đồng nhằm tăng thu nhập cho cư d&acirc;n đầu nguồn, bảo vệ m&ocirc;i trường sinh th&aacute;i v&agrave; ổn định nguồn nước cung cấp cho sản xuất n&ocirc;ng nghiệp nơi hạ nguồn. "Khi dự &aacute;n được triển khai sẽ đem lại lợi &iacute;ch lớn cho tất cả d&acirc;n tộc sống gần rừng v&agrave; phụ thuộc v&agrave;o rừng", Chỉnh khẳng định.</p>\r\n<p class="Normal">"T&ocirc;i tin chắc dự &aacute;n của t&ocirc;i sẽ g&oacute;p phần hạn chế được hiện tượng ph&aacute; rừng đầu nguồn như hiện nay, n&acirc;ng cao chất lượng cuộc sống của đồng b&agrave;o sống tr&ecirc;n đầu nguồn từ c&acirc;y jatropha đem lại, đồng thời điều h&ograve;a nguồn nước cho sản xuất, sinh hoạt, thủy điện nơi hạ nguồn v&agrave; bảo vệ m&ocirc;i trường sinh th&aacute;i", Chỉnh tự tin n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%" bgcolor="#cfcfcf">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class="Normal">Cuộc thi "&Yacute; tưởng xanh 2010" l&agrave; một trong những hoạt động nằm trong chương tr&igrave;nh Go green &ndash; h&agrave;nh tr&igrave;nh xanh tổng thể, do C&ocirc;ng ty &ocirc;t&ocirc; Toyota Việt Nam (TMV) phối hợp với Bộ GD&amp;ĐT, Tổng cục M&ocirc;i trường v&agrave; Văn ph&ograve;ng Tiết kiệm năng lượng (Bộ C&ocirc;ng thương) tổ chức.</p>\r\n<p class="Normal">Cuộc thi được ph&aacute;t động v&agrave;o ng&agrave;y 28/8 với chủ đề sử dụng năng lượng bền vững. Hạn ch&oacute;t nhận b&agrave;i l&agrave; ng&agrave;y 28/1/2011. V&ograve;ng 1 sẽ chấm c&aacute;c b&agrave;i thi gửi về tr&ecirc;n giấy v&agrave; chọn ra tối đa 20 &yacute; tưởng xuất sắc. V&ograve;ng 2 chấm c&aacute;c dự &aacute;n qua thuyết tr&igrave;nh trực tiếp v&agrave; chọn ra c&aacute;c &yacute; tưởng đoạt giải.</p>\r\n<p class="Normal">C&aacute;c b&agrave;i dự thi cần phải được đ&aacute;p ứng đầy đủ 7 ti&ecirc;u ch&iacute;: t&iacute;nh s&aacute;ng tạo v&agrave; cơ sở khoa học; t&iacute;nh thực tiễn v&agrave; khả thi; t&iacute;nh an to&agrave;n cho m&ocirc;i trường; khả năng<sup>&nbsp;</sup>phổ biến; khả năng duy tr&igrave;; t&iacute;nh ph&aacute;p l&yacute; v&agrave; h&igrave;nh thức thể hiện;<strong><em>&nbsp;</em></strong>v&agrave; được thể hiện dưới dạng một đề &aacute;n bằng tiếng Việt với văn phong mạch lạc, dễ hiểu.</p>\r\n<p class="Normal">B&agrave;i dự thi xin gửi theo đường bưu điện về địa chỉ: C&ocirc;ng ty TNHH Quảng c&aacute;o Sao H&ocirc;m, ph&ograve;ng 201, tầng 2, t&ograve;a nh&agrave; Sao Bắc, số 4 D&atilde; Tượng, H&agrave; Nội v&agrave; bản mềm gửi v&agrave;o h&ograve;m thư&nbsp;<a class="Normal" href="mailto:ytuongxanh2010@gogreen.com">ytuongxanh2010@gogreen.com</a><span style="text-decoration: underline;"><span style="color: #0000ff;">.vn.</span></span></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Kiều Trinh</strong></p>', 33, 8, 1111, 0, 1),
(662, 'Tòa nhà nổi trên nước', 'Một kiến trúc sư Nga thiết kế tòa nhà có khả năng nổi trên nước và chống chịu mọi thảm họa mang tính hủy diệt của môi trường.', 'nha0.jpg', NULL, '<h1 class="Title">T&ograve;a nh&agrave; nổi tr&ecirc;n nước</h1>\r\n<h2 class="Lead">Một kiến tr&uacute;c sư Nga thiết kế t&ograve;a nh&agrave; c&oacute; khả năng nổi tr&ecirc;n nước v&agrave; chống chịu mọi thảm họa mang t&iacute;nh hủy diệt của m&ocirc;i trường.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/54/3C/nha0.jpg" alt="Ng&ocirc;i nh&agrave; của tương lai" width="450" height="253" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image"><span style="color: #303030;">Alexander Remizov, một kiến tr&uacute;c sư người Nga, vừa c&ocirc;ng bố bản thiết kế n</span>g&ocirc;i nh&agrave; c&oacute; khả năng bảo vệ con người trước mọi loại thảm họa m&ocirc;i trường.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/54/3C/nha2.jpg" alt="" width="450" height="253" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Kiểu nh&agrave; n&agrave;y được gọi l&agrave; Ark. Người ta c&oacute; thể x&acirc;y dựng n&oacute; rất nhanh bằng c&aacute;ch dựng sẵn khung được l&agrave;m từ gỗ, th&eacute;p. M&aacute;i v&agrave; tường được l&agrave;m bằng nhựa chất lượng cao, chứ kh&ocirc;ng phải k&iacute;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/54/3C/nha3.jpg" alt="" width="450" height="253" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&ocirc;ng tr&igrave;nh c&oacute; thể chứa được 10 ngh&igrave;n người v&agrave; cho ph&eacute;p c&acirc;y cối mọc b&ecirc;n trong. Người ta c&oacute; thể dựng n&oacute; ở mọi địa h&igrave;nh, thậm ch&iacute; tr&ecirc;n b&atilde;i c&aacute;t.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/54/3C/nha1.jpg" alt="Nh&agrave;" width="450" height="253" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ark được thiết kế để c&oacute; thể nối tr&ecirc;n mặt nước v&agrave; chịu được những trận lụt lớn tương đương với thảm họa đại hồng thủy trong Kinh th&aacute;nh. Ngo&agrave;i ra n&oacute; c&ograve;n chịu được nhiều thảm họa mang t&iacute;nh hủy diệt kh&aacute;c.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/54/3C/nha4.jpg" alt="" width="450" height="253" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Khi tr&ocirc;i nổi tr&ecirc;n nước, một nửa t&ograve;a nh&agrave; sẽ ch&igrave;m xuống dưới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/54/3C/nha6.jpg" alt="" width="450" height="344" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">T&ograve;a nh&agrave; c&oacute; khả năng từ sản xuất điện từ nhiệt của nước, gi&oacute;, &aacute;nh s&aacute;ng mặt trời.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong>&nbsp;(Ảnh:&nbsp;<span style="color: #303030;"><em>Alexander Remizov</em>)</span></p>', 33, 8, 1237, 0, 1),
(663, 'Làng sinh thái Hà Tĩnh được hỗ trợ 800 triệu đồng', 'Dự án xây làng sinh thái tại thôn Tiên Chương, xã Tiên Điền (Nghi Xuân, Hà Tĩnh) được hỗ trợ hơn 800 triệu đồng nhằm giải quyết các vấn đề môi trường của địa phương. Lễ khởi động dự án đã diễn ra vào ngày 28/12.\r\n', 'lang.JPG', NULL, '<h1 class="Title">L&agrave;ng sinh th&aacute;i H&agrave; Tĩnh được hỗ trợ 800 triệu đồng</h1>\r\n<h2 class="Lead">Dự &aacute;n x&acirc;y l&agrave;ng sinh th&aacute;i tại th&ocirc;n Ti&ecirc;n Chương, x&atilde; Ti&ecirc;n Điền (Nghi Xu&acirc;n, H&agrave; Tĩnh) được hỗ trợ hơn 800 triệu đồng nhằm giải quyết c&aacute;c vấn đề m&ocirc;i trường của địa phương. Lễ khởi động dự &aacute;n đ&atilde; diễn ra v&agrave;o ng&agrave;y 28/12.</h2>\r\n<p class="Normal">Dự &aacute;n đ&atilde; gi&uacute;p n&acirc;ng cao năng lực v&agrave; nhận thức bảo vệ m&ocirc;i trường của cộng đồng d&acirc;n cư trong việc sử dụng bền vững t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n đất v&agrave; nước, tạo cảnh quan xanh - sạch - đẹp v&agrave; ph&aacute;t triển cộng đồng.</p>\r\n<p class="Normal">Dự &aacute;n cũng sẽ tập huấn về sử dụng nước sạch v&agrave; sức khỏe cộng đồng, bảo vệ m&ocirc;i trường trước nguy cơ biến đổi kh&iacute; hậu....; hỗ trợ c&aacute;c hộ d&acirc;n c&acirc;y giống (c&acirc;y ăn quả); x&acirc;y dựng một hố thu gom r&aacute;c cho to&agrave;n th&ocirc;n, xe vận chuyển thu gom r&aacute;c v&agrave; 60 th&ugrave;ng đựng r&aacute;c bằng nhựa c&oacute; nắp đậy; cải tạo nh&agrave; văn h&oacute;a th&ocirc;n, tổ chức c&aacute;c cuộc thi phong tr&agrave;o vệ sinh nh&agrave; n&ocirc;ng v&agrave; phổ biến kinh nghiệm v&agrave; nh&acirc;n rộng m&ocirc; h&igrave;nh l&agrave;ng sinh th&aacute;i ven biển sang c&aacute;c địa phương kh&aacute;c&hellip; v&agrave; cung cấp &ldquo;Tủ s&aacute;ch nh&agrave; n&ocirc;ng&rdquo; với khoảng 200 cuốn.</p>\r\n<p class="Normal">Cũng trong khu&ocirc;n khổ dự &aacute;n, con đường d&agrave;i một km mang t&ecirc;n &ldquo;Con đường xanh Toyota&rdquo; sẽ được x&acirc;y dựng tại ng&atilde; ba dẫn đến khu mộ đại thi h&agrave;o Nguyễn Du.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="lang.JPG" alt="C&ocirc;ng ty Toyota Việt Nam." width="450" height="296" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lễ khởi động dự &aacute;n đ&atilde; diễn ra v&agrave;o ng&agrave;y 28/12.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vấn đề vệ sinh m&ocirc;i trường ở Ti&ecirc;n Chương c&ograve;n nhiều hạn chế. &Yacute; thức của người d&acirc;n về bảo vệ m&ocirc;i trường, nguồn nước thấp. C&ocirc;ng t&aacute;c thu gom r&aacute;c thải chưa được triển khai. To&agrave;n th&ocirc;n kh&ocirc;ng c&oacute; hệ thống thu gom nước thải n&ecirc;n t&igrave;nh trạng &ocirc; nhiễm cục bộ do nước thải tồn đọng thường xuy&ecirc;n xảy ra. C&aacute;c loại r&aacute;c thải từ n&ocirc;ng nghiệp như chai, lọ, bao b&igrave; ph&acirc;n b&oacute;n, thuốc bảo vệ thực vật chưa được người d&acirc;n thu gom đ&uacute;ng chỗ n&ecirc;n xảy ra ph&aacute;t t&aacute;n trong m&ocirc;i trường, g&acirc;y &ocirc; nhiễm c&aacute;c nguồn nước sinh hoạt v&agrave; chăn nu&ocirc;i.</p>\r\n<p class="Normal">Chương tr&igrave;nh được C&ocirc;ng ty &ocirc;t&ocirc; Toyota Việt Nam (TMV) thực hiện từ năm 2009 v&agrave; l&agrave; một trong c&aacute;c hoạt động nằm trong khu&ocirc;n khổ chương tr&igrave;nh Go Green - H&agrave;nh tr&igrave;nh xanh do TMV, Tổng cục M&ocirc;i trường v&agrave; Bộ Gi&aacute;o dục Đ&agrave;o tạo phối hợp thực hiện.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>C&ocirc;ng ty Toyota Việt Nam</em>)</p>', 33, 8, 2455, 0, 1),
(664, 'Ô nhiễm không khí đô thị tăng vì đèn điện', 'Ánh sáng nhân tạo tại các thành phố vào ban đêm cản trở sự hình thành các chất làm sạch không khí.', 'Losa.jpg', NULL, '<h1 class="Title">&Ocirc; nhiễm kh&ocirc;ng kh&iacute; đ&ocirc; thị tăng v&igrave; đ&egrave;n điện</h1>\r\n<h2 class="Lead">&Aacute;nh s&aacute;ng nh&acirc;n tạo tại c&aacute;c th&agrave;nh phố v&agrave;o ban đ&ecirc;m cản trở sự h&igrave;nh th&agrave;nh c&aacute;c chất l&agrave;m sạch kh&ocirc;ng kh&iacute;.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Losa.jpg" alt="Th&agrave;nh phố Los Angeles về đ&ecirc;m" width="450" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Th&agrave;nh phố Los Angeles về đ&ecirc;m. Ảnh:<em>&nbsp;flickr.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&aacute;c nh&agrave; khoa học thuộc Cơ quan quản l&yacute; Hải dương v&agrave; Kh&iacute; quyển quốc gia Mỹ d&ugrave;ng m&aacute;y bay để đo cường độ &aacute;nh s&aacute;ng th&agrave;nh phố tr&ecirc;n kh&ocirc;ng phận th&agrave;nh phố Los Angeles, bang California bốn lần trong m&ugrave;a h&egrave; vừa qua,&nbsp;<em>Discovery</em>&nbsp;đưa tin. Họ nhận thấy &aacute;nh s&aacute;ng nh&acirc;n tạo tại khu vực th&agrave;nh phố Los Angeles yếu hơn 10.000 lần so với &aacute;nh s&aacute;ng mặt trời, song n&oacute; k&igrave;m h&atilde;m tốc độ l&agrave;m sạch kh&ocirc;ng kh&iacute; v&agrave;o ban đ&ecirc;m.</p>\r\n<p class="Normal">Theo nh&oacute;m nghi&ecirc;n cứu, &aacute;nh s&aacute;ng cản trở qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh một loại ph&acirc;n tử nitơ oxit m&agrave; người ta gọi l&agrave; gốc nitrat. Gốc n&agrave;y được tạo n&ecirc;n bởi những phản ứng giữa nitơ dioxide (NO2) với ozone (O3). N&oacute; li&ecirc;n kết với c&aacute;c chất g&acirc;y &ocirc; nhiễm trong kh&ocirc;ng kh&iacute;, song ph&acirc;n hủy nhanh khi tiếp x&uacute;c với &aacute;nh s&aacute;ng.</p>\r\n<p class="Normal">&ldquo;Gốc nitrat kh&ocirc;ng tồn tại v&agrave;o ban ng&agrave;y. N&oacute; xuất hiện v&agrave;o ban đ&ecirc;m, tương t&aacute;c với c&aacute;c chất g&acirc;y &ocirc; nhiễm v&agrave; l&agrave;m sạch kh&ocirc;ng kh&iacute;&rdquo;, Stark giải th&iacute;ch.</p>\r\n<p class="Normal">Ngo&agrave;i việc l&agrave;m giảm tốc độ l&agrave;m sạch kh&ocirc;ng kh&iacute;, &aacute;nh s&aacute;ng điện c&ograve;n l&agrave;m tăng số lượng ph&acirc;n tử g&acirc;y &ocirc; nhiễm.</p>\r\n<p class="Normal">Anne Douglass, một nh&agrave; khoa học của NASA, n&oacute;i rằng những t&aacute;c nh&acirc;n g&acirc;y &ocirc; nhiễm m&agrave; con người lo ngại nhất l&agrave; hạt si&ecirc;u nhỏ v&agrave; ozone tr&ecirc;n mặt đất. Ch&uacute;ng được tạo n&ecirc;n từ c&aacute;c h&oacute;a chất kh&aacute;c. Phản ứng tạo ra ch&uacute;ng cần &aacute;nh s&aacute;ng để xảy ra.</p>\r\n<p class="Normal">C&aacute;c nh&agrave; khoa học nhận định việc thay đổi loại &aacute;nh s&aacute;ng c&oacute; thể kh&ocirc;ng gi&uacute;p cải thiện t&igrave;nh h&igrave;nh, trừ khi c&aacute;c th&agrave;nh phố d&ugrave;ng &aacute;nh s&aacute;ng đỏ, loại &aacute;nh s&aacute;ng kh&ocirc;ng t&aacute;c động tới sự h&igrave;nh th&agrave;nh của gốc nitrat.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 2727, 0, 1),
(665, 'Huế giao 10 con khỉ quý hiếm cho Hà Nội', 'Sáng nay, 10 con khỉ đuôi lợn và khỉ mắt đỏ, loại có tên trong sách đỏ, đã được thu gom từ các hộ gia đình ở thành phố Huế, để bàn giao cho Trung tâm cứu hộ động vật hoang dã Sóc Sơn, Hà Nội.', 'khi.jpg', NULL, '<h1 class="Title">Huế giao 10 con khỉ qu&yacute; hiếm cho H&agrave; Nội</h1>\r\n<h2 class="Lead">S&aacute;ng nay, 10 con khỉ đu&ocirc;i lợn v&agrave; khỉ mắt đỏ, loại c&oacute; t&ecirc;n trong s&aacute;ch đỏ, đ&atilde; được thu gom từ c&aacute;c hộ gia đ&igrave;nh ở th&agrave;nh phố Huế, để b&agrave;n giao cho Trung t&acirc;m cứu hộ động vật hoang d&atilde; S&oacute;c Sơn, H&agrave; Nội.</h2>\r\n<p class="Normal">Ph&aacute;t hiện c&aacute;c c&aacute; thể khỉ đu&ocirc;i lợn, khỉ mắt đỏ tại 7 nh&agrave; d&acirc;n v&agrave; nh&agrave; h&agrave;ng ở th&agrave;nh phố Huế từ năm 2008, c&aacute;c t&igrave;nh nguyện vi&ecirc;n của Trung t&acirc;m gi&aacute;o dục thi&ecirc;n nhi&ecirc;n (ENV) đ&atilde; ki&ecirc;n tr&igrave; thuyết phục người d&acirc;n thả ch&uacute;ng về tự nhi&ecirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="khi.jpg" alt="" width="495" height="343" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những con khỉ n&agrave;y sẽ được b&agrave;n giao cho Trung t&acirc;m cứu hộ S&oacute;c Sơn, H&agrave; Nội. Ảnh:&nbsp;<em>Văn Nguyễn.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">S&aacute;ng nay, sau khi được sự đồng &yacute; của người d&acirc;n, Chi cục Kiểm l&acirc;m Thừa Thi&ecirc;n - Huế phối hợp ENV đ&atilde; thu gom để chuyển giao về c&aacute;c trung t&acirc;m cứu hộ v&igrave; những con khỉ n&agrave;y bị nu&ocirc;i nhốt một thời gian d&agrave;i, chưa thể thả ngay về tự nhi&ecirc;n.</p>\r\n<p class="Normal">Theo &ocirc;ng Hồ Văn Phước, Trưởng ph&ograve;ng Bảo tồn thi&ecirc;n nhi&ecirc;n, Chi cục Kiểm l&acirc;m Thừa Thi&ecirc;n - Huế, hiện c&aacute;c trung t&acirc;m cứu hộ ở tỉnh kh&ocirc;ng c&ograve;n khả năng tiếp nhận. Do đ&oacute; ENV đ&atilde; li&ecirc;n lạc với Trung t&acirc;m Cứu hộ động vật hoang d&atilde; S&oacute;c Sơn v&agrave;o Huế tiếp nhận số khỉ n&agrave;y về nu&ocirc;i dưỡng, sau đ&oacute; thả v&agrave;o tự nhi&ecirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Văn Nguyễn</strong></p>', 33, 8, 1411, 0, 1),
(666, 'Nhiều di sản thế giới bị đe dọa', 'Hiện tượng trái đất ấm lên khiến nhiều kim tự tháp bị chôn vùi dưới cát, xác ướp trong băng phân hủy và những ngôi đền sụp đổ.', 'pyramids.jpg', NULL, '<h1 class="Title">Nhiều di sản thế giới bị đe dọa</h1>\r\n<h2 class="Lead">Hiện tượng tr&aacute;i đất ấm l&ecirc;n khiến nhiều kim tự th&aacute;p bị ch&ocirc;n v&ugrave;i dưới c&aacute;t, x&aacute;c ướp trong băng ph&acirc;n hủy v&agrave; những ng&ocirc;i đền sụp đổ.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="pyramids.jpg" alt="" width="450" height="296" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c kim tự th&aacute;p tại Meroe, Sudan đang l&uacute;n dần xuống c&aacute;t bởi biến đổi kh&iacute; hậu. Ảnh:<em>abc.net.au</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Henri-Paul Francfort, một nh&agrave; khoa học l&agrave;m việc tại Viện Nghi&ecirc;n cứu khoa học quốc gia Ph&aacute;p (CNRS), n&oacute;i với&nbsp;<em>AFP&nbsp;</em>rằng, do nhiệt độ trung b&igrave;nh của tr&aacute;i đất tăng dần, c&aacute;c khối băng tr&ecirc;n thế giới tan chảy, sa mạc mở rộng, mực nước biển tăng v&agrave; sức mạnh của c&aacute;c cơn b&atilde;o cũng tăng. Chẳng hạn, sự tan chảy của tầng đ&oacute;ng băng vĩnh cửu dưới l&ograve;ng đất đang đe dọa những x&aacute;c ướp cổ đại tại d&atilde;y n&uacute;i Altai, v&ugrave;ng Siberia, Nga.</p>\r\n<p class="Normal">Giới khoa học cảnh b&aacute;o hiện tượng nước biển d&acirc;ng sẽ x&oacute;a sổ h&agrave;ng loạt di chỉ khảo cổ dọc bờ biển, trong đ&oacute; những h&ograve;n đảo thuộc Th&aacute;i B&igrave;nh Dương đối mặt với nguy cơ lớn nhất. Tại Tanzania, hiện tượng x&oacute;i m&ograve;n ở bờ biển ph&aacute; hủy một bức tường của ph&aacute;o đ&agrave;i Kilwa &ndash; c&ocirc;ng tr&igrave;nh do thực d&acirc;n Bồ Đ&agrave;o Nha x&acirc;y dựng tr&ecirc;n bờ biển của một h&ograve;n đảo v&agrave;o năm 1505.</p>\r\n<p class="Normal">Ở Bangladesh, th&agrave;nh phố Panam, kinh đ&ocirc; của vương quốc Bengal từ thế kỷ 15 tới thế kỷ 19, thường xuy&ecirc;n hứng chịu những trận lụt. UNESCO đưa Panam v&agrave;o danh s&aacute;ch 100 di sản bị đe dọa bởi biến đổi kh&iacute; hậu.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/46/8A/Tabasqueno2.jpg" alt="Ng&ocirc;i đền" width="450" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ng&ocirc;i đền Tabasqueno tại Mexico. Ảnh:&nbsp;<em>megalithic.co.uk</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Dominique Michelet, một nh&agrave; khoa học kh&aacute;c của CNRS, cho rằng sự gia tăng c&aacute;c sự kiện thời tiết bất ngờ - như b&atilde;o &ndash; cũng l&agrave; mối nguy đối với c&aacute;c di sản. Theo &ocirc;ng, th&agrave;nh phố Chan Chan tại Peru, đang bị hủy hoại bởi lũ lụt. Chan Chan từng l&agrave; kinh đ&ocirc; của đế chế Chimu xưa.</p>\r\n<p class="Normal">Tương tự, ng&ocirc;i đền Tabasqueno tại Mexico &ndash; di sản nổi tiếng c&ograve;n s&oacute;t lại từ nền văn minh Maya &ndash; bị ph&aacute; hủy nghi&ecirc;m trọng bởi hai cơn b&atilde;o v&agrave;o năm 1995.</p>\r\n<p class="Normal">&ldquo;C&aacute;c nh&agrave; khảo cổ đ&atilde; cố gắng gia cố ng&ocirc;i đền, nhưng do nước chiếm tỷ lệ qu&aacute; lớn ở b&ecirc;n dưới n&ecirc;n n&oacute; sụp v&agrave;o b&ecirc;n trong&rdquo;, Michelet n&oacute;i.</p>\r\n<p class="Normal">C&aacute;t l&agrave; một trong những kẻ th&ugrave; đ&aacute;ng sợ nhất của c&aacute;c địa điểm khảo cổ trong bối cảnh sức mạnh của c&aacute;c cơn b&atilde;o ng&agrave;y c&agrave;ng tăng. Chẳng hạn, tại Sudan, c&aacute;c đụn c&aacute;t nhấn ch&igrave;m những kim tự th&aacute;p trong th&agrave;nh phố Meroe &ndash; kinh đ&ocirc; của một vương quốc h&ugrave;ng mạnh từ thế kỷ thứ 3 trước C&ocirc;ng nguy&ecirc;n tới thế kỷ thứ 4 sau C&ocirc;ng nguy&ecirc;n. Tại Oman, hai si&ecirc;u b&atilde;o Gonu (năm 2007) v&agrave; Phet (năm 2009) đ&atilde; v&ugrave;i lấp ho&agrave;n to&agrave;n những địa điểm khảo cổ c&oacute; ni&ecirc;n đại từ thi&ecirc;n ni&ecirc;n kỷ thứ 5 v&agrave; 6 trước C&ocirc;ng nguy&ecirc;n dưới c&aacute;t.</p>\r\n<p class="Normal">Michelet cảnh b&aacute;o rằng việc UNESCO x&aacute;c định những di sản c&oacute; nguy cơ biến mất bởi biến đổi kh&iacute; hậu l&agrave; cần thiết, song chưa đủ.</p>\r\n<p class="Normal">&ldquo;Khảo cổ l&agrave; một phần của k&yacute; ức nh&acirc;n loại. V&igrave; thế lo&agrave;i người cần thực hiện c&aacute;c giải ph&aacute;p triệt để nhằm bảo vệ c&aacute;c di sản khỏi t&aacute;c động của biến đổi kh&iacute; hậu&rdquo;, Francfort ph&aacute;t biểu.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 2663, 0, 1),
(667, 'Thứ trưởng Bộ Nội địa Mỹ ca ngợi việc làm sạch hồ Đền Lừ', 'Từng là một nơi ô nhiễm khiến mọi người xa lánh, hồ Đền Lừ ở phía nam Hà Nội đã trở thành điểm vui chơi giải trí trong lành, nhờ nỗ lực bảo vệ môi trường của phụ nữ.', 'ho-den-lu.jpg', NULL, '<h1 class="Title">Thứ trưởng Bộ Nội địa Mỹ ca ngợi việc l&agrave;m sạch hồ Đền Lừ</h1>\r\n<h2 class="Lead">Từng l&agrave; một nơi &ocirc; nhiễm khiến mọi người xa l&aacute;nh, hồ Đền Lừ ở ph&iacute;a nam H&agrave; Nội đ&atilde; trở th&agrave;nh điểm vui chơi giải tr&iacute; trong l&agrave;nh, nhờ nỗ lực bảo vệ m&ocirc;i trường của phụ nữ.</h2>\r\n<p class="Normal">Mỗi s&aacute;ng sớm, nhiều người d&acirc;n c&oacute; mặt tại hồ để tập thể dục, h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh, c&aacute;c em nhỏ vui chơi.</p>\r\n<p class="Normal">B&aacute;c Ho&agrave;ng Thanh Tam, 60 tuổi, s&aacute;ng v&agrave; chiều n&agrave;o cũng ra hồ tập dưỡng sinh vui mừng cho biết: "Từ ng&agrave;y được chị em phụ nữ trong phường tiến h&agrave;nh thực hiện nhiều biện ph&aacute;p l&agrave;m sạch vệ sinh m&ocirc;i trường, kh&ocirc;ng c&ograve;n m&ugrave;i h&ocirc;i thối như trước. &Yacute; thức người d&acirc;n cũng được n&acirc;ng cao hơn".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ho-den-lu.jpg" border="1" alt="" width="480" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hồ Đền Lừ. Ảnh:&nbsp;<em>H.T</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chị Thảo, người d&acirc;n ở đ&acirc;y n&oacute;i: "Trước đ&acirc;y c&oacute; h&ocirc;m nh&agrave; c&aacute;ch 3-4km vẫn c&ograve;n ngửi thấy m&ugrave;i c&aacute; chết, nhất l&agrave; những ng&agrave;y trời nắng, c&ograve;n b&acirc;y giờ th&igrave; hồ trở th&agrave;nh nơi vui chơi giải tr&iacute; của người d&acirc;n&rdquo;.</p>\r\n<p class="Normal">Hồ Đền Lừ l&agrave; một trong những v&iacute; dụ tốt về cải tạo m&ocirc;i trường, do Hội phụ nữ phường Ho&agrave;ng Văn Thụ thực hiện. Trong chuyến thăm Việt Nam vừa rồi, b&agrave; Anne Castle, Thứ trưởng Bộ Nội địa phụ tr&aacute;ch c&aacute;c vấn đề về nước v&agrave; khoa học của Mỹ, đ&atilde; tới thăm v&agrave; chia sẻ kinh nghiệm c&ugrave;ng người d&acirc;n địa phương.</p>\r\n<p class="Normal">&ldquo;Người d&acirc;n nơi đ&acirc;y, nhất l&agrave; c&aacute;c qu&yacute; b&agrave;, đ&atilde; biến c&aacute;i hồ vốn bị mọi người xa l&aacute;nh th&agrave;nh nơi c&ocirc;ng cộng cho tất cả người d&acirc;n. Đ&acirc;y l&agrave; th&agrave;nh c&ocirc;ng rất lớn. Sự th&agrave;nh c&ocirc;ng n&agrave;y kh&ocirc;ng chỉ thế hệ ng&agrave;y nay m&agrave; mai sau sẽ được hưởng n&oacute;&rdquo;.</p>\r\n<p class="Normal">B&agrave; cũng dẫn một c&acirc;u th&agrave;nh ngữ của người Mỹ: &ldquo;Ở đất nước ch&uacute;ng t&ocirc;i c&oacute; c&acirc;u, nếu c&oacute; c&ocirc;ng việc m&agrave; người đ&agrave;n &ocirc;ng phải l&agrave;m, nhưng nếu giao cho phụ nữ chắc chắn c&ocirc;ng việc ấy th&agrave;nh c&ocirc;ng&rdquo;.</p>\r\n<p class="Normal">B&agrave; Anne Castle chia sẻ về tr&aacute;ch nhiệm quản l&yacute; nguồn lực tự nhi&ecirc;n cũng như g&igrave;n giữ m&ocirc;i trường sạch đẹp, v&agrave; cho biết ở Mỹ để l&agrave;m được điều n&agrave;y cũng phải dựa v&agrave;o cộng đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/41/21/den-lu2.jpg" border="1" alt="" width="480" height="321" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&agrave; Anne Castle, Thứ trưởng Bộ Nội địa Mỹ. Ảnh:&nbsp;<em>H.T</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">B&agrave; Castle sẽ tham gia k&yacute; kết bản ghi nhớ giữa Bộ Nội vụ Mỹ v&agrave; Bộ T&agrave;i nguy&ecirc;n M&ocirc;i trường Việt Nam về dự &aacute;n gi&uacute;p dự b&aacute;o t&aacute;c động của c&aacute;c đập thuỷ điện ở thượng nguồn s&ocirc;ng Mekong đối với hạ nguồn, trong đ&oacute; c&oacute; Việt Nam.</p>\r\n<p class="Normal">H&agrave; Nội c&oacute; hơn 100, trong đ&oacute; khu vực nội th&agrave;nh c&oacute; khoảng 20 hồ lớn. Nhiều nh&agrave; khoa học cho rằng, &ocirc; nhiễm hồ H&agrave; Nội đang ở mức nghi&ecirc;m trọng, đặc biệt c&aacute;c hồ nhỏ, lưu th&ocirc;ng k&eacute;m.&nbsp;<br /><br />Hầu như nước thải từ tất cả c&aacute;c khu d&acirc;n cư sống tại khu vực quanh những hồ của H&agrave; Nội đều dồn xuống đ&oacute;. Hiện tượng c&aacute; chết h&agrave;ng loạt v&agrave; tảo độc phủ đầy mặt nước xuất hiện ng&agrave;y c&agrave;ng nhiều v&agrave; li&ecirc;n tục.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hương Thu</strong></p>', 33, 8, 2710, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(668, 'Những bức ảnh chống biến đổi khí hậu chụp từ vệ tinh', 'Các tác phẩm nghệ thuật sắp đặt với mục đích hưởng ứng chiến dịch chống biến đổi khí hậu được chụp từ vệ tinh bay trên không với tốc độ 17.000 dặm một giờ và cách hành tinh xanh 400 dặm. ', 'CH-Dominica.jpg', NULL, '<h1 class="Title">Những bức ảnh chống biến đổi kh&iacute; hậu chụp từ vệ tinh</h1>\r\n<h2 class="Lead">C&aacute;c t&aacute;c phẩm nghệ thuật sắp đặt với mục đ&iacute;ch hưởng ứng chiến dịch chống biến đổi kh&iacute; hậu được chụp từ vệ tinh bay tr&ecirc;n kh&ocirc;ng với tốc độ 17.000 dặm một giờ v&agrave; c&aacute;ch h&agrave;nh tinh xanh 400 dặm.&nbsp;<br />&gt;&nbsp;<a class="Lead" href="http://vnexpress.net/GL/Khoa-hoc/2010/10/3BA21774/">Việt Nam c&ugrave;ng thế giới hưởng ứng chống biến đổi kh&iacute; hậu</a></h2>\r\n<table border="1" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td><img src="Australia.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Normal" align="left">Ng&agrave;y kh&iacute; hậu năm nay mang chủ đề 350 để nhắc tới mục ti&ecirc;u giảm lượng kh&iacute; CO2 trong kh&ocirc;ng kh&iacute; từ mức hiện nay xuống dưới 350 ppm, l&agrave; giới hạn an to&agrave;n tối đa cho tr&aacute;i đất, theo t&iacute;nh to&aacute;n của c&aacute;c nh&agrave; khoa học. Ảnh chụp ở Australia.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="Los-Angeles,-M-.jpg" border="1" alt="" width="350" height="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Nghệ sĩ biểu diễn nghệ thuật tr&ecirc;n kh&ocirc;ng Spectral Q đ&atilde; c&ugrave;ng với những c&ocirc;ng d&acirc;n của th&agrave;nh phố Los Angeles, Mỹ, tạo th&agrave;nh h&igrave;nh ảnh chim đại b&agrave;ng khổng lồ bay qua một c&aacute;nh đồng c&aacute;c tấm pin thu năng lượng mặt trời</td>\r\n</tr>\r\n<tr>\r\n<td><img src="Los-Angeles,-M--(2).jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Cận cảnh con chim.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="New-Delhi.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Tại New Delhi, c&aacute;c em học sinh xếp th&agrave;nh một ch&uacute; voi khổng lồ, thể hiện th&ocirc;ng điệp ẩn dụ &ldquo;ch&uacute; voi trong căn ph&ograve;ng&rdquo;, trong đ&oacute; căn ph&ograve;ng ch&iacute;nh l&agrave; hiện tượng biến đổi kh&iacute; hậu</td>\r\n</tr>\r\n<tr>\r\n<td><img src="Santa-Domingo.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">V&agrave; c&aacute;c c&ocirc;ng d&acirc;n trẻ tại Santo Domingo, nước Cộng h&ograve;a Dominica, c&ugrave;ng l&agrave;m th&agrave;nh một bức tranh mi&ecirc;u tả ng&ocirc;i nh&agrave; của họ bị nhấn ch&igrave;m do mực nước biển d&acirc;ng.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="TBN.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Tại đồng bằng s&ocirc;ng Del Ebro, T&acirc;y Ban Nha, nghệ sĩ đường phố nổi tiếng Jorge Rodriguez-Gerada v&agrave; người d&acirc;n địa phương tạo h&igrave;nh khu&ocirc;n mặt một c&ocirc; g&aacute;i trẻ hoảng sợ v&igrave; biến đổi kh&iacute; hậu.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="Santa-Fe,-New-Mexico,-M-.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Tại Santa Fe, New Mexico (Mỹ), Viện Nghệ thuật Santa Fe c&ugrave;ng với hơn 1.000 nữ hướng đạo sinh v&agrave; người d&acirc;n trong khu vực cầm c&aacute;c tranh &aacute;p-ph&iacute;ch m&agrave;u xanh đứng trong l&ograve;ng con s&ocirc;ng Santa Fe đ&atilde; bị cạn kh&ocirc; bởi sự thiếu hụt tuyết do t&igrave;nh trạng n&oacute;ng l&ecirc;n to&agrave;n cầu.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="Vancouver,-Canada.jpg" border="1" alt="" width="350" height="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Trong khi đ&oacute;, tại Van Vancouver h&agrave;ng trăm người xếp h&igrave;nh tạo n&ecirc;n một dấu ch&acirc;n m&agrave;u xanh khổng lồ ngay tại trung t&acirc;m của th&agrave;nh phố, thể hiện dấu ch&acirc;n sinh th&aacute;i của lo&agrave;i người tr&ecirc;n h&agrave;nh tinh n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="New-York,-M-.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Tại th&agrave;nh phố New York, một bức tranh mi&ecirc;u tả đường bờ biển th&agrave;nh phố New York v&agrave; New Jersey khi mực nước biển d&acirc;ng l&ecirc;n 7 m&eacute;t được thể hiện tr&ecirc;n một n&oacute;c nh&agrave; v&agrave; được chụp ảnh từ kh&ocirc;ng gian.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="Mexico-City,-Mehico.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Mexico City, h&agrave;ng ngh&igrave;n trẻ em tạo h&igrave;nh một cơn b&atilde;o lớn, v&agrave; số 350 được đặt trong mắt b&atilde;o.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="CH-Dominica.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Xếp h&igrave;nh tại Cộng h&ograve;a Dominica.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ki&ecirc;n Cường</strong></p>', 33, 8, 656, 0, 1),
(669, 'Số người chết vì hạn hán và lũ lụt tăng gấp đôi', 'Một tổ chức nhân đạo tại Anh hôm qua thông báo khoảng 21 nghìn trên khắp thế giới thiệt mạng vì hạn hán và lũ lụt trong 9 tháng đầu năm nay, cao gấp hơn hai lần so với cả năm ngoái.', 'Pakistan_floods.jpg', NULL, '<h1 class="Title">Số người chết v&igrave; hạn h&aacute;n v&agrave; lũ lụt tăng gấp đ&ocirc;i</h1>\n<h2 class="Lead">Một tổ chức nh&acirc;n đạo tại Anh h&ocirc;m qua th&ocirc;ng b&aacute;o khoảng 21 ngh&igrave;n tr&ecirc;n khắp thế giới thiệt mạng v&igrave; hạn h&aacute;n v&agrave; lũ lụt trong 9 th&aacute;ng đầu năm nay, cao gấp hơn hai lần so với cả năm ngo&aacute;i.</h2>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="Pakistan_floods.jpg" alt="" width="450" height="313" /></td>\n</tr>\n<tr>\n<td class="Image">Gần 2.000 người chết trong trận lũ lụt khủng khiếp tại Pakistan hồi th&aacute;ng 7. Ảnh:<em>sethbarnes.com</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal"><span style="color: #404040;"><span style="color: #000000;">Đ&oacute; l&agrave; thống k&ecirc; của Oxfam, một tổ chức nh&acirc;n đạo phi ch&iacute;nh phủ tại Anh,<em>Telegraph</em>&nbsp;cho biết. Một b&aacute;o c&aacute;o của tổ chức n&agrave;y chỉ ra rằng c&aacute;c hiện tượng thời tiết khắc nghiệt đang tăng dần c&ugrave;ng với t&igrave;nh trạng n&oacute;ng l&ecirc;n của tr&aacute;i đất.</span></span></p>\n<p><span style="color: #404040;">\n<p class="Normal"><span style="color: #000000;">Tim Gore, một chuy&ecirc;n gia của Oxfam, n&oacute;i rằng những trận lụt quy m&ocirc; lớn ở Pakistan v&agrave; hạn h&aacute;n khủng khiếp ở Nga vừa qua đều do biến đổi kh&iacute; hậu g&acirc;y n&ecirc;n.</span></p>\n<p class="Normal"><span style="color: #000000;">&ldquo;Những thảm họa thời tiết g&acirc;y n&ecirc;n tổn thất to lớn trong năm nay. T&igrave;nh h&igrave;nh c&oacute; thể trở n&ecirc;n tồi tệ hơn do biến đổi kh&iacute; hậu. Những t&aacute;c động của biến đổi kh&iacute; hậu đối với con người trong năm 2010 l&agrave; lời cảnh b&aacute;o mạnh mẽ đối với c&aacute;c cuộc thương lượng về cắt giảm kh&iacute; thải ở Mexico&rdquo;, &ocirc;ng Gore ph&aacute;t biểu.</span></p>\n<p class="Normal"><span style="color: #000000;">B&aacute;o c&aacute;o của Oxfam được đưa ra trong ng&agrave;y khai mạc v&ograve;ng đ&agrave;m ph&aacute;n mới nhất trong khu&ocirc;n khổ Hội nghị về biến đổi kh&iacute; hậu do Li&ecirc;n Hợp Quốc chủ tr&igrave; tại th&agrave;nh phố Cancun, Mexico. Hội nghị diễn ra từ ng&agrave;y 29/11 tới 10/12.</span></p>\n<p class="Normal"><span style="color: #000000;">Oxfam đang k&ecirc;u gọi cộng đồng quốc tế th&agrave;nh lập Quỹ Xanh (Green Fund) với mức đ&oacute;ng g&oacute;p tối thiểu 100 tỷ USD mỗi năm để gi&uacute;p c&aacute;c nước ngh&egrave;o th&iacute;ch nghi với biến đổi kh&iacute; hậu.</span></p>\n<p class="Normal"><span style="color: #000000;">Chris Huhne, Bộ trưởng Năng lượng v&agrave; Biến đổi kh&iacute; hậu Anh, n&oacute;i rằng c&aacute;c b&ecirc;n tham gia v&ograve;ng đ&agrave;m ph&aacute;n tại Cancun sẽ quyết định c&aacute;ch thức chi tiền cho những nước ngh&egrave;o để giảm hoạt động ph&aacute; rừng v&agrave; tiếp thu những c&ocirc;ng nghệ kh&ocirc;ng g&acirc;y tổn hại tới m&ocirc;i trường. Mặc d&ugrave; vậy, &ocirc;ng nhận định c&aacute;c nước chưa thể đạt được một thỏa thuận mang t&iacute;nh r&agrave;ng buộc ho&agrave;n to&agrave;n trong v&ograve;ng đ&agrave;m ph&aacute;n tại Cancun.</span></p>\n<p class="Normal" style="text-align: right;"><strong><span style="color: #000000;">Minh Long</span></strong></p>\n</span></p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/3A/04/fire.jpg" alt="Một l&iacute;nh cứu hỏa dập ch&aacute;y rừng tại Nga" width="450" height="338" /></td>\n</tr>\n<tr>\n<td class="Image">Một l&iacute;nh cứu hỏa dập ch&aacute;y rừng tại Nga hồi th&aacute;ng 8. Nước Nga vừa trải qua một m&ugrave;a h&egrave; khủng khiếp nhất trong hơn một thế kỷ. Ảnh:&nbsp;<em>rferl.org</em>.</td>\n</tr>\n</tbody>\n</table>', 33, 8, 197, 0, 1),
(671, 'Lắp máy phát điện chạy bằng bio-gas tại 42 trang trại', 'Sau 2 năm triển khai, dự án sản xuất điện năng quy mô nhỏ bằng bio-gas (dự án Bio-Gas) đã hỗ trợ cho 42 trang trại, hộ gia đình và doanh nghiệp lắp đặt máy phát điện chạy bằng bio-gas.', '350x232.JPG', NULL, '<p class="Title">Lắp m&aacute;y ph&aacute;t điện chạy bằng bio-gas tại 42 trang trại</p>\r\n<p class="Lead">Sau 2 năm triển khai, dự &aacute;n sản xuất điện năng quy m&ocirc; nhỏ bằng bio-gas (dự &aacute;n Bio-Gas) đ&atilde; hỗ trợ cho 42 trang trại, hộ gia đ&igrave;nh v&agrave; doanh nghiệp lắp đặt m&aacute;y ph&aacute;t điện chạy bằng bio-gas.</p>\r\n<p class="Normal">Bio-gas l&agrave; năng lượng t&aacute;i sinh nhận được từ qu&aacute; tr&igrave;nh ph&acirc;n hủy c&aacute;c chất hữu cơ trong m&ocirc;i trường thiếu kh&ocirc;ng kh&iacute;. R&aacute;c thải sinh hoạt, chất thải của qu&aacute; tr&igrave;nh sản xuất n&ocirc;ng nghiệp, chăn nu&ocirc;i, xử l&yacute; nước... l&agrave; nguồn nguy&ecirc;n liệu tốt để sản xuất bio-gas. Sử dụng bio-gas v&agrave; c&aacute;c nguồn năng lượng t&aacute;i sinh c&oacute; nguồn gốc từ năng lượng mặt trời kh&ocirc;ng l&agrave;m tăng nồng độ CO2 trong bầu kh&iacute; quyển, tiết kiệm nhi&ecirc;n liệu v&agrave; giảm &ocirc; nhiễm m&ocirc;i trường.</p>\r\n<p class="Normal">Dự &aacute;n Bio-Gas trong khu&ocirc;n khổ chương tr&igrave;nh "Go Green - H&agrave;nh tr&igrave;nh xanh" do Toyota Việt Nam (TMV) phối hợp với Tổng cục M&ocirc;i trường v&agrave; Bộ Gi&aacute;o dục Đ&agrave;o tạo thực hiện từ năm 2008. Theo kế hoạch, 500 cụm m&aacute;y ph&aacute;t điện chạy bằng bio-gas sẽ được lắp đặt c&aacute;c hộ d&acirc;n, trang trại chăn nu&ocirc;i, doanh nghiệp vừa v&agrave; nhỏ với 2 giai đoạn: Th&iacute; điểm trong phạm vi hẹp (từ năm 2009 đến 2011) v&agrave; đại tr&agrave; (từ năm 2012). Dự &aacute;n g&oacute;p phần giảm thiểu &ocirc; nhiễm m&ocirc;i trường th&ocirc;ng qua việc sử dụng chất thải v&agrave;o mục đ&iacute;ch hữu &iacute;ch; giảm thiểu nồng độ CO2 trong kh&ocirc;ng kh&iacute;; t&igrave;m nguồn năng lượng sạch, rẻ, bền vững v&agrave; chủ động.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="350x232.JPG" border="1" alt="" width="350" height="232" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đầu th&aacute;ng 10, tại Đ&agrave; Nẵng, TMV đ&atilde; phối hợp với đại học Đ&agrave; Nẵng tổ chức hội thảo &ldquo;Sản xuất điện năng bằng bio-gas - một giải ph&aacute;p tiết kiệm năng lượng v&agrave; bảo vệ m&ocirc;i trường ở n&ocirc;ng th&ocirc;n&rdquo; nhằm đ&aacute;nh gi&aacute; kết quả 2 năm triển khai dự &aacute;n Bio-Gas cho c&aacute;c hộ d&acirc;n, trang trại chăn nu&ocirc;i. Những hộ &aacute;p dụng c&ocirc;ng nghệ cho biết việc ứng dụng bio-gas để sản xuất điện năng gi&uacute;p họ tiết kiệm năng lượng, chi ph&iacute; sản xuất v&agrave; bảo vệ m&ocirc;i trường. Với c&ocirc;ng nghệ n&agrave;y, chất thải trong chăn nu&ocirc;i sẽ được đưa v&agrave;o hầm tạo ra kh&iacute; biogas để ph&aacute;t điện, phục vụ cho nhu cầu sinh hoạt v&agrave; chăn nu&ocirc;i. B&ecirc;n cạnh đ&oacute;, r&aacute;c thải sau bio-gas c&ograve;n được tận dụng l&agrave;m ph&acirc;n b&oacute;n cho c&acirc;y trồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/18/51/300x250.JPG" border="1" alt="" width="350" height="250" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="normal">Theo GS. TSKH B&ugrave;i Văn Ga, Thứ trưởng Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, chủ nhiệm đề t&agrave;i ứng dụng bio-gas để chạy động cơ diesel v&agrave; sản xuất điện năng, năng lượng tối đa của bio-gas gấp 20 lần thủy điện. Sử dụng 1 kW điện bằng bio-gas sẽ tiết kiệm được 0,4 l&iacute;t xăng dầu v&agrave; giảm 1 kg CO2". Dự &aacute;n ho&agrave;n th&agrave;nh sẽ g&oacute;p phần tiết kiệm được 6.000 l&iacute;t xăng dầu một ng&agrave;y, giảm ph&aacute;t thải 15 tấn CO2 một ng&agrave;y.</p>\r\n<p class="normal">Để biết th&ecirc;m th&ocirc;ng tin, truy cập website&nbsp;<a class="normal" href="http://www.gogreen.com.vn/">http://www.gogreen.com.vn/</a>.</p>\r\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>Toyota Việt Nam</em>)</p>', 33, 8, 1880, 0, 1),
(672, 'Nước sông Danube không còn độc', 'Các hợp chất kim loại và hữu cơ độc hại ngừng chảy vào sông Danube nhờ những nỗ lực khử độc trên một nhánh của nó.', 'a9.jpg', NULL, '<p class="Title">Nước s&ocirc;ng Danube kh&ocirc;ng c&ograve;n độc</p>\n<p class="Lead">C&aacute;c hợp chất kim loại v&agrave; hữu cơ độc hại ngừng chảy v&agrave;o s&ocirc;ng Danube nhờ những nỗ lực khử độc tr&ecirc;n một nh&aacute;nh của n&oacute;.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="a9.jpg" alt="" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">M&aacute;y đ&agrave;o đổ thạch cao xuống s&ocirc;ng Marcal để l&agrave;m đ&ocirc;ng đặc c&aacute;c chất độc trong b&ugrave;n đỏ. Ảnh:<em>AFP</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal"><span style="color: #000000;">Gần 700 ngh&igrave;n m3 chất thải tr&agrave;n v&agrave;o 7 l&agrave;ng sau khi bể chứa chất thải b&ocirc;x&iacute;t khổng lồ của một nh&agrave; m&aacute;y sản xuất nh&ocirc;m gần thị trấn Ajka ở miền t&acirc;y Hungary vỡ h&ocirc;m 4/10. Đ&acirc;y l&agrave; thảm họa m&ocirc;i trường nghi&ecirc;m trọng nhất trong lịch sử Hungary. &Iacute;t nhất 7 người chết v&agrave; khoảng 150 người bị thương v&igrave; b&ugrave;n độc.</span></p>\n<p class="Normal">B&ugrave;n độc tr&agrave;n tới s&ocirc;ng Raab v&agrave; Danube tại th&agrave;nh phố Gyor h&ocirc;m 7/10. Một quan chức quản l&yacute; nước của Hungary th&ocirc;ng b&aacute;o mức độ &ocirc; nhiễm của nước s&ocirc;ng Danube đ&atilde; vượt mức cho ph&eacute;p.</p>\n<p class="Normal">Theo&nbsp;<em>Xinhua</em>, h&ocirc;m qua Cơ quan Quản l&yacute; thảm họa quốc gia Hungary đ&atilde; đo độ kiềm tr&ecirc;n đoạn s&ocirc;ng Danube gần l&agrave;ng Gonyu. Đ&acirc;y l&agrave; một trong những l&agrave;ng bị b&ugrave;n đỏ tr&agrave;n qua. Kết quả cho thấy độ pH giảm xuống mức 7,54 &ndash; tức l&agrave; trung t&iacute;nh.</p>\n<p class="Normal">Suốt mấy ng&agrave;y qua c&aacute;c c&ocirc;ng nh&acirc;n đổ h&agrave;ng tấn thạch cao (để l&agrave;m kết tủa c&aacute;c chất độc) v&agrave; axit axetic (để giảm nồng độ kiềm) xuống s&ocirc;ng Marcal - một phụ lưu của s&ocirc;ng Danube. Người ta c&ograve;n &aacute;p dụng một số biện ph&aacute;p kh&aacute;c để chất độc lắng xuống l&ograve;ng s&ocirc;ng.</p>\n<p class="Normal">Danube l&agrave; s&ocirc;ng c&oacute; độ d&agrave;i thứ hai ở ch&acirc;u &Acirc;u (sau s&ocirc;ng Volga). N&oacute; chảy qua Hungary, Croatia, Serbia, Romania, Bulgaria, Ukraine v&agrave; Moldova trước khi đổ v&agrave;o Biển Đen.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/17/4C/sld.jpg" alt="C&ocirc;ng nh&acirc;n dọn b&ugrave;n trong một l&agrave;ng" width="450" height="308" /></td>\n</tr>\n<tr>\n<td class="Image">C&ocirc;ng nh&acirc;n dọn b&ugrave;n trong l&agrave;ng Kolontar, một trong những nơi chịu thiệt hại nặng nề nhất trong thảm họa b&ugrave;n độc tại Hungary, h&ocirc;m 7/10. Ảnh:&nbsp;<em>AP</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Hiện tại giới chức Hungary đang hối hả x&acirc;y một đ&ecirc; v&agrave; sơ t&aacute;n d&acirc;n sau khi người ta ph&aacute;t hiện nhiều vết nứt lớn tr&ecirc;n th&agrave;nh ph&iacute;a bắc của bể chứa chất thải từng vỡ h&ocirc;m 4/10. Bộ trưởng M&ocirc;i trường Zoltan Illes h&ocirc;m qua n&oacute;i th&agrave;nh ph&iacute;a bắc của bể chắc chắn vỡ trong v&ograve;ng một tuần.</p>\n<p class="Normal">Bể chứa gần thị trấn Ajka vẫn c&ograve;n chứa khoảng 2,5 triệu m3 b&ugrave;n đỏ. Nếu th&agrave;nh bể vỡ ho&agrave;n to&agrave;n, b&ugrave;n sẽ tr&agrave;n tới những vị tr&iacute; c&aacute;ch bể từ 1.000 tới 1.500 m.</p>\n<p class="Normal">&ldquo;Ch&uacute;ng t&ocirc;i kh&ocirc;ng biết liệu th&agrave;nh ph&iacute;a bắc sẽ chịu đựng được th&ecirc;m một ng&agrave;y hay một tuần nữa, song thảm họa c&oacute; thể xảy ra bất cứ l&uacute;c n&agrave;o&rdquo;, &ocirc;ng n&oacute;i.</p>\n<p class="Normal">Nhiều chuy&ecirc;n gia nhất tr&iacute; rằng th&agrave;nh bể sẽ vỡ, song chặn b&ugrave;n đỏ tr&agrave;n khỏi bể l&agrave; việc m&agrave; họ c&oacute; thể l&agrave;m được. Một trong những giải ph&aacute;p l&agrave; chuyển chất thải sang bể chứa kh&aacute;c.</p>\n<p class="Normal">Bộ trưởng Illes n&oacute;i bể chứa n&agrave;y sẽ chỉ an to&agrave;n nếu n&oacute; bị đ&oacute;ng cửa vĩnh viễn, c&ograve;n chất thải b&ecirc;n trong được chuyển sang một bể mới. Tuy nhi&ecirc;n, dự &aacute;n x&acirc;y bể mới chưa bắt đầu.</p>\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 432, 0, 1),
(673, 'Việt Nam cùng thế giới hưởng ứng chống biến đổi khí hậu', 'Ngày 10/10, Việt Nam cùng nhiều quốc gia trên thế giới tổ chức các sự kiện như lắp pin mặt trời, biểu tình, diễu hành... hưởng ứng ngày Toàn cầu chống biến đổi khí hậu.', '11_1.jpg', NULL, '<p class="Title">Việt Nam c&ugrave;ng thế giới hưởng ứng chống biến đổi kh&iacute; hậu</p>\r\n<p class="Lead">Ng&agrave;y 10/10, Việt Nam c&ugrave;ng nhiều quốc gia tr&ecirc;n thế giới tổ chức c&aacute;c sự kiện như lắp pin mặt trời, biểu t&igrave;nh, diễu h&agrave;nh... hưởng ứng ng&agrave;y To&agrave;n cầu chống biến đổi kh&iacute; hậu.</p>\r\n<table border="1" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td><img src="11_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Tuổi trẻ TP HCM c&ugrave;ng nhau n&oacute;i "T&ocirc;i đồng &yacute; cam kết xanh".</td>\r\n</tr>\r\n<tr>\r\n<td><img src="7_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Trồng 1.000 c&acirc;y xanh ở trường học tại H&agrave; Nội.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="10_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Rửa xe g&acirc;y quỹ ủng hộ ng&agrave;y chống biến đổi kh&iacute; hậu (Việt Nam).</td>\r\n</tr>\r\n<tr>\r\n<td><img src="1_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">C&aacute;c nh&agrave; sư cầu nguyện cho con số 350 tại Dharamsala, Ấn Độ. Ng&agrave;y kh&iacute; hậu năm nay mang chủ đề 350 để nhắc tới mục ti&ecirc;u giảm lượng kh&iacute; CO2 trong kh&ocirc;ng kh&iacute; từ mức hiện nay xuống dưới 350 ppm, l&agrave; giới hạn an to&agrave;n tối đa cho tr&aacute;i đất, theo t&iacute;nh to&aacute;n của c&aacute;c nh&agrave; khoa học.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="2_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Bếp năng lượng mặt trời tại Altea, T&acirc;y Ban Nha.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="3_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Biểu t&igrave;nh k&ecirc;u gọi chống biến đổi kh&iacute; hậu luật năng lượng sạch trước Nh&agrave; Trắng, Washington, Mỹ.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="5_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Đạp xe tại c&ocirc;ng vi&ecirc;n ở Sydney, Australia.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="4_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Chihuahua, Mexico.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="6_1.jpg" border="1" alt="" width="350" height="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Diễu h&agrave;nh tại th&aacute;p Kuwait, Kuwait.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="8_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Lắp pin mặt trời v&agrave; trồng c&acirc;y xanh tại Igang, Uganda.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="12_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="center">Tuần h&agrave;nh ở Istanbul, Thổ Nhĩ Kỳ.</td>\r\n</tr>\r\n<tr>\r\n<td><img src="9_1.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Ng&agrave;y hội tại Sharjah, Li&ecirc;n hiệp quốc c&aacute;c tiểu vương quốc Ả Rập Thống Nhất.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Ki&ecirc;n Cường</strong></p>', 33, 8, 2166, 0, 1),
(674, 'Tảo độc thảm sát các rạng san hô', 'Sự phát triển mạnh mẽ của các loài tảo độc khiến san hô trong một vịnh chết hàng loạt.', 'alg.jpg', NULL, '<p class="Title">Tảo độc thảm s&aacute;t c&aacute;c rạng san h&ocirc;</p>\n<p class="Lead">Sự ph&aacute;t triển mạnh mẽ của c&aacute;c lo&agrave;i tảo độc khiến san h&ocirc; trong một vịnh chết h&agrave;ng loạt.<br /><br /></p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="alg.jpg" alt="San h&ocirc; kh&ocirc;ng thể sống s&oacute;t b&ecirc;n dưới những đ&aacute;m tảo độc. Ảnh: BBC" width="450" height="276" /></td>\n</tr>\n<tr>\n<td class="Image">San h&ocirc; kh&ocirc;ng thể sống s&oacute;t b&ecirc;n dưới những đ&aacute;m tảo độc. Ảnh:&nbsp;<em>BBC.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">San h&ocirc; đang bị đe dọa bởi hiện tượng ấm l&ecirc;n to&agrave;n cầu, sự ph&aacute;t triển đ&ocirc; thị dọc c&aacute;c bờ biển, hoạt động khai th&aacute;c thủy sản qu&aacute; mức v&agrave; &ocirc; nhiễm m&ocirc;i trường nước. Nhưng trong một b&agrave;i viết tr&ecirc;n tạp ch&iacute;&nbsp;<em>Marine Pollution Bulletin</em>, c&aacute;c nh&agrave; khoa học của Li&ecirc;n Hợp Quốc khẳng định tảo cũng l&agrave; một hiểm họa đ&aacute;ng sợ đối với san h&ocirc;.</p>\n<p class="Normal"><em>BBC</em>&nbsp;cho biết, Viện Nước, M&ocirc;i trường v&agrave; Sức khỏe thuộc Đại học Li&ecirc;n Hợp Quốc t&igrave;m hiểu m&ocirc;i trường sống của san h&ocirc; trong vịnh Oman, một nh&aacute;nh của biển Ảrập. Trong lần nghi&ecirc;n cứu đầu ti&ecirc;n họ nh&igrave;n thấy tảo bao phủ một v&ugrave;ng c&oacute; diện t&iacute;ch hơn 500 km2.</p>\n<p class="Normal">Ba tuần sau đ&oacute;, họ quay lại v&agrave; ph&aacute;t hiện 95% rạn san h&ocirc; trong v&ugrave;ng nước c&oacute; tảo đ&atilde; chết.</p>\n<p class="Normal">Sự hiện diện của tảo ở mặt nước khiến lượng &aacute;nh s&aacute;ng mặt trời v&agrave; oxy d&agrave;nh cho c&aacute;c lo&agrave;i sinh vật kh&aacute;c, trong đ&oacute; c&oacute; tảo, giảm mạnh.</p>\n<p class="Normal">C&aacute;i chết đột ngột của san h&ocirc; g&acirc;y n&ecirc;n hậu quả nặng nề cho c&aacute;c lo&agrave;i động vật biển sống nhờ san h&ocirc;. Nh&oacute;m nghi&ecirc;n cứu nhận thấy số lượng c&aacute; thể của mọi lo&agrave;i động vật biển đều giảm từ 70% tới 83% khi họ khảo s&aacute;t tại một v&ugrave;ng nước gần b&aacute;n đảo Oman.</p>\n<p class="Normal">Kết quả ph&acirc;n t&iacute;ch cho thấy tất cả san h&ocirc; cứng đều chết bởi một lo&agrave;i tảo c&oacute; t&ecirc;n<em>Cochlodinium polykrikoides</em>. C&aacute;c chuy&ecirc;n gia đang t&igrave;m hiểu mức độ nguy hiểm của c&aacute;c lo&agrave;i tảo kh&aacute;c.</p>\n<p class="Normal">&ldquo;Ch&uacute;ng t&ocirc;i ngạc nhi&ecirc;n trước quy m&ocirc; v&agrave; tốc độ hủy diệt san h&ocirc; của tảo&rdquo;, nh&agrave; sinh th&aacute;i hải dương Andrew Bauman, một th&agrave;nh vi&ecirc;n trong nh&oacute;m nghi&ecirc;n cứu, ph&aacute;t biểu.</p>\n<p class="Normal">Hoạt động của con người l&agrave; nguy&ecirc;n nh&acirc;n khiến tảo ph&aacute;t triển mạnh trong thời gian gần đ&acirc;y. Ch&uacute;ng b&ugrave;ng nổ nhờ nước chứa ph&acirc;n b&oacute;n h&oacute;a học v&agrave; c&aacute;c loại chất thải gi&agrave;u nitơ m&agrave; những d&ograve;ng s&ocirc;ng mang ra biển.</p>\n<p class="Normal">Nhiều lo&agrave;i tảo độc c&oacute; thể g&acirc;y t&aacute;c hại to lớn đối với hệ sinh th&aacute;i. Ch&uacute;ng mắc v&agrave;o mang c&aacute; khiến c&aacute; chết, l&agrave;m giảm chất lượng nước, cướp kh&iacute; oxy của c&aacute;c lo&agrave;i sinh vật biển.</p>\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 1346, 0, 1),
(675, 'Chất thải bôxit gây bỏng da, ung thư phổi', 'Ngoài khả năng giết chết toàn bộ cá trong sông, chất thải bô xit tại Hungary còn có thể gây ung thư và bỏng cho người, biến đất trồng thành đất hoang hóa và phá hoại hệ sinh thái trên diện rộng.', 'Marcal.jpg', NULL, '<p class="Title">Chất thải b&ocirc;xit g&acirc;y bỏng da, ung thư phổi</p>\r\n<p class="Lead">Ngo&agrave;i khả năng giết chết to&agrave;n bộ c&aacute; trong s&ocirc;ng, chất thải b&ocirc; xit tại Hungary c&ograve;n c&oacute; thể g&acirc;y ung thư v&agrave; bỏng cho người, biến đất trồng th&agrave;nh đất hoang h&oacute;a v&agrave; ph&aacute; hoại hệ sinh th&aacute;i tr&ecirc;n diện rộng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Marcal.jpg" alt="Những người t&igrave;nh nguyện vớt x&aacute;c c&aacute; tr&ecirc;n s&ocirc;ng Marcal tại Hungary h&ocirc;m 7/10. Ảnh: AFP." width="450" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những người t&igrave;nh nguyện vớt x&aacute;c c&aacute; tr&ecirc;n s&ocirc;ng Marcal tại Hungary h&ocirc;m 7/10. Ảnh:&nbsp;<em>AFP</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chất thải từ b&ocirc;x&iacute;t của một nh&agrave; m&aacute;y sản xuất nh&ocirc;m ở miền t&acirc;y Hungary h&ocirc;m 4/10 tấn c&ocirc;ng ba thị trấn, giết chết 4 người v&agrave; l&agrave;m &iacute;t nhất 120 người bị thương. H&ocirc;m qua b&ugrave;n độc đ&atilde; tới s&ocirc;ng Danube, Raab v&agrave; Marcal. To&agrave;n bộ c&aacute; trong s&ocirc;ng Marcal đ&atilde; bị giết chết.</p>\r\n<p class="Normal"><em>Livescience</em>&nbsp;đưa tin c&ocirc;ng nh&acirc;n đang đổ thạch cao xuống s&ocirc;ng Marcal để l&agrave;m đ&ocirc;ng đặc b&ugrave;n đỏ. Họ cũng d&ugrave;ng h&oacute;a chất để trung h&ograve;a c&aacute;c chất độc. Cơ quan Cứu hộ quốc gia Hungary th&ocirc;ng b&aacute;o c&aacute;c kỹ sư đề nghị chỉnh d&ograve;ng chảy của s&ocirc;ng Marcal để nước chảy v&agrave;o những c&aacute;nh đồng. Tuy nhi&ecirc;n, giới chức phản đối kế hoạch v&igrave; sợ rằng h&agrave;nh động đ&oacute; sẽ g&acirc;y n&ecirc;n t&aacute;c hại lớn hơn.</p>\r\n<p class="Normal">B&ugrave;n đỏ l&agrave; sản phẩm được thải ra trong qu&aacute; tr&igrave;nh xử l&yacute; b&ocirc;x&iacute;t để sản xuất nh&ocirc;m. Loại b&ugrave;n n&agrave;y thường được chứa trong c&aacute;c bể, hồ. Ở trạng th&aacute;i b&ugrave;n, chất thải c&oacute; thể ph&aacute; hủy quần &aacute;o v&agrave; g&acirc;y bỏng tr&ecirc;n da. Sau khi nước trong b&ugrave;n bốc hơi, b&ugrave;n biến th&agrave;nh đất kh&ocirc; m&agrave;u đỏ giống đất s&eacute;t. Những hạt bụi si&ecirc;u nhỏ tạo n&ecirc;n loại đất đ&oacute; c&oacute; thể bay lơ lửng trong kh&ocirc;ng kh&iacute;. Nếu người d&acirc;n h&iacute;t phải ch&uacute;ng, họ sẽ c&oacute; nguy cơ mắc ung thư phổi rất cao. C&aacute;c tổ chức m&ocirc;i trường cho rằng, sau thảm họa n&agrave;y c&aacute;c ch&iacute;nh phủ n&ecirc;n cấm việc chứa chất thải b&ocirc; xit trong c&aacute;c bể lộ thi&ecirc;n.</p>\r\n<p class="Normal">Giới chuy&ecirc;n gia vẫn chưa biết hết t&ecirc;n của những chất độc trong b&ugrave;n đỏ. C&aacute;c quan chức cho rằng trong số chất độc c&oacute; arsen (thạch t&iacute;n) v&agrave; crom. Chưa ai dự đo&aacute;n được những t&aacute;c động l&acirc;u d&agrave;i của b&ugrave;n đỏ.</p>\r\n<p class="Normal">Giới chức Hungary v&agrave; c&aacute;c chuy&ecirc;n gia nhận định việc l&agrave;m sạch b&ugrave;n v&agrave; phục hồi cuộc sống tại những v&ugrave;ng bị b&ugrave;n đỏ tấn c&ocirc;ng sẽ k&eacute;o d&agrave;i v&agrave;i năm. Nước uống c&oacute; thể nhiễm độc do h&oacute;a chất ngấm xuống đất v&agrave; mạch nước ngầm. Người d&acirc;n cũng sẽ kh&ocirc;ng thể trồng trọt hay sản xuất n&ocirc;ng nghiệp trong nhiều năm.</p>\r\n<p class="Normal">B&aacute;o&nbsp;<em>New Zealand Herald</em>&nbsp;cho hay, c&aacute;c tổ chức bảo vệ m&ocirc;i trường lo ngại b&ugrave;n đỏ c&oacute; thể g&acirc;y n&ecirc;n hậu quả lớn đối với hệ sinh th&aacute;i biển quốc tế, bởi s&ocirc;ng Danube chảy qua Hungary, Croatia, Serbia, Romania, Bulgaria, Bulgaria, Ukraine v&agrave; Moldova trước khi đổ v&agrave;o Biển Đen.</p>\r\n<p class="Normal">&ldquo;Thảm họa tại Hungary c&oacute; thể vượt qua bi&ecirc;n giới nước n&agrave;y&rdquo;, &ocirc;ng Joe Hennon, người ph&aacute;t ng&ocirc;n của Li&ecirc;n minh ch&acirc;u &Acirc;u, n&oacute;i.</p>\r\n<p class="Normal">Li&ecirc;n minh ch&acirc;u &Acirc;u từng k&ecirc;u gọi giới chức Hungary thực hiện mọi biện ph&aacute;p để ngăn cho b&ugrave;n kh&ocirc;ng tới s&ocirc;ng Danube, nhưng điều họ kh&ocirc;ng mong muốn đ&atilde; xảy ra.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 2123, 0, 1),
(676, 'Bùn độc tràn tới sông dài thứ hai của châu Âu', 'Ngày 7/10, chất thải bôxit tràn ra từ bể chứa khổng lồ tại Hungary đã tới sông Danube, đe dọa hệ sinh thái của dòng sông này.', 'Red-mud.jpg', NULL, '<p class="Title">B&ugrave;n độc tr&agrave;n tới s&ocirc;ng d&agrave;i thứ hai của ch&acirc;u &Acirc;u</p>\n<p class="Lead">Ng&agrave;y 7/10, chất thải b&ocirc;xit tr&agrave;n ra từ bể chứa khổng lồ tại Hungary đ&atilde; tới s&ocirc;ng Danube, đe dọa hệ sinh th&aacute;i của d&ograve;ng s&ocirc;ng n&agrave;y.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="Red-mud.jpg" alt="Người d&acirc;n" width="450" height="300" /></td>\n</tr>\n<tr>\n<td class="Image">Người d&acirc;n v&agrave; nh&acirc;n vi&ecirc;n ch&iacute;nh phủ dọn dẹp trong l&agrave;ng Kolontar ở ph&iacute;a t&acirc;y Hungary sau khi b&ugrave;n đỏ từ bể chứa chất thải b&ocirc;xit tr&agrave;n qua l&agrave;ng. ẢNh:&nbsp;<em>AFP</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Bể chứa chất thải b&ocirc;x&iacute;t khổng lồ của một nh&agrave; m&aacute;y sản xuất nh&ocirc;m tại thị trấn Ajka ở miền t&acirc;y Hungary đột nhi&ecirc;n vỡ khiến h&agrave;ng triệu l&iacute;t chất thải tr&agrave;n xuống 7 l&agrave;ng ở vị tr&iacute; thấp hơn. Chất thải lan ra một khu vực c&oacute; diện t&iacute;ch 40 km2 khiến 4 người chết, 123 người bị thương v&agrave; 3 người mất t&iacute;ch. Đ&acirc;y l&agrave; thảm họa h&oacute;a chất nghi&ecirc;m trọng nhất trong lịch sử Hungary.</p>\n<p class="Normal"><em>AFP</em>&nbsp;cho biết, b&ugrave;n độc tr&agrave;n tới s&ocirc;ng Raab v&agrave; Danube tại th&agrave;nh phố Gyor v&agrave;o khoảng 13h30 theo giờ H&agrave; Nội. Một quan chức quản l&yacute; nước của Hungary th&ocirc;ng b&aacute;o mức độ &ocirc; nhiễm của nước s&ocirc;ng Danube đ&atilde; vượt mức th&ocirc;ng thường.</p>\n<p class="Normal">&ldquo;Kết quả ph&acirc;n t&iacute;ch nhiều mẫu nước ở đoạn hợp lưu của s&ocirc;ng Raab v&agrave; s&ocirc;ng Danube cho thấy nồng độ kiềm trong nước tăng th&ecirc;m 8,96 tới 9,07%&rdquo;, vị quan chức giấu t&ecirc;n n&oacute;i.</p>\n<p class="Normal">Nồng độ kiềm l&agrave; một trong những ti&ecirc;u ch&iacute; được d&ugrave;ng đ&aacute;nh gi&aacute; mức độ &ocirc; nhiễm của nước. Chất lượng nước được coi l&agrave; b&igrave;nh thường nếu nồng độ kiềm bằng hoặc dưới 8%.</p>\n<p class="Normal">&Ocirc;ng Gbor Figeczky, quyền gi&aacute;m đốc điều h&agrave;nh của Quỹ Bảo tồn thi&ecirc;n nhi&ecirc;n quốc tế (WWF) tại Hungary, cảnh b&aacute;o rằng sau khi tr&agrave;n xuống s&ocirc;ng Danube, chất thải b&ocirc;x&iacute;t c&oacute; thể g&acirc;y n&ecirc;n t&aacute;c hại c&oacute; quy m&ocirc; quốc tế.</p>\n<p class="Normal">&ldquo;Một số lo&agrave;i động vật v&agrave; thực vật chết ngay lập tức, một số lo&agrave;i kh&aacute;c sẽ bị nhiễm độc trong thời gian d&agrave;i do b&ugrave;n độc t&iacute;ch tụ trong cơ thể ch&uacute;ng&rdquo;, &ocirc;ng n&oacute;i.</p>\n<p class="Normal">WWF h&ocirc;m qua cũng tuy&ecirc;n bố b&ugrave;n độc tại Hungary sẽ để lại t&aacute;c động l&acirc;u d&agrave;i trong đất, nước, hệ động vật v&agrave; hệ thực vật.</p>\n<p class="Normal">B&ocirc;xit l&agrave; một loại quặng nh&ocirc;m trầm t&iacute;ch c&oacute; m&agrave;u hồng, n&acirc;u được h&igrave;nh th&agrave;nh từ qu&aacute; tr&igrave;nh phong h&oacute;a c&aacute;c đ&aacute; gi&agrave;u nh&ocirc;m hoặc t&iacute;ch tụ từ c&aacute;c quặng c&oacute; trước bởi qu&aacute; tr&igrave;nh x&oacute;i m&ograve;n. Chất thải từ b&ocirc;xit chứa nhiều kim loại nặng cực độc.</p>\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 84, 0, 1),
(678, 'Có nên mở rộng đánh thuế môi trường?', 'Tại Hội thảo phản biện về Luật thuế Môi trường hôm qua, nhiều nhà khoa học cho rằng, còn nhiều mặt hàng tác động xấu đến môi trường như: pin, ắc quy, vải, đồ da…vẫn chưa chịu thuế môi trường.', '1_6.jpg', NULL, '<p class="Title">C&oacute; n&ecirc;n mở rộng đ&aacute;nh thuế m&ocirc;i trường?</p>\r\n<p class="Lead">Tại Hội thảo phản biện về Luật thuế M&ocirc;i trường h&ocirc;m qua, nhiều nh&agrave; khoa học cho rằng, c&ograve;n nhiều mặt h&agrave;ng t&aacute;c động xấu đến m&ocirc;i trường như: pin, ắc quy, vải, đồ da&hellip;vẫn chưa chịu thuế m&ocirc;i trường.</p>\r\n<p class="Normal">Dự thảo luật thuế m&ocirc;i trường mới đ&acirc;y chỉ quy định 8 nh&oacute;m đối tượng thịu thuế l&agrave; xăng dầu, than, dung dịch HCFC, t&uacute;i ni l&ocirc;ng, thuốc diệt cỏ thuộc loại hạn chế sử dụng, thuốc trừ mối thuộc loại hạn chế sử dụng, thuốc bảo quản l&acirc;m sản thuộc loại hạn chế sử dụng, v&agrave; thuốc khử tr&ugrave;ng kho thuộc loại hạn chế sử dụng.</p>\r\n<p class="Normal">Trong khi đ&oacute;, c&ograve;n nhiều mặt h&agrave;ng kh&aacute;c t&aacute;c động xấu đến m&ocirc;i trường vẫn chưa chịu thuế như: Pin, ắc quy, m&aacute;y t&iacute;nh điện tử, vải, đồ da, c&aacute;c sản phẩm du lịch (nhất l&agrave; lữ h&agrave;nh), nước uống đ&oacute;ng chai&hellip;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="1_6.jpg" border="1" alt="Ảnh" width="472" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nhiều mặt h&agrave;ng g&acirc;y &ocirc; nhiễm m&ocirc;i trường như ắc quy vẫn chưa chịu thuế m&ocirc;i trường. Ảnh: Viettogo.com</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cũng trong hội thảo, PGS.TS Nguyễn Đ&igrave;nh H&ograve;e, trưởng ban phản biện x&atilde; hội, Hội Bảo vệ Thi&ecirc;n nhi&ecirc;n &amp;M&ocirc;i trường Việt Nam, đưa ra &yacute; kiến cần phải đ&aacute;nh gi&aacute; mức độ g&acirc;y &ocirc; nhiễm v&agrave; suy tho&aacute;i m&ocirc;i trường của sản phẩm để l&agrave;m cơ sở cho việc t&iacute;nh thuế như nguy&ecirc;n tắc t&iacute;nh thuế do dự thảo n&ecirc;u ra l&agrave; mức thuế đối với h&agrave;ng h&oacute;a chịu thuế bảo vệ m&ocirc;i trường được x&acirc;y dựng ph&acirc;n biệt theo mức độ g&acirc;y t&aacute;c động xấu đến m&ocirc;i trường của h&agrave;ng h&oacute;a.</p>\r\n<p class="Normal">Theo nguy&ecirc;n tắc n&agrave;y, mức thuế đ&aacute;ng l&yacute; phải căn cứ v&agrave;o h&agrave;m lượng c&aacute;c chất độc hại trong h&agrave;ng h&oacute;a, tuy nhi&ecirc;n, thuế lại được thu theo nguy&ecirc;n tắc mức thuế tuyệt đối. Đơn cử, đối với nhi&ecirc;n liệu đốt, thuế suất nhiều hay &iacute;t của xăng dầu phụ thuộc v&agrave;o h&agrave;m lượng kh&iacute; thải SO2 thải ra khi đốt hoặc h&agrave;m lượng lưu huỳnh trong xăng dầu.</p>\r\n<p class="Normal">Thế nhưng, trong dự thảo, mức thuế suất đối với xăng (v&iacute; dụ xăng A92 vốn c&oacute; h&agrave;m lượng lưu h&ugrave;ynh chỉ 0,05%) lại cao hơn dầu mazut (vốn c&oacute; h&agrave;m lượng lưu huỳnh l&ecirc;n tới 3%) l&agrave; kh&ocirc;ng hợp l&yacute;.</p>\r\n<p class="Normal">Tương tự, than l&agrave; mặt h&agrave;ng cần đ&aacute;nh thuế. Mức độ g&acirc;y t&aacute;c động xấu đến m&ocirc;i trường của than gầy, than mỡ, than n&acirc;u rất kh&aacute;c nhau lại c&ugrave;ng một mức thuế v&agrave; tuy sử dụng than g&acirc;y hại cho m&ocirc;i trường hơn xăng dầu nhưng mức thuế lại thấp hơn xăng dầu.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n dự thảo luật thuế cũng gặp phải &yacute; kiến phản đối. Thạc sĩ V&otilde; Thị Mỹ Hương, khoa luật, Đại học Huế n&ecirc;u quan điểm, cần xem x&eacute;t kỹ lưỡng việc c&oacute; n&ecirc;n ban h&agrave;nh Luật thuế m&ocirc;i trường v&agrave;o l&uacute;c n&agrave;y, hay ban h&agrave;nh trong v&agrave;i năm tới.</p>\r\n<p class="Normal">ThS Hương đưa ra dẫn chứng, tr&ecirc;n thế giới c&oacute; 30 nước đ&aacute;nh thuế m&ocirc;i trường nhưng đại bộ phận l&agrave; những nước c&ocirc;ng nghiệp ph&aacute;t triển, người d&acirc;n c&oacute; thu nhập rất cao. Chỉ c&oacute; s&aacute;u nước l&agrave; Mexico, Congo, Philippines, Malawi, Zimbabwe, v&agrave; Trung Quốc l&agrave; thuộc diện c&aacute;c nước đang ph&aacute;t triển. Tuy nhi&ecirc;n, thuế m&ocirc;i trường ở s&aacute;u nước đang ph&aacute;t triển n&agrave;y chỉ đ&aacute;nh v&agrave;o 1 &ndash; 2 mặt h&agrave;ng, chủ yếu l&agrave; thuốc trừ s&acirc;u, nhất l&agrave; đ&aacute;nh v&agrave;o loại thuốc trừ s&acirc;u độc hại. Ri&ecirc;ng Trung Quốc cũng chỉ mới đ&aacute;nh v&agrave;o than đ&aacute; v&agrave; t&uacute;i ni l&ocirc;ng loại mỏng</p>\r\n<p class="Normal">&ldquo;Kh&ocirc;ng c&oacute; nước đang ph&aacute;t triển n&agrave;o, kể cả Trung Quốc, đ&aacute;nh thuế m&ocirc;i trường v&agrave;o xăng dầu. Ngay cả Nhật Bản l&agrave; nước c&oacute; nền kinh tế ph&aacute;t triển cao cũng đ&atilde; quyết định tạm dừng thu&ecirc; thuế m&ocirc;i trường trong năm t&agrave;i kho&aacute;i bắt đầu từ th&aacute;ng 4/2010&rdquo;.</p>\r\n<p class="Normal">Theo c&aacute;c nh&agrave; khoa học, nếu thực hiện Dự thảo thuế m&ocirc;i trường trong giai đoạn hiện nay c&oacute; thể sẽ g&acirc;y kh&oacute; khăn cho sự nghiệp x&oacute;a đ&oacute;i giảm ngh&egrave;o, nhất l&agrave; khu vực n&ocirc;ng th&ocirc;n. Thuế bảo vệ m&ocirc;i trường &aacute;p dụng cho to&agrave;n thể nh&acirc;n d&acirc;n, trong khi thực tế họ kh&aacute;c hẳn nhau về mức sống, kinh tế &hellip;Nếu chia trung b&igrave;nh gi&aacute; trị thuế bảo vệ m&ocirc;i trường theo Dự thảo h&agrave;ng năm l&agrave; 57.000 tỷ đồng cho 40 triệu d&acirc;n lao động th&ugrave; thuế m&ocirc;i trường b&igrave;nh qu&acirc;n l&agrave; sẽ l&agrave; 1, 4 triệu/người/năm, tương đương khoảng 7 th&aacute;ng thu nhập người ngh&egrave;o n&ocirc;ng th&ocirc;n, v&agrave; khoảng 5,5 th&aacute;ng thu nhập của người ngh&egrave;o đ&ocirc; thị.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hương Thu</strong></p>', 33, 8, 667, 0, 1),
(679, 'Sản xuất thủy điện, nước sạch phải trả phí môi trường', 'Từ ngày 1/1/2011, các cơ sở sản xuất thủy điện, nước sạch, công nghiệp, kinh doanh du lịch hưởng lợi từ môi trường rừng... phải chi trả tiền để bảo vệ môi trường. Còn người trồng rừng sẽ nhận được tiền công.', 'Thuy-dien.jpg', NULL, '<p class="Title">Sản xuất thủy điện, nước sạch phải trả ph&iacute; m&ocirc;i trường</p>\n<p class="Lead">Từ ng&agrave;y 1/1/2011, c&aacute;c cơ sở sản xuất thủy điện, nước sạch, c&ocirc;ng nghiệp, kinh doanh du lịch hưởng lợi từ m&ocirc;i trường rừng... phải chi trả tiền để bảo vệ m&ocirc;i trường. C&ograve;n người trồng rừng sẽ nhận được tiền c&ocirc;ng.</p>\n<p class="Normal">Theo Nghị định 99 về ch&iacute;nh s&aacute;ch chi trả dịch vụ m&ocirc;i trường rừng, từ ng&agrave;y 1/1/2011, c&aacute;c cơ sở sản xuất thủy điện<em>&nbsp;</em>phải chi trả tiền dịch vụ về bảo vệ đất, hạn chế x&oacute;i m&ograve;n v&agrave; bồi lắng l&ograve;ng hồ, l&ograve;ng s&ocirc;ng, l&ograve;ng suối; về điều tiết v&agrave; duy tr&igrave; nguồn nước cho sản xuất thủy điện. Mức chi trả tiền dịch vụ m&ocirc;i trường rừng đối với c&aacute;c cơ sở n&agrave;y l&agrave; 20 đồng một 1kwh điện thương phẩm.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="Thuy-dien.jpg" alt="Ảnh: Ho&agrave;ng H&agrave;." width="480" height="360" /></td>\n</tr>\n<tr>\n<td class="Image">Hệ thống truyền tải điện tại Nh&agrave; m&aacute;y Thủy điện H&ograve;a B&igrave;nh. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">C&aacute;c<em>&nbsp;</em>cơ sở sản xuất v&agrave; cung ứng nước sạch, phải chi trả tiền dịch vụ về điều tiết v&agrave; duy tr&igrave; nguồn nước cho sản xuất nước sạch với mức 40 đồng/m3 nước thương phẩm. Cơ sở sản xuất c&ocirc;ng nghiệp<em>&nbsp;</em>sử dụng nước trực tiếp từ nguồn nước phải trả tiền dịch vụ về điều tiết v&agrave; duy tr&igrave; nguồn nước cho sản xuất. Mức chi trả n&agrave;y sẽ do Bộ NN&amp;PTNT phối hợp với c&aacute;c bộ, ng&agrave;nh li&ecirc;n quan tr&igrave;nh Thủ tướng quy định.</p>\n<p class="Normal">Ngo&agrave;i ra, c&aacute;c tổ chức, c&aacute; nh&acirc;n kinh doanh dịch vụ du lịch<em>&nbsp;</em>c&oacute; hưởng lợi từ dịch vụ m&ocirc;i trường rừng sẽ phải trả tiền dịch vụ m&ocirc;i trường rừng t&iacute;nh bằng 1-2% tr&ecirc;n doanh thu thực hiện trong kỳ. Dịch vụ hấp thụ v&agrave; lưu giữ c&aacute;c bon của rừng, cung ứng b&atilde;i đẻ, nguồn thức ăn v&agrave; con giống tự nhi&ecirc;n, sử dụng nguồn nước từ rừng cho nu&ocirc;i trồng thủy sản. cũng phải trả tiền dịch vụ m&ocirc;i trường.</p>\n<p class="Normal">Trong khi đ&oacute;, chủ c&aacute;c khu rừng c&oacute; cung ứng dịch vụ m&ocirc;i trường rừng v&agrave; c&aacute;c tổ chức, hộ gia đ&igrave;nh, c&aacute; nh&acirc;n, cộng đồng d&acirc;n cư th&ocirc;n c&oacute; hợp đồng nhận kho&aacute;n bảo vệ rừng<em>&nbsp;</em>ổn định l&acirc;u d&agrave;i với c&aacute;c chủ rừng l&agrave; tổ chức nh&agrave; nước sẽ được trả tiền dịch vụ m&ocirc;i trường rừng. Việc chi trả được thực hiện trực tiếp hoặc th&ocirc;ng qua Quỹ bảo vệ v&agrave; ph&aacute;t triển rừng Việt Nam hoặc Quỹ bảo vệ v&agrave; ph&aacute;t triển rừng cấp tỉnh.</p>\n<p class="Normal" style="text-align: right;"><strong>Anh Khoa</strong></p>', 33, 8, 1094, 0, 1),
(680, 'Ô nhiễm không khí trong mắt trẻ thơ', 'Môi trường trong tình trạng SOS, cháy rừng, rác thải ngập tràn trên các đường phố đã được các em học sinh thể hiện qua các nét vẽ hồn nhiên.', 'IMG_4003.JPG', NULL, '<p class="Title">&Ocirc; nhiễm kh&ocirc;ng kh&iacute; trong mắt trẻ thơ</p>\n<p class="Lead">M&ocirc;i trường trong t&igrave;nh trạng SOS, ch&aacute;y rừng, r&aacute;c thải ngập tr&agrave;n tr&ecirc;n c&aacute;c đường phố đ&atilde; được c&aacute;c em học sinh thể hiện qua c&aacute;c n&eacute;t vẽ hồn nhi&ecirc;n.</p>\n<p class="Normal">H&ocirc;m qua lễ trao giải cuộc thi Vẽ tranh d&agrave;nh cho thiếu nhi với chủ đề "&Ocirc; nhiễm kh&ocirc;ng kh&iacute;" đ&atilde; diễn ra tại H&agrave; Nội. Mười s&aacute;u giải thưởng đ&atilde; được trao cho 16 em học sinh đến từ khắp đất nước.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="IMG_4003.JPG" border="1" alt="Em Nguyễn Ch&acirc;u Hải My nhận phần thưởng cho giải nhất của m&igrave;nh. Ảnh: G.H" width="480" height="467" /></td>\n</tr>\n<tr>\n<td class="Image">Hải My v&agrave; mẹ đi từ Huế ra H&agrave; Nội nhận giải thưởng. Hai mẹ con chụp ảnh b&ecirc;n những bức tranh đoạt giải. Ảnh: Gia H&ograve;a</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Được ph&aacute;t động từ đầu th&aacute;ng 4/2010, cuộc thi Vẽ tranh d&agrave;nh cho thiếu nhi với chủ đề "&Ocirc; nhiễm kh&ocirc;ng kh&iacute;" do Trung t&acirc;m Gi&aacute;o dục Thi&ecirc;n nhi&ecirc;n, h&atilde;ng khẩu trang Karibon v&agrave; h&atilde;ng thời trang Boo đồng tổ chức với mục đ&iacute;ch khuyến kh&iacute;ch c&aacute;c em học sinh chia sẻ những g&oacute;c nh&igrave;n, những cảm nhận rất ri&ecirc;ng của bản th&acirc;n về t&igrave;nh trạng &ocirc; nhiễm kh&ocirc;ng kh&iacute; ng&agrave;y c&agrave;ng gia tăng hiện nay. C&oacute; 335 bức vẽ của c&aacute;c em học sinh thuộc khắp c&aacute;c tỉnh th&agrave;nh gửi về dự thi.</p>\n<p class="Normal">Dưới đ&acirc;y l&agrave; một số bức vẽ đoạt giải.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/0E/67/Bia%203%201.jpg" border="1" alt="Bức tranh đạt giải nhất của em Nguyễn Ch&acirc;u Hải My, học sinh lớp Nawm1, Trường Tiểu học số 1, Lộc Tr&igrave;, Ph&uacute; Lộc, Huế." width="480" height="432" /></td>\n</tr>\n<tr>\n<td class="Image">Giải nhất, Nguyễn Ch&acirc;u Hải My, học sinh lớp Năm 1, Trường Tiểu học số 1, Lộc Tr&igrave;, Ph&uacute; Lộc, Huế.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/0E/67/Bia%203%202a.jpg" border="1" alt="Bức tranh đạt giải nh&igrave; của em Nguyễn V&acirc;n Nhi, lớp 7G, THCS Nguyễn Du, Phương Mai, Đống Đa, H&agrave; Nội" width="480" height="452" /></td>\n</tr>\n<tr>\n<td class="Image">Giải nh&igrave; của em Nguyễn V&acirc;n Nhi, lớp 7G, THCS Nguyễn Du, Phương Mai, Đống Đa, H&agrave; Nội.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/0E/67/Bia%203%202b.jpg" border="1" alt="Bức tranh đạt giải nh&igrave; thứ hai của em Đ&agrave;o Th&ugrave;y Dung, lớp 8C, THCS S&agrave;i Đồng, Long Bi&ecirc;n, H&agrave; Nội" width="480" height="453" /></td>\n</tr>\n<tr>\n<td class="Image">Tranh của Đ&agrave;o Th&ugrave;y Dung, lớp 8C, THCS S&agrave;i Đồng, Long Bi&ecirc;n, H&agrave; Nội. Giải nh&igrave;.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/0E/67/Bia%203%203a.jpg" border="1" alt="Em Huỳnh Thị Tr&iacute;, lớp 8A, THCS Cần Thạnh, Cần Giờ, TP Hồ Ch&iacute; Minh với bức tranh mi&ecirc;u tả về nạn ch&aacute;y rừng." width="480" height="441" /></td>\n</tr>\n<tr>\n<td class="Image">Em Huỳnh Thị Tr&iacute;, lớp 8A, THCS Cần Thạnh, Cần Giờ, TP Hồ Ch&iacute; Minh vẽ tranh mi&ecirc;u tả về nạn ch&aacute;y rừng.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/0E/67/Bia%203%203b.jpg" border="1" alt="Tranh của em Ho&agrave;ng Ngọc Trang, THCS Nguyễn Du, Trần Kh&aacute;t Ch&acirc;n, H&agrave; Nội thể hiện sự đối lập của m&ocirc;i trường trong hai khoảng thời gian trước đ&acirc;y v&agrave; hiện tại. Bức tranh đoạt giải ba của cuộc thi." width="480" height="470" /></td>\n</tr>\n<tr>\n<td class="Image">Tranh của em Ho&agrave;ng Ngọc Trang, THCS Nguyễn Du, Trần Kh&aacute;t Ch&acirc;n, H&agrave; Nội thể hiện sự đối lập của m&ocirc;i trường trong hai khoảng thời gian trước đ&acirc;y v&agrave; hiện tại. Giải ba.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/0E/67/Bia%203%203c.jpg" border="1" alt="Những t&ograve;a nh&agrave; nguy nga v&agrave; đường phố đầy ngập r&aacute;c trong tranh của em U&ocirc;ng Gia Bảo, THCS S&agrave;i Đồng, Long Bi&ecirc;n, H&agrave; Nội" width="480" height="465" /></td>\n</tr>\n<tr>\n<td class="Image">Những t&ograve;a nh&agrave; nguy nga v&agrave; đường phố đầy ngập r&aacute;c trong tranh của em U&ocirc;ng Gia Bảo, THCS S&agrave;i Đồng, Long Bi&ecirc;n, H&agrave; Nội.</td>\n</tr>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n</tbody>\n</table>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n</tbody>\n</table>\n<p class="Normal" style="text-align: right;"><strong>Gia H&ograve;a</strong></p>', 33, 8, 2146, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(681, 'Hà Nội tăng gấp đôi khả năng bơm thoát nước', 'Trạm bơm Yên Sở giai đoạn 2, vừa được đưa vào sử dụng, dự kiến nâng gần gấp đôi khả năng thoát nước cho thành phố.', '2 (1)_1.jpg', NULL, '<p class="Title">H&agrave; Nội tăng gấp đ&ocirc;i khả năng bơm tho&aacute;t nước</p>\r\n<p class="Lead">Trạm bơm Y&ecirc;n Sở giai đoạn 2, vừa được đưa v&agrave;o sử dụng, dự kiến n&acirc;ng gần gấp đ&ocirc;i khả năng tho&aacute;t nước cho th&agrave;nh phố.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="2 (1)_1.jpg" border="1" alt="Trạm bơm Y&ecirc;n Sở đi v&agrave;o hoạt động sẽ l&agrave;m giảm thiểu ngập lụt. Ảnh: Ho&agrave;ng H&agrave;" width="476" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trạm bơm Y&ecirc;n Sở đi v&agrave;o hoạt động sẽ l&agrave;m giảm thiểu ngập lụt. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Như vậy, c&ocirc;ng suất của trạm bơm Y&ecirc;n Sở nay l&ecirc;n th&agrave;nh 90 m&eacute;t khối/gi&acirc;y, tăng gấp đ&ocirc;i so với c&ocirc;ng suất của giai đoạn một. Trạm bơm l&agrave; hạng mục quan trọng nhất trong dự &aacute;n ti&ecirc;u tho&aacute;t nước lớn nhất của H&agrave; Nội, kh&aacute;nh th&agrave;nh giai đoạn hai h&ocirc;m thứ bảy.</p>\r\n<p class="Normal">Giai đoạn một của trạm bơm Y&ecirc;n Sở, ho&agrave;n th&agrave;nh năm 2005, c&oacute; khả năng ti&ecirc;u tho&aacute;t nước nếu c&oacute; mưa với cường độ 170 mm trong hai ng&agrave;y.</p>\r\n<p class="Normal">Mục ti&ecirc;u của giai đoạn hai l&agrave; bơm ti&ecirc;u tho&aacute;t lượng mưa 310 mm trong hai ng&agrave;y.</p>\r\n<p class="Normal">Ph&oacute; Chủ tịch UBND TP Nguyễn Văn Kh&ocirc;i đ&aacute;nh gi&aacute;, dự &aacute;n n&agrave;y ho&agrave;n th&agrave;nh trước tiến độ 2 th&aacute;ng, c&oacute; &yacute; nghĩa quan trọng trong việc giải quyết t&igrave;nh trạng &uacute;ng ngập khu vực nội th&agrave;nh khi c&oacute; mưa b&atilde;o.</p>\r\n<p class="Normal">H&agrave; Nội vẫn thường xuy&ecirc;n &uacute;ng ngập mỗi khi c&oacute; mưa lớn, điển h&igrave;nh l&agrave; trận mưa lụt lịch sử vào th&aacute;ng 11/2008. Giai đoạn hai c&ocirc;ng tr&igrave;nh trạm bơm Y&ecirc;n Sở được khởi c&ocirc;ng ngay sau trận lụt n&agrave;y.</p>\r\n<p class="Normal">Khi hỏi về khả năng chống ngập &uacute;ng của H&agrave; Nội, &ocirc;ng Phạm Văn Cường, Gi&aacute;m đốc Ban quản l&yacute; dự &aacute;n tho&aacute;t nước H&agrave; Nội khẳng định, nếu lượng mưa &iacute;t hơn 310mm/2 ng&agrave;y th&igrave; H&agrave; Nội sẽ kh&ocirc;ng bị &uacute;ng ngập.</p>\r\n<p class="Normal">Nếu so với trận mưa đại hồng thủy năm 2008 với lượng mưa xấp xỉ 600 mm, &ocirc;ng Cường cho rằng, kh&ocirc;ng thể n&oacute;i l&agrave; H&agrave; Nội sẽ kh&ocirc;ng bị ngập, nhưng thời gian gian r&uacute;t nước sẽ nhanh. Trong trận mưa lớn n&oacute;i tr&ecirc;n, phải mất bốn ng&agrave;y nhiều khu vực mới r&uacute;t hết nước. C&ograve;n với khả năng ti&ecirc;u tho&aacute;t như hiện nay, chỉ cần một ng&agrave;y hoặc &iacute;t hơn, &ocirc;ng Cường khẳng định.</p>\r\n<p class="Normal">Trạm bơm Y&ecirc;n Sở l&agrave; nơi chứa nước từ c&aacute;c tuyến s&ocirc;ng như s&ocirc;ng T&ocirc; Lịch, Kim Ngưu v&agrave; c&aacute;c k&ecirc;nh khẩn cấp, k&ecirc;nh th&ocirc;ng thường. Mỗi trận mưa, trạm bơm n&agrave;y thu nước từ c&aacute;c s&ocirc;ng, k&ecirc;nh, mương đưa về rồi bơm đổ ra s&ocirc;ng Hồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hương Thu</strong></p>', 33, 8, 1773, 0, 1),
(682, 'Đi bộ vì môi trường', 'Lần đầu được tổ chức tại TP Hội An, Quảng Nam, “Ngày đi bộ vì môi trường” diễn ra vào 26/9 dự kiến thu hút gần 1.500 người tham gia.', 'GoGreen_350x232.jpg', NULL, '<p class="Title">Đi bộ v&igrave; m&ocirc;i trường</p>\n<p class="Lead">Lần đầu được tổ chức tại TP Hội An, Quảng Nam, &ldquo;Ng&agrave;y đi bộ v&igrave; m&ocirc;i trường&rdquo; diễn ra v&agrave;o 26/9 dự kiến thu h&uacute;t gần 1.500 người tham gia.</p>\n<p class="Normal">Hoạt động đi bộ v&igrave; m&ocirc;i trường sẽ diễn ra tr&ecirc;n đoạn đường d&agrave;i khoảng 2 km, bắt đầu từ quảng trường S&ocirc;ng Ho&agrave;i, xuy&ecirc;n qua khu phố cổ Hội An v&agrave; cầu An Hội. Đo&agrave;n đi bộ sẽ chia th&agrave;nh 10 nh&oacute;m kh&aacute;c nhau, c&oacute; khẩu hiệu v&agrave; quản tr&ograve; ri&ecirc;ng như &ldquo;V&igrave; Hội An kh&ocirc;ng kh&oacute;i xe&rdquo;, &ldquo;V&igrave; Hội An kh&ocirc;ng r&aacute;c thải&rdquo;, &ldquo;V&igrave; Hội An trong l&agrave;nh&rdquo;...</p>\n<p class="Normal">B&ecirc;n cạnh đ&oacute;, tr&ecirc;n suốt h&agrave;nh tr&igrave;nh, c&aacute;c tiết mục nhảy m&uacute;a đường phố v&agrave; hoạt động hoạt n&aacute;o sẽ diễn ra li&ecirc;n tục. Ngo&agrave;i ra, chương tr&igrave;nh c&ograve;n c&oacute; nhiều hoạt động như nhảy Green Dance - một điệu nhảy của ri&ecirc;ng C&acirc;u lạc bộ Go Green, diễu h&agrave;nh với d&agrave;n trống hội tr&ecirc;n x&iacute;ch l&ocirc;, nhảy m&uacute;a tr&ecirc;n đường phố&hellip;</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="GoGreen_350x232.jpg" border="1" alt="" width="350" height="232" /></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Ng&agrave;y hội được tổ chức bởi C&acirc;u lạc bộ Go Green &ndash; H&agrave;nh tr&igrave;nh Xanh (GGC) với sự hỗ trợ của Sở Văn h&oacute;a Thể thao v&agrave; Du lịch tỉnh Quảng Nam, UBND v&agrave; Trung t&acirc;m Văn h&oacute;a Thể thao TP Hội An.</p>\n<p class="Normal">Chương tr&igrave;nh gi&uacute;p cộng đồng c&aacute;c bạn trẻ, người d&acirc;n, du kh&aacute;ch trong nước v&agrave; quốc tế tại khu vực Đ&agrave; Nẵng &ndash; Hội An thể hiện t&igrave;nh y&ecirc;u, tr&aacute;ch nhiệm giữ g&igrave;n m&ocirc;i trường. &ldquo;Ng&agrave;y đi bộ v&igrave; m&ocirc;i trường&rdquo; l&agrave; hoạt động nằm trong khu&ocirc;n khổ của chương tr&igrave;nh Go Green &ndash; H&agrave;nh tr&igrave;nh Xanh tổng thể do C&ocirc;ng ty &Ocirc;t&ocirc; Toyota Việt Nam (TMV) khởi xướng, phối hợp thực hiện c&ugrave;ng Tổng Cục M&ocirc;i trường v&agrave; Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo.</p>\n<p class="Normal" style="text-align: right;">(Nguồn:&nbsp;<em>C&acirc;u lạc bộ Go Green &ndash; H&agrave;nh Tr&igrave;nh Xanh (GGC))</em></p>', 33, 8, 850, 0, 1),
(683, 'Hoãn dự án nạo vét hồ Gươm đến sau Đại lễ', 'Việc nạo vét hút bùn hồ Gươm sẽ lùi qua Đại lễ 1.000 năm Thăng Long – Hà Nội, ông Hà Đình Đức, chủ nhiệm Dự án "Phục hồi và ổn định bền vững hồ Gươm" cho biết hôm qua.', 'bo ho.jpg', NULL, '<p class="Title">Ho&atilde;n dự &aacute;n nạo v&eacute;t hồ Gươm đến sau Đại lễ</p>\r\n<p class="Lead">Việc nạo v&eacute;t h&uacute;t b&ugrave;n hồ Gươm sẽ l&ugrave;i qua Đại lễ 1.000 năm Thăng Long &ndash; H&agrave; Nội, &ocirc;ng H&agrave; Đ&igrave;nh Đức, chủ nhiệm Dự &aacute;n "Phục hồi v&agrave; ổn định bền vững hồ Gươm" cho biết h&ocirc;m qua.</p>\r\n<p class="Normal">Về nguy&ecirc;n nh&acirc;n, theo &ocirc;ng L&ecirc; Xu&acirc;n Rao, GĐ Sở KH&amp;CN H&agrave; Nội, do trong qu&aacute; tr&igrave;nh đ&agrave;m ph&aacute;n mua b&aacute;n thiết bị gặp phải một số kh&oacute; khăn, n&ecirc;n l&agrave;m chậm tiến tr&igrave;nh nạo v&eacute;t hồ Gươm.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bo ho.jpg" border="1" alt="" width="470" height="268" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hồ Gươm, H&agrave; Nội. Ảnh:&nbsp;<em>Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đến nay, việc đ&agrave;m ph&aacute;n giữa UBND TP v&agrave; Đức về c&ocirc;ng nghệ v&agrave; m&aacute;y m&oacute;c cơ bản đ&atilde; ho&agrave;n th&agrave;nh. Việc h&uacute;t b&ugrave;n trong giai đoạn tiếp theo được UBND TP giao cho c&ocirc;ng ty Tho&aacute;t nước H&agrave; Nội tiếp quản v&agrave; vận h&agrave;nh.</p>\r\n<p class="Normal">Hiện đang trong thời gian chuẩn bị Đại lễ 1.000 năm Thăng Long, việc nạo v&eacute;t hồ Gươm phải &ldquo;nhường chỗ&rdquo; cho chỉnh trang khu vực Hồ Gươm v&agrave; c&aacute;c tuyến phố kh&aacute;c.</p>\r\n<p class="Normal">Cuối th&aacute;ng 11/2009, việc thử nghiệm nạo h&uacute;t b&ugrave;n Hồ Gươm bằng c&ocirc;ng nghệ của Đức được triển khai v&agrave; cho kết quả khả quan sau 100 ng&agrave;y. Với kết quả n&agrave;y UBND TP H&agrave; Nội tiếp tục cho thực hiện Dự &aacute;n nạo v&eacute;t tr&ecirc;n to&agrave;n bộ hồ v&agrave; dự kiến đến cuối th&aacute;ng 12/2009 tiến h&agrave;nh. Nhưng kh&ocirc;ng thực hiện được, với l&yacute; do chuy&ecirc;n gia Đức về nước nghỉ Noel, dời đến th&aacute;ng 1/2009 th&igrave; ngừng trệ v&igrave; đến Tết Nguy&ecirc;n Đ&aacute;n của Việt Nam.</p>\r\n<p class="Normal">Tiếp đ&oacute;, dự &aacute;n dự kiến nạo v&eacute;t v&agrave;o th&aacute;ng 3/2010, rồi chuyển sang th&aacute;ng 7, th&aacute;ng 8/2010 vẫn chưa thể tiến h&agrave;nh, v&igrave; c&ograve;n ho&agrave;n tất thủ tục chuyển giao m&aacute;y m&oacute;c c&ocirc;ng nghệ. Đến khi thủ tục ho&agrave;n th&agrave;nh th&igrave; gặp kh&oacute; khăn do chỉnh trang Đại lễ v&agrave; nay quyết định l&ugrave;i sau Đại lễ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Hương Thu</strong></p>', 33, 8, 950, 0, 1),
(685, 'Ô nhiễm trong nhà gây tử vong cao nhất', 'Các chuyên gia cảnh báo rằng ô nhiễm không khí trong nhà đang ở mức đáng báo động, khi có tới 70% số người được hỏi trong một khảo sát phàn nàn về không khí tại nơi họ ở.', 'bep.jpg', NULL, '<p class="Title">&Ocirc; nhiễm trong nh&agrave; g&acirc;y tử vong cao nhất</p>\n<p class="Lead">C&aacute;c chuy&ecirc;n gia cảnh b&aacute;o rằng &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave; đang ở mức đ&aacute;ng b&aacute;o động, khi c&oacute; tới 70% số người được hỏi trong một khảo s&aacute;t ph&agrave;n n&agrave;n về kh&ocirc;ng kh&iacute; tại nơi họ ở.</p>\n<p class="Normal">Thạc sĩ Nguyễn Trinh Hương thuộc Viện nghi&ecirc;n cứu Khoa học kỹ thuật Bảo hộ lao động Việt Nam cho biết nếu x&eacute;t về mức độ t&aacute;c động về sức khỏe con người so với c&aacute;c loại &ocirc; nhiễm kh&aacute;c, th&igrave; &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave; c&oacute; tỷ lệ tử vong cao nhất.</p>\n<p class="Normal">Ph&aacute;t biểu n&agrave;y được đưa ra tại một hội nghị khoa học về &ocirc; nhiễm kh&ocirc;ng kh&iacute; đ&ocirc; thị, diễn ra h&ocirc;m thứ bảy.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="bep.jpg" border="1" alt="" width="480" height="318" /></td>\n</tr>\n<tr>\n<td class="Image">Việc đun nấu l&agrave; một trong những nguồn quan trọng g&acirc;y &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave;. Ảnh minh họa<em>afamily.</em></td>\n</tr>\n</tbody>\n</table>\n<p class="Normal">Hiện ở Việt Nam chưa c&oacute; nghi&ecirc;n cứu cụ thể về sự nguy hại của &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave;. Theo b&agrave; Hương, trong c&aacute;c yếu tố nguy cơ t&aacute;c động đến sức khỏe người lao động như bụi, hơi kh&iacute; độc, h&oacute;a chất, vi sinh, tiếng ồn, rung động, g&aacute;nh nặng, v&agrave; tư thế lao động&hellip; th&igrave; nh&oacute;m yếu tố g&acirc;y &ocirc; nhiễm m&ocirc;i trường nơi l&agrave;m việc ảnh hưởng nhiều nhất đến sức khỏe. Nh&oacute;m yếu tố n&agrave;y gồm bụi, hơi kh&iacute; hộc, hơi h&oacute;a chất.</p>\n<p class="Normal">Kết quả điều tra tại 10 chung cư H&agrave; Nội của Trung t&acirc;m khoa học m&ocirc;i trường v&agrave; ph&aacute;t triển Bền vững cho thấy, hơn 70% số người ph&agrave;n n&agrave;n về &ocirc; nhiễm kh&ocirc;ng kh&iacute; nơi họ ở.</p>\n<p class="Normal">Trung t&acirc;m cũng tiến h&agrave;nh điều tra tại hai t&ograve;a cao ốc ở Việt Nam thập ni&ecirc;n 2000, thấy nhiều người c&oacute; triệu chứng mần ngứa, mệt mỏi. C&aacute;c triệu chứng n&agrave;y mất đi nếu những người l&agrave;m việc trong đ&oacute; nghỉ cuối tuần hoặc nghỉ ph&eacute;p d&agrave;i ng&agrave;y.</p>\n<p class="Normal">Th&ocirc;ng thường, mức độ &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave; cao gấp 2-5 lần so với ngo&agrave;i trời, theo một nghi&ecirc;n cứu của Mỹ. Gần đ&acirc;y, Trung t&acirc;m Kiểm so&aacute;t v&agrave; Ph&ograve;ng ngừa bệnh dịch Trung Quốc vừa c&ocirc;ng bố một b&aacute;o c&aacute;o về t&aacute;c động của t&igrave;nh trạng &ocirc; nhiễm kh&ocirc;ng kh&iacute;, cho thấy mức độ &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave; thậm ch&iacute; c&ograve;n lớn gấp 5-10 lần so với kh&ocirc;ng kh&iacute; ngo&agrave;i trời.</p>\n<p class="Normal">Tổ chức Y tế thế giới cũng cho biết, &ocirc; nhiễm kh&ocirc;ng kh&iacute; trong nh&agrave; được đ&aacute;nh gi&aacute; l&agrave; vấn đề nghi&ecirc;m trọng nhất, v&igrave; c&oacute; tới 80% hoạt động của con người diễn ra trong nh&agrave;.</p>\n<p class="Normal">Tại m&ocirc;i trường lao động c&ocirc;ng nghiệp, &ocirc; nhiễm kh&ocirc;ng kh&iacute; c&oacute; thể g&acirc;y ra một số bệnh nghề nghiệp như bụi phổi silic, bụi phổi b&ocirc;ng, lao phổi, trong đ&oacute; bệnh bụi phổi silic c&oacute; thể chiếm tới 74,5% số t&iacute;ch lũy bệnh.</p>\n<p class="Normal">Theo Tổ chức Y tế Thế giới, &ocirc; nhiễm kh&ocirc;ng kh&iacute; đ&ocirc; thị l&agrave;m khoảng 800.000 người chết v&agrave; 4,6 triệu người giảm tuổi thọ tr&ecirc;n thế giới mỗi năm. 2/3 số người chết v&agrave; giảm tuổi thọ do &ocirc; nhiễm kh&ocirc;ng kh&iacute; thuộc c&aacute;c nước đang ph&aacute;t triển ở ch&acirc;u &Aacute;.</p>\n<p class="Normal" style="text-align: right;"><strong>Hương Thu</strong></p>', 33, 8, 723, 0, 1),
(686, 'Chai nhựa - mỏ vàng ở Đài Loan', 'Những chai nhựa tại đảo Đài Loan sẽ biến thành các mái tóc giả, quần áo, chăn và thậm chí thành cả tòa nhà.', 'tai.jpg', NULL, '<p class="Title">Chai nhựa - mỏ v&agrave;ng ở Đ&agrave;i Loan</p>\r\n<p class="Lead">Những chai nhựa tại đảo Đ&agrave;i Loan sẽ biến th&agrave;nh c&aacute;c m&aacute;i t&oacute;c giả, quần &aacute;o, chăn v&agrave; thậm ch&iacute; th&agrave;nh cả t&ograve;a nh&agrave;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tai.jpg" alt="Chai nhựa được chất th&agrave;nh đống tại một nh&agrave; m&aacute;y t&aacute;i chế r&aacute;c nhựa tr&ecirc;n đảo Đ&agrave;i Loan. Ảnh: washingtonpost.com." width="450" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chai nhựa được thu gom tại một nh&agrave; m&aacute;y t&aacute;i chế r&aacute;c nhựa tr&ecirc;n đảo Đ&agrave;i Loan. Ảnh: washingtonpost.com.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>AFP&nbsp;</em>cho biết đảo Đ&agrave;i Loan bắt đầu t&aacute;i chế nhựa phế thải c&aacute;ch đ&acirc;y hơn một thập kỷ nhằm giảm thiểu t&aacute;c hại m&agrave; loại r&aacute;c n&agrave;y c&oacute; thể g&acirc;y n&ecirc;n với m&ocirc;i trường. Cơ quan Bảo vệ m&ocirc;i trường của đảo th&ocirc;ng b&aacute;o hiện tại 73% lượng nhựa phế thải ở đ&acirc;y được t&aacute;i chế.</p>\r\n<p class="Normal">Năm ngo&aacute;i, gần 180.000 tấn nhựa phế thải được thu gom v&agrave; biến th&agrave;nh c&aacute;c dạng nguy&ecirc;n liệu th&ocirc;. Tổng gi&aacute; trị của c&aacute;c nguy&ecirc;n liệu th&ocirc; l&ecirc;n tới 140 triệu USD. B&ecirc;n cạnh đ&oacute; hoạt động t&aacute;i chế c&ograve;n l&agrave;m giảm chi ph&iacute; xử l&yacute; r&aacute;c v&agrave; lượng kh&iacute; thải carbon.</p>\r\n<p class="Normal">&ldquo;Nhựa t&aacute;i chế c&oacute; thể được d&ugrave;ng để sản xuất nhiều loại h&agrave;ng h&oacute;a như vải, lọ hoa, t&oacute;c giả v&agrave; bật lửa. Ch&uacute;ng t&ocirc;i kh&ocirc;ng chỉ bảo vệ m&ocirc;i trường m&agrave; c&ograve;n kiếm được tiền từ r&aacute;c&rdquo;, Ma Nien-ho, người ph&aacute;t ng&ocirc;n của Cơ quan Bảo vệ m&ocirc;i trường, ph&aacute;t biểu.</p>\r\n<p class="Normal">Đ&agrave;i Loan tự h&agrave;o với sản phẩm &ldquo;sợi sinh th&aacute;i&rdquo;, nguy&ecirc;n liệu m&agrave; c&aacute;c c&ocirc;ng ty tr&ecirc;n đảo sử dụng để sản xuất &aacute;o nịt d&agrave;nh cho 9 đội tuyển b&oacute;ng đ&aacute; tranh t&agrave;i trong World Cup 2010 tại Nam Phi. Mỗi chiếc &aacute;o nịt được tạo ra từ 9 chai nhựa phế thải. Viện Nghi&ecirc;n cứu Dệt Đ&agrave;i Loan khẳng định mỗi sợi trong &aacute;o nịt nhẹ hơn 13% so với sợi truyền thống v&agrave; c&oacute; khả năng hấp thụ v&agrave; ph&acirc;n t&aacute;n mồ h&ocirc;i nhanh hơn.</p>\r\n<p class="Normal">Super Textile, một trong những c&ocirc;ng ty h&agrave;ng đầu trong lĩnh vực sản xuất sợi sinh th&aacute;i tại Đ&agrave;i Loan, xuất khẩu sản phẩm dệt sang Mỹ v&agrave; Nhật Bản trong v&agrave;i năm gần đ&acirc;y. Nhờ thế m&agrave; doanh số của h&atilde;ng tăng th&ecirc;m 10%.</p>\r\n<p class="Normal">&ldquo;Mức độ đ&oacute;n nhận sản phẩm từ nhựa t&aacute;i chế của kh&aacute;ch h&agrave;ng ng&agrave;y c&agrave;ng tăng trong hai năm qua do nhận thức của dư luận về t&igrave;nh trạng ấm l&ecirc;n to&agrave;n cầu v&agrave; sự biến động của gi&aacute; b&ocirc;ng&rdquo;, Alex Lo, gi&aacute;m đốc điều h&agrave;nh của c&ocirc;ng ty Super Textile, ph&aacute;t biểu.</p>\r\n<p class="Normal">Người d&acirc;n tr&ecirc;n đảo Đ&agrave;i Loan vứt khoảng 4,5 tỉ chai nhựa mỗi năm. Nhờ chi ph&iacute; vận chuyển v&agrave; t&aacute;i chế thấp m&agrave; ng&agrave;nh t&aacute;i chế r&aacute;c tr&ecirc;n đảo ng&agrave;y c&agrave;ng ph&aacute;t triển.</p>\r\n<p class="Normal">Quỹ Tzu Chi, một trong những tổ chức từ thiện lớn nhất tr&ecirc;n đảo, quản l&yacute; tới 4.500 nh&agrave; m&aacute;y t&aacute;i chế r&aacute;c với sự hỗ trợ của khoảng 70.000 t&igrave;nh nguyện vi&ecirc;n. Quỹ n&agrave;y đ&atilde; ph&acirc;n phối hơn 300.000 chăn được sản xuất từ chai nhựa tới c&aacute;c v&ugrave;ng gặp thi&ecirc;n tai tr&ecirc;n thế giới kể từ năm 2007.</p>\r\n<p class="Normal">Nhựa t&aacute;i chế cũng sẽ được d&ugrave;ng để x&acirc;y nh&agrave; tr&ecirc;n đảo Đ&agrave;i Loan. Eco Ark, một t&ograve;a nh&agrave; được x&acirc;y từ 1,5 triệu chai nhựa t&aacute;i chế, sẽ ra mắt c&ocirc;ng ch&uacute;ng v&agrave;o th&aacute;ng 11 tới. Kiến tr&uacute;c sư Arthur Huang, người thiết kế t&ograve;a nh&agrave;, tuy&ecirc;n bố những vi&ecirc;n gạch từ nhựa t&aacute;i chế c&oacute; khả năng chống động đất, b&atilde;o, hỏa hoạn. Eco Ark lấy được nhiều &aacute;nh s&aacute;ng tự nhi&ecirc;n n&ecirc;n sẽ tiết kiệm một lượng điện lớn. Gạch từ nhựa phế thải c&oacute; gi&aacute; th&agrave;nh thấp hơn so với gạch truyền thống v&agrave; gỗ n&ecirc;n chi ph&iacute; x&acirc;y dựng t&ograve;a nh&agrave; cũng thấp hơn nhiều so với những c&ocirc;ng tr&igrave;nh kh&aacute;c c&oacute; c&ugrave;ng k&iacute;ch cỡ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 2176, 0, 1),
(688, 'Ảnh đẹp môi trường qua ống kính học sinh', '3 tháng hè vừa qua, hàng trăm học sinh tham gia thi ảnh "Lăng kính xanh" do một hãng máy ảnh tổ chức, với chủ đề nâng cao nhận thức về bảo vệ môi trường trong giới trẻ học đường.', 'dac biet.jpg', NULL, '<div>\r\n<p class="Title">Ảnh đẹp m&ocirc;i trường qua ống k&iacute;nh học sinh</p>\r\n<p class="Lead">3 th&aacute;ng h&egrave; vừa qua, h&agrave;ng trăm học sinh tham gia thi ảnh "Lăng k&iacute;nh xanh" do một h&atilde;ng m&aacute;y ảnh tổ chức, với chủ đề n&acirc;ng cao nhận thức về bảo vệ m&ocirc;i trường trong giới trẻ học đường.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dac biet.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">T&aacute;c phẩm "Lo lắng" của Trần Huy Ph&uacute; đoạt giải đặc biệt của cuộc thi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="HoaThuoc-website%20Jun.JPG" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">"Hoa thuốc" của Trần Đăng Khoa được nhiều người b&igrave;nh chọn qua website trong th&aacute;ng 6.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Nhat-Jun.JPG" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">"Mong ước một khoảng trời" của &Ocirc;n Nhất Đ&ocirc;ng, giải nhất th&aacute;ng 6.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Nhat-Jul.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">T&aacute;c phẩm "C&ugrave;ng chung tay" của Nguyễn Hạnh Dung dẫn đầu bảng xếp hạng trong th&aacute;ng 7.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Nhat-Aug.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">T&aacute;c giả Nguyễn Qu&yacute; An đoạt giải nhất với "Kon Tum" trong th&aacute;ng 8.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Nhi-Aug.jpg" border="1" alt="" width="350" height="490" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Giải nh&igrave; " Xanh thẳm " của Ph&iacute; Duy Anh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Ba-Aug.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Giải ba "Cự đ&agrave; xanh" của Đỗ Cao Tr&iacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="KK-Aug.jpg" border="1" alt="Giải khuyến kh&iacute;c " width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image" align="left">Giải khuyến kh&iacute;ch "Hội nghị ốc s&ecirc;n" của bạn Nguyễn Nam Anh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Mai</strong></p>\r\n</div>', 33, 8, 1116, 0, 1),
(690, 'Xây khu bảo tồn thiên nhiên tại quê hương Hải đội Hoàng Sa', 'Các nhà khoa học đề nghị xây dựng khu bảo tồn thiên nhiên cho vùng biển huyện đảo Lý Sơn (Quảng Ngãi), do hệ sinh thái biển ở đây đang bị tác động mạnh bởi thiên tai và tình trạng khai thác ồ ạt của con người.', 'Ly Son.jpg', NULL, '<p class="Title"><strong>X&acirc;y khu bảo tồn thi&ecirc;n nhi&ecirc;n tại qu&ecirc; hương Hải đội Ho&agrave;ng Sa</strong></p>\r\n<p class="Lead">C&aacute;c nh&agrave; khoa học đề nghị x&acirc;y dựng khu bảo tồn thi&ecirc;n nhi&ecirc;n cho v&ugrave;ng biển huyện đảo L&yacute; Sơn (Quảng Ng&atilde;i), do hệ sinh th&aacute;i biển ở đ&acirc;y đang bị t&aacute;c động mạnh bởi thi&ecirc;n tai v&agrave; t&igrave;nh trạng khai th&aacute;c ồ ạt của con người.</p>\r\n<p class="Normal">Đề xuất n&agrave;y được nhiều nh&agrave; khoa học đưa ra tại hội thảo về đề t&agrave;i Điều tra, đ&aacute;nh gi&aacute; hiện trạng c&aacute;c hệ sinh th&aacute;i, x&acirc;y dựng luận cứ khoa học đề xuất dự &aacute;n khu bảo tồn thi&ecirc;n nhi&ecirc;n biển phục vụ du lịch ở v&ugrave;ng biển ven bờ huyện L&yacute; Sơn, s&aacute;ng 18/8.</p>\r\n<p class="Normal">Chủ nhiệm đề t&agrave;i, PGS-TS Vũ Thanh Ca, Ph&oacute; Viện Trưởng Nghi&ecirc;n cứu Quản l&yacute; biển v&agrave; hải đảo cho biết, sau một năm khảo s&aacute;t, nh&oacute;m nghi&ecirc;n cứu của &ocirc;ng đ&atilde; ph&aacute;t hiện ở huyện đảo L&yacute; Sơn c&oacute; 685 lo&agrave;i động, thực vật. Trong đ&oacute; c&oacute; 150 lo&agrave;i c&aacute;, 94 lo&agrave;i th&acirc;n mềm, 15 lo&agrave;i gi&aacute;p x&aacute;c, 36 lo&agrave;i san h&ocirc;, 150 lo&agrave;i rong biển.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Ly Son.jpg" border="1" alt="" width="490" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&ograve;n Đụn, thắng cảnh hấp dẫn du kh&aacute;ch ở x&atilde; An B&igrave;nh (đảo B&eacute;), huyện đảo L&yacute; Sơn. Ảnh:<em>&nbsp;Tr&iacute; T&iacute;n</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Xung quanh đảo c&oacute; rạn san h&ocirc; v&agrave; cỏ biển l&agrave; hai kiểu sinh th&aacute;i đặc trưng. Nơi đ&acirc;y c&ograve;n l&agrave; m&ocirc;i trường sinh sống của nhiều loại hải sản c&oacute; gi&aacute; trị kinh tế cao như c&aacute;, t&ocirc;m h&ugrave;m, r&ugrave;a biển, san h&ocirc; xanh, san h&ocirc; đen, b&agrave;o ngư, trai tai tượng... "Tuy nhi&ecirc;n, hiện nay hệ sinh th&aacute;i ở huyện đảo đang bị ph&aacute; hủy từng ng&agrave;y do t&aacute;c động xấu của thi&ecirc;n nhi&ecirc;n v&agrave; con người", &ocirc;ng Ca nhận định.</p>\r\n<p class="Normal">Theo &ocirc;ng Ca, việc th&agrave;nh lập khu bảo tồn thi&ecirc;n nhi&ecirc;n biển tại đảo L&yacute; Sơn sẽ gi&uacute;p bảo tồn v&agrave; kh&ocirc;i phục t&iacute;nh nguy&ecirc;n vẹn của c&aacute;c gi&aacute; trị đa dạng sinh học, cảnh quan thi&ecirc;n nhi&ecirc;n, sự trong l&agrave;nh của m&ocirc;i trường sinh th&aacute;i đảo L&yacute; Sơn.</p>\r\n<p class="Normal">Dự kiến đề t&agrave;i nghi&ecirc;n cứu n&agrave;y sẽ kết th&uacute;c v&agrave;o cuối năm 2011.</p>\r\n<p class="Normal">Gần 200 năm trước, từ đảo L&yacute; Sơn, nhiều d&acirc;n binh thừa lệnh vua triều Nguyễn đ&atilde; tập hợp nhau giong buồm ra quần đảo Ho&agrave;ng Sa để đo đạc, cắm mốc chủ quyền quốc gia, bảo vệ biển đảo. Từ đ&oacute;, những đội d&acirc;n binh n&agrave;y mang t&ecirc;n Hải đội Ho&agrave;ng Sa v&agrave; được lưu truyền, ghi nhớ c&ocirc;ng ơn đến ng&agrave;y nay.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tr&iacute; T&iacute;n</strong></p>', 33, 8, 424, 0, 1),
(692, 'Phần lớn hệ sinh thái rừng nhiệt đới sẽ bị hủy diệt', 'Một nghiên cứu cho thấy đa số động vật và thực vật trong các rừng nhiệt đới hiện nay sẽ không tồn tại sau 90 năm nữa bởi tác động của biến đổi khí hậu và nạn phá rừng.', 'for.jpg', NULL, '<p class="Title">Phần lớn hệ sinh th&aacute;i rừng nhiệt đới sẽ bị hủy diệt</p>\n<p class="Lead">Một nghi&ecirc;n cứu cho thấy đa số động vật v&agrave; thực vật trong c&aacute;c rừng nhiệt đới hiện nay sẽ kh&ocirc;ng tồn tại sau 90 năm nữa bởi t&aacute;c động của biến đổi kh&iacute; hậu v&agrave; nạn ph&aacute; rừng.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="for.jpg" alt="" width="450" height="338" /></td>\n</tr>\n<tr>\n<td class="Image">Ảnh minh họa:&nbsp;<em>tripod.com</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal"><span style="color: #000000;">Rừng nhiệt đới hiện l&agrave; m&ocirc;i trường sống của hơn một nửa c&aacute;c lo&agrave;i động vật v&agrave; thực vật tr&ecirc;n tr&aacute;i đất. Tuy nhi&ecirc;n, c&aacute;c nh&agrave; khoa học dự đo&aacute;n rằng, t&aacute;c động kết hợp của biến đổi kh&iacute; hậu v&agrave; ph&aacute; rừng c&oacute; thể buộc ch&uacute;ng phải th&iacute;ch ứng, di chuyển đến nơi kh&aacute;c hoặc chết. Đến năm 2100, biến đổi kh&iacute; hậu v&agrave; hoạt động ph&aacute; rừng c&oacute; thể thay đổi 2/3 số rừng nhiệt đới tại khu vực Trung Mỹ v&agrave; Nam Mỹ, 70% rừng nhiệt đới tại ch&acirc;u Phi. Ri&ecirc;ng tại v&ugrave;ng ch&acirc;u thổ s&ocirc;ng Amazon mức độ thay đổi của hệ sinh th&aacute;i sẽ l&ecirc;n tới 80%.</span></p>\n<p class="Normal">Greg Asner, một chuy&ecirc;n gia thuộc khoa Sinh th&aacute;i to&agrave;n cầu, Viện Carnegie, bang California, Mỹ, c&ugrave;ng c&aacute;c đồng nghiệp t&igrave;m hiểu t&igrave;nh trạng của những khu rừng nhiệt đới tr&ecirc;n thế giới bằng c&aacute;ch ph&acirc;n t&iacute;ch dữ liệu h&igrave;nh ảnh từ vệ tinh v&agrave; 16 dự &aacute;n nghi&ecirc;n cứu về biến đổi kh&iacute; hậu tr&ecirc;n khắp thế giới. Họ chạy thử những m&ocirc; h&igrave;nh giả lập về sự thay đổi của c&aacute;c lo&agrave;i động vật, thực vật về mặt địa l&yacute; từ nay tới năm 2100.</p>\n<p class="Normal">Kết quả cho thấy chỉ c&oacute; từ 18 tới 45% tổng số động vật v&agrave; thực vật trong c&aacute;c rừng nhiệt đới c&ograve;n tồn tại tới năm 2100.</p>\n<p class="Normal">Asner khẳng định đ&acirc;y l&agrave; nghi&ecirc;n cứu đầu ti&ecirc;n chứng minh rằng c&aacute;c hệ sinh th&aacute;i trong rừng nhiệt đới sẽ trải qua những biến đổi s&acirc;u sắc bởi hiệu ứng nh&agrave; k&iacute;nh.</p>\n<p class="Normal">"Với những v&ugrave;ng c&oacute; nguy cơ chịu ảnh hưởng nặng nhất từ biến đổi kh&iacute; hậu, c&aacute;c nh&agrave; chức tr&aacute;ch địa phương n&ecirc;n tập trung v&agrave;o việc giảm chặt ph&aacute; rừng, qua đ&oacute; gi&uacute;p c&aacute;c lo&agrave;i sinh vật kịp th&iacute;ch nghi. C&ograve;n những nơi chịu &iacute;t ảnh hưởng từ biến đổi kh&iacute; hậu c&oacute; thể tập trung phục hồi rừng", &ocirc;ng n&oacute;i.</p>\n<p class="Normal">Nghi&ecirc;n cứu được c&ocirc;ng bố tr&ecirc;n tạp ch&iacute;&nbsp;<em>Conservation Letters</em>.</p>\n<p class="Normal" style="text-align: right;"><strong>Thu Thảo</strong></p>', 33, 8, 1589, 0, 1),
(693, 'Cháy rừng tại Nga gây nên đám khói khổng lồ', 'Hình ảnh vệ tinh cho thấy đám khói khổng lồ bốc lên từ những đám cháy than bùn đang trôi dạt khắp nước Nga.', 'fire2.jpg', NULL, '<p class="Title">Ch&aacute;y rừng tại Nga g&acirc;y n&ecirc;n đ&aacute;m kh&oacute;i khổng lồ</p>\r\n<p class="Lead">H&igrave;nh ảnh vệ tinh cho thấy đ&aacute;m kh&oacute;i khổng lồ bốc l&ecirc;n từ những đ&aacute;m ch&aacute;y than b&ugrave;n đang tr&ocirc;i dạt khắp nước Nga.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="russia-fires.jpg" alt="" width="450" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Kh&oacute;i từ c&aacute;c đ&aacute;m ch&aacute;y được thể hiện bằng m&agrave;u v&agrave;ng trong một ảnh vệ tinh. Ảnh:&nbsp;<em>Đại học Leicester</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal"><em>Livescience&nbsp;</em>cho biết, vệ tinh MetOp-A của Li&ecirc;n minh ch&acirc;u &Acirc;u chụp ảnh kh&oacute;i bốc l&ecirc;n từ c&aacute;c đ&aacute;m ch&aacute;y tại Nga v&agrave;o c&aacute;c ng&agrave;y 4, 8 v&agrave; 9/8. C&aacute;c nh&agrave; khoa học của Đại học Leicester tại Anh đ&atilde; ph&acirc;n t&iacute;ch ch&uacute;ng.</p>\r\n<p class="Normal">&ldquo;Nhờ những thiết bị tr&ecirc;n vệ tinh, ch&uacute;ng t&ocirc;i c&oacute; thể quan s&aacute;t kh&oacute;i từ v&ocirc; số đ&aacute;m ch&aacute;y ở miền t&acirc;y nước Nga. Những chất g&acirc;y &ocirc; nhiễm trong kh&oacute;i c&oacute; thể l&agrave;m giảm chất lượng kh&ocirc;ng kh&iacute; v&agrave; thay đổi th&agrave;nh phần h&oacute;a học của kh&iacute; quyển&rdquo;, David Moore, một nh&agrave; nghi&ecirc;n cứu của Đại học Leicester tại Anh, ph&aacute;t biểu.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="fire2.jpg" alt="" width="450" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một đ&aacute;m ch&aacute;y rừng tại Nga v&agrave;o ng&agrave;y 6/8. Ảnh:&nbsp;<em>AFP.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Những đ&aacute;m m&acirc;y d&agrave;y đặc v&agrave; cao ng&uacute;t trời n&agrave;y h&igrave;nh th&agrave;nh khi sức n&oacute;ng dữ dội từ đ&aacute;m ch&aacute;y đẩy kh&ocirc;ng kh&iacute; l&ecirc;n cao hơn. C&aacute;c dữ liệu h&igrave;nh ảnh từ nhiều vệ tinh cho thấy ch&uacute;ng bay tới tầng b&igrave;nh lưu (bắt đầu từ độ cao 11 km). C&aacute;c đ&aacute;m m&acirc;y đ&oacute; c&oacute; thể &ldquo;nhốt&rdquo; những chất g&acirc;y &ocirc; nhiễm kh&ocirc;ng kh&iacute; v&agrave; đem ch&uacute;ng đi xa v&agrave;i ngh&igrave;n km.</p>\r\n<p class="Normal">Kể từ khi những đ&aacute;m ch&aacute;y rừng v&agrave; ch&aacute;y than b&ugrave;n bắt đầu b&ugrave;ng ph&aacute;t tại Nga, đ&aacute;m kh&oacute;i khổng lồ đ&atilde; bao phủ một khu vực c&oacute; chiều d&agrave;i chừng 3.000 km từ ph&iacute;a đ&ocirc;ng sang ph&iacute;a t&acirc;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 2452, 0, 1),
(694, 'Năng suất lúa châu Á sẽ giảm', 'Hiện tượng ấm lên toàn cầu sẽ khiến sản lượng gạo tại châu Á, trong đó có Việt Nam, giảm trong vài thập kỷ tới.', 'lua.jpg', NULL, '<p class="Title">Năng suất l&uacute;a ch&acirc;u &Aacute; sẽ giảm</p>\n<p class="Lead">Hiện tượng ấm l&ecirc;n to&agrave;n cầu sẽ khiến sản lượng gạo tại ch&acirc;u &Aacute;, trong đ&oacute; c&oacute; Việt Nam, giảm trong v&agrave;i thập kỷ tới.</p>\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\n<tbody>\n<tr>\n<td><img src="lua.jpg" alt="Thu hoạch l&uacute;a" width="450" height="338" /></td>\n</tr>\n<tr>\n<td class="Image">Thu hoạch l&uacute;a ở B&aacute;t X&aacute;t, L&agrave;o Cai. Ảnh:&nbsp;<em>laocai.gov</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class="Normal"><em>AFP</em>&nbsp;đưa tin c&aacute;c nh&agrave; nghi&ecirc;n cứu từ Mỹ, Philippines v&agrave; Tổ chức N&ocirc;ng lương Thế giới (FAO) ph&acirc;n t&iacute;ch t&aacute;c động của t&igrave;nh trạng nhiệt độ tăng đối với sản lượng gạo tr&ecirc;n 227 c&aacute;nh đồng tại Trung Quốc, Indonesia, Ấn Độ, Philippines, Th&aacute;i Lan v&agrave; Việt Nam.</p>\n<p class="Normal">Họ nhận thấy nguy&ecirc;n nh&acirc;n ch&iacute;nh khiến năng suất l&uacute;a giảm l&agrave; sự gia tăng nhiệt độ tối thiểu v&agrave;o ban ng&agrave;y v&agrave; ban đ&ecirc;m.</p>\n<p class="Normal">&ldquo;Sản lượng gạo giảm do nhiệt độ tối thiểu ban ng&agrave;y tăng, hoặc do ban đ&ecirc;m trở n&ecirc;n n&oacute;ng hơn&rdquo;, Jarrod Welch, trưởng nh&oacute;m nghi&ecirc;n cứu, tuy&ecirc;n bố. Welch l&agrave; nh&agrave; khoa học của Đại học California, Mỹ.</p>\n<p class="Normal">Năng suất l&uacute;a sẽ tăng nếu nhiệt độ ban ng&agrave;y tăng. Song nếu nhiệt độ cũng tăng v&agrave;o ban đ&ecirc;m th&igrave; năng suất lại giảm. Do nhiệt độ tăng nhanh hơn v&agrave;o buổi tối, mức giảm năng suất ban đ&ecirc;m lớn hơn so với mức tăng v&agrave;o ban ng&agrave;y. V&igrave; thế rốt cuộc năng suất l&uacute;a sẽ giảm sau một thời gian. Thực tế cho thấy, sự gia tăng nhiệt độ trong 25 năm qua khiến năng suất tại nhiều khu vực trồng l&uacute;a quan trọng ở ch&acirc;u &Aacute; giảm từ 10 tới 20%. Năng suất sẽ tiếp tục giảm do nhiệt độ li&ecirc;n tục tăng tới tận giữa thế kỷ 21.</p>\n<p class="Normal">&ldquo;Khi nhiệt độ ban ng&agrave;y l&ecirc;n tới một ngưỡng n&agrave;o đ&oacute;, sản lượng l&uacute;a kh&ocirc;ng tăng nữa m&agrave; sẽ giảm, khiến t&igrave;nh h&igrave;nh trở n&ecirc;n tồi tệ hơn&rdquo;, Welch giải th&iacute;ch.</p>\n<p class="Normal">L&uacute;a l&agrave; một trong những c&acirc;y lương thực chủ yếu của thế giới. Mỗi ng&agrave;y khoảng ba tỷ người ăn cơm hoặc c&aacute;c sản phẩm từ gạo. Tại ch&acirc;u &Aacute;, gạo l&agrave; loại ngũ cốc quan trọng đối với khoảng 600 triệu người ngh&egrave;o nhất ch&acirc;u lục. Sự sụt giảm năng suất l&uacute;a đồng nghĩa với việc sẽ c&oacute; nhiều người trở n&ecirc;n ngh&egrave;o v&agrave; đ&oacute;i hơn.</p>\n<p class="Normal">&ldquo;Nếu ch&uacute;ng ta kh&ocirc;ng thể thay đổi c&aacute;c phương ph&aacute;p canh t&aacute;c l&uacute;a hoặc tạo ra những giống l&uacute;a mới c&oacute; khả năng chống chọi nhiệt độ cao, sản lượng gạo trong v&agrave;i thập kỷ tới sẽ giảm do nhiệt độ ban ng&agrave;y v&agrave; ban đ&ecirc;m đều tăng&rdquo;, Welch cảnh b&aacute;o.</p>\n<p class="Normal" style="text-align: right;"><strong>Minh Long</strong></p>', 33, 8, 1879, 0, 1),
(696, 'Bà bầu liên tiếp phạm tội', 'Thấy ví căng đầy của ông hàng xóm đặt trên bàn, người đàn bà nhanh tay lấy trộm mang về nhà. Đốt hết giấy tờ bên trong, cô đưa tập tiền cho chồng.', 'Hai-Duong-250.jpg', NULL, '<h1 class="Title">B&agrave; bầu li&ecirc;n tiếp phạm tội</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="Hai-Duong-250.jpg" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trần Thị Hải Đường đang mang bầu 7 th&aacute;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Thấy v&iacute; căng đầy của &ocirc;ng h&agrave;ng x&oacute;m đặt tr&ecirc;n b&agrave;n, người đ&agrave;n b&agrave; nhanh tay lấy trộm mang về nh&agrave;. Đốt hết giấy tờ b&ecirc;n trong, c&ocirc; đưa tập tiền cho chồng.</h2>\r\n<p class="Normal">C&ocirc;ng an th&agrave;nh phố Y&ecirc;n B&aacute;i vừa khởi tố người phụ nữ mang bầu 7 th&aacute;ng Trần Thị Hải Đường (33 tuổi, phường Nguyễn Th&aacute;i Học) về tội trộm cắp t&agrave;i sản. Mới chỉ hơn 20 ng&agrave;y trước, Đường bị t&ograve;a &aacute;n th&agrave;nh phố kết &aacute;n 3 năm t&ugrave; v&igrave; mua b&aacute;n ma t&uacute;y, song được tại ngoại chờ sinh nở.</p>\r\n<p class="Normal">Theo kết quả x&aacute;c minh của c&ocirc;ng an, đầu th&aacute;ng 2 Đường sang nh&agrave; anh L&ecirc; Quốc H&ugrave;ng (h&agrave;ng x&oacute;m) thanh to&aacute;n tiền d&ugrave;ng nước sinh hoạt v&igrave; hai nh&agrave; sử dụng chung. Sau khi trả anh H&ugrave;ng hơn 150.000 đồng, thấy chiếc v&iacute; da căng đầy của chủ nh&agrave; để tr&ecirc;n b&agrave;n uống nước, người phụ nữ đ&atilde; nấn n&aacute; ở lại. Nh&acirc;n l&uacute;c anh H&ugrave;ng đi ra ph&iacute;a sau nh&agrave;, b&agrave; bầu nhanh tay lấy trộm.</p>\r\n<p class="Normal">R&uacute;t gần 10 triệu đồng b&ecirc;n trong v&iacute;, Đường đưa chồng cất giữ. To&agrave;n bộ giấy tờ của anh H&ugrave;ng, c&ocirc; ta đem đốt sạch.</p>\r\n<p class="Normal">Cảnh s&aacute;t cho biết, Đường đ&atilde; khai nhận to&agrave;n bộ h&agrave;nh vi. Người phụ nữ n&agrave;y kh&ocirc;ng nghề nghiệp, nghiện ma t&uacute;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tuệ L&acirc;m</strong></p>', 25, 6, 2595, 0, 1),
(697, 'Cứu con trai, đôi vợ chồng bị chém chết', 'Nhóm thanh niên xông vào nhà Cường "đại náo" khiến cậu và 5 người khác bị thương. Riêng cha mẹ Cường tử vong khi can ngăn đám côn đồ.', 'giet-nguoi.jpg', NULL, '<h1 class="Title">Cứu con trai, đ&ocirc;i vợ chồng bị ch&eacute;m chết</h1>\r\n<h2 class="Lead">Nh&oacute;m thanh ni&ecirc;n x&ocirc;ng v&agrave;o nh&agrave; Cường "đại n&aacute;o" khiến cậu v&agrave; 5 người kh&aacute;c bị thương. Ri&ecirc;ng cha mẹ Cường tử vong khi can ngăn đ&aacute;m c&ocirc;n đồ.</h2>\r\n<p class="Normal">Hiện, hai can phạm li&ecirc;n quan vụ &aacute;n mạng nghi&ecirc;m trọng khiến hai người chết, 6 người trọng thương đ&atilde; bị C&ocirc;ng an tỉnh Y&ecirc;n B&aacute;i khởi tố, tạm giam về tội giết người. Trong đ&oacute; c&oacute; chủ mưu Ho&agrave;ng Tuấn Anh, 23 tuổi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="giet-nguoi.jpg" alt="" width="350" height="267" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ho&agrave;ng Tuấn Anh tr&uacute; tại khu vực ga Mậu A, huyện Văn Y&ecirc;n, Y&ecirc;n B&aacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo kết quả x&aacute;c minh, trưa 4/2 (mồng 2 Tết) Ngọc Tiến Cường (x&atilde; M&ocirc;ng Quy, Trấn Y&ecirc;n, Y&ecirc;n B&aacute;i) chơi xu&acirc;n với nh&oacute;m bạn tại khu vực cầu Khe th&igrave; gặp một c&ocirc; bạn học đi c&ugrave;ng Tuấn Anh v&agrave; 3 người kh&aacute;c. Cường tung khăn tr&ecirc;u bạn n&ecirc;n xảy ra va chạm với những thanh ni&ecirc;n đi c&ugrave;ng c&ocirc; g&aacute;i.</p>\r\n<p class="Normal">L&aacute;t sau, nh&oacute;m bạn của c&ocirc; g&aacute;i mang hung kh&iacute; t&igrave;m đến nh&agrave; của Cường. Hai b&ecirc;n tiếp tục x&ocirc; x&aacute;t v&agrave; ẩu đả. Cuộc hỗn chiến giữa hơn 10 thanh ni&ecirc;n đ&atilde; khiến Cường bị đ&acirc;m tr&uacute;ng ngực. Những người v&agrave;o can ngăn cũng bị nh&oacute;m c&ocirc;n đồ "xuống tay". Trong số n&agrave;y, bố của Cường tử vong tại chỗ, mẹ chết tr&ecirc;n đường v&agrave;o bệnh viện. Cường c&ugrave;ng em trai v&agrave; 4 người kh&aacute;c bị trọng thương.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Cuong-tren-giuong-benh.jpg" border="1" alt="" width="350" height="269" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Đến 11/2 sau nhiều ng&agrave;y điều trị tại bệnh viện, Cường vẫn chưa hay biết tin bố mẹ đ&atilde; qua đời.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Trưởng c&ocirc;ng an x&atilde; M&ocirc;ng Quy Trần Văn Chung cho biết: &ldquo;Ở địa phương chưa c&oacute; vụ việc n&agrave;o d&atilde; man đến như vậy. Người l&agrave;ng, người x&atilde; với nhau m&agrave; h&agrave;nh xử kh&ocirc;ng kh&aacute;c trong phim x&atilde; hội đen&rdquo;.</p>\r\n<p class="Normal">C&ocirc;ng an tỉnh Y&ecirc;n B&aacute;i đang tiếp tục truy bắt những người c&ograve;n lại li&ecirc;n quan vụ &aacute;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tuệ L&acirc;m</strong></p>', 25, 6, 541, 0, 1),
(701, 'Kẻ tham gia vụ bắn người trước trung tâm Parkson đầu thú', 'Sau 5 ngày Tết, Lê Trọng Nghĩa - người bị truy nã trong vụ bắn chết người trước khu mua sắm Parkson - đã ra cơ quan điều tra trình diện.', 'sat-thu.jpg', NULL, '<h1 class="Title">Kẻ tham gia vụ bắn người trước trung t&acirc;m Parkson đầu th&uacute;</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="sat-thu.jpg" alt="L&ecirc; Trọng Nghĩa." width="250" height="195" align="left" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&ecirc; Trọng Nghĩa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Sau 5 ng&agrave;y Tết, L&ecirc; Trọng Nghĩa - người bị truy n&atilde; trong vụ bắn chết người trước khu mua sắm Parkson - đ&atilde; ra cơ quan điều tra tr&igrave;nh diện.</h2>\r\n<p class="Normal">L&ecirc; Trọng Nghĩa (30 tuổi, quận Cầu Giấy) bị x&aacute;c định li&ecirc;n quan vụ nổ s&uacute;ng bắn chết anh Nguyễn Minh Dương v&agrave;o rạng s&aacute;ng 26/1 tại ng&atilde; tư Th&aacute;i H&agrave; - Ch&ugrave;a Bộc, H&agrave; Nội. Sau gần nửa th&aacute;ng bỏ trốn, tối 7/2 (m&ugrave;ng 5 Tết) Nghĩa đến Ph&ograve;ng cảnh s&aacute;t điều tra tội phạm về trật tự x&atilde; hội, C&ocirc;ng an H&agrave; Nội đầu th&uacute;.</p>\r\n<p class="Normal">Theo kết quả x&aacute;c minh, anh Dương c&oacute; vay chị Ho&agrave;ng Hải H&agrave; (31 tuổi ở Ninh B&igrave;nh) 30 triệu đồng. Sau nhiều lần đ&ograve;i tiền người đ&agrave;n &ocirc;ng 30 tuổi n&agrave;y, chủ nợ chỉ thu về được 5 triệu đồng.</p>\r\n<p class="Normal">Ng&agrave;y 25/1, đến gặp anh Dương tại một kh&aacute;ch sạn ở phố Th&aacute;i H&agrave; nhưng kh&ocirc;ng được trả tiền, H&agrave; kể lại c&acirc;u chuyện cho Nghĩa. Nghĩa liền gọi điện thoại cho Dương.</p>\r\n<p class="Normal">Rạng s&aacute;ng 26/1, Nghĩa rủ Lưu Quang Thạc (31 tuổi ở quận Hai B&agrave; Trưng) đến ng&atilde; tư Th&aacute;i H&agrave; - Ch&ugrave;a Bộc để "n&oacute;i chuyện" với Dương. Tại điểm hẹn trước khu trung t&acirc;m mua sắm Parkson, Thạc đ&atilde; bắn s&uacute;ng v&agrave;o ngực phải khiến anh Dương tử vong.</p>\r\n<p class="Normal">Kh&aacute;m người nạn nh&acirc;n, cảnh s&aacute;t thu một khẩu K54 v&agrave; 7 vi&ecirc;n đạn. Trong ph&ograve;ng kh&aacute;ch sạn của Dương, trinh s&aacute;t tiếp tục ph&aacute;t hiện s&uacute;ng Col c&ugrave;ng 19 vi&ecirc;n đạn.</p>\r\n<p class="Normal">Ng&agrave;y 29/1, C&ocirc;ng an Đống Đa ph&aacute;t lệnh truy n&atilde; Nghĩa v&agrave; Thạc.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 1155, 0, 1),
(703, 'Nữ sinh về quê ăn Tết bị tài xế làm nhục', 'Ngày 28/1 công an đã bắt lái xe Lê Ngọc Hùng để điều tra tố cáo hiếp dâm một nữ sinh viên khi cô này đi nhờ xe tải từ Đà Nẵng về Thanh Hóa ăn Tết.', 'hiep-dam (1).jpg', NULL, '<h1 class="Title">Nữ sinh về qu&ecirc; ăn Tết bị t&agrave;i xế l&agrave;m nhục</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hiep-dam (1).jpg" alt="" width="250" height="195" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&aacute;i xe L&ecirc; Ngọc H&ugrave;ng, 29 tuổi, tr&uacute; tại Y&ecirc;n B&aacute;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Ng&agrave;y 28/1 c&ocirc;ng an đ&atilde; bắt l&aacute;i xe L&ecirc; Ngọc H&ugrave;ng để điều tra tố c&aacute;o hiếp d&acirc;m một nữ sinh vi&ecirc;n khi c&ocirc; n&agrave;y đi nhờ xe tải từ Đ&agrave; Nẵng về Thanh H&oacute;a ăn Tết.</h2>\r\n<p class="Normal">Khoảng 19h ng&agrave;y 27/1, tại Đ&agrave; Nẵng 2 nữ sinh vi&ecirc;n đ&oacute;n &ocirc;t&ocirc; tải để về qu&ecirc; Thanh H&oacute;a, tr&ecirc;n xe c&oacute; 2 t&agrave;i xế. Đến địa phận th&ocirc;n H&agrave; Thanh, x&atilde; Gio Ch&acirc;u, huyện Gio Linh, tỉnh Quảng Trị, một c&ocirc; bị l&aacute;i xe đẩy xuống đường. Người c&ograve;n lại bị t&agrave;i xế H&ugrave;ng giữ lại trong xe để hiếp d&acirc;m.</p>\r\n<p class="Normal">Sau khi giở tr&ograve; đồi bại, anh ta đẩy c&ocirc; n&agrave;y xuống đường. Nạn nh&acirc;n được người d&acirc;n xung quanh gi&uacute;p đỡ đưa đến C&ocirc;ng an huyện Gio Linh tr&igrave;nh b&aacute;o sự việc.</p>\r\n<p class="Normal">S&aacute;ng 28/1, C&ocirc;ng an huyện Gio Linh phối hợp với C&ocirc;ng an tỉnh Quảng B&igrave;nh v&acirc;y bắt được chiếc xe tải tr&ecirc;n khi chạy đến thị trấn Ho&agrave;n L&atilde;o, huyện Bố Trạch, Quảng B&igrave;nh. Tại cơ quan điều tra, t&agrave;i xế 29 tuổi đ&atilde; thừa nhận h&agrave;nh vi hiếp d&acirc;m.</p>\r\n<p class="Normal" style="text-align: right;">(Theo&nbsp;<em>N&ocirc;ng Nghiệp Việt Nam</em>)</p>', 25, 6, 1971, 0, 1),
(705, 'Nhóm thanh niên giết người ở quán karaoke ra đầu thú', '6 thanh niên liên quan vụ hành hung đến chết một khách hát karaoke tại quận Đống Đa, Hà Nội, vừa ra đầu thú. ', 'dung.jpg', NULL, '<h1 class="Title">Nh&oacute;m thanh ni&ecirc;n giết người ở qu&aacute;n karaoke ra đầu th&uacute;</h1>\r\n<h2 class="Lead">6 thanh ni&ecirc;n li&ecirc;n quan vụ h&agrave;nh hung đến chết một kh&aacute;ch h&aacute;t karaoke tại quận Đống Đa, H&agrave; Nội, vừa ra đầu th&uacute;.&nbsp;</h2>\r\n<p class="Normal">Nh&oacute;m thanh ni&ecirc;n gồm: Nguyễn Mạnh H&ugrave;ng (15 tuổi, từng đi trường gi&aacute;o dưỡng), Nguyễn Quang Huy (16 tuổi, c&oacute; một tiền sự), Phạm Ho&agrave;ng Giang (16 tuổi), Trần Nam Sơn (18 tuổi), Nguyễn Xu&acirc;n Trường (17 tuổi, từng c&oacute; tiền &aacute;n) v&agrave; Phạm Anh Dũng đều ở quận Ho&agrave;ng Mai v&agrave; Hai B&agrave; Trưng, H&agrave; Nội.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dung.jpg" alt="Phạm Anh Dũng tại cơ quan điều tra." width="490" height="378" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Phạm Anh Dũng tại cơ quan điều tra.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại cơ quan điều tra, H&ugrave;ng, Huy, Giang, Trường v&agrave; Sơn khai đang chơi bi-a ở đường Trương Định th&igrave; nhận điện thoại của Anh Dũng rủ l&ecirc;n một qu&aacute;n h&aacute;t ở tr&ecirc;n phố Nguyễn Khuyến. Khi cả hội đến nơi, Anh Dũng n&oacute;i muốn đ&aacute;nh anh Lưu Đ&igrave;nh Dũng (21 tuổi, ở quận Thanh Xu&acirc;n) đang ngồi trong ph&ograve;ng v&igrave; thấy "ngứa mắt".</p>\r\n<p class="Normal">5 thanh ni&ecirc;n n&agrave;y đ&atilde; hưởng ứng v&agrave; gom tiền mua 6 con dao. C&oacute; hung kh&iacute; trong tay, H&ugrave;ng vung dao đe dọa mọi người đang ngồi trong ph&ograve;ng h&aacute;t rồi cả nh&oacute;m lao v&agrave;o đ&aacute;nh anh Đ&igrave;nh Dũng... Anh Dũng cầm dao đ&acirc;m tr&uacute;ng bụng người n&agrave;y. Huy x&ocirc;ng tiếp v&agrave;o g&acirc;y &aacute;n khiến nạn nh&acirc;n gục ng&atilde; b&ecirc;n vũng m&aacute;u, chết tr&ecirc;n đường đi cấp cứu. Trong vụ &aacute;n mạng n&agrave;y, nh&oacute;m c&ocirc;n đồ c&ograve;n ch&eacute;m trọng thương một người kh&aacute;c.</p>\r\n<p class="Normal">Sau khi g&acirc;y &aacute;n, cả bọn tẩu tho&aacute;t. Ch&uacute;ng vứt hung kh&iacute; dưới một hố ga gần s&acirc;n b&oacute;ng Thịnh Liệt.</p>\r\n<p class="Normal">Cảnh s&aacute;t cho biết m&acirc;u thuẫn giữa Anh Dũng v&agrave; nạn nh&acirc;n Đ&igrave;nh Dũng bắt nguồn v&agrave;o tối 16/1 khi cả hai c&ugrave;ng tới dự tiệc sinh nhật của Nguyễn Thu Hường (15 tuổi) tổ chức tại qu&aacute;n karaoke n&agrave;y. Trong l&uacute;c h&aacute;t, thấy chủ bữa tiệc l&agrave; Hường v&agrave; Đ&igrave;nh Dũng &ocirc;m nhau Anh Dũng đ&atilde; rất tức tối.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 1202, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(706, 'Vạch mặt hung thủ hại hàng xóm', 'Sau khi hại chết bà lão hàng xóm, Tám lấy đôi hoa tai mang về giấu kỹ trong hòm cá nhân. Chân tướng hung thủ bị lật tẩy sau ít tiếng điều tra.', 'an.jpg', NULL, '<h1 class="Title">Vạch mặt hung thủ hại h&agrave;ng x&oacute;m</h1>\r\n<h2 class="Lead">Sau khi hại chết b&agrave; l&atilde;o h&agrave;ng x&oacute;m, T&aacute;m lấy đ&ocirc;i hoa tai mang về giấu kỹ trong h&ograve;m c&aacute; nh&acirc;n. Ch&acirc;n tướng hung thủ bị lật tẩy sau &iacute;t tiếng điều tra.</h2>\r\n<p class="Normal">Ng&agrave;y 14/1, Cơ quan điều tra c&ocirc;ng an tỉnh Bắc Giang cho biết đ&atilde; khởi tố v&agrave; bắt tạm giam H&agrave; Văn T&aacute;m (24 tuổi ở huyện Lục Ngan) về h&agrave;nh vi giết người, cướp t&agrave;i sản.</p>\r\n<p class="Normal">Theo cơ quan điều tra, tối v&agrave;i ng&agrave;y trước đ&oacute;, lợi dụng b&agrave; Leo Thị T&acirc;m (63 tuổi) ở nh&agrave; một m&igrave;nh, hung thủ cầm ống tu&yacute;p sắt mai phục trong vườn. Thấy b&agrave; l&atilde;o vừa tắm giặt đi ra s&acirc;n, t&ecirc;n n&agrave;y lao từ đằng sau phang đến khi nạn nh&acirc;n nằm bất động. Sau khi g&acirc;y &aacute;n, T&aacute;m th&aacute;o đ&ocirc;i hoa tai của b&agrave; T&acirc;m mang về giấu ở h&ograve;m c&aacute; nh&acirc;n.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="an.jpg" border="1" alt="C&aacute;c điều tra vi&ecirc;n lấy lời khai hung thủ." width="490" height="362" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">C&aacute;c điều tra vi&ecirc;n lấy lời khai hung thủ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ocirc;ng an x&aacute;c định hung thủ phải l&agrave; người quen biết v&agrave; nam thanh ni&ecirc;n 24 tuổi sống cạnh nh&agrave; nạn nh&acirc;n đ&atilde; được triệu tập l&ecirc;n UBND x&atilde;. Tại đ&acirc;y, người n&agrave;y lu&ocirc;n đưa ra những l&yacute; do, dẫn chứng chứng minh m&igrave;nh v&ocirc; can trước c&aacute;i chết của b&agrave; l&atilde;o.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, sau v&agrave;i tiếng biết kh&ocirc;ng qua được mặt c&aacute;c điều tra vi&ecirc;n, T&aacute;m đ&atilde; thừa nhận h&agrave;nh vi của m&igrave;nh. Hung thủ khai do vợ mới sinh con nhỏ, kinh tế gia đ&igrave;nh eo hẹp n&ecirc;n v&agrave;i ng&agrave;y trước khi g&acirc;y &aacute;n c&oacute; đi l&agrave;m đồng ph&aacute;t hiện b&agrave; T&acirc;m c&oacute; đeo đ&ocirc;i khuy&ecirc;n tai bằng v&agrave;ng đ&atilde; nảy sinh &yacute; đồ cướp.</p>\r\n<p class="Normal">Cảnh s&aacute;t cho hay, T&aacute;m mồ c&ocirc;i cha mẹ từ nhỏ. Được sự cưu mang của của người th&acirc;n t&ecirc;n n&agrave;y đ&atilde; ăn học hết THPT, đ&atilde; x&acirc;y dựng gia đ&igrave;nh v&agrave; c&oacute; con nhỏ.</p>\r\n<p class="Normal">Sau 9 tiếng kể từ khi nhận được điện b&aacute;o, C&ocirc;ng an tỉnh Bắc Giang đ&atilde; điều tra r&otilde;. Vụ &aacute;n được kh&aacute;m ph&aacute; trong đợt cao điểm tấn c&ocirc;ng trấn &aacute;p tội phạm, bảo vệ Tết Nguy&ecirc;n đ&aacute;n được C&ocirc;ng an tỉnh ph&aacute;t động từ 4/1 đến 28/2.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Đặng Nam - Phi H&ugrave;ng</strong></p>', 25, 6, 2785, 0, 1),
(707, 'Tiếp thị gái gọi 9X ở quán nước vỉa hè', 'Sỹ nuôi gần 10 thôn nữ mới lớn, trả lương hằng tháng để họ đi bán dâm theo yêu cầu. Đường dây gái gọi này được tiếp thị đến nhiều quán nước ở Hà Nội.', 'mai-dam.jpg', NULL, '<h1 class="Title">Tiếp thị g&aacute;i gọi 9X ở qu&aacute;n nước vỉa h&egrave;</h1>\r\n<h2 class="Lead">Sỹ nu&ocirc;i gần 10 th&ocirc;n nữ mới lớn, trả lương hằng th&aacute;ng để họ đi b&aacute;n d&acirc;m theo y&ecirc;u cầu. Đường d&acirc;y g&aacute;i gọi n&agrave;y được tiếp thị đến nhiều qu&aacute;n nước ở H&agrave; Nội.</h2>\r\n<p class="Normal">Ng&agrave;y 5/1, C&ocirc;ng an quận Cầu Giấy (H&agrave; Nội) cho biết vừa ph&aacute; đường d&acirc;y g&aacute;i gọi tuổi đ&ocirc;i mươi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="mai-dam.jpg" alt="" width="400" height="240" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Những người c&oacute; li&ecirc;n quan tại cơ quan điều tra.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo cơ quan điều tra, ập v&agrave;o một kh&aacute;ch sạn tr&ecirc;n đường Trần Duy Hưng cảnh s&aacute;t bắt quả tang 2 đ&ocirc;i nam nữ mua b&aacute;n d&acirc;m. Kh&aacute;ch l&agrave;ng chơi khai li&ecirc;n hệ với Chu Th&ugrave;y Linh nhờ t&igrave;m "h&agrave;ng" với gi&aacute; 500.000 đồng một lần. Qua m&ocirc;i giới của Linh, Đỗ Văn Sỹ đ&atilde; điều g&aacute;i mại d&acirc;m đến "phục vụ".</p>\r\n<p class="Normal">Linh c&ugrave;ng Sỹ v&agrave; "tay ch&acirc;n" Cao Văn Lệ bị tạm giữ về h&agrave;nh vi m&ocirc;i giới mại d&acirc;m. Kiểm tra nơi ở của Sỹ, c&ocirc;ng an ph&aacute;t hiện 5 c&ocirc; g&aacute;i đang ngồi chờ đợi đến lượt đi b&aacute;n d&acirc;m. Họ đều tuổi mới lớn, đến từ một tỉnh ở miền Trung.</p>\r\n<p class="Normal">Tại cơ quan điều tra, Sỹ khai đ&atilde; "chăn dắt" c&aacute;c thiếu nữ n&agrave;y, nu&ocirc;i ăn ở. Mỗi lần đưa người đi b&aacute;n d&acirc;m "T&uacute; &ocirc;ng" thu 500.000-1,5 triệu đồng. Tuy nhi&ecirc;n, số tiền n&agrave;y c&aacute;c "đ&agrave;o" kh&ocirc;ng được Sỹ chia ch&aacute;c m&agrave; chỉ được ph&aacute;t lương v&agrave;o cuối th&aacute;ng. Với c&ocirc;ng chở g&aacute;i đến nơi h&agrave;nh lạc, Lệ được hưởng 50.000 đồng mỗi lượt; c&ograve;n Linh nhận 100.000 đồng tiền m&ocirc;i giới.</p>\r\n<p class="Normal">Cảnh s&aacute;t l&agrave;m r&otilde;, h&agrave;ng ng&agrave;y Linh l&ecirc; la ở nhiều qu&aacute;n nước ven đường Nguyễn Khang, khu vực Cầu Giấy để "tiếp thị" về đường d&acirc;y g&aacute;i gọi của của Sỹ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 1416, 0, 1),
(708, 'Kẻ sát hại bạn gái rồi lẩn trốn suốt 10 năm bị bắt', 'Bị người yêu kháng cự quyết liệt, Tuấn lấy giầy đang đi đập vào mặt rồi đẩy cô gái xuống mương nước. Hung thủ bỏ trốn sau khi dìm nạn nhân đến chết và cướp đi một số nữ trang.', 'tuan.jpg', NULL, '<h1 class="Title">Kẻ s&aacute;t hại bạn g&aacute;i rồi lẩn trốn suốt 10 năm bị bắt</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="tuan.jpg" alt="Trịnh Văn Tuấn." width="250" height="195" align="left" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trịnh Văn Tuấn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Bị người y&ecirc;u kh&aacute;ng cự quyết liệt, Tuấn lấy giầy đang đi đập v&agrave;o mặt rồi đẩy c&ocirc; g&aacute;i xuống mương nước. Hung thủ bỏ trốn sau khi d&igrave;m nạn nh&acirc;n đến chết v&agrave; cướp đi một số nữ trang.</h2>\r\n<p class="Normal">Ng&agrave;y 4/1, Đội bắt truy n&atilde; (C&ocirc;ng an H&agrave; Nội) cho biết vừa bắt được Trịnh Văn Tuấn (31 tuổi ở huyện Thạch Thất, H&agrave; Nội). Tuấn được x&aacute;c định l&agrave; kẻ s&aacute;t hại người y&ecirc;u cũ chục năm về trước v&agrave; bị cơ quan cảnh s&aacute;t điều tra ph&aacute;t lệnh truy n&atilde; to&agrave;n quốc.</p>\r\n<p class="Normal">Theo hồ sơ vụ &aacute;n, s&aacute;ng một ng&agrave;y đầu th&aacute;ng 8/2000, người d&acirc;n huyện Thạch Thất ph&aacute;t hiện x&aacute;c c&ocirc; g&aacute;i chết ở mương nước thuộc c&aacute;nh đồng Săn. Nạn nh&acirc;n được x&aacute;c định l&agrave; Cấn Thị Duy&ecirc;n (19 tuổi).</p>\r\n<p class="Normal">Người nh&agrave; nạn nh&acirc;n xấu số cho biết, tối h&ocirc;m trước khi xảy ra vụ &aacute;n, Duy&ecirc;n c&oacute; ra ngo&agrave;i c&ugrave;ng bạn trai l&agrave; Trịnh Văn Tuấn. Đ&ecirc;m đ&oacute; kh&ocirc;ng thấy con về họ tổ chức t&igrave;m kiếm nhưng kh&ocirc;ng c&oacute; kết quả.</p>\r\n<p class="Normal">20 ng&agrave;y điều tra, Trịnh Văn Tuấn bị cảnh s&aacute;t ph&aacute;t lệnh truy n&atilde; to&agrave;n quốc. Th&aacute;ng 12/2010, c&ocirc;ng an tỉnh C&agrave; Mau nhận được một tờ giấy nặc danh với nội dung: &ldquo;Trịnh Văn Tuấn, qu&ecirc; ở Thạch Thất, H&agrave; Nội can tội giết người hiện đ&atilde; đổi t&ecirc;n th&agrave;nh Trần Minh Ph&uacute;c&hellip;&rdquo;. Vụ việc đ&atilde; được b&aacute;o c&ocirc;ng an H&agrave; Nội.</p>\r\n<p class="Normal">Sau một tuần, c&aacute;n bộ Đội bắt truy n&atilde; x&aacute;c định Trần Minh Ph&uacute;c, sống ở x&atilde; Kh&aacute;nh An, huyện U Minh (C&agrave; Mau) gần chục năm nay kh&ocirc;ng đăng k&yacute; hộ khẩu m&agrave; chỉ l&agrave;m tạm tr&uacute;. Ch&acirc;n tướng Ph&uacute;c bị lộ ra từ đ&acirc;y. Theo cơ quan điều tra, Trần Minh Ph&uacute;c v&agrave; kẻ đang bị ph&aacute;t lệnh truy n&atilde; Trịnh Văn Tuấn l&agrave; một.</p>\r\n<p class="Normal">Cuối th&aacute;ng 12, kế hoạch bắt kẻ phạm tội n&agrave;y đ&atilde; được đưa ra. Theo lịch tr&igrave;nh, 4h s&aacute;ng h&agrave;ng ng&agrave;y, Tuấn v&agrave;o khu rừng tr&agrave;m để l&agrave;m việc. C&ocirc;ng việc của người n&agrave;y chỉ kết th&uacute;c l&uacute;c 14h. Tuy nhi&ecirc;n, ng&agrave;y 22/12, kẻ phạm tội n&agrave;y kh&ocirc;ng xuất hiện theo lịch l&agrave;m việc khiến cảnh s&aacute;t phải ập v&agrave;o nh&agrave; bắt khi anh ta đang nằm tr&ecirc;n v&otilde;ng bế con.</p>\r\n<p class="Normal">Tại cơ quan điều tra, Tuấn khai, khoảng 19h ng&agrave;y 2/8 của mười năm về trước, sau khi đi ăn ch&egrave; đ&atilde; đưa đưa bạn g&aacute;i l&agrave; Cấn Thị Duy&ecirc;n ra c&aacute;nh đồng Săn ở th&ocirc;n Nội, x&atilde; Ph&uacute; K&iacute;m để t&acirc;m sự. Trong l&uacute;c tr&ograve; chuyện, Tuấn ngỏ &yacute; muốn cưới c&ocirc; g&aacute;i n&agrave;y l&agrave;m vợ nhưng kh&ocirc;ng được chấp nhận dẫn đến x&ocirc; x&aacute;t.</p>\r\n<p class="Normal">Cho rằng, Duy&ecirc;n muốn giữ trinh tiết cho người đ&agrave;n &ocirc;ng kh&aacute;c, nam thanh ni&ecirc;n 21 tuổi đ&atilde; giở tr&ograve; đồi bại với ch&iacute;nh người y&ecirc;u của m&igrave;nh nhưng bị Duy&ecirc;n kh&aacute;ng cự quyết liệt. Đang cơn tức, Tuấn d&ugrave;ng chiếc gi&agrave;y đang đi đập v&agrave;o mặt rồi đẩy d&igrave;m nạn nh&acirc;n ng&atilde; xuống mương nước.</p>\r\n<p class="Normal">Sau khi g&acirc;y &aacute;n, Tuấn giật đ&ocirc;i hoa tai c&ugrave;ng sợi d&acirc;y chuyền đeo cổ của c&ocirc; bạn g&aacute;i rồi bỏ trốn. Dọc đường đi, anh ta b&aacute;n số đồ cướp được lấy 2 triệu đồng.</p>\r\n<p class="Normal">Trốn v&agrave;o C&agrave; Mau, Tuấn đổi t&ecirc;n Trần Minh Ph&uacute;c (ở B&igrave;nh Định). Năm 2002, kẻ phạm tội n&agrave;y lấy vợ v&agrave; sinh được hai người con.</p>\r\n<p class="Normal">Một trinh s&aacute;t cho biết, gần 10 năm sinh sống nhưng vợ Tuấn kh&ocirc;ng hề hay biết đang sống với kẻ phạm tội. Khi bị tra tay v&agrave;o c&ograve;ng, gia đ&igrave;nh nh&agrave; vợ mới t&aacute; hỏa.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 2625, 0, 1),
(709, 'Vung dao chém nhiều người trên phố', 'Người đàn ông 3 lần đi tù Đoàn Thái Quân cầm hai con dao khua loạn xạ trong ngõ, làm bị thương 3 nạn nhân gặp trên đường. Cảnh sát nhận định, Quân bị ảo giác do dùng ma túy.', 'dao1_1.jpg', NULL, '<h1 class="Title">Vung dao ch&eacute;m nhiều người tr&ecirc;n phố</h1>\r\n<h2 class="Lead">Người đ&agrave;n &ocirc;ng 3 lần đi t&ugrave; Đo&agrave;n Th&aacute;i Qu&acirc;n cầm hai con dao khua loạn xạ trong ng&otilde;, l&agrave;m bị thương 3 nạn nh&acirc;n gặp tr&ecirc;n đường. Cảnh s&aacute;t nhận định, Qu&acirc;n bị ảo gi&aacute;c do d&ugrave;ng ma t&uacute;y.</h2>\r\n<p class="Normal">Chiều 30/12, C&ocirc;ng an quận Đống Đa, H&agrave; Nội cho biết đang tạm giữ Đo&agrave;n Th&aacute;i Qu&acirc;n (49 tuổi, khu tập thể Văn Chương) để điều tra h&agrave;nh vi d&ugrave;ng dao ch&eacute;m nhiều người bị thương.</p>\r\n<p class="Normal">Theo cơ quan điều tra, ng&agrave;y 28/12, Qu&acirc;n cầm dao bầu v&agrave; con dao d&agrave;i dọa dẫm nhiều người tại ng&otilde; Trung Tả, phường Thổ Quan. Ba nạn nh&acirc;n bị Qu&acirc;n ch&eacute;m v&ocirc; cớ l&agrave; &ocirc;ng Nguyễn Ngọc Quang (58 tuổi), b&agrave; Vũ Thị Được (63 tuổi) v&agrave; anh Đặng Tiến Huy (30 tuổi) với c&aacute;c vết thương ở đầu, tay...</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="dao1_1.jpg" alt="Hai con dao bị cơ quan điều tra thu giữ." width="450" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai con dao bị thu giữ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cơ quan điều tra nhận định, nhiều khả năng Qu&acirc;n bị ảo gi&aacute;c do d&ugrave;ng ma t&uacute;y. Người n&agrave;y từng bị kết &aacute;n 18 th&aacute;ng t&ugrave; về tội cướp t&agrave;i sản, sau đ&oacute; thụ &aacute;n 6 năm do cố &yacute; g&acirc;y thương t&iacute;ch. Trong thời gian ở trại giam, anh ta đ&aacute;nh chết bạn t&ugrave; v&agrave; nhận th&ecirc;m 10 năm t&ugrave;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Nam Anh</strong></p>', 25, 6, 2087, 0, 1),
(710, 'Hai tên cướp nhí sa lưới', 'Hàng loạt vụ cướp trang sức phụ nữ, dây chuyền và tài sản của học sinh liên tiếp xảy ra tại thành phố biển Đà Nẵng vào lúc trời nhá nhem tối. Thủ phạm được xác định là hai thanh niên mới lớn.', 'Tang_vat_vu_cuop_.jpg', NULL, '<h1 class="Title">Hai t&ecirc;n cướp nh&iacute; sa lưới</h1>\r\n<h2 class="Lead">H&agrave;ng loạt vụ cướp trang sức phụ nữ, d&acirc;y chuyền v&agrave; t&agrave;i sản của học sinh li&ecirc;n tiếp xảy ra tại th&agrave;nh phố biển Đ&agrave; Nẵng v&agrave;o l&uacute;c trời nh&aacute; nhem tối. Thủ phạm được x&aacute;c định l&agrave; hai thanh ni&ecirc;n mới lớn.</h2>\r\n<p class="Normal">Ng&agrave;y 29/12, C&ocirc;ng an quận Li&ecirc;n Chiểu, Đ&agrave; Nẵng cho biết Trương Văn Hảo (17 tuổi) v&agrave; Nguyễn Quốc Vương (17 tuổi) - bộ đ&ocirc;i g&acirc;y ra c&aacute;c vụ cướp đ&atilde; bị bắt.</p>\r\n<p class="Normal">Theo thượng t&aacute; Qu&aacute;ch Văn Dũng (Ph&oacute; trưởng C&ocirc;ng an quận Li&ecirc;n Chiểu) chiều 28/12 tr&ecirc;n đường Thanh Tịnh, 5 học sinh cấp một đi học về đ&atilde; bị hai thanh ni&ecirc;n x&ocirc;ng ra đ&aacute;nh, cướp t&agrave;i sản. Qua nhận dạng của c&aacute;c nạn nh&acirc;n, tối c&ugrave;ng ng&agrave;y Hảo v&agrave; Vương bị cảnh s&aacute;t bắt khi đang tr&ecirc;n đường ti&ecirc;u thụ "chiến lợi phẩm".</p>\r\n<p class="Normal">Tại cơ quan c&ocirc;ng an, bộ đ&ocirc;i khai, trong th&aacute;ng 12 đ&atilde; g&acirc;y ra gần 10 vụ cướp giật t&agrave;i sản của người đi đường. Vương v&agrave; Hảo chọn thời điểm "ra tay" l&agrave; l&uacute;c trời chập tối v&agrave; ở những đoạn đường vắng vẻ. Ch&uacute;ng &eacute;p s&aacute;t v&agrave;o xe m&aacute;y của nạn nh&acirc;n, d&ugrave;ng vũ lực đe dọa để lấy bằng được t&agrave;i sản. Bị hại chủ yếu l&agrave; phụ nữ đi xe m&aacute;y một m&igrave;nh, sinh vi&ecirc;n v&agrave; học sinh ở địa b&agrave;n c&aacute;c quận Cẩm Lệ, Thanh Kh&ecirc; v&agrave; Li&ecirc;n Chiểu.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Tang_vat_vu_cuop_.jpg" alt="" width="450" height="280" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Một trong c&aacute;c tang vật được thu hồi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cơ quan c&ocirc;ng an cho rằng số vụ m&agrave; hai thanh ni&ecirc;n n&agrave;y g&acirc;y ra nhiều so với khai b&aacute;o. C&ocirc;ng an Li&ecirc;n Chiểu đang mở rộng điều tra.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Nhật</strong></p>', 25, 6, 1872, 0, 1),
(711, 'Teen vác dao rình trước cổng trường', 'Kiểm tra balô của 4 học sinh trong quán trà đá, cảnh sát phát hiện có con dao sắc lẹm. Nhóm teen khai, do mâu thuẫn với vài học sinh THCS Dịch Vọng nên rủ nhau rình trước cổng trường để dằn mặt "đối thủ".', 'phong.jpg', NULL, '<h1 class="Title">Teen v&aacute;c dao r&igrave;nh trước cổng trường</h1>\r\n<h2 class="Lead">Kiểm tra bal&ocirc; của 4 học sinh trong qu&aacute;n tr&agrave; đ&aacute;, cảnh s&aacute;t ph&aacute;t hiện c&oacute; con dao sắc lẹm. Nh&oacute;m teen khai, do m&acirc;u thuẫn với v&agrave;i học sinh THCS Dịch Vọng n&ecirc;n rủ nhau r&igrave;nh trước cổng trường để dằn mặt "đối thủ".</h2>\r\n<p class="Normal">Ng&agrave;y 24/12, trung t&aacute; B&ugrave;i Văn Đang, Trưởng c&ocirc;ng an phường Dịch Vọng (Cầu Giấy, H&agrave; Nội) cho biết đơn vị vừa kịp thời ngăn chặn một cuộc hỗn chiến giữa hai nh&oacute;m tuổi teen.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="phong.jpg" border="1" alt="Tang vật bị cảnh s&aacute;t thu giữ." width="490" height="332" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tang vật bị cảnh s&aacute;t thu giữ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo cơ quan điều tra, trưa 23/12, c&ocirc;ng an phường nhận được tin b&aacute;o tại cổng trường THCS Dịch Vọng tr&ecirc;n đường Nguyễn Kh&aacute;nh To&agrave;n c&oacute; 2 tốp học sinh chuẩn bị đ&aacute;nh nhau. Hai c&ocirc;ng an được cử xuống hiện trường.</p>\r\n<p class="Normal">Kiểm tra h&agrave;nh ch&iacute;nh 4 thiếu ni&ecirc;n c&oacute; dấu hiệu khả nghi đang ngồi trong qu&aacute;n tr&agrave; đ&aacute;, cảnh s&aacute;t ph&aacute;t hiện trong bal&ocirc; của họ c&oacute; con dao phay sắc lẹm. Tại trụ sở, nh&oacute;m teen khai t&ecirc;n l&agrave; Nghi&ecirc;m Văn Hiến, Tạ Đăng Giang, Nguyễn Tiến Đạt (c&ugrave;ng 15 tuổi) v&agrave; L&ecirc; Xu&acirc;n Trường (13 tuổi). Cả 4 đều l&agrave; học sinh tr&ecirc;n địa b&agrave;n phường.</p>\r\n<p class="Normal">Theo lời khai, trước đ&oacute; mấy h&ocirc;m, qua chat tr&ecirc;n mạng, nh&oacute;m n&agrave;y c&oacute; m&acirc;u thuẫn với một số học sinh của trường THCS Dịch Vọng n&ecirc;n rủ nhau đến đ&acirc;y để dằn mặt. Tuy nhi&ecirc;n, nh&oacute;m teen chưa kịp ra tay h&agrave;nh động th&igrave; bị ph&aacute;t gi&aacute;c.</p>\r\n<p class="Normal">C&ocirc;ng an phường Dịch Vọng cho biết đang phối hợp với nh&agrave; trường, gia đ&igrave;nh gi&aacute;o dục số học sinh tr&ecirc;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Th&aacute;i Thịnh</strong></p>', 25, 6, 1841, 0, 1),
(712, 'Truy đuổi nhóm cướp xế hộp trong đêm', 'Ba thanh niên khống chế người lái, cướp chiếc Toyota Fortuner mang ra nước ngoài tiêu thụ nhưng đã bị vây bắt giữa đêm.', 'cuop.jpg', NULL, '<h1 class="Title">Truy đuổi nh&oacute;m cướp xế hộp trong đ&ecirc;m</h1>\r\n<h2 class="Lead">Ba thanh ni&ecirc;n khống chế người l&aacute;i, cướp chiếc Toyota Fortuner mang ra nước ngo&agrave;i ti&ecirc;u thụ nhưng đ&atilde; bị v&acirc;y bắt giữa đ&ecirc;m.</h2>\r\n<p class="Normal">Khoảng 22h ng&agrave;y 20/12, nhận tin b&aacute;o c&oacute; nh&oacute;m cướp &ocirc;t&ocirc; tr&ecirc;n đường th&aacute;o chạy từ Thừa Thi&ecirc;n - Huế ra th&agrave;nh phố Đ&ocirc;ng H&agrave;, Quảng Trị, cảnh s&aacute;t 113 c&ugrave;ng c&ocirc;ng an giao th&ocirc;ng v&agrave; nhiều lực lượng tổ chức v&acirc;y bắt.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cuop.jpg" alt="Chiếc &ocirc;t&ocirc; bị cướp chặn bắt. Ảnh: Phong Ba." width="490" height="325" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Chiếc &ocirc;t&ocirc; bị cướp chặn bắt. Ảnh:&nbsp;<em>Phong Ba.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Bị truy đuổi, chiếc Toyota Fortuner bỏ chạy về hướng huyện Cam Lộ. Đến T&acirc;n Tr&uacute;c, x&atilde; Cam Hiếu, C&ocirc;ng an Quảng Trị chặn được đầu xe, ba thanh ni&ecirc;n ngồi b&ecirc;n trong đạp cửa chạy v&agrave;o khu d&acirc;n cư gần đ&oacute;.</p>\r\n<p class="Normal">Sau một giờ truy đuổi, hai người trong số n&agrave;y đ&atilde; bị bắt. Trường hợp c&ograve;n lại lẩn trốn trong rừng. Kh&aacute;m xe tại hiện trường, cảnh s&aacute;t ph&aacute;t hiện &ocirc;t&ocirc; đăng k&yacute; biển số tại H&agrave; Nội đ&atilde; đeo biển giả một tỉnh ph&iacute;a Nam. Trong xe c&ograve;n c&oacute; nhiều biển giả kh&aacute;c v&agrave; 3 tấm hộ chiếu.</p>\r\n<p class="Normal">Tại cơ quan c&ocirc;ng an, hai người bị bắt khai t&ecirc;n Nguyễn Văn Nam (23 tuổi, Vĩnh Ph&uacute;c) v&agrave; Nguyễn Văn Thắng (22 tuổi, tỉnh Tuy&ecirc;n Quang). Họ thừa nhận thu&ecirc; xe ở TP HCM dọc đường đi v&agrave; đ&atilde; cướp &ocirc;t&ocirc;, định đưa sang L&agrave;o ti&ecirc;u thụ.</p>\r\n<p class="Normal">Cảnh s&aacute;t đang truy l&ugrave;ng can phạm c&ograve;n lại v&agrave; điều tra l&agrave;m r&otilde; vụ việc.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Phong Ba</strong></p>', 25, 6, 1076, 0, 1),
(713, 'Cầm dao phay đi giật túi xách', 'Tính rủ bạn chí cốt từ quê lên Hà Nội, sắm dao và khẩu trang bịt mặt để đi cướp.', 'tinh-1.jpg', NULL, '<h1 class="Title">Cầm dao phay đi giật t&uacute;i x&aacute;ch</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="tinh-1.jpg" alt="T&iacute;nh tại cơ quan điều tra." width="250" height="195" align="left" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">T&iacute;nh tại cơ quan điều tra.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">T&iacute;nh rủ bạn ch&iacute; cốt từ qu&ecirc; l&ecirc;n H&agrave; Nội, sắm dao v&agrave; khẩu trang bịt mặt để đi cướp.</h2>\r\n<p class="Normal">Ng&agrave;y 17/12, trung t&aacute; Ho&agrave;ng Văn Bốn (Trưởng c&ocirc;ng an phường Y&ecirc;n H&ograve;a, Cầu Giấy, H&agrave; Nội) cho biết vừa bắt hai t&ecirc;n cướp bịt khẩu trang, giật t&uacute;i x&aacute;ch phụ nữ tr&ecirc;n đường Phạm H&ugrave;ng.</p>\r\n<p class="Normal">Theo cảnh s&aacute;t, đầu năm 2010, Nguyễn Văn T&iacute;nh m&atilde;n hạn t&ugrave; v&igrave; tội cố &yacute; g&acirc;y thương t&iacute;ch. Kh&ocirc;ng c&oacute; c&ocirc;ng ăn việc, T&iacute;nh nghĩ c&aacute;ch đi cướp để c&oacute; tiền ti&ecirc;u s&agrave;i.</p>\r\n<p class="Normal">Chiều 16/12, anh ta gọi điện thoại rủ bạn th&acirc;n Trần Xu&acirc;n Huy từ huyện Thanh Oai l&ecirc;n H&agrave; Nội để thực hiện &acirc;m mưu. Trước khi ra tay h&agrave;nh động, cả hai mua dao phay v&agrave; khẩu trang bịt mặt.</p>\r\n<p class="Normal">Ngay h&ocirc;m đ&oacute;, l&ograve;ng v&ograve;ng qua nhiều tuyến phố ở quận Cầu Giấy v&agrave; huyện Từ Li&ecirc;m, bộ đ&ocirc;i ph&aacute;t hiện người phụ nữ l&aacute;i xe m&aacute;y một m&igrave;nh ở gần Trung t&acirc;m Hội nghị Quốc gia. Sau khi tăng ga &aacute;p s&aacute;t giật được chiếc t&uacute;i ở giỏ xe (b&ecirc;n trong c&oacute; điện thoại, gần một triệu đồng) ch&uacute;ng ph&oacute;ng xe bỏ chạy về khu vực s&acirc;n b&oacute;ng trong khu đ&ocirc; thị mới của phường Y&ecirc;n Ho&agrave;.</p>\r\n<p class="Normal">Cảnh s&aacute;t ập đến bắt bộ đ&ocirc;i sau khi kiểm tra h&agrave;nh ch&iacute;nh v&igrave; c&oacute; những dấu hiệu khả nghi. Tại trụ sở cảnh s&aacute;t, T&iacute;nh v&agrave; Huy khai to&agrave;n bộ h&agrave;nh vi. Hiện hai can phạm đ&atilde; được chuyển sang C&ocirc;ng an Cầu Giấy để điều tra h&agrave;nh vi cướp giật t&agrave;i sản.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 715, 0, 1),
(717, 'Chủ quán nước chè giết con nợ', 'Đòi mãi không được tiền đã cho vay, Minh cầm dao đi "nói chuyện" với con nợ. Trong cơn tức tối, hắn đã đâm 6 nhát vào người nạn nhân.', 'minh.jpg', NULL, '<h1 class="Title">Chủ qu&aacute;n nước ch&egrave; giết con nợ</h1>\r\n<h2 class="Lead">Đ&ograve;i m&atilde;i kh&ocirc;ng được tiền đ&atilde; cho vay, Minh cầm dao đi "n&oacute;i chuyện" với con nợ. Trong cơn tức tối, hắn đ&atilde; đ&acirc;m 6 nh&aacute;t v&agrave;o người nạn nh&acirc;n.</h2>\r\n<p class="Normal">S&aacute;ng nay, C&ocirc;ng an quận Đống Đa, H&agrave; Nội đ&atilde; khởi tố v&agrave; tạm giam B&ugrave;i Đ&igrave;nh Minh (26 tuổi) về h&agrave;nh vi giết người. Nạn nh&acirc;n l&agrave; anh B&ugrave;i Thọ Ngọ (32 tuổi, ở phố Trần Qu&yacute; C&aacute;p).</p>\r\n<p class="Normal">Theo cơ quan điều tra, Minh cho anh Ngọ vay một triệu đồng từ th&aacute;ng 8/2009. Sau nhiều lần đ&ograve;i nhưng con nợ khất lần khiến Minh tức tối. C&aacute;ch đ&acirc;y 2 ng&agrave;y, Minh mua dao chọc tiết lợn giấu sẵn ở nh&agrave; v&agrave; đợi thời cơ "ra tay" với anh Ngọ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="minh.jpg" alt="Minh tại cơ quan điều tra." width="450" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Minh tại cơ quan điều tra.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chiều 7/12, thấy anh Ngọ đang cho g&agrave; chọi ăn tr&ecirc;n vỉa h&egrave; ở phố Trần Qu&yacute; C&aacute;p, Minh từ b&ecirc;n kia đường lao sang, đ&acirc;m 2 nh&aacute;t v&agrave;o ph&iacute;a sau lưng nạn nh&acirc;n. Anh Ngọ van xin nhưng chủ nợ đ&acirc;m th&ecirc;m nhiều nh&aacute;t dao khiến anh n&agrave;y gục xuống bất tỉnh v&agrave; tử vong tại bệnh viện. B&aacute;c sĩ x&aacute;c định nạn nh&acirc;n bị mất m&aacute;u cấp, tr&ecirc;n cơ thể c&oacute; 6 vết đ&acirc;m.</p>\r\n<p class="Normal">Tại cơ quan điều tra, Minh khai giết người v&igrave; bức x&uacute;c kh&ocirc;ng đ&ograve;i được tiền trong khi gia cảnh đang quẫn b&aacute;ch. Minh đang nu&ocirc;i vợ v&agrave; con nhỏ 3 th&aacute;ng bằng qu&aacute;n nước ch&egrave; ở phố Trần Qu&yacute; C&aacute;p.</p>\r\n<p class="Normal" style="text-align: right;"><strong>N.Anh</strong></p>', 25, 6, 1494, 0, 1),
(718, 'Côn đồ đi xế hộp xả súng bắn chết người', 'Hơn 10 thanh niên đi hai ôtô bất ngờ ập đến nhà anh Đình đập phá tài sản và đuổi đánh gia chủ. Chúng nổ súng làm một người chết và 3 trường hợp bị thương.', 'ban_sung-400.jpg', NULL, '<h1 class="Title">C&ocirc;n đồ đi xế hộp xả s&uacute;ng bắn chết người</h1>\r\n<h2 class="Lead">Hơn 10 thanh ni&ecirc;n đi hai &ocirc;t&ocirc; bất ngờ ập đến nh&agrave; anh Đ&igrave;nh đập ph&aacute; t&agrave;i sản v&agrave; đuổi đ&aacute;nh gia chủ. Ch&uacute;ng nổ s&uacute;ng l&agrave;m một người chết v&agrave; 3 trường hợp bị thương.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ban_sung-400.jpg" border="1" alt="" width="400" height="278" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nơi nh&oacute;m c&ocirc;n đồ nổ s&uacute;ng v&agrave;o 4 nạn nh&acirc;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&Aacute;n mạng xảy ra chiều 6/12 tại khu vực x&oacute;m 21, x&atilde; Nghi Phong, huyện Nghi Lộc, Nghệ An.</p>\r\n<p class="Normal">&Ocirc;ng Phạm Dũng Sỹ (Trưởng c&ocirc;ng an x&atilde; Nghi Phong) cho biết khoảng 14h30 c&oacute; khoảng 10-12 người đi &ocirc;t&ocirc; mang theo dao kiếm, s&uacute;ng, gậy gộc&hellip; x&ocirc;ng v&agrave;o nh&agrave; anh Nguyễn C&ocirc;ng Đ&igrave;nh. Bị đ&aacute;m đ&ocirc;ng đập ph&aacute; t&agrave;i sản v&agrave; v&acirc;y đ&aacute;nh, anh Đ&igrave;nh h&ocirc; ho&aacute;n. Người d&acirc;n xung quanh nghe thấy chạy đến th&igrave; bị ch&uacute;ng nổ s&uacute;ng uy hiếp.</p>\r\n<p class="Normal">Sau tiếng s&uacute;ng nổ, 4 người gục xuống, nh&oacute;m c&ocirc;n đồ l&ecirc;n xe bỏ chạy. Cảnh s&aacute;t cho biết vụ xả s&uacute;ng đ&atilde; khiến anh Nguyễn Hữu Phước (32 tuổi) tử vong. Bố con anh Nguyễn Hữu H&ograve;a (32 tuổi) v&agrave; một người kh&aacute;c bị thương.</p>\r\n<p class="Normal">Một số người chứng kiến sự việc cho biết, trong l&uacute;c đập ph&aacute; nh&oacute;m c&ocirc;n đồ tỏ th&aacute;i độ th&aacute;ch thức những h&agrave;ng x&oacute;m của anh Đ&igrave;nh: "Thằng m&ocirc; th&iacute;ch đập th&igrave; ra đ&acirc;y&rdquo;. Khi 4 người tr&ecirc;n vừa chạy đến hiện trường th&igrave; lập tức bị d&iacute;nh đạn.</p>\r\n<p class="Normal">C&ocirc;ng an huyện Nghi Lộc đang l&agrave;m r&otilde; nguy&ecirc;n nh&acirc;n vụ việc v&agrave; truy t&igrave;m những kẻ li&ecirc;n quan vụ nổ s&uacute;ng. Theo nhận định ban đầu, nh&oacute;m c&ocirc;n đồ đ&atilde; sử dụng s&uacute;ng săn bắn đạn hoa cải.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Trường Long</strong></p>', 25, 6, 661, 0, 1),
(719, 'Thiếu niên 14 tuổi quấy rối cảnh sát 113', 'Dùng điện thoại di động gọi vào tổng đài 113 quấy rối, chửi bới cán bộ, chiến sĩ trực, Thạch Phụng (14 tuổi) đã bị cơ quan chức năng bắt giữ.', 'quay-roi.jpg', NULL, '<h1 class="Title">Thiếu ni&ecirc;n 14 tuổi quấy rối cảnh s&aacute;t 113</h1>\r\n<h2 class="Lead">D&ugrave;ng điện thoại di động gọi v&agrave;o tổng đ&agrave;i 113 quấy rối, chửi bới c&aacute;n bộ, chiến sĩ trực, Thạch Phụng (14 tuổi) đ&atilde; bị cơ quan chức năng bắt giữ.</h2>\r\n<p class="Normal">Ng&agrave;y 7/12, thiếu t&aacute; Huỳnh Văn Hai, Đội trưởng Cảnh s&aacute;t 113, C&ocirc;ng an B&igrave;nh Dương cho biết, ng&agrave;y 6/12, cảnh s&aacute;t đ&atilde; t&igrave;m ra nơi ở của Nguyễn Dũng Phương Thanh (26 tuổi) sau khi x&aacute;c định người n&agrave;y đ&atilde; c&oacute; h&agrave;nh vi d&ugrave;ng điện thoại quấy rối tổng đ&agrave;i 113. Theo đ&oacute;, nhiều lần từ chỗ l&agrave;m l&agrave; một mỏ đ&aacute;, Thanh đ&atilde; d&ugrave;ng điện thoại di động gọi v&agrave;o tổng đ&agrave;i 113 nhưng kh&ocirc;ng n&oacute;i g&igrave;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="quay-roi.jpg" border="1" alt="Hai nghi can quấy rối cảnh s&aacute;t. Ảnh: Minh Tuệ" width="480" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai nghi can quấy rối cảnh s&aacute;t. Ảnh:&nbsp;<em>Minh Tuệ</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Với "sở th&iacute;ch qu&aacute;i đản" tương tự, Thạch Phụng (14 tuổi, ngụ huyện Thống Nhất, Đồng Nai) đ&atilde; d&ugrave;ng di động gọi hơn 30 cuộc để chửi bới, văng tục nh&acirc;n vi&ecirc;n trực đ&agrave;i cũng bị sờ g&aacute;y.</p>\r\n<p class="Normal">Trước đ&oacute; ng&agrave;y 21/11, cơ quan chức năng đ&atilde; tạm giữ Nguyễn Văn Hưng (22 tuổi, qu&ecirc; Tr&agrave; Vinh) v&agrave; Nguyễn Văn Trường (17 tuổi, qu&ecirc; C&agrave; Mau) v&igrave; h&agrave;nh vi quấy rối c&aacute;n bộ l&agrave;m nhiệm vụ. Bộ đ&ocirc;i n&agrave;y đ&atilde; "trả miếng" lực lượng Cảnh s&aacute;t 113 sau khi bị C&ocirc;ng an x&atilde; Thuận Giao mời l&ecirc;n l&agrave;m việc v&igrave; nghi can li&ecirc;n quan đến một vụ đ&aacute;nh nhau giữa 2 băng nh&oacute;m. Cả hai đ&atilde; bị xử phạt h&agrave;nh ch&iacute;nh hơn 3 triệu đồng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Minh Tuệ - Hữu Thuận</strong></p>', 25, 6, 750, 0, 1),
(720, 'Nữ nhân viên thuê người hành hung sếp', 'Chủ mưu thuê người đánh, xịt hơi cay, đổ sơn vào người hiệu trưởng trường THPT Lê Quý Đôn (Hà Nội) được xác định là nữ nhân viên thư viện của trường. 7 người liên quan vụ việc đã bị tạm giữ.', 'hang.jpg', NULL, '<h1 class="Title">Nữ nh&acirc;n vi&ecirc;n thu&ecirc; người h&agrave;nh hung sếp</h1>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n<tbody>\r\n<tr>\r\n<td><img src="hang.jpg" border="1" alt="L&ecirc; Thị Thu Hằng." width="250" height="195" align="left" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&ecirc; Thị Thu Hằng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class="Lead">Chủ mưu thu&ecirc; người đ&aacute;nh, xịt hơi cay, đổ sơn v&agrave;o người hiệu trưởng trường THPT L&ecirc; Qu&yacute; Đ&ocirc;n (H&agrave; Nội) được x&aacute;c định l&agrave; nữ nh&acirc;n vi&ecirc;n thư viện của trường. 7 người li&ecirc;n quan vụ việc đ&atilde; bị tạm giữ.</h2>\r\n<p class="Normal">Ng&agrave;y 6/12, C&ocirc;ng an H&agrave; Nội cho biết đ&atilde; x&aacute;c định nh&oacute;m người li&ecirc;n quan vụ "dằn mặt" &ocirc;ng Nguyễn Sỹ Khi&ecirc;m - Hiệu trưởng THPT L&ecirc; Qu&yacute; Đ&ocirc;n (H&agrave; Đ&ocirc;ng, H&agrave; Nội). Hầu hết họ ở huyện Thủy Nguy&ecirc;n, Hải Ph&ograve;ng. Trong số n&agrave;y c&oacute; một nam sinh vi&ecirc;n năm cuối Đại học Kiến tr&uacute;c H&agrave; Nội. Chủ mưu l&agrave; L&ecirc; Thị Thu Hằng (40 tuổi).</p>\r\n<p class="Normal">C&ocirc;ng an H&agrave; Nội x&aacute;c định, sau một thời gian l&agrave;m nhiệm vụ tr&ocirc;ng giữ kho s&aacute;ch ở trường THPT L&ecirc; Qu&yacute; Đ&ocirc;n, b&agrave; Hằng đ&atilde; tự &yacute; đi học lớp Cao đẳng thư viện ở Cao đẳng Nội vụ. Sau một thời gian, nơi đang theo học y&ecirc;u cầu phải c&oacute; x&aacute;c nhận của trường L&ecirc; Qu&yacute; Đ&ocirc;n việc b&agrave; Hằng l&agrave; c&aacute;n bộ thư viện nhưng &ocirc;ng Khi&ecirc;m chỉ x&aacute;c nhận cho nh&acirc;n vi&ecirc;n h&agrave;nh ch&iacute;nh.</p>\r\n<p class="Normal">Tức tối với sếp, b&agrave; Hằng đ&atilde; nhiều lần c&oacute; lời lẽ lăng mạ v&agrave; dọa đ&aacute;nh &ocirc;ng Khi&ecirc;m; đồng thời gửi thư tố c&aacute;o đến nhiều ban ng&agrave;nh của th&agrave;nh phố. Do tố c&aacute;o kh&ocirc;ng đ&uacute;ng sự thật, nữ nh&acirc;n vi&ecirc;n thư viện n&agrave;y bị Thanh tra Sở gi&aacute;o dục giao Ban gi&aacute;m hiệu trường kỷ luật.</p>\r\n<p class="Normal">Sau sự việc tr&ecirc;n, Hằng nhờ người quen t&igrave;m người dằn mặt &ocirc;ng Khi&ecirc;m c&ugrave;ng một nữ c&aacute;n bộ đang c&ocirc;ng t&aacute;c tại trường. Nguyễn Tăng Quỳnh (23 tuổi, sinh vi&ecirc;n năm cuối ĐH Kiến tr&uacute;c H&agrave; Nội) đ&atilde; đứng ra đảm nhận c&ocirc;ng việc với khoản tiền th&ugrave; lao 30 triệu đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="tham-gia.jpg" border="1" alt="Số thanh ni&ecirc;n được cảnh s&aacute;t x&aacute;c định tham gia vụ dằn mặt hiệu trưởng Khi&ecirc;m." width="490" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Số thanh ni&ecirc;n được cảnh s&aacute;t x&aacute;c định tham gia vụ dằn mặt hiệu trưởng Khi&ecirc;m.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ng&agrave;y 12/11, Quỳnh c&ugrave;ng 6 thanh ni&ecirc;n đến cổng trường L&ecirc; Qu&yacute; Đ&ocirc;n chờ cơ hội phục đ&aacute;nh &ocirc;ng Khi&ecirc;m. Ch&uacute;ng đ&atilde; chuẩn bị sẵn hộp sơn h&ograve;ng đổ l&ecirc;n đầu "mục ti&ecirc;u" nhưng vị hiệu trưởng ph&oacute;ng xe m&aacute;y nhanh n&ecirc;n ch&uacute;ng kh&ocirc;ng thể thực hiện được &yacute; đồ.</p>\r\n<p class="Normal">Một ng&agrave;y sau (đ&uacute;ng kỷ niệm 40 năm th&agrave;nh lập trường), Hằng hối th&uacute;c Quỳnh phải tiếp tục kế hoạch. Ph&aacute;t hiện &ocirc;ng Khi&ecirc;m đi bộ đến cơ quan, ba thanh ni&ecirc;n B&ugrave;i Văn Tuấn (24 tuổi), Vũ Quang Minh (23 tuổi), v&agrave; B&ugrave;i Xu&acirc;n Bằng (23 tuổi) đ&atilde; nhảy v&agrave;o đấm đ&aacute;, đổ sơn l&ecirc;n đầu v&agrave; d&ugrave;ng b&igrave;nh xịt hơi cay thịt thẳng v&agrave;o mặt người đứng đầu trường L&ecirc; Qu&yacute; Đ&ocirc;n. Sau khi g&acirc;y &aacute;n, nh&oacute;m thanh ni&ecirc;n bỏ chạy.</p>\r\n<p class="Normal">Hiện, cảnh s&aacute;t đ&atilde; khởi tố vụ &aacute;n để điều tra h&agrave;nh vi cố &yacute; g&acirc;y thương t&iacute;ch. 7 người tham gia vụ việc đ&atilde; bị bắt. Ri&ecirc;ng người thứ 8 l&agrave; Vũ Quang Minh (23 tuổi, Ti&ecirc;n L&atilde;ng, Hải Ph&ograve;ng) đang bị truy t&igrave;m.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 2579, 0, 1),
(721, 'Sát hại vợ vì nghi ngoại tình', 'Bị chồng dùng dao đâm trúng người, chị Tâm ôm bụng bỏ chạy ra cổng nhờ hàng xóm đưa đi cấp cứu nhưng vẫn không qua khỏi. Người từng kề vai ấp má với nạn nhân đã ra đầu thú sau vài giờ bỏ trốn.', 'ba-lao.jpg', NULL, '<h1 class="Title">S&aacute;t hại vợ v&igrave; nghi ngoại t&igrave;nh</h1>\r\n<h2 class="Lead">Bị chồng d&ugrave;ng dao đ&acirc;m tr&uacute;ng người, chị T&acirc;m &ocirc;m bụng bỏ chạy ra cổng nhờ h&agrave;ng x&oacute;m đưa đi cấp cứu nhưng vẫn kh&ocirc;ng qua khỏi. Người từng kề vai ấp m&aacute; với nạn nh&acirc;n đ&atilde; ra đầu th&uacute; sau v&agrave;i giờ bỏ trốn.</h2>\r\n<p class="Normal">Ng&agrave;y 4/12, c&ocirc;ng an huyện Đan Phượng đ&atilde; tạm giữ h&igrave;nh sự với Nguyễn Văn T&igrave;nh (43 tuổi ở huyện Đan Phượng, H&agrave; Nội) để điều tra về h&agrave;nh vi giết người.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="ba-lao.jpg" border="1" alt="Mẹ đẻ nạn nh&acirc;n xấu số kể lại buổi s&aacute;ng định mệnh với đứa con g&aacute;i duy nhất trong nh&agrave;. Ảnh: Th&aacute;i Thịnh." width="490" height="361" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Mẹ đẻ nạn nh&acirc;n xấu số kể lại buổi s&aacute;ng định mệnh với đứa con g&aacute;i duy nhất trong nh&agrave;. Ảnh:&nbsp;<em>Th&aacute;i Thịnh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo người nh&agrave; nạn nh&acirc;n, trước khi xảy ra vụ &aacute;n (s&aacute;ng 2/12), anh T&igrave;nh v&agrave; vợ l&agrave; chị Ho&agrave;ng Thị T&acirc;m đ&atilde; c&atilde;i nhau. "V&agrave;i ng&agrave;y trước khi xảy ra &aacute;n mạng, do thua bạc T&igrave;nh c&oacute; về đ&ograve;i vợ đưa cho 2 triệu đồng để trang trải nhưng kh&ocirc;ng được chấp nhận...", Điệp, anh trai của nạn nh&acirc;n n&oacute;i.</p>\r\n<p class="Normal">B&agrave; Ng&ocirc; Thị Thư (70 tuổi), mẹ đẻ người phụ nữ xấu số cho hay, tối trước khi xảy ra &aacute;n mạng b&agrave; c&oacute; đến nh&agrave; con g&aacute;i để ngủ. S&aacute;ng h&ocirc;m sau, thấy chị T&acirc;m &ocirc;m bụng chạy ra ngo&agrave;i k&ecirc;u cứu, b&agrave; c&ugrave;ng đứa ch&aacute;u ngoại chạy v&agrave;o giằng co với T&igrave;nh khi tr&ecirc;n tay anh ta vẫn cầm dao. Nạn nh&acirc;n được đưa v&agrave;o viện cấp cứu nhưng kh&ocirc;ng qua khỏi do vết thương qu&aacute; nặng.</p>\r\n<p class="Normal">Cũng theo người th&acirc;n của nạn nh&acirc;n, T&igrave;nh đ&atilde; nhiều lần d&ugrave;ng dao đe dọa vợ nếu bỏ hoặc ly th&acirc;n anh ta sẽ giết.</p>\r\n<p class="Normal">Sau khi g&acirc;y &aacute;n, T&igrave;nh đ&atilde; bỏ trốn. Tối c&ugrave;ng ng&agrave;y, người đ&agrave;n &ocirc;ng n&agrave;y đ&atilde; ra cơ quan điều tra đầu th&uacute;. Tại đ&acirc;y, bước đầu T&igrave;nh khai nhận, nghe một số người đồn vợ ngoại t&igrave;nh n&ecirc;n đ&atilde; g&acirc;y tội &aacute;c.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Th&aacute;i Thịnh</strong></p>', 25, 6, 1329, 0, 1),
(722, 'Đôi vợ chồng sát hại hàng xóm để cướp vàng', 'Thấy chị Lan đeo nhẫn vàng, vợ chồng Tâm vờ tổ chức tiệc sinh nhật, chuốc rượu bà hàng xóm rồi rủ vào nhà nghỉ để thực hiện mưu đồ giết người, cướp của. Đôi vợ chồng sát nhân vừa bị cảnh sát bắt.', '2kegietnguoi.jpg', NULL, '<h1 class="Title">Đ&ocirc;i vợ chồng s&aacute;t hại h&agrave;ng x&oacute;m để cướp v&agrave;ng</h1>\r\n<h2 class="Lead">Thấy chị Lan đeo nhẫn v&agrave;ng, vợ chồng T&acirc;m vờ tổ chức tiệc sinh nhật, chuốc rượu b&agrave; h&agrave;ng x&oacute;m rồi rủ v&agrave;o nh&agrave; nghỉ để thực hiện mưu đồ giết người, cướp của. Đ&ocirc;i vợ chồng s&aacute;t nh&acirc;n vừa bị cảnh s&aacute;t bắt.</h2>\r\n<p class="Normal">Trưa 1/12, l&atilde;nh đạo c&ocirc;ng an th&agrave;nh phố Th&aacute;i Nguy&ecirc;n (tỉnh Th&aacute;i Nguy&ecirc;n) cho<em>VnExpress.net</em>&nbsp;biết, nạn nh&acirc;n l&agrave; chị Nguyễn Thị Lan, 37 tuổi, trọ ở thị x&atilde; S&ocirc;ng C&ocirc;ng. Đ&ecirc;m 29/11, thi thể chị Lan được ph&aacute;t hiện trong nh&agrave; nghỉ tại x&atilde; Lương Sơn, với hai vết thương s&acirc;u ở ngực v&agrave; sau lưng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="2kegietnguoi.jpg" border="1" alt="" width="430" height="303" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Cặp vợ chồng L&ecirc; Hồng T&acirc;m v&agrave; Th&aacute;i Thị Nga tại cơ quan điều tra. Ảnh:&nbsp;<em>B&aacute;o Th&aacute;i Nguy&ecirc;n.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ocirc;ng an Th&aacute;i Nguy&ecirc;n cho biết thủ phạm g&acirc;y &aacute;n l&agrave; vợ chồng L&ecirc; Hồng T&acirc;m (27 tuổi) v&agrave; Th&aacute;i Thị Nga. Tại cơ quan điều tra, họ khai 10 ng&agrave;y trước đ&atilde; chuyển đến sống gần ph&ograve;ng trọ của Lan. Ph&aacute;t hiện người phụ nữ h&agrave;ng x&oacute;m đeo nhẫn v&agrave;ng, đ&ocirc;i vợ chồng nghiện ma t&uacute;y n&agrave;y nảy sinh &yacute; đồ chiếm đoạt để lấy tiền chuộc xe m&aacute;y đang cầm cố.</p>\r\n<p class="Normal">Tối 29/11, T&acirc;m v&agrave; Nga lấy cớ tổ chức sinh nhật, mời chị Lan đến ph&ograve;ng ăn uống. Sau khi chuốc rượu thấy người h&agrave;ng x&oacute;m vẫn chưa say, vợ chồng T&acirc;m tiếp tục mời đi h&aacute;t karaoke để tiếp tục uống rượu... Đ&ecirc;m h&ocirc;m đ&oacute; cả ba người kh&ocirc;ng về nh&agrave; m&agrave; rẽ v&agrave;o một nh&agrave; nghỉ. Tại đ&acirc;y, T&acirc;m cầm dao chuẩn bị sẵn đ&acirc;m nạn nh&acirc;n chết tại chỗ.</p>\r\n<p class="Normal">Một điều tra vi&ecirc;n cho biết, chiếc nhẫn v&agrave;ng 2 chỉ c&ugrave;ng điện thoại Nokia 1200 cướp được của chị Lan, đ&ocirc;i vợ chồng n&agrave;y đem b&aacute;n lấy hơn 6 triệu đồng ngay trong đ&ecirc;m g&acirc;y &aacute;n.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 2426, 0, 1),
(723, 'Học sinh lớp 10 đi cướp lấy tiền mua xăng', 'Không tiền đổ xăng dọc đường, hai thanh niên bàn nhau đi cướp cửa hàng điện thoại di động. Ngày 29/11, một người trong số này đã phải nhận án tù khi đang ngồi ghế học sinh.', 'cuop (1).jpg', NULL, '<h1 class="Title">Học sinh lớp 10 đi cướp lấy tiền mua xăng</h1>\r\n<h2 class="Lead">Kh&ocirc;ng tiền đổ xăng dọc đường, hai thanh ni&ecirc;n b&agrave;n nhau đi cướp cửa h&agrave;ng điện thoại di động. Ng&agrave;y 29/11, một người trong số n&agrave;y đ&atilde; phải nhận &aacute;n t&ugrave; khi đang ngồi ghế học sinh.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="cuop (1).jpg" border="1" alt="Thịnh v&agrave; Vượng. Ảnh: Tuệ L&acirc;m." width="490" height="341" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thịnh v&agrave; Vượng. Ảnh:&nbsp;<em>Tuệ L&acirc;m.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Ng&agrave;y 29/11, tại TAND th&agrave;nh phố Y&ecirc;n B&aacute;i, Nguyễn Văn Thịnh (20 tuổi) v&agrave; Trần Xu&acirc;n Vượng (16 tuổi, lớp 10) khai 3 th&aacute;ng trước tr&ecirc;n đường từ Văn Chấn ra th&agrave;nh phố Y&ecirc;n B&aacute;i chơi, thấy xe m&aacute;y sắp hết xăng n&ecirc;n b&agrave;n với nhau đi cướp để lấy tiền đổ xăng.</p>\r\n<p class="Normal">Đến cửa h&agrave;ng b&aacute;n điện thoại di động ở phường Hồng H&agrave;, Thịnh nổ m&aacute;y sẵn ở b&ecirc;n ngo&agrave;i chờ, c&ograve;n Vượng vờ xem h&agrave;ng rồi nhanh tay cầm chiếc điện thoại Nokia 6300 chạy vụt đi. Cả hai ph&oacute;ng tẩu tho&aacute;t ngay sau đ&oacute;. Bị nạn nh&acirc;n h&ocirc; ho&aacute;n đuổi theo, bộ đ&ocirc;i bị bắt sau khoảng 2 km bỏ chạy.</p>\r\n<p class="Normal">Trước những chứng cứ v&agrave; lời khai nhận của 2 bị c&aacute;o, TAND th&agrave;nh phố Y&ecirc;n B&aacute;i tuy&ecirc;n Thịnh 18 th&aacute;ng t&ugrave;. Vượng một năm t&ugrave; nhưng cho hưởng &aacute;n treo.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Tuệ L&acirc;m</strong></p>', 25, 6, 2048, 0, 1),
(725, 'Nổ súng sát hại hàng xóm', 'Tối 28/11, sau tiếng súng nổ, người đàn ông gục ngã trước cổng nhà. Vụ án mạng khiến người dân trong ngõ nhỏ trên phố Kim Giang (Hà Nội) hoảng sợ.', 'no-sung.jpg', NULL, '<h1 class="Title">Nổ s&uacute;ng s&aacute;t hại h&agrave;ng x&oacute;m</h1>\r\n<h2 class="Lead">Tối 28/11, sau tiếng s&uacute;ng nổ, người đ&agrave;n &ocirc;ng gục ng&atilde; trước cổng nh&agrave;. Vụ &aacute;n mạng khiến người d&acirc;n trong ng&otilde; nhỏ tr&ecirc;n phố Kim Giang (H&agrave; Nội) hoảng sợ.</h2>\r\n<p class="Normal">22h h&agrave;ng trăm người vẫn k&eacute;o nhau về con ng&otilde; nhỏ tr&ecirc;n đường Kim Giang để b&agrave;n t&aacute;n v&agrave; theo d&otilde;i sự việc. Nạn nh&acirc;n l&agrave; anh Trần Kiều Hưng (34 tuổi), l&agrave;m thợ điện ở phường Đại Kim, quận Ho&agrave;ng Mai, H&agrave; Nội.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="no-sung.jpg" border="1" alt="Nh&agrave; chức tr&aacute;ch tổ chức kh&aacute;m nghiệm hiện trường. Ảnh: H&agrave; Anh." width="490" height="360" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nh&agrave; chức tr&aacute;ch tổ chức kh&aacute;m nghiệm hiện trường. Ảnh:&nbsp;<em>Th&aacute;i Thịnh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo những người d&acirc;n sống trong con ng&otilde; nhỏ, trước khi xảy ra &aacute;n mạng, anh Hưng v&agrave; h&agrave;ng x&oacute;m Nguyễn Việt Đức c&oacute; xảy ra x&ocirc; x&aacute;t. Mẹ anh Hưng đ&atilde; l&ecirc;n trụ sở cảnh s&aacute;t tr&igrave;nh b&aacute;o sự việc. Tuy nhi&ecirc;n, khi trở về nh&agrave;, b&agrave; hoảng hốt khi thấy con trai nằm gục chết b&ecirc;n vũng m&aacute;u trước cổng.</p>\r\n<p class="Normal">Cơ quan điều tra x&aacute;c định nghi phạm d&ugrave;ng s&uacute;ng hai n&ograve;ng chĩa thẳng v&agrave;o cổ người đ&agrave;n &ocirc;ng 34 tuổi để b&oacute;p c&ograve; l&agrave; Nguyễn Việt Trung (28 tuổi, em trai anh Nguyễn Việt Đức). Nghi phạm đ&atilde; ra cơ quan điều tra đầu th&uacute;</p>\r\n<p class="Normal">Vụ nổ s&uacute;ng c&aacute;ch trụ sở c&ocirc;ng an phường Đại Kim khoảng gần 100 m&eacute;t.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Th&aacute;i Thịnh</strong></p>', 25, 6, 1628, 0, 1),
(726, 'Trốn cảnh sát, con bạc nhảy xuống hồ chết đuối', 'Thấy đồng bọn chạy tán loạn khi cảnh sát hình sự ập đến, nam thanh niên liều mình nhảy xuống hồ Ba Mẫu (Hà Nội) tìm đường thoát thân. Sau hơn hai tiếng, xác người này mới được tìm thấy.', 'bac.jpg', NULL, '<h1 class="Title">Trốn cảnh s&aacute;t, con bạc nhảy xuống hồ chết đuối</h1>\r\n<h2 class="Lead">Thấy đồng bọn chạy t&aacute;n loạn khi cảnh s&aacute;t h&igrave;nh sự ập đến, nam thanh ni&ecirc;n liều m&igrave;nh nhảy xuống hồ Ba Mẫu (H&agrave; Nội) t&igrave;m đường tho&aacute;t th&acirc;n. Sau hơn hai tiếng, x&aacute;c người n&agrave;y mới được t&igrave;m thấy.</h2>\r\n<p class="Normal">Chiều 28/11, Ph&ograve;ng cảnh s&aacute;t điều tra tội phạm về trật tự x&atilde; hội (C&ocirc;ng an H&agrave; Nội) phối hợp c&ugrave;ng c&ocirc;ng an quận Đống Đa ập v&agrave;o một sới bạc b&ecirc;n hồ Ba Mẫu (phường Phương Li&ecirc;n, quận Đống Đa, H&agrave; Nội).</p>\r\n<p class="Normal">Người d&acirc;n nơi đ&acirc;y cho biết, sới bạc n&agrave;y tồn tại đ&atilde; l&acirc;u nhưng đến nay mới được triệt ph&aacute;. Những người tụ tập đến đ&acirc;y c&oacute; cả nam lẫn nữ. Họ thường đi những chiếc xe tay ga đắt tiền.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="bac.jpg" border="1" alt="Lực lượng chức năng t&igrave;m kiếm x&aacute;c nạn nh&acirc;n l&uacute;c 20h. Ảnh: Th&aacute;i Thịnh." width="490" height="351" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Lực lượng chức năng t&igrave;m kiếm x&aacute;c nạn nh&acirc;n l&uacute;c 20h. Ảnh:&nbsp;<em>Th&aacute;i Thịnh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">"L&uacute;c đến cảnh s&aacute;t mặc thường phục đi taxi, với số lượng kh&aacute; lớn. Thấy b&oacute;ng d&aacute;ng họ, một số con bạc lao ra đường t&agrave;u để chạy tho&aacute;t th&acirc;n nhưng đ&atilde; bị t&oacute;m ngay...", người phụ nữ sống cạnh hồ kể.</p>\r\n<p class="Normal">L&uacute;c n&agrave;y, dưới l&ugrave;m c&acirc;y, thấy "đồng bọn" chạy t&aacute;n loạn, một số con bạc đ&atilde; nhảy xuống hồ t&igrave;m đường tho&aacute;t th&acirc;n. Sau tiếng s&uacute;ng nổ, 2 con bạc đ&atilde; được đưa l&ecirc;n bờ ngay. Một người bị mất t&iacute;ch. H&agrave;ng chục cảnh s&aacute;t c&ugrave;ng c&aacute;n bộ d&acirc;n ph&ograve;ng được huy động bảo vệ hiện trường.</p>\r\n<p class="Normal">Theo ghi nhận của&nbsp;<em>VnExpress.net</em>, l&uacute;c xảy ra vụ việc, thời tiết kh&aacute; lạnh. Tuy nhi&ecirc;n, h&agrave;ng trăm người d&acirc;n vẫn k&eacute;o đến hồ để xem. Sau gần 3 tiếng, x&aacute;c nạn nh&acirc;n xấu số đ&atilde; được đưa l&ecirc;n bờ. Người n&agrave;y khoảng 25-30 tuổi.</p>\r\n<p class="Normal">Một c&aacute;n bộ c&ocirc;ng an quận Đống Đa cho&nbsp;<em>VnExpress.net</em>&nbsp;biết, c&oacute; khoảng 30 con bạc đ&atilde; được đưa về trụ sở để điều tra. Danh t&iacute;nh những người n&agrave;y cũng như nạn nh&acirc;n xấu số chưa được tiết lộ.</p>\r\n<p class="Normal">Cuối năm 2007, khi ập v&agrave;o sới bạc của vợ chồng Th&ecirc; "Thọt" ở Ph&uacute;c Thọ (H&agrave; T&acirc;y cũ), Bộ c&ocirc;ng an cũng bắt được hơn 50 con bạc đang s&aacute;t phạt nhau dưới h&igrave;nh thức x&oacute;c đĩa. Tuy nhi&ecirc;n, khi lực lượng cảnh s&aacute;t r&uacute;t qu&acirc;n sau trận đột k&iacute;ch, li&ecirc;n tiếp 5 x&aacute;c chết đ&atilde; nổi l&ecirc;n ở hồ nước v&agrave; đoạn s&ocirc;ng cạnh đ&oacute;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Th&aacute;i Thịnh</strong></p>', 25, 6, 645, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(728, 'Sinh viên náo loạn vì người đàn ông tự sát ở phòng học', 'Sáng nay, người đàn ông khoảng 30 tuổi chạy xộc vào Đại học Ngoại ngữ, hớt hải nói: "Có người đuổi muốn giết tôi". Hàng trăm sinh viên hoảng sợ khi anh này cố thủ trong giảng đường, cầm mảnh kính tự rạch bụng.', 'lop-1.jpg', NULL, '<h1 class="Title">Sinh vi&ecirc;n n&aacute;o loạn v&igrave; người đ&agrave;n &ocirc;ng tự s&aacute;t ở ph&ograve;ng học</h1>\r\n<h2 class="Lead">S&aacute;ng nay, người đ&agrave;n &ocirc;ng khoảng 30 tuổi chạy xộc v&agrave;o Đại học Ngoại ngữ, hớt hải n&oacute;i: "C&oacute; người đuổi muốn giết t&ocirc;i". H&agrave;ng trăm sinh vi&ecirc;n hoảng sợ khi anh n&agrave;y cố thủ trong giảng đường, cầm mảnh k&iacute;nh tự rạch bụng.</h2>\r\n<p class="Normal">Sự việc xảy ra l&uacute;c hơn 7h tại khu nh&agrave; A2 Đại học Ngoại ngữ (Đại học Quốc gia H&agrave; Nội). Theo thầy Phạm Đức Trung (Khoa ng&ocirc;n ngữ văn h&oacute;a Trung Quốc) thời điểm đ&oacute; thầy v&agrave; khoảng 10 sinh vi&ecirc;n năm thứ 4 đang trong giảng đường th&igrave; bất ngờ người đ&agrave;n &ocirc;ng đầu t&oacute;c rối b&ugrave;, vẻ mặt hớt hải x&ocirc;ng v&agrave;o.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="lop-1.jpg" border="1" alt="" width="490" height="370" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Thầy gi&aacute;o Trung kể lại sự việc diễn ra s&aacute;ng 26/11.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">"Anh ta n&oacute;i: ''C&oacute; người đuổi theo v&agrave; muốn giết t&ocirc;i'', y&ecirc;u cầu cho mượn điện thoại để nhờ cảnh s&aacute;t 113 đến trợ gi&uacute;p", vị giảng vi&ecirc;n kể lại. Quan s&aacute;t thấy xung quanh kh&ocirc;ng như những g&igrave; anh ta n&oacute;i, thầy Trung kh&ocirc;ng l&agrave;m theo. Khi đề nghị kh&ocirc;ng được đ&aacute;p ứng, người đ&agrave;n &ocirc;ng bất ngờ đấm vỡ cửa k&iacute;nh ngay cạnh b&agrave;n gi&aacute;o vi&ecirc;n. Thầy Trung nhanh ch&oacute;ng bảo học tr&ograve; chạy ra ngo&agrave;i, ph&ograve;ng trường hợp xấu xảy ra.</p>\r\n<p class="Normal">"May l&agrave; anh ta hung hăng nhưng kh&ocirc;ng g&acirc;y thương t&iacute;ch cho sinh vi&ecirc;n n&agrave;o", nam giảng vi&ecirc;n n&oacute;i.</p>\r\n<p class="Normal">Bảo vệ Đinh Quang B&igrave;nh cho biết khi v&agrave;o ph&ograve;ng anh thấy người đ&agrave;n &ocirc;ng n&agrave;y lăm lăm trong tay mảnh k&iacute;nh vỡ. "T&ocirc;i định x&ocirc;ng v&agrave;o khống chế nhưng liền bị qu&aacute;t: "M&agrave;y v&agrave;o tao n&eacute;m chết...", anh B&igrave;nh kể với&nbsp;<em>VnExpress.net</em></p>\r\n<p class="Normal">Sau khoảng 15 ph&uacute;t cảnh s&aacute;t 113 xuất hiện. Mặc d&ugrave; được động vi&ecirc;n, thuyết phục song anh ta vẫn rất hoảng loạn, li&ecirc;n tiếp đập vỡ những mảng k&iacute;nh. Gần 9h, người đ&agrave;n &ocirc;ng chừng 30 tuổi đ&atilde; tự rạch bụng. Trong l&uacute;c đau đớn, anh ta lu&ocirc;n miệng k&ecirc;u: "C&ocirc;ng an ơi, ch&uacute;ng n&oacute; giết t&ocirc;i".</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="anh.jpg" border="1" alt="" width="350" height="533" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hiện trường vụ việc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo ghi nhận của&nbsp;<em>VnExpress.net</em>, tại hiện trường l&agrave; ph&ograve;ng học 105 m&aacute;u l&ecirc;nh l&aacute;ng tr&ecirc;n mặt b&agrave;n v&agrave; s&agrave;n nh&agrave;. Một c&ocirc;ng an tham gia đưa người đi cấp cứu kể lại: "M&aacute;u chảy qu&aacute; nhiều, ch&uacute;ng t&ocirc;i phải d&ugrave;ng chăn để thấm. Mặt anh ta t&aacute;i dại...".</p>\r\n<p class="Normal">Nạn nh&acirc;n được đưa đến Bệnh viện 198 của Bộ C&ocirc;ng an ở gần đ&oacute;. Nhận định ban đầu về nguy&ecirc;n nh&acirc;n vụ việc, một c&aacute;n bộ c&ocirc;ng an c&oacute; thẩm quyền cho biết nhiều khả năng người đ&agrave;n &ocirc;ng n&agrave;y bị hoang tưởng.</p>\r\n<p class="Normal">Chiều 26/11, b&aacute;c sĩ Đặng Ngọc Hanh (Ph&oacute; trưởng khoa Ngoại 3) cho<em>VnExpress.net</em>&nbsp;biết, trước khi nằm l&ecirc;n b&agrave;n phẫu thuật anh ta n&oacute;i t&ecirc;n l&agrave; Nguyễn Văn Trung, 34 tuổi, tr&uacute; ở phường Ho&agrave;ng Văn Thụ, Th&aacute;i Nguy&ecirc;n.</p>\r\n<p class="Normal">"Khi đưa v&agrave;o cấp cứu, bệnh nh&acirc;n bị sốc do mất qu&aacute; nhiều m&aacute;u, cơ thể c&oacute; nhiều vết thương. Qu&aacute; tr&igrave;nh mổ, c&aacute;c b&aacute;c sĩ ph&aacute;t hiện trong bụng người n&agrave;y c&oacute; mảnh k&iacute;nh d&agrave;i 12 cm...", vị ph&oacute; trưởng khoa n&oacute;i.</p>\r\n<p class="Normal">Căn cứ kết quả thăm kh&aacute;m ban đầu, b&aacute;c sĩ Hanh nhận định Trung c&oacute; dấu hiệu bị hoang tưởng v&agrave; k&iacute;ch động. L&uacute;c n&agrave;o anh ta cũng lo lắng c&oacute; người theo sau để &aacute;m hại.</p>\r\n<p class="Normal">Hiện, người đ&agrave;n &ocirc;ng vẫn chưa tỉnh ho&agrave;n to&agrave;n. Hai cảnh s&aacute;t được cử t&uacute;c trực b&ecirc;n giường bệnh.</p>\r\n<p class="Normal">Nguy&ecirc;n nh&acirc;n vụ việc đang được cơ quan chức năng l&agrave;m s&aacute;ng tỏ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 588, 0, 1),
(729, 'Con trai tống tiền bố đẻ', 'Hết tiền sau một tuần ăn chơi trác táng trong khách sạn, Bắc cùng đám bạn lập kế hoạch tống tiến tiền gia đình lấy 25 triệu đồng.', 'Le-Huy-Bac-400.jpg', NULL, '<h1 class="Title">Con trai tống tiền bố đẻ</h1>\r\n<h2 class="Lead">Hết tiền sau một tuần ăn chơi tr&aacute;c t&aacute;ng trong kh&aacute;ch sạn, Bắc c&ugrave;ng đ&aacute;m bạn lập kế hoạch tống tiến tiền gia đ&igrave;nh lấy 25 triệu đồng.</h2>\r\n<p class="Normal">Đ&ecirc;m 23/11, &ocirc;ng L&ecirc; Huy Lộc (phường Nghi Hải, thị x&atilde; Cửa L&ograve;, Nghệ An) li&ecirc;n tục nhận được điện thoại tống tiền với nội dung con trai &ocirc;ng l&agrave; L&ecirc; Huy Bắc đang bị khống chế, gia đ&igrave;nh phải đưa ngay 25 triệu đồng tiền chuộc nếu kh&ocirc;ng con tin v&agrave; tất cả mọi người trong nh&agrave; sẽ bị giết.</p>\r\n<p class="Normal">Sau đ&oacute;, những tin nhắn tống tiền li&ecirc;n tục được gửi đến điện thoại của nhiều th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh &ocirc;ng Lộc. Sau khi b&iacute; mật b&aacute;o cảnh s&aacute;t, &ocirc;ng Lộc chuẩn bị tiền chuộc theo y&ecirc;u cầu của nh&oacute;m bắt c&oacute;c.</p>\r\n<p class="Normal">&Iacute;t ph&uacute;t sau, một đ&ocirc;i nam nữ bịt mặt đi xe m&aacute;y x&ocirc;ng thẳng v&agrave;o nh&agrave; &ocirc;ng để nhận tiền. C&ocirc;ng an thị x&atilde; Cửa L&ograve; ập tới bắt giữ hai người n&agrave;y.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Le-Huy-Bac-400.jpg" border="1" alt="" width="400" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&ecirc; Huy Bắc - chủ mưu vụ tống tiền. Ảnh:&nbsp;<em>H&agrave; Khoa</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại cơ quan điều tra, họ khai t&ecirc;n Trần Văn Qu&yacute; (18 tuổi) v&agrave; Nguyễn Thị Thu Thảo (19 tuổi) đều tr&uacute; tại thị x&atilde; Cửa L&ograve;. Bộ đ&ocirc;i cho biết kế hoạch tống tiền do ch&iacute;nh Bắc v&agrave; đồng bọn vạch ra.</p>\r\n<p class="Normal">Ng&agrave;y 24/11, Bắc c&ugrave;ng một số người li&ecirc;n quan bị bắt. Cảnh s&aacute;t x&aacute;c định, kh&ocirc;ng c&ocirc;ng ăn việc l&agrave;m, cậu con trai 18 tuổi của &ocirc;ng Lộc thường xuy&ecirc;n tụ tập ăn chơi tr&aacute;c t&aacute;ng, thu&ecirc; ph&ograve;ng kh&aacute;ch sạn sống bầy đ&agrave;n với Trần Văn Qu&yacute;, Nguyễn Thị Thu Thảo, Nguyễn Do&atilde;n Linh, L&ecirc; Huy Chi. Hết tiền, nh&oacute;m thanh ni&ecirc;n n&agrave;y nghĩ c&aacute;ch "xoay" từ người th&acirc;n.</p>\r\n<p class="Normal">Bắc d&agrave;n dựng cho cả nh&oacute;m tống tiền bố đẻ m&igrave;nh. Qu&yacute; v&agrave; Thảo được cử đi lấy.</p>\r\n<p class="Normal"><strong>H&agrave; Nguy&ecirc;n Khoa</strong></p>\r\n<h1 class="Title">Con trai tống tiền bố đẻ</h1>\r\n<h2 class="Lead">Hết tiền sau một tuần ăn chơi tr&aacute;c t&aacute;ng trong kh&aacute;ch sạn, Bắc c&ugrave;ng đ&aacute;m bạn lập kế hoạch tống tiến tiền gia đ&igrave;nh lấy 25 triệu đồng.</h2>\r\n<p class="Normal">Đ&ecirc;m 23/11, &ocirc;ng L&ecirc; Huy Lộc (phường Nghi Hải, thị x&atilde; Cửa L&ograve;, Nghệ An) li&ecirc;n tục nhận được điện thoại tống tiền với nội dung con trai &ocirc;ng l&agrave; L&ecirc; Huy Bắc đang bị khống chế, gia đ&igrave;nh phải đưa ngay 25 triệu đồng tiền chuộc nếu kh&ocirc;ng con tin v&agrave; tất cả mọi người trong nh&agrave; sẽ bị giết.</p>\r\n<p class="Normal">Sau đ&oacute;, những tin nhắn tống tiền li&ecirc;n tục được gửi đến điện thoại của nhiều th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh &ocirc;ng Lộc. Sau khi b&iacute; mật b&aacute;o cảnh s&aacute;t, &ocirc;ng Lộc chuẩn bị tiền chuộc theo y&ecirc;u cầu của nh&oacute;m bắt c&oacute;c.</p>\r\n<p class="Normal">&Iacute;t ph&uacute;t sau, một đ&ocirc;i nam nữ bịt mặt đi xe m&aacute;y x&ocirc;ng thẳng v&agrave;o nh&agrave; &ocirc;ng để nhận tiền. C&ocirc;ng an thị x&atilde; Cửa L&ograve; ập tới bắt giữ hai người n&agrave;y.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="Le-Huy-Bac-400.jpg" border="1" alt="" width="400" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&ecirc; Huy Bắc - chủ mưu vụ tống tiền. Ảnh:&nbsp;<em>H&agrave; Khoa</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại cơ quan điều tra, họ khai t&ecirc;n Trần Văn Qu&yacute; (18 tuổi) v&agrave; Nguyễn Thị Thu Thảo (19 tuổi) đều tr&uacute; tại thị x&atilde; Cửa L&ograve;. Bộ đ&ocirc;i cho biết kế hoạch tống tiền do ch&iacute;nh Bắc v&agrave; đồng bọn vạch ra.</p>\r\n<p class="Normal">Ng&agrave;y 24/11, Bắc c&ugrave;ng một số người li&ecirc;n quan bị bắt. Cảnh s&aacute;t x&aacute;c định, kh&ocirc;ng c&ocirc;ng ăn việc l&agrave;m, cậu con trai 18 tuổi của &ocirc;ng Lộc thường xuy&ecirc;n tụ tập ăn chơi tr&aacute;c t&aacute;ng, thu&ecirc; ph&ograve;ng kh&aacute;ch sạn sống bầy đ&agrave;n với Trần Văn Qu&yacute;, Nguyễn Thị Thu Thảo, Nguyễn Do&atilde;n Linh, L&ecirc; Huy Chi. Hết tiền, nh&oacute;m thanh ni&ecirc;n n&agrave;y nghĩ c&aacute;ch "xoay" từ người th&acirc;n.</p>\r\n<p class="Normal">Bắc d&agrave;n dựng cho cả nh&oacute;m tống tiền bố đẻ m&igrave;nh. Qu&yacute; v&agrave; Thảo được cử đi lấy.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Nguy&ecirc;n Khoa</strong></p>', 25, 6, 2296, 0, 1),
(733, 'Toán cướp bắt cóc nữ sinh viên giữa ban ngày', 'Sau vụ va chạm giao thông, 4 thanh niên lặng lẽ bám theo đôi trai gái đến khu vực nghĩa trang Mai Dịch (Hà Nội) rồi cướp lắc và dây chuyền bạc. Bạn gái của nạn nhân cũng bị chúng ép lên xe chở đi.', 'giai-cuu.jpg', NULL, '<h1 class="Title">To&aacute;n cướp bắt c&oacute;c nữ sinh vi&ecirc;n giữa ban ng&agrave;y</h1>\r\n<h2 class="Lead">Sau vụ va chạm giao th&ocirc;ng, 4 thanh ni&ecirc;n lặng lẽ b&aacute;m theo đ&ocirc;i trai g&aacute;i đến khu vực nghĩa trang Mai Dịch (H&agrave; Nội) rồi cướp lắc v&agrave; d&acirc;y chuyền bạc. Bạn g&aacute;i của nạn nh&acirc;n cũng bị ch&uacute;ng &eacute;p l&ecirc;n xe chở đi.</h2>\r\n<p class="Normal">Ng&agrave;y 14/11, C&ocirc;ng an quận Cầu Giấy cho biết vừa l&agrave;m r&otilde; h&agrave;nh vi của nh&oacute;m thanh ni&ecirc;n cướp t&agrave;i sản, bắt giữ một nữ sinh vi&ecirc;n Cao đẳng Sư phạm H&agrave; Nội.</p>\r\n<p class="Normal">Trước đ&oacute;, chiều 10/11, anh Hải (18 tuổi, huyện Đan Phượng, H&agrave; Nội) tr&igrave;nh b&aacute;o, anh v&agrave; chị Phượng - bạn học c&ugrave;ng lớp - tr&ecirc;n đường về qu&ecirc; c&oacute; va chạm xe với 4 thanh ni&ecirc;n đi tr&ecirc;n 2 xe m&aacute;y. Sau khi giải quyết ổn thỏa nhưng nh&oacute;m thanh ni&ecirc;n vẫn lặng lẽ b&aacute;m theo. Đến khu vực nghĩa trang Mai Dịch (H&agrave; Nội), ch&uacute;ng &eacute;p xe anh Hải v&agrave;o lề đường.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="giai-cuu.jpg" border="1" alt="" width="490" height="311" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ba trong số 4 thanh ni&ecirc;n tham gia vụ "bắt c&oacute;c" nữ sinh. Ảnh:&nbsp;<em>H&agrave; Anh.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Một trong số n&agrave;y khống chế &eacute;p anh Hải cướp đi lắc v&agrave; d&acirc;y chuyền bạc c&ugrave;ng chiếc điện thoại di động. Sau khi trả chiếc sim điện thoại cho nạn nh&acirc;n, ch&uacute;ng buộc Phượng l&ecirc;n xe m&aacute;y chở đi.</p>\r\n<p class="Normal">Qua x&aacute;c minh, c&ocirc;ng an quận Cầu Giấy v&agrave; phường Mai Dịch dựng l&ecirc;n ch&acirc;n dung 4 thanh ni&ecirc;n. Chiều 12/11, Nguyễn Văn Khỏe, Nguyễn Mạnh Thưởng (21 tuổi), Nguyễn Văn Quyết (20 tuổi) v&agrave; Đ&agrave;o Duy Tuấn (19 tuổi) c&ugrave;ng ở huyện Thanh Oai, (H&agrave; Nội) đ&atilde; đến c&ocirc;ng an phường Mai Dịch đầu th&uacute;.</p>\r\n<p class="Normal">Tại cơ quan điều tra, 4 thanh ni&ecirc;n n&agrave;y khai số t&agrave;i sản cướp của anh Hải đ&atilde; đem b&aacute;n lấy hơn một triệu đồng để chi ti&ecirc;u. Trong qu&aacute; tr&igrave;nh đưa Phượng đi, ch&uacute;ng đ&atilde; tịch thu điện thoại của c&ocirc;.</p>\r\n<p class="Normal">Chở l&ograve;ng v&ograve;ng nữ sinh n&agrave;y tr&ecirc;n nhiều tuyến phố, đợi trời tối ch&uacute;ng đưa Phương v&agrave;o một qu&aacute;n rượu ăn nhậu. Tại đ&acirc;y, một v&agrave;i t&ecirc;n trong nh&oacute;m giở tr&ograve; s&agrave;m sỡ với Phượng. Lợi dụng l&uacute;c cả bọn ng&agrave; ng&agrave; say, nạn nh&acirc;n trốn ra ngo&agrave;i nhờ chủ qu&aacute;n gi&uacute;p đỡ đưa về nh&agrave;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 1739, 0, 1),
(734, 'Học sinh cấp 3 giả danh cảnh sát cơ động', 'Mua quân phục, sắm mũ cứng, dùi cui... "hóa trang" thật giống cảnh sát cơ động, các cậu học sinh cấp 3 lượn xe máy quanh khu vực sân Mỹ Đình, hù dọa cưỡng đoạt tiền của nhiều đôi tình nhân không đội mũ bảo hiểm.', 'canh-sat.jpg', NULL, '<h1 class="Title">Học sinh cấp 3 giả danh cảnh s&aacute;t cơ động</h1>\r\n<h2 class="Lead">Mua qu&acirc;n phục, sắm mũ cứng, d&ugrave;i cui... "h&oacute;a trang" thật giống cảnh s&aacute;t cơ động, c&aacute;c cậu học sinh cấp 3 lượn xe m&aacute;y quanh khu vực s&acirc;n Mỹ Đ&igrave;nh, h&ugrave; dọa cưỡng đoạt tiền của nhiều đ&ocirc;i t&igrave;nh nh&acirc;n kh&ocirc;ng đội mũ bảo hiểm.</h2>\r\n<p class="Normal">Tối 10/11, tr&ecirc;n đường Nguyễn Cơ Thạch (H&agrave; Nội) tổ tuần tra cảnh s&aacute;t cơ động ph&aacute;t hiện hai "đồng nghiệp" đi xe m&aacute;y c&oacute; biểu hiện nghi vấn liền b&iacute; mật b&aacute;m theo. Bị y&ecirc;u cầu xuất tr&igrave;nh thẻ ng&agrave;nh, hai cảnh s&aacute;t n&agrave;y chống đối, buộc lược lượng l&agrave;m nhiệm vụ phải khống chế, đưa về Đồn c&ocirc;ng an số 1 huyện Từ Li&ecirc;m.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="canh-sat.jpg" border="1" alt="Nguyễn Anh Duy v&agrave; Nguyễn Quang Nhật." width="490" height="381" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nguyễn Duy Anh v&agrave; Nguyễn Quang Nhật.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tại đ&acirc;y, ch&acirc;n tướng của hai cảnh s&aacute;t cơ động mạo danh bị lật tẩy. Hai "cảnh s&aacute;t" khai t&ecirc;n Nguyễn Duy Anh v&agrave; Nguyễn Quang Nhật, mới 15-17 tuổi, đang l&agrave; học sinh lớp 10 v&agrave; 12 ở H&agrave; Nội.</p>\r\n<p class="Normal">Hai anh em họ n&agrave;y thừa nhận tối 6/11 v&agrave; 10/11 đ&atilde; chặn đường một số đ&ocirc;i nam nữ kh&ocirc;ng đội mũ bảo hiểm ph&oacute;ng xe m&aacute;y ở khu vực s&acirc;n vận động Mỹ Đ&igrave;nh để &ldquo;xin đểu&rdquo; h&agrave;ng trăm ngh&igrave;n đồng.</p>\r\n<p class="Normal">Trước đ&oacute;, Duy Anh v&agrave; bạn c&ugrave;ng trường l&agrave; L&ecirc; Tuấn Anh cũng đ&atilde; ba lần giả danh cảnh s&aacute;t cơ động, cưỡng đoạt tổng cộng 700.000 đồng của người đi đường.</p>\r\n<p class="Normal">Điều tra của cảnh s&aacute;t cho thấy, Tuấn Anh v&agrave; Duy Anh ra khu vực ga H&agrave; Nội mua hai bộ quần &aacute;o giống trang phục của cảnh s&aacute;t cơ động gi&aacute; 700.000 đồng. Hai cậu học sinh mua th&ecirc;m mũ cứng, d&ugrave;i cui v&agrave; ph&ugrave; hiệu... tạo h&igrave;nh cho thật giống "cảnh s&aacute;t xịn". Sau 3 lần đi c&ugrave;ng nhau, c&oacute; v&agrave;i h&ocirc;m Tuấn Anh, Duy Anh đ&atilde; rủ em họ Quang Nhật đ&oacute;ng giả cảnh s&aacute;t đi "xin đểu".</p>\r\n<p class="Normal">Cơ quan c&ocirc;ng an đang l&agrave;m r&otilde; vụ việc để c&oacute; h&igrave;nh thức xử l&yacute; với nh&oacute;m học sinh n&agrave;y.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Anh</strong></p>', 25, 6, 1075, 0, 1),
(741, 'Nỗi ân hận của bà mẹ đầu độc ba con', 'Chắp tay quay xuống phía chồng và 3 con thơ, Ngân khóc nức nở: “Chỉ một phút mất khôn mà chút nữa mẹ đã giết các con. Mẹ hối hận lắm rồi. Mẹ ngàn lần xin lỗi”. ', 'giet-con.jpg', NULL, '<h1 class="Title">Nỗi &acirc;n hận của b&agrave; mẹ đầu độc ba con</h1>\r\n<h2 class="Lead">Chắp tay quay xuống ph&iacute;a chồng v&agrave; 3 con thơ, Ng&acirc;n kh&oacute;c nức nở: &ldquo;Chỉ một ph&uacute;t mất kh&ocirc;n m&agrave; ch&uacute;t nữa mẹ đ&atilde; giết c&aacute;c con. Mẹ hối hận lắm rồi. Mẹ ng&agrave;n lần xin lỗi&rdquo;.&nbsp;</h2>\r\n<p class="Normal">Từ mờ s&aacute;ng, 4 bố con anh Chu Văn Lĩnh (40 tuổi) lọ mọ bắt xe v&agrave;o th&agrave;nh phố Vinh tham dự phi&ecirc;n t&ograve;a x&eacute;t xử can phạm giết người ch&iacute;nh l&agrave; vợ, l&agrave; mẹ của m&igrave;nh - bị c&aacute;o L&ecirc; Thị Ng&acirc;n.</p>\r\n<p class="Normal">S&aacute;ng nay, kh&ocirc;ng kh&iacute; kh&aacute;n ph&ograve;ng của TAND Nghệ An lặng lẽ một c&aacute;ch kh&aacute;c thường. Hơn 20 người dự kh&aacute;n buồn b&atilde;, sụt sịt bước v&agrave;o ph&ograve;ng xử, kh&ocirc;ng quan t&acirc;m đến vị tr&iacute; n&agrave;o d&agrave;nh cho gia đ&igrave;nh bị hại hay gia đ&igrave;nh bị c&aacute;o bởi họ l&agrave; người th&acirc;n với kẻ g&acirc;y ra tội &aacute;c lẫn c&aacute;c nạn nh&acirc;n. 4 bố con anh Lĩnh cũng mang những t&acirc;m trạng kh&aacute;c nhau.</p>\r\n<p class="Normal">Trong khi anh kh&ocirc;ng muốn nh&igrave;n mặt người vợ đ&atilde; su&yacute;t cướp đi mạng sống của 3 con th&igrave; đứa &uacute;t cứ kh&oacute;c tu tu khi thấy mẹ bị c&ograve;ng tay v&agrave; cảnh s&aacute;t &aacute;p. C&ocirc; con g&aacute;i lớn hơn 10 tuổi &ocirc;m chầm bố, l&eacute;n l&eacute;n ngước nh&igrave;n mẹ&hellip;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/34/69/giet-con.jpg" border="1" alt="mẹ" width="400" height="295" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ba đứa con su&yacute;t chết dưới tay người mẹ n&ocirc;ng nổi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Đứng tr&ecirc;n v&agrave;nh m&oacute;ng ngựa, thỉnh thoảng Ng&acirc;n quay xuống nh&igrave;n c&aacute;c con với th&aacute;i độ ăn năn, hối hận. Phần thẩm vấn tr&ocirc;i qua nhanh ch&oacute;ng với lời th&uacute; tội trong nước mắt của bị c&aacute;o. Ng&acirc;n cho biết c&ocirc; k&eacute;m anh Lĩnh 10 tuổi, được nh&agrave; chồng y&ecirc;u qu&yacute; bởi sống c&oacute; trước c&oacute; sau. Tuy nhi&ecirc;n, đời sống vợ chồng của họ lại kh&ocirc;ng &ecirc;m đẹp. Sau khi 3 đứa con ra đời, kinh tế kh&oacute; khăn, chồng mỗi lần rượu v&agrave;o lại thượng cẳng ch&acirc;n hạ cẳng tay, Ng&acirc;n tỏ ra ch&aacute;n nản, thấy cuộc sống kh&ocirc;ng lối tho&aacute;t.</p>\r\n<p class="Normal">Nhiều lần c&ocirc; định tự tử nhưng nghĩ mất mẹ c&aacute;c con sẽ khổ n&ecirc;n t&igrave;m c&aacute;ch đưa bọn nhỏ "đi theo". Ng&agrave;y 4/7, Ng&acirc;n đưa 20.000 đồng cho con g&aacute;i lớn bảo đi mua 3 hộp sữa. Pha hai g&oacute;i thuốc diệt cỏ v&agrave;o cốc sữa to, người mẹ bắt con viết thư cho bố, theo lời đọc của m&igrave;nh: &ldquo;Bố Lĩnh c&oacute; khỏe kh&ocirc;ng, trong l&ograve;ng con c&oacute; nhớ bố nhưng ch&uacute;ng con thương mẹ hơn, y&ecirc;u mẹ hơn...&rdquo;.</p>\r\n<p class="Normal">Rồi Ng&acirc;n cầm b&uacute;t viết tiếp: &ldquo;B&acirc;y giờ bốn mẹ con uống thuốc cỏ chết, để lại nh&agrave; cửa, đất đai cho bố, bố muốn lấy vợ kh&aacute;c th&igrave; t&ugrave;y&rdquo;. Chia nhỏ cốc sữa cho c&aacute;c con, Ng&acirc;n n&oacute;i: &ldquo;Đứa m&ocirc; theo mẹ th&igrave; uống, đứa m&ocirc; theo bố th&igrave; kh&ocirc;ng phải uống&rdquo;. N&oacute;i xong, c&ocirc; cầm ca sữa uống hết sạch. 2 c&ocirc; con g&aacute;i v&agrave; cậu con trai chưa đầy 5 tuổi lần lượt l&agrave;m theo mẹ. Ng&acirc;n đưa 3 con l&ecirc;n giường nằm.</p>\r\n<p class="Normal">Khi bọn nhỏ k&ecirc;u đau bụng, n&ocirc;n &oacute;i v&agrave; la h&eacute;t dữ dội th&igrave; cũng l&agrave; l&uacute;c anh Lĩnh đi l&agrave;m đồng về. Thấy m&ugrave;i thuốc trừ cỏ bốc l&ecirc;n nồng nặc, &ocirc;ng bố hiểu chuyện g&igrave; đ&atilde; xảy ra, vội đưa vợ v&agrave; c&aacute;c con đi cấp cứu. Tất cả được cứu sống. Ng&acirc;n bị cảnh s&aacute;t bắt.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/34/69/ba-dua-con.jpg" border="1" alt="" width="400" height="270" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Anh Lĩnh v&agrave; ba con ngồi ở ghế d&agrave;nh cho bị hại.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Suốt phi&ecirc;n x&eacute;t xử, Ng&acirc;n nghẹn ng&agrave;o nh&igrave;n về ph&iacute;a chồng v&agrave; c&aacute;c con van xin tha tội. N&oacute;i lời sau c&ugrave;ng, c&ocirc; chắp tay kh&oacute;c nức nở: &ldquo;Chỉ một ph&uacute;t mất kh&ocirc;n m&agrave; ch&uacute;t nữa mẹ đ&atilde; giết c&aacute;c con. Mẹ hối hận lắm rồi. Con ơi, mẹ ng&agrave;n lần xin lỗi&rdquo;.</p>\r\n<p class="Normal">Tại t&ograve;a, Lĩnh cho biết, thương vợ nhiều lần muốn viết đơn xin giảm nhẹ h&igrave;nh phạt nhưng nh&igrave;n thấy con cứ &egrave;o uột, đau yếu thường xuy&ecirc;n v&igrave; di chứng sau lần uống thuốc độc n&ecirc;n anh lại th&ocirc;i. H&ocirc;m nay, trước th&aacute;i độ ăn năn của vợ, người chồng b&agrave;y tỏ: "Vợ t&ocirc;i tuy đ&atilde; 30 tuổi nhưng nhiều khi c&ograve;n dại dột. Ch&uacute;ng t&ocirc;i l&agrave; một gia đ&igrave;nh, c&aacute;c con cần c&oacute; mẹ. Xin t&ograve;a xem x&eacute;t, giảm &aacute;n cho Ng&acirc;n. Mong c&ocirc; ấy cải tạo tốt để sớm l&agrave;m người lương thiện&rdquo;.</p>\r\n<p class="Normal">X&eacute;t c&aacute;c t&igrave;nh tiết giảm nhẹ h&igrave;nh phạt, TAND tỉnh Nghệ An tuy&ecirc;n phạt Ng&acirc;n 7 năm t&ugrave; về tội giết người. Trước khi trở về trại giam, người mẹ nước mắt gi&agrave;n giụa cố ngo&aacute;i lại nh&igrave;n chồng v&agrave; con, lu&ocirc;n miệng n&oacute;i: &ldquo;C&aacute;c con ơi, mẹ xin lỗi&rdquo;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>H&agrave; Nguy&ecirc;n Khoa</strong></p>', 26, 6, 1076, 0, 1),
(743, 'Trả giá cho tội ác man rợ tại nhà nghỉ', 'Phiên tòa vừa bắt đầu mà gã đã không thể đứng nổi, thân hình còm cõi chao đảo liên hồi. Nếu không có sự trợ giúp của cảnh sát, Tống không thể bước lên vành móng ngựa.', 'to (1).JPG', NULL, '<h1 class="Title">Trả gi&aacute; cho tội &aacute;c man rợ tại nh&agrave; nghỉ</h1>\r\n<h2 class="Lead">Phi&ecirc;n t&ograve;a vừa bắt đầu m&agrave; g&atilde; đ&atilde; kh&ocirc;ng thể đứng nổi, th&acirc;n h&igrave;nh c&ograve;m c&otilde;i chao đảo li&ecirc;n hồi. Nếu kh&ocirc;ng c&oacute; sự trợ gi&uacute;p của cảnh s&aacute;t, Tống kh&ocirc;ng thể bước l&ecirc;n v&agrave;nh m&oacute;ng ngựa.</h2>\r\n<p class="Normal">L&ecirc; Xu&acirc;n Tống sinh ra v&agrave; lớn l&ecirc;n tại một huyện ngh&egrave;o thuộc tỉnh Đồng Th&aacute;p. Ho&agrave;n cảnh kh&oacute; khăn, cha bị liệt, mẹ l&agrave;m thu&ecirc; l&agrave;m mướn nhưng Tống vẫn được gia đ&igrave;nh cho ăn học th&agrave;nh người với mong muốn n&uacute;m ruột của m&igrave;nh sẽ c&oacute; cơ hội thay đổi số phận. Tốt nghiệp phổ th&ocirc;ng, Tống tiếp tục theo học một lớp sửa chữa điện tử để c&oacute; nghề thủ th&acirc;n.</p>\r\n<p class="Normal">19 tuổi, như bao thanh ni&ecirc;n kh&aacute;c, Tống cũng được trau dồi trong qu&acirc;n ngũ v&agrave; trở về l&agrave;ng v&agrave;o năm 2007. Kh&ocirc;ng chấp nhận cuộc sống b&aacute;n mặt cho đất, b&aacute;n lưng cho trời, g&atilde; trai trẻ tiếp tục xin học ngoại ngữ để ấp ủ ph&aacute;t triển mộng kinh doanh. Xoay xở được &iacute;t vốn, Tống thử vận bằng việc bu&ocirc;n b&aacute;n hạt sen. Tuy nhi&ecirc;n, kh&ocirc;ng l&acirc;u sau đ&oacute; việc kinh doanh l&acirc;m v&agrave;o t&igrave;nh trạng thua lỗ. Ch&aacute;n nản, Tống sinh tật nhậu nhẹt v&agrave; t&igrave;m vui trong buổi tụ tập với bạn b&egrave;.</p>\r\n<p class="Normal">D&ugrave;ng đến đồng tiền cuối c&ugrave;ng, Tống t&igrave;m đến nh&agrave; b&agrave; nội để vay cả tiền hậu vận của b&agrave; n&eacute;m v&agrave;o những cuộc ăn chơi tr&aacute;c t&aacute;ng. Tiền hết, g&atilde; đem thế chấp cả chiếc &ldquo;cần c&acirc;u cơm&rdquo; của m&igrave;nh để tiếp tục &ldquo;giải sầu&rdquo; bằng men rượu v&agrave; thu&ecirc; trọ tại nh&agrave; nghỉ Phước H&ugrave;ng (x&atilde; T&acirc;n B&igrave;nh, huyện Ch&acirc;u Th&agrave;nh).</p>\r\n<p class="Normal">Tại đ&acirc;y, Tống l&agrave;m quen với b&agrave; Tư (60 tuổi, chủ nh&agrave; trọ) v&agrave; b&agrave; Thu (51 tuổi, người l&agrave;m c&ocirc;ng). Sau v&agrave;i ng&agrave;y t&aacute; t&uacute;c, g&atilde; thanh ni&ecirc;n biết cả khu nh&agrave; trọ chỉ c&oacute; hai người đ&agrave;n b&agrave; n&agrave;y tr&ocirc;ng coi n&ecirc;n nảy sinh &yacute; đồ chiếm đoạt t&agrave;i sản.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/23/F0/to.JPG" border="1" alt="Tống được dẫn giải đến ph&ograve;ng xử. Ảnh: Vũ Mai" width="480" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Tống được dẫn giải đến ph&ograve;ng xử. Ảnh:&nbsp;<em>Vũ Mai</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo đ&oacute;, s&aacute;ng 24/6/2009, khu nh&agrave; trọ vắng kh&aacute;ch. Biết chỉ c&oacute; mỗi b&agrave; nh&acirc;n c&ocirc;ng trong ph&ograve;ng nghỉ, Tống y&ecirc;u cầu b&agrave; mở cửa v&igrave; &ldquo;c&oacute; chuyện muốn n&oacute;i&rdquo; nhưng bị b&agrave; n&agrave;y từ chối.</p>\r\n<p class="Normal">T&igrave;m một kh&uacute;c c&acirc;y thật to, Tống mang đến đập cửa ph&ograve;ng b&agrave; Thu v&agrave; đổ cho b&agrave; n&agrave;y đ&atilde; trộm v&iacute; tiền của m&igrave;nh. Bực tức v&igrave; bị nghi oan, b&agrave; Thu mở cửa định thanh minh liền bị g&atilde; trai trẻ x&ocirc; ng&atilde; rồi d&ugrave;ng gậy đập v&agrave;o đầu bất tỉnh. Sau đ&oacute;, Tống k&eacute;o x&aacute;c b&agrave; Thu bỏ v&agrave;o nh&agrave; tắm th&igrave; c&oacute; kh&aacute;ch đến thu&ecirc; ph&ograve;ng. C&ugrave;ng l&uacute;c, hung thủ ph&aacute;t hiện nạn nh&acirc;n c&ograve;n cử động, miệng mấp m&aacute;y n&ecirc;n tiếp tục xuống tay cho đến khi nạn nh&acirc;n chết hẳn.</p>\r\n<p class="Normal">Giết b&agrave; Thu xong, Tống gom đồ đạc, x&oacute;a dấu vết chuẩn bị bỏ trốn th&igrave; nghe tiếng b&agrave; Tư về. Viện cớ ph&ograve;ng m&igrave;nh n&oacute;ng nực, g&atilde; xin b&agrave; Tư cho m&igrave;nh được ở ph&ograve;ng n&agrave;y để chủ nh&agrave; kh&ocirc;ng ph&aacute;t hiện x&aacute;c nạn nh&acirc;n v&agrave; t&igrave;m c&aacute;ch lấy lại giấy tờ, bỏ trốn.</p>\r\n<p class="Normal">Trưa h&ocirc;m đ&oacute;, giả vờ trong ph&ograve;ng c&oacute; chuột, Tống y&ecirc;u cầu b&agrave; Tư l&ecirc;n ph&ograve;ng xử l&yacute;. Khi người đ&agrave;n b&agrave; đang lui cui th&igrave; bất ngờ bị g&atilde; kh&aacute;ch trọ cầm c&acirc;y đập hai nh&aacute;t v&agrave;o đầu, bất tỉnh tại chỗ. Sau đ&oacute;, Tống lấy ch&igrave;a kh&oacute;a mở cửa ph&ograve;ng b&agrave; Tư lấy giấy chứng minh nh&acirc;n d&acirc;n, sổ đăng k&yacute; kh&aacute;ch ở trọ c&oacute; ghi t&ecirc;n của m&igrave;nh nhằm phi tang, đồng thời cướp đi số tiền 7 triệu đồng v&agrave; chiếc xe m&aacute;y tẩu tho&aacute;t. Được đưa đi cấp cứu kịp thời, b&agrave; Tư chỉ bị thương tật 2% vĩnh viễn.</p>\r\n<p class="Normal">Tối h&ocirc;m đ&oacute;, Tống đến thị x&atilde; Sa Đ&eacute;c, t&igrave;m một g&aacute;i b&aacute;n d&acirc;m để &ldquo;xả xui&rdquo; rồi bỏ trốn l&ecirc;n TP HCM. Gần 6 th&aacute;ng sau hung thủ bị bắt theo lệnh truy n&atilde;.</p>\r\n<p class="Normal">Ng&agrave;y 28/8, TAND tỉnh Đồng Th&aacute;p đ&atilde; đưa vụ &aacute;n ra x&eacute;t xử lưu động, tuy&ecirc;n phạt L&ecirc; Xu&acirc;n Tống &aacute;n tử h&igrave;nh về c&aacute;c tội &ldquo;giết người&rdquo; v&agrave; &ldquo;cướp t&agrave;i sản&rdquo;. Ngay sau đ&oacute;, g&atilde; s&aacute;t nh&acirc;n đ&atilde; l&agrave;m đơn kh&aacute;ng c&aacute;o xin giảm nhẹ h&igrave;nh phạt, đồng thời b&agrave; Tư cũng l&agrave;m đơn đề nghị t&ograve;a ph&uacute;c thẩm tha tội chết cho bị c&aacute;o.</p>\r\n<p class="Normal">Tại buổi xem x&eacute;t kh&aacute;ng c&aacute;o, d&ugrave; chỉ mới khai mạc m&agrave; Tống như kh&ocirc;ng thể đứng nổi, c&aacute;i th&acirc;n h&igrave;nh c&ograve;m c&otilde;i cứ chao đảo tr&ecirc;n đ&ocirc;i ch&acirc;n xiềng x&iacute;ch. Nếu kh&ocirc;ng c&oacute; sự trợ gi&uacute;p của cảnh s&aacute;t, hắn kh&ocirc;ng thể bước l&ecirc;n v&agrave;nh m&oacute;ng ngựa. Tr&ocirc;ng hắn l&uacute;c n&agrave;y, kh&oacute; c&oacute; thể ngờ đ&oacute; chỉ l&agrave; một thanh ni&ecirc;n chỉ vừa 24 tuổi v&agrave; l&agrave; hung thủ của một vụ giết người t&agrave;n độc.</p>\r\n<p class="Normal">Cũng như tại phi&ecirc;n t&ograve;a trước, Tống khai nhận to&agrave;n bộ h&agrave;nh vi phạm tội trong gi&agrave;n giụa nước mắt. Tuy nhi&ecirc;n, g&atilde; thanh ni&ecirc;n lu&ocirc;n miệng cho rằng kh&ocirc;ng c&oacute; &yacute; định giết người để cướp t&agrave;i sản. &ldquo;Bị c&aacute;o ra tay với b&agrave; Thu chỉ v&igrave; qu&aacute; tức giận b&agrave; n&agrave;y đ&atilde; trộm v&iacute; tiền của bị c&aacute;o. C&ograve;n sự việc xảy ra với b&agrave; Tư l&agrave; v&igrave; bị c&aacute;o sợ bị ph&aacute;t hiện v&agrave; qu&aacute; n&ocirc;n n&oacute;ng muốn tẩu tho&aacute;t&hellip;&rdquo;, Tống c&uacute;i đầu run rẩy.</p>\r\n<p class="Normal">Được mời l&ecirc;n thẩm vấn, b&agrave; Tư phải lần từng bước mới đến được gần b&agrave;n HĐXX. Di chứng từ lần bị Tống quật c&acirc;y l&ecirc;n đầu khiến cho b&agrave; đ&atilde; yếu đi rất nhiều. Ngang qua chỗ Tống đang đờ đẫn, ủ rũ trước v&agrave;nh m&oacute;ng ngựa, b&agrave; kh&ocirc;ng giấu được &aacute;nh nh&igrave;n thương cảm đối với g&atilde; tử t&ugrave; trẻ. Tr&igrave;nh b&agrave;y l&yacute; do xin giảm &aacute;n cho kẻ từng ra tay s&aacute;t hại m&igrave;nh, người đ&agrave;n b&agrave; rơi nước mắt: &ldquo;Mạng sống con người l&agrave; qu&yacute; lắm, t&ocirc;i may mắn sống s&oacute;t thế n&agrave;y l&agrave; ph&uacute;c đức lắm rồi. Nếu c&oacute; bắt n&oacute; phải chết th&igrave; b&agrave; Thu cũng kh&ocirc;ng sống lại được nữa. Vả lại, t&ocirc;i biết ho&agrave;n cảnh gia đ&igrave;nh n&oacute;, cha n&oacute; bị liệt kh&ocirc;ng thể l&agrave;m g&igrave; c&ograve;n mẹ n&oacute; th&igrave; cũng l&acirc;m bệnh nặng từ ng&agrave;y n&oacute; g&acirc;y ra tội. Th&ocirc;i th&igrave; xin t&ograve;a giảm &aacute;n cho n&oacute;, cho n&oacute; cơ hội sống để suy ngẫm về tội lỗi của m&igrave;nh&rdquo;.</p>\r\n<p class="Normal">Ph&iacute;a dưới, nhiều người dự kh&aacute;n bu&ocirc;ng tiếng thờ d&agrave;i. Tại g&oacute;c khuất của kh&aacute;n ph&ograve;ng, đ&ocirc;i vợ chồng gi&agrave; ngồi thu m&igrave;nh đưa tay quẹt ngang d&ograve;ng nước mắt. Họ l&agrave; cha mẹ của Tống.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/23/F0/to%202.JPG" border="1" alt="Người cha hối hả nhờ người c&otilde;ng để được nh&igrave;n thấy con lần cuối nhưng c&aacute;nh cổng xe t&ugrave; đ&atilde; đ&oacute;ng sập từ l&acirc;u. Ảnh: Vũ Mai" width="480" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Người cha hối hả nhờ người c&otilde;ng để được nh&igrave;n thấy con lần cuối nhưng c&aacute;nh cửa xe t&ugrave; đ&atilde; đ&oacute;ng sập từ l&acirc;u. Ảnh:&nbsp;<em>Vũ Mai</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">B&agrave;o chữa cho Tống, vị luật sư thay mặt th&acirc;n chủ gửi lời xin lỗi đến gia đ&igrave;nh c&aacute;c nạn nh&acirc;n. Theo &ocirc;ng, giết người l&agrave; một tội &aacute;c kh&ocirc;ng thể tha thứ, nhưng với Tống l&agrave; sự n&ocirc;ng nổi nhất thời. Ch&iacute;nh tấm l&ograve;ng vị tha của b&agrave; Tư sẽ l&agrave;m cho người ta sống nhẹ nh&agrave;ng, thư th&aacute;i hơn, l&agrave;m cho kẻ tội đồ như Tống nghiệm ra tội lỗi của m&igrave;nh. Tống c&oacute; nhiều t&igrave;nh tiết giảm nhẹ, gia đ&igrave;nh đ&atilde; khắc phục hậu quả, được nạn nh&acirc;n xin giảm &aacute;n&hellip; n&ecirc;n tha chết cho bị c&aacute;o.</p>\r\n<p class="Normal">&ldquo;Bị c&aacute;o rất ăn năn về tội lỗi của m&igrave;nh. Từ ng&agrave;y g&acirc;y ra chuyện, bị c&aacute;o kh&ocirc;ng sao ngủ được. Cha mẹ bị c&aacute;o giờ đ&atilde; gi&agrave; yếu, bệnh tật&hellip; Xin t&ograve;a xem x&eacute;t&rdquo;, Tống kh&oacute;c rưng rức khi được n&oacute;i lời sau c&ugrave;ng.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, ng&agrave;y 28/10, cho rằng bản &aacute;n m&agrave; cấp sơ thẩm &aacute;p dụng đối với bị c&aacute;o l&agrave; đ&uacute;ng người, đ&uacute;ng tội. H&agrave;nh vi phạm tội của bị c&aacute;o l&agrave; mất hết t&iacute;nh người, phạm một l&uacute;c hai tội nghi&ecirc;m trọng, g&acirc;y hậu quả đặc biệt nghi&ecirc;m trọng, b&agrave; Tư kh&ocirc;ng chết l&agrave; ngo&agrave;i &yacute; muốn của bị c&aacute;o&hellip; T&ograve;a ph&uacute;c thẩm TAND Tối cao tại TP HCM đ&atilde; giữ nguy&ecirc;n mức &aacute;n như cấp sơ thẩm đ&atilde; tuy&ecirc;n.</p>\r\n<p class="Normal">Phi&ecirc;n t&ograve;a kết th&uacute;c, Tống kh&ocirc;ng thể nhấc nổi ch&acirc;n theo cảnh s&aacute;t ra xe về trại. G&atilde; cũng chẳng buồn nh&igrave;n lại ph&iacute;a sau, nơi cha mẹ m&igrave;nh đang lịm dần trong đau khổ. Chới với trong nước mắt, cha Tống lập cập nhờ người quen c&otilde;ng m&igrave;nh chạy theo nh&igrave;n con trai lần cuối. Nhưng c&aacute;nh cửa xe t&ugrave; đ&atilde; đ&oacute;ng sập t&ugrave; l&acirc;u&hellip;</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ Mai</strong></p>', 26, 6, 2213, 0, 1),
(747, 'Nỗi lòng nữ hiệu trưởng ly hôn ở tuổi xế chiều', 'Chị Lan buông mình xuống ghế băng dài trong phòng xử án, thẫn thờ nhìn ra cửa sổ. Một lúc sau, anh Đức bước vào, lạnh lùng bước qua như giữa họ chưa hề tồn tại những năm tháng mặn nồng.', 'cap.jpg', NULL, '<p class="Title">Nỗi l&ograve;ng nữ hiệu trưởng ly h&ocirc;n ở tuổi xế chiều</p>\r\n<p class="Lead">Chị Lan bu&ocirc;ng m&igrave;nh xuống ghế băng d&agrave;i trong ph&ograve;ng xử &aacute;n, thẫn thờ nh&igrave;n ra cửa sổ. Một l&uacute;c sau, anh Đức bước v&agrave;o, lạnh l&ugrave;ng bước qua như giữa họ chưa hề tồn tại những năm th&aacute;ng mặn nồng.</p>\r\n<p class="Normal">Phi&ecirc;n xử ly h&ocirc;n giữa chị v&agrave; anh diễn v&agrave;o chiều thu se lạnh tại TAND H&agrave; Nội. Hơn 30 năm chung sống đồng cam cộng khổ, vậy m&agrave; giờ khi cả hai m&aacute;i đầu đ&atilde; điểm hai thứ t&oacute;c, sắp l&ecirc;n chức &ocirc;ng b&agrave; nội lại phải dắt nhau ra t&ograve;a với l&yacute; do "kh&ocirc;ng hợp nhau".</p>\r\n<p class="Normal">Đ&acirc;y l&agrave; lần thứ hai anh chị ra t&ograve;a. Trước đ&oacute; phi&ecirc;n sơ thẩm ở t&ograve;a &aacute;n cấp quận đ&atilde; b&aacute;c đơn xin ly h&ocirc;n của anh với hy vọng cuộc h&ocirc;n nh&acirc;n của anh chị vẫn c&ograve;n c&oacute; thể h&agrave;n gắn được. Nhưng sau đ&oacute;, anh Đức lại l&agrave;m đơn kh&aacute;ng &aacute;n.</p>\r\n<p class="Normal">L&yacute; do xin ly h&ocirc;n anh đưa ra tại phi&ecirc;n t&ograve;a lần n&agrave;y l&agrave; kh&ocirc;ng hợp nhau, do vợ hay "để quần &aacute;o mốc xanh mốc đỏ kh&ocirc;ng chịu giặt"... khiến cho chị sững sờ. L&agrave; một c&ocirc; gi&aacute;o dạy văn, lại l&agrave; người đứng đầu một trường học, chị đủ nhạy cảm để biết được những g&igrave; đang diễn ra xung quanh cuộc h&ocirc;n nh&acirc;n đến hồi đổ vỡ kh&ocirc;ng thể n&iacute;u k&eacute;o được.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/04/3A/cap.jpg" border="1" alt="Đ&ocirc;i" width="400" height="281" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">"Đ&ocirc;i khi, sự giải tho&aacute;t cũng l&agrave; một sự khởi đầu lại, khiến cho cuộc sống tốt hơn". Ảnh minh họa.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Theo chị Lan, sau những giờ l&agrave;m việc, anh kh&ocirc;ng về nh&agrave; m&agrave; đi đến khuya, thường về nh&agrave; l&agrave; đ&oacute;ng cửa ph&ograve;ng. M&acirc;u thuẫn giữa hai người l&ecirc;n đến đỉnh điểm khi đầu năm 2009 họ bắt đầu ly th&acirc;n. Người trong gia đ&igrave;nh cũng đ&atilde; nhiều lần khuy&ecirc;n nhủ, thậm ch&iacute; c&oacute; người c&ograve;n kh&ocirc;ng th&egrave;m nh&igrave;n mặt khi biết anh đ&atilde; "say nắng" một người phụ nữ chỉ đ&aacute;ng tuổi con m&igrave;nh.</p>\r\n<p class="Normal">Một người th&acirc;n của gia đ&igrave;nh anh cho biết, anh Đức v&agrave; c&ocirc; người t&igrave;nh "sắp l&agrave;m đ&aacute;m cưới". Họ đến đ&acirc;y để khuy&ecirc;n giải anh suy nghĩ kỹ, quay đầu lại l&agrave; bờ.</p>\r\n<p class="Normal">Tr&igrave;nh b&agrave;y quan điểm trước t&ograve;a, chị vẫn tha thiết b&agrave;y tỏ mong muốn được h&agrave;n gắn cuộc h&ocirc;n nh&acirc;n, v&igrave; giờ hai người kh&ocirc;ng c&ograve;n trẻ nữa, c&aacute;c con đ&atilde; lớn, hơn nữa chị tin anh vẫn c&ograve;n t&igrave;nh y&ecirc;u với vợ...</p>\r\n<p class="Normal">Nhưng đ&aacute;p lại, anh Đức r&agrave;nh rọt: "Khi kh&ocirc;ng c&ograve;n t&igrave;nh y&ecirc;u th&igrave; cuộc sống chẳng kh&aacute;c n&agrave;o địa ngục. Mong t&ograve;a h&atilde;y xem x&eacute;t nguyện vọng của t&ocirc;i".</p>\r\n<p class="Normal">Vị chủ tọa hỏi lại: "Anh đ&atilde; suy nghĩ kỹ chưa?". Đ&aacute;p lại l&agrave; c&aacute;i gật đầu của anh rất nhanh của anh khiến chị ngồi bất động hồi l&acirc;u.</p>\r\n<p class="Normal">Quay sang ph&iacute;a chị, vị chủ tọa n&oacute;i: "Cuộc sống l&agrave; của chị, chị kh&ocirc;ng n&ecirc;n n&iacute;u k&eacute;o chỉ v&igrave; c&aacute;c con, hay v&igrave; những mối quan hệ r&agrave;ng buộc xung quanh m&igrave;nh. Đ&ocirc;i khi, sự giải tho&aacute;t cũng l&agrave; một sự khởi đầu lại, khiến cho cuộc sống của chị tốt hơn".</p>\r\n<p class="Normal">Hơn hai tiếng đồng hồ ngồi cạnh nhau trong ph&ograve;ng x&eacute;t xử m&agrave; chị cảm thấy như d&agrave;i v&ocirc; tận. Giờ nghị &aacute;n, anh ngồi lặng tr&ecirc;n chiếc ghế d&agrave;nh cho đương sự, &ocirc;m đầu gục xuống. C&ograve;n chị lẳng lặng xuống dưới ph&iacute;a h&agrave;ng ghế nhận sự chia sẻ, an ủi của đồng nghiệp.</p>\r\n<p class="Normal">Một người bạn của chị cho biết, sự nghiệp th&agrave;nh đạt, gia đ&igrave;nh hạnh ph&uacute;c với con c&aacute;i ngoan ngo&atilde;n trưởng th&agrave;nh của anh chị từng khiến bao người "ngưỡng mộ" vậy m&agrave; kh&ocirc;ng ai c&oacute; thể tin rằng cuối c&ugrave;ng họ lại dắt nhau ra t&ograve;a. "Suy cho c&ugrave;ng th&igrave; chỉ v&igrave; t&igrave;nh y&ecirc;u anh d&agrave;nh cho chị kh&ocirc;ng đủ để c&oacute; thể c&ugrave;ng nhau đi đến cuối cuộc đời", người đ&agrave;n b&agrave; luống tuổi thở d&agrave;i.</p>\r\n<p class="Normal">Một c&ocirc; gi&aacute;o ngồi b&ecirc;n cạnh g&oacute;p lời: "Đ&ocirc;i khi, để giữ một m&aacute;i ấm gia đ&igrave;nh, người phụ nữ th&ocirc;ng minh cần phải tỏ ra m&igrave;nh ngu đi một ch&uacute;t". Cả mấy người c&ugrave;ng im lặng, họ cũng hồi hộp chờ ph&aacute;n quyết của t&ograve;a.</p>\r\n<p class="Normal">Vị chủ tọa c&ocirc;ng bố: "Nếu duy tr&igrave; cuộc h&ocirc;n nh&acirc;n như hiện nay sẽ kh&ocirc;ng đem lại hạnh ph&uacute;c cho cả hai b&ecirc;n. V&igrave; vậy, t&ograve;a chấp thuận cho anh Đức ly h&ocirc;n".</p>\r\n<p class="Normal">Chị như chết lặng. Mấy năm y&ecirc;u nhau, 30 năm chung sống, th&ecirc;m một năm ly th&acirc;n... cuối c&ugrave;ng l&agrave; quyết định ly h&ocirc;n. Rồi mỗi người sẽ bắt đầu một cuộc đời mới. Nhưng chị sẽ thế n&agrave;o đ&acirc;y, khi tuổi xu&acirc;n chị đ&atilde; d&agrave;nh hết cho anh, giờ tr&ecirc;n đầu chị t&oacute;c đ&atilde; điểm bạc. Đau đớn, chị vẫn quay sang anh n&oacute;i "ch&uacute;c anh hạnh ph&uacute;c" trước khi rời khỏi ph&ograve;ng x&eacute;t xử.</p>\r\n<p class="Normal">Rồi chị bước đi trong những &aacute;nh mắt x&oacute;t xa, chia sẻ của đồng nghiệp "Phận đ&agrave;n b&agrave; mỏng như c&aacute;nh chuồn chị ạ. Th&ocirc;i, h&atilde;y cố gắng l&ecirc;n", một người động vi&ecirc;n.</p>\r\n<p class="Normal">Chị dắt xe rời khỏi t&ograve;a &aacute;n. Lời b&agrave;i h&aacute;t nhạc của Ph&uacute; Quang "<em>Ng&agrave;y ấy anh bắt đầu bằng bước ch&acirc;n của ng&agrave;y qu&ecirc;n em</em>" r&eacute;o rắt ph&aacute;t ra từ chiếc loa của người b&aacute;n dạo b&ecirc;n đường khiến tr&aacute;i tim chị tan n&aacute;t. Cơn mưa cuối ng&agrave;y ập đến, mặt chị nh&ograve;e nước lẫn c&ugrave;ng những giọt nước mắt mằn mặn.</p>\r\n<p class="Normal">Ng&agrave;y mai, chị sẽ bắt đầu một cuộc đời mới, d&ugrave; đ&atilde; kh&aacute; muộn m&agrave;ng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Anh Thư</strong></p>', 26, 6, 475, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(748, 'Những cuộc chơi trác táng sau phi vụ cướp tiền tỷ', 'Gã thanh niên nhỏ thó cúi gằm mặt giấu ánh mắt rệu rã, mệt mỏi khai những cảnh ngập ngụa thác loạn của mình tại vũ trường hay nhà nghỉ, nơi hắn đã chi hơn chục triệu đồng cho mỗi đêm từ số tiền cướp được.', 'bai.JPG', NULL, '<p class="Title">Những cuộc chơi tr&aacute;c t&aacute;ng sau phi vụ cướp tiền tỷ</p>\r\n<p class="Lead">G&atilde; thanh ni&ecirc;n nhỏ th&oacute; c&uacute;i gằm mặt giấu &aacute;nh mắt rệu r&atilde;, mệt mỏi khai những cảnh ngập ngụa th&aacute;c loạn của m&igrave;nh tại vũ trường hay nh&agrave; nghỉ, nơi hắn đ&atilde; chi hơn chục triệu đồng cho mỗi đ&ecirc;m từ số tiền cướp được.</p>\r\n<p class="normal">Ph&ograve;ng xử b&ecirc;n h&ocirc;ng của TAND TP HCM vốn chật hẹp c&agrave;ng ngột ngạt hơn bởi lượng người dự kh&aacute;n qu&aacute; đ&ocirc;ng. Trước v&agrave;nh m&oacute;ng ngựa, những g&atilde; choai choai mặt cứng đờ v&igrave; căng thẳng, chỉ c&oacute; Nguyễn Văn L&acirc;m (24 tuổi) - v&oacute;c d&aacute;ng nhỏ th&oacute; nhất ra vẻ dửng dưng. Thỉnh thoảng, hắn c&ograve;n quay sang Phạm Văn Mạnh (25 tuổi) cười tủm tỉm mặc cho th&aacute;i độ kh&ocirc;ng vừa &yacute; của đứa bạn th&acirc;n n&agrave;y.</p>\r\n<p class="normal">Theo c&aacute;o trạng, tối 5/5/2009, nh&oacute;m bạn của L&acirc;m gồm 4 người đến qu&aacute;n uống c&agrave; ph&ecirc; với Huỳnh Thanh Việt (Việt Kh&ugrave;ng) v&agrave; Ph&uacute; M&aacute;t (kh&ocirc;ng r&otilde; lai lịch). Tại đ&acirc;y, Việt rủ cả bọn cướp t&agrave;i sản của người đ&agrave;n &ocirc;ng chạy xe Attila m&agrave;u đỏ v&igrave; &ocirc;ng n&agrave;y l&agrave; d&acirc;n chuy&ecirc;n thu gom tiền h&agrave;ng trong chợ đầu mối Thủ Đức, trong cốp xe c&oacute; rất nhiều tiền. Mọi h&agrave;nh tung của "con mồi" đ&atilde; được bạn của Việt theo d&otilde;i, sẽ thường xuy&ecirc;n li&ecirc;n lạc b&aacute;o c&aacute;o để mọi người xử l&yacute;.</p>\r\n<p class="normal">Nghe xong kế hoạch "ăn h&agrave;ng", 2 người bạn của L&acirc;m từ chối tham gia n&ecirc;n bỏ về trước, chỉ c&ograve;n lại Mạnh ở lại v&igrave; đ&atilde; tr&oacute;t đi chung với g&atilde; bạn th&acirc;n. Sau đ&oacute;, Việt đưa cho L&acirc;m một con dao l&ecirc; c&ograve;n m&igrave;nh giữ một ống tu&yacute;p sắt l&agrave;m hung kh&iacute;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/04/94/bai.JPG" border="1" alt="" width="480" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Trong khi Mạnh (&aacute;o trắng) c&ograve;n e d&egrave; th&igrave; L&acirc;m thản nhi&ecirc;n kể về những đ&ecirc;m th&aacute;c loạn bằng tiền cướp được. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="normal">Khoảng 19h c&ugrave;ng ng&agrave;y, trong khi Mạnh v&agrave; Ph&uacute; M&aacute;t chờ trước hẻm 18 đường Ng&ocirc; Ch&iacute; Quốc th&igrave; Việt v&agrave; L&acirc;m bịt mặt đi v&agrave;o trong, n&uacute;p tại bụi cỏ chờ "l&agrave;m thịt con mồi". Khi ph&aacute;t hiện người đ&agrave;n &ocirc;ng chạy chiếc Attila đỏ vừa đi ra từ nh&agrave; một mối h&agrave;ng, Việt bất ngờ lao đến d&ugrave;ng tu&yacute;p sắt đập v&agrave;o mặt khiến nạn nh&acirc;n bất tỉnh. Lập tức hai t&ecirc;n cướp lấy xe tẩu tho&aacute;t c&ugrave;ng đồng bọn.</p>\r\n<p class="normal">Chạy đến bờ s&ocirc;ng thuộc khu d&acirc;n cư Vĩnh Ph&uacute; 2 (tỉnh B&igrave;nh Dương), kiểm tra thấy trong hộc xe c&oacute; nhiều g&oacute;i tiền, cả bọn sướng rơn gom lấy rồi đẩy chiếc xe xuống s&ocirc;ng phi tang m&agrave; kh&ocirc;ng hề biết c&ograve;n rất nhiều tiền tại những nơi kh&aacute;c trong chiếc Attila n&agrave;y. Sau đ&oacute; bọn ch&uacute;ng k&eacute;o nhau về nh&agrave; người quen của Việt c&ugrave;ng nhau đếm được hơn 900 triệu đồng. L&acirc;m được Việt chia cho 125 triệu đồng, c&ograve;n Mạnh cũng bỏ t&uacute;i 120 triệu.</p>\r\n<p class="normal">Ngay tối đ&oacute;, người d&acirc;n ph&aacute;t hiện &ocirc;ng Thăng gặp nạn n&ecirc;n đ&atilde; đưa đến bệnh viện cấp cứu v&agrave; &ocirc;ng n&agrave;y đ&atilde; tr&igrave;nh b&aacute;o sự việc với c&ocirc;ng an.</p>\r\n<p class="normal">Về số phận chiếc xe Attila, sau v&agrave;i ng&agrave;y nằm y&ecirc;n dưới nước đ&atilde; được những người l&agrave;m nghề bắt c&aacute; ph&aacute;t hiện bởi con s&ocirc;ng v&agrave;o m&ugrave;a nước cạn. Khi k&eacute;o chiếc xe l&ecirc;n bờ, c&aacute;c ngư d&acirc;n đem về định rửa sạch để sử dụng th&igrave; t&igrave;m thấy g&oacute;i tiền hơn 37 triệu đồng tại khe b&igrave;nh xăng. Sau khi chia nhau số tiền đ&oacute;, họ đem xe vứt ngo&agrave;i ruộng rau muống để tr&aacute;nh li&ecirc;n lụy. Nhận được tin b&aacute;o c&oacute; chiếc xe v&ocirc; chủ, c&ocirc;ng an quận Thủ Đức đem về điều tra lại ph&aacute;t hiện trong cốp trước c&oacute; gần 140 triệu đồng.</p>\r\n<p class="normal">Qua điều tra truy x&eacute;t, gần 6 th&aacute;ng sau, cơ quan chức năng đ&atilde; bắt Phạm Văn Mạnh v&agrave; người n&agrave;y đ&atilde; khai ra g&atilde; bạn th&acirc;n t&ecirc;n L&acirc;m. Tuy nhi&ecirc;n l&uacute;c n&agrave;y L&acirc;m c&ugrave;ng bạn g&aacute;i đang bị c&ocirc;ng an tỉnh B&igrave;nh Dương giam về tội &ldquo;mua b&aacute;n tr&aacute;i ph&eacute;p chất ma t&uacute;y&rdquo;. C&ocirc;ng an cũng t&iacute;ch cực truy l&ugrave;ng Việt v&agrave; Ph&uacute; M&aacute;t nhưng cả hai đ&atilde; bỏ trốn. Ri&ecirc;ng hai người đ&atilde; biết L&acirc;m b&agrave;n bạc đi cướp tại qu&aacute;n c&agrave; ph&ecirc; nhưng kh&ocirc;ng tham gia cũng bị truy tố về tội &ldquo;kh&ocirc;ng tố gi&aacute;c tội phạm&rdquo;.</p>\r\n<p class="normal">Được gọi l&ecirc;n thẩm vấn đầu ti&ecirc;n, L&acirc;m mất hẳn vẻ dửng dưng, khai nhận to&agrave;n bộ h&agrave;nh vi phạm tội. Về số tiền được chia sau vụ cướp &ldquo;ho&agrave;nh tr&aacute;ng&rdquo;, L&acirc;m h&agrave;o ph&oacute;ng tặng người t&igrave;nh 25 triệu đồng để mua xe tay ga v&agrave; n&oacute;i dối đ&oacute; l&agrave; tiền gia đ&igrave;nh vừa b&aacute;n đất. Để &ldquo;lấy le&rdquo; với người đẹp, g&atilde; dẫn c&ocirc; đi du lịch nhiều nơi v&agrave; tổ chức nhiều cuộc ăn chơi tr&aacute;c t&aacute;ng tại c&aacute;c vũ trường &ldquo;xịn&rdquo; trong th&agrave;nh phố. Đến khi sạch t&uacute;i, L&acirc;m lấy lại chiếc xe của bạn g&aacute;i đem b&aacute;n để c&oacute; tiền tiếp tục lao v&agrave;o c&aacute;c cuộc &ldquo;bay đ&ecirc;m&rdquo;. Cuối c&ugrave;ng, do kh&ocirc;ng c&ograve;n tiền để ăn chơi, đ&ocirc;i nh&acirc;n t&igrave;nh rủ nhau đi b&aacute;n ma t&uacute;y th&igrave; bị c&ocirc;ng an tỉnh B&igrave;nh Dương bắt v&agrave; bị tuy&ecirc;n phạt 3 năm t&ugrave;.</p>\r\n<p class="normal">&ldquo;Người ta phải đổ mồ h&ocirc;i mới c&oacute; nổi v&agrave;i chục ng&agrave;n đi chợ mỗi ng&agrave;y. C&ograve;n c&aacute;c bị c&aacute;o đi cướp một đ&ecirc;m được cả trăm triệu đem đổ hết v&agrave;o ăn chơi. Bị c&aacute;o thử suy ngẫm xem c&oacute; nhẫn t&acirc;m kh&ocirc;ng? Chơi kiểu g&igrave; m&agrave; cuối c&ugrave;ng kh&ocirc;ng c&ograve;n g&igrave; để khắc phục hậu quả cho bị hại?&rdquo;, nữ chủ tọa lớn tiếng hỏi.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A2/04/94/bai%202.JPG" border="1" alt="" width="330" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">L&acirc;m b&igrave;nh thản ngo&aacute;i nh&igrave;n người th&acirc;n khi vừa mang &aacute;n 21 năm t&ugrave;. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="normal">&ldquo;Mỗi đ&ecirc;m bị c&aacute;o đi chơi hết cả chục triệu n&ecirc;n&hellip;&rdquo;. Chỉ n&oacute;i được đến đ&oacute;, g&atilde; thanh ni&ecirc;n nhỏ th&oacute; c&uacute;i gằm mặt giấu &aacute;nh mắt rệu r&atilde;. Hắn kh&ocirc;ng đủ can đảm để khai những cảnh ngập ngụa th&aacute;c loạn của m&igrave;nh tại vũ trường hay nh&agrave; nghỉ, nơi hắn đ&atilde; chi bộn tiền cho mỗi đ&ecirc;m từ &ldquo;th&agrave;nh quả&rdquo; cướp được.</p>\r\n<p class="normal">Thế nhưng, chỉ &iacute;t ph&uacute;t sau trong giờ nghị &aacute;n, L&acirc;m lại thản nhi&ecirc;n bộc bạch: &ldquo;Mỗi &ldquo;chấm đ&aacute;&rdquo; (ma t&uacute;y tổng hợp) v&agrave;i triệu đồng rồi, c&ograve;n tiền rượu, tiền thu&ecirc; kh&aacute;ch sạn nữa&hellip; Ng&agrave;y n&agrave;o cũng chơi như thế tiền g&igrave; chịu nổi. Giờ bị thế n&agrave;y em cũng chấp nhận rồi, cả chuyện t&igrave;nh y&ecirc;u cũng vậy. Bạn g&aacute;i bị bắt chung vụ ma t&uacute;y với em chỉ ở t&ugrave; v&agrave;i năm l&agrave; về, chứ em &ldquo;đi&rdquo; l&acirc;u như vậy th&igrave; mong đợi g&igrave; ở người ta&rdquo;.</p>\r\n<p class="normal">Ngồi cạnh b&ecirc;n, Mạnh vẫn giữ nguy&ecirc;n th&aacute;i độ e d&egrave;. Phải một l&uacute;c l&acirc;u cậu mới buồn b&atilde; cho biết m&igrave;nh vốn được sinh ra trong một gia đ&igrave;nh cơ bản. Tuy cha Mạnh từng l&agrave; bộ đội nhưng &ocirc;ng rất cưng chiều đứa con trai &uacute;t. Cũng ch&iacute;nh từ sự nu&ocirc;ng chiều th&aacute;i qu&aacute; của cha mẹ m&agrave; Mạnh đ&atilde; sống ỷ v&agrave;o gia đ&igrave;nh. Trước khi phạm tội cậu từng l&agrave; chủ một cửa h&agrave;ng b&aacute;n điện thoại nhưng do sa đ&agrave; v&agrave;o cờ bạc n&ecirc;n chẳng bao l&acirc;u cửa h&agrave;ng đ&oacute; phải đ&oacute;ng cửa. Thất nghiệp, Mạnh thường tụ tập uống c&agrave; ph&ecirc; ở khu vực gần nh&agrave; v&agrave; cặp k&egrave; với L&acirc;m.</p>\r\n<p class="normal">&ldquo;Tụi em l&agrave; bạn chung x&oacute;m chơi với nhau từ hồi nhỏ. H&ocirc;m đ&oacute; L&acirc;m rủ em đi uống c&agrave; ph&ecirc; chứ kh&ocirc;ng b&agrave;n bạc g&igrave; trước. Ra đến qu&aacute;n em mới nghe họ n&oacute;i về &acirc;m mưu cướp tiền nhưng nghĩ cứ đi chung với L&acirc;m xem sao. Nếu em biết cướp tiền tại con hẻm đ&oacute; em đ&atilde; kh&ocirc;ng d&aacute;m tham gia bởi vị tr&iacute; đ&oacute; chỉ c&aacute;ch nh&agrave; chị g&aacute;i em chục m&eacute;t&rdquo;, Mạnh vừa n&oacute;i vừa buồn buồn nh&igrave;n L&acirc;m đang len l&eacute;n c&uacute;i đầu.</p>\r\n<p class="normal">N&oacute;i về số tiền hơn trăm triệu được chia, Mạnh thở d&agrave;i: &ldquo;Em trả nợ cũ, mua xe mới v&agrave;&hellip; thua b&agrave;i hết. Giờ nghĩ lại thấy m&igrave;nh dại dột qu&aacute;&rdquo;</p>\r\n<p class="normal">H&ocirc;m đ&oacute;, TAND TP HCM đ&atilde; tuy&ecirc;n phạt L&acirc;m mức &aacute;n 18 năm t&ugrave; về tội &ldquo;cướp t&agrave;i sản&rdquo;, buộc bị c&aacute;o phải chấp h&agrave;nh chung với bản &aacute;n 3 năm t&ugrave; tội &ldquo;mua b&aacute;n tr&aacute;i ph&eacute;p chất ma t&uacute;y&rdquo;. C&ograve;n Mạnh cũng phải nhận 13 năm t&ugrave; về tội &ldquo;cướp t&agrave;i sản&rdquo;. Li&ecirc;n quan đến vụ &aacute;n, 2 người bạn của họ cũng l&atilde;nh 1 năm t&ugrave; nhưng cho hưởng &aacute;n treo về h&agrave;nh vi &ldquo;kh&ocirc;ng tố gi&aacute;c tội phạm&rdquo;. Ngo&agrave;i ra t&ograve;a cũng buộc L&acirc;m v&agrave; Mạnh li&ecirc;n đới bồi thường to&agrave;n bộ thiệt hại cho nạn nh&acirc;n.</p>\r\n<p class="normal">Phi&ecirc;n t&ograve;a kết th&uacute;c, nh&igrave;n hai bị c&aacute;o riu r&iacute;u bước l&ecirc;n xe về trại trong tiếng nức nở của người th&acirc;n, một th&agrave;nh vi&ecirc;n trong HĐXX ngao ng&aacute;n lắc đầu: &ldquo;Chỉ v&igrave; ch&acirc;y lười lao động, muốn c&oacute; tiền bằng mọi c&aacute;ch để sống hưởng thụ m&agrave; những người trẻ đ&atilde; bất chấp tất cả. D&ugrave; họ phải trả gi&aacute; nhưng hệ lụy m&agrave; họ g&acirc;y ra từ h&agrave;nh vi của m&igrave;nh đ&atilde; l&agrave; qu&aacute; lớn&rdquo;.</p>\r\n<p class="normal" style="text-align: right;"><strong>Vũ Mai</strong></p>', 26, 6, 913, 0, 1),
(750, 'Âm mưu man rợ của cặp sát thủ tuổi teen', 'Hai gã trai trẻ cúi gằm đầu, run rẩy trong tiếng la ó phẫn nộ của hàng trăm người dự khán. Chỉ cách hàng rào cảnh sát, gương mặt gia đình nạn nhân đau đớn khi những kẻ sát nhân tường thuật chi tiết sát hại việc con gái họ.', 'bai (1).JPG', NULL, '<p class="Title">&Acirc;m mưu man rợ của cặp s&aacute;t thủ tuổi teen</p>\r\n<p class="Lead">Hai g&atilde; trai trẻ c&uacute;i gằm đầu, run rẩy trong tiếng la &oacute; phẫn nộ của h&agrave;ng trăm người dự kh&aacute;n. Chỉ c&aacute;ch h&agrave;ng r&agrave;o cảnh s&aacute;t, gương mặt gia đ&igrave;nh nạn nh&acirc;n đau đớn khi những kẻ s&aacute;t nh&acirc;n tường thuật chi tiết s&aacute;t hại việc con g&aacute;i họ.</p>\r\n<p class="Normal">D&ugrave; an ninh được tăng cường tối đa, nhưng phải kh&oacute; khăn lắm cảnh s&aacute;t mới giữ được trật tự tại phi&ecirc;n t&ograve;a lưu động x&eacute;t xử L&ecirc; Thanh Vũ (19 tuổi, ngụ Tiền Giang) v&agrave; Ho&agrave;ng Cao Nguy&ecirc;n (20 tuổi, ngụ TP Huế). H&agrave;ng trăm con người, h&agrave;ng trăm &aacute;nh nh&igrave;n căm phẫn n&eacute;m về ph&iacute;a hai thanh ni&ecirc;n nhỏ th&oacute; trước v&agrave;nh m&oacute;ng ngựa. Phi&ecirc;n xử vừa được khai mạc, lại th&ecirc;m một đợt phản ứng của người d&acirc;n khi họ x&ocirc; đẩy nhau để tr&agrave;n l&ecirc;n trước. Ai cũng muốn nh&igrave;n r&otilde; mặt những kẻ s&aacute;t nh&acirc;n đ&atilde; g&acirc;y ra vụ &aacute;n kinh ho&agrave;ng tại địa phương m&igrave;nh c&aacute;ch đ&oacute; kh&ocirc;ng l&acirc;u.</p>\r\n<p class="Normal">Trong khi Nguy&ecirc;n co người tr&ecirc;n chiếc ghế nhựa b&eacute; x&iacute;u trong v&ograve;ng v&acirc;y cảnh s&aacute;t, Vũ nặng nhọc bước l&ecirc;n trả lời thẩm vấn của HĐXX. Mưu đồ s&aacute;t nh&acirc;n của những kẻ l&agrave;m thu&ecirc; nhắm v&agrave;o gia đ&igrave;nh &ocirc;ng Trần, tiểu thương tại chợ Nguyễn Tri Phương (quận 10, TP HCM) lộ r&otilde; khiến nhiều người dự kh&aacute;n rợn người.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/F8/9C/bai.JPG" border="1" alt="" width="480" height="330" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Vũ (&aacute;o sọc) v&agrave; Nguy&ecirc;n c&uacute;i gằm đầu khi được dẫn giải đến v&agrave;nh m&oacute;ng ngựa. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Vũ khai, khoảng th&aacute;ng 4/2008, g&atilde; được &ocirc;ng Trần nhận l&agrave;m phụ việc trong gia đ&igrave;nh. H&agrave;ng ng&agrave;y, Vũ đến chợ gi&uacute;p b&agrave; chủ kinh doanh thịt heo n&ecirc;n biết r&otilde; b&agrave; n&agrave;y thu rất nhiều tiền sau một ng&agrave;y bu&ocirc;n b&aacute;n. Hơn một năm sau, do c&ocirc;ng việc bận rộn, &ocirc;ng Trần tiếp tục thu&ecirc; Ho&agrave;ng Cao Nguy&ecirc;n. Cả hai được &ocirc;ng b&agrave; chủ cho ăn ở trong nh&agrave; cho đến khi Vũ c&oacute; bạn g&aacute;i v&agrave; dọn ra thu&ecirc; ph&ograve;ng ở ri&ecirc;ng.</p>\r\n<p class="Normal">Thời gian rảnh rỗi trong ng&agrave;y, hai kẻ l&agrave;m thu&ecirc; thường rủ nhau đi uống c&agrave; ph&ecirc; hay nhậu nhẹt. Trong một lần t&acirc;m sự, Nguy&ecirc;n th&uacute; thật với Vũ, chứng minh nh&acirc;n d&acirc;n m&agrave; anh ta đưa cho &ocirc;ng b&agrave; chủ l&agrave; của người kh&aacute;c v&agrave; chỉ c&oacute; &yacute; định l&agrave;m tạm ở đ&acirc;y, chờ c&oacute; cơ hội sẽ ra tay chiếm đoạt t&agrave;i sản của &ocirc;ng b&agrave; chủ. Vũ cũng cho biết m&igrave;nh đang rất cần tiền v&igrave; vừa g&acirc;y tai nạn giao th&ocirc;ng bị c&ocirc;ng an giam xe, c&ograve;n bạn g&aacute;i c&oacute; nguy cơ phải đi l&agrave;m tiếp vi&ecirc;n qu&aacute;n c&agrave; ph&ecirc;. Quyết t&acirc;m phải l&agrave;m một c&aacute;i g&igrave; đ&oacute; để c&oacute; thật nhiều tiền, Vũ rủ bạn về qu&ecirc; m&igrave;nh tại Tiền Giang để&hellip; cướp tiệm v&agrave;ng.</p>\r\n<p class="Normal">Đồng &yacute; kiếm tiền c&ugrave;ng Vũ nhưng Nguy&ecirc;n gạt phắt &yacute; định t&aacute;o bạo n&agrave;y v&igrave; biết kh&ocirc;ng thể thực hiện. Sau một hồi suy nghĩ, g&atilde; thanh ni&ecirc;n đến từ TP Huế b&egrave;n n&oacute;i dối m&igrave;nh đang giấu 5 b&aacute;nh heroin ở nh&agrave;, dụ Vũ cướp t&agrave;i sản của &ocirc;ng b&agrave; chủ l&agrave;m chi ph&iacute; về qu&ecirc; &ldquo;thanh l&yacute;&rdquo; số ma t&uacute;y đ&oacute; b&aacute;n lấy tiền chia nhau. Tưởng thật, Vũ mau mắn l&ecirc;n kế hoạch sẽ canh sơ hở để giật giỏ tiền của b&agrave; chủ v&agrave;o cuối ng&agrave;y khi từ chợ về. Tuy nhi&ecirc;n, mấy ng&agrave;y sau đ&oacute; cả hai kh&ocirc;ng thực hiện được bởi khu vực đ&oacute; đ&ocirc;ng d&acirc;n cư v&agrave; gần trụ sở c&ocirc;ng an.</p>\r\n<p class="Normal">Chiều 15/6/2009, cũng l&agrave; ng&agrave;y sinh nhật thứ 18 của Vũ, cả hai rủ th&ecirc;m Thắng (16 tuổi, con trai của &ocirc;ng Trần) ra gần nh&agrave; nhậu. Trong l&uacute;c Thắng đi vệ sinh, hai g&atilde; người l&agrave;m thống nhất: &ldquo;Tối nay m&agrave; kh&ocirc;ng giật được giỏ tiền của b&agrave; chủ sẽ ra tay &lsquo;khử&rsquo; hết những người trong nh&agrave; để cướp t&agrave;i sản&rdquo;. Một lần nữa bọn ch&uacute;ng đ&atilde; kh&ocirc;ng c&oacute; cơ hội giật được giỏ tiền.</p>\r\n<p class="Normal">&ldquo;T&ocirc;i gặp Nguy&ecirc;n ở ban c&ocirc;ng b&agrave;n bạc sẽ lấy m&aacute;y nhạc của Thắng l&ecirc;n lầu 2 mở nghe, thể n&agrave;o n&oacute; cũng l&ecirc;n lấy lại v&agrave; ch&uacute;ng t&ocirc;i sẽ giết n&oacute; trước. Sau đ&oacute; sẽ xuống tầng một giết chết Loan (19 tuổi, con g&aacute;i &ocirc;ng Trần), rồi xuống tầng lửng giết &ocirc;ng b&agrave; chủ&hellip;&rdquo;, Vũ vẫn c&uacute;i gằm đầu, nhỏ giọng khai lại kế hoạch phạm tội của m&igrave;nh. Lời bị c&aacute;o vừa dứt, nhiều người dự kh&aacute;n k&ecirc;u l&ecirc;n: &ldquo;Qu&acirc;n d&atilde; man! Đồ độc &aacute;c&hellip;&rdquo;.</p>\r\n<p class="Normal">&ldquo;Chỉ vừa tr&ograve;n 18 tuổi nhưng c&aacute;c bị c&aacute;o lại sẵn s&agrave;ng c&oacute; những h&agrave;nh động độc &aacute;c như vậy sao? Hai mẹ con b&agrave; Trần ra khỏi nh&agrave; v&agrave; may mắn tho&aacute;t chết l&agrave; ngo&agrave;i dự kiến của c&aacute;c bị c&aacute;o?&rdquo;, vị thẩm ph&aacute;n nghi&ecirc;m giọng hỏi.</p>\r\n<p class="Normal">Kh&ocirc;ng thể chối c&atilde;i, Vũ vội thở dốc rồi run rẩy khai tiếp. Thấy trong nh&agrave; chỉ c&ograve;n cha con &ocirc;ng chủ, hai g&atilde; thanh ni&ecirc;n ph&acirc;n c&ocirc;ng Vũ sẽ giết Loan, c&ograve;n Nguy&ecirc;n sẽ xuống tầng lửng giết &ocirc;ng Trần.</p>\r\n<p class="Normal">&ldquo;Loan đang ngồi trước m&aacute;y vi t&iacute;nh n&ecirc;n bị c&aacute;o đến bịt miệng rồi đ&acirc;m v&agrave;o bụng. Khi c&ocirc; ấy v&ugrave;ng vẫy ng&atilde; xuống nệm k&ecirc;u la, bị c&aacute;o nh&agrave;o đến cắt nhiều nh&aacute;t v&agrave;o cổ Loan&hellip;&rdquo;. Lời khai lạnh l&ugrave;ng của Vũ một lần nữa đứt qu&atilde;ng bởi tiếng la &oacute; của mọi người. Nhiều &aacute;nh mắt &aacute;i ngại cũng đổ dồn về ph&iacute;a gia đ&igrave;nh nạn nh&acirc;n, nơi &ocirc;ng Trần lặng lẽ lau đ&ocirc;i k&iacute;nh cận nh&ograve;e nước mắt. C&ograve;n vợ &ocirc;ng, sau giờ ph&uacute;t chịu đựng, đ&atilde; bật kh&oacute;c trước nỗi đau qu&aacute; lớn. Đứa con g&aacute;i ngoan hiền m&agrave; b&agrave; y&ecirc;u qu&yacute; đ&atilde; vĩnh viễn ra đi bởi tội &aacute;c man rợ của những kẻ l&agrave;m thu&ecirc; cho gia đ&igrave;nh.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/F8/9C/bai%202.JPG" border="1" alt="" width="330" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nỗi đau chất chứa của gia đ&igrave;nh nạn nh&acirc;n. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cũng bị đ&acirc;m nhiều nh&aacute;t nhưng m&aacute;y mắn tho&aacute;t chết, mang thương tật 30% vĩnh viễn, &ocirc;ng Trần bước về ph&iacute;a Hội đồng x&eacute;t xử nghẹn giọng thuật lại buổi tối kinh ho&agrave;ng của cuộc đời m&igrave;nh.</p>\r\n<p class="Normal">Theo đ&oacute;, khi nghe tiếng k&ecirc;u của con g&aacute;i, &ocirc;ng hối hả chạy l&ecirc;n th&igrave; thấy Loan ng&atilde; gục dưới đất c&ograve;n Vũ th&igrave; lăm lăm dao nhọn tr&ecirc;n tay. Vừa thấy &ocirc;ng, Vũ lao đến ch&eacute;m thẳng v&agrave;o đầu rồi quay dao đ&acirc;m &ocirc;ng chủ t&uacute;i bụi. Đỡ được nh&aacute;t những dao hiểm &aacute;c, &ocirc;ng Trần quay lưng bỏ chạy đồng thời k&ecirc;u Nguy&ecirc;n cứu m&igrave;nh. Người đ&agrave;n &ocirc;ng kh&ocirc;ng hề biết g&atilde; người l&agrave;m c&ograve;n lại l&agrave; đồng bọn của Vũ v&agrave; hắn đang ẩn m&igrave;nh dưới cầu thang v&agrave; thấy rất r&otilde; &ocirc;ng chủ đ&atilde; bị bạn m&igrave;nh truy s&aacute;t. Đuổi theo &ocirc;ng Trần xuống tầng lửng, Vũ đ&acirc;m xối xả v&agrave;o nạn nh&acirc;n rồi b&oacute;p cổ &ocirc;ng n&agrave;y đến ngất lịm.</p>\r\n<p class="Normal">C&oacute; mặt tại t&ograve;a, h&agrave;ng x&oacute;m của &ocirc;ng Trần, người đ&atilde; ph&aacute;t hiện sự việc cho biết m&igrave;nh ở đối diện nh&agrave; của nạn nh&acirc;n. Khi nghe thấy tiếng động kh&aacute;c lạ, &ocirc;ng nh&igrave;n sang ban c&ocirc;ng nh&agrave; &ocirc;ng Trần th&igrave; thấy Loan nằm sấp tr&ecirc;n nền nh&agrave;, người đầy m&aacute;u n&ecirc;n đ&atilde; tri h&ocirc;. &ldquo;Sau khi b&aacute;o c&ocirc;ng an, t&ocirc;i quay về th&igrave; thấy Loan tỉnh lại. Tới b&acirc;y giờ t&ocirc;i vẫn kh&ocirc;ng khỏi &aacute;m ảnh v&igrave; &aacute;nh mắt v&agrave; tiếng k&ecirc;u cứu đau thương của con b&eacute; l&uacute;c đ&oacute;. N&oacute; kh&ocirc;ng kh&aacute;c g&igrave; con t&ocirc;i&hellip;&rdquo;, người đ&agrave;n &ocirc;ng rưng rưng nước mắt.</p>\r\n<p class="Normal">Theo hồ sơ vụ &aacute;n, biết sự việc bị ph&aacute;t hiện, Nguy&ecirc;n đ&atilde; ra hiệu cho Vũ tr&egrave;o l&ecirc;n s&acirc;n thượng định tẩu tho&aacute;t nhưng đ&atilde; bị bắt giữ khi trốn trong toilet nh&agrave; b&ecirc;n cạnh. C&ograve;n Nguy&ecirc;n vờ tr&egrave;o ban c&ocirc;ng xuống đường k&ecirc;u cứu nhưng cũng bị bắt giữ sau đ&oacute;.</p>\r\n<p class="Normal">Giờ nghị &aacute;n, cả gia đ&igrave;nh &ocirc;ng Trần ngồi chết lặng, vẻ mặt ai cũng chất chứa nỗi đau. &ldquo;Chỉ đến khi xuất viện t&ocirc;i mới biết m&igrave;nh đ&atilde; mất con. Mọi người kh&ocirc;ng d&aacute;m cho t&ocirc;i biết v&igrave; sợ t&ocirc;i kh&ocirc;ng chịu nổi mất m&aacute;t n&agrave;y. Con b&eacute; vẫn đang tr&agrave;n trề hy vọng được v&agrave;o l&agrave;m trong ng&agrave;nh h&agrave;ng kh&ocirc;ng&hellip;&rdquo;, từng giọt nước mắt lại rơi tr&ecirc;n gương mặt người cha đau khổ.</p>\r\n<p class="Normal">Ng&agrave;y 21/8, đ&aacute;nh gi&aacute; tội &aacute;c của c&aacute;c bị c&aacute;o l&agrave; rất d&atilde; man, kh&ocirc;ng c&ograve;n t&iacute;nh người&hellip; TAND TP HCM đ&atilde; tuy&ecirc;n phạt L&ecirc; Thanh Vũ &aacute;n tử h&igrave;nh, Ho&agrave;ng Cao Nguy&ecirc;n cũng phải nhận &aacute;n t&ugrave; chung th&acirc;n c&ugrave;ng về tội &ldquo;giết người&rdquo; v&agrave; &ldquo;cướp t&agrave;i sản&rdquo;.</p>\r\n<p class="Normal">Kh&ocirc;ng kịp nghe hết bản &aacute;n, Vũ vịn tay v&agrave;o v&agrave;nh m&oacute;ng ngựa để khỏi ng&atilde; quỵ. Khi chiếc c&ograve;ng kh&oacute;a ngoặt tay, g&atilde; thanh ni&ecirc;n thảng thốt, d&aacute;o d&aacute;c t&igrave;m cha nhưng kh&ocirc;ng thể. Hắn kh&ocirc;ng biết &ocirc;ng đang chết lặng giữa phi&ecirc;n t&ograve;a.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ Mai</strong></p>', 26, 6, 271, 0, 1),
(751, 'Tiếng khóc trẻ thơ trước vành móng ngựa', 'Chuông tòa vang lên cũng là lúc người mẹ giằng bầu sữa ra khỏi miệng đứa bé. Tiếng khóc thét của trẻ thơ xé toang không khí trang nghiêm chốn pháp đình, như xé nát trái tim người mẹ trước giờ lãnh án dành cho kẻ giết người.', 'to (3).JPG', NULL, '<p class="Title">Tiếng kh&oacute;c trẻ thơ trước v&agrave;nh m&oacute;ng ngựa</p>\r\n<p class="Lead">Chu&ocirc;ng t&ograve;a vang l&ecirc;n cũng l&agrave; l&uacute;c người mẹ giằng bầu sữa ra khỏi miệng đứa b&eacute;. Tiếng kh&oacute;c th&eacute;t của trẻ thơ x&eacute; toang kh&ocirc;ng kh&iacute; trang nghi&ecirc;m chốn ph&aacute;p đ&igrave;nh, như x&eacute; n&aacute;t tr&aacute;i tim người mẹ trước giờ l&atilde;nh &aacute;n d&agrave;nh cho kẻ giết người.</p>\r\n<p class="Normal">12 năm trước, sau khi ly dị, &ocirc;ng Qu&aacute;ch về chung sống với mẹ của Nguyễn Thị Hồng V&acirc;n (22 tuổi) tại phường An Lạc, quận B&igrave;nh T&acirc;n (TP HCM). D&ugrave; ở ho&agrave;n cảnh &ldquo;rổ r&aacute; cạp lại&rdquo; nhưng người đ&agrave;n &ocirc;ng rất thương y&ecirc;u vợ v&agrave; 5 đứa con ri&ecirc;ng của b&agrave; n&agrave;y.</p>\r\n<p class="Normal">S&aacute;ng ng&agrave;y 16/7/2009, do bị bệnh kh&ocirc;ng thể đi lấy h&agrave;ng về b&aacute;n, V&acirc;n nhờ mẹ mua gi&uacute;p 10 kg khổ qua v&agrave; 2 kg x&agrave; l&aacute;ch. Do chở rau quả qu&aacute; nặng v&agrave; cồng kềnh, khi về đến sạp &ocirc;ng Qu&aacute;ch bực bội cằn nhằn vợ. Thấy vậy, mẹ V&acirc;n liền xẵng giọng: &ldquo;Hổm r&agrave;y con V&acirc;n n&oacute; bệnh n&ecirc;n mới gửi mua đồ chứ l&agrave;m g&igrave; m&agrave; cứ cằn nhằn ho&agrave;i&hellip;&rdquo;. Thấy vậy, &ocirc;ng Qu&aacute;ch l&ecirc;n tiếng chửi thề v&agrave; hất tung m&acirc;m h&agrave;nh tỏi của vợ.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/EB/C4/to.JPG" border="1" alt="" width="400" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nước mắt người mẹ trẻ thấm ướt chiếc &aacute;o m&agrave;u l&aacute; mạ, rơi tr&agrave;n xuống khu&ocirc;n mặt ng&acirc;y thơ của đứa trẻ đang &ocirc;m gh&igrave; v&uacute; mẹ&hellip; Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Tức giận v&igrave; th&aacute;i độ của cha dượng, V&acirc;n chạy đến g&acirc;y gổ cũng bị &ocirc;ng Qu&aacute;ch chửi l&acirc;y. C&ocirc; đưa tay chỉ v&agrave;o mẹ th&aacute;ch thức: &ldquo;Bả n&egrave;, &ocirc;ng th&iacute;ch th&igrave; cứ chửi đi&rdquo;. Thấy V&acirc;n hỗn h&agrave;o cả với mẹ, &ocirc;ng Qu&aacute;ch vung tay t&aacute;t v&agrave;o mặt c&ocirc; mấy c&aacute;i. Bị con ri&ecirc;ng của vợ đ&aacute; lại, người đ&agrave;n &ocirc;ng chụp ch&acirc;n V&acirc;n khiến c&ocirc; ng&atilde; xuống nền chợ rồi k&eacute;o đi. Nh&igrave;n thấy con dao trong m&acirc;m đựng khổ qua, V&acirc;n chụp lấy đ&acirc;m mạnh v&agrave;o ngực cha dượng khiến &ocirc;ng gục xuống. D&ugrave; được đưa đi cấp cứu nhưng &ocirc;ng Qu&aacute;ch đ&atilde; chết trước đ&oacute; do vết thương qu&aacute; nặng.</p>\r\n<p class="Normal">Ngay sau đ&oacute; V&acirc;n bị khởi tố về h&agrave;nh vi &ldquo;giết người&rdquo; nhưng được tại ngoại hầu tra do c&oacute; con nhỏ dưới 3 tuổi. Hơn một năm sau, ng&agrave;y 30/7, TAND TP HCM đưa vụ &aacute;n ra x&eacute;t xử. L&uacute;c n&agrave;y, V&acirc;n đ&atilde; c&oacute; th&ecirc;m một đứa con chỉ gần 3 th&aacute;ng tuổi.</p>\r\n<p class="Normal">Trả lời thẩm vấn của HĐXX, V&acirc;n lu&ocirc;n đổ tội cho cha dượng đ&atilde; chửi mắng, đ&aacute;nh mẹ v&agrave; bản th&acirc;n m&igrave;nh n&ecirc;n mới lấy dao đ&acirc;m. Thậm ch&iacute;, V&acirc;n c&ograve;n khai th&ecirc;m những điều m&agrave; trong hồ sơ vụ &aacute;n kh&ocirc;ng thể hiện. Cuối c&ugrave;ng, c&ocirc; đổ thừa do m&igrave;nh chỉ mới học hết lớp 1 n&ecirc;n mới h&agrave;nh xử như thế.</p>\r\n<p class="Normal">&ldquo;D&ugrave; &ocirc;ng ấy kh&ocirc;ng phải l&agrave; cha ruột cũng l&agrave; chồng của mẹ bị c&aacute;o bao nhi&ecirc;u năm trời. &Ocirc;ng ấy bực th&igrave; chửi v&agrave;i c&acirc;u, hất đổ m&acirc;m h&agrave;ng cũng l&agrave; của &ocirc;ng ấy. Ch&iacute;nh bị c&aacute;o đ&atilde; k&iacute;ch động, hỗn h&agrave;o với cả mẹ n&ecirc;n &ocirc;ng ấy mới đ&aacute;nh. Bị c&aacute;o đ&atilde; c&oacute; hai đứa con rồi m&agrave; kh&ocirc;ng nhận ra sai tr&aacute;i của m&igrave;nh th&igrave; sau n&agrave;y l&agrave;m sao dạy con tốt được. Chả lẽ cứ giận nhau l&agrave; cầm dao đ&acirc;m hay sao? C&ograve;n tr&igrave;nh độ văn h&oacute;a c&oacute; thấp cũng kh&ocirc;ng c&oacute; nghĩa l&agrave; kh&ocirc;ng biết c&aacute;ch cư xử&rdquo;, vị chủ tọa kh&ocirc;ng giấu được bức x&uacute;c.</p>\r\n<p class="Normal">Đ&aacute;p lại, V&acirc;n ng&oacute; lơ xuống đất, hai tay vịn ngang v&agrave;nh m&oacute;ng ngựa. C&ograve;n mẹ V&acirc;n, được hỏi đến, b&agrave; cũng một mực b&ecirc;nh vực con g&aacute;i d&ugrave; những điều khai nại rất vụng về, ng&ocirc; ngh&ecirc;. T&ograve;a hỏi v&igrave; sao kh&ocirc;ng lo tang lễ cho chồng, b&agrave; n&agrave;y cho rằng do chị chồng kh&ocirc;ng cho ph&eacute;p v&agrave; ho&agrave;n cảnh ngh&egrave;o khổ, kh&oacute; khăn qu&aacute;.</p>\r\n<p class="Normal">&ldquo;Ch&iacute;nh b&agrave; l&agrave; một phần nguy&ecirc;n nh&acirc;n dẫn đến vụ &aacute;n n&agrave;y. Chồng con xung khắc b&agrave; cũng kh&ocirc;ng khuy&ecirc;n can, con hỗn h&agrave;o b&agrave; cũng kh&ocirc;ng la rầy. Nếu b&agrave; biết c&aacute;ch cư xử th&igrave; hậu quả đ&acirc;u đau x&oacute;t đến độ một mạng người phải mất đi, con g&aacute;i phải ngồi t&ugrave;&rdquo;, vị Hội thẩm nh&acirc;n d&acirc;n ngồi b&ecirc;n lớn giọng.</p>\r\n<p class="Normal">Giờ nghị &aacute;n, mọi &aacute;nh mắt dồn hẳn về cuối ph&ograve;ng, nơi c&oacute; người đ&agrave;n b&agrave; luống tuổi đang sang sảng n&oacute;i, b&agrave; l&agrave; chị của &ocirc;ng Qu&aacute;ch. Cạnh b&ecirc;n, mẹ của V&acirc;n sau v&agrave;i tiếng thanh minh liền ngồi lặng c&acirc;m đầy vẻ cam chịu:</p>\r\n<p class="Normal">&ldquo;Chị n&oacute;i thiệt, chị thương vợ chồng em lắm n&ecirc;n mới cho tiền nhiều lần, c&ograve;n mua cho mảnh đất. Chị cũng biết, bao nhi&ecirc;u tiền mọi người cho n&oacute;, em đều giữ hết rồi đem gi&uacute;p đỡ con. Bao nhi&ecirc;u lần chị tha thứ cho em nhưng em kh&ocirc;ng sửa đổi m&agrave; cứ ăn hiếp em chị m&atilde;i. Em c&oacute; nhớ ng&agrave;y l&agrave;m đ&aacute;m ma mẹ, em c&ograve;n đ&aacute;nh n&oacute; bể đầu kh&ocirc;ng? Em đối xử với n&oacute; như vậy l&agrave;m sao con em t&ocirc;n trọng n&oacute; được. Giờ th&igrave; con em giết n&oacute; lu&ocirc;n rồi đấy&rdquo;, người đ&agrave;n b&agrave; rơm rớm nước mắt khi nhắc về c&aacute;i chết của em trai.</p>\r\n<p class="Normal">Tuy tr&aacute;ch m&oacute;c l&agrave; thế, nhưng trước đ&oacute; ch&iacute;nh b&agrave; đ&atilde; đề nghị t&ograve;a kh&ocirc;ng buộc V&acirc;n phải bồi thường to&agrave;n bộ chi ph&iacute; ma chay, tổn thất tinh thần hay cấp dưỡng nu&ocirc;i con ruột của &ocirc;ng Qu&aacute;ch với người vợ trước. Bởi theo b&agrave;, V&acirc;n đ&atilde; qu&aacute; ngh&egrave;o khổ.</p>\r\n<p class="Normal">Tr&ecirc;n ghế bị c&aacute;o, V&acirc;n giấu mặt v&agrave;o tay, nức nở. Chợt nghe tiếng kh&oacute;c của đứa con thơ kh&aacute;t sữa m&agrave; người nh&agrave; vừa bế trộm v&agrave;o ph&ograve;ng xử (quy định của t&ograve;a kh&ocirc;ng cho trẻ em v&agrave;o), c&ocirc; nho&agrave;i người khỏi v&agrave;nh m&oacute;ng ngựa, vội v&agrave;ng cho con b&uacute;. Cứ thế, nước mắt người mẹ trẻ thấm ướt chiếc &aacute;o m&agrave;u l&aacute; mạ, rơi tr&agrave;n xuống khu&ocirc;n mặt ng&acirc;y thơ của đứa trẻ đang &ocirc;m gh&igrave; v&uacute; mẹ&hellip;</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/EB/C4/to%202.JPG" border="1" alt="" width="300" height="400" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Hai đứa trẻ &ocirc;m lấy mẹ sau phi&ecirc;n t&ograve;a. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Chu&ocirc;ng t&ograve;a vang l&ecirc;n cũng l&agrave; l&uacute;c người mẹ giằng bầu sữa ra khỏi miệng đứa b&eacute;. Tiếng kh&oacute;c ngằn ngặt của trẻ thơ x&eacute; toang bầu kh&ocirc;ng kh&iacute; trang nghi&ecirc;m của chốn ph&aacute;p đ&igrave;nh. V&acirc;n đưa tay quyệt vội d&ograve;ng nước mắt. Chứng kiến cảnh ấy, nhiều người dự kh&aacute;n kh&ocirc;ng khỏi x&oacute;t xa.</p>\r\n<p class="Normal">Cho rằng chỉ v&igrave; m&acirc;u thuẫn nhỏ nhưng với bản t&iacute;nh hung hăng, thiếu k&igrave;m chế, thiếu gi&aacute;o dục m&agrave; V&acirc;n đ&atilde; d&ugrave;ng dao đ&acirc;m chết cha dượng, nhưng HĐXX cũng cho rằng ph&iacute;a nạn nh&acirc;n cũng c&oacute; một phần lỗi n&ecirc;n đ&atilde; tuy&ecirc;n phạt Nguyễn Thị Hồng V&acirc;n mức &aacute;n 8 năm t&ugrave; về tội &ldquo;giết người&rdquo;.</p>\r\n<p class="Normal">Phi&ecirc;n t&ograve;a kết th&uacute;c nhưng V&acirc;n vẫn đứng chết lặng tr&ecirc;n v&agrave;nh m&oacute;ng ngựa, đ&ocirc;i mắt sưng mọng thất thần nh&igrave;n theo b&oacute;ng HĐXX. Chỉ đến khi người thư k&yacute; t&ograve;a giải th&iacute;ch th&ecirc;m về bản &aacute;n, về quyền kh&aacute;ng c&aacute;o v&agrave; khả năng bị bắt thi h&agrave;nh &aacute;n khi bản &aacute;n c&oacute; hiệu lực, V&acirc;n mới lủi thủi quay lưng.</p>\r\n<p class="Normal">Bước nhanh đến chiếc ghế đ&aacute; dưới t&agrave;n c&acirc;y v&uacute; sữa, nơi hai con nhỏ đang ng&oacute;ng đợi, c&ocirc; &ocirc;m chặt ch&uacute;ng v&agrave;o l&ograve;ng thổn thức. Những đứa b&eacute; ng&acirc;y thơ được quện hơi mẹ cứ cười đ&ugrave;a vui sướng. Ch&uacute;ng đ&acirc;u biết rằng m&igrave;nh sắp phải chia l&igrave;a khỏi v&ograve;ng tay mẫu tử chỉ v&igrave; một ph&uacute;t sai lầm của người sinh th&agrave;nh. Một lần nữa, những giọt nước mắt hối hận muộn mằn của người mẹ trẻ lại vương đầy tr&ecirc;n m&aacute; con thơ.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ Mai</strong></p>', 26, 6, 1779, 0, 1),
(752, 'Trải lòng của người mẹ có con phạm tội giết cha', '"Ông ấy suốt ngày đánh đập vợ, mắng nhiếc con và cấm không cho chúng được đi học hành. Có lần ông ấy còn đốt nhà...", mẹ bị cáo bị buộc tội giết người nấc nghẹn trong phòng xét xử.', 'toa.jpg', NULL, '<p class="Title"><strong>Trải l&ograve;ng của người mẹ c&oacute; con phạm tội giết cha</strong></p>\r\n<p class="Lead">"&Ocirc;ng ấy suốt ng&agrave;y đ&aacute;nh đập vợ, mắng nhiếc con v&agrave; cấm kh&ocirc;ng cho ch&uacute;ng được đi học h&agrave;nh. C&oacute; lần &ocirc;ng ấy c&ograve;n đốt nh&agrave;...", mẹ bị c&aacute;o bị buộc tội giết người nấc nghẹn trong ph&ograve;ng x&eacute;t xử.</p>\r\n<p class="Normal">Kể từ ng&agrave;y nhận được giấy triệu tập của t&ograve;a với tư c&aacute;ch vừa l&agrave; đại diện của người bị hại, vừa l&agrave; đại diện cho bị c&aacute;o, b&agrave; Hạnh thấp thỏm kh&ocirc;ng y&ecirc;n.</p>\r\n<p class="Normal">Ng&agrave;y TAND Nghệ An đưa Lữ Văn Ho&agrave;n (24 tuổi ở huyện Quế Phong) ra x&eacute;t xử tội giết cha dượng cũng l&agrave; ng&agrave;y anh em họ tộc tổ chức lễ c&uacute;ng cho nạn nh&acirc;n. Sau khi tất tả ngược xu&ocirc;i lo lắng đồ lễ để l&agrave;m v&iacute;a cho chồng theo phong tục của d&acirc;n tộc Th&aacute;i, 3h s&aacute;ng 20/7, b&agrave; Hạnh c&ugrave;ng hai con trai ra trung t&acirc;m huyện để bắt xe về Vinh dự phi&ecirc;n t&ograve;a.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/EA/75/toa.jpg" border="1" alt="Sau phi&ecirc;n xử, người nh&agrave; bị c&aacute;o cũng l&agrave; người nh&agrave; của nạn nh&acirc;n lục tục rời t&ograve;a. Ảnh: Trường Long." width="490" height="331" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Sau phi&ecirc;n xử, người nh&agrave; bị c&aacute;o cũng l&agrave; người nh&agrave; của nạn nh&acirc;n lục tục rời t&ograve;a. Ảnh:&nbsp;<em>Trường Long.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">C&ugrave;ng đi với c&ograve;n c&oacute; cậu con trai Lữ Văn Hợi, người trực tiếp g&acirc;y ra c&aacute;i chết cho nạn nh&acirc;n. Tuy nhi&ecirc;n Hợi được miễn truy tố v&igrave; chưa đủ tuổi th&agrave;nh ni&ecirc;n.</p>\r\n<p class="Normal">Lần đầu ti&ecirc;n bước ra khỏi bản khiến ba mẹ con kh&ocirc;ng khỏi ngơ ng&aacute;c v&agrave; lo sợ. Xuống xe, mất gần một tiếng hỏi đường, họ mới t&igrave;m được đến được trụ sở của TAND tỉnh. Khi cửa xe chở phạm nh&acirc;n vừa mở, người mẹ ngh&egrave;o l&oacute;ng ng&oacute;ng chạy tới nh&igrave;n mặt đứa con trai bị cảnh s&aacute;t dẫn giải v&agrave;o kh&aacute;n ph&ograve;ng.</p>\r\n<p class="Normal">Hội trường vắng tanh. Suốt phi&ecirc;n xử, mặc d&ugrave; ngồi ở vị tr&iacute; của người bị hại nhưng người phụ nữ c&ugrave;ng hai đứa con trai vẫn chỉ hướng về ph&iacute;a v&agrave;nh m&oacute;ng ngựa. Nước mắt người phụ nữ chảy d&agrave;i mỗi khi nghe đứa con ri&ecirc;ng của m&igrave;nh chậm r&atilde;i trả lời hội đồng x&eacute;t xử.</p>\r\n<p class="Normal">&ldquo;Thưa t&ograve;a, bố con l&agrave; một người thường xuy&ecirc;n uống rượu. Nhiều lần, mấy anh em ch&uacute;ng con phải cắn răng chứng kiến bố đ&aacute;nh đập mẹ đến g&atilde;y răng, g&atilde;y tay, d&ugrave;ng dao ch&eacute;m sắp g&atilde;y ch&acirc;n. Khi ch&uacute;ng con v&agrave;o can ngăn cũng bị bố cho ăn đ&ograve;n&rdquo;, bị c&aacute;o Ho&agrave;n vừa kể vừa kh&oacute;c.</p>\r\n<p class="Normal">Ngồi ph&iacute;a dưới, chị Hạnh cũng nghẹn ng&agrave;o n&oacute;i: &ldquo;&Ocirc;ng ấy cấm kh&ocirc;ng cho con c&aacute;i được học h&agrave;nh. C&oacute; lần c&ograve;n đốt nh&agrave; khiến mấy mẹ con kh&ocirc;ng c&oacute; chỗ t&aacute; t&uacute;c&hellip;&rdquo;.</p>\r\n<p class="Normal">25 năm về l&agrave;m vợ cũng l&agrave; từng đ&oacute; năm người đ&agrave;n b&agrave; d&acirc;n tộc Th&aacute;i phải chịu những trận đ&ograve;n mỗi khi &ocirc;ng Long say xỉn. &ldquo;Mặc d&ugrave; con giết cha l&agrave; kh&ocirc;ng đ&uacute;ng nhưng &ocirc;ng ấy đ&aacute;ng tội chết. C&oacute; ai đời n&agrave;o lại kết tội mẹ con ch&uacute;ng t&ocirc;i "ăn ngủ" với nhau", người mẹ b&ecirc;nh vực cho cậu con trai đang đứng trước v&agrave;nh m&oacute;ng ngựa.</p>\r\n<p class="Normal">Ở h&agrave;ng ghế b&ecirc;n cạnh, Lữ Văn Hợi cũng kh&ocirc;ng cầm được nước mắt: &ldquo;Sau nhiều lần mắng chửi v&agrave; kết tội một c&aacute;ch loạn lu&acirc;n, mấy anh em ch&uacute;ng con rất đau l&ograve;ng nhưng kh&ocirc;ng d&aacute;m l&agrave;m g&igrave; kh&aacute;c m&agrave; chỉ thấy thương mẹ&rdquo;.</p>\r\n<p class="Normal">Khi t&ograve;a hỏi động cơ n&agrave;o đưa đến h&agrave;nh động giết người, bị c&aacute;o Ho&agrave;n khai, h&ocirc;m đ&oacute;, cả gia đ&igrave;nh tổ chức ăn tiệc. Sau khi uống xong mấy ch&eacute;n rượu &ocirc;ng Long liền x&aacute;ch dao chạy ra đường. Lữ Văn Hợi chạy theo n&oacute;i với bố về nh&agrave; ngủ th&igrave; bị bố t&aacute;t v&agrave; d&ugrave;ng dao dọa ch&eacute;m rồi chửi rủa: &ldquo;M&agrave;y kh&ocirc;ng phải l&agrave; con của tao m&agrave; l&agrave; con của thằng Ho&agrave;n với mẹ m&agrave;y&rdquo;.</p>\r\n<p class="Normal">Kh&ocirc;ng giữ được b&igrave;nh tĩnh trước những c&acirc;u mắng nhiếc của bố, Ho&agrave;n v&agrave; Hợi đ&atilde; b&agrave;n nhau mang thuổng v&agrave; gậy chạy đi t&igrave;m bố để "xử l&yacute;". Sau trận đ&ograve;n của hai đứa con, nạn nh&acirc;n gục chết tr&ecirc;n vũng m&aacute;u trước nh&agrave;. Cảnh s&aacute;t đ&atilde; x&aacute;c định, Hợi l&agrave; người trực tiếp g&acirc;y ra c&aacute;i chết cho nạn nh&acirc;n, nhưng l&uacute;c phạm tội, hung thủ mới gần 14 tuổi n&ecirc;n chưa bị truy tố h&igrave;nh sự.</p>\r\n<p class="Normal">Cảm th&ocirc;ng với ho&agrave;n cảnh của mấy mẹ con khi phải sống với một người đ&agrave;n &ocirc;ng vũ phu nhưng đại diện VKS khẳng định h&agrave;nh vi giết chết bố dượng của Ho&agrave;n l&agrave; rất nghi&ecirc;m trọng, đi ngược lại thuần phong mĩ tục của d&acirc;n tộc v&agrave; phải xử l&iacute; nghi&ecirc;m để răn đe, l&agrave;m gương cho x&atilde; hội. L&uacute;c n&agrave;y, chị Hạnh nước mắt tu&ocirc;n tr&agrave;o xin t&ograve;a giảm &aacute;n. C&ograve;n Lữ Văn Hợi v&agrave; cậu em trai đi c&ugrave;ng cũng kh&oacute;c than xin giảm nhẹ tội cho người anh c&ugrave;ng mẹ kh&aacute;c cha của m&igrave;nh.</p>\r\n<p class="Normal">Khi t&ograve;a tuy&ecirc;n 13 năm t&ugrave; giam, Ho&agrave;n kh&oacute;c nức nở. Ở ph&iacute;a dưới, 3 mẹ con chị Hạnh cũng gục xuống.</p>\r\n<p class="Normal">Phi&ecirc;n xử kết th&uacute;c cũng l&agrave; l&uacute;c người mẹ ngh&egrave;o lại thủi lủi dắt hai đứa con ra bến, bắt xe trở về bản.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Trường Long</strong></p>', 26, 6, 2165, 0, 1),
(758, 'Bi kịch của người đàn bà ném con xuống giếng', 'Trong bộ quần áo tối màu, người đàn bà nức nở quằn quại trong vành móng ngựa trả lời thẩm vấn về hành vi tội lỗi với đứa con vừa rứt ruột đẻ ra. Phía dưới khán phòng, chồng cô bình thản một mực xin tòa xử nghiêm vợ mình.', 'nu.jpg', NULL, '<p class="Title">Bi kịch của người đ&agrave;n b&agrave; n&eacute;m con xuống giếng</p>\r\n<p class="Lead">Trong bộ quần &aacute;o tối m&agrave;u, người đ&agrave;n b&agrave; nức nở quằn quại trong v&agrave;nh m&oacute;ng ngựa trả lời thẩm vấn về h&agrave;nh vi tội lỗi với đứa con vừa rứt ruột đẻ ra. Ph&iacute;a dưới kh&aacute;n ph&ograve;ng, chồng c&ocirc; b&igrave;nh thản một mực xin t&ograve;a xử nghi&ecirc;m vợ m&igrave;nh.</p>\r\n<p class="normal">Đ&ocirc;i tay bị c&ograve;ng, ngồi tr&ecirc;n ghế d&agrave;nh cho kẻ mang tội, Nụ len l&eacute;n quay xuống dưới để t&igrave;m b&oacute;ng d&aacute;ng người th&acirc;n sau nhiều th&aacute;ng xa c&aacute;ch. Nhưng kh&aacute;n ph&ograve;ng vắng tanh chỉ c&oacute; mẹ đẻ của c&ocirc; tới nghe t&ograve;a xử con g&aacute;i về tội giết người. Gia đ&igrave;nh nh&agrave; chồng của Nụ cũng c&oacute; mặt nhưng giờ giữa c&ocirc; v&agrave; họ "hố ngăn c&aacute;ch" kh&ocirc;ng thể lấp đầy. Nụ bật kh&oacute;c nức nở.</p>\r\n<p class="normal">Năm 21 tuổi, Nụ về nh&agrave; chồng khi t&igrave;nh y&ecirc;u với anh Nguyễn Văn Linh (23 tuổi, ở Thạch Thất, H&agrave; Nội) mới chớm nở được một th&aacute;ng. Chưa kịp h&ograve;a nhập với sinh hoạt của nh&agrave; chồng, c&ocirc; đ&atilde; l&agrave;m mẹ. C&ocirc; g&aacute;i qu&ecirc; ngh&egrave;o, bỏ học từ sớm, &iacute;t va chạm đ&atilde; phải đối mặt với nhiều kh&oacute; khăn trong cuộc sống mới.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/CA/DF/nu.jpg" border="1" alt="" width="480" height="350" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Nụ ngồi thụp xuống dưới v&agrave;nh m&oacute;ng ngựa khi t&ograve;a v&agrave;o nghị &aacute;n...</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="normal">Nụ bảo từ khi biết vợ mang thai, chồng c&ocirc; c&agrave;ng ng&agrave;y hờ hững chuyện "gần gũi" khiến người đ&agrave;n b&agrave; đang căng tr&agrave;n sức trẻ cảm thấy bị hụt hẫng. Linh cũng &iacute;t quan t&acirc;m đến Nụ khiến cuộc sống vợ chồng c&agrave;ng th&ecirc;m căng thẳng.</p>\r\n<p class="normal">Nghi ngờ chồng c&oacute; mối quan hệ bất ch&iacute;nh b&ecirc;n ngo&agrave;i n&ecirc;n kh&ocirc;ng c&ograve;n mặn m&agrave; với vợ, Nụ nhiều lần dọa l&agrave;m đơn ly dị nhưng kh&ocirc;ng được chồng chấp nhận.</p>\r\n<p class="Normal">Rạng s&aacute;ng 18/11/2009, con trai bị ốm quấy kh&oacute;c nhưng anh Linh kh&ocirc;ng chăm s&oacute;c m&agrave; ph&oacute; mặc cho vợ v&agrave; mẹ. Cho rằng quan hệ vợ chồng kh&ocirc;ng c&ograve;n t&igrave;nh y&ecirc;u, m&agrave; "gắn kết" chỉ v&igrave; đứa trẻ, Nụ bế con ra giếng. Nụ khai c&ocirc; đứng đ&oacute; một hồi l&acirc;u, v&ograve;ng tay &ocirc;m con cứ lỏng dần cho tới khi đứa trẻ rơi xuống nước.</p>\r\n<p class="Normal">Nụ sực tỉnh lao v&agrave;o giường đ&aacute;nh thức chồng dậy, vờ bảo rằng khi đi vệ sinh v&agrave;o đ&atilde; kh&ocirc;ng thấy con đ&acirc;u. Cả gia đ&igrave;nh nh&aacute;o nh&agrave;o đi t&igrave;m. Đến 6h c&ugrave;ng ng&agrave;y, x&aacute;c đứa trẻ được ph&aacute;t hiện ở dưới giếng.</p>\r\n<p class="normal">Suốt phi&ecirc;n xử, Nụ l&iacute; nh&iacute; trả lời thẩm vấn trong nước mắt, thừa nhận mọi tội lỗi. Ph&iacute;a dưới, gia đ&igrave;nh chồng Nụ nh&igrave;n c&ocirc; với &aacute;nh mắt xa lạ. Chỉ c&oacute; b&agrave; mẹ đẻ ngồi thu m&igrave;nh b&ecirc;n g&oacute;c ph&ograve;ng, li&ecirc;n tục đưa tay quệt ngang d&ograve;ng nước mắt.</p>\r\n<p class="normal">Mẹ Nụ bảo gia đ&igrave;nh b&agrave; ngh&egrave;o, thu nhập chủ yếu từ việc h&aacute;i ch&egrave; thu&ecirc;, mỗi ng&agrave;y được 10-15 ngh&igrave;n đồng n&ecirc;n kh&ocirc;ng gi&uacute;p g&igrave; nhiều cho con khi lấy chồng. Nụ l&agrave; con g&aacute;i đầu l&ograve;ng, vốn lầm l&igrave;. Sau khi lấy chồng, thỉnh thoảng c&ocirc; về thăm nh&agrave;, mặt l&uacute;c n&agrave;o cũng buồn rười rượi nhưng chẳng bao giờ chia sẻ chuyện gia đ&igrave;nh với mẹ.</p>\r\n<p class="normal">"T&ocirc;i tiếc kh&ocirc;ng biết chuyện của con m&agrave; khuy&ecirc;n bảo n&oacute;, khiến giờ ra n&ocirc;ng nỗi n&agrave;y", b&agrave; đưa khăn chấm nước mắt. Giờ nh&igrave;n đứa con g&aacute;i ủ rũ b&ecirc;n v&agrave;nh m&oacute;ng ngựa, đối mặt với h&agrave;nh vi giết người m&agrave; nạn nh&acirc;n ch&iacute;nh l&agrave; đứa con vừa đẻ ra, b&agrave; &ocirc;m mặt kh&oacute;c với nỗi &acirc;n hận gi&agrave;y v&ograve;.</p>\r\n<p class="normal">Ph&iacute;a dưới kh&aacute;n ph&ograve;ng, chồng Nụ mặt b&igrave;nh thản, đ&ocirc;i mắt anh r&aacute;o hoảnh. Nh&igrave;n bị c&aacute;o run rẩy trong bộ quần &aacute;o cũ tối m&agrave;u, một hội thẩm nh&acirc;n d&acirc;n ph&acirc;n t&iacute;ch, để xảy ra bi kịch n&agrave;y c&oacute; phần lỗi của người chồng. D&ugrave; Nụ c&oacute; g&acirc;y ra tội &aacute;c t&agrave;y đ&igrave;nh, nhưng Linh nghĩ đến t&igrave;nh nghĩa vợ chồng cũng n&ecirc;n nh&igrave;n nhận tr&aacute;ch nhiệm của m&igrave;nh.</p>\r\n<p class="normal">Tuy nhi&ecirc;n, người chồng vẫn một mực đề nghị "t&ograve;a xử nghi&ecirc;m, đ&uacute;ng người, đ&uacute;ng tội" với người đ&agrave;n b&agrave; từng đầu gối tay ấp với m&igrave;nh..</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/CA/DF/1.jpg" border="1" alt="B&agrave; Ninh" width="350" height="480" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">.. ở ph&iacute;a dưới mẹ c&ocirc; &ocirc;m mặt kh&oacute;c. Ảnh:&nbsp;<em>Anh Thư</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="normal">Kh&ocirc;ng kh&iacute; phi&ecirc;n t&ograve;a như ch&ugrave;ng xuống, v&agrave;i người dự kh&aacute;n rơm rớm nước mắt khi luật sư của Nụ cho biết, sau khi sinh con cuộc sống của c&ocirc; qu&aacute; khổ. Suốt 3 th&aacute;ng, người đ&agrave;n b&agrave; nu&ocirc;i con b&uacute; n&agrave;y chỉ 4 lần được ăn cơm với thịt. Kh&ocirc;ng chỉ vậy, việc bị chồng bỏ b&ecirc; khiến Nụ rơi v&agrave;o cảm gi&aacute;c trống rỗng... dẫn đến bệnh trầm cảm.</p>\r\n<p class="normal">Nữ luật sư bảo đ&atilde; đến bệnh viện, hỏi c&aacute;c chuy&ecirc;n gia t&acirc;m l&yacute; v&agrave; c&oacute; căn cứ về chứng trầm cảm sau khi sinh m&agrave; Nụ mắc phải. "Biểu hiện của căn bệnh n&agrave;y l&agrave; thường nảy sinh những &yacute; nghĩ &aacute;m ảnh, li&ecirc;n quan đến bạo lực, xuất hiện ảo gi&aacute;c...", b&agrave; n&oacute;i.</p>\r\n<p class="normal">Trong l&uacute;c luật sư tr&igrave;nh b&agrave;y, chồng v&agrave; gia đ&igrave;nh Nụ ngồi lặng lẽ. C&ograve;n người đ&agrave;n b&agrave; mang tội nước mắt gi&agrave;n giụa.</p>\r\n<p class="normal">Đồng quan điểm với luật sư, cho rằng ho&agrave;n cảnh khiến Nụ c&oacute; biểu hiện của hội chứng trầm cảm sau sinh, TAND H&agrave; Nội chỉ tuy&ecirc;n phạt người mẹ n&eacute;m con xuống giếng &aacute;n 12 năm t&ugrave;, thấp hơn mức h&igrave;nh phạt đề nghị của VKS.</p>\r\n<p class="normal">Giữa trưa nắng oi ả, Nụ bị dẫn giải ra khỏi ph&ograve;ng bỏ lại đằng sau tiếng kh&oacute;c nghẹn của mẹ. C&ograve;n Linh kh&ocirc;ng buồn liếc mắt nh&igrave;n vợ, anh lạnh l&ugrave;ng tiến về ph&iacute;a luật sư, tranh thủ hỏi thủ tục ly dị.</p>\r\n<p class="normal" style="text-align: right;"><strong>Anh Thư</strong></p>', 26, 6, 537, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(765, 'Mưu đồ sát hại thầy bói của người đàn bà', 'Vừa bước xuống khỏi xe phạm, người đàn bà nhỏ thó đã bật khóc nức nở, đưa tay che mặt tránh những cái nhìn của hàng trăm người dự khán. Nhiều tiếng chỉ trích liên tục vang lên: "Người trông thế kia mà độc ác quá"...', 'bai 2 (1).JPG', NULL, '<p class="Title">Mưu đồ s&aacute;t hại thầy b&oacute;i của người đ&agrave;n b&agrave;</p>\r\n<p class="Lead">Vừa bước xuống khỏi xe phạm, người đ&agrave;n b&agrave; nhỏ th&oacute; đ&atilde; bật kh&oacute;c nức nở, đưa tay che mặt tr&aacute;nh những c&aacute;i nh&igrave;n của h&agrave;ng trăm người dự kh&aacute;n. Nhiều tiếng chỉ tr&iacute;ch li&ecirc;n tục vang l&ecirc;n: "Người tr&ocirc;ng thế kia m&agrave; độc &aacute;c qu&aacute;"...</p>\r\n<p class="Normal">8 giờ s&aacute;ng, khu vực trước chợ Hạnh Th&ocirc;ng T&acirc;y (quận G&ograve; Vấp) đ&atilde; n&ecirc;m k&iacute;n người đến dự phi&ecirc;n t&ograve;a x&eacute;t xử lưu động của t&ograve;a &aacute;n TP HCM đối với V&otilde; Thị Bờ (48 tuổi) về h&agrave;nh vi &ldquo;giết người&rdquo; v&agrave; &ldquo;cướp t&agrave;i sản&rdquo;.</p>\r\n<p class="Normal">Vừa bước xuống khỏi xe phạm, người đ&agrave;n b&agrave; nhỏ th&oacute; đ&atilde; bật kh&oacute;c nức nở, đưa tay che mặt tr&aacute;nh những c&aacute;i nh&igrave;n của h&agrave;ng trăm người dự kh&aacute;n. Chỉ đến khi đ&atilde; y&ecirc;n vị trước v&agrave;nh m&oacute;ng ngựa, b&agrave; ta mới len l&eacute;n đưa mặt t&igrave;m kiếm người th&acirc;n. &Aacute;nh mắt b&agrave; dừng tại g&oacute;c khuất cuối hi&ecirc;n chợ, nơi c&oacute; hai người đ&agrave;n b&agrave;, một gi&agrave;, một trẻ đang &ocirc;m đứa b&eacute; ngủ ngặt nghẽo. Họ l&agrave; chị, con g&aacute;i, v&agrave; ch&aacute;u ngoại của bị c&aacute;o.</p>\r\n<p class="Normal">C&aacute;o trạng được c&ocirc;ng bố dần hiện r&otilde; h&agrave;nh vi giết người man rợ của người đ&agrave;n b&agrave; nhỏ nhắn c&oacute; gương mặt thanh t&uacute; n&agrave;y.</p>\r\n<p class="Normal">Rời v&ugrave;ng qu&ecirc; Đồng Th&aacute;p, mẹ con Bờ dắt d&iacute;u nhau l&ecirc;n TP HCM kiếm sống. Trong khi c&aacute;c con đi l&agrave;m thu&ecirc;, Bờ cũng lập một qu&aacute;n cơm b&igrave;nh d&acirc;n để kiếm tiền trang trải. Đầu năm 2007, b&agrave; chủ qu&aacute;n quen biết v&agrave; kết th&acirc;n với người phụ nữ h&agrave;nh nghề coi b&oacute;i t&ecirc;n Lũy. Thương Bờ, b&agrave; Lũy gọi về sống chung trong căn ph&ograve;ng thu&ecirc; tại đường Quang Trung, phường 10, quận G&ograve; Vấp. Do cuộc sống quạnh quẽ, c&aacute;c con đ&atilde; đi xuất cảnh hết n&ecirc;n b&agrave; Lũy coi Bờ như người th&acirc;n.</p>\r\n<p class="Normal">Về sau, c&ocirc;ng việc bu&ocirc;n b&aacute;n ế ẩm, qu&aacute;n cơm của Bờ phải đ&oacute;ng cửa. Thấy b&agrave; Lũy c&oacute; ch&uacute;t tiền, Bờ ngỏ lời vay một &iacute;t để l&agrave;m vốn b&aacute;n hủ tiếu ven đường với l&atilde;i suất 2% một ng&agrave;y. Tiếp đ&oacute;, khi mẹ Bờ mất, con trai bị tai nạn giao th&ocirc;ng hay l&agrave;m đ&aacute;m cưới cho con g&aacute;i&hellip; Bờ cũng được &ldquo;m&aacute; Lũy&rdquo; cho mượn tiền với h&igrave;nh thức như tr&ecirc;n, tổng cộng 6 triệu đồng vốn.</p>\r\n<p class="Normal">Thời gian đầu Bờ thanh to&aacute;n tiền lời cho b&agrave; Lũy đầy đủ. Tuy nhi&ecirc;n, đến th&aacute;ng 4/2009, người đ&agrave;n b&agrave; n&agrave;y kh&ocirc;ng thể trả nổi v&agrave; số tiền nợ đ&atilde; được b&agrave; Lũy t&iacute;nh l&ecirc;n đến 20 triệu đồng. Từ đ&oacute;, giữa hai người nảy sinh m&acirc;u thuẫn, nhiều lần b&agrave; Lũy chửi mắng, hăm dọa buộc Bờ phải trả tiền cho m&igrave;nh.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/AC/FC/bai%202.JPG" border="1" alt="" width="400" height="300" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">Ngay từ khi bắt đầu phi&ecirc;n xử, V&otilde; Thị Bờ đ&atilde; kh&oacute;c nức nở. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Cuộc sống chung đụng h&agrave;ng ng&agrave;y c&agrave;ng trở n&ecirc;n căng thẳng khi mỗi lần chạm mặt l&agrave; &ldquo;m&aacute; Lũy&rdquo; lại tr&uacute;t giận l&ecirc;n đầu Bờ. Tối ng&agrave;y 5/5/2009, vừa đi chợ, Bờ lại bị chửi t&eacute; t&aacute;t. Cảm thấy bản th&acirc;n bị x&uacute;c phạm, cuộc sống qu&aacute; bế tắc, Bờ nảy sinh &yacute; định giết b&agrave; Lũy để &ldquo;x&ugrave;&rdquo; nợ v&agrave; chiếm đoạt t&agrave;i sản của b&agrave; n&agrave;y.</p>\r\n<p class="Normal">Thực hiện &yacute; định, Bờ đến nơi b&aacute;n hủ tiếu của m&igrave;nh lấy một con dao mang về giấu ở h&ocirc;ng ph&ograve;ng trọ. Chờ cho b&agrave; Lũy ngủ say, khoảng 2 giờ s&aacute;ng, Bờ lấy hung kh&iacute; đến giường của b&agrave; Lũy nhắm đầu b&agrave; n&agrave;y ch&eacute;m xối xả. Biết nạn nh&acirc;n đ&atilde; chết, người đ&agrave;n b&agrave; th&aacute;o to&agrave;n bộ nữ trang tr&ecirc;n người b&agrave; Lũy, lục t&uacute;i lấy tiền rồi g&oacute;i tất cả v&agrave;o một t&uacute;i nylon (gi&aacute; trị tổng cộng hơn 46 triệu đồng). Sau đ&oacute;, Bờ đem dao đi vứt tại một b&atilde;i r&aacute;c rồi đi chợ mua đồ nấu hủ tiếu b&aacute;n như thường lệ.</p>\r\n<p class="Normal">Đến rạng s&aacute;ng, Bờ chạy đến ph&ograve;ng trọ của con g&aacute;i, giấu to&agrave;n bộ số t&agrave;i sản cướp được của b&agrave; Lũy v&agrave;o gầm tủ. Nửa tiếng sau, người đ&agrave;n b&agrave; quay lại ph&ograve;ng trọ v&agrave; tiếp tục giả vờ h&ocirc; ho&aacute;n cho mọi người biết b&agrave; Lũy bị giết. Tuy nhi&ecirc;n, bằng c&aacute;c biện ph&aacute;p nghiệp vụ, cơ quan điều tra đ&atilde; x&aacute;c định V&otilde; Thị Bờ ch&iacute;nh l&agrave; hung thủ. Trước những chứng cứ kh&ocirc;ng thể chối c&atilde;i, người đ&agrave;n b&agrave; n&agrave;y đ&atilde; nhận tội.</p>\r\n<p class="Normal">Suốt thời gian VKS c&ocirc;ng bố c&aacute;o trạng, Bờ cứ &uacute;p mặt v&agrave;o tay kh&oacute;c nức nở. Khu&ocirc;n mặt trắng xanh bị che khuất bởi những lọn t&oacute;c bện chặt v&agrave;o da. Với vẻ ăn ăn nhưng Bờ ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y được thiện cảm cho những người c&oacute; mặt. Mỗi h&agrave;nh vi giết người của b&agrave; n&agrave;y được c&aacute;o trạng lột tả, người dự kh&aacute;n lại nhăn mặt kinh h&atilde;i rồi nhao nhao phẫn nộ. Mỗi lần như thế, Bờ lại co r&uacute;m người lại, tiếng nấc c&agrave;ng to hơn.</p>\r\n<p class="Normal">&ldquo;Bị c&aacute;o quẫn qu&aacute;. Kh&ocirc;ng l&agrave;m sao c&oacute; mấy trăm ng&agrave;n đồng trả l&atilde;i cho b&agrave; ấy mỗi ng&agrave;y. Ng&agrave;y n&agrave;o cũng bị b&agrave; ấy chửi mắng, bị c&aacute;o chịu kh&ocirc;ng nổi n&ecirc;n mới h&agrave;nh động thế&rdquo;, Bờ nức nở tr&igrave;nh b&agrave;y.</p>\r\n<p class="Normal">&ldquo;B&agrave; ấy đ&atilde; thương m&agrave; k&ecirc;u bị c&aacute;o về ở chung cho đỡ tiền nh&agrave;. Đ&atilde; vậy hết lần n&agrave;y đến lần kh&aacute;c cho bị c&aacute;o vay tiền lo cho gia đ&igrave;nh. Nếu c&oacute; chuyện t&iacute;nh l&atilde;i cao th&igrave; cũng do bị c&aacute;o tự nguyện chứ ai bắt buộc. B&agrave; ấy l&agrave; người ơn của bị c&aacute;o cơ m&agrave;&rdquo;, vị chủ tọa lớn tiếng.</p>\r\n<p class="Normal">&ldquo;Bị c&aacute;o thật c&oacute; tội với b&agrave; ấy, c&oacute; tội với x&atilde; hội. Xin mọi người tha thứ cho bị c&aacute;o. Xin gia đ&igrave;nh h&atilde;y tha tội cho t&ocirc;i&hellip;&rdquo;, Bờ quay người về ph&iacute;a những người th&acirc;n của nạn nh&acirc;n, c&uacute;i đầu, chắp tay khẩn khoản.</p>\r\n<p class="Normal">Tr&igrave;nh b&agrave;y với t&ograve;a, em trai b&agrave; Lũy nghẹn giọng: &ldquo;D&ugrave; sao chị t&ocirc;i cũng đ&atilde; chết rồi kh&ocirc;ng g&igrave; c&oacute; thể b&ugrave; đắp được. Xin t&ograve;a xem x&eacute;t m&agrave; giảm nhẹ cho bị c&aacute;o v&igrave; b&agrave; ấy cũng rất khổ. Gia đ&igrave;nh t&ocirc;i chỉ y&ecirc;u cầu bị c&aacute;o x&acirc;y cho chị t&ocirc;i c&aacute;i mồ cho tử tế&hellip;&rdquo;. Bờ lại kh&oacute;c ngất trước tấm l&ograve;ng của gia đ&igrave;nh nạn nh&acirc;n.</p>\r\n<p class="Normal">Giờ nghị &aacute;n k&eacute;o d&agrave;i rất l&acirc;u, người đ&agrave;n b&agrave; cứ sụt sịt m&atilde;i trước v&agrave;nh m&oacute;ng ngựa. Ph&iacute;a dưới, nhiều người chồm hẳn l&ecirc;n h&agrave;ng r&agrave;o ngăn c&aacute;ch với bị c&aacute;o để nh&igrave;n cho r&otilde; mặt Bờ. Văng vẳng nhiều tiếng chỉ tr&iacute;ch: &ldquo;Người tr&ocirc;ng thế kia m&agrave; độc &aacute;c qu&aacute;. Sao l&uacute;c ch&eacute;m người ta tới mấy chục nh&aacute;t kh&ocirc;ng kh&oacute;c đi&rdquo;...</p>\r\n<p class="Normal">Hai người phụ nữ cuối hi&ecirc;n chợ vẫn đứng bất động d&otilde;i mắt về ph&iacute;a bị c&aacute;o. Thấy nhiều người nh&igrave;n m&igrave;nh d&ograve; x&eacute;t, họ vội k&eacute;o khẩu trang che k&iacute;n mặt, chỉ c&ograve;n hở đ&ocirc;i mắt đỏ hoe v&agrave; mọng nước.</p>\r\n<p class="Normal">H&ocirc;m nay, HĐXX cho rằng, V&otilde; Thị Bờ đ&atilde; phạm một l&uacute;c nhiều tội nghi&ecirc;m trọng, h&agrave;nh vi giết người rất d&atilde; man, quyết t&acirc;m thực hiện tội &aacute;c đến c&ugrave;ng để thỏa m&atilde;n động cơ đ&ecirc; h&egrave;n&hellip; đ&aacute;ng lẽ phải &aacute;p dụng mức &aacute;n cao nhất. Tuy nhi&ecirc;n, sau khi phạm tội, bị c&aacute;o đ&atilde; bồi thường thiệt hại v&agrave; được gia đ&igrave;nh nạn nh&acirc;n xin giảm &aacute;n. Đặc biệt, bị c&aacute;o c&oacute; cha l&agrave; liệt sĩ, gia đ&igrave;nh c&oacute; c&ocirc;ng với c&aacute;ch mạng&hellip; n&ecirc;n t&ograve;a đ&atilde; tuy&ecirc;n phạt V&otilde; Thị Bờ mức &aacute;n chung th&acirc;n về c&aacute;c tội &ldquo;giết người&rdquo; v&agrave; &ldquo;cướp t&agrave;i sản&rdquo;.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ Mai</strong></p>', 26, 6, 1947, 0, 1),
(766, 'Nỗi lòng người mẹ của đứa con lầm lạc', 'Chiếc xe tù đỗ xịch dưới sân tòa, bà lão vơ vội bịch xốp rồi lật đật bước theo đám đông để nhìn cho rõ phạm nhân đang bị dẫn giải. Bước chân run rẩy của bà càng quýnh quáng hơn khi bị họ dần bỏ xa.', 'anh-bai.jpg', NULL, '<p class="Title">Nỗi l&ograve;ng người mẹ của đứa con lầm lạc</p>\r\n<p class="Lead">Chiếc xe t&ugrave; đỗ xịch dưới s&acirc;n t&ograve;a, b&agrave; l&atilde;o vơ vội bịch xốp rồi lật đật bước theo đ&aacute;m đ&ocirc;ng để nh&igrave;n cho r&otilde; phạm nh&acirc;n đang bị dẫn giải. Bước ch&acirc;n run rẩy của b&agrave; c&agrave;ng qu&yacute;nh qu&aacute;ng hơn khi bị họ dần bỏ xa.</p>\r\n<p class="Normal">Ngo&agrave;i chiếc t&uacute;i đựng lổn ngổn chai nước suối v&agrave; c&aacute;i b&aacute;nh m&igrave; m&oacute;p m&eacute;o, b&agrave; c&ograve;n khư khư chiếc khăn tay m&agrave;u trắng để thỉnh thoảng quẹt ngang mặt - nơi nước mắt v&agrave; mồ h&ocirc;i đua nhau tr&agrave;n xuống theo từng sải ch&acirc;n yếu ớt của b&agrave;.</p>\r\n<p class="Normal">C&oacute; mặt tại đ&acirc;y từ mờ s&aacute;ng nhưng b&agrave; vẫn chưa biết đứa con lầm lạc của m&igrave;nh bị x&eacute;t xử ở đ&acirc;u. Tần ngần trước căn ph&ograve;ng gần cuối d&atilde;y xử ph&uacute;c thẩm, một lần nữa b&agrave; đưa khăn lau mắt để nh&igrave;n r&otilde; hơn người đ&agrave;n &ocirc;ng đang đứng trước v&agrave;nh m&oacute;ng ngựa. Nhận ra c&aacute;i &aacute;o sơ mi quen thuộc của con trai, b&agrave; lập cập bước v&agrave;o, len lỏi t&igrave;m chỗ ngồi gần con hơn.</p>\r\n<p class="Normal">L&uacute;c n&agrave;y, phi&ecirc;n t&ograve;a đ&atilde; xong phần thủ tục. Vị chủ tọa c&ocirc;ng bố nội dung vụ &aacute;n chỉ vỏn vẹn v&agrave;i ph&uacute;t. Do c&oacute; m&acirc;u thuẫn từ trước, chiều 23/3/2009, con trai b&agrave; - Nguyễn Vũ H&ugrave;ng (35 tuổi), mang theo dao đến nh&agrave; b&agrave; Linh, đ&acirc;m một nh&aacute;t tr&uacute;ng lưng người phụ nữ n&agrave;y, g&acirc;y thương t&iacute;ch 9%.</p>\r\n<p class="Normal">Với h&agrave;nh vi phạm tội tr&ecirc;n, xử sơ thẩm, H&ugrave;ng bị tuy&ecirc;n phạt 18 th&aacute;ng t&ugrave; về tội &ldquo;cố &yacute; g&acirc;y thương t&iacute;ch&rdquo;. Kh&ocirc;ng chấp nhận bản &aacute;n n&agrave;y, nạn nh&acirc;n kh&aacute;ng c&aacute;o đề nghị cấp ph&uacute;c thẩm tăng h&igrave;nh phạt v&agrave; buộc H&ugrave;ng phải bồi thường 30 triệu đồng.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/AB/BF/anh-bai.jpg" border="1" alt="" width="400" height="247" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">H&ugrave;ng bị dẫn giải rời t&ograve;a về trại giam. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">&ldquo;B&agrave; Linh cho rằng bị c&aacute;o cố t&igrave;nh giết chết b&agrave; ấy n&ecirc;n kh&ocirc;ng chấp nhận mức &aacute;n như cấp sơ thẩm đ&atilde; tuy&ecirc;n. Bị c&aacute;o nghĩ sao?&rdquo;, vị thẩm ph&aacute;n chau m&agrave;y hỏi.</p>\r\n<p class="Normal">&ldquo;T&ocirc;i bị cận tới 3 độ. L&uacute;c đ&oacute; bị bay k&iacute;nh ra ngo&agrave;i kh&ocirc;ng nh&igrave;n thấy g&igrave; cả n&ecirc;n cứ đ&acirc;m về đằng trước. C&oacute; biết tr&uacute;ng ai đ&acirc;u m&agrave; bảo t&ocirc;i cố t&igrave;nh giết b&agrave; ấy. C&oacute; th&igrave; t&ocirc;i nhận, c&ograve;n kh&ocirc;ng c&oacute; đ&aacute;nh chết t&ocirc;i cũng kh&ocirc;ng chịu. Ng&agrave;y xưa phạm tội, phải nhận 17 năm t&ugrave; nhưng t&ocirc;i c&oacute; c&atilde;i tiếng n&agrave;o đ&acirc;u. C&ograve;n b&acirc;y giờ, nếu t&ograve;a thấy mức &aacute;n đ&oacute; l&agrave; nhẹ th&igrave; cứ tăng l&ecirc;n. B&agrave; Linh muốn bồi thường th&igrave; t&ocirc;i bồi thường&rdquo;, H&ugrave;ng b&igrave;nh thản đ&aacute;p.</p>\r\n<p class="Normal">Kh&ocirc;ng chỉ HĐXX m&agrave; tất cả những người dự kh&aacute;n đều bất ngờ trước kiểu trả lời &ldquo;tưng tửng&rdquo; của H&ugrave;ng chả ai lại d&aacute;m &ldquo;th&aacute;ch&rdquo; t&ograve;a như vậy. Chỉ c&oacute; b&agrave; l&atilde;o l&agrave; ngơ ng&aacute;c đến tội nghiệp.</p>\r\n<p class="Normal">Giờ nghị &aacute;n, H&ugrave;ng quay quắt kiếm t&igrave;m. Vẻ mặt người đ&agrave;n &ocirc;ng chợt gi&atilde;n ra khi thấy mẹ gi&agrave; lặng lẽ ngồi ngay sau m&igrave;nh, chỉ c&aacute;ch một băng ghế của lực lượng cảnh s&aacute;t.</p>\r\n<p class="Normal">&ldquo;Mẹ t&ocirc;i bị l&atilde;ng tai nặng chẳng nghe thấy g&igrave; đ&acirc;u. Cũng tại t&ocirc;i kh&ocirc;ng nghe lời b&agrave; mới ra n&ocirc;ng nỗi n&agrave;y. Đ&atilde; nhiều lần hứa với b&agrave; sẽ kh&ocirc;ng đề đ&oacute;m nữa nhưng cay c&uacute; v&igrave; bị thua nhiều n&ecirc;n t&ocirc;i cứ l&eacute;n chơi. Ai d&egrave;, khi tr&uacute;ng được một &iacute;t th&igrave; b&agrave; Linh lại kh&ocirc;ng chịu chung tiền c&ograve;n chửi bới. T&ocirc;i tức n&ecirc;n g&acirc;y ra chuyện n&agrave;y, l&agrave;m khổ mẹ nhiều qu&aacute;&rdquo;, H&ugrave;ng buồn b&atilde;.</p>\r\n<p class="Normal">Thấy &aacute;nh mắt của con trai hướng về m&igrave;nh, b&agrave; l&atilde;o bật kh&oacute;c. Đưa c&aacute;nh tay run rẩy lau đ&ocirc;i mắt mờ đục, b&agrave; chậm r&atilde;i cho biết, H&ugrave;ng l&agrave; đứa con b&agrave; đứt ruột sinh ra khi c&ograve;n l&agrave; thiếu nữ 17 tuổi. Chưa hưởng được hạnh ph&uacute;c bao l&acirc;u, chồng b&agrave; tham gia c&aacute;ch mạng v&agrave; hy sinh.</p>\r\n<p class="Normal">Rồi b&agrave; đi bước nữa v&agrave; sinh th&ecirc;m được 3 người con. Tuy nhi&ecirc;n giữa H&ugrave;ng v&agrave; những đứa em kh&ocirc;ng được h&ograve;a thuận bởi t&iacute;nh kh&iacute; ngang t&agrave;ng của cậu cả. Học hết lớp 12, H&ugrave;ng đi nghĩa vụ. Những tưởng m&ocirc;i trường qu&acirc;n đội sẽ r&egrave;n được đứa con ương bướng nhưng b&agrave; kh&ocirc;ng ngờ H&ugrave;ng lại g&acirc;y ra tội với ch&iacute;nh đồng đội m&igrave;nh.</p>\r\n<p class="Normal">Trong một lần được giao tr&aacute;ch nhiệm tr&ocirc;ng coi kho h&agrave;ng của đơn vị, H&ugrave;ng để mất v&agrave;i thứ. Bị khiển tr&aacute;ch, con trai b&agrave; v&agrave; một số người nhất định t&igrave;m cho được thủ phạm trừng trị. V&igrave; thế, khi &ldquo;bắt&rdquo; được người n&agrave;y, cả nh&oacute;m H&ugrave;ng tham gia đ&aacute;nh &ldquo;hội đồng&rdquo; cho b&otilde; tức. Một c&uacute; đ&aacute; của H&ugrave;ng đ&atilde; l&agrave;m người n&agrave;y thiệt mạng. Với lần phạm tội n&agrave;y, H&ugrave;ng bị t&ograve;a &aacute;n qu&acirc;n sự xử phạt 17 năm t&ugrave;.</p>\r\n<p class="Normal">Cũng từ đ&oacute;, t&igrave;nh cảm anh em của H&ugrave;ng với những kh&uacute;c ruột sau c&agrave;ng phai nhạt. Họ c&ograve;n ngăn cản b&agrave; đi thăm nu&ocirc;i anh, khiến người mẹ gi&agrave; rất kh&oacute; xử.</p>\r\n<p class="Normal">Kh&ocirc;ng muốn phiền phức, b&agrave; l&eacute;n mọi người lặn lội h&agrave;ng trăm c&acirc;y số theo ch&acirc;n con đi hết nh&agrave; giam n&agrave;y đến nh&agrave; giam kh&aacute;c. Cũng may, do cải tạo tốt, con trai b&agrave; được tha trước thời hạn 6 năm.</p>\r\n<p class="Normal">Ng&agrave;y trở về, thấy H&ugrave;ng ngơ ng&aacute;c, xa lạ giữa x&atilde; hội v&agrave; sự lạnh nhạt của những đứa em, b&agrave; bảo l&ograve;ng đau như muối x&aacute;t. Người mẹ muốn mang lại cho con hơi ấm gia đ&igrave;nh m&agrave; n&oacute; tr&oacute;t đ&aacute;nh mất v&igrave; h&agrave;nh vi n&ocirc;ng nổi năm xưa. Nhưng o&aacute;i ăm thay, những đứa em của H&ugrave;ng th&igrave; kh&ocirc;ng muốn. Họ buộc b&agrave; phải xa rời &ldquo;kẻ tội đồ&rdquo; m&agrave; b&agrave; đứt ruột đẻ ra.</p>\r\n<p class="Normal">Kh&ocirc;ng chấp nhận, b&agrave; bỏ ra ngo&agrave;i thu&ecirc; nh&agrave; sống với H&ugrave;ng, kh&ocirc;ng nhận bất kỳ sự gi&uacute;p đỡ n&agrave;o từ họ. H&agrave;ng ng&agrave;y, b&agrave; nấu nồi b&uacute;n ri&ecirc;u b&aacute;n ở vỉa h&egrave; c&ograve;n H&ugrave;ng chạy xe &ocirc;m đ&oacute;n kh&aacute;ch ngay &ldquo;cửa h&agrave;ng&rdquo; của mẹ. Hai mẹ con b&agrave; đắp đổi qua ng&agrave;y từ đ&oacute;.</p>\r\n<p class="Normal">Thế nhưng, H&ugrave;ng lại sinh tật &ldquo;đỏ đen&rdquo; với số đề giống nhiều người ngh&egrave;o sống dọc bờ k&ecirc;nh Nhi&ecirc;u Lộc (quận 3). L&agrave;m được bao nhi&ecirc;u tiền, con trai b&agrave; nướng sạch v&agrave;o những con số. Nhiều lần b&agrave; phải nhịn ăn để d&agrave;nh tiền chuộc &ldquo;chiếc cần c&acirc;u cơm&rdquo; của con về để anh c&oacute; c&ocirc;ng ăn việc l&agrave;m. Bao lời khuy&ecirc;n của b&agrave;, người n&agrave;y đều bỏ ngo&agrave;i tai.</p>\r\n<p class="Normal">Trước h&ocirc;m xảy ra vụ &aacute;n, như thường lệ, H&ugrave;ng đến nh&agrave; b&agrave; Linh (thầu đề) đ&aacute;nh thiếu một con 3.000 đồng. Chiều h&ocirc;m đ&oacute;, số n&agrave;y về thật khiến g&atilde; xe &ocirc;m khấp khởi mừng v&igrave; sắp c&oacute; tiền trả nợ. Thế nhưng khi đến t&igrave;m b&agrave; Linh l&atilde;nh tiền, b&agrave; n&agrave;y lớn tiếng: &ldquo;M&agrave;y c&ograve;n thiếu tiền n&ecirc;n tao c&oacute; ghi số đ&oacute; đ&acirc;u m&agrave; giờ đ&ograve;i tr&uacute;ng&rdquo;. Tức giận v&igrave; bị &ldquo;chơi đểu&rdquo;, H&ugrave;ng t&aacute;t b&agrave; n&agrave;y một bạt tai. Thấy ồn &agrave;o, người mẹ chạy sang căn ngăn th&igrave; l&atilde;nh nguy&ecirc;n chiếc gậy v&agrave;o đầu bật m&aacute;u. Sau khi đưa mẹ đến bệnh viện chữa trị, H&ugrave;ng mang dao sang &ldquo;hỏi tội&rdquo; b&agrave; Linh n&ecirc;n xảy ra cớ sự.</p>\r\n<p class="Normal">&ldquo;Biết n&oacute; bị người ta đ&ograve;i tăng &aacute;n, t&ocirc;i lo đến mất ăn mất ngủ. S&aacute;ng sớm nay t&igrave;m đến đ&acirc;y xem sự thể thế n&agrave;o nhưng t&ocirc;i c&oacute; nghe được g&igrave; đ&acirc;u. D&ugrave; sao cũng được nh&igrave;n thấy n&oacute; cho đỡ nhớ. C&ograve;n n&oacute; cũng đỡ tủi th&acirc;n v&igrave; chỉ c&oacute; m&igrave;nh t&ocirc;i tr&ecirc;n đời&hellip;&rdquo;, b&agrave; l&atilde;o đưa &aacute;nh mắt mờ đục về ph&iacute;a con trai, mếu m&aacute;o.</p>\r\n<p class="Normal">H&ocirc;m đ&oacute;, TAND TP HCM đ&atilde; giữ nguy&ecirc;n mức &aacute;n sơ thẩm đối với H&ugrave;ng v&agrave; c&ocirc;ng nhận bị c&aacute;o tự nguyện bồi thường cho nạn nh&acirc;n 30 triệu đồng.</p>\r\n<p class="Normal">H&ugrave;ng mỉm cười nh&igrave;n mẹ gi&agrave; &acirc;u yếm. Lại một lần nữa b&agrave; l&atilde;o tất tả chạy theo con với vẻ thất thần, ngơ ng&aacute;c. Chỉ đến khi c&ocirc; thư k&yacute; giải th&iacute;ch về mức &aacute;n, b&agrave; qu&yacute;nh qu&aacute;ng cảm ơn rồi khập khễnh bước ra về. C&aacute;i d&aacute;ng li&ecirc;u xi&ecirc;u của b&agrave; như chao đảo hơn dưới s&acirc;n t&ograve;a chang chang nắng.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Vũ Mai</strong></p>', 26, 6, 1248, 0, 1),
(769, 'Cuộc ly hôn của người đàn bà 20 năm bị hành hạ', '“Hai mươi năm lấy chồng là ngần đấy năm tôi sống trong tủi nhục. Vì không sinh được con trai, vì thói ăn chơi đàn đúm của chồng mà tôi luôn bị đánh đập, có lần còn bị tẩm xăng đốt”, người vợ nghẹn giọng trình bày trong phiên xử ly hôn.', '1 (1)_3.jpg', NULL, '<p class="Title">Cuộc ly h&ocirc;n của người đ&agrave;n b&agrave; 20 năm bị h&agrave;nh hạ</p>\r\n<p class="Lead">&ldquo;Hai mươi năm lấy chồng l&agrave; ngần đấy năm t&ocirc;i sống trong tủi nhục. V&igrave; kh&ocirc;ng sinh được con trai, v&igrave; th&oacute;i ăn chơi đ&agrave;n đ&uacute;m của chồng m&agrave; t&ocirc;i lu&ocirc;n bị đ&aacute;nh đập, c&oacute; lần c&ograve;n bị tẩm xăng đốt&rdquo;, người vợ nghẹn giọng tr&igrave;nh b&agrave;y trong phi&ecirc;n xử ly h&ocirc;n.</p>\r\n<p class="Normal">Tại TAND tỉnh Nghệ An, b&agrave; Lan gạt nước mắt kể về "s&oacute;ng gi&oacute;" trong cuộc h&ocirc;n nh&acirc;n của m&igrave;nh. B&agrave; cho biết n&ecirc;n duy&ecirc;n vợ chồng c&ugrave;ng L&acirc;m, người k&eacute;m một tuổi ở c&ugrave;ng l&agrave;ng, trước sức &eacute;p của cả hai b&ecirc;n gia đ&igrave;nh.</p>\r\n<p class="Normal">Hạnh ph&uacute;c lứa đ&ocirc;i chỉ k&eacute;o d&agrave;i được gần một năm. Khi b&agrave; mang thai đứa con g&aacute;i đầu l&ograve;ng th&igrave; người chồng bắt đầu c&oacute; những h&agrave;nh động vũ phu, thường xuy&ecirc;n đ&agrave;n đ&uacute;m, rượu ch&egrave;.</p>\r\n<p class="Normal">"Đ&ecirc;m đ&ecirc;m, sau những cuộc chơi, &ocirc;ng ấy về nh&agrave; khật khưỡng, đuổi vợ xuống đất c&ograve;n m&igrave;nh nằm tr&ecirc;n giường. Chỉ cần t&ocirc;i chậm ch&acirc;n một t&yacute; l&agrave; bị đ&aacute;nh ngay", b&agrave; n&oacute;i, n&eacute;m &aacute;nh mắt tức giận về ph&iacute;a người chồng ngồi kế b&ecirc;n.</p>\r\n<p class="Normal">Li&ecirc;n tục bị chồng đ&aacute;nh nhưng thương con, thương cha mẹ, người phụ nữ th&ocirc;n qu&ecirc; vẫn cố chịu đựng. 4 đứa con g&aacute;i nữa lần lượt ra đời khiến cuộc sống gia đ&igrave;nh ng&agrave;y c&agrave;ng th&ecirc;m "ngột ngạt". B&agrave; Lan cho biết, &ocirc;ng L&acirc;m lu&ocirc;n t&igrave;m c&aacute;ch g&acirc;y sự với vợ v&igrave; "kh&ocirc;ng biết đẻ con trai&rdquo;.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n<tbody>\r\n<tr>\r\n<td><img src="http://vnexpress.net/Files/Subject/3B/A1/A5/37/1.jpg" border="1" alt="" width="350" height="281" /></td>\r\n</tr>\r\n<tr>\r\n<td class="Image">B&agrave; Lan (&aacute;o đen) c&ugrave;ng c&ocirc; con g&aacute;i học lớp 10 đến phi&ecirc;n xử. Ảnh:&nbsp;<em>Trường Long.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal">Năm 1997, trong một lần tụ tập đ&aacute;nh nhau, chồng b&agrave; Lan phải ngồi t&ugrave;. Nghĩ sau đợt cải tạo n&agrave;y, bố của 4 đứa con sẽ thay t&iacute;nh đổi nết n&ecirc;n b&agrave; "tha thứ" hết những chuyện đ&atilde; xảy ra.</p>\r\n<p class="Normal">"Nhưng khi &ocirc;ng ấy ra t&ugrave;, những trận đ&aacute;nh vợ diễn ra thường xuy&ecirc;n hơn. Nhiều đ&ecirc;m đang ngủ t&ocirc;i bị &ocirc;ng b&oacute;p cổ, bảo kh&ocirc;ng được k&ecirc;u la nếu kh&ocirc;ng sẽ bị đ&aacute;nh", b&agrave; Lan nức nở tr&igrave;nh b&agrave;y. Vị thẩm ph&aacute;n chủ tọa ngồi trước mặt phải li&ecirc;n tục động vi&ecirc;n đương sự "cứ b&igrave;nh tĩnh".</p>\r\n<p class="Normal">Tiếp lời của mẹ, c&ocirc; con g&aacute;i học lớp 10 cho biết: "Năm học trước, con được đi thi học sinh giỏi tỉnh nhưng v&igrave; nửa đ&ecirc;m, bố đ&aacute;nh mẹ, lại dọa đ&aacute;nh mấy chị em n&ecirc;n con phải bỏ thi v&igrave; lo mẹ ở nh&agrave;... Nhiều lần thấy bố b&oacute;p cổ mẹ, nhưng ch&uacute;ng con kh&ocirc;ng d&aacute;m l&agrave;m g&igrave; v&igrave; c&agrave;ng phản ứng th&igrave; mẹ c&agrave;ng bị đ&aacute;nh đau hơn".</p>\r\n<p class="Normal">&ldquo;V&agrave;o năm 2004, trong một lần đi chơi về, &ocirc;ng L&acirc;m tỏ &yacute; kh&oacute; chịu v&igrave; t&ocirc;i l&uacute;i h&uacute;i trong nh&agrave;, kh&ocirc;ng ra đ&oacute;n. Kh&ocirc;ng n&oacute;i kh&ocirc;ng rằng, &ocirc;ng lấy xăng trong xe m&aacute;y, đổ ra chậu rồi tưới l&ecirc;n người t&ocirc;i, ch&acirc;m lửa. Nếu kh&ocirc;ng c&oacute; những người h&agrave;ng x&oacute;m tốt bụng th&igrave; c&oacute; lẽ t&ocirc;i đ&atilde; chết h&ocirc;m đ&oacute; rồi&rdquo;, người phụ nữ kh&ocirc;ng giấu nổi bức x&uacute;c khi nhắc lại trận chồng thi&ecirc;u .</p>\r\n<p class="Normal">B&agrave; cho biết, ch&iacute;nh quyền địa phương đ&atilde; nhiều lần can thiệp, h&ograve;a giải nhưng người chồng vẫn chứng n&agrave;o tật ấy. B&agrave; v&agrave; c&aacute;c con kh&ocirc;ng chỉ phải chịu đựng nỗi đau thể x&aacute;c m&agrave; c&ograve;n phải "vờ như m&ugrave;" khi biết chồng quan hệ bất ch&iacute;nh với người đ&agrave;n b&agrave; kh&aacute;c. "&Ocirc;ng L&acirc;m kh&ocirc;ng che giấu việc n&agrave;y, thậm ch&iacute; c&oacute; h&ocirc;m c&ograve;n đưa c&ocirc; ta về nh&agrave;", người vợ kể.</p>\r\n<p class="Normal">Sức chịu đựng l&ecirc;n đến "đỉnh điểm", b&agrave; Lan viết đơn xin ly dị. B&agrave; cho biết, &ocirc;ng L&acirc;m c&ograve;n bảo: &ldquo;Nếu kh&ocirc;ng viết th&igrave; đời mi sẽ c&agrave;ng khổ&rdquo;.</p>\r\n<p class="Normal">Những tr&igrave;nh b&agrave;y của b&agrave; Lan trong phi&ecirc;n xử ly h&ocirc;n mở cuối th&aacute;ng 3 đ&atilde; nhận được sự cảm th&ocirc;ng của nhiều người c&oacute; mặt ở ph&ograve;ng xử &aacute;n. C&ograve;n &ocirc;ng L&acirc;m kh&ocirc;ng "thanh minh" điều g&igrave;, chỉ một mực y&ecirc;u cầu được ly dị, y&ecirc;u cầu t&ograve;a tuy&ecirc;n to&agrave;n bộ điền sản của gia đ&igrave;nh l&agrave; thuộc về m&igrave;nh.</p>\r\n<p class="Normal">Tuy nhi&ecirc;n, phi&ecirc;n xử sau &iacute;t ph&uacute;t tiến h&agrave;nh đ&atilde; phải tạm ho&agrave;n v&igrave; vướng c&aacute;c thủ tục định gi&aacute; nguồn gốc đất đai c&ugrave;ng ng&ocirc;i nh&agrave; họ đang sử dụng.</p>\r\n<p class="Normal">Trước khi rời ph&ograve;ng xử, người đ&agrave;n b&agrave; với d&aacute;ng vẻ khắc khổ n&oacute;i trong nước mắt: &ldquo;20 năm lấy chồng l&agrave; ngần đấy năm t&ocirc;i sống trong tủi nhục. 4 đứa con g&aacute;i t&ocirc;i từ khi sinh ra đều phải li&ecirc;n tiếp hứng những trận đ&ograve;n của cha. Nay &ocirc;ng ấy sắp c&oacute; con với người đ&agrave;n b&agrave; kh&aacute;c, mẹ con t&ocirc;i kh&ocirc;ng chấp nhận một người chồng, người cha như vậy. Chỉ c&oacute; ly h&ocirc;n, mẹ con t&ocirc;i mới hết khổ&rdquo;.</p>\r\n<p class="Normal">Giữa s&acirc;n t&ograve;a chang chang nắng, b&oacute;ng hai mẹ con b&agrave; Lan vịn v&agrave;o nhau li&ecirc;u xi&ecirc;u bước ra về.</p>\r\n<p class="Normal" style="text-align: right;"><strong>Trường Long</strong></p>', 26, 6, 1149, 0, 1),
(776, 'Những khoảnh khắc thể thao ấn tượng tuần qua', 'Một CĐV chỉ mặc quần lót, nhảy vào sân gây rối trong trận giao hữu Bosnia Herzegovina - Mexico. Thể thao tuần qua còn chứng kiến thời khắc hạnh phúc của U20 Brazil, góc hình đẹp ở World Cup trượt tuyết...', 'sport7.jpg', NULL, '<h1 class="Title">Những khoảnh khắc thể thao ấn tượng tuần qua</h1>\r\n<h2 class="Lead">Một CĐV chỉ mặc quần l&oacute;t, nhảy v&agrave;o s&acirc;n g&acirc;y rối trong trận giao hữu Bosnia Herzegovina - Mexico. Thể thao tuần qua c&ograve;n chứng kiến thời khắc hạnh ph&uacute;c của U20 Brazil, g&oacute;c h&igrave;nh đẹp ở World Cup trượt tuyết...</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td align="middle"><img src="sport6.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Nam CĐV cởi hết quần &aacute;o ngo&agrave;i, nhảy từ kh&aacute;n đ&agrave;i v&agrave;o s&acirc;n khiến lực lượng bảo vệ trận giao hữu kh&ocirc;ng kịp phản ứng. C&aacute;c cầu thủ đ&atilde; ra tay hỗ trợ đưa CĐV n&agrave;y rời s&acirc;n.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport.jpg" border="1" alt="" width="490" height="294" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Đội tuyển trẻ Brazil đăng quang chức v&ocirc; địch U20 Nam Mỹ. Chiến thắng n&agrave;y đảm bảo cho họ một suất dự Olympic m&ugrave;a h&egrave; năm sau ở London, Anh.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport3.jpg" border="1" alt="" width="490" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Tiền đạo Neymar (cầm Cup) đ&atilde; chứng tỏ t&agrave;i năng thi&ecirc;n ph&uacute; bằng danh hiệu Vua ph&aacute; lưới tại giải U20 Nam Mỹ. Neymar ghi được 9 b&agrave;n, trong đ&oacute; c&oacute; 2 b&agrave;n trong trận chung kết (Brazil 6-0 Uruguay).</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport2.jpg" border="1" alt="" width="490" height="643" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Khoảnh khắc vinh quang của tay vợt số 4 thế giới Robin Soderling sau trận chung kết giải Rotterdam mở rộng. Soderling thắng Jo-Wilfried Tsonga với tỷ số 6-3, 3-6, 6-3 để bảo vệ th&agrave;nh c&ocirc;ng chức v&ocirc; địch. Đ&acirc;y l&agrave; danh hiệu thứ t&aacute;m trong sự nghiệp của tay vợt n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport4.jpg" border="1" alt="" width="490" height="337" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">C&aacute;c đồng đội "rước kiệu" VĐV người &Aacute;o Thomas Morgenstern sau chiến thắng tại World Cup trượt tuyết ở Vikersund, Nauy.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport5.jpg" border="1" alt="" width="490" height="337" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Mặt trăng xuất hiện sau lưng Tom Hilde khi VĐV n&agrave;y thực hiện phần thi tại World Cup trượt tuyết Vikersund.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport7.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Một nh&oacute;m CĐV của đại học Duke h&oacute;a trang như c&aacute;c nh&acirc;n vật trong bộ phim kinh điển Avatar, cổ vũ cho đội nh&agrave; tại giải b&oacute;ng rổ c&aacute;c trường đại học Mỹ.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport8.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Nữ golf thủ Rickie Fowler thực hiện một c&uacute; đ&aacute;nh khi &aacute;nh ho&agrave;ng h&ocirc;n bao phủ tại giải Phoenix mở rộng.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport9.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Tiền vệ David Beckham kh&ocirc;ng bỏ lỡ cơ hội xem trận derby th&agrave;nh Manchester với hai con trai: Romeo v&agrave; Cruz. MU, đội b&oacute;ng cũ của Beckham, thắng Man City 2-1 trong trận n&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Nguyễn Tuấn</strong>&nbsp;(theo&nbsp;<em>SN</em>)</p>', 24, 5, 2313, 0, 1),
(790, 'Biếm họa về sự kiện Dalglish trở lại dẫn dắt Liverpool', 'Kenny Dalglish có thể là "vua" trong lòng nhiều thế hệ người hâm mộ Liverpool, nhưng sự xuất hiện của ông chưa thể giúp đội bóng thành phố cảng nước Anh hồi sinh ngay lập tức.', 'liverpool10.jpg', NULL, '<h1 class="Title">Biếm họa về sự kiện Dalglish trở lại dẫn dắt Liverpool</h1>\r\n<h2 class="Lead">Kenny Dalglish c&oacute; thể l&agrave; "vua" trong l&ograve;ng nhiều thế hệ người h&acirc;m mộ Liverpool, nhưng sự xuất hiện của &ocirc;ng chưa thể gi&uacute;p đội b&oacute;ng th&agrave;nh phố cảng nước Anh hồi sinh ngay lập tức.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool10.jpg" border="1" alt="" width="490" height="354" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Trong khi Dalglish (tr&aacute;i) được xem như "vua" ở Liverpool, người tiền nhiệm của &ocirc;ng - Roy Hodgson - bị đối xử chẳng kh&aacute;c n&agrave;o một kẻ l&agrave;m thu&ecirc;.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool9.jpg" border="1" alt="" width="490" height="441" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Dalglish từng c&ugrave;ng Liverpool gi&agrave;nh 6 chức v&ocirc; địch quốc gia trong tư c&aacute;ch cầu thủ v&agrave; HLV.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool8.jpg" border="1" alt="" width="490" height="284" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Bởi vậy đ&atilde; c&oacute; rất nhiều kỳ vọng rằng sự t&aacute;i xuất của Dalglish sẽ cứu vớt được con t&agrave;u Liverpool đang đắm.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool7.jpg" border="1" alt="" width="490" height="368" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Thậm ch&iacute;, &ocirc;ng được v&iacute; như vị thần m&agrave; mọi người ở Liverpool rất tr&ocirc;ng mong.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool2.jpg" border="1" alt="" width="490" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Tuy nhi&ecirc;n, thực tế ở Liverpool kh&oacute; khăn hơn Dalglish tưởng tượng. Ba trận cầm qu&acirc;n đầu ti&ecirc;n của &ocirc;ng, hai ở Ngoại hạng Anh v&agrave; một ở Cup FA, Liverpool đ&atilde; kh&ocirc;ng thắng lần n&agrave;o. Những kiến thức &ocirc;ng c&oacute; từ ng&agrave;y xưa, nay c&oacute; vẻ như đ&atilde; lỗi thời.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool5.jpg" border="1" alt="" width="490" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">N&ecirc;n chăng Dalglish h&atilde;y k&yacute; hợp đồng với những cựu danh thủ kh&aacute;c của Liverpool, Ian St. John v&agrave; Tommy Smith d&ugrave; họ đ&atilde; ở tuổi xưa nay hếm để tăng cường cho đội chủ s&acirc;n Anfield...</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool4.jpg" border="1" alt="" width="490" height="276" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">... hoặc mượn một chiếc m&aacute;y thời gian để mời Kenny Dalglish của qu&aacute; khứ đến tương lai trợ gi&uacute;p.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool6.jpg" border="1" alt="" width="490" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Gi&aacute; m&agrave; Liverpool b&acirc;y giờ c&oacute; 10 Dalglish, mọi chuyện hẳn sẽ chuyển biến rất nhanh.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool3.jpg" border="1" alt="" width="490" height="227" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Trong mắt Liverpool l&uacute;c n&agrave;y, tất cả c&oacute; vẻ như đều đang chống lại họ - kể cả trọng t&agrave;i. Thế mới c&oacute; chuyện ng&ocirc;i so Ryan Babel đ&atilde; gh&eacute;p ảnh trọng t&agrave;i Howard Webb v&agrave;o chiếc &aacute;o đấu của MU.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool1.jpg" border="1" alt="" width="490" height="367" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Th&agrave;nh phố cảng Liverpool l&agrave; nơi con t&agrave;u huyền thoại Titanic đ&atilde; khởi h&agrave;nh để rồi nhanh ch&oacute;ng ch&igrave;m v&agrave;o l&ograve;ng biển s&acirc;u. V&agrave; liệu nơi n&agrave;y c&oacute; phải chứng kiến một thảm họa tương tự với đội b&oacute;ng h&ugrave;ng mạnh của họ? Đ&atilde; bước sang giai đoạn hai của m&ugrave;a giải Ngoại hạng Anh nhưng Liverpool hiện chỉ hơn nh&oacute;m của nguy cơ xuống hạng 4 điểm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="1" cellpadding="3" width="45" align="center" bgcolor="#408080">\r\n<tbody>\r\n<tr>\r\n<td class="PnDTitle" align="middle" bgcolor="#408080">Ngoại hạng Anh 2010-2011</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor="#ffffff">\r\n<table border="0" cellspacing="1" cellpadding="1" width="246">\r\n<tbody>\r\n<tr>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">TT</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">Đội</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">Trận</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">H/S</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">Điểm</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">1</td>\r\n<td class="BoxLink" align="middle">MU</td>\r\n<td class="BoxLink" align="middle">21</td>\r\n<td class="BoxLink" align="middle">24</td>\r\n<td class="BoxLink" align="middle">45</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">2</td>\r\n<td class="BoxLink" align="middle">Man City</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n<td class="BoxLink" align="middle">18</td>\r\n<td class="BoxLink" align="middle">45</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">3</td>\r\n<td class="BoxLink" align="middle">Arsenal</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n<td class="BoxLink" align="middle">43</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">4</td>\r\n<td class="BoxLink" align="middle">Chelsea</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">19</td>\r\n<td class="BoxLink" align="middle">38</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">5</td>\r\n<td class="BoxLink" align="middle">Tottenham</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">6</td>\r\n<td class="BoxLink" align="middle">37</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">6</td>\r\n<td class="BoxLink" align="middle">Sunderland</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n<td class="BoxLink" align="middle">3</td>\r\n<td class="BoxLink" align="middle">34</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">7</td>\r\n<td class="BoxLink" align="middle">Bolton</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n<td class="BoxLink" align="middle">3</td>\r\n<td class="BoxLink" align="middle">30</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">8</td>\r\n<td class="BoxLink" align="middle">Stoke</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">2</td>\r\n<td class="BoxLink" align="middle">30</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">9</td>\r\n<td class="BoxLink" align="middle">Newcastle</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">3</td>\r\n<td class="BoxLink" align="middle">29</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">10</td>\r\n<td class="BoxLink" align="middle">Blackpool</td>\r\n<td class="BoxLink" align="middle">21</td>\r\n<td class="BoxLink" align="middle">-5</td>\r\n<td class="BoxLink" align="middle">28</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">11</td>\r\n<td class="BoxLink" align="middle">Blackburn</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n<td class="BoxLink" align="middle">-8</td>\r\n<td class="BoxLink" align="middle">28</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">12</td>\r\n<td class="BoxLink" align="middle">Everton</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">-2</td>\r\n<td class="BoxLink" align="middle">26</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><strong>13</strong></td>\r\n<td class="BoxLink" align="middle"><strong>Liverpool</strong></td>\r\n<td class="BoxLink" align="middle"><strong>22</strong></td>\r\n<td class="BoxLink" align="middle"><strong>-4</strong></td>\r\n<td class="BoxLink" align="middle"><strong>26</strong></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">14</td>\r\n<td class="BoxLink" align="middle">West Brom</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">-12</td>\r\n<td class="BoxLink" align="middle">25</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">15</td>\r\n<td class="BoxLink" align="middle">Fulham</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">-2</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">16</td>\r\n<td class="BoxLink" align="middle">Birmingham</td>\r\n<td class="BoxLink" align="middle">21</td>\r\n<td class="BoxLink" align="middle">-5</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">17</td>\r\n<td class="BoxLink" align="middle">Aston Villa</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">-15</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">18</td>\r\n<td class="BoxLink" align="middle">Wigan</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">-15</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">19</td>\r\n<td class="BoxLink" align="middle">Wolves</td>\r\n<td class="BoxLink" align="middle">22</td>\r\n<td class="BoxLink" align="middle">-14</td>\r\n<td class="BoxLink" align="middle">21</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">20</td>\r\n<td class="BoxLink" align="middle">West Ham</td>\r\n<td class="BoxLink" align="middle">23</td>\r\n<td class="BoxLink" align="middle">-19</td>\r\n<td class="BoxLink" align="middle">20</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Do&atilde;n Mạnh</strong></p>', 24, 5, 474, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TomTat`, `Hinh`, `Ngay`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(778, 'Kỳ thủ Quang Liêm rộng cửa bảo vệ ngôi vô địch ở Nga', 'Tối 14/2 đại kiện tướng cờ vua số một của Việt Nam xuất sắc đánh bại kỳ thủ chủ nhà Kobalia Mikhail (elo 2666), qua đó tạo khoảng cách một điểm với nhóm phía sau khi giải Aeroflot tại Moscow chỉ còn 2 ván.', 'liem.jpg', NULL, '<h1 class="Title">Những khoảnh khắc thể thao ấn tượng tuần qua</h1>\r\n<h2 class="Lead">Một CĐV chỉ mặc quần l&oacute;t, nhảy v&agrave;o s&acirc;n g&acirc;y rối trong trận giao hữu Bosnia Herzegovina - Mexico. Thể thao tuần qua c&ograve;n chứng kiến thời khắc hạnh ph&uacute;c của U20 Brazil, g&oacute;c h&igrave;nh đẹp ở World Cup trượt tuyết...</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td align="middle"><img src="sport6.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Nam CĐV cởi hết quần &aacute;o ngo&agrave;i, nhảy từ kh&aacute;n đ&agrave;i v&agrave;o s&acirc;n khiến lực lượng bảo vệ trận giao hữu kh&ocirc;ng kịp phản ứng. C&aacute;c cầu thủ đ&atilde; ra tay hỗ trợ đưa CĐV n&agrave;y rời s&acirc;n.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport.jpg" border="1" alt="" width="490" height="294" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Đội tuyển trẻ Brazil đăng quang chức v&ocirc; địch U20 Nam Mỹ. Chiến thắng n&agrave;y đảm bảo cho họ một suất dự Olympic m&ugrave;a h&egrave; năm sau ở London, Anh.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport3.jpg" border="1" alt="" width="490" height="326" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Tiền đạo Neymar (cầm Cup) đ&atilde; chứng tỏ t&agrave;i năng thi&ecirc;n ph&uacute; bằng danh hiệu Vua ph&aacute; lưới tại giải U20 Nam Mỹ. Neymar ghi được 9 b&agrave;n, trong đ&oacute; c&oacute; 2 b&agrave;n trong trận chung kết (Brazil 6-0 Uruguay).</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport2.jpg" border="1" alt="" width="490" height="643" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Khoảnh khắc vinh quang của tay vợt số 4 thế giới Robin Soderling sau trận chung kết giải Rotterdam mở rộng. Soderling thắng Jo-Wilfried Tsonga với tỷ số 6-3, 3-6, 6-3 để bảo vệ th&agrave;nh c&ocirc;ng chức v&ocirc; địch. Đ&acirc;y l&agrave; danh hiệu thứ t&aacute;m trong sự nghiệp của tay vợt n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport4.jpg" border="1" alt="" width="490" height="337" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">C&aacute;c đồng đội "rước kiệu" VĐV người &Aacute;o Thomas Morgenstern sau chiến thắng tại World Cup trượt tuyết ở Vikersund, Nauy.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport5.jpg" border="1" alt="" width="490" height="337" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Mặt trăng xuất hiện sau lưng Tom Hilde khi VĐV n&agrave;y thực hiện phần thi tại World Cup trượt tuyết Vikersund.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport7.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Một nh&oacute;m CĐV của đại học Duke h&oacute;a trang như c&aacute;c nh&acirc;n vật trong bộ phim kinh điển Avatar, cổ vũ cho đội nh&agrave; tại giải b&oacute;ng rổ c&aacute;c trường đại học Mỹ.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport8.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Nữ golf thủ Rickie Fowler thực hiện một c&uacute; đ&aacute;nh khi &aacute;nh ho&agrave;ng h&ocirc;n bao phủ tại giải Phoenix mở rộng.</td>\r\n</tr>\r\n<tr>\r\n<td align="middle"><img src="sport9.jpg" border="1" alt="" width="490" height="334" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Tiền vệ David Beckham kh&ocirc;ng bỏ lỡ cơ hội xem trận derby th&agrave;nh Manchester với hai con trai: Romeo v&agrave; Cruz. MU, đội b&oacute;ng cũ của Beckham, thắng Man City 2-1 trong trận n&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class="Normal" style="text-align: right;"><strong>Nguyễn Tuấn</strong>&nbsp;(theo&nbsp;<em>SN</em>)</p><h1 class="Title">Biếm họa về sự kiện Dalglish trở lại dẫn dắt Liverpool</h1>\r\n<h2 class="Lead">Kenny Dalglish c&oacute; thể l&agrave; "vua" trong l&ograve;ng nhiều thế hệ người h&acirc;m mộ Liverpool, nhưng sự xuất hiện của &ocirc;ng chưa thể gi&uacute;p đội b&oacute;ng th&agrave;nh phố cảng nước Anh hồi sinh ngay lập tức.</h2>\r\n<table border="0" cellspacing="0" cellpadding="3" width="100%">\r\n<tbody>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool10.jpg" border="1" alt="" width="490" height="354" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Trong khi Dalglish (tr&aacute;i) được xem như "vua" ở Liverpool, người tiền nhiệm của &ocirc;ng - Roy Hodgson - bị đối xử chẳng kh&aacute;c n&agrave;o một kẻ l&agrave;m thu&ecirc;.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool9.jpg" border="1" alt="" width="490" height="441" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Dalglish từng c&ugrave;ng Liverpool gi&agrave;nh 6 chức v&ocirc; địch quốc gia trong tư c&aacute;ch cầu thủ v&agrave; HLV.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool8.jpg" border="1" alt="" width="490" height="284" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Bởi vậy đ&atilde; c&oacute; rất nhiều kỳ vọng rằng sự t&aacute;i xuất của Dalglish sẽ cứu vớt được con t&agrave;u Liverpool đang đắm.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool7.jpg" border="1" alt="" width="490" height="368" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Thậm ch&iacute;, &ocirc;ng được v&iacute; như vị thần m&agrave; mọi người ở Liverpool rất tr&ocirc;ng mong.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool2.jpg" border="1" alt="" width="490" height="302" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Tuy nhi&ecirc;n, thực tế ở Liverpool kh&oacute; khăn hơn Dalglish tưởng tượng. Ba trận cầm qu&acirc;n đầu ti&ecirc;n của &ocirc;ng, hai ở Ngoại hạng Anh v&agrave; một ở Cup FA, Liverpool đ&atilde; kh&ocirc;ng thắng lần n&agrave;o. Những kiến thức &ocirc;ng c&oacute; từ ng&agrave;y xưa, nay c&oacute; vẻ như đ&atilde; lỗi thời.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool5.jpg" border="1" alt="" width="490" height="318" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">N&ecirc;n chăng Dalglish h&atilde;y k&yacute; hợp đồng với những cựu danh thủ kh&aacute;c của Liverpool, Ian St. John v&agrave; Tommy Smith d&ugrave; họ đ&atilde; ở tuổi xưa nay hếm để tăng cường cho đội chủ s&acirc;n Anfield...</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool4.jpg" border="1" alt="" width="490" height="276" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">... hoặc mượn một chiếc m&aacute;y thời gian để mời Kenny Dalglish của qu&aacute; khứ đến tương lai trợ gi&uacute;p.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool6.jpg" border="1" alt="" width="490" height="310" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Gi&aacute; m&agrave; Liverpool b&acirc;y giờ c&oacute; 10 Dalglish, mọi chuyện hẳn sẽ chuyển biến rất nhanh.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool3.jpg" border="1" alt="" width="490" height="227" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Trong mắt Liverpool l&uacute;c n&agrave;y, tất cả c&oacute; vẻ như đều đang chống lại họ - kể cả trọng t&agrave;i. Thế mới c&oacute; chuyện ng&ocirc;i so Ryan Babel đ&atilde; gh&eacute;p ảnh trọng t&agrave;i Howard Webb v&agrave;o chiếc &aacute;o đấu của MU.</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle"><img src="http://vnexpress.net/Files/Subject/3B/A2/59/80/liverpool1.jpg" border="1" alt="" width="490" height="367" /></td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="left">Th&agrave;nh phố cảng Liverpool l&agrave; nơi con t&agrave;u huyền thoại Titanic đ&atilde; khởi h&agrave;nh để rồi nhanh ch&oacute;ng ch&igrave;m v&agrave;o l&ograve;ng biển s&acirc;u. V&agrave; liệu nơi n&agrave;y c&oacute; phải chứng kiến một thảm họa tương tự với đội b&oacute;ng h&ugrave;ng mạnh của họ? Đ&atilde; bước sang giai đoạn hai của m&ugrave;a giải Ngoại hạng Anh nhưng Liverpool hiện chỉ hơn nh&oacute;m của nguy cơ xuống hạng 4 điểm.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellspacing="1" cellpadding="3" width="45" align="center" bgcolor="#408080">\r\n<tbody>\r\n<tr>\r\n<td class="PnDTitle" align="middle" bgcolor="#408080">Ngoại hạng Anh 2010-2011</td>\r\n</tr>\r\n<tr>\r\n<td bgcolor="#ffffff">\r\n<table border="0" cellspacing="1" cellpadding="1" width="246">\r\n<tbody>\r\n<tr>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">TT</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">Đội</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">Trận</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">H/S</td>\r\n<td class="PnDTitle" align="middle" bgcolor="#3f3f3f">Điểm</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">1</td>\r\n<td class="BoxLink" align="middle">MU</td>\r\n<td class="BoxLink" align="middle">21</td>\r\n<td class="BoxLink" align="middle">24</td>\r\n<td class="BoxLink" align="middle">45</td>\r\n</tr>\r\n<tr>\r\n<td class="BoxLink" align="middle">2</td>\r\n<td class="BoxLink" align="middle">Man City</td>\r\n<td class=